INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1, 'a''dan z''ye (kadar)', 'baştan aşağı, tamamen, tamamıyla, bütünüyle: Evini a''dan z''ye değiştirdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2, 'aba altında er yatar', 'giyim kuşam kişiliğe ölçü olamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3, 'aba gibi', 'kaba ve kalın (kumaş).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4, 'aba vakti yaba, yaba vakti aba', 'gereksinimler vaktinden önce ve ucuz olduğu zaman karşılanmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5, 'abanın kadri yağmurda bilinir', 'bir şeyin gerçek değeri, ancak ona çok ihtiyaç duyulduğu zaman iyi anlaşılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6, 'abanoz gibi', '1) çok sert: Abanoz gibi tahta. 2) kapkara.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7, 'abazan kalmak', '1) uzun süre cinsel ilişkide bulunmamak; 2) aç kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8, 'abdal ata binince bey oldum sanır, şalgam aşa girince yağ oldum sanır', 'görmemiş kişi, rastlantı sonucu layık olmadığı bir duruma kavuşursa bu durum kendisinin hakkıymış gibi aptalca böbürlenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9, 'abdal düğünden, çocuk oyundan usanmaz', 'bir kimse sevdiği işi sürekli olarak yapmaktan bıkmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10, 'abdal tekkede, hacı Mekke''de bulunur', 'herkes kendisine yakışan ve uğraştığı işle ilgili olan yerdedir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11, 'abdala ''kar yağıyor'' demişler, ''titremeye hazırım (durmuşum)'' demiş', 'varlıklılar için sıkıntı olabilecek bir durum, yoksullar için söz konusu bile olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12, 'abdala malum olur', 'bir şeyin olacağını önceden sezen kimseler için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13, 'abdalın dostluğu köy görününceye kadar', 'çıkarı dolayısıyla yakınlık gösteren kimse, işini yürütecek başkalarını bulduğunda sizinle ilgisini keser.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (14, 'abdalın karnı doyunca gözü pabucundadır (yolda olur)', 'çıkarına düşkün kimselerin arkadaşlığı işi bitinceye kadardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (15, 'abdalın yağı çok olursa gâh borusuna çalar, gâh gerisine', 'varlıklı ama akılsız ve hesapsız kişi malını gereksiz yerlere harcar, telef eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (16, 'abdest almak', '1) Müslümanlar, belli ibadetleri yapabilmek için bir düzen içerisinde bazı organları yıkayıp bazılarını mesh ederek arınmak; 2) boy abdesti almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (17, 'abdest bozmak', 'idrar veya dışkı yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (18, 'abdest tazelemek', 'abdesti bozulmadığı hâlde yeniden abdest almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (19, 'abdesti gelmek', 'abdest bozmaya gereksinim duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (20, 'abdesti kaçmak', 'abdesti bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (21, 'abdestinde namazında olmak', 'dindar olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (22, 'abdestinden şüphesi olmamak', 'yaptığı işte kusuru olmadığını kesin olarak bilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (23, 'abdestsiz sofuya namaz mı dayanır', 'kurallara, koşullara uyulmadıktan sonra bir sürü iş yapılabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (24, 'abdestsiz yere basmamak', 'dinî kurallara titizlikle bağlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (25, 'abesle iştigal etmek (uğraşmak)', 'yersiz, yararsız işlerle vakit öldürmek: ''Yazarlarımızın çoğu yalnızca kendi ürünlerinin ne amaçla üretildiğini sayıp dökerek bir anlamda abesle iştigal ediyorlar.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (26, 'abıhayat içmiş', 'yaşı çok ilerlemiş olmasına karşın genç görünen (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (27, 'abliyi kaçırmak (bırakmak, koyuvermek)', '1) soğukkanlılığını yitirip davranışlarını denetleyememek; 2) şaşırıp ne yapacağını bilememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (28, 'ablukayı yarmak', 'kuşatılan bölgeden zor kullanarak dışarı çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (29, 'abuk sabuk konuşmak', 'ne söylediğini bilmeden, düşüncesiz, tutarsız konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (30, 'acayibine gitmek', 'yadırgamak, tuhafına gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (31, 'acele ile menzil alınmaz', 'acele etmekle daha çabuk sonuç alınır sanılmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (32, 'acele ile yürüyen yolda kalır', 'iş yaparken acele eden şaşırır, işini bitiremez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (33, 'acele işe şeytan karışır', 'düşünüp taşınmadan ivedi olarak yapılan işten iyi sonuç alınamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (34, 'acele işin sonu pişmanlık', 'acele ederek yaptığımız işten istediğimiz sonucu alamayabiliriz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (35, 'Acem kılıcı gibi', 'her iki tarafı da idare edebilen, güvenilmez (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (36, 'acemi katır kapı önünde yük indirir', 'beceriksiz ve anlayışsız kişi kendisine yaptırılan işi en önemli yerinde bırakır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (37, 'acemi nalbant gâvur eşeğinde öğrenir', 'mesleğinde ustalığa erişmemiş kimse, ilk denemelerini gözden çıkarılabilecek malzeme üzerinde yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (38, 'acemilik çekmek', 'alışamadığı bir işte zorluk çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (39, 'acı (acılar) görmek', 'kötü günler yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (40, 'acı acıyı keser, su sancıyı', 'bir güçlüğü yenmek için başka bir güç yola başvurulmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (41, 'acı çekmek (duymak)', '1) ağrı, sızı duymak: Ameliyattan sonra çok acı çekti. 2) mec. üzülmek, üzüntü içinde kalmak: ''Bu faciaya bizzat karışmışım gibi bir acı duyuyordum.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (42, 'acı gelmek', 'dokunmak, kırmak, üzmek: ''Bu durumun gerçeklerle uyumsuzluğu ona acı geliyor.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (43, 'acı (kötü) söz insanı (adamı) dinden çıkarır, tatlı söz yılanı inden çıkarır', 'gönül alıcı, okşayıcı sözlerle karşımızdakinin inadı yenilebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (44, 'acı patlıcanı kırağı çalmaz', 'herhangi bir duruma alışkın olan kimseyi benzer kötü durumlar etkilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (45, 'acı söylemek', 'olumsuz bir davranış karşısında gerçeği olduğu gibi söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (46, 'acıkan doymam sanır, susayan kanmam sanır', 'bir şeyi uzun süre elde edemeyen kimse, daha sonra o şeyden ne kadar çok edinirse edinsin yine kendisine yetmeyeceği kanısında bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (47, 'acıkan ne olsa yer, acıyan ne olsa söyler', 'geçim sıkıntısı içinde bulunan kişi geçinebilmek için her yolu dener, her işi yapar, canı yanan kişi de sonunu düşünmeden ağzına geleni söyler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (48, 'acıkan ne yemez, acıyan ne demez', 'geçim sıkıntısı içinde bulunan kişi geçinebilmek için her yolu dener, her işi yapar, canı yanan kişi de sonunu düşünmeden ağzına geleni söyler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (49, 'acıkanın yanağından, susayanın dudağından belli olur', 'bir insanın ne durumda olduğu yüzünden anlaşılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (50, 'acıklı başta akıl olmaz', 'büyük sıkıntılar içinde bulunanlar mantık dışı işler yapabilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (51, 'acıkmış kudurmuştan beterdir', 'uzun süre bir nesnenin yokluğunu çeken kimse, onu gördüğünde büyük bir istekle ona saldırır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (52, 'acından kimse ölmemiş', 'kişi yoksul olabilir, işsiz ve parasız kalabilir ama aç kalmaz, mutlaka bir geçim yolu bulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (53, 'acından ölmek', '1) çok acıkmak; 2) aşırı derecede yoksul olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (54, 'acındırırsan arsız olur, acıktırırsan hırsız olur', 'koruduğunuz kimsenin sürekli acınmasına izin verirseniz arsız olur, emeğinin karşılığını tam olarak vermediğiniz kişi de hırsız olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (55, 'acısı içine (yüreğine) çökmek (işlemek)', '1) bir şeyin acısını derinden duymak; 2) kötü bir şey olacağını düşünerek önceden üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (56, 'acısı ortaya çıkmak', 'olumsuz sonucu yavaş yavaş ortaya çıkmak: ''Dur bakalım daha hele, o içtiklerinin acısı bir bir çıkacak ortaya.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (57, 'acısını almak', '1) acılığını gidermek; 2) sızıyı dindirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (58, 'acısını bağrına (içine) basmak (gömmek)', 'bir üzüntüye, sıkıntıya yakınmadan katlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (59, 'acısını çekmek', 'yapılan yanlış bir işin doğurduğu sıkıntı ve üzüntü içinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (60, 'acısını çıkarmak', '1) acılığını yok etmek: Soğanın acısını çıkarmak. 2) mec. uğradığı maddi veya manevi zararı karşılayacak bir iş yapmak. 3) mec. öç almak: ''Ustanın kendisini küçük burjuva münevveri diye aşağılık görmesinin acısını çıkarıyor.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (61, 'acısını görmek', 'bir yakınının ölümünü görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (62, 'acıyan uyumuş, acıkan uyumamış', 'insan sıkıntıya katlanır da açlığa katlanamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (63, 'aciz içinde olmak', 'gücü yetmemek, becerememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (64, 'âciz kalmak', 'çok uğraşmasına karşın bir işi yapamamak: ''Kitaplar Taptuk''u anlatmaktan âciz kalır.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (65, 'acze düşmek', 'çaresiz kalmak, elinden bir şey gelmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (66, 'aç aç ile yatınca arada dilenci doğar', 'karı koca yoksul olursa bunların çocukları da yoksul olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (67, 'aç açık kalmak', 'yoksulluk içinde, evsiz barksız kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (68, 'aç aman bilmez, çocuk zaman bilmez', 'aç hiçbir mazeretle susturulamaz, çocuk da istediği şeyi hemen elde etmek ister.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (69, 'aç aslandan tok domuz yeğdir', 'soysuz olup para kazanan, soylu olup da para kazanmayandan üstündür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (70, 'aç at yol almaz, aç it av almaz', 'iş gördürdüğünüz kimselerin haklarını tam olarak vermezseniz kendilerinden yararlanamazsınız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (71, 'aç ayı oynamaz', 'kendisinden iş beklenilen kimseden emeğinin karşılığı esirgenmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (72, 'aç domuz darıdan çıkmaz', 'kötü yaradılışlı aç olan kimse kime, neye zarar verdiğini düşünmeden sadece karnını doyurmaya bakar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (73, 'aç doymam, tok acıkmam sanır', 'aç insan elde ettiğinden çoğunu ister, varlıklı insan ise daha fazlasını ister.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (74, 'aç doyurmak', 'yoksulları beslemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (75, 'aç elini kora sokar', 'aç insan, geçimini sağlamak için kendisini her türlü tehlikeye atar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (76, 'aç esner, âşık gerinir', 'herkes içinde bulunduğu koşula göre davranır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (77, 'aç gezmektense tok ölmek yeğdir', 'yoksulluk ölümden de beterdir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (78, 'aç gözünü, açarlar gözünü', 'yaptığın işlerde uyanık davranmazsan çok kötü durumlarla karşılaşır, gözünü dört açmak zorunda kalırsın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (79, 'aç ile dost olayım diyen peşin karnını doyursun', 'yakınlık kurduğumuz kimsenin sağlama olanağı bulunmayan şeyi, ondan beklemeden kendimiz elde etmeye çalışmalıyız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (80, 'aç ile eceli gelen söyleşir', 'açın gözü hiçbir şeyi görmez, karnını doyurabilmek için kendisine güçlük çıkaran bir kimseyi öldürebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (81, 'aç it fırın duvarını deler', 'aç kimse karnını doyurmak için önüne çıkan engellerin tamamını aşar ve isteğini elde eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (82, 'aç kalmak', '1) karnını doyuramamak: ''Fatma''nın yemek çantası olmasaydı, dün aç kalmıştım.'' -F. R. Atay. 2) yoksulluğa düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (83, 'aç kalmak, borçlu olmaktan iyidir', 'sözünün eri olana, borcunu ödeyememek aç kalmaktan daha ağır gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (84, 'aç köpek fırın deler', 'aç kimse karnını doyurmak için önüne çıkan engellerin tamamını aşar ve isteğini elde eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (85, 'aç kurt aslana saldırır', 'aç kimse karnını doyurmak için gerekirse ölümü göze alır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (86, 'aç kurt gibi', 'büyük bir istekle.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (87, 'aç kurt yavrusunu yer', 'açın gözü kararmıştır, karnını doyurmak için ölümü bile göze alarak kendisinden kat kat güçlü olan yaratıklarla boğuşur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (88, 'aç mezarı yoktur', 'kişi yoksul olabilir, işsiz ve parasız kalabilir ama aç kalmaz, mutlaka bir geçim yolu bulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (89, 'aç ne yemez, tok ne demez', 'yoksul kimse eline geçen şeyin iyisine kötüsüne bakmaz, varlıklı kişi ise en güzel şeylerde bile kusur bulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (90, 'aç ölmez gözü kararır, susuz ölmez benzi sararır', 'yoksulluk insanı öldürmez ama türlü türlü üzüntü ve sıkıntı içinde yıpratır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (91, 'aç susuz kalmak', '1) yoksulluktan yaşayamayacak bir duruma gelmek; 2) yoksul bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (92, 'aç tavuk kendini arpa (buğday) ambarında sanır', 'insanlar, yokluğunu, yoksulluğunu çektikleri şeyler için olmayacak hayaller, düşler kurar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (93, 'aç tokun gözüne bakmakla doymaz', 'yoksul insanla ilgilenmek ancak ona yardım etmekle olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (94, 'aç yanında sarpın kurcalanmaz', 'bir kimsenin yanında, onun çok duyarlı olduğu konuya değinmemek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (95, 'aç yanından kaç', 'aç insan tehlikelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (96, 'aç yeri başka, acı yeri başka', 'insanın yüreği ne denli acıyla dolu olsa da yemek yemeyi ister.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (97, 'aça dokuz yorgan örtmüşler, yine uyuyamamış', '1) aç olan kimse, kendisine ne kadar rahatlık sağlanırsa sağlansın, dinlendirilemez. 2) bir şeye ihtiyaç duyan kimse, ancak onun giderilmesiyle rahata kavuşturulabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (98, 'açığını kapamak (kapatmak)', '1) eksiğinin veya küçük düşürücü durumunun anlaşılmamasını sağlamak; 2) eksiğini tamamlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (99, 'açık ağız aç kalmaz', 'isteklerini uygun bir biçimde söylemesini bilen kimse, onları önünde sonunda elde eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (100, 'açık düşmek', '1) herhangi bir sebeple bir filodan veya istenilen yerden uzakta kalmak; 2) sp. yağlı güreşte yenilgi sebebi olan sırtı veya yanı toprağa değmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (101, 'açık göte herkes tükürür', 'utanç verici, iğrendirici davranışları herkes ayıplar, tiksinti ile karşılar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (102, 'açık kaba it değer (siyer)', 'gizli kalması gereken şeyler herkese söylenirse bundan büyük zararlar doğar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (103, 'açık kapamak', 'bütçenin gider fazlasını, para sağlayarak ortadan kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (104, 'açık kapı bırakmak', 'gereğinde, bir konuya yeniden dönebilme imkânı bırakmak, kesip atmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (105, 'açık vermek', '1) geliri, giderini karşılamamak; 2) gizlenmek istenen bir olayı, bir düşünceyi veya durumu elde olmayarak ortaya koymak, açıklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (106, 'açık yaraya tuz ekilmez', 'acısı henüz taze olan bir kimsenin üzüntüsü, birtakım söz ve davranışlarla artırılmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (107, 'açıklar livası olmak', ' alay işsiz ve kazançsız kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (108, 'açıklığa kavuşturmak', 'bir konu veya sorunu aydınlatmak, kapalılıktan kurtarmak, anlaşılır duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (109, 'açıklık getirmek', 'bir konu veya sorunu anlaşılır duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (110, 'açıklık kazanmak', 'bir konu aydınlanmak, anlaşılır duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (111, 'açıkta kalmak (olmak)', '1) iş ve görev bulamamak; 2) yersiz yurtsuz kalmak; 3) birkaç kişinin birlikte eriştiği bir iyilikten yararlanamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (112, 'açıktan almak', '1) den. açıktan geçmek; 2) mec. bir tehlikenin uzağından geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (113, 'açılan solar, ağlayan güler', 'hiçbir durum olduğu gibi kalmaz, gün gelir tersine döner.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (114, 'açılıp saçılmak', '1) kadın açık saçık giyinmeye başlamak; 2) kadın eskisine göre ölçüsüz davranışlarda bulunmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (115, 'açın gözü ekmek teknesinde olur', 'kişinin tek düşüncesi, yaşaması için gerekli olan şeyi elde etmektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (116, 'açın imanı olmaz', 'aç olan kimseden her türlü kötülük beklenebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (117, 'açın karnı doyar, gözü doymaz', 'tutkulu olduğu konuda insan doyumsuzdur, yetinmek bilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (118, 'açın koynunda (karnında) ekmek durmaz (eğleşmez)', 'kazancı yetmeyen kişi, eline geçeni hemen harcar, yarını için bir şey saklayamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (119, 'açın kursağına çörek dayanmaz', 'yoksulluk içinde bulunan kimsenin bir eksiği giderilse başka bir eksiği kendini gösterir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (120, 'açın uykusu gelmez', '1) aç olan kimse, kendisine ne kadar rahatlık sağlanırsa sağlansın, dinlendirilemez. 2) bir şeye ihtiyaç duyan kimse, ancak onun giderilmesiyle rahata kavuşturulabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (121, 'açlığını öldürmek', 'açlık duygusunu yatıştırmak: ''Kaldırılmış harman yerlerinden buğday toplayıp açlığımızı öldürdük.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (122, 'açlık çekmek', 'yoksulluk içinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (123, 'açlık ile tokluğun arası yarım yufka', 'yoksul olan buna üzülmemelidir, küçücük bir şey bile en büyük ihtiyacı gidermeye yeter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (124, 'açlıktan gözü (gözleri) dönmek (kararmak)', 'çok acıkmak: ''Bu akşam açlıktan gözü dönmüş bir hâlde bir evin mutfağına girmişti.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (125, 'açlıktan imanı gevremek', 'çok acıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (126, 'açlıktan nefesi kokmak', 'yoksulluk içinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (127, 'açlıktan ölmek', 'dayanılmaz derecede acıkmak, çok acıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (128, 'açlıktan ölmeyecek kadar', 'çok az: Açlıktan ölmeyecek kadar yiyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (129, 'açma sırrını dostuna, dostunun dostu vardır o da söyler dostuna', 'bir sır en yakın dosta bile söylenmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (130, 'açmaza düşmek', 'içinden çıkılması güç durumda kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (131, 'açmaza getirmek (düşürmek)', 'düzen, hile yapmak, bir kimseyi oyuna getirmek, zor duruma sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (132, 'açtı ağzını, yumdu gözünü', 'öfkelenerek veya kızarak ağır sözler söyledi.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (133, 'açtırma ağzımı', '''kötü bir söz söylememe fırsat verme'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (134, 'açtırma kutuyu, söyletme kötüyü', 'kendin hakkındaki kötü düşüncelerimi veya bildiklerimi bana söyletme.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (135, 'ad almak', '1) kendisine ad verilmek; 2) ün kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (136, 'ad çekmeye girmek', '1) kuraya tabi olmak: ''Yüksek Seçim Kurulu Başkanı ve Başkan Vekili ad çekmeye girmezler.'' -Anayasa. 2) sp. oyunun başlangıcında, alan seçimi, başlama atışı veya karşılama hakkı için öncelik sağlamak amacıyla kura çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (137, 'ad koymak', 'adlandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (138, 'ad takmak', '1) adlandırmak: ''Çadırlarının başından ayrılmayan inatçı grevcilere öteki işçiler, çadır tutan diye ad taktı.'' -L. Tekin. 2) alay etmek veya övmek amacıyla lakap takmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (139, 'ad vermek', 'adlandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (140, 'ad yapmak', 'bir alanda ün kazanmak, ün almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (141, 'ada bana, adayım sana', 'sen bir kimse için fedakârlıkta bulunursan o da senin için fedakârlıkta bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (142, 'ada gibi', 'pek büyük (gemi).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (143, 'adak adamak', 'bir dileğin gerçekleşmesi amacıyla kurban kesip yoksullara dağıtmak veya kutsal olduğuna inanılan bir güce niyette bulunmak: ''Sen bana niye söylemedin? Sadaka verirdik, adak adardık.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (144, 'adalet dağıtmak', 'kanunların saydığı hakları sahiplerine vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (145, 'adam adama (gene, her zaman) gerek olur', 'insanların birbirlerine her zaman gereksinimleri olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (146, 'adam adama yük değil, can gövdeye mülk değil', 'konuğumuzdan veya yanımıza bir iş için gelen kimseden yüksünmemeliyiz çünkü onlar yanımızda sürekli olarak kalmazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (147, 'adam adamdan korkmaz, utanır', 'insanları ahlaklı davranmaya iten korku değil, küçük görülme duygusudur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (148, 'adam adamdır olmasa da pulu, eşek eşektir atlastan olsa çulu', 'insanın değeri zengin olmakla artmaz, asıl olan insanlığıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (149, 'adam adamı bir kere aldatır', 'bir kimse başka bir kimseyi ancak bir kez aldatabilir, diğeri bir daha aldatmasına izin vermez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (150, 'adam beğenmemek', 'herkesi değersiz görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (151, 'adam değilim', '''herhangi bir durumun gerçekleşmemesi durumunda, kendisinin insan sayılamayacağı'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (152, 'adam gibi', '1) terbiyeli, akıllı uslu; 2) adamlığa, insanlığa yaraşır yolda; 3) iyice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (153, 'adam içine çıkmak', 'topluluğa karışmak, insanların bulunduğu yerlere gitmek, eşe dosta gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (154, 'adam içine karışmak', 'bir topluluğa girmek, kendisine değer verilir olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (155, 'adam kullanmak', '1) birini çalıştırmasını bilmek; 2) birini kendi çıkarına alet etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (156, 'adam olacak çocuk bokundan belli olur', 'bir kimsenin yeni başladığı işte usta olup olamayacağı ilk davranışlarından anlaşılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (157, 'adam olana bir söz yeter', 'anlayışlı olan kimse için bir şeyin bir kez söylenmesi yeterli olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (158, 'adam olana çok bile', 'layık olmadığı, hak etmediği hâlde kişinin beklentisi daha fazla olduğu durumlarda kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (159, 'adam sen de!', 'bir işin önemsenmediğini anlatmak için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (160, 'adam sırasına geçmek (girmek)', 'daha önce toplumda önemli bir yeri veya özel bir değeri yokken artık kendisine önem ve değer verilmek: ''Bize yol aç, erkân göster; yollar aç bize de, biz de adam sırasına girelim.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (161, 'adama benzemek (dönmek)', '1) giyim kuşamıyla, davranışlarıyla insana yakışır bir biçim almak: ''Bak gördün mü, isteyince adama dönüyorsun.'' -E. Işınsu. 2) beğenilir duruma gelmek: Şimdiki belediye başkanı sayesinde şehir adama benzedi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (162, 'adamak kolay, ödemek zordur', 'söz vermek kolaydır ancak o sözü yerine getirmek zordur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (163, 'adamakla mal tükenmez', 'yardım sözle değil, gerçekten fedakârlık yapılarak gerçekleştirilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (164, 'adamın adı çıkacağına canı çıksın', 'insanın haklı veya haksız yere adı bir defalık kötüye çıktı mı ondan sonra yaptıkları hep o gözle değerlendirilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (165, 'adamın iyisi işbaşında (alışverişte) belli olur', 'bir kişinin iyi ve becerikli olduğu yaptığı işlerden anlaşılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (166, 'adamın (kimsenin) adı çıkmadansa canı çıkması (yeğdir)', 'insanın haklı veya haksız yere adı bir defalık kötüye çıktı mı ondan sonra yaptıkları hep o gözle değerlendirilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (167, 'adamın kötüsü olmaz, meğer züğürt ola', 'toplum içinde herkesin bir değeri vardır ancak züğürtlere değer verilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (168, 'adamın yere bakanından, suyun yavaş akanından kork', 'duygu ve düşüncelerini açığa vurmayan sessiz insan yavaş akan derin su gibi tehlikelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (169, 'adamına göre', '1) kişiler arasında ayrıcalık gözeterek; 2) herkesin yeteneğine uygun olarak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (170, 'adamlık sende kalsın', '1) karşı taraf iyilik bilmese de sen yine iyilik et; 2) bu işi nasıl olsa sana yaptıracaklar, bari kendiliğinden yap da onurunu koru.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (171, 'aday göstermek', 'bir iş veya bir görev için birini aday olarak belirlemek, namzet göstermek: ''Siyasi parti grupları Başkanlık için aday gösteremezler.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (172, 'adaylığını koymak', 'bir iş veya göreve seçilmek için kendini ileri sürmek: ''Bankacılardan birkaçının kurgularıyla belediye başkanlığına adaylığını koymuştu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (173, 'âdembaba gibi', 'parasız pulsuz, perişan, zavallı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (174, 'âdembabaya dönmek', 'malını mülkünü kaybetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (175, 'âdet görmek', 'kadın aybaşı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (176, 'âdet olduğu üzere', 'alışıldığı gibi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (177, 'âdet yerini bulsun diye', '''gerekli görüldüğü için değil, yalnız alışılmış olduğu için'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (178, 'adı batmak', 'sevilmeyen bir şey veya kimse unutulmak, adı anılmaz olmak, artık sözü edilmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (179, 'adı bile okunmamak', 'birine veya bir şeye hiç önem verilmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (180, 'adı (bile) olmamak', 'değeri olmamak: Bir baş soğanın da adı mı olurmuş?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (181, 'adı çıkmak', '1) kötü bir ün kazanmak: Onun adı çıkmış yoksa fena adam değil. 2) hakkı olmayan bir ün kazanmak: O berberin adı çıkmış, aslında iyi tıraş edemiyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (182, 'adı çıkmış dokuza, inmez sekize', '''birinin bir kere adı çıktıktan sonra onun hakkındaki yaygın inanç artık kolay kolay düzelemez'' anlamında kullanılan bir söz: ''Artık o yana bir daha gelme, adın çıktı dokuza, inmez sekize, demedim miydi?'' -B. Günel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (183, 'adı deliye çıkmak', 'deli olmadığı hâlde deli olarak tanınmak: ''Böyle bir şey yazmaya kalkarsam adım deliye çıkacak.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (184, 'adı geçmek', 'anılmak, söz konusu olmak, ismi geçmek: ''Necip Fazıl, adı geçen iki şiirden birer bölüm okudu.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (185, 'adı gibi bilmek', 'çok iyi bilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (186, 'adı kaldırılmak', 'anılmaz olmak, silinip gitmek: ''Saatlerce adı dünya yüzünden kaldırılmaya çalışılan Türklüğün talihini düşünürdüm.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (187, 'adı kalmak', 'bir kimse veya bir şey öldükten, ortadan çekildikten sonra dillerde yalnız adı dolaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (188, 'adı kötüye çıkmak', 'ünü kötü olarak yayılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (189, 'adı olmak', 'gereksiz, yersiz ünü olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (190, 'adı sanı olmak', 'bilinmek, tanınmak, ünlü olmak: ''Osman Efendi''nin adı sanı vardı.'' -İ. H. Baltacıoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (191, 'adı var', '1) beklenilen, istenilen özelliklere sahip olmayan; 2) tercih edilecek özelliklere sahip.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (192, 'adım (adımını) atmak', '1) yürümek için ayağını öne doğru uzatıp basmak. 2) mec. bir işe ilk kez girişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (193, 'adımını attırmamak', '1) rahat davranmasını engellemek amacıyla sürekli olarak denetim altında bulundurmak; 2) bir yere girmesine engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (194, 'adımını geri atmak', 'başladığı bir işten geri dönmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (195, 'adımlarını açmak', 'yürürken hızlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (196, 'adımlarını seyrekleştirmek', 'hızlı yürürken yavaşlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (197, 'adımlarını sıklaştırmak', 'daha küçük ve çabuk adımlar atarak hızlı yürümek, ivmek, acele etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (198, 'adını ağzına almamak', 'dargınlık, kırgınlık, kızgınlık vb. sebeple bir kimseden söz etmemek: ''Seniha''nın adını asla ağzıma almıyordum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (199, 'adını bağışlamak', ' hlk. kendi adını başka bir kimseye söylemek: Adınızı bağışlar mısınız?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (200, 'adını çıkarmak', 'kişi hakkında kötü bir niyetle asılsız söylentiler yaymak: ''Kadın durmadan vır vır eder, yakınır diye adımızı çıkarmışlar.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (201, 'adını koymak', 'karşılığını veya fiyatını kararlaştırmak: Bu evi alabilmemiz için adını koyalım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (202, 'adını ...-ye çıkarmak', 'bir kişinin sahip olmadığı niteliklerle tanınmasına yol açmak: Adını deliye çıkardılar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (203, 'adres bırakmak (göstermek, vermek)', 'arandığında bulunabileceği, oturduğu yeri bildirmek: ''Kendisi, soracak olurlarsa Hayrettin Ağa''nın adresini vermesini söyledi.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (204, 'aferin almak', 'değerli görülüp beğenilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (205, 'affa uğramak', 'bağışlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (206, 'affetmişsin', '''hiç de öyle değil, yanılıyorsun'' anlamında kullanılan bir söz: ''Yakın tarihe ait tefrikaların ezelî okuyucusu Başefendi, affetmişsin sen onu, dedi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (207, 'affını dilemek (istemek)', 'bir iş veya görevi yerine getiremeyeceğini nezaketle bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (208, 'affınıza sığınarak', '''hoşgörünüze güvenerek'' anlamında kullanılan bir nezaket sözü: ''Affınıza sığınarak malumatınızı da madamdan aldım.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (209, 'afi kesmek (satmak, yapmak)', 'birine karşı gösteriş yapmak: ''Yanındaki kıza afi yapmak için onun önüne, dilenciye sadaka verir gibi bahşiş fırlatan bir züppeyi, bıraksalar öldürecekti.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (210, 'afiş yutmak', 'yalana dolana kanmak: Geç baba, geç, artık afiş yutmuyoruz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (211, 'afişte kalmak', ' tiy. oyun ilgi görerek günlerce oynanmak: Oyunun afişte kalması için başarıyla oynanması gerekir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (212, 'afiyet (afiyet şeker) olsun', '''yarasın, ağız tadıyla yensin'''' anlamında kullanılan bir iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (213, 'afiyet bulmak', 'iyileşmek, sağlığını kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (214, 'afiyet üzere olmak', 'sağlıklı, rahat yaşıyor olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (215, 'aforoz etmek', '1) kilise birliğinden çıkarmak: ''Aforoz edilmiş, kiliseden kovulmuş.'' -N. F. Kısakürek. 2) mec. darılıp biriyle konuşmamak, ilgiyi kesip kendinden uzaklaştırmak, toplum dışılamak: ''Siz kendi milletiniz için bunun yarısını söyleyin, milletin çoğunluğu sizi hemen aforoz eder.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (216, 'afura tafura gelmemek', '1) çalım satmadan hoşlanmamak; 2) böyle bir davranışa karşı tepki göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (217, 'afyon çekmek', 'keyif için afyon yutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (218, 'afyon yutmak', '1) uyuşturucu olarak afyon kullanmak; 2) mec. gerçeği göremeyecek kadar kendinde olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (219, 'afyonu başına vurmak', 'aşırı davranışlarda bulunacak kadar öfkelenmek, ne yaptığını bilememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (220, 'afyonu patlamak', 'ayılmak, kendine gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (221, 'Agop''un kazı gibi bakmak', 'aptal aptal bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (222, 'ağa borç eder, uşak harç', 'ağa para sıkıntısı içinde olup borç etse de uşak, bunu anlamaz ve bol harcamayı sürdürür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (223, 'ağaca balta vurmuşlar ''sapı bedenimden'' demiş', 'insana en yakını bile kötülük edebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (224, 'ağaca çıkan keçinin dala bakan oğlağı olur', 'çocuklar ana ve babalarından öğrendiklerini yapmaya özenirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (225, 'ağaca çıksa pabucu yerde kalmaz', 'davranışlarına engel olacak hiçbir takıntısı yok.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (226, 'ağaca dayanma kurur, adama (insana) dayanma ölür', 'insan yapacağı işte başkalarına değil, kendine güvenmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (227, 'ağacı kurt, insanı dert yer', 'kurt ağacı nasıl içten içe kemirirse dert de insanı içten içe yer bitirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (228, 'ağacın kurdu içinde olur', 'bir topluluğu çökertecek olan şey yine kendi içinden çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (229, 'ağacın meyvesi olunca, başını aşağı salar', 'yararlı eserler veren, bilgi ve erdemle donanmış kimse alçak gönüllü olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (230, 'ağaç kökünden yıkılır', 'bir düzen, ayrıntıların değişmesiyle değil temelin bozulmasıyla yıkılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (231, 'ağaç ne kadar uzasa göğe ermez', 'insan ne kadar yükselirse yükselsin bir yerde durur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (232, 'ağaç olmak', ' argo bir yerde ayakta durarak çokça beklemek: Neredesin yahu, seni bekleye bekleye ağaç olduk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (233, 'ağaç ucuna yel değer, güzel kişiye söz değer', 'güzel insanlar her yerde ilgi çekerler, her zaman onların sözü kabul edilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (234, 'ağaç yaprağıyla gürler (güzeldir)', 'insan önemli işleri akrabası, yakınları, yandaşlarından güç alarak daha kolay yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (235, 'ağaç yaş (fidan) iken eğilir', 'insanlar küçük yaşta kolay eğitilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (236, 'ağaç yeşert meyve getirsin, oğlan büyüt ekmek getirsin', 'erkek evlat meyve veren ağaç gibidir, günü gelince evin geçim yükünü hafifletir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (237, 'ağaçtan maşa, abdaldan paşa olmaz', 'yeteneksiz, beceriksiz kimse önemli işlerde kullanılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (238, 'ağaçtan maşa olmaz', 'yeteneksiz, beceriksiz kimse önemli işlerde kullanılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (239, 'ağalık (beylik) vermekle, yiğitlik vurmakla', 'sözü geçer bir adam olmak istersen herkese yardımda bulunacaksın, yiğit adam olmak için de savaşta da barışta da vurucu, kırıcı olacaksın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (240, 'ağanın alnı terlemezse ırgadın burnu kanamaz', 'işveren işçisi ile birlikte çalışmazsa işçi işe var gücüyle sarılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (241, 'ağanın eli tutulmaz', 'zengin olarak düşünülen kişiden anılmaya değer bir bağış beklenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (242, 'ağanın gözü ata tımardır', 'iş sahipleri denetimlerini sürekli yaparlarsa işler yolunda gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (243, 'ağanın gözü öküzü (ineği) semiz eder', 'ana babalar çocuklarına, mal sahipleri de mallarına iyi bakarlarsa iyi sonuçlar alınır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (244, 'ağanın gözü, yiğidin sözü', 'çalışanlarını gereği gibi yöneten ve çalıştıran kişi iyi bir yöneticidir, sözünün eri olan kimse de yiğittir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (245, 'ağanın malı çıkar, uşağın canı', 'bir afeti önlemek için işveren malını, işçi de canını feda eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (246, 'ağaran baş, ağlayan göz gizlenmez', 'belirtileri meydanda olan yaşlılık ve izleri ortada duran üzüntü ne yapılsa gizlenemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (247, 'ağası güçlü olanın kulu asi olur', 'dişli birine dayanan, güvenen kişi herkese kafa tutar, kabadayıca davranır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (248, 'ağası güçlü olanın, kulu suçlu olur', 'kuvvetli kimselerin suçları yanındakilere yüklenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (249, 'ağası yiğit olanın etbaı sarhoş gezer', 'dişli birine dayanan, güvenen kişi herkese kafa tutar, kabadayıca davranır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (250, 'ağı gibi', '1) acı veren, çok etkileyen; 2) çok sert, keskin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (251, 'ağılda oğlak doğsa ovada otu biter', 'Tanrı her yarattığının rızkını verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (252, 'ağına düşürmek', 'tuzağına düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (253, 'ağır ağır demeli, çabuk çabuk yemeli', 'yemeği çabuk yemelisin, dediğinin anlaşılabilmesi için de sözleri tane tane ve yavaş yavaş söylemelisin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (254, 'ağır basar yeğni kalkar', 'ağırbaşlı olan, herkesten saygı görür, ağırbaşlı olmayana ise kimse değer vermez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (255, 'ağır basmak', 'ağırlık olarak fazla gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (256, 'ağır çekmek', 'tartıda ağır gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (257, 'ağır durmak', 'ciddi, ağırbaşlı, oturaklı, soğukkanlı hareket etmek: ''Devlet adamlarının ileri gelenleri böyle sözlere karışmaz, ağır dururlar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (258, 'ağır gelmek', '1) gücüne gitmek, onuruna dokunmak: ''Bu vazife bana çok ağır geliyor.'' -N. F. Kısakürek. 2) yapılması güç gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (259, 'ağır git ki yol alasın', 'bir işte başarılı olmak isteyen kimse, ağır ağır ama güvenilir adımlarla yürümelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (260, 'ağır kaçmak', '1) gücendirici olmak, uygun düşmemek: Bu şaka biraz ağır kaçtı. 2) beklenenden fazla olmak: Hakem tarafından verilen kırmızı kart ağır kaçtı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (261, 'ağır kayba uğramak', 'maddi ve manevi büyük zarar görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (262, 'ağır kazan geç kaynar', '1) kalın kafalı insan bir konuyu zor anlar; 2) tembel olan işi geç yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (263, 'ağır ol!', '1) ''ciddi, ağırbaşlı, soğukkanlı, sabırlı ol!'' anlamında kullanılan bir söz; 2) ''acele etme, yavaş ol!'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (264, 'ağır ol, batman gel', 'ağırbaşlı ol ki el üstünde tutulasın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (265, 'ağır ol da molla desinler', 'ağırbaşlı davranan itibarlı olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (266, 'ağır otur ki bey (ağa, molla) desinler', 'ağırbaşlı ol ki büyüğümüz diye sana saygı göstersinler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (267, 'ağır oturmak', 'ağırbaşlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (268, 'ağır söylemek', 'acı, dokunaklı sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (269, 'ağır taş batman döver', 'ağırbaşlı insan kimsenin oyuncağı olmaz, onu yıpratmaya kimsenin gücü yetmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (270, 'ağır taş yerinden oynamaz', 'ağırbaşlı insan kimsenin oyuncağı olmaz, onu yıpratmaya kimsenin gücü yetmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (271, 'ağır yara almak', '1) kavgada veya savaşta önemli ölçüde zarar görmek; 2) bir olayda beklenmeyen sıkıntılı ve olumsuz bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (272, 'ağır yongayı yel kaldırmaz', 'ağırbaşlı kimseye ufak tefek olaylar etki edemez, zarar veremez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (273, 'ağırdan almak', '1) bir işi gereken süre içinde bitirmemek, geciktirmek: ''Görüyorsunuz ki bu soyadı konusunda benim ağırdan alışım, bir tembellik değil.'' -M. Ş. Esendal. 2) bir işi gönülsüz, isteksiz yapmak: ''Ama üstüme düşüldü mü bende bir gönül tokluğu, bir nazlanma, bir ağırdan alış.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (274, 'ağırına gitmek', 'onuruna dokunmak veya gücüne gitmek: ''Kimse, dört çocuklu bir aileye ev vermek istememiş. Bu, büsbütün ağırına gitmiş.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (275, 'ağırlığı olmak', 'etkisi büyük olmak: ''Başsavcının yargıçlar arasında belli bir ağırlığı var kuşkusuz.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (276, 'ağırlığınca altın etmek (değmek)', 'çok değerli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (277, 'ağırlığını (ortaya) koymak', 'kimliğini ve kişiliğini kabul ettirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (278, 'ağırlık basmak (çökmek)', '1) gevşeklik ve uyku gelmek; 2) ağır bir hava kaplamak; 3) sessizlik oluşmak: ''Yavaş yavaş bir ağırlık çöktü. Bir sakinlik herkesi kapladı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (279, 'ağırlık olmak', '1) sıkıntı vermek: ''Kimseye ağırlık olmaz, kimseyi sıkıştırmaz, iyilikten başka bir şey yapmaz.'' -Ö. Seyfettin. 2) birine yük olmak, kendi masrafını başkasına çektirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (280, 'ağız açtırmamak', 'çok konuşarak başkalarının söz söylemesine, konuşmasına engel olmak: Yusuf Efendi biçareye ağız açtırmıyordu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (281, 'ağız ağıza vermek (konuşmak)', 'iki kişi birbirine pek yakın durarak başkaları işitmeyecek bir biçimde konuşmak: ''Tenha köşelerde ağız ağıza konuşurken yanlarına biri gelecek olursa hemen susuyorlardı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (282, 'ağız (ağzını) açmak', '1) konuşmaya başlamak; 2) kesici aletleri keskin duruma getirmek; 3) ağır sözler söylemeye başlamak; 4) azarlamak, paylamak: ''Aman efendim, bendenize bir ağız açtılar, donakalmışım.'' -M. Ş. Esendal. 5) alık alık bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (283, 'ağız aramak (yoklamak)', 'öğrenmek istenilen şeyi söyletecek yolda dil kullanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (284, 'ağız birliği etmek', 'bir konuda anlaşarak aynı biçimde konuşmak, söz birliği etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (285, 'ağız burun birbirine karışmak', '1) dayak sonucunda yüz yara bere içinde kalmak; 2) yüzde aşırı öfke, üzüntü, yorgunluk vb. durumların izleri görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (286, 'ağız değiştirmek', 'önce söylediğini başka türlü anlatmak: ''Gelgelelim Akif, Berlin''e gidip de oradaki kahveleri gördüğü vakit ağız değiştirmek zorunda kalır.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (287, 'ağız dil vermemek', 'konuşmamak, susmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (288, 'ağız etmek', 'yaranmak için kibar konuşmaya çalışmak: ''Kolonya dökmekten, şeker tutmaktan iyi gözükeceğim diye ağız etmekten yoruldu.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (289, 'ağız kalabalığına getirmek', '1) birini gereksiz sözlerle şaşırtmak; 2) ilgisiz sözler söyleyerek asıl konudan uzaklaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (290, 'ağız kullanmak', 'duruma, ortama göre söz söylemek: Ben nasıl ağız kullanıyorsam sen de o yolda konuş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (291, 'ağız satmak', 'yüksekten atarak kendini övmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (292, 'ağız tamburası çalmak', '1) sözle avutmaya, oyalamaya çalışmak; 2) soğuktan dişleri birbirine çarpmak, çenesi titremek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (293, 'ağız yapmak', 'birini kandırmak, yanıltmak amacıyla duygularını, düşüncelerini olduğundan başka türlü gösterecek biçimde konuşmak: ''Kaçacağım, tövbeler olsun, bir fırsatını bulayım diye ağız yaptı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (294, 'ağız yaymak', 'açık ve dürüst konuşmaktan kaçınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (295, 'ağız yemese, yüz utanmaz', 'armağan alan, armağanı verenin isteğini yerine getirmemeye çekinir ve mutlaka yapmaya çalışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (296, 'ağız yer, yüz utanır', 'armağan alan, armağanı verenin isteğini yerine getirmemeye çekinir ve mutlaka yapmaya çalışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (297, 'ağızda dağılmak', 'genellikle hamur işi, iyi pişmiş ve lezzetli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (298, 'ağızda sakız gibi çiğnemek', 'bir söz veya düşünceyi sık sık tekrarlayıp durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (299, 'ağızdan ağıza dolaşmak (geçmek)', 'bir söz herkes arasında söylenilmek: ''Gazeteye yansıyan haber ağızdan ağıza geçerken açıklığını hemen hemen tamamen kaybetmiştir.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (300, 'ağızdan burun yakın, kardeşten karın yakın', 'insanın kendi yararı her şeyden önemlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (301, 'ağlama duvarına dönmek', 'herkesin şikâyetini, derdini dinler duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (302, 'ağlama ölü için, ağla deli için', 'yakınlarından biri ölenin acısı zamanla küllenir ancak bir yakını deli olanın acısı hiçbir zaman dinmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (303, 'ağlama ölü için ağla diri için', 'ölüp giden aslında dünyanın bütün dert ve sıkıntılarından kurtulmuştur onun adına üzülmek yersizdir, esas dünyada kalan ve onun sıkıntısını çekenler için üzülmek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (304, 'ağlamak para etmez', 'üzülmenin yararı olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (305, 'ağlamakla yâr ele girmez', 'kişi çok sevdiği şeye yalnızca özlemini çekmekle kavuşamaz, onu elde etmenin yollarını bulmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (306, 'ağlamayan çocuğa meme vermezler', 'hakkını aramasını bilmeyen kimsenin işi görülmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (307, 'ağlar gözden, sahte sözden kendini sakın', 'kendini acındıranlardan kork.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (308, 'ağlarsa anam ağlar, başkası (kalanı) yalan ağlar', 'insanın sıkıntısını yürekten paylaşan yalnızca annesidir, diğerlerinin üzülmesi yüzeyseldir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (309, 'ağlatan gülmez', 'başkalarına zulmeden kimsenin kötülüğü yerde kalmaz, kendisine döner, o da ağlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (310, 'ağlayanın malı gülene hayretmez', 'birinden haksız olarak alınan mal, alana yarar sağlamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (311, 'ağlayıp da gözden mi olayım?', 'meseleyi büyütüp sıkıntıya girmek gereksiz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (312, 'ağrılardan göz ağrısı, her kişinin öz ağrısı', 'herkesi en çok ilgilendiren kendi derdidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (313, 'ağrısız baş mezarda gerek', 'herkesin bir sıkıntısı vardır, bu sıkıntılar ancak ölümle biter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (314, 'ağrısız başına kaşbastı bağlamak', '''kendine gereksiz yere iş çıkarmak'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (315, 'ağustosta beyni kaynayanın zemheride kazanı kaynar', 'yazın çalışan kışın rahat eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (316, 'ağustosta gölge kovan zemheride karnın ovar', 'elinde fırsat varken geleceğini sağlamaya gayret göstermeyip eğlenceye, keyfe dalan kimse sonunda aç kalır ve perişan olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (317, 'ağustosta yatanı, zemheride büvelek tutar', 'elinde fırsat varken geleceğini sağlamaya gayret göstermeyip eğlenceye, keyfe dalan kimse sonunda aç kalır ve perişan olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (318, 'ağustosun yarısı yaz yarısı kıştır', 'ağustos ayının ortalarında yaz sıcakları azalır, serinlik başlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (319, 'ağza (ağızlara) düşmek', 'dedikodu konusu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (320, 'ağza alınmaz (alınmayacak)', 'söylenmesi ayıp, çirkin (söz, küfür): ''Bu ağza alınmaz söz üzerine karşıdakiler birden alevlendiler.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (321, 'ağza almamak', 'anmamak, sözünü etmemek: ''Tövbekâr olduktan sonra eskiden işlediğimiz günahlar ağza alınmaz.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (322, 'ağza tat, boğaza feryat', '''miktarı çok az olan yiyecek'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (323, 'ağzı açık ayran delisi (budalası)', '1) yeni gördüğü her şeye şaşkınlıkla bakan; 2) saf, bön.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (324, 'ağzı açık (bir karış açık) kalmak', 'çok şaşırmak, şaşakalmak: ''Başımı kaldırıp yukarı bakınca şaşkınlıktan ağzım açık kalıyor.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (325, 'ağzı açık kalmak', 'şaşırmak: ''Dillere destan İstanbul nezaketini o evde gördüm, ağzım açık kaldı.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (326, 'ağzı burnu yerinde', 'oldukça güzel, yakışıklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (327, 'ağzı çiriş çanağına dönmek', 'ağzı kuruyup acılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (328, 'ağzı dili (ağzı) kurumak', '1) susuz kalmak; 2) konuşamaz duruma gelmek: ''Ağzım dilim kurudu, kız yalvara yalvara'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (329, 'ağzı dili bağlanmak', 'herhangi bir sebeple konuşamaz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (330, 'ağzı dili tutulmak', '1) konuşamamak; 2) beklenmedik bir durum karşısında heyecanlanmak, hayranlık duymak: ''Kızları gördün, ağzın dilin tutuldu gayri.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (331, 'ağzı dolu dolu konuşmak', 'heyecanlı söz söylemek: ''Birkaç kişiyle, garip bir lisanla ağzı dolu dolu konuşmaya başladı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (332, 'ağzı eğri, gözü şaşı ensesinden belli olur', 'bir kişinin tutum ve davranışları, o kişide birtakım eksiklikler bulunduğunu gösterir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (333, 'ağzı kilitlenmek', 'konuşamaz duruma gelmek: ''Fakat yalnız kaldıkları vakit ağzı kilitlendi ve tek gözü de Gülizar''ı görmez oldu.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (334, 'ağzı köpürmek', 'çok öfkelenmek: ''Âdeta saldırdı üstüme ağzı köpürmüş, çirkin bayan.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (335, 'ağzı kulaklarına varmak', 'çok sevinmek: ''Çocuklarıma beni misal gösterdiğini, ağzım kulaklarıma vararak öteden beriden işitiyordum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (336, 'ağzı laf (lakırtı) yapmak', '1) kolay konuşma yeteneği olmak; 2) inandırıcı söz söyleme yeteneği olmak: ''Çok şükür, ağzı laf yapandan çok, eli işe yatkın aydınlara muhtaç olduğumuzu, anlar gibiyiz.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (337, 'ağzı olan konuşuyor', '''konuyla ilgisi olmayan, bilir bilmez herkesin söyleyecek sözü var'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (338, 'ağzı oynamak', '1) bir şeyler yemek; 2) konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (339, 'ağzı sulanmak', '1) imrenmek; 2) yeme, içme isteği artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (340, 'ağzı süt kokmak', 'çok genç ve toy olmak: ''Yazmaya başladığım günden bu yana ağzı süt kokan bir yazar olmaktan korkmuşumdur.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (341, 'ağzı teneke kaplı', ' şaka çok sıcak veya çok acı şeyleri kolaylıkla içebilen, yiyebilen (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (342, 'ağzı var dili yok', '1) ''pek sessiz, kendi hâlinde'' anlamında kullanılan bir söz: ''Benim gibi ağzı var dili yok bir kadınla ne zevkleniyorsunuz?'' -B. Felek. 2) ''konuşamayan, derdini anlatamayan'' anlamında kullanılan bir söz: ''Hey zavallı balık, diyor, ağzın var dilin yok.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (343, 'ağzı varmamak', 'söylemeye, açıklamaya gönlü elvermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (344, 'ağzına almak', '1) yemek, içmek; 2) söylemek: ''Bir daha millet kelimesini ağzına alırsan dilini koparırım, anladın mı?'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (345, 'ağzına bir kemik atmak', 'birini küçük bir çıkarla susturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (346, 'ağzına bir zeytin verir, altına (ardına) tulum tutar', 'yaptığı küçük iyiliklere karşılık büyük çıkar bekler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (347, 'ağzına burnuna bulaştırmak', 'bir işi beceremeyip berbat etmek, bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (348, 'ağzına etmek', ' argo haddini bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (349, 'ağzına geldiği gibi', 'önünü sonunu düşünmeden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (350, 'ağzına geleni söylemek', '1) nezaket dışına çıkarak ağır ve kırıcı sözler söylemek; 2) gelişigüzel, saçma sapan konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (351, 'ağzına gem vurmak', 'susturmak, söyletmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (352, 'ağzına kadar', 'boş yeri kalmayacak bir biçimde: ''Bir bardağı bu yeşil şerbetle ağzına kadar doldurdu.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (353, 'ağzına kilit takmak (vurmak)', '1) susmak; 2) susturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (354, 'ağzına kira istemek', 'söylemesi beklenen şeyi söylemekte nazlı davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (355, 'ağzına sağlık', '1) bir sözü yerinde söyleyen kişilere söylenen bir beğenme sözü; 2) yapılan konuşmanın beğenildiğini belirtmek için söylenen söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (356, 'ağzına sıçmak', '1) birini çok kötü duruma sokmak; 2) bir şeyi, bir işi işe yaramaz duruma getirmek, bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (357, 'ağzına takılmak', 'bir sözü konuşması sırasında bilinçsiz bir biçimde sürekli söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (358, 'ağzına taş almak', 'söze karışmayıp susmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (359, 'ağzına tıkmak', 'susturmak, konuşmasına engel olmak: ''Aleyhinde kim ne söylerse hemen ağızlarına tıkarlardı.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (360, 'ağzına verilmesini beklemek (istemek)', 'çalışmayıp işlerinin başkaları tarafından yapılmasını beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (361, 'ağzına vur, lokmasını al', 'yumuşak huylu kimseye her istenilen kolaylıkla yaptırılabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (362, 'ağzına yakışmamak', 'söylemesi ayıp kaçmak, uygun düşmemek, yakışık almamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (363, 'ağzına yüzüne bulaştırmak', 'bir işi kötü yapmak, becerememek: ''Yapılacak şey ehemmiyetsizce bir pansuman ama ağızlarına yüzlerine bulaştırmalarından korkuyorum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (364, 'ağzında bakla ıslanmamak', 'sır saklamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (365, 'ağzında yaş kalmamak', 'bir düşüncesini bir kimseye birçok kez söylemiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (366, 'ağzından baklayı çıkarmak', 'baklayı ağzından çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (367, 'ağzından bal damlamak (akmak)', 'çok tatlı konuşmak: ''Öyle zekiler vardır, konuştular mı ağızlarından bal akıyor sanırsın.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (368, 'ağzından burnundan getirmek', '1) huzurunu bozmak, sıkıntıya sokmak: ''Siz buraya bizi eğlendirmeye mi geldiniz yoksa ağzımızdan burnumuzdan getirmeye mi?'' -O. C. Kaygılı. 2) pişman etmek için uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (369, 'ağzından çıkanı (çıkan sözü) kulağı duymamak (işitmemek)', 'sözlerini tartmadan söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (370, 'ağzından çıkmak', 'bir sözü istemeden, farkına varmadan söylemek, söylemiş bulunmak: Bir kez ağzımdan çıktı, o fiyata vereceğim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (371, 'ağzından çıt çıkmamak', 'hiçbir şey söylememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (372, 'ağzından dökülmek', 'açıkça söylemekten çekindiği şey, konuşmasından belli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (373, 'ağzından düşmemek (düşürmemek)', 'her zaman sözünü etmek, söylemek: ''Bu ne cehennemdir lafı ağzından düşmüyordu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (374, 'ağzından girip burnundan çıkmak', '1) türlü yollara başvurarak birini bir şeye razı etmek, kandırmak: ''O, köylülerin ağzından girip burnundan çıkmayı mükemmel becerir.'' -S. Ertem. 2) iyice dövmek: ''Ulan, ağzını topla! Şimdi ağzından girer, burnundan çıkarım!'' -M. Rona.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (375, 'ağzından hayır çıkmazsa bari şer söyleme', 'lehte konuşmuyorsun, hiç olmazsa aleyhte de konuşma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (376, 'ağzından inci saçmak', 'birbirinden güzel sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (377, 'ağzından kaçırmak', 'istemediği hâlde boş bulunup söyleyivermek: ''Sen onun için en fena tabirleri kullanıyorsun, asabisin, ağzından çirkin şeyler kaçırıyorsun.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (378, 'ağzından lakırtı (laf) almak (çekmek)', 'karşısındakini konuşturarak birtakım şeyleri öğrenmek: ''Ağzımdan lakırtı almak istiyorsun ama demeyeceğim.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (379, 'ağzından söz (laf, lakırtı) eksik etmemek', 'o sözü sürekli söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (380, 'ağzından (söz, lakırtı) dirhemle çıkmak', 'çok az veya zorla konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (381, 'ağzını açacağına gözünü aç', 'dikkatli ol, uyanık ol.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (382, 'ağzını açıp gözünü yummak', 'öfke ile, sonunu düşünmeden ağzına gelen bütün ağır sözleri söylemek: ''Fakat bu inat, Emine''nin çenesini açmış; kızın ne kadar kusuru varsa babasından geldiğini söylerken, Tevfik''e ağzını açmış, gözünü yummuştu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (383, 'ağzını aramak (yoklamak)', 'konuşturarak düşüncesini öğrenmeye çalışmak: ''Ağzımı aradı, rahat mıydım, burada okuyacağımı aklım kesmiş miydi?'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (384, 'ağzını bırakıp kıçıyla (bir tarafıyla) gülmek', 'alay ederek karşısındakine gülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (385, 'ağzını bozmak', 'kaba sözler söylemek, küfretmek: ''Bütün yapma inceliğine karşın kabaydı karısına karşı. Dövdüğü de oluyordu, ağzını bozduğu da.'' -O. Rifat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (386, 'ağzını burnunu çarşamba pazarına (çanağına) çevirmek', 'aşırı bir biçimde döverek perişan duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (387, 'ağzını burnunu dağıtmak (kırmak, parçalamak)', 'aşırı bir biçimde döverek perişan duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (388, 'ağzını havaya (poyraza) açmak', ' alay umduğunu elde edememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (389, 'ağzını hayra aç!', 'kötü ihtimaller söz konusu edildiğinde ''Tanrı korusun'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (390, 'ağzını kapamak (kilitlemek)', 'susmak, bir şey söylemek istememek: ''Kendini tutamıyorsun, bari ağzını kapa, sus, küçülme.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (391, 'ağzını kiraya vermek', 'kendini de ilgilendiren bir konuda düşüncesini söylememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (392, 'ağzını koklamak', 'niyetini ve durumunu öğrenmek istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (393, 'ağzını mühürlemek', 'konuşmamak, susmak: ''Yine o değişmeyen ızdırap ile ağzını mühürler.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (394, 'ağzını sıkı (pek) tutmak', 'sır vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (395, 'ağzını toplamak', 'söylemekte olduğu kötü söz veya küfürleri kesmek: ''Evvela ağzını topla! Ağzını bozarsan ben de senden aşağı kalmam.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (396, 'ağzını tutmak', '1) boşboğazlık etmemek; 2) kötü söz söylememek; 3) bir konuda arzu edilmeyen düşüncelerin açığa çıkmasını susarak önlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (397, 'ağzının içi yangın yerine dönmek', 'ağzının tadı bozulmak, tat alma duyusunu yitirmek: ''Ağzımın içi yangın yerine dönüp yine de ağrılar kesilmeyince çok sıkıntılı bir vaziyete düştüm.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (398, 'ağzının içine baktırmak', 'sözlerini seve seve ve dikkatle dinletmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (399, 'ağzının kâhyası olmak', 'birinin alışkanlıklarına, davranışlarına, düzenine karışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (400, 'ağzının mührü ile', 'oruçlu olarak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (401, 'ağzının payını (ölçüsünü) almak', 'verilen karşılıkla bir kimseye söylediğine veya yaptığına pişman olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (402, 'ağzının perhizi yok', '''ağzına geleni söyler'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (403, 'ağzının suyu akmak', 'çok beğenip istemek, imrenmek: ''Bu ziyafete elimiz erişmiyor, uzaktan ağzımın suyu akıyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (404, 'ağzının tadı bozulmak (kaçmak)', 'bir kimsenin kurulu düzeni, dirliği bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (405, 'ağzının tadını bilmek', '1) güzel yemeklerden anlamak; 2) her şeyin güzelini, iyisini bilmek, anlamak: ''Demek sen artık ağzının tadını bilmiyorsun! Demek senin hiçbir zevkin kalmamış!'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (406, 'ağzının tadını kaçırmak', '1) neşesini, keyfini bozmak: ''Ben o kadınlardan değilim ki, evin büyüğü ben olacağım diye tutturup akılsızlıklarla ağzımın tadını kaçırayım.'' -M. Ş. Esendal. 2) bir kimsenin kurulu düzenini bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (407, 'ağzıyla içmesini bilmek', 'sözünü, sohbetini karşıdaki kişiyi incitmeyecek bir biçimde ayarlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (408, 'ağzıyla kuş tutsa...', '''ne yapsa, ne kadar çaba ve ustalık gösterse'' anlamında kullanılan bir söz: ''Aktör, o her günkü pırtısını giyip de sahneye çıkarsa, ağzıyla kuş tutsa seyirciye Demirhane Müdürü olduğunu yutturamaz.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (409, 'ah alan onmaz', 'kötülük ettiği için beddua alan iflah olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (410, 'ah almak', 'birinin ilenmesini üstüne çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (411, 'ah çekmek', 'derin bir keder veya özlemle içten gelerek ah demek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (412, 'ah etmek', '1) acı ile içini çekmek; 2) mec. ilenmek: ''Vakit vakit gözlerini kapayarak o herkesin / Ah ettiği sevda adlı günahkârı düşündün mü?'' -E. B. Koryürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (413, 'ah vah etmek (demek)', 'pişman olmak: ''Yaptığım deliliğe ne zaman ah vah diyeceğimi bir kestirebilsem.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (414, 'ah yerde kalmaz', 'kötülük cezasız kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (415, 'ahbap çıkmak', 'önceden tanışmış olmak: ''Gümrükten itibaren her rast geldiği adamla ahbap çıktı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (416, 'ahbap kusuruna bakan ahbapsız kalır', 'dostların ufak tefek kusurlarına bakmamak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (417, 'ahenk almak', 'uyumlu duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (418, 'ahenk vermek', 'düzeni, uyumu sağlamak: ''Türk diline en asil ahengini veren sanatkârı düşüneceğiz.'' -O. S. Orhon.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (419, 'Ahfeş''in keçisi gibi başını sallamak', 'söylenen sözü anlama dan kafa sallayarak onaylamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (420, 'ahı çıkmak', 'yaptığı ilenme, etkisini göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (421, 'ahı gitmek vahı kalmak', 'iyice zayıflamak, iş göremez duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (422, 'ahını yerde koymamak', 'öcünü almak: ''Sen öz babanın öcünü alamadın diye o da dedesinin ahını yerde mi koyacaktı?'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (423, 'ahireti boylamak', ' tkz. ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (424, 'ahiretini yapmak (zenginleştirmek)', 'hayır işleri yaparak sevap kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (425, 'ahirette on parmağı yakasında olmak', 'kendisine karşı sorumlu olan kimseden ahirette hesap sormak: ''Artık sana dünyada rastlayamazsam yarın ahirette on parmağım yakanda olsun!'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (426, 'ahkâm çıkarmak', 'kendi düşüncelerine dayanarak birtakım yargılara varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (427, 'ahkâm kesmek', '1) çekinmeden kesin yargılarda bulunmak: ''İşin içinde olmanın verdiği rahatlıkla bol keseden ahkâm kesen akıl hocalarının eleştirilerine hedef olmayı önleyemezler.'' -T. Halman. 2) bilir bilmez konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (428, 'ahkâm yürütmek', 'bir sözden kendi anlayışına göre sonuçlar çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (429, 'ahlatın iyisini ayılar yer', 'kendilerine yakışmayan güzel bir şeyi eline geçirenler için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (430, 'ahlatın iyisini dağda ayılar yer', 'kendilerine yakışmayan güzel bir şeyi eline geçirenler için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (431, 'ahmağa yüz, abdala söz vermeye gelmez', 'ahmağa gereğinden çok ilgi gösterir, abdala gereğinden çok söz hakkı verirseniz sizi çok uğraştırır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (432, 'ahmak gelin yengeyi halayığı sanır', 'ahmak kimse kendisini koruyup gözeten kişiye hizmetine verilmiş biri gözüyle bakar ve saygısız davranışlarıyla onun gönlünü kırarak hizmetinden yoksun kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (433, 'ahmak misafir ev sahibini ağırlar', 'başkalarının görev ve yetkilerine karışmak ahmaklıktır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (434, 'ahtapot gibi', '1) sırnaşık, yapışkan (kimse); 2) sömürmek amacıyla birçok işe, konuya el atan (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (435, 'ahu gibi', 'çok güzel, çekici.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (436, 'ak akçe kara gün içindir', 'çalışarak kazandığımız para, dar zamanımızda bizi sıkıntıdan kurtarır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (437, 'ak göt (don, bacak) kara göt (don, bacak) kara geçit başında (hamamda) belli olur', 'bir iddiadaki doğruluk ancak deney veya sınav sonucunda belli olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (438, 'ak gün ağartır, kara gün karartır', 'mutlu bir yaşayış kişiyi dinç kılar, mutsuz bir yaşam ise yıpratır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (439, 'ak koyunu gören içi dolu yağ sanır', 'bir şeyin dış görünüşüne bakarak içinin de öyle olduğunu sananlar yanılırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (440, 'ak koyunun kara kuzusu da olur', 'iyi bir ailenin çocuğu kötü de olabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (441, 'ak köpeğin (itin) pamuk pazarına (pamuğa, pamukçuya) zararı vardır', 'kötü şey, görünüşte iyi şeye benziyorsa iyi şeyin değeri azalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (442, 'ak sakaldan yok sakala gelmek', 'çok yaşlanıp iyice kuvvetten düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (443, 'akacak kan damarda durmaz', 'kişi, alın yazısında olanla kesinlikle karşılaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (444, 'akamete uğramak', 'başarısız olmak, sonuçsuz kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (445, 'akan su yosun tutmaz', 'tembel tembel oturan kimse hantallaşır, iş yapma yeteneğini yitirir, çalışan kimse gittikçe açılır, daha yararlı işler yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (446, 'akan sular durmak', 'itiraz edememek, söyleyecek sözü kalmamak: ''Böyle duru bir mantık karşısında akan sular duruyordu.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (447, 'akara kokara bakma çuvala girene bak', 'iyi, kötü deme; mal ve para biriktir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (448, 'akarı kokarı olmamak', 'bilinen herhangi bir eksiği, kusuru bulunmamak: ''Ev bize dar geliyor, çürük çarık, akarı kokarı eksik değil.'' -N. Kurşunlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (449, 'akarına bırakmak', 'işin sonucunu sabırla beklemek, doğal gelişmeyi beklemek: ''İçeriğin nasıl aktarılacağına dikkat etmiş, dilin olanaklarını akarına bırakmıştır.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (450, 'akarsu çukurunu kendi kazır', 'bir şeyi yapma isteği ve gücü bulunan kimse, uygun bir çalışma yönü ve alanı bulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (451, 'akarsu gibi', 'aralıksız, kesintisiz: ''Cevapları pek açık ve akarsu gibi idi.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (452, 'akarsu pislik tutmaz', 'bir insan ne kadar çok çalışırsa o kadar kötü düşünceden ve kötülük yapmaktan uzak olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (453, 'akarsuya inanma, eloğluna dayanma', 'akışı ne kadar yavaş olursa olsun akarsuya girmek tehlikelidir, eloğluna güvenmek de doğru değildir, insanı zarara sokabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (454, 'akçe akıl öğretir, don yürüyüş', 'imkânların fazlalığı insanların iyi işler yapmasını kolaylaştırır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (455, 'akı ak karası kara', 'beyaz tenli, kara gözlü, kara saçlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (456, 'akı karası geçitte belli olur', 'bir iddiadaki doğruluk ancak deney veya sınav sonucunda belli olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (457, 'akıl adama sermayedir', 'bir kimsenin giriştiği işlerde en büyük yardımcısı aklıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (458, 'akıl akıl, gel çengele takıl', 'bir sorunun nasıl çözümleneceğini düşünememe durumunda söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (459, 'akıl akıldan üstündür', 'bir kimsenin aklına gelmeyen bir çare, başka birinin aklına gelebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (460, 'akıl almak', 'danışmak, görüş almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (461, 'akıl almamak', 'inanılacak gibi olmamak, akla uygun gelmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (462, 'akıl bırakmamak', 'kafa karıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (463, 'akıl danışmak', 'bir konuda birinin görüşünü sormak: ''O cinayeti işlemeden evvel gelip bize akıl mı danıştın?'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (464, 'akıl durdurmak', 'bir şey çok şaşırtıcı olmak, insanı şaşırtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (465, 'akıl erdirememek (ermemek)', '1) ne olduğunu anlayamamak, sırrını çözememek: ''Çalıştıkça da borcumuz azalacağına artıyor, işte buna bir türlü akıl erdiremiyorum.'' -Halikarnas Balıkçısı. 2) kabul edememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (466, 'akıl erdirmek', 'ne olduğunu anlamak, sırrını çözmek: ''Yaşadığımız müddetçe bu muammaya akıl erdirmek bizim için pek kabil değildi.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (467, 'akıl ermek', 'anlamak, çözmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (468, 'akıl etmek', 'herhangi bir önlem veya çareyi zamanında düşünmek: ''Duvar saatine bakmayı akıl ettiğinde ise zihni adamakıllı bulandı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (469, 'akıl (göz) var, izan (mantık, yakın) var', '1) herhangi bir şey bilgiye ve mantığa dayalı olarak yapılmalıdır; 2) her şey ortadadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (470, 'akıl havsala almamak', 'akla mantığa sığmamak: ''Artık bu kadarını akıl havsala alamaz.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (471, 'akıl hocalığı taslamak', 'bir işte doğruyu, iyi olanı gösterdiğini sanmak: ''Burada akıl hocalığı taslıyorum ama ben böyle akılsızlıkları çok yapıp birkaç kere sorunla karşılaştım.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (472, 'akıl için yol (tarik) birdir', 'doğruyu bulmak için aklın izleyeceği bir tek yol vardır, bu yoldan gidenlerin hepsi aynı sonuca varır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (473, 'akıl işi değil', '''akla uygun değil, doğru değil'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (474, 'akıl kişiye (adama) sermayedir', 'bir kimsenin giriştiği işlerde en büyük yardımcısı aklıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (475, 'akıl olmayınca ne yapsın sakal?', 'kişi yaşlandığında olgunlaşmayıp akılsız kalmışsa çocukça işler yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (476, 'akıl öğretmek', 'birine nasıl davranacağını göstermek, yol göstermek, akıl vermek: ''Sana ne oluyor? Akıl öğretecek sen mi kaldın?'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (477, 'akıl para ile satılmaz', 'delice iş yapan zenginler bulunduğu gibi akıllıca iş yapan fakirler de vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (478, 'akıl vermek', 'akıl öğretmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (479, 'akıl yaşta değil, baştadır', 'akıllı olmanın yaşla ilgisi yoktur, bazı küçükler büyüklerden daha akıllı olabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (480, 'akıl yürütmek', '1) herhangi bir konuda fikir vermek; 2) tahminde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (481, 'akılda tutmak', 'unutmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (482, 'akıldan çıkarmak', '1) düşünmemek; 2) unutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (483, 'akıllar gelin olmuş, herkes kendininkini beğenmiş', 'insan kendi aklını, düşüncesini başkasınınkinden üstün görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (484, 'akılları pazara çıkarmışlar, herkes yine kendi aklını almış', 'insan kendi aklını, düşüncesini başkasınınkinden üstün görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (485, 'akıllı düşman akılsız dosttan hayırlıdır', 'akılsız kimse iyi niyetli olsa dahi yaptığı işin ne gibi kötü sonuçlar doğuracağını hesap edemediğinden dostuna bilmeyerek fenalık edebilir, akıllı düşmanın yapacağı kötülükse akıl yoluyla sezilir ve gereken tedbir alınabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (486, 'akıllı düşününceye kadar deli çocuğunu (oğlunu) everir', 'kendilerini akıllı sananlar çok kez akılsız diye tanınanlardan daha az başarı gösterir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (487, 'akıllı geçinmek', 'kendini çok akıllı sanmak: ''Akıllı geçinen kadınlardan beklenebilecek tepkileri vermedi hiç.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (488, 'akıllı köprü arayıncaya dek deli suyu geçer', 'atak kişi tehlikeyi göze alarak işe girişir ve çabuk sonuç alır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (489, 'akıllı oğlan neyler ata malını, akılsız oğlan neyler ata malını', 'çocuk akıllı ise babasından mal kalsın diye beklemez, malı kendisi kazanır; akılsızsa babası ne kadar çok mal bırakırsa bıraksın, değerini bilmez ve onu kısa zamanda bitirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (490, 'akıllı olmak', 'gerçeklere uygun davranmak: ''Mesut olmak için akıllı olmak kifayet eder, baht, talih bunlar boş şeydir!'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (491, 'akıllı, sözünü akılsıza söyletir', 'başkası adına konuşmak, insanın başını derde sokar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (492, 'akıllılık etmek', '1) yerinde ve uygun davranmak; 2) uyanık davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (493, 'akılsız başın cezasını (zahmetini) ayak çeker', 'bir işte düşüncesizce davranan kişi her türlü olumsuz sonuca katlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (494, 'akılsız köpeği (ahmak iti) yol kocatır', 'iyice düşünülmeden, tasarlanmadan yapılmaya çalışılan iş sırasında birçok sorun ortaya çıkar ve kolay bir iş bile zorlaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (495, 'akılsızlık- akılsızlık etmek', 'düşüncesiz ve yersiz davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (496, 'akım derken bokum demek', ' kaba sözünü yerli yerince söyleyememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (497, 'akın (beyazın) adı (var), karanın (esmerin) tadı (var)', 'beyaz tenli olanlar güzel sayılsa da gerçek güzellik ve şirinlik esmerlerdedir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (498, 'akıntıya (akıntıya karşı) kürek çekmek', 'olmayacak bir iş uğrunda boşuna çabalamak: ''Böyle akıntıya kürek çektiğine çok acıdım doğrusu.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (499, 'akıntıya kapılmak', '1) bir akıntının etki alanına girmek, akıntı ile birlikte sürüklenmek: ''Aralarından biri akıntıya kapıldığı zaman ötekiler var kuvvetleriyle dayanarak onu geri çekiyorlardı.'' -R. N. Güntekin. 2) mec. etki altında kalarak bir topluluğun davranışına katılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (500, 'akıp gitmek', 'çabuk geçmek: ''Ebediyete akıp giden her on senede, bu büyük millet bayramını daha büyük şereflerle, saadetlerle huzur ve refah içinde kutlamanı gönülden dilerim.'' -Atatürk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (501, 'akideyi bozmak (akidesi bozulmak)', 'doğru bilinen bir inanış veya gidişten ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (502, 'akil isen açma sırrın dostuna, çünkü dostun dostu vardır, o da söyler dostuna', 'bir sır en yakın dosta bile söylenmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (503, 'akim kalmak', 'sonuca ulaşamamak, başarı sağlayamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (504, 'akis uyandırmak', 'bir konu üzerinde düşünülmesine, tartışılmasına yol açmak, ilgi veya tepki yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (505, 'akla (akıllara) durgunluk vermek', 'hayranlık uyandırmak: ''Fatih Sultan Mehmet Han hazretlerinin akıllara durgunluk veren bir fetihle Osmanlı mülkü hâline getirdiği İstanbul dünyanın en güzel, en harikulade şehridir.'' -B. Akyavaş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (506, 'akla fenalık vermek', 'çok şaşırtmak, çıldırtmak, zıvanadan çıkarmak: ''Aman ya Rabbi, akla fenalık verecek hadiseler bundan sonra başladı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (507, 'akla gelmek', 'hatırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (508, 'akla gelmemek', '1) hatırlanamamak; 2) olabileceğini düşünmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (509, 'akla gelmeyen başa gelir', 'insan ummadığı, düşünmediği şeylerle karşılaşabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (510, 'akla hayale gelmemek', 'inanılmamak: ''En akla hayale gelmeyen şeylere dikkat eder, bunları derler toplar ve umumi büyük neticeler çıkarır.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (511, 'akla karayı seçmek', 'bir işi başarıncaya değin çok sıkıntı çekmek, güçlüklerle karşılaşmak: ''Ben kendi hesabıma bir parça Fransızca öğrenebilmek için akla karayı seçtim.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (512, 'akla sığar gibi', 'aklın kabul edebileceği bir biçimde, makul: Söyledikleriniz akla sığar gibi değil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (513, 'akla sığmamak', 'inanılacak gibi olmamak, akla uygun gelmemek: ''Ismarlama bir hükümdar soyu bulmak ve yaratmak pek akla sığacak bir yol görünmüyordu.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (514, 'aklı almamak', '1) biri bir şeyi anlayamamak, kavrayamamak; 2) bir şeyin olabileceğine inanmamak; 3) uygun bulmamak: Çocuğun bu geç saatte evden izinsiz çıkıp gitmesini aklım almıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (515, 'aklı başa yaş getirir', 'deneyim, yıllar içerisinde elde edilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (516, 'aklı başına gelmek', '1) davranışlarının yanlışlığını sezerek doğru yolu bulmak: ''O zaman her şey düzelir, erkeğin de aklı başına gelir.'' -P. Safa. 2) ayılmak, kendine gelmek: ''Bir hastalık hâli olduğu anlaşılan bu ilk sersemlikten sonra yavaş yavaş aklı başına gelmektedir.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (517, 'aklı başında olmamak', 'iyi düşünebilir durumda olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (518, 'aklı başından gitmek', 'çok sevinçten veya çok korkudan ne yapacağını şaşırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (519, 'aklı başka yerde olmak', 'başka şeyler düşünmek: ''Affet Kâmuran, aklım başka yerdeydi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (520, 'aklı bir (beş) karış yukarıda (havada) olmak', 'değişik sebeplerden dolayı dengeli düşünemez durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (521, 'aklı (bir şeye) takılmak', 'zihni bir şeyle sürekli olarak uğraşmak: ''Aklı bir şeye takılmış gibiydi komiserin, konuşuyor boyuna.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (522, 'aklı bir yerde olmak', 'bir iş yaparken başka bir şey düşünmek: ''Aklı hep evde, Gülsüm''deydi.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (523, 'aklı bokuna karışmak', ' kaba korkudan şaşırıp ne yapacağını bilememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (524, 'aklı çıkmak', 'sonucun kötü olacağını düşünerek korkuya kapılmak: Para harcayacak diye aklı çıkıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (525, 'aklı dağılmak', 'düşünceyi belli bir konu, sorun üzerinde toplayamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (526, 'aklı durmak', 'düşünemez bir duruma gelmek, şaşırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (527, 'aklı ermek', '1) anlayabilmek: ''Bir sihirbaz inceliği ile başlayan iş, bir hamal kabalığı ile bitirilmeli ki neticeye aklı ersin.'' -N. F. Kısakürek. 2) akılca olgunlaşmak: ''Aklı her şeye eriyor, eli her işe yatıyor.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (528, 'aklı fikri bir şeyde olmak', 'düşüncesini bir konuda yoğunlaştırmak: ''Aklı fikri bostanda olduğu için bunlardan nasıl ayrılacağını tekrarlıyordu.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (529, 'aklı gitmek', '1) şaşırmak, korkmak; 2) çok beğenmek, bayılmak: ''Leman''ın aklı gitti bu anda sinemaya.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (530, 'aklı kalmak', 'beğendiği bir şeyi düşünmekten kendini alamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (531, 'aklı karışmak', 'ne yapacağını bilememek, şaşırmak, bocalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (532, 'aklı kesmek', '1) anlamak, idrak etmek; 2) bir şeyin olabileceğine inanmak: ''Ağzımı aradı, rahat mıydım, burada okuyacağımı aklım kesmiş miydi?'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (533, 'aklı kesmemek', '1) anlayamamak, idrak edememek; 2) sonucu tahmin edememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (534, 'aklı sonradan gelmek', '1) verdiği kararın yanlış olduğunu anlayıp vazgeçmek; 2) bir şeyi sonradan hatırlayarak yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (535, 'aklı yatmak', 'anlamaya başlamak, olacağına inanmak: ''Söylediklerimin doğru olabileceğine aklı yatmaya başladı.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (536, 'aklı zıvanadan çıkmak', 'delirmek, aklını oynatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (537, 'aklıma gelen başıma geldi', '''olmasından korktuğum şey oldu'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (538, 'aklın süzgecinden geçirmek', 'etraflıca düşünmek, çok iyi muhakeme etmek: ''Aklın olmadıktan sonra istediğin denli deneylerden geç. O deneyleri aklın süzgecinden geçirmedikten sonra.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (539, 'aklın yolu birdir', 'iyi düşünüldüğünde ayrı ayrı kimselerce varılacak sonuç hep aynıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (540, 'aklına bir şey gelmek', 'hatırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (541, 'aklına düşmek', '1) hatırlamak: ''Kırmızı gül goncasına kavuştu / Sılada sevdiğim aklıma düştü'' -Halk türküsü. 2) kafasında bir düşünce doğmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (542, 'aklına esmek', 'daha önce düşünmemiş olduğu şeyi birden yapmaya karar vermek: ''O da, aklına ne eserse işleyen biri.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (543, 'aklına geleni işleme, her ağacı taşlama', 'sonunu düşünmeksizin aklına eseni yapan, herkese sataşan kimse bu davranışının büyük zararlarını görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (544, 'aklına gelmek', '1) hatırlamak, anımsamak: ''Kız kaybolduktan sonra aklına geldi babası olduğu.'' -A. Ümit. 2) bir şeyi yapmayı düşünmek, tasarlamak: ''Bu düşünce aklına gelince delikanlı hemen söze başladı.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (545, 'aklına getirmek', '1) hatırlatmak; 2) olabileceğini düşünmek: ''Ve birdenbire o kendini, şimdiye kadar gelmediği, böyle olacağını aklına bile getirmediği bir yerde buldu.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (546, 'aklına sığdırmak', 'bir şeyin olabileceğine inanmak, aklı almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (547, 'aklına sığmamak', '1) anlayamamak, kavrayamamak; 2) olabileceğine inanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (548, 'aklına takmak', 'sürekli olarak bir şeyi düşünmek, bir düşünceye saplanıp kalmak: ''Tartışma, grevin nereden çıktığını aklına takanlar yüzünden büyüyüp genişledi.'' -N. Uygur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (549, 'aklına turp sıkayım', ' tkz. aklına şaşayım: ''Bu soğukta vapurun burasında oturmayı akıl edenin aklına turp sıkayım.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (550, 'aklına uymak', 'başka birinin düşüncesine göre iş yapmak, davranmak: ''Zaten bizim Hacer''in aklına uydum da geldim.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (551, 'aklına yatmak', 'doğru olduğunu kabul etmek: ''Söyledikleri aklıma yattı, eli ayağı düzgün, iyi bir Türk kızı bulup evlenebilir, geç de olsa çoluk çocuğa karışabilirdim.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (552, 'aklına yelken etmek', 'düşüncesizce davranmak veya aklına geleni hemen yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (553, 'aklında kalmak', '1) hatırlamak: ''Aklımda kaldığına göre, Raşit çocukla aramızda ancak iki üç aylık bir fark var.'' -R. N. Güntekin. 2) unutamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (554, 'aklından çıkarmamak', 'sürekli hatırlamak, unutmamak: ''Ben senin yengenim, amcanın karısıyım, bunu sakın aklından çıkarma!'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (555, 'aklından çıkmak', 'unutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (556, 'aklından geçirmek', 'bir şeyi yapmayı düşünmek, tasarlamak: ''Aklından geçirdiği gerçekmiş gibi telaşlanmıştı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (557, 'aklından geçmek', 'düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (558, 'aklından zoru olmak', 'akla sığmayacak işler yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (559, 'aklını başına almak (toplamak, devşirmek)', 'akılsızca davranışlarda bulunmaktan kendini kurtarmak: ''Burası Ankara değil, aklını başına al, uslu otur.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (560, 'aklını başka yere vermek', 'konuşulan konudan başka bir şey düşünür olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (561, 'aklını (bir şeyle) bozmak', 'bir şey üzerine çok düşerek hep onunla uğraşıp durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (562, 'aklını devşirmek', 'aklı başına gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (563, 'aklını kaçırmak', '1) delirmek: ''Cesareti de adamakıllı kırılmış, aklını kaçıran babasının hâli onu perişan etmişti.'' -İ. O. Anar. 2) gereksiz, yersiz iş yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (564, 'aklını kullanmak', 'iyice düşünüp taşınarak hareket etmek: ''Hayatta güçlü olacaksın, parasız kalmayacaksın, aklını kullanacaksın.'' -Ü. Dökmen.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (565, 'aklını oynatmak', 'çıldırmak: ''Allah Allah, bu adam gittikçe aklını oynatıyor.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (566, 'aklını peynir ekmekle yemek', ' alay akılsızca ve düşüncesizce davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (567, 'aklını şaşırmak', 'yerinde olmayan bir iş yapmak, yersiz düşünmek: ''Bu kadar genç bir kızla evlenmek için Şakir amca aklını şaşırdı herhâlde.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (568, 'aklını takmak', 'aklına takmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (569, 'aklını yormak', 'hatırlamaya çalışmak, zihnini zorlamak: ''Aklını yorma, bulamazsın.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (570, 'aklının ayarını bozmak', 'doğru düşünemez, davranamaz duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (571, 'aklının bir köşesine yazmak', 'ileride hatırlamak üzere belleğine almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (572, 'aklının köşesinden geçmemek', 'hiçbir zaman düşünmemek: ''Rahmetliyi suçlamak aklımın köşesinden geçmez.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (573, 'aklının terazisi bozulmak', 'akıllıca olmayan davranışlarda bulunacak bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (574, 'aklının ucundan bile geçirmemek', 'hiçbir biçimde düşünmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (575, 'aklınla bin yaşa', 'herhangi bir sorun karşısında hemen çözüm üreten kişiye bu özelliğinin beğenildiğini belirtmek için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (576, 'aklınla rezil olursun, aklınla vezir olursun', 'aklını iyi kullanan saygı görür, kullanmayan kendini küçük düşürür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (577, 'akmasa da damlar', 'çok değilse bile az çok bir gelir veya kazanç sağlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (578, 'akraba çıkmak', 'konuştuktan sonra akraba olduklarını anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (579, 'akraba ile ye, iç alışveriş etme', 'alışverişte iki taraf da kendi çıkarını düşündüğünden iki dost arasındaki alışveriş dostluğu bozabilir, bu nedenle de dostluklarını sürdürmek isteyenler birbirleriyle alışverişte bulunmamalıdırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (580, 'akrep gibi', 'her fırsatta sözleriyle başkalarını inciten veya onlara kötülük eden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (581, 'aksak eşekle yüksek dağa çıkılmaz', 'eksik aletle sağlıklı iş yapılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (582, 'aksi gibi', 'istenmediği hâlde, aksilik olarak: ''Oysa ki askerdeyken aksi gibi, bir kere bile hastalanmamıştı.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (583, 'aksi şeytan', 'işler yolunda gitmediği zaman ''ne kadar ilgisiz, münasebetsiz'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (584, 'aksiliği tutmak', 'güçlük çıkarmak, inadında direnmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (585, 'aksiliği üstünde (olmak)', 'olumsuz davranış içerisinde (olmak): ''Hacı Ömer''in bütün aksiliği üstündeydi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (586, 'akşam ahıra sabah çayıra', 'hayatta yiyip içip yatmaktan başka kaygısı olmayanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (587, 'akşam ise yat, sabah ise git', 'geceler uyku, gündüzler iş zamanıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (588, 'akşam oldu kon, sabah oldu göç', 'geceler uyku, gündüzler iş zamanıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (589, 'akşama kalmak', 'iş gecikmek, bitmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (590, 'akşama karşı gitme, tana karşı yatma', 'yolculuğa gece değil sabah erken çıkılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (591, 'akşamdan kalmış (kalma)', 'geceki sarhoşluğun mahmurluğunu taşıyan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (592, 'akşamdan kavur, sabaha savur', '1) kazandığını günü gününe harcayan tutumsuz kimselerin durumunu anlatmak için kullanılan bir söz; 2) söylediği sözü tutmayan kişiler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (593, 'akşamdan sonra merhaba', 'iş işten geçtikten, olan olduktan sonra gösterilen ilginin yararlı olmayacağını belirten bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (594, 'akşamı akşam etmek', 'akşamın olmasını sabırsızlıkla beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (595, 'akşamı bulmak (etmek)', 'akşamlamak, günü bitirmek: ''Halk baharları ve yazları, dolmalarla, helvalarla gidip akşamı eder, şen şatır dönerlermiş.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (596, 'akşamı zor etmek', 'bir türlü akşam olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (597, 'akşamın hayrından sabahın şerri iyidir (yeğdir)', 'işinizi akşamüzeri veya gece yapmayın, sabaha bırakın çünkü gece iş yapmanın kötü yönleri daha çoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (598, 'akşamın işini sabaha (yarına) bırakma', 'bugün yapılması gereken bir işi ertesi güne bırakma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (599, 'aküsü bitmek', '1) taşıtlarda depolanan enerji bitmek; 2) mec. bitkin düşmek, çok yorulmak: ''Ok gibi taa Viyanalara kadar gelmişsiniz ama şimdi akünüz bitmiş, yürümeye korkuyorsunuz.'' -F. Baykurt. 3) mec. iyice yaşlanmak, elden ayaktan düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (600, 'al (alın) ...', 'işte: ''Uykuysa, uyumak bir marifetse al uykuyu diyerek akşama kadar uyudum.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (601, 'al aslan tutar, güç sıçan tutmaz', 'bir kimse zekâsını kullanarak kendisinden güçlü olan yaratığı yenebilir ancak gücünü kullanarak kendisinden daha güçsüz ama zeki olan bir yaratığın üstesinden gelemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (602, 'al aşağı vur yukarı', 'çekişe çekişe pazarlık yapılırken söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (603, 'al benden de o kadar', ' tkz. ''ben de aynı düşüncedeyim, aynı durumdayım'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (604, 'al birini, vur ötekine (birine)', 'hiçbiri işe yaramaz, hepsi bir ayarda.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (605, 'al elmaya taş atan çok olur', 'değerli kimselere sataşan çok olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (606, 'al giyen alınır', '1) göz alıcı giysi giyen güzele hemen istekli çıkar; 2) bir işin yapılışıyla uzaktan bile olsa ilgisi bulunan kimse, o iş üzerindeki eleştirileri üzerine alır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (607, 'al giymedim ki alınayım', '''bu işle hiçbir ilgim olmadığı için söylenen sözleri kendi üzerime almadım'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (608, 'al gömlek gizlenmez', 'bazı kötü şeylerin gizlenmesi mümkün değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (609, 'al gülüm ver gülüm', '1) çıkar ilişkilerinde bazı sıkıntıları karşılıklı olarak görmezden gelme; 2) bir kimseye yapılan hizmetin hemen karşılığını bekleme durumu: ''Yooo, dedi, al gülüm ver gülüm. On para için ben senin canını alırım, on para için sen benim canımı al.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (610, 'al ile aslan tutulur, güç ile sıçan (gücüğen) tutulmaz', 'bir kimse zekâsını kullanarak kendisinden güçlü olan yaratığı yenebilir ancak gücünü kullanarak kendisinden daha güçsüz ama zeki olan bir yaratığın üstesinden gelemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (611, 'al kanlara boyanmak', '1) yaralanmak; 2) vurularak ölmek; 3) şehit olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (612, 'al kaşağıyı gir ahıra, yarası (yağırı) olan gocunur (gocunsun)', 'bir yolsuzluğun suçluları aranırken o işte kusuru olan kişi telaşlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (613, 'al kiraz üstüne kar yağmış', 'düşünülmeyen, beklenilmeyen şeylerin de olabileceğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (614, 'al malın iyisini, çekme kaygısını', 'malın iyisini alan, onu tasasız kullanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (615, 'al sana bir ... daha', 'yeni bir aksilik olduğunda bezginlik bildirmek için ''işte'' anlamında kullanılan bir söz: ''Al sana bir hayal kırıklığı daha!'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (616, 'al takke ver külah', '1) uzun bir çekişmeden sonra, çekişe çekişe: ''Al takke ver külah, kırsal kesimi çocuğunu okutmanın yararına inandırdık.'' -A. İlhan. 2) aralarındaki senli benli ilişkiyi sürdürerek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (617, 'ala keçi her vakit püsküllü oğlak doğurmaz', 'değerli bir şeyden her zaman istenilen verim alınmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (618, 'ala keçiyi gören içi dolu yağ sanır', 'bir şeyin dış görünüşüne bakarak içinin de öyle olduğunu sananlar yanılırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (619, 'alabanda vermek', ' argo azarlamak, paylamak, haşlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (620, 'alabandayı yemek', ' argo adamakıllı azarlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (621, 'alabora olmak', '1) tekne, sandal vb. deniz araçları devrilip ters dönmek: ''Kaptan, gemiyi ağzına kadar doldurmuş, gemi yan yatmış, bir deniz çalkantısıyla alabora olmuş.'' -N. Hikmet. 2) mec. işler altüst olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (622, 'alaca düşmek', 'meyve olgunlaşmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (623, 'alacağım olsun da alakargada olsun', 'borçlu olmaktansa alacaklı olmak iyi bir şeydir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (624, 'alacağına şahin, vereceğine karga (kuzgun)', 'alacağını isterken ısrar eden, borcunu öderken de güçlük çıkaran kimse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (625, 'alacakla verecek (borç) ödenmez', 'bir yerden alacağınız parayla başka bir yere olan borcunuzu kapatamazsınız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (626, 'alacaklı çıkmak', 'alacağı vereceğinden çok olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (627, 'alaka (alakasını) çekmek (toplamak, uyandırmak)', 'ilgi çekmek: ''Bu sahneyi mangalın başında Havva Hanım bize kaç defa tekrar etti, hatırlayamam. Ama her defasında bizde büyük bir alaka uyandırıyordu.'' -H. E. Adıvar. ''Yahu, biz bu hanımın sadece elini öpebilmek için ne yapacağımızı bilemez ve alakasını çekemezdik.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (628, 'alaka duymak', 'ilgi duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (629, 'alakargada alacağım olsun, alamazsam gözümü oysun', 'borçlu olmaktansa alacaklı olmak iyi bir şeydir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (630, 'alakayı (alakasını) kesmek', 'ilgisi kalmamak, ayrılmak: Fabrikayla alakamı kestim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (631, 'alarma geçmek', 'beliren tehlikeye karşı direnebilecek, dayanabilecek duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (632, 'alaşağı etmek', '1) yetkilerini elinden alıp birini yerinden uzaklaştırmak, atmak, kovmak; 2) kapıp yere vurmak; 3) mec. kötülemek, değersiz göstermek: ''Her on yılda bir, geçmişten bu yana süregelen edebiyatı alaşağı ediyoruz.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (633, 'alay geçmek', ' argo alay etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (634, 'alaya almak', 'alay etmek, eğlenmek: ''Büyük İskender''den de bir şey yaşadığını seziyor ve kendi kendisini alaya alıyor.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (635, 'alaya bozmak', 'alay niteliği vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (636, 'alaya vurmak', 'ciddiyken sonradan alay ediyormuş gibi bir havaya girmek: ''Hep şakaya almış, alaya vurmuştu ablasıyla eski kocasının savaşını.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (637, 'albeni vermek', 'çekiciliğini artırmak, ilgi toplamak, hoş ve güzel göstermek: ''Son yirmi yılın matematikçileri bilimlerine albeni verebilmek için yeni bir matematik buldular.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (638, 'alçacık dağları ben yarattım demek', 'çok kurumlu olmak, kendini çok beğenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (639, 'alçacık eşeğe herkes biner', 'güçsüz ve koruyucusuz bir kimseyi buyruk altına almak ve ezmek kolaydır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (640, 'alçak eşek binmeye kolay, öksüz çocuk dövmeye kolay', 'nasıl ki boyu kısa olan eşeğe binmek kolaysa öksüz çocuğa kötü davranmak da onu koruyan kimse olmadığı için kolay olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (641, 'alçak uçan yüce konar, yüce uçan (konan) alçak konar (uçar)', 'alçak gönüllü olan toplum içinde saygı görür ve yücelir, kendisini herkesten üstün gören sevilmez ve toplum içinde iyi bir yer edinemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (642, 'alçak yer yiğidi hor gösterir', 'basit bir çevrede yaşayan, önemsiz bir görevde çalışan her yönden değerli olan kişi önemsiz bir görevde çalışıyorsa yeteneklerini tam olarak gösteremez, bundan dolayı değeri anlaşılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (643, 'alçak yerde tepecik kendisini dağ sanır', 'bilgili kimselerin bulunmadığı yerde cahil kişi bilgiçlik taslar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (644, 'alçak yerde yatma sel alır, yüksek yerde yatma yel alır', 'insan kendi durumuna göre bir yaşam tarzı benimsemeli, arkadaşlarını da ona göre seçmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (645, 'alçaktan uçmak', ' argo atılan palavra düzeyi az olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (646, 'aldı', ' hlk. ''söylemeye başladı'' anlamında kullanılan bir söz: Aldı Kerem. Aldı Köroğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (647, 'aldı sazı eline', 'hiç kimseyi konuşturmadan konuşan kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (648, 'aldığı abdest ürküttüğü kurbağaya değmemek', 'sağladığı yarar, verdiği zararı karşılamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (649, 'aldırış etmemek', '1) ilgi göstermemek, ilgilenmemek, ilgisiz kalmak; 2) önem vermemek, aldırmamak, umursamamak: ''Kendi alanına dokunmayan bir şeye aldırış etmez.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (650, 'âleme verir talkını (telkini), kendi yutar salkımı', 'kendisinin inanmadığı ve tutmadığı öğütleri başkalarına kolayca verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (651, 'âlemin ağzı torba değil ki büzesin', 'başkalarının söyleyeceklerine engel olamazsınız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (652, 'alet işler, el övünür', 'bir kimse ne kadar usta olursa olsun gerekli araçları olmadan kusursuz iş yapamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (653, 'alet olmak', 'bilerek veya bilmeyerek kötü bir işe aracılık etmek, vasıta olmak: ''Mustafa Kemal''de tek olmayan şey, ''alet olmak'' zaafı idi.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (654, 'alev almak', '1) tutuşmak, yanmaya başlamak: ''Sobada çıralar hemen alev almış, odunları da tutuşturmuştu.'' -T. Buğra. 2) mec. coşmak, heyecanlanmak, heyecana gelmek; 3) mec. öfkelenmek, kızmak; 4) mec. telaşlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (655, 'alev bacayı (saçağı) sarmak', 'ateş bacayı sarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (656, 'alev gibi parlamak', 'canlı, ışıl ışıl olmak: ''Gözleri siyah bir alev gibi parlıyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (657, 'aleyhe dönmek', 'karşı durum almak, karşı duruma geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (658, 'aleyhinde (aleyhine) söylemek (bulunmak)', 'çekiştirmek, yermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (659, 'alı al, moru mor', 'telaş veya yorgunluktan yüzü kıpkırmızı kesilmiş (olarak): ''Çıplak, kuvvetli topuklarının altında şıpıdıklarının ökçelerini ezerek alı al, moru mor bir telaşla geliyordu.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (660, 'alı alına, moru moruna', 'sağlıklı, kanlı canlı: ''Şahsına bakarsan iri yarı, alı alına, moru moruna, dinç, ablak bir insan...'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (661, 'alıcı çıkmak', '1) müşteri olmak; 2) istemek, talip olmak: ''İzmir''den gelmiş birtakım hanımlar onu kız sanıp alıcı çıktılar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (662, 'alıcı gözüyle bakmak', 'inceden inceye gözden geçirmek: ''Şimdiye kadar pek alıcı gözüyle bakmamıştı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (663, 'alıcı kuşun ömrü az olur', 'başkalarına saldırmayı alışkanlık edinen kimsenin düşmanı çok olur, bu düşmanlar onun canına kıyarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (664, 'alın damarı çatlamış', 'ar damarı çatlamış.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (665, 'alın teri dökmek', 'çok emek vermek, zahmetli bir iş görmek: ''Doğrusu çok alın teri döktük amma değerdi / Neşe veren kasvetimiz yorgunluğu giderdi'' -E. B. Koryürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (666, 'alıp başını gitmek', 'başını alıp gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (667, 'alıp satmaz görünmek', 'ilgisiz görünmek veya davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (668, 'alıp sattığı olmamak', 'hiç ilgisi bulunmamak: ''Lisan kursunu filan alıp sattığı yokmuş.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (669, 'alıp vereceği olmamak', 'bir kimseyle hiçbir ilgisi olmamak: Onun benimle ne alıp vereceği olabilir?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (670, 'alıp vermek', '1) kalp çarpıntısı geçirmek; 2) herhangi bir konu üzerinde yoğun olarak düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (671, 'alıp yürümek', 'az zamanda çok ilerlemek, yayılmak, çoğalmak, artmak: ''Bu kasıt tertibi, aramızı bozabilecek bir cinayet davasının alıp yürümesine, dallanıp budaklanmasına yol açtı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (672, 'alışkanlıktan (alışkanlığından) kopamamak', 'belli bir huydan vazgeçememek, alışıklığı bırakamamak: ''Bir zorunluluk olmadan alışkanlıklarımızdan kolay kolay kopamıyoruz.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (673, 'alışmadık götte don durmaz', 'bir kimse alışmadığı, sıkıcı bir duruma kendini kolay kolay uyduramaz, ondan bir an önce kurtulmaya çalışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (674, 'alışmış kudurmuştan beterdir', 'alışılan bir şeyden kolayca vazgeçilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (675, 'alışmış kursak bulamacını ister', 'kişi, yararlanmaya alıştığı şeyden yoksun kalmak istemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (676, 'alışverişe çıkmak', 'alım satım işi için çarşıya gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (677, 'alışverişi kesmek', 'biriyle ilgisi kalmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (678, 'Ali kıran baş kesen', 'zorba.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (679, 'Ali''nin külahını Veli''ye, Veli''nin külahını Ali''ye giydirmek', 'birinden aldığını öbürüne, bir başkasından aldığını da ona vererek işini yürütmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (680, 'âlim unutmuş, kalem unutmamış', 'insan ne kadar bilgili olursa olsun her şeyi aklında tutamayacağı için unutulmaması istenilen şey mutlaka yazılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (681, 'âlimden zalim doğar', 'topluma yaptıklarıyla daima yararlı olmuş bir bilginin çocuğu da öyle olacak diye bir kural yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (682, 'alkış almak', 'çok beğenilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (683, 'alkış kopmak', 'birdenbire güçlü bir biçimde el çırpılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (684, 'alkış toplamak', 'çok alkışlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (685, 'alkış tufanı kopmak', 'sürekli ve coşkun alkış başlamak: ''Daha ilk nağmelerde meyhaneyi sarsan bir alkış tufanı koptu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (686, 'alkış tutmak', '1) topluca el çırparak yüksek sesle ''yaşa, var ol'' vb. sözler söyleyerek birini alkışlamak; 2) taraftar olmak, belli bir görüşten yana olmak: ''Batıla alkış tutanların karşısına geçip hata eylediğimi yeni yeni öğrenmiş bulunuyorum.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (687, 'alkol duvarını aşmak', 'çok sarhoş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (688, 'Allah Allah!', '1) şaşma veya can sıkıntısı anlatan bir ünlem: ''Allah Allah! Köşedeki cılız erik ağacı böyle nasıl çiçeklenivermiş.'' -A. İlhan. 2) Türk askerinin hücum narası: ''Misilsiz Mehmetçiğin süngüsü ucunda Allah Allah diye bir ses ihtizaz eder.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (689, 'Allah (Allah''ı) var', '''doğrusunu söylemek gerekirse'' anlamında kullanılan bir söz: Allah var, böyle bir işi o yapmaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (690, 'Allah bal mumu yakana bal mumu, yağ mumu yakana yağ mumu verir', 'Tanrı bol harcayana bol, az harcayana az verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (691, 'Allah bana, ben de sana', 'şimdi sana borcumu ödeyecek param yok, kazanırsam öderim.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (692, 'Allah bana, ben de sana', '''şimdi sana borcumu ödeyecek param yok, kazanırsam öderim'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (693, 'Allah bilir', '1) ''belli değil'' anlamında kullanılan bir söz: Yağmur yağar mı dersin? -Allah bilir! 2) ''bana öyle geliyor ki'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (694, 'Allah bilir ama kul da sezer', 'bir işin nasıl bir sonuç vereceğini ancak Tanrı bilir ama insan da kafasını kullanarak aşağı yukarı bir tahminde bulunabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (695, 'Allah bir dediğinden başka sözüne inanılmaz', 'birinin çok yalancı olduğunu anlatmak için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (696, 'Allah çam isteyene çam, mum isteyene mum verir', 'Tanrı bol harcayana bol, az harcayana az verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (697, 'Allah dağına göre kar verir', 'Tanrı herkese dayanabileceği ölçüde sıkıntı verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (698, 'Allah derim', 'yapılan bir iş için sorulan ''ne dersin?'' sorusuna karşı ''söyleyecek başka söz bulamıyorum'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (699, 'Allah dokuzda verdiğini sekizde almaz', 'alın yazısı ne ise o olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (700, 'Allah gümüş kapıyı kaparsa altın kapıyı açar', 'işi bozulan kişi umutsuzluğa düşmemeli, Tanrı''nın onu daha iyi bir işe kavuşturacağına inanmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (701, 'Allah için', 'gerçekten, doğrusu: ''Allah için kız buna layık görünüyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (702, 'Allah kardeşi kardeş yaratmış, kesesini ayrı yaratmış', 'geçim konusunda kimse kimseye yük olmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (703, 'Allah kulundan geçmez', 'Tanrı dar zamanlarında kulunun imdadına yetişir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (704, 'Allah kulunu kısmeti ile yaratır', 'bu dünyada herkesin dar veya geniş, bir geçim yolu vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (705, 'Allah manda şifalığı versin', ' tkz. çok veya ağır yemek yiyenler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (706, 'Allah ne verdiyse', '''yiyecek olarak evde ne varsa'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (707, 'Allah övmüş de yaratmış', 'çok güzel olanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (708, 'Allah sabırlı kulunu sever', 'Tanrı sabırlı kulunu sevdiği için sabırlı olmaya daha çok dikkat etmeliyiz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (709, 'Allah sağ gözü (eli) sol göze (ele) muhtaç etmesin', 'Tanrı kimseyi kimseye, en yakınlarına bile muhtaç etmesin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (710, 'Allah sevdiğine dert verir', 'Tanrı, derdin kendisinden geldiğine inanarak yakınmayanları ödüllendireceği için sevdiğine dert verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (711, 'Allah uçamayan kuşa alçacık dal verir', 'Allah, yetenekleri kısıtlı olanlara durumlarına uygun bir yaşama düzeni verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (712, 'Allah vere de', '''inşallah, dileriz ki'' anlamında kullanılan iyi dilek sözü: Allah vere de yağmur yağmasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (713, 'Allah verince kimin oğlu, kimin kızı demez', 'Tanrı dilerse hiç tanınmayan, yoksul bir aile çocuğunu da üne, zenginliğe kavuşturur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (714, 'Allah verirse el getirir, sel getirir, yel getirir', 'Tanrı bir kimseyi zengin etmek isterse ona hiç umulmadık yerden mal ve para gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (715, 'Allah yarattı dememek', 'kıyasıya dövmek, çok hırpalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (716, 'Allah yürü ya kulum demiş', 'az zamanda çok para kazananlar veya işinde çok ilerleyenler için söylenen bir söz: ''Son yıllarda Allah yürü ya kulum demiş ve birdenbire ünlü bir iş adamı oluvermiştir.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (717, 'Allah''a bir can borcu var', 'Allaha vereceği canından başka hiç kimseye bir borcu yok.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (718, 'Allah''a emanet', '1) ''Tanrı esirgesin'' anlamında birini överken söylenen bir söz: Allah''a emanet, iyi çocuktur. 2) tutar yanı olmayan kimse veya nesne için şaka yollu söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (719, 'Allah''a ısmarladık', 'ayrılanın kalan veya kalanlara söylediği bir iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (720, 'Allah''ın adamı', 'garip, saf, zavallı (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (721, 'Allah''ın belası', 'sıkıntı veren, kötü olan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (722, 'Allah''ın bildiği kuldan saklanmaz', 'kişi işlediği suçtan dolayı önce Tanrı''ya karşı sorumludur ve bu suçu da Tanrı bilir, bu nedenle onu kuldan saklamak gerekmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (723, 'Allah''ın binasını yıkmak', 'kendini veya başkasını öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (724, 'Allah''ın cezası', 'pek yaramaz, şirret.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (725, 'Allah''ın emri', 'kader.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (726, 'Allah''ın evi', '1) cami, mescit; 2) Kâbe; 3) mec. insan gönlü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (727, 'Allah''ın gazabı', 'çok sıkıntı veren şey.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (728, 'Allah''ın günü', 'hemen hemen her gün.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (729, 'Allah''ın hikmeti', 'beklenmeyen, sebebi anlaşılmayan veya şaşılan şeyler için kullanılan bir söz: Allahın hikmeti, kayanın içinde kocaman bir ağaç bitmiş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (730, 'Allah''ın işine bak', 'bir işin, bir olayın beklenmedik, şaşılacak bir durum alması karşısında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (731, 'Allah''ın kulu', 'insan, kimse, kişi: Burada yol gösterecek bir Allah''ın kulu yok mu?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (732, 'Allah''ın ondurmadığını peygamber sopa ile kovar', 'Allah bir kişiyi mutsuz yaratmışsa hiç kimse onun kaderini değiştiremez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (733, 'Allah''ından bulsun', '''ben kendisine bir şey yapmayacağım, yaptığı kötülüğün cezasını Tanrı versin'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (734, 'Allah''ını seversen', 'olması ve gerçekleşmesi çok istenilen bir durum karşısında kullanılan yalvarma sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (735, 'Allah''tan', '1) iyi ki: ''Allah''tan sessizdi sarhoşluğu.'' -C. Uçuk. 2) yaradılıştan: Gözleri Allah''tan sürmeli.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (736, 'Allah''tan korkmaz', 'can yakıcı, insafsız, acımasız.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (737, 'allahı çok, insanı az bir yer', 'pek ıssız ve kuytu bir yer.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (738, 'allahlık Ali Bey', 'hiçbir şeyle ilgilenmeyen, olaylardan habersiz olan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (739, 'allame kesilmek', 'allamelik taslamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (740, 'allamelik taslamak', 'bilgisiz olduğu hâlde her şeyi bilir görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (741, 'alma alı, sat yağızı, bin doruya, besle kırı', 'biniciler doru ve kır donlu atları, sarı, al ve yağız renkli olanlardan daha üstün sayarlar, o nedenle at alırken seçimi ona göre yapmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (742, 'alma mazlumun ahını çıkar, aheste aheste', 'kimseye eziyet edip ahını alma, sonra yaptığın kötülüklerin cezasını ömür boyu çekersin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (743, 'almadan vermek Allah''a mahsus', 'insan yaptığı herhangi bir şey için mutlaka karşılık bekler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (744, 'almadığın hayvanın kuyruğunu tutma', 'almayacağın bir şeye alacakmışsın gibi yakın ilgi gösterme, işinde çalıştırmayacağın kimseye çalıştıracakmışsın gibi umut verme.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (745, 'alna yazılan başa gelir', 'kişi, kaderi ne ise onu görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (746, 'alnı açık yüzü ak', 'çekinecek hiçbir durumu veya ayıbı olmayan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (747, 'alnına kara sürmek', 'bir kimsenin haksız yere kötü tanınmasına yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (748, 'alnında yazılmış olmak', 'bir olayın, kişinin başına gelmesini Allah yazmış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (749, 'alnından öpmek', 'beğenmek, takdir etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (750, 'alnının akıyla', 'ayıplanacak bir duruma düşmeden, şerefiyle başarı göstermiş olarak: ''Bütün savaşlardan alnının akıyla çıkmış bir denizci. Hiç yenik düşmemiş.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (751, 'alnının kara yazısı', 'kötü kaderi, kötü talihi: ''Alnımın ne kara yazısı varmış.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (752, 'alt alta üst üste', 'birbirleriyle itişir kakışır durumda: ''Alt alta üst üste boğuşmaya başladık.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (753, 'alt çenesi oynamak', 'rüşvet alıp yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (754, 'alt değirmen güçlü akar', 'kaynakları eski ve bol olan kuruluşlar sağlam ve verimli olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (755, 'alt etmek', 'üstünlük sağlamak, yenmek: ''Sanatı kendi şartları, kendi ölçüleri içinde alt etmeye yanaşmadıkça gerçek sanatkâr olmaya imkân yok.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (756, 'alt olmak', 'yenilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (757, 'alt perdeden konuşmak', 'hafif sesle yavaş konuşmak: ''Gözlerini süze süze alt perdeden, tane tane konuşur.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (758, 'alt yanı çıkmaz sokak', 'sonu gelmeyen, sonuç alınamayan işler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (759, 'altı alay üstü kalay', 'içi, dışı gibi özenilmiş olmayan şeyler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (760, 'altı aylık seyislikle kırk yıllık fışkı karıştırılmaz', 'bir işi tam öğrenmeden, inceliklerini kavramadan, ustalaşmadan o işte bilgiçlik taslanmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (761, 'altı karış beberuhi', ' alay kısa boylu kimse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (762, 'altı kaval, üstü şişhane (şeşhane)', 'giysilerini birbirine uygun düşüremeyen, yakıştıramayanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (763, 'altı olur, yedi olur, hep Allah''ın dediği olur', 'önceden ne kadar hesap yapılırsa yapılsın, sonunda Tanrı''nın dilediği olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (764, 'altıdan yemek', 'hastanelerde perhizi olmayan hastalara verilen tam yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (765, 'altın adı pul oldu, kız adı dul oldu', 'uygunsuz davranışları yüzünden temiz tanınan kişiliği lekelendi.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (766, 'altın adını bakır etmek', 'kötü işler yaparak temiz ve parlak ününü karartmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (767, 'altın anahtar her kapıyı açar', 'para olduğunda her güçlük yenilebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (768, 'altın ateşte, insan mihnette belli olur', 'altına benzeyen maddenin altın olup olmadığı ateşe dayanıklılık derecesi ile anlaşıldığı gibi bir kişinin değeri de sıkıntılara katlanma, zorlukları yenme ve benliğini koruma gücü ile ölçülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (769, 'altın çağını yaşamak', 'en başarılı, en verimli döneminde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (770, 'altın eli bıçak kesmez', '1) varlıklı veya değerli kişilerin elini kimse bükemez; 2) herhangi bir işte usta olan her zorluğun üstesinden gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (771, 'altın eşik gümüş eşiğe muhtaç olur', 'hiç kimse zenginliğine güvenmemelidir, gün gelir yoksullaşır ve fakir kimseye muhtaç olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (772, 'altın gibi', '1) altına benzeyen; 2) mec. değerli, kıymetli; 3) mec. saf.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (773, 'altın kesmek', 'çok para kazanır olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (774, 'altın leğene kan kusmak', 'varlık içinde hastalık veya sıkıntı çekerek yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (775, 'altın pas tutmaz', 'şerefli, temiz insana hiç kimse leke süremez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (776, 'altın top gibi', 'güzel ve tombul (çocuk).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (777, 'altın tutsa toprak olur (altına yapışsa elinde bakır kesilir)', 'giriştiği işlerde büyük talihsizliklere uğrayan kimsenin durumunu anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (778, 'altın yerde paslanmaz, taş yağmurdan ıslanmaz', 'değerli kişi veya nesneler, ne türlü uygunsuz koşullar içinde bulunurlarsa bulunsunlar değerlerini ve niteliklerini yitirmezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (779, 'altın yere düşmekle pul olmaz', 'üstün nitelikli kişinin değeri, bulunduğu yerden uzaklaştırılmasıyla azalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (780, 'altın yumurtlayan tavuk', '1) mesleği, sanatı, parası olan, gelirli kimse; 2) turist.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (781, 'altın yürekli olmak', 'çok iyi niyetli, merhametli olmak: ''O kadar fazla altın yürekli olacağına bir parça daha zarif ve cazibeli bir adam olsaydı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (782, 'altına etmek (kaçırmak)', '1) yatağına veya donuna işemek; 2) mec. çok korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (783, 'altına imza koymak', 'konuyu veya anlaşmayı kabul ettiğini belirtmek: ''İlgili sözleşmelerin altına imzamızı koyarken bu imzaya sadık kalma konusunda ne ölçüde niyetliydik?'' -A. Cemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (784, 'altında kalmamak', 'karşılığını vermek, gördüğü iyilik veya kötülüğü karşılıksız bırakmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (785, 'altından çapanoğlu çıkmak', 'girişilen işte başa dert olacak bir durumla karşılaşmak: ''Kısa kesmekten yanaydı ama paraları uzatsa altından bir çapanoğlu çıkar mıydı?'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (786, 'altından girip üstünden çıkmak', '1) malı, parayı düşüncesizce harcayıp tüketmek: ''Babasından kalan servetin altından girip üstünden çıkmıştı.'' -R. N. Güntekin. 2) ne yapıp edip istediğini yaptırmak; 3) halletmek; 4) karıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (787, 'altından kalkamamak', '1) bir işi başaramamak, becerememek, üstesinden gelememek: Bu işin altından kolay kolay kalkamaz. 2) mec. kendini savunamamak: Altından kalkamayacağı suçlamalar ileri sürdüler.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (788, 'altını çizmek', 'bir sözün önemini belirtmek, üzerine dikkati çekmek, vurgulamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (789, 'altını saklamak değil, kuruşu saklamak hünerdir', 'tutumluluk değersiz görünen şeyleri değerlendirmekle olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (790, 'altını üstüne getirmek', '1) bir şey bulmak için aramadık yer bırakmamak; 2) söz veya tutumuyla çevreyi birbirine düşürmek, karmakarışık etmek: ''İnsanın gözü bir şey görmedi mi dünyanın altını üstüne getirmeli.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (791, 'altının kadrini sarraf bilir', 'bir kimsenin, bir şeyin değerini ancak bu konularda uzmanlığı bulunanlar bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (792, 'altının kıymetini sarraf bilir', 'bir kimsenin, bir şeyin değerini ancak o konuda uzmanlığı olanlar bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (793, 'altmışaltıya bağlamak', 'geçici bir çözümle durumu kurtarmış görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (794, 'altta kalanın canı çıksın', 'herkes başının çaresine baksın, gücü yetmeyen ne olursa olsun.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (795, 'altta kalmak', '1) herhangi bir iyiliğin karşılığını ödeyememek; 2) herhangi bir çatışmada, çekişmede yenilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (796, 'altta yok üstte yok', 'yoksul, fakir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (797, 'alttan almak', 'sert konuşan bir kimseye yumuşak bir dil kullanmak, aşağıdan almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (798, 'alttan güreşmek', 'gizli gizli yenme yollarını kollamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (799, 'altüst etmek', '1) alt yüzünü üst yüzüne getirmek; 2) çok karışık duruma getirmek, düzenini bozmak: ''Ama tutkunluklarımız yapraklara benzer, en hafif bir rüzgâr altüst edebilir onları.'' -C. Meriç. 3) yıkmak, harap etmek: Deprem köyü altüst etti. 4) huzursuz etmek, rahatsızlık vermek: ''Kalbini altüst eden yeni durumu orada öğrendi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (800, 'altüst olmak', '1) çok karışık duruma gelmek; 2) üzülmek, tedirgin olmak, yıkılmak: ''Tasarısı birdenbire altüst olan insanlar gibi bakınıyordu.'' -İ. H. Baltacıoğlu. 3) rahatsızlanmak: Yediğim yemekten midem altüst oldu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (801, 'ama ne', '1) ''ne hoş'' anlamında kullanılan bir söz: Ama ne manzara! Ama ne film! 2) ''şaşılacak niteliği olan'' anlamında kullanılan bir söz: Ama ne kılık!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (802, 'amaç gütmek', 'bir amacı gerçekleştirmeye çalışmak: ''Dernekler, 13''üncü maddedeki genel sınırlamalara aykırı hareket edemeyecekleri gibi siyasi amaç güdemezler.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (803, 'aman bulmak', 'kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (804, 'aman dedirtmek (amana getirmek)', 'karşı koyan birini boyun eğmek zorunda bırakmak, zor durumda bırakmak: ''Galiba bu sene soğuk aman dedirtecek.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (805, 'aman derim!', '''sakın ha, böyle bir iş yapayım deme'' anlamında kullanılan bir söz: Evi satacakmışsın, aman derim!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (806, 'aman diyene kılıç kalkmaz', 'mertliğinize güvenip size teslim olan düşmanın canına kıyılmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (807, 'aman vermek', 'canını bağışlamak, öldürmemek: ''Teslim olan halka aman vererek hepsini evlerine yolladı.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (808, 'aman vermemek', '1) rahat bırakmamak, göz açtırmamak: ''İri sivrisinekler gece gündüz aman vermiyordu.'' -N. Cumalı. 2) acımayıp öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (809, 'aman zaman bilmemek', 'fırsat vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (810, 'aman zaman dedirtmemek', 'aman vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (811, 'amana gelmek', 'önce direnirken zor karşısında boyun eğmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (812, 'aması maması yok!', 'hiçbir özrün geçerli olamayacağını anlatan bir söz: ''Ama diye sözünü kestim adamın. Aması maması yok, dedi o, sert bir sesle. Niye istifa etmedin?'' -N. Eray.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (813, 'Amasya''nın bardağı, biri olmazsa biri daha', 'ele geçirilmeyen veya kaçan bir şeye üzülmek boştur çünkü her zaman benzeri sağlanabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (814, 'amcamla dayım, hepsinden aldım payım', 'yakınlarından beklediği ilgi ve yardımı görmeyen bir kimse onlardan artık yeni bir istekte bulunamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (815, 'ameliyat (... ameliyatı) geçirmek', 'ameliyat edilmiş olmak: ''Bu koğuşta ayak ameliyatı geçirmiş hasta Şahin''di.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (816, 'ameliyata almak', 'gerekli hazırlıkların yapılmasından sonra hastayı ameliyat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (817, 'ameliyata girmek', '1) ameliyat işlemlerini gerçekleştirmek; 2) ameliyat olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (818, 'amma da yaptın ha!', 'söylenen bir söze pek inanılmadığını ve şaşıldığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (819, 'an beni bir kozla, o da çürük çıksın', 'bir dostun verdiği armağan küçük ve değersiz olsa bile verilen kişinin hatırlandığını göstermesi bakımından çok değerlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (820, 'an iti, kap sopayı', 'saldırgan biriyle karşılaşma olasılığı varsa kendini korumaya hazırlıklı ol.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (821, 'ana baba bir', 'aynı anne ve babadan olan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (822, 'ana baba eline bakmak', 'ana ve babanın verdiği para ile geçinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (823, 'ana baba yavrusu', 'nazlı büyütülmüş çocuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (824, 'ana bir, baba ayrı', 'anaları bir, babaları ayrı olan (kardeşler).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (825, 'ana gibi yâr olmaz, Bağdat gibi diyar olmaz', 'insanlar içinde bize anne kadar candan bağlı dost yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (826, 'ana ile kız, helva ile koz', 'anne ile kız, koz helvasının içindeki cevizle helva gibidirler, birbirlerinden kesinlikle ayrılmazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (827, 'ana kızına taht kurar, kız bahtı kocadan arar', 'kocası iyi olmayan bir kadın, kendi ne kadar zengin olursa olsun, mutlu olamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (828, 'ana kızına taht kurmuş, baht kuramamış', 'kocası iyi olmayan bir kadın, kendi ne kadar zengin olursa olsun, mutlu olamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (829, 'ana rahmine düşmek', 'döl yatağında cenin oluşmak: ''Ulan Mustafa, insanoğlu ana rahmine düşer de dokuz ay on gün sonra capcanlı fırlar.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (830, 'anadan doğmuşa dönmek (anadan yeni doğmuş gibi olmak)', '1) dertsiz, tasasız bir duruma gelmek; 2) günahlardan arınmış duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (831, 'anafora kaptırmak', 'başkasının emeksiz ve karşılıksız olarak yararlanmasına sebep olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (832, 'anahtar vermek', ' tiy. tuluat tiyatrosunda komiğe nükte yapma kolaylığı vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (833, 'anahtarı beline takmak', 'evde yönetimi ele almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (834, 'analı kuzu, kınalı kuzu', '1) her işi yolunda giden; 2) annesi sağ olan çocukların mutluluğunu anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (835, 'analık fenalık (kara yamalık)', 'üvey ana fenalık simgesidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (836, 'anam babam', 'içtenlik bildiren bir söz: Bilir miyim ben anam babam!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (837, 'anamın (babamın) öleceğini bilseydim kulağı dolu darıya satardım', 'insan en değerli malının karşılıksız olarak elinden gideceğini bilse onu yok denecek kadar az bir paraya satar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (838, 'anan güzel idi hani yeri, baban güzel idi hani evi', 'hiçbir duruma güvenilmez, bizim olan şeyler elimizde sürekli olarak kalmazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (839, 'anan yahşi, baban yahşi', 'birini, bir işe razı etmek için onu övmek amacıyla söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (840, 'ananın (anasının) ak sütü gibi (helal olsun)', '''anamın sütü bana nasıl helal ise bu da sana öyle helal olsun'' anlamında kullanılan bir söz: ''Şimşek gibi çakan ağrılardan beni kurtarsınlar, servetimin yarısını anamın ak sütü gibi vereyim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (841, 'ananın bahtı kızına', 'bir anne nasıl bir evlilik hayatı geçirirse kızının evlilik hayatı da kendisininkine benzer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (842, 'ananın bastığı yavru (civciv) incinmez (ölmez)', 'annenin acı sözü çocuğuna ağır gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (843, 'anası turp (sarımsak), babası şalgam (soğan)', ' hlk. ne olduğu belirsiz kimselerin çocuğu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (844, 'anası yerinde', 'anne gibi kabul edilen (kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (845, 'anasına bak, kızını al, kenarına (kıyısına, tarağına) bak, bezini al', 'bir kızın karakterini öğrenmek isteyenler, anasının durumunu göz önüne alırlarsa aldanmamış olurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (846, 'anasından doğduğuna bin pişman', '1) çok tembel, üşengeç; 2) canından bezmiş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (847, 'anasından doğduğuna pişman olmak', 'çok eziyet görmek, çok üzülmek, bezdirilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (848, 'anasından emdiği süt burnundan (fitil fitil) gelmek', 'bir işi yaparken çok sıkıntı çekmek: ''Mütercim olarak işe başladığım gün anamdan emdiğim süt burnumdan gelmiştir.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (849, 'anasından emdiği sütü burnundan getirmek', 'birine bir iş yaptırırken çok sıkıntı çektirmek: ''Hiç belli olmaz insanoğlu! Bir gün anadan emdiği sütü burnundan getirir.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (850, 'anasını bellemek', ' kaba en büyük kötülüğü yapmak: ''Geçmeyen zamanın anasını uyku beller.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (851, 'anasını eşek kovalasın!', ' kaba sözü edilen kimse veya iş için bıkkınlık, dikkate almama ve umursamama anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (852, 'anasını sat! (satayım!)', ' hlk. ''önem verme (vermem), aldırma (aldırmam), umursama (umursamam)!'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (853, 'anasının gözü', ' argo çok kurnaz, çok açıkgöz, dalavereci, hinoğluhin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (854, 'anasının ipini satmış (pazara çıkarmış)', 'ipsiz, kendisinden her türlü soysuzluk beklenebilen (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (855, 'anasının kızı', 'her yönüyle annesine benzeyen kız çocuğu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (856, 'anasının körpe kuzusu', 'pek küçük kucak çocuğu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (857, 'anasının nikâhını istemek', 'bir şeye değerinden çok para istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (858, 'anasının oğlu', 'her yönüyle annesine benzeyen erkek çocuğu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (859, 'anca beraber, kanca beraber', 'iki veya daha çok kişi yaptıkları iş kötü de gitse birbirlerinden ayrılmamalıdırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (860, 'andını bozmak', 'andına uymamak, andına aykırı davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (861, 'angarya (angaryasını) çekmek', 'bir işi isteksizce, hatır için yapmak zorunluluğunda olmak: ''Benim bu angaryalarımı da başka türlü kimsecikler çekmez.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (862, 'angaryaya koşmak', 'birini zorunlu olmadığı hâlde bir işte çalışmaya zorlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (863, 'anız bozmak', 'anızı altüst etmek için toprağı yüzden sürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (864, 'anladımsa arap olayım', ' tkz. ''hiçbir şey anlamadım'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (865, 'anlam çıkarmak', '1) bir cümleden veya metinden yeni ve değişik bir anlam yakalamak; 2) mec. yersiz ve gereksiz bir yargıya varmak, yanlış değerlendirmek; bir söze, söyleyenin aklından geçmeyen bir anlam vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (866, 'anlam vermek', 'kendince bir yargıya varmak, yorumlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (867, 'anlamazlıktan gelmek (anlamazlığa vurmak)', 'bir şeyi anladığı hâlde anlamamış gibi davranmak: ''Anlamazlığa vuruyorum, teşekkür ederek ayrılıyorum daireden.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (868, 'anlamına gelmek', 'bir anlam bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (869, 'anlarsın ya!', 'açıklanmaması gereken bir olayı dolaylı yoldan anlatmak için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (870, 'anlaşıldı Vehbi''nin kerrakesi', '''işin içyüzü, gerçeği öğrenildi'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (871, 'anlaşmaya varmak', 'bir konuda birisiyle anlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (872, 'anlaşmazlığa düşmek', 'anlaşamamak, uyuşamamak: ''Anlaşmazlığa düşmezdik. İyi çocuklardı.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (873, 'anlaşmazlık çıkmak', 'bir konuda uyuşmazlık söz konusu olmak: ''Miras dağılımında üvey annesiyle aralarında anlaşmazlık çıkmış.'' -C. Külebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (874, 'anlatışa göre verirler fetvayı', 'haksız kişi, olayı kendisini haklı gibi göstererek anlatırsa dinleyen ona hak verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (875, 'anlayana sivrisinek saz, anlamayana davul zurna az', 'anlayışlı kimseleri en küçük bir söz bile etkiler oysa anlayışsız kimselere ne söylense yararsızdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (876, 'anlayıp dinlemek', 'bir olayı iyice anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (877, 'anlayış göstermek', 'istenilen veya söylenilen bir şeyi hoşgörüyle karşılamak: ''Yaşamı her yönden yalnızlığa yaslanmış olan bu kadına tek çocuğun bile anlayış gösterdiğini sanmam.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (878, 'ant içmek', 'bir şeyi yapmaya veya yapmamaya söz vermek, yemin etmek: ''Böyle uzaklaşınca ağır ağır o bizden / Biz ayrı düşmemeye ant içmiştik denizden'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (879, 'ant verdirmek', '1) ant içmesini sağlamak; 2) herhangi bir şeyi yapmaması için söz almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (880, 'ant vermek', 'Allah aşkına, çocuklarının başı için vb. sözlerle karşısındakini bir şeye zorlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (881, 'antant kalmak', 'anlaşmak, uzlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (882, 'antikasını bilmek', 'en iyisini bilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (883, 'antipati duymak', 'kanı kaynamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (884, 'antipatik bulmak', 'sevimsiz bulmak, kanı kaynamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (885, 'apışıp kalmak', 'ne yapacağını bilemez duruma gelmek: ''Bu sonsuzluğun içinde işte besbelli sen de kendini kaybederek apışıp kalmışsın.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (886, 'apoletleri sökülmek', 'bir suç sebebiyle rütbesi indirilmek veya askerlikten atılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (887, 'aportta beklemek', '1) köpek avını kovalamak üzere hazırda beklemek; 2) fırsat kollamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (888, 'aptal yerine koymak', '1) hiçbir şeyden anlamaz, bilmez sanmak: ''Bu kadını zaman zaman aptal yerine koymam, büyük aptallık galiba.'' -R. Erduran. 2) birine, aptal gözüyle bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (889, 'aptallığa vurmak', 'bir şeyi bilmez, anlamaz gibi görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (890, 'ar damarı çatlamış', 'utanç duyulacak şeyleri sıkılmadan yapan, utanmaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (891, 'ar dünyası değil, kâr dünyası', 'kişi para kazanmak için namusuna dokunmadıktan sonra şu veya bu işi yapmaktan utanmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (892, 'ar etmek', 'utanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (893, 'ar kadar eri olanın, dağ kadar yeri olur', 'çalışkan kişileri olan aile ve toplumlar her yerde bol kazanç elde ederler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (894, 'ar namus tertemiz', 'utanması olmayan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (895, 'ar ve hayâ perdesi yırtılmak', '1) utanmamak, utanç duymamak: ''Atalarımızın ar ve hayâ perdesi yırtılmak diye pek düşündürücü bir tabirleri vardır.'' -R. N. Güntekin. 2) yüzsüzlük etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (896, 'ar yılı değil, kâr yılı', 'birinin sıkılmayı bir yana bırakarak yalnız çıkarına baktığı anlatılırken söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (897, 'araba devrilince (kırıldıktan sonra, kırılınca) yol gösteren çok olur', 'iş işten geçtikten sonra verilen öğüdün değeri yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (898, 'araba ile tavşan avlanmaz', 'her işte başarıya ulaşabilmek için kullanılması gereken özel yöntemler vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (899, 'arabanın ön tekerleği nereden geçerse art tekerleği de oradan geçer', 'kıdem ve yaş bakımından büyük olanların yaptıkları her zaman örnek alınır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (900, 'arabanın tekerine taş koymak', 'güçlük çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (901, 'arabasını düze çıkarmak', 'karşılaştığı güçlükleri yenip işini kolay yürür duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (902, 'aracı koymak', 'bir kimseyi, uzlaşma sağlamak için görevlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (903, 'arada çıkarmak', 'başka işler arasında bir işi de yapıvermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (904, 'arada kalmak', 'iki tarafı uzlaştırmak üzere araya girme dolayısıyla güç duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (905, 'arada kaynamak', 'karışık bir durumda gereken ilgiyi görmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (906, 'aradan çekilmek', '1) ara bulucu olmaktan vazgeçmek; 2) herhangi bir iş yapılırken işi başkalarına bırakmak, ilişiğini kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (907, 'aradan çıkarmak', 'birçok işten birini yapıp bitirivermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (908, 'aradan çıkmak', '1) yapılması gereken öteki işlerle uğraşılabilmesi için bir iş önce bitirilmek; 2) sıkışık bir durumda, sıkıntılı bir zamanda işe engel olan kimse oradan uzaklaşmak; 3) kendini bir sorunun, bir davanın dışında tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (909, 'aradan kaldırmak', 'iş yapma imkânını yok etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (910, 'aradan sıyrılmak', '1) kötü bir işten kendini kurtarmak; 2) mec. çatışan gruplar arasındaki rekabetten yararlanarak öne çıkmak; 3) sp. yarışta rakiplerinden uzaklaşmak, öne çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (911, 'Arafat''ta soyulmuş hacıya dönmek', 'her şeyini kaybedip çırılçıplak kalmak, çaresiz kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (912, 'araları limoni olmak', 'aralarında hafif bir kırgınlık olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (913, 'araları şekerrenk (serin) olmak', 'iki kişi arasında dostluk ilişkileri bozuk olmak: ''Vergi kâtibi ile de araları şekerrenk olmuştu.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (914, 'aralarına kara kedi girmek', 'iki dost birbirine gücenmek, iki dostun arasına soğukluk girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (915, 'aralarında dağlar kadar fark olmak', 'aralarında her yönden büyük ayrılıklar bulunmak, benzer nitelikler çok az olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (916, '(aralarında) kan olmak', 'aralarında kan davası bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (917, 'aralarındaki buzları eritmek', 'kırgınlığı, küslüğü ortadan kaldırmak: ''Kerim ve karısı Necla aralarındaki buzları eritmek amacıyla otele gelmişlerdir.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (918, 'aralarından kara kedi geçmek', 'birbirinden soğumak, aralarına soğukluk girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (919, 'aralarından su sızmamak', 'aralarında çok yakın, sıkı fıkı arkadaşlık bulunmak: ''Bir vakitler aralarından su sızmayan o dünya ahiret kardeş hatun kişiler, şimdi birbirlerini çekemiyor, birbirlerinin arkasından söylemediklerini bırakmıyorlardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (920, 'aralık vermek', '1) yeniden başlamak üzere bir işi kısa süre bırakmak; 2) harfler veya satırlar arasında boşluk bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (921, 'aramakla bulunmaz', 'çok değerli ancak rastlantı ile ele geçer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (922, 'arap gibi olmak', 'simsiyah olmak, kararmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (923, 'arap olayım', 'söylenen bir şeyin doğruluğuna inandırmak için ''söylediğim söz doğru değilse kararayım, esmerleşeyim'' anlamında kullanılan bir söz: Yalan söylüyorsam arap olayım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (924, 'Arap uyandı (Arap''ın gözü açıldı)', 'geçen bir olaydan ders alındığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (925, 'Arap''ın yalellisi gibi', 'yalelli gibi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (926, 'arapsaçı gibi', 'karmakarışık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (927, 'arapsaçına dönmek', 'işler çok karışıp çözümlenmesi güç bir duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (928, 'arası (araları) açılmak (açık olmak, bozulmak)', 'arkadaşlıkları sarsılmak, arkadaşlık bağları kopmak, birbirine darılmak: ''O ara garajcı ile de araları açıldığından tiyatroculuk oyununu bırakıp dergicilik oyununa geçtiler.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (929, 'arası geçmeden', 'vakit geçmeden, sıcağı sıcağına.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (930, 'arasına (aralarına) karışmak', 'büyüyüp yetişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (931, 'arasını (aralarını) açmak (bozmak)', 'iki kişi arasındaki dostluğu, ilişkiyi bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (932, 'arasını (aralarını) bulmak', 'araları bozulmuş iki kişiyi uzlaştırmak, barıştırmak: ''Emine ile aralarını bulmaya çalışacağını söyledi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (933, 'araya almak', '1) bir çevreye kabul etmek; 2) argo dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (934, 'araya (aralarına) soğukluk girmek', 'arada kırgınlık oluşmak: ''Hiç cevap vermedi, ağlamaya başladı ama aramıza soğukluk girdi.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (935, 'araya girmek', '1) iki kişinin arasındaki bir işe karışmak; 2) iki kişiyi uzlaştırmaya çalışmak; 3) bir iş yapılırken ona engel olacak başka bir şey çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (936, 'araya gitmek', 'harcanmak, karışıklığa kurban olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (937, 'araya kaynayıp gitmek', 'göz ardı edilmek: ''Bu arada üç beş yazarın üretimi de araya kaynayıp gidiyor.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (938, 'araya vermek', 'yararsız bir işe harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (939, 'arayan Mevla''sını da bulur, belasını da', 'iyiyi amaçlayanlar iyiye, kötüyü amaçlayanlar ise kötüye ulaşırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (940, 'arayı açmak', '1) aradaki uzaklık artmak; 2) mec. görüşmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (941, 'arayı soğutmak', 'eski yakınlık, dostluk kalmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (942, 'arayı yapmak', 'arasını bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (943, 'arayıp da bulamamak', 'beklenmedik iyi bir durumla karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (944, 'arazi açmak', 'fundalık, koruluk, sazlık yerleri temizleyerek tarıma elverişli duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (945, 'arazi olmak', '1) sıvışmak; 2) işten kaçmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (946, 'araziye uymak', '1) ortama, çevreye uymak; 2) görünmemeye çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (947, 'ardı arası kesilmemek', 'aralıksız olarak gelmek: ''Olayların ardı arası kesilmez. Hepsi birbirini kovalar.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (948, 'ardı kesilmemek', 'arkası gelmemek, tükenmemek: Gidiş gelişin ardı hiç kesilmiyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (949, 'ardıcın közü olmaz, yalancının sözü olmaz', 'ardıç ağacının ateşi çabuk geçer, kül olur; yalancının sözü de böyledir, ona da güvenilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (950, 'ardında yüz köpek havlamayan kurt, kurt sayılmaz', 'önemli kimseleri çekemeyip onlara dil uzatanlar çok olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (951, 'ardından atlı kovalamak', 'arkasından atlı kovalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (952, 'ardından gitmek', '1) peşine takılmak; 2) mec. aynı düşünceye sahip olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (953, 'ardından sapan taşı yetişmez', 'çok hızlı koşuyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (954, 'ardını almak (getirmek)', 'bitirmek, tamamlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (955, 'ardını bırakmamak', 'peşini bırakmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (956, 'ardını kesmek', 'arkasını getirmemek, önlemek, son vermek, durdurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (957, 'arı bal alacak çiçeği bilir', 'işini bilen kimse nereye başvuracağını bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (958, 'arı bey olan kovana üşer', 'halk, kendisine önderlik edecek kişinin çevresinde toplanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (959, 'arı gibi', '1) çok çalışkan; 2) hızlı ve sürekli bir biçimde: ''Gürültü etmeden, iz bırakmadan, hadise çıkarmadan çalışıyorlar, arılar gibi.'' -E. M. Karakurt.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (960, 'arı gibi eri olanın dağ kadar yeri olur', 'çalışkan kişileri olan aile ve toplumlar her yerde bol kazanç elde ederler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (961, 'arı gibi sokmak', 'iğnelemek, acı söz söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (962, 'arı kızdıranı sokar', 'kişi, sonunda öleceğini de bilse kendisini sinirlendirene saldırır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (963, 'arı kovanı gibi işlemek', 'bir yerin gireni çıkanı çok olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (964, 'arı söğüdü, akıllı öğüdü sever', 'herkes işine yarayan şeyi benimser.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (965, 'arığa su gelene kadar kurbağanın gözü patlar', 'yapılması geciken iyilikler, bekleyenleri sıkıntı içinde bırakır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (966, 'arık ata kuyruğu da yüktür', 'güçsüz kişi, kimseye yardım edecek durumda değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (967, 'arık çekmek', 'tıkanan, bozulan arkları temizleyip açmak: ''Beş gün belinin, kollarının ağrısını duymadan Binnaz''ın önü sıra arık çekti.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (968, 'arık etten yağlı tirit olmaz', 'değersiz kişiden yararlı iş, verimsiz tarladan bol ürün beklenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (969, 'arık öküze bıçak olmaz (çalınmaz)', '1) güçsüz kimseyi ezmek yiğitlik değildir; 2) kendisinden yararlanılamayacak kişiye yararlanmak amacıyla eziyet edilmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (970, 'arına dokunmak', 'utanç duymak: ''Hele meydanı hasımlarına bırakmak arıma dokunuyor.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (971, 'arının yuvasına kazık (çöp) dürtmek', 'tehlikeli kişiyi kışkırtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (972, 'arif olan anlar (anlasın)', 'herkesin anlayacağı kadar açık söylenmeyen bir sözün gerçek anlamını kavrayanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (973, 'arife günü yalan söyleyenin (oruç yiyenin) bayram günü yüzü kara çıkar (olur)', 'bir sözün yalan olduğu çabuk anlaşılır ve söyleyen toplum içinde utanılacak bir duruma düşer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (974, 'arifeyi gösterip bayramı göstermemek', 'bir işi sonuna kadar başarılı götürüp, sonunda olumlu sonuca ulaşamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (975, 'arka arkaya vermek', 'birbirini korumak için birleşmek, destek olmak, dayanışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (976, 'arka bulmak', 'bir koruyucu, kayırıcı bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (977, 'arka çevirmek', 'sırt çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (978, 'arka çıkmak', 'bir kimseyi başkalarına karşı korumak, kayırmak: Annesi arka çıktı da çocuğu dayaktan kurtardı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (979, 'arka (geri) planda kalmak', '1) gözden düşmek; 2) önemini yitirmek, değersizleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (980, 'arka kapıdan çıkmak', 'okuldan başarısızlık nedeniyle ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (981, 'arka olmak', 'maddi veya manevi yönden destek olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (982, 'arka plana kaymak', '1) gözden düşmek: ''Bu üslubu ve bakış açısı yüzünden arka plana kaymış.'' -T. Buğra. 2) önemini yitirmek, değersizleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (983, 'arka vermek', '1) desteklemek: ''Sen ona arka vermesen o bize böyle sırtarmazdı.'' -O. Kemal. 2) dayamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (984, 'arkada kalmak', '1) geriden gelmek, geride kalmak; 2) değerce ileride olanların arkasında kalmak, ileri gidememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (985, 'arkadan söylemek', 'kendisi bulunmadığı bir yerde bir kimseyi çekiştirmek, dedikodusunu yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (986, 'arkadan vurmak', 'bir kimse kendisine güvenen ve inanan birine gizlice kötülük etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (987, 'arkadaş değil, arka taşı', 'zarar veren arkadaş için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (988, 'arkadaşını söyle, kim olduğunu söyleyeyim', 'kişi kendisine uygun kimselerle arkadaşlık kuracağı için arkadaşını tanıdığımızda o kişinin de kimliğini öğrenmiş oluruz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (989, 'arkası alınmak', 'sona erdirilmek, bitirilmek, bir yerde durdurulmak: Kaçakçılığın arkası alındı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (990, 'arkası olmamak', 'kayıracak kimsesi olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (991, 'arkası yere gelmemek', 'sırtı yere gelmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (992, 'arkasına almak', '1) sırtına yüklemek, taşımak; 2) mec. desteğini sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (993, 'arkasına (bile) bakmadan gitmek (kaçmak)', 'arkada kalanlarla ilgilenmeden bir yerden hızlıca ayrılmak: ''O kadar korktular, o kadar pıstılar ki arkalarına bile bakmadan kaçmaya başladılar.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (994, 'arkasında dolaşmak (gezmek)', 'bir işi yaptırmak için ilgili veya yetkili bir kimsenin uğradığı yerlere giderek görüşme fırsatı aramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (995, 'arkasında yumurta küfesi yok ya! (olmamak)', 'sırtında yumurta küfesi yok ya!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (996, 'arkasından atlı kovalamak', 'bir işi gereksiz bir telaşla yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (997, 'arkasından atmak (konuşmak)', 'dedikodusunu yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (998, 'arkasından koşmak', '1) iş yaptırmak için birinin arzusunu kollamak, görüşme fırsatı aramak; 2) birine çok ilgi duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (999, 'arkasından sürüklemek', 'arkasından gelmesini sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1000, 'arkasından teneke çalmak', 'tenekeye sopa vb. ile vurarak giden bir kişiye hakaret etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1001, 'arkasından zil takıp oynamak', 'birinin bir yerden ayrılmasına veya bir işte başarısızlığa uğramasına çok sevinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1002, 'arkasını almak', 'bir işi tamamlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1003, 'arkasını bırakmak', 'peşini bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1004, 'arkasını (bir şeye) vermek', 'dönmek: Ateşe arkasını verdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1005, 'arkasını (birine) vermek', 'birinin koruyuculuğuna güvenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1006, 'arkasını getirememek', 'başladığı bir işi sürdürüp sona erdirememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1007, 'arkasını sağlama almak', 'bir işe başlarken çok güçlü bir destek bulmuş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1008, 'arkaya bırakmak (koymak)', 'sonraya, başka zamana bırakmak, ertelemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1009, 'arkaya kalmak', 'geride kalmak, sonraya kalmak, geriden gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1010, 'arlı arından, huylu huyundan vazgeçmez', 'herkes kendi karakterine göre davranışta bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1011, 'arma donatmak', ' den. armayı yerli yerine koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1012, 'arma soymak', ' den. hareketli olan armayı, limanda kışlamak, yağmur ve kardan korumak amacıyla bir süre için sökmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1013, 'arma uçurmak (budatmak)', ' den. armayı rüzgâra kaptırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1014, 'armudu soy ye, elmayı say ye', 'armut kabuğu soyularak elma da aşırı gidilmeden sayıyla yenilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1015, 'armudun iyisini (dağda) ayılar yer', 'kendilerine yakışmayan güzel bir şeyi eline geçirenler için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1016, 'armudun önü kirazın sonu', 'armut ilk çıktığında, kiraz da biteceği zaman yenilmeli.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1017, 'armudun sapı var, üzümün (kirazın) çöpü var demek', 'her şeye kusur bulmak, hiçbir şeyi beğenmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1018, 'armut dalının dibine düşer', 'bir kimse önce yakınlarına yararlı olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1019, 'armut gibi', 'çok anlayışsız, bön.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1020, 'armut piş ağzıma düş!', 'bir işe emek harcamaksızın onun kendiliğinden olmasını bekleyenlerin durumunu anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1021, 'arpa eken buğday biçmez', 'kötü davranışın karşılığı iyi olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1022, 'arpa ektim, darı çıktı', 'ters sonuç veren işler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1023, 'arpa samanıyla, kömür dumanıyla', 'yararlanılan nesneleri eksiklikleriyle birlikte kabullenmek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1024, 'arpa unundan kadayıf olmaz', 'kötü gereçle iyi şey yapılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1025, 'arpa verilmeyen at, kamçı zoruyla yürümez', 'bir kişinin verimli olarak iş görmesi, onun geçiminin sağlanmasına bağlıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1026, 'arpacı kumrusu gibi düşünmek', 'içinde bulunduğu sorunu nasıl çözeceğini uzun uzun düşünmek: ''Bak, o şoförün yanında arpacı kumrusu gibi düşünen maarif müdürü beye.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1027, 'arpacıya borç eden ahırını tez satar', 'bir iş borçla sağlıklı bir biçimde yürütülemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1028, 'arpalık yapmak', 'bir kaynaktan sürekli olarak çıkar sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1029, 'arpası çok gelmek', 'coşmak, azmak, kudurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1030, 'arsız neden arlanır, çul da giyer sallanır', 'arsız hiçbir şeyden utanmadığı için elbise diye çul da giyse salına salına gezer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1031, 'arsızın yüzüne tükürmüşler ''yağmur yağıyor'' demiş', 'arsız ne kadar ağır hakaret görse de aldırmaz, pişkinliğe vurur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1032, 'arslan postunda gönül dostunda', 'her şey kendi yerinde değer kazanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1033, 'arslanın adı çıkmış, çakallar baş keser', 'haksızlık veya kötülük yapacağı düşünülen kişi yerine bu konuda adı ön plana çıkan kişiler asıl haksızlığı ve kötülüğü yaparlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1034, 'arşın malı kantar ile satılmaz', 'bir nesne için kullanılan ölçü, niteliği ayrı olan başka bir nesne için kullanılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1035, 'art eteğinde namaz kılmak', 'çok temiz huylu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1036, 'artık mal göz çıkarmaz', 'ne kadar ve ne türden mal olursa olsun malın fazlası elden çıkarılmamalıdır çünkü mutlaka bir gün gelir lazım olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1037, 'artist gibi', 'boylu boslu, güzel ve alımlı, yakışıklı (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1038, 'arzu duymak', 'birine veya bir şeye karşı istek duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1039, 'arzuhâl gibi (kadar)', 'çok uzun (mektup).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1040, 'arzusu kalmak', 'isteği yerine gelmemek, hevesini alamamak: ''Arzum kaldı toprağında taşında'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1041, 'asabına dokunmak', 'sinirine dokunmak: ''Bombardımanlar asabıma dokunuyordu, sakin bir yere kaçmak istiyordum.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1042, 'asayiş berkemal', 'güvenliğin yerinde olduğunu anlatan bir söz: ''Efendiler, mühim bir şey yok yahu!.. Asayiş berkemaldir.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1043, 'asıda olmak (kalmak)', 'bir işe son verilmeyip öylece bırakılmış olmak: Bu iş bundan fazla asıda kalamaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1044, 'asıl azmaz, bal kokmaz', 'bir kimse veya nesne, ne denli biçim değiştirirse değiştirsin aslını yitirmez, soyluluğunu korur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1045, 'asıp kesmek', 'işbaşında bulunan bir kimse yasayı çiğneyerek sert davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1046, 'asil ile taş taşı, bedasıl ile yeme aşı', 'asil insanla beraber bulunmak her şartta faydalıdır, en iyi şartları önüne getirse bile kötü ve bayağı insandan kaçmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1047, 'asker gibi', 'disiplinli, düzgün.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1048, 'askıda bırakmak', 'sonuca vardırmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1049, 'askıda kalmak', 'bir iş bir engel dolayısıyla sonuca varamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1050, 'askıya almak', '1) altı boşalıp desteği kalmayan yapıyı dikmelerle boşlukta tutarak yıkılmaktan kurtarmak; 2) oturmuş veya batmış bir gemiyi yüzdürmek için başka teknelere asarak kaldırmak; 3) mec. bir işi zamanında yapmayıp belirsiz bir zamana bırakmak, savsaklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1051, 'askıya çıkarmak', 'evlenecek kimselerin durumunu nüfus kayıtlarının bulunduğu yerde askı yoluyla ilan etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1052, 'askıya çıkmak', '1) ipek böceği koza sarmak üzere dallara çıkmak; 2) evlenecek kimselerin durumu nüfus kayıtlarının bulunduğu yerde askı yoluyla ilan edilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1053, 'aslan gibi', '1) boylu boslu, güçlü ve yakışıklı; 2) sağlığı yerinde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1054, 'aslan kesilmek', 'aslan gibi güçlü ve cesur duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1055, 'aslan kocayınca sıçan deliği gözetir', 'güçlü olduğunda ağır ve büyük işler yapan, büyük kazançlar elde eden kimse, güçten düşünce pek küçük işlerle uğraşır, azla yetinir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1056, 'aslan kükrerse atın ayağı kösteklenir', 'güçlü kimsenin korkutucu sözleri, güçsüzü kıpırdayamayacak duruma getirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1057, 'aslan postunda, gönül dostunda', 'canlı, cansız her şeyin bir yakışığı vardır, insan onları bu durumda görmek ister.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1058, 'aslan yatağından (yattığı yerden) bellidir (belli olur)', 'bir kimsenin oturduğu yerin durumu, onun kişiliğini belli eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1059, 'aslı astarı olmamak', 'gerçekliği, doğruluğu bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1060, 'aslı çıkmak', 'gerçek olduğu anlaşılmak, gerçek olduğu ortaya çıkmak: Söylenenlerin aslı çıkarsa güç duruma düşecek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1061, 'aslı faslı olmamak', 'gerçekliği, doğruluğu bulunmamak: ''Birtakım aslı faslı olmayan ağrılar yaratan sıtma mikrobu gibi sinsi bir yorgunluk vardı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1062, 'aslı faslı yok', 'yalan, uydurma.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1063, 'aslına bakarsan', '''doğruyu, gerçeği ararsan'' anlamında kullanılan bir söz: ''Ömrü savaş içinde geçer insanın, aslına bakarsan ekmeğini topraktan çıkarmak için.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1064, 'aslını saklayan (inkâr eden) haramzadedir', 'kişi yoksul, görgüsüz bir aileden gelmekle değersiz olmaz, toplum içindeki yerini kendisi kazanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1065, 'astar bol olmayınca yüze gelmez', 'bir iş yapmak için gerekli olan şeyler, ölçü biraz geniş tutularak hazırlanmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1066, 'astarı yüzünden pahalı olmak (pahalıya gelmek)', 'bir işin ayrıntılarına harcanılan para veya emek, elde edilen sonucun değerini aşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1067, 'astığı astık, kestiği kestik', 'acımasız, çok sert veya istediği gibi davranan kimse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1068, 'aş taşınca kepçeye paha olmaz', 'sıkışık zamanlarda önemsiz şeylerin değeri çoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1069, 'aş tuz ile, tuz oran ile', 'bir şeyin hoşa gitmesi onun birtakım nitelikler taşımasına ve bu niteliklerin de gerektiği oranda bulunmasına bağlıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1070, 'aşağı almak', 'devirmek, yıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1071, 'aşağı (aşağısı) kurtarmaz', '1) ''bundan daha ucuza olmaz'' anlamında kullanılan bir söz; 2) alay ''daha aşağı bir durumu kendine layık görmez'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1072, 'aşağı çekmek', 'değerini düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1073, 'aşağı düşmek', 'düzeyi, miktarı, niteliği azalmak: ''Bunlar arasında birkaç gazete ve dergi alanları hesaba katacak olursanız gazete ve dergi okuyucularının nispeti daha da aşağı düşer.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1074, 'aşağı görmek', 'küçük görmek, beğenmemek, hor görmek: ''Bu kadar fütursuz bir kitleyi ne diye aşağı görüyoruz?'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1075, 'aşağı tükürsem sakal, yukarı tükürsem bıyık', 'iki karşıt ve aynı derecede sakıncalı durum karşısında karar verme zorluğunu anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1076, '(...) aşağı (...) yukarı', '1) bir kimsenin adının dilden düşürülmediğini, onun pek gözde olduğunu anlatan bir söz: ''Adı erken yaşta şaire çıkmıştı. Şair aşağı, şair yukarı.'' -H. Taner. 2) bir hizmette çok kullanılan kişice, yakınma olarak kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1077, 'aşağıdan almak', 'alttan almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1078, 'aşı pişiren yağ olur, gelinin yüzü ağ olur', 'güzel şey, iyi gereç kullanılarak meydana gelir, bundan da iş yapana övünme payı çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1079, 'aşı vurmak (yapmak)', 'bağışıklık veya tedavi amacıyla vücuda aşı vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1080, 'âşığa Bağdat sorulmaz', 'bir şeye çok istekli olan kimse, o şeyi elde etmedeki zorlukları hiçe sayar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1081, 'âşığa Bağdat uzak (ırak) değil (gelmez)', 'bir şeyi elde etmek için aşırı istekli olan kimseye, bu uğurda katlanacağı fedakârlıklar güç gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1082, 'aşığı cuk oturmak', 'işi çok olumlu bir biçim almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1083, 'âşığın gözü kördür', 'kendisini aşka kaptıran kimse, sevgilisinin kusurlarını görmediği gibi çevresinde olup bitenlerle de ilgilenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1084, 'âşık âlemi kör, dört yanını duvar sanır', 'aşktan gözü kararmış kimse, hoş karşılanmayacak aşırı davranışlarda bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1085, 'aşık daima bey oturmaz', 'işi çoğunlukla iyi giden bir kimse, talihinin her zaman ona yâr olamayacağını bilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1086, 'aşını, eşini, işini bil', 'sağlık ve mutluluk içinde yaşamak isteyen kişi, yiyeceğine dikkat etmeli, arkadaşını iyi seçmeli ve bir iş sahibi olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1087, 'aşırı gitmek', 'ölçüyü kaçırmak, usandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1088, 'aşinalık göstermek', 'ilgilenmek, tanıdığını belli etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1089, 'aşk ağlatır, dert söyletir', 'âşığın yüreği yaralıdır ve daima ağlar, bir derdi olan da herkese derdini anlatır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1090, 'aşk olmayınca meşk olmaz', 'güçlü bir istek olmayınca hiçbir şey elde edilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1091, 'aşka düşmek', 'âşık olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1092, 'aşka gelmek', ' tkz. bir şeyi yapmak için büyük bir istek duymak, coşmak, coşkunluk göstermek: ''Meltemler tanrısı aşka gelip bu yeni varlığı yelpazelemeye koyuldu.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1093, 'aşure yemeye giden kaşığını cebinde taşır', 'bir işten yararlanmak isteyen gerekli araçları hazırlamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1094, 'at, adımına göre değil, adamına göre yürür', 'atın yürüyüşü binicisinin yönetimine bağlı olduğu gibi bir işin gidişi de iş başındakinin bilgisine ve çabasına bağlıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1095, 'at arıklıkla, yiğit gariplikle', 'zayıf ve garipken kişiliklerini yitirmeyip görevlerini başarı ile yapanlar üstün niteliklerini o zaman belli etmiş olurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1096, 'at at oluncaya kadar sahibi mat olur', 'bir çocuğu, bir işçiyi yetiştiren her bakımdan çok yorulur, yıpranır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1097, 'at beslenirken kız istenirken', 'at bakımlı olduğunda satılmalı, kız da güzelliği geçmeden evlendirilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1098, 'at binenin, kılıç kuşananın', 'her şey, onu gereği gibi kullanmasını bilene yakışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1099, 'at binicisine (sahibine) göre kişner (eşinir)', 'insanlar başlarında bulunan kişinin etkisi altında kalarak onun tutumuna göre davranırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1100, 'at binicisini bilir (tanır)', 'işçi, yöneticisinin işten anlayıp anlamadığını bilir ve çalışmalarını ona göre yürütür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1101, 'at bulunur meydan bulunmaz, meydan bulunur at bulunmaz', 'bir işi başarabilmek için gerekli olan koşullar her zaman eksiksiz olarak ele geçmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1102, 'at çalındıktan sonra ahırın kapısını kapamak', 'iş işten geçtikten sonra önlem almaya kalkışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1103, 'at gibi', 'vücudu iri yarı olan (kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1104, 'at görür aksar, su görür susar', 'meraklı kimse öğrenmek istediği bilgiye ulaşabilmek için her yola başvurur, her kılığa girer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1105, 'at ile avrat yiğidin bahtına', 'kişinin satın aldığı attan ve evlendiği kadından memnun kalıp kalmayacağı önceden kestirilemez, her ikisi de talihine kalmıştır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1106, 'at izi it izine karışmak', 'iyiyi kötüden ayıramayacak kadar bir karışıklık ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1107, 'at koşturacak kadar', 'pek geniş, çok geniş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1108, 'at nalı kadar', ' alay pek büyük (nişan, madalya, elmas, plaka vb. şeyler).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1109, 'at olur, meydan olmaz (bulunmaz), meydan olur (bulunur), at olmaz (bulunmaz)', 'gerekli şartlar her zaman bir arada bulunmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1110, 'at oynatmak', '1) atla hüner göstermek; 2) mec. yarışmak: Ben onunla at oynatamam. 3) mec. bildiği ve istediği gibi davranmak: ''Bizde ilk kurulan parlamento da Avrupa''daki benzerleri gibi, özel menfaatlerin gizlice at oynattığı bir alan olmakta gecikmemiş.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1111, 'at ölür, itlere bayram olur', 'değerli kimselerden birinin ölümü veya görevden ayrılması, kimi zaman aşağılık kimselerin işine yarar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1112, 'at ölür meydan (nalı) kalır, yiğit ölür şan (namı) kalır', 'yaşarken iyi işler yapmalı, iyi bir ad bırakılmaya çalışılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1113, 'at pazarında eşek osurtmuyoruz!', ' kaba söyleneni dinlemeyene uyarı amacıyla söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1114, '(at) tırısa kalkmak', 'tırıs gitmeye başlamak: ''Atlar bazen dörtnala kalkıyor, bazen tırısa geçiyordu.'' -R. Enis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1115, 'at var, meydan yok', 'yapacak güç var ancak kullanma imkânı yok.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1116, 'at yedi günde, it yediği günde (belli olur, semirir)', 'değerli kişilikler zamanla gelişir, kısa sürede beliren kişilikler gerçek değer taşımayanlardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1117, 'at yiğidin yoldaşıdır', 'bir insanın atı daima onun yanındadır ve zor anlarında ona yardım eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1118, 'ata arpa, yiğide pilav', 'canlıların güçleri, gelişmelerine yarayan şeylerle artar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1119, 'ata binen nalını, mıhını arar', 'kişi, kullanacağı şeyin ayrıntılarına dikkat etmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1120, 'ata binersen Allah''ı, attan inersen atı unutma', 'ata bindiğin zaman hayvanı hırpalama, attan indiğin zaman da onun yemini, suyunu, tımarını unutma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1121, 'ata da soy gerek, ite de', 'bütün yaratıkların soylusu üstün niteliktedir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1122, 'ata dost gibi bakmalı, düşman gibi binmeli', 'çalışanınızı iyi beslerseniz onun gücü artar ve daha verimli işler yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1123, 'ata dostu oğla mirastır', 'baba dostları, babamızdan bize kalan miras gibidirler, bizi kollarlar ve bize her türlü yardımı yaparlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1124, 'ata et, ite ot vermek', 'bir işi ters yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1125, 'ata eyer gerek, eyere er gerek', 'bir işletmeyi önce donatıp sonra da iyi bir yöneticiye teslim ederseniz istediğiniz verimi alırsınız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1126, 'ata malı mal olmaz, kendin kazanmak gerek', 'babadan kalan mal kalıcı değildir, çabuk biter; kişinin gerçek malı, kendi çalışmasıyla elde ettiği maldır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1127, 'atalar çıkarayım der tahta, döner dolaşır gelir bahta', 'ana baba, çocuğuna mutlu bir yaşam sağlamaya çalışır ancak kaderde yazılı olan gerçekleşir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1128, 'atalar sözünü tutmayanı yabana atarlar', 'büyüklerinin sözünü tutmayıp onların gösterdikleri yoldan gitmeyenler toplum içinde ziyan olup giderler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1129, 'atanın sanatı oğla mirastır', 'bir evlat babasının sanatını onun ölümünden sonra sürdürür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1130, 'atasını tanımayan Allah''ını tanımaz', 'babaya itaat etmeyen kimse, ana ve babaya itaat etmeyi buyuran Tanrıyı da tanımıyor demektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1131, 'atbaşı (beraber) gitmek', 'eşit durumda olmak: ''Bu çeneyle atbaşı giden keskin bir zekâsı var.'' -C. S. Tarancı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1132, 'ateh getirmek', 'bunamak: ''Sen sahiden budalaymışsın dostum hem adamakıllı ateh getirmişsin.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1133, 'ateş açmak', 'ateşli silahla mermi atmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1134, 'ateş almak', '1) yanmak, tutuşmak; 2) ateşli silah patlamak; 3) mec. coşmak: ''Bir sözden, bir asker geçişinden, bir düşünceden yüreği parlar, gönlü ateş alır.'' -M. Ş. Esendal. 4) mec. telaşlanmak, heyecanlanmak; 5) mec. öfkelenmek; 6) mec. acele davranmak, acele etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1135, 'ateş almaya mı geldin?', 'uğradığı yerden hemen gitmeye kalkan kimseye sitem olarak söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1136, 'ateş bacayı (saçağı) sarmak', 'bir olay, önüne geçilemez, tehlikeli bir durum almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1137, 'ateş demekle ağız yanmaz', 'kişi, zararlı bir eylemin sözünü etmekle kendisini zarara sokmuş olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1138, 'ateş düştüğü yeri yakar', 'bir acıyı onu çekenden başkası tam anlayamaz veya aynı ölçüde üzülemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1139, 'ateş gibi', '1) çok sıcak; 2) zeki, çalışkan ve becerikli; 3) kıpkırmızı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1140, 'ateş gibi kesilmek', 'beklenmedik bir olay karşısında öfke sonucu kanı beynine sıçramak: ''Yüzüm nasıl bir hâl aldı bilmiyorum fakat ateş gibi kesildiğini iyi biliyorum.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1141, 'ateş gibi yanmak', 'ateşi yükselmek: ''Alnı, yanakları ateş gibi yandığı hâlde vücudu tir tir titriyor, dişleri birbirine çarpıyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1142, 'ateş kesilmek', '1) çok kızgın davranışlarda bulunmak, ateş püskürmek; 2) sonradan çok çalışkan, hareketli ve becerikli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1143, 'ateş kesmek', 'ateşli silahlarla yapılan atışa son vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1144, 'ateş olmayan yerden duman çıkmaz', 'küçük de olsa birtakım belirtilerin önemli olaylara işaret olduğunu anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1145, 'ateş olsa cirmi kadar yer yakar', 'hasmın pek önemsenmediğini anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1146, 'ateş püskürmek', 'çok öfkeli olmak: ''Parça parça morarmış yüzüyle ateş püskürüyordu.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1147, 'ateş saçmak', 'çok kızmak, çok öfkelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1148, 'ateş vermek', 'tutuşturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1149, 'ateş yağdırmak', '1) ateşli silahlarla aralıksız mermi atmak; 2) mec. çevresindekilere ağır sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1150, 'ateşe tutmak', '1) az ısıtmak; 2) üzerine ateşli silahla mermi atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1151, 'ateşe vermek', '1) ateş içine sokmak: ''Bir parça büküyor, onu tekrar ateşe verinceye kadar evvelki hazır oluyordu.'' -M. Ş. Esendal. 2) bir yeri kasten yakmak, kundak sokmak; 3) mec. aşırı telaşa ve sıkıntıya düşürmek; 4) mec. bir ülkeyi savaşa sokarak veya kargaşa ve karışıklık yaratarak sıkıntı ve yıkıma uğratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1152, 'ateşe vurmak', 'bir yemeği pişmek üzere ocağa koymak: ''Taş ocağın üstünde, ateşe vurduğu güveçten, kaynayan etin kokusu geliyordu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1153, 'ateşe vursa duman vermez', 'pek cimri olanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1154, 'ateşi başına vurmak', 'çok öfkelenmek, sinirlenmek, coşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1155, 'ateşi çıkmak (yükselmek)', 'hasta vücut ısısı olağandan çok artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1156, 'ateşi düşmek', 'hastanın ateşi geçmek veya azalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1157, 'ateşi uyandırmak', 'sönmek üzere olan ateşi canlandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1158, 'ateşini almak', '1) yüksek vücut ısısını düşürmek: Alnına sirkeli bez koyun, ateşini alır. 2) derece ile ateşi ölçmek; 3) mec. acıyı, yanmayı azaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1159, 'ateşle barut bir yerde durmaz (olmaz)', 'kızla erkeğin baş başa yalnız kalmaları kötü sonuçlar doğurabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1160, 'ateşle oynamak', 'pek tehlikeli bir işle uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1161, 'ateşler içinde yanmak', '1) hasta çok ateşli bir durumda olmak; 2) mec. bir şeye fazlasıyla tutulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1162, 'atı alan Üsküdar''ı geçti', 'fırsatın kaçırılıp artık yapılacak bir şeyin kalmadığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1163, 'atı atasıyla, katırı anasıyla', 'soylu kişiden korkulmaz, soysuz kişiden korkulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1164, 'atılan ok geri dönmez', 'iyi düşünmeden yaptığımız işlerden pişman olarak geri dönmek isteriz ama artık iş işten geçmiştir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1165, 'atılı bulunmak', 'ertelenmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1166, 'atım tepmez, itim kapmaz deme', 'size çok bağlı olan kimseler bile zaman gelir sizi incitebilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1167, 'atın bahtsızı arabaya düşer', 'değerli ama talihsiz kimseler, kişiliklerine uygun olmayan ağır ve aşağılık işlerde kullanılırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1168, 'atın dorusu, yiğidin delisi', 'atın doru renkli olanı, kişinin ise gözünü budaktan esirgemeyeni makbuldür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1169, 'atın ölümü arpadan olsun', 'çok sevilen bir şey yapılırken veya sevilen bir yiyecek yenilirken sonuç kötü de olsa zor veya kötü şeylere katlanılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1170, 'atın tepmezi, itin kapmazı olmaz', 'size çok bağlı olan kimseler bile zaman gelir sizi incitebilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1171, 'atın ürkeği, yiğidin korkağı', 'at da kişi de hep tehlike içinde imişler gibi uyanık olmalıdırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1172, 'atın varken yol tanı, ağan varken el tanı', 'elde imkân varken gezip dolaşmak, dost edinmek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1173, 'atına bakan ardına bakmaz', 'görevini eksiksiz yapan, aracını iyi kullanan kimse kendisini kötü duruma düşmekten kurtarmış olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1174, 'atını sağlam kazığa bağlamak', 'eşeğini sağlam kazığa bağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1175, 'atıp (atmak) tutmak', '1) bir kimse veya bir şey için kötü konuşmak: ''Hatta aleyhimde atıp tuttuğunu bile duysam kendimi tanıtmamalıydım.'' -O. V. Kanık. 2) abartmalı konuşmak: ''Dünyanın siyasetiyle meşgul oluyorlar, büyük olaylar hakkında atıp tutuyorlar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1176, 'atla arpayı dövüştürmek (dalaştırmak)', 'fesat karıştırmak, arabozanlık etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1177, 'atladı geçti Genç Osman!', 'bir işin bittiğini veya tehlikenin atlatıldığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1178, 'atlama taşı yapmak', 'daha iyi bir yere geçmek için bir durumu veya bir kimseyi araç olarak kullanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1179, 'atlar nallanırken kurbağalar ayak uzatmaz', 'küçükler büyüklerin yanında hadlerini bilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1180, 'atlar tepişir, arada eşekler ezilir', 'büyüklerin çatışmasından küçükler zarar görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1181, 'atlıya saat olmaz', 'elinde bol imkânlar olan kimse, uzun bir süre içinde yapılabilecek işi çok kısa bir zamanda yapabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1182, 'atma Recep, din kardeşiyiz', ' argo ''söylediklerin hep yalan, abartma ancak biz bunun farkındayız'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1183, 'atsan atılmaz, satsan satılmaz', 'işe yaramadığı veya sıkıntı verdiği hâlde vazgeçilemeyen şeyler ve kimseler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1184, 'atta, avratta uğur vardır', 'insana atı ve evlendiği kadın uğur getirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1185, 'atta karın, yiğitte burun', 'iyi koşan atın karnı, yiğit erkeğin burnu büyük olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1186, 'attan düşene yorgan döşek, eşekten düşene kazma kürek', 'soylu kimse yüzünden başımıza gelen felaketi çabuk atlatırız, soysuz kimse yüzünden başımıza gelen felaketi kolay kolay atlatamayız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1187, 'attan inip eşeğe binmek', 'bulunduğu önemli görevden daha aşağı bir göreve alınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1188, 'av avlanmış, tav tavlanmış', 'olan olmuş, iş işten geçmiş, artık yapacak bir şey yok.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1189, 'av avlayanın, kemer bağlayanın', 'bir şey, onu elde etmenin yolunu bilenin; bir şeyden yararlanma, onu kullanmasını becerebilenin hakkıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1190, 'av köpeği avdan kalmaz', 'hazıra konmayı alışkanlık yapmış kimse her zaman bu yolu izler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1191, 'av vuranın değil, alanın', 'bir şeyden, sahibi değil de başkası yararlanıyorsa asıl sahip yararlanan kişidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1192, 'ava gelmez kuş olmaz, başa gelmez iş olmaz', 'kuşlar avlanmaktan kurtulamazlar, insanlar da hatıra, hayale gelmeyen çeşit çeşit felaketle karşılaşabilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1193, 'ava giden avlanır', 'çıkarını başkalarına zarar vermekte arayan kimse, o zarara kendisi uğrar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1194, 'avaz avaz bağırmak', 'var gücüyle bağırmak: ''İspanyol denizcisi hâlâ avaz avaz bağırıyordu.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1195, 'avazı çıktığı kadar', 'çok yüksek sesle: ''Avazı çıktığı kadar haykırmak istiyordu.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1196, 'avcı ne kadar hile (al) bilse ayı o kadar yol bilir', 'bir kişi başkasını alt etmek için çeşit çeşit ustalık kullanır ama karşısındaki de yenilmemek için türlü önlemler alır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1197, 'avradı eri saklar, peyniri deri', 'her şey, durumuna uygun yöntemlerle saklanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1198, 'avrat (kadın) malı, kapı mandalı', 'bir erkek, karısının malından yararlanmayı düşünmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1199, 'avrat tuz dedi mi ciğeri cız der', 'karısı herhangi bir şey isteyince ihtiyaçları karşılayamayan kocanın içi sıkıntıyla dolar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1200, 'avrat var, arpa unundan aş yapar; avrat var, buğday unundan keş yapar', 'iş bilen kadın, elverişsiz gereçle güzel şeyler meydana getirir, iş bilmeyen kadın ise en iyi gereci kullansa bile bir şey yapamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1201, 'avrat var ev yapar, avrat var ev yıkar', 'öyle kadınlar vardır ki bir aileye düzen verir, mutluluk getirir; öyle kadınlar da vardır ki ailenin düzenini, mutluluğunu bozarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1202, 'avucu (avuçları) kaşınmak', 'avucundaki kaşıntıyı bir yerden para geleceğine yormak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1203, 'avucunu yalamak', ' alay umduğunu ele geçirememek: ''Sen avucunu yalarsın! Beni daha fazla rahatsız etme, tamam mı?'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1204, 'avucunun içi gibi bilmek', 'bir yeri, bir şeyi çok iyi ve ayrıntılı olarak bilmek: ''Sizin analarınızın, babalarınızın hayat idealini avucumun içi gibi bilirim.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1205, 'avucunun içine almak', 'bir kimseyi baskı ve etkisi altına almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1206, 'avuç (avucunu) açmak', '1) dilenmek, para istemek: ''İki gündür yemek yemedim ama daha avuç açmadım.'' -N. Hikmet. 2) yardım istemek: ''Elinde böyle bir sanat varken herkes sana avuç açmaktan başka ne yapabilir?'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1207, 'avuç içi kadar', 'pek küçük, dar (yer).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1208, 'avukat tutmak', 'adli işlemleri gereğince yerine getirmek için bir avukata vekâletname verip onu yetkili kılmak: ''Kasabadan Bilâl Efendi''yi avukat tuttular.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1209, 'avurdu avurduna geçmek', 'çok zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1210, 'avurt satmak (avurt zavurt etmek)', '1) beceremeyeceği şeyleri becerebilecekmiş gibi konuşmak; 2) korkutucu sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1211, 'avurtları çökmek (birbirine geçmek)', 'çok zayıfladığı yüzünden belli olmak: ''Hüdai, Bayram''ın avurtları çökmüş solgun yüzüne bakarak bir varsayım yapmıştı.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1212, 'ay ayakta çoban yatakta, ay yatakta çoban ayakta', '1) çobanların akşam erken yatması, sabahleyin erken kalkması gerekir; 2) genel düzene yardımcı olan araçlar varsa yönetici rahat eder yoksa çok uyanık olması gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1213, 'ay aydın, hesap belli', 'anlaşılmayacak bir şey yok, hesap ortada, açık.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1214, 'ay gibi', 'ay parçası.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1215, 'ay görmüşün yıldıza minneti (itibarı) yoktur', 'bir şeyin çok üstününü, çok güzelini görmüş olan kimse, ondan daha az değerde olanını beğenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1216, 'ay (gün) var yılı besler, yıl var ayı (günü) beslemez', 'öyle zaman olur ki bir aylık kazanç insanı bir yıl geçindirir, öyle zaman da olur ki bir yıllık kazanç bir ay geçindirmeye yetmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1217, 'ay harmanlanmak', 'ayın çevresinde ayla oluşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1218, 'ay ışığında ceviz silkilmez', 'yeterli olmayan koşullarda yapılan işlerden, beklenilen verim alınamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1219, 'ayağa değmedik taş olmaz, başa gelmedik iş olmaz', 'insan, yaşamı boyunca çeşitli engellerle ve güçlüklerle karşılaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1220, 'ayağa fırlamak', 'hızla ayağa kalkmak: ''Derken balıkçı öfkeyle ayağa fırladı, kafese kapatılmış bir kaplan gibi dolandı güvertede.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1221, 'ayağa kaldırmak', 'telaş ve heyecana düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1222, 'ayağa kalkmak', '1) ayakları üzerinde durmak, dikilmek: ''Yeniden ayağa kalkıyorum, pencereye kadar gidiyorum.'' -A. Ümit. 2) hasta iyi olmak, iyileşmek; 3) saygı göstermek için oturma durumundan ayaküzeri durumuna geçmek; 4) harekete geçmek: ''O gün yer yerinden oynadı, bütün İstanbul''a ayağa kalkmıştı.'' -H. Topuz. 5) isyan etmek; 6) mec. telaşlanmak, telaşa kapılmak, heyecanlanmak: ''Bütün kahve halkı ayağa kalkıyor.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1223, 'ayağı almak', ' hlk. halay oyunlarında ayağı tempoya uydurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1224, 'ayağı (ayakları) dolaşmak', 'yürürken telaştan ayakları birbirine takılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1225, 'ayağı (ayakları) suya ermek', 'bir gerçeği anlayarak aklı başına gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1226, 'ayağı düze basmak', 'güçlükleri yenerek ilerisinden korkmayacak bir duruma girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1227, 'ayağı gitmemek', '1) gitmek istememek; 2) oynarken çalınan oyun havasının ritmine uygun hareket edememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1228, 'ayağı ile gelmek', '1) kendi isteğiyle gelmek; 2) emek çekilmeden elde edilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1229, 'ayağı yerden kesilmek', '1) ayağı yere değmez olmak; 2) bir taşıta binip yaya yürümekten kurtulmak; 3) mec. çok mutlu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1230, 'ayağı yürüten baştır', 'halkın düzen içinde çalışmasını baştakiler sağlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1231, 'ayağına (ayaklarına) kapanmak', '1) alçalırcasına yalvarmak: ''Sandılar ki ihtiyar bahçıvan, paçaları sıvayacak, yeğenine Rabia''yı almak için paşanın ayaklarına kapanacak.'' -H. E. Adıvar. 2) bağışlanmak için yalvarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1232, 'ayağına bağ olmak', 'birinin bulunduğu yerden ayrılmasına veya yaptığı işi sürdürmesine engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1233, 'ayağına bağ vurmak', 'önüne bir engel çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1234, 'ayağına çağırmak', 'yanına gelmesini istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1235, 'ayağına çelme takmak', '1) biri yürürken ayakları arasına ayak uzatıp düşürmek; 2) mec. birinin işinde yükselmesine engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1236, 'ayağına dolanmak (dolaşmak)', '1) başkasına yapmayı tasarladığı kötülük kendi başına gelmek; 2) iş yapmakta olan birine engel olmak, yürümesine engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1237, 'ayağına düşmek', 'çok yalvarmak: ''Obanın bütün kadınları, delikanlıları ayağına düştü.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1238, 'ayağına geçirmek', 'bir şeyi aceleyle giymek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1239, 'ayağına gelmek', '1) alçak gönüllülük göstererek birinin yanına gelmek; 2) emek çekilmeden elde edilmek: Kısmet ayağına geldi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1240, 'ayağına getirmek', 'sıra, saygı gözetmeksizin birinin yanına gelmesini sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1241, 'ayağına ip takmak', 'bir kimseyi çekiştirmek: ''Ara sıra ötekinin berikinin ayağına ip takmaktan başka konuşacak lakırtıları olmazdı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1242, 'ayağına kira istemek', 'gelmeye nazlanmak, üşenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1243, 'ayağına sağlık', '''gelmen çok memnun etti'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1244, 'ayağına sıcak su mu, soğuk su mu dökelim?', 'seyrek gelen bir konuğa yarı sitem, yarı sevinçle söylenen söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1245, 'ayağına sıkmak', 'ayağına ateş ederek tehdit amacıyla gözdağı vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1246, 'ayağına üşenmemek', 'hamarat olmak, ayak işlerini bıkmadan, yorulmadan yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1247, 'ayağında donu yok, fesleğen ister (takar) başına', 'yoksulluğuna bakmayarak süs ve gösteriş yapmak ister.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1248, 'ayağını alamamak', '1) ağrı veya uyuşma dolayısıyla ayağını oynatamamak; 2) alışılan bir yere gitmekten kendini alamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1249, 'ayağını (ayaklarını) öpeyim', ' hlk. ''yalvarırım'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1250, 'ayağını (ayaklarını) sürümek', '1) verilen bir işi ağırdan almak; 2) bir yerden uzaklaşmak üzere bulunmak; 3) halk inanışına göre bir kimse gelirken ardından başkalarının da gelmesine yol açmak; 4) ölmek üzere olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1251, 'ayağını bağlamak', 'engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1252, 'ayağını denk almak', '1) başkalarının kendisine yapma ihtimali bulunan kötülüklere karşı uyanık davranmak; 2) dikkat etmek: ''Ayağınızı denk alıp, bu sorunu bir an evvel çözümlemenizi istiyorum.'' -R. Mağden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1253, 'ayağını denk basmak', 'dikkatli ve uyanık davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1254, 'ayağını giymek', 'ayakkabısını giymek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1255, 'ayağını kaydırmak', 'bir yolunu bulup birini işinden veya görevinden uzaklaştırmak: ''Hatta vekilin bile ayağını kendisinin kaydırdığını iddia ediyor.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1256, 'ayağını sıcak tut, başını serin; gönlünü ferah tut, düşünme derin', 'hastalıktan korunmak istiyorsak ayağımızı sıcak, başımızı serin tutmalı, olur olmaz şeyleri sıkıntı konusu yapmamalı, geniş yürekli olmalıyız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1257, 'ayağını tek almak', 'bir işte iyi düşünüp dikkatli davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1258, 'ayağını yorganına göre uzat', 'giderini mutlaka gelirine uydurmalısın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1259, 'ayağının altına karpuz kabuğu koymak', 'bir yolunu bulup bir kimseyi düzenle işinden uzaklaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1260, 'ayağının (ayaklarının) altını öpeyim', '''yalvarırım'' anlamında kullanılan bir söz: ''Dadıcığım merhamet... Ayaklarının altını öpeyim...'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1261, 'ayağının bağını çözmek', '1) karısını boşamak; 2) sıkıntılı bir durumdan kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1262, 'ayağının bastığı yerde ot bitmez', 'uğradığı yere bereketsizlik, uğursuzluk getirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1263, 'ayağının pabucunu başına giymek', '1) dengi olmayan bir kimseyle evlenmek; 2) değersiz bir kimseyi üstün bir yere geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1264, 'ayağının tozu ile', 'yoldan gelir gelmez, henüz dinlenmeden: ''Halep''ten İstanbul''a döndüğü gün ayağının tozu ile devrin padişahını görmeye gitmişti.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1265, 'ayağının tozunu silmeden', 'ayağının tozu ile.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1266, 'ayak açmak (vermek)', 'âşıklar arasındaki tartışmalarda veya sıralı söyleyişlerde söze başlamak amacıyla kelime, kelimeler takımı, dize, beyit ile konuyu belirtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1267, 'ayak almadık taş olmaz, başa gelmedik iş olmaz', 'insan, yaşamı boyunca çeşitli engellerle ve güçlüklerle karşılaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1268, 'ayak almak', ' müz. hlk. ayak, çalınan çalgıya uymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1269, 'ayak atmak', '1) girmek: ''Kalabalıktan en hoşlanan insan vagona ayak attı mı derhâl bir inziva hastalığına tutulur.'' -R. N. Güntekin. 2) ilk kez gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1270, 'ayak bağı olmak', 'bir yere gidilmesine veya bir işin yapılmasına engel olmak: Bu çocuk bana ayak bağı oluyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1271, 'ayak basmak', '1) bir yere varmak, ulaşmak: ''Bu bahçeye ayak bastığım andan beri toprağın iyiliğini kendimde hissediyordum.'' -K. Bilbaşar. 2) girmek, gelmek, uğramak: ''Köy evinin içine ayak basar basmaz, elbette bir saman ve hafif tezek kokusu duyulur.'' -S. F. Abasıyanık. 3) mesleğe girmek; 4) bir yere bağlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1272, 'ayak basmamak', 'bir yere hiç uğramamak: ''Tevfik''in kızı, kendi evladı gibi büyüttüğüm çocuk, konağa ayak basmıyor.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1273, 'ayak çekmek', 'kandırmaya çalışmak, avutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1274, 'ayak diremek', 'bir düşünceyi, bir davranışı sonuna kadar sürdürmek, kendi tutumundan şaşmamak: ''İnek sütü içmemekte hep böyle ayak direyecek misiniz?'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1275, 'ayak oyununa gelmek', 'kandırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1276, 'ayak sürümek', '1) verilen bir işi ağırdan almak; 2) gönderilen yere isteği ile gitmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1277, 'ayak tutmak', ' hlk. 1) mâni yarışmalarında karşısındakine uyması gereken uyağı vermek: ''Mânicilerden biri ''gülerler'' diye bir ayak tutar, ona biri karşılık verir.'' -S. Birsel. 2) öncülük etmek; 3) söz açmak; 4) ileride söylenecek bir söze önceden zemin hazırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1278, 'ayak uydurmak', '1) yürüyüşte adım atışını başkalarınınkine uydurmak; 2) ayak açmak; 3) mec. kendi gidiş ve davranışını başkasınınkine benzetmek: ''Âdettir, genç kızlar girdikleri ailenin terbiyesine, gidişine ayak uydururlar.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1279, 'ayak üstünde olmak', '1) dinç olmak, canlı olmak: ''Enişte, delikanlıları gölgede bırakacak kadar çalıştı; hâlâ ayak üstünde.'' -S. M. Alus. 2) iş görür durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1280, 'ayak vermek', 'âşık atışmalarında dinleyicilerden biri uyak belirtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1281, 'ayak yapmak', 'birini aldatmak, kandırmak için dalavere çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1282, 'ayakaltına almak', 'hakir görmek, gözden çıkarmak: ''Bunlar kolay kolay ayakaltına alınamaz, değil mi?'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1283, 'ayakaltında bırakmak', 'ezilmesine, yok olmasına göz yummak, korumamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1284, 'ayakaltında dolaşmak', 'bir işe yaramadığı hâlde herkesin işine engel olacak bir biçimde ortalıkta dolaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1285, '(ayakkabı) ayağını vurmak', 'ayakkabı ayağını yara etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1286, 'ayakkabı vurmak', 'ayakkabı ayağı zedelemek, ayağı rahatsız etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1287, 'ayakkabılarını çevirmek', '1) konuk ayakkabılarını gidiş yönüne doğru düzgün bir biçimde sıralamak; 2) mec. bazı davranışlarla konuğu gitmeye zorlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1288, 'ayaklar altına almak', 'önem verilmesi gereken şeyleri hiçe saymak, çiğnemek: Şerefini, namusunu ayaklar altına aldı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1289, 'ayaklar baş, başlar ayak olmak', 'değersiz kimseler başa geçip değerli kimseler ise en geride bırakılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1290, 'ayakları geri geri gitmek', 'bir yere gönülsüz, istemeye istemeye gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1291, 'ayakları üstünde durmak', 'başkasının yardımına ihtiyaç duymadan güçlü bir biçimde sorunları çözebilecek durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1292, 'ayakları yere değmemek', 'çok sevinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1293, 'ayaklarına (ayağına) kara su (sular) inmek', 'çok yorulmak, güçsüz, dermansız kalmak: ''Bu şehirde akşama doğru / İçime korku / Ayaklarıma kara su iner'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1294, 'ayaklarını yerden kesmek', 'bir taşıta binerek yürümekten kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1295, 'ayakta kalmak', '1) oturacak yer bulamamak; 2) yıkılmamak, çökmemek: Bu yapı beş yüz yıldan beri ayakta kalmıştır. 3) değerini yitirmemek, önemini korumak: ''Ömrü boyunca bu vatan için, bu devletin ayakta kalabilmesi için çalıştı.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1296, 'ayakta uyumak', 'aşırı dalgın, şaşkın veya yorgun olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1297, 'ayaz kesmek', 'uzun süre soğukta kalıp üşümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1298, 'ayaz paşa kol geziyor', 'dışarıda çok soğuk var.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1299, 'ayaz vurmak', 'sebze ve meyveler donmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1300, 'ayazda kalmak', '1) soğukta kalmak; 2) argo boş yere beklemek; 3) argo eline bir şey geçmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1301, 'aydedeye misafir olmak', 'gece açıkta yatmak, geceyi açıkta geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1302, 'aygır gibi', 'iri yarı, cüsseli, güçlü (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1303, 'ayı gibi', '1) iri yarı; 2) kaba, anlayışsız (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1304, 'ayı gördüm, yıldıza itibarım (minnetim) yok', 'bir şeyin en iyisine alıştıktan sonra ondan aşağı olanlar beni ilgilendirmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1305, 'ayı görmeden bayram etme', 'bir iş gerçekleşmeden ona oldu gözüyle bakılıp sevinilmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1306, 'ayı sevdiği yavrusunu hırpalar', 'hırpalamak her zaman kötülük yapmak için olmaz, sevgiden kaynaklanan hırpalamalar da vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1307, 'ayı yavrusu ile oynuyor', ' alay iri ve yetişkin birinin ufak tefek birine, bir çocuğa el şakası yapması veya gücünü onda denemesi karşısında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1308, 'ayıbını yüzüne vurmak', 'birinin kusurunu yüzüne söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1309, 'ayıkla pirincin taşını!', 'bir işin pek karışık ve içinden çıkılmaz durumda olduğunu anlatmak için kullanılan bir söz: ''Karıya bir de nikâh yaptırdı mı ondan sonra ayıkla artık pirincin taşını!'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1310, 'ayılık etmek', 'kaba davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1311, 'ayılıp bayılmak', '1) birini kendinden geçercesine sevmek; 2) aşırı ölçüde sinir bunalımları geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1312, 'ayın on dördü gibi', 'yüzü çok güzel (kadın veya kız).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1313, 'ayının kırk türküsü var, kırkı da ahlat üstüne', 'bir kimsenin hep aynı şeyi veya hikâyeyi anlatması karşısında söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1314, 'ayınları çatlatmak', 'ayın harfinin Arapçaya özgü sesini gırtlakta boğumlamaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1315, 'ayıp kaçmak', ' argo uygun düşmemek: ''Daha ne sözler ki açıklayamam burada, ayıp kaçar.'' -M. Seyda.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1316, 'ayıpsız yâr arayan (dost isteyen), yârsız (dostsuz) kalır', 'kusursuz eş veya iş olmayacağı için böyle özellikte insan arayan eşsiz veya işsiz kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1317, 'ayıptır söylemesi', '1) ''bunu söylemek size karşı saygısızlık olacak ancak söylemek zorundayım'' anlamında özür dilemek için kullanılan bir söz: ''Ayıptır söylemesi, muzundan dalağına kadar ne bulurlarsa yedirdiler.'' -A. Ümit. 2) ''övünmek gibi olmasın ama'' anlamında kullanılan bir söz: Ayıptır söylemesi, akşam kuzu dolması yedik.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1318, 'ayıya kaval çalmak', 'anlayışsız bir kimseye bir şey anlatmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1319, 'ayıyı (maymunu) fırına (ateşe) atmışlar, yavrusunu ayağının altına almış', 'duygusuz insanlar, kendilerini kurtarmak için gerekiyorsa çocuklarını bile tehlikeye atmaktan çekinmezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1320, 'ayıyı vurmadan postunu satmak', 'henüz ele geçmemiş bir şey üzerinde hesap yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1321, 'aykırı düşmek', 'uygun gelmemek, ters gelmek, ters düşmek: ''Yüzük ona biraz aykırı düşen bir parlaklıkla parmağında parlıyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1322, 'aylak adam işidir', '''işsiz güçsüz adama uygun bir iştir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1323, 'aylığa geçmek', '1) çalışması karşılığı olarak her ay belirli bir para alınacak bir işe başlamak, maaşa geçmek; 2) gündelikten veya ücretten kadroya geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1324, 'aylık bağlamak', 'emekli olan veya başka sebeplerle çalışmayanlara her ay için belirli bir parayı ödemeyi üstlenmek, maaş bağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1325, 'ayna gibi', '1) dümdüz ve parlak; 2) kımıltısız, durgun (deniz).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1326, 'aynı ağzı kullanmak', 'aynı şeyi söylemek, aynı düşünceyi ileri sürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1327, 'aynı kapıya çıkmak', 'sonuç bakımından fark etmemek, aynı sonuca varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1328, 'aynı karede yer almak (bulunmak)', '1) kameranın çektiği görüntü içinde birlikte bulunmak; 2) mec. biriyle duygu ve düşüncesi aynı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1329, 'aynı potada erimek', 'benzer konuları ve sorunları birlikte düşünmek veya değerlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1330, 'aynı telden çalmak', 'aynı şeyi söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1331, 'aynı yolun yolcusu (olmak)', '1) kötü sonları birbirine benzer olan: ''O haspa da aynı yolun yolcusu, elbet birbirlerini kollayacaklar.'' -A. İlhan. 2) kaderleri, düşünceleri, davranışları birbirine benzer olan: ''Bu inanç aynı yolun yolcusu olmak niteliğini yitirecek ve siyasal rekabete dönüşecektir.'' -M. C. Anday.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1332, 'ayraç açmak', 'söz veya yazı içine, asıl konu ile ilgisi az olan bir bölüm sıkıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1333, 'ayranı kabarmak', '1) öfkelenmek, coşmak; 2) aşırı bir cinsel arzu duymak: ''Sadrazamın ayranı kabarsın diye üç gün beklenildikten sonra ... gelini, alayla, eşinin konağına iletmişlerdir.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1334, 'ayranı yok içmeye, atla (tahtırevanla) gider sıçmaya', ' kaba yoksulluğuna bakmadan gösteriş yapmaya kalkanların gülünçlüğünü anlatmak için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1335, 'ayranım budur, yarısı sudur', 'bir iş yarım yamalak yapıldığında özür dilemek için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1336, 'ayrı baş çekmek', 'topluluktan ayrılıp kendi başına iş yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1337, 'ayrı düşmek', '1) birbirinden uzakta kalmak: ''Köyümden, şehrimden ayrı düştüm.'' -A. Kabaklı. 2) mec. uyuşmamak: Düşüncelerimiz ayrı düşüyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1338, 'ayrı seçi yapmak', 'birkaç şey arasında fark gözetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1339, 'ayrı tutmak', 'farklı davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1340, 'ayrıcalık gözetmek', 'ayrıcalık tanımak: ''Annem, babam çocuklar arasında hiçbir ayrıcalık gözetmezlerdi.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1341, 'ayrıcalık tanımak (göstermek)', '1) birine özel hak vermek; 2) birini kayırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1342, 'ayrıntıda boğulmak', '1) gereksiz ayrıntılarla ilgilenmek zorunda kalmak; 2) mec. ilgilenilen herhangi bir konunun aslından uzaklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1343, 'ayrıntıya inmek', 'bir konuyu en küçük noktasına kadar inceleyip araştırmak: ''Ne var ki genelleyici bakış açısı, bizi bazen yararlı ayrıntılara inmekten ister istemez alıkoyuyor.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1344, 'ayrısı gayrısı olmamak', 'birbirinden hiçbir şey esirgemeyecek durumda olmak, samimi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1345, 'ayvayı yemek', ' argo kötü duruma düşmek, işi bozulmak: ''Ortak bir kaderimiz var biz kadınların, sonunda ayvayı yiyen biz oluyoruz.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1346, 'ayvaz, kasap hep bir hesap', ' hlk. ''ha öyle ha böyle, ikisi de bir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1347, 'ayyar tilki art ayağından tutulur', 'işini hile ile yürüten kimse sonunda yakayı ele verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1348, 'ayyuka çıkmak', '1) ses yükselmek: ''Camlar çerçeveler parçalanıyor, küfürler ayyuka çıkıyordu.'' -A. Ümit. 2) dedikodu herkesçe duyulmak, yayılmak: ''Rezalet ayyuka çıktı, bütün İstanbul bundan bahsediyor.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1349, 'az ateş çok odunu yakar', 'az sayıda kötü insan, çok sayıda iyi insanın başını belaya sokabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1350, 'az bulmak', 'yeterli görmemek, az saymak, azımsamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1351, 'az buz olmamak', 'bir şey azımsanacak kadar olmamak: ''Saçlarının tamamı ağarmış. Az buz değil üç yılı doldurduk birlikte.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1352, 'az değil', 'birinin herhangi bir karakter bakımından göründüğü gibi olmadığını anlatmak için söylenen bir söz: Sen de az değilsin, muziplikte ona taş çıkartırsın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1353, 'az el aş kotarır, çok el iş kotarır', 'yemek az kişiyle onun dışında kalan işler ise çok kişiyle daha çabuk yapılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1354, 'az eli aşta gör, çok eli işte gör', 'yemek az kişiyle onun dışında kalan işler ise çok kişiyle daha çabuk yapılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1355, 'az görmek', '1) umduğundan eksik bulmak; 2) azımsamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1356, 'az günün adamı olmamak', 'çok yaşamış, çok görmüş bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1357, 'az kaldı (kalsın)', '1) bir işin gerçekleşmesi söz konusuyken gerçekleşmemesi durumunda kullanılan bir söz: ''Zavallıyı az kalsın gırtlağından yakalayıp boğacaktı.'' -Y. K. Karaosmanoğlu. 2) bir işin gerçekleşmemesi söz konusuyken gerçekleşmesi durumunda kullanılan bir söz: ''Az kalsın açmadan onu da buruşturup atacaktı.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1358, 'az kaz, uz kaz, boyunca kaz', 'sana yapılmasını istemediğin bir kötülüğün daha ağırını başkasına yapma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1359, 'az olsun, uz (öz) olsun', 'yaptığınız iş, edindiğiniz şey az olabilir ancak temiz ve iyi olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1360, 'az söyle çok dinle', 'kişinin gereksiz konuşmaktansa az konuşması ve konuşulanları dinlemesi daha iyidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1361, 'az tamah çok ziyan getirir', 'hırslı ve pinti insan her zaman zararlı çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1362, 'az veren candan, çok veren maldan', 'varlıklı olmayan kimsenin yardım veya armağan olarak az şey vermesi büyük fedakârlıktır, varlıklı kimsenin vereceği armağan ve yardımlar fedakârlık sayılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1363, 'az yiyen az uyur, çok yiyen güç uyur', 'kişi iyi uyuyabilmek için pek az da yememeli, pek çok da.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1364, 'aza çoğa bakmamak', 'olanla yetinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1365, 'aza kanaat etmeyen çoğu hiç bulamaz', 'büyük şeyleri elde edebilmek için önce küçük şeylerle yetinmek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1366, 'aza sormuşlar: ''nereye?'', ''çoğun yanına'' demiş', 'küçük kazançların bile hep varlıklı kimselere düştüğü inancını belirten bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1367, 'azamet satmak', 'büyüklük taslamak, çalım satmak, böbürlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1368, 'azan kurda kızan köpek', 'belalı kişinin hakkından kötü kişi gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1369, 'azap çekmek', '1) eziyet çekmek, üzüntü içinde olmak; 2) ceza görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1370, 'azap duymak', 'acı çekmek, üzülmek: ''Bu şehrin, takdir fukaralarının orta malına dönüşmüş olmasından azap duyuyorum.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1371, 'azap vermek', 'acı çektirmek, üzmek: ''Bu düşünce ona epeyce azap verdi.'' -A. Midhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1372, 'azar işitmek', 'azarlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1373, 'azı bilmeyen çoğu hiç bilmez', 'küçük de olsa bir iyiliğin değerini bilmeyen, daha büyük iyiliklere layık değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1374, 'azı çoğa saymak (tutmak)', 'verilen küçük bir armağanı çok beğenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1375, 'azıcık aşım, ağrısız (kaygısız) başım', 'derdim olmasın da başka bir şey istemem.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1376, 'azıksız yola çıkanın gözü el torbasında kalır', 'ileride gereksinim duyacağı şeyleri zamanında hazırlamayan kişi, hazırlık yapan diğer insanlardan yardım bekler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1377, 'azınlıkta kalmak', 'bir toplulukta belli bir sorun üzerine oy verenler, karşı düşünceye oy verenlerden daha az olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1378, 'azizlik etmek', 'muziplik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1379, '... azmanı', '...''nın çok gelişmişi, iri yapılısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1380, 'azmış kudurmuştan beterdir', 'coşkun ve heyecana kapılmış kimseyi zapt etmek zordur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1381, 'aznavur gibi', 'zalimce davranan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1382, 'Azrail gelince oğul, uşak sormaz', 'Azrail büyük küçük demez, eceli gelenin canını alır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1383, 'Azrail''e bir can borcu olmak (kalmak)', '1) nasıl olsa öleceğini kabul etmek; 2) hiç kimseye borcu kalmamak, bütün borçlarından kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1384, 'Azrail''in elinden kurtulmak', 'ölümden kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1385, 'Azrail''le burun buruna gelmek', 'ölümle karşı karşıya gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1386, 'baba değil, tırabzan babası', 'çocuklarına karşı babalık görevlerini yerine getirmeyen, onlara hayrı olmayan baba.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1387, 'baba (evlat, oğul) ekmeği zindan ekmeği, koca (er) ekmeği meydan ekmeği', 'bir kadın için babasının veya çocuğunun evinde barınıp onların eline bakmak çok kötü bir durumdur; onun gönül ferahlığı ile yaşayacağı yer, kocasının evi, serbestçe harcayacağı para kocasının parasıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1388, 'baba himmet, oğul hizmet', 'büyüklerin, kendilerine el uzatıp yardım etmelerini istemeye hak kazanabilmek için küçüklerin görevlerini iyi yapmaları gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1389, 'baba kırk oğul beslemiş, kırk oğul bir babayı beslememiş', 'baba kaç çocuğu olursa olsun, hiçbir ayrım yapmadan hepsine bakar, onları büyütür; çocukları ise yoksul ve yaşlı durumdaki babalarının bakımını ''sen bak, o baksın'' gibi gerekçelerle bir türlü sağlayamazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1390, 'baba koruk (erik) yer, oğlunun dişi kamaşır', 'babanın yaptığı kötü işin sıkıntısını çocuğu çeker.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1391, 'baba malı tez tükenir, evlat gerek kazana', 'kendini bilen, yaşama sorumluluğu duyan akıllı evladın gerçek malı, kendisinin kazandığı maldır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1392, 'baba oğluna bir bağ bağışlamış; oğul babaya bir salkım üzüm vermemiş', 'babalar çocukları için büyük fedakârlıklara katlanırlar ancak çocuklar babaları için fedakârlıkta bulunmazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1393, 'baba vergisi görümlük, koca vergisi doyumluk', 'bir babanın kızı için harcadığı para, hazırladığı çeyiz göstermelik olmaktan ileri gidemez, kızın yaşam boyu süren giderlerini kocası üzerine almıştır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1394, 'babalık fırın has işler', 'babasının parası ile geçinenlere sitem olarak kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1395, 'babamın adı Hıdır, elimden gelen budur', 'gücüm ancak bu kadarını yapmaya yeter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1396, 'babana rahmet', ' tkz. yapılan bir iş, bir davranış karşısında ''Allah senden razı olsun.'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1397, 'babanın sanatı oğla mirastır', 'bir evlat babasının sanatını onun ölümünden sonra sürdürür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1398, 'babasından mal kalan, merteği içinden bitmiş sanır', 'miras yoluyla mal edinen kimse, onun için ne denli çabalar gösterilip sıkıntı çekildiğini bilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1399, 'babasının (babalarının) çiftliği', 'bir malı veya kuruluşu yalnızca kendi çıkarlarına araç yapanlar için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1400, 'babasının hayrına', 'hiçbir çıkar gözetmeksizin: Herkes babasının hayrına çalışmıyor ya!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1401, 'babasının kızı', 'her yönüyle babasına benzeyen kız çocuğu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1402, 'babasının oğlu', 'her yönüyle babasına benzeyen erkek çocuğu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1403, 'babasız oğlan doğurmak', 'bir işte aşırı zorluk, büyük güçlük çekilmesine rağmen başarılı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1404, 'babayla oğlanın pabucu bir olunca evde kavga eksik olmaz', 'ortaklaşa kullanılan bir mal, kimi zaman baba ile oğlu arasında bile kavgaya neden olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1405, 'baca eğri de olsa dumanı doğru çıkar', 'yaradılıştan iyi ve doğru olan kimse, ne denli elverişsiz ortam içinde bulunursa bulunsun niteliğini yitirmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1406, 'bacağına geçirmek', 'bir şeyi aceleyle giymek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1407, 'bacak kadar', 'ufacık: ''Bacak kadar çocuğa da ne oluyordu sanki.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1408, 'bacak kadar boyu var, türlü türlü huyu var', 'yaşı küçük ancak herkesten farklı alışkanlıklar, huylar edinmiş.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1409, 'bacakları kopmak', 'çok yorulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1410, 'bacakları tutmaz olmak', 'yürüyemeyecek duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1411, 'bacaklarını uzatmak', 'hiçbir şey yapmadan, hiçbir şeyle ilgilenmeden oturmak, tembel tembel zaman öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1412, 'bacası tütmek', 'ailenin yaşamı sürüp gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1413, 'bacası tütmez olmak', 'aile dağılmak veya işi bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1414, 'badem gibi', 'taze ve gevrek (salatalık).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1415, 'badem olmak', ' argo sonu kötü olmak, kötü bitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1416, 'bağ babadan, zeytin dededen kalmalı', 'bağ, bir kuşak geçecek kadar yaşlandıktan sonra bol ürün verir, zeytinin bol ürün verebilmesi için en azından iki kuşaklık bir zaman geçmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1417, 'bağ bayırda, tarla çayırda', 'her şey kendisi için en elverişli ortamda gelişir, verimli duruma gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1418, 'bağ bozmak', 'bağın üzümlerini toplamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1419, 'bağa bak, üzüm olsun, yemeye yüzün olsun', 'kişi, karşılık beklediği işten istediğini alabilmek için gereken harcamaları yapmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1420, 'bağı ağlayanın yüzü güler', 'bir işe gereken özen gösterildiğinde olumlu sonuçlar alınır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1421, 'bağın taşlısı, karının saçlısı', 'kadının saçlı olanı ile tarlanın taşlı olanı makbuldür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1422, 'bağın vurmak', 'çökmemesi için kazı duvarlarını bağınlarla desteklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1423, 'bağırıp çağırmak', 'öfkeyle bağırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1424, 'bağırsakları bozulmak', 'ishal olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1425, 'bağışıklık kazanmak', '1) bazı mikroplara karşı aşı veya doğal yolla dirençli duruma gelmek; 2) mec. korunaklı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1426, 'bağlanıp kalmak', 'tutulmak, sevdalanmak: ''Bunca güzellere bağlandım kaldım / Ne bir vefa aldım ne faydalandım'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1427, 'bağlı kalmak', 'uymak, tabi olmak: ''Atatürk ilke ve inkılaplarına ve laik Cumhuriyet ilkesine bağlı kalacağıma ant içerim.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1428, 'bağrı yanmak', '1) üzüntü çekmek, çok acı duymak: ''En büyüğünü kaybeden halk sanatkârının birkaç mısrası ile türkü bize bağrı yanan Anadolu''nun feryadını getirecek.'' -B. R. Eyuboğlu. 2) çok susamış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1429, 'bağrına basmak', '1) kucaklamak: ''İzmir''den kalkıp Mısır''a kadar beni görmeye, beni okşamaya, beni bağrına basıp sevmeye gelirdi.'' -Y. K. Karaosmanoğlu. 2) biriyle ilgilenerek onu koruyup kayırmak, yetiştirmek: ''Sen onu bambaşka duygularla, heyecanlarla bağrına basmak isteyeceksin.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1430, 'bağrına taş basmak', 'sesini çıkarmaksızın her türlü acıya katlanmak: ''Acı çekerdim ama makul bir çocuktum. Bağrıma taş bastım.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1431, 'bağrını delmek', 'çok dokunmak, içine işlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1432, 'bağrını ezmek', 'üzülmek, dertlenmek: ''Kışlanın uğrunda bir ufak mezar / Anama söylemen bağrını ezer'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1433, 'baharı başına vurmak', ' alay gençliğin verdiği coşkuyla gereksiz veya aşırı davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1434, 'bahis açmak', 'belli bir konuda konuşmaya başlamak: ''Senden bahis açılmadıkça susmak isterim.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1435, 'bahse girmek (tutuşmak)', 'görüşünde veya iddiasında haklı çıkacak tarafa bir şey verilmesini kabul eden sözlü anlaşma yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1436, 'bahsi geçmek', '1) bir konu üzerinde konuşulmuş olmak; 2) söz konusu edilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1437, 'bahsi kapamak', 'bir konu üzerindeki konuşmayı kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1438, 'bahsi kaybetmek', 'ileri sürülen, savunulan görüşün yanlış olduğu ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1439, 'bahsi kazanmak', 'ileri sürülen, savunulan görüşün doğru olduğu belli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1440, 'bahsi tazelemek', 'konuşmayı aynı konu üzerine getirmek: ''İkide bir, bahsi tazeleyip bir yandan da etrafı araştırıyordu.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1441, 'bahşiş (beleş) atın dişine (yaşına) bakılmaz', 'para verilmeden sağlanan bir şeyin ufak tefek kusurları hoş görülmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1442, 'baht (akıl) olmayınca başta, ne kuruda biter ne yaşta', 'kişi talihsiz olursa giriştiği hiçbir işten olumlu sonuç alamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1443, 'bahtı açık olmak', 'herhangi bir konuda şansı yaver gitmek, talih yüzüne gülmek: ''Zaten başak burcunda doğmuş ananın kızında evlenme bahtı açık olur.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1444, 'bahtı açılmak', 'talihi dönüp uygun duruma veya arzulanan sonuca gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1445, 'bahtı bağlı olmak', '1) talihi kapalı olmak; 2) kız için evlenecek istekli çıkmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1446, 'bahtı kapanmak', '1) talihsizliğe uğramak, istenen sonuca ulaşmamak; 2) evlenememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1447, 'bahtı kara olmak', 'sürekli olarak talihi yaver gitmemek, mutsuz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1448, 'bahtına küsmek', 'talihsizliğinden yakınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1449, 'bahtsızın bağına yağmur, ya taş yağar ya dolu', 'talihsizin işleri ters gider, bağına yağmur yerine taş veya dolu yağar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1450, 'bakan göze bağ (yasak) olmaz', 'herkesin gözü önündeki şeye bakılması önlenemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1451, 'bakan yemez, kapan yer', 'bir şey yalnızca bakmakla elde edilemez, onu ele geçirmek için davranmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1452, 'bakarsan bağ olur, bakmazsan dağ (olur)', 'bakılıp onarılan şeylerden yararlanılır, bakımsız bırakılanlardan bir yarar elde edilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1453, 'bakır çalmak', 'bakır kapta oluşan bakır tuzları nedeniyle yemek insanı zehirlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1454, 'bakış atmak', 'kısa bir süre bakıp geçmek: ''İki yanından bağrışanlara anlamadığı bir dilden konuşuyorlarmış gibi birer bakış attı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1455, 'bakkala bırakma!', 'bir işi ''bakalım'' diyerek savsaklamak isteyenlere söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1456, 'bakla dökmek (atmak)', 'bakla ile fala bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1457, 'bakla kadar', 'çok iri (böcek).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1458, 'baklava börek olsa yemem', 'fazlasıyla tok olunduğunda söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1459, 'baklayı ağzından çıkarmak', 'açık söylemekten kaçındığı bir sorunu sonunda açıklamak: ''Bırak muamma konuşmayı / Çıkar ağzından baklayı / Bahtımız aydınlanıversin'' -C. S. Tarancı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1460, 'bakmakla usta olunsa (öğrense), köpekler (kediler) kasap olurdu (kasaplığı öğrenirdi)', 'yapılmadan yalnızca nasıl yapıldığı görülerek hiçbir şey öğrenilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1461, 'baktıkça alır', '''güzelliği birdenbire göze çarpmaz'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1462, 'baktın kar havası, eve gel kör olası', 'tehlikeli bir durum belirmeye başlayınca ondan uzaklaşmanın çaresine bakılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1463, 'bal alacak çiçeği bilmek (bulmak)', 'çıkar sağlanabilecek yeri veya şeyi bilmek, bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1464, 'bal bal demekle ağız tatlanmaz', 'sözde kalan dilek ve tasarıların iş bitirmede hiçbir etkisi olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1465, 'bal dök de yala', 'bir yerin çok temiz olduğunu anlatan bir söz: ''Nuri, şöyle böyle ama teyzen çok temiz bir kadına benziyor. Evin her tarafına bal dök de yala.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1466, 'bal gibi', '1) pek tatlı; 2) şüpheye yer bırakmadan, çok iyi, adamakıllı: ''Hepsi o kadar sahici ki telefonun öbür ucundaki, bal gibi inanıyor.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1467, 'bal ile kaymak yenir ama her keseye göre değil', 'güzel yemeyi, güzel giymeyi, güzel eşya kullanmayı herkes ister ama bunları ancak parası bol olanlar yapabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1468, 'bal mumu gibi erimek', 'çok zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1469, 'bal mumu yapıştırmak', 'söz, davranış vb.nin unutulmaması için bir işaret koyup dikkati çekmek: ''Pervin''in şimdilik bu sözüne bir bal mumu yapıştırarak tekrar Bedia yengeye döndüm.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1470, 'bal olan yerde sinek de olur (bulunur)', 'güzel şeylerin çevresinde, ondan yararlanmak isteyen asalaklar dolaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1471, 'bal tutan parmağını yalar', 'imkânları geniş bir işin başında bulunan kimse bunlardan az da olsa yararlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1472, 'balcı kızı daha tatlı', 'güzel mal satan kimselerden alınan şeyler daha çok hoşa gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1473, 'balcının var bal tası, oduncunun var baltası', 'her işin kendine özgü aracı vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1474, 'balı dibinden, yağı yüzünden', 'değerleri derinleştikçe artan veya değerleri yüzeyde kalan insanlar vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1475, 'balı olan bal yemez mi?', 'bir kimsenin elinde başkasına verilecek veya satılacak bir şey bulunması, ondan kendisinin de yararlanmasına engel değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1476, 'balı parmağı uzun yemez, kısmetlisi yer', 'güzel bir şey, onu isteyen ve elde edecek gibi görünenin değil kısmeti olanın eline geçer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1477, 'balık ağa girdikten sonra aklı başına gelir', 'insan, tedbirsizliği yüzünden bir felakete uğradıktan ve iş işten geçtikten sonra neden şöyle yapmadım, neden böyle yapmadım diye üzülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1478, 'balık baştan avlanır', 'bir şeyi ele geçirebilmek için onu yönetenleri elde etmek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1479, 'balık baştan kokar', 'bir işte aksaklık başta olanlardan kaynaklanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1480, 'balık demiş ki ''etimi yiyen doymasın, avımı yapan gülmesin (onmasın)''', 'balık çok lezzetlidir etine doyum olmaz ama balık avcıları hep geçim darlığı içindedir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1481, 'balık kavağa çıkınca', ' alay hiçbir zaman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1482, 'balın âlâsı (tazesi) oğlun tazesinden', 'ana baba için en tatlı şey, çocuklarıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1483, 'ballı börek olmak', 'çok iyi anlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1484, 'balo vermek', 'balo hazırlamak, düzenlemek: ''İki ay sonra sahici bir balo vereceğiz.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1485, 'balon uçurmak', 'ilgililerin ne diyeceklerini ve nasıl davranacaklarını anlamak amacıyla aslı olmayan bir haber yaymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1486, 'balon yapmak', 'bisiklet, araba vb.nde lastiğin yüzeyinde şişlik oluşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1487, 'balta değmedik ağaç olmaz', 'zarar görmeyen, başına felaket gelmeyen kimse yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1488, 'balta değmemiş (girmemiş, görmemiş)', 'içinden hiç ağaç kesilmemiş, sık ve gür (orman, koru).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1489, 'balta vurmak', 'balta ile kesmek, parçalamak: ''Sakın kesme, yaş ağaca balta vuran el onmaz.'' -M. E. Yurdakul.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1490, 'baltası kütükten çıkmak', 'bir engelden, bir sıkıntıdan kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1491, 'baltayı taşa vurmak', 'farkında olmayarak birine dokunacak sözler söylemek, pot kırmak: ''Baltayı taşa mı vurduk, diyor, iyice görmemiş olacağım.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1492, 'balya yapmak', 'balyalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1493, 'balyoz gibi', 'çok ağır, ezici (kol veya yumruk).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1494, 'bana bak!', ' tkz. ''beni dinle'' anlamında kullanılan bir seslenme ve gözdağı sözü: Bana bak, karışmam sonra!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1495, 'bana da ... demesinler', 'bir işin kesinlikle yapılacağını belirtmek için söylenen bir söz: ''Fırat''ın iki yakasını birbirine bağlamazsam bana da vali demesinler.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1496, 'bana da mı lolo', 'bir sözün, bir tutumun veya davranışın gerçek ve geçerli olmadığını, başkalarının söz konusu olayda aldanabileceğini ancak söz sahibinin aldanmayacağını, aldatılamayacağını belirten bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1497, 'bana dokunmayan (beni sokmayan) yılan bin yaşasın', 'birçok kimse, kendilerine kötülüğü dokunmayan kişiye ilişmek istemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1498, 'bana mısın dememek', 'hiçbir şey etkili olmamak, hiçbir şeye aldırış etmemek: ''Kilidi çekiştiriyorum, bana mısın demiyor.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1499, 'banda almak', 'bir sesi, ses cihazı ile bant üzerine kaydetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1500, 'banka gibi', 'çok zengin (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1501, 'bankaya yatırmak', 'bankadaki hesabına para koymak, biriktirmek: ''İyi kazanmıyordu fakat ne kazanıyorsa hepsini bankaya yatırıyordu.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1502, 'banko geçmek', 'yarışlarda, toto, loto vb. oyunlarda, bir atın veya sayının kesin olarak tutturulacağını tahmin edip işaretlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1503, 'bant çözmek', 'manyetik bir bant üzerine alınmış sesleri yazıya aktarmak, deşifre etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1504, 'bant doldurmak', 'bir banda ses kaydetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1505, 'bar tutmak', 'bar oynamak için hazırlanmak ve oyuna başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1506, 'barajı aşmak', 'herhangi bir sebeple konulmuş olan koşulu yerine getirip başarı sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1507, 'bardağı taşıran damla', 'sabır tüketen aşırı davranış veya durum: ''Son tartışmamızın bardağı taşıran damla olduğu belli oluyordu.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1508, 'bardağı taşırmak', 'sabrını tüketmek: Son davranışı bardağı taşırmaya yetti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1509, 'bardaktan boşanırcasına yağmak', 'yağmur çok şiddetli yağmak: ''Sabahleyin yağmur bardaktan boşanırcasına yağıyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1510, 'barış görüş olmak', ' hlk. her türlü dargınlığı unutarak barışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1511, 'barut fıçısı gibi', '1) çok kızgın, sinirli (kimse): ''Sadi Nahit''i delice kıskanıyordu, içi bir barut fıçısı gibi hasetle doluydu.'' -T. Buğra. 2) kavgaya yol açacak (durum).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1512, 'barut gibi', '1) öfkeli, huysuz, sert, aksi (kimse): ''Hocamız barut gibi sert bir adam.'' -H. R. Gürpınar. 2) pek ekşi; 3) acı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1513, 'barut kesilmek (olmak)', 'çok öfkelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1514, 'barut kokusu gelmek', 'savaş tehlikesi sezilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1515, 'barutla ateş bir yerde durmaz', 'kızla erkeğin baş başa yalnız kalmaları kötü sonuçlar doğurabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1516, 'barutla oynamak', 'tehlikeli işlerle uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1517, 'bas tutmak', 'ince sesli çalgılara tek perdeden eşlik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1518, 'basamak yapmak', 'bir kişiyi, bir durumu bulunduğu konumdan daha yükseğine erişmek için araç olarak kullanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1519, 'basıp geçmek', '1) önde gideni geçmek: ''Yastık koşusunu kazanan tayın, biraz idman edilirse çok atları basıp geçeceğini konuşuyorlardı.'' -M. Ş. Esendal. 2) önem vermeyerek uğramamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1520, 'basıp gitmek', 'birdenbire gitmek, aklına koyduğu şeyi yapmak üzere bulunduğu yerden uzaklaşmak, çekip gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1521, 'basireti bağlanmak', 'iyi düşünemez, gerçeği göremez bir duruma düşmek: ''Bazen en mahir canilerin bile böyle mühim nisyanlarda bulunacak kadar basiretleri bağlanır.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1522, 'baskı yapmak', '1) bir kimseyi bir işi yapmaya zorlamak, zor kullanmak; 2) sp. oyuncunun rahat hareket etmesini engellemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1523, 'baskıda kalmak', 'yağmur yağdıktan sonra toprağın üst kısmı sertleşerek tohumlar fidelenip toprak üstüne çıkamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1524, 'baskıdaki altından askıdaki salkım yeğdir', 'kullanılan, işe yarayan değersiz şey, saklanan ve kullanılmayan çok değerli şeyden daha iyidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1525, 'baskın basanındır', 'düşmanı gafil avlayıp saldıran taraf savaşı kazanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1526, 'baskın çıkmak (gelmek)', 'karşılaştırma konusu olan kimseyi geçmek, ona karşı üstünlüğünü göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1527, 'baskın vermek', 'ani ve habersiz girmek, saldırıda bulunmak: ''İbiş''in odasına cennet kuşları baskın vermişti.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1528, 'baskın yapmak', '1) suç işlendiği veya suçluların bulunduğu sanılan bir yere ansızın girmek; 2) düşmana ansızın saldırmak; 3) mec. ansızın konuk gelmek: ''Behçet''e bu evin merdiven altındaki bakla gibi odasında baskın yaparlar.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1529, 'baskına uğramak', '1) düşmanın beklenmedik bir saldırısıyla karşılaşmak; 2) bir yerde suçüstü yakalanmak; 3) beklenmedik bir zamanda konuklar gelmek: ''Ne çeşit, ne türlüsü olursa olsun baskına uğramayı isteyen olmaz.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1530, 'baskısız tahtayı yel alır, yel almazsa sel alır', 'kontrol altında bulundurulmayan veya gereği gibi korunmayan gençler kötü yollara sürüklenebilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1531, 'baskısız yongayı yel alır; sahipsiz tarlayı sel alır', 'kontrol altında bulundurulmayan veya gereği gibi korunmayan gençler kötü yollara sürüklenebilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1532, 'bastığı yeri bilmemek', '1) çok sevinmek; 2) şaşkınlıktan nerede olduğunu seçememek, durumunu kontrol edememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1533, 'baston (baston yutmuş) gibi', 'dimdik duran veya yürüyen (kimse): ''Omuzlarını kısıyor, kafasını dimdik tutuyor, baston yutmuş gibi katılaşıyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1534, 'baş ağır gerek, kulak sağır', 'kişi ağırbaşlı olmalı ve dedikoduları dinlememelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1535, 'baş ağrıtmak', 'tedirgin etmek, bıkkınlık vermek, can sıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1536, 'baş aşağı düşmek', 'kişiliğinden kaybederek toplum içindeki durumu sarsılmak: ''Onun için hayatın bütün kanunu, bütün manası bu baş aşağı düşüşteydi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1537, 'baş aşağı etmek', 'tersine çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1538, 'baş aşağı gelmek', 'tepesi üstü düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1539, 'baş aşağı gitmek', 'işleri ters gitmek, sürekli zarar etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1540, 'baş bağlamak', '1) başına bir örtü örtmek; 2) başak vermek; 3) birine veya bir şeye bağlanmak, intisap etmek; 4) hlk. nişanlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1541, 'baş başa bağlı, baş da şeriata (yasaya, padişaha)', 'biz yöneticinin buyruğu altındayız ama yönetici de toplum için konulmuş olan kanunlar ne diyorsa onu uygular, onun dışına çıkamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1542, 'baş başa bırakmak', 'birinin, bir şeyle veya bir kimseyle yalnız kalmasını sağlamak: ''İçten içe bu duruma memnun olarak onları kavgalarıyla baş başa bıraktım.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1543, 'baş başa olmak', 'birlikte bulunmak, beraber yaşamak: ''Keyfimizce yaşamamıza mâni olur, baş başa olmamızı tercih ederim.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1544, 'baş başa vermek', '1) iki veya daha çok kimse bir kenara çekilip konuşmak: ''Nahiye müdürü, mebus ve belediye reisi ile baş başa vererek bir şeyler konuşuyor.'' -R. N. Güntekin. 2) dayanışmak: ''Neydi onunla böyle sıkı fıkı baş başa vermen, gizli planlar kuracak tenha köşelere çekilmen?'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1545, 'baş (başı) çekmek', 'herhangi bir konuda önde gitmek, önayak olmak: ''Hacı Reşit''in dükkânında post kuran orta yolcular arasında Muallim Naci başı çeker.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1546, 'baş bulmak', 'kazanç bırakmak: Bu fiyata verirsem baş bulmaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1547, 'baş çevirtmek', '1) başı arkaya doğru döndürtmek; 2) mec. birinin arkasından hayranlıkla baktırmak: ''Uzun boyu, kumral saçları, sevimli çehresiyle birçok kadınlara sokakta baş çevirtiyordu.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1548, 'baş dille tartılır', 'kişinin aklı, söylediği sözlerle ölçülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1549, 'baş döndürmek', 'başarıdan, gururdan, sevinçten çok mutlu duruma getirmek, aşırı heyecanlandırmak: ''Ordu karargâhına giriş, artık bir mabede çıkılıyor gibi baş döndürür.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1550, 'baş edememek', '1) gücü yetmemek; 2) engel olamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1551, 'baş eğmek', '1) saygı göstermek için baş eğerek selamlamak: ''Ulema, şeyhler, yerden selam verdiler, baş eğip el öptüler.'' -R. E. Ünaydın. 2) direnmekten vazgeçip buyruk altına girmek: ''Gittikçe yükselen başı Allah''a kalkıyor / Asrın baş eğdi sandığı at şaha kalkıyor'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1552, 'baş eldeyken', 'ölmeden, yaşarken, sağken.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1553, 'baş etmek', '1) gücü yetmek: ''Ben onlarla baş etmeye çalışıyordum ki Hasan''ın kapısı birden açıldı.'' -E. Bener. 2) başarı kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1554, 'baş gelmek', 'yenmek, gücü yetmek: Bir orduya baş gelir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1555, 'baş göstermek', 'belirmek, ortaya çıkmak, zuhur etmek, vuku bulmak: ''Komün üyeleri arasında sorunlar baş göstermeye başladı.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1556, 'baş göz etmek', ' hlk. evlendirmek: ''Oğullarının artık normal bir yaşam süreceğini sanan anne baba ona güzel de bir kız bularak baş göz etmişler.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1557, 'baş göz olmak', ' hlk. evlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1558, 'baş kes yaş kesme', 'ağaç kesmek, insan öldürmek kadar büyük bir suçtur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1559, 'baş kesmek', 'selam vermek için baş eğmek: ''Gülerken de göğsünün sağ köşesine baş kesmeyi unutmaz.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1560, 'baş kıç vurmak', ' den. baştan gelen dalgalarla gemi, başı ve kıçı üzerinde inip kalkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1561, 'baş kırılır (yarılır) fes (börk) içinde, kol kırılır yen (kürk) içinde', 'aile içindeki, arkadaşlar arasındaki uyuşmazlıklar yabancılara duyurulmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1562, 'baş koşmak', 'bir işi başarmak için çalışmak: ''Artık evde herkesten fazla bağırıp gülmüyor, çocuklarla eskisi gibi baş koşmuyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1563, 'baş nereye giderse ayak da oraya gider', 'küçükler büyüklerin izinde gider, her işte onları örnek tutarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1564, 'baş ol da eşek başı (soğan başı) ol', 'en önemsiz işlerde bile baş olmak, buyruk altında bulunmaktan iyidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1565, 'baş ol da istersen soğan başı ol', 'küçük bir işte de olsa başta olmak önemlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1566, 'baş olan boş olmaz', '1) bir yerde baş olan kimse taşıdığı değer dolayısıyla o yere gelmiştir; 2) işbaşındaki kişinin işi daima çoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1567, 'baş olmak', '1) küçük bir işte de olsa başta olmak, sözü dinlenir bir kimse olmak; 2) önde gelmek, lider olmak: ''Hep baş olmaya bakarız ve olduktan sonra nasihat veririz.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1568, 'baş sağlığı, dünya varlığı', 'en büyük zenginlik, beden sağlığıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1569, 'baş sallamakla kavuk eskimez', 'bir kimsenin suyuna gitmekten, söylediklerine ''evet, peki'' demekten zarar gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1570, 'baş tacı etmek', 'çok sevmek ve saymak, el üstünde tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1571, 'baş üstünde tutmak', 'çok iyi ağırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1572, 'baş üstünde yeri var', '''büyük bir saygı ve ilgi ile karşılanır veya ağırlanır'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1573, 'baş vermek', '1) çıban olgunlaşmak; 2) buğday vb. bitkiler başak bağlamaya başlamak, başak oluşmak; 3) gemi, kayık vb.ni döndürmek, çevirmek: ''En sonunda rüzgârların istikametine baş verdi.'' -S. F. Abasıyanık. 4) ortaya çıkmak, belirmek: ''Fransızlardan, neler olup bittiğini, neden müttefikler arasında bazı sorunların baş vermiş olduğunu öğrenmeleri isteniyordu.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1574, 'baş yakmak', 'kötü duruma düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1575, 'baş yastığı baş derdini bilmez', 'insanın derdi içindedir, en yakını bile onu anlamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1576, 'baş yemek', '1) birinin ölümüne veya yok olmasına sebep olmak; 2) birinin güç duruma düşmesine yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1577, 'başa baş gelmek (kalmak)', '1) eşit olmak, denk olmak; 2) berabere kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1578, 'başa gelen çekilir', 'çaresiz durumlara düşüldüğünde insan kendini üzüntüye kaptırmayıp bu durumlara sabır göstermelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1579, 'başa gelmek', 'kötü bir durumla karşı karşıya kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1580, 'başa gelmeyince bilinmez', 'başına bir felaket gelmeyen, başkasına gelen felaketin ne denli acı olduğunu gereği gibi anlayamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1581, 'başa güreşmek', '1) yağlı güreşte, en usta pehlivanlar başpehlivanlık için yarışmak; 2) mec. en üstün sonucu elde etmek veya bir işte en üst noktaya gelmek için mücadele vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1582, 'başa vermek', ' hlk. değiş tokuş yaparken üste bazı şeyler vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1583, 'başa yazılan gelir', 'kişi, kaderi ne ise onu görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1584, 'başağrısı olmak', 'sıkıntı vermek, uğraştırmak: ''Efendim nemize lazım, sonra size başağrısı olur.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1585, 'başak bağlamak (tutmak)', 'arpa, buğday, yulaf vb. ekinlerde başak oluşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1586, 'başarı göstermek (kazanmak)', 'başarmak: ''Arandığı, fikri sorulduğu, başarı kazandığı da oluyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1587, 'başarısızlığa uğramak', 'başarısız olmak: ''Paşa acele bir taarruzun başarısızlığa uğramasından çekinmektedir.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1588, 'başı ağrımak', 'sorunu olmak, sıkıntı içinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1589, 'başı bağlanmak', '1) evlendirilmek; 2) birini yandaş olarak kazanmak, kendi yanında tutmak: ''Başı bağlananların vekillerine birer samur kürk gelmiştir.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1590, 'başı belada olmak', 'çözülmesi güç, sıkıntılı bir durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1591, 'başı belaya girmek (uğramak)', 'sıkıcı, üzücü bir durumla karşılaşmak: ''Bir keresinde başı polisle belaya girmişti.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1592, 'başı çatlamak', 'başı çok ağrımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1593, 'başı dara düşmek', 'sıkıntıya girmek: ''Adamın başı dara düşünce yardımına Hayrullah koşmaz da kim koşar?'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1594, 'başı daralmak', 'para yönünden sıkıntıya, darlığa düşmek: Başınız daralırsa beni arayın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1595, 'başı darda kalmak', 'parasızlıktan dolayı sıkıntıda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1596, 'başı derde girmek', 'sıkıntılı bir duruma düşmek: ''İlişkilerdeki rol dağılımını sürekli karıştırdığımdan, benim de temizlikçilerle başım hep derde girmiştir.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1597, 'başı dik olmak', '1) onurlu, gururlu olmak; 2) cesur, yürekli olmak: ''Daima başı dik olacak, idare dâhil, kimseye boyun eğmeyecekti.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1598, 'başı dönmek', '1) insana, eşyanın dönmesi, ayağının altından yerin çekilmesi vb. bir duygu gelmek: ''Cümle kapısının önüne geldiği zaman başının dönmeye başladığını hissetti.'' -P. Safa. 2) sıkıntı yaratan bir durum karşısında bunalmak; 3) görkemli bir şey karşısında şaşırmak; 4) para veya makam sebebiyle şaşırıp şımarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1599, 'başı göğe ermek (değmek)', ' alay beklenmeyen bir mutluluğa ermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1600, 'başı kazan gibi olmak', 'başında çok ağrı ve uğultulu bir sersemlik olmak: ''Başım kazan gibiydi, bir kavanoz aspirin içsem ağrımın geçeceğine ihtimal vermiyordum.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1601, 'başı nâra yanmak', 'başkası uğruna büyük bir zarara uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1602, 'başı sıkılmak (sıkışmak)', 'herhangi bir güçlük karşısında kalmak, bunalmak: ''Baba dostu bir adam, başı sıkıldıkça Edip Münir ona koşar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1603, 'başı sıkıya gelmek', 'herhangi bir güçlük karşısında bunalmak, zor durumda kalmak: ''Başımız sıkıya geldi mi hemen onlara koşacağız.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1604, 'başı taşa değmek', 'ağır bir durum kendisine ders olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1605, 'başı tutmak', 'gürültüden veya üzüntüden başı ağrımak: ''... poker oynanıyor. Yenilirse kızıyor. Başı tutuyor, komşu doktorun hizmetçisini çağırıp çenesini ovduruyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1606, 'başı üstünde yeri olmak', '1) her zaman iyi karşılanmak, ağırlanmak: ''İyi, sefa geldiler, hoş geldiler, başımızın üstünde yerleri vardı elbet.'' -T. Dursun K. 2) bir düşünce veya davranışı uygun bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1607, 'başı yastığa düşmek', 'yorgunluktan veya güçsüzlükten uykuya dalmak: ''Ve tekrar başı yastığa düştü ve uyudu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1608, 'başı yastık yüzü görmemek', 'yatağa yatıp uyumuş olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1609, 'başı yerine gelmek', 'zihin yorgunluğu geçmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1610, 'başıboş kalmak', 'baskı altında bulunmamak, karışanı, görüşeni olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1611, 'başım gözüm üstüne', 'belirtilen istekleri içtenlikle yapmayı kabul etmeyi anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1612, 'başımla beraber', 'memnunlukla, seve seve: ''Ben de postu senin eve sererim. A, gel Sedat, başımla beraber.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1613, 'başın başı var, başın da başı var', 'toplum içinde hiç kimse başına buyruk değildir, başta bulunan her kişinin üstünde daha büyük bir baş vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1614, 'başın sağlığı, dünyanın varlığı', 'dünyanın en büyük zenginliği, beden sağlığından başka bir şey değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1615, 'başına balta kesilmek (olmak)', 'sürekli istemek, ısrar etmek, inat etmek: ''Bir kere tadına varanlar, yine ondan ver diye başıma balta kesiliyorlar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1616, 'başına bela açmak', 'kötü bir olay dolayısıyla dert sahibi olmak: ''Şu Yaşar kaçakçılıkla başına bir bela açabilir.'' -N. Araz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1617, 'başına bela almak', 'bir sorunla karşılaşmak, kötü bir duruma düşmek: ''Al başına belayı, bir de hasta bakıcılık edeceğiz.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1618, 'başına bela olmak (kesilmek)', 'sıkıntı vermek, tedirgin etmek, musallat olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1619, 'başına bir hâl gelmek', '1) kötü bir duruma uğramak; 2) ölüm ihtimali olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1620, 'başına çalsın!', 'birine verilmek istenilen bir şeyin öfke ve nefretle geri çevrildiğini anlatmak için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1621, 'başına çıkarmak', 'şımartmak, çok yüz vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1622, 'başına dert açmak', 'kendini kötü ve zor bir duruma düşürmek: ''Giderayak başımıza yeni bir dert açmayasın!'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1623, 'başına devlet (talih) kuşu konmak', 'beklemediği büyük bir nimeti ele geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1624, 'başına dikmek', '1) birini veya bir şeyi korumak için bir kimseyi görevlendirmek: ''Başıma bir nöbetçi diktikten sonra bırakıp gitti.'' -T. Buğra. 2) bir içeceği kabı yukarı kaldırarak sonuna dek içmek: ''Orada alışmışlar, su yerine lık lık lık bira şişesini dikerlermiş başlarına, içerlermiş.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1625, 'başına dolamak', 'musallat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1626, 'başına dünyanın belasını sarmak', 'büyük felaket getirmek: ''Sonradan Kayabaşı''nın başına ve bizim başımıza dünyanın belasını saracak kadar zengindik.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1627, 'başına geçirmek', '1) başına giymek: Şapkasını başına geçirdi. 2) bir şeyi öfke ile birisinin başına vurmak: Şimdi tencereyi başına geçiririm!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1628, 'başına gelen başmakçıdır', 'başından bir iş geçmiş olan kimse o işte deneyimli olur, uğradığı zarara bir daha uğramamak için önlem alır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1629, 'başına (...) gelmek', 'kötü bir durumla karşı karşıya kalmak: ''Yarın senin de başına bir felaket gelmesinden çok korkuyorum.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1630, 'başına güneş geçmek', 'güneş çarpmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1631, 'başına iş açmak', 'uğraştırıcı ve üzücü bir işin çıkmasına yol açmak: ''Herkesten size ne? Çocuğun başına iş açacaksınız.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1632, 'başına iş çıkarmak', 'istenilmeyen veya uğraştırıcı bir işe yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1633, 'başına iş çıkmak', 'hoşa gitmeyen ve beklenmedik bir iş veya olayla karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1634, 'başına kakmak (kakınç etmek)', 'yapılan bir iyiliği yüzüne vurarak birini üzmek: ''Ali Rıza Bey onu şirkete yerleştirmekle Allah razı olsun, büyük bir iyilik etmişti. Fakat onu ikide birde başına kakması doğru olmazdı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1635, 'başına kan çıkmak', 'öfkelenmek, hiddete kapılmak, kontrolünü yitirmek: ''Bizim merkez memuru celallidir, başına çabuk kan çıkar, hatırınızı kıracak şeyler yapar.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1636, 'başına karalar bağlamak', 'çok kederlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1637, 'başına taç etmek', 'çok değer vermek, ilgi göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1638, 'başına taş düşmek (yağmak)', 'felakete uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1639, 'başına vur, ağzından lokmasını al', 'uysal ve sessiz kimseler için kullanılan bir söz: ''Temizdim, sakindim, başıma vur, ağzımdan lokmamı al.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1640, 'başına vurmak', '1) içki, gaz veya sıcak baş ağrısı yapmak; 2) dayanamaz olmak: Bekârlık başına vurdu. 3) ne yapacağını bilemez hâle gelmek: ''Bu harp başına vurdu galiba, sapıtmışsın sen.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1641, 'başına yıkmak', 'harap etmek, zor durumda bırakmak: ''Babamın evinden çıktım / Evini başına yıktım'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1642, 'başında kavak yeli esmek', '1) genç sorumluluk duygusundan uzak, zevk, eğlence peşinde koşmak: ''Kocası yaşlı diye genç bir kadının başında kavak yelleri estiğine hükmetmek lazım gelmez.'' -R. H. Karay. 2) gerçekleşmeyecek şeyler düşünerek vakit geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1643, 'başında paralansın', 'yapılan bir iyilik çok söylendiğinde o iyiliğin artık istenmediğini belirten bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1644, 'başında torbası eksik', 'kaba saba, yontulmamış (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1645, 'başından almak', 'kurtarmak, sorumluluğunu almak: ''Çiftlikte bir kısım toprakları başımızdan alacak müşteriyi beklemekten başka bir tasamız kalmadı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1646, 'başından aşağı kaynar sular dökülmek', 'üzüntülü veya kötü bir olay karşısında birdenbire büyük bir sıkıntı duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1647, 'başından atmak', '1) yapılması güç bir işi yapmaktan kendini kurtarmak: ''Madem bunları siz kendi başınızdan atmak istiyorsunuz, emanet olarak şu masaya bırakın.'' -S. Birsel. 2) sürdürülmesi gereksiz görülen bir bağlılığa, bir ilişkiye son vermek: ''Hilmi Efendi''yi başlarından atmak yolunu arıyorlardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1648, 'başından büyük işlere girişmek (kalkışmak)', 'gücünün üstünde olan işlere kalkışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1649, 'başından geçmek', 'daha önce aynı duruma uğramış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1650, 'başından korkmak', 'hayatından kaygı duymak, cezalandırılmaktan korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1651, 'başından savmak', 'bir istekte bulunanı sözde bir sebeple uzaklaştırmak: ''Yoksa başımdan savmak için akla karayı mı seçeceğim?'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1652, 'başını acemi berbere teslim eden cebinden pamuğu eksik etmez (etmesin)', 'işbaşına tecrübesiz yönetici getirenler, onun yaratacağı sıkıntı ve zararları çekmeye hazır olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1653, 'başını ağrıtmak', '1) gereksiz sözlerle birini bunaltmak; 2) bir iş için birini tedirgin etmek, uğraştırmak: ''İkide bir ah Çingeneler vah Çingeneler diye gelip böyle başımı ağrıtma.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1654, 'başını alıp gitmek', 'izin almadan ve gideceği yeri bildirmeden gitmek, savuşmak: ''Bir akşam ciğerci söve saya kondusundan çıktı. Başını alıp gitti.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1655, 'başını ateşlere yakmak', 'başına büyük bir dert almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1656, 'başını bağlamak', '1) başına örtü vb. bağlamak; 2) birini nişanlamak veya evlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1657, 'başını belaya sokmak', 'birini, kötü sonuçlar verecek bir duruma itmek: ''Ayrıca benim başımı belaya sokmaktan da çekinmiş olabilir.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1658, 'başını bir yere bağlamak', 'birini bir işe yerleştirmek, işsizlikten, başıboşluktan kurtarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1659, 'başını boş bırakmak', 'yalnız veya serbest bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1660, 'başını çıkarmak', 'bitki filizlenmeye başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1661, 'başını derde sokmak', 'sıkıntılı bir duruma girmek veya getirilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1662, 'başını dik tutmak', 'onurunu korumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1663, 'başını dinlemek', 'kafasını dinlemek: ''Robenson, akıllı Robenson''um / Ne imreniyorum sana bilsen / Göstersen adana giden yolu / Başımı dinlemek istiyorum'' -C. S. Tarancı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1664, 'başını döndürmek', '1) mutluluktan yarı sarhoş duruma getirmek; 2) kendine hayran bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1665, 'başını duman almak', 'efkârlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1666, 'başını ezmek', 'bir daha kötülük edemeyecek duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1667, 'başını gözünü yarmak', 'bir işi kötü yapmak, bir işi istenildiği gibi yapmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1668, 'başını kaldırmamak (kaldıramamak)', '1) bir işi aralıksız sürdürmek; 2) iyileşememek, yataktan çıkamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1669, 'başını kaşımaya (kaşıyacak) vakti olmamak', 'arada en ufak başka bir iş yapamayacak kadar sıkışık durumda bulunmak: ''Büyük babanın artık başını kaşıyacak vakti yoktur. Kâh çocukları kırda oynamaya götürüyor, kâh onlara ocakbaşında masallar söylüyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1670, 'başını koltuğunun altına almak', 'ölümü göze alarak bir işe girişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1671, 'başını kurtarmak', '1) canını korumak; 2) geçimini sağlayacak bir duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1672, 'başını ortaya koymak', 'bir işe girişirken ölümü göze almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1673, 'başını taştan taşa vurmak', 'çaresiz kalarak çok pişman olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1674, 'başını uçurmak', 'kellesini uçurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1675, 'başını vermek', 'kendini feda etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1676, 'başını yakmak', 'güç bir duruma sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1677, 'başının çaresine bakmak', 'kimseden yardım görmeden kendi işini kendi yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1678, 'başının derdine düşmek', 'başka bir şeyle ilgilenmeyecek kadar sıkıntılı durumda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1679, 'başının gözünün sadakası', 'başa gelecek bir belayı savmak veya önlemek için yapılan bağış, özveri: ''Bir herif çıksa da şunu başımdan alsa... Başım gözüm sadakası üç beş parça eşya, beş, on kuruş da para veririm.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1680, 'başka işi yok mu?', '''bu işe ne diye karışıyor, bu iş onu ilgilendirmez'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1681, 'başköşeye kurulmak', 'saygın kişilere ve büyüklere ayrılan yere oturmak: ''Adamakıllı bol entarisinin eteklerini savurta savurta geldi, başköşeye kuruldu.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1682, 'başlama!', '''hoş olmayan bir söz veya davranışı tekrarlama!'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1683, 'başlık almak', 'bazı bölgelerde, evlenirken kızın babası oğlanevinden para veya mal almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1684, 'başlık vermek', 'bazı bölgelerde, evlenirken kızın babasına oğlanevi tarafından para veya mal vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1685, 'başsağlığı dilemek', 'ölen bir kimsenin yakınlarını ziyaret ederek ilgi ve yakınlık belirten sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1686, 'başsağlığında bulunmak', 'başsağlığı dilemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1687, 'başsız bırakmak', '1) yöneticisiz bırakmak; 2) büyüğünü yitirmesine sebep olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1688, 'başsız kalmak', '1) yöneticisi, başkanı bulunmamak: ''Fakat o gözünü kapayınca başsız kalan konak ...'' -R. N. Güntekin. 2) büyüğünü yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1689, 'başta (başında) bulunmak', 'bir işin yöneticisi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1690, 'başta gelmek', 'önde olmak, üstün durumda olmak: İpekçilikte Bursa başta gelir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1691, 'başta gitmek', 'en ileri durumda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1692, 'başta taşımak', 'çok saygı göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1693, 'baştan aşmak', 'pek çok olmak, pek çoğalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1694, 'baştan çıkarmak', '1) kötü yola sürüklemek, doğru yoldan saptırmak: ''Perihan adında bir bayan, bizim güveyi dans arasında ayartıp baştan çıkarmış.'' -M. Ş. Esendal. 2) karşı cinsi bir ilişkiye ikna etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1695, 'baştan çıkmak', 'ahlakı bozulmak, doğru yoldan ayrılıp uygunsuz işlere yönelmek: ''Edebiyatı zenginleştiren genellikle bu tür, baştan çıkmış yazarlardı.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1696, 'baştan kara etmek', 'batma tehlikesi karşısında, gemi başını karaya vurup oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1697, 'baştan kara gitmek', 'sonunu düşünmeyerek hesapsız, batarcasına yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1698, 'batağa saplanmak', 'içinden çıkılması güç bir durumda olmak: ''Uzun yıllardan beri parasal bakımdan tam bir batağa saplanmıştı.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1699, 'batkıya uğramak', 'hüsranla karşılaşmak: ''O geniş caddeler bugünkü hazin görünümleriyle nihayet bulurlar. Edebiyatın özlemleri acı bir batkıya uğrar.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1700, 'battı balık yan gider', 'işler kötü gittiğine göre artık istenildiği gibi davranılabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1701, 'bayağı kaçmak', 'söz, davranış, giyiniş yakışmamak, uygunsuz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1702, 'baygın düşmek', 'çok yorulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1703, 'baygınlık geçirmek', '1) bayılmak; 2) mec. çok heyecanlanmak, telaşlanmak: ''Annem, üç gün sonra, sevinç baygınlıkları geçiren Yahudi''nin avucuna on altın sayıp yalvardı.'' -Y. Z. Ortaç. 3) mec. çok sıkılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1704, 'baykuş gibi', 'uğursuzluk getirdiğine inanılan (kimse): ''Hangi evde cenaze varsa oraya baykuş gibi tüner.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1705, 'baykuşun kısmeti ayağına gelir', 'Tanrı hiçbir canlıyı aç bırakmaz, kımıldamadan duran baykuşun rızkını bile önüne koyar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1706, 'bayrak açmak', '1) gönüllü asker toplamaya girişmek; 2) bir ülkü yolunda toplanmaya çağırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1707, 'bayrak gibi', 'kendini belli edecek bir biçimde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1708, 'bayrakları açmak', 'bağırıp çağırarak hırçınlık etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1709, 'bayram değil, seyran değil (eniştem beni niye öptü)', '''gösterilen bu ilginin, bu yakınlığın bir sebebi olacak'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1710, 'bayram etmek (yapmak)', 'çok sevinmek: ''Sabaha kadar tepindiler. Bayram ediyorlar.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1711, 'bayram haftasını mangal tahtası anlamak', ' şaka sözü, konu ile hiçbir ilgisi olmayacak biçimde ters anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1712, 'bayram havası esmek', 'ortam neşeli, sevinçli bir duruma gelmek: ''Ziyaret günleri hapishanelerde bir bayram havası eser.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1713, 'bayram koçu gibi', 'gösterişli görünmek amacıyla aşırı biçimde süslenmiş olan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1714, 'bayramda borç ödeyene ramazan ağır (kısa) gelir', 'vadesi yaklaşan bir borcu ödemek zorunda olan kimseye günler çok çabuk geçer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1715, 'bayramlık ağzını açmak', 'kaba konuşmak, küfretmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1716, 'baz almak', 'esas veya temel olarak almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1717, 'baz bazla, kaz kazla, kel tavuk topal horozla', 'bir kişi, kendine denk ve uygun olan kişiyle arkadaş olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1718, 'bazı dingil döner bazı teker', 'karşılıklı ilişkilerde her iki tarafa da zaman zaman söz söyleme hakkı doğar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1719, 'bebek gibi', '1) çok güzel (kadın); 2) bebeğe yakışır bir biçimde: ''Annesinin arkasında asılı bir bebek gibi de çantada mışıl mışıl uyurdu.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1720, 'bedava sirke baldan tatlıdır', 'masrafsız ve emeksiz elde edilen şeyler insana hoş gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1721, 'bedduası tutmak', 'ilenci yerine gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1722, 'beğenmeyen kızını (küçük kızını) vermesin', 'bir durumun beğenilmemesi karşısında, beğenmeyenin umursanmadığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1723, 'beka bulmak', 'ölmezlik erdemine ulaşmak, ölümsüzleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1724, 'bekâr gözü, kör gözü', 'bekâr erkek, evlenme istek ve heyecanı içinde olduğundan alacağı kızın kusurlarını göremez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1725, 'bekâra karı boşaması kolaydır', 'bilgi ve deneyimi olmayan bir kimsenin işi hafife alması, önemsememesi, gereğince değerlendirememesi doğaldır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1726, 'bekârın parasını it yer, yakasını bit', 'bekâr kimse parasını gereksiz harcar, yaşayışı ise düzensizdir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1727, 'bekârlık maskaralık', 'bekâr kimse bakımsızdır, derbeder bir yaşayışı vardır ve herkesin eğlencesi olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1728, 'bekârlık sultanlık(tır)', 'evlenmeden tek başına yaşamak daha iyidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1729, 'bekçi kalmak', 'koruyucu, gözcü, denetleyici olarak beklemek: ''Yıkılan o saltanatlar üzerinde bir kandil gibi artık sonsuzluğa dek bekçi kalacaktı.'' -R. E. Ünaydın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1730, 'bekle yârin köşesini!', 'yakında gerçekleşeceği beklenmeyen umutlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1731, 'beklemeye almak', '1) herhangi bir şeyi kısa veya uzun bir süre ertelemek; 2) telefonla yapılan iletişim sırasında karşı tarafı geçici bir süre bekletmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1732, 'bel bağlamak', 'birisinin kendisine yardımcı olacağına inanmak, güvenmek: ''Ne var ki böyle araçlara biz pek bel bağlayamayız.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1733, 'bel kırmak', 'kırıtmak, salınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1734, 'bel vermek', '1) duvar gibi dik şeyler dışarıya veya tavan gibi yatay şeyler aşağıya doğru kamburlaşmak: ''İsli tavan bel vermiş, duvarları içeri kamburlaşmıştı.'' -O. Kemal. 2) mec. herhangi bir konuda destek olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1735, 'bela aramak', 'kavga çıkarmak için fırsat kollamak: ''Geceleyin belanı arama, haydi nerden geldinse bas git oraya.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1736, 'bela çıkarmak', 'kavga çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1737, 'bela getirmek', 'kötülüğe, felakete uğratmak: ''Yurtlarına bela getiren bu kadını, ayıplamıyor kentin uluları.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1738, 'bela kesilmek', 'birisine sıkıntı ve eziyet vermek, musallat olmak: ''Zavallı Reşat Efendi kendisinden başkaları için âdeta bir bela kesilmişti.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1739, 'bela okumak', 'birine ilenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1740, 'belalar mübareği', ' alay istenilmeyen, kaçınılan bir durumun gerçekleştiği bildirilirken söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1741, '(...) belası', '-den dolayı, sebebiyle: ''İlme karşı saygı belası olarak dinlemek zaruridir.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1742, 'belasını bulmak', 'hak ettiği cezayı görmek: ''Hâlime dünya acıyor, rakiplerim de belasını buldu diye seviniyor.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1743, 'belaya çatmak (girmek, uğramak)', 'beklenmedik bir bela ile karşılaşmak: ''Çattık belaya, ne ister bu adam benden canım, şamaroğlanına döndürdü.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1744, 'belayı satın almak', 'göz göre göre belayı üstüne çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1745, 'belden aşağı vurmak', 'iş hayatında, insan ilişkilerinde, siyasette kural dışı saldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1746, 'beleşe konmak', 'bir şeyi emek harcamadan, para vermeden elde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1747, 'belge almak', 'başarısızlık yüzünden öğretim kurumuyla ilişiği kesilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1748, 'beli açılmak', 'küçük abdestini tutamaz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1749, 'beli bükülmek', 'yaşlılık yüzünden güçsüz kalmak, bir iş yapamayacak duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1750, 'beli çökmek', 'kamburlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1751, 'belinden gelmek', 'birinin dölü olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1752, 'belini doğrultmak', 'yeniden durumunu düzeltmek: ''Belini biraz doğrultmuş, borçlarını ödemiş, daha rahat bir yaşam düzeyine erişmişti.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1753, 'belini kırmak', 'birini bir şeyi yapamaz duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1754, 'belini vermek', 'dayanmak, yaslanmak: ''Avlunun en uzak köşesine, duvara belini verir otururdu.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1755, 'belsoğukluğuna uğratmak', ' kaba bir işe veya bir söze gereksiz yere karışarak onun akışını sektirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1756, 'bembeyaz kesilmek (olmak)', 'beklemediği bir durum karşısında beti benzi atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1757, 'ben hancı, sen yolcu oldukça', '''düzen bu biçimde devam ettiği sürece'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1758, 'ben şahımı (şeyhimi) bu kadar severim', '''ben bundan daha çok özveride bulunamam'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1759, 'benden günah gitti', 'benden söylemesi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1760, 'benden paso', '''benim yapabileceğim ancak bu kadar'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1761, 'benden söylemesi', '''ben üzerime borç saydığım şeyi söyledim, kendimi suçlu saymam'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1762, 'bendeniz cennet kuşu', ' alay kendini tanıtırken kullanılan bir deyim: Bendeniz cennet kuşu Tahir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1763, 'benim diyen', 'kendine güvenen, güçlü olduğuna inanan: Benim diyen adam bu işi yapamaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1764, 'benim oğlum bina okur, döner döner yine okur', 'çok çalışmasına karşın belli bir düzeyden öteye gidemiyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1765, 'benliği yoğurmak', 'kişiliği oluşturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1766, 'benliğinden çıkmak', 'kendine benzemez olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1767, 'benzetmek gibi olmasın', 'kötü bir sona uğramış birinden veya bir şeyden söz ederken, ona benzetilen kimse veya şey için kötü bir duygu beslenilmediğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1768, 'benzeye benzeye yaz, benzeye benzeye kış olur', 'günler birbirinden çok farklı olmadığı hâlde hava yavaş yavaş ısınarak yaz, aynı biçimde yavaş yavaş soğuyarak kış olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1769, 'benzi atmak', 'ansızın yüzünün rengi sararmak, solmak: ''Necdet''in benzi atıyor, kesik kesik soluyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1770, 'benzi geçmek', 'benzi solmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1771, 'benzi kanlanmak', 'sağlıklı duruma gelmek, canlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1772, 'benzi kül gibi olmak', 'yüzünden kan çekilmek, yüzü sararmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1773, 'benzi sararmak', 'yüzünün rengi solmak: ''O böyle söylerken yanında bulunanların benzi sararırdı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1774, 'benzi solmak', 'gücünü yitirmek, sağlık sorunu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1775, 'benzi uçmak', 'yüzü sararmak: ''Benzi uçtu, dudaklarındaki gülümseme soldu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1776, 'benzinde kan kalmamak', 'kansızlık sebebiyle yüzü sararmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1777, 'benzine kan gelmek', 'sağlıklı duruma gelmek, canlanmak: ''Yirmi dört saat evvel Allah''tan ziyade Abdülhamit''ten korkan kâtiplerin henüz benizlerine kan gelmemişti.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1778, 'beraatizimmet asıldır', '''tersi kanıtlanmadıkça insanların suçsuz sayılmaları gerekir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1779, 'berabere kalmak', '1) aynı sayıyı almak; 2) başa baş gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1780, 'berber berbere benzer ama başın Allah''a emanet', 'kendisini uzman olarak gösteren her kişiye güvenilmemelidir, malınızı canınızı tehlikeye sokabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1781, 'berberin solumazı, tellağın terlemezi, kahvecinin söylemezi', 'çalışan kişinin kötü özellikleriyle müşterilerine rahatsızlık vermeyeni makbuldür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1782, 'bereket ki (bereket versin ki)', '''iyi ki, Tanrıya şükür ki'' anlamında kullanılan bir söz: ''Bereket versin ki padişahın cellatları kapıya dayanmadılar.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1783, 'bereket versin', '1) para alan kimsenin söylediği iyi dilek sözü; 2) bir kimsenin bir durumdan hoşnutluğunu anlatan söz: ''Bereket versin, gece bu kır yolu tenha idi.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1784, 'berhudar ol!', '''iyi günler göresin'' anlamında kullanılan bir iyi dilek sözü: ''Fahim Bey''in kısa boylu, ak sakallı babası, berhudar ol oğlum, gel seni alnından öpeyim, demiş.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1785, 'besiye çekmek', 'hayvanı semirtmek için beslemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1786, 'besle kargayı, oysun gözünü', 'elinde büyüttüğün kişi gün gelip sana nankörlük edebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1787, 'besledik büyüttük danayı, şimdi tanımaz oldu anayı', 'elinde büyüttüğün kişi gün gelip sana nankörlük edebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1788, 'besleme gibi', 'giydiğini kendine yakıştıramayan (kız).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1789, 'beslemeyi eslemeden alma', 'sürekli bir hizmet için evine kabul edeceğin kimseyi iyice sorup soruşturmadan alma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1790, 'besmele çekmek', 'bir işe başlarken ''bismillahirrahmanirrahim'' sözünü söylemek: ''Üç dört kişi birden besmele çekmişlerdi ve hepsi birden okumaya başlamışlardı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1791, 'beş aşağı beş yukarı', 'üç aşağı, beş yukarı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1792, 'beş para almamak', 'hiç para almamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1793, 'beş para etmez', 'hiçbir değeri yok, işe yaramaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1794, 'beş para etmez', '''hiçbir değeri yok, işe yaramaz'' anlamında kullanılan bir söz: ''Doktorun oğlu imtihansız geçmek değil, ağzı ile kuş tutsa bile beş para etmez.'' -A. H. Çelebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1795, 'beş paralık etmek', 'zor durumda bırakmak, dile düşürmek, rezil etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1796, 'beş paralık olmak', 'zor durumda kalmak, dile düşmek, rezil olmak: Yaptığı bu hatadan dolayı onuru beş paralık oldu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1797, 'beş parasız kalmak', 'harcayacak parası olmamak: ''Kış hâlâ çok zordu. Beş parasız kalındığı günler sürüp gidiyordu çünkü.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1798, 'beş parmağın hangisini kessen acımaz?', 'insan evlatlarını birbirinden ayırt etmez, hangisine zarar gelse aynı üzüntüyü duyar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1799, 'beş parmak (parmağın) bir (biri) olmaz', 'belirli bir insan topluluğu içinde benzerlikler olabileceği gibi farklılıklar da olabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1800, 'beş tavuğa bir horoz yeter', 'birçok kadını yönetmek veya korumak için bir erkek yeter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1801, 'beşer şaşar', '''insan her zaman yanılabilir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1802, 'beşiğini sallamak', 'çocukluğundan veya çok eskiden tanımak, büyümesine hizmet etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1803, 'beşiklik etmek', 'beşiklik görevini yapmak: ''Anadolu bugünkü Türklerin vatanı olmadan önce, sayısız topluma beşiklik etmiştir.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1804, 'beşikten mezara kadar', 'bütün hayatı boyunca, ölünceye kadar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1805, 'beşlik simit gibi kurulmak', 'kendini bir şey sanarak bir yere yayılıp oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1806, 'beterin beteri var', 'çok kötü bir duruma düşen kimse, bundan daha kötü durumların da bulunduğunu düşünerek avunmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1807, 'beti benzi atmak (solmak, uçmak, kül kesilmek, kireç kesilmek)', 'herhangi bir sebeple kanı çekilip yüzü solmak, korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1808, 'beti benzi kalmamak', 'yüzü sararıp solmak: ''Müşterim bu sesi duyunca arabayı durdurdu. Beti benzi kalmadı. Eli ayağı titremeye başladı.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1809, 'beti bereketi kalmamak (kaçmak)', 'azalmak, kıtlaşmak, çabuk tükenmek: Paranın beti bereketi kalmadı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1810, 'beton gibi', '1) çok sağlam, dayanıklı, sert; 2) güçlü: ''Yere hem tüy gibi hafif hem de beton gibi sağlam basabilmek.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1811, 'bey ardından çomak çalan çok olur', 'güçlü bir kişi ile yüz yüze bulunduklarında ağızlarını açamayanlar, o gittikten sonra aleyhinde atıp tutarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1812, 'bey aşı borç, düğün aşı ödünç', 'beyin sofrasında ağırlanan kimsenin karşılık olarak beye ziyafet vermesi kolay olmadığından bu bir borç olarak kalır; düğün aşı yiyen de günün birinde kendisinin yapacağı düğüne önceden gittiği düğün sahibini çağırır böylece borcun altından kalkmış olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1813, 'bey gibi yaşamak', 'bolluk içinde yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1814, 'bey mi yaman, el mi yaman', 'baştaki ne kadar güçlü görünürse görünsün, asıl güç halktadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1815, 'beyaz sayfa açmak', 'bir konuda geçmişi unutarak geleceğe umutla bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1816, 'beyaza çekmek', 'yazıyı temize çekmek: ''Dört satırlık bir beyaza çekmek için de kan terlere batar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1817, 'beyazın (akın) adı (var), esmerin (karanın) tadı (var)', 'beyaz tenli olanlar güzel sayılsa da gerçek güzellik ve şirinlik esmerlerdedir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1818, 'beyde bulunmayan elde neler var', 'beylerde olmayan öyle şeyler vardır ki halkta bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1819, 'beyin yıkamak', ' ruh b. insanı, kendine özgü düşünce ve dünya görüşüne yabancılaştırmak, başka yönde düşünür ve davranır duruma getirmek amacıyla çeşitli yollarla etkilemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1820, 'beyler buyruğu yoksula kan ağlatır', 'yöneticiler, uygulanması güç buyruklar vererek halkı sıkıntıya sokarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1821, 'beylik çeşmeden su içme', 'resmî işlerde dikkatli olmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1822, 'beylik fırın has çıkarır', 'devlet görevlisi olmak insana birçok kazanç sağlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1823, 'beyni atmak', 'tepesi atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1824, 'beyni bulanmak', '1) sersemlemek, düşünemez olmak; 2) kötü bir şey sezinlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1825, 'beyni karıncalanmak', 'zihin yorgunluğundan düşünemez olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1826, 'beyni kaynamak', 'aşırı sıcaktan sersemlemek, bunalmak: ''Kızgın güneşin altında bütün gün beynim kaynıyor.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1827, 'beyni sıçramak', 'aklı başından gitmek: ''Akşam eve gelip de heykelin başını boyun yerinden çatlamış ve güzelim mermer başlığı tuzla buz olmuş görünce beynim sıçradı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1828, 'beyni sulanmak', 'düzgün düşünemez olmak, bunamak: ''Beyni sulanan bu ayyaş, iğrenç mahluku onlara anlatmakta ne fayda olabilirdi.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1829, 'beyninde şimşekler çakmak', '1) çok üzülmek, sarsılmak; 2) zihninde birden bir düşünce doğmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1830, 'beyninden vurulmuşa dönmek', 'beklenmedik bir durum karşısında olağanüstü bir üzüntü ve şaşkınlığa uğramak: ''Bu satırları okuyunca Mustafa beyninden vurulmuşa döndü.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1831, 'beynine girmek', 'herhangi bir konuda birisini yönlendirmek, ikna etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1832, 'beynine vurmak', 'içki etkisiyle ne yaptığını bilemez duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1833, 'beynini dağıtmak', 'öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1834, 'beynini kemirmek', 'rahatsızlık vermek, huzurunu kaçırmak: ''İşte birkaç zamandır beynini kemiren şüphe: Ben deli miyim?'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1835, 'bez alırsan Mısır''dan (Musul''dan), kız alırsan asilden', 'ne alacaksanız cinsini, aslını biliniz, güvenerek alınız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1836, 'bez bağlamak', '1) bebeklere altlarını kirletmesinler diye bez koymak; 2) dileğin yerine gelmesi ümidiyle yatıra bir parça çaput veya eski kumaş parçası bağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1837, 'bezginlik getirmek', 'usanmak, bıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1838, 'bezini yıkamak', '1) bebeklerin altına bağlanan bezi temizlemek; 2) mec. çok emek sarf etmek: Ben senin az mı bezini yıkadım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1839, 'bezirgân züğürtleyince geçmiş defterleri yoklar', '1) tüccar züğürtleyince, belki bir kimsede alacağım kalmıştır diye eski defterlerini gözden geçirir; 2) vaktiyle önemli işler yapmış olanlar, düşkünlüklerinde eski durumlarını anarak, anlatarak avunmaya çalışırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1840, 'bıçağı kestiren kendi suyu, insanı sevdiren kendi huyu', 'bir şeyin, bir kimsenin değeri, kendisinde aranılan özel niteliklerle artar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1841, 'bıçak altına yatmak', 'ameliyat olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1842, 'bıçak atmak', '1) bir hedefe bıçak fırlatmak; 2) bıçaklamak; 3) ameliyat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1843, 'bıçak bıçağa gelmek', 'bıçakla birbirine saldıracak kadar zorlu kavga etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1844, 'bıçak çekmek', 'üzerindeki bıçağı birden eline alarak birine saplamaya hazırlanmak: ''Köy delikanlılarının bıçak çekmeye elleri bile değmedi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1845, 'bıçak gibi', 'ince, keskin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1846, 'bıçak gibi kesilmek', 'söz, konuşma, sohbet birden bitmek, duruvermek: ''Bu tatlı sohbetin arasında kapı çalındı, lakırtıları bıçak gibi kesildi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1847, 'bıçak gibi kesmek', '1) çok keskin olmak; 2) birdenbire ve tamamen ortadan kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1848, 'bıçak gibi saplanmak', 'sancı, ağrı birden ve güçlü olarak gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1849, 'bıçak kemiğe dayanmak', 'çekilen sıkıntı artık katlanılamayacak bir duruma gelmek: ''Bıçak kemiğe dayandı mı başkaldırır, canını sakınmaz, hakkını ister.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1850, 'bıçak kınını kesmez', 'kötüler yararlandıkları kimselere kötülük etmekten çekinirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1851, 'bıçak silmek', 'bir işi bitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1852, 'bıçak suyu kesiyor', '''çok körleşmiş'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1853, 'bıçak vurmak', '1) bıçakla kesmek; 2) bıçaklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1854, 'bıçak yarası geçer (onulur), dil yarası geçmez (onulmaz)', 'hakaret, ağır söz vb. gönül kırıcı davranışların hiçbir zaman unutulmayacağını anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1855, 'bıçak yemek', 'bıçaklanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1856, 'bıkkınlık gelmek', 'bıkmak, usanmak, bunalmak: ''Zaman olur, en yakın arkadaşından bile bıkkınlık gelir insana.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1857, 'bıkkınlık vermek', 'bir şeyi sürekli tekrarlayarak karşısındakini usandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1858, 'bıldır yediği hurmalar, bu yıl götünü tırmalar', 'aradan bir yıl geçmiş, davranışının karşılığını şimdi görüyor, yaptığının acısı bugün çıkıyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1859, 'bıldırcın gibi', 'kısa boylu, dolgunca, alımlı (kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1860, 'bıldırcının beyliği arpa biçimine kadardır', 'her kazancın bir sonu vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1861, 'bırak Allah''ını seversen', 'bir kimse veya nesnenin değersizliğini belirtmek için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1862, 'bırak ki', 'varsay ki: ''Filan hekim, dediler, geldi baktı, anlamadı / Bırak ki anlasalar var mı çare hiç, ne gezer'' -M. A. Ersoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1863, 'bıyığı (bıyıkları) terlemek', 'bıyığı yeni yeni çıkmaya başlamak: ''Çocukları ve bıyıkları terlemeye yüz tutmuşları selamlıktan çağırdılar.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1864, 'bıyığını balta kesmez olmak', 'kimseden korkusu olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1865, 'bıyığını silmek', 'bir işi olmuş bitmiş sayarak onunla uğraşmaktan vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1866, 'bıyık altından gülmek', 'birinin durumuna belli etmemeye çalışarak gülümsemek: ''Sanki yarım ağız söylediğimi anlamış gibi bıyık altından gülerek şöyle bir süzüyor beni.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1867, 'bıyık burmak (bükmek)', 'çalım yapmak amacıyla bıyıklarını kıvırmak: ''Bıyık buran, göğüs geren erleriz.'' -E. B. Koryürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1868, 'bıyıkları ele almak', 'delikanlılık çağına girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1869, 'biber gibi', 'çok acı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1870, 'biber gibi yakmak', '1) deri, göz vb.ni çok acıtmak; 2) çok üzmek, dertlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1871, 'biber gibi yanmak', '1) deri, göz vb. çok acımak; 2) çok üzülmek, dertlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1872, 'biblo gibi', 'ufak tefek, zarif (kız).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1873, 'biçim almak', 'biçimlenmek, belli bir biçime girmek, şekillenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1874, 'bigâne düşmek', 'yabancılaşmak: ''Birkaç yabancı dili rahatlıkla konuşurken ana dilini bilmeyen ve bigâne düşmüş dudaklar susmalıdır.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1875, 'bildiğinden şaşmamak (kalmamak)', 'hiçbir etkiye aldırış etmeyerek doğru bildiği davranışı sürdürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1876, 'bildiğini okumak', 'herkes ne derse desin bildiği, istediği gibi davranmak: ''Efendiden gizli yine herkes bildiğini okuyordu.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1877, 'bildiğini yapmak', 'verilen öğütleri dinlemeyerek tutumunu sürdürmek: ''Her şeye peki, olur der fakat sonunda gene bildiğini yapardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1878, 'bildiğini yedi mahalle bilmez', 'bir kimsenin çok kurnaz, çokbilmiş olduğunu anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1879, 'bildik çıkmak', 'birbirlerini eskiden bildiklerini veya ailece tanıştıklarını anlamak: ''Hâlbuki ayrılık acısına ve ayrılık seslerine, bildik çıkmaklığım gerekti.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1880, 'bildim bileli', 'öteden beri, eskiden beri: ''Sütannenin sandık odası, bildim bileli akar.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1881, 'bile bile lades', '1) kötü bir durumu öyle gerektiği için kabullenmiş görünme, bilerek aldanmış görünme: ''Benimki bir yapı meselesi. Ben böyleyim. Benimki bile bile lades. Aldırmıyorum, hoşgörümü kullanıyorum.'' -N. Meriç. 2) sonucun kötü olacağını bilse bile bir işe girme.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1882, 'bileğinde altın bileziği olmak', 'kolunda altın bileziği olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1883, 'bileğine güvenmek', 'gücüne veya hünerine güvenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1884, 'bileğinin hakkıyla (gücüyle, kuvvetiyle, zoruyla)', 'kendi gücü ve kendi çalışması ile.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1885, 'bilek gibi', 'gür, kalın (saç veya akarsu).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1886, 'bilet kesmek', '1) bileti koparıp alıcıya vermek, bilet satmak: ''Benimki paso, dedi, hanımefendiye bir bilet kes.'' -R. H. Karay. 2) mec. işine son vermek, işten uzaklaştırmak, ayırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1887, 'biletini kesmek', '1) ölümüne karar vermek; 2) işine son vermek, işten uzaklaştırmak, ayırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1888, 'bilgi tazelemek', 'önceden sahip olduğu bilgiyi yenilemek, güncelleştirmek: Matematikle ilgili bilgilerimi tazeledim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1889, 'bilgiçlik satmak (taslamak)', 'bilmediği hâlde bilir görünmek, bilgin geçinmek: ''Hazır olanlar, bilgiçlik tasladılar, tasdik ettiler.'' -N. Araz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1890, 'bilincine varmak', 'anlamak, kavramak: ''İnsanın herhangi bir araçla ne yaşadığının bilincine varmasının bir doyum ve haz kaynağı olduğu unutulmamalıdır.'' -A. Cemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1891, 'bilir bilmez', 'yarım bilgi ile, bilip bilmediğine aldırmadan: ''Günde beş yüz defa, kendiliğimden bilir bilmez bunu haykırıyordum.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1892, 'billur gibi', '1) çok duru, çok temiz (su); 2) çok beyaz ve pürüzsüz (kol, gerdan, göğüs); 3) pürüzsüz (ses).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1893, 'bilmece gibi konuşmak', 'açık, anlaşılır bir biçimde konuşmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1894, 'bilmem hangi (kaç, kim, nasıl, ne)', 'önemli veya anlatılması gerekli görülmeyen şeyler için kullanılan bir söz: Bilmem hangi dairede kâtipmiş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1895, 'bilmemek ayıp değil, öğrenmemek (sormamak) ayıp', 'insanın her şeyi bilmemesi kusur değildir ama bilmediği bir işi sorup öğrenmeden yapmaya kalkışması kusurdur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1896, 'bilmezlikten gelmek', 'bilmiyor görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1897, 'bin bilsen de bir bilene danış', 'bir insan bir şeyi ne kadar iyi bilirse bilsin, gene de onu kendisinden daha iyi bilen bulunabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1898, 'bin can ile', 'çok isteyerek, gönülden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1899, 'bin derde deva', '1) pek çok işe yarayan; 2) her sıkıntıyı gideren.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1900, 'bin dereden su getirmek', 'birini kandırmak için birçok sebep ileri sürmek, dil dökmek: ''Rıfat Paşa gibi terbiyeli bir zat bile bin dereden su getirir, harp siyasetimizi methederdi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1901, 'bin dost az, bir düşman çok', 'dostun ne denli çok olursa olsun onlardan zarar gelmez ama bir tek düşmanın olsa hep zarar görme tehlikesi içerisinde yaşarsın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1902, 'bin işçi, bir başçı', 'her işe, baş olacak bir kimse gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1903, 'bin kalıba girmek', 'birbirine benzeyen birçok iş yapmak, sürekli olarak düşünce değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1904, 'bin nasihatten bir musibet yeğdir', 'yaşanan olaylar, öğütlerden çok daha etkilidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1905, 'bin ölçüp bir biçmeli', 'yapılacak bir işin bütün yönleri önceden çok iyi düşünülmeli, sonra işe başlanmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1906, 'bin pişman olmak', 'çok pişman olmak: ''Kaç kez yakındım ona, yüzünü öyle bir yas kapladı ki yakındığıma da yakınacağıma da bin pişman oldum.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1907, 'bin tarakta bezi olmak', 'birçok işi veya ilişkisi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1908, 'bin tasa bir borç ödemez', 'borçlu ne denli üzülürse üzülsün borç sıkıntısından kurtulamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1909, 'binbir ayak bir ayak üstüne', '''herkesin ayakta olduğu kalabalık'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1910, 'bindiği dalı kesmek', 'kendisine gerekli ve yararlı olan şeyi farkında olmadan yararsız duruma getirmek, kendi eliyle yok etmek: ''Bindiği dalı kesmek diye bir deyim vardır ya, sanki insanlığın bugünkü bunalımını anlatmak için bulunmuş.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1911, 'bini bir paraya', '1) pek çok ve ucuz; 2) pek çok yapılan, pek çok olan: ''Ali Çavuş''un hiddeti daha ziyadeleşti. Küfrün bini bir paraya.'' -N. Nâzım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1912, 'binicinin sağı solu olmaz', '1) uzman kişi, hangi yöntemi uygularsa uygulasın başarılı olur; 2) işini titizlikle yürüten kişinin, çalışanlarına her zaman iyi davranması beklenemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1913, 'binin yarısı beş yüz (o da bizde yok)', ' şaka çok düşünceli görünen birine ''aldırma!'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1914, 'bir abam (postum) var atarım, nerede olsam yatarım', 'tek başına yaşayan bir kimse, sorumluluğunda başkaları olmadığı için rahat hareket eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1915, 'bir acı kahvenin kırk yıl hatırı vardır', 'iyilik küçük de olsa unutulmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1916, 'bir adama kırk gün (deli dersen deli, akıllı dersen akıllı olur) ne dersen o olur', 'sürekli telkinlerle bir kişinin bilinç altına birtakım inançlar, duygular yerleştirilebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1917, 'bir ağaçta gül de biter, diken de', 'bir aileden iyi adam da çıkar, kötü adam da.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1918, 'bir ağaçtan okluk da çıkar, bokluk da', 'bir aileden iyi adam da çıkar, kötü adam da.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1919, 'bir ağızdan çıkıp (çıkan) bin dile (ağza) yayılır', 'ortaya atılan bir söz çok çabuk yayılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1920, 'bir anaya bir kız, bir kafaya bir göz', 'bir başa bir göz ne kadar gerekli ise bir anneye bir kız da o denli gereklidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1921, 'bir araya gelmek', 'bir yerde toplanmak, buluşmak: ''Hep böyle bir araya gelip gülüp eğlenebilseler!'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1922, 'bir araya getirmek', 'toplamak: ''Gurbet duygusu sevgi ile ayrılık ve birleşme özlemini bir araya getirir.'' -M. Kaplan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1923, 'bir arpa boyu (gitmek, yol almak)', 'çok az (gitmek veya yol almak): ''Süfli gayeler, kütleleri ya oldukları yere mıhlayan ve bir arpa boyu ileri götürmeyen sefil isteklerdir.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1924, 'bir aşağı bir yukarı', 'amaçsız olarak gidip gelmeyi anlatan bir söz: ''Karabibik tenha sokakta bir aşağı bir yukarı gezinmekteydi.'' -N. Nâzım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1925, 'bir atımlık barutu olmak (kalmak)', 'bir konuda yapabileceği çok az şeyi bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1926, 'bir avuç altının olacağına bir avuç toprağın olsun', 'altın harcanıp gider, toprak ise sürekli ürün veren, para getiren bir maldır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1927, 'bir avuç toprak olmak', 'ölmek: ''O olmasaydı, sen şimdi bir avuç toprak olmuştun.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1928, 'bir ayağı çukurda olmak', '1) yaşayacak çok az zamanı kalmış olmak: ''Ben, bir ayağı çukurda hasta bir ihtiyarım.'' -M. Yesari. 2) çok yaşlanmış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1929, 'bir ayak üstünde bin yalan söylemek', 'çok kısa sürede pek çok yalan söylemek: ''Bir ayak üstünde kırk yalanın belini büktüğü hâlde para hesabına bir türlü akıl erdiremez, bakkala bozdurulan paranın gerisini daima eksik getirirdi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1930, 'bir ayak üstünde kırk yalanın belini bükmek', 'çok kısa sürede pek çok yalan söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1931, 'bir (aynı) yastıkta kocamak', 'karı koca birlikte uzun bir ömür sürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1932, 'bir baba dokuz evladı besler, dokuz evlat bir babayı beslemez', 'çok çocuğu olan baba, her çocuk babasına bakılmasını ötekinden beklediği için sıkıntıda kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1933, 'bir baltaya sap olamamak', 'belli bir iş sahibi olamamak: ''Tavla, domino ve muhtelif kâğıt oyunlarından başka bir şey bilmediği için bir baltaya sap olamamıştı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1934, 'bir bardak suda fırtına koparmak', 'önemsiz, küçük bir sorunu büyütmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1935, 'bir baş soğan bir kazanı kokutur', 'kötü bir kişi, kötü bir davranış, kötü bir söz büyük bir topluluğun huzurunu bozmaya yeter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1936, 'bir başa bir göz yeter', 'azla yetinmek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1937, 'bir baştan (uçtan) bir başa (uca)', 'bir yerin bir sınırından öbür sınırına kadar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1938, 'bir ben, bir de Allah bilir', '''çok sıkıntı içindeyim'' anlamında kullanılan bir söz: ''Üç aydan üç aya maaş alıyoruz. Üç ayın sonunu nasıl bulduğumuzu bir biz biliriz, bir de Allah bilir.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1939, 'bir biçimine getirmek', '1) çözüm yolu bulmak: ''Ne olur bir biçimine getir/ yak şu linyiti çıtır çıtır'' -B. R. Eyuboğlu. 2) sırasını, fırsatını bulmak, punduna getirmek, en uygun durumunu yakalamak: ''Bir biçimine getirip benimle Samim''e de veriştiriyormuş.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1940, 'bir ... bir (bir de)', 'hem ... hem: ''Denize bir konup bir kalkan martılar yüksekten avlarına bakarak haykırışırlar.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1941, 'bir boka yaramamak', 'hiçbir şeye elverişli olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1942, 'bir boydan bir boya', 'bir yerin bir ucundan öbür ucuna kadar, baştan başa: ''Önce, bir boydan bir boya sokağı gözden geçirdik.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1943, 'bir bu eksikti', 'sıkıntılı bir durum varken bir yenisinin çıkması üzerine söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1944, 'bir buldu iki ister, akça buldu çıkın ister', 'hırslı insanlar, hiçbir zaman ellerindekiyle yetinmez, daima daha fazlasını isterler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1945, 'bir çatı altında (olmak, bulunmak)', 'aynı yapı, kurum, kuruluş vb. içinde (olmak).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1946, 'bir çekirdek geri kalmamak', 'bütünüyle denk olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1947, 'bir çiçekle bahar (yaz) olmaz', '1) küçük, güzel bir belirti ile doyurucu sonuca ulaşılmaz; 2) çapkın kimseler için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1948, 'bir çift lakırtı etmek', 'kısa konuşmak: ''Adam hesabına koyup bir hatır sormaz, bir çift lakırtı etmezler.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1949, 'bir çift sözü olmak', 'söyleyecek bir şeyleri bulunmak: ''Gel gör ki dilimin ucunda kağnı var. Kağnılar için de bir çift sözüm var.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1950, 'bir çöplükte iki horoz ötmez', 'bir yerde iki kişi baş olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1951, 'bir çuval inciri berbat etmek', 'düzelmekte olan bir durumu yersiz, yanlış davranışlarla bozmak: ''Bir çuval inciri berbat etmişlerin süklüm püklümlüğüyle müfettişin yanına çıktı.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1952, 'bir daha mı', 'hiçbir zaman: Bir daha mı, tövbeler tövbesi!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1953, 'bir dalda durmamak', 'sık sık iş veya düşünce değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1954, '(bir davranışı birine) reva görmek', 'bir davranışı, bir olayı bir kimse için uygun görmek: ''İstanbul''da işgal kuvvetleri fertlerinin halka reva görmediği cefa ve zulüm kalmamıştır.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1955, 'bir dediği bir dediğini tutmamak', 'söyledikleri birbirine uymamak, tutarsız konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1956, 'bir dediği iki olmamak', 'her istediği yapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1957, 'bir deli kuyuya bir taş atar, kırk akıllı çıkaramazmış', 'bir insan bazen akla ve mantığa sığmayan bir iş yapar; yapılan iş, hiçbir kurala uymadığı için pek çok akıllı insan bunu düzeltmeye çalışır, fakat başaramaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1958, 'bir dereceye kadar', 'bir noktaya veya sınıra kadar: Bu sıcak bir derece kadar çekilir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1959, 'bir deri bir kemik (kalmak)', 'çok zayıf (olmak): ''Zaten bir deri bir kemik, zayıf bir adamdı.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1960, 'bir dikili ağacı olmamak', 'hiçbir şeyi olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1961, 'bir dikiş kaldı', 'nerede ise, az kaldı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1962, 'bir dirhem bal için bir çeki keçiboynuzu çiğnemek', 'verimi az, zahmeti çok olan bir işle çok uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1963, 'bir dirhem et bin ayıp örter', 'biraz kilo almak pek çok kusuru örter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1964, 'bir dokun bin ah işit (dinle) (kâseifağfurdan)', 'insanları konuşturmak için biraz dertlerini deşmek yeter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1965, 'bir don bir gömlek', 'yarı çıplak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1966, 'bir dostluk kaldı!', 'mal azaldığında satıcıların kullandığı bir müşteriyi özendirme sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1967, 'bir dönüm güzlük on dönüm yazlığa bedeldir', 'sonbaharda ekilen bir dönümlük yerden, yazın ekilen on dönümlük yerin ürünü kadar ürün alınır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1968, 'bir dudağı yerde bir dudağı gökte', 'masallardaki dev gibi korkunç ve çirkin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1969, '(bir durum) açığa çıkmak', '1) belli olmak, anlaşılmak: ''Ama daha önemlisi komünle bizim aramızda bir anlayış farkı olduğu açığa çıktı.'' -A. Ümit. 2) rıhtıma aborda veya kıçtankara olmuş bir gemi bulunduğu yerden kalkarak daha uzaktaki bir yere demirlemek üzere kıyıdan uzaklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1970, '(bir durum birinin) sinirine dokunmak', 'hoşuna gitmemek, sinirlendirmek: ''Bu söz sarhoş olmayan zevcesinin fena hâlde sinirine dokunmuş.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1971, '(bir durum, düşüncenin) ışığı altında', '''bir durum veya düşüncenin konuyu aydınlatmasından yararlanarak, onu göz önünde tutarak'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1972, '(bir durum) gün ışığına çıkmak', 'açıklığa kavuşmak, aydınlanmak: ''Bu mesele gün ışığına çıkmadıkça toplumun doğru dürüst bir düzen kurabileceğine inanmak zordur.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1973, '(bir durumu) açığa çıkarmak', 'ortaya çıkarmak, gözler önüne sermek, anlaşılır duruma getirmek: ''Yolsuzluklarını açığa çıkarması bardağı taşıtan damla oldu.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1974, '(bir durumu) açığa vurmak', 'belli etmek, ortaya çıkarmak: ''Mantıksal bir dille açığa vurduğu bu harika önerinin aksayan bir yanı vardı.'' -N. Nadi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1975, 'bir düşüncedir (düşünce) almak', 'bir konuda kaygılanarak çözüm yolu bulmaya çalışmak: ''Neyse, sıramızı savdık ve yine yola çıktık ve yolda beni bir düşüncedir aldı.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1976, 'bir el bir eli yıkar, iki el bir yüzü yıkar', 'bazı durumlarda yardımcısız iş yapılamayacağını anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1977, 'bir eli yağda bir eli balda (olmak)', 'varlık ve bolluk içinde (olmak): ''Onlara göre bir eli yağda bir eli balda olan babam için dünyalık hiç bir sıkıntı ve tasa olmamak lazımdı.'' -K. Bilbaşar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1978, 'bir elin nesi var, iki elin sesi var', 'başarıya ulaşmak için birlik olmak gerek.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1979, 'bir elin sesi çıkmaz', '1) bir davanın bir kişi tarafından savunulması etkili ve yeterli değildir; 2) yardımlaşarak işler daha kolay başarılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1980, 'bir elini bırakıp ötekini öpmek', 'aşırı saygı göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1981, 'bir elle verdiğini öbür elle almak', 'yapar göründüğü bir iyiliği, sağladığı bir çıkarla ödetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1982, 'bir elmanın yarısı o, yarısı bu', 'birbirlerine çok benzeyen kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1983, 'bir ev (gemi) donanır, bir kız (çıplak) donanmaz', 'bir kızı donatmak, bir ev düzmekten daha güç, daha masraflıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1984, 'bir evde iki kız, biri çuvaldız biri biz', 'bir evde iki kız olursa her biri bir taraftan aileyi sıkıştırıp giyim kuşam ister, çeyiz ister. Onlar istemese bile aile kendini böyle bir sorumluluk altında bilir ve bunun sıkıntısını çekerler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1985, 'bir felaket bin nasihatten yeğdir', 'yaşanan olaylar, öğütlerden çok daha etkilidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1986, 'bir fende kazık kakmak (çakmak)', 'bir bilgi veya bilim dalında saplanmış kalmak: ''Bir fende kazık kakmaktansa hepsinden birer parça malumat kapma fikrinde idi.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1987, 'bir fincan kahvenin kırk yıl hatırı (hakkı) vardır', 'iyilik küçük de olsa unutulmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1988, 'bir fit bin büyü yerini tutar (yerine geçer)', 'bir kimseyi başkasına karşı kışkırtmak için ara bozacak bir söz, bin büyü kadar etkilidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1989, 'bir gömlek aşağı', 'birinden bir derece daha düşük.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1990, 'bir gömlek fazla eskitmiş olmak', 'birinden daha yaşlı ve daha görmüş geçirmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1991, 'bir görüş bir kör biliş', 'bir kez görmekle bir şey iyice anlaşılmaz, öğrenilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1992, 'bir göz ağlarken öbür göz gülmez', 'keder veya sıkıntı varken dostlar, akrabalar eğlenmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1993, 'bir göz gülmek', 'hem gülüp hem ağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1994, 'bir günlük beylik beyliktir', 'hoşa giden bir durum, kısa da sürse çekici ve güzeldir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1995, '... bir hâl almak (hâle girmek)', '... bir duruma gelmek: Hastalık tehlikeli bir hâl aldı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1996, 'bir hâl olmak', '1) bir şeyin çok tekrarlanması yüzünden bitkin duruma gelmek, usanmak, bezmek, fenalık gelmek: Çocuklar, yapmayın, etmeyin demekten bir hâl oluyorum. 2) huyu değişmek: ''Bu çocuğa bir hâl oldu, bu çocuk avareleşti.'' -Y. K. Karaosmanoğlu. 3) kazaya uğramak; 4) ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1997, 'bir hoş eylemek', 'hüzünlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1998, 'bir hoş olmak', '1) şaşırmak; 2) hüzünlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (1999, 'bir hoşluğu olmak', 'garip veya tuhaf bir durumda olmak: ''Bir hoşluğu var üstünde bugün / Dursun Ağa''nın / Biraz başı ağrıyor / Biraz dişi ağrıyor'' -O. Rifat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2000, 'bir içim su (gibi)', 'çok güzel (kadın): ''Görmeyeli sen büsbütün bir içim su olmuşsun.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2001, 'bir iğne bir iplik olmak', 'iğne ipliğe dönmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2002, 'bir iki demeden (demeye kalmadan)', 'duraksamadan, karşısındakine vakit bırakmadan: ''Sol ayağımı eline aldı, bir iki demeye kalmadan çevirdi mi, bastırdı mı, bilmiyorum.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2003, 'bir iki derken', 'az olmakla birlikte.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2004, 'bir ilke imza atmak', 'bir konuda hiç kimsenin veya kuruluşun yapmadığı bir işi gerçekleştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2005, 'bir inat, bir murat', 'inatçı kişi, her inadında istediği bir şeyi elde eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2006, '(bir iş) aceleye gelmek', 'bir iş yapılırken zaman yetersizliğinden dolayı gereken önem verilememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2007, '(bir iş) akıl kârı olmamak', 'akıllı bir kişinin yapacağı iş olmamak: ''Bunu sormadım; akıl kârı olmadığından soramazdım, zaten.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2008, '(bir iş birinin) vaktini almak (yemek)', 'epey zaman harcanmasını gerektirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2009, '(bir iş) çorba olmak (çorbaya dönmek)', 'karmakarışık duruma gelmek, içinden çıkılmaz bir durum almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2010, '(bir iş) elinde olmak', 'isteyince o işi yapabilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2011, '(bir iş) felce uğramak', 'bir iş yarım kalmak, yürümez duruma gelmek, tam olarak durmak: Yağmur yüzünden trafik felce uğradı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2012, '(bir iş) gâvur orucu gibi uzamak', 'bir iş gereğinden çok sürmek, sürüncemede kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2013, '(bir iş) kâğıt üzerinde (üstünde) kalmak', '1) yapılması düşünülmüş olduğu hâlde yapılmamak; 2) kararı bağlandığı hâlde uygulanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2014, '(bir iş ki) değme keyfine', 'söz konusu işten çok hoşlanıldığını anlatmak için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2015, '(bir iş) medreseye düşmek', ' alay içinden çıkılmaz boş tartışmaların konusu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2016, 'bir iş olmak', 'anlaşılmaz, bilinmeyen bir durum olmak: ''Kaynının geceyi onlarda geçirmesinde vardı bir iş.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2017, '(bir iş) sallantıda kalmak', 'bir çözüme bağlanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2018, '(bir iş) sekteye uğramak', 'kesilmek, kesintiye uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2019, '(bir iş) sürüncemede kalmak', 'bir iş sonuçlanıncaya kadar boş yere gecikmek, uzamak, askıda kalmak, bir türlü sonuçlanamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2020, '(bir iş) uykuda olmak', 'yürütülmemek, olduğu gibi durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2021, '(bir iş veya durum) tersine dönmek', 'beklenildiği, umulduğu gibi gerçekleşmemek, aksi olmak: ''Ya hesapları tersine dönüverirse o vakit başımıza gelecek belada ortağız kardeşim!.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2022, '(bir iş veya durum) tersine gitmek', '1) istenildiği gibi gerçekleşmemek, iyi sonuç vermemek; 2) bir işten veya bir durumdan hoşlanmamak: ''Kızların keman çalması benim o zamanlar bir tersime giderdi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2023, 'bir işaretine bakmak', 'bir işi yapmak için hazır beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2024, '(bir işe) adı karışmak', 'kötü bir işle birinin ilgisi bulunduğu söylenilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2025, '(bir işe) burnunu sokmak', 'gerekmeden her işe karışmak: ''Bir kere burnumu sokmuştum işin içine, sonuna kadar gitmekten başka çare yoktu.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2026, '(bir işe) dört elle sarılmak (yapışmak)', 'bir işe büyük bir özen ve önem vererek girişmek: ''Sen bize dört elle sarılırsan zarar etmezsin.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2027, '(bir işe) eli yatmak', 'eli alışmak: ''Daha çatal ve bıçağı tutmasına eli yatmamıştı, ikide bir düşürürdü.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2028, '(bir işe) kendini vermek (vurmak veya çalmak)', 'bir şeye bütün varlığıyla bağlanmak, başka her şeyle ilgisini kesip tek şeyle aşırı ölçüde ilgilenmek: ''Sattım dükkânı, verdim kendimi tiyatroculuğa.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2029, '(bir işi) aceleye getirmek', 'bir işi üstünkörü, özenmeden yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2030, 'bir işi başından kesmek', 'yapılması istenmeyen bir işi baştan engellemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2031, '(bir işi birinin) sütüne havale etmek', 'işi, beklenen biçimde yapmasını o kişinin vicdanına bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2032, '(bir işi) dallandırıp budaklandırmak', 'bir işi, bir sorunu büyüterek karışık duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2033, '(bir işi) gözü yememek', 'bir işi yapacak güç ve yeteneği kendinde bulamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2034, '(bir işi) pamuk ipliğiyle bağlamak', 'etkisi az sürecek bir çare ile geçiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2035, '(bir işi) piç etmek', ' tkz. 1) yapayım derken bozmak, çıkmaza sokmak; 2) tadını kaçırmak, tatsız bir durum yaratmak: ''Can sıkıntısı, pişmanlık ve öfkenin, bu Vaniköy akşamını nasıl piç edeceğini şimdiden kestirebiliyordum.'' -A. İlhan. 3) boş geçirmek, boşa harcamak: ''Nasıl olsa bugünü de harcadık, piç ettik.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2036, '(bir işi) resmiyete dökmek', 'bir iş veya durumu resmî bir yola sokmak, resmî bir nitelik vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2037, '(bir işi) sürüncemede bırakmak (tutmak)', 'bir işi sonuçlanıncaya kadar boş yere geciktirmek, uzatmak: ''Bana niye bu davayı böyle sürüncemede tuttuğunu izah etsin.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2038, '(bir işi) tatlıya bağlamak', 'kavgalı bir işi gönül hoşluğuyla bitirmek: ''Hayır kardeşim, istemez diye tatlıya bağladım.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2039, '(bir işi) yokuşa koşmak', 'bir konuda güçlük çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2040, '(bir işin) adamı', 'bir işi ustalıkla yapan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2041, '(bir işin) alayında olmak', '1) işi önem vermeyerek yapmak; 2) işi şaka konusu yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2042, '(bir işin) altı yaş olmak', 'işe birtakım oyunlar karışmak, böyle bir işe girişmekte sakıncalar bulunduğu anlaşılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2043, '(bir işin) başında olmak', '1) yöneticisi olmak: Senin müdür başımda olduğu sürece bana da rahat yüzü yoktur. 2) işe sahip çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2044, '(bir işin, bir kimsenin) arkasına düşmek (takılmak)', '1) bir işi sona erdirmek için sıkı çalışmak; 2) birini gözden ayırmayarak arkasından gitmek: ''İstanbul''da ne kadar şair, hikâyeci varsa hepsinin arkasına düşüyor, hepsiyle tanışıyordu.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2045, '(bir işin) hamallığını etmek (yapmak)', 'bir işin önemsiz fakat ağır ve yorucu yükünü taşımak: ''Yok, yok! Sizi kimse hamallık etmeye bırakmaz.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2046, '(bir işin) içinden çıkmak', 'karışık bir işin güçlüklerini yenebilmek, üstesinden gelmek: ''Pek cazip bir iş fakat çok paraya, çok vasıtaya ihtiyaç var. Bakalım bunun içinden nasıl çıkabileceğim?'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2047, '(bir işin) ilerisine gitmek', 'bir işin sonuna kadar gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2048, '(bir işin) ipleri birinin elinde olmak', 'o işi el altından yönetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2049, '(bir işin) kolayına bakmak (kaçmak)', 'bir işi yaparken kolay ve kestirme yolu seçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2050, '(bir işin) sakalı bitmek', ' tkz. bir iş sürüncemede kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2051, '(bir işin, şeyin) başına oturmak', 'bir işi yapmaya başlamak, işe koyulmak: ''Kendine güvenini tam bulduğu, oyununu yazabileceğine inandığı gün oturacaktı masanın başına.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2052, '(bir işin) ucunu kaçırmak', 'iş kötüye girmek, çıkmaza girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2053, '(bir işin veya bir şeyin) ucundan tutmak', '1) bir şeyle meşgul olmak, katkı sağlamak, yardımcı olmak: ''Ömür boyu hiçbir işin ucundan tutmamış insanlar için bile bir yaşlılık fonu düzenlenmiş.'' -H. Taner. 2) mec. bir işi yeterince ilgilenmeden, önemsemeden yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2054, '(bir işin) yolunu yapmak', 'bir işin istediği gibi olması için uygun zemin hazırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2055, '(bir işte) aktif rol oynamak', 'etkili olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2056, '(bir işte) eli olmak', 'karışmış olmak, gizli bir ilgisi bulunmak: ''Şu hâlde Sırrı Beyi Ahmet Samim''in ölümünde de eli olanlardan saymak lazım geliyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2057, '(bir işte) methali olmak', 'bir işe karışmış bulunmak, bir işte parmağı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2058, '(bir işte) parmağı olmak', 'bir işi olumsuz yönde etkilemek, bir işe karışmış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2059, '(bir işte) saç sakal ağartmak', 'o işte uzun zaman çalışmış, emek vermiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2060, '(bir işte) tulum çıkmak', 'amacını eksiksiz elde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2061, '(bir işte) yer almak', '1) bir işi hazırlayanlar arasında bulunmak; 2) ayrılan yerde durmak, bulunmak: ''Derginizde ne tür yazılar yer alıyor?'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2062, '(bir işten) boş çıkmamak', 'bir işten az da olsa bir kazançla çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2063, '(bir işten) el yıkamak', 'ilgisini kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2064, '(bir işten) hariç olmak', 'o işin içinde olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2065, '(bir işten) yüz (yüzünün) akı ile çıkmak', 'bir işi kendi saygınlığını yitirmeden eksiksiz ve başarılı olarak yapıp bitirmek: ''Biz buraya geldi isek her hâlde yüzümüzün akı ile çıkacağımızdan şüphe etmeyesin!'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2066, 'bir iştir oldu', 'istenmeyen, kötü bir durum karşısında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2067, '(bir kadın bir erkekte) gözünü açmak', 'kadın ilk cinsel ilişkiyi o erkekle kurmuş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2068, 'bir kalem geçmek', 'boş vermek, bir an için göz ardı etmek: ''Üniversiteyi filan bir kalem geçin, liseyi bile okuyamamıştı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2069, 'bir kapıya çıkmak', 'aynı sonuca varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2070, 'bir kararda bir Allah', 'gücü, büyüklüğü eksilmeyip aynı kalan yalnızca Tanrıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2071, 'bir karış beberuhi', ' alay çok kısa boylu kimse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2072, 'bir karıyla bir koca, dırdır eder her gece', 'sıkıntı veya yalnızlık yüzünden iki dost bile birbiriyle dalaşır, anlamsız konuşur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2073, 'bir kazanda kaynamak', 'anlaşmak, uyuşmak, bağdaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2074, 'bir kenara atılmak', 'unutulmak, terk edilmek, ilgi kesilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2075, 'bir kenarda durmak', 'gerektiği zaman kullanmak üzere hazırda tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2076, 'bir kıza dünür düşmek', 'bir kızı evlenmek üzere başkası için istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2077, '(bir kıza) dünür düşmek', 'bir kızı evlenmek üzere başkası için istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2078, 'bir kızı bin kişi ister, bir kişi alır', 'bir şeyi herkes ister ancak onu bir kişi elde edebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2079, '(bir kızı) leğen başından almak', 'hamarat diye seçerek almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2080, 'bir kimsenin adı çıkacağına canı çıksın', 'insanın haklı veya haksız yere adı bir defalık kötüye çıktı mı ondan sonra yaptıkları hep o gözle değerlendirilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2081, '(bir kimseye, bir şeye) ihtiyaç duymak', 'o kimse veya şey gerekli saymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2082, 'bir kol çengi', 'şen sözler ve davranışlarla çevresine neşe saçanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2083, 'bir kolayını aramak', 'bir şeyi yapmak, çözmek için gerekli kolay ve kestirme yöntemi araştırmak: ''Yanlışını düzeltmek için bir kolayını aramaya başladı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2084, 'bir kolayını bulmak', 'kolaylıkla yapabilmeyi sağlamak veya yapma yolunu bulmak: ''Etrafında, bir kolayını bulup dışarıya sızanlardan birkaç kişi ha bire ellerinden öpüyor.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2085, 'bir koltuğa iki karpuz sığmaz', 'aynı zamanda birden çok işle ilgilenmek başarı için sakıncalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2086, 'bir korkak bir orduyu bozar', 'bir toplumda korkak kişi, kaygılı, heyecanlı sözleriyle kargaşa çıkarır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2087, 'bir koyundan iki post çıkarmak', 'olması gerekenden daha fazla elde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2088, 'bir Köroğlu, bir Ayvaz', 'bir karı kocanın çocuklarının, yakınlarının yanlarında bulunmadığını veya çocukları olmadığını anlatan bir söz: ''Eve işçi, aşçı tutmam, kaynana, baldız istemem. Bir Köroğlu bir Ayvaz.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2089, 'bir köşeye atılmak', 'terk edilmek, ilgilenilmemek, kendi kaderine terk edilmek: ''Böyle bir köşeye atılmak, iktidardan uzak kalmak, diri diri gömülmekti benim için.'' -T. Oflazoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2090, 'bir köşeye çekilmek', 'hiçbir işe karışmayarak yaşamak: ''Bir köşeye çekilip ölümü beklemek.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2091, 'bir köşeye koymak', 'saklamak, biriktirmek: ''Yıllardan beri dişinden tırnağından artırdığı, çoluk çocuğunun nafakasından kestiği parayı günün birinde, ben de bu zilletten kurtulurum umuduyla bir köşeye koymuştu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2092, 'bir köşeye oturmak', 'gelin olmak, evlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2093, 'bir kötünün yedi mahalleye zararı vardır (dokunur)', 'bir kötünün, yalnızca yakın çevresine değil daha geniş çevrelere de zararı dokunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2094, 'bir kulağından girip öbür kulağından çıkmak', 'söylenen söze önem vermemek: ''Fakat bütün bu sözler benim bir kulağımdan girip öbür kulağımdan çıkıyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2095, 'bir kurşun atımı', 'kurşunun gidebileceği uzaklık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2096, 'bir kuşsütü eksik', 'her türlü yiyecek var.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2097, 'bir lokma bir hırka', 'hayatta azla yetinmeyi, dervişçe geçinmeyi anlatan bir söz: ''Toplumumuzda sanatçı-edebiyatçının bir lokma bir hırka felsefesi uyarınca yaşaması beklenir.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2098, 'bir mıh bir nal kurtarır, bir nal bir at kurtarır', 'herhangi bir olayı, bir işi, bir ödevi küçümsememek, önemle ele almak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2099, 'bir mum al da derdine yan', 'başkalarıyla uğraşacağına kendi durumunu düşün.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2100, 'bir o kadar', 'ne kadar varsa o kadar daha, bir katı, bir misli.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2101, 'bir o yana, bir bu yana', 'rastgele, birçok yere, çeşitli yönlere.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2102, '(bir olayın) arası soğumak', 'aradan zaman geçerek önemini yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2103, 'bir olmak', 'bir araya gelmek, iş birliği yapmak: ''Böyle bir iftirayı yedi düvel bir olsa, yedi hafta tetkik eylese, künhüne eremez.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2104, 'bir papel (pul) etmemek', 'değeri olmamak: ''Topunuzu satsam bir papel etmezsiniz. Hele bunu şehirde yapaydınız dumanınızı savururlardı, dedi.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2105, 'bir paralık etmek', 'çok utanacak, işe yaramaz bir duruma düşürmek: ''Burnumuzun dibinde araba soydular, namusumuzu bir paralık ettiler.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2106, 'bir (sağ) elinin verdiğini öbür (sol) elin duymasın (görmesin)', 'birine yaptığın iyiliği gizli tut.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2107, 'bir selam bin hatır yapar', 'selam bir ilgi ve sevgi belirtisidir, gönül kazanmakta büyük önemi vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2108, 'bir sıçrarsın çekirge, iki sıçrarsın çekirge, sonunda yakalanırsın çekirge (üçüncüsünde avucuma düşersin çekirge)', 'birkaç kez saklanabilen bir suç günün birinde ortaya çıkarak yapanı kötü bir duruma düşürür, suçlu cezasız kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2109, 'bir sıkımlık canı olmak', 'çok cılız ve güçsüz olmak: ''Bir sıkımlık canın var. Bu boyla bir de adam korkutmaya kalkarsın ha diye ensesine iki tokat attım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2110, 'bir söyle on dinle', 'az konuşup çok dinlemek yararlı olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2111, 'bir söylemek pir söylemek', 'uzatmadan gereği gibi söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2112, '(bir söz, birilerinin) ağzında çalkalanmak', 'üzerinde çok konuşulmak: ''Fakat bütün memleketin ağzında çalkalanan bu evlerin anha minha 5000 liradan fazlaya çıkmayacağı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2113, 'bir sürçen atın başı kesilmez', 'şimdiye kadar sizi memnun etmiş olan kişi bir kez yanlış iş yaptığında kendisine hemen ağır ceza verilmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2114, '(bir şey) abanoz kesilmek', '1) sertleşerek dayanıklılığı artmak; 2) kirden dolayı matlaşmak, rengini kaybetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2115, '(bir şey) akılda kalmak', 'akılda yer etmek, unutulmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2116, '(bir şey) akıldan çıkmak', 'unutulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2117, '(bir şey) aleyhe dönmek', 'destek vermekten vazgeçip karşı duruma geçmek: ''Şimdi iş tamamıyla aleyhimize döndü.'' -A. Rasim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2118, '(bir şey) aslanın ağzında olmak', 'elde edilmesi çok güç olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2119, '(bir şey) ayağa düşmek', '1) ilgisiz ve yetkisiz kimseler karışmak; 2) artık her yerde bulunabilir olmak: Bu ürünler ayağa düştü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2120, '(bir şey) az gelmek', 'yetmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2121, '(bir şey başka bir şeyi) mumla aratmak', 'daha kötü olan yeni bir şey, bir durum, bir kimse, pek iyi olmayan eskisini aratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2122, '(bir şey) bini aşmak', 'çok fazla olmak, sınırı aşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2123, '(bir şey, bir şeyi) ağır basmak', '1) taşıdığı özellikler üstün gelmek: ''Yerli halıları gördüm; koyu sıcak kırmızılarla diri maviler ağır basıyordu.'' -B. R. Eyuboğlu. 2) bir işte gücü ve etkisi üstün gelmek: ''Peki deyişleri de akılları yattığı için değil, korkuları ağır bastığı için oldu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2124, '(bir şey, bir) vadiye dökülmek', 'sohbet belirli bir konuya kaymak: ''Musahabe bu vadiye dökülünce tekrar karışmak ihtiyacını duydum.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2125, '(bir şey birine) alay gibi gelmek', 'inanılacak gibi olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2126, '(bir şey birine) haram olmak', 'bir şeyden gereği gibi yararlanamamak: ''Yanında oturup iki laf etmek haram oldu bize.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2127, '(bir şey, birinin) ağzının kaşığı (kalıbı, lokması) olmamak', '1) bir şey, bir kimsenin uğraşabileceği konulardan olmamak; 2) bir şey, bir kimsenin sözünü edemeyeceği kadar değerli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2128, '(bir şey birinin) aklını başından almak', 'bir şey birini düşünemeyecek bir duruma getirmek, çok şaşırtmak: ''Beyim böyle latife olur mu? Aklımızı başımızdan aldınız diye isyan etti.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2129, '(bir şey birinin) aklını çalmak', 'ilgisini aşırı derecede çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2130, '(bir şey birinin) başının altından çıkmak', 'birinin hilesiyle yapılmak: ''Anlaşıldığına göre bu iş Saniye''nin İstanbullu anasının başının altından çıkmıştı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2131, '(bir şey birinin) belini bükmek', 'çaresizlik içinde bırakmak: ''Şu kör olası işsizlik belimi fena hâlde büküyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2132, '(bir şey birinin) boyunu aşmak', 'kişinin gücünün, yeteneğinin, yetkisinin üstünde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2133, '(bir şey birinin) gözünde olmamak', 'herhangi bir üzüntü veya zor durum dolayısıyla o şeye değer verecek durumda bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2134, '(bir şey birinin) gücüne gitmek', 'gönlü kırılmak, onuruna dokunmak: ''Bugünkü hâlimizle tabiatın sırlarını kavrayamayacağımızı düşünmek bizi sinirlendiriyor, gücümüze gidiyor.'' -N. Ataç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2135, '(bir şey birinin) tuhafına gitmek', 'o şeyi tuhaf bulmak: ''Ömründe bu kadar tuhafına giden söz işitmemiş olduğunu söylerdi.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2136, '(bir şey, birinin) vazifesinden olmak', 'bir şey o kimsenin görevleri arasında olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2137, '(bir şey birinin) zevkini okşamak', 'o şeyden hoşlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2138, '(bir şey birinin) zıddı olmak', 'bir şey birini tedirgin etmek, hoşuna gitmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2139, '(bir şey birinin) zihnini bulandırmak', 'kuşkuya düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2140, '(bir şey birinin) zihnini kurcalamak (tırmalamak)', '1) bir şey sık sık hatırlanıp insanı düşündürmek: ''Beni sevmiyor, yeniden zihnimi kurcalamak, sinirlerimi üzüntü içinde bırakmak istiyor.'' -E. İ. Benice. 2) çözülmesi gerekli bir konu üzerinde durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2141, '(bir şey birinin) zoruna gitmek', 'onuruna dokunmak, gücüne gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2142, '(bir şey) buradayım diye bağırmak', 'göze çarpacak bir yerde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2143, '(bir şey) çok gelmek', '1) gereğinden fazla olmak: ''Mahkeme masrafı çok geldiği için bu isteklerini gerçekleştiremediler.'' -Ü. Dökmen. 2) çekilmez ve katlanılmaz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2144, '(bir şey) dişe dokunur (olmak)', 'işe yarar, belirtilmeye değer, önemli (olmak): ''Bu türlü yazıları okumaktan -içlerinde dişe dokunur bir şey olmadığı için midir, nedir- pek hoşlanmıyorum.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2145, '(bir şey) el değiştirmek', 'bir şeyin kullanımı veya mülkiyeti bir kimseden başka bir kimseye geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2146, '(bir şey) elden gitmek', 'bir şeyi yitirmek, o şeyden yoksun kalmak: ''Kahramanlıktı yurdun meyve veren tek dalı / O da elden giderse nereye başvurmalı?'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2147, '(bir şey) eli altında olmak', 'buyruğunda olmak, istediği anda o şeyden yararlanabilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2148, '(bir şey) gözüne ilişmek', 'birdenbire, istemeden görmek: ''Tam kapı yanında bir sütçü dükkânı gözüme ilişti.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2149, '(bir şey) gözünü almak', '1) şiddetli ışık sebebiyle gözü iyi göremez duruma getirmek; 2) mec. aşırı biçimde etkilenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2150, '(bir şey) haritada olmak', 'göz önünde bulundurulması gerekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2151, '(bir şey) hava almak', 'içine hava girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2152, '(bir şey için) birebir gelmek', 'etkisini hemen ve kesin olarak göstermek: ''Şeftali suyu ile yapılacak gargaranın sonradan olma kekemeliğe birebir geleceğini söyler.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2153, '(bir şey için veya bir şeye) deli olmak', 'tkz. 1) çok sevmek: Deli oluyordu çocuklara, onlarsız edemiyordu. 2) çok sinirlenmek; 3) delirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2154, '(bir şey) içinde yüzmek', 'olumlu veya olumsuz bir durumun aşırı derecesinde bulunmak: Para içinde yüzmek. Sefalet içinde yüzmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2155, '(bir şey) iki baştan olmak', 'bir şey, her iki tarafın aynı şeyi istemesiyle, iyi niyetiyle gerçekleştirilebilmek: İyi geçim iki baştan olur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2156, '(bir şey) kanına işlemek', '1) bir şeyi aşırı ölçüde benimsemek: ''Kanıma işleyen müzik ateşinden, kurduğum şarkıcılık düşlerinden ne yazık ki söz edemeyeceğim.'' -M. Mungan. 2) büyük ölçüde etkisinde kalmak: ''Her şeye karşın iş adamlığı kanına işlemişti.'' -T. Yücel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2157, '(bir şey) kapak atmak', 'aşırı, tıka basa dolmuş olmak: Elbise dolabı kapak atıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2158, '(bir şey) kir götürmek', 'kirini belli etmeyecek bir renkte olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2159, '(bir şey) kir tutmak', 'kirini hemen belli edecek bir renkte olmak, çok kirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2160, '(bir şey) kursağında kalmak', 'istenilen bir şey gerçekleşememek, yarım kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2161, '(bir şey) mecrasında gitmek', 'bir iş kurallarına uygun bir biçimde yürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2162, '(bir şey) muallakta olmak (kalmak)', 'sonuca bağlanmamak, sürüncemede kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2163, '(bir şey olmaya) yüz tutmak', '1) bir şey, olmak üzere bulunmak: ''Duvarları sıvasız, kepenkleri boyanmadan bırakıldığı için çürümeye yüz tutmuş evde Hatice nine oturuyordu.'' -N. Cumalı. 2) giderek biçim ve renk değiştirmek: ''Hepimiz gölgelenmeye yüz tutan ateşe gözlerimizi dikmiştik.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2164, 'bir şey sanmak', 'bir kimseyi, bir şeyi, bir yeri gerçeğinden, olduğundan başka türlü düşünerek hayal kırıklığına uğramak, değerlendirmede yanılmak: Tüccar deyince biz de onu bir şey sandık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2165, 'bir şey söylemek', '1) konuşmak; 2) belirtmek, anlatmak, ifade etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2166, '(bir şey) su gibi gitmek', 'bol bol harcanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2167, '(bir şey) su sabun görmemek', 'çok kirli olmak: ''Elleri, tırnakları, yüzü günlerdir su sabun görmemişti sanki.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2168, '(bir şey) suyu nereden geliyor?', '''bir işi görmek için harcanan para hangi kaynaktan sağlanıyor?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2169, 'bir şey (şeyler) olmak', '1) huyu, durumu, tutumu değişmek, yeni huylar edinmek: Son zamanlarda ona bir şeyler oldu. 2) bayılır gibi olmak, birden fenalık gelmek: Bana bir şeyler oluyor, dedi ve bayıldı. 3) ölmek: Bana bir şey olursa çocuklar size emanet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2170, '(bir şey) ters gitmek', 'bir iş doğru ve düzgün yürümemek, sorun çıkmak: ''Kadın elinde olmadan yutkundu, bir şeylerin ters gittiğini hemen anlamıştı.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2171, '(bir şey veya bir şeyi) suya düşmek', 'gerçekleşme olasılığı kalmamak: ''Artık karşı koyma ümidi suya düşmüştü, harp her cephede kaybedilmişti.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2172, '(bir şey) yolunda görünmek', 'sorunsuz olduğu anlaşılmak: ''Gecekondunun ışıkları yanıyor ve her şey yolunda görünüyordu.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2173, '(bir şey) yüzü görmemek', '...-den yoksun olmak, uzak bulunmak: ''Yüzyıllardan beri sabah yüzü görmemiş uçsuz bucaksız kıraç topraklar.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2174, '(bir şeyde) gönlü olmak', 'sevip istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2175, '(bir şeyde) gözü olmak', 'bir şeyi ele geçirmek isteği beslemek: ''Allah bilir, milletvekilliğinde de gözü vardır.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2176, '(bir şeyde) gözü olmamak', '1) bir şeye sahip olmayı istememek; 2) heves beslememek, fazla önem vermemek: ''Giyinip kuşanmakta, gezip tozmakta gözüm yok.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2177, '(bir şeyde) hayır kalmamak', 'işe yarar durumu kalmamak, artık işe yaramaz olmak: ''Bir iki yıla varmaz, ne evden ne eşyadan hayır kalır.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2178, '(bir şeyde) iş yok', ' tkz. ''o şeyden yarar beklememeli'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2179, '(bir şeyde) karar bulmak', '1) kararlı bir durum almak; 2) yatışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2180, '(bir şeyde) tuzu olmak', 'katkısı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2181, '(bir şeyden) baş alamamak', 'çok uğraştıran bir konu yüzünden vakit ve fırsat bulamamak: ''Benim hilem hurdam yoktur, canı isteyen baktırmasın, zaten bu sanattan memnun değilim. Lakin baş alamıyorum ki.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2182, '(bir şeyden) baş almak', 'fırsat bulmak: ''Ara sıra işten baş aldıkça Semiha''yı özlüyordum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2183, '(bir şeyden) başını alamamak', 'bir şeyden kurtulamamak: Dertten başını alamıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2184, '(bir şeyden, birinden) geri kalmamak', '1) yapmaktan kaçınmamak: ''Daima müttefikler lehine çarpışmaktan geri kalmadılar.'' -F. R. Atay. 2) birinden daha az başarılı olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2185, '(bir şeyden) dem vurmak', 'bir şeyden söz etmek, konu açmak: ''Benim gibi kurak çölde yaşayanların şiirden, hünerden dem vurmaları nasıl mümkün olur?'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2186, '(bir şeyden) el ayak (etek) çekmek', 'uzaklaşmak, kaybolmak: ''Tarzının, yönteminin piyasadan el ayak çekmek zorunda kalacağını açık seçik kavrıyorsunuz.'' -S. İleri. ''Bazı meddahlar da Karagöz oynatmış, şahbaz, hayalbaz veya hayalî isimleriyle yaşadıktan sonra temaşa hayatımızdan el etek çekmişlerdir.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2187, '(bir şeyden) geçilmemek', 'bol, çok, aşırı olmak: Çarşıda meyveden geçilmiyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2188, '(bir şeyden) gözünü ayırmamak', 'bir şeye sürekli olarak bakmaktan kendini alamamak: ''Ateşoğlu, bir yandan da gözlerini deniz yüzüne gelen ve yüzde suyu fokurdatan hava habbelerinden ayırmıyordu.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2189, '(bir şeyden) hayır görmemek', 'bir şey kendisine yararlı olmamak: ''Ne o, ne ben bu seçimlerimizin hayrını görmüştük!'' -H. F. Ozansoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2190, '(bir şeyden) hayır yok', '''bir şey yararlı değil'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2191, '(bir şeyden) ileri gelmek', '1) oluşmak, meydana gelmek: ''O kadar üşümesi trende saatlerce hareketsiz kalmasından ileri geliyordu.'' -S. F. Abasıyanık. 2) neden olmak; 3) bağlı bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2192, '(bir şeyden) kâm almak', 'umduğunu ve istediğini elde etmek, dilediği biçimde zevk almak, keyfini çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2193, '(bir şeyden) kapı açmak', '1) bir şeyin sözünü etmek veya bir işe başlamak; 2) pazarlığa çok yüksek bir fiyatla başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2194, '(bir şeyden) mana (manası) çıkmak', 'anlamına gelmek, anlamını taşımak: ''Kızın adını Emel koydu. Oğlanınkini Fethi ... Sanki bundan emelini fethetmiş manası çıkıyordu.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2195, '(bir şeyden) söz etmek', 'o şey üzerinde konuşmak: ''Dilin çağdaş kadın yazara hazırladığı tuzaklardan söz etmişti.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2196, '(bir şeyden) sünger geçirmek', 'silip atmak, unutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2197, '(bir şeyden) zevk almak (duymak)', 'hoşlanmak, beğenmek: ''Yılan gibisin, insanları sokmaktan zevk alırsın.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2198, '(bir şeye) akıl sır ermemek', 'bir işin niteliğini, gizli yönlerini anlayamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2199, 'bir şeye benzememek', 'işe yarar durumda olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2200, '(bir şeye, bir kimseye) yazık olmak', 'boş yere zarar verilmek: Masrafa yazık oldu. Adama yazık oldu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2201, '(bir şeye) can dayanmamak', 'bir şey karşısında insanın dayanıklılığı elden gitmek: ''Bir lacivert petunya vardır ki renginin hoşluğuna canlar dayanmaz.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2202, '(bir şeye) can gelmek', 'canlanmak, güçlenmek: ''Vücudumuza serinlik, ferahlık yayılıyor / Kan verilen bir yaralı imişçesine cismime can geliyor'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2203, '(bir şeye) cila vermek', 'aydınlatmak: ''Çocukluk günlerin hatırası zihinlerine cila vermişti.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2204, '(bir şeye) doyum olmamak', '1) bir şeyden bıkılmamak: ''İnsanına, nimetine, hayaline, hürriyetine, şairine, şarabına doyum olmuyor.'' -N. Hikmet. 2) bir şey yetmemek, bir şeye kanamamak; 3) bir şeyi çok fazla beğenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2205, '(bir şeye) dürbünün tersiyle bakmak', 'bir şeyi küçümsemek, olduğundan çok daha az önemli görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2206, '(bir şeye) düşkün olmak', 'çok önem, değer vermek: ''Şiire milletçe düşkün oluşumuzun sebeplerini araştırırken kafiye merakımıza takıldım.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2207, '(bir şeye) elini sürmemek', '1) eliyle dokunmamak; 2) mec. hiç karışmamak, bir şey yapmamak: ''O gün akşamı böyle ettik, kimse elini işe sürmedi.'' -M. İzgü. 3) mec. bir işi kendine yakıştırmayarak tenezzül etmemek; 4) mec. ilgi göstermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2208, '(bir şeye) Fatiha okumak', 'o şeyden umudunu kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2209, '(bir şeye) gebe olmak', 'bir şeyin gerçekleşme olasılığı bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2210, '(bir şeye) gölge düşürmek', 'bir şeyin değerini veya ününü azaltacak işler yapmak: ''Bu iki yazarın usta hikâyeci vasıflarına gölge düşürmüştür.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2211, '(bir şeye) gönlü olmak', 'razı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2212, '(bir şeye) gözü gitmek', 'bir şeyi istemeden görmek, elinde olmayarak bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2213, '(bir şeye) gözü (gözleri) takılmak', 'dikkati çeken bir şeyden bakışlarını ayıramamak: ''Gözleri başka bir sahifenin ortalarına takıldı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2214, '(bir şeye) gözünü yummak', 'görmezlikten gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2215, '(bir şeye) ... gözüyle bakmak', 'yerine koymak: Kardeş gözüyle bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2216, '(bir şeye) haciz koymak', 'borçlunun malına el koymak: ''Ya parayı verirsiniz ya da haciz korum.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2217, '(bir şeye) hasret bırakmak', 'gerektiği anda bir şeyin yokluğunu hissettirmek: ''Kış günü, çoluğu çocuğu battaniyeye hasret bırakıp hepsini topladım, balkonda yattım.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2218, '(bir şeye) hayat vermek', 'canlılık vermek, canlandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2219, '(bir şeye) imza atmak', 'imzalamak: ''Önüne bir tomar parşömen çeken ağa, yeni öğrendiği imzasını atmaya başladı.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2220, '(bir şeye) kanaat getirmek', 'kanmak, aklı yatmak, inanmak: ''Artık Kâmuran''ın ömrümün en büyük aşkı, geleceğime bir tek hâkim kudret olduğuna kanaat getirdim.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2221, '(bir şeye) ket vurmak', 'engel olarak güçleştirmek: ''Yerli atölyelerin işine ket vuruyorlarmış.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2222, '(bir şeye, kimseye) yazık etmek', 'boş yere zarar vermek: Kumaşa yazık etti. Çocuğa yazık ettiniz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2223, '(bir şeye) kul olmak', 'aşırı derecede bağlanmak, boyun eğmek: ''Ben serüvenlere kul olmayacağım, serüvenler bana kul olacak.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2224, '(bir şeye) kulak (kulaklarını) tıkamak', 'bir şeyi duymazlıktan gelmek: ''Vücudu içinden duyduğu çöküntülere kulaklarını tıkar, gözlerini yumar.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2225, '(bir şeye) kulak vermek', 'değer vermek, önemsemek: ''Usa ve gerçeğe uygun anlatışlara kulak verenin olmadığı görüldü.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2226, '(bir şeye) kuvvet vermek', 'bir konuya çok önem vermek: Matematiğe kuvvet verince öbür derslerini yetiştiremedi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2227, '(bir şeye) merak sarmak (duymak, salmak)', 'bir şeyi edinme, yapma veya onunla uğraşma isteğine kapılmak, bir şeye eğilim duymak: ''Bu adama, her gördüğüm vakit, merhamet ve korku ile karışık bir merak duyuyordum.'' -R. N. Güntekin. ''Miralay beyimiz, emekli olduktan sonra komisyonculuğa kalkan veya cins tavuk yetiştirmeye merak salan soydan değildir.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2228, '(bir şeye) pamuk ipliğiyle bağlanmak', 'her an bozulmaya, kopmaya hazır olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2229, '(bir şeye) renk gelmek', 'renklenmek, canlanmak: ''Sarı yanaklarına hafif bir renk geldi.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2230, '(bir şeye) sünger çekmek', 'bir şeyi hiç olmamış saymak, silmek, silip atmak, unutmak: ''Bir türlü doyamadığım hürriyetimin üstüne sünger çekmek lazım geliyordu.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2231, '(bir şeye şu veya bu) nazarıyla bakmak', 'ona öyle imiş gibi, o gözle bakmak: Ona düşman nazarıyla bakıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2232, '(bir şeye) tuz biber ekmek', 'üzüntüyü, kusuru artıracak durum yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2233, '(bir şeye) yatkın bulmak', 'uygun görmek: ''Bugün birçoğumuzun romana yatkın bulmayacağı anlatımları pek rahat kullanmıştır.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2234, '(bir şeye) yüz tutmak', 'yönelmek: ''Biçare Yunus''un çoktur günahı / Hakk''ın dergâhına yüz tutmuşum ben'' -Yunus Emre.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2235, '(bir şeye) yüzü olmamak', '1) o şeye dayanamamak; 2) cüret ve cesareti olmamak; 3) utanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2236, '(bir şeye) yüzü tutmamak', '1) haklı da olsa karşısındakini kıracak bir davranışta bulunmaktan çekinmek: ''O böyle kimseyi kırmak istemedikçe, kimseye olmaz demeye yüzü tutmadıkça ne kadar istemese çevresi onu kıracak, üzecekti.'' -N. Cumalı. 2) utanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2237, '(bir şeye) zihni takılmak', '1) yanlış bir kanıya takılıp kalmak; 2) çözülmesi gerekli bir konu üzerinde durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2238, '(bir şeyi) abes bulmak', 'gereksiz, saçma sapan olarak kabul etmek: ''Annem eniştemizin bu son sözlerini dinlemeyi artık abes bulurdu.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2239, '(bir şeyi) ağzına sürmemek', 'herhangi bir yiyeceği veya içeceği hiç yememek veya içmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2240, '(bir şeyi) ağzında gevelemek', 'açıkça söylememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2241, '(bir şeyi) aklına koymak', '1) bir şeyi yapmaya kesin olarak karar vermek: ''Fakat Ömer birinci mevkiye oturmayı aklına koymuştu.'' -N. Hikmet. 2) çok istemek: ''Bir düşünsün, meslekte kalmayı aklına koyuyorsa gecikmesin, vardiyaya buyursun.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2242, '(bir şeyi) aklında tutmak', '1) bellemek; 2) unutmamak: ''Nasıl aklında tutar bilinmez, gelmiş geçmiş onca başbakanın adlarını sayar.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2243, '(bir şeyi) anlata anlata bitirememek', 'beğenilen bir şeyden çok söz etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2244, '(bir şeyi) arkada bırakmak', '1) bir şeyden epey uzaklaşmış bulunmak; 2) zaman veya düşünce bakımından geçmişte bırakmak: ''Uyandığımız zaman üçte birini arkada bırakmışızdır başlayan günün.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2245, '(bir şeyi) ayakta tutmak', '1) o şeyin sürekliliğini sağlamak: ''Meddahlar seyirciyi meraklandırmayı, ilgilerini sürekli ayakta tutmayı çok iyi bilirler.'' -M. And. 2) bozulmasına, yıkılmasına, çökmesine engel olmak; 3) bir kuruluşun yaşamasını sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2246, '(bir şeyi) başlangıç tutmak (almak)', 'bir işi, bir dönemin, başladığı nokta veya tarih olarak kabul etmek, belirtmek: ''Tarihler, bu sorunu açıklarken 1071 yılını başlangıç tutarlar.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2247, '(bir şeyi) bir köşeye atmak', 'gerektiğinde kullanılmak için bir yere koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2248, '(bir şeyi, bir şeye) nişan koymak', 'ileride tanıyabilmek veya ölçebilmek için bir şeyin durumunu, onun herhangi bir özelliğini akılda tutmak veya iz bırakmak: Dönüşte yolumuzu şaşırmamak için şu çifte kavakları nişan koymuştuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2249, '(bir şeyi, bir yeri) siper almak', 'bir şeyi siper olarak kullanarak gizlenmek: ''Kayaların arasını siper aldım, çevreyi gözetlemeye başladım.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2250, '(bir şeyi birine) çok görmek', 'yadırgamak: ''Mehmetçiğimiz ayrıca anıtlara layıktır. Onun köylere kadar anıtlaştırılmasını çok görmem.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2251, '(bir şeyi birine) haram etmek', 'o şeyden umulan yarar ve rahatı tattırmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2252, '(bir şeyi birinin) başına sarmak', 'birine musallat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2253, '(bir şeyi) çorba etmek', 'karıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2254, '(bir şeyi) deve yapmak (etmek)', 'başkasının malını kendine mal etmek: ''Onu soyup soğana çevirecek, babasından kalan evleri, dükkânları birtakım maceralar yüzünden deve yapacaktı.'' -O. C. Kaygılı. ''Allem ettiler kallem ettiler sonunda bizim eşeği deve ettiler.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2255, '(bir şeyi) garanti etmek', '1) o şeyle ilgili olarak güvence vermek; 2) bir işin gerçekleşmesi için gerekli önlemleri almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2256, '(bir şeyi) gâvur etmek', 'boşuna harcamak, yerinde harcamamış olmak, işe yaramaz duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2257, '(bir şeyi) geri vermek', 'aldığı yere veya kimseye vermek, iade etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2258, '(bir şeyi) gizli tutmak', 'başkalarına duyurmamak, saklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2259, '(bir şeyi) gözü gibi sakınmak (saklamak veya esirgemek)', 'bir şeye aşırı ilgi göstermek, önemle bakıp korumak: ''Doğru, hakları vardı, koskoca sandalıyla da beraber gömemezdiler ama çok sevdiği, gözü gibi esirgediği ağlarıyla gömebilirlerdi.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2260, '(bir şeyi) gözü gibi sevmek', 'pek çok sevmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2261, '(bir şeyi) hazır etmek', 'hemen kullanabilecek duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2262, '(bir şeyi) hedef almak', '1) nişan almak; 2) ulaşılmak istenen amaca göre davranmak; 3) bir kimseyi, bir yeri yıpratmak, eleştirmek amacıyla karşısına almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2263, '(bir şeyi) hesaba almak', 'göz önünde bulundurmak, işini yürütürken o şeyi de düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2264, '(bir şeyi) hesaptan düşmek', 'hesaptan, borçtan, alacaktan indirmek, çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2265, '(bir şeyi) hurdaya çevirmek', 'işe yaramaz duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2266, '(bir şeyi) içi kabul etmemek', '1) bir şeyden midesi bulanmak; 2) mec. benimsememek, kabullenememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2267, '(bir şeyi) içinde duymak', 'hissetmek, varlığını algılamak: ''Donmak üzere olan insanların tatlılığını içimde duymaya başladım.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2268, '(bir şeyi) imza etmek', 'imzalamak: ''Bir haftaya kalmayacak, bizim delegeler sulhu imza edecekler.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2269, '(bir şeyi) katık etmek', 'ekmeğin çok, yemeğin az olduğu durumlarda yemeği ölçülü yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2270, '(bir şeyi) kendi hâlinde bırakmak', 'üzerinde çalışmayarak geliştirmemek veya bakımsız bırakmak, işlememek: ''Nasıl çalışmayan küf tutarsa bir müessese de gençleştirilmez, kendi hâlinde bırakılırsa ihtiyarlar, yıkılır, dağılır.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2271, '(bir şeyi, kendini) siper etmek', '1) kendini veya bir şeyi korumak amacıyla bir başka şeyi siper olarak kullanmak: ''Tuğla harmanındaki ameleler durup ellerini gözlerine siper ederek etrafı aradılar.'' -S. F. Abasıyanık. 2) bir şey veya bir kimse için kendini tehlikeye atmak: ''Siper ederek etrafı aradılar.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2272, '(bir şeyi) kir götürmek', 'bir şey çok kirli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2273, '(bir şeyi) kitaba (kitabına) uydurmak', 'yasal olmayan bir işi hile, düzen vb. ile kanuna uygun gibi göstermek: ''Müfettiş Bey güldü ama babacan gülüşü değildi bu; tam tersine, işini kitaba uydurmuşların güveni vardı onda.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2274, '(bir şeyi) kuvveden fiile çıkarmak', 'düşünülen, tasarlanan şeyi gerçekleştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2275, '(bir şeyi) masaya yatırmak', 'bir konuyu, olayı enine boyuna araştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2276, '(bir şeyi) maymuna benzetmek (çevirmek, döndürmek)', 'gülünç ve çirkin duruma sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2277, '(bir şeyi) mesele yapmak', 'önemsiz bir şeyi önemli bir sorun durumuna getirmek: ''Orada kimseyi kıskanmamışken bu sonuncu kumasını büyük mesele yaptı.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2278, '(bir şeyi) meslek edinmek', '1) bir işi meslek olarak yapacak bilgi ve beceriyi kazanmak; 2) mec. bir şeyi yapmayı alışkanlık hâline getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2279, '(bir şeyi) nefsine yedirememek', 'bir şey yapmayı kendisi için ağır, onur kırıcı bulmak: ''Riyakârlığı da bir türlü nefsine yediremiyordu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2280, '(bir şeyi) rafa koymak (kaldırmak)', 'savsamak, artık üstünde durmamak, ihmal etmek: ''Anayasayı rafa kaldırarak keyfî, gelişigüzel sınırlar çizmeye kalkışmak, bu yaygaraları koparanların başlıca özelliğidir.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2281, '(bir şeyi) sağlam kazığa bağlamak', 'işin sonuçlanmasına engel olacak şeyleri ortadan kaldırmak, işin aksamadan yürümesini sağlayacak önlemleri almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2282, '(bir şeyi) sağlama bağlamak', 'sağlam kazığa bağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2283, '(bir şeyi) sokaktan toplamak', 'kolayca sağlamak, masrafsız ve zahmetsiz elde etmek: ''Baban parayı sokaktan topluyordu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2284, '(bir şeyi) söz etmek', 'o şeyin dedikodusunu yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2285, '(bir şeyi) zihnine yerleştirmek', 'unutulmayacak biçimde aklında tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2286, '(bir şeyi) zimmetine geçirmek', 'emanet edilmiş para veya eşyayı kendine mal etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2287, '(bir şeyin) acısı çıkmak', 'bir şeyin olumsuz, kötü sonucu bir süre sonra ortaya çıkmak: Dünkü yorgunluğun acısı bugün çıktı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2288, '(bir şeyin) altına imza atmak', 'destek vermek amacıyla aynı düşüncede olduğunu göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2289, '(bir şeyin) altında kalmak', '1) ezilmek: ''Bir şey değil, karşıdan bir otomobil filan gelir de altında kalırım diye korktum.'' -B. Felek. 2) mec. karşılığını verememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2290, '(bir şeyin) altını kapatmak', 'ocağın alevini kapatmak: ''Fokurdamaya başlayan çaydanlığın altını kapadı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2291, '(bir şeyin) altını kısmak', 'ocağın alevini azaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2292, '(bir şeyin) aması var', '''herkesin bilmediği sakıncası veya kusurları var'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2293, '(bir şeyin) arkası gelmek', 'devamlı olmak, sürekli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2294, '(bir şeyin) arkası kesilmek', 'tükenmek, son bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2295, '(bir şeyin) âşığı kesilmek', 'tutku durumuna getirmek: ''Boks merakından çok sonra güreşe merak sardı, güreş âşığı kesildi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2296, '(bir şeyin) başına geçmek', '1) görevi altında bulundurmak; 2) bir işin yönetimini ele almak; 3) bir işi yapmaya başlamak; 4) bir şeyin etrafında toplanmak, yer almak: ''Adam ağır adımlarla gelip masanın başına geçiyor.'' -E. M. Karakurt.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2297, '(bir şeyin) başında beklemek (durmak)', 'yanında durup gözetlemek: ''Birkaç fukara köylü sabaha kadar cenazenin başında bekleyerek Kur''an okudular.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2298, '(bir şeyin) başını beklemek', '1) gözetlemek; 2) hastanın yanında bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2299, '(bir şeyin, bir kimsenin) etrafını almak (sarmak)', 'çevresinde toplanmak, ortaya almak, kuşatmak: ''Ön arabanın karşısına geçerler, bohçacı ve yazmacı kadınların tuhaflığa vurarak etrafını alırlar.'' -R. H. Karay. ''Herkes etrafımı sarmış, beni hararetle tebrik ediyorlardı.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2300, '(bir şeyin, bir kimsenin) üstüne üstüne gitmek', 'çekinmeden sonucu tehlikeli olabilecek bir şeyle uğraşmak, yılmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2301, '(bir şeyin, birinin) attığı tırnağa değmemek', 'değerce ondan çok aşağı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2302, '(bir şeyin) çaresine bakmak', 'gerekeni yapmak, çözüm yolu aramak: ''Sıkboğaz etme çocuğum. Bir çaresine bakacağız. Ben annenle konuşurum.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2303, '(bir şeyin) çivisi çıkmak', 'düzeni bozulmak, kargaşa içinde bulunmak: ''Bu ülkenin, bu dünyanın çivisi çıkmış, ben mi çakacağım?'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2304, '(bir şeyin) davasını gütmek', 'sürekli olarak bir konuyu savunmak veya gündemde tutmak: ''Bütün edebiyatım, Tanin gazetesinin cumartesi sayılarında garpçılık davasını gütmekle geçiyor.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2305, '(bir şeyin) delisi (olmak)', 'bir şeye aşırı derecede düşkün (olmak): Oyun delisi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2306, '(bir şeyin) derdine düşmek', 'yapılması gereken bir şeyi gerçekleştirmenin yollarını aramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2307, '(bir şeyin) dibi görünmek', 'içindeki şey tükenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2308, '(bir şeyin) dibini boylamak', 'batmak: Kayık denizin dibini boyladı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2309, '(bir şeyin) dibini bulmak', '1) içindekini tüketmek; 2) aslına veya sonucuna ulaşmak: ''Bu sırrı çözmeye kalkışırsan dibini bulamazsın.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2310, '(bir şeyin) divanesi olmak', 'bir şeye çok düşkün olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2311, '(bir şeyin) dozu kaçmak', 'dozunu kaçırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2312, '(bir şeyin) dümenini elinde tutmak', 'yönetmek, istediği yöne doğru götürmek: ''Başımıza gelenler, son elli yılda ekonominin dümenini elinde tutan sıfırlardan kaynaklanıyor.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2313, '(bir şeyin) esprisi kalmamak', 'ilgi çekici olmaktan çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2314, '(bir şeyin) eti kemiği', 'esası, ana özelliği, asıl ağırlığı: ''Bu iki ana renk pazar yerinin etini kemiğini teşkil ediyor.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2315, '(bir şeyin) girdabına kapılmak', 'etkisinde kalmak, o şeyin çekiciliğinden kurtulamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2316, '(bir şeyin) gözü kör olsun', ' tkz. 1) bazı zorunlu durumlarda zararı istemeyerek kabullenmeyi anlatan bir söz; 2) gereksinim duyulan şeyin yokluğunda söylenen bir söz: Paranın gözü kör olsun.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2317, '(bir şeyin) gözünü çıkarmak', '1) beceriksizce davranmak, zarara uğratmak; 2) tkz. iyisi dururken en kötüsünü seçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2318, '(bir şeyin) hastası olmak', 'bir şeye çok düşkün olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2319, '(bir şeyin) içine etmek (sıçmak)', ' kaba bozup berbat etmek, içine etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2320, '(bir şeyin) ilminden anlamak', 'herhangi bir şeyin uzmanı olmak: ''Onun ilminden anlayan şoför seni istediği yere götürür.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2321, '(bir şeyin) izini düşürmek', 'iz düşümünü çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2322, '(bir şeyin) kanını emmek', 'insafsızca sömürmek: ''Yıllarca, yüzyıllarca onun kanını emdikten ve onu bir posa hâlinde katı toprak üzerine attıktan sonra, şimdi de gelip ondan tiksinmek hakkını kendinde buluyorsun.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2323, '(bir şeyin) kaymağını almak (yemek)', 'bir şeyin en büyük payını, kârını ele geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2324, '(bir şeyin) kefaretini ödemek', 'cezasını çekmek: ''O, kendisine düşen zulüm payının kefaretini ödedi.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2325, '(bir şeyin) keyfini çıkarmak', 'bir şeyden iyice tat almak: ''Pazarın keyfini çıkarmak için saat ona doğru villanın ucu deniz kıyısına varan bahçesine çıktı.'' -S. Kocagöz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2326, '(bir şeyin, kimsenin) üstüne toz kondurmamak', 'bir şeyin veya kimsenin kusurlu olabileceğini kabul etmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2327, '(bir şeyin) künhüne varmak', 'bir şeyin özünü, aslını anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2328, '(bir şeyin) makarasını çözmek', 'ayrıntılarıyla sayıp dökmek: ''Yukarı katta ihtiyar imamla yatalak hasta karısının aşağıdan tamamıyla işitilen kavgalarına dair hikâyelerinin makarasını çözerdi.'' -H. Z. Uşaklıgil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2329, '(bir şeyin) muhasebesini yapmak', 'bir şeyin olumlu veya olumsuz yönlerini gözden geçirerek bir yargıya varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2330, '(bir şeyin olmasına) kıl (kadar) kalmak', 'çok az kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2331, '(bir şeyin) orucunda olmak', '1) herhangi bir şeyi yemez içmez olmak; 2) bir şeyi yapmaz olmak: ''Ayıplama kardeş, üç gündür lakırtı orucundayım.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2332, '(bir şeyin) önünü almak', 'önlemek: ''En ucuz şekilde bu fesadın önünü almak için ne yapmak lazımsa söyleyiniz.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2333, '(bir şeyin) örneğini almak', 'biçimini çizmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2334, '(bir şeyin) örneğini çıkarmak', 'benzerini yapmak veya çizmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2335, '(bir şeyin) pençesine düşmek', 'yakalanmak: ''Karaborsa davalarında ise bunların nüfuzları sıfırdan aşağıdır çünkü bu hususta birçoğu Millî Korunma''nın pençesine düşmeye namzettir.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2336, '(bir şeyin) rezili çıkmak', 'çok eskimek, bozulmak, parçalanmak: ''Şu gömleğe bak, rezili çıkmış!'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2337, '(bir şeyin) sınırlarını (sınırını) zorlamak', '1) en son noktaya kadar çaba göstermek; 2) bütün gücünü en son noktaya kadar kullanmak: ''Hayatı boyunca akıl sınırlarını zorlayan bir korkusuzluğa sahip olacaktı.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2338, '(bir şeyin) telaşına dalmak', 'herhangi bir şeyle ilgili olarak heyecanla, aceleyle, sıkıntıyla davranmak: ''Karısı akşam telaşına dalmış, çardağın etrafında dolanıp duruyordu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2339, '(bir şeyin) üstüne bir bardak (soğuk) su içmek', ' alay o işten umudunu kesmek, o işin olacağına inanmamak, o işten vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2340, '(bir şeyin) üstüne gelmek', 'bir şey yapılırken veya konuşulurken çıkagelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2341, '(bir şeyin) üstüne kapanmak', 'belli bir işi aralıksız bir biçimde yapmak: ''Nevin tercüme etmeye hazırlandığı romanın üstüne kapandı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2342, '(bir şeyin) üstüne yatmak', 'hakkı yokken bir şeyi kendine mal etmek, bir şeyi alıp vermemek: ''Bunlar eşeğin büsbütün üstüne mi yatmak istiyorlar?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2343, '(bir şeyin) üzerine bir bardak (soğuk) su içmek', ' alay üstüne bir bardak su içmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2344, '(bir şeyin) üzerine üzerine gitmek', 'üstüne üstüne gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2345, '(bir şeyin) yanından bile geçmemiş', '''o şeyle hiçbir ilgisi yok'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2346, '(bir şeyin) yerini tutmak', '1) bulunmayan bir nesnenin yerini almak, onu aratmamak: ''Hiçbir kahvaltı simitle çayın yerini tutamaz.'' -S. F. Abasıyanık. 2) görevinden ayrılan birinin yaptığı işi yapabilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2347, '(bir şeyin) yolunu tutmak', 'benimsemek, gereğini yerine getirmek: ''Sen de biraz adamlığın yolunu tutmalısın.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2348, '(bir şeyin) yüzü açılmak', 'güzelliği, parlaklığı ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2349, '(bir şeyin) yüzüne hasret kalmak', 'o şeyden yoksun kalmak, hasret kalmak: ''Burada yağdan yumurtadan geçtik, ekmek yüzüne hasret kaldık.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2350, '(bir şeyin) zamanı geçmek', '1) o şey artık gerekli ve yerinde olmaktan çıkmak; 2) mevsimi geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2351, '(bir şeyin) zevkini çıkarmak', 'ondan olabildiği kadar zevk almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2352, '(bir şeyle) arası hoş (iyi) olmamak', 'o şeyden hoşlanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2353, '(bir şeyle) başa çıkmak', 'bir şeye gücü yetmek: ''Varsın kıraç olsun tarlam / Taşlarını ayıklayacağım / Kazmayı sallayacağım / Karar vermişim / Toprakla başa çıkacağım'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2354, '(bir şeyle) başı hoş olmamak', 'bir şeyden hoşlanmamak: ''Benim içki ile başım hoş olmadı, şampanyadan sonra ha bire yedim durdum.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2355, 'bir şeyler, bir şeyler', 'daha fazla açıklamamak, kısa kesmek gerektiğinde söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2356, 'bir tahtası eksik', ' tkz. akılca eksik, yarım akıllı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2357, 'bir tarafa bırakmak (koymak)', 'önemsememek, benimsememek, ertelemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2358, 'bir tarakta bezi olmamak', 'sözü edilen konu ile ilgisi olmamak, bilgisi bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2359, 'bir taşla iki kuş vurmak', 'bir davranışla birden çok yararlı sonuca ulaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2360, 'bir tat, bin feryat', 'mutluluktan çok, sıkıntısı olan: ''O zamana kadar kira köşelerinde sürünmekten bir tat, bin feryat, türlü sıkıntılara giriftar olmuşken...'' -H. Z. Uşaklıgil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2361, 'bir tek atmak', 'bir kadeh içki içmek: ''Canım şurada bir tek atalım, serinleriz, konuşuruz, dediler.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2362, 'bir tepe yıkılır, bir dere dolar', 'dünyada hiçbir şey kaybolmaz; birinin kaybettiğini başkası kazanır, bir zengin fakirleşirken bir fakir de zenginleşebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2363, 'bir torba kemik', 'çok zayıf.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2364, 'bir tutam ot deveye hendek atlatır', 'ufak bir para veya iyilik insana güç işler yaptırır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2365, 'bir tutmak (görmek)', 'eşit saymak, eşit görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2366, 'bir uyuz keçi bir sürüyü boklar', 'kötü yaradılışlı, kötü huylu kişi, çevresine hep kötülük aşılar, bir toplulukta huzursuzluk çıkmasına sebep olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2367, 'bir varmış bir yokmuş', 'masal gibi geçip gitmiş, artık hayal olmuş.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2368, 'bir vuruşla ağaç devrilmez', 'olumlu bir sonuç elde etmek için, tek bir girişimle yetinmemeli, o işe devam edilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2369, 'bir yakadan baş çıkarmak', 'bir çatı altında dirlik düzenlik içinde yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2370, 'bir yastığa baş koymak', 'evli bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2371, 'bir yaşına daha girmek', 'şimdiye değin görmediği şaşılacak yeni bir şeyle karşılaşmak: ''Ah anacığım bir yaşıma daha girdim, dünyada her şey aklıma gelirdi de tefle ayı oynatmak gelmezdi.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2372, 'bir yemem diyenden kork, bir oturmam diyenden', 'oturmayacağını belirten konuk yatıya kalır, yemeyeceğini söyleyen de bir türlü doyurulamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2373, '(bir yer) adam almamak', 'son derece kalabalık olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2374, '(bir yer) ayağının (ayaklar) altında', 'yüksek bir yerden geniş bir alanı görür durumda.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2375, '(bir yer bir olaya) sahne olmak', 'bir yerde bir olay geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2376, '(bir yer, birine) açık olmak', 'bir yerde her zaman iyi karşılanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2377, '(bir yer) güneş almak (görmek)', 'güneş ışınlarıyla aydınlanacak durumda olmak: ''O ev güneş görmüyor. Soba yanmazsa her şey nemleniyor.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2378, '(bir yer, işte) yabancılık çekmek', 'bir iş veya çevrede yabancı olmaktan doğan güçlüklere uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2379, '(bir yer) karınca yuvası gibi kaynamak', 'çok kalabalık ve hareketli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2380, '(bir yer) kazan (biri) kepçe', '''bir yeri etraflıca (dolaşmak, aramak)'' anlamında kullanılan bir söz: İstanbul kazan ben kepçe, üç gün onu aradım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2381, '(bir yer) mahşere dönmek', 'çok kalabalıklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2382, '(bir yer) örümcek bağlamak', '1) üzerinde örümcek ağı olmak; 2) mec. bir şey uzun süre kullanılmadan kendi hâline bırakılmış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2383, '(bir yer) pazar yerine dönmek', 'kalabalıklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2384, '(bir yer) zindan kesilmek', '1) çok karanlık duruma gelmek; 2) çok sıkıcı ve içinde yaşanmaz duruma gelmek: ''Lakin bir gün öyle bir şey olmuştu ki Özbekiye Bahçesi gözümde âdeta zindan kesildiydi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2385, '(bir yer) zindan olmak', 'yaşanmaz, huzursuz, rahatsız, zevk alınmaz duruma gelmek: Evi ona zindan oldu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2386, '(bir yerde) cinler cirit (top) oynamak', 'o yer ıssız olmak: ''Şimdi koca çiftliğin yirmi odasında cinler top oynuyor.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2387, '(bir yerde) cirit atmak', 'bir yerde çokça bulunmak, sık dolaşmak ve serbestçe davranmak: Fareler evde cirit atıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2388, '(bir yerde) ecinniler top oynuyor', '''bomboş, kimse yok, ıssız ve sessiz'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2389, '(bir yerde) gözünü açmak', 'o yerde olduğunun farkına varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2390, '(bir yerde) hazır bulunmak (olmak)', '1) bir yerde var olmak, kendi bulunmak; 2) bir şeyi hemen yapabilecek durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2391, '(bir yerde) içecek suyu olmak', 'o yere gitmesi kısmet olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2392, '(bir yerde) ikamete memur edilmek', ' esk. sürgün cezası verilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2393, '(bir yerden) ayağını çekmek', 'sık sık gittiği bir yere artık uğramaz olmak, ilgiyi kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2394, '(bir yerden) ayağını kesmek', '1) bir yere gitmez olmak, uğramamak; 2) başkasını bir yere artık uğramaz duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2395, '(bir yerden, bir şeyden) elini ayağını (eteğini) kesmek (çekmek)', '1) uğramaz olmak; 2) uğraşmamak, ilgilenmemek: ''Ben artık öyle şeylerden elimi ayağımı çektim.'' -O. C. Kaygılı. 3) o şeyle ilgisini kesmek: ''Odasına kapandı, aylarca dünyadan elini eteğini çekti.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2396, '(bir yerden) kendini dar atmak', 'güçlükle ve ivedi olarak bir yere sığınmak, kaçmak: ''Zavallı ihtiyarlar, sabah oldu mu bir yangından kaçar gibi kendilerini evden dar atıyorlar, gece yarısına kadar kahvede oturuyorlar, kavga ediyorlar, uyukluyorlardı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2397, '(bir yerden) payandaları çözmek', ' argo ayrılmak, kaçmak, uzaklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2398, '(bir yere) abayı sermek', '1) istenilmediği hâlde teklifsizce yerleşmek; 2) uzun süre yerleşip kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2399, '(bir yere) adım (adımını) atmamak', 'gitmemek, uğramamak: ''Faik Bey artık konağa adımını atmıyor, artık ne Servet Bey''e hatta ne de Cemal''e görünüyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2400, '(bir yere) ayağı alışmak', 'bir yere sürekli gitmek: ''Ayağı buraya alışmasın, sonra yabancı misafirler varken de gelir, beni rezil eder.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2401, '(bir yere) ayağı düşmek', 'yolu düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2402, '(bir yere) ayak atmamak', 'bir yere hiç gitmemek, uğramamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2403, '(bir yere) baş tutmak', 'elebaşı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2404, '(bir yere) başını sokmak', 'barınacak bir yer bulmak: ''Çok şükür başımızı bir yere soktuk, şimdilik tatlı söyleyelim tatlı yiyelim.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2405, '(bir yere) kakılıp kalmak', 'beklemek zorunda kalmak, hiçbir yere gidememek: ''Dedeye -yeni şakirdiniz efendim- diyerek çekilip gidince kız odanın ortasında kakılıp kaldı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2406, '(bir yere) kapağı atmak', 'sıkıntısız, rahat bir yere sığınmak, kaçıp kurtulmak: ''Garajlara en yakın bir otele kapağı atmış, hemen yatıp uyumuştu.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2407, '(bir yere) kendini atmak', 'vakit geçirmeden hemen gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2408, '(bir yere) para akmak', 'yatırım yapılmak: ''Yeteneksiz, hırslı mahalle politikacıları, kendi şehirlerine para aksın diye üniversite açma ticaretine girdiler.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2409, '(bir yere) parmak basmak', '1) imza yerine parmağını mürekkebe batırarak bir yere bastırmak: ''Ben bu dileğin altına bilmem kaç kuruşluk pul yapıştırtıp, binlerce yurttaşa parmak bastırtıp yirmi metre uzunluğunda bir dilek kâğıdı olarak size sunabilirdim.'' -N. Hikmet. 2) mec. bir konu üzerine dikkati, ilgiyi çekmek: ''Bu arada benim öteden beri gözüme çarpan bir noktaya şimdi parmak basacağım.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2410, '(bir yere) temel kakmak', 'bulunduğu yerden kolay kolay ayrılacak gibi olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2411, '(bir yere) yolu düşmek', 'o yerden geçmesi gerekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2412, '(bir yeri) adım adım gezmek', 'her yerini dolaşıp görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2413, '(bir yeri) ahıra çevirmek', 'pis, bakımsız, dağınık, harap, gürültülü duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2414, '(bir yeri, bir şeyi) tozpembe görmek', 'aşırı iyimser olmak: ''Fakat aynı adamın bütün sıkıntılarına rağmen, kara ufukları tozpembe gördüğü ... anlar da vardır.'' -Ş. Rado.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2415, '(bir yeri birine) zindan etmek', 'bir yeri yaşanmaz, huzursuz, rahatsız, zevk alınmaz bir duruma getirmek: ''Ah evladım, sorma, onu bir zalim herif aldı, zavallı tazeye dünyayı zindan etti.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2416, '(bir yeri) curcunaya çevirmek (döndürmek veya vermek)', 'ortalığı karışık, gürültülü duruma sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2417, '(bir yeri) gürültüye vermek', 'telaş ve karışıklığa yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2418, '(bir yeri) ırgat pazarına döndürmek', 'karışık ve dağınık bir duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2419, '(bir yeri) komşu kapısı yapmak', 'sık gidilen yer hâline getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2420, '(bir yeri) komşu kapısına çevirmek', 'yakın olmadığı ve sık sık uğranılması gerekmediği hâlde bir yere çok sık gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2421, '(bir yeri) örümcek sarmak', 'bir yer örümcek ağları ile dolmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2422, '(bir yeri) patırtıya vermek', 'gürültüye vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2423, '(bir yeri) pislik götürmek', 'o yer, çok pis olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2424, '(bir yeri) sel götürmek', '1) çok yağmur yağmak; 2) çok yağmurdan dolayı bir bölgede, yollar zor geçilir duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2425, '(bir yeri) yol etmek', 'o yere sık sık gitmek: ''Yol etti kendisine ihtiyarlar kahvesini.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2426, '(bir yerin, bir işin) başına gelmek', 'bir görevi üstlenmek, yüklenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2427, '(bir yerin, bir şeyin) havasını teneffüs etmek', '1) içinde hissetmek; 2) ortamı yaşamak: ''Orada insanlığın, faziletin, sevginin havasını teneffüs edeceğiz.'' -O. S. Orhon.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2428, '(bir yerin) içinden olmak', 'bir yerin merkezinde yaşamak veya orada doğmuş bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2429, '(bir yerin) suyu mu çıktı?', '''beğenilmeyecek nesini gördün?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2430, '(bir yerin) üst başı', 'yukarı yanı, yukarıda olan bölümü: ''... önlerine katıp köyün üst başındaki pınar yerine çıktılar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2431, '(bir yiyecek) ağzında büyümek', 'sevmediğinden veya içi almadığından yutamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2432, 'bir yiyip bin şükretmek', 'kötü durumda olanlara bakarak kendi durumunun değerini bilmek: ''Bekâr olduğumuza bir yiyelim de bin şükredelim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2433, 'bir yol tutturmak', 'bir davranış, bir tutum biçimi belirlemek: ''Herkes bir yol tutturmuş kendince / Bir düzen kurmuş iyi kötü'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2434, '(bir yola) baş koymak', 'bir şey uğruna ölümü göze almak: ''Çeşitli tehlikelerden var olduğunu bilerek bu işe girişip baş koymuşlardı.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2435, 'bir yolunu bulmak', 'çare bulmak, çözüm üretmek: ''Hemen bir yolunu bulurlar yükü üstlerinden aşırmanın.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2436, 'birbiri için yaratılmış olmak', 'birbiriyle çok iyi anlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2437, 'birbiri üstüne gelmek', 'arka arkaya meydana gelmek, ara vermeden olmak: ''Son günlerde birbiri üstüne gelen yorgunluklardan söz etti.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2438, 'birbirine düşmek', 'araları açılmak, aralarında anlaşmazlık çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2439, 'birbirine girmek', '1) karışmak; 2) iplik vb. dolaşmak, çözülmeyecek duruma gelmek; 3) mec. kavga etmek, dövüşmek: ''Bunun için sabır, sükûnet, soğukkanlılık gerek hâlbuki biz birbirimize giriyoruz.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2440, 'birbirine katmak', '1) aralarını açmak, aralarını bozmak, olay çıkarmak; 2) karıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2441, 'birbirini çekememek', 'kıskanmak: ''Bu iki birbirini çekemezin kişiliklerini kendi imbiğinde eritmiş bir şair olduğu söylenir.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2442, 'birbirini tutmaz', 'birbiriyle ilgisi olmayan, tutarsız.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2443, 'birbirini yemek', 'iki veya daha çok kimse birbiriyle uğraşmak, birbirine kötülük etmek: ''Birbirimizi yiyecek zaman değil çocuklar.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2444, 'birbirinin ağzına girmek', 'birbirine çok düşkün olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2445, 'birbirinin ağzına tükürmek', ' tkz. bir sorunda, bir olayda sözleşmiş gibi ağız birliği yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2446, 'birbirinin gözünü çıkarmak', 'kıyasıya dövüşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2447, 'birbirinin gözünü oymak', 'aralarında aşırı geçimsizlik olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2448, 'bire beş katmak', 'bire bin katmak: ''Rahmi''nin neyi var neyi yoksa özellikle de son zamanda aldıklarını, bire beş katarak sayanlar ... çıktı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2449, 'bire bin katmak', 'çok abartmak: ''Hiç merak etmeyin, hep bire bin katarak anlatır.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2450, 'bire ... vermek', '1) buğday, arpa, nohut, fasulye vb. ürünler için toprak, kullanılan tohumun belli bir katı kadar ürün vermek; 2) şans oyunlarında verilen paradan daha fazla para kazandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2451, 'biri bilmeyen bini hiç bilmez', 'küçük de olsa bir iyiliğin değerini bilmeyen, daha büyük iyiliklere layık değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2452, '(biri, bir şey) bir yana dünya bir yana', 'bir varlığa çok değer verildiğini anlatmak için kullanılan bir söz: ''Mercan Usta bir yana dünya bir yana.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2453, '(biri bir şeyle) zihnini bozmak', 'sürekli olarak aynı şeyi düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2454, '(biri, birine) deli divane âşık olmak', 'aşırı derecede sevmek: ''Bütün yaratıklar birbirlerine deli divane âşık oldular.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2455, '(biri, birine) deli divane olmak', 'aşırı derecede ilgi göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2456, '(biri birinin) attığı tırnak kadar olamamak', 'bir kimse, sözü edilenden daha değersiz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2457, '(biri biriyle) zıt gitmek', 'birine karşı sürekli ters davranmak, istediklerinin tersini yapmak: ''... ahlakını az çok bilirim, onunla zıt gitmeye gelmez.'' -A. Haşim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2458, '(biri) çok olmak', 'haddini aşarak karşısındakini usandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2459, 'biri eşikte biri beşikte', 'küçük çocuğu çok olan kimseler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2460, '(biri) fena olmak', '1) hasta gibi olmak, fenalaşmak: ''Bütün bu hatıraların yerini bir tek duygu, fena bir duygu, fenayım, fena oluyorum duygusu kapladı.'' -P. Safa. 2) kötüleşmek; 3) çok üzülmek, bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2461, '(biri) fitili almak', 'birdenbire telaşlanmak, kaygılanmak, öfkelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2462, '(biri) hava almak', '1) açık havada gezmek: ''Biraz hava almak için niye Hürriyet tepesine kadar bir gezinti yapmasınlar?'' -A. Gündüz. 2) argo umduğunu bulamamak, hiçbir şey kazanmamak; 3) ferahlamak, açılmak, hoş vakit geçirmek: ''Hava alalım diye beni bir akşam bir yazlık bahçeye götürdüler.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2463, '(biri için) iyi söylemek', 'övmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2464, '(biri için) kötü söylemek', 'birtakım olumsuz, beğenilmeyen, istenmeyen tutum ve davranışları olduğunu söylemek, kötülemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2465, '(biri ötekinin) ayağının pabucu olamamak', 'değerce ondan çok aşağı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2466, '(biri ötekinin) babasına rahmet okumak', 'hakkında iyilik düşünmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2467, '(biri ötekinin) eline doğmak', 'yaşlı bir kimse, birini, çocukluğundan beri çok yakından tanımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2468, '(biri ötekinin yanında) zemzemle yıkanmış olmak', 'biri, ötekine göre çok iyi nitelikte olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2469, '(biri ötekiyle) mahkemelik olmak', 'istemediği hâlde dava konusu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2470, '(biri) vazifesinden olmak', 'görevini yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2471, '(biri veya bir şey) ortadan kaybolmak', '1) saklanılmak, bulunmaz olmak; 2) nereye gittiği bilinmemek, kimseye sezdirmeden gitmek: ''Nihayet hakikaten de bir gün söylediği gibi büsbütün ortadan kayboldu.'' -A. H. Çelebi. 3) yok edilmek, kullanılmamak: ''Eski, büyük teşrifat kayıkları ortadan kalkmışsa da yine iki, üç çifte kayıklara rast gelinirdi.'' -A. Ş. Hisar. 4) öldürülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2472, 'biri yer biri bakar, kıyamet ondan kopar', 'herkesin yararlanabileceği şeyden bazıları yararlanır da başkalarına yararlanma fırsatı vermezlerse büyük kavga çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2473, 'birinci elden kaynağa gitmek', 'bilimsel çalışmalarda kaynakların aslına, özgününe dayanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2474, 'birinci gelmek (çıkmak)', 'birçokları arasında en iyi olarak seçilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2475, '(birinde) akıl terelelli (olmak)', 'pek delişmen, kendisinden ciddi bir düşünce, davranış beklenmeyen kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2476, '(birinde birinin) havası olmak', 'o kimseye benzemek, o kimseyi hatırlatmak: Onda babasının havası var.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2477, '(birinde) dil bir karış', 'saygısızca karşılık verenler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2478, '(birinde) hoşafın yağı kesilmek', 'söyleyecek söz, verecek karşılık veya yapacak bir şey bulamayacak bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2479, '(birinde) kalp olmamak', 'acıma duygusu olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2480, '(birinde) şafak atmak', '1) birden önemli bir durumla karşı karşıya olduğunu anlamak; 2) öfkelenmek: ''Kapıyı kapatınca bende şafak attı.'' -B. Felek. 3) korku ve telaşa kapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2481, '(birinde) şeytan tüyü olmak', 'kendini herkese kolaylıkla sevdirme özelliği bulunmak: ''Bende şeytan tüyü vardır.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2482, '(birinde) yürek Selânik olmak', ' şaka çok korkmak ve çok heyecanlanmak: ''İkisinde de yürek Selânik.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2483, '(birinden, bir şeyden) ağzı yanmak', 'bir şeyden veya kişiden büyük zarar görmek: ''Ağzım yanmıştı bir kez şişman kadından, biz etine buduna aldanmıştık.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2484, '(birinden, bir şeyden) aşağı kalır yeri (yanı) yok', 'nitelikleri bakımından başkalarıyla karşılaştırıldığında eksiği olmayan, denk olan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2485, '(birinden, bir şeyden) aşağı kalmamak', 'herhangi bir nitelik bakımından geri olmamak: ''Karısı kibarlıktan yana ondan aşağı kalmıyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2486, '(birinden, bir şeyden) sıdkı sıyrılmak', '1) birine karşı duyulan güven ve inancı yitirmek: ''Adına en soylu dileklerde bulunduğumuz bu bağırgan, kaba ve düşüncesiz insan yığınından, o dakikada sıdkım sıyrılmaya yetti.'' -A. İlhan. 2) birinden veya bir şeyden soğumak; 3) birinden veya bir şeyden bıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2487, '(birinden) buz gibi soğumak', 'birinden tiksinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2488, '(birinden) fikir almak', 'birinin düşüncesinden yararlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2489, '(birinden) gözü su içmemek', 'güvenmemek: ''Azarlayıp adam olmazsın sen nafile... Gözüm hiç su içmiyor senden.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2490, '(birinden) kan gitmek', '1) büyük ve küçük abdestini yaparken kan gelmek; 2) kadınlarda aybaşı çok kanlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2491, '(birinden) öfkesini çıkarmak (almak)', 'öfkeli kişi haksız yere ilgisiz birine çatmak: ''Evde önüne gelenin öfkesini kendisinden çıkarmasına alışıktı.'' -N. Cumalı. ''Adamı pataklamadan bırakmazdım, pataklamadıkça öfkemi alamazdım.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2492, '(birinden) ricada bulunmak', 'rica etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2493, '(birinden) şeytan elini çekmiş', 'uygunsuz bir iş yapacak veya kötülük düşünecek durumu olmayan çok yaşlı kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2494, '(birinden) tarafa olmak (çıkmak)', 'birinin görüş ve düşüncesini benimsemek, desteklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2495, '(birinden) terbiye almak (görmek)', 'belli bir eğitimle yetişmek: ''Allah rahmet eyleye, ben terbiyemi anamdan aldım.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2496, '(birinden veya bir şeyden) cesaret almak (bulmak)', 'herhangi bir durumdan, davranıştan güç almak: ''Biraz da bu tanışıklıktan cesaret alarak konuşmak istiyordum kızla.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2497, '(birinden veya bir şeyden) pay biçmek', 'durumu bir kişi veya bir şeyin durumu ile karşılaştırıp yargıya varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2498, '(birinden) yaka silkmek', 'bıkmak, usanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2499, '(birine) abayı yakmak', ' tkz. aşırı biçimde gönül vermek, tutulmak, âşık olmak: ''Sen mi verdin ona gönül yoksa o mu yaktı sana daha önce abayı?'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2500, '(birine) acı vermek', 'üzülmesine sebep olmak, incitmek: ''Başkalarına elinden geldiğince acı vermeye çalışmak başlıca eğlencesiydi.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2501, '(birine) açık bono vermek', 'sınırsız yetki tanımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2502, '(birine) açık olmak', 'dürüst olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2503, '(birine) ağzının payını (ölçüsünü) vermek', 'verilen karşılıkla bir kimseyi söylediğine veya yaptığına pişman etmek: ''İyi oldu ağzının payını verdiğim, artık bana karşı daha dikkatli olur.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2504, '(birine) arkasını dayamak', 'birinin koruyuculuğuna güvenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2505, '(birine) ateş basmak', 'kızarmak, sıkılıp başına kan yürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2506, '(birine) balta olmak', ' argo direnerek bir şey istemek, asılmak, musallat olmak: ''Yeryüzünde balta olacak bundan daha belalı bir adam olacağını tasavvur edemiyorum.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2507, '(birine) beddua sinmek', 'ilencin tutması yüzünden birinin işi sürekli ters gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2508, '(birine) biliş çıkmak', 'tanımak, önceden tanış olmak: ''Hiç kimse bu kara yağız garip yiğide biliş çıkmadı.'' -K. Tahir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2509, '(birine bir şey) vız gelip tırıs gitmek', ' tkz. önemsememek, aldırış etmemek: ''Bu ölümle Ahmet, dünya yüzünde sahibi olunacak şeyin yalnız bir kadın olabileceğini, ötesinin ise yalan, haksız olduğunu ve kendisine kadından gayrı bir şeye sahip olmanın vız gelip tırıs gittiğinin farkına varmıştı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2510, '(birine bir şey) vız gelmek', ' tkz. pek önemsiz görünmek: ''Fakat bu da Nahit''e vız geldi çünkü kız koltuğa oturmuştu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2511, '(birine, bir şeye) çekidüzen vermek', '1) düzgün duruma getirmek, düzeltmek: ''Bir iki yutkunup sesine çekidüzen verdikten sonra şu ninniyi tutturdu.'' -O. C. Kaygılı. 2) belirlenen ölçülere uydurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2512, '(birine, bir şeye) güveni olmak', 'güvenmek, inanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2513, '(birine, bir şeye) kanat germek', 'koruması altına almak, himaye etmek: ''Bazı işsiz güçsüz takımı, beş para etmez yapılara kanat gererek kendilerini tatmin etme girişimindeler.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2514, '(birine, bir şeye) kendini adamak', 'kendini vermek: ''Kendini bir ülkeye adayacak her kişi, bir kere bu yoldan geçmeli.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2515, '(birine, bir şeye) kıymet vermek', 'değerli olarak kabul etmek, değerlendirmek: ''Müdür bey onun tecrübelerine kıymet vermek şöyle dursun, onu hafife almakla gururunu da kırıyordu.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2516, '(birine, bir şeye) pabuç bırakmamak', 'yapacağından vazgeçmemek, hiçbir şeye aldırmamak, korkmamak: ''Bu tehditlere hiç pabuç bırakmadı.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2517, '(birine) bok yemek düşmek', ' kaba birinin bir işe karışmaması, burnunu sokmaması gerekir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2518, '(birine) borçlu bulunmak (olmak)', 'borçlu duruma düşmek: ''Dehasını, geçirdiği sara nöbetlerinin şokuna borçlu bulunuyordu.'' -H. Taner. ''Fakat ben bu ağırlığı o kadar az yükleneceğim ki söylemeye borçlu olduğumdan bir adım ileri geçmeyeceğim.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2519, '(birine) cephe almak', 'hasım durumu takınmak, bir düşünceye karşı olmak, direnmek: ''Çekinmiyor, bizzat imparatora karşı cephe alıyordu.'' -F. F. Tülbentçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2520, '(birine) cesaret gelmek', 'yılgınlığı gitmek, yüreklenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2521, '(birine) cesaret vermek', 'birinin yılgınlığını gidermek, birini yüreklendirmek: ''Ben size ne cesaret verdim ki bana böyle bir teklifte bulunabiliyorsunuz?'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2522, '(birine) ceza kesmek', 'görevli, para cezası yazmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2523, '(birine) çamur atmak (sıçratmak)', 'birini kötü bir işe karışmış göstermek, kara çalmak, iftira etmek: ''Onlarla başa çıkmak kolay değildi, çünkü her an bir çamur atabilirlerdi kızdıklarında.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2524, '(birine) çelme atmak (takmak veya vurmak)', '1) çelme ile yıkmaya çalışmak: ''Ders aralarında ittikleri, çelme taktıkları da olurdu.'' -Y. Atılgan. 2) mec. bir işi veya bir kimseyi baltalamak, gelişmesini engellemek: ''Bana kanun ve hukuk yolundan çelme atılabilir mi?'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2525, '(birine) çirkef atmak', 'iftira atmak: ''Bütün hayatında görmediği bir çirkef attı müdürün üzerine.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2526, '(birine) damla inmek', 'felç olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2527, '(birine) dil çıkarmak', 'alay etmek, eğlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2528, '(birine) diş bilemek', 'kötülük yapmak için fırsat beklemek, hıncını gösterir bir durum almak: ''Temiz, aydınlık, hayran ve sinsi, alaycı, diş bileyici yüzler bir arada.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2529, '(birine) diş geçirememek', 'gücü yetmemek: ''Anası cahil kadın... Delikanlı oğluna diş geçiremedi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2530, '(birine) dünyayı zindan (zehir) etmek (dünyayı başına dar etmek)', 'bir kimseyi çok sıkıntılı bir duruma sokmak: ''En güzel zamanında hiç olmayacak bir şey çıkarır, dünyayı kendine zehir edersin.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2531, '(birine) felfelek sokmak', 'birini kuşkuya düşürmek: ''Sen beni bekle, bir gün seni alırım diye kıza bir felfelek sokmuş.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2532, '(birine) fena gözle bakmak', 'kötü niyetini anlatır biçimde bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2533, '(birine) fikir danışmak', 'bilgi edinmek için bir yetkiliden bilgi almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2534, '(birine) fit vermek (sokmak)', '1) birini başkasına karşı kışkırtmak, arayı açmak; 2) kuşku uyandırmak: ''Muhtar, paraları alıp kaçmış olmasınlar diye zihnine bir fit sokmaya bakıyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2535, '(birine) fitil vermek', 'kızdırmak, azdırmak, kışkırtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2536, '(birine) gariplik basmak', 'yalnızlık çökmek: ''Başka yerlerde bana bir gariplik basıyor.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2537, '(birine göre) hava hoş', '''bir şeyin olmasıyla olmaması arasında fark yok'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2538, '(birine) gözdağı vermek', 'sonradan verilecek bir ceza ile korkutmak, yıldırmak, tehdit etmek, caydırmaya çalışmak: ''Sarhoş ağabeyi, parası pulu ile gözdağı vermeye kalktı onlara.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2539, '(birine) gözünün üstünde kaşın var dememek', 'birinin her davranışını hoş görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2540, '(birine) gurur gelmek', ' hlk. kurumlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2541, '(birine) gün doğmak', 'isteklerini gerçekleştirmek için iyi bir duruma erişmek veya eline olağanüstü bir fırsat geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2542, '(birine) gün geçmek', 'güneş çarpmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2543, '(birine) haddini bildirmek', 'sert bir karşılıkla uslandırmak, yola getirmek, cezalandırmak: ''Pestil gibi yerlerde uzandığıma bakma, anam, ben şu huysuza haddini bildirirdim.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2544, '(birine) hak vermek', 'birinin düşüncesini, davasını, iddiasını doğru bulmak: ''Annem de ağzının içinde sessizce söylenmeye koyulduğunda ona da hak vermiyordum.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2545, '(birine) hayatı cehennem etmek', 'büyük üzüntü ve sıkıntı vermek: ''En yakınlarından başlayarak herkese hayatı cehennem ettiği de doğrudur.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2546, '(birine) hor bakmak', 'değersiz saymak, değer vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2547, '(birine) içinden gülmek', 'sezdirmeden eğlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2548, '(birine) iltimas etmek (geçmek)', 'kayırmak, korumak: ''Sanırım ki öğretmenler bana iltimas geçiyorlardı.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2549, '(birine) ip takmak', 'birinin kötülüğü için çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2550, '(birine) iş düşmek', 'birinin iş yapması gerekmek: ''Hizmetçiden, aşçıdan, sana iş düşmeyecek bile.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2551, '(birine) iş etmek', 'aldatmak, birine beklemediği bir davranışta bulunarak onu zarara sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2552, '(birine) işi düşmek', 'birinin yardımına gereksinim duymak: ''Ara sıra işim düşerek kalem odasına girdikçe ona nazik ve kibar bir arkadaş muamelesi ediyordum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2553, '(birine) kan kusturmak', 'çok eziyet çektirmek: ''Fakat sonra bana haftalarca kan kusturdunuz, dedim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2554, '(birine) kanat açmak', 'birini korumak, himaye etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2555, '(birine) kanı kaynamak', 'çabucak sevgi duymak: ''Sonra da kanları kaynamıştı bu genç, yakışıklı ve zeki çocuğa.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2556, '(birine) kara çalmak', 'birine iftira etmek, kara sürmek: ''Allah için güzel kapışıyoruz, birbirimize kara çalmakta üstümüze yok!'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2557, '(birine karşı) boynu eğri olmak', 'herhangi bir sebeple birine karşı direnecek veya söz söyleyecek durumda olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2558, '(birine) karşı durmak', 'direnmek, dayanmak: ''Bak, eğer yüklendiğimiz, karşı durduğumuz kimseler yöneticiler olsalar ılımlılıktan söz açmazdım.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2559, '(birine) karşı gelmek', '1) başkaldırmak: ''Acaba böyle bir meraka uymak perilere karşı gelmek midir?'' -H. R. Gürpınar. 2) birini karşılamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2560, '(birine) kastı olmak', 'ona karşı kötülük etmek, zarar verme isteği beslemek: Bana kastı mı var?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2561, '(birine) kavuk sallamak', 'bir kimseye yaranmak için onun söz veya davranışlarını uygun bulmak, onaylamak: ''Boş bulundun, oğlum, hiç olmazsa bir iki saat kavuk sallayacaksın.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2562, '(birine) kemik atmak', ' hkr. susturmak, oyalamak için birini küçük bir şeyle avutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2563, '(birine) kıç attırmak', ' tkz. ondan üstün olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2564, '(birine) kılçık atmak', 'bir kimsenin işini karıştırmak, bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2565, '(birine) kıyak yapmak', ' argo maddi ve manevi destek olmak, yardım etmek: ''O kadar uzatmayalım bu işi, sana bir kıyak yapalım.'' -T. Yücel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2566, '(birine) kol kanat olmak (germek)', 'yardım etmek, korumak, himaye etmek: ''Sade çocuğuna değil, eşine de kol kanat gerer, ona da analık eder.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2567, '(birine) kolaylık göstermek', 'yapabilme yolu, imkânı sağlamak: ''Bu arzumda bana en çok kolaylık gösteren Behiç''tir.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2568, '(birine) kollarını açmak', '1) içtenlikle karşılamak veya kucaklamaya hazırlanmak, sevgisini ve dostluğunu göstermek: ''O gün ... bütün bir yıl dargın durduklarına kollarını açarlardı.'' -H. Taner. 2) korumak, yardım etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2569, '(birine) koltuk vermek', '1) yüzüne karşı övmek, pohpohlamak; 2) mec. koltuklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2570, '(birine) korku salmak', 'korkutmak: ''Devletin bu türden denetimlere kalkması, korku salma amacına yöneliktir.'' -M. C. Anday.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2571, '(birine) korku vermek', 'korkutmak: ''Kadınlıktan, erkeklikten tiksiniyordu ve etteki sır ona korku veriyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2572, '(birine) kredi açmak', '1) birine peşin para istemeden belirli bir ölçüye kadar mal vermeyi kabul etmek; 2) ödünç para vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2573, '(birine) kucak (kucağını) açmak', '1) korumak: ''Paris''teki hemşehriler bana büyük bir sevgi ve emniyetle kucaklarını açmışlardı.'' -R. N. Güntekin. 2) sığınacak yer vermek: ''Her çalışmak isteyene kucak açmışlardı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2574, '(birine) kul köle olmak', 'tam bir doğruluk ve özveri ile bağlanarak bütün isteklerini yerine getirmeye hazır olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2575, '(birine) külah giydirmek', 'hile ile, oyunla aldatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2576, '(birine) külahını ters giydirmek', 'çok kurnaz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2577, '(birine) madik atmak (etmek veya oynamak)', ' argo dolap çevirmek, hile yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2578, '(birine) malum olmak', 'içine doğmak: ''Ona da malum oldu haber / Koşup geldi odama'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2579, '(birine) meydan dayağı çekmek', 'herkesin içinde veya çok dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2580, '(birine) meydanı dar etmek', 'birini çok sıkıntıya sokmak, her yönden sıkıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2581, '(birine) mum tutturmak', 'aşırı disiplin altına almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2582, '(birine) müşkülat çıkarmak', 'yapmakta bulunduğu işi güçleştirecek durumlar yaratmak: ''Kaynanam olacak o kadın her türlü müşkülatı çıkarıyor.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2583, '(birine) nazı geçmek', 'dilediğini kabul ettirecek kadar hatırı sayılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2584, '(birine) ot yoldurmak', 'çok zor bir iş gördürmek, çok uğraştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2585, '(birine) oyun etmek', 'kurnazlıkla birini aldatmak: ''Kendisine oyun ettim diye, benden kuşkulandığı hâlde gene bana başvuruyor.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2586, '(birine) öyle gelmek', 'sanmak, zannetmek: ''Bana öyle gelirdi ki çocuklar yalnız kışın büyürler.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2587, '(birine) perestiş etmek', 'sevmek: ''Küçük hanıma bütün ruhumla perestiş ediyorum.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2588, '(birine) pervane olmak', 'birinin yanında onun hizmetine hazır olduğunu gerekli gereksiz göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2589, '(birine) rahat batmak', ' tkz. iyi bir durumdayken bu durumu olmayacak sebepler yüzünden bırakanlar için sitem yollu söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2590, '(birine) sempati duymak (beslemek)', 'birini sevimli, cana yakın bulmak: ''Şahsıma karşı gerçek bir sempati besliyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2591, '(birine) sırtını dayamak (vermek)', '1) bir yere dayanmak, yaslanmak: ''Kocaman duvara sırtını vererek üstüne zencefil ve tarçın serpilmiş salep içerlerdi.'' -S. F. Abasıyanık. 2) güçlü birine, bir yere güvenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2592, '(birine) silah çekmek', '1) silahla vurmaya davranmak; 2) silahla vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2593, '(birine) söz düşmemek', '1) başkalarının konuşmasından kendisine sıra gelmemek; 2) başkaları dururken kendisinin söz söylemesine gereklik bulunmamak: ''Bu toplantıda büyüklere söz düşmüyor.'' -H. E. Adıvar. 3) birinin söz hakkı olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2594, '(birine) söz gelmek', 'bir davranışından dolayı eleştiriye konu olmak, yerilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2595, '(birine) söz getirmek', '1) birinin eleştirilmesine sebep olmak, bir kimseye söz gelmesine yol açmak; 2) bir kimseye söz gelmesine yol açmak: ''Hâlbuki bu münasebetsiz dedikodular mektebe de söz getirmeye başladı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2596, '(birine) tarziye vermek', 'gönül almaya çalışmak, özür dilemek: ''Yüzüme bakmadan bana tarziye verdi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2597, '(birine) tavır almak (takınmak veya koymak)', 'mesafeli davranmak, uzak durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2598, '(birine) tepeden bakmak', 'küçümsemek: ''Bilakis amele olmayanlara karşı tepeden bakar, onları bir ağacın üstündeki mantarlar gibi görür.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2599, '(birine) uşaklık etmek', '1) bir kimseye hizmet veya kulluk etmek; 2) mec. kendi çıkarı için yasal veya ahlaki olmasa bile başkasının her dediğini yapmak zorunda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2600, '(birine) verip veriştirmek', 'ağzına geleni söylemek: ''Bunca yıl yalan okuduk, yalan dinledik / Aklına kim gelirse bağır, ver veriştir'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2601, '(birine veya bir şeye) söz geçirmek', 'söylediğini, istediğini, yaptırmak: ''Düğün sahipleri onlara söz geçiremediler.'' -M. Ş. Esendal. ''Her seferinde kalbine söz geçirerek zaaflarını denetleyebiliyordu.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2602, '(birine veya bir şeye) taş çıkarmak (çıkartmak)', 'biri ötekinden özellik, yetenek vb. bakımından üstün olmak: ''Zaten yol boyunca hem lezzetli hem de buzdolabına taş çıkartacak sulardan geçeceğiz.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2603, '(birine veya biriyle) ters düşmek', 'aykırı durumda olmak, karşıt olmak: ''Daha sonra o eşsiz lidere ters düşmek bahtsızlığına kapılmıştır.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2604, '(birine) yağcılık etmek', 'gereksiz biçimde övmek, dalkavukluk etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2605, '(birine) yetki vermek', 'yetkilendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2606, '(birine) yıkıntı olmak', 'birini çok zarara sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2607, '(birine) yuf borusu çalmak', 'kınama, üzüntü ve nefretini bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2608, '(birine) yukarıdan bakmak', 'kendini karşısındakinden üstün görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2609, '(birine) yük olmak', '1) bir kimse, sıkıntılı bir işini başkasına yaptırmak: ''Onların hepsinde sanki bulundukları yere yük oluyorlarmış gibi utangaç ve ürkek bir hâl vardır.'' -B. R. Eyuboğlu. 2) kendisi için başkasına para harcatmak, masraf yaptırmak: ''Bunları gazetelere verebilirsem amcama yük olmaktan kurtulacağıma emindim.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2610, '(birine) yüksekten bakmak', 'kendini karşısındakinden üstün görmek: ''O kadar nefret ettiğim İsmail, kim bilir bana ne yüksekten bakacak.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2611, '(birine) zahmet olmak', 'yapılan bir işten sıkıntı, yorgunluk duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2612, '(birine) zar atmak', '1) henüz başarısını kanıtlamamış biri için önceden olumlu düşünce belirtmek; 2) birinin ağzından laf alabilmek için onun düşüncesindeymiş gibi konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2613, '(birine) zevali olmak', 'zararı olmak, zararı dokunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2614, '(birine) zifos atmak', '1) sataşmak; 2) kara sürmek, iftira atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2615, '(birine) zincir vurmak', '1) elini ayağını bağlamak; 2) özgürlüğünü elinden almak: ''Ben ezelden beridir hür yaşadım, hür yaşarım / Hangi çılgın bana zincir vuracakmış? Şaşarım'' -M. A. Ersoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2616, '(birini) açığa çıkarmak', 'işinden çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2617, '(birini) açıkta bırakmak', '1) iş ve görev vermemek; 2) yersiz yurtsuz bırakmak; 3) birkaç kişiye sağlanması gereken olanaktan bir kişiyi yararlandırmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2618, '(birini) adam yerine (hesabına) koymak', 'adamdan saymak, varlığını kabul etmek: ''Anasını durmadan nefes aldırmadan azarlıyor, babasını adam yerine koymuyor, ağzını açarken susturuyordu.'' -R. H. Karay. ''Adam hesabına koyup bir hatır sormaz, bir çift lakırtı etmezler.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2619, '(birini) adamdan saymak', 'bir kimseye gereğinden fazla değer vermek, saygı duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2620, '(birini) adres göstermek', 'birini hedef göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2621, '(birini) ağzına baktırmak', 'kendini zevkle dinletmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2622, '(birini) ahmak yerine koymak', 'bir kimseye aptalmış, anlamazmış gibi davranmak: ''Beni bir ahmak yerine koyarak bu yığını babamın rahat rahat uyuduğu bir yatak diye göstermesi...'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2623, '(birini) altı okka etmek', 'birini kollarından ve bacaklarından tutup yukarı kaldırarak sallamak veya götürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2624, '(birini) anasından doğduğuna pişman etmek', 'çok eziyet etmek, çok üzmek, bezdirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2625, '(birini) araya koymak', 'bir işte sözü geçer bir kimsenin aracılığına başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2626, '(birini) arayıp sormak', '1) biri hakkında haber sormak; 2) birinin ziyaretine giderek ona karşı ilgi göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2627, '(birini) arkada bırakmak', 'birinden daha ileri gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2628, '(birini) avucunun içinde tutmak', 'ona istediğini yaptıracak güçte olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2629, '(birini) ayağının altına almak', 'tekme ile dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2630, '(birini) ayakta tutmak', '1) oturtmak gerekirken oturtmamak; 2) oyalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2631, '(birini) bir pula satmak', 'bir kimseyi bir çıkar uğruna harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2632, '(birini bir şeye) alet etmek', 'bir kimseyi hoş olmayan bir işte aracı olarak kullanmak: ''Sen kalktın, onu şakaya, latifeye, alaya alet etmek istedin.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2633, '(birini bir şeye) gark etmek', '1) batırmak, boğmak; 2) mec. birine bir şeyi bol bol vermek: ''Bu hayrı ile milletimizi nura gark edeceğine herkes kani idi.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2634, '(birini, bir şeyi) adam etmek', '1) eğitmek, yetiştirmek, topluma yararlı duruma getirmek; 2) bir yeri düzene sokmak; 3) işe yarar duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2635, '(birini, bir şeyi) başıboş bırakmak', 'üstünde hiçbir baskı veya denetim bulundurmamak, kendi havasına bırakmak: ''Durgun sular, başıboş bıraktığım sandalı / Yalıların önünden geçirdi yavaş yavaş'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2636, '(birini, bir şeyi) hor görmek', 'bir kimseye değersiz gözle bakmak: ''Çenelileri hor görmemeliyiz, gereğinde söze atılmak, konuşmak hatta epeyce konuşmak suspus oturmaktan yeğdir.'' -N. Uygur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2637, '(birini, bir şeyi) iki paralık etmek', 'değerini düşürmek: ''Talebeliğin şerefini iki paralık etmişti gene.'' -R. Ilgaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2638, '(birini, bir şeyi) musallat etmek', 'birini, bir başkasının başına bela etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2639, '(birini, bir şeyi) salık vermek', '1) tavsiye etmek: ''Dün akşam, bana bu kahveyi salık verdikleri zaman bütün gece sevincimden gözüme uyku girmedi.'' -Y. K. Karaosmanoğlu. 2) esk. haber vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2640, '(birini, bir yeri) haraca bağlamak', 'bir kimseyi belli zamanlarda kendisine belli miktarda para vermeye zorlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2641, '(birini) bozuk para gibi harcamak', 'değerini düşürecek biçimde bir kimseden yararlanmaya kalkışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2642, '(birini) burnundan yakalamak', 'birini yönetimi altına almak, kaçamak bulamayacağı duruma getirmek: ''Muhasebe ile defter tutma işlerini de üzerine aldığından milleti burnundan yakalamıştı.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2643, '(birini) cebinden çıkarmak', 'ondan çok üstün olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2644, '(birini) cepten aramak', 'bir kimseyi cep telefonundan aramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2645, '(birini) cin tutmak', 'bir inanışa göre cinlerin etkisiyle delirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2646, '(birini) çamurdan çekip çıkarmak', 'birini kötü veya onurunu tehlikeye düşüren bir durumdan kurtarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2647, '(birini) çarşamba pazarına çevirmek', 'özellikle yüze vurarak çok dayak atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2648, '(birini) çiğ çiğ yemek', 'parçalayıp öldürecek derecede kızdığı birini tehdit etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2649, '(birini) çileden çıkarmak', 'çok kızdırmak: ''Karşı taraftan konuşanın kolağası Mustafa Kemal oluşu hepsini çileden çıkarır.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2650, '(birini) defterden (defterinden) silmek', 'adını anmaz olmak, dost saymaz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2651, '(birini) demire vurmak', 'birini demir zincirle bağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2652, '(birini) deniz tutmak', 'deniz taşıtlarında sallantıdan etkilenmek: ''Biz tayfaları da deniz tuttu ama geminin doktoru bir defacık olsun, görünmedi.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2653, '(birini) dişine kestirmek', 'birini alt edeceğine veya dövebileceğine inanmak: ''Bunlar dişlerine kestirdikleri mahkûma iflahını kesinceye kadar gaddarca saldırırlar.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2654, '(birini) el üstünde tutmak', 'bir kimseye çok saygı ve sevgi göstermek: ''Ama azdır sanatçılara saygı gösterenler, onları el üstünde tutmak isteyenler.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2655, '(birini) fena etmek', '1) kötü davranmak; 2) kötü bir duruma düşürmek: Bu koku beni fena etti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2656, '(birini) gafil avlamak', 'umulmadık, beklenmedik bir zamanda yakalamak, zor duruma düşürmek: ''Nasıl sinsice yaklaşmıştı baykuş, düşmanlarını nasıl gafil avlamıştı.'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2657, '(birini) gaza getirmek', 'birini olmadık bir şey veya hayalî bilgilerle coşturmak, ileri sürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2658, '(birini) gır gıra almak (getirmek)', 'alaya almak: ''Baskına dikkat et diye emir yayımlamıştı da gır gıra aldık adamı.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2659, '(birini) gömleğinden (gömlekten) geçirmek', 'evlat olarak kabul etmek, evlat edinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2660, '(birini) gönülden çıkarmamak', 'sevilen kimseyi unutmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2661, '(birini) görüp gözetmek', 'korumak, yardım etmek, mukayyet olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2662, '(birini) gözü ısırmak', 'bir kimseyi tanıyacak gibi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2663, '(birini) gözüm görmesin', '''bana görünmesin, yüzünü görmek istemem'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2664, '(birini) hacı bekler gibi beklemek', 'büyük bir sabırsızlıkla beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2665, '(birini) haraca kesmek', 'zorbalıkla para koparmak veya çıkar sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2666, '(birini) helalliğe almak', 'biriyle evlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2667, '(birini) hırs basmak', 'hırslı duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2668, '(birini) hor tutmak', 'birine karşı küçümseyici, incitici davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2669, '(birini) hoş tutmak', 'birine iyi ve sevecenlikle davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2670, '(birini) içine sokacağı gelmek', 'birini çok sevmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2671, '(birini) ifrit etmek', 'çok kızmasına yol açmak, öfkelendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2672, '(birini) ipe çekmek', 'asarak öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2673, '(birini) işe almak', 'iş yerinde çalıştırmaya başlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2674, '(birini) işe koşmak', 'birine iş yaptırmak: ''Babama varıncaya kadar hepimizi işe koşuyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2675, '(birini) kan boğmak', 'beynine kan hücumuyla ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2676, '(birini) kanadı altına almak', 'korumak, himayesine almak: ''Yazarları, ressamları, müzikçileri kanatlarının altına alan krallar, padişahlar elbette hesaba sığmaz.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2677, '(birini) karşısına almak', 'birinin düşünce ve tutumuna katılmadığını belli etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2678, '(birini) kayışa çekmek', ' argo aldatmak, kandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2679, '(birini) kesintiye almak', 'biriyle sezdirmeden alay etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2680, '(birini) komalık etmek', '1) döverek kıpırdamayacak duruma getirmek; 2) mec. çok sinirlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2681, '(birini) koynuna almak', '1) biriyle beraber yatmak; 2) biriyle sevişmek için yatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2682, '(birini) kukla gibi oynatmak', '1) birine her istediğini yaptırmak; 2) birinin istediğini yapıyor görünerek onu oyalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2683, '(birini) kurşuna dizmek', '1) verilen ölüm cezasını askerî bir kıtanın attığı kurşunlarla yerine getirmek: ''Sarı çam deresinde bu otuz kadar eşkıyayı kurşuna dizdiler.'' -Y. Kemal. 2) öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2684, '(birini) linç etmek', 'yargılamadan öldürmek: ''Yakalamışlar adamı. Ahali linç edecekmiş az kalsın.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2685, '(birini) makaraya almak (sarmak)', 'bir kimseyle alay etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2686, '(birini) maskaraya almak', 'biriyle eğlenmek, alay etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2687, '(birini) maskaraya çevirmek', 'gülünç bir duruma sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2688, '(birini) maşa gibi kullanmak', 'maşası olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2689, '(birini) maytaba almak', 'biriyle alay etmek, eğlenmek: ''Bu evde hepsi beni maytaba alıyor.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2690, '(birini) mecbur tutmak', 'zorlamak, yükümlü saymak, mecbur etmek: ''Memleketin büyük menfaati, beni bu yolda harekete mecbur tutuyordu.'' -Atatürk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2691, '(birini) minder dışına atmak', 'ortadan kaldırmak, silmek, kovmak: ''Kötü para iyi parayı kovduğu gibi kötü yazar da iyi yazarı minder dışına atmaya çabalamaktadır.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2692, '(birini) on paralık etmek', 'birine hakarette bulunmak, birini kötü duruma düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2693, '(birini) paravan yapmak', 'kendini belli etmeyerek başkasının adından, yetkisinden, gücünden yararlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2694, '(birini) parmağında oynatmak', 'her istediğini yaptırmak, kukla gibi kullanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2695, '(birini) patentinin altına almak', 'egemenliği altına almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2696, '(birini) rezil etmek', 'isteyerek veya istemeyerek birini çok utanacak güç bir duruma sokmak: ''Sadece rezil etmekle kalmayacağım, hapse de tıktıracağım.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2697, '(birini) sarakaya almak', 'alay etmek, alaya almak: ''Taşralarda ağırbaşlı kitaplar okumaya kalkışan öğrencileri, arkadaşları sarakaya alır.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2698, '(birini) şaşkına çevirmek', 'şaşırtmak: ''Bir mektupla kadınlarınız sizi şaşkına çeviriyorlar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2699, '(birini) tefe koymak', 'biri hakkında alaylı dedikodu yapmak: ''Sonradan anlaşıldı ki adam hükûmeti tefe koymuş.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2700, '(birini) telaş almak', 'herhangi bir sebeple heyecanlanmak, endişelenmek, acele etmek: ''Mabeyni büyük bir telaş alıyor.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2701, '(birini) teneşir paklamak', 'yaşarken kirli işlere bulaşan kimseler için tek çıkar yol ölüm olmak: ''Pis herif, o huyundan vazgeçmez. Onu ancak teneşir paklayacak.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2702, '(birini) terkisine almak', 'üzerinde bulunduğu atın sağrısına bindirmek: ''Sonra atlarının terkisine aldılar, benimle beraber kaçtılar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2703, '(birini veya bir şeyi) göklere çıkarmak', 'aşırı derecede övmek: ''Kadın dergileri bizi göklere çıkarıyorlardı, bunu da hak etmemiştik.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2704, '(birini veya bir şeyi) gölgede bırakmak', 'ondan daha üstün bir düzeye yükselmek, ondan çok daha başarılı olmak: ''Enişte, delikanlıları gölgede bırakacak kadar çalıştı; hâlâ ayak üstünde.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2705, '(birini veya bir şeyi) gözü görmez olmak', 'artık ona değer vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2706, '(birini veya bir şeyi) gözü kesmek', 'bir işi yapabilme konusunda kendisine veya başkalarına güvenmek: ''Şimdi Murat dağlarında eğlenirim, beni bulmak istersen adamlarının da gözü keserse oraya yolla.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2707, '(birini veya bir şeyi) gözü kesmemek', '1) bir işi yaparken kendine veya başkalarına güvenmemek; 2) beğenip seçememek: ''Kendi deyimiyle otuzu geçtiği hâlde isteyenler arasında kendine uygun birisini gözü kesmediği için evlenmemişti.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2708, '(birini veya bir şeyi) gözü tutmak', 'güvenmek, beğenmek: ''Bu genç çocukla bu üstü başı oldukça eski ihtiyar adamı gözü tutmamıştı.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2709, '(birini veya bir şeyi) kayıttan düşmek', 'bir yere mal olmaktan çıkararak defterde bu durumu belirtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2710, '(birini veya bir şeyi) kendi hâline bırakmak', 'ilgilenmemek, karışmamak: ''Ertesi sabah beni balığa çıkarken uyandırmayacaklardı. Bırakacaklardı kendi hâlime.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2711, '(birini veya bir şeyi) kurşun tutmak', 'kurşuna hedef olmak, kurşun değecek gibi olmak: ''Çatın arkadaşlar da atları çatın / Kurşun bizi tutuyor sipere yatın'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2712, '(birini veya bir şeyi) kurşun yağmuruna tutmak', 'çok sayıda ve sürekli kurşun atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2713, '(birini veya bir şeyi) tepe tepe kullanmak', 'sağlamlığına güvenilen şeyleri yıpranacağını düşünmeden, esirgemeden, sakınmadan hoyratça kullanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2714, '(birini) yalancı çıkarmak', 'birinin yalan söylediğini ortaya koymak veya yalan söylememesini sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2715, '(birini) yarı yolda bırakmak', 'yapılan yardımı sonuna kadar sürdürmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2716, '(birini) yatak çekmek', 'çok bitkin ve güçsüz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2717, '(birini) yer kabul etmez', 'çok günahkâr.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2718, '(birini) zıvanadan çıkarmak', 'sinirlendirmek, öfkelendirmek: ''Herhangi bir hastada aldığı tedbirlere rağmen beklediği sonucun doğmaması onu zıvanadan çıkarırdı.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2719, '(birini) zincire vurmak', 'prangaya vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2720, '(birinin) abdestini vermek', ' argo azarlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2721, '(birinin) acısına dayanamamak', 'bir kimse bir yakınının ölümünden büyük üzüntü duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2722, '(birinin) acısını almak', 'sıkıntısını, üzüntüsünü azaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2723, '(birinin) açığı çıkmak', 'saklamakla görevli bulunduğu paranın veya malın eksik olduğu anlaşılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2724, '(birinin) adaletine sığınmak', 'birinden anlayış, hoşgörü, yakınlık beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2725, '(birinin) adını ağzına abdestle almak', 'bir kişiyi anarken çok saygılı davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2726, '(birinin) adını kirletmek (lekelemek)', 'adının kötüye çıkmasına yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2727, '(birinin) adını taşımak', '1) birinin adıyla anılmak; 2) sahip olduğu adın sorumluluğunu yüklenmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2728, '(birinin) adını vermek', 'birinin adını söylemek: ''Bunlar yaşama yolunda bir engele çarptılar mı hemen dedelerinin adını verirler ve kendilerini güçlükten sıyırıp çıkarırlardı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2729, '(birinin) afyonunu patlatmak', ' argo kendi keyfine dalmış olan birini öfkelendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2730, '(birinin) ağzına bakakalmak', 'sözlerine hayran olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2731, '(birinin) ağzına bir parmak bal çalmak', 'birini tatlı sözlerle veya çeşitli hediyelerle bir süre için kandırmak, oyalamak: ''Hürriyet, müsavat diye herkesin ağzına bir parmak bal çaldılar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2732, '(birinin) ağzına sakız olmak', 'dedikodusuna konu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2733, '(birinin) ağzına tükürmek', 'hakaret ederek uyarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2734, '(birinin) ağzından', '1) birisinden dinleyerek: Bu şiiri Âşık Veysel''in ağzından yazdım. 2) adına.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2735, '(birinin) ağzından kapmak', '1) birinin bildiği şeyleri, ustalıklı konuşmalarla ona sezdirmeden öğrenmek: ''Bütün bu lafları harfi harfine Fatma Hanım''ın ağzından kapmış, bana kendi sözleri imiş gibi tekrar ediyor.'' -Y. K. Karaosmanoğlu. 2) birinin konuşmasını keserek kendisi söze başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2736, '(birinin) ağzından lokmasını almak', 'birinin hakkı olan şeyi ondan almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2737, '(birinin) ağzını bağlamak', 'bir kimseyi herhangi bir sebeple söz söyleyemez duruma getirmek, susmak zorunda bırakmak: ''Ortağım burada kocama basmış büyüyü, basmış büyüyü. Dilini, ağzını bağlamış adamcağızın.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2738, '(birinin) ağzını bıçak açmamak', 'üzüntüsünden söz söyleyecek durumda olmamak: ''O gittiği günden beri Zeynep kadının ağzını bıçak açmıyor.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2739, '(birinin) ağzını dilini bağlamak', 'birini konuşamaz duruma getirmek: ''O şıllık basmış büyüyü, adamcağızın ağzını dilini bağlamıştı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2740, '(birinin) ağzını kapamak', 'kendisine çıkar sağlaması için bir kimseyi susturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2741, '(birinin) ağzını kullanmak (satmak)', 'birinin söylediklerinin aynısını söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2742, '(birinin) ağzını tıkamak', 'sözünü kesmek, susturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2743, '(birinin) ağzının içine bakmak', '1) ne söyleyeceğini beklemek; 2) onun sözüne göre davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2744, '(birinin) ağzının içine girmek', '1) çok yanaşmak, iyice sokulmak; 2) hayranlıkla, büyük bir zevkle seyredip dinlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2745, '(birinin) ağzının kokusunu çekmek', 'bir kimsenin çekilmez davranışlarına katlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2746, '(birinin) ağzının kokusunu çekmek', 'birinin her türlü isteğine, kaprisine boyun eğmek: ''Onca yıl gurbetin kahrını, gâvurun ağzının kokusunu çekmiştik.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2747, '(birinin) ahı tutmak', 'birinin ilenmeleri gerçekleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2748, '(birinin) ahı yerde kalmamak', 'yaptığı ilenme er geç etkisini göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2749, '(birinin) ahını almak', 'ah almak: Ana baba ahını almak doğru değildir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2750, '(birinin) ak dediğine kara demek', 'inatçılık ederek karşısındaki ile anlaşmaya yanaşmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2751, '(birinin) aklına koymak', 'bir kimse birine, bir şey telkin etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2752, '(birinin) aklını çelmek', '1) niyetinden, kararından caydırmak: ''Böyle olursa zamanla kızının aklını çelmek kolaylaşırdı.'' -N. Cumalı. 2) ayartmak, baştan çıkarmak: ''Hasan gelip Reha Bey''in, beni filan gazinoda beklediğini söyleyerek aklımı çeliyordu.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2753, '(birinin) aklını karıştırmak', 'birini ne yapacağını bilemez duruma getirmek, şaşırtmak, bocalatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2754, '(birinin) aleyhine dönmek', 'destek vermekten vazgeçip karşı duruma geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2755, '(birinin) alnını karışlamak', 'küçümseyerek meydan okumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2756, '(birinin) anası ağlamak', 'çok sıkıntı çekmek, eziyet çekmek, bitkin duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2757, '(birinin) anasını ağlatmak', ' kaba bir kimseye çok eziyet etmek, çok sıkıntı çektirmek: ''Kim ona yan bakarsa kemiklerini kırar, anasını ağlatırım.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2758, '(birinin) arkasını sıvamak', 'okşamak, övmek, iltifat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2759, '(birinin) ateşine yanmak', 'bir kimse yüzünden zarara uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2760, '(birinin) ayağına gitmek', 'alçak gönüllülük ederek veya saygı göstererek birinin yanına varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2761, '(birinin) ayağının türabı olmak', 'bir kimse başka bir kimseye kul gibi bağlanıp onun her emrini yerine getirmek: ''Ayağınızın türabıyım, çakeriniz efendimizi dünyada bırakmam.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2762, '(birinin) bam teline basmak (dokunmak)', 'en çok kızacağı şeyi yapmak veya sözü söylemek: ''Firuzan bam teline basıyor, aksi sesler çıkarıyor.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2763, '(birinin) baş (başının) belası olmak', 'sıkıntı, üzüntü, eziyet vermek: ''Benim bir köpeğim vardır. Başımın belası!'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2764, '(birinin) başı için', '''çocuğumuzun başı için, annenizin başı için'' vb. sözlerde değerli bir kişi ortaya konarak kullanılan ant veya yalvarma sözü: ''Aman Ali Bey''in başı için beni ele vermeyin.'' -M. C. Kuntay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2765, '(birinin) başına çalmak', 'bir şeyi öfkeyle, nefretle geri vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2766, '(birinin) başına çıkmak', 'birinden yüz bulup ona karşı pek şımarıkça davranmak: ''Hizmetçi kadınlarla içli dışlı olmamak, onlara mesafeli davranmak gerekirdi, yoksa başınıza çıkarlardı.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2767, '(birinin) başına çorap örmek', 'birine, haberi olmadan kötü duruma düşürücü davranışta bulunmak: ''Ya başına bir çorap ördürüsrse?'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2768, '(birinin) başına dikilmek', '1) birinin yanından uzaklaşmamak, onu denetim altında bulundurmak; 2) bir işi yaptırmak için yanında ayakta durmak; 3) bir şeyin yanında ve ayakta beklemek: ''Gidip iskelenin başına dikiliyor gelen yolcuyu buyur etmek için.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2769, '(birinin) başına ekşimek', '1) ağır yük olmak; 2) üstüne kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2770, '(birinin) başına gaile açmak', 'sıkıntı yaratmak, üzüntü vermek: ''Devletin başına sayısız gaileler açmak yolunda hiçbir fırsatı kaçırmadı.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2771, '(birinin) başına geçmek', 'en üstün yeri almak, önderlik yapmak: ''Onları bahçeye toplayarak başlarına geçerek akşama kadar âdeta kudurturdum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2772, '(birinin) başına gelmek', 'beklenmedik, şaşırtıcı bir olay veya durumla karşılaşmak: ''Onu dinledikten sonra olanaklı olduğunca ilişkimizi gizleyerek Mine''nin başına gelenleri anlatıyorum.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2773, '(birinin) başına kâhya kesilmek', 'olur olmaz her işine karışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2774, '(birinin) başına kalmak', 'istemediği hâlde bir işi yapmak veya bir kimseye bakmak zorunluluğu ile karşılaşmak: ''Adam yüzüstü bırakıp gidince böyle bir numara çevirip başına kalmayı deniyor anlaşılan.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2775, '(birinin) başında değirmen çevirmek', 'gürültü ile tedirgin etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2776, '(birinin) başını istemek', 'öldürülmesini istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2777, '(birinin) başını nâra yakmak', 'birini ağır bir zarara uğratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2778, '(birinin) başını yemek', 'güç duruma düşmesine yol açmak: ''Birbirlerinden şüphelensinler, birbirlerinin başını yesinler.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2779, '(birinin) başının etini yemek', 'karşısındakini bezdirinceye, bıktırıncaya kadar sürekli konuşmak veya söylemek: ''Köyde patladığını telefonlarla, telgraflarla bana bildirerek başımın etini yiyen sen değil misin?'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2780, '(birinin) bedduasını almak', 'biri tarafından kendisine ilenilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2781, '(birinin) bıraktığı (bağladığı) yerde (çayırda) otlamak', ' hlk. uzun süredir hiçbir ilerleme veya değişim gösterememek: ''Tek suçu, kendini yeni devre uyduramayışı, bıraktığım yerde otluyormuş, ne bileyim.'' -E. Işınsu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2782, '(birinin) bir dediğini iki etmemek', 'her istediğini hemen yapmak: ''Bu adamın bir dediğini iki etmediği genç bir sevgilisi varmış.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2783, '(birinin, bir işin) ardına (arkasına) düşmek', 'arkasından gitmek, peşini bırakmamak: ''Muhatabı da olmayan gecikmiş hesapların ardına düşüyordu.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2784, '(birinin) bir sözünü (dediğini) iki etmemek', 'birinin her istediğini hemen yerine getirmek: ''Maliye müfettişi sizin beyin mektep arkadaşıymış. Sözünden çıkmaz, bir dediğini iki etmezmiş. O isterse arkasından söyler, kocamı kurtarır.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2785, '(birinin bir şey) gözünü bağlamak', 'doğruyu bulamaz, düşünemez duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2786, '(birinin, bir şeyin) akıbetine uğramak', 'birinin içinde bulunduğu kötü duruma benzer bir duruma düşmek: ''Ben Kristof Kolomb''un akıbetine uğramak istemiyorum.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2787, '(birinin, bir şeyin) peşinde olmak', 'o şeyi çok istemek: O şimdi koltuk peşinde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2788, '(birinin, bir şeyin) tiryakisi olmak', 'bir şeye veya birine çok düşkün olmak: ''Artık birbirimizin tiryakisi olmuştuk.'' -Y. Z. Ortaç. ''Açık söyleyeyim, ben parasızlığın tiryakisi bile oldum.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2789, '(birinin, birilerinin) ağzına düşmek', 'dile düşmek: ''Doğrusu ben ne güzelliğimin ne de ilmimin kimsenin ağzına düşmesine razı değilim.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2790, '(birinin, birilerinin) takdirini kazanmak', 'bir kimse veya bir topluluk tarafından beğenilmek: ''İhtimal ki senin alın yazında şunlar yazılıydı: Âlemin saygı ve takdirini kazanmış bir adam olacaksın.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2791, '(birinin) bokunda boncuk bulmak', ' kaba birine hak etmediği hâlde çok değer vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2792, '(birinin) borusu ötmek', ' hlk. sözü geçmek, yetkisi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2793, '(birinin) borusunu çalmak', 'çıkar sağladığı kimsenin davasını gütmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2794, '(birinin) burnuna girmek', 'birine çok sokulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2795, '(birinin) burnundan ayrılmamak', 'yanından gitmemek, uzaklaşmamak: ''Demesin ki gece gündüz kızın burnundan ayrılmıyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2796, '(birinin) can damarına basmak', 'bir işin en önemli yönü üzerinde durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2797, '(birinin) canı yok mu?', 'birinin katlandığı sıkıntıyı başkalarına örnek göstermek için söylenen bir söz: Onun canı yok mu, sabahtan beri çalışıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2798, '(birinin) canına susamak', 'birini öldürmeyi istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2799, '(birinin) canını acıtmak', 'birine acı vermek: ''Korku, canını acıtacak, elle tutulur gözle görülür bir madde oldu.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2800, '(birinin) cemaziyelevvelini bilmek', 'bir kimsenin herkesçe bilinmeyen, geçmişteki her türlü yönünü veya kötü durumunu bilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2801, '(birinin) cesaretini kırmak', 'yürekliliğini gidermek, korkutmak: ''Zekânız size yardım etmez, bilakis cesaretinizi kırar.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2802, '(birinin) ciğerine işlemek', '1) kötü söz, kötü davranış çok dokunmak, etkilemek; 2) kötü koku rahatsız etmek: ''Tezek kokusu burnumun direğini kırmış, ciğerime işlemişti.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2803, '(birinin) ciğerini okumak', 'onun aklından geçenleri, gizli düşüncelerini bilmek: ''Mademki ... her baktığı insanın ciğerini dahi okuyordu, nasıl olup da etrafını saran mideci dalkavukların ikiyüzlülüğünü anlayamıyordu?'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2804, '(birinin) ciğerini sökmek', 'bir kimseyi çok büyük zararlara uğratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2805, '(birinin) ciğerini yakmak', 'bir kimseye büyük bir acı çektirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2806, '(birinin) ciğerinin içini bilmek', 'çok yakından tanımak, her türlü düşüncesini bilmek: ''Ben böylelerinin ciğerinin içini bilirim, dedi. Bu kız hanım ölürse belki beni alır diye ümitlendi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2807, '(birinin) çalımından geçilmemek', 'çok kurumlu olmak, çok çalımlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2808, '(birinin) çanına ot tıkmak (tıkamak)', 'sesini çıkaramayacak, kötülük edemeyecek bir duruma getirmek, susturmak: ''İsterseniz çanıma ot tıkar, beni mahvedersiniz.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2809, '(birinin) çenesini açtırmak', 'söz fırsatı vermek: ''Büyük hanım gece erken yatıp kocasının çenesini açtırmamak için şimdi öğle yemeklerinden sonra biraz kestiriyormuş.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2810, '(birinin) çenesini bıçak açmamak', 'sıkıntı ve üzüntüden konuşamamak: ''Hiçbirimizin çenesini bıçak açmıyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2811, '(birinin) çukurunu kazmak', 'birinin felaketine yol açacak bir düzen kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2812, '(birinin) damarını bulmak', 'hoşlanabileceği biçimde davranıp uysallığını sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2813, '(birinin) dediğine gelmek', 'birinin düşüncesini önce kabul etmezken sonradan doğru bulup kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2814, '(birinin) derdi başından aşkın (olmak)', '1) birçok sorunu bulunmak: ''Kendi derdimiz başımızdan aşkın, bir de başkasının derdi ile uğraşacak vaktimiz yok.'' -H. Taner. 2) aşırı derecede meşgul olmak: ''Benim derdim başımdan aşkın, bir de onunla uğraşamam şimdi.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2815, '(birinin) derdi günü', 'çok ilgilenilen, üzerinde çok düşünülen şey: ''Onun derdi günü roman okumak! Dağ başındasın / Derdin günün hasretlik / Akşam olmuş / Güneş batmış / İçmeyip de ne halt edeceksin?'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2816, '(birinin) derdini deşmek (depreştirmek)', 'derdini hatırlatıp yeniden üzülmesine yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2817, '(birinin) diline düşmek', 'yermek veya alay etmek amacıyla birinin kötü veya yanlış davranışını sürekli söylemek: ''Mahallede acubelerin diline düşmekten korkuyorum.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2818, '(birinin) dilini bağlamak', 'bir kimseyi herhangi bir sebeple söz söyleyemez duruma getirmek, susmak zorunda bırakmak: ''Ortağım burada kocama basmış büyüyü, basmış büyüyü. Dilini, ağzını bağlamış adamcağızın.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2819, '(birinin) dişini sökmek', 'kötülük edemeyecek duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2820, '(birinin) dizginini çekmek', 'birinin aşırı davranışlarına engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2821, '(birinin) dizginini kesmek', 'üzerindeki baskıyı artırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2822, '(birinin) dizlerine kapanmak', 'çok yalvarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2823, '(birinin) düğününde kalburla (elekle) su taşımak', 'bir yardımına karşılık olarak bekâr bir kimseye çok büyük bir yardımda bulunma sözü vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2824, '(birinin) dümen suyunda gitmek', 'birine bağımlı olmak, her şeyde ona uyarak davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2825, '(birinin) dümenini bozmak', ' argo hileli işe engel olmak: ''Müdür beyin onların dümenini bozabilecek bir kudreti olmadığı belliydi.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2826, '(birinin) ekmeğine yağ sürmek', 'istemediği hâlde birinin işine yarayacak biçimde davranmak: ''Bu name, cumhuriyetçilerin ekmeğine yağ sürdü.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2827, '(birinin) ekmeğiyle oynamak', 'geçim kaynağını tehlikeye düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2828, '(birinin) eli ayağı (olmak)', 'yardımcısı (olmak), her işine yarar (olmak).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2829, '(birinin) elinde ... var', 'yapar, bilir, bulundurur: Elinde güzel bir mesleği var.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2830, '(birinin) elinden (bir şey) düşmemek', 'bir şeyle sürekli ilgilenmek: ''Hiç keser, çapa elinden düşmüyordu, yeri kazıyor kazıyordu.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2831, '(birinin) eline bakmak', '1) bir kimsenin yardımıyla geçinmek: ''Bir senedir burada oturuyorlar, o küçüğün eline bakıyorlar.'' -P. Safa. 2) ''ne getirdi'' diye gözlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2832, '(birinin) eline kalmak', 'ondan başka yardım edeni olmamak, yalnız ona muhtaç olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2833, '(birinin) eline su dökemez', '''değerce ondan çok geride'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2834, '(birinin) elini kolunu bağlamak', 'bir şey yapamayacak duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2835, '(birinin) emrine girmek', 'bir kimsenin buyruğu altında bulunmayı kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2836, '(birinin) ensesine yapışmak', 'yakalayıp sıkıştırmak: ''Polisler ikametgâhsız diye ensene yapışırlar, seni deliğe tıkarlar.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2837, '(birinin) eteğini toplamak', '1) birinin derli toplu olmasını sağlamak, birini düzenli yaşatmak: ''Senin eteğini toplamaktan hamur açacak zamanı mı var onun?'' -A. Kulin. 2) birinin kötü yaptığı işleri düzeltmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2838, '(birinin) etrafında dört dönmek (pervane olmak)', 'isteğini elde etmek için birinin yanından ayrılmayıp gönlünü etmeye çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2839, '(birinin) fikrini almak', 'fikir almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2840, '(birinin) fikrini çelmek', 'kandırmak, düşüncesini değiştirtmek, ikna etmek: ''Belki bir yolunu buluruz da kızın fikrini çeleriz, diyormuş.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2841, '(birinin) geçmişlerini karıştırmak', '1) birinin ölmüşlerini yermek veya onlara sövmek; 2) geçmişini araştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2842, '(birinin) geçtiği yoldan geçmek', 'daha önce aynı olayları yaşamış olmak, deneyimli olmak: ''Onun geçtiği yollardan geçtiğim için tahminlerim biraz daha kolaylaşıyor.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2843, '(birinin) gemisi şapa oturmak', 'iş, düzelemeyecek kadar bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2844, '(birinin) gırtlağına basmak', 'birine bir şey yaptırmak için dayatmak veya inat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2845, '(birinin) gırtlağına sarılmak', 'peşini bırakmamak, musallat olmak: ''Şimdiye kadar bana iki paralık faydan dokundu mu ki her gün alacaklı gibi gırtlağıma sarılıyorsun!'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2846, '(birinin) gönlü ile oynamak', 'sever görünüp eğlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2847, '(birinin) gönlünü etmek (yapmak)', 'birini razı ve hoşnut etmek: ''Ben patronun gönlünü ederim, hafta arasında.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2848, '(birinin) gönlünü hoş etmek', 'birinin dileğini yerine getirerek onu sevindirmek: ''Feride, çocukların birini bırakıp ötekini alıyor, hepsinin sıra ile gönlünü hoş etmek istiyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2849, '(birinin) gözlerine mil çekmek', 'birinin gözlerini kızgın mille kör etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2850, '(birinin) gözü önünde', 'yanında, yakınında: ''Çocukluğundan beri onun bir siniri de aydınlıkta başkasının gözü önünde uyumaktı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2851, '(birinin) gözüne girmek', 'sevgi ve ilgisini kazanmak: ''Tevfik Bey''in gözüne girdiğini de etraflıca anlattı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2852, '(birinin) gözünü açmak', 'görüşünü değiştiren bilgi vermek, uyarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2853, '(birinin) gözünü korkutmak', 'yıldırmak: ''Şimdiden gözünü korkutmazsan ileride büsbütün başa çıkılmaz bu bacaksızlarla.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2854, '(birinin) gözünün (gözlerinin) içine bakmak', '1) bir kimsenin üstüne titremek; 2) buyruğunu yerine getirmeye hazır bulunmak; 3) bir arzunun gerçekleşmesi için gözleriyle birine yalvarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2855, '(birinin) gözünün yaşına bakmamak', 'acımamak, merhamet etmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2856, '(birinin) günahına girmek (günahını almak)', '1) birisi için haksız olarak kötü düşünmek, kuşkulanmak: ''Ne yazık, günahına girdim bu halkın demin / Zehir nefesleri var bu seslerde matemin'' -F. N. Çamlıbel. 2) iftira etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2857, '(birinin) günahını çekmek', 'birinin yaptığı veya birine karşı yapılan kötülüğün cezasını görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2858, '(birinin) güzel hatırı için', 'yüzünden, sebebiyle: ''İnanınız ki müdürün güzel hatırı için işime başladım.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2859, '(birinin) harcı olmamak', 'bir iş, birinin yapabileceği nitelikte olmamak: ''Gitmeyin, uzaktan davulun sesi hoş gelir, yazı yaban, sizin harcınız değil, dedik.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2860, '(birinin) hatırı için', 'bir kimsenin, gönlü hoş olsun diye: ''Ama bunca eziyete sırf oğlunun hatırı için katlandığına emin değildi artık.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2861, '(birinin) hatırı kalmak', 'gücenmek, kırılmak: ''Eve geldiğiniz, gittiğiniz, bir yerde rastlaştığımız zaman elimi saygıyla öpmezseniz hatırım kalır.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2862, '(birinin) hatırından çıkmamak', 'sevdiği, saydığı birinin isteğini reddetmeyip gönlünü kırmaktan çekinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2863, '(birinin) hatırını kırmak', 'üzmek, gücendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2864, '(birinin) havası olmak', 'bir kimsenin albenisi veya cana yakınlığı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2865, '(birinin) hesabına gelmek', 'yararına uygun, elverişli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2866, '(birinin) hışmına uğramak', 'birinden zulüm görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2867, '(birinin) hoşuna gitmek', 'beğenmek: ''Zamanları yararak hatta zamanı geriye doğru sürerek kendisini bulmam hoşuna gitmişti.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2868, '(birinin) huyuna suyuna gitmek', 'birini kızdırmayacak veya ürkütmeyecek biçimde uysalca davranmak, alışkanlıklarına, isteklerine uygun davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2869, '(birinin) içini okumak', 'birinin gizli, saklı düşüncelerini anlamak: ''Çökük gözlerinin arkasında insanın içini ezberden okuyan bir hayat sezişi var.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2870, '(birinin) içini sarmak', 'sürekli düşünmek, hep onunla meşgul olmak: ''Saat dokuza yaklaşırken onun içini bir bayram sevinci sarardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2871, '(birinin) ifadesini almak', '1) sorguya çekmek: ''Komiser Efendi, masanın başına oturup ifadesini almaya başladığı zaman ayağa kalktı.'' -R. N. Güntekin. 2) görgü tanığının anlattıklarını yazmak; 3) argo tepelemek; 4) argo üstün gelmek, yenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2872, '(birinin) iltiması olmak', 'arkası, kayırıcısı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2873, '(birinin) ipini çekmek', 'birini ölçülü davranmaya zorlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2874, '(birinin) ipiyle kuyuya inilmez', '''kendisine güvenilmez'' anlamında kullanılan bir söz: ''O tüysüz keratanın ipiyle kuyuya inilmez.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2875, '(birinin) ismini vermek', 'adını vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2876, '(birinin) işi başından aşmak (aşkın olmak)', 'pek çok işi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2877, '(birinin) işi rast gitmek', 'şans yardımıyla işi iyi, istediği gibi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2878, '(birinin) işini bitirmek', ' argo öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2879, '(birinin) iştahı açılmak', 'yemek isteği artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2880, '(birinin) iştahı kabarmak', 'isteği çoğalmak, heveslenmek: ''Derken, yavaş yavaş benim de iştahım kabarmaya başladı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2881, '(birinin) izinden yürümek', 'birine içten bağlanarak onun başladığı işi aynı anlayışla sürdürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2882, '(birinin) izine basmak', ' esk. gözden uzaklaştırmayarak ne yaptığını gözetlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2883, '(birinin) kafasını vurmak', ' esk. bir kimsenin kafasını kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2884, '(birinin) kalbini doldurmak', 'yüreğini sevgiyle ısıtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2885, '(birinin) kanı kaynamak', 'coşkun ve kıpırdak olmak: Çocuğun kanı kaynıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2886, '(birinin) kanına ekmek doğramak', '1) birinin ölümüne yol açarak sevinmek; 2) birini küçük düşürmek, birine zarar vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2887, '(birinin) kanına susamak', 'belasını aramak: ''Ben onun kanına susadım diyor, başka bir şey demiyor.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2888, '(birinin) kanını kurutmak', 'canından bezdirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2889, '(birinin) kanını yerde koymak', 'birini öldüreni ölümle cezalandırmamak: ''Oğlum Halil''in kanını yerde koyarsanız bu dünyada da öteki dünyada da ak sütüm size haram olsun.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2890, '(birinin) kanlısı olmak', 'birinin katili olmak: ''Ananın kanlısı olmak, ölünceye kadar ateşten gömlek giymektir.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2891, '(birinin) kapısını aşındırmak', 'yanına çok sık gitmek: ''Sabahtan akşama kadar belki kapısını aşındıranlar elli altmışı bulur.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2892, '(birinin) kapısını çalmak', 'birine başvurmak: ''İskele memurluğu isteyen işçiler hep benim kapımı çalıyorlar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2893, '(birinin) kârı olmamak', 'yapabileceği iş olmamak: ''Yaralı yaban domuzu gibi kaçan canavara yetişmek lalanın kârı değildi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2894, '(birinin) karışanı görüşeni olmamak', 'işine kimse karışmamak, özgür olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2895, '(birinin) karşısına geçmek', '1) karşı düşünceye katılmak; 2) karşı partiye, gruba gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2896, '(birinin) kaydını silmek', 'kayıttan düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2897, '(birinin) kazanı kapalı kaynamak', 'içyüzü bilinmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2898, '(birinin) keli görünmek', ' tkz. kusuru ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2899, '(birinin) keli kızmak', 'öfkelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2900, '(birinin) kellesini uçurmak', 'kafasını keserek koparmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2901, '(birinin) kellesini vurdurmak', 'öldürtmek: ''Atı kimin evinde, kimin elinde bulursa onun kellesini vurduracakmış.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2902, '(birinin) kemikleri sayılmak', 'çok zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2903, '(birinin) kemiklerini kırmak', 'birini çok dövmek, aşırı dayak atmak: ''Kim ona yan bakarsa kemiklerini kırar, anasını ağlatırım.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2904, '(birinin) kestiği tırnak olamamak', 'bir kimse, söz konusu olan kimseden değerce çok aşağı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2905, '(birinin) keyfini kaçırmak (bozmak)', 'üzmek: ''Ne istedin adamdan, dedi. Keyfini kaçırdın oruçlu oruçlu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2906, '(birinin) keyfini yapmak', 'her türlü istek ve dileği yerine getirmek: ''Ben dünyaya sanki herkesin keyfini yapmak, herkesin menfaatine hizmet etmek için gelmiştim.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2907, '(birinin) keyfinin kâhyası olmamak', 'birine alışkanlıklarına, davranışlarına, düzenine karışmaya hakkı olmamak: ''Eloğlunun keyfinin kâhyası değiliz.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2908, '(birinin) kılığına girmek', 'onun gibi giyinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2909, '(birinin) kılına dokunmamak', 'bir kimseye dokunacak, zarar verecek en ufak bir davranışta bile bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2910, '(birinin) kızı kısrağı', 'birinin ailesindeki kızlar ve kadınlar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2911, '(birinin) kirli çamaşırlarını ortaya dökmek', 'birinin ayıp, kusur veya suçlarını açıklamak, söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2912, '(birinin) koluna girmek', 'kolunu birinin koltuğu altından geçirmek: ''Koluna iki polis girmişti.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2913, '(birinin) kolunda altın bileziği olmak', 'kazanç sağlayan bir mesleği, zanaatı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2914, '(birinin) konuğu olmak', 'birine konuk olarak gidip kalmak: ''Onun köyüne gittim, onun konuğu oldum orada on beş gün.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2915, '(birinin) koynuna girmek', 'biriyle yatıp sevişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2916, '(birinin) kucağına oturmak', '1) dizlerinin üstüne oturmak; 2) argo yaltaklanmak; 3) argo birinin amaçlarına alet olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2917, '(birinin) kulağını bükmek', 'bir sorun karşısında dikkatli davranması için uyarıda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2918, '(birinin) kulağını çekmek', '1) ceza olarak kulağını tutup bükerek çekmek; 2) uyarmak için hafif bir ceza vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2919, '(birinin) kulağını çınlatmak', 'birini anmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2920, '(birinin) kulağını doldurmak', 'bir kimseye başkasından bilgi almadan önce konu üzerinde bilgi verirken kendi düşüncesini aşılamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2921, '(birinin) kulağının zarı patlamak', 'gürültü yüzünden rahatsız olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2922, '(birinin) kuyruğuna basmak', 'birini incitip saldırıda bulunmasına yol açmak, tahrik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2923, '(birinin) kuyruğuna teneke bağlamak', '1) biriyle aşırı derecede alay etmek; 2) birini, herkesin alay edeceği biçimde kovmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2924, '(birinin) kuyruğunu kıstırmak', 'birini güç bir duruma düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2925, '(birinin) kuyusunu kazmak', 'birinin yıkımına çalışmak, kötü duruma düşmesini istemek: ''Yüzden ağır durup arkadan kabinenin kuyusunu kazacaksın!'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2926, '(birinin) künyesini okumak', 'ayıplarını yüzüne vurarak bir kimseye sövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2927, '(birinin) leşini çıkarmak', 'çok dövmek, adamakıllı dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2928, '(birinin) leşini sermek', 'öldürmek: ''Evin içini allak bullak edip leşini gözünün önüne sereyim mi?'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2929, '(birinin) lokmasını dökmek', 'bir ölünün anısına lokma tatlısı yapıp dağıtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2930, '(birinin) lokmasını saymak', 'sofrada yemek yiyen kimsenin ne kadar yediğine dikkat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2931, '(birinin) mahkemede dayısı olmak', 'yüksek bir makamda koruyucusu, kayırıcısı bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2932, '(birinin) mahremiyetine girmek', 'bir kimsenin özel hayatını öğrenecek kadar ona yakın olmak: ''Türkünün bir tarafında kapılar açılıyor ve siz durup dururken hiç tanımadığınız bir insanın mahremiyetine girmiş oluyorsunuz.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2933, '(birinin) maskarası olmak', 'birinin eğlencesi olmak: ''Süslü kafeslere, hazır yemeğe tamah edip insanların maskarası olmanın âlemi var mı?'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2934, '(birinin) maskarasını çıkarmak', 'birini rezil etmek, küçük düşürerek gülünç duruma sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2935, '(birinin) maşası olmak', 'sakıncalı bir işte biri tarafından araç olarak kullanılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2936, '(birinin) muzipliğine uğramak', 'aldatılmak, şakaya hedef olmak: ''Gülmüşler ve kendisine, arkadaşının bir muzipliğine uğradığını söylemişler.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2937, '(birinin) nabzına girmek', 'elindeki imkânları kullanarak birinin hoşnutluğunu kazanmak, birini yola getirmek ve düşüncelerini benimsetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2938, '(birinin) nabzına göre şerbet vermek', 'birinin hoşuna gidecek, gururunu okşayacak yolda davranmak: ''Başına gelmeyen bela kalmadı. Azıcık nabza göre şerbet versen başına bu dertler gelmezdi.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2939, '(birinin) nazını çekmek', 'her istediğini yerine getirmek: ''Ben karım için çalışıyorum. Epeyce kazanıyorum. Onun nazını çekerek bütün çocuklarına katlanıyorum.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2940, '(birinin) notunu vermek', 'bir kimse için kötü bir kanıya varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2941, '(birinin) ocağına düşmek', 'birine koruması için sığınmak veya yardım etmesi için yalvarmak: ''Hanımefendi, gençliğin kadrini biliniz... Ocağınıza düştük.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2942, '(birinin) ocağına incir (darı) dikmek (ekmek)', 'birinin evini barkını dağıtmak: ''İhmal bütün ocaklara incir diker.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2943, '(birinin) omzuna binmek', 'yük olmak, ağırlık vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2944, '(birinin) onuruna ... vermek', 'birine saygı göstermek için yemek, toplantı vb. ağırlamada bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2945, '(birinin) ödünü koparmak (patlatmak)', 'çok korkutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2946, '(birinin) öfke topuklarına çıkmak', 'çok öfkelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2947, '(birinin) öl dediği yerde ölmek, kal dediği yerde kalmak', 'onun sözünden çıkmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2948, '(birinin önünde, yanında) perende atamamak (atılmamak)', '1) herhangi bir konuda birinden aşağı, beceriksiz olmak; 2) oyun çevirememek, aldatamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2949, '(birinin) pabuçlarını çevirmek', 'dolaylı olarak kovmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2950, '(birinin) parasını çekmek', 'para sızdırmak, birinden birtakım gerekçelerle para almak: ''Şunu yaparız, bunu yaparız diye Paşa''yı aldatmaktan ve parasını çekmekten başka bir şey yaptıkları yok.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2951, '(birinin) parasını yemek', 'çalışmadan bedavadan geçinmek, birinin sırtından geçinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2952, '(birinin) parmağı ağzında kalmak', 'şaşakalmak, şaşmak, hayret etmek: ''Haftasına kalmadı, o sert şiş kayboldu, semirmeye başladım. Doktorların parmağı ağzında kaldı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2953, '(birinin) parmağını aramak', 'ilgisini, bağlantısını aramak, kurulan düzeni araştırmak: ''Bu polemik kampanyasında bazı gizli teşekküllerin parmağını aramak gerektiği fikrinde idi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2954, '(birinin) pasaportunu eline vermek', 'kovmak, işten atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2955, '(birinin) pastırmasını çıkarmak', ' tkz. bir kimseyi iyice dövmek, hırpalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2956, '(birinin) pestilini çıkarmak', '1) çok yormak; 2) çok dövmek: ''Bu karıncaya dokunmayan çocuk o kocaman adamın oracıkta pestilini çıkaracaktı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2957, '(birinin) peşinde dolaşmak (gezmek)', 'bir amaçla birisini izlemek: Tarlayı satın almak için peşinde dolaşıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2958, '(birinin) peşinde gitmek', '1) bir kimseyi izlemek; 2) düşünce ve görüşlerini benimsemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2959, '(birinin) pirinci (çok) su kaldırmamak (götürmemek)', 'alıngan, çabuk darılır olmak, şakadan anlamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2960, '(birinin) postuna oturmak', 'bir başkasının makamına geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2961, '(birinin) pöstekisini sermek', 'döverek kımıldamayacak duruma getirmek, pestilini çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2962, '(birinin) sakalına gülmek', 'ciddi gibi görünen sözlerle alay etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2963, '(birinin) sakalının altına girmek', 'yakınlık kurarak ona düşüncesini aşılamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2964, '(birinin) sesi çıkmamak (kesilmek)', 'bir şey söylemeyerek susmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2965, '(birinin) sesi soluğu çıkmamak (kesilmek)', 'sesi çıkmamak: ''Koskoca adam eriyiverdi sanki, sesi soluğu çıkmazdı.'' -Y. Atılgan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2966, '(birinin) sırtından çıkarmak', 'o kimseye ödetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2967, '(birinin) sırtından geçinmek', 'geçimini o kimseden sağlamak: ''Öteki karınca türlerinin yuvalarını yağma edip kendi boyunduruklarına alıyor, onların sırtından geçiniyorlarmış.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2968, '(birinin) sırtından (para) kazanmak', 'para kazanmak için birini kullanmak: ''Benim bu marifetimi bilmeyenlerle bahse girip sırtımdan para kazanan açıkgözler bile oldu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2969, '(birinin) sırtını sıvazlamak', 'birini desteklediğini göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2970, '(birinin) sırtını yere getirmek', '1) güreşte hasmı sırtüstü yere yatırarak yenmek; 2) üstün gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2971, '(birinin) sinirleri altüst olmak', 'sinirleri bozulmak, sinirlenip ne yapacağını şaşırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2972, '(birinin) sinirleri gerilmek', 'sinirlenmeye hazır bir durumda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2973, '(birinin) sinirlerini bozmak', 'kızdırmak, sinirlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2974, '(birinin) sinirlerini germek', 'birini sinirlenmeye hazır bir duruma getirmek: ''Bu olay, on beş gündür sıcak yemek yemeyen askerlerin morallerini bozup sinirlerini iyice gerdi.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2975, '(birinin) sözüne gelmek', 'sonunda birinin söylediğini kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2976, '(birinin) sözünü tutmak', 'öğüdüne uymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2977, '(birinin) suçundan geçmek', 'suçunu bağışlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2978, '(birinin) sulbünden gelmek', 'bir kimsenin öz evladı olmak: ''Yarım asır evvel göç etmiş Çekoslovak Yahudisi bir babanın sulbünden geliyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2979, '(birinin) şapkasını giymek (taşımak)', 'kendi kimliğinin veya düşüncelerinin dışında başka birinin kimliğini geçici olarak taşımak veya onun düşünceleriyle ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2980, '(birinin) tebdili şaşmak', 'ne yapacağını bilememek, telaşa kapılmak: ''Haydar''ın kılıcını görenin tebdili şaşar.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2981, '(birinin) tepesinde bitmek', '1) istenmediği hâlde birinin yanına gelip ayrılmak istememek, türlü isteklerle canını sıkmak, rahatsız etmek; 2) ansızın yanına gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2982, '(birinin) tepesine binmek (çıkmak)', 'genellikle kendinden daha güçsüz kimseleri ezmek, kötü davranmak: ''Böyle kız gibi nazik bir zabiti askerler sayarlar mı? Askerlerimiz tepenize çıkıyordur, nedir?'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2983, '(birinin) tepesine dikilmek', 'başına dikilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2984, '(birinin) terbiyesini vermek', 'sert sözlerle terbiyesizliğini kendisine anlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2985, '(birinin) tırnağı olamamak', 'birinden değerce çok aşağı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2986, '(birinin) tırnaklarını sökmek', 'elindeki güçten yoksun bırakmak, etkisini yok etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2987, '(birinin) türküsünü çağırmak', 'bir kimsenin hoşuna gidecek biçimde söz söylemek veya davranışta bulunmak: ''Azizim, biz kimsenin arabasında kimsenin türküsünü çağırmayız, kendi havamızı mırıldanırız.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2988, '(birinin) tütününü tüttürmek', 'ev ve aile düzeninin sürmesini sağlamak: ''Ben rahmetlinin tütününü tüttürmek için o rahatlığı da teptim.'' -A. Sayar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2989, '(birinin) tüyleri diken diken olmak', 'üşümekten veya korkmaktan vücuttaki kılların dipleri kabarıp kıllar dikilmek: ''Hayret! Bu tür yakıştırmalardan tüylerimin diken diken olduğunu sanırdım.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2990, '(birinin) tüylerini diken diken etmek', 'korkutmak, tiksindirmek: ''Ne vahşi, ne korkunç; insanın tüylerini diken diken eden bir ölü sessizliği var.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2991, '(birinin) üstü başı dökülmek', 'giyecekleri çok eski olmak: ''Böyle üstü başı dökülen bir adama bu kadar yakınlık göstermesi karşısında şaşırıp kaldı.'' -T. Yücel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2992, '(birinin) üstünden geçmek', 'ırzına geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2993, '(birinin) üstünden silindir gibi geçmek', 'perişan etmek, çok yormak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2994, '(birinin) üstüne atmak', 'bir suçu birine yüklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2995, '(birinin) üstüne başına etmek', ' kaba ağır bir biçimde sövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2996, '(birinin) üstüne gül koklamamak', 'sevdiği birinden başkasını sevmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2997, '(birinin) üstüne güneş doğmamak', 'güneş doğmadan önce kalkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2998, '(birinin) üstüne kalmak', 'güçlükler birinin omuzlarına yüklenmek: ''O giderse bütün yük kızın üstüne kalacak.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (2999, '(birinin) üstüne sevmek', 'birini severken bir başkasını daha sevmek: ''Sanki ben sizin üstünüze birini sevmişim, herkese rezil olmuşum, siz de onu duymuşsunuz.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3000, '(birinin) üstüne varmak', '1) bir şey yapmasını baskı yaparak istemek: ''Bir gün o kadar üstüne vardılar ki Resul Efendi zıvanadan çıktı.'' -Y. Kemal. 2) saldırmak; 3) kadın evli bir erkekle evlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3001, '(birinin) üstüne vazife olmamak', 'görevi olmamak, o görev kendini ilgilendirmemek: ''Hiç de değil, üstümüze vazife olmayan şeylere ne karışalım.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3002, '(birinin) üstüne yıkılmak', 'yamanmak: ''Kız belli ki seni gözüne kestirmiş. Üstüne yıkılmak istiyor.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3003, '(birinin) üstüne yıkmak', '1) kendisinin de sorumlu olduğu bir işin ağırlığını başkalarına yüklemek: ''Tek tük torunlar doğmaya, yetişmeye başlamış, kendi havalarında olan genç babalar, cahil anneler bu çocukların bütün yükünü onunla karısının üstüne yıkmışlar.'' -R. N. Güntekin. 2) kendi suçunu başkasına yüklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3004, '(birinin) üstüne yürümek', 'korkutmak, yıldırmak amacıyla saldıracakmış gibi yapmak: ''Bir gün üstüme yürüdü, sen benim kâğıtlarımı karıştırıyorsun, beni polise gammazlıyorsun diye.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3005, '(birinin) üzerine atmak', 'üstüne atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3006, '(birinin veya bir şeyin) hasretini çekmek', '1) çok özlemek: ''Ben dört sene onun hasretini çektim.'' -A. Gündüz. 2) mec. gereksinim duyduğu şeyi elde edememenin üzüntüsü içinde bulunmak: Dünya, barışın hasretini çekiyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3007, '(birinin veya bir şeyin) kıymetini bilmek', 'önemini, değerini bilmek: ''Güneş yalnız dirileri ısıtır / Güneşin kıymetini bil'' -O. Rifat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3008, '(birinin veya bir şeyin) kurbanı olmak', 'uğruna ızdırap veya büyük üzüntü, sıkıntı çekmek, zarara girmek, ölmek: ''Üçümüzün müşterek kurbanı olduğumuz acı bir devir, bahçenin tatlı havasını ağırlaştırmıştı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3009, '(birinin veya bir şeyin) posasını çıkarmak', '1) bir kişi veya şeyi sonuna kadar sömürmek: ''Onlar öyledir, adamın posasını çıkarırlar, dedi.'' -R. H. Karay. 2) birini çok dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3010, '(birinin veya bir şeyin) tırnağına değmemek', 'değerce ondan çok aşağı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3011, '(birinin veya bir şeyin) üstüne yüklenmek', '1) saldırmak; 2) mec. ısrar etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3012, '(birinin veya bir şeyin) yüzü suyu hürmetine', '''birinin veya bir şeyin hatırına veya varlığına değer verildiği için'' anlamında kullanılan bir söz: ''Ben şu iki kolumun yüzü suyu hürmetine yaşıyorum, yaşıyorsam.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3013, '(birinin \tveya bir şeyin) yüzü suyu hürmetine\t\t', '''birinin veya bir şeyin hatırına veya varlığına değer verildiği için'' anlamında kullanılan bir söz: ''Ben şu iki kolumun yüzü suyu hürmetine yaşıyorum, yaşıyorsam.'' -Z. Selimoğlu.\t', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3014, '(birinin veya bir şeyin) yüzünü unutmak', 'uzun süre görmemek, varlığına hasret kalmak: ''İnsanlar Tanrı rahmeti olan yağmurun yüzünü çoktan unutmuşlardı.'' -N. Araz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3015, '(birinin) yâdını uyandırmak', 'onu hatırlatmak: ''Ona ait her hatıra muazzez ve yâdını uyandıracak her vesile kıymetlidir.'' -İ. A. Gövsa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3016, '(birinin) yakasına asılmak (yapışmak)', 'hesap sormak veya bir şey istemek için tutup bırakmamak: ''Sonra eşyaya bir zarar gelecek olursa Gülsüm''ün yakasına yapışıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3017, '(birinin) yakasına sarılmak', 'istediği şeyi almak veya dövüşmek için birini bırakmamak, zorlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3018, '(birinin) yakasını bırakmamak', 'bezdirecek kadar üstüne düşmek, rahat vermemek, ısrar etmek: ''Kendimi unutturup kaybettirmeye çalıştığım burada da Başkan, yakamı bırakmadı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3019, '(birinin) yalancısı olmak', 'doğruluğu bilinmeyen bir bilgiyi başkasından duyup iletmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3020, '(birinin) yasını tutmak', 'kötü bir olay sonunda acı ve üzüntü duymak: ''Ben gittiğimde karısı hasta olduğu için kliniğe götürülmüş, hepsi onun yasını tutuyordu.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3021, '(birinin) yatağına girmek', 'kadın biriyle evlilik dışı ilişkide bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3022, '(birinin) yoluna bakmak', 'beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3023, '(birinin) yuvasını yapmak', ' tkz. birine gereken ceza veya cevabı vermek, hakkından gelmek: ''Hiç canını sıkma, dedi, ben şimdi onun yuvasını yaparım!'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3024, '(birinin) yüreğine işlemek', 'yüreğe işlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3025, '(birinin) yüreğine oturmak', 'çok üzmek: ''İşte, yine başındaki bu dert de gelip yüreğine oturmuştu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3026, '(birinin) yüreğini tüketmek', 'bir şeyi anlayıncaya kadar anlatanı çok yormak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3027, '(birinin) yüzü kâğıt gibi olmak', 'kanı çekilip benzi solmak: ''Babuş da uyanmış ve yatağın içine oturmuş. Korkudan onun da yüzü kâğıt gibi.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3028, '(birinin) yüzüne bağırmak', 'birine öfke ile saygısızca sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3029, '(birinin) yüzüne karşı', 'bir kimsenin kendi önünde ve ondan çekinmeden: Yüzüne karşı da söylerim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3030, '(birinin) yüzünü ağartmak', 'beğenilir iş yapmak, iş ve davranışlarıyla yakınlarının övünmesine sebep olmak: ''Bu zaferle Mustafa Kemal, ordumuzun yüzünü ağartmış, milletimizin yüzünü güldürmüştür.'' -E. C. Güney.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3031, '(birinin) yüzünü gözünü açmak', 'bir çocuğa veya gence o zamana kadar bilmediği birtakım cinsel bilgiler vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3032, '(birinin) yüzünü güldürmek', 'birini mutlu etmek, birine iyilik etmek: ''Bu zaferle Mustafa Kemal, ordumuzun yüzünü ağartmış, milletimizin yüzünü güldürmüştür.'' -E. C. Güney.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3033, '(birinin) yüzünü kızartmak', 'bir kimsenin utanmasına sebep olmak, birini utanacak duruma düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3034, '(birinin) zayiçesine bakmak', 'bir inanışa göre, yıldızlara bakarak birinin gelecekteki talihini anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3035, '(birinin) zebunu olmak', 'birini çok sevmek, ona aşırı düşkün olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3036, '(birinin) zihnine girmek', 'düşüncesini değiştirmesine yol açmak: ''Nezihe ne yapıp yapmış, genç zabitin zihnine girmiş, bir hafta sonra, onunla nişanlanmış.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3037, '(birinin) zihnini çelmek', '1) bir kimseyi yanıltmak, yanlış yola sürüklemek; 2) baştan çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3038, '(birinin) zimmetine geçirmek', 'bir hesabı birinin borcuna eklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3039, 'birisinden biri', 'içlerinden biri, birkaç kişiden herhangi biri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3040, '(biriyle) alıp verememek', 'anlaşamamak, çekememek, geçinememek: Onunla alıp veremediğiniz nedir, ne alıp veremiyorsunuz?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3041, '(biriyle) arası hoş (iyi) olmamak', 'o kimseyle aralarında gerginlik, geçimsizlik olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3042, '(biriyle) arası olmamak', 'geçinememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3043, '(biriyle) aşık atmak', '1) yarış etmek, yarışmak: ''Yonca, bu iki erkek çocuktan ayrı bir yaratık olduğunu, onlarla aşık atamayacağını bilir.'' -O. Rifat. 2) boy ölçüşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3044, '(biriyle) başa çıkmak', 'güçlükler çıkaran biriyle olan işini, kendi istediği yolda sonuçlandırabilmek: ''Onlarla başa çıkmak kolay değildi, çünkü her an bir çamur atabilirlerdi kızdıklarında.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3045, '(biriyle, bir şeyle) baş başa kalmak', 'biriyle veya bir şeyle yalnız kalmak: ''Düşünceleriyle, iç sesiyle baş başa kalmayı tercih ederdi.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3046, '(biriyle, bir şeyle) baş edebilmek', 'bir kimseyi yola getirmeye veya bir şeyi yapmaya gücü yetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3047, '(biriyle, bir şeyle) ilişiği olmamak', 'bağlantısı olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3048, '(biriyle) boy ölçüşmek', 'yarışmak: ''Hani yüksek dağlarla boy ölçüşen dalgalar? / Kartalı gökten alıp yola düşen dalgalar'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3049, '(biriyle) düşüp kalkmak', '1) erkek kadınla veya kadın erkekle yasa ve töre dışı yakın ilişki kurmak: ''Beni tanımadan önce de beni tanıdıktan sonra da başka erkeklerle düşüp kalktı.'' -N. Cumalı. 2) biriyle çok yakın arkadaşlık etmek: ''Onu bu hâle sokan düşüp kalktığı arkadaşlarıdır.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3050, '(biriyle) geçmişi olmak', '1) aralarında eskiye dayanan dostluk, arkadaşlık olmak; 2) aralarında kırgınlığa yol açacak bir durum geçmiş bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3051, '(biriyle) gır gır geçmek', 'alay etmek: ''Kendi sinema serüveniyle gır gır geçen Sadri Bey, herhâlde yaşantısını sinemaya yansıtmak isterdi.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3052, '(biriyle) hesabı kesmek', 'alışverişi veya ilgiyi kesmek: ''Bu hırsızın hesabını kesip kanunun pençesine teslim etmeliyiz.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3053, '(biriyle) ilişki kurmak', 'bağlantı sağlamak, ilgi sağlamak: ''Hasta ile ofis dışı ilişki kurduğunu duyarsam şikâyet dilekçemi işleme koyacağım.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3054, '(biriyle) ilişkiye girmek', '1) bağlantı kurmak; 2) yakınlaşmak; 3) cinsel ilişkide bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3055, '(biriyle) kafa bulmak', 'alay etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3056, '(biriyle) kozunu paylaşmak (pay etmek)', 'aralarındaki anlaşmazlığı zora başvurarak çözümlemek, sona erdirmek: ''Mümeyyiz Efendi varsın bekçi ile kozunu pay etsin.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3057, '(biriyle) merhabası olmak', 'esenleşecek kadar tanışıklığı, yakınlığı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3058, '(biriyle) merhabayı kesmek', 'biriyle ilgisini kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3059, '(biriyle) selam yollamak (salmak)', 'birine esenleme haberi göndermek: ''Züğürtlükten telefonumuz kesildi mi ona bir selam yollar, açtırırdık.'' -Y. Z. Ortaç. ''Şimdi bizden yüz çevirdi ahbaplar / Bir çift selam salanım yok, gardiyan'' -Âşık Ali İzzet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3060, '(biriyle) selamı sabahı kesmek', 'her türlü ilişkisine son vermek: ''Onunla tamamıyla selamı sabahı kestim. Ne olursa olsun deyip adını bile artık ağzıma almaz oldum.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3061, '(biriyle) temas etmek', '1) görüşüp konuşmak; 2) cinsel ilişkide bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3062, '(biriyle) temasta bulunmak', 'temas etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3063, 'birlik olmak', 'bir işi yapmak için anlaşmak: ''Bu ayıbı işleyenlerle birlik olmayı bir türlü kibrime yediremiyorum.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3064, 'birlikten kuvvet doğar', 'toplu veya beraber davranmak daha büyük güç sağlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3065, 'bis yapmak', 'seyirci, beğenilen bir konserin sonunda tempolu biçimde alkışlayarak sanatçıyı veya sanatçıları bir eser seslendirmesi için yeniden sahneye çağırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3066, 'bismillah demek', 'bir işe uğurlu olması dileği ile başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3067, 'bit kadar', 'çok küçük.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3068, 'bitap düşmek', 'çok yorulmak, yorgun düşmek: ''Sabaha doğru bitap düştü, onun kucağında uyuyakaldı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3069, 'biti kanlanmak', 'sıkıntı içinde yaşayan bir kişi para ve varlık yönünden güçlenmek: ''Fakat geçim durumunu az çok düzene sokmuş ve biti kanlanmışlar için rütbe ve şeref, paranın da üstündedir.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3070, 'bitli baklanın da kör alıcısı olur', 'işe yaramaz da olsa her şeyin isteklisi bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3071, 'bitmek tükenmek bilmemek', 'bir türlü sonu gelmemek, eksilmemek: ''Kendisine ikram edilen kahveyi içerken her nefes alış verişinde göğsündeki taşın o bitmek tükenmek bilmez takırtıları duyuluyordu.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3072, 'bitmez (bitip) tükenmez', 'bitmeyen, sonu gelmeyen, uçsuz bucaksız: ''Kırk yıl bana bitmez tükenmez çok uzun bir süre gibi görünürdü.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3073, 'biz attık kemik diye, el kaptı ilik diye', 'bizim işe yaramaz diye vazgeçtiğimizi başkaları değerli buldu.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3074, 'biz bize benzeriz', 'aramızda fark yok, özelliklerimiz veya tutum ve davranışlarımız aynıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3075, 'biz kırk kişiyiz, birbirimizi biliriz', 'birbirimizi çok yakından tanırız, onun öyle bir üstün durumu olmadığını biliriz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3076, 'bize de mi lolo?', '''işin içinde bir iş olduğunu bilmez miyiz sanıyorsunuz?'' anlamında kullanılan bir söz: ''Şehir uşağıyız. Bize de mi lolo? Bu işin içinde bir karı dalaveresi olduğunu anladım.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3077, 'bizim gelin bizden kaçar, tutar ellere başını açar', 'bize yabancı duran yakınımız, dostumuz, akrabamız başkalarına rahatça, içtenlikle yardım eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3078, 'boca etmek', '1) geminin başını rüzgâr almayan tarafa çevirmek: ''Ne var ki Ateşoğlu dümendeydi. Yükseldi, yine boca etti.'' -Halikarnas Balıkçısı. 2) mec. birden çevirip boşaltmak, dökmek: ''Şarap koyuyorum diye sirke şişesini boca etmişsin.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3079, 'bocuk domuzuna dönmek', 'çok semiz ve besili olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3080, 'bocur bocur kabarmak', 'duygulanıp içi kabarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3081, 'bodur kalmak', '1) boyu uzamamak: ''Boyu bosu kötü toprağa düşmüş İdris ağacı gibi bodur kalmış.'' -Y. K. Karaosmanoğlu. 2) mec. gelişmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3082, 'bodur tavuk her gün (dem) piliç', 'kısa boylular olduklarından daha genç görünürler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3083, 'boğa gibi', 'çok güçlü görünen, vücudu iyi gelişmiş (delikanlı).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3084, 'boğaya çekmek', 'ineği boğa ile cinsel ilişkide bulundurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3085, 'boğaya gelmek', 'çiftleşme zamanı gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3086, 'boğaz açmak', 'ağaçların dibini kazarak toprağı kabartmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3087, 'boğaz boğaza gelmek', 'zorlu kavga etmek: ''Birbiriyle boğaz boğaza gelen okul çocuklarını, Samet''in varlığı bugünlerde tek bir vücut gibi bir araya toplayabilirdi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3088, 'boğaz dokuz boğumdur', 'bir söz iyice düşünmeden söylenmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3089, 'boğaz durmaz', 'yeme içme gereksiniminin başka ihtiyaçlar gibi geri bırakılamayacağını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3090, 'boğaz içinde kavga var', 'açlığını aşırı bir biçimde gidermeye çalışanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3091, 'boğaz ola', ' hlk. ''afiyet olsun, yarasın, bereketli olsun'' anlamında kullanılan bir iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3092, 'boğaz olmak', '1) boğazı ağrımak: ''Çocukluğumdan beri sık sık boğaz olurdum.'' -B. Felek. 2) imrenmekten boğazı şişmek: ''Fazla imrendiriyorsun insanı, boğaz olacağız.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3093, 'boğazı açılmak', 'iştahı artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3094, 'boğazı düğümlenmek', 'üzüntüden boğazı tıkanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3095, 'boğazı inmek', 'bademcikleri şişmek, iltihaplanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3096, 'boğazı işlemek', 'durmadan bir şeyler yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3097, 'boğazı kurumak', 'çok susamak: ''Kediyi karşısında gördükçe yüreği titriyor, boğazı kuruyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3098, 'boğazına bir yumruk tıkanmak (gelip oturmak)', 'konuşamaz olmak, sesi çıkmamak: ''Babasının adı anılınca Ferit''in boğazına bir yumruk tıkandı.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3099, 'boğazına dizilmek', 'üzüntü, kaygı vb. sebeplerle isteksiz yemek, iştahı kesilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3100, 'boğazına durmak', 'yediği şeyi yutamamak: ''Nankörler! Yediğiniz ekmek boğazınızda dursun.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3101, 'boğazına indirmek', 'fazla ve gelişigüzel yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3102, 'boğazına kadar', 'pek çok, gereğinden fazla, aşırı ölçüde: ''Baba daima boğazına kadar borç içinde yaşar, müsrif, batakçı bir memurdu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3103, 'boğazına sarılmak', 'üstüne yürümek: ''Tam boğazına sarılacaktım, yere düştü, bir daha kalkamadı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3104, 'boğazında düğümlenmek', 'söylemek istediğini heyecan veya üzüntü yüzünden diyememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3105, 'boğazından artırmak', 'yiyeceğinden kısıp parasını artırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3106, 'boğazından geçmemek', 'sevdiği bir kimsenin yokluğu veya yoksulluğu dolayısıyla bir yiyeceği yalnız başına yemekten üzüntü duymak: ''Her gün evde pişen türlü yemeklerin hiçbiri sensiz boğazımdan geçmiyor.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3107, 'boğazından kesmek', 'yiyip içmede çok tutumlu davranmak: ''Ekonomi, kendinin ve çoluk çocuğunun boğazından kesmek demekti.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3108, 'boğazını doyurmak', 'karın doyurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3109, 'boğazını sevmek', 'yiyip içmeye düşkün olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3110, 'boğazını sıkmak', 'bunaltmak, sıkıntı vermek: ''Müfit, boğazını sıkan büyük öfke ile titreyerek başını çevirdi.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3111, 'boğazını yırtmak', 'olanca gücüyle bağırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3112, 'boğuntuya getirmek', ' argo birini bunaltıp şaşırtmak yolu ile kendisinden, bir iş veya mal karşılığı olarak çok miktarda para çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3113, 'bohçanın dört ucunu bir araya getirememek', '1) iki yakayı bir araya getirememek; 2) dengeyi sağlayamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3114, 'bohçasını koltuğuna almak', 'kendi isteğiyle ayrılmak: ''Günün birinde bohçasını koltuğuna alıp kıyı mahallelerden birinde oturan ablası Fitnat Hanım''ın evine gitti.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3115, 'bohçasını koltuğuna vermek', 'kovmak, işine son vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3116, 'bohçasını toplamak', 'eşyasını toplamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3117, 'bok atmak', ' kaba birine leke sürmek, kara çalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3118, 'bok boku kenefte bulur', 'kötüler birbirlerini nerede bulacaklarını bilirler ve orada buluşurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3119, 'bok etmek (bokunu çıkarmak)', ' kaba bir işi, bir şeyi bozmak, berbat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3120, 'bok karıştırmak', ' kaba bir işi bozacak biçimde davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3121, 'bok soyu (bokun soyu)', ' kaba kızılan veya tiksinilen bir şeye karşı sövgü olarak söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3122, 'bok üstün bok', ' kaba çok kötü, çok berbat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3123, 'bok yedi başı', ' kaba burnunu her işe sokan, her işe karışan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3124, 'bok yemek', ' kaba yakışıksız bir iş yapmak: ''Merak etme kızım, bok yiyor o herif, dedi.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3125, 'bok yemenin Arapçası', ' kaba ''yakışıksızlığın büyüğü'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3126, 'bok yoluna gitmek', ' kaba yararsız, gereksiz bir şey uğruna yok olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3127, 'boka nispetle tezek amberdir', 'çok kötü bir şeyin yanında, ondan daha az kötü olanı güzel görünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3128, 'boku çıkmak', ' kaba bir iş veya durum tatsızlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3129, 'bokuyla kavga etmek', ' kaba çok sinirli ve geçimsiz olmak, her şeye öfkelenir olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3130, 'bol bol yiyen bel bel bakar', 'kazandığını ölçüsüzce harcayan ve ilerisi için bir şey artırmayan kişi kazançsız kaldığında acıklı bir duruma düşer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3131, 'bol doğramak', 'parasını saçıp savurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3132, 'bomba gibi', '1) iyi, sağlam, göz alıcı, gösterişli; 2) iyi hazırlanmış, çok çalışmış (öğrenci).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3133, 'bomba gibi patlamak', '1) öfkelenerek birdenbire ve yüksek sesle bağırıp çağırmak; 2) bir olay birdenbire ortaya çıkarak herkesi şaşırtmak: ''Babamın Üsküp''ü terk etmek ve Selanik''e gidip yerleşmek hakkında verdiği karar ailemiz arasında bir bomba gibi patladı.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3134, 'boncuk gibi', 'çok küçük.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3135, 'bora gibi', 'çok sert, öfkeli, şiddetli.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3136, 'borca almak', 'veresiye almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3137, 'borca batmak', 'çok borçlu olmak: ''Şevket ölesiye çalışmak pahasına acaba bu korkunç masrafı karşılayacak kadar para kazanıyor mu idi yoksa çocukcağız borca mı batıyordu?'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3138, 'borca girmek', 'borçlanmak, borç para almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3139, 'borcun iyisi vermek, derdin iyisi ölmek', 'borçlu ve dertli bir biçimde yaşanılmaz; borçtan kurtulmanın yolu onu vermek, onulmaz dertten kurtulmanın çıkar yolu ise ölmektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3140, 'borcun yoksa kefil ol, işin yoksa şahit ol', 'tanıklık boş oturan kimselerin, kefillik ise parası çok olan kimselerin işidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3141, 'borcunu bilmek', '1) bir şey yapmayı yerine getirilmesi gereken bir iş olarak değerlendirmek; 2) borcunu zamanında öder olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3142, 'borcunu kapatmak', 'borcunu ödeyip bitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3143, 'borç almak', 'daha sonra ödemek üzere birinden para veya bir şey almak: ''On beş lira borç aldıktan sonra eve döndüm.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3144, 'borç altına girmek', 'borç para almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3145, 'borç bini aşmak', 'borç, altından kalkılamayacak duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3146, 'borç etmek (yapmak)', 'borçlanmak: ''Altlarında şilte, dolaplarında eşya kalmadı ama kimseye de borç yapmadılar.'' -P. Safa. ''Babasından bir şey koparamadığı zaman borç ediyor, sonra ona ödetiyordu.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3147, 'borç gırtlağına çıkmak', 'borca batmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3148, 'borç harç etmek', 'sürekli borç alıp vermek: ''Hazır param var biraz, biliyorsun. Yetmezse borç harç ederim.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3149, 'borç iyi güne kalmaz', 'borcu ilk fırsatta ödemek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3150, 'borç ödemekle (vermekle), yol yürümekle tükenir', 'birden ödenemeyen bir borç azar azar verilerek ödenebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3151, 'borç ödenir, kira ödenmez', 'kiracı olmaktansa, borca girip ev sahibi olmak daha iyidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3152, 'borç uzayınca kalır, dert uzayınca alır', 'borç zamanında ödenmezse borçluluk duygusu gevşer, hastalık uzun süreli olursa ölüme yol açar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3153, 'borç vermekle, düşman vurmakla', 'borç vermekle, düşman vurmakla yok edilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3154, 'borç yemek', 'borçla geçinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3155, 'borç yiğidin kamçısıdır', 'borç, kişiyi daha çok çalışmaya zorlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3156, 'borç yiyen kesesinden yer', 'borçla alışveriş yapan, aldıklarının parasını hemen ödemese de günün birinde mutlaka ödeyecektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3157, 'borçlu çıkmak', 'görülen hesapta vereceği kalmak: ''Para muamelelerinden borçlu çıkmıştı.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3158, 'borçlu ölmez, benzi sararır', 'borç kişiyi öldürmez ancak hasta edecek kadar üzer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3159, 'borçlunun dili kısa gerek', 'borcu olan kimse, alacaklısına karşı ileri geri konuşmamalı, aşağıdan almalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3160, 'borçlunun duacısı alacaklısıdır', 'borçlunun ölmemesi ve para kazanması için en çok dua eden alacaklısıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3161, 'borçlunun yalımı alçak olur', 'borçlu, özellikle alacaklısının yanında göğsünü gere gere gezemez, suçlu gibi durur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3162, 'borçsuz çoban yoksul beyden yeğdir', 'yoksulluk ve sıkıntı içinde olup da bey adı taşımaktansa borçsuz ve sıkıntısız bir çoban olmak daha iyidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3163, 'borçtan korkan kapısını büyük açmaz (küçük açar)', 'borçlanmak istemeyen fazla açılmaz, giderlerini kısar, kendi durumuna uygun bir yaşama yolu tutar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3164, 'boru değil', 'hlk. ''azımsanacak, küçümsenecek, önem verilmeyecek şey değil'' anlamında kullanılan bir söz: ''Gençlik bu, boru değil.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3165, 'boru mu bu?', '''azımsanacak, küçümsenecek, önem verilmeyecek şey değil'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3166, 'bostan gök iken pazar(lık) yapılmaz', 'ayrıntıları belli olmayan bir iş için anlaşma yapılmamalı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3167, 'bostana dadanan eşeğin kuyruğu, kulağı olmaz', 'çalıp çırpmayı alışkanlık edinen kimse yakalanıp ceza göre göre insanlıktan çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3168, 'boş atıp dolu tutmak (vurmak)', 'umutsuz olarak girişilen bir iş, iyi sonuç vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3169, 'boş başağın başı dik olur', 'bilgisiz olan üstün görünmek için kasılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3170, 'boş başak dik durur', 'bilgisiz olan üstün görünmek için kasılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3171, 'boş bırakmak', 'bir yerde kimse oturmamak, boş kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3172, 'boş bırakmamak', '1) para, yiyecek vb. şeylerle yardım etmek; 2) işsiz bırakmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3173, 'boş bulunmak', '1) dikkatsiz ve dalgın bulunmak: ''Nasıl boş bulunup o gazeteci kızın resmini çekmesine imkân verdi?'' -A. İlhan. 2) söylenmesi sakıncalı olan bir şeyi söyleyivermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3174, 'boş çıkmak', 'umduğu gerçekleşmemek, sonuç vermemek: ''Ben birkaç gündür arıyorum, birkaç yerlere başvurdum, boş çıktı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3175, 'boş çuval ayakta (dik) durmaz', '1) karnı doymayan kimse çalışamaz; 2) bilgisiz ve yeteneksiz bir kişi, kendisine verilen görevlerde tutunamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3176, 'boş dönmek', 'hiçbir şey elde edemeden geri gelmek: ''Ankara''ya giden hiçbir heyetin geri boş döndüğünü görmedik.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3177, 'boş durmak', 'işsiz kalmak, çalışmamak: ''Mustafa Kemal''in hiç boş durduğu yoktu.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3178, 'boş durmamak', '1) her zaman bir işle uğraşmak; 2) birinin yaptığına karşılık olarak bir harekette bulunmak: ''Bizden sonra cenaze çıkmış bir eve benzeyen Bekirağa bölüğündeki arkadaşlar boş durmamışlardı.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3179, 'boş düşmek', ' esk. İslam hukukuna göre, kadın kocasından ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3180, 'boş eşek yorga gider', 'üzerinde bir görev bulunmayan kaygısız kişi, rahat rahat, istediği gibi yaşar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3181, 'boş gezenin boş kalfası', 'işsiz güçsüz dolaşan (kimse): ''Oraya daha çok boş gezenin boş kalfası emekliler ya da ağırbaşlı orta yaşlılar giderdi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3182, 'boş gezmek (gezinmek)', 'işsiz güçsüz dolaşmak: ''On gün boş mu gezdin?'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3183, 'boş gezmekten bedava çalışmak yeğdir', 'çalışmak insanı tembellikten kurtarır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3184, 'boş gözlerle bakmak', 'anlamsız bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3185, 'boş ite menzil olmaz', 'aylak kimsenin yeri yurdu belli değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3186, 'boş kalmak', '1) kimse oturmamak: ''Bir kayıkta boş kalan son yere atlayıp Galata''ya geçerken kafası hem umut hem de endişeyle doluydu.'' -İ. O. Anar. 2) işsiz kalmak: ''Her senede üç dört ay, bahusus kışın boş kalırız.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3187, 'boş kile dipsiz ambar', 'dipsiz kile boş ambar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3188, 'boş konuşmamak', 'gerçekleri söylemek, bilgisine dayanarak anlatmak: ''Amiralin sözlerine inanmak lazım, boş konuşmaz.'' -F. F. Tülbentçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3189, 'boş koymak', 'yoksun bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3190, 'boş ol (olsun)', ' esk. erkeğin karısını boşamak için söylediği söz: ''Boş ol deyince karılarının pılı pırtı toplayıp gitmesini hayalliyorlar.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3191, 'boş oturmak', 'hiçbir işi olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3192, 'boş torba ile at tutulmaz', 'çıkar veya karşılık gösterilmeden bir kimse bir yere bağlanmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3193, 'boş vermek', ' argo aldırmamak: ''Aldırmayacaksın, boş vereceksin, güleceksin.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3194, 'boş yerine vurmak', 'böğürlerine vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3195, 'boşa almak', '1) askıya almak; 2) motorlu araçlarda vites kolunu vitesten kurtarmak, rölantiye almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3196, 'boşa çıkarmak', 'olumlu bir sonuç alınmasını engellemek: ''Çocuklar her atılımını boşa çıkarıyor, onunla alay ediyorlar.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3197, 'boşa çıkmak', 'umut, düşünce vb. şeyler sonuç vermemek, gerçekleşmemek: ''Ümidim boşa çıkınca dizlerimin bağı çözülür.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3198, 'boşa gitmek', 'harcanan emek, para hiçbir işe yaramamak, olumlu bir sonuca ulaşamamak: ''Bir fikrin gerçekleştirilmesine yaramayan zaferler boşa gider.'' -Atatürk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3199, 'boşa kodum dolmadı, doluya kodum almadı', 'içinden çıkılmayan güç bir durum karşısında söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3200, 'boşa koysan dolmaz, doluya koysan almaz', 'içinden çıkılamayan güç bir durum karşısında kalındığında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3201, 'boşa vermek', 'boş geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3202, 'boşboğazı ateşe atmışlar, odunum yaş (az) demiş', 'çenesi düşükler umulmadık anlarda densizce konuşabilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3203, 'boşta gezmek', 'işsiz olmak: ''Huriye Hanım, kızının bu boşta gezer oğlana vardığı zaman ...'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3204, 'boşta kalmak', 'işsiz kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3205, 'boy almak (sürmek)', 'boyu uzamak, boylanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3206, 'boy atmak', 'boyu uzamak, boylanmak, gelişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3207, 'boy bos yerinde', 'uzun ve biçimli: Boyu bosu yerinde, yakışıklı adam.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3208, 'boy göstermek', '1) görünmek: ''Burada biraz boy gösterdikten sonra bir yolunu bulup kapağı Paris''e attı.'' -H. E. Adıvar. 2) gösteriş yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3209, 'boy vermek', '1) su insan boyunu aşacak kadar derin olmak; 2) suya dalarak boyu ile suyun derinliğini ölçmek; 3) büyümek: ''Eğer fideleriniz nitelikli değilse boy verip yapraklandıkça, çiçek açtıkça, meyve verdikçe fideliğe kızmaya hakkınız yoktur.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3210, 'boya tutmak', 'bir şey iyi boyanır olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3211, 'boya vurmak (çekmek, sürmek)', 'boyamak: ''Kimi kirpiklerine boya sürüyordu.'' -R. H. Karay. ''Kıpkızıl bir boya çektin odanın her yerine.'' -M. A. Ersoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3212, 'boyası atmak', 'boyası solmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3213, 'boynu armut sapına dönmek', 'çok zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3214, 'boynu kıldan ince olmak', 'haksız olduğu anlaşıldığında verilecek her türlü cezaya razı olmak: ''Eğer efendim, bir kelime yalanım varsa hükûmete karşı boynum kıldan incedir. Vurunuz.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3215, 'boynuna almak', 'bir şeyi borç veya ödev olarak üzerine almak: ''Çobanın hekim parasını, ilaç parasını boyunlarına aldılar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3216, 'boynuna geçirmek', 'bir şeyi kendine mal etmek, zimmetine geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3217, 'boynunda kalmak', 'bir sözü iletmediği veya birine ödenecek parayı ödemediği için üzerinde borç kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3218, 'boynunu bükmek', '1) acındırıcı, çaresiz bir durumda kalmak: ''Biraz düşündükten sonra ağır ağır başını eğip yere baktı ve boynunu büktü.'' -Y. Z. Ortaç. 2) bir durumu, bir işi ister istemez kabul etmek: ''Şoför yine boynunu büktü, ''O yürüyemezse, ben de yürüyemem ne yapayım?'' der gibi yüzüme baktı.'' -N. Hikmet. 3) bitki canlılığını yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3219, 'boynunu kırmak', ' hlk. çekip gitmek: ''Daha bir ay tutunamazlar, boyunlarını kırarlar deniliyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3220, 'boynunu uzatmak', 'her şeye, her cezaya razı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3221, 'boynunu vurmak', 'başını keserek öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3222, 'boynuz çekmek', 'boynuz kullanarak kan çekmek, hacamat etmek: ''Hastalık göğse inip ateş başlayınca yapılacak şey hastaya boynuz çekmek olurdu.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3223, 'boynuz dikmek', 'kadın başka erkekle ilişki kurarak kocasını aldatmak: ''Ah ayol, kadın bu yaştan sonra boynuz dikiyor diye ondan iğrenirler.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3224, 'boynuz eğmek', 'istemeyerek uymak, karşı tarafın gücünü kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3225, 'boynuz isterken kulaktan olmak', 'daha iyisini, mükemmelini ararken mevcut olanı yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3226, 'boynuz kulağı geçmek', 'bir konuda daha sonra yetişenler yetenek bakımından eskileri geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3227, 'boynuz kulaktan sonra çıkar, ama kulağı geçer', 'bir konu üzerinde sonradan yetiştikleri hâlde kendilerinden önce yetişmiş olanları geçenler vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3228, 'boynuz takmak (takınmak, taktırmak)', 'karısı başka bir erkekle ilişki kurarak aldatmak (aldatılmak): ''Onlar da sana seksen zamparayla boynuz taktırdılar ya.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3229, 'boyu beraber', 'kendi boyu kadar: Boyu beraber çocuğu var.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3230, 'boyu boyuna, huyu huyuna', '''karı koca veya arkadaşlar arasında her bakımdan uygunluk olması gerekir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3231, 'boyu devrilsin (devrilesi)', '''ölsün'' anlamında kullanılan bir ilenme sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3232, 'boyuma göre (boyumca) boy buldum, huyuma göre (huyumca) huy bulmadım', 'bir kimse, beden yapısı, zenginlik vb. konularda kendisine uyanı bulabilir ama huyu kendisine uyan bir kimseyi kolay kolay bulamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3233, 'boyun bir karış uzadı', ' alay ''gereği olmayan o işi yapmakla sanki yükseldin'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3234, 'boyun bükmek', 'boynunu bükmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3235, 'boyun eğmek', 'isteyerek veya istemeyerek uymak, katlanmak: ''Para bulabilmek için ya bir tarafa boyun eğmeli ya öbür tarafla birleşmeli idik.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3236, 'boyun kesmek', 'selam vermek için başını eğmek: ''Eli göğsünde, boyun keserek dervişçe bir selamla alçak bir sedirin ucuna ilişti.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3237, 'boyun kırmak', 'saygı duyulan bir kimse karşısında, ayaktayken başı öne bükmek: ''Hürrem Hakkı, Ferhunde''nin önünde boyun kırdı.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3238, 'boyun olmak', ' hlk. kefil olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3239, 'boyun vermek', 'buyruk altına girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3240, 'boyuna bosuna bakmadan', '''fizik yapısının gereğince gelişmemiş olmasını göz önünde bulundurmadan'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3241, 'boyunca çocuğu olmak', 'yetişkin çocuğu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3242, 'boyunduruğa atmak (almak)', 'güreşte hasmın başını koltuk altına alıp boynuna kol dolamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3243, 'boyunduruğa vurmak', 'baskı altına almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3244, 'boyunduruk altına girmek', 'başkasının baskısı altında kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3245, 'boyunun ölçüsünü almak', '1) kendi yetersizliğini, beceriksizliğini anlamak: ''Gelsin de görsün bakalım... Boyunun ölçüsünü alsın. Anlasın yük gemisiyle yola çıkmanın ne demek olduğunu...'' -Z. Selimoğlu. 2) beklediği yakınlığı görememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3246, 'boza gibi', 'koyu ve bulanık (sıvılar).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3247, 'boza olmak', ' hlk. utanmak, bozum olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3248, 'bozacının şahidi şıracı', 'aynı düşüncede ve aynı yapıda olan insanlar birbirlerini korurlar, kollarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3249, 'bozdur bozdur harca', ' alay çok az olan şeyler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3250, 'bozguna uğramak (vermek)', 'yenilip perişan olmak, dağılmak, hezimete uğramak: ''Durdu ve bir anda bütün mukavemeti bozguna uğradı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3251, 'bozuk çalmak', ' argo canı sıkılmış, yüzü asılmış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3252, 'bozuk plak gibi', 'sürekli tekrarlanarak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3253, 'bozum etmek', ' argo utandırmak, mahcup etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3254, 'bozum olmak', ' argo utanmak, utanacak duruma düşmek, mahcup olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3255, 'bozuntuya uğramak', 'şaşkınlığa kapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3256, 'bozuntuya vermemek', 'bir kimsenin hoşa gitmeyen bir durumunda fark etmemiş gibi davranmak: ''Bozuntuya vermedim, yürüdüm, yanına gittim.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3257, 'böcek çıkarmak', 'ipek böceği yetiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3258, 'böcek gibi', 'ufak tefek ve esmer (çocuk).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3259, 'böyle başa böyle tıraş', 'kişi nasıl birisiyse ona uygun biçimde davranılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3260, 'böyle gelmiş böyle gider', 'her zaman böyle olmuş, gene de böyle olacak.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3261, 'bronz gibi', 'tunca benzeyen, tunç renginde olan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3262, 'bu abdestle daha çok namaz kılınır', 'bir tutum veya davranışın etkisi sürekli olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3263, '(bu işe) Rufailer karışır', '''bu iş öyle karışık ki bunu kimse çözemez'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3264, 'bu kadar kusur kadı kızında da bulunur', 'üzerinde durulmaya değmeyecek kadar küçük bir kusurdur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3265, 'bu ne perhiz bu ne lahana turşusu!', 'sözleri ve davranışları birbirini tutmuyor, çelişiyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3266, 'bu sıcağa kar mı dayanır?', 'aşırı harcamalarla eldeki imkânlar çok çabuk tükenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3267, 'bucak bucak aramak', 'her yerde aramak: ''Sizi bucak bucak arayan ölüm, nihayet izinizi bulup karşınıza dikildi mi?'' -A. N. Asya.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3268, 'bucak bucak kaçmak', 'bir olay, bir durum veya bir kimseyle karşılaşmamaya çalışmak: ''Sen gerçek hayattan bucak bucak kaçıyorsun.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3269, 'bugün bana ise yarın sana', 'bugün birinin başına gelen kötü bir durum, daha sonra başka birinin de başına gelebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3270, 'bugün git, yarın gel', 'bir iş yapılmak istenmediğinde baştan savmak için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3271, 'bugünden tezi yok', 'hemen şimdi, derhâl: ''Bugünden tezi yok, şimdi buradan çıkıp oraya gidiyorum.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3272, 'bugünkü (akşamın) işini yarına (sabaha) bırakma (koyma)', 'bugün yapılması gereken bir işin ertesi güne bırakılması iyi değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3273, 'bugünkü günde', 'şimdi, içinde bulunduğumuz zamanda, şimdiki şartlarda: ''Bugünkü günde İngilizcesiz olmuyor çok iş.'' -N. Uygur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3274, 'bugünkü tavuk yarınki kazdan iyidir', 'sağlanmış bir kazanç beklenen, umulan daha büyük bir kazanca feda edilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3275, 'buğday başak verince orak pahaya çıkar', 'gereksinim duyulan şey değer kazanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3276, 'buğday ekmeğin yoksa buğday dilin de mi yok?', 'görüştüğün kimseyi ağırlayacak, onun istediklerini yapacak durumda olmayabilirsin ama tatlı dille onun gönlünü hoş edebilirsin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3277, 'buğday ile koyun, geri yanı (kalanı) oyun', 'çiftçi için koyun ve buğdaydan değerli bir şey yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3278, 'buğdayım var deme ambara girmeyince, oğlum var deme yoksulluğa ermeyince (düşmeyince)', 'bir şeyin senin olduğundan kuşkun kalmaması için gereken bütün koşullar gerçekleşmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3279, 'buhar olmak', ' hlk. yok olmak, kaybolmak: ''Sanki buhar olup göğe çekilmişlerdi.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3280, 'buhran geçirmek', 'bunalım geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3281, 'buhrana tutulmak', 'buhran geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3282, 'bukağı vurmak', 'bukağı takmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3283, 'bukalemun gibi renkten renge girmek', 'sürekli düşünce değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3284, 'bula bula bunu (onu, bir şeyi, birini) bulmak', '1) var olanların en değersizini seçmek; 2) kötü bir şeye rastlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3285, 'bulantı vermek', '1) midesini bulandırmak; 2) mec. bıkkınlık vermek: ''Gözlerime, kulaklarıma, beş duyuma birden tiksinti, bulantı veren bu manzaraların ortasında niye duruyordum?'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3286, 'bulaşık suyu gibi', 'kötü hazırlanmış, tadı tuzu olmayan (sulu yiyecek ve içecek).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3287, 'buldukça bunar (bulmuş da bunuyor)', 'bulduğuyla yetinmiyor da daha çoğunu istiyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3288, 'buldum bilemedim, bildim bulamadım', 'kişi elinde fırsat varken bundan yararlanmayı bilmez, yararlanma yollarını öğrendiği zaman da eline fırsat geçmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3289, 'buldumcuk olmak', 'bir şeye sonradan ulaşınca şımarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3290, 'bulunmaz Bursa (Hint) kumaşı', ' alay çok az bulunduğu ve çok değerli olduğu sanılan şey: ''Nuri''ye gelince bulunmaz bir Hint kumaşı sayılmazdı o da.'' -O. Rifat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3291, 'bulup buluşturmak', 'çaba göstererek bir şeyler sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3292, 'bulut gibi', 'çok sarhoş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3293, 'bulut olmak', 'çok sarhoş olmak: ''Meyhaneli köylerin her birinde üçer beşer çekmiş, bulut olmuştur.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3294, 'buluttan nem kapmak', 'en küçük bir şeyden alınmak, çok alıngan olmak: ''Biraz gariptir ki buluttan nem kapan o zamanki sansür bu cinayetler ve tesadüflerden ahkâm çıkararak hafiyelik etmezdi.'' -A. Ş. Hisar. ''İhtiyatlı ol, bunlar tilkidir, rüzgârdan nem kapar; elden kaçırmayalım.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3295, 'buna değdi (idi) buna değmedi (idi) demek', 'birçok şeyin, iyilerini seçip önceden beğenmeyip bıraktıklarını da sonradan almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3296, 'bunda bir iş var', 'gizli veya bilinmeyen bir yönü olan olay veya durum için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3297, 'bundan iyisi can sağlığı', '''bundan daha iyisi olamaz'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3298, 'burnu (bile) kanamamak', '1) zarar görmemek, yarasız beresiz olmak; 2) tehlikeli bir durumdan yara bere almadan kurtulmak: ''Burunları bile kanamadan ganimete kavuşacaklardı.'' -F. F. Tülbentçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3299, 'burnu büyümek', 'kibirlenmek, büyüklenmek: ''Yalnız onun mu burnu büyüdü? Burnu büyüyen büyüyene!'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3300, 'burnu çenesine değmek', 'çok yaşlanmak: ''Bu kez gelen, burnu çenesine değmiş bir acuzeydi.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3301, 'burnu Kafdağı''na çıkmak (varmak)', 'kibirlenmek, şımarmak, burnu büyümek: ''Nikâh ettirir ettirmez kadının burnu Kafdağı''na çıkmış.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3302, 'burnu Kafdağı''nda (olmak)', 'çok kibirli (olmak): ''Çeltikçiler, o burunları Kafdağı''nda çeltikçiler çarşıya düşmüşler, önlerine gelene dert yanıyorlar.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3303, 'burnu kırılmak', 'büyüklenemez duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3304, 'burnu sızlamak', 'duygulanmak: ''Orada zaman zaman sebepsiz yere burnu sızlardı insanın.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3305, 'burnu sürtülmek', 'sıkıntı çektikten sonra daha önce beğenmediği bir durumu kabul etmek, gururundan vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3306, 'burnu yere düşse almaz', 'kendini beğenmiş, kibirli.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3307, 'burnuna karıncalar dolmak', 'ölmek: ''Bundan sonra müteahhit eline çay verenin burnuna karıncalar dolsun!'' -A. Dino.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3308, 'burnuna koymak', 'aldırış etmek, göz önünde tutmak, değer vermek, kale almak: ''Oğlan mahalle arkadaşlarıyla samimi idi. Kızsa ne anasını ne babasını ne de kardeşlerini burnuna kor, bu mahalle ve bu mahalleliden nefret ederdi.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3309, 'burnunda tütmek', 'çok özlemek: ''Benim Nazlılarım, Gülizarlarım hatta Ethemlerim burnumda tütmeye başladı.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3310, 'burnundan düşen bin parça olmak', 'çok asık suratlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3311, 'burnundan (fitil fitil) gelmek', 'elde ettiği güzel şey, sonradan gelen üzüntüler üzerine kendisine zehir olmak: ''Sabahki o tatlı eğlentiler şimdi fitil fitil burnumdan gelmeye başladığı için bugün buralara geldiğime bin kere pişman oluyordum.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3312, 'burnundan gelmek', 'iyi niyetle girişilen bir işten beklenen sonuç alınamadığından dolayı sıkıntı içinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3313, 'burnundan getirmek', 'yaptığına pişman etmek: ''Hele onu bir elime geçireyim, görürsün, burnundan getireceğim.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3314, 'burnundan kıl aldırmamak', 'kendisine söz söyletmemek, çok huysuz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3315, 'burnundan solumak', 'çok öfkelenmiş olmak: ''İnliyor, göz süzüyor, burnundan soluyarak konuşuyordu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3316, 'burnunu çekmek', '1) sümüğünü çekmek: ''Madam, küçük bir çocuk gibi burnunu çekerek eliyle içerideki odayı gösteriyor.'' -A. Ümit. 2) mec. umduğunu bulamamak, amacına ulaşamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3317, 'burnunu kırmak', 'birini güç durumda bırakarak büyüklenmesini veya direnişini yok etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3318, 'burnunu sıksan canı çıkacak', 'çok zayıf ve güçsüz kimseler için kullanılan bir söz: ''Nerdee iş nerede. Bizimkinin ağzını bıçak açmıyor. Burnunu tutsan canı çıkacak.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3319, 'burnunu sürtmek', 'sıkıntı çektikten sonra daha önce beğenmediği bir durumu kabul etmek, gururundan vazgeçmek: ''Hadisat şimdi burnunu da sürtmüş olduğundan ilk karısına karşı iyi davranıyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3320, 'burnunun dibine sokulmak', 'çok yaklaşmak, iyice yaklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3321, 'burnunun dikine (doğrusuna) gitmek', 'öğüt dinlemeyerek kendi bildiği gibi davranmak: ''Soruların yanıtlarını buldum mu ne gezer ama nedense aptal kafam burnunun dikine gitmeyi sürdürdü.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3322, 'burnunun direği kırılmak (düşmek)', 'çok pis bir koku duyarak tedirgin olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3323, 'burnunun direği sızlamak', 'maddi veya manevi çok acı duymak, çok üzülmek: ''Burnunun direği sızlaya sızlaya evini özlemektedir.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3324, 'burnunun direğini kırmak', 'çok pis bir koku yayarak tedirgin etmek: ''Tezek kokusu burnumun direğini kırmış, ciğerime işlemişti.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3325, 'burnunun ucundan ötesini (ilerisini) görmemek', 'dar düşünceli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3326, 'burnunun ucunu görmemek', '1) çok sarhoş olmak; 2) dalgın, dikkatsiz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3327, 'burnunun yeli harman savurmak', '1) büyüklenmek, kibirlenmek; 2) çok öfkelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3328, 'burnunun yeli kırılmak', 'öfkesi yok olmak: ''Vazgeçin, dedi Nuh, kızlara yazık... Niye yazık olsun? Burnunun yeli kırılır, cart curt edemez millete!'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3329, 'burun buruna gelmek', '1) beklenmedik bir anda karşılaşmak, birbirlerine çok yaklaşmak: ''Nabi Efendi, merdivenleri yorgun yorgun çıkarken sofada karısıyla burun buruna geldi.'' -M. Yesari. 2) karşısında hissetmek: ''O kadar gururlu bir tavrı vardı ki onu ilk kez gören birisi, bu kişinin az önce ölümle burun buruna geldiğini düşünemezdi.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3330, 'burun bükmek', 'beğenmemek, önem vermemek: ''... şöyle demiştim, böyle yapmıştım, diyene burun büker.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3331, 'burun kıvırmak', 'önem vermemek, küçümsemek, beğenmemek: ''Açıkçası durmadan yakınan o kadınlara burun kıvırdım.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3332, 'burun şişirmek', 'kibirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3333, 'burun yapmak', 'üstünlük taslamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3334, 'burusu tutmak (tutulmak)', 'sancılanmak: ''Ben evin içinde zaten burusu tutulanlardan bahsedildiğine pek çok defalar müsadif olmuştum.'' -H. Z. Uşaklıgil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3335, 'buyruğu altına girmek', 'bir kimse başka bir kimsenin isteklerini ister istemez yerine getirmek zorunda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3336, 'buyur etmek', '1) buyurun diyerek konuğu saygı ile içeri almak: ''Soldaki bahçeli kahveye buyur ettim.'' -S. F. Abasıyanık. 2) sofraya çağırmak: ''Aliş''e de buyur ettiler, ekmek, peynir ve üzümden ibaret yemeklerini yemeye koyuldular.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3337, 'buyurun cenaze namazına!', ' şaka beklenmedik kötü bir durum karşısında üzüntü anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3338, 'buz bağlamak', 'sıvıların yüzeyi donmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3339, 'buz gibi', '1) çok soğuk; 2) çok soğuk bir etki uyandıran (şey veya kimse); 3) kötü nitelikler için kesinlik: Adam buz gibi hırsız. 4) kesinlikle: ''Elbette can sıkıntısına düşer, buz gibi düşman kesilir erkeğe.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3340, 'buz kesilmek', 'şaşılacak, üzülecek bir durum karşısında donakalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3341, 'buz kesmek', 'çok üşümek: ''Beton döşeme bir türlü ısınmak bilmiyordu. Ve akşamlardan sabahlara kadar ayakları, baldırları buz kesiyordu.'' -R. Enis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3342, 'buz tutmak', 'sıvının üstünde buz oluşmak, buzla kaplanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3343, 'buz üstüne yazı yazmak', '1) süresi, etkisi çok az olacak bir iş yapmak; 2) bir kimseye etki yapmayan sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3344, 'buzdolabı gibi', 'çok soğuk bir etki uyandıran (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3345, 'buzdolabına kaldırmak', 'bir konuda anlaşmaya varılamadığı için onu bir süre gündem dışında bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3346, 'buzlar çözülmek', '1) buzlar erimeye ve kırılmaya başlamak; 2) mec. aradaki soğukluk, dargınlık, gerginlik ortadan kalkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3347, 'büğersen göl olur, açarsan çöl olur', 'birikim, ancak tutmayı bilirsen olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3348, 'bükemediğin eli öp başına ko', 'düşmanını yenemiyorsan ona hoş görünmeye çalışarak kötülüğünden kendini koru.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3349, 'bülbül gibi konuşmak (okumak)', '1) kolaylıkla konuşmak, okumak: ''Kadın bülbül gibi Fransızca konuşuyor.'' -H. E. Adıvar. 2) itiraf etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3350, 'bülbül gibi konuşturmak (söyletmek)', 'itiraf ettirmek: ''Buluştukları zaman da onu bülbül gibi konuşturdu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3351, 'bülbül gibi söylemek', 'hiçbir şey saklamadan bildiklerini söylemek, itiraf etmek: ''Mahkemeye havale edeceğim, orada bülbül gibi söylersin.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3352, 'bülbül gibi şakımak', 'güzel sesle, neşeyle konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3353, 'bülbül kesilmek', 'bir etki veya baskı altında çokça konuşmak: ''İnsan bir garip nesnedir. Bir korku atlattıktan sonra bülbül kesilir.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3354, 'bülbülü altın kafese koymuşlar, ''ah vatanım'' demiş', 'kişi, başka yerlerde ne kadar rahat ve mutlu olursa olsun yine de kendi yurdunu özler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3355, 'bülbülün çektiği dili belası', 'ilerisi düşünülmeden söylenen söz insanın başına dert açabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3356, 'büyü bozmak', 'yapılmış bir büyüyü etkisiz duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3357, 'büyü bozulmak', '1) yapılmış bir büyü etkisiz duruma getirilmek: ''Öldük, ölümden bir şeyler umarak / Bir büyük boşlukta bozuldu büyü'' -C. S. Tarancı. 2) mec. önceden hissedilen duygular hissedilmez olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3358, 'büyük abdesti gelmek', 'dışkı yapma ihtiyacı duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3359, 'büyük balık küçük balığı yutar', 'güçlüler, güçsüzleri ezer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3360, 'büyük başın derdi büyük olur', 'büyük işlerin başında bulunanların karşılaşacağı güçlükler de çoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3361, 'büyük gelmek', 'kıyafet, bol ve geniş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3362, 'büyük görmek (bilmek, tutmak)', 'kendini veya başkasını olduğundan üstün saymak, yüceltmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3363, 'büyük laf etmek', 'büyük söz söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3364, 'büyük lokma ye büyük söz söyleme', 'başaramayacağın, sonuçlandıramayacağın bir konuda kesin sözler söyleme.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3365, 'büyük oynamak', '1) çok para koyarak kumar oynamak; 2) mec. büyük risk ve beklentilerle bir işe girişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3366, 'büyük (söz) söylemek', 'yapacağı bir şey hakkında kesin konuşarak övünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3367, 'büyük sözüme tövbe!', 'bir konuda çok kesin konuşulduğunda tersi bir durumun başa gelmemesi dileğini belirten bir söz: ''Büyük sözüme tövbe, hatır ve hayalime bile getiremem.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3368, 'büyük yemin etmek', 'bir şeyi yapmamak konusunda en kutsal şeyler üzerine ant içmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3369, 'büyükle büyük, küçükle küçük olmak', 'her yaş ve durumdaki kişilere karşı dostça, arkadaşça davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3370, 'büyüklerin ellerinden, küçüklerin gözlerinden öpmek', 'saygı ve sevgi göstermek: ''Buralara kadar zahmet ettiniz, büyüklerin ellerinden, küçüklerin gözlerinden öperim.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3371, 'büyüklük göstermek', 'gönül ululuğu göstermek: ''İnsan yaptığı işler ve bıraktığı eserlerle büyüklüğünü gösterir.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3372, 'büyüklük satmak', 'gururlanıp üstünlük taslamak: ''Bir eski muallime olan annem, istese de büyüklük satamazdı, elinden gelmezdi.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3373, 'büyüklük taslamak', 'kendini üstün görmeye çalışmak, böbürlenmek: ''Düne kadar kibir onların, büyüklük taslamak onların.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3374, 'büyümüş de küçülmüş', 'konuşması ve davranışları yaşına uymayan, büyüklerinki gibi olan: ''Küçücük gözlü, çokbilmiş suratlı, büyümüş de küçülmüş, kavruk bir oğlandı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3375, 'büyüsüne kapılmak (tutulmak)', 'bir şeyin, bir kimsenin çekiciliğinden kurtulamamak: ''Durup durup başıma gelenlerin büyüsüne kapılıyordum.'' -O. Pamuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3376, 'büzülüp oturmak (kalmak)', 'bir kenarda çekingen bir tavırla oturmak: ''Ankara''ya kadar bir köşeye büzülüp kaldım.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3377, 'caddeyi tutmak', '1) herhangi bir sebeple bir yoldan geçişi engellemek, kapamak; 2) argo korkulu bir durumda başını alıp gitmek, uzaklaşmak: ''Emine bağırarak caddeyi tutarken peşi sıra ötekiler de kalktılar.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3378, 'cadı gibi', '1) saçı başı dağınık, tırnakları uzun ve pis (kadın); 2) çok becerikli.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3379, 'cadı kazanı gibi kaynamak', 'dedikodu, kargaşa çok olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3380, 'cafcafından geçilmemek', 'her zaman ve her yerde gösteriş yapmak: Cafcafından geçilmiyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3381, 'cahil kalmak', 'bilgi edinememek, bilgisi olmamak: ''Bu konularda yeni kuşağın yanında her zaman cahil kalmaya mahkûmuz.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3382, 'cahile söz (laf) anlatmak deveye hendek atlatmaktan güçtür (zordur)', 'ne kadar uğraşılırsa uğraşılsın deve hendekten atlatılamaz, cahile söz anlatmak bundan da zor bir şeydir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3383, 'cahilin dostluğundan arifin düşmanlığı yeğdir', 'akılsız kimse iyi niyetli olsa dahi yaptığı işin ne gibi kötü sonuçlar doğuracağını hesap edemediğinden dostuna bilmeyerek fenalık edebilir, akıllı düşmanın yapacağı kötülükse akıl yoluyla sezilir ve gereken tedbir alınabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3384, 'caka satmak', 'gösteriş yapmak, çalım satmak: ''Askerliğin palavra ile olmadığını anladı ama hâlâ caka satıyor.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3385, 'caka yapmak', 'gösterişli davranmak, fiyakalı durumda olmak: ''Baktım ki caka yapıyor, vesikayı el âleme göstere göstere eviriyor, çeviriyor.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3386, 'cakasından geçilmemek', 'her zaman ve her yerde gösteriş yapmak: ''Dünyaları yakarım diyen, o cakasından geçilmeyen genç adamdan geriye bir enkaz kalmıştı.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3387, 'cakasını bozmak', 'çalımına engel olmak, böbürlenmesini boşa çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3388, 'cam gibi', '1) arkası görünen, saydam, şeffaf; 2) donuk, cansız (göz).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3389, 'cama çıkmak', 'pencereden görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3390, 'camadan vurmak', 'fazla rüzgâra karşı yelkeni kasmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3391, 'camadanı fora etmek', 'bağları koyuverip kısılmış yelkeni açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3392, 'cambaz ipte balık dipte gerek', 'kişi, sadece uzman olduğu alanda çalışmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3393, 'camı çerçeveyi indirmek', 'etrafı kırıp dökmek, her şeyi parçalayıp dağıtmak: ''Neden soğuk değil bu su diye hır çıkarıp camı çerçeveyi indiriyor.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3394, 'cami ne kadar büyük olsa imam gene bildiğini okur', 'bir yetkili kimse, çevresindekilerin düşüncesi ne olursa olsun kendi istediğini yapmaya çalışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3395, 'cami olmak', 'toplamak, bir araya getirmek, bir arada bulundurmak: ''Umumi kütüphane, ilmin, edebiyatın her şubesine ait kitapları cami olmak lazım gelir.'' -Z. Gökalp.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3396, 'cami yıkılmış ama mihrabı yerinde', '''yaşlandığı hâlde güzelliği bozulmamış (kadın)'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3397, 'caminin (mescidin) mumunu yiyen kedinin gözü kör olur', 'kendisini yetiştiren kimsenin malına hıyanet eden, el uzatan kimse cezasını bulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3398, 'can alacak nokta (yer)', 'bir şeyin en önemli yeri: ''Bağırasım geldi ama bey kardeşim, bağıramadım bir türlü, kâfirin kızları oyunlarının tam can alacak noktalarına gelmişlerdi.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3399, 'can alıp can vermek', 'ölüm sıkıntısı ve acısı içinde bunalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3400, 'can atmak', 'şiddetle arzu etmek, çok istemek: ''O zaman herkes böyle bir tecride can atardı.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3401, 'can baş üstüne', 'istenilen şeyin büyük bir memnunlukla yapılacağını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3402, 'can başına sıçramak', 'çok korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3403, 'can beslemek', '1) kaygısızca yiyip içip rahatına bakmak; 2) başkasının yiyeceğini, içeceğini sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3404, 'can boğazdan gelir (geçer)', 'insan yiyeceğine önem vererek güçlenebilir veya yemeden yaşamak mümkün değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3405, 'can borcunu ödemek', 'ölmek: ''Sırası gelince kendi paylarına düşen can borcunu da ödediler.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3406, 'can bostanda bitmez', 'insan, canının değerini bilmeli, onu yıpratmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3407, 'can bulmak', 'dirilmek, canlanmak: ''Eylül sonunda ruhunu teslim eden heves / Can bulmak üzredir yeni baştan bahar ile'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3408, 'can cana, baş başa', '1) bir tehlike anında herkesin kendi canının, kendi başının kaygısına düştüğünü anlatan bir söz: ''Gecenin karanlığında bütün bir mahalle donanma fişekleri gibi ateş almış. Sokaklarda herkes can cana, baş başa... Tulumbacı naraları, çığlıklar, borular.'' -R. N. Güntekin. 2) birbirini seven iki kişi bir arada yalnız olarak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3409, 'can candan şirindir (tatlıdır)', 'bir kişi için kendi canı, başkasının canından daha tatlıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3410, 'can canın yoldaşıdır', 'insan tek başına yaşayamaz, konuşup görüşmek için arkadaş arar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3411, 'can cümleden aziz', 'insanın kendisi herkesten önce gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3412, 'can çekişmek', '1) ölmek üzere bulunmak: ''Bir uzun can çekişme bunun her anı bence / İçimi sızlatan şey ölüm değil işkence'' -F. N. Çamlıbel. 2) sona ermek, tükenmek, bitmek: ''Yazdığım satırlara bakarsanız manevi varlığımın can çekiştiğini görürsünüz.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3413, 'can çekişmektense ölmek yeğdir', 'bir işte çeşitli sıkıntı ve üzüntülerle karşılaşıp olağanüstü gayret harcamaktansa o işten vazgeçmek daha iyidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3414, 'can çıkmayınca (çıkmadan) huy çıkmaz', 'insanı alışkanlıklarından, huylarından vazgeçirmek mümkün değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3415, 'can damarından yakalamak', '1) konuya en önemli yerinden yaklaşmak; 2) birinin en zayıf noktasından yararlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3416, 'can derdinde olmak', 'zor bir durumdan kurtulmaya çalışmak: ''Herkes can derdinde, ben de Şahin''in ardına düşmüşüm.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3417, 'can kalmamak', 'bitkin bir duruma gelmek, gücü tükenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3418, 'can kaygısına düşmek', 'her şeyden vazgeçip sadece kendi hayatını koruma veya kurtarma çabasında olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3419, 'can olmak', 'sevimli, hoş görünmek: ''Yok canım, öyle demişim demek, derken ne kadar da can olurdu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3420, 'can sıkmak', '1) bıkkınlık vermek: ''Adam sizin çok can sıktığınızı, çok anlayışsız ve inatçı olduğunuzu ifade etmek üzere ters bir bakışla bakıyor yüzünüze.'' -İ. Özel. 2) huzur bozmak: ''Hani hakikati bilmek iyi şeydir ama bu kadarı da onu değiştiremediğiniz için, can sıkıyor.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3421, 'can simidi olmak', 'birinin kötü durumda kalmasını engellemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3422, 'can vermek', '1) ölmek: ''Göçük altında can vermiş kaç insan görmüştü bugüne kadar?'' -A. Kulin. 2) ruha güç vermek: Bu sözleriniz bana can verdi. 3) canlanmasına yol açmak: ''Bahar toprağa gene can verdi.'' -F. R. Atay. 4) bir şeyi çok istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3423, 'can yakmak', '1) zulmetmek, eziyet etmek: ''Yahu! Sen en az çirkin, en az can yakıcı bir usulle para çalan bir zavallıdan başka nesin?'' -N. F. Kısakürek. 2) bir kimseyi büyük zarar ve ziyana sokmak; 3) üzmek, acı vermek: ''Ayrılık! Her vakit can yakar, ağlatır.'' -A. Rasim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3424, 'cana (canına) can katmak', 'yaşama gücünü artırmak: ''Pınarların dibindeki çimenlik, sofada kuşların çığlıkları geziye çıkanların canına can katar.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3425, 'cana gelecek kaza (zarar) mala gelsin', 'zarardan kurtulma olanağı yoksa, bunun cana değil, mala gelmesi yeğlenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3426, 'cana gelecek mala gelsin', 'zarardan kurtulma olanağı yoksa, bunun cana değil, mala gelmesi yeğlenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3427, 'cana kıymak', 'öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3428, 'cana minnet saymak (bilmek)', 'bir lütuf olarak kabul etmek: ''Yeni yıla değil, yeni bir sabaha sağ çıkabilmeyi cana minnet sayıyorlardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3429, 'canavar gibi', '1) iri yarı, saldırgan; 2) çok fazla: Canavar gibi çalışıp sınavlara hazırlandı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3430, 'canavar kesilmek', 'hırçınlaşmak, canavar gibi olmak: Fakat o, bu gece sahiden canavar kesilmiş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3431, 'canciğer kuzu sarması', 'içli dışlı, candan, pek içten: ''Bir gün evvel canciğer kuzu sarması, ferdası günü sen kimsin efendi ben seni tanımıyorum.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3432, 'canciğer olmak', 'birbiriyle çok yakın arkadaş olmak: ''Birbirinizin yüzüne karşı canciğer olursunuz fakat sekiz on adım ayrıldığınız gibi başka birine mükemmel çekiştirirsiniz.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3433, 'candan ahbap, kandan şarap olmaz', 'kandan şarap yapılamayacağı gibi hiç kimse de tam anlamıyla içten, fedakâr dost olamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3434, 'candan geçmek', 'ölmek: ''Geçti Galip Dede candan yahu.'' -Süruri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3435, 'canevinden vurmak', 'en etkileyici yönünden saldırmak: ''Zeki bakışı, hınzır nükteleriyle beni canevinden vurmaktan geri kalmadı.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3436, 'canfes gibi', 'ince, taze ve sinirsiz (asma ve dut yaprağı).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3437, 'canı acımak', '1) çarpma, vurma vb. sonucu acı duymak: ''Şaşkınlığından bir kestane yığınına çarptı, canı acıyordu.'' -S. F. Abasıyanık. 2) üzülmek, rahatsız olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3438, 'canı ağzına (boğazına) gelmek', '1) büyük bir tehlike karşısında ölecekmiş gibi bir korkuya kapılmak: ''Bunlardan biri elimden kayarak ayağım üstüne şiddetle düşüverdi, az kalsın canım ağzıma gelecekti.'' -Y. K. Karaosmanoğlu. 2) aşırı duygulanmak, çok heyecanlanmak: ''Bitip tükenmek bilmeyen bir tablo gibi serilip giden lale tarlası renkten renge geçtikçe herkesin canı ağzına geliyor.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3439, 'canı bayılmak', 'iç geçmek, takatsizlik göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3440, 'canı burnuna (burnundan) gelmek', 'bir şey yaparken çok zorluk çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3441, 'canı burnundan çıkmak', 'çok kızgın olmak, öfkelenmek: ''Öte yandan Osman da canı burnundan çıkarak ''karışma, hırsını alsın, anne!'' der.'' -M. Seyda.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3442, 'canı cana ölçmek', 'başkasına yapılacak şeyi kendine yapılacak gibi düşünmek: ''Canı cana ölç. Allah esirgesin bize birisi böyle bir şey yapsa Allah razı olsun der miyiz?'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3443, 'canı canına (içine) sığmamak', 'sabırsızlık göstermek, tahammül etmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3444, 'canı cehenneme', 'sevilmeyen bir kimse için duyulan öfke ve nefreti bildiren bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3445, 'canı çekilmek', '1) vücudun herhangi bir organının canlılığı azalır gibi olmak; 2) içi ezilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3446, 'canı çekmek', 'bir şeyi istemek, istek duymak, arzulamak: ''Yufka, dedim de canım bir ıspanaklı börek çekti ki.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3447, 'canı çıkmak', '1) çok yorulmak veya çok zorluk çekmek: Çalışmaktan canım çıktı. 2) ölmek: ''Herifin burnunu sıksan canı çıkacak.'' -S. F. Abasıyanık. 3) çok yıpranmak: Her gün giyilmekten elbisenin canı çıktı. 4) zarar etmek: ''Kazandığımız paranın aslan payını gidip ona toka etmekle canımız çıkıyor.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3448, 'canı gelip gitmek', '1) ayılıp bayılmak; 2) ümit ve ümitsizlik arasında kalıp heyecanlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3449, 'canı gelmek', 'yeniden canlanmak, canı yerine gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3450, 'canı gibi sevmek', 'çok güçlü bir sevgiyle bağlanmak: ''Amcasının sırtını, canı gibi sevdiği sekiz yaşındaki Serdar''ı nasıl okşarsa öyle sıvazlıyor.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3451, 'canı gitmek', 'özen gösterilen, çok sevilen bir şeye zarar gelecek diye kaygılanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3452, 'canı ile oynamak', 'tehlikeli işlerle uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3453, 'canı ile uğraşmak', '1) ağır hasta olmak, ölüm döşeğinde can çekişmek: ''Kadıncağız canı ile uğraşıyor, sen de eğleniyorsun.'' -R. N. Güntekin. 2) büyük sıkıntıya düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3454, 'canı istemek', 'heves duymak: ''Şehre ineceğiz, canı dans etmek istiyormuş.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3455, 'canı isterse', '''kabul etmezse etmesin'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3456, 'canı kaymak isteyen mandayı yanında taşır', 'güzel yaşamak isteyen kişi, bu yaşayışın yükünü çekmeyi göze almalı ve gerekli kaynakları elinin altında bulundurmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3457, 'canı sıkılmak', '1) içi sıkılmak, yapacak bir işi olmamaktan tedirginlik duymak. 2) öfkelenmek: ''Belki de kitapları bedavaya getireceğimi düşündüğü için canı sıkılıyor.'' -A. Ümit. 3) üzülmek: ''Atölyede bellediğim dünya kadar söze gazetelerde, kitaplarda rastlamayınca enikonu canım sıkılıyordu.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3458, 'canı yanan eşek attan yüğrük olur', 'zarara veya kötülüğe uğrayan kimse acısını çıkarmak için aşırı çaba harcar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3459, 'canı yanmak', '1) çok acı duymak; 2) acı bir deneme geçirmek; 3) bir işte zarar görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3460, 'canı yerine gelmek', '1) yorgunluğu geçmek; 2) sağlığını, gücünü kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3461, 'canım dese canın çıksın diyor sanmak', 'birinin en gönül okşayıcı sözleri bile kendisine dokunmak, batmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3462, 'canımı sokakta bulmadım', 'tehlikeye veya herhangi bir sıkıntıya katlanmaya niyetim yok.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3463, 'canımın içi', 'çok sevilen bir kimse için kullanılan bir söz: ''Gel benim canımın içi, gel yanıma.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3464, 'canına acımamak', 'kendini düşünmeden, kendine bakmadan yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3465, 'canına değmek', '1) çok hoşlanmak: Bu limonata canıma değdi. 2) ruhu şad olmak: Babanın canına değsin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3466, 'canına ezan okumak', 'bir kimsenin hakkından gelmek, öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3467, 'canına geçmek (işlemek veya kâr etmek)', 'çok etkilemek: ''Yalnızlık canıma kâr etti, bilmem neylesem.'' -Ruhi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3468, 'canına kastetmek', '1) intihara kalkışmak; 2) birini öldürmeye hazırlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3469, 'canına kıymak', '1) acımadan öldürmek; 2) kendini öldürmek: ''Başyardımcının canına kıymasından birkaç gün sonra, gece çalışma odama geldi.'' -C. Külebi. 3) gücünden fazla iş görerek aşırı derecede kendini yormak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3470, 'canına minnet (olmak)', 'beklenilmeyen iyi bir durumla karşılaşıldığında duyulan memnunluğu anlatmak için söylenen bir söz: ''Mektep işi canına minnet ya!'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3471, 'canına okumak', ' tkz. berbat ve perişan etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3472, 'canına susamak', 'ölmek istemek: ''Canına susamış kim varsa bir adım yaklaşsın.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3473, 'canına tak demek (etmek)', 'dayanamaz duruma gelmek, sabrı kalmamak: ''Kimsesizlik bu sefer tak dedi artık canıma.'' -M. A. Ersoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3474, 'canına tükürdüğümün (üfürdüğümün)', ' argo kızgınlık ve öfke belirten bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3475, 'canına yandığım (yandığımın)', ' argo sevgi, hayranlık, öfke vb. duygular anlatan bir söz: ''Hep böyle canına yandığımın, hep geç kalırım, hep treni kaçırırım.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3476, 'canına yetmek', 'katlanamayacak duruma gelmek, bezmek, bıkmak: ''Vatan hasreti artık canına yetmiş.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3477, 'canından bezmek (bıkmak veya usanmak)', 'ölümü göze alacak kadar sıkıntı içinde olmak: ''Artık doğrusu bendeniz canımdan bıktım.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3478, 'canından geçmek', 'ölmek için hazır olmak: ''Millet her ne zaman isterse uğrunda canımdan geçmeye hazırım.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3479, 'canını almak', '1) öldürmek: ''Ya hemen canını almaz da sana işkence edersem?'' -N. F. Kısakürek. 2) canını verdirecek kadar memnun etmek; 3) sıkıntıya sokmak: ''İşi o makamdan o makama sora sora dolaşır, parasını almaz ama iş sahibinin canını alır.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3480, 'canını bağışlamak', 'öldürülmesi gerekirken vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3481, 'canını (bir yere) dar atmak', 'bir tehlikeden güçlükle kurtularak bir yere sığınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3482, 'canını burnundan getirmek', 'çok yormak, fazla çalıştırmak: ''Bize soluk aldırmaz, canımızı burnumuzdan getirir.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3483, 'canını cehenneme göndermek (yollamak)', ' argo öldürmek: ''Elim değmişken, elmas kılıcımla canını cehenneme yollayayım.'' -T. Oflazoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3484, 'canını çıkarmak', 'hırpalamak, çok yormak, yıpratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3485, 'canını dişine almak (takmak)', '1) her tehlikeyi göze alarak işe girişmek: ''Öyleyse niye uğraşıyoruz, canımızı dişimize takmışız, sen, ben, Ali, Yel Musa?'' -Y. Kemal. 2) bütün gücünü harcayarak yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3486, 'canını sıkmak', 'sözlerle veya davranışlarla kişinin neşesini kaçırmak, huzurunu bozmak: ''Serbestçe birisi olursa sizin canınızı sıkar.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3487, 'canını sokakta bulmak', 'sağlığı korumak gerektiğini anlatan bir söz: Canımı sokakta mı buldum? Elbette biraz dinleneceğim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3488, 'canını vermek', '1) kendini feda etmek: ''En küçük sevgi sözüne canımızı verecek hâle geliriz.'' -S. F. Abasıyanık. 2) hiçbir şey esirgememek; 3) bir şeye çok düşkün olmak, çok sevmek: O, kitap için canını verir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3489, 'canını yakmak', '1) acı verecek bir biçimde cezalandırmak: ''Eskiden uzun seneler askerî rüştiyelerde hocalık etmiş olan bu adam, kim bilir ne kadar çocuğun canını yakmıştı.'' -R. N. Güntekin. 2) bir kimseyi, çok sıkıntı ve zarara sokmak: ''Ne derse desin, gözü bunun canını yakmakta.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3490, 'canının derdine düşmek', 'ölüm korkusuna kapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3491, 'canının derdine düşmek', 'canından başka bir şey düşünemeyecek kadar sıkıntıda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3492, 'canının içine sokacağı gelmek', 'çok hoşlanmak, çok sevmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3493, 'cankulağı ile dinlemek', 'çok dikkatli dinlemek: ''Atölyede duyduğum kelimeleri, cümleleri cankulağı ile dinliyor, bunları aynen Fransızlar gibi kullanmak için can atıyordum.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3494, '... canlısı', '... düşkünü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3495, 'cansız düşmek', 'hastalık veya yorgunluk yüzünden bitkin bir duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3496, 'cart cart ötmek', 'çok konuşmak: ''Öldürecek adam öldüreceğim diye cart cart ötmez, sıkı ise gelir öldürür.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3497, 'cart curt etmek', 'göz korkutmak veya övünmek amacıyla abartılı konuşmak: ''Cart curt etmesine bakma, korkaktır.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3498, 'cart (zırt) kaba kâğıt', ' argo yüksekten atana veya çalımlı bir tavır takınana karşı ''senin bu tavrına değer veren var sanki'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3499, 'cartayı çekmek', ' argo ölmek: ''Yarın cartayı çektim mi, kefensiz mefensiz it ölüsü gibi meydanda kalacak leşim.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3500, 'cascavlak (ortada) kalmak', 'bütün imkânları elinden alınmak: ''Bütün paran yok olur gider, cascavlak ortada kalırsın.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3501, 'cavlağı çekmek', ' argo ölmek: ''Ne olacak a canım, hepimiz de ya bir kaza neticesinde veyahut kazasız olarak cavlağı çekeceğiz.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3502, 'cayırtı koparmak', 'çok gürültü koparmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3503, 'cayırtı vermek', 'gürültü ile gözdağı vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3504, 'cayırtıyı basmak', 'birdenbire bağırıp çağırmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3505, 'caz yapmak', '1) boşa konuşmak, gevezelik etmek; 2) aykırı düşünceler ortaya atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3506, 'ce demeye mi geldin?', ' şaka ''bu kadar az oturmaya mı geldin?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3507, 'cebellezi etmek', 'cebine indirmek: ''Dalsın içeri de el âlemin kirazını, şeftalisini cebellezi mi etsin?'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3508, 'cebi para görmek', 'parası yokken para kazanmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3509, 'cebine indirmek (atmak)', 'hakkı olmadığı hâlde parayı kendine mal etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3510, 'cebini doldurmak', 'karşılaştığı elverişli durumlardan yararlanarak bol para kazanmak: ''Dünyayı bir tüketim çılgınlığına itip ceplerini doldurmuşlardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3511, 'cebir kullanmak', 'bir işi yaptırmak için zora başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3512, 'cefa çekmek (görmek)', 'zulüm görmek: ''Mektubumda yazmamış mıydım, senin yüzünden ne cefalar çektiğimi?'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3513, 'cefaya katlanmak', 'zulme tahammül etmek: ''Hediye istemezler, fazla kıskanmazlar, cefaya katlanırlar, can sıkmazlar.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3514, 'cefayı çekmeyen sefanın kadrini bilmez', 'sıkıntı çekmemiş olanlar, eriştikleri rahatlığın ve mutluluğun değerini bilemezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3515, 'cehennem gibi', 'çok sıcak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3516, 'cehennem ol!', '''defol!'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3517, 'cehennem olup gitmek', 'defolmak: ''Başımı örtünce cehennem olur giderim.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3518, 'cehenneme çevirmek', 'yaşanılmaz bir duruma getirmek: ''Kafamın çatlaklığı yüzünden bir anda orasını zindana, cehenneme çevirdim.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3519, 'cehenneme kadar yolu var', '''defolsun, istediği yere kadar gitsin'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3520, 'cehennemi boylamak', 'sevilmeyen bir kimse ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3521, 'cehennemin bucağı (dibi)', 'çok uzak yer: ''Var git de ne hâlin varsa gör! Cehennemin dibine kadar yolun var!'' -N. Hikmet. ''... inerseniz çok büyük sevaba girmiş olursunuz. Yoksa bilirsiniz ki ben, cehennemin bucağı olsa giderim...'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3522, 'cehennemin dibine gitmek', 'kızılan bir kimse defolup gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3523, 'ceketini alıp çıkmak', '1) ilişkisini tamamen koparmak; 2) hiçbir şey almadan birlikteliği bitirmek, ortaklıktan ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3524, 'cellat gibi', 'acımasız.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3525, 'cemaat ne kadar çok olsa imam gene bildiğini okur', 'bir yetkili kimse, çevresindekilerin düşüncesi ne olursa olsun kendi istediğini yapmaya çalışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3526, 'cemaate uymak', 'içinde bulunulan bir topluluğa uyarak davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3527, 'cemaatle namaz kılmak', 'imama uyarak namaz kılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3528, 'cemre düşmek', 'sıcaklık yükselmek: Bugün cemre suya düştü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3529, 'cenaze gibi', 'benzi sararmış.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3530, 'cenazeyi kaldırmak', '1) ölüyü gömmek üzere götürmek; 2) ölüyü gömmek; 3) mec. ortada kalan bir işi bitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3531, 'cendereye sokmak', 'manevi baskı altına almak: ''Böyle sabit bir görüş tarzı, fikir ve hayali bir cendereye sokmak demektir.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3532, 'cennet gibi', 'güzel, bakımlı (yer): ''Bu cennet gibi yerler gözümde zindan kesiliyor.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3533, 'cennete çevirmek', 'temiz, bakımlı, güzel bir yer durumuna getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3534, 'cennete dönmek', 'güzel, rahat yaşanılır, bakımlı bir yer durumuna gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3535, 'cep harçlığını çıkarmak', 'günlük masrafını karşılayacak kadar kazanç sahibi olmak: ''Tuttuğu odayı, ayda üç bin Frankla başkasına veriyor; arada hiç olmazsa cep harçlığını çıkarıyordu.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3536, 'cep yakmak', 'çok pahalı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3537, 'cephe açmak', 'savaş olmayan bir bölgede, savaşa hazırlanmak ve başlamak: ''Avusturyalılara karşı Makedonya''da cephe açarak Selanik''e otuz bin asker çıkardılar.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3538, 'cepheden cepheye koşmak', 'durmadan değişik cephelerde savaşmak, yılmak bilmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3539, 'cepten harcamak', 'bir başkasının söylemediği bir sözü söylemiş gibi aktarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3540, 'cepten vermek', 'kendi kesesinden, kendi malından ödemek: ''Kâğıt parasını oyuna başlamadan peşinen cepten vereceğiz.'' -K. Tahir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3541, 'ceremesini çekmek', 'başkasının yol açtığı zararı ödemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3542, 'cereyan çarpmak', 'elektrik akımına tutulup etkisinde kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3543, 'cereyana kapılmak', '1) elektrik akımıyla çarpılmak; 2) suyun akışı içinde kalıp sürüklenmek; 3) bir eğilim, bir görüş hareketi içinde yer almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3544, 'cereyanda kalmak', '1) kapalı bir yerde, karşılıklı açık pencere veya kapı arasında meydana gelen hava akıntısında kalmak; 2) buna bağlı olarak üşütmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3545, 'cerre çıkmak', 'medreselerde okuyan softalar para ve erzak toplamak için belli aylarda köylere dağılıp imamlık veya müezzinlik yapmak: ''Padişahlardan birinin torunu çıkageldi, yarı ümmi bir adamla cerre çıkmıştı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3546, 'cesaret göstermek', 'yürekli davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3547, 'cesarete gelmek', 'yılgınlığı gitmek, yüreklenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3548, 'cesaretini toplamak', 'kendine güven duygusunu, yürekliliğini ve atılganlığını bir araya getirmek: ''Bir gün bütün cesaretini toplayıp beyefendiye gider.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3549, 'cevabı dikmek (dayamak veya yapıştırmak)', 'hlk. kesin, ters ve karşısındakinin beklemediği bir karşılık vermek: ''Usta hemen cevabı yapıştırmıştı.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3550, 'cevahir yumurtlamak', ' tkz. cevher yumurtlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3551, 'cevapsız bırakmak', 'karşılığında herhangi bir cevap vermemek, bir tepki göstermemek: ''O yüzden, seni ödüllendirmek için sorunu cevapsız bırakmayacağım.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3552, 'cevapsız kalmak', 'cevap alınamamak: ''Suallerim cevapsız kaldı.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3553, 'cevher yumurtlamak', ' tkz. değerli sözler söylediğini sanarak saçmalamak: ''Sofrada biraz fazla kaçırdığı şarabın ateşiyle daha saatlerce cevherler yumurtlayacaktı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3554, 'ceviz gölgesi yavuz gölgesi, söğüt gölgesi yiğit gölgesi', 'ağaçların gölgeleri bile doğal özelliklerini yansıtır, koz, yani ceviz ağacının altında insan, herkesin peşinde koştuğu bir kızın yanındaymışçasına mutluluk duyar; söğüdün gölgesi, boylu boslu bir yiğidin güvenini kazanır; dut ise altına meyvelerini döktüğü için gölgesi çıkarcıların üşüştüğü bir yerdir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3555, 'ceviz kırmak', 'yanlış tutum veya davranışta bulunmak, hata yapmak: ''Onun kırdığı cevizler artık haddini aştı.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3556, 'ceylan gibi', 'yapısı ince ve uyumlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3557, 'ceza almak', 'cezalandırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3558, 'ceza çekmek', '1) hapiste yatmak: Hırsızlıktan üç ay ceza çekti. 2) manevi bakımdan işlenen suçun ağırlığını çekip sıkıntı ve üzüntü içinde kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3559, 'ceza görmek', 'kendisine ceza verilmek, cezalandırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3560, 'ceza vermek', '1) cezalandırmak; 2) para cezası ödemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3561, 'ceza yemek', 'cezalandırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3562, 'cezasını bulmak', 'hak ettiği kötü sona uğramak: ''Hasretten lime lime olmuş zavallı kalbinle oynayanlar cezalarını buldular.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3563, 'cezasını çekmek', '1) yaptığı bir kusur veya tedbirsizliğin zararına uğramak: ''Bu haylazlığının cezasını çeker.'' -P. Safa. 2) hükmedilen cezayı bitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3564, 'cezaya çarptırmak', 'cezalandırmak: ''Seni yalana tövbe ettirecek bir cezaya çarptırmalıyım.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3565, 'cezbeye tutulmak (kapılmak)', 'bir duygu veya bir inanışın etkisiyle aşırı ölçüde coşup kendinden geçmek: ''Cezbeye tutulmuş gibi haykırdım, Türkçe haykırdım.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3566, 'cezve sürmek', 'kahveyi pişirmek için cezveyi ateşe koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3567, 'cıcığı çıkmak', '1) çok yorulmak; 2) hırpalanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3568, 'cıcığını çıkartmak', 'cıcığı çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3569, 'cılk çıkmak', 'kusurlu, boş veya bozuk çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3570, 'cılk etmek', 'bozmak, çürütmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3571, 'cılkı çıkmak', 'bozulmak, doğru ve uygun yolundan ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3572, 'cıva gibi', 'yerinde durmaz, ele avuca sığmaz, çok hareketli.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3573, 'cız etmek', '1) ''cız'' diye ses çıkarmak; 2) acı duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3574, 'cızlamı çekmek (cızlam etmek)', '1) kaçmak, savuşup gitmek; 2) argo ölmek: ''Adam bir hafta içinde cızlamı çekerse hiç günahım yokken adım kötüye çıkar.'' -T. Yücel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3575, 'ciddiye almak', 'inanmak, gerçek sanmak, önem vermek: ''Halkımız sanatçıyı baştan beri pek ciddiye almaz, ona bir delişmen gözüyle bakar.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3576, 'ciğer kebap olmak', 'büyük bir acıya uğramak, yüreği yanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3577, 'ciğeri beş (on) para etmemek', 'değersiz, aşağılık bir kimse olmak: ''Önüme hiç kimse duramaz bunun için, ciğeri on para etmez adam onlar.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3578, 'ciğeri parçalanmak', 'yüreği parçalanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3579, 'ciğeri sızlamak', 'yüreği sızlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3580, 'ciğeri yanmak', 'çok acı ve sıkıntı çekmek, büyük bir acıya uğramak, yüreği yanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3581, 'ciğerimin köşesi', '1) çok sevdiğim: ''Oturmuş kumar oynar / Ah ciğerimin köşesi'' -Halk türküsü. 2) çok sevgili evladım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3582, 'ciğerine oturmak', 'masraf çok ağır gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3583, 'ciğerini (delip) delmek (geçmek)', 'acıklı bir durum, kişiye dayanılmaz bir üzüntü vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3584, 'ciğerleri bayram etmek', '1) her zamankinden daha iyi cins sigara içmek; 2) temiz havaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3585, 'cihana gelmek', '1) doğmak; 2) mec. meydana gelmek, ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3586, 'cihanı tutmak', 'her tarafa yayılmak, dünyayı tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3587, 'cihat açmak', 'savaş için çağrı yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3588, 'cila çekmek', 'içilen içkinin etkisini azaltmak veya artırmak amacıyla bir şey içmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3589, 'cim karnında bir nokta', '1) hiçbir bilgisi olmayan, cahil; 2) acemi, toy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3590, 'cin çarpmak', 'bir inanışa göre, cinlerin öfkesiyle inme inmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3591, 'cin çarpmışa dönmek', 'neye uğradığını bilemeyecek kadar kötü bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3592, 'cin damarına basmak', 'kişiyi çok sinirlendirecek söz söylemek, çileden çıkarmak: ''İnadıma iş yapmasın, dokunmayım hatırına. Cin damarıma niye basıyor?'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3593, 'cin gibi', 'anlayışlı ve zeki: ''Bir kedi kadar çevik, açıkgöz olan İzmirli Nusret, lisenin onuna kadar okumuş, cin gibi bir delikanlıydı.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3594, 'cin ifrit olmak (kesilmek)', 'son derece kızmak, öfkelenmek: ''O mirasın ağırlığı altında ezilip susacaklarına, bir de ülkemizde insan haklarının avukatı kesilmiyorlar mı cin ifrit oluyorum.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3595, 'cin olmadan şeytan (adam) çarpmak', 'gücünün üstündeki işleri başarmaya kalkışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3596, 'cin tutana bir muska yeter', 'çok kızmış birisini yatıştırmak için akıllı bir kimsenin sözlü veya yazılı öğüdü etkili olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3597, 'cini tutmak', 'çok sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3598, 'cinine gitmek', 'nefret etmek, tiksinmek: ''Karaya uğramak, her denizci gibi cinine gidiyordu.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3599, 'cinleri ayağa kalkmak', 'sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3600, 'cinleri başına toplanmak (üşüşmek)', 'öfkelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3601, 'cinleri (cin) tepesine çıkmak (binmek)', 'çok kızmak: ''Gidip oyunu seyretmiş. Seyretmiş ama, bütün cinleri de tepesine çıkmış, ağızlarının payını vermiş.'' -N. Meriç. ''Biraz fazlaca gülsen, bir parça kısa giysen cin tepesine biniyor.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3602, 'cinnet geçirmek', 'delirmek, aklını kaçırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3603, 'cinnet getirmek', 'bir an için delilik belirtisi göstermek: ''Ayol, duydunuz mu? Fahim Bey cinnet getirmiş.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3604, 'cins cinse çeker', 'her insan veya yaratık az çok soyuna benzer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3605, 'cins horoz yumurtada öter', 'çocuğun soyluluğu ve değeri daha bebekken her hâlinden anlaşılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3606, 'cins kedi ölüsünü göstermez', 'soylu kişi, acınacak kötü durumunu kimseye göstermez ve söylemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3607, 'cirit oynamak', '1) cirit oyununu oynamak: ''Bu dallardan kendimize atlar yapar, cirit oynar, yarışa çıkardık.'' -Ö. Seyfettin. 2) istediği biçimde, keyfince davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3608, 'civcivde gözün, et yemeye yüzün olsun', 'kişi, karşılık beklediği işten istediğini alabilmek için gereken harcamaları yapmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3609, 'cömert derler maldan ederler, yiğit derler candan ederler', 'başkalarının pohpohlamalarına kananlar mallarından ve canlarından olurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3610, 'cömertle nekesin harcı birdir', '1) cömertin de cimrinin de sarıldığı kefen aynıdır; 2) cimri, ucuz diye her şeyin kötüsünü alır, bunlar işe yaramadıklarından veya çarçabuk bozulduklarından yenilerini almak zorunda kalır ve birkaç kez para harcar; cömert ise bir kez çok para verip her şeyin iyisini alır. Sonuç olarak cimri de, cömert de aynı parayı harcamış olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3611, 'cukkayı yutmak', 'oyunda ütülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3612, 'cumartesi kibarı gibi süslenmek', 'özentili fakat zevksiz süslenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3613, 'cumburlopu çekmek', 'pat diye düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3614, 'cumhura muhalefet kuvveihatadandır', 'olağan sayılmayacak kadar kısa bir zamanda olan bir gelişmenin sürekli olamayacağını anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3615, 'cübbe gibi', 'çok geniş ve uzun (giysi).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3616, 'cümbüş yapmak', 'toplu olarak eğlenmek: ''Bir ziyafette cümbüş yapanları teker teker tokatlamış.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3617, 'çaba göstermek', 'bir işi başarmak için çalışmak, uğraşmak, gayret göstermek: ''Onu kurtarabilmek için olmayacak şeylere saldırmak derecesinde bir çaba gösteriyorsunuz.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3618, 'çaba harcamak', 'bir işi yapabilmek için elinden geleni yapmak: ''Tehlikeyi anlamış olacak ki seçimlerde oylarını dağıtmamaya çaba harcıyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3619, 'çabalama kaptan ben gidemem', 'bu işi yapacak güçte değilim, zorlamanın yararı yok.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3620, 'çağ açmak', 'herhangi bir bakımdan öncekilerden farklı olan yeni bir evrensel gidişe yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3621, 'çağ atlamak', 'büyük ilerleme sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3622, 'çağ dışı olmak (kalmak)', '1) çağın gerektirdiği şartların gerisinde kalmak; 2) ask. yedek askerlik çağını doldurmuş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3623, 'çağanoz gibi', 'eğri büğrü (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3624, 'çağı geçmek', '1) eskimek; 2) dönemi veya modası geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3625, 'çağı yakalamak', 'çağın gerektirdiği gelişmişlik düzeyine ulaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3626, 'çağın gerisinde kalmak', 'gelişmelere ve yeni düşüncelere uyum sağlayamamak, ayak uyduramamak: ''Ben yeniliklere yabancı, eski moda, çağın gerisinde kalmış, emekli bir istihbaratçıyım.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3627, 'çağını aşmak', 'düşünce, tutum ve davranışlarıyla bulunduğu çağdan daha ileride olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3628, 'çağlamadan çatlamak', 'gerekli olgunluğa erişmeden olgun davranışlarda bulunmak, büyüklük taslamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3629, 'çağrılan yere erinme, çağrılmayan yere görünme', 'kişi, çağrıldığı yere gitmeli, çağrılmadığı yere gitmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3630, 'çağrılan yere git, ar eyleme; çağrılmadığın yere gidip yerini dar eyleme', 'kişi, çağrıldığı yere gitmeli, çağrılmadığı yere gitmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3631, 'çağrılmayan yere çörekçiyle börekçi gider', 'çağrılmadığın yere sakın gitme.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3632, 'çakı gibi', '1) canlı ve atik: ''Övünmek saymazsanız çakı gibi topçu subayı oluyordum.'' -R. Erduran. 2) sağlıklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3633, 'çakı suyu kesiyor', 'bıçak suyu kesiyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3634, 'çakılı kalmak', '1) yerini veya biçimini değiştirmeden durmak; 2) iz bırakmak: ''O günkü sözleri çakılı kaldı bende.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3635, 'çakılıp kalmak', 'bir yerde uzun süre hareketsiz kalmak: ''Bir arıza yapsa araba çakılıp kalacağız.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3636, 'çalı gibi', 'sık ve sert (saç, sakal).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3637, 'çalı idi çırpı idi, evim idi ya, ayı idi uyu idi, kocam idi ya', 'her ne kadar evim derme çatma, kocam kaba saba idiyse de, bir düzen kurmuş, yaşayıp gidiyordum.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3638, 'çalım atmak (yapmak)', 'çalımlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3639, 'çalım satmak', 'kurulup büyüklük taslamak: ''İzmir ve dolaylarında çalım satıp dolaşmaya başlayacaklar.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3640, 'çalım yemek', 'futbolda çalım ile geçilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3641, 'çalımına gelmek (getirmek)', 'uygun zaman veya durumu ele geçirmek: ''Sanki demek istediğim bir çalımına gelseydi seni de yüzdürürdü.'' -M. Ş. Esendal. ''Yıldız, çalımına getirdikçe ateş ediyordu.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3642, 'çalıp çırpmak', 'hırsızlık yapmak: ''Müşteri ise her zamanki oyunbazlığıyla çalıp çırptıklarını eve yığıyordu.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3643, 'çalma elin kapısını, çalarlar kapını', 'kimseye kötülük yapma yoksa onlar da sana aynı kötülüğü yaparlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3644, 'çalmadan oynamak', '1) çok keyifli ve sevinçli durumda bulunmak; 2) bir işe çok hevesli görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3645, 'çalyaka etmek', 'yakasına yapışıp sıkıca tutmak: ''Şimdi karakoldan görürlerse kudurmuşsun diyerek çalyaka ederler.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3646, 'çam ağacından ağıl olmaz, el çocuğundan oğul olmaz', 'her şeyin bir değeri vardır; yapacağı iş, kullanılacağı yer ayrıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3647, 'çam devirmek', 'karşısındakine dokunacak veya kötü bir sonuç doğuracak söz söylemek: ''Bu hoppa oğlan, karısına ne diller dökecek, ne potlar kıracak, ne çamlar devirecekti.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3648, 'çam sakızı çoban armağanı', 'verilen bir armağanın sunulduğu kimseye değerine uygun olmadığını ve verenin gücünün ancak buna yettiğini özür yollu anlatmak için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3649, 'çam sakızı gibi', 'tedirgin edecek kadar bir insanın peşinden ayrılmayan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3650, 'çamaşır ertesi olmak', 'çamaşır yıkamaktan aşırı yorulup hasta olmak: ''Kaynanam da yıkar ama iki gün de çamaşır ertesi olur yatar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3651, 'çamur gibi', '1) iyi pişmemiş ve siyah unla yapılmış (ekmek); 2) herkese sataşıp tedirginlik veren (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3652, 'çamura bulaşmak (batmak)', 'kirli ve uygunsuz bir işe karışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3653, 'çamura taş atmak', 'çirkefe taş atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3654, 'çamura yatmak', ' argo 1) borcunu ödememek; 2) sözünü yerine getirmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3655, 'çamuru karnında, çiçeği burnunda', 'çiçeği burnunda, çamuru karnında.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3656, 'çan çalmak', 'herkese bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3657, 'çan çan etmek (ötmek veya konuşmak)', 'yüksek sesle sürekli gevezelik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3658, 'çanağa ne doğrarsan kaşığında o çıkar', 'kişi, kendisi için önceden yaptığı hazırlıkların verimini ileride alır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3659, 'çanak tutmak (açmak)', 'davranışları veya sözleriyle kötü bir sonuca yol açmak: ''Oh olsun... Vallahi memnun oldum, diyordu. Çanak tuttun. Şunun şurasında rahat sana battı mıydı?'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3660, 'çanak yalamak', 'dalkavukluk etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3661, 'çanakta balın olsun, Yemen''den (Bağdat''tan) arı gelir', 'malı güzel olan kimse için müşteri kaygısı yoktur, onun malına uzak yerlerden bile istekli çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3662, 'çantadan yetişmek', 'bir mesleği eğitim görmeden deneyimlerle kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3663, 'çapanoğlunun abdest suyu gibi', 'çok sulu, tatsız ve kötü görünüşlü olan (içilecek şeyler).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3664, 'çapraza almak', '1) karşı yönlerden kuşatmak: ''Mitralyözler onu çapraza almış, kızıl iğneleriyle gövdesini delik deşik ediyorlardı.'' -A. İlhan. 2) mec. herhangi bir konuda çeşitli yönlerden sıkıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3665, 'çapraza sarmak', 'bir iş içinden çıkılmaz duruma gelmek, çaprazlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3666, 'çaptan düşmek', 'çalışma gücü, verimi azalmış veya tükenmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3667, 'çaresiz kalmak', 'çözüm yolu, çıkar yolu bulamamak: ''Köyde kim çaresiz kalırsa, kimin işi bozulursa İstanbul yolunu tutar.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3668, 'çark çevirmek', 'aynı yol üzerinde dönerek gitmek: ''Kırmızı balıklar birdenbire canlanırlar ve kavanozun içinde birbiri ardınca keyifli keyifli çark çevirmeye başlarlar.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3669, 'çark etmek', '1) bir doğrultuda giden kimse, şey sağa veya sola doğru yön değiştirmek: ''Küçük hizmetçi masanın öbür ucundan yarım sağa çark ederek elektrik düğmesine doğru döndü.'' -S. F. Abasıyanık. 2) geri dönmek; 3) mec. savunduğu düşünceden vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3670, 'çarka vermek (çektirmek)', 'kesici araçları bileği çarkı ile biletmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3671, 'çarkı döndürmek', ' hlk. geçimini sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3672, 'çarkına etmek (okumak)', ' argo birine büyük kötülük yapmak veya işini bozarak zarar vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3673, 'çarpıntısı tutmak', 'heyecan, korku veya üzüntüden çarpıntı nöbeti gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3674, 'çarşaf gibi', 'dalgasız, dümdüz ve durgun (deniz, göl).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3675, 'çarşaf kadar', 'pek büyük, çok geniş: ''Çarşaf kadar bir kâğıda künyemi yazmışlar.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3676, 'çarşafa dolanmak', ' argo bir işin içinden çıkamamak, kötü ve başarısız duruma düşmek, zor durumda kalmak, çarşaflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3677, 'çarşafa girmek', 'çarşaf giymeye başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3678, 'çarşı iti ev beklemez', 'başıboş gezmeye alışanlar, disiplinli iş yapmaya gelemezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3679, 'çat orada çat burada çat kapı arkasında', 'çok çabuk yer değiştiren bir şeyin durumunu anlatan bir söz: ''Sizin sevgili bir yerde durmaz, çat orada çat burada çat kapı arkasındadır.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3680, 'çatal görmek', 'net görememek, bir şeyi iki görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3681, 'çatal kazık yere batmaz (geçmez, çakılmaz)', 'birden çok kimsenin söz sahibi olduğu iş yürümez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3682, 'çatır çatır çatlamak', '1) çok çatlamak: ''Kızgın güneşe maruz bırakılmış çam fıstıkları çatır çatır çatlıyor, sapır sapır dökülüyordu.'' -E. E. Talu. 2) çok kıskanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3683, 'çatıyı almak', 'çatıya ulaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3684, 'çatkın olmak', 'kendini ağırdan satmak: ''Müdüre göre idareci biraz çatkın olacak yani oldukça ağırdan satacak kendini.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3685, 'çatlasa da (çatlasa da patlasa da)', ' tkz. ''elinden gelen her çareye başvursa da'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3686, 'çay kenarında kuyu kazmak', 'elde, amaca ulaşılacak bol araç varken emek harcayarak başka yollar aramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3687, 'çay kuşu, çay taşı ile vurulur', 'her iş, ancak ve ancak o iş için gerekli araçla yapılabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3688, 'çaydan geçip derede boğulmak', 'büyük güçlükleri yenmişken önemsiz bir sebepten başarısızlığa uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3689, 'çayı görmeden paçaları sıvamak', 'dereyi görmeden paçaları sıvamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3690, 'çehre almak', 'tavır takınmak: ''Benimle yalnız kalınca yine bir nöbet ağlayıp sızlayacaklarını hissettiğim için çatkın bir çehre almıştım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3691, 'çehre etmek', 'surat etmek: ''Bir şeyim yok, asabım bozuk diye cevap veriyor, çehre ediyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3692, 'çehresi bozulmak', 'yüzü düşmek: ''İhtiyarın çehresi fena hâlde bozulmuştu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3693, 'çek! (çek arabanı!)', ' hkr. ''git buradan!'' anlamında kullanılan bir söz: ''Ben şimdi boya mı düşünüyorum? Çek arabanı şuradan diyecektim, diyemedim.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3694, 'çekeceği olmak', 'başına sıkıntılı çok iş gelecek olmak: Bu laf anlamaz ustadan çekeceğin var.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3695, 'çeki taşı gibi', 'ağır ve kımıldamaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3696, 'çekince koymak', 'bir karara katılmadığını belirtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3697, 'çekip almak', 'uzaklaştırmak, uğraşısına son vermek, koparmak: ''Beni tiyatrodan çekip alırken alıştığım yaşayışın giderlerini karşılayıp karşılayamayacağını sonradan anladım.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3698, 'çekip çevirmek', 'hâle yola koymak, yönetmek: ''Zavallı madam, o sakat hâliyle nasıl çekip çevirsin bu eski binayı?'' -A. Dino.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3699, 'çekip gitmek', 'bırakıp gitmek, ayrılmak, savuşmak: ''Sırf bu parayı ödeyemiyorum diye çekip gitmesini bağışlamıyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3700, 'çekip vurmak', 'bir anda karar verip silahla öldürmek: ''Eğer üstümde tabanca olsa bir iki demez çekip vururdum.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3701, 'çekişilmeyince pekişilmez', 'düşünceler karşılıklı tartışılmadan sağlam bir anlaşma ve uzlaşma ortaya çıkmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3702, 'çekiver kuyruğunu', ' argo ''artık ondan hayır bekleme'' anlamında kullanılan bir söz: ''Bir defa rakı adamın beynine vurdu mu çekiver kuyruğunu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3703, 'çekiye gelmek', 'düzene uymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3704, 'çekiye gelmez', 'ölçüsüz derecede çok veya büyük.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3705, 'çeliğe su vermek', 'çeliği özel bir biçimde hızla soğutarak daha çok sertleşmesini sağlamak: ''Yaptığı kısacık bıçaklar bile iki kat olur kırılmazdı, çeliğe su vermek sanatının yalnız ona mahsus bir sırrı idi.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3706, 'çember geçirmek', 'çemberle kuşatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3707, 'çember içine almak (çembere almak)', 'kuşatmak: ''İktidar muhalefet partilerini gittikçe daralan bir çember içine alıyor.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3708, 'çemberden dönmek', 'başarıya ulaşmak üzereyken olumsuz bir sonuçla karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3709, 'çemberi yarmak', 'bir veya birkaç noktayı delerek kuşatmadan kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3710, 'çene çalmak', 'gevezelik etmek: ''Komşu kadınlar akşam yemeğinden sonra onun etrafında toplanırlar, geç vakitlere kadar çene çalarlardı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3711, 'çene patlatmak', 'karşı tarafa anlatabilmek veya kabul etmesini sağlamak için bir konu üzerinde uzun uzun konuşmak: ''Feti Bey''in boşu boşuna çene patlatmayacağı herkesçe bilinirdi.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3712, 'çene yarışına girmek', 'birbirinin sözünü keserek susmamacasına konuşmak: ''Arif gibi bir adamla çene yarışına girmek istememekle beraber susup oturamazdı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3713, 'çene yarıştırmak', 'karşılıklı gevezelik etmek, çok konuşmak: ''Çene yarıştırmak gelmiyordu içimden.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3714, 'çene yetiştirmek', 'konuşmayı sürdürmek: ''Şu bitkin hâliyle dayısına çene yetiştirecek gücü yoktu.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3715, 'çene yormak', 'boşuna söyleyip durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3716, 'çenesi açılmak', 'durmadan konuşmak, gevezelik etmek: ''Sabahtan akşama kadar uyukluyorsun, gece olunca çenen açılıyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3717, 'çenesi atmak', 'can çekişirken çenesi titremek: ''Hasta aksırır gibi bir ses çıkardı. Döndü, baktı; -Ne istiyor?... dedi, ağa cevap vermedi. Çenesi atıyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3718, 'çenesi durmamak', 'gereksiz yere sürekli konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3719, 'çenesi düşmek', 'yerli yersiz konuşup gevezelik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3720, 'çenesi kilitlenmek', 'alt ve üst çene sımsıkı bir durumda bir araya gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3721, 'çenesi oynamak', '1) bir şey yemek: ''Yavrum gelinim! Kapının önünden yemiş mi aldın? Ölmüşlerinin canı için biraz da bana tattır; canım sıkılıyor, azıcık çenem oynarsa sevinirim.'' -H. R. Gürpınar. 2) çok konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3722, 'çenesine vurmak', 'aşırı derecede konuşmak, gevezelik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3723, 'çenesini açmak', '1) çok konuşmasına sebep olmak: ''Fakat bu inat, Emine''nin çenesini açmış; kızın ne kadar kusuru varsa babasından geldiğini söylerken Tevfik''e ağzını açmış, gözünü yummuştu.'' -H. E. Adıvar. 2) çok konuşmak, gevezelik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3724, 'çenesini bağlamak', '1) ölen bir kimsenin çenesi altından geçirilen tülbendi başının üstünde düğümlemek; 2) mec. bir kimsenin ölümünü istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3725, 'çenesini dağıtmak', 'iyice dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3726, 'çenesini kapatmak', 'susturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3727, 'çenesini tutmak', 'ağzını tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3728, 'çenesinin bağı çözülmek', 'gevezelik etmek, yerli yersiz, sürekli konuşmak: ''Çenesinin bağı çözülmüştü, cıvıldıyor, annesinden, babasından söz açıyordu.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3729, 'çengel atmak', 'bir konuya taraftar toplama girişiminde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3730, 'çengel takmak', 'uğraşmak veya kötülük etmek için el atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3731, 'çengi ölüsü çalgı (daire, tef) ile kalkar', 'zevk ve sefa içinde ömür sürmüş bir kimse, en sıkıntılı günlerinde bile bu alışkanlığını bırakamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3732, 'çerçi başındakini satar', 'satıcı elinde neyi varsa satar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3733, 'çerçi kızı boncuğa âşık(tır)', '1) bir insan hangi işle uğraşıyorsa yakınındakiler de o işi çok sever; 2) ticaretle uğraşanlar, sattıkları şeylerden evdekilerini yoksun bırakarak onların bu eşyanın özlemini çekmelerine neden olurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3734, 'çeşmeye gitse çeşme kuruyacak', 'çok talihsiz kimseler için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3735, 'çeşni tutmak', 'ekmekçilikte una karıştırılacak suyun oranını belirtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3736, 'çetele çekmek (tutmak)', 'hesap tutmak amacı ile bir yere çizgiler çizmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3737, 'çeteleye dönmek', 'insanın yüzünde veya başka bir tarafında birçok kesik ve sıyrık oluşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3738, 'çevir kazı yanmasın', ' alay karşısındakine dokunacak yersiz bir söz söylediğini fark eder etmez sözünü çevirmeye kalkışanlara söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3739, 'çeyiz düzmek', 'çeyiz hazırlamak: ''Kazandığını bir yana atar, kendine çeyiz düzer.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3740, 'çıbanın başını koparmak', 'ağır bir sorunun patlak vermesine yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3741, 'çığ gibi büyümek', 'bir olay birdenbire ve etkileyici bir biçimde büyümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3742, 'çığır açmak', 'bir alanda yeni bir yol, yöntem başlatmak: ''Hepsi birden Atatürk''ün açmakta olduğu bir çığırda çalışıyorlardı.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3743, 'çığlık atmak (koparmak, basmak)', 'kulak tırmalayıcı korkunç sesler çıkararak acı acı bağırmak: ''Deve acı bir çığlık atarak yere yığıldı.'' -N. F. Kısakürek. ''Bir gün işte bu çalgı çalınırken küçük kız olanca kuvveti ile tepinmeye, çığlık basmaya başlamıştır.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3744, 'çıkar gözetmek', 'çıkarına bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3745, 'çıkarına bakmak', 'yalnızca kendini ve kendi durumunu gözeterek çıkar sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3746, 'çıkarını tepmek', '1) kendisine yarar sağlayacak bir şeyi veya bir durumu istememek; 2) kendisine yarar sağlayacak bir şeyden veya durumdan yararlanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3747, 'çıkıntılık etmek', '1) itiraz etmek; 2) yolunda giden işi bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3748, 'çıkış almak', '1) işten ayrılmak; 2) çıkış belgesi almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3749, 'çıkış vermek', 'belge düzenleyip işine son vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3750, 'çıkış yapmak', '1) bir tartışmada, karşı düşüncede olanları alt etmek için sert davranışta bulunmak; 2) ask. uçak herhangi bir görevle havalanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3751, 'çıkmadık canda umut var', 'elden gitti sandığımız bir şeyle ilgimiz büsbütün kesilmemişse gereken çabayı harcayarak onun elimizde kalmasını sağlayabileceğimizi umabiliriz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3752, 'çıkmadık candan umut kesilmez', 'elden gitti sandığımız bir şeyle ilgimiz büsbütün kesilmemişse gereken çabayı harcayarak onun elimizde kalmasını sağlayabileceğimizi umabiliriz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3753, 'çıkmaz ayın son çarşambası', ' şaka işin hiçbir zaman yapılmayacağını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3754, 'çıkmaza girmek', 'bir iş çözümlenemeyecek, içinden çıkılmayacak bir duruma düşmek: ''Kıbrıs sorunu, şu ya da bu siyasal oyunla, yeniden çıkmaza girecektir.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3755, 'çıkmaza sokmak', 'bir işi, bir durumu çözümlenemez, güç bir duruma getirmek: ''Bu çelişki, kıyafetinin seçimi konusunda onu çıkmaza sokuyordu.'' -N. Bezmen.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3756, 'çıkmazda olmak', 'çözüm bulamamak, çözümsüz durumda olmak: ''Şu sıralar tam bir çıkmazdayım anlayacağın.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3757, 'çılgına dönmek', '1) sevinç, öfke, kızgınlık vb. duygular sonucu aşırı ölçüde heyecanlanmak; 2) kendine hâkim olamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3758, 'çın çın inletmek', 'gür ve keskin ses çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3759, 'çın çın ötmek', 'sürekli olarak keskin ses çıkarmak: ''Rüzgâr vurdukça çın çın öten tabakların sesini dinleye dinleye uykuya geçtiler.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3760, 'çın tutmak', 'doğru olduğunu söylemek, doğrulamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3761, 'çıngar çıkarmak (koparmak)', 'gürültü, kavga çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3762, 'çıngar kopmak (çıkmak)', 'gürültü, kavga çıkmak: ''Bu son rolü, ihtiyaten, büyük çıngarın kopacağı güne sakladı.'' -N. Araz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3763, 'çıngırağı çekmek', ' argo ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3764, 'çıngıraklı deve kaybolmaz', 'nerede olsa varlığını gösteren kimse unutulmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3765, 'çıra dibi karanlık olur', 'bir kimse, başkalarına bol bol yaptığı yardımı kendi yakınlarına yapmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3766, 'çırak almak', 'yanında çırak çalıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3767, 'çırak çıkarmak', '1) bir kimseyi beklediğinden az bir kazançla ortaklıktan uzaklaştırmak; 2) esk. cariye veya odalıkların saray, konak, köşk vb. büyük yerlerde yıllarca hizmet ettikten sonra evlenmelerine veya o yerlerden ayrılmalarına izin vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3768, 'çırak vermek', 'çırak olarak çalışması için bir iş yerine göndermek: ''Bu çocuğu sekiz yaşındayken, araba boyacısına çırak vermişler.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3769, 'çırpı gibi', 'çok ince, çok zayıf (kol ve bacak).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3770, 'çırpı vurmak', 'boyaya batırılmış ipin gerilip çabucak çırpılmasıyla yüzeylere çizgi çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3771, 'çırpıya getirmek', 'bir sıra veya çizgi üzerine getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3772, 'çıt çıkmamak', 'en hafif bir ses bile çıkmamak: ''Bir müddet hiçbirisi kımıldamadı ve çıt çıkmadı, sonra bir hıçkırık duyuldu.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3773, 'çıt (çıtını) çıkarmamak', '1) ses çıkarmamak: ''İşte bak, hücre kapısını çıt çıkarmadan araladı, yine bir şey diyecek.'' -A. İlhan. 2) hiç konuşmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3774, 'çıtayı yükseltmek', 'hedefi yüksek belirlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3775, 'çıtı çıkmamak', 'hiç konuşmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3776, 'çıyan gibi', 'hain bakışlı (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3777, 'çiçeğe kesmek', 'çiçek açmak: ''Ovalar, dağlar tepeden tırnağa çiçeğe kesmiş, bütün dünya çiçek kokuyordu.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3778, 'çiçeği burnunda, çamuru karnında', 'çok taze (sebze veya meyve).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3779, 'çiçek açmak (vermek)', '1) çiçeklenmek: ''Hiç kurumuş ağaç yeşerir, çiçek açar mı?'' -Ö. Seyfettin. 2) mec. yeniden ortaya çıkmak, görüntü vermek: ''Her biri bir mazinin çiçek açmasıdır.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3780, 'çiçek çıkarmak', 'çiçek hastalığına tutulmak: ''Küçükken çıkardığı çiçek, sabanla tarla sürer gibi çehresinin altını üstüne getirmiş.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3781, 'çiçek gibi', 'temiz, bakımlı, güzel: ''Sen yirmi beşine bile gelmemiş çiçek gibi bir taze duldun.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3782, 'çiçek olmak', 'yaşına, durumuna uymayan aşırı davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3783, 'çift edersen bağlanırsın, bağ edersen eğlenirsin', 'bakımını iyi yaparsan bağın iyi olur, bağla uğraşmak bir eğlencedir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3784, 'çift görmek', 'sarhoş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3785, 'çift ile koyun, gerisi oyun', 'en zor çiftçilik, tarla sürülerek ve koyun beslenerek yapılan çiftçiliktir, diğer işler eğlence gibidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3786, 'çift koşmak', 'hayvanları sabana, pulluğa koşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3787, 'çift sürmek', 'saban, pulluk kullanarak toprağı ekilebilir duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3788, 'çiftçinin karnını yarmışlar, kırk tane ''gelecek yıl'' çıkmış', 'çiftçinin ürünü her yıl bir afete uğrar, o da hep gelecek yıla umut bağlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3789, 'çiftçiye yağmur, yolcuya kurak; cümlenin muradını verecek Hak', 'kullar Tanrıdan kendilerine gerekli olan şeyleri dilerler, bu dilekleri kabul edecek olan Tanrıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3790, 'çifte atmak', '1) at, eşek arka ayakları ile vurmak; 2) iki namlulu av tüfeğini patlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3791, 'çifte çubuğa gitmek', 'ekim ve biçim işleriyle uğraşmak: ''Yatağa düşersen hayvanlara kim bakar nine, çifte çubuğa kim gider?'' -T. Oflazoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3792, 'çifte gelmeyen öküz olsun, işe gitmeyen oğlun', 'herhangi bir biçimde yararlanılabilecek bir malın olması iyidir, çifte gelmeyen öküzü satabilir, iş yapmaktan kaçınan oğlunu eğitebilirsin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3793, 'çifte koşmak', 'çift koşmak: ''Harmanı biz dövelim, öküzleri biz çifte koşalım, tarlayı biz sürelim, siz yukarıda aşık atın.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3794, 'çifte vurmak', 'çiftelemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3795, 'çifte yemek', 'hayvanın çiftesine maruz kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3796, 'çifti bozmak', 'çiftçilik yapmaktan vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3797, 'çiğ düşmek', 'hoş karşılanmamak, kaba ve yersiz bulunmak: ''Onların da belki merhume ve öksüzler hakkında söylenilecek bir fikirleri olurdu. Fakat şimdilik ne deseler çiğ düşecekti.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3798, 'çiğ kaçmak', 'yersiz, yakışıksız olmak: Onun o sözü pek çiğ kaçtı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3799, 'çiğ süt emmiş', 'iyiliğini gördüğü insana kötülük yapan kişiler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3800, 'çiğ yemedim ki karnım ağrısın', 'suç işlemedim ki korkayım.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3801, 'çiğden vermek', ' ask. yiyecek karşılığını para olarak ödemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3802, 'çiğlik etmek', 'ters veya yersiz bir davranışta bulunmak: ''Etrafını saranla, çiğlik ettin, adam sana vereceğini vermiş, daha ne versin, dediler.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3803, 'çiğnemeden yutulmaz', 'en kolay bir iş dahi emek gerektirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3804, 'çil yavrusu gibi dağılmak', 'toplu olarak bulunan insanların her biri bir yana dağılmak: ''Çocuklar çil yavrusu gibi dağıldılar, biz de baştaki boş çadıra gittik.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3805, 'çile çekmek', 'büyük sıkıntı ve üzüntü içinde yaşamak: ''Âşıkın olmaz mı çile çekmesi / Çilenin olmaz mı boyun bükmesi'' -Seyrani.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3806, 'çile çıkarmak (doldurmak)', 'sıkıntılı bir işin veya bir durumun sona ermesini beklemek: ''Yirmi beş senedir Beykoz''daki o tekke gibi evde çile dolduruyorum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3807, 'çileden çıkmak', '1) olup bitenler karşısında sabrı ve dayanıklılığı kalmayıp taşkınlık göstermek: ''Ben ötede beride tanıdığım yaşlı başlı Fransızlarla böyle konuştukça kardeşim çileden çıkıyordu.'' -B. R. Eyuboğlu. 2) çile süresini bitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3808, 'çilesi dolmak', '1) esk. derviş ve tarikat ehli, sadece dua ve ibadetle geçirmeleri gereken süreyi tamamlayarak çileden çıkmak; 2) üzücü ve sıkıntılı bir durumdan kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3809, 'çileye girmek', 'dervişlerin kırk gün süre ile kendilerine uyguladıkları zorlu ve perhizli döneme girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3810, 'çimdik atmak (basmak)', 'çimdiklemek: ''Kadınlar halay çeken işçi kadınları göstere göstere gülüşüp birbirlerine çimdik attı.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3811, 'çiriş gibi', 'yapışkan ve acı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3812, 'çirkefe taş atmak (çirkefi üzerine sıçratmak)', 'edepsiz bir kimsenin tepkisine yol açacak bir davranışta bulunmak: ''Çirkefe taş atma, hikmetini mırıldanarak kaçar gibi uzaklaştı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3813, 'çirkin kaçmak', 'hoş olmayan bir durum olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3814, 'çişi gelmek', 'işeyeceği gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3815, 'çitlembik gibi', 'ufak tefek, esmer ve sevimli.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3816, 'çivi çıkar ama yeri kalır', 'gönül yarası kapansa da unutulmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3817, 'çivi çiviyi söker', 'güçlü bir şey, kendisi güçlü olan başka bir şeyle veya durumla etkisiz bırakılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3818, 'çivi gibi', '1) çok sağlam ve çevik (kimse); 2) çok soğuk: ''Suyu çivi gibi tutan toprak testiyi çarpıp kırmıştı bir seferinde.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3819, 'çivi kesmek', ' tkz. çok üşümek: ''Ayağının çivi kestiğini ancak o zaman fark etti.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3820, 'çivi kırmak', 'ayakkabıların içinden çıkan çivi uçlarını bir aletle kesip raspa ile eğeleyerek köselenin içine gömmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3821, 'çivi sokmak (sürmek)', 'bir işin olmasında engel, güçlük çıkarmak: ''Bakanlıktan biri bir çivi sürer diye korkuyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3822, 'çizgi çekmek', '1) bir noktayı hat biçiminde çeşitli yönde uzatmak: ''Yerlerinden kalkıp duvara bir çizgi çekmişler.'' -N. F. Kısakürek. 2) mec. bitirmek, sona erdirmek: ''Tüm Müslümanlar aralarındaki kızgınlıklara, kinlere, o gün bir çizgi çekeceklerdi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3823, 'çizgisinden sapmamak', 'görüşlerinden vazgeçmemek, kararlı davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3824, 'çizmeden yukarı çıkmak', 'bilmediği, aklının ermediği, yetkisi dışındaki bir işe karışmak: ''Daha çoğunu istemeye kalkarsa iş değişir o zaman; buna çizmeden yukarı çıkmak denir, herkes haddini bilmeli.'' -M. C. Anday.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3825, 'çizmeleri çekmek', 'bir işe girişmek: ''Yaptığım işe hâlâ şaşmaktan ve inanamamaktan vazgeçemediğim hâlde çizmeleri çekmiştim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3826, 'çizmeyi aşmak', 'bilmediği, aklının ermediği, yetkisi dışındaki bir işe karışmak: ''Politika üstüne fikir yürütmek, çizmeyi aşmaktı.'' -E. Atasü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3827, 'çoban aldı bağa gitti, kurt aldı dağa gitti', 'malını, varlığını başkaları kullandı, kendisine bir şey kalmadı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3828, 'çoban armağanı çam sakızı', 'verilen bir armağanın sunulduğu kimseye değerine uygun olmadığını ve verenin gücünün ancak buna yettiğini özür yollu anlatmak için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3829, 'çoban kulübesinde padişah rüyası görmek', 'içinde bulunduğu duruma uygun düşmeyen düşler kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3830, 'çobana verme kızı, ya koyun güttürür ya kuzu', 'nazik bir işi, o işin inceliğini anlamayan bir kimseye yaptırma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3831, 'çobanın gönlü olursa (olunca) tekeden yağ (süt, köremez) çıkarır', 'kişi istediğinde olmayacak gibi görünen işlere çözüm yolu bulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3832, 'çobanın yağı çok olursa çarığına sürer', 'varlıklı ama akılsız ve hesapsız kişi malını gereksiz yerlere harcar, telef eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3833, 'çobansız koyunu kurt kapar', 'yöneticisi, koruyucusu olmayan kişiyi ve topluluğu düşman ezer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3834, 'çocuğa iş, ardına sen düş', 'çocuk kendisine ısmarlanan işi yapamayacağından işi buyuran kimsenin onun arkasından gitmesi gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3835, 'çocuğa iş buyuran, ardınca kendi gider', 'çocuk kendisine ısmarlanan işi yapamayacağından işi buyuran kimsenin onun arkasından gitmesi gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3836, 'çocuğu işe sal, ardınca sen var', 'çocuk kendisine ısmarlanan işi yapamayacağından işi buyuran kimsenin onun arkasından gitmesi gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3837, '(çocuğu) süt çalmak', 'bozuk süt, çocuğu hasta etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3838, 'çocuğun bulunduğu yerde dedikodu olmaz', 'küçük çocuğun bulunduğu yerde herkes çocukla uğraşmaktan dedikodu yapmaya fırsat bulamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3839, 'çocuğun yediği helal, giydiği haram', 'çocuğun iyi beslenmesi için ne kadar para harcansa yerindedir ancak hor kullanacağı ve kısa zamanda da küçüleceği için pahalı giysiler giydirilmesi doğru değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3840, 'çocuk aldırmak', ' tıp kadın karnındaki bebeği hekime ameliyatla aldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3841, '(çocuk) boya çekmek', 'boyca uzamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3842, 'çocuk (çocuğunu) düşürmek', 'gebe kadın çocuğunu vaktinden önce ve ölü olarak doğurmak, düşük yapmak: ''Beklenmedik acı olaydan ötürü Ayşe de çocuğunu düşürdü, ağır hastalandı.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3843, 'çocuk dünyaya getirmek', 'çocuk doğurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3844, 'çocuk düşe kalka büyür', 'çocuk yürümeye başladığı sırada sık sık düşer, anne baba bu duruma üzülmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3845, 'çocuk gibi', '1) yetenekleri gelişmemiş, çocuk kalmış: Çocuk gibi adam. 2) kolay kanar, kolay inanır: Sen de çocuk gibisin, o adamın sözüne inanılır mı?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3846, 'çocuk gibi sevinmek', 'çok sevinmek: ''Güzel sözler duyduğunda çocuk gibi sevinir.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3847, 'çocuk kalmak', 'büyümüş olmasına rağmen çocukça düşünceler taşıyıp çocuk gibi davranmak: ''Araya araya bu oyunu mu buldun? Ayol sen sahiden çocuk kalmışsın.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3848, 'çocuk mu avutuyorsun (kandırıyorsun)', 'söylenenlere inanmadığını belirtmek bir için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3849, 'çocuk olmak', 'çocuklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3850, 'çocuk oyuncağı hâline getirmek', 'yeteneksiz kimselerin karışmasıyla bir işi değerinden düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3851, 'çocuk seversen beşikte, koca seversen döşekte', 'çocuğu kucağına almadan, beşikte yatarken sev; kocana karşı olan sevgini de başkalarının yanında değil, döşekte göster.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3852, 'çocukla çocuk, büyükle büyük olmak', 'içinde bulunulan yere veya çevredeki insanlara uymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3853, 'çocukluğu tutmak', 'çocuksu davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3854, 'çocukluk etmek', '1) çocukça davranışlarda bulunmak; 2) gereği gibi düşünmeden deneyimsizce davranmak: ''Çocukluk etme, Halis, âlemin kulağına gider. Sonra büyük dedikodu olur.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3855, 'çocuktan al haberi', 'bir aile sorunu veya ailece gizli tutulan bir şey, çocukların rastgele söyledikleri bir sözle açığa çıktığında söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3856, 'çoğu gitti, azı kaldı', 'yapılmakta olan işin en önemli, en güç bölümü bitti, az ve önemsiz bölümü kaldı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3857, 'çoğu zarar, azı karar', 'hiçbir zaman aşırıya kaçılmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3858, 'çok baharın otunu yemek', 'hayatı dolu dolu yaşamış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3859, 'çok bilen (söyleyen) çok yanılır', 'çok bildiği için kendine güvenen kişi, bilmediği şeylere de karışır ve bunlarda yanılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3860, 'çok el ya yağmaya ya yolmaya', 'çok kimsenin katılmasıyla yağma ve yolmadan başka bir iş başarılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3861, 'çok gezen çok bilir', 'çok gezen, çok yer gören çok şey öğrenir; çok yaşayan, çok okuyan onun bildiklerini bilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3862, 'çok gezen tavuk ayağında pislik getirir', 'gezip dolaştığı yerlerde kötü şeyler de bulunan kimse, kötü alışkanlıklar ve zararlı bilgiler elde ederek yerine döner.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3863, 'çok havlayan köpek ısırmaz', 'karşısındakini bağırıp çağırmakla korkutmaya çalışan kimse eylemli bir saldırıda bulunmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3864, 'çok koşan çabuk (çok, tez) yorulur', 'sürekli çalışabilmek ve sonuç elde edebilmek için harcanan çabanın yormayacak ölçüde olması gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3865, 'çok naz âşık usandırır', 'başka şeylerde olduğu gibi nazlanmada da aşırı gidilmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3866, 'çok söyleme arsız edersin, aç bırakma hırsız edersin', 'yönetimi altında bulunan kimselere sık sık müdahale edenler bekledikleri verimi alamadıkları gibi onları da arsız ederler; yiyecek ve para bakımından da sıkıntıya düşürenler onları hırsızlığa itmiş olurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3867, 'çok söz (laf) yalansız, çok para (mal) haramsız olmaz', 'bol kazancın içinde yasa dışı elde edilmiş para bulunduğu gibi çok konuşanın sözleri arasında da mutlaka yalan bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3868, 'çok şey!', 'şaşma anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3869, 'çok yaşayan (okuyan) bilmez, çok gezen bilir', 'çok gezen, çok yer gören çok şey öğrenir; çok yaşayan, çok okuyan onun bildiklerini bilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3870, 'çokbilmişlik taslamak', 'kendini çokbilmiş gibi göstermek: ''Zekâ gösterisine yeltenmemiş, çokbilmişlik taslamamıştı.'' -K. Tahir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3871, 'çoluk çocuğa karışmak', 'evlenip çocukları dünyaya gelmek: ''İsa Bey, burada zengin bir eşraf kızıyla evlenerek çoluk çocuğa karıştığı için 24 Meşrutiyeti''nde İstanbul''a dönmemiştir.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3872, 'çoluk çocuk elinde kalmak', 'deneyimsiz, çok genç kişilerin eline geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3873, 'çomak sokmak (koymak)', 'bir işi aksatan, engelleyen davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3874, 'çorap kaçmak', 'çorabın bir teli kopup örgüsü uzunlamasına açılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3875, 'çorap söküğü gibi gitmek (gelmek)', 'başlayan bir iş veya birbirine bağlı birçok iş arka arkaya ve kolayca sürüp gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3876, 'çorba gibi', '1) pek sulu (yemek); 2) karmakarışık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3877, 'çorba içmeye çağırmak', 'yemek yemeye çağırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3878, 'çorbada tuzu (maydanozu) bulunmak', 'bir iş veya görevde az da olsa emeği geçmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3879, 'çorbaya sinek düşmek', 'işin tadı kaçmak, yeteri kadar iyi ve güzel olmadığı anlaşılmak: ''Bu canım yolların, bu sevimli yapıların harcına dünyamızdan nasibini almamış yüz binlerce insanın alın teri karıştığı akla gelince çorbaya sinek düşüyor.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3880, 'çöle dönmek', 'harap olmak, bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3881, 'çömlek patlatmak', 'çocuk oyunlarında ebeyi yanıltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3882, 'çömlekçi suyu saksıdan içer', 'kişi, geçimini sanatı ile sağlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3883, 'çöp gibi', 'çok ince, zayıf.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3884, 'çöpe dönmek', 'çok zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3885, 'çöpe gitmek', 'yapılan iş boşa gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3886, 'çöreğin büyüğü, hamurun (unun) çoğundan olur', 'verimin bol olması, kullanılan malzemenin bol olmasına bağlıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3887, 'çörten gibi', 'oluk gibi, çok gür bir biçimde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3888, 'çözümsüz kalmak', 'çözümü olmamak, çözüm bulunamamak: ''Yalın bir söyleyişin altında hemen hep çözümsüz kalan birtakım belirtkeler göze çarpıyor.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3889, 'çubuğunu tüttürmek', 'üzüntüsüz, kaygısız yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3890, 'çukura düşmek', 'kötü ve uygunsuz bir duruma girmek: ''Kendi ayağınız ve büyük aklınızla gidip düştüğünüz çukurdan bize ne?'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3891, 'çul içinde arslan yatar', 'bir kimsenin değeri, kılık kıyafeti ile değil kişiliğindeki cevherle ölçülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3892, 'çulu düzmek (düzeltmek)', '1) giyimi kuşamı yenilemek: ''Muharrem, çulu epey düzmüş vaziyetteydi.'' -S. F. Abasıyanık. 2) maddi durumu iyileşmek: ''Aranızdan ayrılalı bir ay var mı? Belki yok bile. Çulu derhâl düzelttim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3893, 'çuval gibi', '1) kaba ve seyrek (kumaş); 2) bol ve ütüsüz (giysi).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3894, 'çuvaldız yurdusu (gözü) kadar yerden deve denli soğuk girer', '1) soğuk küçücük bir delikten bile girebilir ve rahatsızlık verebilir; 2) bazı küçük olaylar büyük olaylara yol açabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3895, 'çuvalla para kazanmak', 'aşırı kazanç sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3896, 'çürüğe çıkarmak', 'bir nesneyi işe yaramayacak durumda olmasından dolayı kullanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3897, 'çürük (çürüğe) çıkmak', '1) birinin sağlam olmadığı anlaşılmak; 2) ask. sağlık durumunun elverişsiz olması yüzünden askerlik ödevinden bağışlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3898, 'çürük tahta çivi (mıh) tutmaz', 'aslında yaramaz olan veya sonradan o duruma getirilen şeyi, ne kadar uğraşsanız da işe yarar duruma getiremezsiniz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3899, 'çürük tahtaya basmak', 'tedbirsizlik edip sonu tehlikeli olabilecek bir işe girişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3900, 'dadılık etmek', '1) çocuk bakıcılığı ile uğraşmak: ''Elli yaşlarında bir adam, konaklarda dadılık eden bir kadının erkeğidir.'' -M. Ş. Esendal. 2) mec. üzerine sorumluluk almak, göz kulak olmak, sahip çıkmak, sahiplenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3901, 'dağ ardında olsun da, yer altında olmasın', 'yaşasın da uzakta olsun.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3902, 'dağ başına harman yapma, savurursun yel için; sel önüne değirmen yapma, öğütürsün sel için', 'yapacağın iyi bir işi, sonunu hesaplamadan yapma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3903, 'dağ başına kış gelir, insanın başına iş gelir', 'dağ başında kışın fırtına eksik olmadığı gibi kişinin yaşamında da yıpratıcı olaylar eksik olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3904, 'dağ başından duman eksik olmaz', 'büyük adamların, büyük iş yapanların her zaman üzüntüleri, sıkıntıları vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3905, 'dağ dağ üstüne olur, ev ev üstüne olmaz', 'aynı evde oturan iki aile arasında er geç birtakım anlaşmazlıklar çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3906, 'dağ dağa kavuşmaz, insan insana kavuşur', 'ne kadar uzak düşmüş olurlarsa olsunlar, insanlar günün birinde birbirleriyle karşılaşabilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3907, 'dağ (dağlar) gibi (kadar)', '1) çok büyük, çok iri, çok güçlü: ''Bizim de kalbimizi kımıldatır yerinden / Toprağa diz vuruşu dağ gibi bir zeybeğin'' -F. N. Çamlıbel. 2) pek çok: ''Önüne bırakılmış dosyalar dağ gibiydi.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3908, 'dağ (dağları) devirmek', 'çok zor işleri başarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3909, 'dağ doğura doğura bir fare doğurmuş', 'büyük şeyler beklenen bir işten önemsiz bir sonuç alındığında söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3910, 'dağ ne kadar yüce olsa yol üstünden aşar', 'yenilmesi imkânsız gibi görünen zorlukların da üstesinden gelinir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3911, 'dağ, ovanın süt anasıdır', 'ticareti besleyen, üretim kaynaklarıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3912, 'dağ yürümezse abdal yürür', 'büyüklük taslayan birinde bitecek bir işimiz varsa biz onun ayağına gidip işimizi görmeliyiz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3913, 'dağa çıkmak', '1) eşkıyalık etmek; 2) hükûmete karşı gelmek için dağlara çekilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3914, 'dağa kaldırmak', 'birini, herhangi bir amaçla, zorla dağa veya ıssız bir yere götürüp orada tutmak: ''Yalnız Efe''den kimsenin şikâyeti yokmuş. Ne kimseyi dağa kaldırırmış ne de fidye istermiş.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3915, 'dağarcığına atmak', 'bir bilgiyi eski bilgilerine katmak, zihnine yerleştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3916, 'dağarcığındakini çıkarmak', 'hazırladığı bir sözü söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3917, 'dağarcıkta bir şey kalmamak', 'her şeyi tüketmek, bitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3918, 'dağda bağın var, yüreğinde dağın var', 'malı, mülkü veya evladı olanlar kaygı ve tasadan uzak olamazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3919, 'dağda büyümüş', 'kaba ve görgüsüz kimse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3920, 'dağda gez belde gez, insafı elden bırakma', 'eşkıya dahi olsan insafı elden bırakma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3921, 'dağdan gelip bağdakini kovmak', 'sonradan geldiği bir yerde, kendinden önce gelen kişinin yerini almaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3922, 'dağlara düşmek', 'büyük bir üzüntü dolayısıyla insanlardan kaçıp ıssız yerlerde yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3923, 'dağlara taşlara', 'kötü bir durumdan söz edilirken ''hepimizden ırak olsun'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3924, 'dağların misafir aldığı mevsim', ' şaka yaz mevsimi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3925, 'dağların şenliği (gelin anası)', ' şaka kaba, anlayışsız kimse: ''Hay kör olası, dağların şenliği, bak şimdi de hanımın saksısını devirdi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3926, 'daha iyisi can sağlığı', 'bulunabileceklerin en iyisi oldu.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3927, 'daha neler!', '''hiç öyle şey olur mu?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3928, 'dahası var', 'bir konuda bilinmesi gereken başka şeyler de olduğunu anlatmak için kullanılan bir söz: ''Dahası var fakat dahasını siz merak edip arayın, bulun.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3929, 'dakikası dakikasına uymamak', 'her an başka bir ruh durumu göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3930, 'daktilo etmek', 'yazı makinesiyle yazmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3931, 'daktiloya çekmek', 'yazı makinesiyle yazmak: ''İmtihanlarına çalışırken destek veriyor, tezlerini daktiloya çekiyordu.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3932, 'dal budak salmak', '1) karmaşık bir biçimde yayılıp genişlemek: ''Samimiyetimizin her köşesinde heybetli çınarlar gibi dal budak salmıştı.'' -O. S. Orhon. 2) soy yönünden genişleyip yayılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3933, 'dal gibi', 'ince uzun yapılı: ''Dal gibi bir vücut üzerinde dev gibi bir baş!'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3934, 'dal gibi kalmak', 'vücudu çok zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3935, 'dal sürmek', 'yayılmak, kaplamak: ''Yüreğinde onmaz bir karıncalanma vardı; onmaz bir kıpırtı dal sürüyordu, durmadan filizleniyordu.'' -B. Günel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3936, 'dal vermek', 'dayanmak, yaslanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3937, 'dala çıka', 'büyük güçlüklerle.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3938, 'dalak kestirmek', ' hlk. sıtmadan büyümüş dalağı eski bir yöntemle tedavi ettirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3939, 'dalalete düşmek', 'doğru yoldan ayrılmak, sapkınlık etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3940, 'dalavere çevirmek (dalaveresini döndürmek)', 'yalan dolanla gizlice kötü iş görmek: ''Beyefendi dalaveresini döndüreceği yerleri adamlarından hiç kimseye söylemedi.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3941, 'daldan dala konmak', 'sık sık iş, konu veya düşünce değiştirmek: ''Çalı kuşu gibi daldan dala konan kararsız bir çocuktu.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3942, 'dalga geçmek', ' argo 1) üzerinde durulması gereken işle ilgilenmeyerek başka şeyler düşünmek veya yapmak: ''İki delikanlı dalga geçip otururlarken kapı yeniden sürüldü.'' -M. Ş. Esendal. 2) eğlenmek, alay etmek; 3) geçici sevgi ilişkisi kurmak, gönül eğlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3943, 'dalga saymak', '1) boş ve aylak durmak; 2) yersiz ve gereksiz şeylerle uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3944, 'dalgalanmaya bırakmak', '1) ekon. paranın gerçek değerini bulması için girişimde bulunmadan beklemek; 2) mec. bir konu için girişimde bulunmadan beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3945, 'dalgalanmaya bırakmak', ' argo dalgınlığından yararlanarak birini kandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3946, 'dalgasına taş atmak', ' argo işini bozmak, keyfini kaçırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3947, 'dalgasını taşlamak', 'birinin işini bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3948, 'dalgaya düşmek (gelmek)', ' argo yanılmak, dalgınlıkla unutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3949, 'dalgayı başa almak', 'gemi veya sandalın başını dalgaların geldiği yöne çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3950, 'dalgınlığına gelmek', 'dalgınlık dolayısıyla fark edememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3951, 'dalgınlığına getirmek', 'birinin dalgınlığından yararlanıp kendi isteğini gerçekleştirmek: ''Bir dalgınlığına getirip dışarı kaçıyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3952, 'dalına basmak', 'hoşlanmadığı şeyleri yaparak birini kızdırmak: ''Efendi aksi mi? -Pek dalına basmazsan kuzu gibi bir adamdır.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3953, 'dalına binmek', 'bir kimseye bir iş yaptırmak için asılmak, musallat olmak, sıkıştırmak: ''Orada başefendinin de dalına şöyle bir biniliyordu.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3954, 'dalıp çıkmak', '1) deniz, göl vb. yerlerde suyun içinde kaybolup yeniden görünmek; 2) deniz, göl vb. içinde kısa süre kalmak: Biz bir dalıp çıkacağız. 3) birçok yere girmek: Nerede bulunduğu belli olmaz, her yere dalıp çıkar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3955, 'dalıp gitmek', 'bir düşünce veya hayal ile bulunduğu ortamdan uzaklaşmak: ''İlk geldiğimizde hava değiştirmekten olmalı, dalıp gidiyorduk.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3956, 'dallanıp budaklanmak', 'bir iş, bir sorun büyüyerek karışık duruma gelmek: ''İş iyice dallanıp budaklanmadan amcayla konuşsam mı acaba?'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3957, 'dalları basmak', 'ağaçta dalları eğecek kadar çok meyve olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3958, 'dalmasını bilmeyen ördek, kıçından dalar', 'ne yaptığını bilmeyen kişi işi tersinden yürütmeye kalkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3959, 'dalyan gibi', 'boylu boslu: ''Gidip de gelmeyen kocaları, yetişmiş dalyan gibi evlatları...'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3960, 'dam dolusu tükenmiş, damlayan tükenmemiş', 'az da olsa sürekli gelir, ne denli çok da olsa, giderek tükenen hazırı yemekten daha iyidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3961, 'dam üstünde saksağan, vur beline kazmayı', 'yersiz ve saçma sözler karşısında söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3962, 'dam yandı, içindeki sıçan da (birlikte) yandı', 'bu, büyük bir kayıp ancak eskiden yol açtığı rahatsızlık da sona erdi.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3963, 'dama çıkmak', 'cinsel istekleri artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3964, 'dama demek', '1) gücü kalmayarak bir işi daha ileri götüremeyecek duruma gelmek: ''Konya''ya döndüğüm vakit benim motor, dama Erol Efendi, dedi. Kıtipiyoz bir tamirhaneye verdim.'' -A. Gündüz. 2) tükenmek: ''Konyak şişesi dama dedi.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3965, 'dama taşı gibi oynatmak', 'birini sık sık bir yerden bir yere göndermek veya atamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3966, 'damardan girmek', ' argo karşısındaki kişiyi en fazla etkileyebilecek noktadan konuya girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3967, 'damarı (damarları) kabarmak', 'bir huy veya duygu güçlü bir biçimde ortaya çıkmak: ''Birden nasihat damarlarının kabardığını duydu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3968, 'damarı kurusun!', 'birinin huysuzluğuna öfkelenildiğinde söylenen bir ilenme sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3969, 'damarı tutmak', 'kötü huyu, aksiliği depreşmek, inatlaşmak: ''Tutarsa onun bir damarı, yıkar adamın başına çadırı.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3970, 'damarına basmak', 'birini, duyarlı olduğu bir konuda kızdırmak: ''Ne olur biraz uslu otursanız, şu adamların da damarına basmasanız, olmaz mı?'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3971, 'damarına çekmek', 'soyunun özelliklerini taşımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3972, 'damarına (damarlarına) işlemek', 'kötü bir huy, vazgeçilmez bir biçimde yerleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3973, 'damarına girmek', 'birinin hoşlanacağı şeyler yaparak kendisini ona sevdirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3974, 'damat girmek', 'aileye güveyi olarak katılmak: ''Öyle bir aileye damat girmek isterim.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3975, 'damdan çardağa atlamak', 'hiçbir mantık bağı kurmadan konudan konuya geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3976, 'damdan düşen damdan düşenin hâlinden (hâlini) bilir', 'iyi bir durumdayken kötü duruma düşen kimse, başına aynı durum gelen kimsenin derdini iyi anlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3977, 'damdan düşer gibi', 'birdenbire ve yersiz olarak: ''Damdan düşer gibi birdenbire söyleyecek, açacak olursam itiraz eder.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3978, 'damdan düşercesine', 'birdenbire ve yersiz olarak: ''Baba çocuğunun bu damdan düşercesine cevabına kızdı.'' -İ. H. Baltacıoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3979, 'damga vurmak', '1) damgalamak: ''Nuri Usta, sanki çırılçıplakmış da derisine kızgın demirle damga vuruluyormuş gibi irkildi.'' -N. Hikmet. 2) mec. iz bırakmak: ''Belli semtler, yüzyıllar boyu, oraların sakinlerine belli bir damga vurmuş gibidirler.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3980, 'damga yemek', 'biri kötü bir yargıya veya nitelenmeye uğramak: ''Şiirlerini bir araya toplayan bir kitap yüzünden kızıl bir damga yemiş.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3981, 'damgasını vurmak', 'biri hakkında kötü bir yargıya varmak: ''Fakat gel gör ki insana aşüfte yahut hırsız damgasını vurmak için bu kâfi değildir.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3982, 'damlaya damlaya göl olur', 'azar azar olagelen şeyler birikerek önemli bir niceliğe ulaşacağı için küçümsenmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3983, 'damlaya uğramak', 'yüreğine inmek, felç olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3984, 'dan dun konuşmak (etmek)', 'yerli yersiz, ileri geri konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3985, 'danalar gibi bağırmak (böğürmek)', 'çok kuvvetle bağırmak, haykırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3986, 'dananın kuyruğu kopmak', 'beklenen veya korkulan sonuç gerçekleşmek: ''İstediğimiz parayı vermezse işte o zaman dananın kuyruğu kopar.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3987, 'danışan dağı aşmış, danışmayan düz yolda şaşmış', 'bilmediği şeyi bir bilene soran, en zor işlerin bile üstesinden gelir; sormayan ise güçlükler içinde yuvarlanır gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3988, 'danışan dağı aşmış, danışmayanın yolu şaşmış', 'bilmediği şeyi bir bilene soran, en zor işlerin bile üstesinden gelir; sormayan ise güçlükler içinde yuvarlanır gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3989, 'daniskasını yapmak', 'bir işi her yolu deneyerek gerçekleştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3990, 'dar gelmek', 'sıkıntı ve huzursuzluk vermek: ''Acaba bu içinde yaşadığımız hava neden bu kadar dar geliyor?'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3991, 'dar kaçmak', 'istemediği bir çevreden kendini dışarı atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3992, 'dara boğmak', 'birinin güç durumundan yararlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3993, 'dara düşmek', 'para sıkıntısına düşmek: ''Madam onu çocuğu gibi seviyordu. Dara düştüğü günlerde hizmetini hiç aksatmadan para mara istemedi.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3994, 'dara gelmek', '1) aceleye gelmek; 2) mecbur olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3995, 'dara getirmek', 'aceleye getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3996, 'darağacına çekmek', 'idam cezası alan bir kimseyi asmak: ''Darağacına çekilmiş bir adam gibi göğsüm, nefes borularım birdenbire tıkanıverdi.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3997, 'darasını almak', 'içine bir şey konulacak kabın ağırlığını tartmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3998, 'darasını düşmek', 'kabın ağırlığını hesaba katmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (3999, 'daraya atmak (çıkarmak)', 'değer vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4000, 'darbe almak', 'kötü bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4001, 'darbe (darbeyi) yemek', '1) gücü sarsılmak: ''Seniha''nın kaçışı üzerine en müthiş darbeyi yiyen kalp, Celis''in kalbi oldu.'' -Y. K. Karaosmanoğlu. 2) kötü, olumsuz bir duruma maruz kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4002, 'darbe vurmak (indirmek)', 'iyi olan bir durumu kötüye dönüştürmek: ''Abdülhamit, Midhat Paşa''nın katli ile fikir denilen kuvvete ağır bir darbe vurmuş.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4003, 'darda bulunmak', 'bir şeyin sıkıntısını çekmek: ''En darda bulunduğu zamanlarda bile ihtiyacı olanlar ondan bir şey isteyebilirlerdi.'' -İ. A. Gövsa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4004, 'darda kalmak', '1) paraca sıkıntı içine girmek; 2) zor duruma düşmek: ''Lala da pek darda kaldığı zaman kabahati Gülsüm''ün üstüne yıkıyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4005, 'dargın durmak', 'küskün durumda olmak: ''Bu olaydan sonra benimle aylarca dargın durdu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4006, 'darı unundan baklava, incir ağacından oklava olmaz', 'kötü gereçle iyi iş görülemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4007, 'darısı ... başına (darısı başına)', 'bir başarı, bir mutluluk başkası için istendiğinde söylenen bir söz: ''Geçenlerde, darısı dostlar başına, kızını everdi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4008, 'darmadağın etmek', '1) dağıtmak, karıştırmak; 2) mec. dayak atıp iyice dövmek: ''Köy kahvesinden yetişen birkaç delikanlı bu haşarıları darmadağın etmişti.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4009, 'darmadağın olmak', '1) dağınık ve karışık duruma gelmek: ''Gözlerinin sürmesi akmış, saçları darmadağın olmuştu.'' -Y. K. Karaosmanoğlu. 2) mec. kötü bir biçimde dövülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4010, 'darmaduman etmek', 'karmakarışık bir duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4011, 'darmaduman olmak', 'karmakarışık bir duruma gelmek: ''Eskiler, genç hürriyet jandarmalarının aman vermez takipleri altında darmaduman olmuşlardı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4012, 'dava etmek (açmak)', 'hukuksal korunmanın bir hüküm ile sağlanması için yargı organlarına başvurmak: ''Dayak yiyen kadın kimi, kime dava edecek?'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4013, 'dava görmek', 'açılan davaları incelemek ve sonuca bağlamak: ''Danıştay, davaları görmek ... ve kanunlarla gösterilen diğer işleri yapmakla görevlidir.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4014, 'davacı olmak', 'dava etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4015, 'davacın kadı olursa yardımcın Allah olsun', 'seni yargılayacak kişi, senden davacı olan kişi ise elbette kendisini haklı çıkaracak ve sana ağır ceza verecektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4016, 'davacısı kadı olanın yardımcısı Allah olsun', 'seni yargılayacak kişi, senden davacı olan kişi ise elbette kendisini haklı çıkaracak ve sana ağır ceza verecektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4017, 'davar gütmek', '1) sürüyü otlatmak, korumak ve gerektiğinde süt sağmak: ''Davar güden, tarlaya bakan, odun kesmeye giden hep benim.'' -Y. K. Karaosmanoğlu. 2) argo işe yaramayan, aptal veya acemi insanları kendi çıkarları doğrultusunda kullanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4018, 'davaya bakmak', 'açılan davayı incelemek, araştırmak ve sonuçlandırmak: ''Hiçbir mahkeme görev ve yetkisi içindeki davaya bakmaktan kaçınamaz.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4019, '(davayı) nakzen görmek', ' huk. Yargıtay tarafından bozulan bir karar üzerine bozma sebeplerini de göz önünde tutarak davaya yeniden bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4020, '(davayı) nakzen iade etmek', ' huk. bir yargı kararını, yargılama yöntemine ilişkin hükümler bakımından yerinde görmeyip bozarak hükmü veren mahkemeye geri göndermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4021, 'davet etmek', '1) çağırmak: ''Bir bakanmışım gibi beni kürsüye davet etti.'' -Y. K. Karaosmanoğlu. 2) birinin bir şeye uymasını istemek: ''Kimin kimi istifaya davet edeceğini pek yakında gösterecekti.'' -R. N. Güntekin. 3) mec. yol açmak: Hastalığı davet ediyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4022, 'davete icabet etmek', 'çağrılı olduğu yere gitmek: ''Fakat kâbus içinde bunalmış bir kimse gibi bir türlü bu davete icabet edemez.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4023, 'davetsiz gelen döşeksiz oturur', 'çağrılmadan bir yere giden kimse iyi bir ağırlanma beklememelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4024, 'davetsiz yere kedilerle köpekler gider', 'bir yere çağrılmadan gitmek, kişiyi aşağılatan bir davranıştır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4025, 'davul birinin boynunda, tokmak bir başkasının elinde', 'sorumluluk birinde olmasına karşın bir başkasının sözü geçiyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4026, 'davul çalmak (dövmek)', '1) davula vurarak ses çıkarmak; 2) mec. bir şeyi herkesin haber alabileceği biçimde ortalığa yaymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4027, 'davul çalsan işitmez', '1) sağır; 2) uykusu çok ağır, derin uykuda.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4028, 'davul dengi dengine diye çalar', 'evlenecek kimselerin birbirlerine denk olması gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4029, 'davul gibi', 'şiş ve gergin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4030, 'davulu biz çaldık, parsayı başkası topladı', 'biz çalıştık, uğraştık, başkası yararlandı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4031, 'davulun sesi uzaktan hoş gelir', 'işin içinde olmayanlar o işi kolay veya kârlı sanırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4032, 'dayağa idmanlı olmak', 'dayak yemeye alışmış olmak: ''Bereket versin ki boksör, dayağa idmanlıydı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4033, 'dayak atmak', 'dövmek, sopa ile dövmek: ''Gece tenha bir sokakta parasını aldığı bir adama dayak atıyormuş.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4034, 'dayak cennetten çıkmıştır', 'dayağın yola getirici bir etkisi vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4035, 'dayak yemek', 'dövülmek, sopa ile dövülmek: ''Ertesi gün dayak yemiş gibi yorgun uyandım.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4036, 'dayayıp döşemek', 'evi, odayı mobilya vb. ile döşemek: ''Karyolalar, koltuklar, kanepelerle dayayıp döşemek lazım.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4037, 'dazlayan daza düşer, kel başlı kıza düşer', 'evleneceği kişiyi seçmekte çok titizlik gösteren kimse, çoğu kez istemediği, beğenmediği bir kişiye düşer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4038, 'dede (dedesi) koruk yer, torununun dişi kamaşır', 'eskilerin yaptığı yanlış işlerden daha sonrakiler de zarar görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4039, 'dediği çıkmak', 'dediği şey gerçekleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4040, 'dediğim dedik, öttürdüğüm (çaldığım) düdük', 'bir insanın sözünde direndiğini anlatmak için söylenen bir tekerleme: ''Adamın dediği dedik, çaldığı düdüktür. Böyle olduğu için de her istediğini yapabilen toy ve şımarık bir çocuğu hatırlatır.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4041, 'dediğinden (dışarı) çıkmak', 'sözünü dinlememek: Dediğimden dışarı çıkarsa kendi bilir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4042, 'dedikodu çıkarmak', 'birisi hakkında dedikodu ortaya atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4043, 'dedikodu sermayesi olmak', 'konuşma, sohbet konusu durumuna gelmek: ''Munise''nin süsü günlerce Zeyniler köyüne dedikodu sermayesi olmuş.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4044, 'defibela kabîlinden', 'bir belayı savarcasına.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4045, 'defter açmak', '1) para yardımı veya gönüllü toplamaya girişmek; 2) hesap açmak, banka cüzdanı vermek; 3) mec. bir şeye yeniden başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4046, 'defter tutmak', ' tic. işlem veya hesapları düzenli olarak bir deftere geçirmek: ''Bir defterler tutardı, bayılırdık.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4047, 'defteri dürülmek', '1) ölmek; 2) öldürülmek; 3) görevine son verilerek bir yerden uzaklaştırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4048, 'defteri kapamak (kapatmak)', '1) söz konusu işi artık yapmaz olmak; 2) bir şeyle ilgiyi kesmek: ''Saçmalama, ben o defteri kapatalı bir yılı geçti.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4049, 'defterinde olmamak', 'sahip bulunmamak, tabiatında bulunmamak: ''Sevmek, inanmak, bağlanmak gibi şeyler defterinde yoktu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4050, 'defterinde yazmamak', 'kitabında yer almamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4051, 'defterini dürmek', '1) öldürmek: ''Bir gün senin defterini dürerler bir eyyam gelir.'' -Yunus Emre. 2) işine son vermek, işten çıkarmak; 3) başarısını kıskanarak yükselmesine engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4052, 'değer biçmek', 'bir şeyin değerini belirtmek, bir şeye değer koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4053, 'değer düşümüne uğramak', 'değersizleşmek: ''Artık tanıyamadığımız bu ışıksız kentte üç ay içinde korkunç bir değer düşümüne uğrayan hayatlarımızı düşünmüştük.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4054, 'değer vermek', 'değerli saymak, önem vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4055, 'değirmen iki taştan, muhabbet iki baştan', 'karı koca gibi, iş ortağı gibi birlikte yaşayacak, birlikte iş görecek kimseler arasında karşılıklı sevgi bulunmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4056, 'değirmen taşının altından diri çıkar', 'en ağır şartlarda bütün güçlükleri yener.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4057, 'değirmenden gelenden poğaça umarlar', 'başka bir yerden gelen kimseden, geldiği yerle ilgili, küçük de olsa bir armağan beklenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4058, 'değirmene gelen nöbet bekler', 'bir şeyden birçok kimse yararlanacaksa herkes geliş sırasıyla işini görmek üzere beklemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4059, 'değirmenin suyu nereden geliyor?', 'bu işin masrafını karşılayacak para nasıl kazanılıyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4060, 'değirmi yurt tutmaya değirmi göt ister', 'bir yerde, bir işte tutunup başarı kazanabilmek için sebatla çalışmak gerektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4061, 'değiş etmek', ' hlk. bir şey verip yerine başka bir şey almak: Buğdayı pirinçle değiş etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4062, 'değme gitsin', '''anlatılması güç, anlatılamaz'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4063, 'değme sarhoşa yıkılana kadar gitsin', 'kendi aklını beğenip başkasını dinlemeyen kimseyi gittiği yanlış yoldan döndürmeye kalkmayın, bırakın cezasını çeksin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4064, 'değnek gibi', 'çok zayıf ve ince.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4065, 'dehşet saçmak', 'ortalığa korku vermek: ''Oyun, okuyanı hiçbir tarih kitabının etkilemeyeceği kadar kuvvetle Fransız İhtilali''nin amansız, dehşet saçan günleriyle karşı karşıya bırakır.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4066, 'dehşete düşürmek', 'çok korkutmak, dehşete kapılmasına sebep olmak: ''Korkunç jestlerle yaptığı kara bulut tasvirleri bizi yeniden dehşete düşürdü.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4067, 'dehşete kapılmak (düşmek)', 'çok korkmak: ''Ev sahibi dehşete kapılmış gibiydi.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4068, 'deke düşmek', 'hileye, oyuna gelmek: ''İlkin deke düştüğümün hiç farkına varmadım.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4069, 'dekolte konuşmak', ' tkz. açık saçık konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4070, 'deli arlanmaz, soyu arlanır', 'densizce, delice iş yapanlar yaptıklarından utanacak durumda değillerdir ama ailesi, yakınları onların davranışlarından üzüntü duyarlar, utanırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4071, 'deli bayrağı açmak', ' şaka âşık olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4072, 'deli çıkmak', '1) çıldırmak; 2) mec. çok sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4073, 'deli dana (danalar) gibi dönmek', 'ne yapacağını bilemeyerek şaşkınca davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4074, 'deli deli akanı bura bura tıkarlar', 'aşırı ve ölçüsüz davrananlara karşı önleyici, sert tedbirler alınır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4075, 'deli deliden hoşlanır, imam ölüden', 'kişi, kendisine benzeyen veya yarar sağlayacağı kimseden hoşlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4076, 'deli deliyi görünce çomağını (değneğini) saklar (gizler)', 'saldırgan kimse, kendisi gibi birine saldırmaktan çekinir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4077, 'deli dostun olacağına akıllı düşmanın olsun', 'akılsız kimse iyi niyetli olsa dahi yaptığı işin ne gibi kötü sonuçlar doğuracağını hesap edemediğinden dostuna bilmeyerek fenalık edebilir, akıllı düşmanın yapacağı kötülükse akıl yoluyla sezilir ve gereken tedbir alınabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4078, 'deli etmek', '1) çılgına çevirmek: ''Muhtarın maksadı bizi meraktan deli etmek.'' -M. İzgü. 2) sinirlendirmek; 3) sağlıklı düşünemeyecek duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4079, 'deli gibi', 'deliye yaraşır davranışta, delicesine: ''Bizimkinin kıza deli gibi âşık olduğu daha ilk bakışta anlaşılıyor.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4080, 'deli ile çıkma yola, başına getirir bela', 'deli, kendisiyle arkadaşlık edenin başına çeşit çeşit dert açar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4081, 'deli kız düğün etmiş, kendi baş sedire geçmiş', 'densiz, budala ev sahibi, konuklarından çok kendini ağırlanacak konuk yerine koyar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4082, 'deli kızın çeyizi gibi', 'bir arada sergilenen ve birbirine yakışmayan (eşya).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4083, 'deli olmak işten değil', 'densiz davranışlar, güç durumlar veya duyulan öfke karşısında düşülen çaresizliği anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4084, 'deli pösteki sayar gibi', 'çok karışık, çok ayrıntılı, sıkıcı bir işle uğraşma.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4085, 'deli Raziye gibi', 'delice davranışlarda bulunan (kız veya kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4086, 'deli saraylı gibi', 'acayip biçimde giyinen, takıp takıştıran (kimse): ''Teğmenin, teyzem dediği, altmışlık, altmış beşlik, suratı hâlâ düzgünlü, kirpikleri hâlâ sürmeli, deli saraylı gibi bir kadıncağızmış.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4087, 'deliden al uslu haberi', 'deli, sır saklamasını bilmediği için haberin doğrusu ondan alınır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4088, 'deliğe tıkmak', ' argo tutuklamak, hapsetmek: ''O nasıl yarmıştı benim kafacığımı, şimdi de yakalasınlar kuyruğundan onu da tıksınlar deliğe.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4089, 'delik büyük, yama küçük', 'eldeki imkânlar gerekenden çok az.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4090, 'delik deşik etmek', '1) bir canlının vücudunda bir araçla birçok yara, kesik açmak; 2) bir şeyin her yanında delikler açmak: ''Üst üste attığı kurşunlarla hedefin içini delik deşik etmeye başlamıştı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4091, 'delik deşik olmak', '1) bir canlının vücudunda bir araçla birçok yara, kesik oluşmak: ''... düşman süngüleriyle delik deşik olmaktansa tabancasını şakağına dayayıp tetiği çekmeyi düşünüyordu.'' -N. Cumalı. 2) bir şeyin her yanı delinmek: ''Adamın kuruyup kalan kanının üstüne delik deşik olan yatakların pamukları saçıldı.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4092, 'delik eğirmek', ' argo hapse girmek, tutuklanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4093, 'delikli boncuk (taş) yerde kalmaz', 'az çok işe yarayan her şeyin isteklisi bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4094, 'deliliğe vurmak', 'kendini deli gibi göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4095, 'deliliği tutmak', 'delice davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4096, 'delilsiz cennete bile girilmez', 'kişinin istediği şeyi elde edebilmesi için bir yol gösterenin olması gerekmektedir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4097, 'delinin eline değnek vermek', 'kötülük yapabilecek bir kimsenin davranışlarını kolaylaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4098, 'deliye bal tattırmışlar, çarşıda katran bırakmamış', 'aklı kıt olan kimse, bir kez hoşuna gitmiş olan şeye benzettiği nesneyi, gerçekten ona benzemese de elde etmeye çalışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4099, 'deliye dönmek', '1) çok sevinmek: ''Haber aldığı gün âdeta deliye dönmüş.'' -H. F. Ozansoy. 2) çok üzülmek: ''En bildiği derste bile kopya çeker, çekmezse hasta olur, deliye döner.'' -H. Taner. 3) çok kızmak: ''Patronun deliye döndüğünden habersizce geldi, elindeki şemsiyeye yapıştı.'' -R. Ilgaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4100, 'deliye göre her gün bayram', 'her fırsattan yararlanarak bayrammış gibi davrananlara ve her şeyi eğlenceli yönden alanlara söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4101, 'deliye her gün bayram', 'her fırsattan yararlanarak bayrammış gibi davrananlara ve her şeyi eğlenceli yönden alanlara söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4102, 'deliye taş atma, başını yarar', 'davranışlarında çılgınlık bulunan kimseye dokunma yoksa sana öyle çılgınca saldırır ki yaptığına pişman olursun.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4103, 'dem çekmek', '1) kuşlar uzun ve güzel ezgiler çıkarmak: ''Akasya dallarında bir tek bülbül uzun uzun dem çekiyor.'' -H. Taner. 2) şaka içki içmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4104, 'dem dökmek', 'kadınlar aybaşında kan yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4105, 'dem tutmak', 'bir çalgıya başka bir çalgı veya sesle eşlik etmek: ''Dinî seslere şarkı, çalgı sesleri cevap verir, onlara âdeta dem tutardı.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4106, 'deme gitsin', '''anlatılması güç, anlatılamaz'' anlamında kullanılan bir söz: Öyle sevindim ki deme gitsin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4107, 'demeç vermek', 'yetkili bir kimse bir konuda yayın organlarına açıklama yapmak, beyanat vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4108, 'demediğini bırakmamak (koymamak)', 'birisi için kırıcı, ağır, ileri geri konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4109, 'demek istemek', '1) bir şeyi anlatmak istemek: ''Gazete yazarlığını, edebiyatın, sanatın dışında sayanların ne demek istediklerini hiçbir zaman anlamadım.'' -N. Ataç. 2) bir düşünceyi söylemek istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4110, 'demek olmak', 'anlamına gelmek: Sene ''yıl'' demektir, senevi de ''yıllık'' demek olacak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4111, 'demem o (ki)', ' hlk. ''benim söylemek istediğim'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4112, '... demeye getirmek', 'doğrudan söylemeyip dolayısıyla anlatmak: ''Hadi, sedirin önünde tepsiyi elimden sen al, demeye getiriyormuş.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4113, '... demeye kalmamak', 'söylemeye, yapmaya fırsat olmamak: İşimiz bitiyor demeye kalmadı, herkes ayağa kalktı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4114, 'demir almak', ' den. 1) gemi yola çıkmak için çıpasını denizden çekmek, gitmeye hazırlanmak: ''Artık demir almak günü gelmişse zamandan / Meçhule giden bir gemi kalkar bu limandan'' -Y. K. Beyatlı. 2) mec. ölmek, çekip gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4115, 'demir atmak', ' den. 1) gemi çıpasını denize salmak: ''Açıkta demir atmış kotrayı görüyor musun?'' -F. R. Atay. 2) mec. bir kimse bir yerde uzun süre kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4116, 'demir gibi', '1) çok sağlam: ''Ben akide yemedim, gönlümde yumuşaklık yok, midem demir gibi.'' -H. R. Gürpınar. 2) çok güçlü, çok kuvvetli: ''Demir gibi kolları vardı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4117, 'demir ıslanmaz, deli uslanmaz', 'her nesnenin, her kişinin değiştirilemeyen bir özelliği vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4118, 'demir nemden, insan gamdan çürür', 'nem demiri nasıl paslandırıp çürütürse gam da insanı öylece yıpratır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4119, 'demir taramak', 'gemi rüzgâr veya akıntı yüzünden çıpasını sürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4120, 'demir tavında, dilber çağında', 'her iş zamanında ve uygun durumda yapılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4121, 'demir tavında dövülür', 'her iş zamanında ve uygun durumda yapılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4122, 'demir üzerinde', ' den. demirini almış ve kalkmaya hazır (gemi).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4123, 'demirbaştan düşmek', 'demirbaş listesinden çıkarmak, kaydını silmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4124, 'demiri tavında dövmeli', 'her iş zamanında ve uygun durumda yapılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4125, 'Demokles''in kılıcı', 'her an gerçekleşebilecek tehlike.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4126, 'deneme tahtasına çevirmek', 'bir şey üzerinde bilgisizce tedavi, onarım vb. işler yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4127, 'deneyim kazanmak', 'deneyimli duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4128, 'dengeli kılmak', 'huzura, düzene kavuşturmak: ''Sevgimde bir azalma olsaydı, bu bir bakıma beni dengeli kılardı.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4129, 'dengesi bozulmak', '1) dik durumdan düşecek duruma gelmek; 2) tanınan ve bilinen ölçülerin dışına çıkmak; 3) tutum ve davranışlarında tutarlılık olmamak; 4) mec. aralarında ilişki bulunan şeyler arasındaki uyum bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4130, 'dengi dengine', 'uygun olanıyla: ''Şehrin ortasında bir kurulu düzen var ki dengi dengine işleyip duruyor.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4131, 'dengine getirmek', 'punduna getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4132, 'dengiyle karşılamak', 'kendisine yapılan bir işin karşılığını aynı değerde iş yaparak vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4133, 'deniz bindirmek', 'denizde birden fırtına çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4134, 'deniz çıkmak', 'denizde fırtına olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4135, 'deniz dalgasız olmaz, gönül sevdasız olmaz', 'her denizde az çok dalga bulunduğu gibi her gönülde de bir sevda vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4136, 'deniz dalgasız olmaz, kapı halkasız', 'her nesnenin kendisine özgü nitelikleri, kendisinden ayrılmayan özellikleri vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4137, 'deniz durmak (düşmek)', 'denizdeki fırtına geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4138, 'deniz kenarında dalga eksik olmaz', 'içinde çeşitli olayların geçmesi doğal olan bir ortamda zaman zaman sert çatışmaların, fırtınaların çıkması da olasıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4139, 'denizde kum, onda para', '''parası çok kimse, zengin'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4140, 'denizdeki balığın bini bir paraya', 'henüz elde olmayan bir nesnenin alımı, satımı üzerinde konuşulmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4141, 'denizdeki balığın karada komisyonculuğunu yapmak', 'gerçekte bulunmayan bir konu üzerinde varmış gibi savunuculuğunu yapmak, hayalî konularda gereksiz söz söylemek: ''Denizdeki balığın karada komisyonculuğunu yapıyorlardı.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4142, 'denizdeki balığın pazarı (pazarlığı) olmaz', 'henüz elde olmayan bir nesnenin alımı, satımı üzerinde konuşulmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4143, 'denizden çıkmış balığa dönmek', 'sudan çıkmış balığa dönmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4144, 'denizden (denizi) geçip çayda boğulmak', 'bir işte büyük güçlükleri yendikten sonra önemsiz bir sebeple başarısızlığa uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4145, 'denize açılmak', 'kıyıdan çok uzaklaşmak: ''Denize açıldıktan beş on gün sonra iki ciddi fırtına ile karşılaştım.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4146, 'denize çıkmak', 'gezi veya av için kıyıdan ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4147, 'denize dökmek', 'düşmanı denize kadar sürüp yok etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4148, 'denize düşen yılana (yosuna) sarılır', 'güç bir duruma düşenlerin bundan kurtulmak için her türlü çareye başvurmaları olağandır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4149, 'denk düşmek', '1) uygun vakit ve fırsat olmak; 2) rast gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4150, 'denk gelmek', '1) uygun düşmek, uygun gelmek: ''Neleri, nasıl yazacağımıza gelince, yaşadığım günden başlayıp, denk geldikçe geriye dönüşlerle.'' -N. Meriç. 2) rast gelmek, rastlamak: ''Dolunun her biri, denk gelse bir kafa yarardı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4151, 'denk getirmek', 'uygun zamanını ve durumunu bulmak, rastlatmak: ''Bizimkinin evde olmadığı bir zamana denk getirirsem çağıracağım, bakalım gelecek mi?'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4152, 'denli densiz söz söylemek', 'uygunsuz, yakışıksız ve saygısız sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4153, 'depara geçmek', 'koşuya veya yarışa hızla başlamak: ''Onu kaptırınca kıyıdaki öbür kayalara konmak için depara geçerler.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4154, 'depara kalkmak', 'koşu veya yarış içinde hızını birdenbire artırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4155, 'depo etmek', 'yığmak, biriktirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4156, 'der oğlu der', 'bir şeyin sürekli söylendiğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4157, 'derde (derdine) derman olmak', 'soruna çözüm bulmak, sıkıntıyı geçirmeye çare göstermek: ''Hurşit Bey seni ağırlar, derdine derman olur.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4158, 'derde (dertlere) düşmek', '1) sorunla karşılaşmak; 2) mec. hastalanmak: ''Mutlaka umarsız dertlere düştüğümü biliyor.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4159, 'derde düçar olmak', 'kötü bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4160, 'derdest etmek', 'yakalamak: ''Bu iddiayla yola çıktılar mı Millî Kongre''yi basarlar, Esat Paşa''yı derdest ederler.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4161, 'derdi veren devasını da verir', 'her sıkıntının, üzüntünün bir çaresi vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4162, 'derdin yoksa söylen, borcun yoksa evlen', 'derdi olmayan kimse önemsiz şeyleri kendisine dert edinerek söylenir, borcu olmayan kimse de evlenirken birçok şey satın almak zorunda kaldığı için borçlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4163, 'derdine deva bulmak', 'sıkıntıyı çözümlemek, atlatmak, çaresizliği yenmek: ''Ağlamak, dertleşmek, dertlerine deva bulmak ihtiyacı her zamankinden fazla idi.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4164, 'derdine yanmak', 'kendi durumuna üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4165, 'derdini çekmek', 'üzüntüsüne katlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4166, 'derdini dökmek', 'derdini, sıkıntılarını ayrıntılı olarak anlatmak, dile getirmek: ''Efendinin ona ihtiyacı en ziyade kendi derdini dökmek, kalbini boşaltmak içindi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4167, 'derdini Marko Paşa''ya anlat', '''yakınmanı dinleyecek kimse yok'' anlamında kullanılan bir söz: ''Herif öylesine müzevir ki anlatılmaz efendim, anlatılmaz. İrtica yapıyor diye tutturdu mu anlat derdini Marko Paşa''ya efendim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4168, 'derdini söylemeyen (saklayan) derman bulamaz', 'insan sıkıntısını başkasına açıklayarak giderebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4169, 'dere tepe düz gitmek', 'engelleri aşarak gitmek: ''Gece boyunca kırlarda yürüdü, dere tepe düz gitti ve bir dağın eteğine geldi.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4170, 'derece almak', 'başarı göstererek ödül kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4171, 'dereceye girmek', 'yarışma, sınav vb.nde üst sıralarda yer almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4172, 'derede tarla sel için, tepede harman yel için', 'elden çıkarmak istemediğimiz şeyleri tehlikeye açık durumlardan uzak tutmalıyız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4173, 'dereden tepeden konuşmak', 'gelişigüzel konuşmak, rastgele konular üzerinde konuşmak: ''Kahveler içilip dereden tepeden konuştuktan sonra yataklara kavuştuk.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4174, 'dereyi (çayı, ırmağı) geçerken at değiştirilmez', 'bir yöntemden başka bir yönteme geçiş tehlikeli bir durum veya zamanda yapılmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4175, 'dereyi görmeden paçaları sıvamak', 'gerektiğinden çok önce veya henüz ortada hiçbir şey yokken hazırlanmaya kalkışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4176, 'derine inmek', 'bir konu üzerinde uzun uzadıya durup araştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4177, 'derisi kemiklerine yapışmak', 'çok zayıflamak: ''Bu efendi, derisi kemiklerine yapışmış, gözleri çukura kaçmış, hastaneye yatırılacak kılığa girmişti.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4178, 'derisine sığmaz', 'çok kibirli.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4179, 'derisini yüzmek', '1) derisini soymak, sıyırmak; 2) işkence ederek öldürmek; 3) mec. birinin bütün varlığını elinden almak: Tefecilerin eline düşerse derisini yüzerler.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4180, 'dermanı kesilmek (dermandan kesilmek)', 'yorgunluktan güçsüzleşmek: ''Çok uzak yerlerden geldim, ayaklarımın dermanı kesildi.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4181, 'ders almak', '1) bir konu üzerinde bir öğrenci yetkili bir kimseden bilgi edinmek; 2) mec. bir olaydan deneyim kazanmak, ibret almak: ''Demokrasiyle ilgili sorunlara tanık olmuş ve önemli dersler almıştı.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4182, 'ders başı etmek (yapmak)', 'tatil sonrası öğrenciler yeni öğretime başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4183, 'ders (dersi) asmak', 'dersten kaçmak, derse gitmemek: ''Metin''in bu dersi asma teklifi hiç hoşuma gitmedi doğrusu.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4184, 'ders görmek', 'bir konu üzerinde bir öğrenci yetkili bir kimseden bilgi edinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4185, 'ders olmak', 'kötü bir olay bir daha yapmamak üzere örnek olmak, ibret olmak: ''Bu seneki tecrübe aynı zamanda bir de ders oldu.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4186, 'ders vermek', '1) öğretmek, yetiştirmek: İyi konuşurdu, ders vermek sanatını bilirdi. 2) azarlamak, sert davranmak, sert bir karşılıkla yola getirmek: ''Evvela kendi kendisini cezalandırdı, sonra kendisi gibi yaşamak istemeyenlere ders verdi.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4187, 'ders yapmak', '1) sınıfta belli bir programa bağlı olarak herhangi bir konuyu işlemek; 2) sınıfta verilen ödevi daha sonra yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4188, 'dert ağlatır, aşk söyletir', 'derdi olan acı çeker, ağlar; âşık olan kimse de içindeki duyguları dışa vurup ferahlamak için durmadan söylenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4189, 'dert anlatmak', 'derdini dökmek: ''Elimden çeker alır, kime dert anlatırım o zaman?'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4190, 'dert, çekene göredir', 'bir derdin ağırlığı, hafifliği ona uğrayan kimsenin etkilenme derecesiyle ölçülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4191, 'dert değil', '''önemsemeye, üzülmeye değmez'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4192, 'dert eğirmek', 'içinden çıkılması güç bir sorunla uğraşmak zorunda kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4193, 'dert etmek (edinmek)', 'bir sorunu veya durumu üzüntü konusu yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4194, 'dert gider amma yeri boş kalmaz', 'insan bir dertten kurtulduğunda onun yerine başka bir dert geleceğini iyi bilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4195, 'dert gitmez, değişir', 'insan bir dertten kurtulduğunda onun yerine başka bir dert geleceğini iyi bilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4196, 'dert olmak (kesilmek)', 'bir kimse veya olay sıkıntı vermek: ''Artık açıkça mahallenin başına dert olmaya başlamış.'' -Y. N. Nayır. ''Nereden buraya gelmiş, âlemin başına dert kesilmişti.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4197, 'dert yanmak', 'derdini sızlanarak anlatmak: ''Müşteriler ay başında borç ödeyeceklerine Tevfik''e dert yanıyorlar.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4198, 'dertsiz baş (kul) olmaz', 'derdi olmayan kimse yoktur, az çok herkesin derdi vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4199, 'dertsiz baş terkide gerek', 'bir kişi ancak öldükten sonra dertten kurtulabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4200, 'dertsiz başını derde sokmak', 'bir derdi yokken gereksiz yere üzüntü veren bir işe girişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4201, 'dervişe ''Bağdat''ta pilav var'' demişler, ''yalan değilse ırak değil'' demiş', 'bir şeyi elde etmek için aşırı istekli olan kimseye, bu uğurda katlanacağı fedakârlıklar güç gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4202, 'dervişin fikri ne ise zikri de odur', 'insan, önem verip düşündüğü şeyi konuşmaktan kendini alamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4203, 'derya gibi', '1) çok bilgili; 2) pek çok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4204, 'destan düzmek', 'kahramanlık hikâyesi veya herhangi bir olayı anlatan şiir yazmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4205, 'destan gibi', 'uzun yazılmış (mektup).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4206, 'destan yazmak', 'olağanüstü kahramanlık, yararlık veya başarı göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4207, 'destek görmek', 'yardım edilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4208, 'destek olmak', 'güç sağlamak, yardımcı olmak: ''Böyle zor bir zamanda, birbirinizi kırmak yerine destek olmalısınız.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4209, 'desteksiz atmak', 'abartılı konuşmak, yalan söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4210, 'destursuz atmak', 'kolay yalan söyleyebilmek, palavra atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4211, 'destursuz bağa gireni sopa ile kovarlar', 'bir yere izinsiz girmek veya bir işe izinsiz el atmak kötü karşılanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4212, 'destursuz bağa girenin yediği sopayı Mevla bilir', 'bir yere izinsiz girmek veya bir işe izinsiz el atmak kötü karşılanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4213, 'dev adımlarla ilerlemek', 'çok çabuk ilerlemek, üst üste başarılar göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4214, 'dev gibi', 'iri ve korkunç: ''O kadar kaba saba, öyle dev gibi bir adamdı ki...'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4215, 'deve bir akçeye, deve bin akçeye', 'çok ucuza alınmayan bir şey gerekli olduğunda çok pahalıya alınabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4216, 'deve boynuz ararken kulaktan olmuş', 'elindekiyle yetinmeyip daha çoğunu arayan, elindekinden de olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4217, 'deve büyüktür amma beşini bir eşek yeder', 'insan görünüşte büyük olmakla akıl büyük olmaz, bir akıllı birçok az akıllıyı arkasından sürükler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4218, 'deve deve yerine çöker', 'yitirilen değerli kimsenin, elden çıkan değerli şeyin yeri boş kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4219, 'deve dişi gibi', '1) iri görünüşlü; 2) sıradan olmayan, tanınmış, güçlü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4220, 'deve gibi', '1) uzun boylu; 2) hantal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4221, 'deve Kâbe''ye gitmekle hacı olmaz', 'gerekli niteliklerden yoksun olan kişi, biçimsel işler yapmakla kişiliğine değer kazandıramaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4222, 'deve kuşu gibi başını kuma sokmak (gömmek)', '1) bir tehlike, bir olay karşısında yararlı olmayacağı apaçık ortada olan kaçamak bir yola sapmak; 2) başkalarını aldattığını sanarak kendisini aldatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4223, 'deve kuşu gibi (yüke gelince kuş, uçmaya gelince deve)', 'uygun şartlarda terslik çıkaran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4224, 'deve kuşuluk etmek', 'deve kuşu gibi başını kuma sokup gerçeklerden uzak duracağını sanmak: ''Bu harekete sadece şımarık gözü ile bakmak deve kuşuluk etmek olur.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4225, 'deve nalbanda bakar gibi', ' alay hiç görmediği, bilmediği bir şeye bakar gibi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4226, 'deve olmak', 'para veya yiyecek kaybolmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4227, 'deve yerine deve çöker', 'değerli bir kimseden boşalacak yeri ancak o değerde olan başka bir kimse doldurabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4228, 'deveci ile görüşen kapısını yüksek açmalı', 'yüksek makam sahibi kimselerle ilgisi olanlar durumlarının gerektirdiği özveriyi göze almalıdırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4229, 'deveci ile konuşan kapısını büyük açar', 'yüksek makam sahibi kimselerle ilgisi olanlar durumlarının gerektirdiği özveriyi göze almalıdırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4230, 'devede kulak (kulak gibi) kalmak', '1) çok az önemi olmak, söz etmeye değer bulmamak: ''Kitaptan öğrendikleri, hayattan gözlediklerinin yanında devede kulak kalır.'' -S. Birsel. 2) yetersiz, çok küçük veya az olmak: ''Tekaüt aylıkları günün ihtiyaçları karşısında devede kulak gibi kalıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4231, 'deveden büyük fil var', 'herhangi bir konuda söz sahibi olanlardan daha büyük, daha yetkili biri mutlaka vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4232, 'devenin derisi eşeğe yük olur', 'zengin ne kadar fakir düşse de yoksula göre yine varlıklıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4233, 'deveye bindikten sonra çalı ardına gizlenilmez', 'herkesin gözü önündeki bir olayı şöyle böyle yorumlarla gizlemeye çalışmak boşunadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4234, 'deveye burç gerek olursa boynunu uzatır', 'kişi kendisine gerek olan şeyi elde etmek için yorgunluğa katlanmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4235, 'deveye hendek atlatmak', 'birine yapılması çok zor, hemen hemen imkânsız olan işleri yaptırabilmek: ''Görülüyor ki insanlara bir şeyi anlatmak deveye hendek atlatmaktan güçtür.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4236, 'deveye ''inişi mi seversin, yokuşu mu?'' demişler; ''düz yere mi (düze kıran mı) girdi?'' demiş', 'bir işin kolay yapılabilmesi için bir yol varken zor yolu seçmek doğru olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4237, 'deveyi düze çıkarmak', 'güçlükleri giderip işleri yoluna koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4238, 'deveyi havuduyla yutmak', 'eline geçen ve hakkı olmayan şeyleri kendi menfaati için kullanmak, hiç çekinmeden büyük suistimal yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4239, 'deveyi yardan uçuran bir tutam ottur', 'gözü doymayan hırslı insanlar küçük bir çıkar için bütün varlığını tehlikeye atar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4240, 'devir açmak', 'tarihte özellik taşıyan yeni bir çağ başlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4241, 'devlet adama ayağıyla gelmez', 'zenginlik ve talih kişiyi kendiliğinden gelip bulmaz, çalışıp çabalamakla elde edilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4242, 'devlet oğul, mal tahıl, mülk değirmen', 'en büyük mutluluk ve zenginlik, çocuk sahibi olmak; en gerekli mal, tahıl; en değerli mülk, değirmendir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4243, 'devletin malı deniz, yemeyen domuz', 'devlete hıyanet etmeyi sanat hâline getirenlere göre devletin bitmez tükenmez malı vardır. Yolunu bulup ondan aşırmayan budaladır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4244, 'devletli gözü perdeli olur', 'zengin, işi yolunda kimse yoksulların hâlinden anlamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4245, 'devletli ile deli bildiğini işler', 'yüksek rütbeliler, deliler, kimsenin sözünü dinlemez, akıllarına geleni yaparlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4246, 'devletli yanını kaşısa yoksul para verecek sanır', 'bir isteğinin yerine getirilmesini ilgililerden bekleyen kimse, onların bu işle ilgisi bulunmayan davranışlarını, isteğini karşılamak üzere yapıyorlar diye yorumlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4247, 'devletliye dokun geç, fukaradan sakın geç', 'zenginle, az da olsa bir ilişkin bulunsun belki yararlanırsın ama fakire yaklaşma, o senden yararlanmak ister.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4248, 'devre dışı kalmak', 'konudan uzak düşmek, konuyla ilgilenememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4249, 'devre dışı tutmak (bırakmak)', 'konudan uzaklaştırmak, ilgilenmemesini sağlamak: ''Özellikle torununun boşanmasında onu devre dışı tutmuşlardı.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4250, 'devreye alınmak', 'işin içine girmesi sağlanmak: Devlet Bakanı borçların eritileceğini, dış borçlanma için bankaların ve özel sektörün devreye alınacağını kaydetti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4251, 'devreye girmek', 'ilgilenmek, karışmak, araya girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4252, 'devreye sokmak', 'işin içine girdirmek, karıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4253, 'deyip de geçmemek', 'önemsemek: ''Yengeye yenge deyip geçmeyelim. Bir mahalleyi susta durdurur.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4254, 'dımdızlak ortada kalmak', 'elindeki her şeyi, imkânlarını yitirmek: ''Zehra dımdızlak ortada kalacak.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4255, 'dımdızlak ortalıkta bırakmak', 'her türlü varlıktan, olanaktan mahrum kılmak, yokluğa mecbur etmek: ''Sanıyorum ki bazıları dünyayı altımızdan çekip bizi dımdızlak ortalıkta bırakmaya çalışıyorlar.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4256, 'dırıltı çıkarmak (etmek)', 'çekişmeye yol açmak: ''Rica ederim bey, gelir gelmez ayağının tozu ile dırıltı çıkarma.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4257, 'dış kapının mandalı', '1) uzak akraba; 2) önemsiz, değersiz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4258, 'dışa vurmak', 'belli etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4259, 'dışarı atmak', '1) kovmak; 2) zararlı bir maddeyi terleme, idrar vb. yollarla vücuttan çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4260, 'dışarı çıkmak', 'büyük abdest yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4261, 'dışarı vurmak', 'belli etmek, açıklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4262, 'dışı eli yakar, içi beni yakar', 'görünüşe aldanmamalı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4263, 'dışı kalaylı, içi alaylı', '''dışı süslü, güzel görünüşlü ancak içi berbat'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4264, 'dışına çıkmak', 'tanınan hak ve yetkileri aşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4265, 'dışında bırakmak', 'hariç tutmak: ''Biz herhangi bir teşebbüs ihtimalini ebediyen hudutlarımız dışında bırakmak istiyoruz.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4266, 'dışında kalmak', 'karışmamak, ilgilenmemek: ''Hiçbir şeye karışmadan olayların dışında kalmak isteyenlerin çabaları boşunaydı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4267, 'dibe vurmak', 'en kötü duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4268, 'dibek gibi', '1) bütün ağırlığıyla: ''Elbette dibek gibi otururuz televizyonun başına.'' -M. İzgü. 2) şişmiş; 3) ağır, ağırlaşmış.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4269, 'dibi görünmeyen sudan geçme', 'bir işe girişirken her yönünü iyice araştır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4270, 'dibi görünmeyen tastan su içme', 'bir işe girişirken her yönünü iyice araştır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4271, 'dibi kırmızı mumla (bal mumuyla) mı çağırdım', 'üzerinde önemle durarak çağırmadım.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4272, 'dibine darı ekmek', 'bir şeyi sonuna kadar tüketmek, bitirmek: ''Eline geçirince dibine darı ekmeden bırakmazsın.'' -R. Ilgaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4273, 'dibine kadar', 'en ince ve gizli noktasına kadar: ''Hakkında söylenti çıkan, derhâl dibine kadar incelenir, ya mahkûm olur ya temize çıkardı.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4274, 'dibini kurcalamak (karıştırmak)', 'araştırmak, sorup öğrenmek: ''Dibini kurcalıyorsun, ... birkaç merkez dışında Ege üreticisi çoğunluk küçük çiftçi, orta çiftçi!'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4275, 'dibini tutmak', 'pişen yemekler tencerenin dibine yapışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4276, 'didişip durmak', 'sürekli olarak birbirini hırpalamak: ''Böylece, Serdar''la didişip durmak derdinden de kurtulmuştu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4277, 'dik dik bakmak', 'çok sert bir biçimde, sert sert, öfkeli öfkeli bakmak: ''Karşı sıradaki bıyıklı adam gelmiş yanında duruyor, dik dik bakıyordu.'' -R. Mağden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4278, 'diken battığı yerden çıkar', 'zarar hangi yönden geldiyse ancak o yönden giderilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4279, 'diken diken olmak', 'dik duruma gelmek, dikleşmek: ''Kâhyamın, pos bıyıkları kirpi sırtı gibi diken diken oldu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4280, 'diken üstünde oturmak (olmak)', 'bir yerde tedirginlik duymak: ''O bir yıl içinde diken üstünde otururum o evde; düş kuramam, şiir yazamam.'' -M. C. Anday. ''Konuşmaya başladık. Yine kavga ederiz diye diken üstündeyim.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4281, 'dikensiz gül olmaz', 'iyi veya güzel olan her şeyin az çok sıkıntı veren bir yanı da bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4282, 'dikilip durmak (kalmak)', 'bir yerde kısa bir süre ayak üstünde durmak: ''Dükkânın önünde bu kadar dikilip kalmasının sebebi de bu olabilirdi.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4283, 'dikine gitmek', 'kimsenin sözünü dinlemeyerek kendi bildiğini yapmak: ''Öyle fazla dikine gitmek iyi değildir hayatta.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4284, 'dikiş atmak', 'yarılan veya yırtılan deriyi dikişle bir araya getirip tutturmak: Kafasına iki dikiş attılar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4285, 'dikiş tutturamamak', 'bir işte veya bir yerde herhangi bir sebeple uzun süre kalmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4286, 'dikişini almak', 'dikilmiş yaranın ipliklerini kesip çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4287, 'dikiz etmek (geçmek)', 'gözetlemek: ''İsterseniz siz masanın altından dikiz edin ama belli olmasın.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4288, 'dikize almak', 'gözetlemek: ''Jale ... bilmem ben onu yine yakın dikize almış mıydım?'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4289, 'dikkat çekmek', '1) ask. ''dikkat'' komutunu yüksek sesle söylemek; 2) mec. ilgi toplamak: ''Hangi konudan söz etse dikkati çekecek bir hava veriyor.'' -N. Cumalı. 3) mec. göze batmak, fark edilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4290, 'dikkat kesilmek', 'bütün dikkatini bir şey üzerinde toplamak: ''Naci, dikkat kesilmiş bütün davranışlarımı izliyor.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4291, 'dikkate almak', 'göz önünde bulundurmak, hesaba katmak, gereğini düşünmek: ''O yüzden annemin sözlerini dikkate almadı.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4292, 'dikkati calip olmak', 'dikkati çeken kimse veya şey olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4293, 'dikkatini çekmek', 'uyarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4294, 'dikkatini çekmemek', 'birinin ilgisini uyandırmamak: ''Arapça konuşan milletler arasındaki ayrılıklar da onun dikkatini çekmemişti.'' -M. Kaplan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4295, 'dikkatini toplamak', 'duygu ve düşünceyi bir konu veya yapılan iş üzerinde yoğunlaştırmak: ''Dikkatini topladı, yürüyen insanlara daha bir titizlikle bakmaya başladı.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4296, 'dikte etmek', '1) yazdırmak için söylemek: ''Şimdi sana bir mektup dikte edeceğim.'' -H. E. Adıvar. 2) mec. birine isteklerini zorla kabul ettirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4297, 'dil ağız vermemek', 'ağız dil vermemek: ''Çocuk, hâlâ dil ağız vermeden yatıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4298, 'dil (diller) dökmek', 'kandırmak, inandırmak veya yararlanmak için tatlı sözler söylemek: ''Ninniyi mutlaka söylemesi için ona bir sürü dil döktü.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4299, 'dil ebsem (olsa) baş esen (esendir)', 'kişi dilini tutar, her şeyi söylemezse başını belaya sokmamış olur, rahat eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4300, 'dil otu yemek', 'çok konuşmak: ''Mütemadiyen gülüp söylüyordum. Hacı Kalfanın ellerini dizlerine vurarak: -Dil otu mu yedin be kızım? diye bir gülmesi var ki...'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4301, 'dil tutmak', ' esk. sorguya çekmek için düşman askeri yakalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4302, 'dil uzatmak', 'bir kimse veya bir şey için kötü söylemek: ''Başka ulusların kabahatleri ne olursa olsun, dost ve düşman bize nasıl dil uzatırlarsa uzatsın...'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4303, 'dilden düşmez olmak', 'herkes tarafından sürekli tekrar edilir olmak: ''Kapsamı iyice belirtilmeyen, gerektiği gibi tanımlanmayan sanat sözü, dillerden düşmez oldu.'' -S. Hilav.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4304, 'dilden düşürmemek', 'sürekli tekrar etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4305, 'dilden gelen elden gelse, her fukara padişah olur', 'kişi her söylediğini yapamaz, her dilediğini elde edemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4306, 'dile (dillere) düşmek', 'hakkında dedikodu yapılmak: ''Yâr adını desem olmaz / Düşer dillere dillere'' -Erzurumlu Emrah.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4307, 'dile dolamak', 'bir şeyi veya konuyu sık sık tekrar etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4308, 'dile gelen ele gelir', 'insanlar yapacakları işler hakkında önce konuşurlar, sonra da o işi gerçekleştirirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4309, 'dile gelmek', '1) dile düşmek; 2) konuşma kudreti, yeteneği, olmayan varlık konuşmak, dillenmek, lisana gelmek: ''Günlerce elin, dile gelmeyen çocuğunu bağrına basan fabrika sahibine acındı.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4310, 'dile getirmek', '1) konuşturmak: ''Yıllar yılı, bu amaçları devlet adamlarımız, basınımız, sanat âlemimiz dile getirip durmuştur.'' -T. Halman. 2) belirtmek, anlatmak, açıklamak, ifade etmek: ''Kendi kendime, adlı şiirinde bunu şöyle dile getirir.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4311, 'dile vermek', 'gizli tutulması gereken bir şeyi açığa vurmak, duyurmak, yaymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4312, 'dilediği gibi', 'kendi düşünce, görüş ve isteğine göre: ''Duygu, düşünce, dilediğim gibi yaşamak özgürlüğümü korumak isterim.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4313, 'dilenci bir olsa şekerle beslenir', 'yardım bekleyen bir tane olsa umduğundan aşırı şeyler verilerek sevindirilir ancak bunların sayısı çok olduğundan hepsine aynı cömertlik gösterilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4314, 'dilencilik etmek', 'dilenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4315, 'dilencinin torbası dolmaz', 'şundan bundan yardım dileyerek geçinmeye çalışanların istekleri bitmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4316, 'dilenciye borçlu olma, ya düğünde ister ya bayramda', 'çıkarından başka bir şey düşünmeyen kimse ile ilişki kurma, seni nerede rahatsız edeceği belli olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4317, 'dilenciye hıyar vermişler de eğri diye beğenmemiş', 'hem gereksinim duyduğu konuda yardım istiyor hem de yapılan yardımı küçümsüyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4318, 'dili açılmak', 'herhangi bir sebeple konuşmayan kimse konuşmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4319, 'dili ağırlaşmak', 'hastalık sebebiyle güçlükle söz söyleyebilmek, güçlükle konuşmak: ''Hastaya bazı şeyler soruyor. Fakat anlaşılır cevaplar alamıyordu. Birkaç saatin içinde kaynımın dili ağırlaştı.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4320, 'dili alışmak', 'çok kullandığı bir söze alışmak: ''Bizim moruk ertesi güne devrisi der de ondan dilim alışmış.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4321, 'dili (başka bir dile) çalmak', 'bir kimsenin konuşması başka bir dile benzemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4322, 'dili bir karış dışarı çıkmak (sarkmak)', 'koşmaktan, yürümekten ve yorulmaktan çok susamak: ''Koştu koştu da dili bir karış sarktı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4323, 'dili bir karış (olmak)', 'fazla konuşan, her söze karşılık veren.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4324, 'dili boğazına akmak', 'konuşamaz olmak, sesi soluğu çıkmamak: ''Kılıcı görünce dili boğazına aktı hayranlığından.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4325, 'dili çözülmek', 'konuşamayan veya susan kişi konuşmaya başlamak: ''Aslında ben çok az konuşan biriyim. Dilimin böyle birdenbire çözülmesi çok garip.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4326, 'dili damağına yapışmak (dili damağı kurumak)', 'susuzluktan ağzı kurumak, çok susamak: ''Kupkuru dili damağına yapışıyor, boğazından midesine doğru...'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4327, 'dili (dilinin) döndüğü kadar', 'söyleyebildiği kadar, anlatma gücünün elverdiği ölçüde: ''Mademki çocuk terbiyesi hakkında konuşmak istiyorsunuz, dilimin döndüğü kadar söyleyeyim.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4328, 'dili dolaşmak', 'korku, heyecan, hastalık, utangaçlık, sarhoşluk gibi sebeplerle şaşırarak söyleyeceğini karıştırmak: ''Vehbi Dedenin kendini dinlediğinin farkına varır varmaz dili dolaştı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4329, 'dili dönmemek', '1) bir sözü doğru, düzgün söylemeyi becerememek: ''Üstelik ben dilim dönmezken armağan ettiğim çiçeklerle konuşmuyor muyum?'' -R. Mağden. 2) amacını iyi anlatamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4330, 'dili durmak', 'susmak, dedikodu etmemek: ''Götüreceği cadı karının dili dursa neyse... Okuyup iyileştirirse herkese yayar.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4331, 'dili durmamak', '1) sürekli konuşmak; 2) söylenemeyecek şeyleri de söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4332, 'dili ensesinden çekilsin!', 'bıktıracak kadar çok konuşan veya kötü sözler söyleyenler için kullanılan bir ilenme sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4333, 'dili kılıçtan keskin', 'kırıcı ve ağır konuşan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4334, 'dili kurusun!', '''söz söyleyemez olsun!'' anlamında kullanılan bir ilenme sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4335, 'dili olsa da söylese (anlatsa)', 'cansız nesneler konuşabilseler bazı olaylara tanıklık da edebilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4336, 'dili pabuç kadar', 'saygısızca ve gönül kırıcı bir biçimde konuşan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4337, 'dili sürçmek', '1) konuşma sırasında kelimeleri yanlış söylemek: ''Bir dil sürçmesi sonucu, bu tartışmayı yarım saat kadar yürütmüşüm.'' -S. İleri. 2) istenmeyen bir konudan söz etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4338, 'dili tutulmak', 'sevinç, korku, şaşkınlık vb. sebeplerle birdenbire söz söyleyemez olmak: ''Elmas''ın dili tutulmuştu. Çıt çıkarmadan bakıyordu sadece.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4339, 'dili uzamak', 'haddini bilmeden konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4340, 'dili varmak', 'bir sözü söylemeye gönlü razı olmak: ''Süleyman Kâhyaya söylemeye kimsenin dili varmıyor, gücü yetmiyordu.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4341, 'dili yanmak', '1) üzüntü ve eziyet çekmek, zarara uğramak: ''Otobüs yolculuğundan bir hayli dilim yandı.'' -B. R. Eyuboğlu. 2) bıkmak, nefret etmek: ''Şair neslinin şarkıdan o kadar dili yandı ki şarkı kelimesini nerede görse silip üstüne türkü diyecek.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4342, 'dilim dilim etmek', 'dilimlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4343, 'dilim seni dilim dilim dileyim, başıma geleni senden bileyim', 'kişinin başına ne gelirse dilini tutmamasından gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4344, 'dilin cirmi küçük, cürmü büyük', 'dil küçük bir nesnedir ama söylediği kötü sözlerle kişinin başını belaya sokarak büyük suç işler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4345, 'dilin kemiği yok', 'insan doğru veya yanlış her şeyi söyleyebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4346, 'dilin kemiği yok', '''insan doğru veya yanlış her şeyi söyleyebilir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4347, 'dilinde tüy bitmek', 'tekrar tekrar söylemekten usanmak, bıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4348, 'dilinden anlamak', '1) bir canlının çıkardığı seslerden veya onun davranışlarından ne anlatmak istediğini anlamak; 2) mec. söz konusu olan şeyin özelliğini bilmek: ''Bunda yenilmiş, içilmiş bir şey yok ya! Sen onun dilini de anlarsın.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4349, 'dilinden düşürmemek', 'sürekli olarak aynı kişiden veya şeyden söz etmek, sık sık anmak: ''Dilinden hiç düşürmediği cümleleri hep birer bilmece gibi şeylerdi.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4350, 'dilinden kurtulamamak', 'sürekli olarak bir kimsenin sitem, eleştiri ve sataşmalarına uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4351, 'diline dolamak (takmak)', '1) aynı şeyi durmadan ve her yerde tekrarlamak: ''Bu aydınlardan bazılarının son zamanlarda dillerine doladıkları bir hikmet var.'' -O. V. Kanık. 2) bir kimseyi her yerde kötülemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4352, 'diline kira istemek', 'ağzına kira istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4353, 'diline pelesenk etmek', 'diline dolamak: ''Hacı Kasap, âdeta bu sözleri aferin tarzında diline pelesenk etmişti.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4354, 'diline sağlam olmak', '1) saklanacak konuları açığa vurmamak; 2) kötü söz söylemekten kaçınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4355, 'diline sağlık', 'ağzına sağlık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4356, 'diline virt etmek', 'diline dolamak: ''Şartını âdeta manzum, kafiyeli bir nakarat gibi diline virt etmişti.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4357, 'dilini değdirmemek', 'hiç yememek veya içmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4358, 'dilini eşek arısı soksun', 'hoşa gitmeyen bir şey konuşan kimseye söylenen bir ilenme sözü.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4359, 'dilini kedi (fare) mi yedi?', '''neden konuşmuyorsun?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4360, 'dilini kesmek (kesip oturmak)', 'susmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4361, 'dilini tutamamak', 'sonunu düşünmeden gelişigüzel konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4362, 'dilini tutmak', 'sonunu düşünmeden gelişigüzel konuşmaktan sakınmak: ''Şarkta, insanın selameti dilini tutmasındadır diye bir söz vardır.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4363, 'dilini yutmak', 'sevinç, korku, heyecan vb. sebeplerle konuşamaz olmak: ''Satılmışın hiddetli hiddetli çıkışması üzerine dilini yutup ters yüzüne mutfağına döndü.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4364, 'dilinin altında bir şey olmak', 'bir kimsenin sözlerinden, açıkça söylemediği bir şeyler anlaşılmak: ''Günlerdir doktorun dilinin altında bir şeyler olduğunun farkındaydı.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4365, 'dilinin altındaki baklayı çıkarmak', 'gizli tutulması gereken bir şeyi söylemek: ''Çıkar şu dilinin altındaki baklayı da ne demek istiyorsan söyle, ben de anlayayım.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4366, 'dilinin cezasını (belasını) çekmek (bulmak)', 'ölçüsüz, düşüncesiz konuşma yüzünden zarar görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4367, 'dilinin ucuna gelmek', 'söyleyecek duruma gelmek: ''İsmi dilimin ucuna gelir gelmez kalbimden hafif bir cereyanın kopup damarlarıma aktığını duyuyorum.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4368, 'dilinin ucunda', 'bir söz hatırlanacak gibi olup da hatırlanamadığında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4369, 'dilinin ucuyla', 'içten, yürekten olmayarak, laf olsun diye.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4370, 'diliyle sokmak', 'bir kimseye ağır ve kırıcı sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4371, 'diliyle tutulmak (yakalanmak)', 'suçunu, kendi konuşması ile açığa vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4372, 'dille düğümlenen, dişle çözülmez', 'verdiği sözü yerine getirmeyen kişi, sebebi sorulduğu zaman kendini savunmakta zorlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4373, 'dillerde dolaşmak (gezmek)', 'her yerde kendisinden söz edilmek: ''Hangi hanım, dillerde gezen aşk maceralarından dolayı mevkisinden düşüyor?'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4374, 'dillere destan olmak', 'herkes tarafından konuşulur olmak: ''Dillere destan İstanbul nezaketini o evde gördüm, ağzım açık kaldı.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4375, 'dilsizin dilinden anası anlar', 'başkalarının kolay kolay anlayamadıkları şeyi, her gün onunla uğraşan kimse çok kolay anlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4376, 'dimdik ayakta durmak', 'yıkılmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4377, 'dimdik durmak', '1) tam dik durumda olmak: ''Pencerenin önünde, sırtı odaya dönük olarak dimdik durdu.'' -T. Buğra. 2) mec. tutumunu değiştirmemek, yılmamak: ''Onun nasıl hiç sarsılmadan dimdik durduğunu gördüm.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4378, 'dinden imandan çıkmak', 'kendini kontrol edemeyecek kadar çok öfkelenmek, çok sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4379, 'dinden imandan olmak', 'dinî inancını yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4380, 'dini bir uğruna', 'Müslümanlık için: ''Senin yanına fedai yazılacağım ve dini bir uğruna çalışacağım.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4381, 'dini gibi bilmek', 'çok iyi bilmek: ''Ufacık bir düşüncenin en büyük bir dikkati iflas ettirdiğini dini gibi bilirdi.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4382, 'dini imanı para', 'tek düşüncesi para olan kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4383, 'dinim hakkı için (aşkına)', '''dinimi tanık tutarım'' anlamında kullanılan bir ant sözü: ''Şevki Bey dedi, dinin aşkına sen Romenlerin gemi yaptıklarını işittin mi?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4384, 'dinine yandığım', ' argo öfke, kızgınlık vb. duyguları belirtmek için kullanılan bir ilenme sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4385, 'dinsizin hakkından imansız gelir', 'acımasız olan kişiyi, kendisinden daha acımasız biri yola getirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4387, 'dipsiz kile, boş ambar', 'para, mal tutmayanın durumunu veya bir iş için boş yere uğraşıldığını anlatan bir söz: ''Politika ne nankör bir meslek, ne dipsiz kile, boş ambar imiş.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4388, 'direk gibi', 'sağlam, iri yapılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4389, 'direksiyon kırmak', 'aracı istenilen yöne çevirebilmek için direksiyonu o yöne döndürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4390, 'direksiyon sallamak', ' argo motorlu taşıt kullanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4391, 'direksiyona geçmek', '1) aracı kullanmak üzere sürücü yerine oturmak; 2) mec. bir işin yönetimini üzerine almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4392, 'direktif almak', 'talimat almak, emredilmek: ''Herkes benden emir, direktif almaya mecbur değil!'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4393, 'direktif vermek', 'talimat vermek, emretmek, buyurmak: ''Projelere, tasavvurlara geçildi, Paşa direktifler veriyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4394, 'dirhemle söylemek (konuşmak)', 'çok az veya yavaş konuşmak: ''Üstadı, profesörle taban tabana zıt yaradılışlı bir insandı yani dirhemle lakırtı söylüyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4395, 'diri kalmak', '1) dinç, sağlıklı görünmek; 2) pirinç, bulgur vb. bakliyat gereği kadar pişmemiş olmak: Pirinçler biraz diri kalmış.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4396, 'dirlik yüzü görmemek', 'rahata kavuşamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4397, 'dirsek çevirmek', 'daha önce iş birliği yaptığı kişiyi uzaklaştıracak davranışlarda bulunmak: ''Bugünlerde size dirsek çevirmişler, sebebini biliyor musunuz?'' -E. Işınsu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4398, 'dirsek çürütmek', '1) okumak için yıllarca çalışmak: ''Dirsek çürütüp emek verdiği kitapları, can vermeden can bulunamayacağını ona hiç söylememişti.'' -S. Ayverdi. 2) öğrenimde veya meslekte uzun yıllar geçirmek: ''Bu meslekte senelerce dirsek çürüttüğüne göre kendisini gayet iyi anlayabilirdi.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4399, 'disipline girmek', 'disiplinli bir biçimde yaşamaya başlamak: ''İnsan hoşlandığı işte hamaratlaşıyor, gerekli disipline farkına bile varmadan giriyor.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4400, 'diskur geçmek (çekmek)', ' argo nutuk verir gibi konuşmak: ''Adayın etnoloji kürsüsüne layık olmadığına dair bir diskur geçer.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4401, 'diş açmak', 'madenî boruları birbirine birleştirebilmek amacıyla özel aletle sarmal yiv ve set oluşturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4402, 'diş çıkarmak', 'çene kemikleri içinde bulunan diş, diş etini deldikten sonra ağız boşluğuna doğru sivrilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4403, 'diş geçirmek', 'zorla veya inatla istediğini yaptırmak: ''Karşısındakine diş geçirmek inadı gene kabarmıştı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4404, 'diş gıcırdatmak', 'öfkesini davranışlarıyla göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4405, 'diş göstermek', 'güçlü olduğunu, saldırıya geçebileceğini durumuyla belli etmek, tehdit etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4406, 'dişe dokunmak', 'işe yarar olmak, önemli olmak, yerinde ve anlamlı olmak: ''Şöyle iki dişe dokunan, ciğere işleyen söz işitsem, şöyle tatlı, basit bir nağme duysam yok mu...'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4407, 'dişi köpek kuyruğunu sallamayınca, erkek köpek ardına düşmez', 'kadın istek göstermezse, yüz vermezse erkek onun peşine düşmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4408, 'dişi yalanmazsa erkek dolanmaz', 'kadın istek göstermezse, yüz vermezse erkek onun peşine düşmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4409, 'dişinden tırnağından artırmak', 'yiyecek giderlerini kısarak para biriktirmek: ''Susuz Yaz adlı öykü kitabımı, oyunlarımı hep böyle dişimden tırnağımdan artırarak bastırdım.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4410, 'dişine göre', '1) gücünün yeteceği, altından kalkabileceği bir durumda; 2) uygun, kolay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4411, 'dişine vurmak', '1) ısırmak, dişlemek; 2) mec. değerini anlamak için kontrol etmek: ''Kelimeyi dişimize vurmuşuz, beğenmişiz, saklamışız. Benimsemişiz.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4412, 'dişini sıkmak', 'darlığa, sıkıntıya dayanmak, katlanmak: ''Hele biraz dişini sık, hepsi yoluna girer.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4413, 'dişini tırnağına takmak', '1) çok büyük güçlüklere, sıkıntılara katlanmak: ''Türk milleti İstiklal Savaşı''nda varlığını dişini tırnağına takarak göstermişti.'' -A. Erhat. 2) bütün gücünü kullanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4414, 'dişinin kovuğuna bile gitmemek', 'yiyecek çok az gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4415, 'dişleri dökülmek', 'yaşlanmak, ihtiyarlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4416, 'dişli tırnaklı', 'saldırıcı olan, sözünü geçiren.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4417, 'dişten tırnaktan artırmak', 'dişinden tırnağından artırmak: ''Sabah akşam nerde, kimin tarlasında iş varsa gittik, dişten tırnaktan artırdık, zorla üç beş kuruş sahibi olduk.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4418, 'divan durmak', 'el pençe divan durmak: ''Araba yürürken karşımda divan durur gibi el pençe duruyor.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4419, 'divaneye dönmek', 'çok üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4420, 'diyalize girmek', 'diyaliz makinesine bağlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4421, 'diyalog kurmak', 'anlaşma ve uyum sağlayacak yolda karşılıklı konuşmak: ''Kendisiyle diyalog kuramamaktan yakındığımız insan, bazen en yakın çevremizden olabilir.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4422, 'diyecek yok', '''eleştirilecek bir yanı yok, söz yok'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4423, 'diz çökmek', '1) dizlerini yere koyarak oturmak: ''Beni dinleyin deyip hemen önümüze diz çöktü.'' -S. M. Alus. 2) dize gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4424, 'dize gelmek', 'baş eğmek, boyun eğmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4425, 'dize getirmek', 'kendisine karşı geleni yenerek buyruğuna uyacak duruma getirmek: ''Beş yüz sene evvel bahadır babalarımızın sizi dize getirerek zapt ettiği yerleri alamayacaksınız.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4426, 'dizgin vurmak', 'dizgin takmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4427, 'dizgine gelmek', 'düzelmek, belli bir disipline ve sisteme girmek: ''Kötülerin pek azı terbiyeye ve dizgine gelebilir.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4428, 'dizginleri (dizginlerini) ele almak', 'yönetimi eline geçirmek: ''Uykusunun dizginlerini ele almak ve istediği zaman uyanmak.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4429, 'dizginleri ele vermek', 'başkasının yönetimini kabullenmek: ''O koşturmalar yakayı kaptırışın, dizginleri ele verişin açıklamaları gibi geliyordu ona.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4430, 'dizginleri gevşetmek', 'birinin üzerindeki baskıyı azaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4431, 'dizginleri koparmak', 'her türlü bağ ve baskıdan kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4432, 'dizginleri salıvermek', 'başıboş bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4433, 'dizi (dizinin) dibinden ayrılmamak', 'yanından hiç gitmemek, ayrılmamak: ''Bir nişanlısı var ki hiçbir iş görmez, evden dışarı çıkmaz, kızın dizi dibinden ayrılmaz.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4434, 'dizini (dizlerini) dövmek', 'pişmanlık duymak: ''Ne ettik de kaderimizi İngilizlerle Fransızların kaderine bağladık diye dizlerini döven ... amatör diplomatlar.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4435, 'dizleri kesilmek (tutmamak)', 'dizlerinde derman, güç kalmamak: ''Şuracıktan şuracığa yürüyemedim. Dizlerim kesiliverdi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4436, 'dizlerine kara su inmek', 'beklemekten veya yorgunluktan güçsüz kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4437, 'dizlerinin bağı çözülmek', 'korkudan ayakta duramayacak duruma gelmek: ''Mengene gibi bir el, cerrahın yakasına yapışınca zavallının dizlerinin bağı çözülecek gibi oldu.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4438, 'doğan anası olma, doğuran anası ol', 'bir çocuk, annesinin değerini ancak kendisi de çocuk sahibi olduktan sonra anlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4439, 'doğduğuna bin pişman', 'anasından doğduğuna bin pişman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4440, 'doğduğuna pişman etmek', 'anasından doğduğuna pişman etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4441, 'doğduğuna pişman olmak', 'anasından doğduğuna pişman olmak: ''Doğduğuma pişman olacak kadar sıkıntı çektim.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4442, 'doğmadık çocuğa don biçilmez', 'ele geçeceği, ortaya çıkacağı daha belli olmayan şey için önceden hazırlık yapmak doğru değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4443, 'doğru bildiği yoldan ayrılmamak (şaşmamak)', 'her ne olursa olsun inandığı ilkelere bağlı kalmak: ''Bunları asla yapmayacağımı biliyorsun, su testisi su yolunda kırılır; ben doğru bildiğim yoldan ayrılmayacağım.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4444, 'doğru bilinmeyince eğri bilinmez', 'doğru ile yanlışı, iyiyle kötüyü birbirinden ayırmak için her ikisini de bilmek, tanımak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4445, 'doğru bulmak', 'uygun görmek, onamak: Onun yaptıklarını doğru buluyor musunuz?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4446, 'doğru çıkmak', 'gerçek olduğu anlaşılmak: ''Bu bari doğru çıksaydı, yazarlığıma geçmişte bir ipucu bulacaktım.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4447, 'doğru doğru dosdoğru', '''en doğrusu şudur ki'' anlamında kullanılan bir söz: Doğru doğru dosdoğru, bu işi yapan odur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4448, 'doğru durmak', '1) dik durmak; 2) uslu durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4449, 'doğru oturmak', 'uslu oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4450, 'doğru söyleyeni dokuz köyden kovarlar', 'doğru olmakla birlikte başkalarının işine gelmeyen sözleri söyleyenlerin sevilmediğini anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4451, 'doğru söyleyenin bir ayağı üzengide gerek', 'doğru sözlü olan kişi sevilmeyen, istenmeyen kişi olacağı için bulunduğu yerden ayrılmaya hazır olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4452, 'doğru söyleyenin tepesi delik olur', 'sözüm bir kimseye dokunur mu diye düşünmeyerek doğruyu söyleyen kişi çok düşman kazanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4453, 'doğru söz acıdır', 'eksikleri, yanlışları, yolsuzlukları bütün çıplaklığıyla ortaya koyan ve eleştiren söz, bu işi yapanlara acı gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4454, 'doğru söz yemin istemez', 'sözün doğruluğunda kuşku yoksa yemine gerek yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4455, 'doğruluk minarede kalmış onun da içi eğri', 'doğru görünen nice kişiler vardır ki içyüzlerini bilenlerden nasıl düzenbaz oldukları öğrenilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4456, 'doğrunun yardımcısı Allah''tır', 'işlerinde doğruluktan ayrılmayan kişiye Tanrı her zaman yardım eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4457, 'doğuran avrat Azrail''i yenmiş', 'yeni doğan çocuğu sürekli ölen kadın doğurmaktan usanmazsa elbet bir gün isteğine kavuşur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4458, 'doksan kapının ipini çekmek', 'içinde bulunduğu sorunu çözmek için kapı kapı dolaşmak, birçok yere uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4459, 'doktor doktor gezmek (dolaşmak)', 'tedavide çabuk ve kesin sonuç almak ümidiyle birçok doktora başvurmak: ''Çare bulunsun diye az mı ebe kapısı çaldılar, doktor doktor gezdiler?'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4460, 'doktora görünmek', 'muayene olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4461, 'dokunca görmek', 'zarara uğramak, harap olmak: ''Yangın çıkıp da okul büyük ölçüde dokunca görünce Galatasaray Lisesi buraya taşınmıştır.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4462, 'dokuz at bir kazığa bağlanmaz', 'bir işin başına, tanınmış kişiliği bulunan birçok kimse birden getirilmemelidir, bunlar anlaşamaz ve birbirlerine düşerler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4463, 'dokuz ayın çarşambası bir araya gelmek', 'birçok iş birden ortaya çıkıp sıkışık bir durum yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4464, 'dokuz doğurmak', 'merakla, heyecanla, sabırsızlıkla beklemek: ''Sabahtan beri kamış kökünün içine sığınmış, yüreği ağzında, dokuz doğurarak şahinini bekleyişi...'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4465, 'dokuz körün bir değneği', 'birçok kimsenin tek yardımcısı, tek dayanağı: ''Dokuz körün bir değneği, işte bir kızımız var.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4466, 'dokuz köyden kovulmuş', 'geçimsizliği veya başka davranışları yüzünden birçok yerden atılmış.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4467, 'dokuz yorgan eskitmek (paralamak)', 'çok uzun yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4468, 'dolaba girmek (gelmek)', 'aldatılmak, oyuna gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4469, 'dolabı bozulmak', '1) kurduğu iş düzeni bozulmak; 2) mec. hilesi ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4470, 'dolanıp durmak', 'sürekli olarak aynı yerde gezinmek: ''Dolap beygirinin en büyük şansı gözlerinin bağlı olmasıdır; böylece aynı çember içinde dolanıp durduğunun farkında olmaz.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4471, 'dolap beygiri gibi dönüp durmak (dolaşmak)', 'dar bir çevrede hep aynı işi yapmak: ''Bir dolap beygiri gibi dönüp dolaşarak ağaçları, çiçekleri sulardım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4472, 'dolap çevirmek (döndürmek)', 'hile ve dalavere ile iş yapmak: ''İleride işler yapmaya, dolaplar çevirmeye başlarsa kendi de bundan istifade edecekti.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4473, 'dolduruşa gelmek', ' argo 1) olumsuz yönde yönlendirilmek, kışkırtılmak; 2) biri çeşitli yollarla pohpohlanarak yönlendirilmek, kışkırtılmak, gaza getirilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4474, 'dolduruşa getirmek', ' argo birini çeşitli yollarla pohpohlayarak yönlendirmek, kışkırtmak, gaza getirmek: ''Kimler dolduruşa getirdi sizleri, kimlere kandınız?'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4475, 'dolduruşa kapılmak', 'dolduruşa gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4476, 'dolma yutmak', ' argo kanıp aldanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4477, 'dolmuş yapmak', 'dolmuşla yolcu taşımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4478, 'dolmuşa gelmek (binmek)', 'dolduruşa gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4479, 'doludizgin gitmek', '1) son hızla koşmak; 2) mec. kendini kaptırıp sürüklenmek: ''Kendimi yalnız sanıyor ve talihin arabasında doludizgin gidiyordum.'' -A. H. Tanpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4480, 'dolup taşmak', '1) gereğinden çok olmak, gereğinden çok kaplamak: ''Dışarıda bulutsuz bir temmuz göğü, öğle güneşinin yakıcı aydınlığıyla dolup taşıyordu.'' -N. Cumalı. 2) çok kalabalık olmak: Millî takımın karşılaşmalarında stadyumlar dolup taşıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4481, 'doluya koydum almadı, boşa koydum dolmadı', 'içinden çıkılmayan güç bir durum karşısında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4482, 'domuz derisi post olmaz eski düşman dost olmaz', 'birçok nedenin birbirini izlemesiyle sürüp gelmiş olan eski düşmanlık, dostluğa dönüştürülemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4483, 'domuz gibi', ' tkz. 1) kötü huylu ve hain; 2) adamakıllı, iyice: Domuz gibi bilir ama söylemez!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4484, 'domuz gibi tıkınmak (yemek)', 'oburcasına çok yemek: ''Şişmanlıyorum, neden yine bir domuz gibi tıkındım?'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4485, 'domuzdan (bir) kıl çekmek (koparmak)', 'sevilmeyen veya eli sıkı olan birinden bir şey alabilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4486, 'domuzdan toklu çıkmaz (doğmaz)', 'kötü huylu kimsenin çocuğu melek huylu olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4487, 'domuzluk etmek', 'hainlik etmek, haince davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4488, 'domuzun kuyruğunu kes yine domuz', 'yaradılıştan kötü olan kişinin şu, bu yönünü düzeltseniz de mayasındaki bozukluğu gideremezsiniz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4489, 'don çekmek', 'donmak: ''Badem ağacı, ayaz vurmaz, don çekmez, solmaz, dökülmez çiçeklerini açmıştı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4490, 'don çözülmek', 'hava ısınarak buzlar erimeye başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4491, 'don gömlek kalmak', 'her şeyini kaybetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4492, 'don kesmek', ' hlk. bitki soğuktan bozulmak, donmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4493, 'don tutmak', 'buz tutmak, donmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4494, 'don yağı gibi', 'konuşmayan, hareketsiz (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4495, 'don yağının tortusu gibi kalmak (oturmak)', 'çevresindekilerle iletişim kurmadan ilgisiz ve donuk kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4496, 'dona çekmek', 'hava, suları donduracak derecede soğumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4497, 'donsuzun gönlünden dokuz top bez geçer', 'bir şeyden yoksun olan kişinin gönlünden hep o şeyden bol bol edinmek geçer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4498, 'donuna etmek (kaçırmak veya doldurmak veya yapmak)', '1) küçük veya büyük abdestini donuna etmek; 2) mec. çok korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4499, 'donup kalmak', 'donakalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4500, 'doping yapmak', '1) bazı bedensel özellikleri değiştiren veya artıran bir uyarıcı maddeyi çok az miktarda almak: ''Günahı boynuna, doping de yapıyormuş.'' -H. Taner. 2) mec. uyarıcı etkide bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4501, 'dost acı söyler', 'yakınlarımız, eksikliklerimizi çekinmeden söylerler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4502, 'dost ağlatır, düşman güldürür', 'dost olan kimsenin söylediği söz, acı da olsa insanın iyiliği içindir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4503, 'dost (akraba) ile ye, iç, alışveriş etme', 'alışverişte iki taraf da kendi çıkarını düşündüğünden iki dost arasındaki alışveriş dostluğu bozabilir, bu nedenle de dostluklarını sürdürmek isteyenler birbirleriyle alışverişte bulunmamalıdırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4504, 'dost başa, düşman ayağa bakar', 'iyi bir görüntü verebilmek için her zaman temiz giyinip kuşanmak gereklidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4505, 'dost bin ise azdır, düşman bir ise çoktur', 'dostlarını olabildiğince çoğalt, düşmanlarını olabildiğince azalt.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4506, 'dost dostun ayıbını yüzüne söyler', 'gerçek dost uyarmak, kusurun düzeltilmesini sağlamak amacıyla ayıbı yüze karşı söyler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4507, 'dost dostun eyerlenmiş atıdır', 'gerçek dost, arkadaşının sıkışık zamanında yardımına koşmaya hazır durumdadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4508, 'dost edinmek', 'dost kazanmak: ''Yolda iki dost edinip on gün birisinin, on gün ötekinin erzak torbasından karnını doyurdu.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4509, 'dost (iyi dost) kara günde belli olur', 'gerçek dost üzüntülü, sıkıntılı günlerde insanı yalnız bırakmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4510, 'dost sözü acıdır', 'yakınlarımız, eksikliklerimizi çekinmeden söylerler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4511, 'dost tutmak', 'erkek veya kadın evlilik dışı ilişki kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4512, 'dosta düşmana karşı', '''dostlara üzüntü vermemek, düşmanları da sevindirmemek için, ele güne karşı'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4513, 'dostlar alışverişte görsün (diye)', '''gösteriş olsun, iş görüyor densin (diye)'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4514, 'dostlar başına', 'bir şeyi dostları için de dilemek amacıyla kullanılan bir iyi dilek sözü: ''Doğrusu böyle bir düğün dostlar başınaydı. Arkadaşları arasında, günlerden beri hep bunun lafı ediliyordu.'' -R. Çalapala.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4515, 'dostlar başından ırak', 'sözü edilen kötü bir durumla yakınların karşılaşmaması için söylenen iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4516, 'dostlar şehit, biz gazi', ' alay tehlikeli işleri başkalarına bırakıp kendileri sonuçtan yararlanmak için bir kenara çekilenlerin bencilliğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4517, 'dostluk başka, alışveriş başka', 'iki kişi arasındaki dostluk, alışverişte birinin ötekine özverili davranmasını gerektirmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4518, 'dostluk etmek', 'yakınlık kurmak, dost gibi candan davranmak: ''Lokanta müşterisi hanımlardan kendi kendine tanıştığı, konuştuğu, dostluk ettiği hanımlar var!'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4519, 'dostluk kantarla, alışveriş (hesap) miskalle', 'iş ilişkilerine dostluk karıştırılmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4520, 'dostluk okkayla, alışveriş dirhemle', 'dostluğun tartısı olmaz, alışveriş ise ölçüye göre olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4521, 'dostun attığı taş baş yarmaz', 'dostun acı sözü veya sert davranışı insana ağır gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4522, 'dosya açmak (hazırlamak)', 'bir kimse, konu veya işle ilgili yeni bir dosya düzenlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4523, 'dosyası kabarmak (kabarık olmak)', 'yaptığı yanlış işleri çoğalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4524, 'doyuma ulaşmak', 'istek ve gereksinimlerinin en üst düzeyini elde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4525, 'doyurucu bulmak', 'yeterli görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4526, 'doyurucu gelmek', 'yeterli olmak: ''Verdiğim yanıt doyurucu gelmemişti madama ama kibarlığını da bozmamıştı.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4527, 'dozunu ayarlamak', '1) ilacın ölçüsünü aşmamak, gerektiği kadar vermek; 2) mec. ölçüyü aşmamak, aşırı davranmamak: ''Saygının ve sevginin dozunu iyi ayarlayabilmeli insan.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4528, 'dozunu kaçırmak', '1) ilaçta ölçüyü tutturamamak; 2) mec. ölçüyü aşmak, aşırı gitmek: ''Şakanın dozu kaçmıştı.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4529, 'dökme su ile değirmen dönmez', 'işi yapacak olanda yeteri kadar güç bulunmadıkça başkalarının küçük katkılarıyla sürekli ve büyük bir iş yürütülemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4530, 'dökülüp saçılmak', '1) soyunmak, çok açılmak; 2) bir şey uğruna çok para harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4531, 'döküm almak', 'ayrıntılı hesap listesini toplu olarak göstermek: ''Bu hesapların dökümlerini alıp sizlere vereceğim.'' -N. Eray.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4532, 'döküm çıkarmak', 'bütün hesap işlemlerini bir listeye yazmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4533, 'döküp saçmak', 'dağıtmak, ziyan etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4534, 'döl almak', 'cins bir hayvandan yararlanarak iyi cins yavru almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4535, 'döl döş sahibi olmak', 'çocuk ve torunları bulunmak: ''Gün gelir, evlenir, döl döş sahibi olur, durulur.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4536, 'döl vermek', '1) yavru vermek, üremek; 2) ürün vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4537, 'dönüp dolaşmak', '1) uzun süre gezmek; 2) mec. arayış içinde olmak, her çareye başvurmak: ''Yirmi sene hep aynı renkler içinde dönüp dolaştık.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4538, 'dönüp geriye bakmak', 'eskiyi hatırlamak, geçmişi gözden geçirmek: ''Şimdi dönüp geriye baktığımda ne görüyorum? Kimi insanlar hayatımızı bir karikatüre çevirmek için ellerinden geleni yapıyorlar.'' -S. Dölek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4539, 'dönüşü olmayan yola girmek', 'asla bırakılmayacak, vazgeçilmeyecek bir durumda olmak: ''Artık ok yaydan çıkmış sayılırdı, dönüşü olmayan bir yola girdikleri kesindi.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4540, 'dört ayak üstüne düşmek', '1) tehlikeli bir durumdan zarar görmeden kurtulmak; 2) işi rast gitmek: ''Yüze gülücü, her dönemde dört ayak üstüne düşen Efruz''un hayat hikâyesini sergileyen piyesim, yurtta bini aşkın defa oynadıktan sonra, televizyon oyunu hâline getirilince yasaklandı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4541, 'dört bir taraf (yan)', 'her yan, bütün çevre: ''Oğulları babasını iyileştirmek için dört bir yana koşuşurdu.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4542, 'dört dönmek', '1) telaşla çare aramak: ''Cemil, Cemil! diye haykırarak yağmurun altında dört dönüyordum.'' -R. N. Güntekin. 2) bir iş yapmak için telaşla sağa sola koşmak: ''Bizi memnun etmek için etrafımızda dört dönüyordu.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4543, 'dört duvar arasında kalmak', 'evde, kapalı bir yerde kalmak zorunda olmak: ''Ömrünü dört duvar arasında geçirmiş, çocuklarından başka insan yüzü görmemiş temiz bir ev kadını birdenbire değişemezdi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4544, 'dört göz bir evlat için', 'anne ve babanın bütün emek ve didinmesi evlat içindir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4545, 'dört gözle beklemek (bakmak)', 'çok isteyerek veya özleyerek beklemek: ''Terekesini paylaşmak için dört gözle ölümünü beklemekteydiler.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4546, 'dört üstü, murat üstü', 'işi her zaman yolunda olanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4547, 'dört yanı deniz kesilmek', 'çaresiz ve umutsuz kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4548, 'dörtköşe olmak', 'çok keyiflenmek, çok zevk almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4549, 'dörtnala kaldırmak', 'dörtnal koşturmaya başlamak: Atı dörtnala kaldırdı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4550, 'dörtnala kalkmak', 'dörtnal koşmak: ''Atlar bazen dörtnala kalkıyor, bazen tırısa geçiyordu.'' -R. Enis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4551, 'döşeğe düşmek', 'yatağa düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4552, 'döviz kaçırmak', 'yurt dışına izinsiz döviz çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4553, 'dramatize etmek', '1) bir edebî eseri radyo, televizyon veya sahne oyunu biçimine getirmek; 2) mec. bir olayı olduğundan daha acıklı, abartılı bir biçimde ortaya koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4554, 'dua (duasını) almak', 'iyi yapılan bir işle birinin hoşnutluğunu kazanmak: ''Elini öpüp duasını almak istedim.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4555, 'duası tutmak', '1) duası gerçekleşmek: ''Duasının tutup tutmayacağını söyleyemezdi.'' -T. Buğra. 2) etkili olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4556, 'duba gibi', 'çok şişman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4557, 'duble etmek', 'astar geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4558, 'dudağını (dudaklarını) ısırmak', 'yakışıksız bir durum karşısında şaşmak: ''Koca Ali bu kararı duyunca ömründe ilk defa olarak sarardı. Dudaklarını ısırdı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4559, 'dudağının ucuna gelmek', 'hemen söyleyecek durumda olmak: ''Bayram, dudağının ucuna gelen soruyu soramadı.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4560, 'dudak dudağa gelmek (kalmak)', 'öpüşmek: ''Bir zaman böyle birbirini karşılıklı öpücüklere boğduktan sonra, nefesleri kesilinceye kadar dudak dudağa kaldılar.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4561, 'dudak (dudağını) bükmek', '1) bir şeyi beğenmediğini, küçümsediğini belli etmek, umursamamak, pek aldırış etmemek: ''Masalların yıllarca uzakları gösteren büyülü aynasına bugünün çocukları dudak bükerler.'' -N. Hikmet. 2) ağlayacak gibi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4562, 'dudak ısırmak', '1) hayran kalmak; 2) hayrete, şaşkınlığa düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4563, 'dudak sarkıtmak', 'somurtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4564, 'dudak ucuyla söylemek', 'belli belirsiz anlatmak, isteksizce söylemek: ''Size hayır kalmadığını dudak ucuyla söyleyiverirler ve gerçekten dedikleri de çıkar.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4565, 'dul kalmak', 'kadın veya erkeğin eşi ölmek: ''Hatice Hanım pek genç dul kalmış zengin bir hanımcağızdı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4566, 'dulda tutmak', 'örtünmek, koruyacak biçimde sarınmak: ''Bulgar dağında yatarım / Yorganı dulda tutarım'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4567, 'duman almak', '1) sis kaplamak, sis bürümek; 2) sigara dumanını içine çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4568, 'duman altı etmek', 'bulunulan yerin havasını esrar, sigara vb. dumanıyla doldurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4569, 'duman altı olmak', 'esrar, sigara vb. içilen bir yerin havasından etkilenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4570, 'duman attırmak', ' argo kötü duruma düşürmek, geride bırakmak, birini yıldırmak: ''Ama yerine göre karşısına dikilenlere de duman attırır.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4571, 'duman etmek', ' argo 1) dağıtmak, bozmak, yok etmek: ''Ortalığı duman görür, duman etmek isterdi.'' -S. F. Abasıyanık. 2) yenmek, başarı sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4572, 'duman vermek', '1) çok duman çıkarmak; 2) mec. ortalığı karıştırmak: ''Sonra sen gazetende istediğin gibi ver dumanı.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4573, 'dumana boğmak', '1) duman içinde bırakmak; 2) mec. bunaltmak, şüphe içinde bırakmak: ''Adamın kafasını katiyen aydınlatmamalı, karıştırmalı ve dumana boğmalısınız.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4574, 'dumanı doğru çıksın', '''iyi ve güzel olmasa bile yönteme uygun olsun'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4575, 'dumanı tepesinden çıkmak', 'bir acının ateşiyle yanıp tutuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4576, 'dumansız baca olmaz, kahırsız koca olmaz', 'dumanı olmayan baca olamayacağı gibi karısına sıkıntı vermeyen koca da olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4577, 'dumura uğramak', 'körelmek: ''Aşk, bende öyle dumura uğramış bir duygu ki sevmek hasretini bile duyamıyorum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4578, 'dur durak (dur dinlen veya dur otur) yok', 'durup dinlenmeden sürekli çalışmayı anlatan bir söz: ''Gayri bana dur durak yok... Muhasebe müdürü ... çalışmamdan hoşnut değilmiş.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4579, 'dur! (durun!)', '''biraz zaman geçsin'' anlamıyla cümlelerin başına gelen bir söz: Dur! Bu işi ben yaparım. Durun hele, bakalım ne olacak!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4580, 'durdu durdu, turnayı gözünden vurdu', 'uzun süre bekledi ancak sonunda isteğini elde etti.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4581, 'durgunluk çökmek', 'sessiz, sakin duruma girmek: ''Posta kâtibi eskiden çok sert bir adamdı. Fakat gitgide ona garip bir durgunluk çökmüştü.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4582, 'durum almak', '1) belli bir duruş biçimine geçmek; 2) bir olay karşısında belli bir tavır almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4583, 'durumdan ders çıkarmak', 'içinde bulunulan şartları değerlendirerek yanlış adım atmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4584, 'durumdan vazife çıkarmak', 'içinde bulunulan şartları değerlendirerek sorumluluk yüklenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4585, '... durumuna düşmek', 'şartları kötüleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4586, '... durumunda olmak (bulunmak)', 'zorunluluğunda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4587, 'dut gibi olmak', '1) çok sarhoş olmak; 2) utanmak, mahcup olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4588, 'dut kurusu ile yâr sevilmez', 'ancak büyük fedakârlıklarla elde edilebilecek güzel bir şey, fedakârlık yapılmadan elde edilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4589, 'dut yaprağı açtı, soyun; döktü giyin', 'dut ağacı yapraklanınca soğuklar biter, dut ağacı yaprağını döktüğü zaman soğuklar başlar; ona göre giyinmek lazımdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4590, 'dut yemiş bülbüle dönmek', 'neşe ve konuşkanlığını yitirmek, susmak: ''Sabahtan akşama kadar durmadan söyleyen geveze Çalıkuşu, dut yemiş bülbüle dönmüştü.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4591, 'duvağına doymamak', 'yeni gelinken ölmek veya kocasından ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4592, 'duvar çekmek', '1) duvar örmek; 2) mec. aradaki ilişkiye son vermek, görüşmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4593, 'duvar gibi', 'sağır.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4594, 'duvar yapmak', 'baraj yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4595, 'duvarı nem, insanı gam yıkar', 'nem demiri nasıl paslandırıp çürütürse gam da insanı öylece yıpratır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4596, 'duygu uyanmak', 'bir duygu oluşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4597, '... duygusu uyandırmak', 'bir duygu oluşturmak: ''Bu çeşit mülahazalar bizde ancak bir isyan duygusu uyandırabilirdi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4598, 'duymazlıktan gelmek', 'ilgilenmek istemediği için duymamış gibi davranmak: ''Evine gönderilen haberleri hep duymazlıktan gelmişti.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4599, 'duyulur duyulmaz', '1) çok alçak ancak işitilebilen (ses); 2) haber öğrenilir öğrenilmez.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4600, 'duyum almak', 'bir konu hakkında haber almak, bilgi edinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4601, 'duyuruda bulunmak', 'duyurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4602, 'düdük gibi', 'çok dar, daracık (giysi).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4603, 'düdük gibi kalmak', '1) yapayalnız kalmak; 2) zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4604, 'düğmük atmak', 'düğümlemek: ''Bazı aileler resmî nikâh yanında bir de imam nikâhı kıyıveriyorlarmış. Bunu da bir paketin kınnapla bağlandıktan sonra düğmük üstüne düğmük atılmasına benzetiyorlar.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4605, 'düğüm atmak', 'düğümlemek: ''Bir ağıtla mendillerinin, yazmalarının ucuna düğüm attılar.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4606, 'düğüm düğüm olmak (düğümlenmek)', '1) çok karışık bir duruma gelmek; 2) birbirine geçmek; 3) içinden çıkılmaz bir durum almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4607, 'düğüm üstüne düğüm vurmak (atmak)', 'parasını pintilik ederek saklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4608, 'düğüm vurmak', '1) düğümlemek; 2) parasını pintilik ederek saklamak, biriktirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4609, 'düğümünü çözmek', 'anlaşılmaz bir şeyi anlaşılır duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4610, 'düğün aşıyla dost ağırlanmaz', 'ağırlamanın değeri, özel olarak hazırlanmasında, bir fedakârlık yapılmasındadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4611, 'düğün aşıyla dost ağırlanmaz', '''ağırlamanın değeri, özel olarak hazırlanmasında, bir fedakârlık yapılmasındadır'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4612, 'düğün bayram etmek', 'çok sevinmek, çok sevinç duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4613, 'düğün değil bayram değil, eniştem beni niye öptü', 'gösterilen yakınlığın, iltifatın gizli bir nedeni olduğu düşünüldüğünde söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4614, 'düğün dernek, hep bir örnek', 'olayların veya yapılan işlerin hep birbirine benzediğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4615, 'düğün olur iki kişiye, kaygısı düşer deli komşuya', 'akılsız kişi, başkalarının eğlence programlarında bir aksama olmasın diye çabalar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4616, 'düğün pilavıyla dost ağırlamak', 'başkasının kesesinden veya elinden ikramda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4617, 'düğünevi gibi', 'sevinçli ve telaşlı bir kalabalık bulunan (yer).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4618, 'düğünü okuyucu boklar', 'iki taraf arasındaki güzel ilişkileri, söz götürüp getiren anlayışsız aracı bozar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4619, 'dümen çevirmek', ' tkz. hileye, düzene başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4620, 'dümen kırmak', 'yön değiştirmek: ''Herhâlde kaçmayı düşünüyor olmalıydı. Yolun kenarındaki tek tük ağaçlara doğru dümen kırdı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4621, 'dümen kullanmak', ' argo bir işi kurnazca yönetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4622, 'dümen tutmak', ' den. teknenin gideceği yolu gözleyerek dümeni yönetmek: ''Kimimiz dümen tutar mavnalarda / Kimimiz çımacıdır halat başında'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4623, 'dümen yapmak', ' argo dalavere, hile ile birini kandırmak, aldatmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4624, 'dümeni elinde tutmak', 'yönlendirici durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4625, 'dümeni kırmak', ' argo çekip gitmek, kaçmak, uzaklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4626, 'dümenine bakmak', ' argo şartlar ne olursa olsun çıkarını gözetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4627, 'dün bir, bugün iki', '''herhangi bir şeye başladığından beri çok az zaman geçtiği hâlde'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4628, 'dün cin olmuş, bugün adam çarpıyor', 'işinde ustalaşmadan hile yollarına başvuruyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4629, 'dün öleni dün gömerler', 'bir üzüntü sürdürülmemeli, unutulmaya çalışılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4630, 'dünden hazır (razı) olmak', 'kendisine yapılan bir öneriyi seve seve ve hemen kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4631, 'dünür gezmek', 'evlenecek erkek için kız aramaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4632, 'dünür gitmek', 'evlenecek kimse için kız istemeye gitmek: ''Dayısı, amcası dâhil, obadan, oymaktan kimse dünür gitmeye gönüllü değildir.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4633, 'dünya ahret kardeşim (bacım) (olsun)', 'bir kişiye kardeşlik duygusundan başka bir gözle bakılmadığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4634, 'dünya başına dar olmak (gelmek)', 'çok sıkılmak, büyük bir çaresizlik içinde kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4635, 'dünya başına yıkılmak', 'çok sıkılmak, umutlarını yitirmek: ''Defteri abimin elinde görünce, dünya başıma yıkıldı, basbayağı gözlerim karardı.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4636, 'dünya bir araya gelse', '1) ''dünyadaki bütün insanlar engel olmaya kalksa bile'' anlamında kullanılan bir söz: ''... bütün dünya bir araya gelse fikrimi değiştiremez.'' -Ö. Seyfettin. 2) ''dünyadaki bütün insanlar bir araya toplansa bile'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4637, 'dünya bir, işi bin', 'bu dünyada insanın hatır ve hayaline gelmeyen türlü türlü durumlar ortaya çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4638, 'dünya durdukça durasın!', '''çok yaşa, Tanrı sana sonsuz bir ömür versin!'' anlamında kullanılan bir iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4639, 'dünya (dünyalar) birinin olmak', 'çok sevinmek: ''Suların üzerimize devrilmesinden önce yukarıya bir varsak dünya bizim olacaktı.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4640, 'dünya gözü ile görmek', 'ölmeden önce görmek: ''Seni dünya gözüyle bir daha görmeyi nasip edene şükrolsun.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4641, 'dünya gözüne zindan olmak (görünmek veya kesilmek)', 'büyük bir karamsarlık ve umutsuzluk içinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4642, 'dünya kadar', 'pek çok: ''Eve döneyim desen Feneryolu istasyonuna dünya kadar yol var.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4643, 'dünya kelamı etmek', '1) konuşmak; 2) konuşulmaması gereken yerde konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4644, 'dünya malı dünyada kalır', 'insan öldüğü zaman malını öbür dünyaya götüremez, bu nedenle gerek kendisi için gerekse hayırlı işler için para harcamaktan kaçınmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4645, 'dünya ölümlü, gün akşamlı', 'hiçbir durum sürekli değildir, her iyi durumun bir sonu vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4646, 'dünya Süleyman''a bile kalmamış', 'insan ne kadar zengin olursa olsun bu dünyadan göçüp gidecektir, bu nedenle dünyaya bel bağlamamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4647, 'dünya tükenir, yalan tükenmez', 'dünyada çok sayıda yalancı vardır, bunları huylarından vazgeçirmek de imkânsızdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4648, 'dünya varmış', 'sıkıntılı bir durumdan kurtulan kimsenin söylediği söz: İçerisi zindan gibiydi, oh burada dünya varmış!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4649, 'dünya yıkılsa umurunda değil', 'hiçbir şeyle ilgilenmez, sorumsuz, kaygısızdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4650, 'dünya yıkılsa umurunda değil', '''hiçbir şeyle ilgilenmez, sorumsuz, kaygısız'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4651, 'dünya yüzü görmemek', 'kapalı bir yerde sürekli kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4652, 'dünyada tasasız baş bostan korkuluğunda bulunur', 'bu dünyada tasasız olan insan yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4653, 'dünyadan el etek (elini eteğini) çekmek', 'bir kenara çekilip çevresiyle ilgisini kesmek, toplumun yaşayışına karışmamak, dünya işleriyle ilgilenmez olmak: ''Yedi saatlik evliler, şimdiden mi dünyadan el etek çekiyor?'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4654, 'dünyadan geçmek (el çekmek)', 'bir kenara çekilip toplum yaşamına karışmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4655, 'dünyadan haberi olmamak', 'çevresinde olup bitenleri bilmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4656, 'dünyaevine girmek', 'evlenmek: ''Yaşları daha genç görünüyor fakat buralarda yapılan ilk iş eli ekmek tutar tutmaz dünyaevine girmek olduğu için kim bilir kaç sene evvel evlendiler.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4657, 'dünyalara değişmemek', 'her şeyden daha fazla sevmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4658, 'dünyalığı doğrultmak', 'yaşamı süresince yetecek parayı kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4659, 'dünyanın dört bucağı', 'dünyanın her yanı, her yönü: Dünyanın dört bucağından gelen gezginler...', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4660, 'dünyanın kaç bucak (köşe) olduğunu göstermek (anlamak)', 'dünyada ne gibi güçlükler olduğunu bildirmek (anlamak), insanın başına neler gelebileceğini öğretmek veya öğrenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4661, 'dünyanın öbür ucu', 'çok uzak yer.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4662, 'dünyanın tadını çıkarmak', 'bütün zevklerden yararlanmak, mutlu ve rahat yaşamak: ''Dünyanın tadını çıkarmaya devam ettik.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4663, 'dünyanın ucu uzundur', 'insanın yaşadıkça türlü durumlarla, çeşitli olaylarla karşılaşabileceğini anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4664, 'dünyasından geçmek', 'her şeye karşı ilgisiz duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4665, 'dünyaya gelmek', 'insan, doğmak: ''Sonunda ne kadar istedilerse de erkek çocukları dünyaya gelmedi.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4666, 'dünyaya getirmek', 'doğurmak: ''Hayriye Hanım yedi gün evvel ilk çocuğunu dünyaya getirmiştir.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4667, 'dünyaya gözlerini kapamak (yummak)', 'ölmek: ''Bir sabah söyledi son sözlerini / Yumdu dünyaya ela gözlerini'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4668, 'dünyaya kazık çakmak (kakmak)', ' tkz. çok uzun ömürlü olmak, çok yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4669, 'dünyaya yuf borusu öttürmek', 'ölmek: ''Mektubun elinize değmesinden epeyce zaman evvel dünyaya yuf borusu öttürmüş olacak.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4670, 'dünyayı anlamak', 'dünyada neler olduğunu öğrenmek, deneyimi artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4671, 'dünyayı ben yarattım demek', 'aşırı mağrur olmak, büyüklenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4672, 'dünyayı ben yarattım havasında olmak', 'çevresinde güçlü olduğu düşüncesini oluşturmak: ''O da oğlanın, dünyayı ben yarattım havalarındaki tavrından rahatsız olmuştu.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4673, 'dünyayı görmemek', 'bir konuya veya bir işe aşırı odaklanıp çevre ile ilgilenmemek: ''Günlerce, haftalarca kitapların içine gömülür, dünyayı görmezdim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4674, 'dünyayı haram etmek', 'bir yeri yaşanılmaz duruma getirmek: ''... kadıncağıza, o iki zavallı öksüz kızcağıza, dünyayı haram ediyor.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4675, 'dünyayı sel bassa ördeğe vız gelir', 'birçok kimse için felakete yol açan bir olay, bazı insanları ilgilendirmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4676, 'dünyayı tozpembe görmek', 'üzücü durumlara bile iyimser gözle bakmak: ''Gümüş şamdanların, pembe karanfillerin, kristallerin renk renk, ışık ışık parladığı sofralarda melek yüzlü, tatlı dilli insanlarla konuşur, dünyayı tozpembe görürdük.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4677, 'dünyayı tutmak', 'çok yayılmak, her yere dağılmak: ''Şöhreti dünyayı tutan Paris kadını nadiren güzeldir.'' -A. Haşim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4678, 'düş kırıklığı yaratmak', 'beklentileri karşılayamamaktan dolayı burukluğa yol açmak: ''Karşısındaki kadında düş kırıklığı yarattığının farkında.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4679, 'düş kırıklığına uğramak', 'beklediği sonucu alamamak: ''Uğradığı düş kırıklığı adımlarına yansıyan sünepe bir adam.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4680, 'düş uykudan sonra olur (gelir)', 'bir işin temeli gerçekleşmedikçe ayrıntılarına sıra gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4681, 'düşenin dostu olmaz', 'varlıklı kişi yoksullaşınca çevresindeki dostlarından kimse kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4682, 'düşenin dostu olmaz hele bir düş de gör', 'varlıklı kişi yoksullaşınca çevresindeki dostlarından kimse kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4683, 'düşeş atmak', '1) tavlada zarlar altı altı gelmek: ''Terlikçi İhsan, üst üste iki düşeş atmakla marsı sağlamış gibiydi.'' -H. Taner. 2) mec. umulmadık bir başarı kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4684, 'düşman başına', 'durumun kötü olduğunu göstermek için kullanılan bir söz: ''Hele ihtiyarlıkta yatağa düşmek, düşman başına.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4685, 'düşman çatlatmak', 'iyi durum ve başarılarla düşmanı kıskandırmak veya kızdırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4686, 'düşman düşmana gazel (Yasin) okumaz', 'düşmandan ancak kötülük beklenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4687, 'düşman (düşmanı) kesilmek', 'düşman olmak, düşman gibi görmek: ''Şu dakika yalnız bu memleketin değil, bütün insanlığın düşmanı kesilmişti.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4688, 'düşman olmak', 'kin beslemeye başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4689, 'düşmana yarak (silah) gerek, ya düşmandan ırak gerek', 'düşmanın hakkından gelebilmek için silah gerekir eğer silahınız yoksa düşmandan uzak durunuz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4690, 'düşmanın karınca ise de hor bakma', 'düşmanın ne kadar güçsüz olursa olsun dikkat et, uyanık ol.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4691, 'düşmanın karınca olsa kendini merdane tut', 'düşmanın ne kadar güçsüz olursa olsun dikkat et, uyanık ol.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4692, 'düşmez kalkmaz bir Allah', 'insanların talihsizliklere uğraması olağandır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4693, 'düşün düşün, boktur işin', 'kötü bir durumdan çıkar yol bulunamadığı zaman söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4694, 'düşüncesini açmak', 'görüşünü bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4695, 'düşüncesini okumak', 'bir kimsenin ne düşündüğünü anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4696, 'düşünceye dalmak', 'derin derin düşünmek: ''Rıhtımda bir aşağı bir yukarı dolaşanları seyre müsait bir iskemlede düşünceye daldım.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4697, 'düşünceye varmak', 'bir görüşe veya karara varmak, bir inanca ulaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4698, 'düşünüp (düşünmek) taşınmak', 'konuyu bütün yönleriyle inceleyip ona göre davranmak, iyice düşünmek: ''Düşündüm taşındım. Yani, insan dişi ağrırken ne kadar düşünüp taşınabilirse, o kadar düşünüp taşındım işte.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4699, 'düt demeye dudak gerek (ister)', 'bir işin gerçekleştirilebilmesi, gerekli koşulların, araçların bulunmasına bağlıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4700, 'düven sürmek (dövmek)', 'düvenle ekinlerin tanelerini başaklarından çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4701, 'düz duvara tırmanmak', 'çocuk, çok yaramazlık yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4702, 'düze indirmek', 'eşkıyalıktan vazgeçirmek: ''Ezilmemiş onun karşısında, kısadan giderivermiş vaatle düze indirdiği adamın merakını.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4703, 'düze inmek', 'eşkıyalıktan vazgeçmek: ''Düğünevinin avlusuna girerken yeni düze inmiş efeler gibi nara attı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4704, 'düzen kurmak', '1) işler duruma getirmek; 2) düzenlemek: ''Ağaçlarla evler arasında bir düzen kurmadıkça bir şehrin tadı tuzu kalır mı?'' -B. R. Eyuboğlu. 3) mec. hileye başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4705, 'düzen vermek (düzene koymak, düzene sokmak)', '1) düzenlemek, dağınıklıktan kurtarmak: ''Onun kendi yaşayışına yeni bir düzen vermesi gerekiyordu.'' -T. Buğra. ''Yatak odasını düzene sokmakla meşguldü.'' -R. H. Karay. 2) akort etmek: ''Şu sazıma bir düzen ver.'' -Âşık Ali İzzet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4706, 'ebedî uykuya dalmak', 'ölmek: ''Bu mezarda iki harp ve aile kahramanı ebedî uykusuna dalmıştı.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4707, 'ebediyete intikal etmek', 'ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4708, 'ecel aman verirse', '''ömür yeterse, ölmezsem'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4709, 'ecel geldi cihana, baş ağrısı bahane', 'herkesin ölümü için bir sebep vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4710, 'ecele çare bulunmaz', 'ölüm dışında, çaresiz gibi görünen her güç işin bir çıkar yolu vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4711, 'eceli gelen fare kedi taşağı kaşır', 'herkesin üzerine titrediği, kutsal saydığı şeyi kötüleyen, bozan kimse mutlaka kötü bir sonuçla karşılaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4712, 'eceli gelen (yaklaşan) köpek cami (mescit) duvarına (avlusuna) siyer (işer)', 'herkesin üzerine titrediği, kutsal saydığı şeyi kötüleyen, bozan kimse mutlaka kötü bir sonuçla karşılaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4713, 'eceli gelmek', 'ölümü veya yok olması kaçınılmaz duruma gelmek: ''Bizim oralarda eceli gelmeden vuruyorlar adamı.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4714, 'eceline susamak', 'ölmek istermiş gibi tehlikeli işlere girişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4715, 'eceliyle ölmek', 'olağan sayılan herhangi bir biçimde ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4716, 'edebi edepsizden öğren', 'edepsizin yaptığı işlerin yapılmaması gereken işler olduğunu düşünmekle doğru yolu bulmuş, böylece edebi edepsizden öğrenmiş olursun.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4717, 'edebini takınmak', 'edepli davranmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4718, 'edebiyat yapmak', 'bir konu üzerinde gereksiz yere süslü sözler söylemek: ''Biz edebiyat yapmıyoruz, gazetecilik ediyoruz, modern gazetecilik!'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4719, 'eden bulur, inleyen ölür', 'nasıl davranırsan öyle karşılık görürsün.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4720, 'edep etmek', 'utanmak, sıkılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4721, 'edep yahu', 'kötü davranışlarda bulunanlara ''utan, edebini takın'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4722, 'edeptir söylemesi', ' hlk. ''affedersiniz, söylemesi ayıptır ama'' anlamında kullanılan bir söz: Edeptir söylemesi, donuna kaçırmış.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4723, 'efendi gibi yaşamak', 'sıkıntısız, varlık içinde yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4724, 'efendiden bir adam', 'terbiyeli, kibar ve ağırbaşlı kimse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4725, 'efendim nerede, ben nerede?', 'ben ne diyorum, siz ne diyorsunuz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4726, 'efendime söyleyeyim', '1) söz söylerken gerekli kelimeyi bulamayan bir kimsenin kullandığı bir söz: ''Efendime söyleyeyim, sütlü bir mısır kebabı derken bir sivrisinek bulutudur havalanmış çeltik batağından.'' -B. R. Eyuboğlu. 2) örnek olarak, mesela.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4727, 'efkâr basmak', 'tasalanmak, kaygılanmak: ''Efkâr basınca, haftaları ay, ayları yıl diye hesap eder mahkûm.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4728, 'efkâr dağıtmak', 'sıkıntıyı gidermek, üzüntüden uzaklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4729, 'efkârı dağılmak', 'sıkıntı ve üzüntüden kurtulmak, rahatlamak, huzur bulmak: ''Ona ne zaman rastlarsanız, konuşsanız içiniz açılır, efkârınız dağılır.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4730, 'eflake ser çekmek', 'çok yüksek olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4731, 'efradını cami, ağyarını mâni', '''ne eksik ne fazla, eksiği artığı olmayan'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4732, 'eğilen baş kesilmez', 'kusurunu anlayıp özür dileyen kişi bağışlanmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4733, 'eğilip bükülmek', 'bir kimsenin karşısında sıkıntı, utanç vb. duygularını açığa vuracak hareketlerde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4734, 'eğitim almak', 'belli bir bilim dalı veya sanat kolunda yetişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4735, 'eğitim vermek', 'belli bir bilim dalı veya sanat kolunda yetiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4736, 'eğreti almak', 'ödünç almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4737, 'eğreti ata binen tez iner', 'ödünç alınmış araçlarla girişilen işler çok kez yürütülemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4738, 'eğreti kuyruk tez kopar', 'temeli olmayan işlere güvenilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4739, 'eğreti oturmak', 'bir yerde çok kısa süre kalacakmış gibi oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4740, 'eğreti vermek', 'ödünç vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4741, 'eğretiye almak', 'bir yapının alt bölümünü onarmak için üstünü destekler üzerinde durdurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4742, 'eğri düzü beğenmez, bu da bizi beğenmez', 'hiçbir şeyi beğenmeyenlerin de kusurları vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4743, 'eğri (eğri gözle) bakmak', 'kötü düşünce ile bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4744, 'eğri gemi doğru sefer', '''kullanılan araç yetersiz ancak yapılan iş isteğe uygun'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4745, 'eğri oturup doğru konuşalım', 'birisine karşı tutumumuz ne olursa olsun doğruyu söylemeliyiz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4746, 'eğrisi doğrusuna gelmek', 'olmayacak gibi görünen bir iş, bir girişim, rastlantı sonucu olumlu bitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4747, 'eğriye eğri doğruya doğru', 'gerçek neyse aynen belirtilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4748, 'ejder gibi', 'iri yapılı ve korkunç görünüşlü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4749, 'ejderha gibi', 'ejder gibi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4750, 'ek bent olmak', 'şaşırıp ne diyeceğini bilememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4751, 'ek tohumun hasını, çekme yiyecek yasını', 'bir girişimden iyi sonuç almak isteyen, o işin temelini sağlam kurmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4752, 'eken biçer, konan göçer', 'her davranış doğal sonucuna varır; emek verip ekin eken ürün alır, gezerken bir yerde konaklayan oradan kalkarak başka bir yere gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4753, 'ekini belli etmemek', 'eksik, bozuk, yanlış, kusurlu bir işi sağlam, doğru ve doğal imiş gibi gösterme becerisini kanıtlamak: ''Ben doğrusu beğeniyorum, dedi, kadın yağ satıyor, yumurta satıyor, ekini belli etmiyor ya!'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4754, 'ekip biçmek', 'tarım yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4755, 'ekmeden biçilmez', 'emek vermeden beklenen bir sonuca erişilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4756, 'ekmediği yerden biter', 'umulmayan ve istenilmeyen yerde karşılaşılan kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4757, 'ekmeği ekmekçiye ver, bir ekmek de üste ver', 'verilecek ücret ne kadar çok olursa olsun, her iş uzmanına yaptırılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4758, 'ekmeğin büyüğü, hamurun çoğundan olur', 'verimin bol olması, kullanılan malzemenin bol olmasına bağlıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4759, 'ekmeğinden etmek', 'işinden çıkarmak, işinden atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4760, 'ekmeğinden olmak', 'geçimini sağlayan işinden zorunlu olarak ayrılmak: ''Bu anormal gidiş bir yerden patlak verirse ahir ömründe ekmeğinden de olabilirdi.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4761, 'ekmeğine göz koymak (dikmek)', 'birinin geçimini sağlayan işi elinden almaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4762, 'ekmeğini çıkarmak', 'çalıştığı işten geçimini karşılayacak kadar kazanç sağlamak: ''Şu dünyada her birimiz alnımızın teriyle ekmeğimizi çıkarmak zorundayız.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4763, 'ekmeğini ekmekçiye ver, yarısını yerse helal olsun', 'verilecek ücret ne kadar çok olursa olsun, her iş uzmanına yaptırılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4764, 'ekmeğini kana doğramak', 'büyük bir sıkıntı ve üzüntüye katlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4765, 'ekmeğini kazanmak', 'geçimini sağlamak: ''İçi huzurlu, akşama dek çalışmış, ekmeğini kazanmış.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4766, 'ekmeğini taştan çıkarmak', '1) geçimini sağlamakta çok becerikli olmak; 2) en zor koşullarda bile kazancını sağlamak: ''Bu cins çocukların da ekmeğini taştan çıkarmak için ölürcesine çalıştıklarını görüyorum.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4767, 'ekmeğini yemek', '1) birisinin işinde çalışarak kendi geçimini sağlamak: ''Bedavadan ekmeğini yediği gazeteyi tekmeledikten sonra, aynı gazete geriye döneni tekrar bağrına nasıl basar?'' -N. F. Kısakürek. 2) geçim yönünden birisinin yardımından yararlanmak: Oğlunun ekmeğini yiyemeden öldü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4768, 'ekmek aslanın ağzında', 'geçim sağlayacak bir iş bulmak ve para kazanmak kolay değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4769, 'ekmek elden su gölden', '''kendisi çalışmayıp başkasının kazancıyla geçinme durumu'' anlamında kullanılan bir söz: ''Uygar yaşamlarında ekmek elden su göldendi.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4770, 'ekmek istemez su istemez', 'hiçbir masrafı yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4771, 'ekmekle oynayanın ekmeğiyle oynanır', 'insanların kazançlarına, rızıklarına engel olanlara bir gün aynı şeyler yapılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4772, 'ekmekten kaşık olur ama her yoğurdun hakkına değil', 'iyi nitelikli işler kullanılan araç elverişsiz de olsa kolaylıkla yürütülebilir ama her iş elverişsiz araçla yürütülemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4773, 'ekonomi yapmak', 'tutumlu davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4774, 'eksik doğmak', 'vaktinden önce veya organları gelişmeden doğmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4775, 'eksik etmemek', 'her zaman bulundurmak: ''Sağ gözünden, güneş vurdukça sağa sola yansıyan tek gözlüğünü eksik etmezdi.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4776, 'eksik gedik kapamak', 'ufak tefek gereksinimleri karşılamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4777, 'eksik gelmek', 'yetişmemek, yetmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4778, 'eksik olma!', '''var ol, sağ ol!'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4779, 'eksik olmamak', 'her vakit ve her fırsatta bulunmak: ''Yalnız bizim Babıali yazı piyasasında sipahi bölüğü imamının bulduğu çareye başvurup bir gaza kahramanı kesilenler eksik değil.'' -N. Hikmet. ''Bir ufak sac mangal, kış yaz önünden eksik olmaz.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4780, 'eksiltmeye çıkarmak', 'bir işi, istekliler arasında en ucuz fiyat verene bırakmak için ihaleye çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4781, 'ekşi yemedim ki karnım ağrısın', 'suç işlemedim ki korkayım.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4782, 'ekşi yüzlünün balı acı olur', 'istemeden iyilik yapan kişi karşısındakini de hoşnut edemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4783, 'el açmak', '1) dilenmek: ''Oturup kör gibi, namerde el açmak iyi mi?'' -M. A. Ersoy. 2) başkasının yardımını isteyecek durumda olmak; 3) kâğıt açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4784, 'el ağzına bakan, karısını tez boşar', 'kişi, özel hayatı ile ilgili ciddi konularda başkasının düşüncesiyle değil kendi düşünceleriyle karar vermelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4785, 'el almak', '1) esk. tarikatlarda bir mürit, mürşidinden, başkalarına yol gösterme iznini almak; 2) bir sanatı yapmak için ustanın iznini almak; 3) kâğıt oyunlarında karşı tarafın oynadığı kâğıdın daha önemlisini oynayarak üstünlük sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4786, 'el arı düşman gayreti', '''dosta düşmana karşı küçük düşmemek için çaba gösterme'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4787, 'el atmak', '1) birisinin işine karışmak, müdahale etmek: ''Nereye el atsak, altından kirli işler çıkıyor.'' -H. Topuz. 2) bir işe girişmek, teşebbüs etmek: ''Elbette birçok önemli konulara el attı ama ulusumuzun temel sorunlarından bazıları yüzüstü duruyor.'' -T. Halman. 3) sarkıntılık etmek: ''Üvey babasının teklifleri, tenhalarda şurasına burasına el atması.'' -O. Kemal. 4) yardım etmek, ilgilenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4788, 'el ayak çekilmek', 'ortalıkta hiç kimse kalmamak, ıssızlaşıp sessizleşmek: ''Yollar ıssızdı, el ayak çekilmişti, sokaklarda yolu şaşırdım.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4789, '(el, ayak, parmak) çivi gibi olmak', 'çok üşümek, donmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4790, 'el bağlamak', '1) saygı için ellerini göbeğinin üstüne kavuşturup durmak; 2) namaza durmak: ''Durup el bağlayalar yâran saf saf.'' -Baki.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4791, 'el basmak', 'kutsal bir şey üzerine el koyarak yemin etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4792, 'el bebek gül bebek', 'nazlı, şımarık bir biçimde: ''Varlıklı, görgülü bir ailenin el bebek gül bebek yetiştirilmiş çocuğusunuz.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4793, 'el beğenmezse yer beğensin', 'çocuğun öldüğünde iyi anılması, yaşarken de beğenilir bir kişi olması için anne baba çocuklarının eğitimine çok önem vermelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4794, 'el bende!', '''tekrarlanan oyunda başlama sırası veya hakkı bende'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4795, 'el birliği etmek', 'birlikte davranmak, dayanışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4796, 'el çekmek', 'vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4797, 'el çektirmek', 'görevinden uzaklaştırmak: ''Sorumluları tespit edildi, işten el çektirildi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4798, 'el çırpmak', '1) alkışlamak, tempo tutmak: ''Bir köylü oturduğu yerde cura çalıyor, birkaç delikanlı etrafında el çırparak ayak vurarak türkü söylüyorlardı.'' -R. N. Güntekin. 2) birini çağırmak için ellerini birbirine vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4799, 'el dokunulmamak', 'daha önce kullanılmamak, el değmemiş olmak: ''El dokunulmamışından canı yandığından artık az kullanılmışına fit oldu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4800, 'el el ile, değirmen yel ile', 'insanlar bir araya gelmeden yaşayamazlar, birbirlerine yardım etmeden başarıya ulaşamazlar; değirmenin çalışabilmesi için rüzgâr gereklidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4801, 'el el üstünde olur, ev ev üstünde olmaz', 'her şey birbirinin üstüne konulamaz, birbiriyle birleştirilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4802, 'el el üstünde oturmak', 'herhangi bir iş yapmadan boş oturmak: ''Herhâlde konağın kuytu bir köşesinde, gene el el üstünde oturuyor olmalıydı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4803, 'el elde baş başta', 'elde bulunan her şeyin tükendiğini anlatan bir söz: ''Balya''da beş on lira kazanmıştı. Onları da yedik, el elde baş başta.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4804, 'el elden kalmaz, dil dilden kalmaz', 'bir kişi başkasına vurursa o da ona vurur, başkasına kötü söz söylerse diğeri de kendisine kötü söz söyler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4805, 'el elden üstündür (ta arşa kadar)', 'bir kimse, kendisinden üstün bir başkasının da olabileceğini bilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4806, 'el ele vermek', '1) el tutuşmak: ''Haydi, ateş dansı yapalım deniyor, el ele verip bir halay çekiyoruz.'' -A. Erhat. 2) mec. birlikte davranmak, bir konuda birleşmek: ''Yoksa el ele verip hep beraber dünyayı mı uçuralım?'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4807, 'el eli yıkar, iki el yüzü', 'bir kişi başka bir kişiye yardım ederse o da bu iyiliğin altında kalmaz, güçlenmiş olarak yardımlara koşar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4808, 'el elin aynasıdır', 'kişi kendi özelliklerini zaman zaman yabancıdan öğrenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4809, 'el elin eşeğini türkü çağırarak arar', 'insanın kendi sıkıntı ve sorunlarına başkaları gereken önemi vermez, gerektiği kadar ilgilenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4810, 'el elin eşeğini yırlaya yırlaya, kendi eşeğini terleye terleye arar', 'insanın kendi sıkıntı ve sorunlarına başkaları gereken önemi vermez, gerektiği kadar ilgilenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4811, 'el elin nesine, gülerek gider yasına', 'bir kimsenin acısı, başkalarının umurunda değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4812, 'el eliyle yılan tut, onu da yalan tut', 'kişi kendi işini kendisi yapmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4813, 'el eliyle yılan tutan, yarısını yalan tutar', 'kişi kendi işini kendisi yapmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4814, 'el eliyle yılan tutulur', 'kişi kendi işini kendisi yapmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4815, 'el emeği göz nuru', 'yapımı uzun zaman alan ve çok emek isteyen iş, el işi göz nuru.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4816, 'el ense çekmek (etmek)', '1) sp. güreşte, kolunu hasmın boynuna getirip başparmağı gırtlağa, dört parmağı da enseye geçirerek hasmı yıkmak amacıyla çekmek; 2) mec. yenmek, mağlup etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4817, 'el ermez, güç yetmez', 'bir iş karşısındaki güçsüzlüğü anlatmak için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4818, 'el etek öpmek', '1) bir işi yaptırmak için çok yalvarmak; 2) yaltaklanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4819, 'el etek tutmak', 'tarikata girmek, derviş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4820, 'el etmek', '1) bir kimseyi el işaretiyle çağırmak: ''Hemen ablasına bulunduğu yerden el etti.'' -N. Cumalı. 2) uzaktan el sallamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4821, 'el(I) el koymak', '1) bir yolsuzluğu ortaya çıkarmak, incelemek, vaziyet etmek; 2) üstüne konmak: ''Herkesin olan bir olanağa el koyup onu kendi çıkarına kullananı neden seveyim?'' -A. Ağaoğlu. 3) zorla almak: ''Bizi işimizde gücümüzde serbest bırakmak şöyle dursun, çoluk çocuğumuzun nafakasına el koymaya kalkıştılar.'' -Y. K. Karaosmanoğlu. 4) işi üzerine almak, sorumluluğu üstlenmek: ''Annem hemen işe el koydu.'' -A. Kutlu. 5) yetkili organ bir malı veya bir kuruluşu kendi yönetimine almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4822, 'el için ağlayan gözden olur', 'başkası için yapılacak fedakârlığın bir sınırı vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4823, 'el için kuyu kazan, evvela kendisi düşer', 'başkasına tuzak hazırlayan kimse, bu tuzağa ondan önce kendisi düşer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4824, 'el için yanma nâra, yak çubuğunu bak keyfine', 'başkalarının derdini kendine sorun yapıp da kendi rahatını ve düzenini bozma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4825, 'el ile gelen düğün bayram', 'herkese birden gelen sıkıntı ve felakete katlanmak, yalnızca bir kişiye gelene katlanmaktan daha kolaydır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4826, 'el işi göz nuru', 'el emeği göz nuru.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4827, 'el iyisi olmak', 'yakın çevresine değil, yabancılara yardımcı olmayı sevmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4828, 'el kadar', 'çok küçük, küçücük.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4829, 'el kaldırmak', '1) oy verdiğini veya söz istediğini elini kaldırarak belirtmek; 2) birine, bir şeye vurmaya kalkışmak: ''İtlerden birine el kaldırmanın cezası ölüm idi.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4830, 'el kapısına düşmek', 'yabancıya muhtaç olmak: ''Başından nasıl bir sergüzeşt geçmişti de böyle el kapılarına düşmüştü?'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4831, 'el katmak', '1) bir işe karışmak, müdahale etmek; 2) bir işin yapılmasına yardım etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4832, 'el kazanı ile aş kaynamaz', 'önemli bir iş, başkalarının yardımıyla başarılamaz, iş her an yarıda kalabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4833, 'el kazanıyla aş kaynatmak', 'başkasının hazırladığı imkânları kendi hesabına kullanarak iş çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4834, 'el mi yaman bey mi yaman? el yaman!', 'baştaki ne kadar güçlü görünürse görünsün, asıl güç halktadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4835, 'el ovuşturmak', '1) birinin karşısında ezilip büzülmek; 2) birinin kötü duruma düşmesine içten içe sevinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4836, 'el öpmekle ağız aşınmaz', 'çok önemli bir iş için bir kimseye ricada bulunmak hatta yalvarmak gerekirse, yapılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4837, 'el pençe', 'el pençe divan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4838, 'el pençe divan', '1) saygı gösterilen kimse karşısında el kavuşturmuş bir biçimde: ''Doğruldu, el pençe divan durdu, başını önüne eğdi.'' -P. Safa. 2) aşırı saygı göstererek: ''Dayımı el pençe divan karşılar, ne yiyip ne içeceğini sormazdı, çünkü bilirdi.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4839, 'el sıkışmak', 'pazarlıkta anlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4840, 'el sıkmak', 'selamlaşmak için birinin elini tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4841, 'el sikiyle gerdeğe girilmez', 'başkasının olanaklarına güvenip gelecek için girişimler tasarlanmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4842, 'el sürmemek', '1) dokunmamak, değmemek; 2) bir işi yapmamak, ilgilenmemek: ''Canım dalga geçmek, akşama kadar bir şeye el sürmemek istiyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4843, 'el tazelemek', 'bir işte yorulan kimse yerine başka birini getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4844, 'el terazi, göz mizan', 'elle tartıp ağırlığı, gözle bakıp hacmi tahmin edebiliriz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4845, 'el tutmak', 'bir iş uzun süre uğraştırmak, vakit kaybettirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4846, 'el uzatmak', '1) birinden bir hakkı almaya kalkışmak: ''Ne var ki niye bizim lokmamıza el uzatırlar?'' -A. İlhan. 2) yardım etmek: ''Sözü geçecek, en umulmadık bir zamanda kendine el uzatabilecek bir adam olmadığı nereden belli?'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4847, 'el üstünde gömlek eskimez', 'eğreti olarak alınan şey, dikkatle korunur; bir süre sonra olduğu gibi geri verilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4848, 'el vergisi, gönül sevgisi', 'bize bir şey verene, armağan edene karşı gönlümüzde sevgi uyanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4849, 'el vermek', '1) yardım etmek; 2) esk. tarikatlarda mürşit, bir müride, başkalarına yol gösterme izni vermek; 3) halk hekimliği ile uğraşan kimse bilgilerini bir başkasına öğretmek; 4) kâğıt oyunlarında elde olan veya olmayan sebeplerle oyun üstünlüğünü karşı tarafa bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4850, 'el vurmamak', 'bir işi yapmaya yanaşmamak ve başlamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4851, 'el yarası onulur, dil yarası onulmaz', 'silahla açılan el yarası çabukça iyi olur ama kötü sözle açılan gönül yarası kolay kolay kapanmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4852, 'el yumruğu yemeyen kendi yumruğunu değirmen taşı sanır', 'başkasının gücü karşısında boyun eğmek zorunda kalacağını anlayamamış kimse, kendi gücünün herkese boyun eğdireceğini sanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4853, 'el yumruğunu yemeyen, kendi yumruğunu bozdoğan armudu sanır', 'başkasının gücü karşısında boyun eğmek zorunda kalacağını anlayamamış kimse, kendi gücünün herkese boyun eğdireceğini sanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4854, 'elaman çekmek', 'bezginlik gösterip yakınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4855, 'elaman demek', 'çok bezmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4856, 'elçiye zeval olmaz', 'bir kimseden başka bir kimseye herhangi bir haber ulaştıran, bu aracılığından dolayı sorumlu tutulmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4857, 'elde avuçta (bir şey) kalmamak', 'mal ve parasını harcayıp bitirmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4858, 'elde avuçta (ne varsa)', 'sahip olunan mal, para vb., her şey: ''Ailesi de elde avuçta ne var ne yok satarak İstanbul''a göçmek zorunda kalmıştı.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4859, 'elde bulunan beyde bulunmaz', 'beylerde olmayan öyle şeyler vardır ki halkta bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4860, 'elde (elinde) olmamak', 'iradesi dışında gerçekleşmek: ''Elinde olmadan başını kaldırdı ve göz göze gelince de konuşmak zorunda kaldı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4861, 'elde etmek', '1) bir şeye sahip olmak: ''O parlak siyah gözler, onları bir daha elde edemeyecek miydi?'' -H. Z. Uşaklıgil. 2) bir kimseyi kendi hizmetine almak veya kendinden yana çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4862, 'elde kalmak', 'geride kalmak: ''Çöküyor dört tarafa uğursuz bir karanlık / Elde kalan, çökmeyen bir şey var: Kahramanlık'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4863, 'elde tutmak', 'sahibi olsun olmasın, bir malı mülkiyeti altında bulundurmak, zilyet olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4864, 'eldeki yara, yarasıza duvar deliği', 'bir kimsenin acı ve sıkıntısı başkasına dert gibi görünmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4865, 'elden ağza yaşamak', 'günlük kazancı ancak gereksinimlerini karşılayacak kadar olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4866, 'elden almak', '1) bir malı pazara çıkarılmadan sahibinden doğrudan satın almak; 2) herhangi bir şeyi biriyle yüz yüze görüşerek almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4867, 'elden ayaktan düşmek (kesilmek)', 'yaşlılık sebebiyle veya sağlığı büsbütün bozularak çalışamaz duruma gelmek: ''Ve gün battığı zaman artık Gülbahar''ın hâli kalmamış, elden ayaktan kesilmişti.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4868, 'elden bırakmamak (düşürmemek)', 'bir şeyle sürekli ilgilenmek, elden düşürmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4869, 'elden çıkarmak', '1) bir şeyin sahipliğini başkasına geçirmek, satmak: ''Eskilerden bir kısmını yok pahasına elden çıkarmak gerekecek.'' -H. Taner. 2) yitirmek: ''Sanki o, kaçırdığım, elden çıkardığım bir fırsattı.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4870, 'elden çıkmak', '1) malı olmaktan çıkmak, malı satılmak; 2) kaybedilmek: ''Selanik elden çıkınca ailesi İzmir''e göçmüştür.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4871, 'elden ele dolaşmak (gezmek)', 'iyi nitelikleri dolayısıyla çok ilgi görmek, çok beğenilmek: ''Gönülden Sesler, Meşrutiyet gençliğinin elden ele dolaşan kitabı idi.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4872, 'elden ele geçmek', 'çok sahip değiştirmek: ''Elden ele geçen ve fiyatı giderek artan bu silahlar eski ve güçsüzdür ama çetecilik için yeterlidir.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4873, 'elden geçirmek', 'eksiklik veya bozukluklarını gidermek veya denetlemek için incelemek: ''Otomobil tamircisi bir akrabaları varmış, o da arabayı elden geçirmiş.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4874, 'elden gel!', ' argo 1) ver! Elden gel bakalım iki papeli. 2) tkz. kutlamak amacıyla söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4875, 'elden geldiği kadar', 'yapılabildiği, olabildiği kadar: ''Müsteşardan kapıcıya kadar bütün nezaret mensupları elden geldiği kadar gayret ettiler.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4876, 'elden gelen öğün olmaz, o da vaktinde bulunmaz', 'kişi yalnızca kendi kazancına güvenmeli, başkasının yardımını beklememelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4877, 'elden gelmemek', 'yapamamak, dayanamamak: Bu üzücü durum karşısında ağlamamak elden gelmiyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4878, 'elden kaçırmak', 'elde edilebilecek bir şeyden türlü sebeplerle yararlanamamak: ''Cin yahut periler bu evi elden kaçırmamak için ne kadar hırçınlık etseler yeridir.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4879, 'elden kaçmak', '1) sahip olamamak; 2) değerlendirememek: ''Kibar kıyafetli bir hanım, elden kaçmış eski fırsatların hırsı gözlerinde parlayarak dedikodu yapmaya başladı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4880, 'elden ne gelir?', 'çaresiz bir durumda yapılacak bir şey olmadığını anlatan bir söz: ''Elden ne gelir, merdivenden düşüp ayak kırılırsa.'' -A. K. Tecer.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4881, 'elden vefa, zehirden şifa', 'zehirden şifa beklenilmeyeceği gibi yabancılardan da yardım ve iyilik beklenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4882, 'ele alınır', 'oldukça iyi, işe yarar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4883, 'ele alınmaz', 'çok kötü, berbat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4884, 'ele almak', '1) bir şey üzerinde çalışmaya başlamak: ''Sözlerini bambaşka bir anlayışla ele almış ve kendi kendine sormuştu.'' -T. Buğra. 2) bir konuyu görüşmek; 3) bir konuyu incelemek, araştırmak: ''Kamu düzeniyle ilgili bu konuların yanında toplum ve aile sorunları da ele alınıyordu.'' -M. And. 4) herhangi bir şeyi iş edinmek: ''Fakat dediğim gibi ben yüzsüzlüğü ele almıştım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4885, 'ele avuca sığmamak', '1) söz dinlememek, baskı altına alınmamak, zapt edilememek: ''İzmir''deyiz. Ele avuca sığmaz haşarı bir çocuğum.'' -R. N. Güntekin. 2) şımarık davranmak: ''Hani vatandaşlarımız da güç, ele avuca sığmaz, kanmaz, doymaz insanlar olsa bari!'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4886, 'ele bakmak', '1) avuç içindeki çizgilere bakıp kişinin geleceğini okumak, el falına bakmak; 2) muhtaç olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4887, 'ele geçirmek', '1) yakalamak: ''Hele onu bir elime geçireyim, görürsün, burnundan getireceğim.'' -H. Topuz. 2) sahibi olmak: ''İstanbul''u ele geçirmek için bu muharebeye girdiklerini ilan etmekten başka bir şey yapamadılar.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4888, 'ele geçmek', '1) yakalanmak: ''Nihayet bir defasında tam iki ay izini kaybetmiş, bir türlü ele geçmemişti.'' -R. H. Karay. 2) edinilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4889, 'ele gelmek', '1) tutulabilmek; 2) bebek kucağa alınacak kadar büyümüş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4890, 'ele verir talkını (telkini), kendi yutar salkımı', 'kendisinin inanmadığı ve tutmadığı öğütleri başkalarına kolayca verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4891, 'ele vermek', '1) suçlu bir kimseyi haber verip yakalatmak, ihbar etmek: ''O adamlar kim ise haber vermeli, dikkat etsinler, kendilerini sakın ele vermesinler.'' -H. E. Adıvar. 2) herhangi kötü bir şey yapanın yaptığını herkese bildirmek; 3) ortaya çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4892, 'elekten geçirmek', '1) elemek; 2) ayıklamak; 3) araştırma sonunda doğruyu yanlışı, iyiyi kötüyü ayırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4893, 'elektriği kesmek', 'elektrik enerjisinin akışına engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4894, 'elektriği yakmak', 'bir yeri aydınlatmak için elektrik enerjisini açıp kullanmak: ''Ondan hemen ayrılıp elektriği yaktı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4895, 'elektrik almak', 'etkilenmek, etkisi altında kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4896, 'elektrik vermek', '1) bir yeri elektrikle donatmak; 2) işkence amacıyla birinin çıplak bedenine doğru akım vermek; 3) elektrik enerjisini kullandırmak; 4) mec. etkilemek, etkisi altında bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4897, 'eli alışmak', '1) bir işte uzluk, ustalık kazanmak; 2) herhangi bir davranışı âdet edinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4898, 'eli armut devşirmek', 'birisini bir iş yaparken öbürü boş durmak: ''Bu insanlar bu güzel şehirleri kurarken bizim ellerimiz armut mu devşiriyordu?'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4899, 'eli ayağı (ayağına) dolaşmak', 'şaşırmak, telaşlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4900, 'eli ayağı buz kesilmek (tutmamak)', 'güçsüz, dermansız kalmak: ''Bu hâli biraz yapmacık da olsa şimdi ben de şaşırmış, elim ayağım buz kesilmişti.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4901, 'eli ayağı titremek', 'korku, sinir vb. sebeplerle heyecanlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4902, 'eli ayağı tutmak', 'beden gücü yerinde olmak: ''Eli ayağı tutanlar, hiçbir haksızlığa razı olmamalıydı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4903, 'eli aza varmamak', 'bir şeyi çok alma veya verme alışkanlığında olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4904, 'eli boş çıkmak', 'umduğunu alamamak, başarısızlığa uğramak: ''Sağa döndü, sola baktı, seksen sergüzeşte atıldı, eli boş çıktı, parasız, kıyafetsiz ve mevkisiz olup...'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4905, 'eli boş dönmek (çevrilmek veya geri gelmek)', 'umduğunu alamadan dönmek: ''Nereyi arayıp taradılarsa elleri hemen hemen boş döndüler.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4906, 'eli boş gelmek', '1) armağansız gelmek; 2) umulan şeyi getirmeden gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4907, 'eli boşa ''ağa uyur'' derler; eli doluya ''ağa buyur'' derler', 'armağansız gelen kişiye yüz verilmez, armağanla gelen kişi ise güler yüzle ve saygı ile karşılanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4908, 'eli böğründe kalmak', 'başarısızlığa uğramak, bir şey yapamaz duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4909, 'eli cebine (cüzdanına veya kesesine) gitmemek (varmamak)', 'çok cimri olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4910, 'eli dar (darda) olmak', 'para sıkıntısı içinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4911, 'eli değmek', 'bir şey yapmaya vakit ve fırsat bulmak: ''Elim değmişken bir açıklamada bulunayım.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4912, 'eli dursa ayağı durmaz', 'kıpırdak, hareketli (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4913, 'eli ekmek tutmak', 'geçimini kendi emeğiyle sağlayacak duruma gelmek: ''İşi var, eli ekmek tutuyor. İyi çocuktur.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4914, 'eli eline değmemek', '1) herhangi bir yakınlaşma olmamak; 2) birisiyle cinsel ilişkiye girmemiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4915, 'eli ermek', '1) yapabilmek, ulaşabilmek: ''Zaman zaman, şiirin ne olduğunu elimin erdiği, gücümün yettiği kadar anlatmaya çalıştım.'' -O. V. Kanık. 2) bir işi yapmak için zaman bulabilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4916, 'eli ermez gücü yetmez', 'çaresiz, zavallı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4917, 'eli genişlemek', 'bolca paraya kavuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4918, 'eli gitmek', 'bir şeyi kavramak, tutmak istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4919, 'eli harama uzanmak', 'dinî bakımdan yasaklanmış bir işe yönelmek: ''Eli ne vakit harama uzandı?'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4920, 'eli işe yatmak', 'becerikli, eli yatkın, uz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4921, 'eli kalem tutmak', '1) yazı yazmayı bilmek; 2) düşündüğünü güzel bir anlatımla yazmak: ''Saz sanatkârı bütün kedileri sever. Aynı zamanda eli kalem tuttuğundan sevdiği kedilerin bir bir hikâyesini yazar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4922, 'eli kırılmak', 'eli, işe yatkın bir duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4923, 'eli kolu (eli ayağı) bağlı kalmak (durmak veya olmak)', 'bir engel dolayısıyla hiçbir iş yapamaz duruma gelmek: ''Diplomatlarımıza, büyükelçilik ve temsilcilik binalarımıza, tankerlerimize yapılan saldırılara karşı elimiz kolumuz bağlı duruyoruz.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4924, 'eli koynunda kalmak', 'çaresiz kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4925, 'eli mahkûm olmak', 'mecbur durumda kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4926, 'eli para görmek', 'eline para geçmek: ''Elli yaşlarına doğru pazarcılık yapmaya başladı; eli para gördü, yüzü güldü.'' -Ü. Dökmen.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4927, 'eli silah tutmak', 'silah kullanabilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4928, 'eli varmamak (gitmemek)', 'bir işi yapmaya gönlü razı olmamak: ''Temiz yere kolay çöp atamazsınız. Eliniz varmaz.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4929, 'elifi mertek sanmak', 'çok cahil olmak: ''Bir şişe görürsün, üstünde ''ilaç'' yazar. Benim gibi elifi mertek sanan takımdansan şurup sanır içersin, zehirlenir ölürsün.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4930, 'elifin hecesi var, gündüzün gecesi var', 'kolay ve düzgün başlayan bir iş hep öyle sürüp gitmez, güçlüklerle ve aksaklıklarla da karşılaşılabilinir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4931, 'elimi sallasam ellisi, başımı sallasam tellisi', 'elini sallasa ellisi, başını sallasa tellisi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4932, 'elin ağzı torba değil ki büzesin', 'başkalarının söyleyeceklerine engel olamazsınız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4933, 'elinde avucunda nesi varsa', '''maddi olarak sahip olduğu her şey'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4934, 'elinde bulunmak (olmak)', 'o şeye sahip bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4935, 'elinde büyümek', '1) büyütülmek, bakılmak: ''Çocuklar Nimet Hanım adında bir kadının elinde büyüdüler.'' -R. N. Güntekin. 2) eğitilmek, bilgi, görgü ve terbiye sahibi olmak, yetiştirilmek: ''Üstadım, ben sizin elinizde büyüdüm, sizden feyzaldım.'' -F. F. Tülbentçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4936, 'elinde kalmak', '1) birinin bakımında, yönetiminde olmak; 2) bir şey satılamayıp sahibinde kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4937, 'elinde olmak', '1) bakımı, gözetimi altında olmak; 2) egemenliği altında, yetkisinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4938, 'elinde patlamak', '1) bir şey satılamayıp sahibinde kalmak; 2) haber vb.ni uygun zamanda kullanamayıp fırsatı kaçırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4939, 'elinde tutmak', '1) kendi tekelinde bulundurmak, başkalarına kaptırmamak; 2) bir malı satmayıp bekletmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4940, 'elinden almak', 'bir şeyden mahrum etmek: ''Özgürlüklerini ellerinden alıp birer araç hâline getiriyor onları.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4941, 'elinden bir iş (şey) gelmemek', 'çaresizlikten veya yeteneksizlikten bir iş yapamamak: ''Matbu kâğıtları doldurmaktan başka elinden bir iş gelmez, sorulmadıkça kendiliğinden konuştuğu görülmezdi.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4942, 'elinden bir kaza (sakatlık) çıkmak', 'istemeyerek birini yaralamak veya öldürmek: ''Belki elinden bir kaza çıkar diye evine girmeye cesaret edemezdi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4943, 'elinden (bir şeyi) düşürmemek', 'sürekli onunla ilgilenmek: ''Kendileri sanata çok meraklılar, ellerinden hiç kitap düşürmezler.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4944, '... elinden çıkmak', 'biri tarafından yapıldığı belli olmak: ''Giysi belli oldu, çok kaliteli, çok iyi terzi elinden çıkmış.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4945, 'elinden geleni ardına (arkasına) koymamak', 'yapabileceği bütün kötülükleri yapmak: ''Düşüncesini en iyi biçimde anlatabilmek için elinden geleni ardına koymamıştır.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4946, 'elinden geleni yapmak', 'gücünün yettiği kadarını yapmak: ''Bunu başarmak için elinden geleni yapacaksın, dedi.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4947, 'elinden gelmek', 'yapabilmek: ''Nesir az çok benim de elimden geldiği için midir nedir kabul edemiyorum şiirden güç olduğunu.'' -N. Ataç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4948, '(elinden gelse, bıraksalar) bir kaşık suda boğmak', 'bir kimseye çok kızmak veya çok öfkelenmek: ''Muhalifler bizi bir kaşık suda boğmak istidadını gösteriyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4949, 'elinden hiçbir şey kurtulmamak', 'her şeyi becerebilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4950, 'elinden iş çıkmamak', 'çabuk iş görememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4951, 'elinden iyi iş gelmek', 'becerikli, hünerli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4952, 'elinden kan çıkmak', 'cinayet işlemek: ''Kırk kanını Allah''a affettirmeye çalışırken kazara, elinden yeni bir kan çıkmıştı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4953, 'elinden kurtulmak', 'birinden kaçmayı başarmak: ''Birtakım bahanelerle elimden kurtulacağını mı sanıyorsun?'' -A. M. Dranas.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4954, 'elinden tutmak', '1) yardım etmek; 2) kayırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4955, 'eline almak', '1) bir işin veya yerin yönetimini üstlenmek; 2) bir işi kendi yapmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4956, 'eline ayağına kapanmak (sarılmak, düşmek)', 'birine çok yalvarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4957, 'eline ayağına üşenmemek', 'her türlü ayak hizmetini yüksünmeden yapmak, hamarat olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4958, 'eline düşmek', '1) egemenliği, buyruğu altına girmek: Kale düşman eline düştü. 2) yakalanmak: Haydutların eline düştü. 3) birine muhtaç olmak: Elbet bir gün elime düşersin. 4) rastlamak, tesadüf etmek: Çocuk iyi bir öğretmenin eline düştü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4959, 'eline (elinize veya ellerinize) sağlık', 'el emeği ile güzel bir şey yapana söylenen iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4960, 'eline erkek eli değmemiş olmak', 'kız, namuslu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4961, 'eline eteğine doğru', 'her türlü kötülükten uzak olan, dürüst.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4962, 'eline eteğine sarılmak', 'çok yalvarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4963, 'eline fırsat geçmek', 'imkân bulmak: Hazır fırsat geçmiş eline, hiç öyle mi konuşulur?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4964, 'eline geçmek', '1) kazanmak, edinmek, elde etmek: ''Evi sattım, elime bin iki yüz lira kadar bir şey geçti.'' -Ö. Seyfettin. 2) rastlamak, bulmak: Eline geçen her kitabı okur. 3) yakalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4965, 'eline tutuşturmak', 'karşısındakinin isteyip istemediğini düşünmeksizin verivermek: Bir şey demeden mektubu elime tutuşturdu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4966, 'eline yüzüne bulaştırmak', 'bir işi gerektiği gibi yapamamak, başarısız olmak, becerememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4967, 'elini arı kovanına sokmak', 'elini taşın altına koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4968, 'elini ayağını öpeyim', '''çok yalvarırım'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4969, 'elini belli etmek (göstermek)', 'kâğıt, okey vb. oyunlarda elindeki kâğıdı veya taşı, oynayanlara belli edecek biçimde sözle, işaretle açıklayıp oynamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4970, 'elini çabuk tutmak', 'gerekli önlemi zamanında almak: ''Aman elinizi çabuk tutun, yılanın başı küçükken ezilmeli.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4971, 'elini kana bulamak (bulaştırmak)', 'öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4972, 'elini kolunu sallaya sallaya gelmek', '1) gelirken hiçbir armağan getirmemek; 2) bitirmeye gittiği işten sonuç alamadan dönmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4973, 'elini kolunu sallaya sallaya gezmek', '1) ortada görünmemesi gereken kimse pervasızca dolaşmak; 2) pervasızca, kimseden çekinmeden dolaşmak: ''Bütün memleketi, elimi kolumu sallayarak serbest ve rahat dolaşmaya başlamıştım.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4974, 'elini kulağına atmak', 'ezan okumak, gazel veya türkü söylemek için elini kulak kepçesinin arkasına koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4975, 'elini oynatmak', 'parayı esirgememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4976, 'elini sallasa ellisi (başını sallasa tellisi)', 'birinin karşı cinsten birçok insanı kolaylıkla elde edebileceğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4977, 'elini sıcak sudan soğuk suya sokmamak', 'hiçbir iş yapmamak: ''Anneciğim, hayatımı kazandığımda senin elini sıcak sudan soğuk suya sokturmam.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4978, 'elini taşın altına koymak (sokmak)', 'bir konuda sorumluluk üstlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4979, 'elini uzatmak', 'yardım etmek: Kızılay, yoksullara elini uzatır.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4980, 'elini veren kolunu alamaz', 'kendisine iyilik yapıldığında devamını fazlasıyla isteyen kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4981, 'elini vicdanına koymak', 'doğru, yansız, hakça davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4982, 'elinin altında olmak', '1) her zaman kolayca alınıp yararlanılabilecek yerde ve yakınlıkta (olmak): ''Elinin altındaki asker pek azdı.'' -Ö. Seyfettin. 2) hazırda bulundurmak: ''Bütün belgelerin elimin altında olduğunu söylüyordum.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4983, 'elinin hamuruyla erkek işine karışmak', 'kadınlar, beceremeyeceği işleri yapmaya kalkışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4984, 'elinin tersiyle çarpmak', 'ayanın arkasıyla şiddetle tokat atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4985, 'elinin tersiyle itmek', 'reddetmek, kabul etmemek: ''Hangi dolmuşa binersen bin, uzat parayı sürücüye, sürücü hemen elinin tersiyle iter.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4986, 'elinle ver, ayağınla ara', 'ödünç aldığı şeyi geri vermeyi geciktiren veya vermeyenler için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4987, 'eliyle koymuş gibi', 'aramadan, kolayca: ''Eliyle koymuş gibi rafta çay kavanozunu buldu.'' -O. Rifat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4988, 'elle tutulacak tarafı (yanı) kalmamak', '1) sağlam bir yanı kalmamak; 2) güvenilecek veya kayrılacak bir yönü olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4989, 'elle tutulur', '1) çok açık ve belli; 2) somut.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4990, 'elle tutulur gözle görülür (dille anlatılır)', 'çok belirgin, çok açık: ''Sevim''in güzelliği elle tutulur, dille anlatılır makbul bir güzellik değildir.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4991, 'elle tutulur tarafı olmamak', 'hiçbir değerli yanı olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4992, 'ellenmiş dillenmiş', 'iffetsizliği yayılmış (kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4993, 'eller yukarı!', '''ellerini kaldırarak teslim ol'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4994, 'ellerde gezmek', '1) elden ele dolaşmak; 2) mec. el üstünde tutulmak, saygı ve sevgi görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4995, 'elleri (ellerin) dert görmesin', '''ellerine sağlık'' anlamında kullanılan bir iyi dilek sözü: ''Havluyu geri aldığı zaman, oh rahatladım, ellerin dert görmesin, dediği duyulurdu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4996, 'ellerim yanıma gelsin', '''Allah canımı alsın ki doğru söylüyorum'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4997, 'elma da alma da demesini biliriz', 'şartlara göre uygun davranırız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4998, 'elma da alma da demesini biliriz', '''şartlara göre uygun davranırız'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (4999, 'elma gibi', 'kırmızı (yanak).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5000, 'elmanın dibi göl, armudun dibi yol', 'dıştan birbirine benzeyen her şeye aynı işlem uygulanamaz, her biri özelliğine göre ayrı bir davranış ister.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5001, 'elmas gibi', 'çok iyi, çok değerli: Elmas gibi kalbi var. Elmas gibi bir çocuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5002, 'elmayı çayıra, armudu bayıra', 'elma fidanını düz ve sulak yere, armut fidanını bayıra, su tutmayan yere dikmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5003, 'elmayı say da ye, armudu soy da ye', 'armut kabuğu soyularak elma da aşırı gidilmeden sayıyla yenilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5004, 'elmayı soy da ye, armudu say da ye', 'armut kabuğu soyularak elma da aşırı gidilmeden sayıyla yenilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5005, 'elti eltiden kaçar, görümceler bayrak açar', 'eltiler birbirlerinden uzak dururlar, görümceler gelinlerle kavga ederler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5006, 'elti eltiye eş olmaz, arpa unundan aş olmaz', 'arpa unundan yemek yapılamadığı gibi eltilerin de iyi geçinmeleri mümkün değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5007, 'emanet ata binen tez iner', 'ödünç alınmış araçlarla girişilen işler çok kez yürütülemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5008, 'emanet bırakmak (etmek, vermek)', 'bir şeyi veya bir kimseyi birine veya bir yere bir süreliğine bırakmak: ''Çocuğu annesine emanet etmeyecek, kendisi bakacaktır.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5009, 'emanet eşeğin yuları gevşek olur', 'bir kimseye emanet edilen şeyin o kimse tarafından iyi korunmadığı her zaman görülen olaylardandır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5010, 'emanet hayvanın (eşeğin) kuskunu (paldımı) yokuşta kopar', 'eğreti olarak kullanılmak üzere verilen şey uydurma olur, hiç umulmadık bir anda bozulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5011, 'emanete hıyanet olmaz', 'emanet olarak bırakılan şeyi titizlikle korumak gereklidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5012, 'emdiği (helal) süt haram olmak', 'herhangi bir isteğinin yapılmamasından sonra ilenmek: ''Altı mikrobun canını daha cehenneme göndermeden gidersem emdiğim helal süt haram olmaz mı?'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5013, 'emdiği sütü burnundan getirmek', 'birisine çok sıkıntı çektirmek: ''Bu olanları başka birinden işitecek olursam emdiğin sütü burnundan getiririm.'' -B. Günel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5014, 'eme seme yaramamak', 'işe yaradığı kabul edilmemek, makbule geçmemek, takdir edilmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5015, 'eme yaramak', 'işe yaramak, yararlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5016, 'emeği geçmek', 'bir şeyin ortaya çıkması için çalışmış olmak: ''Anneniz, bu kurumun oluşmasında emeği geçmiş belli başlı kişilerdendir.'' -E. Atasü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5017, 'emek çekmek', 'bir işte çok çalışarak yorulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5018, 'emek harcamak', 'çaba göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5019, 'emek olmadan yemek olmaz', 'yaşayabilmek, harcayabilmek için çalışıp kazanmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5020, 'emek vermek', 'bir şeyin meydana gelmesi için özenle ve çok çalışmak: ''Dirsek çürütüp emek verdiği kitapları, can vermeden can bulunamayacağını ona hiç söylememişti.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5021, 'emekliye ayırmak (çıkarmak, çıkartmak)', 'kanuna göre aylık bağlayarak bir görevliyi görevinden ayırmak: ''Size bir fenalık edebilir, sizi işinizden attırır, vekâlet emrine alır, vakitsiz emekliye çıkartabilir.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5022, 'emekliye ayrılmak (çıkmak)', 'emekli olmak, tekaüde sevk olunmak: ''Sakatlığımı öne sürerek emekliye ayrılmamı isteyebilirim.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5023, 'emekliye sevk etmek', 'emekliye ayırmak: ''Bayanı emekliye sevk ederek kendisinin evleneceğini söyledi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5024, 'emel beslemek', 'isteği, arzuyu sürekli düşünmek veya güçlendirmek: ''Size karşı güzel bir emel besleyenler için kazanmak lazım, değil mi?'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5025, 'emeline alet etmek', 'birini veya bir şeyi kendi istekleri doğrultusunda kullanmak: ''İttihat ve Terakki, ordunun genç subaylarını emellerine alet etmeyi başarmıştı.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5026, 'emin olmak', 'inanmak, güvenmek: ''Onları kimsenin görmediğine emin olunca pervasız konuşmaya başladılar.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5027, 'emir komuta zinciri içinde olmak', 'herhangi bir işlem en alt rütbe veya makamdan en üst rütbe veya makama doğru gerçekleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5028, 'emmim dayım kesem, elimi soksam yesem', 'bir kimsenin rahatça harcayabileceği para, başkalarının verdiği değil kendisinin kazandığı paradır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5029, 'emniyet etmek', 'güvenmek: ''Hele emniyet ettiğim birkaç uyanık arkadaşla bulunduğum zaman bülbül gibi ötüyordum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5030, 'emretti patrik efendi', ' alay birinin yersiz bir buyruğuna karşı kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5031, 'emrihak vaki olmak', 'ölmek: ''Bir emrihak vaki olduğu zaman yerimize oğullarımız geçecek.'' -F. F. Tülbentçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5032, 'emrine vermek', '1) görevlendirmek, atamak; 2) yararlanması için ayırmak: Bu daireyi büro olarak onun emrine verdiler.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5033, 'emrivaki yapmak', 'oldu bittiye getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5034, 'enayiliğine doyma!', 'iyi niyetle yaptığı bir davranış sonunda zarar gören kimseye söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5035, 'endazeyi kaçırmak', 'fazla abartmak, ölçüyü kaçırmak: ''Endazeyi kaçırmışsındır çancı ustası, dedi, olmayacak bahse sürersin emmi oğlumu.'' -K. Bilbaşar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5036, 'endazeyi şaşırmak', 'ne yapacağına karar verememek, eli ayağı dolaşmak: ''Biri bu konuda damarına basınca endazeyi şaşırıyor, kendine hükmedemiyordu.'' -N. Araz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5037, 'endişeye düşmek', 'tasaya kapılmak, kaygılanmak: ''Hatta kilise yetkilileri onun sağlığından endişeye düştüler.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5038, 'enfiye çekmek', 'keyiflenmek amacıyla çürütülmüş tütünden yapılan tozu burna nefes yoluyla almak: ''Gözleri dönmüş bir hâlde kendisini sokağa atar, bol enfiye çekerek akşamlara kadar bir başına dolaşır.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5039, 'engel çıkarmak', 'bir işin yapılmasını zorlaştırmak: ''Aslında bütün mesele, düğün için engel çıkarmakta.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5040, 'engel tanımamak', 'her türlü zorluğa karşın başarılı olmak: ''Bir gece içinde donanmasını, bir sepet su çiçeği gibi Haliç''e döken, engel tanımaz hareket şevkiyle Fatih.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5041, 'engin dallardan murt yememek', 'yükseklerden uçmak, burnu büyük olmak: ''Engin dallardan murt yemezdi. Onun alacağı kız ya çok zengin ya da tanınmış bir aileye mensup olmalıydı.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5042, 'ense kulak yerinde olmak', ' tkz. 1) iri yarı olmak; 2) kelli felli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5043, 'ense yapmak', ' argo hiçbir iş yapmadan yan gelip yatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5044, 'ensesinde boza pişirmek', '1) ısıtmak, kızgın duruma getirmek: ''Güneş, bütün gün enselerinde boza pişirmiş, vücutlarının teri mintanlarının üstüne çıkmıştı.'' -H. Taner. 2) birini çok üzmek, tedirgin etmek: ''İhtiyarlık kepaze şey... Şimdi çocuk evde ensemde boza pişiriyor.'' -R. N. Güntekin. 3) birini bir işi yapıp bitirmesi için sürekli sıkıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5045, 'ensesine binmek', 'birine bir işi yaptırmak için sürekli baskı altında bulundurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5046, 'enseyi karartmak', 'ümitsizliğe kapılmak, karamsarlığa düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5047, 'entel takılmak', ' argo bir süre entel gibi yaşamaya, onların yaptıklarını yapmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5048, 'entrika çevirmek', 'entrika ile amacına ermeye çalışmak, dolap çevirmek: ''Küçük entrikalar çevirmek onları mutlu kılıyorsa ne yapabilirdim?'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5049, 'entrikaya kurban gitmek', 'hileli, dalavereli bir iş sonunda zarara uğramak: ''İşi bu kadar sağlama almış olduğu hâlde, dışarıda entrikaya kurban gidiyormuşçasına ağlamaklı...'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5050, 'er ekmeği er kursağında kalmaz', 'bir kimseden iyilik gören kişi mertse bunun altında kalmaz, kendisi de ona iyilik yapmaya çalışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5051, 'er ekmeği, meydan ekmeği', 'kadın, kocasının kazancını rahatça yer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5052, 'er giden, işine; geç giden, boşuna', 'işine sabahleyin erken başlayan kimse başarı elde eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5053, 'er kocar, gönül kocamaz', 'kişi ihtiyarlar ama gönlü taze kalır, sevgisi eksilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5054, 'er lokması er kursağında kalmaz', 'insan, gördüğü iyiliği karşılıksız bırakmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5055, 'er olan ekmeğini taştan çıkarır', 'azimli kimse geçim yolunu bulmak için en güç işlerle bile uğraşmaktan yılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5056, 'er oyunu üçe kadar', 'birinci ve ikinci denemede başarılamayan iş için son kez üçüncü deneme yapılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5057, 'erdiğine erer, ermediğine taş atar', 'sataşkan, edepsiz kimse amacına ulaşamadığında her türlü kötülüğü yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5058, 'ere gitmek (varmak)', ' hlk. kadın veya kız evlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5059, 'ere vermek', 'kızı evlendirmek: ''Ninesini, kardeşini beslemiş hatta kız kardeşini ere vermişti.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5060, 'erenlerin sağı solu olmaz', 'ne zaman ne yapacağı belli olmayan kimseler için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5061, 'ergen gözüyle kız alma, gece gözüyle bez alma', 'insan hiçbir şeyi incelemeden, gözü kapalı biçimde almamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5062, 'ergen olmak', 'evlenecek çağa girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5063, 'ergene karı boşamak kolay', 'bir işin içinde olmayanlar o işteki güçlükleri küçümserler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5064, 'erim er olsun da yerim çalı dibi olsun', 'kadının kocasının fakir olması önemli değildir, yeter ki aile sorumluluklarını yerine getirsin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5065, 'erine göre bağla başını, tencerene göre kaynat (pişir) aşını', 'davranışlarını içinde bulunduğun koşullara uydur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5066, 'erinenin oğlu kızı olmamış', 'bir şeyi elde etmek için çalışmalı, tembel tembel oturmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5067, 'eriyip bitmek', 'üzüntü ve sıkıntıdan çok zayıflamak: ''O zaman da ben kahır yüzünden eriyip bitmiş olacağım.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5068, 'eriyip gitmek', 'yok olmak: ''Bence her şiir, yazılmasından, basılmasından, eriyip gitmesine kadar dört beş dönemden geçer.'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5069, 'erkân göstermek', 'yolunu yordamını öğretmek: ''Bize yol aç, erkân göster; yollar aç bize de, biz de adam sırasına girelim.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5070, 'erkeğin kalbine giden yol midesinden geçer', 'kendini bir erkeğe beğendirmek isteyen kadın, ona güzel yemekler hazırlamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5071, 'erkek arslan arslan da dişi arslan arslan değil mi?', 'güçlülük ve yüreklilik yalnızca erkeklerde değil kadınlarda da vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5072, 'erkek getirmeyi, kadın yetirmeyi bilmeli', 'erkek çalışıp kazanmakla, anne tasarruflu olmakla yükümlüdür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5073, 'erkek gibi', 'erkeğe yakışır, erkeğe benzer: Ayşe hanım erkek gibi sesiyle bağırdı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5074, 'erkek koyun kasap dükkânına yakışır', 'miskin erkek, yaşamaya layık değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5075, 'erkek sel, kadın (avrat) göl', 'erkek, parayı bilinçsizce harcama eğiliminde olsa bile kadın buna meydan vermemeli, tutumlu olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5076, 'erkekliğe sığmamak', 'mertliğe, yiğitliğe yakışmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5077, 'erkekliği kesilmek', 'erkek fizyolojik görevini yerine getirememek: ''Yaşlı imiş ... çoktan erkekliği kesilmiş.'' -K. Tahir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5078, 'erkekliğin onda dokuzu kaçmaktır', 'tehlikeden kaçan kazançlı çıkmış.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5079, 'erkekliğine yedirememek', 'mertliğe, yiğitliğe yakıştıramamak: ''Bir kimsesizlik acısı, sevilme, şefkat ihtiyacı içinde ağlıyor; ağlamayı erkekliğine yediremiyordu.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5080, 'erkeklik öldü mü?', 'haksızlığa karşı koymak, mertlik göstermek gerekiyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5081, 'erkeklik sende kalsın!', 'karşısındakinin yakışıksız davranışına uyup da tatsızlık çıkarma, efendice davran!', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5082, 'erkeklik taslamak', 'kendini erkek gibi göstermek, erkekçe davranışta bulunmak, kabadayıca davranmak: ''Şuna bak, hem karıdan dayak yer hem de erkeklik taslar.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5083, 'erken kalkan (çıkan) yol alır, er evlenen döl alır', 'yapacakları işe erken başlayanlar kazançlı olurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5084, 'erken kalktım işime, şeker kattım aşıma', 'işine sabahleyin erken başlayan kimse başarı elde eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5085, 'erketecilik etmek', 'gözetlemek: ''Hırsızlara erketecilik ettiğini anladı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5086, 'erketelik yapmak', 'gözetlemek: ''Bir kadınla erkeğin buluşmasında erketelik yapmak, pek de onurlu bir iş değildi ne de olsa.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5087, 'Ermeni gelini gibi kırıtmak', 'ağır veya yavaş hareket etmek: ''O kuruntularımız, o tafralarımız, o Ermeni gelini gibi kırıtmalarımız pek boşuna demektir.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5088, 'erozyona uğramak', '1) aşınmak; 2) mec. değer veya saygınlık kaybetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5089, 'es geçmek', ' tkz. üzerinde durmamak, boş vermek, önemsememek: ''Ortaya zam falcısı diye biri çıkar da hiç gazeteler, gazeteciler es geçerler mi bu haberi, olayı.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5090, 'esamesi okunmamak', 'kendisine değer verilmemek, adı anılmamak: ''Sen babasının gönlünü ettikten sonra kızın esamesi mi okunur bre usta!'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5091, 'esarette kalmak', 'uzun süre esir olarak bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5092, 'esas vaziyete geçmek', 'hazır ol durumunu almak: ''Kaldırımın önünde esas vaziyete geçip kasketini çıkardı.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5093, 'esasa bağlamak', 'belirli bir kurala dayandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5094, 'esası olmamak', 'gerçek olmamak, yalan olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5095, 'esasına bakarsan', 'aslına bakarsan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5096, 'esen kalmak', 'ruhsal ve bedensel olarak sağlıklı, sıhhatli olmak: Şen ve esen kalınız.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5097, 'eser kalmamak', 'hiçbir belirti, iz olmamak: ''Bir şirretleşmiş ki dünkü saygısından eser kalmamıştı.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5098, 'esermek besermek', 'emek vererek ortaya çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5099, 'esip gürlemek', 'sinirli bir biçimde kızmak, bağırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5100, 'esir almak', '1) tutsak etmek; 2) alıkoymak, meşgul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5101, 'esir düşmek', 'tutsak olmak: ''Beyhude ölmektense esir düşüp yaşamayı tercih ettikleri için teslim oldular.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5102, 'esir etmek', '1) tutsak durumuna getirmek: ''Beni bir takım vahşi çapulcular esir edip sımsıkı bağladıkları hâlde memleketlerine götürdüler.'' -N. F. Kısakürek. 2) mec. alıkoymak, meşgul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5103, 'esir yatmak', 'savaşta düşman eline düşüp uzun süre tutsak kalmak, esarette kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5104, 'esirgenen (sakınan) göze çöp batar', 'üzerine çok düşülen şeyler genellikle kazaya veya zarara uğrar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5105, 'eski ağza yeni taam', 'turfanda bir şey yenilirken söylenen söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5106, 'eski çamlar bardak oldu', 'devir değişti, eski tutumların değeri kalmadı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5107, 'eski defterleri kapatmak', 'eski olayları yeniden ele almamak: ''O, eski defterleri çoktan kapatmış, Osmanlıya kucağını açmıştı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5108, 'eski defterleri yoklamak (karıştırmak)', 'bir yarar umarak veya başka bir amaçla eski olayları yeniden ele almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5109, 'eski dost düşman olmaz olsa da dürüst olmaz', 'aralarında ufak tefek dargınlıklar olsa bile eski dostlar birbirlerine düşman olmazlar, yeni kazanılan dostlarla arada henüz sıkı bir bağ oluşmadığı için bu durum söz konusu değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5110, 'eski dost düşman olmaz, yenisinden vefa gelmez', 'aralarında ufak tefek dargınlıklar olsa bile eski dostlar birbirlerine düşman olmazlar, yeni kazanılan dostlarla arada henüz sıkı bir bağ oluşmadığı için bu durum söz konusu değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5111, 'eski düşman dost olmaz', 'birçok nedenin birbirini izlemesiyle sürüp gelmiş olan eski düşmanlık, dostluğa dönüştürülemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5112, 'eski hamam eski tas', '''hiçbir şeyi değişmemiş, eski durumunda kalmış'' anlamında kullanılan bir söz: ''Bereket versin, işi kuru gürültüden ileri gitmediği, her şeyin eski hamam eski tas kaldığı çabuk anlaşıldı.'' -K. Tahir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5113, 'eski hayratı da berbat etmek', 'bir işi daha iyi bir duruma sokmaya çalışırken büsbütün bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5114, 'eski kimliğine bürünmek', 'önceki düşüncelerine dönmek: ''Unutmak istediğim eski kimliğime bürünüvermiştim.'' -O. Pamuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5115, 'eski köye yeni âdet getirmek', 'alışılmamış, yadırganan bir yeniliği yapmaya kalkışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5116, 'eski kulağı kesiklerden olmak', 'görmüş geçirmiş, çok deneyimli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5117, 'eskisi kadar (gibi)', 'eskiden olduğu gibi, eskiden olduğu biçimde: ''Doğal güzellikler artık eskisi gibi turist çekmiyor.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5118, 'eskisi olmayanın yenisi (acarı) olmaz', 'yeni bir şey edinildiğinde eskisi hemen elden çıkarılmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5119, 'eskisini aratmamak', 'yenisi eskisinin yerini doldurabilmek, yokluğunu sezdirmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5120, 'eskiye rağbet (itibar) olsaydı bitpazarına nur yağardı', 'her şeyin yenisi sevilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5121, 'esmayı üstüne sıçratmak', 'davranışlarıyla belayı üstüne çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5122, 'esmere al bağla, karşısına geç ağla', 'esmer insana kırmızı renkli giysi yakışmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5123, 'espri patlatmak', 'konuşma sırasında, beklenilmedik anda, ortama uygun hoş, nükteli veya ilginç söz söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5124, 'esrar çekmek', 'esrar içmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5125, 'esrara dalmak', 'sırlara gömülmek: ''Sular büsbütün kararınca Boğaz''ın hayatı da büsbütün esrara dalar.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5126, 'esrik devenin çulu eğri gerek', 'kişi, durumuna uygun davranmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5127, 'estek köstek etmek', 'oyalamak, yersiz bahaneler bulmak, işten kaçınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5128, 'eş tutmak', 'talimde veya oyunda ikişer olmak için arkadaş seçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5129, 'eşeğe altın semer vursalar yine eşektir', 'insanlık değerinden yoksun kişi, kılık kıyafetle, makam ve mevkiyle değer kazanmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5130, 'eşeğe cilve yap demişler, çifte atmış', 'kaba ve ahmak kişinin hoşa gitsin diye söylediği sözler ve yaptığı işler, kaba ve incitici olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5131, 'eşeğe gücü yetmeyip semerini dövmek', ' tkz. güçlü birine kızıp da ondan alamadığı hıncını çevresindekilerden çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5132, 'eşeği dama çıkaran yine kendi indirir', 'yanlış yapan kimse, yanlışı yine kendisi düzeltir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5133, 'eşeği düğüne çağırmışlar, ''ya odun eksik ya su demiş''', 'bir işi yapmamak için bahane bulmayı anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5134, 'eşeği düğüne çağırmışlar, ''ya su lazımdır ya odun'' demiş', 'bir işi yapmamak için bahane bulmayı anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5135, 'eşeği süren (tırmalayan) osuruğuna katlanır', 'kaba bir kimse ile birlikte olan, ondan gelecek kötü davranışları göze almalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5136, 'eşeği yoldan çıkaran sıpanın oynaması', 'çocuklarının düzensiz davranışı, anne babayı rahatsız eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5137, 'eşeğin kuyruğu gibi', 'her zaman aynı durumda kalan, hiç değişikliğe uğramayan: ''Benim aylığım eşeğin kuyruğu gibi ne kısalır ne uzar.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5138, 'eşeğin kuyruğunu kalabalıkta kesme; kimi uzun der, kimi kısa', 'kimseyi ilgilendirmeyen işleri kendi kendine karar verip yapmalısın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5139, 'eşeğin ölümü köpeğe ziyafettir (düğündür)', 'bir kişinin uğradığı zarar kimi zaman bir başkası için çıkar kaynağı olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5140, 'eşeğin sahibinin dediği yere bağla da varsın kurt yesin', 'kötü bir sonuç meydana geldiğinde sorumlu olmamak için işi, sahibinin isteğine uygun olarak yap.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5141, 'eşeğini sağlam kazığa bağlamak', 'işini güven altına almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5142, 'eşek at olmaz, ciğer et olmaz', 'soysuz kişi soylu olmaz, bayağı şey üstün nitelik kazanmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5143, 'eşek bile bir düştüğü yere bir daha düşmez', 'aptal kişi bile başına gelen felaketten ders alır, o felakete yol açan şeylerden kendisini korur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5144, 'eşek çamura çökerse sahibinden gayretlisi olmaz', 'bir kimsenin işi bozulduğunda, durumunu düzeltmek için en büyük çabayı kendisinin göstermesi gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5145, 'eşek derisi gibi', '1) derisi çok kalın; 2) mec. duygusu az, duygusuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5146, 'eşek eşeği ödünç kaşır', 'çıkarcı, başkasına yardım ederken ileride onun da kendisine yardım edeceğini düşünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5147, 'eşek (eşkin) eve gelmiş, yorga yolda kalmış', 'düzenli ve sürekli çalışan güçsüz kimse, düzensiz ve süreksiz çalışan güçlü kimseden daha başarılı olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5148, 'eşek gibi', 'kaba, düşüncesiz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5149, 'eşek hoşaftan ne anlar (suyunu içer, tanesini bırakır)', 'bilgisiz, görgüsüz kimse ince, güzel şeylerin zevkine varamaz, değerini ölçemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5150, 'eşek kadar', ' hkr. büyük, iri, aşırı derecede gelişmiş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5151, 'eşek kocamakla tavla başı olmaz', 'anlayışsız kişi ne kadar yaşlanırsa yaşlansın baş olacak bir olgunluğa ulaşamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5152, 'eşek kulağı kesilmekle küheylan olmaz', 'aslında niteliksiz olan bir şeye ne yapılsa değişmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5153, 'eşek kuyruğu gibi ne uzar ne kısalır', 'durumunda, çalışmasında hiçbir gelişme görülmeyen kimseler için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5154, 'eşek sudan gelinceye kadar dövmek', ' tkz. adamakıllı dövmek: ''Uslu otur yoksa ufak bir münasebetsizliğini duyarsam eşek sudan gelinceye kadar döverim, kemiklerin kırılır, anladın mı?'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5155, 'eşekten düşmüş karpuza (düşmüşe) dönmek', ' argo 1) çok şaşırmak, donup kalmak; 2) kötü bir duruma düşmek: ''Bunlar ezberlerindeki mânileri söylerler, dağarcıklarında mâni kalmayınca da eşekten düşmüş karpuza dönerler.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5156, 'eşi manendi (menendi, benzeri) olmamak (bulunmamak, yok)', 'benzeri olmamak: ''Allah rahmet eylesin, eşi menendi bulunmaz bir adamdı Nazmi Albay.'' -A. Ümit. ''Bir iki çıkışı, Arap Kadir''in bir eşi benzeri bulunmadığı gerçeğini ortaya koymuştu.'' -K. Korcan. ''Bizim dairenin müdürünün bir eşi benzeri daha yoktur.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5157, 'eşiğine yüz sürmek', 'bir dilekte bulunmak için bir kişiye yalvarmaya gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5158, 'eşiğini aşındırmak', 'işini yaptırmak için bir yere çok gidip gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5159, 'eşik (eşiğini) atlamak', '1) herhangi bir konuyu doyasıya yaşayarak belli bir olgunluğa ulaşmak: ''Sevginin, merhametin eşiğini atlayanlar, ızdırabın gömleğini de kendiliğinden giyinirler.'' -A. H. Tanpınar. 2) engelleri aşmak, zorlukları yenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5160, 'eşkıya gibi', 'yüzü, bakışları ve kılığı korkunç olan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5161, 'eşkıyanın (ihtiyarın, fukaranın) düşkünü, beyaz (hasa) giyer kış günü', 'daha önce iyi bir durumda olan kişi bu konumunu kaybettiğinde uygun olmayan, yersiz davranışlarda bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5162, 'et bağlamak', '1) şişmanlamak; 2) yara kapanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5163, 'et kanlı gerek, yiğit canlı', 'kebap çok pişirilmemeli, genç de hareketli ve canlı olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5164, 'et kokarsa tuzlanır, ya tuz kokarsa ne yapılır?', 'bozulan şeyi düzeltecek etken vardır ancak bu etken bozulmuşsa artık düzeltmeden umudu kesmek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5165, 'et ne kadar arık olsa üstüne ekmek yaraşır', 'bilgili ve görgülü kişi, iş başında ve zengin olmasa da bilgisiz ve görgüsüz kişilerin üstünde yer alır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5166, 'et tırnak olmak', 'sıkı aile bağı kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5167, 'et tırnaktan ayrılmaz', 'yakın hısımlar arasındaki bağ kolay kolay kopmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5168, 'et tutmak', 'et bağlamak: ''Ye de biraz et, can tut.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5169, 'ete kemiğe dönüştürmek (büründürmek)', 'canlandırmak: ''Aşkımemnu''da Firdevs Hanım''ı inanılmaz bir başarıyla ete kemiğe dönüştürmüş.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5170, 'eteğe varmak', 'yardım istemeye gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5171, 'eteği ayağına dolaşmak', 'eli ayağı dolaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5172, 'eteği kirlenmek', 'kadının namusuna dokunulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5173, 'eteğinde namaz kılınmak', 'içi dışı çok temiz kişi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5174, 'eteğindeki taşı dökmek', 'bütün bildiklerini açıklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5175, 'eteğinden ayrılmamak', 'peşini bırakmamak: ''Bu ağırsamaları anlamakla beraber aldırmayan Hilmi, eteğinden ayrılmıyor, peşi sıra yürümekten vazgeçmiyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5176, 'eteğinden el çekmek', '1) etliye sütlüye karışmamak; 2) birini tacizden vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5177, 'eteğine düşmek (sarılmak)', 'yalvarıp yakarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5178, 'eteğine yapışmak (sığınmak)', 'birinin koruyuculuğu altına girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5179, 'eteğini başına atmak (sarmak)', 'birini azarlamak, onur kırıcı sözlerle suçlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5180, 'eteğini çekmek', 'günah sayılan işlerden uzak durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5181, 'eteğini tutmak', 'yardım istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5182, 'eteğiyle mum söndürmek', 'uygun olmayan biçimde iş yapmak, sakar olmak, üstünkörü davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5183, 'etek açmak', 'kadın, cinsel arzusunu belirtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5184, 'etek öpmek', 'yaltaklanmak, dalkavukluk etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5185, 'etek öpmekle dudak aşınmaz', 'çok önemli bir iş için bir kimseye ricada bulunmak hatta yalvarmak gerekirse, yapılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5186, 'etek silkmek', '1) el etek çekmek; 2) çekilmek, artık karışmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5187, 'etek takmak (giymek)', ' argo erkek ar, namus, erdem vb. özellikleri bulunmayan duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5188, 'etekleri tutuşmak', 'çok telaşlanmak: ''Öğleden sonra vali yine kıza köpüre arayınca komiser Zihni ''nin etekleri iyice tutuştu.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5189, 'etekleri uzamak', 'yanlışları düzeltmek, ayıbını kapatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5190, 'etekleri zil (ıslık veya çalpara) çalmak', '1) çok sevinmek: ''İlk mektebe gittiği gün Gülsüm''ün sevincinden etekleri zil çalıyordu.'' -R. N. Güntekin. 2) alınan sevinçli bir haber üzerine telaşa ve heyecana kapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5191, 'eteklerini indirmek', ' argo üzerine düşen görevi yerine getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5192, 'eteklerini toplamak', 'düzenli, temiz veya namuslu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5193, 'eti budu yerinde (etine dolgun)', 'şişmanca, tombul.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5194, 'eti kemiğine yapışmak', 'çok zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5195, 'eti ne budu ne?', '1) ''yaşı küçük'' anlamında kullanılan bir söz; 2) ''imkânları, gücü sınırlı, parası az'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5196, 'eti senin, kemiği benim', 'çocuğu ben doğurdum ama onu eğitmek sana düşüyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5197, 'etinden et koparmak (kesmek)', 'çok acı vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5198, 'etki bırakmak', 'kuvvetli bir biçimde etkilemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5199, 'etkisini göstermek', 'etkisini ortaya koymak, belli etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5200, 'etle tırnak arasına girilmez', 'aile anlaşmazlıklarında bir yanı tutmak doğru değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5201, 'etle tırnak gibi', 'birbirlerine candan bağlı, sıkı ilişkili.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5202, 'etliye sütlüye karışmamak', 'toplum içindeki çeşitli hareketlerden uzak durmak, hiçbir şeyle ilgilenmemek, tartışmalı konulardan kaçınmak: ''Etliye sütlüye karışmamak ve hiçbir ideal için hiçbir mücadeleye katılmamak onun mizacıdır.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5203, 'etme bulma dünyası', 'kötülük eden kötülük bulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5204, 'etme bulursun, inleme ölürsün', 'nasıl davranırsan öyle karşılık görürsün.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5205, 'etme bulursun, inleye inleye ölürsün', 'nasıl davranırsan öyle karşılık görürsün.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5206, 'etme eyleme', 'bir davranış karşısında ''yapma'' anlamında kullanılan bir söz: ''Aman etme, aman etme eyleme, şuradan şuraya tek adım atma, saraydan çıkma!'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5207, 'etmediğini bırakmamak (komamak)', 'elinden gelen her türlü kötülüğü yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5208, '(... etmenin) âlemi var mı?', '''yakışık alır mı, uygun olur mu?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5209, 'etrafı boş bulmak', 'kendisini engelleyecek kimse olmamak: ''Vaktiyle etrafı boş bulduğu için mistik sıfatını takınmak istemişti.'' -A. H. Çelebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5210, 'etten duvar örmek', 'korumak amacıyla çevresinde kalabalık insan birikmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5211, 'etten önce çömleğe düşmek', 'bir işte bilgisiz veya yetkisiz olmasına rağmen herkesten önce ortaya atılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5212, 'ettiği hayır, ürküttüğü kurbağaya değmemek', 'yol açtığı zarar, yaptığı iyilikten büyük olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5213, 'ettiği yanına (kâr) kalmak', 'yaptığı kötülük karşılıksız kalmak, cezasını görememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5214, 'ettiğini bulmak (çekmek)', 'yaptığı kötü davranışın karşılığını görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5215, 'ettiğini yanına bırakmamak', 'yapılan kötü davranışa karşılık vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5216, 'ettiğiyle kalmak', '1) yapmak istediği kötülüğü başarıya ulaştıramayan kimse, başarısızlığın üzüntüsü ve utancı içinde kalmak; 2) yapmak istenilen kötülük amacına ulaşamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5217, 'ev açmak', '1) ayrı bir eve yerleşmek, ayrı bir eve geçmek; 2) evlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5218, 'ev alanla evlenene Allah yardım eder', 'evlenene ve ev yapana herkesin kolaylık göstermesi, onlara Allah''ın yardımının dolaylı olarak ulaşıyor olması demektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5219, 'ev alma, komşu al', 'komşuluk ilişkileri, iyi bir komşuya sahip olma çok çok önemlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5220, 'ev bark yıkmak', 'karı kocayı birbirinden ayırmak: ''Bir kızı vardı ki dünyanın bütün kusurları bir araya gelse onun kadar ev bark yıkamazdı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5221, 'ev bozmak', '1) karı koca ayrılmak; 2) karı kocanın ayrılmalarına sebep olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5222, 'ev ev dolaşmak (gezmek)', 'her eve uğrayarak dolaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5223, 'ev işletmek', 'genelev sahibi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5224, 'ev sahibi mülk sahibi, hani nerede bunun ilk sahibi', 'kişi malını mülkünü kaybederim korkusuyla kendini üzüntüye kaptırmamalı, malı mülkü ile övünmemelidir, zira mal mülk dünyaya ait bir şeydir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5225, 'ev sahibinin bir evi, kiracının bin evi var', 'evi olan yalnızca kendi evinde oturur, evi olmayan ise beğendiği evde oturur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5226, 'ev tutmak', 'ev kiralamak: ''Annemden kalma bir evim vardı. Onu rehine koyarak bir ev tuttuk.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5227, 'evci çıkmak', 'tatil günlerinde okul, kışla vb.nden eve gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5228, 'evde kalmak', ' tkz. kızın evlenme çağı geçmiş olmak: ''Hiç evlenmeyen kız olur muymuş, evde kalmış mı dedirtecen kendine?'' -E. Işınsu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5229, 'evdeki pazar (hesap) çarşıya uymaz', 'önceden tasarlanan bir iş umulduğu gibi sonuçlanmaz, düşünüldüğü gibi olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5230, 'eve çıkmak', '1) aileden ayrılıp ayrı bir evde oturmak; 2) öğrenci yurttan ayrılıp ev kiralayarak yaşamak: ''Öğrencilerin bir bölümü, ilk yılı yurtta geçirse bile ikinci yıldan başlayarak eve çıkmayı yeğler.'' -A. Cemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5231, 'evi ev eden avrat', 'bir evin dirlik ve düzenini kadın sağlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5232, 'evimiz bezden, ne umarsın bizden', 'kendisi yardıma muhtaç olandan yardım beklemek boşuna umutlanmaktır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5233, 'evin bağlamak', 'ürün tanelenmek, tane bağlamak, olgunlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5234, 'evirip çevirmek', 'iyice, istediği gibi, adamakıllı gözden geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5235, 'evladı ben doğurdum ama gönlünü ben doğurmadım', 'bir kimse evladına emredip birçok şey yaptırır ancak onun gönlüne hükmedemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5236, 'evladın var mı, derdin var', 'çocuklarının sıkıntıları, hastalıkları ana baba için sürekli derttir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5237, 'evlenenle ev alana Allah yardım eder', 'evlenene ve ev yapana herkesin kolaylık göstermesi, onlara Allah''ın yardımının dolaylı olarak ulaşıyor olması demektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5238, 'evlerden ırak (uzak)', ' hlk. ölüm veya kötü bir durumdan söz edilirken dinleyenlerin aynı durumla karşılaşmamalarını dilemek için söylenen bir söz: Evlerden ırak, dağ gibi delikanlı iki günde devrildi gitti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5239, 'evlere şenlik', ' alay beğenilmeyen, olumsuz karşılanan bir durum, bir davranış karşısında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5240, 'evli evine, köylü köyüne', 'artık dağılalım, herkes evine, işine gitsin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5241, 'evlinin bir evi var, kiracının bin evi var', 'evi olan yalnızca kendi evinde oturur, evi olmayan ise beğendiği evde oturur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5242, 'evliya gibi', '1) uysal (kimse); 2) iyi ahlaklı (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5243, 'evrat çekmek', 'okunması âdet olan duaları ve Kur''an ayetlerini sürekli tekrarlamak: ''Geceleri Hüsnü''nün evinde toplanır, zikreder, evrat çekerlermiş.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5244, 'evvel hesap, sonra kasap', '1) alışverişe çıkan bir kimse para durumunu önceden gözden geçirmelidir; 2) insan bir işe girişmeden önce kendi olanaklarını iyi hesaplamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5245, 'evvel yediğim hurmalar, bugün kıçımı tırmalar', 'aradan bir yıl geçmiş, davranışının karşılığını şimdi görüyor, yaptığının acısı bugün çıkıyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5246, 'evvel zaman içinde, kalbur saman içinde', '''çok zaman önce'' anlamında bir tekerleme.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5247, 'evvela can, sonra canan', 'insanlar bencildir, önce kendilerini, sonra yakınlarını düşünürler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5248, 'eyer boşaltmak', '1) cirit oyununda hedef olmaktan kurtulmak için eyer üzerinde sağa sola eğilmek; 2) saldırıları boşa çıkaracak önlemler almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5249, 'eyer kapatmak (kapamak)', 'eyeri atın sırtına koyup bağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5250, 'eyer vurmak', 'eyeri hayvanın sırtına koyup bağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5251, 'eyere de gelir semere de', 'her işe yarar, her türlü işi görebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5252, 'eyeri boş kalmak', 'atın binicisi ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5253, 'eylem koymak', 'eylemde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5254, 'eylemde bulunmak', 'toplu hâlde hareket etmek: ''Hiçbir Türk, vatana bağlılıkla bağdaşmayan bir eylemde bulunmadıkça vatandaşlıktan çıkarılamaz.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5255, 'eyleme geçmek', 'tasarlanan bir işi uygulamaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5256, 'eyvallah demek', '1) hoş görerek kabul etmek veya edilmek: ''Mersi denir. Sonra teşekkür edilir. Eyvallah denir, çok mersi denir.'' -S. F. Abasıyanık. 2) hoşça kalın, sağlıcakla kalın demek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5257, 'eyvallah etmemek', 'birinden yardım istememek, gönül borcu olmamak, boyun eğmemek: ''Ben kimseye eyvallah etmeyeceğim, dedi. Bir gece amcasının konağından kaçtı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5258, 'eyvallahı olmamak', 'kimseye gönül borcu, minneti olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5259, 'eyyam görmek (sürmek)', 'iyi günler geçirmek, mutlu zamanlar yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5260, 'eyyam ola!', '''havanın iyi olmasını dilerim'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5261, 'ez de suyunu iç', 'değersiz, yararsız şeyler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5262, 'ezber bozmak', 'birinin sahip olduğu düşüncenin yanlış olduğunu göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5263, 'ezber etmek', 'ezberleyerek akılda tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5264, 'ezber okumak', 'bir metni veya sözü herhangi bir yere bakmadan bellekte kalan biçimiyle söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5265, 'ezberden okumak', 'daha önceden belleğine aldığı için herhangi bir yere bakmadan söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5266, 'ezberden yapmak', 'bir yere bakmadan bellekte kalan biçimiyle yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5267, 'ezbere almak', 'dikkat etmeden satın almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5268, 'ezbere anlatmak', 'okunan bir şeyi olduğu gibi, bozmadan anlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5269, 'ezbere bilmek', '1) bir yerin her yanını iyice bilmek: ''Buraların altını ezbere bilirim, ezbere.'' -S. F. Abasıyanık. 2) bir şeyin bütün niteliklerini çok iyi öğrenmiş olmak: ''Yolun neresi kayalık, neresi kumsal hep ezbere bilirdi.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5270, 'ezbere iş görmek', 'incelemeden gelişigüzel yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5271, 'ezbere konuşmak', 'bilmeden, aslını arayıp sormadan konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5272, 'ezbere yapmak', '1) ezberden yapmak; 2) model veya doğa karşısında durmayarak fikirden tasavvur ve tahayyül suretiyle resim yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5273, 'ezberinde olmak', 'aklında tutmuş olmak: ''En az yirmi şiiri ezberimdeydi.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5274, 'eziklik duymak', 'kendini mahcup hissetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5275, 'ezilip büzülmek', 'güç bir duruma düşüp davranışlarıyla utandığını belli etmek: ''Etrafındakiler hanımefendiye karşı bir suç işlemiş gibi ezilip büzülüyorlar.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5276, 'ezilmeden yenilmek', 'başa baş bir karşılaşma çıkararak az farkla yenilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5277, 'ezip büzmek', 'ezip parçalayarak tamamen değiştirerek kullanılmaz veya anlaşılmaz duruma getirmek: ''Bütün ecnebi kelimeleri ezip büzüp anlaşılmaz hâle getirip öyle kullanıyorlar.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5278, 'eziyet çekmek', 'zahmet ve sıkıntıya uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5279, 'eziyet etmek', 'zahmet ve sıkıntı vermek, canını yakmak: ''Annesine eziyet ettiğine inandığı babasından fazla bahsetmek istemediğini sezdim.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5280, 'eziyet vermek', 'zahmet çektirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5281, 'faaliyet göstermek', '1) çalışmak; 2) işler durumda olmak, etkinlik göstermek: ''Casusların en çok faaliyet gösterdikleri liman da burasıydı.'' -F. F. Tülbentçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5282, 'faaliyete geçmek', '1) çalışmaya başlamak, çalışır duruma geçmek, işlemeye başlamak; 2) işler duruma gelmek, etkin duruma gelmek: ''Bir siyasi grup, başka cinsten bir faaliyete geçmiş görünüyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5283, 'faaliyette bulunmak', 'çalışma içine girmek: ''Sendikalar siyasi amaç güdemezler, siyasi faaliyette bulunamazlar.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5284, 'faaliyetten alıkoymak', 'çalışması durdurulmak, çalışmadan alıkonulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5285, 'faça etmek', 'serenleri başa veya geriye doğru çevirerek yelkenleri sarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5286, 'façası olmak', 'havalı, gösterişli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5287, 'façasını almak (al aşağı etmek)', 'birini mahcup etmek, bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5288, 'façuna etmek', 'sürtünme veya hava olaylarından korumak amacıyla halatı ince iple sarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5289, 'faize yatırmak (vermek)', 'parasını faizle çoğaltmak için bankaya para yatırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5290, 'faka basmak', 'aldatılmak, tuzağa düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5291, 'fakir düşmek', 'yoksullaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5292, 'fakir tavuğu tek tek yumurtlar', '''destekçisi olmayan, dayanağı olmayan kimsenin işleri yavaş yürür'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5293, 'fakirlik ayıp değil, tembellik ayıp', 'yoksulluk utanılacak bir şey değildir, çalışmamak en büyük ayıptır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5294, 'fakirlik ayıp değil, tembellik ayıp', '''yoksulluk utanılacak bir şey değildir, çalışmamak en büyük ayıptır'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5295, 'fal açmak (bakmak)', 'bakla, su, iskambil vb.ne bakarak gelecekte olacak şeyleri anlamaya çalışmak: ''Tutun birer niyet de açayım size birer maydanozlu fal!'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5296, 'fal taşı gibi', 'iri, büyük: ''Bu elleri güzel, yüzü çirkin delikanlı, ilk defa, gözleri fal taşı gibi açık, ruhundan bir ses koparabildi.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5297, 'fala bakmak', 'fal açmak: ''Para ile fala baktığı hâlde geçim sıkıntısından kurtulamıyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5298, 'fala inanma, falsız kalma', 'kişinin oyalanmak, yalan da olsa geleceği üzerine bir şeyler dinlemek isteğini hoş karşılamak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5299, 'falakaya çekmek (yatırmak veya vurmak veya yıkmak)', 'falakaya bağlayarak dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5300, 'falso çıkmak', 'bozuk olmak: ''Yüzde beş yüz kâr beklediği bu işlerin alt tarafı falso çıkınca apışmış kalmıştı.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5301, 'falso vermek', '1) bozulmaya yüz tutmak: ''Artık İstanbul''da her şey gevşemiş, falso vermişti.'' -Ö. Seyfettin. 2) açık vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5302, 'falso yapmak', 'yanlış davranışta bulunmak: ''Yeteneksizliğini ortaya koyacak bir falso yapmaktan korkuyordu.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5303, 'faraş gibi (kadar)', 'normalinden fazla açılan (ağız).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5304, 'fare çıktığı deliği bilir', 'bir kabahate, suça veya gizli işe kalkışan kişi, yakalanacağını anladığında nereye sığınacağını bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5305, 'fare çıktığı deliği bilir', '''bir kabahate, suça veya gizli işe kalkışan kişi, yakalanacağını anladığında nereye sığınacağını bilir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5306, 'fare deliğe sığmamış, bir de kuyruğuna (kıçına) kabak bağlamış', '1) yapamayacağı kadar ağır bir işi varken başka bir iş daha yüklenmiş; 2) kendisi sığıntı durumundayken yanına bir kişi daha almış.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5307, 'fare deliğe sığmamış, bir de kuyruğuna (kıçına) kabak bağlamış', '1) ''yapamayacağı kadar ağır bir işi varken başka bir iş daha yüklenmiş'' anlamında kullanılan bir söz; 2) ''kendisi sığıntı durumundayken yanına bir kişi daha almış'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5308, 'fare deliği bin altın', '''herkesin kaçıp saklanacak bir yer aradığı durumlarda, saklanılacak bir yer bulmak çok güçtür ve o yer çok değerlidir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5309, 'fare düşse başı yarılır', 'bir yerin boş ve yoksulluk içinde bulunduğunu anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5310, 'fareler cirit atmak (oynamak)', 'bir yerde hiç insan bulunmamak, o yer çok ıssız olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5311, 'fariğ olmak', 'vazgeçmek, çekilmek, el çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5312, 'fark atmak', 'ileri gitmek, çok üstün gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5313, 'fark etmek', '1) görmek, seçmek: ''Boğaz''ın sisle kaplı olduğunu ancak ön güvertede bir yer bulup oturunca fark etmişti.'' -A. İlhan. 2) anlamak, sezmek: ''Öç almanın fırsatını yakalamış gibi konuştuğunu fark etti.'' -T. Buğra. 3) değişmek, başkalaşmak; 4) ayırt etmek: ''Konuşma kesilmiyor, şimdi yabancı sesleri daha iyi fark etmekteyim.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5314, 'fark etmez', '''önemi yok, etkisi olmaz, değişmez'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5315, 'fark gözetmek', 'ayrı tutmak: ''Siz erkekler ekseriya nikâhlı kadınla nikâhsız kadınlarınız arasında bir fark gözetirsiniz.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5316, 'fark olunmak', '1) seçilip ayırt edilmek; 2) anlaşılmak; 3) sezilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5317, 'fark yapmak', 'üstünlük sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5318, 'farkına varmak', 'gözüne çarpmak, fark etmek, anlamak: ''Bu nedenle karısının gözlerinde biriken öfkenin farkına varmadı.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5319, 'farkında olmak', 'görülmesi veya bilinmesi gereken şeylerden haberi bulunmak, kavranması gereken bir şeye dikkat etmek: ''Farkında olmadan kendini bir gün bu pis, hastalıklı, cerahatli suyun dibinde bulacaksın.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5320, 'fart furt etmek', 'anlamsız, boş sözlerle böbürlenmek, farta furta etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5321, 'farta furta etmek', 'fart furt etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5322, 'fartası furtası olmamak', 'patavatsızca konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5323, 'farz etmek', 'varsaymak: ''Bu bir nevi başkasını yok farz etmek ve sonunda küçümsemek değil midir?'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5324, 'farz olmak', 'yapılması kaçınılmaz olmak: Bunun üzerine, işe bir son vermek farz oldu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5325, 'farz olunmak', 'varsayılmak: ''Vapurun kahvecisi Kefalonyalı denilen ve kötü bir insan farz olunan biriydi.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5326, 'fasıla vermek', 'ara vermek, kesmek: ''Birer kart göndererek baş ağrılarından dolayı bu kabullere fasıla verdiğini bildirmişti.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5327, 'fasit olmak', 'namaz, oruç, abdest vb. bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5328, 'fasulye gibi kendini nimetten saymak', 'kendine çok değer vermek, kendini bir şey sanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5329, 'fasulye sırığı gibi', 'zayıf, sıska ve çok uzun boylu: ''Fasulye sırığı gibi üç buçuk akasya ile park mı olurmuş?'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5330, 'fatura etmek', 'faturalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5331, 'fatura kesmek', 'satılan bir şey için fatura düzenlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5332, 'faturasını (birine) çıkarmak (ödetmek)', 'sorumluluğu birine yüklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5333, 'fayda etmemek', 'etkisi olmamak, işe yaramamak, yararlı olmamak: ''Hekimler epeyce çalıştılar, ilaç verdiler, kan aldılar ise de fayda etmedi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5334, 'fayda vermemek', 'yararlı olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5335, 'faydalı olmak', 'yararlı olmak: ''Şu seyyahlar İstanbul''a faydalı oluyorlar mı bilmem ama bana zararları dokundu.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5336, 'faydası dokunmak', 'yararı dokunmak: ''Şimdiye kadar bana iki paralık faydan dokundu mu ki her gün alacaklı gibi gırtlağıma sarılıyorsun!'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5337, 'faydası olmak', 'yararı olmak: ''Kimseye faydası olmayıp da yalnız kendi nefsine ayırdığın servet, asla makbul değildir.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5338, 'faydasını görmek', '1) yarar sağlamak: ''Faydasını gördüğümüz ve faydasını görürsek tekrar etmemizi doktor tavsiye ettiği için reçeteyi yine aldım ele.'' -N. Hikmet. 2) kâr elde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5339, 'faydasız baş mezara yaraşır', 'yaşayan kimse bir işe yaramalıdır, bir işe yaramayan kimsenin ölüden farkı yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5340, 'faydasız baş mezara yaraşır', '''yaşayan kimse bir işe yaramalıdır, bir işe yaramayan kimsenin ölüden farkı yoktur'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5341, 'fayrap etmek', '1) ocağın ateşini harlandırmak; 2) argo herhangi bir işi veya şeyi hızlandırmak: Beleş rakıyı bulunca fayrap etti. 3) argo açmak, çıkarmak: Pencereleri fayrap etti. Gömleği fayrap etti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5342, 'fazla gelmek (kaçmak)', 'çekilmeyecek, bıktıracak, tedirgin edecek bir durum almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5343, 'fazla kaçırmak', 'alışılmış olan ölçüden çok içmek, yemek veya konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5344, 'fazla mal göz çıkarmaz', 'ne kadar ve ne türden mal olursa olsun malın fazlası elden çıkarılmamalıdır çünkü mutlaka bir gün gelir lazım olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5345, 'fazla mal göz çıkarmaz', '''ne kadar ve ne türden mal olursa olsun elden çıkarılmamalıdır'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5346, 'fazla olmak', 'dayanma gücünü aşacak davranışlarda bulunmak, çok olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5347, 'fazlalık etmek', 'birinin varlığı, bulunduğu yerde gereksiz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5348, 'feda etmek', 'kıymak, gözden çıkarmak: ''Her şeyi feda ederek onun peşine takılmış.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5349, 'feda olmak', 'uğrunda yok olmak: Kim bu cennet vatanın uğruna olmaz ki feda?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5350, 'feda olsun!', '''varsın gitsin, uğrunda yok olsun!'' anlamında kullanılan bir söz: ''Böyle harmancı çingeneler, sana düzinelerle feda olsun!'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5351, 'fedakârlığa katlanmak', 'bir amaca, bir emele ulaşmak için birçok sıkıntıya, üzüntüye, güçlüğe dayanmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5352, 'fedakârlık etmek', '1) özverili davranmak: ''Kadınlar fedakârlık ettikleri erkekleri severler.'' -P. Safa. 2) azlığına katlanmak, az oluşu ile yetinmek, vazgeçmek: ''İlk defa ömründe yemek saatinden fedakârlık etti.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5353, 'fedakârlık yapmak (göstermek)', 'özverisini ortaya koymak: ''Yalnız rica ederim, bir an için zahmet ve fedakârlık daha yapın!'' -H. F. Ozansoy. ''Arkadaşının karısına gösterdiği fedakârlığı o karısına gösteriyordu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5354, 'felah bulmak', 'kurtulmak, onmak: ''Kadın delifişeğin biri ise yine felah bulamazsın.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5355, 'felç gelmek', 'inme inmek: ''Babam kendisine felç geldiği zaman beni affetti ve çağırttı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5356, 'felç olmak', '1) inme inmek; 2) bir iş içinden çıkılamaz durum almak, tıkanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5357, 'feleğe küsmek', 'talihten yakınmak, şanstan ümidini kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5358, 'feleği şaşmak', ' argo feleğini şaşırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5359, 'feleğin çemberinden geçmek', 'hayatta acı tatlı günler görmüş geçirmiş olmak, olgunlaşmış, deneyim kazanmış olmak: ''Oyuna bir de kalender, feleğin çemberinden geçmiş ihtiyar komiser koyacaksınız.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5360, 'feleğin sillesini yemek (sillesine uğramak)', 'büyük bir yıkıma uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5361, 'feleğini şaşırmak', ' argo ummadığı bir durumda kalmak, şaşkınlık içine düşmek: ''Bir gün burada koyu ateş renginde bir hotoz görmüştür ki feleğini şaşırmıştır.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5362, 'felek, kimine kavun yedirir kimine kelek', 'bu dünyada kimi insanlar mutluluk içinde yaşarlar, kimileri de talihsizdirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5363, 'felek, kimine kavun yedirir kimine kelek', '''bu dünyada kimi insanlar mutluluk içinde yaşarlar, kimileri de talihsizdirler'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5364, 'felek yâr olursa', '''bir terslik çıkmazsa, şartlar uygun giderse'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5365, 'felekten bir gün (gece) çalmak', 'güzel bir gün veya gece geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5366, 'felekten kâm almak', 'güzel vakit geçirmek, istediği gibi eğlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5367, 'felsefe yapmak', '1) olayların sebep ve sonuçları üzerine kendince soyut birtakım düşünceler ileri sürmek: ''Sana su şehirlerinin felsefesini yaptım.'' -H. C. Yalçın. 2) bilgiçlik taslamak: ''Saldırmak onun içgüdülerinden biridir ve yöntemi çekiçle felsefe yapmaktır.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5368, 'fena bulmak', 'ölmek, yok olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5369, 'fena değil (sayılmaz)', 'oldukça iyi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5370, 'fena yapmak', 'kötü duruma düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5371, 'fenalık etmek', 'kötülük etmek, kötülükte bulunmak: ''Bilmeyerek sütnineciğime ve kendime büyük bir fenalık etmiştim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5372, 'fenalık geçirmek (gelmek veya çökmek)', 'kendini bilmeyecek veya bayılacak bir duruma gelmek: ''Ben biraz fenalık geçirdim de eczaneden rica ettik.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5373, 'fenasına gitmek', 'üzülmek, gücenmek, kırılmak, sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5374, 'fenaya çekmek', 'söylenen bir sözü kötü tarafından anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5375, 'fenaya sarmak', 'iş veya durum kötüye gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5376, 'fener çekmek', '1) elinde fenerle önden gitmek: ''Fener çeken çocuk, herkese yolunu göstermek mecburiyetinde.'' -B. Felek. 2) mec. bir kalabalığa önderlik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5377, 'feneri nerede söndürdün', ' şaka geç kalanlara takılmak için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5378, 'fenersiz yakalanmak', 'beklenmedik bir zamanda istenmeyen bir durumla karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5379, 'fennini almak (kapmak)', 'bir işin inceliklerini, püf noktalarını kavrayıp o alanda usta olduğunu göstermeye başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5380, 'fent çevirmek', 'düzen, hile yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5381, 'feragat etmek (göstermek)', 'hakkından vazgeçmek, el çekmek: ''Beni çıkardığı tahtımdan arzumla feragat edeceğim.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5382, 'ferah tutmak', 'iç rahatlığını, huzurunu korumak: ''Kendinizi ferah tutunuz. Canınızı hiçbir şeye sıkmayınız.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5383, 'ferahlık duymak', 'içinin açıklığını, rahatlığını hissetmek: ''Şimdi karşımda alevden bir duvar görüyor, içimde bir ferahlık duyar gibi oluyorum.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5384, 'ferahlık vermek', 'iç açmak, rahatlık hissettirmek: ''Yeni boyanıp temizlenmiş bir ev gibi havası ferahlık veriyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5385, 'ferih fahur yaşamak', 'bağımsız, bağlantısız bir biçimde yaşamak: ''Nesir kendini nazımdan ayırarak gazetelerde, kitaplarda, kürsülerde, mikrofonlarda ferih fahur yaşıyor.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5386, 'feriştahı gelse', ' argo 1) ''en güçlüsü, en yetkilisi, en üstünü olsa'' anlamında kullanılan bir söz; 2) ''en iyisi olsa'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5387, 'ferman çıkarmak', '1) padişah tarafından herhangi bir konuda emir verilmek; 2) yetkili bir kimse tarafından buyruk verilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5388, 'ferman dinlememek', 'yasa, kural, yol yöntem tanımamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5389, 'ferman sizin', '''siz nasıl isterseniz öyle olsun'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5390, 'fertik çekmek (fertiği kırmak)', 'kaçmak: ''Kampana vurup tren kalkacağı esnada ''fertik!'' diye bağırırlardı ki ''fertiği kırmak'' tabiri buradan kalmadır.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5391, 'feryadı basmak', 'çığlık koparmak, yüksek sesle haykırmaya başlamak: ''Bu defa da, Sultanahmet''ten gelen efeler değilmiş de feryadı basanlar, onların gündüzki taşkınlığından yüz bulan eroincilermiş.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5392, 'feryat etmek', '1) yüksek sesle haykırmak: ''İnsan tehlike karşısında ancak ana diliyle feryat edebiliyor.'' -N. Hikmet. 2) mec. büyük bir yokluk, zarar ve sıkıntı içinde bulunmak: İstanbul, susuzluktan feryat ediyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5393, 'feryat koparmak', 'yüksek sesle bağırmak, haykırmak: ''Pencereden kopardığım feryadı pek geç işittiler.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5394, 'ferz çıkarmak', 'acemi bir oyuncuya karşı vezirsiz oynamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5395, 'ferz çıkmak', 'satrançta piyon, karşıdaki en son kareye kadar sürülüp vezir olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5396, 'fesat çıkarmak (fesada vermek)', 'ara bozmak, ortalığı karıştırmaya çalışmak, insanları birbirine düşürecek işler yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5397, 'fesat karıştırmak', 'hile yapmak: ''... resmî ihale ve alım satımlara fesat karıştırma ... suçlarından biriyle hüküm giymiş olanlar.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5398, 'fesini havaya atmak', 'sevinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5399, 'fetva çıkarmak', ' esk. belli bir konuda dinî hukuk kurallarına göre izin almak: ''Şimdi müftüye gideceğim, fetva çıkarıp senden boşanacağım.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5400, 'fetva vermek', '1) herhangi bir işlemin veya eylemin din kurallarına uygun olup olmadığı konusunda konuyla ilgili bilim adamlarınca açıklama yapılmak; 2) bir işin yapılabilmesi için yargıda bulunmak; 3) mec. gereksiz yere emir verir gibi konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5401, 'fetvayişerife çıkarmak', '1) şeyhülislam fetvası ilan etmek; 2) mec. kendi kendine yorum getirmek: ''Fetvayişerife mi çıkarıyorsun be?'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5402, 'feveran etmek', 'birdenbire öfkelenmek, köpürmek, parlamak: ''Beni dinlemeden öyle feveran etme ... hiddetlenme!'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5403, 'fevt etmek', 'yitirmek, elden kaçırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5404, 'fevt olmak', '1) yitmek; 2) ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5405, 'fıçı gibi', 'kısa boylu ve çok şişman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5406, 'fıkır fıkır kaynamak', '1) bir şeyden bir yerde çok bulunmak: Peynir tenekesinde fıkır fıkır kurt kaynıyor. 2) yerinde duramamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5407, 'fındık kabuğunu doldurmaz', 'önemsiz, değersiz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5408, 'fındık kırmak', 'çapkınlık yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5409, 'fındık kurdu gibi', 'ufak tefek, tombulca, sevimli.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5410, 'fır dönmek', 'bir kimseye yaranmak veya yardım etmek için üstün çaba harcamak: Kızı, annesinin çevresinde fır dönüyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5411, 'fırça çekmek (atmak)', 'paylamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5412, 'fırça gibi', 'dik, sık ve sert (saç, sakal): ''Fırça gibi sert, gür saçları kırlaşıyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5413, 'fırça yemek', 'paylanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5414, 'fırıldak çevirmek (döndürmek)', 'isteğini elde etmek için hileli yollara başvurmak: ''Anasının gözü kardeşi, işi gücü fırıldak çevirmek.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5415, 'fırıldak gibi', 'düşüncesini sürekli değiştiren, sözünden dönen (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5416, '... fırın ekmek yemesi lazım', '''bir duruma erişmek için pek çok emek vermesi, çalışması gerekir'' anlamında kullanılan bir söz: Onun usta olması için daha beş fırın ekmek yemesi lazım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5417, 'fırın gibi', 'çok sıcak (yer).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5418, 'fırsat beklemek (aramak)', 'en uygun şartı, durumu veya zamanı kollamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5419, 'fırsat bilmek', 'bir şeyden belli bir amaçla hemen yararlanmak: ''Bazı kişiler üstüme varmak için fırsat kolluyorlar; yalnız eski kamyonlarla katırlardan söz açarsam olabilir ki fırsat bilirler.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5420, 'fırsat bu fırsat', '''yararlanılacak en uygun zaman'' anlamında kullanılan bir söz: ''Fırsat bu fırsat deyip gelip görüyorlar, yiyip içiyorlar.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5421, 'fırsat bulmak', 'uygun, elverişli zaman bulmak: ''Ben ve ablanız, fırsat buldukça size serbest ders vermeye geleceğiz.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5422, 'fırsat düşmek (çıkmak)', 'bir imkâna kavuşmak: ''Evet mademki fırsat düşmüştü. Cesaretini göstermek lazımdı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5423, 'fırsat her vakit ele geçmez', 'fırsat insanın eline çok seyrek geçtiği için çıkan fırsat iyi değerlendirilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5424, 'fırsat her vakit ele geçmez', '''fırsat insanın eline çok seyrek geçtiği için çıkan fırsat iyi değerlendirilmelidir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5425, 'fırsat kollamak (gözlemek)', 'yapmak istediği iş için uygun bir zaman veya bir durum beklemek: ''Sonra fırsat kollamasını biliyordu ve tekme yapıştıracak, çelme takacak zamanı içgüdülerin şaşmazlığıyla seçiyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5426, 'fırsat sakal altından geçer', 'fırsatı yakalayabilmek için uygun zamanı kollamak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5427, 'fırsat sakal altından geçer', '''fırsatı yakalayabilmek için uygun zamanı kollamak gerekir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5428, 'fırsat vermek', 'bir işi yapmak için uygun, elverişli şartı sağlamak: ''Bu çeşit yazılara cevap vermek hasma fırsat vermek olur.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5429, 'fırsatı ganimet bilmek', 'çıkan fırsattan en iyi biçimde yararlanmak: ''Fırsatı ganimet bilen İbrahim Ağa, soluğu doğru Eminönü''nde aldı.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5430, 'fırsatı kaçırmak', 'elverişli durumdan yararlanmamak: ''Fırsatı kaçırmadım, hakkında malumat topladım.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5431, 'fırsatını düşürmek', 'kolayını bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5432, 'fırsattan istifade etmek', 'ele geçirilen imkân veya durumdan en iyi biçimde yararlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5433, 'fırtına atlatmak', 'güç durumdan kurtulmak: ''Ne sen gideceksin ne de ben. Böyle kaç fırtına atlattık biz.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5434, 'fırtına çıkmak', 'sert rüzgâr esmeye başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5435, 'fırtına gibi', '1) hızla, birdenbire: Fırtına gibi geldi gitti. 2) aceleci: Fırtına gibi adam.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5436, 'fırtına kopmak (patlamak)', '1) şiddetli fırtına çıkmak: ''Fırtına kopmadan epey önce köpek balıkları açık denizlere kaçarlar.'' -Halikarnas Balıkçısı. 2) mec. bir yerde kavga ve gürültü çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5437, 'fıstık gibi', ' tkz. 1) dolgun, besili ve canlı; 2) mec. çok güzel; 3) argo alımlı, çekici (kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5438, 'fıstıki makamla', 'ağır ağır, yavaş yavaş: ''Akşam serinliğinde fıstıki makamla Sarıyer''in yolunu tuttum.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5439, 'fıtık etmek', 'sıkıntı vermek, üzmek: ''Aklından, fıtık etti ha, cevap olumsuz herhâlde ama insan bir söyleyiverir, diye geçmekteydi.'' -Ü. Dökmen.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5440, 'fıtık olmak', 'büyük sıkıntı duymak, kahrolmak, çaresiz kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5441, 'fidan gibi', 'ince ve uzun (boy): ''Fidan gibi bir boy, yağız bir çehre, üst dudağında hafif bir gölge.'' -C. S. Tarancı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5442, 'figan etmek', 'bağırarak ağlamak, inlemek: ''Emrah eder düştüm dile / Bülbül figan eder güle'' -Erzurumlu Emrah.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5443, 'fiile koymak', 'eyleme geçirmek: ''Dava insanın, ben daha çok işe yararım kanaatine varması ve bunu fiile koyabilmesidir.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5444, 'fikir edinmek', 'kanaat sahibi olmak: ''Ama ben, bir kitap üzerine bir fikir edinmek istedim mi o kitabı kendim okurum.'' -N. Ataç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5445, 'fikir vermek', '1) düşüncesini bildirmek: ''Evet iyi bir şey değil fakat benim için bir fikir verir diye seçtim.'' -R. N. Güntekin. 2) bir konuda yol gösterici bilgi edinmek: ''İşitilen sözler, görülen tavırlar, beğenilen düşünceler Şinasi Bey''e yeni fikirler vermeye başladı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5446, 'fikir yormak', 'bir konuda çok düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5447, 'fikir yürütmek', 'bir konu üzerine düşüncesini söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5448, 'fil dişi kuleden bakmak', 'herkesi küçümseyip kendini farklı görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5449, 'fil dişi kuleye çekilmek', 'herkesi küçümseyip kendisine özgü dünyasına çekilmek: ''Çöküşün ve çöküşten kaçışın, fil dişi kuleye çekilişin yarattığı isyanlar kitaplaşmamıştır.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5450, 'fil gibi', '1) çok yemek yiyen (kimse); 2) çok şişman (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5451, 'fildişi gibi', 'donuk, beyaz (ten).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5452, 'filinta gibi', 'genç, ince uzun boylu, çevik, yakışıklı (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5453, 'filiz gibi', 'ince ve güzel vücutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5454, 'filiz vermek', '1) sürgün çıkmaya başlamak: ''O sene ise buğday ekmişler, tam filiz verecekken Sakarya taşmış, yirmi gün çekilmemişti.'' -S. F. Abasıyanık. 2) mec. ortaya çıkmak: ''Ama bu arada hiç akıllarda olmayan bir sıkıntı filiz vermişti.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5455, 'film çekmek', '1) sin. ve TV bir sinema kamerasıyla görüntüleri tespit etmek veya bir hareket ve görünüşün sıralı resmini çekmek; 2) vücudun röntgenini almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5456, 'film çevirmek', '1) sin. ve TV beyaz perdede oynatılacak bir eseri filme almak veya bu eserin çekilişi sırasında rol yapmak: ''Sanki buraya film çevirmeye gelmişti.'' -S. F. Abasıyanık. 2) argo eğlenmek, hoş vakit geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5457, 'film oynamak', 'bir film, sinemada gösterilmekte olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5458, 'film oynatmak', 'bir filmi sinemada göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5459, 'finale kalmak', 'şampiyonu belirleyecek son yarışmaya katılma hakkını kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5460, 'fincan gibi', 'iri ve patlak (göz).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5461, 'fincancı katırlarını ürkütmek', 'zararı dokunabilecek bir kimsenin hoşuna gitmeyen bir davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5462, 'firar etmek', 'kaçmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5463, 'fire vermek', 'eksilmek, azalmak: ''Halk edebiyatı gibi, divan edebiyatı da çok fire verdi elbet.'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5464, 'fiske fiske kabarmak (olmak)', 'kabarcıklar oluşmak: Yumurta yiyince çocuğun derisi fiske fiske kabardı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5465, 'fiske kondurmamak (dokundurmamak)', 'bir kimse veya nesneyi en küçük bir tehlikeden bile korumak, titizlikle savunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5466, 'fiskos etmek', 'başkalarının bulunduğu yerde birkaç kişi gizlice, alçak sesle konuşmak: ''Düşüncelerimizi açık seçik ortaya koymak yerine fiskos etmeyi yeğleriz.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5467, 'fiş açmak', 'bir işle ilgili konuda gereken bilgileri fiş üzerine yazmaya başlamak, fişlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5468, 'fişek atmak', '1) ortalığı karıştıracak bir söz söylemek; 2) kaba cinsel birleşmede bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5469, 'fişek gibi', 'hızla.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5470, 'fişek salıvermek', 'ara bozacak söz söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5471, 'fişini çekmek', '1) birine zarar vermek; 2) birini öldürmek; 3) yaşama dönme umudu olmayan hastayı, nefes alması, kalbinin atması gibi faaliyetlerini yerine getiren aletlerden ayırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5472, 'fişini tutmak', 'bir kimsenin davranışlarını fiş üzerinde belirlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5473, 'fit olmak', ' argo ödeşmek, razı olmak: ''Kilosunun fiyatına bir fakir ailenin bir hafta fit olduğu çilekler ne çirkin şeylerdir.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5474, 'fitil gibi', 'çok sarhoş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5475, 'fitil olmak', '1) çok kızmak; 2) argo sarhoş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5476, 'fitne fesat çıkarmak', '1) ara bozucu söz söylemek; 2) ara bozucu davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5477, 'fitne sokmak', 'ara bozmak, insanları birbirine katmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5478, 'fiyaka satmak (sökmek)', ' argo gösteriş yapmak, caka yapmak, çalım satmak: ''Okula gidiyor diye fiyaka söküyor bize.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5479, 'fiyasko vermek', 'bir girişim başarısızlıkla sonuçlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5480, 'fiyat ayarlamak', 'para değerindeki değişiklik ve başka ekonomik şartlar dolayısıyla fiyatları düzenlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5481, 'fiyat biçmek', 'bir değer için ödenecek para karşılığını belirlemek: Bu yazmaya ne fiyat biçersiniz?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5482, 'fiyat kırmak', 'fiyatı düşürmek, fiyatı indirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5483, 'fiyat vermek', 'isteyeceği veya ödeyeceği fiyatı bildirmek: ''Ne fena fena bakar, ne de olmayacak bir fiyat verdiğim zaman homurdanır.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5484, 'fiyatları dondurmak', 'fiyatların yükselmesini önlemek, fiyatların olduğu gibi kalmasını sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5485, 'flört etmek', 'karşı cinsten biriyle duygusal ilişki kurmak, çıkmak: ''Bir delikanlıyla flört edince hemen adınız dolaşmaya başlardı ortalıkta.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5486, 'fol yok yumurta yok', 'bir konu ile ilgili ortada hiçbir belirti olmadığı hâlde varmış gibi bir kuşkuya düşüldüğünde kullanılan bir söz: ''Kız kardeşi ile Mahir daha ortada fol yok yumurta yokken gelin güveyi olmuşlar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5487, 'fonda etmek', 'demir atmak: ''Bir iki geminin fonda ediş gürültüsünü duyan Çakır Ayşe, kıyıya seğirtti.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5488, 'fondip yapmak', 'bir solukta, bir dikişte içmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5489, 'fora etmek', '1) açmak, çözmek: ''Diğeri ise yelkeni fora etti.'' -A. Midhat. 2) argo çıkarmak: ''Arkadaşlar da derinliğine bir samimiyetle ceketlerini fora etmişler.'' -Ç. Altan. 3) argo bıçak, tabanca vb.ni çekip çıkarmak; 4) açmak, çıplak duruma getirmek: ''Gözlerine sürme çeken, kolunu ve omzunu fora eden kız yani ben, kendime yabancı geliyordum.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5490, 'format atmak (çekmek)', 'biçimlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5491, 'formda olmak', 'gerekli güç ve yeteneklere sahip olmak: Güreşçilerimiz formda olmak zorundadır.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5492, 'formdan düşmek', 'güç ve yeteneği yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5493, 'formunu korumak', '1) gerekli güç ve yeteneği bozmadan sürdürmek; 2) diri ve canlı görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5494, 'formül bulmak', 'bir işi çözümleyecek çıkar yol bulmak, çözüm bulmak: ''O, bu nazik duruma karşı bir formül bulmuştu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5495, 'forsu olmak', 'bir konuda saygınlığı, gücü, söz geçirirliği bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5496, 'forsunu yitirmek (kaybetmek)', 'etkinliğini ve saygınlığını kaybetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5497, 'fos çıkmak', 'bir işin sonu gelmemek, boş çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5498, 'fotoğraf çekmek', 'fotoğraf makinesiyle görüntü tespit etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5499, 'fotoğrafını almak', 'fotoğraf makinesiyle görüntüsünü tespit etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5500, 'foyası meydana (ortaya) çıkmak', 'bir olay dolayısıyla bir kimsenin kötü niteliği ortaya çıkmak: ''Utanmazlık siyasetinin veya utanmaz siyasinin önünde sonunda foyası meydana çıkar.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5501, 'foyasını belli etmek', 'göz boyacılığı, suçu, kötü niteliği veya gizli niyeti ortaya çıkmak: ''İnsana güzel gibi gelen, foyasını ancak gözle görülür şeklin içinde belli eden bir âlemdedirler.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5502, 'fön çekmek', 'aletle saçı kurutup biçim vererek taramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5503, 'Fransız kalmak', '1) anlatılan bir konuyu anlayamamak; 2) herhangi bir konudan uzak kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5504, 'fren yapmak', 'freni kullanarak taşıtın hızını kesmek veya taşıtı durdurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5505, 'freni patlamak (tutmamak)', '1) fren, görevini yapmamak; 2) mec. bir iş denetimden çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5506, 'frikik vermek', ' argo göğüs, bacak gibi vücudun belirli bölümlerini, bilerek veya bilmeyerek gereğinden fazla açarak göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5507, 'frikik yakalamak', ' argo bilerek veya bilmeyerek gereğinden fazla açılmış olan göğüs, bacak gibi vücudun belirli bölümlerini görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5508, 'fukara tavuğu bir yumurtlar', 'talih, fakire hiç gülmez; aynı sermaye zengine daha çok, fakire daha az gelir getirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5509, 'fukaranın düşkünü, beyaz giyer kış günü', 'daha önce iyi bir durumda olan kişi bu konumunu kaybettiğinde uygun olmayan, yersiz davranışlarda bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5510, 'fukaranın tavuğu tek tek yumurtlar', 'talih, fakire hiç gülmez; aynı sermaye zengine daha çok, fakire daha az gelir getirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5511, 'fukaranın tavuğu, zenginin atı kıymetli olur', 'yoksul geçimini, varlıklı keyfini düşünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5512, 'fücceten gitmek', 'ansızın ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5513, 'fülsüahmere muhtaç olmak', 'çok fakir, düşkün, zavallı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5514, 'fütur etmemek', 'umursamamak, önemsememek: ''El âlem huzurunda fütur etmeden akıllarına estiği zaman gelir, iki tek atarlar.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5515, 'fütur getirmek', 'bezginlik getirmek, bezmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5516, 'gadre uğramak', 'haksız davranışlarla karşı karşıya gelmek: ''Önce kendini gadre uğramış sanan Nahit rolünü öğrenince utandı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5517, 'gaf yapmak', 'bilmeden yersiz bir davranışta bulunmak veya başkasını incitecek söz söylemek, pot kırmak, çam devirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5518, 'gafil avlanmak', 'beklenmedik bir sırada yakalanmak, habersiz ve hazırlıksız bir anda bir olayla karşılaşmak, zor duruma düşürülmek: ''Atatürk bizden ayrılınca öbür sınıflara da girmiş. Fakat onlar bizim gibi önceden hazırlanmadıklarından gafil avlanmışlar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5519, 'gafile kelam, nafile kelam', 'gaflet uykusunda olan kişiye söz kâr etmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5520, 'gaflet basmak', '1) dalgın, dikkatsiz bir durumda bulunmak; 2) uykusu gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5521, 'gaflet uykusuna dalmak (yatmak)', '1) dalgınlıktan ileri gelen uyuşukluk içinde olmak; 2) idraksizlik, bilgisizlik, aymazlık içinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5522, 'gaflet uykusundan uyandırmak', 'bilgisizlikten, idraksizlikten kurtarmak: ''Sanki Orhan Veli''nin okuyucuyu gaflet uykusundan uyandırmak için yazdığı mısra rakı şişesinin içindeymiş gibi.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5523, 'gaflete düşmek', 'gaflet içinde kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5524, 'gagasından yakalamak', 'bir kimseyi karşı koyamayacak duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5525, 'gailesiz baş, yerin altında', 'herkesin bir sıkıntısı vardır, bu sıkıntılar ancak ölümle biter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5526, 'gaipten haber vermek', 'kendisinde manevi güç olduğuna inanılan kimse, gelecekte neler olacağından veya bilinmeyen âlemden haber vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5527, 'galebe çalmak', '1) yenmek: ''Kocanın münasebeti her türlü cazibesini kaybettiği gün rakibine galebe çaldığına emin olabilirsin.'' -H. C. Yalçın. 2) üstün gelmek, baskın çıkmak: ''Kadıncağızın gönlü gence kayıyordu. Fakat neticede akıl ve mantık tarafı galebe çaldı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5528, 'galeyana gelmek', 'coşmak, hiddetlenmek: ''Bir an çalgılar sustu, herkes şaşırmıştı, kimse padişahın birdenbire galeyana gelmesinin sebebini bilmiyordu.'' -F. F. Tülbentçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5529, 'galeyana getirmek', 'coşturmak: ''Nağmeler ve hanende sesleri, uslu ve evcimen halkı heyecana ve galeyana getiriyordu.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5530, 'galip gelmek', 'yenmek, üstün gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5531, 'galop yapmak', 'at yarışında veya hazırlık çalışmasında iyi bir derece elde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5532, 'gam çekmek', 'tasalanmak, kaygılanmak, üzülmek: ''Gam çekme güzel, nasılsa baharın sonu yazdır.'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5533, 'gam yapmak', 'gam biçiminde deneme ve alıştırmayı çalgı veya sesle uygulamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5534, 'gam yememek', 'tasa etmemek, kaygılanmamak, üzülmemek: ''Şu anda bile ölsem gam yemem.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5535, 'gammaz olmasa tilki pazarda gezer', 'yasal olmayan yollardan gizlice çıkar sağlayan kişi, yakayı ele vereceğinden korkmasa bütün bu işleri açıktan yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5536, 'garanti altına almak', 'güvence altına almak: ''Ondan sonra da, zavallı kuzunun artakalan birkaç kemiğini garanti altına aldılar.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5537, 'garanti vermek', 'güvence altına almak: ''Bu konuda size bütün ciddiyetimle garanti verebilirim.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5538, 'garaz bağlamak', 'birine karşı kin beslemek: ''Bana garaz bağladığını seziyorum.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5539, 'gardını almak', '1) savunma durumuna geçmek; 2) mec. önceden önlemini almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5540, 'gargaraya getirmek', '1) gürültüye, karışıklığa boğarak bir sözün veya bir işin etkisini azaltmak, dağıtmak, dikkatten kaçırmak; 2) kandırmak, aldatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5541, 'garibe bir selam bin altın değer', 'yabancı yerde tek başına kalan kimseye karşı gösterilecek küçük bir ilgi, en büyük iyilik yerine geçer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5542, 'garibine gitmek', 'yadırgamak, şaşırmak: ''Frankfurt caddelerinde en çok garibime giden insan, dilencisi olmuştur.'' -A. Haşim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5543, 'garip bulmak', 'yadırgamak, tuhaf ve anlaşılmaz olarak nitelemek: ''Sizin gibi modern bir sosyete adamının böyle düşünmesini garip buluyorum.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5544, 'garip itin kuyruğu bacağı arasında (götünde, kıçına kısık) gerek (olur)', 'sığıntı durumunda olan kişi, yabancı bir yerde hiçbir şeye karışmamalı, sessiz, kendi hâlinde yaşamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5545, 'garip kuşun yuvasını Allah yapar', 'garip ve kimsesiz kişiye Tanrı yardım eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5546, 'gark olmak', '1) gömülmek, batmak: ''Toprağa gark olmuş nazik tenleri / Söylemekten kalmış tatlı dilleri'' -Yunus Emre. 2) mec. boğulmak: Paraya gark oldu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5547, 'gâvur inadı tutmak', 'iyiden iyiye inatlaşmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5548, 'gâvur olmak', '1) Müslüman olmamak; 2) dinsiz olmak; 3) mec. boşuna harcanmak: Aldığım, bu şeylerle, beş bin lira gâvur oldu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5549, 'gâvur ölüsü gibi', 'çok ağır ve hantal: ''Gâvur ölüsü gibi yemek masası, ona benzer büfe, kasvetli.'' -E. Işınsu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5550, 'gâvura kızıp oruç yemek (bozmak)', 'başkasına kızıp kendine zararlı olan bir iş yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5551, 'gâvurun ekmeğini yiyen gâvurun kılıcını çalar', 'kişi, inançları ayrı da olsa, hoşlanmasa da geçimini sağlayan kimseye hizmet eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5552, 'gaybubet etmek', 'göz önünde bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5553, 'gayret dayıya düştü', '''iş, onu başarabilecek olana kaldı'' anlamında kullanılan bir söz: Gayret dayıya düştü, bu işe sen el atmazsan olmayacak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5554, 'gayret göstermek', 'çaba harcamak, başarmak için çalışmak: ''Azar azar fakat ısrarlı bir gayret göstermeye başladı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5555, 'gayret vermek', 'isteklendirmek, özendirmek, yüreklendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5556, 'gayrete gelmek', 'bir işi yapmaya veya bitirmeye özenmek; canlanmak: ''Ekmeğini zeytinyağına banıp öyle lezzetli bir yiyişi vardı ki ben de gayrete gelmiştim onunla.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5557, 'gayretine dokunmak', 'bir işi yapamayacağını ileri sürenlere kızarak veya kendisinin yapması beklenen işi başkasının yapmasından utanç duyarak başarmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5558, 'gaz vermek', '1) motorlu taşıtlarda gaz pedalına basmak; 2) mec. dolduruşa getirmek; 3) mec. coşturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5559, 'gaza basmak (dayanmak)', '1) harekete geçirmek veya hızını artırmak için motorlu taşıtın gaz pedalına basmak; 2) mec. bir işi hızlandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5560, 'gaza gelmek', 'dolduruşa gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5561, 'gaza yüklenmek', 'harekete geçirmek veya hızını artırmak için motorlu taşıtın gaz pedalına çokça basmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5562, 'gazaba gelmek', 'öfkelenmek, kızmak: ''Sert kelimeler kullandı, köpürdü, gazaba geldi.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5563, 'gazaba uğramak', 'güçlü bir kimsenin hışmına uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5564, 'gazabını yenmek', 'öfkesini, şiddetini göstermemek veya bastırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5565, 'gazel okumak', '1) gazel söylemek: ''Karagözcünün makamlar arası dolaşması, şarkı ve gazel okuması lazımdı.'' -S. Ayverdi. 2) mec. oyalamak veya kandırmak üzere boş sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5566, 'gazel tutturmak', 'yüksek sesle şarkı veya türkü söylemek: ''Sonra makinelerin gemiyi sarsan temposuna uyarak yanık bir gazel tuttururdu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5567, 'gebe kalmak', '1) insan veya hayvanın karnında yavru oluşmak: ''Kırk dört yaşında gebe kalan bir kadın böyle bir sabırsızlığa kapılabilir.'' -Y. Atılgan. 2) mec. minnet altında kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5568, 'geberip gitmek', 'istenmedik bir biçimde ve beklenmedik bir zamanda ölmek: ''En ufak sarsıntıda damlarınız kafanıza yıkılıyor, çoluk çocuk geberip gidiyorsunuz.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5569, 'gece gözü, kör gözü', 'geceleyin iyi iş yapılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5570, 'gece gündüz dememek', '1) vaktin uygun olup olmadığına bakmamak, vakit seçmemek; 2) bir işi sürekli olarak, ara vermeksizin yapmak: ''Gece gündüz demez ha bire okurlardı. Sonra başlarlardı yazmaya.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5571, 'gece işi, körler işi', 'gece yapılan iş verimli olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5572, 'gece silahlı, gündüz külahlı', ''' gerçekte iyi olmadığı hâlde iyi gibi görünen kimseler için kullanılan bir söz'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5573, 'gecekondu gibi', 'derme çatma yapılan (yapı).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5574, 'geceler gebedir', 'her sabah yeni olaylarla karşılaşırız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5575, 'geceyi (gecesini) gündüze (gündüzüne) katmak', 'aralıksız, gece gündüz çalışmak, büyük çaba göstermek: ''Başaramayacağı kadar çok işlerin altına girmekten çekinmedi, geceyi gündüze katıp çalışmaya başladı.'' -M. Ş. Esendal. ''Köycülük kollarında gecemi gündüzüme kattım.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5576, 'geç! (geç efendim!)', '''kulak asma, önem verme!'' anlamında kullanılan bir söz: ''Biz ev yaptırdık ama sen bize bakma; bizim paramız vardı. Geç efendim geç; bu işler sizin gibilerin harcı değil.'' -N. Kurşunlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5577, 'geç kalmak', 'vaktinden sonra davranmak, gecikmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5578, 'geç olsun da güç olmasın', 'çeşitli engeller yüzünden gerçekleşemeyen işlerde avunmak için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5579, 'geçimini doğrultmak', 'geçinmek için yeteri kadar para kazanmak: ''Biri elbise askısı yapıyor, diğeri de yapılanları satıyor, böylece geçimlerini doğrultuyorlardı.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5580, 'geçinip gitmek', 'çok iyi değilse de şöyle böyle geçinmek: ''Sözün tam anlamıyla bu sayede geçinip gidiyordu.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5581, 'geçiniz', '1) ''bu söylediklerinizi kabul etmiyorum, daha mantıklı sözler söyleyin'' anlamında kullanılan bir söz; 2) bilgi yarışmalarında kendisinden sonraki yarışmacıya geçilmesini istemek için veya bir sonraki soruya geçmek için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5582, 'geçinmeye gönlü olmamak', 'herhangi bir konuda isteksizliğini belli etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5583, 'geçit vermek', 'çay, ırmak, dağ vb.nin geçilecek bir yeri olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5584, 'geçme namert köprüsünden, ko aparsın su seni', 'namerde karşı minnet altında kalmaktansa sıkıntıya katlan.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5585, 'geçmiş ola', '''o fırsat bir daha ele geçmez'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5586, 'geçmiş olsun', 'hastalananlara, kaza geçirenlere, beklenmedik büyük bir olumsuz durumdan kurtulanlara veya hapishaneye girenlere söylenen iyi dilek sözü: ''Geçmiş olsun ağabey, ne oldu sana böyle?'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5587, 'geçmişe mazi, yenmişe kuzu derler', 'geçmişte kalan olayların üzerinde durulmasında bir yarar yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5588, 'geçmişini kurcalamak', 'geçmişini araştırarak kötü amaçlı kullanmak için birisiyle ilgili bilgi edinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5589, 'geçti Bor''un pazarı (sür eşeğini Niğde''ye)', 'artık iş işten geçti.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5590, 'gedik açılmak', 'giderilmesi çok güç bir eksiklik veya açık ortaya çıkmak: ''El yordamıyla ilerlemeyi sürdürürken, sanki karanlıkta bir gedik açılıyor, bir yerlerden içeriye ışık vuruyor.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5591, 'gedik açmak', ' ask. düşman mevzilerindeki zayıf bir noktadan giriş yeri açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5592, 'gedik kapamak', 'küçük bir gereksinimini karşılamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5593, 'gedik kapmak', 'bir gelir kaynağı ele geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5594, 'gedikleri tıkamak', 'çıkan veya çıkacak olan zorlukları önlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5595, 'gehgeh tutmak', 'nöbetli bir hastalığa yakalanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5596, 'gel demesi kolay ama git demesi güçtür', 'bir kimseyi işe almak, bir misafir çağırmak kolaydır ancak bir kimsenin işine son vermek, misafire git demek zordur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5597, 'gel denilen yere gitmeye ar eyleme, gelme denilen yere gidip yerini dar eyleme', 'çağrıldığın yere gitmekten çekinme, gelme denilen yere de gitme, orada sana ilgi göstermezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5598, 'gel keyfim gel', 'büyük bir memnunluk ve alay anlatan bir söz: ''Oh, artık sabahın bu vaktinde güneş henüz doğarken bu serin harman yerinde, gel keyfim gel.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5599, 'gel zaman git zaman', '''aradan oldukça uzun bir zaman geçtikten sonra'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5600, 'gelberi etmek', ' argo aşırmak, çalmak, kendine mal etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5601, 'geldik yüze, çıktık düze', 'kasım ayından sonra gelen yüzüncü günde, 15 şubatta kışın soğuk günleri geride kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5602, 'geleceği varsa göreceği de var', 'kötülük yapmaya kalkışacak olursa karşılığını elbette görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5603, 'gelen ağam giden paşam', 'yönetim kimde olursa olsun benim için fark etmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5604, 'gelen gidene rahmet okutur', 'beğenmediğimiz bir kişinin yerine öyle birisi gelir ki eskisini aratır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5605, 'gelen gideni aratır', 'beğenmediğimiz bir kişinin yerine öyle birisi gelir ki eskisini aratır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5606, 'gelene git denilmez', 'kendiliğinden gelen bir konuk geri çevrilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5607, 'gelin almak', '1) erkeğe bir eş bulmak; 2) gelini babasının evinden özel bir törenle alıp damadın evine götürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5608, 'gelin altın taht getirmiş, çıkmış kendisi oturmuş', 'toplum içine giren bir kimsenin kendi kullanacağı eşyasının değerli olup olmaması başkalarını ilgilendirmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5609, 'gelin eşikte, oğlan beşikte', 'bir eve gelin gelir gelmez bebek hazırlıklarına başlamak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5610, 'gelin gibi süzülmek', 'geline yakışır biçimde edalı, nazlı yürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5611, 'gelin girmedik ev olur, ölüm girmedik ev olmaz', 'her eve gelin girmeyebilir ama ölüm kesinlikle girer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5612, 'gelin gitmek', 'bir aileye, bir yere gelin olarak gitmek: ''Bin türlü dedikodu içinde ben oraya gelin gittim.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5613, 'gelin yazmak', 'gelinin yüzünü değişik süs gereçleriyle bezemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5614, 'gelini ata bindirmişler ''ya nasip'' demiş', 'kesin sonuç alınmadan hiçbir işe olup bitti gözüyle bakılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5615, 'gelinliği tutmak', 'gelinlik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5616, 'gelinlik etmek', '1) gelin, kendisinden beklenilen hizmeti yapmak; 2) aile büyüklerinin yanında susmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5617, 'gelip çatmak (dayanmak)', 'vakti gelmek, kaçınılmaz olmak: ''Ayrılık günleri geldi dayandı.'' -Âşık Veysel. ''Konser günü gelip çattığındaysa stadyumda mahşeri bir kalabalık vardı.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5618, 'gelip geçici olmak', 'kısa süreli, önemsiz olmak: ''Bu ilişkinin nasıl olsa gelip geçici olduğunu biliyormuş gibi aldırışsızdır.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5619, 'gelip geçmek', '1) bir yerden geçmek; 2) bir makam, bir yer vb.nde kısa bir süre bulunmak; 3) kısa bir süre etkin olmak: ''Kızcağız bilir ki bu sözler kızgınlık sözleridir, gelir geçer.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5620, 'gelsin ... (gelsin ... gitsin ...)', '1) yaşantı veya durumun rahatlığını anlatan bir söz: ''Ondan sonra o masanın üstüne yığılan mezeler, gelsin gır gır, alay, muziplik.'' -H. Taner. 2) sorumsuzca davranıp işine gereken önemi vermemeyi anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5621, 'gem almak', 'at, alışıp hizmete elverişli duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5622, 'gem almamak', 'söz dinlememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5623, 'gem almayan atın ölümü yakındır', 'söz dinlemeyen hırçın kişi, davranışının büyük zararını görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5624, 'gem vurmak', '1) hayvanın ağzına gem takmak; 2) mec. her türlü taşkınlığı, isteği, hevesi vb.ni engellemek: ''Senin bütün emellerin, azgın kalbinden korktuğun, ona gem vurmak istediğin içindir.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5625, 'gemi azıya almak', '1) at, gemi azıları arasına alıp etkisiz bırakarak süvarisinin yönetiminden çıkmak ve alabildiğine koşmak; 2) mec. söz dinlemez olmak: ''Kim var kim yok geldi toplandı. Derken her kafadan bir ses çıktı, kimi kâh nalına, kâh çivisine vurdu, kimi gemi azıya alıp birbiriyle yarıştı.'' -E. C. Güney.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5626, '(gemi) baş tutamamak', 'rüzgâr, fırtına yüzünden, yapılışındaki veya yükselişindeki bir bozukluk sebebiyle gemi dümene uymamak, rotadan çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5627, 'gemi baş vurmak', ' den. önden gelen dalgalarla gemi başı kalkıp kalkıp inmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5628, 'gemi dövünmek', ' den. şiddetli dalgaların etkisiyle gemi bağlı veya demirli olduğu yerde inip kalkmak, sallanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5629, 'gemi gezmek', ' den. dış etkiler yüzünden gemi rota çizgisinden ayrılıp sancak veya iskele yönüne ilerlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5630, 'gemi karaya oturmak', 'gemi, sığ bir yere saplanıp kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5631, 'gemini kısmak', 'bir kimsenin üzerindeki baskıyı arttırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5632, 'gemisini kurtaran kaptan', 'güç bir duruma düşüldüğünde ne yapıp edip kendisini veya yakın çevresindekileri kurtaranlar için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5633, 'gemisini yürütmek', 'bir işi hiçbir engel tanımadan sürdürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5634, 'gemiyi rotasına koymak', ' den. gemiyi pusula ile gideceği yönde belli olan rota çizgisi üzerine getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5635, 'gemiyi tutmak', ' den. gemiyi belirli bir yerde bir süre bekletmek, çalışmadan durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5636, 'gemiyi yatırmak', ' den. altını temizlemek amacıyla küçük gemileri yan döndürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5637, 'gençliğin kıymeti ihtiyarlıkta bilinir', 'insan gençliğinde yaptığı şeylerin çoğunu yaşlandığında yapamaz ve gençliğin ne denli değerli olduğunu o zaman anlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5638, 'gençlikte para kazan, kocalıkta kur kazan', 'kişi gençliğinde çalışıp para biriktirmelidir ki ihtiyarlığında çalışamadığı zaman onunla rahat rahat geçinsin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5639, 'gençlikte taş taşı, kocalıkta ye aşı', 'kişi gençliğinde çalışıp para biriktirmelidir ki ihtiyarlığında çalışamadığı zaman onunla rahat rahat geçinsin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5640, 'geniş bir nefes almak', 'sıkıntılı bir durumdan kurtulmak, ferahlığa kavuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5641, 'geniş karşılamak', 'hoşgörü ile değerlendirmek: ''Bu vahim skandalı, bu mahdut dışarlık çocuğu niçin bu kadar geniş karşılıyordu?'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5642, 'genizden konuşmak (çıkarmak)', 'burnu tıkalı gibi konuşmak: ''Genzinden çıkardığı seslerle ağlama taklidi yapıyordu.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5643, 'gerçek yüzünü göstermek', 'sakladığı düşüncelerini sonradan ortaya koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5644, 'gerdan kırmak', '1) naz ile boynu başla birlikte iki yana oynatarak kırıtmak: ''Avrupa tiyatrosunda işveli gerdan kırışları, meşhur kantolarıyla, ortalığı kırıp geçirdiği zamanlar!'' -A. İlhan. 2) mec. boynu, başı geriye oynatarak büyüklük taslar bir durum almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5645, 'gerdeğe girmek', 'gelinle damat düğün gecesi bir araya gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5646, 'gereği düşünülmek', 'bir sorunu sonuçlandırmak için tutulacak yolu kararlaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5647, 'gereği gibi', 'nasıl olması gerekli ise öyle: Gereği gibi davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5648, 'gerek görmek', 'yapılmasını istemek: ''İçeri giren polisin onları sorgulamaya bile gerek görmeden kurşuna dizdiğini söylediler.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5649, 'gerekçe göstermek', 'gerektirici sebep ve doküman ileri sürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5650, 'gerekli görmek', 'yapılması icap etmek: ''Ruslar, gerekli gördükleri her yerde konsoloshane açabilecekle.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5651, 'gerekli kılmak', 'icap ettirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5652, 'gereksinme duymak', 'ihtiyacı olduğunu anlamak: ''Doğrusu ya, açık havaya, yeni yüzlere, yeni sözcüklere gereksinme duyuyorum.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5653, 'gereksiz görmek', 'lüzum görmemek: ''Ona danışmayı gereksiz görerek Sevim''e yöneldi.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5654, 'gerginlik yaratmak', 'gergin duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5655, 'geri almak', '1) verdiğini almak; 2) geriye doğru götürmek: Koltuğu biraz geri al. 3) düşmandan kurtarmak; 4) arabayı geri geri götürmek için vites kolunu geri durumuna getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5656, 'geri basmak', 'geri geri gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5657, 'geri çekilmek', 'karıştığı bir işi sürdürmekten veya sürdürenler arasında bulunmaktan vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5658, 'geri çevirmek', '1) geri vermek, geldiği yere göndermek, iade etmek; 2) kabul etmemek, reddetmek: ''Oğlunun hiçbir dileğini geri çevirmezmiş.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5659, 'geri dönmek', 'geldiği yere gitmek: ''Arada gelenlerin çoğu kapıdan bakıp oturmadan geri dönüyorlardı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5660, 'geri durmak', 'bir iş yapmaktan kaçınmak: ''Kafası kızınca aklına geleni yapmaktan geri durmuyordu.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5661, 'geri geri çekilmek', 'arka arka gitmek: ''Hamit''in eteğini öpmüş ve geri geri çekilerek odadan çıkmış.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5662, 'geri gitmek', 'kötüleşmek: İşler günden güne geri gidiyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5663, 'geri göndermek', 'geldiği yere göndermek, iade etmek: ''Eteğim gayet fena olmuş, terziye geri gönderdim.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5664, 'geri kalmak', '1) arkada kalmak; 2) gecikmek; 3) çağdaşlarının ve yaşıtlarının düzeyine gelememek veya düzeyinde olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5665, 'geri komamak', 'yapmazlık etmemek, yapmak: Elinden geleni geri koma!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5666, 'geri saymak', 'geriye doğru saymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5667, 'geriye bırakmak', 'tehir etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5668, 'geriye yürümek', ' huk. öncesini kapsamak: ''İptal kararları geriye yürümez.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5669, 'gerize taş atmak', 'edepsiz bir kimseye edepsizliğini göstermeye fırsat vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5670, 'geviş getirmek', 'yutmuş olduğu yiyeceği midesinden ağzına çıkarıp yeniden çiğnemek: ''Evin önünde birkaç davar geviş getiriyordu.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5671, 'gevrek gevrek gülmek', '1) kendine güvenip karşısındakini küçümsemek: ''Diğer dükkânların satılmayan mallarını ben sanki ne yapayım diye gevrek gevrek gülerek kendi kendine hak verirdi.'' -A. Ş. Hisar. 2) neşeli ve kendine güvenli biçimde gülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5672, 'geyik etine girmek', 'genç kız, erginlik çağına girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5673, 'geyik yapmak', 'boş, yararsız konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5674, 'geze almak', 'hedefe doğrultmak: ''Bağ hendeğine sinip tüfeği geze aldım.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5675, 'gezen ayağa taş değer (dolar)', 'gereksiz yere gezen kişi, kendisine zararı dokunacak şeylerle karşılaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5676, 'gezen kurt aç kalmaz', 'geçimini sağlamak için gezip dolaşan, şuraya buraya başvuran kişi aç kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5677, 'gezip tozmak', 'eğlenmek amacıyla çokça gezmek: ''Seher hep Bayram''ın sinirine dokunanlarla gezip tozdu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5678, 'geziye çıkmak', 'uzak yerleri dolaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5679, 'gıcık almak (kapmak, olmak)', ' argo bir davranışa veya bir kimseye sürekli sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5680, 'gıcık tutmak', 'bir süre boğaz gıcıklamasına yakalanmak: ''Gıcık tutmuş gibi manalı manalı öksürdü.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5681, 'gıcık vermek', '1) boğazı yakıp kaşındırarak öksürmeye yol açmak; 2) argo bir kimseyi sürekli sinirlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5682, 'gıcır gıcır etmek', '1) ''gıcırtı'' sesi çıkarmak: Merdiven basamakları gıcır gıcır ediyordu. 2) tertemiz duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5683, 'gık dedirtmemek', 'ses çıkarmasına fırsat vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5684, 'gık demek', 'ses çıkarmak, karşı çıkmak, yakınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5685, 'gık dememek (gıkı çıkmamak)', 'sesini çıkarmamak, karşı çıkmamak, yakınmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5686, 'gıllıgışlı olmak', 'gizli amaçlı, inandırıcılıktan uzak bulunmak: ''Yüreği temiz olan başkalarının gıllıgışlı olabileceğini kolay kolay aklına getirmez.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5687, 'gına gelmek', 'usanmak, bıkmak: Siyasetten, eleştiriden gına gelmişti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5688, 'gına getirmek', 'bıkmak, usanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5689, 'gır atmak', 'konuşmak, laf atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5690, 'gır geçmek', '1) bol bol konuşmak, çene çalmak; 2) dikkat etmemek, aklı başka yerde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5691, 'gır kaynatmak', 'birkaç kişi işlerini bırakıp yârenlik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5692, 'gırla gitmek', '1) uzun sürmek, sürüp gitmek: ''Park enikonu bir olay, tebrik ve övgüler gırla gidiyor.'' -T. Buğra. 2) bol bol ortaya dökülüp harcanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5693, 'gırtlağına kadar', 'çok fazla: ''İşrete düşkünlüğünü anlata anlata bitiremiyorlar, gırtlağına kadar borç içindeymiş.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5694, 'gırtlağından kesmek', 'herhangi bir amaç için yiyeceğinden kısıntı yapmak, boğazından kesmek, tasarruf etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5695, 'gırtlak gırtlağa gelmek', 'kıyasıya dövüşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5696, 'gibi gelmek', '... sanısı vermek, ... sanısı yaratmak: ''Murat''a, boş, kimsesiz ahşap bina, temelinden sallanıyor gibi geldi.'' -K. Tahir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5697, 'gibi olmak', 'bir duruma, bir duyguya yaklaşmak: ''Sorum üzerine biraz çekinir gibi oluyor.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5698, '... gibi yapmak', '... imişçesine davranmak: Sever gibi yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5699, 'gibisine gelmek', 'imiş gibi gelmek, sanmak: Öyle gibime geliyor ki bu işin içinden kolay çıkamayacağız.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5700, 'gibisine getirmek', 'sanısı uyandırmak, sanısı vermek: Bu teklifi doğru bulmamış gibisine getirdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5701, 'giden gelse dedem gelirdi', 'ölen bir kimse dirilemeyeceği gibi elden çıkan bir şey de bir daha ele geçmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5702, 'gidilmeyen yer senin değildir', 'gidemediğiniz yerinizin, kullanamadığınız malınızın size bir yararı olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5703, 'gidip de gelmemek var, gelip de görmemek (bulmamak) var', 'uzak bir yere giden kişi dönmeyebilir, dönebilse de ayrılırken bıraktığı yakınlarını bulmayabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5704, 'gidiş o gidiş', 'konuşmaya konu olan kimsenin bir daha dönmediğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5705, 'girecek delik aramak', 'saklanmak istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5706, 'girip çıkmak', '1) bir yere kısa süre kalmak üzere uğramak; 2) bir yere sık sık gelmek: Onun yanımızdaki eve girip çıktığını görürdük.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5707, 'girişimde bulunmak', 'davranmak, teşebbüs etmek: ''Mahmut Bey''in adamlarını kendi taraflarına çekmek için her türlü girişimde bulunmuşlardı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5708, 'gitti de geldi', 'yaşayabileceğinden umut kesilecek kadar ağır hastalık geçirip de iyi olanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5709, 'gitti gider (dahi gider)', 'söz konusu olan şeyin bir daha gelmeyeceğini, ele geçmeyeceğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5710, 'giydiği yakışırken eller bakışırken', '''gençken, güzelken'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5711, 'giydirip kuşatmak', 'temiz, yeni giysilerle donatmak: ''O da kendisinden yirmi yaş küçük arabacısını sevmiş, nikâhla varmış, bu arabacıyı giydirip kuşatmış, âlâ bir bey yapmıştı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5712, 'giyinip kuşanmak', 'özenle giyinmek: ''Giyinmiş kuşanmış, benim de giyinip kuşanıp hazır olmamı bekliyor.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5713, 'gizli din taşımak', 'göründüğünden farklı bir din veya inanç sahibi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5714, 'gizlide gebe kalan aşikârede doğurur', 'toplum içinde hiçbir davranış gizli kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5715, 'gol atmak', 'topun karşı takımın kalesine girmesini sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5716, 'gol kaçırmak', 'uygun durumda olmasına rağmen karşı takımın kalesine topu sokamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5717, 'gol yemek', 'topun kendi kalesine girmesine engel olamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5718, 'göbeği biriyle bağlı (beraber kesilmiş)', 'her zaman birlikte bulunan, birbirinden ayrılmayan kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5719, 'göbeği çatlamak', 'birçok güçlüğü yenmek için çok uğraşmak: ''Meclisten geçirinceye kadar göbeğim çatlamıştı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5720, 'göbeği çıkmak', 'şişmanlamak: ''Benim oğlanın göbeği çıkıyormuş da biraz, her sabah koşu yapıyor, dedi.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5721, 'göbeği düşmek', 'göbek deliğinin kapanmamasından fıtık oluşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5722, 'göbeği sokakta kesilmiş', 'evde durmayıp hep sokaklarda gezen, sürtük.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5723, 'göbeğini eritmek', 'zayıflamak: ''Göbeğini eritmek için her sabah sıcakta, tozda koşu yapan delikanlıyı düşünüyorum.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5724, 'göbeğini kesmek', '1) çocuğun göbeğiyle etene arasındaki damar örgüsünü kesmek; 2) mec. birini çok eskiden beri tanımak, bilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5725, 'göbek atmak', '1) karnını hareket ettirerek oynamak: ''Dillere destan olan oturak âlemlerinde göbeği atan, erkek değil, kadındır.'' -B. R. Eyuboğlu. 2) mec. çok sevinmek: ''Dolmuştan inince bir yandan saatine bakar, bir yandan da göbek atarmış, daha bir saat var, diye.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5726, 'göbek bağlamak (salıvermek)', 'şişmanlayarak karnı büyümek, göbeklenmek: ''Şimdi gördüğü kişi, ellisinin üstünde, göbek bağlamış, metal gözlük çerçeveli biriydi.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5727, 'göbek çalkamak (çalkalamak)', 'göbeğini sağa sola hareket ettirerek oynamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5728, 'göç etmek (eylemek)', '1) oturduğu yerden başka bir yere gidip yerleşmek, göçmek: ''Kalktı göç eyledi Afşar elleri.'' -Dadaloğlu. 2) mec. ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5729, 'göçüp gitmek', 'ölmek: ''En güzel halk türküleri çok sevilen bir insanın ansızın göçüp gitmesi ile kopan bir feryattır.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5730, 'göğe direk, denize kapak olmaz', 'hem gereksiz hem de gerçekleştirilmesi hayale bile sığmayan şeylerle uğraşılmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5731, 'göğe merdiven dayamış', 'çok uzun boylu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5732, 'göğsü daralmak (tıkanmak)', '1) güçlükle nefes almak; 2) mec. içi sıkılmak: ''Öteden beri yola yüzü yoktu. Hele yokuşları karşıdan gördüğü vakit göğsü tıkanırdı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5733, 'göğsü kabarmak', 'övünç duymak, kıvanmak, iftihar etmek: ''Onu sapasağlam görünce göğsüm kabardı oğul.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5734, 'göğsünü gere gere', '1) kendine güvenerek: ''Ne yazık ki geçtiğimiz yılda, göğsümüzü gere gere, işte zafer diyebileceğimiz pek az başarımız olmuştur.'' -T. Halman. 2) övünerek: ''Kim bilir, bu erkek, kadınların zaafı ile göğsünü gere gere kaç kere istihza etmiştir.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5735, 'göğsünü kabartmak', 'bir olay dolayısıyla kıvanç duygusunu ortaya koymak, övünmek: ''Duvarda, güneşe karşı / Göğsünü kabartan bir güvercin / İçimde öksüzün gözyaşı / Yıkılan yıllar için'' -H. F. Ozansoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5736, 'göğsünü yırtmak', 'coşkunluğunu ortaya koymak, coşmak, cıvıldamak: ''Sevda mevsimi gelince kuşlar bin türlü teranelerle minimini göğüslerini yırtarlar.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5737, 'göğüs bağır açık', 'özensiz bir kılıkta: ''Göğüs bağır açık, ellerinde pankartlarla yürütüyorlar bu savaşı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5738, 'göğüs geçirmek', 'üzülerek derinden soluk almak: ''Birdenbire sustu ve göğüs geçirdi, hüzün, dertlenme derecesini bulmuştu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5739, 'göğüs germek', 'bir güçlüğe karşı koymak, dayanmak: ''Hayatın lezzetleri içinde yüzen bizler, elbette geçici birçok zahmetlere katlanmaya ve birçok zorluklara göğüs germeye mecburduk.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5740, 'göğüs vermek', 'eziyete, sıkıntıya katlanmak, tahammül etmek: ''Ben, onun hatırı ve hatırası için daha ağırlarına da göğüs verirdim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5741, 'gök delinmek', 'birdenbire çok ve hızlı yağmur yağmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5742, 'göklere çıkmak', 'pek çok yükselmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5743, 'gökte ararken yerde bulmak', 'çok güçlükle ele geçirebileceğini sandığı şeyi veya kimseyi birdenbire bulmak: ''Merhaba dostum / Seni gökte ararken / Yerde buldum'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5744, 'gökten ne yağdı da yer kabul etmedi', 'büyüklerden gelen şeyleri küçükler geri çeviremezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5745, 'gökten zembille mi indi', '1) ''Tanrı''nın özel olarak gönderdiği, saygınlık görmesini istediği bir kişi mi?'' anlamında kullanılan bir söz; 2) ''uğraşmadan, didinmeden, kendiliğinden mi türedi?'' anlamında kullanılan bir söz: ''Biz Anadolu''nun ortasına gökten zembille mi indik?'' -O. S. Orhon.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5746, 'gökyüzünde düğün var deseler kadınlar merdiven kurmaya kalkar', 'kadınların düğün ve eğlence için katlanamayacakları fedakârlık yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5747, 'göl olmak', 'gereksiz olarak bir yerde su toplanmak, göllenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5748, 'göle su gelinceye kadar kurbağanın gözü patlar', 'yapılması geciken iyilikler, bekleyenleri sıkıntı içinde bırakır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5749, 'gölge düşmek', 'bir şey üzerine karaltı inmek, üzerine gölge gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5750, 'gölge etmek', '1) ışığa engel olmak; 2) mec. engel olmak; 3) mec. gereksiz yere rahatsız etmek: ''Gölge etme, başka ihsan istemem.'' -Diyojen.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5751, 'gölge gibi', 'varlığını belli etmeden, gizlice: ''Bunlar yekdiğerlerine tutunarak birer gölge gibi duvara siftine, inleye, ıkına orada duran arabalara tırmanmaya başladılar.'' -A. H. Müftüoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5752, 'gölgede (gölgesinde) kalmak', 'adı sanı pek duyulmamak, ön plana çıkamamak, daha az ünlü olmak: ''Önce akranlarının gölgesinde kaldı, sonraları kendinden sonra yetişen şairler gölge ettiler önüne.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5753, 'gölgesine sığınmak', 'birinin emri altına girmek: Yakınları bağışlatınca da ayaklarına kapanarak gölgesine sığınmıştı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5754, 'gölgesine yatmak', 'daha önce elde edilen para, makam, ün vb.ne sığınarak zaman geçirmek veya bundan yararlanmak: ''O, büyük aktörlüğün gölgesine yatmış, günlerini stüdyolara telefon etmekle geçiriyor.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5755, 'gömlek değiştirmek', '1) yılan üst derisini değiştirmek; 2) mec. huy veya düşünce değiştirmek: ''Toplumun gömlek değiştirmesi, siyasal karmaşa elbette onları da etkiliyor.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5756, 'gömlek eskitmek', 'deneyim kazanmış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5757, 'gön yufka yerinden delinir', 'her iş en çürük yerinden patlak verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5758, 'gönderme yapmak', 'konuşurken veya yazarken başka kaynak veya olaylarla bağlantı kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5759, 'gönlü akmak', 'birine karşı güçlü sevgi duymak: ''Bu delikanlının kıza, bu kızın delikanlıya gönlü akınca insanın yüreği kabarıyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5760, 'gönlü bulanmak', '1) kusacak gibi olmak; 2) mec. kuşkulanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5761, 'gönlü çekmek', 'imrenip istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5762, 'gönlü çelinmek', 'güzel sözlere aldanmak, kapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5763, 'gönlü çökmek', 'yaşama gücü azalmak, ruhsal dengesi bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5764, 'gönlü düşmek', 'âşık olmak: ''Çaydan üç güvercin uçtu / Benim gönlüm sana düştü'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5765, 'gönlü istemek', 'dilemek, kuvvetle içten arzulamak: ''Gönül istiyordu ki böyle büyük sanatçılara hastalıklar hiç değmesin, onlardan uzak olsun.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5766, 'gönlü kalmak', '1) isteyip de edinemediği bir şeyi istemekten vazgeçmemek; 2) gücenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5767, 'gönlü kanmak', 'bir işle ilgili kaygısı kalmamak, mutmain olmak, müsterih olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5768, 'gönlü kaymak', 'sevmeye eğimli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5769, 'gönlü kırılmak', 'üzülmek, incinmek, yerinmek: ''Bunları duymakla gönlüm kırıldı.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5770, 'gönlü razı olmamak', 'istememek: ''Ama Salih''in gönlü buna razı olmaz, bu yüzden de sorunları yarım ağızla cevaplandırırdı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5771, 'gönlü takılmak', '1) bir şeye karşı ilgi duymak; 2) aşk ile sevmeye başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5772, 'gönlü varmamak', 'istek duymamak, istememek, çekinmek: ''Birkaç gece evvel gelip de bir şey soracaktım, rahatsız etmeye gönlüm varmadı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5773, 'gönlün yazı var, kışı var', 'insan kimi zaman sevinçli, kimi zaman da üzüntülü olabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5774, 'gönlünde kalmak', 'çok istediği hâlde ulaşamamak, elde edememek: ''Bu soyadı çıkmasaydı, bu hatiplik onun gönlünde kalacakmış.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5775, 'gönlünden geçirmek (geçmek)', '1) bir şeyin olmasını veya bir şey yapmayı istemek: ''Topkapı Sarayı''nda Hünername minyatürlerine bakarken kaç defa gönlümden bu özleyiş geçti.'' -Y. K. Beyatlı. 2) düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5776, 'gönlünden kopmak', 'kendiliğinden vermek: ''Fukara bir denizciye rast gelirsen süngerlerimden birkaç tanesini ona ver, gönlünden koparsa.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5777, 'gönlüne doğmak', 'içine doğmak, sezmek, hissetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5778, 'gönlüne dokunmak', 'üzülmek, rahatsızlık duymak: ''Onun kenar mahallelerde sürüklenen çıplak ayakları benim gönlüme dokunuyor.'' -O. S. Orhon.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5779, 'gönlüne girmek', 'kalbine girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5780, 'gönlüne göre', 'dileğine göre, isteğine uygun olarak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5781, 'gönlünü çalmak', 'kalbini çalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5782, 'gönlünü çelmek', '1) kandırmak, yola getirmek, aşkını kazanmak: ''Nice beyler, paşalar onun peşinde yıllarca dolaşmışlar, onun gönlünü çelmek için her türlü çareye başvurmuşlardı.'' -Y. K. Karaosmanoğlu. 2) kendi yanına çekmek, sempatisini kazanmak: ''İlk tanıştığımız günden beri bana karşı gösterdiği yakınlıkla gönlümü çelmiş bulunmaktaydı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5783, 'gönlünü düşürmek', 'âşık olmak, sevdalanmak: ''Biraz aklı olsa bizim Rabia''ya gönül düşürür mü?'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5784, 'gönlünü eğlemek', 'mutlu, neşeli vakit geçirmek: ''Ne güzel yayla da şu bizim yayla / Çık soğuk su başına da gönlünü eğle'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5785, 'gönlünü kaptırmak', 'âşık olmak: ''Kız kaptırdı gönlünü / Sevdiği kalpsizin biri'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5786, 'gönlünü karartmak', 'yaşamaya karşı sevgi ve isteğini azaltmak: ''Tabiatın bu eşsiz güzellikleri karşısında o birtakım gevezeliklerle benim kafamı ağrıtacak, gönlümü karartacak değil.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5787, 'gönlünü pazara çıkarmak', 'sevmek için kendine yakışanı seçmeyip rastgele birini sevmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5788, 'gönlünü serin tutmak', 'sakin, soğukkanlı olmak, hemen heyecanlanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5789, 'gönlünü söndürmek', 'küstürmek, kırmak, incitmek: ''Kalpsiz bir güzelliğin, fakir teyze kızlarının hayatını kırmaktan, gönlünü söndürmekten başka neye faydası var ki!'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5790, 'gönlünü yaralamak', 'incitmek, kırmak, üzmek: ''Onun gönlünü yaralayarak bir latife ederlerse hemen kaçıyor, sokulmuyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5791, 'gönlünün dümeni bozuk', ' tkz. isteklerinde, özellikle gönül işlerinde tutarlılık göstermeyen, sık sık istek değiştiren.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5792, 'gönül açmak', 'insanın iç sıkıntısını gidermek, iç açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5793, 'gönül akıtmak', 'âşık olmak, sevmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5794, 'gönül avlamak', 'huyunu suyunu yakından bilerek olumlu davranışta bulunmak, tavlamak: ''İstanbul''un yetiştirdiği mizaçtan anlar, gönül avlamasını bilir dalkavuklardan biriydi.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5795, 'gönül avutmak', 'hoşça vakit geçirmek: ''Gözünü ve gönlünü avutmak için türlü hoppalıklar yapıyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5796, 'gönül bağlamak', 'severek bağlanmak, içten sevmek, âşık olmak: ''Gözlerin kızarmış, niye ağladın? / Bir başkasına mı gönül bağladın?'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5797, 'gönül bir sırça saraydır, kırılırsa yapılmaz', 'kolay kolay onarılamayacağı için bir kimsenin özellikle de dostlarımızın gönlünü kırmamaya özen göstermeliyiz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5798, 'gönül birliği etmek', 'duygusal anlamda tam bir uyum içinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5799, 'gönül bulandırmak', '1) mide bulandırmak; 2) mec. kuşkulandırmak; 3) mec. rahatsız etmek: ''Haberler iyi değil, rivayetler gönlümü bulandırıyor, sürgünmüş, göz hapsiymiş, estek köstek.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5800, 'gönül çekmek', 'sevdalı olmak: ''Henüz bu yaşta, zavallı çocuk gönül çekmek nedir, bir büyük adam gibi biliyor ve bir büyük adam gibi yarasının acısını kimseye sır vermeyerek taşıyor.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5801, 'gönül eğlendirmek', 'geçici bir ilgi ve sevgi göstererek hoşça vakit geçirmek: ''O bizim arkadaşı oraya dilber Çingene kızları ile gönlünü eğlendirmeye gelmiş paralıca bir delikanlı sanıyordu.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5802, 'gönül ferman dinlemez', 'gönül sevdiğinden asla vazgeçmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5803, 'gönül gezdirmek', ' hlk. seçmek için aklından birçok şey geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5804, 'gönül (gönlünü) almak', '1) sevindirmek; 2) kırılan bir kimseyi güzel bir davranışla hoşnut etmek: ''Bu oğlanı amcama itmek doğru değil, bir ara gönlünü almalı.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5805, 'gönül indirmek', 'kendisine yakıştıramadığı bir şeye razı olmak: ''Oysa o oturmamıştı bile sofraya, bir fincan çay içmeye gönül indirmemişti.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5806, 'gönül kırmak (yıkmak)', 'birini çok üzecek bir davranışta bulunmak, gücendirmek: ''Osman Efendi iyi adamdı, kimsenin gönlünü kırmazdı.'' -İ. H. Baltacıoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5807, 'gönül kimi severse güzel odur', 'güzellik anlayışı kişiden kişiye değişir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5808, 'gönül kocamaz', 'insanlar yaşlansalar da gönüllerindeki sevgi ve istekler tazeliğini yitirmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5809, 'gönül koymak', 'gücenmek, alınmak, darılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5810, 'gönül okşamak', 'birini hoş bir söz veya davranışla sevindirmek, iltifat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5811, 'gönül rızası ile', 'isteyerek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5812, 'gönül var otluğa, gönül var bokluğa (konar)', 'iyi ve güzel şeyleri seven yüksek ruhlu insanlar olduğu gibi kötü ve pis şeylerden hoşlanan aşağılık insanlar da vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5813, 'gönül verme evliye, eve gider unutur', 'bir kadın, evli bir erkeğe gönlünü kaptırmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5814, 'gönül vermek', '1) sevmek, âşık olmak: ''1934''te yepyeni bir Türkçeye gönül vermiş olan Atatürk, sonraki üç dört yıl içinde, daha ılımlı bir dil devrimine yönelmiş olabilir mi?'' -T. Halman. 2) bir şeyi sevmeye, istemeye veya yapmaya içten yönelmek, eğinmek, meyletmek: ''Atölyelerde bu işe gönül veren idealist öğretmenler ders vermekteydi.'' -C. Uçuk. 3) düşkün olmak: ''Cevizli tel kadayıfına gönül verene de rastlanıyor.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5815, 'gönül yakmak', '1) insanı aşırı derecede etkilemek, sarsmak, kendinden geçmesine yol açmak: ''Bu sesler, o zamanki hayat zevklerinin iç bayıltıcı bir içkisi gibi gönlümüzü yakarak ta derinliklerimize kadar nüfuz etmesini bilirdi.'' -A. Ş. Hisar. 2) aşk dolayısıyla iç yangınına tutulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5816, 'gönül yıkmak', 'birini çok üzecek bir davranışta bulunmak, gücendirmek, gönül kırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5817, 'gönülden gönle yol vardır', 'sevgi karşılıklıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5818, 'gönülden ırak olmak', 'sevilmekten yoksun kalmak, sevilmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5819, 'gönüller bir olunca samanlık seyran olur', 'birbirini sevenler için zenginlik önemli değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5820, 'gönülsüz namaz göğe ağmaz', 'isteksiz yapılan bir işten hayır gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5821, 'gönülsüz yenen aş, ya karın ağrıtır ya baş', 'istenmeyerek yapılan işlerden kötü sonuçlar ortaya çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5822, 'gör bak', '''görürsün, göreceksin'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5823, 'gör (görürsün)', '''işin sonucunu anla, anlarsın'' anlamında kullanılan bir tehdit sözü: ''Birini çağırıp o güvercinleri vereyim de sen de gör.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5824, 'gördün deli, savul geri!', 'dengesiz kimselerden uzak durmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5825, 'gören gözün hakkı vardır', 'yiyecek veya imrenilecek bir şeyi görene o şeyden vermek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5826, 'göresi (göreceği) gelmek', 'görmek isteğini duymak, özlemle görmek istemek, özlemek: ''Geçmiş günlere hasret çekmiyorum. Çocukluğumu göresim gelmedi.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5827, 'görev almak', 'bir görevde bulunmak, bir görevi üstlenmek: ''Hâkimler ve savcılar kanunda belirtilenlerden başka resmî ve özel hiçbir görev alamazlar.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5828, 'görev bilmek (saymak, addetmek)', 'görev olarak üzerine almak, sorumluluk üstlenmek: ''Umutlu da olsam umutsuz da olsam görev bildiğim işi yerine getiririm.'' -M. C. Anday.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5829, 'göreyim seni', '1) senden başarılı sonuçlar bekliyorum: Haydi göreyim seni, bu işi yapıver. 2) ''sen bunu yaparsan karşılığını da görürsün'' anlamında kullanılan bir tehdit sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5830, 'görgülü kuşlar gördüğünü işler, görmedik kuşlar ne görsün ki ne işler?', 'iyi eğitim alanlar aldıkları eğitimin gereğini yaparlar, iyi eğitim görmeyenler bir şey yapamazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5831, 'görmediğe (görmemişe) dönmek', '1) tam bir sağlığa kavuşmak; 2) başından geçmemiş gibi olmak: ''Bir saniye içinde hasret ve firkati hiç görmemişe dönersiniz.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5832, 'görmemiş görmüş, güle güle ölmüş', 'görgüsüz kişi, günün birinde ummadığı bir şeyi elde ederse sevincinden ne yapacağını şaşırır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5833, 'görmemişin oğlu olmuş (çekmiş, çükünü koparmış)', 'görgüsüz kimse ummadığı bir şeyi elde ettiğinde ne yapacağını şaşırır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5834, 'görmezden gelmek', 'görmemiş gibi yapmak, farkında değilmişçesine davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5835, 'görmezlikten gelmek', 'görmemiş gibi davranmak: ''Kibirli değildi, bayağı bir saldırıyı görmezlikten gelecek kadar.'' -R. Mağden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5836, 'görücü gitmek', 'evlenecek erkek için kız görmeye gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5837, 'görücülüğe gitmek', 'evlenmek isteyen erkek için kız görmeye gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5838, 'görücüye çıkmak', 'evlenmesi söz konusu olan kız görücüye görünmek: ''Onu indirmek, görücüye çıkmaya razı etmek için başta haminne olmak üzere bütün ev halkı ağacın altında durdu, yalvardı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5839, 'görümcelik yapmak (etmek)', 'görümce, geline kötü davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5840, 'göründü Sivas''ın bağları', 'umutla beklenen sonuç ters yönde gelişti.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5841, 'görünen dağın (köyün) uzağı olmaz', 'bir durumun nasıl sonuçlanacağı belli olduktan sonra bu sonuç çok geçmeden gerçekleşir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5842, 'görünen köy kılavuz istemez', 'ne kadar gizlense de gerçekler ortadadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5843, 'görünüş almak', 'şekil almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5844, 'görünüşe aldanma', '''yalnızca dış görünüşe bakarak yargıya varmak insanı yanıltabilir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5845, 'görünüşü kurtarmak', 'bir işi gereği gibi değil, yapılıyor dedirtmek için üstünkörü yapmak, zevahiri kurtarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5846, 'görüp göreceği rahmet bu', '''görülecek tek şey'' anlamında kullanılan bir söz: ''Ağız tadı ile oynanan futbolu gördük ama görüp göreceğimiz rahmet de bu kadarmış.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5847, 'görüş bildirmek', 'bir konuda elde edilen düşünce ve deneyimleri vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5848, 'görüş birliği içinde olmak', 'aynı görüş ve düşünceye sahip bulunmak: ''Rahat rahat konuştukça her bakımdan tam görüş birliği içinde olduğumuz açığa çıktı.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5849, 'görüş birliği sağlamak', 'aynı görüş ve düşüncede birleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5850, 'görüş birliğine varmak', 'farklı görüş ve düşüncelerden sonra aynı görüş ve düşünceye ulaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5851, 'gösterime girmek', 'sinema salonlarında bir film oynamaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5852, 'gösterişe kaçmak', 'gösteriş yapmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5853, 'götüne güvenen borazancıbaşı', 'başarabileceğinize eminseniz büyük işlere girişiniz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5854, 'gövdeye atmak (indirmek)', ' tkz. oburca yemek: ''Bir tepsi baklavayı gövdeye indirdikten sonra...'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5855, 'göz açamamak', 'yoğun işler yüzünden bir şeyle ilgilenme imkânı bulamamak: ''İşkembe ayıklamaktan, bulaşık yıkamaktan göz açamıyordum.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5856, 'göz açıp kapayıncaya kadar', 'çok kısa bir sürede: ''Göz açıp kapayana kadar Zafer büyüdü.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5857, 'göz açtırmamak', 'başka bir iş yapmasına vakit veya imkân vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5858, 'göz alabildiğine', '1) gözün görebileceği en uzak yerlere kadar: ''Bu göz alabildiğine düzlük, sinsi bir bataklık gibidir.'' -A. Erhat. 2) çok geniş, engin bir biçimde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5859, 'göz ardı etmek', 'gereken önemi vermemek: ''Kocakarı yöntemlerine inanmayı göz ardı ettiğini söyleyemezdim.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5860, 'göz atmak', 'kısa bir süre, fazla dikkat etmeden bakıvermek: ''Bir ara karşıdaki salaş birahanenin penceresine göz atıyorum.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5861, 'göz boyamak', 'kandırmak, yanıltmak, gösterişle aldatmak: ''Yerine göre fakiri korur gibi görünür, gözleri boyar böylece.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5862, 'göz değmek', 'uğursuzluk, kötülük getirdiğine inanılan kıskanç veya hayran bakışlar dolayısıyla kötü bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5863, 'göz dikmek', 'bir şeyi ele geçirmek isteğine kapılmak: ''Bizim canımıza, malımıza hangi devlet göz dikmişti?'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5864, 'göz doldurmak', 'görünüşü ile umulduğundan çok etkilemek: Bu futbolcu antrenmanda göz doldurdu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5865, 'göz doyurmak', 'bir şey görünüşü ile umulduğundan çok etkilemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5866, 'göz etmek', 'gözle işaret etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5867, 'göz gezdirmek', '1) derinlemesine incelemeden okumak: ''Masanın üstünde bir başka gazete var. Biraz evvel ona göz gezdirdiğim zaman birbiri ardı sıra üç havadis görmüştüm.'' -R. N. Güntekin. 2) bir yeri, bir şeyi çabucak incelemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5868, 'göz gördüğünü (ağız yediğini) ister', 'kişi, her zaman gördüğü, (yemeye) alıştığı güzel şeyleri arzular.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5869, 'göz göre göre', '1) belli ve apaçık olarak, herkesin gözü önünde: ''Göz göre göre masumların kanına girmem için benden ferman almaya mı geldiniz.'' -N. F. Kısakürek. 2) olacağı bilindiği hâlde önlem alınmadan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5870, 'göz görmeyince gönül katlanır', 'yakınımızda bulunmayanların özlemine, acısına daha kolay dayanabiliriz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5871, 'göz görür, gönül ister (çeker)', 'kişi, görmediği şeyi istemez; görüp beğendiği şeye karşı istek duyar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5872, 'göz görür, gönül katlanır', 'kişi, sevdiği bir kimsenin uzak yere gitmesi durumunda onunla görüşmekten umudunu keser, ayrılığa katlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5873, 'göz göz olmak', 'üzerinde birçok göz, delik oluşmak veya bulunmak: ''Yeter oldu bu sitemler yetişir / Göz göz oldu kara bağrım tutuşur'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5874, 'göz göze gelmek', 'her iki tarafın bakışları karşılaşmak: ''İşte bu iki adam bir aralık göz göze geldiler.'' -İ. H. Baltacıoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5875, 'göz (gözler) önüne sermek', 'açıklamak, sergilemek, göstermek, tanıtmak: Adı duyulmamış, şiiri bilinmeyen gençleri tutar, gözler önüne sererdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5876, 'göz gözü görmemek', 'yoğun sis, duman, toz vb. sebeplerle hiçbir şey görülememek: ''Tezek dumanında göz gözü görmez.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5877, 'göz (gözünün) kuyruğuyla bakmak', 'göz ucuyla bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5878, 'göz (gözünün) önünde olmak', '1) sürekli denetimi altında bulunmak; 2) unutmamak, olduğu gibi hatırlamak: ''Hızla açılan kapıdan içeri girişi, hayır girişi değil, atılışı hâlâ gözümün önündedir.'' -Y. Z. Ortaç. 3) gündemde yer almak; 4) kolayca ulaşılabilecek bir yerde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5879, 'göz (gözünün) önüne serilmek', 'görülmek, bütün çıplaklığıyla ortaya çıkmak: ''İstanbul''a bu yükseklikten bakılınca birden gözlerimizin önüne serilir.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5880, 'göz (gözünün) ucuyla bakmak', 'fark ettirmeden gözlemek, belli etmemeye çalışarak başını çevirmeden yandan bakmak: ''Kadın, gözünün ucuyla erkeğe baktı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5881, 'göz hapsine almak', 'bakışlarını üzerinden ayırmamak, gözetlemek, hiçbir davranışını gözden kaçırmamak: ''Sözü sohbeti yerinde görünen birkaç erkeği haftalarca göz hapsine aldı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5882, 'göz kamaştırmak (almak)', '1) kuvvetli ışık veya parlaklık, kısa bir zaman için görüşü bulandırmak; 2) mec. bir niteliğiyle hayran bırakmak: ''O sıralar Avrupa''da bir büyük piyano ustası gözleri kamaştırıyordu.'' -N. Nadi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5883, 'göz kaş süzmek', 'dikkatle ve hissettirmeden bakışlarla kontrol altında tutmak: ''Anlamlı anlamlı birbirine işaretler yaparak, göz kaş süzerek Emine''ye uzun uzun bakıyorlar.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5884, 'göz kesilmek', 'bütün dikkatiyle bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5885, 'göz kırpmadan', '1) acımadan, merhamet etmeden; 2) duraksamadan, çekinmeden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5886, 'göz kırpmak', '1) göz kapağını kapayıp açmak: ''Hem gülüyor hem sık sık bana kaçamak bakışlarla bakıyor, muziplikle göz kırpıyor.'' -A. Ağaoğlu. 2) başkasına söylediklerinin doğru olmadığını işaretle anlatmak için, benimsediği kimseye bakarak gözünü kapayıp açmak: ''İki sahilde pencerelerden damla damla taşan ışıklar güzel aydedeye göz kırpmakta yıldızlarla rekabet ediyor sanılır.'' -A. H. Müftüoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5887, 'göz kırpmamak', 'uyumamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5888, 'göz koymak', 'bir kimseyi veya bir şeyi ele geçirmeyi istemek: ''Kırkyılda bir nişanlı buldum, ona da sen mi göz koydun?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5889, 'göz kulak olmak', '1) görme, işitme yoluyla bilgi edinmeye çalışmak; 2) mec. gözetmek, korumak, bakmak: ''Öbürü göğsünden ağır yaralı iki erin geriye alınmalarına göz kulak oluyordu.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5890, 'göz nuru dökmek', 'fazla emek sarf etmek: ''Kızcağız göz nuru dökmüş, çok ince şeyler işlemiş.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5891, 'göz önünde tutmak (bulundurmak)', 'herhangi bir durumun nasıl bir sonuca yol açacağını hesaba katmak, dikkate almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5892, 'göz önüne almak', 'önceden düşünmek, hesaplamak, dikkate almak: ''1908''den önceki zemin ve zamanı göz önüne almalı.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5893, 'göz önüne getirmek', 'zihinde canlandırmak, tasarlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5894, 'göz süzmek', 'baygın ve anlamlı bakmak: ''Göz süzüp boyun kırması, erkeği baştan çıkarmanın ilmini bilmesi fabrikaların tezgâh başlarında, soyunma odalarında konuşuldu.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5895, 'göz terazi, el mizan', 'elle tartıp ağırlığı, gözle bakıp hacmi tahmin edebiliriz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5896, 'göz ucuyla görmek', 'fark etmek: ''Benim için dualar okuduğunu göz ucuyla görebiliyordum.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5897, 'göz ucuyla süzmek', 'iyice tanımak, bilmek veya dikkat çekmek amacıyla hafif kısık gözle incelemek, bakmak: ''Sokakta göz ucuyla süzdüğüm kadının bana ehemmiyet vermediğini görürsem hoşça bir latife söyleyiveririm.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5898, 'göz yıldırmak', 'gözünü korkutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5899, 'göz yummak', '1) görmezlikten gelmek, hoş görmek, bağışlamak: ''Kendi dillerine başka bir dilden en küçük bir şeyin karışmasına göz yumamazlar.'' -N. Uygur. 2) umudunu kesmek, umutsuzluğa düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5900, 'göz yummamak', '1) uyumamak; 2) mec. hoş görmemek, bağışlamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5901, 'gözaltına almak', 'güvenlik kuvvetleri birini belli bir süre, belli bir yerde tutmak, nezarete almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5902, 'gözaltında tutmak', '1) güvenlik kuvvetleri birini belli bir süre, belli bir yerde tutmak; 2) gözetlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5903, 'gözaydın etmek', 'güzel bir olay için kutlamak, iyi dileklerde bulunmak: ''Bir hafta evimize geldiler, gittiler. Köylerden bizleri tanıyanlar bile geldiler, gözaydın ettiler.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5904, 'gözaydına gelmek', 'birine kavuştuğu sevindirici bir durum dolayısıyla kutlamaya, iyi dilekte bulunmaya gelmek: ''Eve dönünce orasını düğünevi gibi kalabalık buldum. Duyan kadınlar gözaydına gelmişler.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5905, 'gözaydına gitmek', 'birine kavuştuğu sevindirici bir durum dolayısıyla kutlamaya, iyi dilekte bulunmaya gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5906, 'gözden çıkarmak', 'bir mal, para, değer yargısı vb. maddi veya manevi varlığın elden çıkarılmasını kabul etmek: ''İnsan, emeğini o kadar kolay gözden çıkaramıyor.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5907, 'gözden geçirmek', '1) okumak: ''O günkü gazeteleri gözden geçirdi.'' -F. R. Atay. 2) niteliğini anlamak için bir şeyin her yanına bakmak, incelemek, muayene etmek: ''Akşam hazırlanmış sofrayı gözden geçirmek için odasından çıktı.'' -A. Kutlu. 3) araç, motor vb.nin çalışıp çalışmadığını incelemek, denemek, denetlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5908, 'gözden gönülden çıkarmak', 'önem vermemek, ilgisini kesmek: ''Şimdi, artık gözünden ve gönlünden çıkardığı bu adamın her şeyi onun için müsavi idi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5909, 'gözden (gözünden) düşmek', 'bir kişi veya şey değerini yitirmek, rağbet görmemek: ''Muhtarın oğlu bu hasta köpeklere düşman olduğu günden beri gözümden düştü.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5910, 'gözden (gözünden) kaçırmak', 'dalgınlıkla görmemek: ''Fikirleri dağınıklıktan kurtarmak için, özüne irca etmek ve onu gözden kaçırmamak lazımdır.'' -M. Kaplan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5911, 'gözden (gözünden) kaçmak', 'görülmemek, farkına varılmamak: ''Emeğinin ve cesaretinin gözümden kaçmış bulunmasından hâlâ üzgünlük duyuyorum.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5912, 'gözden (gözünden) sürmeyi çalmak (çekmek)', 'hırsızlıkta çok becerikli, çok usta olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5913, 'gözden ırak olan gönülden de ırak olur', 'ayrı düşenlerin arasındaki sevgi de zamanla azalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5914, 'gözden ırak tutmak', 'görmek istememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5915, 'gözden ırak tutulmak', 'önem verilmemek, değersiz bulmak: ''Bunca yüzyıl gözden ırak tutulan gerçek Türkçeyi ön plana almak gerekiyordu.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5916, 'gözden kaybetmek', 'görünmemek, ortadan çekilip gitmek: ''Mektepten sonra birbirimizi gözden kaybetmiştik.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5917, 'gözden kaybolmak', 'ortadan çekilmek veya görünmez olmak, kaybolmak: ''Vakta ki gece mehtaba çıktılar. Senihe ile Faik Bey uzun bir müddet gözden kayboldular.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5918, 'gözden nihan olmak', 'gözden kaybolmak: ''Nihayet yıkık bir kulübe civarında gözden nihan oldular.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5919, 'gözden uzak tutmak', 'önem vermemek, arka plana itmek: Çıkarlarını gözettiği sınıfı gözden uzak tutmak, adını andırmamak isterler.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5920, 'gözden uzaklaşmak', 'ayrılıp başka yere gitmek, görünmez olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5921, 'göze almak', 'gelebilecek her türlü zararı ve tehlikeyi önceden kabul etmek: ''Bunlardan kaç babayiğit bu ölüm yarışını göze alabilir?'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5922, 'göze batmak', '1) aşırı derecede görünür olmak: ''Şöyle kenara göze batmayacak bir masaya iliştik.'' -N. Hikmet. 2) tedirgin etmek, uygunsuz veya yakışıksız görünmek: ''Hiçbir zaman göze batmak ve sivrilmek isteme.'' -N. F. Kısakürek. 3) çekemezliğe yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5923, 'göze çarpmak', 'dikkati üzerine çekmek: ''Evin nizamında Türk kadınlarının vakur zarafeti göze çarpar.'' -O. S. Orhon.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5924, 'göze diken olmak', 'göze batmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5925, 'göze gelmek', 'birisine nazar değmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5926, 'göze girmek', 'davranış ve yetenekleriyle ilgi ve önem kazanmak: ''O fırsatta onu yererek göze girmeye çalışan birkaç tıynetsiz dalkavuk da elbet renk verdiler.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5927, 'göze görünmek', 'belli, açık olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5928, 'göze görünmemek', '1) ortaya çıkmamak, ortalıkta dolaşmamak, saklanmak; 2) kendisi var olduğu hâlde göz onu görememek; 3) değersiz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5929, 'göze yasak olmaz', 'bir kimseye veya nesneye bakılmasını kimse önleyemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5930, 'gözetim altında tutmak', 'göz önünden ayırmamak: ''Onu kolla, gözetim altında tut ama bunu ona hiç belli etme.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5931, 'gözetime almak', 'gözetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5932, 'gözle görülür, elle tutulur hâle gelmek', 'çok açık bir biçimde görülmek, herkes tarafından bilinmek: Haksızlık, rüşvet, gözle görülür, elle tutulur hâle gelmişti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5933, 'gözle yemek', '1) bir şeye çok istekle ve dik dik bakmak; 2) göz değdirmek: Çocuğu gözle yediler.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5934, 'gözlem altına almak', '1) bir nesneyi, olayı veya bir gerçeği, niteliklerinin bilinmesi amacıyla, dikkatli ve planlı olarak ele alıp incelemek; 2) hastanın hastalığını izlemek, denetim altında bulundurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5935, 'gözleri bayılmak', 'uyku, istek vb. bir durum gözlerinden belli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5936, 'gözleri berraklaşmak', 'bakışları daha canlı ve parlak olmak: ''Çocukluğuna ait bazı hatıralarını söylerken, gözleri berraklaşıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5937, 'gözleri buğulanmak (bulutlanmak)', 'gözleri yaşararak çevreyi bulanık görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5938, 'gözleri çakmak çakmak (olmak)', 'ateşli hastalık veya öfkeden gözleri kızarmış ve parlamış (olmak): ''Avuçları ateş gibi fersiz gözleri çakmak çakmak dört dönüyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5939, 'gözleri çukura gitmek (kaçmak)', 'aşırı yorgunluktan göz çevresi kararmak veya çökmek: ''Genç yakışıklı yüzü solmuş, gözleri çukura kaçmıştı.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5940, 'gözleri dolmak (dolu dolu olmak)', 'ağlayacak kadar duygulanmak: ''Gözleri dolu doluydu ama ağlamadı.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5941, 'gözleri dönmek', 'aşırı ateşten veya can çekişirken gözlerin renkli bölümü kapakların altında kalarak görünmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5942, 'gözleri fıldır fıldır etmek', 'şeytanca ve çapkınca bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5943, 'gözleri fıldır fıldır olmak', 'telaşlı bir biçimde bakmak: ''Pipo içer, gözleri yüzünde iki ateş böceği gibi fıldır fıldırdır.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5944, 'gözleri ışıklı (olmak)', 'gözleri ışık içinde (olmak).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5945, 'gözleri kan çanağına dönmek (kanlanmak)', '1) uykusuzluk, yorgunluk, ağlama vb. sebeplerle gözleri çok kızarmak: ''Kerem''in kusacağı geliyordu. Gözleri kan çanağına dönmüştü.'' -Y. Kemal. 2) sinirden, öfkeden, hiddetten gözleri irileşmek ve kızarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5946, 'gözleri kapanmak', '1) ölmek; 2) çok uykusu gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5947, 'gözleri parlamak (parıldamak)', 'gözlerinde sevinç ve istek belirmek: ''İki kere gidip geldikten sonra gözleri parladı, evi bulmuştu.'' -H. E. Adıvar. ''Yavaş yavaş başlarını kaldırıp yekdiğerinin yüzüne baktılar, ikisinin de gözleri parıldadı.'' -A. H. Müftüoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5948, 'gözleri sulanmak', 'gözlerine yaş gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5949, 'gözleri süzülmek', 'göz kapakları hafifçe kapanmaya başlamak: ''İki elini bastonun gümüş topuzuna dayamış, gözleri saadetten süzülmüş, adamı dinliyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5950, 'gözleri şıldır şıldır dönmek', 'gözleri yaş dolu bir biçimde bakmak: ''Gözleri şıldır şıldır dönerek şikâyet ederdi.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5951, 'gözleri takılıp kalmak', 'bir şeyden gözlerini ayıramamak: ''O anda pek çok şeyler yapmak istediği hâlde, gözleri köşeyi ağır ağır dönen tramvaya takılıp kalmıştı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5952, 'gözleri velfecri okumak', 'kurnazlığı gözlerinden belli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5953, 'gözleri yaşarmak', '1) gözleri sulanmak: ''Öyle halk türküleri vardır ki gözleriniz yaşarmadan okuyamaz veya dinleyemezsiniz.'' -M. Kaplan. 2) mec. duygulanmak: ''Bütün başarılarda gözlerim yaşarır, bütün ayrılışlarda aynı şey.'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5954, 'gözleri yuvalarından (evinden) fırlamak (uğramak)', 'korku, öfke ve telaşı gözlerinden belli olmak: ''Cüce rolünde halkı gülmekten katıltan sırıtış, Rakım''ın bütün buruşuklarını kaplamış, ayrık gözleri evlerinden uğramış.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5955, 'gözlerinde şimşek çakmak', 'aşırı parlamak: ''Bazen kara gözlerinde şimşekler çakıyordu.'' -R. Enis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5956, 'gözlerinden okumak', 'düşüncelerini bakışlarından sezmek: ''Doktor, Sevim Hanım''ın içinden geçenleri gözlerinden okuyarak söze karıştığında pişman oldu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5957, 'gözlerine inanamamak', 'hiç umulmayan, hiç beklenmeyen bir şeyin görülmesi karşısında şaşırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5958, 'gözlerini bayıltmak', 'gözlerini yarı kapamak: ''İnleyerek, gözlerini bayıltarak nasıl düştüğünü anlatıyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5959, 'gözlerini belertmek', 'gözlerini, akı çok görünecek biçimde açmak: ''Birisinin âşıklı maşuklu bir masal söylediğini işitti mi karşısında apışıp gözlerini belertiyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5960, 'gözlerini bitirmek', 'gözlerini aşırı yormak: ''Her gece fasılasız çalışmak gözlerimi bitirdi.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5961, 'gözlerini devirmek', 'öfke ile bakmak: ''Şerbetçide temiz bardak bulamayan müşteri, gözlerini devire devire bağırıyor.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5962, 'gözlerini fal taşı gibi açmak', 'şaşkınlıkla, hayretle bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5963, 'gözlerini kaçırmak', 'biriyle göz göze gelmemek için gözlerini başka tarafa çevirmek: ''Bazen böyle bir tesadüf olursa gözlerini kaçırmayı doğru bulmuyorlardı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5964, 'gözlerinin içi gülmek', 'çok sevindiği yüzünden, gözlerinden belli olmak: ''Zayıf bir kızı severdim / Gözlerinin içi gülerdi'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5965, 'gözlerinin içine kadar kızarmak', 'utancından yüzü çok kızarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5966, 'gözlüye gizli yoktur', 'görmesini bilen kişiden hiçbir şey gizlenemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5967, 'gözü açık gitmek', 'gerçekleşmesini çok istediği bir dileğine erişmeden ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5968, 'gözü açık olmak', 'fırsattan yararlanmak, kurnazca davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5969, 'gözü akmak', 'gözü yaralanıp kör olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5970, 'gözü alışmak', '1) önceden iyi göremediği bir şeyi sonradan görür olmak; 2) mec. bir şey ilk etkisini yitirmek, yadırganmaz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5971, 'gözü almamak', 'bir işi becerebileceğine inanmamak, yadırganmaz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5972, 'gözü arkada kalmak', 'bırakılan bir şey veya kimse ile ilgili tedirginliği sürmek: ''Benim gibi bir adama teslim ettikten sonra gözü arkada kalmazdı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5973, 'gözü bağlı olmak', '1) bağlanmak, tutulmak; 2) büyülenmiş bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5974, 'gözü bulanmak', 'bulanık görmeye başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5975, 'gözü büyükte olmak', 'büyük emeller beslemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5976, 'gözü dalmak', 'gözü bir noktaya dikili olarak dalgın dalgın bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5977, 'gözü değmek', 'uğursuzluk, kötülük getirdiğine inanılan kıskanç veya hayran bakışlar dolayısıyla kötü bir duruma düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5978, 'gözü doymak', 'çok istenen bir şeyin yeterli miktarı elde edildikten sonra daha çoğunu istememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5979, 'gözü dumanlanmak', 'öfkeden gözü hiçbir şey görmez duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5980, 'gözü dünyayı görmemek', 'hiç kimseye, hiçbir şeye önem, değer vermemek: ''Bir kere fevri, hemen parlar, kızınca gözü dünyayı görmez.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5981, 'gözü gönlü açılmak', 'neşelenmek, ferahlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5982, 'gözü görmemek', '1) görmez olmak; 2) belli bir şeyden başka bir şeyle ilgilenmemek; 3) öfke sonucu en kötü şeyleri yapacak duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5983, 'gözü göz değil', 'iyi insan olmadığı bakışından belli oluyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5984, 'gözü (gözleri) açılmak', '1) iyiyi kötüyü veya kendisine yarayanı ayırt eder duruma gelmek: ''Mektepten, kitaplardan fazla bu gençlerin muhitinde gözleri açılmış.'' -Y. K. Beyatlı. 2) uyanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5985, 'gözü (gözleri) (bir şeyde, bir şeyin üzerinde) olmak', 'dikkati bir yerde toplanmak: ''Masalarda oturan kadınların en ufak bir harekette gözleri kapıdaydı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5986, 'gözü (gözleri) dönmek', 'aşırı bir isteğin, öfkenin etkisiyle ne yaptığını bilmez duruma gelmek: ''Teşebbüs, hamle, gayret, aksiyon ne demektir, bu gözü dönmüş insanlardan öğrenmek lazım.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5987, 'gözü (gözleri) kamaşmak', '1) güçlü bir ışık sebebiyle göz bakamaz olmak: ''Güneş hiç olmadığı kadar parlaktı, gözlerim kamaştı.'' -E. Işınsu. 2) mec. çok etkilenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5988, 'gözü (gözleri) kararmak', '1) başı dönmek, hafif baygınlık geçirmek: ''Duvar tarafına doğru bir adım atarak evet cevabını veren Orhan''ın gözleri gene kararıyordu.'' -P. Safa. 2) mec. umutsuzluğun veya aşırı bir isteğin etkisi altında ne yaptığını bilmez duruma gelmek: ''İnsan sevgisi ne kadar yoğunsa gözü karardığında cesareti de o denli delice idi.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5989, 'gözü (gözleri) kaymak (kaçmak)', '1) gözünde hafifçe şaşılık bulunmak; 2) istemeyerek bakıvermek: ''İstemeye istemeye gözleri lokantacıya kaçtı.'' -Ö. Seyfettin. 3) bayılmak sırasında gözünün akı çoğalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5990, 'gözü (gözleri) okşamak', 'göze hoş görünmek: ''Kadıköy''den Fenerbahçe''ye kadar olan saha, gözleri okşayan bağlarla örtülüdür.'' -B. Akyavaş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5991, 'gözü (gözleri) üstünde kalmak', '1) kıskançlık sebebiyle herkesin ilgisini çekmek: ''O, dükkânı sana vereyim, dedi, ben istemedim. Neme lazım, bin kişinin gözü üstünde kalacak.'' -M. Ş. Esendal. 2) herkesin dikkatini çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5992, 'gözü hiçbir şey görmemek', 'heyecana kapılıp başka hiçbir şeyle uğraşamaz duruma gelmek: ''O yaz nasıl geçti bilmiyorum; ne yaz ne tatil, hiçbir şeyi gözüm görmüyordu.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5993, 'gözü ilişmek', 'birdenbire veya istemeden görmek: ''Demin şu pencereden gözüm denize ilişince kendimi Roma''ya giden bir vapurda sandım.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5994, 'gözü kalmak', '1) elde edemediği bir şeye karşı isteği sürmek; 2) elde edemediği bir şeyi kıskanmak: ''Ben herkesin gözü kalsın istemem yediğim lokmada.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5995, 'gözü kapalı olmak', 'çevresinde olup bitenin farkına varmamak, ilgisiz kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5996, 'gözü kara çıkmak', 'korkusuz olduğu anlaşılmak: ''Gözü kara çıkmış, yaşamın bozuk para gibi harcanabileceğini kanıtlayan o üstün insanlar arasına katılmıştı.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5997, 'gözü kızmak', 'gözü hiçbir şey görmeyecek ölçüde öfkelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5998, 'gözü korkmak', 'daha önce geçirdiği kötü bir denemeden sonra birinden veya bir şeyden zarar gelebileceği kanısına varmak: ''Yabancı bir iklimde, ebedî olarak yaşamaya mahkûm olduktan sonra bundan üstün hangi bir cezadan gözümüz korkabilir.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (5999, 'gözü sönmek', 'kör olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6000, 'gözü tanede olan kuşun ayağı tuzaktan kurtulmaz', 'her zaman çıkar peşinde koşan kişi, tehlikelerden uzak kalamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6001, 'gözü toprağa bakmak', 'ölmek üzere olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6002, 'gözü uyku tutmamak', 'uyuyamamak: ''O gece Aşağı Sazan''ın gözünü uyku tutmamıştır, birçok pencerede ışık vardır.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6003, 'gözü yılmak', 'daha önceden denediği için o durumla karşılaşmaktan korkmak, o işe girişmekten çekinmek: ''Artık bu tedaviden bıkmış usanmış, adamakıllı gözü yılmıştı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6004, 'gözü yolda (yollarda) kalmak (olmak)', 'birinin gelmesini merak, istek veya özlemle beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6005, 'gözü yüksekte (yükseklerde) olmak', 'bulunduğu durumdan çok üstün olan bir duruma ulaşma amacını gütmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6006, 'gözüm çıksın (kör olsun)', 'bir şeyin doğruluğuna inandırmak için edilen ant.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6007, 'gözün aydın!', 'sevinçli bir olay dolayısıyla kullanılan bir kutlama sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6008, 'gözünde büyümek', 'bir şey bir kimseye olduğundan güç veya önemli görünmek: ''Güneşin altında bu sıcak kırları geçmenin ağırlığı gözümde büyüyordu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6009, 'gözünde büyütmek', 'bir kimseyi, olayı veya şeyi abartmak: ''Bir zamanlar gözünde büyüttüğü adama bir nevi minnet borcu edası olmalıydı bu.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6010, 'gözünde (gözlerinde) şimşek (şimşekler) çakmak', '1) sert ve şiddetli darbe yüzünden göz önünde yıldızlar oluşmak; 2) çok sevindiğini belli etmek: ''Zehra''yı Haşim''e almayı düşünürken, oğlanın gözlerinde nasıl şimşek çakmıştı.'' -H. E. Adıvar. 3) çok kızmak, öfkelenmek: ''Eski oyuncunun gözlerinde şimşekler çaktı, yutkundu.'' -H. E. Adıvar. 4) çok üzücü bir sebeple sarsılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6011, 'gözünde tütmek', 'çok özlemek: ''Akşamlar niçin hâlâ gözünde tütüyor?'' -A. N. Asya.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6012, 'gözünden (gözlerinden) uyku akmak', 'çok uykulu olmak: ''Şilteye diz çökmüş, uyku akan gözlerini parmaklarıyla açıyor, uyumayayım diye ninni söylüyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6013, 'gözünden (gözlerinden) yaş (yaşlar) boşanmak', 'çok ağlamak: ''Gözlerinden yaşlar boşandı birden.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6014, 'gözünden kıskanmak', 'üzerine titremek, kollayıp gözetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6015, 'gözüne batmak', 'tedirgin etmek, rahatsız etmek: ''Kimsenin gözüne batmadan, tanınıp bilinmeden büyük bir kentin kaldırımlarında yaşamanın doyulmaz bir tadı vardı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6016, 'gözüne çarpmak', 'görünür olmak, dikkati çekmek: ''İlk gözüme çarpan köşe minderi ve üstündeki eski nakışlarla işlenmiş yastıklar.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6017, 'gözüne diken olmak', 'gözüne batmak: ''Hasene''yi odadan kovdunuz da şimdi gözünüze ben mi diken oldum?'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6018, 'gözüne dizine dursun', 'nankörlük eden birine ''Allah nankörlüğünün cezasını seni kör ve kötürüm ederek versin'' anlamında söylenen bir ilenme sözü: ''Yaptığım iyilik gözünüze dizinize dursun.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6019, 'gözüne (gözlerine) bakmak', 'gözünün veya gözlerinin içine bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6020, 'gözüne hiçbir şey görünmemek', 'kendi derdi dolayısıyla hiçbir şeye değer vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6021, 'gözüne karasu inmek', '1) karasu hastalığı yüzünden gözü görmez olmak; 2) gelmesini çok istediği kimsenin uzun süre yolunu gözlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6022, 'gözüne kestirmek', '1) başarabileceğini ummak; 2) zevkine uygun bulmak, hoşlanmak: ''Dam olarak beni gözüne kestirdiği anlaşılıyordu.'' -R. N. Güntekin. 3) uygun bulmak, elverişli görmek: ''Kayaların gözüme kestirdiğim bir yerinden aşağı inmeye başladım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6023, 'gözüne sokmak', 'bir kimsenin görmediği veya bulamadığı bir şeyi, ona sert bir tavırla göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6024, 'gözüne uyku girmemek', 'uyuyamamak, uykusuz kalmak: ''Uykum kaçınca aklım bir şeye takılır ve o takıntıyı savuşturuncaya kadar gözüme uyku girmez.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6025, 'gözünü ağartmak', 'gözlerini belertmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6026, 'gözünü alamamak', 'bir şeye, bir yere bakmaktayken, gözünü oradan başka bir yere çevirememek: ''Sermet Bey, gözünü köşkten alamıyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6027, 'gözünü daldan budaktan (çöpten) esirgememek (sakınmamak)', 'tehlikeli işlere atılmaktan çekinmemek: ''Gençliğinde gerçekten delifişek, gözünü daldan budaktan sakınmaz bir askermiş.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6028, 'gözünü doyurmak', 'bol bol vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6029, 'gözünü dört açmak', 'aldanmamak için çok uyanık bulunmak: ''Hop diye giriyoruz, gözünüzü dört açın, tongaya basmayın.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6030, 'gözünü (gözlerini) açmak', '1) uyanmak; 2) kendine gelmek, ayılmak: ''Eczacının yaptığı bir adrenalin iğnesinden sonra gözlerini açtı.'' -H. Taner. 3) uyanık, dikkatli bulunmak: ''Gözünü aç da kâğıdı kaptırma.'' -S. Ali.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6031, 'gözünü (gözlerini) (bir şeye) dikmek', 'dikkatle bakmak, gözünü ayırmadan bir yere veya bir kimseye bakmak: ''O sert bir tavır alıyor, gözlerini Ali Rıza Bey''in gözlerine dikerek adamcağızı büsbütün şaşırtıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6032, 'gözünü (gözlerini) duman bürümek', '1) hayale dalmak, dalgınlaşmak: ''Gözlerini de bir duman bürüyor, başını yana çevirerek uzaklara bakıyordu.'' -R. N. Güntekin. 2) hüzünlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6033, 'gözünü (gözlerini) kan bürümek', 'adam öldürecek kadar öfkelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6034, 'gözünü (gözlerini) kapamak', '1) ölmek: ''Fakat o gözünü kapayınca başsız kalan konak birdenbire karışmış.'' -R. N. Güntekin. 2) görmezden gelmek: ''Dünün kurumları ile birlikte güzellik ölçüleri, değerleri de değişiyor, biz bunlara gözlerimizi kapamak istiyoruz.'' -N. Ataç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6035, 'gözünü (gözlerini) kırpmadan', 'çekinmeden, korkusuzca: ''Bu yüzden gözlerini kırpmadan cinayet işleyebiliyorlar.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6036, 'gözünü (gözlerini) oymak', 'çok kötülük etmek: ''Pembe Teyzenin niyeti bozuk fakat babama göz atarsa gözünü oyacağımı dobra dobra söyledim.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6037, 'gözünü gözüne dikmek', 'başkasının gözüne sürekli olarak bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6038, 'gözünü hırs bürümek', 'aşırı hırslanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6039, 'gözünü ... hırsı bürümek', 'bir şeyi aşırı ölçüde istemek: ''İnsanın gözünü hırs, para hırsı bürümeye görsün!'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6040, 'gözünü kapamak', '1) ölmek: ''Fakat o gözünü kapayınca başsız kalan konak birdenbire karışmış.'' -R. N. Güntekin. 2) görmezden gelmek: ''Dünün kurumları ile birlikte güzellik ölçüleri, değerleri de değişiyor, biz bunlara gözlerimizi kapamak istiyoruz.'' -N. Ataç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6041, 'gözünü karartmak', 'bir işe atılırken hiçbir şeyden çekinmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6042, 'gözünü kin bürümek', 'intikam alma duygusundan başka bir şeye önem vermemek: ''Gözünü kin bürümüş, doğruyu eğriyi seçemiyor, kurunun yanında yaşı da yakacak.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6043, 'gözünü sevda (aşk) bürümek', 'ondan başka hiçbir şeyi düşünmemek, tamamen ona bağlanmak: ''Senin gözünü sevda bürümüş, bey, dedi. Sen bir İzmir''e git de gönlünü eğle!'' -S. Ali.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6044, 'gözünü sevdiğim', 'okşamalık olarak kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6045, 'gözünü seveyim', ' tkz. birinden bir şey isteneceği zaman kullanılan söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6046, 'gözünü toprak doyursun', 'kendinden olan veya kendisine verilen şey ne kadar çok olursa olsun, bununla yetinmeyenler için söylenen bir ilenme sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6047, 'gözünü üstünden ayırmamak', 'sürekli denetim altında bulundurmak: ''Buna rağmen, bir şey yakalamak ümidiyle gözünü üstünden ayırmadığını hissediyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6048, 'gözünü yıldırmak', 'gözünü korkutmak: ''Hem de oraya kadar sürüklenmek, hanlarda birçok para harcamak, günlerce işten güçten kalmak köylülerin gözünü yıldırır.'' -N. Nâzım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6049, 'gözünü yummak', '1) gözünü kapamak; 2) mec. ölmek: ''Atatürk, o zaman için çaresiz bir hastalıktan gözünü yumduğu sırada altmışına basmamıştı.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6050, 'gözünün bebeği gibi sevmek', 'çok sevmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6051, 'gözünün çapağını silmeden', 'sabahleyin uyanır uyanmaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6052, 'gözünün içine baka baka', 'cesaret ve soğukkanlılıkla.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6053, 'gözünün önünden geçmek', 'hatırlamak: ''Selma Hanım''ın salonlarında gördüğü tipler birer birer gözünün önünden geçti.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6054, 'gözünün önünden gitmemek', 'bir türlü unutamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6055, 'gözünün önüne gelmek', 'bir şeyi zihinde canlandırmak, tasarlamak, hatırlamak: ''Doğduğum köydeki çocukluğum, İstanbul''a gelişimiz, mektep, Avrupa. Hep gözümün önüne geldi.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6056, 'gözünün önüne gelmek', 'hatırlamak: ''Mine''nin parçalanmış bedeni gözlerimin önüne geliyor.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6057, 'gözünün önünü görmemek', 'sisten, pustan dolayı etrafını görememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6058, 'gözüyle görmek', 'bir olaya tanık olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6059, 'gözüyle (gözleriyle) tartmak', 'kim ve ne olduğunu anlamak için dikkatle bakmak: ''Beni gözleriyle tartarak önümden geçti, sonra geri döndü geldi, oturmakta olduğun tahta sıranın ucuna ilişti.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6060, 'gözyaşına boğulmak', 'çok ağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6061, 'gradosu düşmek', ' argo itibarı azalmak, derecesi düşmek: ''Kızda insanlığın ve her türlü kabiliyetlerinin gradosu seneden seneye düşerken, böyle sevginin aslındaki temizlikle devam etmesine imkân yoktu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6062, 'granit gibi', 'güçlü, dayanıklı, sert.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6063, 'gurbet çekmek', 'doğup yaşadığı yerleri özlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6064, 'gurbete çıkmak', 'doğup yaşanılan yerden uzaklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6065, 'gurbete (gurbet ele) düşmek', 'aile ocağından uzak bir yere gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6066, 'gurk etmek', 'tavuk kuluçkaya yatmak isterken veya yavrularını çağırırken ''gurk gurk'' diye ses çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6067, 'gurk olmak', 'kuluçkaya yatmaya hazırlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6068, 'gurka yatmak', 'tavuk civciv çıkarmak için yumurta üzerine oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6069, 'gurup etmek', 'güneş, batmak: ''Güneş kuru bir kütük ateşi gibi kımıldayan al alevler arasında gurup ediyordu.'' -A. H. Müftüoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6070, 'gurur duymak', 'gururlanmak: ''Bu acıya kendi sebebiyet verdiğini hissetmekten gurur duyuyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6071, 'gurura kapılmak', 'büyüklenmek, gururlanmak: ''Sataşmalarını artırıyor ve yersiz bir gurura kapılıyordu.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6072, 'gururuna ağır gelmek', 'kişiliğine zor gelmek, büyüklüğünün zedelendiğini düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6073, 'gururuna dokunmak', 'kişiliği zedelenmek, onuru kırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6074, 'gururunu ayakaltına almak', 'her türlü fedakârlığı göze alıp ödün vermek, ilkelerden vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6075, 'gururunu okşamak', 'yüzüne karşı değerlerini belirterek bir kimseyi duygulandırmak: ''Genç, güzel bir kızın kendisinden hoşlandığını görmek, gururunu okşuyor.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6076, 'güce sarmak', 'bir iş güç bir duruma gelmek, güçleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6077, 'gücü gücü yetene', 'haklılığa değil kaba kuvvete veya güce dayanılarak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6078, 'gücü kesilmek', 'kuvveti, takati azalmak: ''Yavaş yavaş gücüm kesiliyor, işte o zaman ağlamaya başlıyorum.'' -N. Eray.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6079, 'gücü yetmek', 'eldeki imkânlarla ancak altından kalkabilmek, üstesinden gelebilmek: ''Zaman zaman, şiirin ne olduğunu elimin erdiği, gücümün yettiği kadar anlatmaya çalıştım.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6080, 'gücüne koşmak', 'bir sorunun kolay çözümü varken onu güçleştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6081, 'güç gelmek', 'bir şeyin yapılmasında zorluk ve sıkıntı ile karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6082, 'güç mevkide kalmak', 'içinden çıkılması zor bir durumda bulunmak: ''Hemen kararını vermekten âciz olan Hasan ne kadar güç bir mevkide kalmıştı?'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6083, 'güçlüğü (güçlükleri) yenmek', 'bir güçlüğü, zorluğu ortadan kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6084, 'güçlük çekmek', '1) maddi açıdan sıkıntı içinde olmak; 2) mec. zorlanmak: ''Cellat bana bu aynanın evveliyatını anlattığında ona inanmakta güçlük çektim.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6085, 'güçlük çıkarmak', 'bir şeyin gerçekleşmesini engelleyici sebepler ileri sürmek: ''Ancak çoğu sansür görevlisi de rüşvet alabilmek için güçlük çıkarıyordu.'' -M. And.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6086, 'güçsüz düşmek', 'gücü yetmemek: ''Silahlarından birini elinden bırakmış, güçsüz düşmüştür.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6087, 'güdük kalmak', '1) büyüyememek, küçük, bodur kalmak; 2) mec. bitmemiş, sonuç vermemiş durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6088, 'gül dalından odun, beslemeden kadın olmaz', 'her şey, kendisinden beklenen görevi yapabilecek niteliklere sahip olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6089, 'gül dikensiz olmaz', 'iyi veya güzel olan her şeyin az çok sıkıntı veren bir yanı da bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6090, 'gül gibi', 'çok iyi, çok güzel: ''Herkes evinin önünü temizlesin, şehir gül gibi olur.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6091, 'gül gibi bakmak', '1) geçimini para sıkıntısı olmadan sağlamak; 2) iyi, temiz bakmak: Çocuğuna gül gibi bakıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6092, 'gül gibi geçinmek (yaşamak)', '1) çok iyi anlaşmak, geçinmek; 2) pek geniş olmayan bir imkânla rahat, sıkıntısız yaşamak: ''Allah bereket versin, gül gibi geçiniyorum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6093, 'gül üstüne gül koklamamak', 'bir sevgili üstüne bir ikincisini sevmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6094, 'güler misin, ağlar mısın!', 'hem gülünecek hem üzülecek nitelikteki şaşırtıcı olaylar karşısında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6095, 'gülerken ısırmak', 'iyilik yapar görünüp kötülük yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6096, 'gülle gibi', '1) çok ağır; 2) hâlsiz, yorgun argın: ''Ayakkabılarını giymeden gülle gibi çocukların yanına düştü.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6097, 'gülleri yarılmak', 'çok keyiflenmek: ''Kahpe karının neredeyse gülleri yarılacaktı.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6098, 'gülme komşuna, gelir başına', 'birinin başına gelen kötü bir durum senin de başına gelebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6099, 'gülmekten kırılmak (katılmak, yarılmak)', 'aşırı derecede gülmek: ''Ahali gülmekten kırılıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6100, 'gülü seven dikenine katlanır', 'insan sevdiği kimse veya sevdiği iş yüzünden gelecek sıkıntılara katlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6101, 'gülü tarife ne hacet, ne çiçektir biliriz', 'birinin uygunsuz özellikleri sayılırken bunların öteden beri bilindiğini anlatmak için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6102, 'gülüp geçmek', 'umursamamak, aldırış etmemek, üzerinde durmamak: ''Bizi şimdi böyle görse yine sadece gülüp geçer miydi?'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6103, 'gülüp oynamak (söylemek)', 'neşeli, sevinçli, keyifli, güzel vakit geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6104, 'güm güm atmak', 'kalp heyecanla çarpmak: ''Göğsünün nasıl güm güm attığını fark eder, ne olur diye meraka düşmekten kendini alamazdı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6105, 'güm güm etmek', 'derinden yankılı ses olmak, ses çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6106, 'güme gitmek', ' tkz. 1) anlaşılmamak: ''Ama sözleri motor gürültüsünün içinde güme gitti.'' -H. Taner. 2) boşu boşuna ölmek, hiç uğruna ölmek: ''Baktım ki güme gideceğim, yavaşça kayığın baş yanına gittim ve kendimi denize salıverdim.'' -Halikarnas Balıkçısı. 3) değeri anlaşılmadan yitip gitmek: ''Gelgelelim çağın sansürü, dine karşı çıkıyorsa Yunus''un nice imanlı şiiri güme gidecekti, demektir.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6107, 'güme götürmek', 'anlaşılmasını engellemek: ''Demagog, kelime oyunu içinde hakikati güme götüren bir hokkabazdır.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6108, 'gümleyip gitmek', ' argo beklenmedik bir zamanda ansızın ölmek: ''Bütün incelikleri titizlikle gözeten bir kadın olduğu için kırk altı yaşında gümledi gitti annem.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6109, 'gümrük koymak', 'engel olmak, kısıtlamak: ''Yalnız hareketlerime değil, sözlerime de gümrük koyacak.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6110, 'gümrükten mal kaçırır gibi', 'yangından mal kaçırır gibi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6111, 'gümüş sağ olsun, altın gidekosun', 'eldeki şey, elde edilmesi güç olan daha değerli bir şeyden üstün tutulmalı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6112, 'gün ağarmak', 'tan yeri aydınlanmak: ''Kalın perdenin ardında gün ağarmıştı.'' -Y. Atılgan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6113, 'gün almak', '1) bir iş görmek için ilgili kişiden bir gün ayırmasını istemek, randevu almak: Doktordan gün almam gerekir. 2) belirli bir yaşı bitirdikten sonra girdiği yaştan süre almak: Beş yaşından iki gün aldı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6114, 'gün atmak', ' hlk. 1) davayı ileri bir tarihe bırakmak; 2) güneş doğmak: ''Süleyman kâhya gün atıncaya kadar çadırların arasında dolaştı.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6115, 'gün batmak', 'güneş batmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6116, 'gün bugün', '1) bugün ne yapabilirsen kazancın odur; 2) bugün kim işbaşında, kim itibarda ise onun sözü geçer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6117, 'gün doğmadan kimliği söylenmez', 'bir iş iyice belli olmadan sonucu hakkında yargı yürütülemez, yarın ne gibi durumlar veya olaylar çıkacağını kimse bilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6118, 'gün doğmadan neler doğar', 'beklenmedik bir sırada umut verici durumlarla da karşılaşma imkânı vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6119, 'gün doğmak', 'sabah olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6120, 'gün eylemek', 'gün geçirmek: ''Çıksam yüksek bellere gün eylesem / Acep nazlı yâr duyar mı ola?'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6121, 'gün geçer, kin geçmez', 'aradan uzun zaman geçse de bir kimsenin başkasına karşı beslediği kin sönmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6122, 'gün geçirmek (öldürmek)', 'boş şeylerle vakit geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6123, 'gün gibi açık', 'çok açık, çok belli.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6124, 'gün görmemek', 'sıkıntı içinde yaşamak: ''Üçü de kocadan gün görmemişler, üçü de mazlum ve boynu bükük kadın...'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6125, 'gün güne uymaz', 'bir günün işleri, durumları, şartları başka bir gününkine uymaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6126, 'gün kavuşmak', 'güneş batmak, akşam olmak: ''Gün kavuşurken Handune''nin de hareket derecesi artmış.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6127, 'gün koymak', 'yapılacak bir iş için gün belirlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6128, 'gün ola harman ola', 'bir gün onun da zamanı gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6129, 'gün olur yılı besler, yıl olur günü beslemez', 'öyle zaman olur ki bir aylık kazanç insanı bir yıl geçindirir, öyle zaman da olur ki bir yıllık kazanç bir ay geçindirmeye yetmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6130, 'gün saymak', 'herhangi bir iş veya olayın belirlenmiş süresinin sonunu heyecanla beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6131, 'gün varken davarını eve götür', 'işlerini en uygun zamanda yap.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6132, 'gün yemek', 'hapis cezası almak: ''Arkadaşım altı ay gün yedi.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6133, 'gün yüzü görmemek', '1) güneş ışığından uzakta kalmak, ışık görmemek; 2) mec. hiç kullanılmamak, yeni kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6134, 'gün yüzü görmemiş (söz, küfür)', '1) hiç kullanılmamış; 2) ortalığa çıkmamış; 3) çok ağır hakaret içeren.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6135, 'günah benden gitti (gitsin)', 'ben görevimi yaptım, bundan sonrası için sorumluluk kabul etmem.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6136, 'günah çıkarmak', '1) Hristiyanlar, Tanrı''nın bağışlaması için papaza gidip işlediği günahları anlatmak; 2) mec. kötü davranışlarını, suçlarını açıklamak, anlatmak: ''Sözlerinin ardında sitem vardı ama daha çok günah çıkarıyordu.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6137, 'günah işlemek', 'günah sayılan davranışta bulunmak: ''Bedia''yı terk edersem büyük bir günah işlemiş olacağım.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6138, 'günah olmak', 'yazık olmak: Bu mala bu kadar para vermek günah olur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6139, 'günaha girmek', 'dinî bakımdan suç sayılan bir iş yapmak: ''Ben bunu kitaplıkta saklayarak günaha giriyorum.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6140, 'günaha sokmak', 'günah işlemesine yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6141, 'günahı (günahı vebali) boynuna', '''ben karışmam, sorumluluk sana veya ona düşer'' anlamında kullanılan bir söz: ''Günahı boynuna, doping de yapıyormuş.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6142, 'günahı kadar sevmemek', 'sevmemek, nefret etmek: ''Kışın çok karlı, tipili günlerinden başka günlerini günahı kadar sevmezdi.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6143, 'günahını vermez', 'çok cimri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6144, 'güncelliğini korumak', 'önemini sürdürmek, yitirmemek: ''Bu sorun hâlâ bütün güncelliğini korumaktadır.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6145, 'güncelliğini yitirmek', 'süre aşımına uğrayarak önem ve değerini yitirmek: ''Güncelliklerini yitirdikçe ölen o yazılar gibi şiirler de ölür.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6146, 'gündeliğe gitmek', 'günlük işler yaparak gelir sağlamak: ''Kör Mustafa bahçelerde çalışır, gündeliğe gider, sarnıç sıvar, dam aktarır, kuyu kazar.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6147, 'gündeme almak', 'bir kurul toplantısında görüşülecek konuları bir listeyle belirlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6148, 'gündeme getirmek', '1) bir toplantıda bir konuyu tartışmak, görüşmek için önermek; 2) mec. bir konuya güncellik kazandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6149, 'gündüz külahlı, gece silahlı', 'gerçekte iyi olmadığı hâlde iyi gibi görünen kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6150, 'gündüzün mum yakan geceleyin bulamaz', 'her şey gerektiği yerde ve zamanda harcanmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6151, 'güne göre kürk giyinmek gerek', 'kılık kıyafetimizi zamanın koşullarına uydurmalıyız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6152, 'güneş açmak', 'güneş bulutlardan sıyrılıp görünmek: ''Batum''da yağmur kırk gün kırk gece yağsa da güneş bir açtı mı, çakıl taşı döşeli sokaklar saniyesinde kuruyuverir.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6153, 'güneş balçıkla sıvanmaz', 'herkesin bildiği gerçek inkâr edilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6154, 'güneş batmak', 'gün sonunda, güneş ufukta kaybolmak: ''Akşam iyice yaklaşmış, güneş batmaya yüz tutmuştu.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6155, 'güneş çarpmak', 'sıcak havada güneş altında çok kalmaktan hasta olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6156, 'güneş çavmak', ' hlk. güneş yayılmak, güneş doğmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6157, 'güneş doğmak', 'sabahleyin güneş ufuktan yükselmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6158, 'güneş girmeyen eve doktor girer', 'güneşsiz evde hastalık eksik olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6159, 'güneş görmek', 'güneş ışığından yararlanır durumda olmak: ''Balık beslenen havuz mutlaka güneş görmelidir.'' -İ. H. Baltacıoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6160, 'güneşe karşı işemek', ' kaba saygı gösterilmesi gereken şeylere saygısızlık göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6161, 'güneşi üzerine doğdurmamak', 'güneş doğmadan önce yataktan kalkmak: ''Ömrübillah güneşi üzerine doğdurmamış olmakla övünüyor.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6162, 'güneşin alnında (altında)', 'güneşin yakıcı ışınları altında.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6163, 'günlerden bir gün', 'herhangi bir gün, önceden belli olmayan bir gün, vaktiyle: ''Günlerden bir gün bu güzel gemilere binme nasip oldu.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6164, 'günleri gece olmak', 'çok kederlenecek bir durum içinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6165, 'günleri sayılı olmak', '1) ölümü yakın olmak; 2) bir yerde kalmak için ancak birkaç günü bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6166, 'günlük güneşlik görünmek', 'sıkıntısız, sorunsuz, huzur ortamında bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6167, 'günlük tutmak', 'her gün yaşananları, olayları ve anıları bir deftere yazmak: ''Şimdiye kadar günlük tutmadım, olanı biteni kaydetmediğim için birçok şeyi unuttum.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6168, 'günü dolmak', '1) önceden belirlenmiş bir süreyi tamamlamak; 2) ömrünü tamamlamak, eceli gelmek: ''Benim tavukların günü daha dolmamışsa suçlu olan ben miyim?'' -Z. Selimoğlu. 3) hamilelikte çocuğun olması gereken süreyi tamamlamak, doldurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6169, 'günü gününe uymaz', 'her zaman aynı durumda bulunmaz, kararsız.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6170, 'günü kurtarmak', 'günün ağır koşullarını ve engellerini bir biçimde atlatmak: ''Gelecek insanın mutluluğu için günümüzü kurtarmak hangi babayiğidin harcıdır?'' -M. C. Anday.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6171, 'günü yetmek', '1) ölüm zamanı gelmek; 2) gebe için doğum vakti gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6172, 'gününü doldurmak', 'bir işin sona ermesi için gereken süreyi tamamlamak: ''Hele günümü doldurup çıkayım, ben ona gösteririm. Onu gebertmezsem anam avradım olsun, derdi.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6173, 'gününü görmek', '1) kötü bir sonla karşılaşmak, cezaya çarptırılmak; 2) çocuklarının iyi, mutlu günlerini görmek; 3) aybaşı görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6174, 'gününü göstermek', 'tehdit yollu cezalandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6175, 'gününü gün etmek', 'hiçbir şeyi dert edinmeyip gününü hoş geçirmek: ''Sevmek, sevilmek, eğlenip yan gelmek, çubuğunu yakıp gününü gün etmek mi?'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6176, 'gününü (günlerini) saymak (beklemek)', 'kurtulamayacak hasta son günlerini yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6177, 'güreş etmek (tutmak)', 'güreşmek: ''Daha bir hafta evvel koruda güreş ederek onu yere yıkmıştı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6178, 'gürleyip gitmek', 'beklenmedik bir zamanda ansızın ölmek: ''Huri''nin anası, doğurduktan sonra bir tifo hastalığında gürleyip gitmişti.'' -N. Nâzım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6179, 'gürültü bastırmak', 'gürültüden daha güçlü ses çıkarıp onu etkisizleştirmek: ''Barın bütün gürültüsünü bastıran kahkahaları bundan sonra başladı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6180, 'gürültü çıkarmak (etmek, koparmak, yapmak)', '1) düzensiz ve rahatsız edici sesler çıkarmak: ''Karanlıkta bana çarpıp da gürültü yapmamaya dikkat ederek kapıyı açtım.'' -H. C. Yalçın. 2) kavga, karışıklık, tartışma çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6181, 'gürültü çıkmak', 'kavga, tartışma, karışıklık olmak: ''Bir gürültü çıkarmadan buradan gidiniz...'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6182, 'gürültü istemeyen kazancı (bakırcı) dükkânına girmez (hırkasını başına çeker)', 'kafasını dinlemek isteyen kimse, gürültülü patırtılı işlerde görev almaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6183, 'gürültüye gelmek', 'bir iş, bir düşünce vb. telaş ve karışıklığa rastlayarak ilgi çekmemek, üzerinde durulmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6184, 'gürültüye getirmek (boğmak)', '1) bir işi, bir düşünceyi telaş ve karışıklık yüzünden ilgi çekmez duruma getirmek; 2) söz kalabalığından, karışıklıktan yararlanarak istediğini elde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6185, 'gürültüye gitmek', 'telaş ve karışıklığa rastlayarak değeri anlaşılmayıp unutulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6186, 'gürültüye pabuç bırakmamak', ' tkz. patırtıya pabuç bırakmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6187, 'güttüğüm domuzu bana öğretme', 'yıllardır tanıdığım bir kimsenin huylarını da bilirim.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6188, 'güven duymak (beslemek)', 'güvenmek, inanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6189, 'güven kazanmak', 'kendisine inandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6190, 'güven vermek', 'güven duygusu uyandırmak, itimat telkin etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6191, 'güvence altına almak', 'koruma sorumluluğunu üstlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6192, 'güvence vermek', '1) bir anlaşmada taraflardan biriyle ilgili olarak sorumluluğu yüklenmek, inanca vermek, teminat vermek, garanti vermek; 2) bir sorumluluk karşılığı olarak para vb. ortaya koymak, inanca vermek, teminat vermek, garanti vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6193, 'güvenceye bağlamak', 'güvence altına almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6194, 'güvendiği dağlara kar yağmak (güvendiği dal elinde kalmak)', 'yardım ve yarar beklediği kimse, yer veya şeyden iyilik gelmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6195, 'güveni sarsılmak', 'güveni kalmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6196, 'güvenme (inanma) dostuna, saman doldurur postuna', 'dost sandığın birtakım kimseler sana kolaylıkla kötülük edebilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6197, 'güvenme varlığa, düşersin darlığa', 'varlıklarına güvenerek ölçüsüz harcamalarda bulunanlar daha sonraları sıkıntıya düşebilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6198, 'güvenmelik vermek', 'bir kimseye pazarlığında anlaşılmış bir paranın küçük bir bölümünü önceden vermek, kapora vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6199, 'güvenoyu almak', 'hükûmetin tutumu milletvekilleri tarafından onaylanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6200, 'güvenoyu vermek', 'hükûmetin tutumu ile ilgili olarak milletvekilleri tarafından olumlu oy kullanılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6201, 'güvensizlik duymak', 'güvenmemek: ''Dikkatle dinlemiyordu bu haberleri. Aksine gittikçe artan bir güvensizlik duyuyordu söylenen sözlere.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6202, 'güveyi girmek', '1) erkek, evlenmek: ''Kostüm yeni, potinler yeni, gömlek yeni. Güveyi mi giriyorsun çapkın?'' -P. Safa. 2) iç güveyisi girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6203, 'güzel bürünür, çirkin görünür', 'güzeller nazlı olur, çirkinler ise kendilerini beğendirmeye çalışırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6204, 'güzele bakmak sevaptır', 'güzele bakarken Tanrının neler yarattığını düşünür ve büyüklüğünü anlarız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6205, 'güzele kırk günde doyulur, iyi huyluya kırk yılda doyulmaz', 'değerli olan yüz güzelliği değil huy güzelliğidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6206, 'güzele ne yakışmaz (yaraşmaz)', 'güzel ne giyse yakışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6207, 'güzeli herkes sever', 'bütün insanlar güzellere ve güzel olan şeylere karşı sevgi beslerler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6208, 'güzellerin talihi çirkin olur', 'güzeller kendilerine yaraşan bir yaşayışı her zaman bulamadıkları için mutsuz olurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6209, 'güzellik ondur, dokuzu dondur', 'güzelliğin büyük bir kısmı giyimle sağlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6210, 'ha babam de babam', 'durmaksızın, sürekli.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6211, 'ha babam (ha)', '1) karşısındakinin çabasını artırmak için kullanılan bir söz; 2) sürekli olarak, durmadan. ''Yirmi iki delikanlı kan ter içinde ha babam ha koşuyorlar.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6212, 'ha bire', 'durmadan, ara vermeden, arka arkaya, sürekli olarak: ''Etrafında, bir kolayını bulup dışarıya sızanlardan birkaç kişi ha bire ellerinden öpüyor.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6213, 'ha bugün ha yarın', 'neredeyse, kısa bir süre içinde: Ha bugün ha yarın gelecek diye bekliyorlar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6214, 'ha deyince', 'istenilen anda.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6215, 'ha gayret', 'kuvvet vermek, cesaretlendirmek, yardım etmek için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6216, 'ha Hoca Ali ha Ali Hoca', 'değişik gibi gösterilen iki şeyin, gerçekte aynı olduğunu anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6217, 'ha şöyle', 'yapılan bir işin beğenildiğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6218, 'ha şunu bileydin', ' tkz. ''bunu çoktan anlaman, bilmen gerekirdi'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6219, 'habbesi kalmadı (yok)', '''kalmadı, bitti, tükendi'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6220, 'habbeyi kubbe yapmak', 'önemsiz bir şeyi abartmak: ''Arkadaşım İrfan''ın habbeyi nasıl kubbe yaptığını çok iyi bilirim.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6221, 'haber almak', 'kendisine bildirilmek, öğrenmek, bilgi edinmek: ''Sizden haber almayalı bir seneden fazla oldu.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6222, 'haber atlamak', 'gazetecilikte bir haberi vaktinde yayımlayamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6223, 'haber çıkmamak', 'biri veya bir şey için beklenen bilgi gelmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6224, 'haber geçmek', 'teleks, telefon vb. ile bilgi iletmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6225, 'haber göndermek', 'herhangi bir araçla bildirmek: ''Kayıkları olmayanlar mahalledeki en alışık oldukları kira sandallarına haber gönderirler.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6226, 'haber patlatmak', 'çok önemli bir haberi ilk kez açıklamak: ''Bu haberi patlatacak olan gazete en az bir hafta gündemi belirlemiş olacak.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6227, 'haber salmak (yollamak)', 'haber göndermek: ''Ben bu sevdadan vazgeçmez iken / Gizli gizli haber salıp durmasın'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6228, 'haber uçurmak', 'gizlice haber göndermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6229, 'haber vermek', '1) bildirmek, haber ulaştırmak: ''O evlerin ısıtılacağını, akşama sıcak yemek yapılacağını, evlerin ıssız olmadığını haber verirdi.'' -A. Kutlu. 2) bir durumun, bir olayın belirtisi olmak: ''Günlerden beri artan iştahsızlık ve derin yorgunluk fena günlerin yaklaştığını haber vermiş olabilirdi.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6230, 'haberden haber vermek', ' tkz. bir kimse veya bir konuda bilgi istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6231, 'haberi olmak', 'bilgisi olmak, bilmek: ''Annesinin bir şeyden haberi olmadığı için hemen söze karıştı.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6232, 'haberin olsun!', 'birine herhangi bir konuda uyarıda bulunmak için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6233, 'haberli olmak', 'öğrenmiş olmak, haber almış bulunmak: ''En yeni teknolojik bilgilerden haberli oluyorlar.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6234, 'hacamat etmek', '1) hacamat yoluyla kan almak; 2) argo hafifçe yaralamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6235, 'hacet dilemek', 'istekte bulunmak: Artık ne hacet dilese, ne murat etse oluyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6236, 'hacet görmek', '1) gerekli bulmak, gerekli saymak: ''Kendi kuvvetlerini ve yiğitliklerini söylemeye, vaka ile tespit etmeye hacet görmüyorlar.'' -H. E. Adıvar. 2) tuvalete gitmek; 3) alışveriş yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6237, 'hacet kalmamak', 'gereği olmamak: ''Lakin zora hacet kalmadı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6238, 'hacet yok', '''gerekliği yok, gerekli değil, istemez'' anlamında kullanılan bir söz: ''Hiç üzülmeyin, yemin etmenize de hiç hacet yok.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6239, 'hacetini yapmak (görmek)', 'küçük veya büyük abdestini yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6240, 'hacı hacı olmaz gitmekle Mekke''ye, dede dede olmaz gitmekle tekkeye', 'bir işi görünüşte ve biçimsel olarak yapmakla o iş gerçekten yapılmış ve sonuç elde edilmiş olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6241, 'hacı hacıyı Mekke''de, derviş dervişi tekkede bulur', 'hedefleri, amaçları aynı olanların yolları aynı noktada kesişir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6242, 'hacıağalık etmek', 'gereksiz yere, gösteriş için bol para harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6243, 'hacir altına almak', '1) kısıtlamak: ''Mümkün olduğu kadar uzun zaman devam etmesi için onu âdeta hacir altına almıştık.'' -R. N. Güntekin. 2) huk. hastalık, bunama vb. sebeplerden dolayı davranışlarının nasıl sonuç vereceğini bilemeyen bir kişiyi mahkeme aracılığıyla mal ve mülk yönetimi bakımından kısıtlamak; 3) huk. Medeni Kanun''a göre çeşitli haklarını kullanmaya yetkili olan kişinin bu haklarını mahkeme kararı ile elinden almak, haklarını kullanma bakımından kısıtlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6244, 'haç çıkarmak', 'Hristiyanlar, sağ ellerini alın, karın, iki omuz başı ve göğüs hizasına götürerek haç biçiminde tapınma işaretini yapmak, istavroz çıkarmak: ''Beraber eski kilise harabesine girdiler, kadın burada haç çıkardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6245, 'haddeden geçirmek', '1) madenleri tel durumuna getirmek için haddeyi kullanmak; 2) mec. en küçük ayrıntısına kadar incelemek, dikkatle araştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6246, 'haddi hesabı yok', 'sayılamayacak kadar çok, sınırsız, ölçüsüz: ''Çocuklara yemiş getirenin haddi hesabı yok.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6247, 'haddi mi (haddine mi düşmüş)', '''onun bunu yapmaya yetkisi veya yeteneği yoktur'' anlamında kullanılan bir söz: ''Haddine mi düşmüş senin, saçımın teline bile ulaşamazsın.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6248, 'haddikifayeyi bulmak', 'yeterince olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6249, 'haddini aşmak', 'ölçüyü kaçırmak, aşırı gitmek: ''Elverir ki insanı insan eden bu kuvvet, haddini aşmasın ve delilik çapına varmasın.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6250, 'haddini bilmek', 'kendi değer ve yeteneğini olduğundan üstün görmemek: ''Kişi haddini bilmeli de kendine yakışacak sevdalara düşmeli.'' -N. Ataç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6251, 'haddini bilmeyene bildirirler', 'çevresindekileri hiçe sayarak yetkili olmadığı konularda yüksekten atanlara sert karşılıklarla gereken dersler verilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6252, 'hadım etmek', '1) kısırlaştırmak; 2) mec. köreltmek, önemini azaltmak: ''Bugün Batı dünyasında sanatı, tüketim toplumu modelinin yararına olmak üzere hadım etme çabalarına rastlanmakta.'' -A. Cemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6253, 'hadi canım sen de', 'haydi canım sen de.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6254, 'hadi oradan', 'haydi oradan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6255, 'hadise çıkarmak', 'olay çıkarmak: ''Gürültü etmeden, iz bırakmadan, hadise çıkarmadan çalışıyorlar, arılar gibi.'' -E. M. Karakurt.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6256, 'hafakanlar basmak (boğmak)', 'sıkıntıdan bunalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6257, 'hafızayı yoklamak', 'hatırlamaya çalışmak: ''Hafızamı yokluyorum, bu imza ile karşılaştığım gün, yirmi yılın gerisinde.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6258, 'hafif atlatmak', 'kötü bir durumdan çok az bir zararla kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6259, 'hafif gelmek', '1) ağırlığı fazla olmamak: ''Çok hafif geldiği için düvene ağır bir taş oturtmuşlardı.'' -R. Enis. 2) mec. önemsiz görmek, değer verilmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6260, 'hafife almak', 'küçümsemek, önemsememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6261, 'hafiflik etmek', 'yakışıksız bir davranışta bulunmak veya söz söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6262, 'hafiften almak', 'önemsiz bulup üzerine düşmemek, yeterince ilgilenmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6263, 'hafta sekiz, gün dokuz', '''tedirgin edercesine sık sık'' anlamında kullanılan bir söz: O, hafta sekiz, gün dokuz bizdedir!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6264, 'hah şöyle', 'ha şöyle: Hah şöyle, biraz kendini göster!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6265, 'hak deyince akan sular durur', 'bir anlaşmazlıkta adalet, tarafsızlık, hakkaniyet devreye girdiğinde kimsenin söyleyecek sözü kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6266, 'hak etmek', '1) bir emek karşılığı hakkı olan şeyi elde etmek, hak kazanmak: ''Mutlu, başarılı, kendine güvenmeyi hak etmiş birisi.'' -T. Buğra. 2) layık olduğu kötü karşılığı almak; 3) bir başarı dolayısıyla ödüllendirilmek: ''Kadın dergileri bizi göklere çıkarıyorlardı, bunu da hak etmemiştik.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6267, 'Hak getire', '''yoktur, bulunmaz, ne arar'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6268, 'hak (hakkını) yemek', 'başkalarının hakkını vermemek: ''Hem benden haber bekleyen okuyucularımın hakkını yiyor, öteki genç okuyucularımın kalbini kırıyorum.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6269, 'hâk ile yeksan etmek (olmak)', '1) yapı, şehir vb. için temelinden yıkıp harap etmek, bütünüyle ortadan kaldırmak veya kalkmak; 2) yapı, şehir vb. için temelinden yıkıp harap olmak, bütünüyle ortadan kaldırmak veya kalkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6270, 'hak kazanmak', 'emeğin karşılığını alabilecek duruma gelmek: ''Senin kadar kimse kendi vatanına sahip olmaya hak kazanamamıştır.'' -A. H. Müftüoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6271, 'hak söz ağıdan acıdır', 'eksikleri, yanlışları, yolsuzlukları bütün çıplaklığıyla ortaya koyan ve eleştiren söz, bu işi yapanlara acı gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6272, 'hak yerde kalmaz', 'emeğin karşılığı daima ödenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6273, 'hak yerini bulur (yerde kalmaz)', 'haksızlık er geç ortaya çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6274, 'hakaret görmek', 'ağır veya küçültücü davranış görmek, aşağılanmak: ''Hakkı da var, tecavüze uğramayan, hakaret görmeyen kalmıyor.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6275, 'hakaret saymak', 'bir sözü veya davranışı hakaret olarak kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6276, 'hakikatsiz çıkmak', 'yakınlığı ve bağlılığı sürekli olmamak: Dost bildiğim insan hakikatsiz çıktı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6277, 'hakir görmek', 'önemsememek, değer vermemek, küçümsemek, küçük görmek, hor görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6278, 'Hakk''ın rahmetine kavuşmak (Hakk''a kavuşmak, Hakk''a yürümek)', 'ölmek: ''Hüsmen Hakk''a kavuştu diye mırıldandı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6279, 'hakkı geçmek', '1) birinin payından başkası almış olmak; 2) birinde veya bir şeyde emeği olmak: ''Hemen hanım teyzemin elini öpmeye gideyim, dedim. Az hakkı mı geçmiştir bana?'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6280, 'hakkı olmak', '1) payı, alacağı, hissesi olmak; 2) sözünde, düşüncesinde, iddiasında haklı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6281, 'hakkı ödenmemek', 'birinin iyiliklerine, emeklerine karşılık olarak ne yapılsa az olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6282, '(...) hakkı tanımak', 'izin vermek: ''Saliha, anneye çocuğunu haftada iki kere görme hakkı tanıyan kararı yazdırıyor.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6283, 'hakkı var', '''doğru düşünüyor, doğru söylüyor, doğru davranıyor'' anlamında kullanılan bir söz: ''Hakkınız var; dağ, çöl ve deniz hasreti dinmez hasretlerdenmiş.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6284, 'hakkından gelmek', '1) zor bir işi başarı ile sona erdirmek: ''Büyük kızı kocaya kaçtığı zaman küçükleri on iki dönüm tarlanın hakkından gelecek kadar yetişkindiler.'' -N. Cumalı. 2) yenmek, öç almak veya cezasını vermek: ''Anlaşılan Cemal Paşa''nın bu işe yarar bir adamı yok, bize bıraksın, haklarından gelelim, dediler.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6285, 'hakkını aramak', 'hakkı olduğuna inandığı şeyi elde etmeye çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6286, 'hakkını helal etmek', 'hakkını, emeğini bağışlamak: ''Bu bahtiyar hanımcağızı sordular, iyi biliriz, dedik, hakkımızı helal ettik.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6287, 'hakkını vermek', '1) gereğini bütün olarak yerine getirmek: Bu yemeğin hakkını vermişsin. 2) birinin çalışmasının karşılığını gereğince değerlendirmek: O öğretmen, öğrencilerin her zaman hakkını verir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6288, 'haklı bulmak', 'davasını, iddiasını, düşüncesini, davranışını doğru bulmak, yerinde görmek: Müdür onu haklı buldu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6289, 'haklı çıkmak', 'davasının, iddiasının, düşüncesinin veya davranışının doğru olduğu anlaşılmak: Bu tartışmada o haklı çıktı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6290, 'haklı söz, haksızı Bağdat''tan çevirir', 'doğru söz, yanlış yolda çok ilerlemiş kişiyi bile yola getirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6291, 'haksız bulmak', 'bir iddiayı, düşünceyi, davranışı doğru ve yerinde bulmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6292, 'haksızlığa uğramak', 'adalete aykırı bir duruma düşmek, haksızlıkla karşılaşmak: ''Gücenik, haksızlığa uğramaktan bezmiş gibi susuyor.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6293, 'hâl hâlin yoldaşıdır', 'aynı durumdaki kimseler, birbirlerinin hâlini daha iyi anlarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6294, 'hâl hatır (hâlini hatırını) sormak', 'bir kimseye ''nasılsınız, ne durumdasınız'' anlamında nezaket sorusu yöneltmek: ''Karşılıklı oturdular, hâl ve hatır sordular, sonra sustular.'' -R. H. Karay. ''Rapor almışsa, çiçekler, kolonyalar getirir, hâlimizi hatırımızı sorar, moral verir.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6295, 'hâlâ o masal', '''hep aynı söz, aynı düşünce, davranış veya sorun'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6296, '(halatı) yısa etmek', 'çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6297, 'halay çekmek (tepmek)', 'halay oyunu oynamak: ''Erkekler dışarıda halay çekip tabanca atarken kadınlar Zekiye''yi getirip ortaya oturttular.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6298, 'halayıktan kadın olmaz, gül ağacından odun', 'her şey, kendisinden beklenen görevi yapabilecek niteliklere sahip olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6299, 'hâlden anlamak (bilmek)', 'bir kimsenin içinde bulunduğu güç durumu anlayarak sezip anlayış göstermek: ''Kız hâlden anlamış olacak ki iki kere daha ikramda bulundu.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6300, 'hâle yola koymak', 'iyi bir düzen vermek, tertiplemek: ''Ben avukatımla Baba meselesini bir hâle yola sokmaya uğraşırken Hacı Ömer ile Müftü arasında epeyce şiddetli bir kavga çıktı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6301, 'halebi orada ise arşın burada', 'bir iddia veya söz abartılı bulunduğunda kanıtını istemek için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6302, 'halef selef olmak', 'biri ötekinin makamını almak, yerine geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6303, 'halel gelmek', 'bozulmak, zarara uğramak: ''İsterdim ki saçlarının rengine, dişlerinin parıltısına ve gözlerinin güzelliğine halel gelmemiş olsun.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6304, 'halel getirmek', 'zarar vermek: ''Son nefesine kadar devlet adamı saygınlığına halel getirmeyen böyle bir metanet örneği olmuştu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6305, 'halel vermek', 'bozmak, sarsmak: ''Yeni mahalleler ayrı yerlerde şehrin tarihî kıymetine halel vermemek üzere inşa olunmaktadır.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6306, 'haleldar etmek', 'bozmak, sarsmak: ''Haysiyetli bir şahsiyetin şeref hakkı haleldar edilemez.'' -M. C. Anday.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6307, 'halı altına süpürmek', 'çözümlenemeyen sorunların görüşülmesini ertelemek, gözden uzak etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6308, 'hâli (hâlleri) duman olmak', ' argo kötü duruma düşmek: ''Anası da artık eskisi gibi çamaşıra falan gidemediğinden hâlleri dumandı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6309, 'hâli harap olmak', 'bitkin, perişan olmak, kötü duruma düşmek: Sınıfı geçmezse hâli haraptır.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6310, 'hâli kalmamak', 'gücü, takati, eski durumu olmamak: ''Ama nasıl kurtulacaktı? Kuvveti bitmiş, kımıldayacak hâli kalmamıştı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6311, 'hâli tavrı yerinde', 'durumu, görünüşü, davranışı düzgün.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6312, 'hâli üzere', 'olduğu gibi: ''Fakat bir zaman sonra tabiata karşı uğraşmanın nafileliğini anlayarak her şeyi hâli üzere bırakmıştı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6313, 'hâli vakti yerinde', 'paraca durumu iyi, zengin: Bu adamın hâli vakti yerinde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6314, 'hâline bakmamak', 'kendisinin ne durumda olduğunu düşünmeden gücünü aşan işlere kalkışmak: ''İhtiyar bunak, hâline bakmıyor da neler söylüyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6315, '... hâline gelmek', 'gibi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6316, 'hâline köpekler bile güler', ' tkz. çok kötü bir duruma düşenler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6317, '(...) hâlini almak', 'herhangi bir duruma gelmek: ''Bu hastalık korkusu onda, hayatı kendine zehreden tehlikeli bir psikoz hâlini almıştı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6318, 'halka inmek', 'halkın anlayışı ve görüşü düzeyinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6319, 'halka olmak', 'bir çember biçiminde dizilmek: ''Alevlerin etrafında halka olduk ve konuştuk.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6320, 'halka verir talkını (telkini), kendi yutar salkımı', 'kendisinin inanmadığı ve tutmadığı öğütleri başkalarına kolayca verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6321, 'hallaç pamuğu gibi atmak', 'toplu durumda bulunan kişi veya nesneleri darmadağın etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6322, 'hâllenip küllenmek', 'kendi imkânlarıyla iyi kötü geçinip gitmek, kendi yağıyla kavrulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6323, 'hallihamur olmak', 'içinde bulunduğu koşullara uymak: ''Suyun, toprağın, gözyaşının ve insan kanının hallihamur olduğu bu Anadolu toprağı susar mı?'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6324, 'halt etmek', ' tkz. uygunsuz bir söz söylemek, uygunsuz davranmak, uygunsuz bir iş yapmak: ''İşlerim var. Sen de peşime takıl benimle in, sonra ne halt edersen et.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6325, 'halt karıştırmak', ' tkz. halt etmek: ''Şu kendisine üç saniye gibi gelen bir saat on beş dakika zarfında ne halt karıştırmıştı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6326, 'halt yemek', ' tkz. halt etmek: ''On beş yaşında bu haltları yerse yirmi yaşına geldiği zaman ne yapacak?'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6327, 'halvet gibi', 'çok sıcak (yer, oda).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6328, 'halvet olmak', 'birisi veya birileriyle yalnız görüşmek amacıyla içeriye başkasını veya başkalarını almamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6329, 'ham çıkmak', 'kavun, karpuz olgunlaşmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6330, 'ham hum etmek', 'belirsiz, önemsiz, boş birtakım sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6331, 'ham hum şorolop', 'düzenle veya el çabukluğu ile yapılan, kimsenin akıl erdiremediği iş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6332, 'hamala semeri yük olmaz', 'insana kendi işi ağır gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6333, 'hamam gibi', 'pek sıcak: ''Bugün deniz hamam gibidir değil mi?'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6334, 'hamama giren terler', 'bir işe girişen, o işin güçlüklerini veya masraflarını göze almalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6335, 'hamamcı olmak', ' argo boy abdesti alması gerekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6336, 'hamamın namusunu kurtarmak', 'görünüşünü kurtarmaya yönelen birtakım yetersiz çarelere başvurarak kötü bilinen bir yere onur kazandırmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6337, 'hamle etmek (yapmak)', '1) atılmak, saldırmak: ''Sinir içindeki kadına o anda hamle etme aptallığını da yapmış ve tokadı yemiş.'' -R. Erduran. 2) önemli bir işe girişmek, bir işte başarı sağlamak için çaba harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6338, 'hamur açmak', 'yoğrulmuş hamuru inceltip yufka durumuna getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6339, 'hamur gibi', '1) yorgunluktan eli ayağı tutmayan; 2) çok pişip bulamaç durumuna gelen (yiyecek).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6340, 'hamur tutmak', 'hamur hazırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6341, 'han gibi', 'gereğinden çok geniş olan (yer).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6342, 'han hamam sahibi (olmak)', 'malı mülkü çok, varlıklı kimse (olmak).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6343, 'han kapısından teğelti atmak', 'defetmek, kovmak: ''Bir adamı hiç sormadan, etmeden böyle han kapısından teğelti atar gibi kolundan tutup fırlatınca içinde bir üzüntü kalır.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6344, 'hancı sarhoş yolcu sarhoş', 'kimin ne yaptığı ne ettiği belli değil.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6345, 'hangar gibi', 'çok büyük ve geniş (yer) anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6346, 'hangi akla hizmet ediyor?', 'ne gibi bir düşünce ile böylesine olmayacak, mantıksız bir iş yapıyor?', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6347, 'hangi dağda kurt öldü?', 'birisinden beklenmeyen bir davranış görüldüğünde şaşma ve sitem bildirmek için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6348, 'hangi gün vardır akşam olmadık', 'sona ermeyecek hiçbir iyi durum, yıldızı sönmeyecek hiçbir ünlü yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6349, 'hangi peygambere kulluk edeceğini şaşırmak', 'kimin sözünü yerine getireceğini bilemeyerek şaşkınlık içinde kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6350, 'hangi rüzgâr attı?', 'bir yere uzun süre uğramamışken beklenmedik bir zamanda gelenlere sitem yollu söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6351, 'hangi taş pekse (katıysa), başını ona vur', 'kendi kusurun yüzünden zor bir duruma düştüğünde kendini suçla ve başkalarından yardım isteme.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6352, 'hangi taşı kaldırsan, altından çıkar', '1) her işten anlar veya anladığı iddiasında bulunur; 2) her işe karışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6353, 'hanım kırarsa kaza, halayık kırarsa ceza', 'buyurucu durumunda olanların yaptığı yanlışlık hoş görülür, buyruk altındakilerin yaptığı yanlışlık suç sayılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6354, 'hani yok mu', 'dikkati arkadan gelen söze çekmek için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6355, 'hant hant ötmek', 'bir şeye aşırı istek duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6356, 'hanumanını yıkmak', 'ocağını yıkmak, evini barkını dağıtmak: ''Bu oğlan hanumanımı yıkar benim, derdi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6357, 'Hanya''yı Konya''yı anlamak (bilmek, görmek)', 'bir işin gerçek yönünü anlayarak aklı başına gelmek, akıllanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6358, 'hap etmek', 'yemek, yutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6359, 'hapı yutmak', ' tkz. kötü bir duruma düşmek: ''Gideceğimiz kasabada iki yazlık sinema varsa hapı yutmuşuzdur.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6360, 'hapis giymek', 'hapis cezasına çarptırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6361, 'hapis kalmak', '1) mahkûm olarak hapiste yatmak; 2) bir yere çıkamaz, gidemez durumda olmak: ''Bu köprünün yol vermeyişinden dolayı, Haliç''te yıllar boyu hapis kalan gemiler oldu.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6362, 'hapis yatmak', 'hükümlü olduğu süreyi hapishanede geçirmek: ''Adamcağız hem hapis yatacak hem dayak yiyecek.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6363, 'hapishane kaçkını gibi', 'kılık kıyafetine dikkat etmeyen (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6364, 'hapislerde çürümek', 'çok uzun süre hapiste kalmak: ''İşinden atıldığını, hapislerde çürüdüğünü, çocuklarının perişanlığını gördü.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6365, 'har vurup harman savurmak', 'Düşüncesizce ve hesapsızca harcamak, bol bol harcayıp tüketmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6366, 'har vurup harman savurmak', 'düşüncesizce ve hesapsızca harcamak, bol bol harcayıp tüketmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6367, 'haraç mezat satmak', 'açık artırma ile satmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6368, 'haraç yemek (almak)', 'başkasının sırtından geçinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6369, 'haram yemek', 'toplumun gelenek ve göreneklerine veya dinî kurallarına aykırı olarak bir şeyi kendi yararına kullanmak, sahiplenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6370, 'harama uçkur çözmek', 'nikâhsız olarak cinsel ilişkide bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6371, 'haramın temeli olmaz', 'haram kazanç, bir işe yaramadan telef olur gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6372, 'haramzade pazar bozar, helalzade pazar yapar', 'sütü bozuk kişi, iki kimsenin arasını açar, anlaşmalarına engel olur; soylu kişi ise arabuluculuk yapar, anlaşmalarına yardım eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6373, 'harap düşmek', 'kötü bir durumla karşı karşıya kalmak: ''Millet, fakruzaruret içinde harap ve bitap düşmüş olabilir.'' -Atatürk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6374, 'harar gibi', 'içine çok şey alabilen, geniş, büyük (eşya).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6375, 'hararet basmak', '1) çok susamak; 2) vücut ısısı artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6376, 'hararet kesmek (söndürmek)', 'susuzluğu gidermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6377, 'hararet vermek', 'susatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6378, 'harekete geçirmek (getirmek)', 'bir işin yapılmasına sebep olmak, kımıldatmak, canlandırmak: ''İlçelerinde ne kadar dernek varsa hepsini harekete geçirdiler.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6379, 'harekete geçmek', '1) bir işi yapmaya başlamak, bitirmek amacı ile bir işe girişmek: ''Saldırma için lazım gelen strateji planını tespit ederler ve ona göre harekete geçerlerdi.'' -Y. K. Karaosmanoğlu. 2) bir yerden bir yere gitmeye başlamak: ''Derken garp istikametinde küçüklü büyüklü muazzam bir bulut kütlesi harekete geçiyor.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6380, 'harem selamlık olmak', 'bir yerde kadın erkek ayrı oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6381, 'harf atmak', 'tanımadığı bir kadına uygunsuz sözler söyleyerek yaklaşmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6382, 'harı başına vurmak', '1) çok kızmak; 2) azmak, kendini tutamayacak duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6383, 'harı geçmek', 'kızgınlığı, sıcaklığı, hevesi, isteği veya öfkesi azalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6384, 'hariçten gazel okumak (atmak)', ' tkz. 1) bir konuyu iyice bilmeden üzerinde görüş ve düşünce ileri sürmek; 2) bir konuşmaya yersiz ve zamansız katılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6385, 'harikalar yaratmak', 'hayranlık uyandıracak başarılar kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6386, 'haritadan silinmek', '1) bir ülke, başka devletin egemenliği altına girmek: ''Koca Rumeli, Edirne''si, Selanik''i, Manastır''ı, Yanya''sı, Kosova''sı, İşkodra''sı ile imparatorluk haritasından silinmişti.'' -Y. Z. Ortaç. 2) bir yerleşim yeri savaş, deprem vb. bir olay sonunda yok olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6387, 'harman çevirmek', 'harmanlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6388, 'harman döven öküzün ağzı bağlanmaz', 'çalışanın emeğinin karşılığı verilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6389, 'harman dövmek', 'ekin tanelerini saptan ayırma işini yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6390, 'harman dövmek keçinin işi değil', 'önemli işler herkese yaptırılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6391, 'harman etmek (yapmak)', 'birçok çeşitten birer parça alıp yeni bir birleşim oluşturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6392, 'harman (harmanını) yakarım diyen orağa yetişmemiş', 'başkasına kötülük yapmayı tasarlayan kimse, kötülüğünü yapmaya fırsat bulmadan cezasını görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6393, 'harman savurmak', 'tahılı samandan ayırmak için dövülmüşünü rüzgâra karşı savurmak: ''Akşam vakti ırgatlarla beraber harman savururum.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6394, 'harman sonu dervişlerindir', 'herkesin bol bol yararlandığı şeylerin artakalanından alçak gönüllüler yararlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6395, 'harman yel ile, düğün el ile', 'her işin gerçekleşmesi birtakım koşulların bulunmasına bağlıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6396, 'harmanda dirgen yiyen sıpa, yılına kadar acısını unutmaz', 'uygunsuz davranışlarından dolayı cezalandırılanlar uzun süre aynı davranışı göstermezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6397, 'harmanı kaldırmak', 'harman işini bitirmek: ''Harmanı kaldırmaktan başka bir şey düşünmüyordu.'' -S. Çokum.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6398, 'harp açmak', '1) savaş açmak; 2) mec. bir konuda güçlü biçimde mücadele etmek, bir konuyu şiddetle savunmak: ''Gençler, kendi cinslerinden riyakârlara karşı harp açmalıdırlar.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6399, 'hart hurt etmek', 'korkutmak amacıyla sert ve yüksek sesle konuşmak: ''O bile, sağa sola hart hurt etmeye başlamış.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6400, 'hasara uğramak', 'zarar görmek, yıkılmak, harap olmak: ''Bir lokomotifle iki vagon hasara uğramışlar.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6401, 'hasbi geçmek', 'bir şeye önem vermemek, ilgi göstermemek, kısa kesmek: ''Aslına bakarsanız karı bana yıllar yılı güler, işaret ederdi de arkadaş karısı diye hasbi geçerdim.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6402, 'hasıraltı etmek', 'bir işi isteyerek, bilerek ve haksız olarak yürütmemek, örtbas etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6403, 'hasret çekmek', 'özlem duymak: ''Geçmiş günlere hasret çekmiyorum. Çocukluğumu göresim gelmedi.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6404, 'hasret gidermek', 'özleme son vermek, kavuşmak: ''Sonra ver elini ana baba ocağı. Hem hasret giderecektim hem de ruhumla dinlenecektim.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6405, 'hasret gitmek', 'özlemini çektiği, sevdiği bir yere veya kimseye kavuşamadan ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6406, 'hasret kalmak', 'özlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6407, 'hasta düşmek', 'hastalanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6408, 'hasta etmek', '1) hasta olmasına yol açmak; 2) mec. bezdirmek, bıktırmak, usandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6409, 'hasta ol benim için, öleyim senin için', 'kişi kendisi için bir fedakârlıkta bulunan kimseye karşı sırası geldiğinde daha büyük fedakârlıkta bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6410, 'hasta olmayan, sağlığın kadrini bilmez', 'insanlar sağlığın değerini ancak hastalıkta acı çekip iyileştikten sonra anlarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6411, 'hastalık almak (kapmak, hastalığa tutulmak)', 'bulaşıcı bir hastalığa yakalanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6412, 'hastalık kantarla girer, miskalle çıkar', 'hastalık birden ve çok zorlu gelir ama yavaş yavaş iyileşir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6413, 'hastalık sağlık bizim için', 'insan sağ, esen olabildiği gibi hasta da olabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6414, 'hastanelik etmek', 'birini aşırı derecede dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6415, 'hastanelik olmak', '1) hastanede tedavi görmeyi gerektirecek kadar hastalanmak: ''Şu son turnuvada dört futbolcu hastanelik olmuş.'' -H. Taner. 2) çok dayak yemek: Çıkan kavgada beş kişi hastanelik oldu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6416, 'hastaneye kaldırmak (yatırmak)', 'tedavi amacıyla hastaneye götürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6417, 'hastaya bakmaktan hasta olması yeğdir', 'ağır bir hastaya bakmak o denli güçtür ki, kimi zaman hasta olmak bundan daha kolay görünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6418, 'hastaya döşek sorulmaz', 'bir kişiye, onsuz yapamayacağı belli olan bir şeyin gerekli olup olmadığı sorulmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6419, 'haşa huzurdan (huzurunuzdan)', 'uygunsuz bir şey söylemek zorunda kalındığında bağışlanma dileği anlatan bir söz: Haşa huzurdan, o hayvan gibi davrandı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6420, 'haşa sümme haşa', '''öyle olmasına ihtimal yok, öyle değildir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6421, 'haşadı çıkmak', '1) bozulmak, işe yaramaz duruma gelmek; 2) çok yorulmak, bitkinleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6422, 'haşır neşir etmek', 'kaynaştırmak, bir arada bulundurmak: ''Bir rüzgâr gibi alıp bunların arasına atar, beni bunlarla haşır neşir ederdi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6423, 'haşır neşir olmak', 'kaynaşmak, bir arada bulunup uğraşmak: ''Her ikisi de vahşi hayvanlarla haşır neşir olur.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6424, 'hat çekmek', 'telefon, telgraf tellerini döşemek veya direklere germek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6425, 'hata etmek (eylemek, işlemek)', 'yanlışlık yapmak, yanılgıya düşmek: ''Batıla alkış tutanların karşısına geçip hata eylediğimi yeni yeni öğrenmiş bulunuyorum.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6426, 'hatasız kul olmaz', 'insan yanılmamak için ne kadar dikkat ederse etsin yine yanılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6427, 'hataya düşmek', 'yanılmak: ''Bu soruya evet cevabını vermekle bir hataya düşmüş sayılmayız.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6428, 'hatır almak', 'gönül almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6429, 'hatır eylemek', 'hatırlamak: ''Benim Orhan isminde bir tanıdığım olmadığından, başka bir nam altında bir nankörü hatır eylemiş olsan bile...'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6430, 'hatır gönül bilmek (saymak veya tanımak)', 'kişilere karşı gösterilmesi gereken saygı kurallarına uymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6431, 'hatır gönül yapmak', 'birini tutum ve davranışlarıyla mutlu etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6432, 'hatır gönül yıkmak (kırmak)', 'kişilere karşı gösterilmesi gereken saygı kurallarına uymamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6433, 'hatır (hatırını) saymak', 'gerekli saygıyı göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6434, 'hatır için çiğ tavuk yemek', 'bir kişiyi gücendirmemek için yapılması güç olan şeyleri bile yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6435, 'hatır sormak', 'hâl hatır sormak: ''Önce karşılıklı hatır sormakla başlayan konuşmaların ardından, tarlaların durumuna geçti.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6436, 'hatıra (hatır ve hayale) gelmemek', 'bir şeyin gerçekleşeceği, olacağı hiç düşünülmemek: ''Yemin, her hatır ve hayale gelmez cümlelerin ucunda bir kurdele, bir fiyonk gibi açılıveriyordu.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6437, 'hatırı sayılır', '1) oldukça çok: ''Adamları aracılığıyla bu konuda hatırı sayılır bir külliyata sahip oldu.'' -İ. O. Anar. 2) önemli, saygın, saygıdeğer: ''Sabit Bey Ağabey mahalle tulumbacıları arasında en hatırı sayılır adamlardandır.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6438, 'hatırına bir şey gelmesin', 'bir düşüncede, sözde veya davranışta kötü bir amaç güdülmediğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6439, 'hatırına gelmek', 'hatırlamak, aklına gelmek: ''İçeriyi dinlemediği hatırına geldi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6440, 'hatırına getirmek', 'hatırlamasına yol açmak: ''Tüfeğini omzuna vurup çapraz fişeklerini kuşanan bir kişinin ölümü hatırına getirmesi garip olmaz mı?'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6441, 'hatırında kalmak', 'unutmamak, hatırlamak: ''Birçok söz daha söylemişti. Hepsi hatırımda kalmamış olsa gerek.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6442, 'hatırında tutmak', 'unutmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6443, 'hatırından (hatır ve hayalinden) geçmemek', 'aklına gelmemek, düşünmemek: ''Herhangi bir devletin İstanbul''a taarruzu artık hatırından geçmiyordu.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6444, 'hatırını hoş etmek', 'sevindirmek, memnun etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6445, 'hatırını sormak', 'hâl hatır sormak: ''Herkes içten görünüyor, hatta yıldızımın hiç barışmadığı insanlar bile dostça elimi sıkıyor, hatırımı soruyorlar.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6446, 'hatiften gelmek', 'gaipten ses gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6447, 'hatim indirmek', 'Kur''an''ı başından sonuna kadar okuyup bitirmek, hatmetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6448, 'hatim sürmek', 'okunan Kur''an''ı, önündeki Kur''an''dan takip etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6449, 'hatime çekmek', 'son vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6450, 'hava açmak (açılmak)', 'bulutlar dağılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6451, 'hava atmak', 'herhangi bir üstünlüğünden dolayı şişinmek, caka yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6452, '(hava) ayaza çekmek', 'kışın kuru soğuk artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6453, 'hava basmak', '1) hava vermek; 2) argo büyüklenmek, gururlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6454, 'hava bozmak', 'havada yağmur, kar, dolu veya fırtına başlamak: ''Hava birden bozmuş, daha doğrusu poyraza çevirmişti.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6455, 'hava bulanmak', 'yağmur yağacak duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6456, 'hava çarpmak', 'iklim ve rüzgâr olumsuz etkilemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6457, 'hava değiştirmek', 'iklimi değişik bir yere gidip bir süre oturmak: ''Hekimleri Seniha''ya biraz yer ve hava değiştirmeyi, biraz kırlarda ve denizlerde gezip eğlenmeyi tavsiye ettiler.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6458, 'hava fena esmek', 'ortamla ilgili her türlü şart kötü durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6459, 'hava iyi esmek', 'ortamla ilgili her türlü şart uygun durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6460, 'hava kaçırmak', '1) nesneler için içindeki havayı tutamayıp dışarıya vermek; 2) yellenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6461, 'hava kapanmak', 'gökyüzü bulutlarla örtülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6462, 'hava kararmak', '1) güneşin batmasıyla ortalık kararmak: ''Hava iyice kararmış, caddenin bütün elektrikleri yanmıştı.'' -P. Safa. 2) gökyüzü iyice bulutlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6463, 'hava patlamak', 'fırtına çıkmak: ''Hava patlamışken, dalgalarla yumruk yumruğa boğuşan bir adamın yazgısını paylaştın mı?'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6464, 'hava vermek', '1) tekerlek vb. cisimleri hava ile şişirmek, şişkinliğini artırmak, hava basmak; 2) tıp akciğerlere basınç altında hava veya oksijen doldurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6465, 'hava yapmak', '1) kalorifer peteğinde sıvının yerine hava dolmak; 2) mec. böbürlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6466, 'havada kalmak', '1) yerden yüksekte bulunmak: Masanın bir ayağı kısa olduğundan havada kalıyor. 2) mec. sonuca ulaşmamak: ''Yine de bir öğretmenin iyi niyetinin, ilgisinin böyle havada kalışından acı duydu.'' -A. Ağaoğlu. 3) mec. bir iddia dayanaksız olduğundan kanıtlanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6467, 'havadan nem kapmak', 'en küçük bir şeyden alınmak, çok alıngan olmak: ''Burası, bir loca meydanı gibi, havadan nem kapmaya alışık bir çevreydi.'' -A. N. Karacan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6468, 'havalara uçmak', 'çok sevinmek: ''Buna pek sevinmişti, oğlum memur oldu diye havalara uçuyordu.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6469, 'havale gelmek', '1) postane veya banka yoluyla para gelmek; 2) gebe ve çocuklara çoğu zaman bayılma, yüksek ateşle beraber çırpınma krizleri gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6470, 'havan batsın', '''böbürlenmen boşa çıksın'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6471, 'havan dövücünün hınk deyicisi', '''başkasına yardım edecek veya yüreklendirecek gücü olmadığı hâlde öyle görünüp yardakçılık eden kimse'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6472, 'havanda su dövmek', 'boşuna uğraşmak: ''Havanda su dövmeyelim, önce namussuzu bulalım sonra bunları konuşalım.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6473, 'havanın gözü yaşlı', '''nerede ise yağmur yağacak'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6474, 'havasına uymak', '1) bulunduğu çevre ve ortamı benimsemek; 2) birinin huyunu almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6475, 'havasını almak', '1) kalorifer peteğinde oluşan havayı boşaltarak sıvı maddenin dolmasını sağlamak; 2) mec. birinin eli boş çıkmak; 3) mec. birini sakinleştirmek; 4) mec. karşıdaki kişinin böbürlenmesinin boşuna olduğunu ortaya çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6476, 'havasını bulmak', 'keyiflenmek, neşelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6477, 'havaya girmek', '1) hazır olmak; 2) kibirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6478, 'havaya gitmek', 'hiçbir şeye yaramamak, boşa gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6479, 'havaya pala (kılıç) sallamak', 'boşuna, gereksiz çaba harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6480, 'havaya savurmak', 'gereksiz yere harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6481, 'havaya uçmak', '1) patlama dolayısıyla zarar görmek; 2) mec. havaya gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6482, 'havayı bozmak', 'bir topluluğun keyfini kaçırmak: ''Şirket kurulalı beri Nihat kadar ticarethanenin havasını bozan bir memur gelmemişti.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6483, 'havayı koklamak', '1) bir yere göz atmak; 2) gelişmeleri veya ortamı anlamaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6484, 'havlu atmak', '1) sp. çalıştırıcı, sporcusunun karşılaşmayı terk ettiğini bildirmek için ringe havlu fırlatmak; 2) başarısızlığını kabul edip mücadeleyi bırakmak, pes etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6485, 'havsalası almamak', 'aklı kabul edememek: ''Bir genç kızın evinden başka bir yerde sabahlamasını havsalası almıyormuş.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6486, 'havsalasına sığmamak', '1) aklı almamak, kavrayamamak; 2) kabul edememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6487, 'havyar kesmek', ' argo çalışmadan vakit geçirmek, vakti boşa harcamak: ''Bu adam bir gün doğar, fena bir aile içine girer, haylaz olur, mektebin arka sıralarında havyar keser, daima tekdir edilir.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6488, 'hay hayı gitmek vay vayı kalmak', 'sağlığını, gençliğini yitirerek yakınır duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6489, 'hayâ perdesi yırtılmak', 'utanç duymamak: ''Atalarımızın ar ve hayâ perdesi yırtılmak diye pek düşündürücü bir tabirleri vardır.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6490, 'hayal gibi', 'ince, zarif: ''Dudaklarının kenarından hayal gibi beyaz bir dil geçti.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6491, 'hayal kırıklığına uğramak', 'çok istenilen veya umulan bir şeyin gerçekleşmemesinden üzüntü duymak: ''Bir hafta sonra sargıları açıp eserini incelediğinde hayal kırıklığına uğradı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6492, 'hayal kurmak', 'gerçekleşmesi istenen, özlenen şeyi düşünmek: ''Biz böyle hayal kurarken rüzgâr çıktı.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6493, 'hayal olmak', '1) gerçekleştirilememek; 2) geçmişte kalmak, hatıra olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6494, 'hayale dalmak', 'dış dünyadan uzaklaşarak gerçekleşmesi istenilen şeyleri veya hatıraları düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6495, 'hayale kapılmak', 'hayallerin etkisi altında kalmak: ''Yine işi büyüttüğüne, hayale kapıldığına hükmetti.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6496, 'hayalî fenere dönmek', 'çok zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6497, 'hayalinden geçirmek', 'olmasını istemek, düşünmek: Fransa''ya gitmeyi hayalinden geçirirdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6498, 'hayat geçirmek', 'yaşamak, varlığını sürdürmek: ''Gayet parlak ve kibar bir hayat geçiriyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6499, 'hayat memat meselesi (yapmak, olmak)', 'ölüm kalım meselesi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6500, 'hayata atılmak', 'geçim sağlamak üzere çalışmaya başlamak: ''Altı yıllık ortaöğretim bitirmek, hayata atılmanın ilk koşulu sayılır orada.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6501, 'hayata bağlamak', 'yaşamayı sevdirmek, hayattan kopmamak: ''Bu sıcak ve içten ses Fikret''i hayata bağlıyor, yaşama sevincini artırıyordu.'' -R. Enis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6502, 'hayata geçirmek', 'uygulanır duruma getirmek, canlılık kazandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6503, 'hayata gözlerini yummak (kapamak)', 'ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6504, 'hayata küsmek', 'bezgin, kötümser olmak, yaşama isteğini yitirmek: ''Adi günlerde size öyle gelir ki bunlar hayata küsmüş insanlardır.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6505, 'hayatı kaymak', 'her işi ters gitmek, mahvolmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6506, 'hayatına girmek', 'yaşamında yer almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6507, 'hayatından çıkarmak', 'ilgisini, ilişkisini tamamen kesmek: ''Beni sırf, Müslüman olmayan bir erkeği sevdim diye hayatından çıkaran babamın evine dönmeyeceğim.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6508, 'hayatını (birine) borçlu olmak', '1) biri tarafından ölümden kurtarılmış olmak; 2) birinin yaşamı bir başkasının desteği ile sağlanmış olmak: Bu hayatımı ağabeyime borçluyum.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6509, 'hayatını kazanmak', 'geçimini sağlamak: ''Hayatımı kazandığımda senin elini sıcak sudan soğuk suya sokturmam.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6510, 'hayatını yaşamak', 'her türlü baskıdan uzak, dilediğince, gönlünce yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6511, 'hayatının baharında olmak', 'hayatının en güzel dönemini yaşıyor olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6512, 'hayatının baharını yaşamak', 'hayatının en güzel günlerini yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6513, 'haybeye kürek çekmek', 'boşu boşuna uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6514, 'haydan gelen huya gider', 'kolay ve emeksiz kazanılan şeyler elden kolay çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6515, 'haydi canım sen de', '''böyle şey olmaz, sana inanmam'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6516, 'haydi oradan', '1) kovmak, azarlamak için kullanılan bir söz; 2) haydi canım sen de.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6517, 'haydut gibi', '1) insana korku veren, iri yarı (kimse); 2) yaramaz ve sevimli (çocuk).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6518, 'hayır beklememek', 'iyilik ummamak, yararlı olacağını sanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6519, 'hayır dememek', 'bir şeyi geri çevirmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6520, 'hayır dile komşuna, hayır gele başına', 'sen başkaları için iyi şeyler dile ve yap ki başkaları da senin için iyi şeyler dilesin, yapsın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6521, 'hayır dua almak', 'kendisi için iyi dilekte bulunulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6522, 'hayır dua etmek', 'iyi dileklerde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6523, 'hayır gelmemek', 'yararlı olmamak: ''Sevmeden yapılan işten hiç kimseye hayır gelmez.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6524, 'hayır işlemek', 'dine ve insanlığa uygun, iyi bir davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6525, 'hayırdır inşallah', '1) anlatılan bir rüyayı iyiye yormak için kullanılan bir söz; 2) şaşma ve merak veren olgular karşısında söylenen bir söz: ''Hayırdır inşallah, rüya mı gördün böyle birdenbire?'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6526, 'hayırlı evlat neylesin malı, hayırsız evlat neylesin malı', 'çocuk akıllı ise babasından mal kalsın diye beklemez, malı kendisi kazanır; akılsızsa babası ne kadar çok mal bırakırsa bıraksın, değerini bilmez ve onu kısa zamanda bitirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6527, 'hayızdan nifastan kesilmek', '1) menopoza girmek; 2) verimsiz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6528, 'hayra alamet değil', 'uğursuz sayılacak bir olay için kullanılan bir söz: ''Bu hayra alamet değil, dedi vali, etrafındakilere.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6529, 'hayra yormak', 'rüya veya olayı iyi bir durumun belirtisi saymak: ''Aslına bakılırsa aktör olmayı rüyasında görse hayra yormazdı.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6530, 'hayran etmek (bırakmak)', 'hayranlık duygusu uyandırmak, çok beğenilmek: ''Mükemmel seciyeler, kafiyeler yapar, hafızamıza, nüktelerimize onları hayran ederdik.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6531, 'hayran olmak (kalmak)', 'çok beğenmek: ''Birkaç defa görüşmüş, mimarideki fikirlerine, zevklerine, görüşlerine hayran olmuştum.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6532, 'hayranlık duymak', 'çok beğenmek, tutkuyla bağlanmak: ''Her zaman, uyumayı düşündüğü anla uykuya dalması bir olan yapısına hayranlık duymuşumdur.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6533, 'hayrete (hayretlere) düşmek', 'şaşakalmak, şaşırmak: ''Vaktiyle Göksel bile bu soğukkanlılığım karşısında hayrete düşmüştü.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6534, 'hayrette bırakmak', 'şaşmasına sebep olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6535, 'hayrette (hayretler içinde) kalmak', 'şaşakalmak, şaşırmak: ''İşin evveliyatını bilmeyen ırgatlar bu tariften bir şey anlayamamış, hayrette kalmışlardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6536, 'hayretten donakalmak', 'çok şaşırmak, inanamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6537, 'hayrı dokunmak', 'yararlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6538, 'hayrı olmamak', 'iyiliği dokunmamak, yarar sağlamamak: ''Öğrencisine hayrı olmayan öğretmenin hiçbir şeye hayrı olmaz.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6539, 'hayrını gör', 'yeni alınan bir şey için ''güle güle kullan'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6540, 'hayrını görmek', 'iyiliği dokunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6541, 'haysiyetine dokunmak', 'onuru incinmek: Bu söz haysiyetine dokundu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6542, 'hayvan gibi', '1) hayvana benzer biçimde; 2) iri yarı; 3) mec. akılsız, duygusuz, kaba.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6543, 'hayvan koklaşa koklaşa, insan konuşa konuşa', 'insanlar konuşarak birbirlerini daha iyi anlarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6544, 'hayvan koklaşa koklaşa, insan söyleşe söyleşe', 'insanlar konuşarak birbirlerini daha iyi anlarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6545, 'hayvan yularından, insan ikrarından (sözünden) tutulur', 'yularından tutulan hayvan başka yöne sapamadığı gibi insan da söylediği sözün dışına çıkamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6546, 'hayvanın alacası dışında, insanın alacası içinde', 'hayvanların işe yarayıp yaramayacakları görünüşlerinden belli olur ancak insanların kötü huylu olup olmadıkları dışarıdan anlaşılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6547, 'haz almak', 'hoşlanmak, keyif almak: ''Bunların hiçbirisinden haz almazdı, bu âlemde bir güzellik olmak lazım gelse bir başka biçimde lazım geleceğini düşünüyordu.'' -H. Z. Uşaklıgil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6548, 'haz duymak', 'hoşlanmak: ''O, kullanmaya alışık olduğu bu şartlı eşyasını gördükçe ve elledikçe bir haz duyardı.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6549, 'haz vermek', 'hoşlanmasını sağlamak: ''Göze bu kadar samimi ve sıcak haz veren bir mahluk çok zamandır görmemiştim.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6550, 'hazır mezarın ölüsü', ' şaka her hizmeti başkalarından bekleyen tembeller için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6551, 'hazıra dağlar dayanmaz', 'sürekli harcama, en büyük birikimleri bile eritir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6552, 'hazıra konmak', 'başkasının emeğiyle ortaya çıkmış bir şeyden yararlanmak: ''Hazıra konmak istemeyen şair, yeni söyleyişler aramak zorundadır.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6553, 'hazırda olmak', 'yararlanılabilecek bir durumda, el altında olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6554, 'hazırdan yemek', 'çalışıp kazanmaksızın elindekini harcamak: ''Hep hazırdan yiyor, içiyor, her gün Fatma Hanım''ın bin türlü bahanelerle parasını çekiyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6555, 'hazırlık görmek (yapmak)', 'hazır olmak için gereken şeyleri toplamak veya durumları sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6556, 'hazırlıklı olmak (bulunmak)', 'hazırlanmış olmak: ''Bir umuttur yok olmaya karşı az çok hazırlıklı olmak.'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6557, 'hazırlıksız olmak (bulunmak)', 'hazırlanmamış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6558, 'hazırlıksız yakalanmak', 'ani gelişen bir olayla beklenmedik bir biçimde karşılaşmak: ''Hazırlıksız yakalandığım için bir an ne yanıt vereceğimi bilememiştim.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6559, 'hazzını çıkarmak', 'zevkini çıkarmak: ''Günün bu son hazzını çıkarmadan ondan niçin vazgeçeriz?'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6560, 'hedef göstermek', '1) birini kötü bir durumda kalması için hedef hâline getirmek; 2) bir kimseyi olumsuz, kötü bir amaç için bir yere veya şeye yönlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6561, 'hedef gütmek', 'asıl amaç olarak belirlemek: ''Metin yayını ve çeviri: İnsancı davranış bu çalışmayı hedef güder.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6562, 'hedef olmak', 'hoş olmayan herhangi bir davranışa uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6563, 'hedef saptırmak', '1) hedefe isabet ettirememek; 2) mec. öngörülen amaçtan uzaklaştırıp başka bir amacı öne çıkarmak: ''Bu işi onların yapmadığına inanıyor, birilerinin hedef saptırmaya çalıştığını söylüyor.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6564, 'hekimden sorma, çekenden sor', 'bir sıkıntının acısını ancak onu çeken bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6565, 'hekimsiz, hâkimsiz memlekette oturma', 'sağlığın güvencesi hekim, toplumun güvencesi hâkimdir, bu iki güvencenin bulunmadığı yerde yaşamak zordur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6566, 'helak etmek', '1) öldürmek, ortadan kaldırmak; 2) mec. aşırı derecede yormak, bitkin duruma getirmek: Bu yolculuk bizi helak etti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6567, 'helak olmak', '1) yok olmak, ölmek: ''Kabızdan helak olma derecesine geldim.'' -N. F. Kısakürek. 2) mec. yorulmak, bitkin duruma gelmek: ''Zavallılar kan ter içinde bir yandan karşı taraf içlerini tutacağız, bir yandan forveti besleyip akına yardım edeceğiz diye ileri geri helak olurlar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6568, 'helal olsun', '1) bir hizmet veya özverinin istenilerek yapıldığını ve takdir edildiğini göstermek için kullanılan bir söz: ''Yol güzel, tarlalar cömert / Helal olsun yol parası'' -B. R. Eyuboğlu. 2) ''hakkımı helal ediyorum'' anlamında kullanılan bir söz; 3) bir davranış karşısında sitemle söylenen bir söz: Helal olsun, bunu senden beklemezdim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6569, 'helal süt emmek', 'doğruluktan ayrılmamak: ''Helal süt emmiş, dürüst, temiz, çalışkan bir mühendis bulalım.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6570, 'helallik dilemek', 'birinden hakkını helal etmesini istemek: ''Şimdi büyüklerinin ellerini öp de helallik dile.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6571, 'helallik vermek', 'helal etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6572, 'helalzade barıştırır, haramzade karıştırır', 'sütü bozuk kişi, iki kimsenin arasını açar, anlaşmalarına engel olur; soylu kişi ise arabuluculuk yapar, anlaşmalarına yardım eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6573, 'hele şükür!', '''çok şükür'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6574, 'helme dökmek', 'kaynatılmış taneler koyulaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6575, 'helme gibi', 'iyice pişmiş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6576, 'hem de nasıl', 'pek çok, çok iyi: Ankara''yı sever misin? -Hem de nasıl.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6577, 'hem İsa''yı hem de Musa''yı memnun etmek', 'istekleri birbirine karşıt olan iki kişiyi birden hoşnut edecek bir davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6578, 'hem kaçar hem davul çalar', 'işi yapmaktan çekindiği hâlde yine de yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6579, 'hem kel hem fodul', 'yetenekli olmadığı hâlde üstünlük taslayanlar için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6580, 'hem nalına hem mıhına (vurmak)', 'karşıt olan iki yanı desteklemek: ''Demokrasi ve adalet konusunda, hem nalına hem mıhına, bir başyazı düşünmüştü.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6581, 'hem suçlu hem güçlü', '''gerçek suçlu kendi olduğu hâlde başkalarını suçlayan'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6582, 'hem ziyaret hem ticaret', '''biriyle görüşmeye giden kimsenin, bu gidişten yararlanarak başka bir işi de yapması durumunda söylenen bir söz'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6583, 'hemhâl olmak', 'bütünleşmek, birliktelik özelliği göstermek: ''Çiçeklerle hemhâl olmuş, güya yumuşayarak çadırlar gibi yamru yumru kalmış duvarlar.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6584, 'hep bir ağız olmak', 'söz birliği etmek, anlaşarak bir konuda aynı şeyleri söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6585, 'her ağacın meyvesi olmaz', 'dıştan başarılı, üretken gibi görülen herkes gerçekte öyle olmayabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6586, 'her ağaç kökünden kurur (çürür)', 'bir topluluğun dayandığı temel bozulursa o topluluk bozulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6587, 'her ağaçtan kaşık olmaz', 'özelliği olan bir iş için sıradan birisi kullanılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6588, 'her aşın kaşığı olmak', 'her şeye karışmak, her şeye burnunu sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6589, '(her biri başka bir) hava çalmak', 'her biri, birbiriyle çelişen, birbirine uymayan davranış ve düşüncede bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6590, 'her boyaya girip çıkmak', 'çeşitli işlerde kısa süre de olsa çalışmış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6591, 'her boyayı boyadı, bir fıstıki yeşil (mi) kaldı?', 'yapılması gereken bir şey varken, önemsiz, zorunlu olmayan şeylerle ilgilenildiğinde söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6592, 'her çiçek koklanmaz', 'her güzelle ilişki kurmak doğru değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6593, 'her çok azdan olur', 'çoğu elde etmek için azları biriktirmek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6594, 'her dağın derdi kendine göre', 'herkesin kendi durumuna bağlı olarak sorunları vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6595, 'her damardan kan alınmaz', 'herkesten yardım istenmez, istense de alınamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6596, 'her deliğe elini sokma, ya yılan çıkar ya çıyan', 'sonunu düşünmeden sana zararı dokunma olasılığı bulunan davranışlarda bulunma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6597, 'her derde deva olmak', 'birçok şeye çare olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6598, 'her düşüş, bir öğreniş', 'kişi her yanlış davranışının acı sonucundan bir ders almalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6599, 'her firavunun bir Musa''sı çıkar', 'insanı, zalimce davranan birinden kurtaracak bir kimse her zaman bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6600, 'her gönülde bir arslan yatar', 'herkesin kendine göre büyük bir emeli vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6601, 'her gördüğü sakallıyı babası sanmak', ' şaka görünüşe aldanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6602, 'her gün baklava börek yense bıkılır', 'hep aynı şeyle uğraşmak insana bıkkınlık verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6603, 'her gün bir olmaz', 'bir günün işleri, durumları, şartları başka bir gününkine uymaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6604, 'her gün papaz pilav yemez', '1) bir insanı hep aynı hileyle kandıramazsın; 2) insan zaman zaman değişiklik ister; 3) her zaman aynı fırsat ele geçmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6605, 'her güzelin bir kusuru vardır', 'her iyi şeyin hoşa gitmeyen bir yönü, her güzelin kusurlu bir tarafı vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6606, 'her horoz kendi çöplüğünde öter', 'herkes ancak kendi çevresinde bir değer taşır ve sözünü orada geçirebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6607, 'her işin (şeyin) başı sağlık', 'insanın yapacağı her şey vücut sağlığına bağlıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6608, 'her işte bir hayır vardır', 'kişi, kötümserliğe kapılmamak için olup biten her işi hayra yormalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6609, 'her kafadan bir ses çıkmak', 'bir konu üzerinde herkes rastgele konuşmak: ''Her kafadan bir ses çıkıyor, sen kazandın ben kazandım, şans mans deyip gülüşüyorlardı.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6610, 'her kaşığın kısmeti bir olmaz', 'herkesin talihi, kazancı bir değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6611, 'her kimin bağı var, yüreğinde dağı var', 'malı, mülkü veya evladı olanlar kaygı ve tasadan uzak olamazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6612, 'her koyun kendi bacağından asılır', 'herkes kendi davranışlarından sorumludur, herkes hatasının cezasını kendisi çeker.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6613, 'her kuşun eti yenmez', 'herkes zorbalığa boyun eğmez, buna karşı gelecekler de çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6614, 'her lafın altından kalkmak', 'genellikle yerme veya hakaret sözlerinin altında kalmayıp cevap verebilmek: ''Böyle horoz gibi her lafın altından kalkarsan kocan tuttuğu gibi geri yollar seni.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6615, 'her ne pahasına olursa olsun', 'ne pahasına olursa olsun.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6616, 'her sakaldan bir tel çekseler köseye sakal olur', 'herkes biraz fedakârlık etse bir yoksul perişanlıktan kurtulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6617, '(her şeye) baş sallamak', 'karşısındakinin her sözünü uygun bulur görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6618, 'her şeyin vakti var, horoz bile vaktinde öter', 'her şey zamanında yapılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6619, 'her şeyin yenisi, dostun eskisi', 'dostluk eskidikçe güç ve değer kazanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6620, 'her şeyin yokluğu yokluktur', 'insana gerekli olan şey küçük ve değersiz de olsa yokluğunda kendini belli eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6621, 'her tarakta bezi olmak', 'birçok işi veya ilişkisi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6622, 'her taş baş yarmaz', 'korkulan her şey tehlikeli değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6623, 'her taşın altına elini sokma, ya yılan çıkar, ya çıyan', 'sonunu düşünmeden sana zararı dokunma olasılığı bulunan davranışlarda bulunma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6624, 'her telden çalmak', '1) her çeşit işi yapabilir durumda olmak; 2) birçok konuda bilgisi olmak: ''Senin anlayacağın, her telden çalıyor benim çocuklar.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6625, 'her yerde okka dört yüz dirhem', 'konuşulan bir gerçeğin açıklığını ve tartışma götürmezliğini anlatmak için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6626, 'her yiğidin bir yoğurt yiyişi vardır', 'herkesin kendine özgü bir çalışma yöntemi, bir iş yapma biçimi vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6627, 'her yiğidin gönlünde bir aslan yatar', 'herkesin kendine göre büyük bir emeli vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6628, 'her yokuşun bir inişi, her inişin bir yokuşu vardır', 'hayat boyunca yükselme, düşme vb. durumlar birbirinin ardından gelebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6629, 'her zaman eşek ölmez, on köfte on paraya olmaz', 'istenilen şeyi kolayca elde etme imkânı ortaya çıkınca fırsat kaçırılmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6630, 'her zaman gemicinin istediği rüzgâr esmez', 'olaylar herkesin istediği biçimde meydana gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6631, 'her ziyan bir öğüttür', 'kişi, uğradığı her zarardan bir ders alır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6632, '(herhangi bir biçim) kolayına gelmek', 'bir işin herhangi bir biçimde yapılmasını daha kolay bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6633, '(herhangi bir nitelikte) kalemi olmak', 'herhangi bir nitelikte yazı yazabilmek: Güçlü bir kalemi var.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6634, '(herhangi bir şey) pahasına', 'karşılığında, uğruna, ... için: Treni kaçırmak pahasına onu bekledim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6635, '(herhangi bir şeye) talim etmek', ' tkz. 1) az para karşılığında çalışmak; 2) hep aynı şeyi yemek zorunda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6636, '(herhangi bir şeye) varıncaya kadar', 'ne varsa her şeyini: Renkli televizyona varıncaya kadar ne varsa aldı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6637, '(herhangi bir şeyi) sokakta bulmamak', 'herhangi bir şeyi değerli ve önemli bulmak: ''Ben böyle şeye gelemem efendim... Ben canımı sokakta bulmadım efendim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6638, '(herhangi bir şeyi) şekle sokmak (koymak)', '1) uygun bir biçime girmesini sağlamak; 2) herhangi bir biçimde sonuca ulaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6639, '(herhangi bir yerde) ne arıyor', '''neden oraya gitmiş'' anlamında kullanılan bir söz: Sen burada ne arıyorsun, haydi çabuk eve!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6640, '(herhangi bir yılın) kurası olmak', ' ask. o yıl askerlik çağına girenlerden olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6641, 'herkes aklını pazara çıkarmış, yine kendi aklını almış', 'insanlar kendi akıllarını başkalarının aklından üstün görürler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6642, 'herkes bildiğini okur', 'başkaları ne söylerse söylesin, herkes kendi düşünüşüne göre iş yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6643, 'herkes davul çalar ama çomağı makama uyduramaz', 'herkes iş yapar ama o işin gerektirdiği ustalığı gösteremez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6644, 'herkes ektiğini biçer', 'nasıl davranırsan öyle karşılık görürsün.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6645, 'herkes evinde ağadır', 'herkesin kendi evinde, kendi çevresinde saygınlığı vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6646, 'herkes gider Mersin''e, biz gideriz tersine', 'bir işin bilerek ters yapıldığını, yolunda yapılmadığını anlatır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6647, 'herkes kaşık yapar ama sapını ortaya (doğru) getiremez', 'herkes bir iş yapar ancak istenildiği kadar güzel ve kusursuz olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6648, 'herkes kendi ayıbını bilmez', 'insan kendi kusurunu göremez, bilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6649, 'herkes kendi ölüsü için ağlar', 'hiç kimse başkasının acısını içinde duymaz, onun yüreğini sızlatan ancak kendi acısıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6650, 'herkes ne ederse kendine eder', 'nasıl davranırsan öyle karşılık görürsün.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6651, 'herkes sakız çiğner ama, Çingene kızı tadını çıkarır', 'severek yapılan iş, insanı mutlu eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6652, 'herkesin ağzı torba değil ki büzesin', 'başkalarının söyleyeceklerine engel olamazsınız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6653, 'herkesin aklı bir olsa koyuna çoban bulunmaz', 'herkes aynı şeyi bilse ve yapabilseydi, geri kalan işleri yapacak kimse bulunamazdı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6654, 'herkesin arşınına göre bez vermezler', 'genel kurallar herkesin istek ve gereksinimine göre bozulamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6655, 'herkesin bir derdi var, değirmencininki su', 'herkesin kendi yaşayışı ile ilgili bir derdi vardır, bir kişinin derdi ötekininkine benzemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6656, 'herkesin ettiği yoluna gelir', 'bir kimse başkasına ne yaparsa kendisi de aynı şeyle karşılaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6657, 'herkesin geçtiği köprüden sen de geç', 'herkesin tuttuğu yoldan sen de git.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6658, 'herkesin gönlünde bir aslan yatar', 'herkesin kendine göre büyük bir emeli vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6659, 'herkesin hamuru ekmeğine göredir', 'bir iş için yapılacak hazırlık, gereksinim ölçüsünde olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6660, 'herkesin tenceresi kapalı kaynar', 'bir kimsenin durumu, içinde bulunduğu yaşayış şartları başkalarınca gereği gibi bilinemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6661, 'herkesin yorulduğu yere han yapılmaz', 'genel kurallar herkesin istek ve gereksinimine göre bozulamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6662, 'herze yemek', ' tkz. 1) yersiz söz söylemek; 2) gereksiz davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6663, 'hesaba almamak (katmamak)', 'önem vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6664, 'hesaba çekmek', 'bir kişiden, bir kuruldan yaptığı işler için açıklama ve savunma istemek: ''Meclis kapanacak ve orada hükûmeti hesaba çekeceklermiş.'' -Atatürk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6665, 'hesaba dökmek', 'sayıyla ilgili bir konuyu açıklığa kavuşturmak için kâğıt üzerinde hesaplamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6666, 'hesaba gelmez', '1) sayılamayacak kadar çok; 2) umulmadık, beklenmedik.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6667, 'hesaba katılmamak', 'göz önüne alınmamak: ''Gürültü de gürültü hani: Çalgının şamatası hesaba katılmasa seyircinin alkışı yeter!'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6668, 'hesaba katmak', 'dikkate almak, göz önünde bulundurmak: ''Hem benim avukat veya yargıç olmak isteyip istemediğimi de hesaba kattıkları yoktu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6669, 'hesaba kitaba gelmemek', 'sınırsız olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6670, 'hesabı kapamak', 'alacak verecek bırakmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6671, 'hesabı kapatmak', 'her türlü ilişkiyi bitirmek, sona erdirmek: ''Bir hesabı daha kapatmış olmanın gönül rahatlığıyla ıslık çalarak indim merdivenlerden.'' -S. Dölek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6672, 'hesabı pak olanın, yüzü ak olur', 'doğruluktan şaşmayan, yasa dışı yollara sapmayanı kimse karalamayız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6673, 'hesabı temizlemek', 'borcunu ödemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6674, 'hesabı yok', 'sayılamayacak kadar çok, sayısız: ''İçtiği kahvenin hesabı yok.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6675, 'hesabını almak', 'bir iş sonunda hakkını almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6676, 'hesabını bilmek', 'tutumlu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6677, 'hesabını bilmeyen kasap, ne satır bırakır ne masat', 'hesabını bilmeyen kişi elinde, avucunda bulunan işe yarar şeyleri de ziyan eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6678, 'hesabını görmek', '1) alacağını verip ilişiğini kesmek; 2) cezalandırmak; 3) ücretini ödemek: ''Kemeraltı Caddesi''ne varınca arabadan inerek hesabını gördüm.'' -H. Z. Uşaklıgil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6679, 'hesabını kitabını bilmek', 'tutumlu olmak: ''Ayşe hesabını kitabını bilir, tutumlu bir ev kadınıydı.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6680, 'hesap açmak', '1) gereğinde çekilmek üzere bankaya yatırılan para için işlem yapmak; 2) birine borçlanma imkânı tanımak, kredi açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6681, 'hesap çıkarmak', 'alacakla vereceği kâğıt üzerinde karşılaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6682, 'hesap etmek', '1) bir işin kazancıyla giderini karşılaştırarak bir sonuca varmak; 2) düşünmek, tasarlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6683, 'hesap etmek, kitap etmek', 'bütün ayrıntılarıyla düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6684, 'hesap görmek', 'alacakla vereceği karşılaştırıp ödeşmek: ''Oraya çıkınca hamallara onar kuruştan hesap göreceksin.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6685, 'hesap kesmek', 'ilişiğini kesmek: ''Hana gelinceye kadar planını kurmuştu. Odabaşı ile hemen hesabını kesti.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6686, 'hesap kitap yapmak (etmek)', 'ayrıntılarıyla hesap edip düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6687, 'hesap sormak', '1) bir konuda açıklama ve savunma istemek, sorumlu tutmak: ''Bu karanlık işlerin hesabını sorarlar.'' -M. Ş. Esendal. 2) birini, birilerini yöntem veya yasa dışı davranışlarından dolayı sorguya çekmek; 3) tehdit ederek uyarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6688, 'hesap tutmak', 'alışverişle ilgili sayıları bir yere yazmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6689, 'hesap vermek (hesabını vermek)', '1) bir işin sorumluluğunu yüklenmek: ''Hesap verin bakalım, nerelerde sürtüyordunuz bu saatlere kadar?'' -R. N. Güntekin. 2) herhangi bir davranışın sebebini açıklamak, anlatmak: ''Evvela, sana birkaç haftadır mektup yazamayışımın hesabını vereyim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6690, 'hesaplamak kitaplamak', 'hesap kitap yapmak: Hesapladım kitapladım, işin içinden bir türlü çıkamadım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6691, 'hesaplı hareket etmek', 'ölçülü davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6692, 'hesapta olmamak', 'daha önce düşünülen şeylerin dışında olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6693, 'heves etmek', 'bir şeye karşı istek duymak, eğilimli olmak: ''Birçoklarının bu havaya uydukları ve artık refahlarını devlet kapılarının dışında aramaya heves ettikleri zamanlardı.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6694, 'hevesi kalmamak', 'şevki kırılmak, isteği kalmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6695, 'hevesi kursağında (boğazında veya içinde) kalmak', 'istediği, imrendiği şeyi elde edememek: ''Gazetenin yayını kesildi; çaresiz İzmir''e döndüm fakat hevesim kursağımda kalmıştı.'' -A. İlhan. ''Bütün hevesim boğazımda kaldı. Küstüm oturdum.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6696, 'hevesine düşmek', 'kuvvetle istemek: ''Bir aralık, büyük bir devlet adamı olmak hevesine düştüm.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6697, 'hevesini almak', 'istediği, imrendiği şeyi elde ederek ona doymak: ''Oluruna bırak gençtir, derim / Hevesini alsın sokaklardan'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6698, 'hevesini kırmak', '1) isteklerini, düşüncelerini engellemek; 2) zevki kaçmak, hevesi kalmamak, şevki kırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6699, 'hey gidi (hey)', 'çeşitli duyguları pekiştiren veya özlem ve acınma bildiren bir söz: ''Hey gidi gençlik hey! Unutulmaz günlerdi onlar, Yenikapı''ya, meyhanelere indik mi şöyle bir.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6700, 'heyecan duymak', 'heyecanlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6701, 'heyecan vermek', 'heyecan duymasına sebep olmak: ''Göz kamaştırıcı bir mücevher, kuyumcuya heyecan verir.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6702, 'heyecana düşürmek', 'heyecanlandırmak: ''Adına ve şimdi gördüğüm şahsiyetine zaten hayran olduğum büyük askerin bu alakası beni heyecana düşürmüştü.'' -İ. A. Gövsa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6703, 'heyecana gelmek', 'heyecanlanmak, heyecan duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6704, 'heyecana getirmek', 'heyecanlandırmak, heyecanlanmasına sebep olmak: ''Nağmeler ve hanende sesleri, uslu ve evcimen halkı heyecana ve galeyana getiriyordu.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6705, 'heyecana kapılmak', 'aşırı derecede heyecan, coşku duymak: ''Ne zaman böyle büyük makineler görsem kolay kolay tarif edilemeyen bir heyecana kapıldığımı duyuyorum.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6706, 'heyheyler geçirmek', 'büyük heyecanlar geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6707, 'heyheyleri tutmak (üstünde olmak)', 'çok sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6708, 'heykel gibi', '1) hareketsiz, duygusuz; 2) çok güzel (vücut).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6709, 'heykelini dikmek', 'türlü alanlarda üstün başarı gösteren kimselere değerbilirlik göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6710, 'heyula gibi', 'pek iri, iri yarı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6711, 'hezimete uğramak', 'bozguna veya büyük bir yenilgiye uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6712, 'hıçkırık tutmak', 'sürekli olarak hıçkırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6713, 'hık demiş (anasının veya babasının) burnundan düşmüş', '''her durumuyla birine çok benziyor'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6714, 'hık mık etmek', '1) bir işten kaçınmak için bahaneler ileri sürmeye çalışmak; 2) sorulan bir soruya açık bir anlamı olmayan, belirsiz cevaplar vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6715, 'hık tutmak', 'hıçkırık tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6716, 'hıncını çıkarmak', 'öcünü almak: ''Hıncını çıkarmak için başka vesileler arıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6717, 'hınç (hıncını) almak', 'öç (öcünü) almak: ''Fakat bu kadarcık bir mukabeleyle bütün hıncını almış değildi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6718, 'hır çıkarmak', 'kavga, gürültü çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6719, 'hırgür çıkarmak', 'kavga etmek, kavga çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6720, 'hırkayı başına çekmek', 'bir köşeye çekilip çevresiyle ilgisini kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6721, 'hırlı mıdır, hırsız mıdır?', 'bir kimsenin ahlakı, kişiliği hakkında kuşku duyulduğunda kullanılan bir söz: Ben onu tanımıyorum; hırlı mıdır, hırsız mıdır?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6722, 'hırs bastırmak', 'aşırı ölçüde açgözlü duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6723, 'hırs bürümek', 'gözünü hırs bürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6724, 'hırsından çatlamak', 'öfkeyle birlikte aşırı derecede kıskanmak: ''Ben kısa yazamıyorum öykülerimi diye hırsımdan çatlıyorum.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6725, 'hırsını alamamak', 'öfkesini yenememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6726, 'hırsını yenmek', 'öfkelenmemek için kendini tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6727, 'hırsız gibi', 'kimseye görünmeden, gizlice: ''Kapıda feneri söndürüp uzun süre bir hırsız gibi bekledi.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6728, 'hırsıza beyler de borçlu', 'zor kullanan kişilerin istediğini en güçlü kimseler bile verirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6729, 'hırsıza kilit olmaz', 'kötü bir iş yapmaya kararlı olan kişiyi önlemek için alınacak tedbirler yararsızdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6730, 'hırsıza yol göstermek', 'birine bilmeyerek kötü bir işte yardımcı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6731, 'hırsızlık bir ekmekten, kahpelik bir öpmekten', 'hırsızlığın büyüğü küçüğü olmaz, nitekim kadının namusunu satmış sayılması için bir öpücük vermiş olması yeter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6732, 'hırtlamba gibi giyinmek', 'gereksiz yere üst üste ve gelişigüzel giyinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6733, 'hırtlambası çıkmak', '1) perişan bir biçimde giyinmiş olmak; 2) eşya, çok eskiyip dökülür durumda olmak: Koltukların hırtlambası çıktı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6734, 'hısım hısımının ne öldüğünü ister, ne onduğunu', 'kardeş, kardeşe zarar gelmesini istemez ama onun kendisinden üstün durumda olmasını da kıskanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6735, 'hışırı çıkmak', '1) eşya, çok hırpalanıp örselenmek; 2) insan ağır işlerle uğraşıp çok yorulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6736, 'hıyar akçesiyle alınan eşeğin ölümü sudan olur', 'çok ucuza alınan mal, çürük, işe yaramaz çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6737, 'hız almak', 'atlamak için geri çekilip birdenbire fırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6738, 'hız vermek', '1) hızını artırmak, hızlandırmak: ''Müdür bey yeni yeni fark etmeye başladığı şartların itişiyle kendine biraz hız verdi.'' -K. Korcan. 2) mec. isteklendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6739, 'hızını alamamak', '1) hızla gidişini yavaşlatamamak; 2) mec. öfkesini yenememek, yatışamamak: ''Münakaşa tekrar eski hızını alamayarak biraz sonra söndü.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6740, 'hızını almak', '1) şiddetini yenmek, yatışmak: Fırtına hızını aldı. 2) yavaşlamak, hızını yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6741, 'hızını kaybetmek (yitirmek)', 'etkisini, geçerliliğini yitirmek, hükmü kalmamak: ''Güneş hızını kaybedince bu yapışkan su donar, yapraklar ellenebilir, toplanabilir duruma gelir.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6742, 'hızlı sağanak tez geçer', 'büyük bir hızla başlayan şeyler az sürer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6743, 'hızlı yaşamak', 'eğlenceye aşırı düşkün olarak yaşamak: ''Bu hızlı yaşamaya elli iki yıl dayanabilmişti ancak!'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6744, 'hicap duymak (etmek)', 'utanmak: ''Kalem aldın kaşlarını çatmaya / Hicap ettim adın sual etmeye'' -Dadaloğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6745, 'hiç de', 'kesinlikle, katiyen: Dersleri hiç de iyi değil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6746, 'hiç değil', 'asla, kesinlikle: -Küçük tıpkı dedesi. -Hiç değil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6747, 'hiç değilse (olmazsa)', '1) önemli olmasa bile, başka bir şey olmasa bile: ''Bu mahluk hiç değilse hep aynı noktada dönüp dolaştığının farkında değil.'' -Y. K. Karaosmanoğlu. 2) en azından: ''Hiç değilse bir gazetemiz, bizim fikirlerimizle taban tabana zıt olacaktır.'' -N. F. Kısakürek. 3) bari: ''İnsan hiç olmazsa arada bir uğrar / Böyle ihmalci değildin önceleri'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6748, 'hiç mi hiç', 'kesinlikle: ''İstanbul''a tayinimi yaptırdım, hiç mi hiç karışmadılar.'' -E. Işınsu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6749, 'hiç yoktan iyidir', 'elde bulunanla yetinmek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6750, 'hiçe saymak (indirgemek)', 'önemsememek, önem vermemek: ''Oyun kurallarını hiçe saymak, tiyatro gerçeğini bile bile bozmak da seyirci üzerinde güldürücü bir etki yapar.'' -M. And.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6751, 'hidayete ermek', '1) Müslüman olmak, İslam dinini kabul etmek: ''Önce onu sünnet ettirmiş, hidayete erdiği için adını da Hadi koymuş ve konağına almış.'' -Y. Z. Ortaç. 2) gerçeği görüp kabullenmek, aklı başına gelmek: ''Bizim gibi nice avareler burada hidayete ermişlerdir.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6752, 'hiddete kapılmak', 'öfkelenmek, kızmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6753, 'hiddetten kudurmak', 'çok öfkelenmek, aşırı derecede kızmak: ''Hele sokakta yüksek sesle gülenler olursa kendisiyle eğleniyorlar sanarak hiddetten kuduruyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6754, 'hikmetinden sual olunmaz', '1) sonucunun sebebi sorulmaz, araştırılmaz; 2) Allahın yaratıcı gücü karşısında sebep aranmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6755, 'hilal gibi', 'ince ve düzgün (kaş).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6756, 'hile hurda bilmemek', 'aldatma yollarını bilmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6757, 'hile ile iş gören mihnet ile can verir', 'işlerine hile karıştırıp başkalarını aldatan kişi son nefesini azap içinde verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6758, 'hile yapmak', '1) aldatmak: ''Yarışmaların eski tadı kalmadı Sabri Bey, binbir türlü hile yapıyorlar.'' -A. İlhan. 2) çıkar sağlamak amacıyla bir şeyin saflığını bozmak, değersiz bir şey karıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6759, 'hilesi hurdası yok', '''yalanı dolanı yok'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6760, 'himaye görmek', 'biri tarafından korunmak, kayırılmak, gözetilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6761, 'himayesine almak', 'koruyucusu olmak, korumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6762, 'hindi gibi kabarmak', 'gururlanmak, kurumlanmak, büyüklük taslamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6763, 'hisse almak', '1) zarara uğramak: ''İstanbul kahvelerinde bu sıkıntıdan en büyük hisseyi alan sanatkârlarımızdandır.'' -B. R. Eyuboğlu. 2) ders çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6764, 'hisse kapmak', 'bir olaydan yararlı bir öğüt çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6765, 'hissine (hislerine) kapılmak', 'duygusal davranmak: ''Ona mantık ve kıyaslarını yaparken, hissine ve taassubuna kapılmamasını tavsiye edecektim.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6766, 'hissini vermek', 'gibi gelmek, ... izlenimini uyandırmak: ''Ağlıyor, yırtınıyor, dövünüyor fakat adamakıllı yuvarlanmaya başladığım hissini veren bu hâlden silkinemiyorum.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6767, 'hitam bulmak', 'sona ermek, bitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6768, 'hitam vermek', 'bitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6769, 'hizaya gelmek', ' tkz. davranışlarını düzeltmek, yola gelmek: ''Ha şöyle, dedi, içinden, adam ol da biraz hizaya gel.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6770, 'hizaya getirmek', 'birinin davranışlarını düzeltmek, yola getirmek: ''Bir defada sözü, beni meslek hayatımda hizaya getiren uyarmalardan biri olmuştur.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6771, 'hizmet etmek', '1) iş görmek, çalışmak; 2) mec. birinin amaçlarının gerçekleşmesini sağlamak: ''Bu davaya en iyi hizmet etmiş olan benim.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6772, 'hizmet görmek', 'birisinden yardım almak: ''Değil kendisine hizmet etmeye, kendisinden herhangi bir hizmet görmeye bile tahammül edemeyeceği bir insana ''-Ne istiyorsunuz?'' demek yok.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6773, 'hizmete girmek', '1) çalışmaya başlamak: ''Hattın hizmete girişinden az sonra savaş başladı.'' -A. Kutlu. 2) görev almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6774, 'hizmeti dokunmak', 'görevde bulunmak, iş yapmak: ''Kendisine büyük hizmeti dokunmuş insanları unutmak bir toplumun yozlaştığını belgeler.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6775, 'hizmetinde olmak', 'birinin yanında çalışmak, işlerini yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6776, 'hocalık etmek', '1) öğretmenlik yapmak: ''Mülkiyede Osmanlı tarihi alanında hocalık, müdürlük, yazarlık etmiş.'' -R. E. Ünaydın. 2) mec. akıl öğretmek, öğüt vermek: ''Böyle heybetli, akıllı adam, sana hocalık etmiş adam ölür mü hiç?'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6777, 'hocanın dediğini yap (söylediğini dinle), yaptığını yapma', 'öğreten kimseler doğruyu bilirler ama çoğu zaman bunu kendileri yapmazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6778, 'hokka gibi', 'ufak ve düzgün (ağız, burun).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6779, 'hokka gibi oturmak', 'giysi, vücuda iyice uymak: ''Biraz kısaca olmasını kalınlığıyla telafi eden vücuduma hokka gibi oturan jaketatayımla bu gibi törenlerde beni daima...'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6780, 'hop oturup hop kalkmak', 'öfke, heyecan vb. duygular sebebiyle yerinde duramaz olmak, kalkıp kalkıp oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6781, 'hor davranmak', 'kıymetini bilmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6782, 'hor kullanmak', 'dikkat etmeyerek hoyratça kullanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6783, 'hora geçmek', ' hlk. beğenilmek, hoşa gitmek, makbule geçmek, kendisine verilen kimsenin çok işine yaramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6784, 'hora tepmek', '1) hora oynamak: ''Derhâl ayağa kalkıp, bir caz havası tutturup hora tepmeye başladı.'' -H. E. Adıvar. 2) mec. ayaklarını vurarak gürültü etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6785, 'horon vurmak', 'horon oyununu oynamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6786, 'horoz evlenir, tavuk tellenir', 'gereği yokken başkasının sevincine katılanlar için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6787, 'horoz gibi', 'kabadayıca davranan (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6788, 'horoz ölür, gözü çöplükte kalır', 'yaşanılmış, alışılmış, erişilmiş bir durum veya makam yitirildikten sonra, göz o durum veya makamda kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6789, 'horozdan kaçmak', 'kadın, erkeklerden uzak durmak, onlardan kaçmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6790, 'horozlar ötmek', 'sabah olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6791, 'horozu çok olan köyde sabah geç olur', 'karışanı çok olan işlerden sonuç güç alınır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6792, 'hortum gibi', 'çok uzun (burun).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6793, 'hortum sıkmak', 'yangına su sıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6794, 'hoş bulduk (gördük)', '''hoş geldiniz'' sözüne verilen karşılık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6795, 'hoş geldiniz', 'gelen kişiye söylenen selamlama sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6796, 'hoş görmek (karşılamak)', 'gücenilecek veya karşılık gelinecek bir davranışı hoşgörü ile karşılamak, anlayışla karşılamak, kusur saymamak: ''Bu hareketi pek hoş görmeyen Şems de onun böyle sellemehüsselam girip çıkmaması için biraz ağırca sözler söylemişti.'' -A. H. Çelebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6797, 'hoşa gitmek', 'beğenilmek, bir kişiden veya bir şeyden hoşlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6798, 'hoşaf gibi', 'çok yorgun.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6799, 'hoşbeş etmek', 'sohbet etmek: ''Birkaç köylü ile hoşbeş ettim.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6800, 'hoşça kal (kalın)', 'ayrılan kimsenin kalanlara söylediği bir iyi dilek sözü: ''Hoşça kalın, diyor aracın kapısından çıkarken.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6801, 'hoşnutluk duymak', 'memnun olmak: ''Durumumdan artık kaygılanmadığımı, tersine oldukça hoşnutluk duymakta olduğumu fark ediyorum.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6802, 'hoşnutluk getirmek', 'memnun olduğunu göstermek: ''Fakat amirleri kendisinden çok hoşnutluk getiriyorlar.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6803, 'hoşnutsuzluk getirmek', 'memnuniyetsizlik göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6804, 'höt demek', 'gözdağı vermek, korkutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6805, 'hu çekmek (demek)', 'tekkelerde, dervişler ayin sırasında sürekli olarak hu demek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6806, 'hulus (huluslar) çakmak', 'dalkavukluk etmek, yaranmaya çalışmak: ''Bunlar aşiret reislerine hulus çakmışlar, hep alttan almışlar belki rüşvetlerini de yemişler ve onları şımartmışlardı.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6807, 'hurdası çıkmak', 'eşya, kullanılmayacak duruma gelmek, eskimek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6808, 'huri gibi', 'çok güzel (genç kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6809, 'husul bulmak', 'husule gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6810, 'husule gelmek', 'olmak, oluşmak, doğmak, çıkmak, meydana gelmek: ''Bu hülya uzaklaştıkça ruhta zehirli bir fütur husule geliyordu.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6811, 'husumet beslemek', 'hasım olmak, düşman olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6812, 'huy canın altındadır', 'insanı alışkanlıklarından, huylarından vazgeçirmek mümkün değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6813, 'huy edinmek', 'bir davranışı alışkanlık durumuna getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6814, 'huylu huyundan teneşirde vazgeçer', 'insanı alışkanlıklarından, huylarından vazgeçirmek mümkün değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6815, 'huylu huyundan vazgeçmez', 'bir huy edinmiş olan kişiyi bu huyundan vazgeçirmek imkânsızdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6816, 'huyu huyuna suyu suyuna (uygun)', 'iki kişinin her yönden birbirine uygunluğunu anlatmak için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6817, 'huyunu suyunu değiştirmek', 'eskisine göre değişik davranmasına sebep olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6818, 'huzur bulmak', 'ruhsal yönden rahatlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6819, 'huzur vermek', 'gönül rahatlığı, dirlik vermek, dinlendirmek: ''Kendisine her zaman huzur veren o kokuyu, anasının kokusunu duyar duymaz tatlı tatlı mırıldanmaya başladı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6820, 'huzurunu kaçırmak', 'tedirgin, rahatsız etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6821, 'hücuma kalkmak', 'asker, siperden düşmana doğru fırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6822, 'hükme varmak', 'iyice düşündükten sonra karar vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6823, 'hükmü geçmek (hüküm yürütmek)', '1) gücü yetmek, sözü geçmek: ''Ne doğan güne hükmüm geçer / Ne hâlden anlayan bulunur'' -C. S. Tarancı. 2) geçerli, etkili durumunu yitirmek: Soğukların hükmü geçti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6824, 'hükmü olmamak', 'önemi, geçerliliği, etkisi bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6825, 'hükmü parasına geçmek', 'para ile dilediğini yapabilme gücünü kazanmak: ''Ulan! Parama geçer hükmüm diye bağırdı. Getir diyorum iki okka ekmek.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6826, '(...) hükmünde olmak', '1) değerinde olmak: Kaynata, baba hükmündedir. 2) yerinde olmak, yerine geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6827, 'hükmünü icra etmek', 'gerekeni yerine getirmek: ''Yaş yine de tabii hükmünü icra ediyor, adaleleri gevşiyor ve eski canlılığını kaybediyordu.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6828, 'hükûmet etmek', 'bir ülkenin yönetimini elinde bulundurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6829, 'hükûmet gibi', 'güçlü, her dediğini yaptıran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6830, 'hükûmet sürmek', 'ülke yönetiminin başında bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6831, 'hükûmeti devirmek', 'zor kullanarak devlet yönetiminde değişiklik yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6832, 'hüküm giymek', 'mahkemece cezalandırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6833, 'hüküm sürmek', '1) işbaşında olmak: Kral otuz yıl hüküm sürdü. 2) yaygın olmak: Hüküm süren kanaat. 3) etki, hız vb. sürmek, devam etmek: ''O yükseklerde fırtına, kar, tipi hüküm sürmekteydi.'' -N. Nâzım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6834, 'hüküm vermek', '1) iyice düşündükten sonra bir karara varmak: ''İnsanlar ellerinden çok gözleriyle hüküm verirler.'' -C. Meriç. 2) bir suçluyu mahkûm etme.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6835, 'hüküm yemek', 'mahkûm olmak: ''Üsküp''ün ceza mahkemesinde on beş sene hüküm yedi.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6836, 'hükümsüz kılmak', 'yürürlükten kaldırmak, iptal etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6837, 'hülle yapmak', '1) hülleyi gerçekleştirmek; 2) bir işte geçici çözüm için hileye başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6838, 'hülyaya dalmak', 'hayal kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6839, 'hüner göstermek', '1) beceriklilik ortaya koymak; 2) herkesin yapamayacağı bir işi yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6840, 'hürmette kusur etmemek', 'karşısındaki kişiyi iyi ağırlamak, isteklerini yerine getirmek, saygısızlık etmemek: ''Hürmette kusur ettin mi işte o zaman kendini yok bil.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6841, 'hürriyeti seçmek', 'baskıdan kurtulmak ve özgür yaşamak için davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6842, 'hüsnü kabul göstermek', 'iyi karşılamak, güler yüz göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6843, 'hüsnüzan etmek', 'iyi niyet beslemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6844, 'hüsrana uğramak', 'beklenilen sonucun elde edilememesi sebebiyle çok üzülmek, acı çekmek: ''Bunun aksini umanlar aldanacaktır, hüsrana uğrayacaktır.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6845, 'hüzne kapılmak', 'hüzünlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6846, 'hüzün çökmek', 'hüzünlenmek: ''O anda yalnız kahveye değil neredeyse bütün Niksar''a hüzün çöker, lambaların ışığı solgunlaşırdı.'' -C. Külebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6847, 'hüzün duymak', 'hüzünlü duruma gelmek, üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6848, 'ıcığını cıcığını çıkarmak', 'incelenmemiş, elden geçirilmemiş hiçbir yerini bırakmamak, en küçük ayrıntısına kadar incelemek, didik didik etmek: ''Allah''ın bildiğini kuldan ne saklamalı, ilk önce aklımdan bazı çirkin şüpheler geçer gibi olmuştu. Hastanenin ıcığını cıcığını çıkarmıştım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6849, 'ıcığını cıcığını sormak', 'bir kimsenin soyunu sopunu, huyunu suyunu iyice öğrenmek için araştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6850, 'ığrıp çekmek', 'balık yakalamak için atılmış ığrıbı yukarı çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6851, 'ığrıp çevirmek', 'yalan dolanla bir şeyden yararlanmak: ''Birinci Dünya Harbi''nde oldukça önemli ığrıp çevirenler olmuştu.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6852, 'ıkınıp sıkınmak', ' tkz. bir iş yapabilmek için kendini çok zorlamak: Ikınıp sıkındı, bir cevap bulup veremedi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6853, 'ırak yerin haberini kervan getirir', 'erişemediğimiz şeyle aramızdaki ilişkiyi bir aracı sağlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6854, 'ırgat gibi çalışmak', 'çok ağır bir işte çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6855, 'ırmak kenarına çeşme yapılmaz', 'zaten var olan ve herkesin işine yarayan bir şeyin yanına aynı işi görmek üzere benzerini yapmak boşunadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6856, 'ırmaktan geçerken at değiştirilmez', 'bir yöntemden başka bir yönteme geçiş tehlikeli bir durum veya zamanda yapılmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6857, 'ırz insanın kanı pahasıdır', 'insan ırzını, namusunu korumak için canını feda eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6858, 'ırzına geçmek', '1) zor kullanarak bir kimseyi cinsel zevkine alet etmek, tecavüz etmek; 2) bekâretini bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6859, 'ırzını bozmak', 'ırzına geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6860, 'ısıracak it (köpek) dişini (dişlerini) göstermez', 'kötülük edecek kimse önceden haber vermez, belli etmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6861, 'ısıramadığın (bükemediğin) eli öp başına ko', 'düşmanını yenemiyorsan ona hoş görünmeye çalışarak kötülüğünden kendini koru.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6862, 'ısırgan ile taharet olmaz', 'kötü kişiden iyilik beklenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6863, 'ısıtıp ısıtıp önüne koymak', 'daha önce geçmiş bir olayı, bir işi, ileri sürülmüş bir düşünceyi sık sık tekrarlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6864, 'ıska geçilmek', 'gözden kaçırılmak, atlanmak, değeri ve önemi anlaşılmamak: ''Bu kadar güzel, bu kadar büyük bir şehir nasıl ıska geçilir diye içerliyordum.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6865, 'ıska geçmek', '1) hedefe rast getirememek; 2) üzerinde durmamak, önem vermemek, atlamak: ''Bedenleriyle ilgili gerçekleri ıska geçerler.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6866, 'ıskartaya çıkarmak (ayırmak)', 'değersiz bularak bir yana atmak, işe yaramadığı için ayırıp bir yana koymak: ''Sekiz balya tütününden bir ya da iki balyasını ıskartaya ayırabileceklerini aklından geçirmeye başladı eksperlerin.'' -N. Cumalı. ''Orada, kim bilir neden ve nasıl, işe yaramaz diye ıskartaya çıkardığı bir sürü film tepeleme yığılı.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6867, 'ıslah etmek', '1) iyi bir duruma getirmek, iyileştirmek, düzeltmek: ''Sanat yalnız insanları ıslah etmeye yarar bir vasıtadır.'' -Y. K. Beyatlı. 2) yola getirmek: ''Nerede o, meclis kürsülerinden ''mahkûmu ıslah ediyoruz'' diye bangır bangır bağıran adliye vekilleri?'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6868, 'ıslanmışın yağmurdan korkusu olmaz', 'daha önce bir zarara uğramış kimse, kendisine aynı zararı verecek şeyden korkmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6869, 'ıslık çalmak', '''ıslık'' sesi çıkarmak: ''Laf atmalar, ıslık çalmalar, kavgaya tutuşmalar gün boyu sürüp gitti.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6870, 'ısmarlama hac, hac olmaz', 'kişi, kendisi yapması gereken bir işi başkasına yaptırmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6871, 'ıspazmoza tutulmak', 'aşırı derecede titremeye başlamak: ''Ben durup dururken ıspazmoza tutulmuş gibi titredikten sonra...'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6872, 'ısrar etmek', '1) bir konuda, bir düşüncede sürekli direnmek, ayak diremek: ''Siz benim usule bakın diye ısrar ediyordu.'' -Ç. Altan. 2) çok istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6873, 'ısrarlı olmak', 'düşüncesinde, kararında direnmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6874, 'ıssız eve it buyruk', 'aklı başında kimselerin sahip çıkmadığı iş, aşağılık kimselerin elinde kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6875, 'ıssız kalmak', 'ıssızlaşmak, tenhalaşmak: ''Yavru gitmiş, ıssız kalmış otağı.'' -Bayburtlu Zihni.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6876, 'ıssızlık çökmek', 'ıssız, tenha duruma gelmek, tenhalaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6877, 'ıstakoz gibi', 'çok kırmızı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6878, 'ıstılah paralamak', 'ağdalı, hiç kimsenin anlamadığı bir biçimde konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6879, 'ışık almak', 'güneş ışığından yararlanır durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6880, 'ışık tutmak', '1) bir yeri ışıkla aydınlatmak: ''Biraz evvel bize ışık tutan sakallı adam bu hareketime dikkat etmişti.'' -R. N. Güntekin. 2) düşüncesiyle kılavuzluk etmek, konuyu aydınlatıcı düşünceler söylemek, tutacağı yolu göstermek: ''Gökalp, bu odada her gün yeni bir konuya ışık tutardı.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6881, 'ışıl ışıl yanmak', 'parlamak: ''Gözleri ışıl ışıl yanan bir kara kedi gibi pusudaydı.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6882, 'ızdırap çekmek', 'ağrı ve acı içinde kıvranmak, aşırı derecede üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6883, 'ibadet de gizli, kabahat de', 'yapılan iyilikler göstermelik olmamalı, işlenen suçlar, ayıplar açığa vurulmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6884, 'ibaret olmak (kalmak)', '1) -den oluşmak, meydana gelmek: ''Büyük önderin bize verdiği mükâfat bundan ibaret değildi.'' -Y. K. Karaosmanoğlu. 2) ancak bu kadar olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6885, 'ibiş gibi', 'yüz ve davranışları gülünç olan (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6886, 'ibre birinden yana dönmek', 'herhangi bir konuda birisi avantajlı duruma geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6887, 'ibret almak', 'ders almak: ''Azizim, korkarım ki günün birinde bizi tamamıyla mahvedecek şey de bu olmasın; karşımızdakilerden biraz ibret almalıyız, efendim.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6888, 'icabına bakmak', '1) gereğini yerine getirmek; 2) mec. bir kimseyi yok etmek, ortadan kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6889, 'icara vermek', 'kiraya vermek: ''Tek hanemi sizin gibi asil bir aileye icara vereyim.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6890, 'icat çıkarmak', '1) hoş görülmeyen yeni bir huy, davranış göstermek; 2) yadırganan bir yol tutmak; 3) ortaya gereği olmayan bir sorun atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6891, 'icat etmek', '1) ilk kez yeni bir şey yaratmak: ''Nihayet, yaza çize ilk satırı üç nokta ile başlayan yeni bir tarz icat ettim.'' -Y. Z. Ortaç. 2) bir şeyi gerçekmiş gibi göstermek: ''Çok durduğumdan şüphelenmesinler diye uydurma bir tamir icat ettim.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6892, 'icazet almak', '1) izin, onay almak: ''Bir çift ayakkabı almak için dahi ondan icazet almak zorunda kalıyorum.'' -A. Kulin. 2) diploma almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6893, 'icazet vermek', 'izin, onay vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6894, 'icraata geçmek', 'uygulamaya veya çalışmaya başlamak: ''Edebiyat konusunda hükûmet daima bizim fikrimizi alır, ondan sonra icraata geçer.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6895, 'icraya vermek', 'alacağın borçludan alınabilmesi için icraya başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6896, 'iç açmak', 'gönle ferahlık vermek, gönlü ferahlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6897, 'iç bağlamak', 'iç tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6898, 'iç çekmek', 'üzüntüyle derinden soluk almak: ''Hafif hafif iç çekmeler, tek hıçkırıklar, konser hâlinde ağlamalar.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6899, 'iç dökmek', 'içini dökmek: ''Akşamları ikişer üçer kadeh içer, karşılıklı iç dökerdik.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6900, 'iç etmek', ' argo eline geçen bir şeyi sahibine bildirmeyerek kendine mal etmek: ''Hem parayı iç et, üstüne bir de söv, ha?'' -O. Hançerlioğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6901, 'iç geçirmek', 'derin soluk alarak üzüntüsünü belli etmek: ''Derin bir iç geçirişti ki ah çekişti denilebilir.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6902, 'iç gıcıklamak', '1) istek uyandırmak; 2) huylandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6903, 'iç güveyisi girmek', 'karısının ailesinin evinde oturmak üzere evlenmek: ''O, zengin bir eve iç güveyisi olarak girmeye razı olmamış.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6904, 'iç güveyisi iç ağrısı', 'iç güveyisi misafir gibidir, evdekiler sürekli olarak onu ağırlamaya çalışır ve bu durumdan da rahatsız olurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6905, 'iç güveyisinden hâllice', ' şaka ''nasılsın'' sorusuna ''eh işte, fena değil'' anlamında verilen karşılık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6906, 'iç içe girmek (geçmek)', '1) karmakarışık olmak; 2) uygun bir biçimde birbirinin içine girmek; 3) kaza sonucu araçlar birbirine girmek; 4) birbirinden ayrılamaz durumda olmak: ''Burada tarih ile masal iç içe girmiş durumdadır; hangisi masal, hangisi tarih, karışır gider birbirine.'' -M. C. Anday.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6907, 'iç tutmak', 'yemişin içi oluşmak: ''Oysaki cevizlerin iç tuttuğuna bakılırsa yaz geçiyordu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6908, 'içeri girmek', '1) bir iş veya alışverişte zarar etmek: Bu işte bir milyar lira içeri girdim. 2) hapse girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6909, 'içeride olmak', '1) zarar etmiş olmak, borçlanmış olmak; 2) hapishanede olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6910, 'içeriden çıkmak', 'hapisten kurtulmak, serbest kalmak: ''Umarım şimdi anlıyorsundur uzun süre yatan kişilerin içeriden nasıl çıktıklarını.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6911, 'içeriye atmak (almak veya tıkmak)', 'hapsetmek: ''Bundan da başka yarın bunu tutar, içeri tıkabilirdi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6912, 'içeriye dalmak', '1) kapalı bir yere hızlıca girmek: ''Bir taş merdivenden çıkıp yarı açık duran bir tahta kapıdan içeriye dalıyorlardı.'' -Y. K. Karaosmanoğlu. 2) bir yere izinsiz girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6913, 'içeriye düşmek', 'hapse girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6914, 'içi açılmak', 'güzel bir şey karşısında sıkıntısı dağılmak, ferahlamak: ''Artık bu çehrenin karşısına geç. Bak, bak, için açılsın.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6915, 'içi alaylı, dışı kalaylı', '''dışı süslü, güzel görünüşlü ancak içi berbat'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6916, 'içi almamak', '1) midesi kabul etmemek; 2) sakıncalı gördüğünden veya beğenmediğinden, bir işi yapmak istememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6917, 'içi bayılmak', '1) çok acıkmak; 2) çok şekerli veya yağlı yiyecek ağır gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6918, 'içi beni yakar, dışı eli (seni) yakar', 'dış görünüşü ile başkalarının hoşuna giden bir şeyin veya durumun gerçekte kötü yönleri olabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6919, 'içi boşalmak', 'önemi ve anlamı kalmamak: ''Biliyorum; bütün sözler yavan, bütün sözcüklerin içi boşalmış, bütün anlamlar kullanılmış.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6920, 'içi bulanmak', 'kusacak gibi olmak: ''Tabanları, dizleri sızlar gibi oldu. Bir de içi bulandı, kusacak gibi oldu.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6921, 'içi burkulmak', 'bir şeye çok üzülmek: ''Hayatımızda bozukluğunu, yokluğunu içlerimiz burkularak duyduğumuz ne vardır ki millî şuur eksikliğinden gelmesin?'' -O. S. Orhon.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6922, 'içi cız etmek', 'ansızın içi sızlamak: ''Otuz sayfa okurum diye umduğum koca bir günün sonunda zar zor üç sayfa okuyabildiğimi anımsayınca içim cız etti.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6923, 'içi çekmek', 'istek duymak: ''Arsız bir tabiatım var. Ne görsem içim çeker.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6924, 'içi daralmak', 'sıkılmak, bunalmak: ''Hayvan aklıma geldikçe içim daralıyor dayı.'' -N. Kurşunlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6925, 'içi dayanmamak', 'acıklı bir durumu kaldıramamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6926, 'içi dışı bir (olmak)', 'düşündüğünü açıkça söyleyen, gizli bir düşüncesi olmayan, ikiyüzlü olmayan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6927, 'içi dışına çıkmak', '1) kusmak; 2) kusacak duruma gelmek: ''Cip hazır, dedi. İnşallah süspansiyonu iyidir yoksa yollarda içimiz dışımıza çıkacak.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6928, 'içi erimek', 'kaygı duymak, çok üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6929, 'içi ezilmek', '1) üzülmek, yüreği burkulmak: ''O kadar tatlıydı ki insanın içi eziliyordu.'' -N. Hikmet. 2) acıkma hissi duymak; 3) mec. sıkıntı ve heyecan içine düşmek: ''Ay içim eziliyor kızım... Uzatma çabuk söyle.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6930, 'içi ezim ezim ezilmek', 'çok üzülmek: ''İçi ezim ezim eziliyordu.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6931, 'içi geçmek', '1) istemeden kısa bir süre uyuyuvermek: ''Hanife kadın hastalandı, şimdi o gelinceye kadar işlerini ben yapıyorum, çamaşır yıkadım da yorulmuşum, şöyle içim geçmiş.'' -R. H. Karay. 2) bir işe yaramaz duruma gelmek: ''Islak duvarların, rüzgâr vurdukça çatırdayan çatıların altında insanların içi geçti.'' -L. Tekin. 3) yaşlılıktan, güçsüzlükten isteksiz olmak, hiçbir şeye ilgi duymamak; 4) kavun, karpuz vb. yenmeyecek biçimde içi bozulmuş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6932, 'içi gitmek', '1) içi sürmek; 2) bir şeyi yapmayı veya elde etmeyi çok istemek: ''Gençtim, güzeldim, düzgüne, rastığa, janjanlı çoraba benim de içim gidiyordu.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6933, 'içi götürmemek', '1) acıklı bir durum karşısında dayanamamak; 2) kıskanmak, çekememek; 3) vicdanına sığdıramamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6934, 'içi hop etmek', 'birdenbire heyecanlanmak: ''Güler''i gördüm ve içim hop etti.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6935, 'içi ısınmak', 'hoşlanmak, sevmek: ''Uzun yıllar içim ısınmadı ona.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6936, 'içi içine geçmek', 'tedirgin olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6937, 'içi içine sığmamak', 'telaş, sabırsızlık, coşkunluk göstermekten kendini alamamak: ''Nazmiye''den çok İhsan''ın içi içine sığmıyor, birazdan başlarına gelecekleri tasarlayarak kahroluyordu.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6938, 'içi içini yemek', '1) istediğini yapamama yüzünden üzülmek: ''Bir an önce varalım diye içim içimi yiyor.'' -A. İlhan. 2) dert etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6939, 'içi kağşamak', 'isteksiz ve gönülsüz olmak: ''Uzunca bir süredir, bir daha âşık olamayacak kadar içinin kağşadığını düşünüyordu.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6940, 'içi kalkmak (kabarmak)', '1) iğrenmek; 2) taşkın bir ağlama duygusu içinde bulunmak; 3) duygulanmak, heyecanlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6941, 'içi kan ağlamak', 'çok üzüntü duymak: ''Demin Raif Efendi''nin karısını dinlerken içim kan ağlıyordu.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6942, 'içi kapanmak', 'sıkılmak, bunalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6943, 'içi kararmak', '1) sıkılmak, bunalmak: ''Hani bazı kadınlar vardır, hödük koca ile düşe kalka eblehleşir, içleri kararır, ispinoz gibi susar otururlar.'' -H. Taner. 2) hiçbir şeyden tat alamaz olmak; 3) umutsuzluğa düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6944, 'içi kazınmak (kıyılmak)', 'açlıktan midesinde eziklik duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6945, 'içi paralanmak (parçalanmak)', 'birine acıyarak çok üzülmek: ''Yusuf için her fedakârlığa razı idim. Fakat buna imkân göremiyordum. İçim parçalandı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6946, 'içi rahat etmek', 'kaygı duyulacak bir konu bulunmadığını öğrenerek ferahlamak: ''Vehbi Dede itiraz etmezse içi rahat edecek.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6947, 'içi sıkılmak', 'bunalmak: ''Sekiz saattir trendeyim. Tren boş ve neşesiz. İçim sıkılıyor.'' -A. Haşim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6948, 'içi sızlamak', 'bir şey veya kişi için çok üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6949, 'içi sürmek', 'ishal olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6950, 'içi titremek', '1) özen göstermek; 2) çok üşümek; 3) duygulanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6951, 'içi vık vık (fık fık, pır pır) etmek', 'sabırsızca, tedirgin davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6952, 'içi yağ bağlamak', 'yüreği yağ bağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6953, 'içi yanmak', '1) çok susamak; 2) büyük bir acı, sıkıntı vb. nedenlerle çok üzülmek: ''Sanki ağlayan ve en çok içi yanan o değildi.'' -T. Buğra. 3) bir şeye karşı büyük bir özlem duymak: ''Biliyorum içiniz vatan aşkıyla yanıyor, aynen benim gibi.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6954, 'için için gülmek (gülümsemek)', 'belli etmeden, gizli gizli gülmek: ''Belki tramvayda, vapurda yan yana giderken, o ona için için gülmüştür, belki de alay etmiştir.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6955, 'için için kaynamak', 'aşırı heyecan, gözü peklik ve hareket içindeyken bunu belli etmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6956, 'için için yanmak', '1) ateşin yanması sürmek, farkına varılmadan yanmak: ''Pamuk için için yanıyor, zaman zaman küçük parıltılar çıkarıyordu.'' -A. Kutlu. 2) mec. dışa vurmadan çok üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6957, '(içinde) at koşturmak', 'bir alanda çok geniş olduğu için alabildiğine rahat hareket edebilmek: ''Büyüklerin, içinde at koştur diye tarif ettikleri taşlık ve sofaları vardı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6958, 'içinde kaybolmak', '1) göze çarpmamak: ''Fakat götürdükleri eşya da kendileri de koca köşkün içinde kayboldular.'' -R. N. Güntekin. 2) giysi çok büyük gelmek; 3) beklenen sonuca ulaşamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6959, 'içinde olmak', '1) herhangi bir özellik yaradılışında var olmak; 2) hevesli, istekli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6960, 'içinden bir şeyler kopmak', 'içi ezilmek: ''İdris Bey atına binip köyden ayrılırken içlerinden bir şeyler koptu.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6961, 'içinden geçirmek', 'bir şeyi yapmayı düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6962, 'içinden geçmek', 'düşünmek, aklından geçmek: ''İçinden geçip de bir türlü açığa vuramadığı sözleri, şimdi ezberlenmiş bir nutuk veya bir dua hâlinde söylüyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6963, 'içinden gelmek', 'bir şeyi yapmak için içten bir istek duymak: ''Yüzüne bakmak istemiyordum, onu aşağılamak istediğimden değil içimden öyle geliyordu.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6964, 'içinden kan gitmek', 'içi kan ağlamak: ''Şu kadıncağıza öyle acıyorum ki içimden kan gidiyor.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6965, 'içinden konuşmak', 'kimsenin duymayacağı kadar yavaş sesle konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6966, 'içinden okumak', '1) ses çıkarmadan okumak; 2) argo sessiz bir biçimde sövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6967, 'içinden yanmak', 'çok istemek, sabırsızlık göstermek: ''Yanımızdan bir ayak evvel kaçmak için içinden yanıyor.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6968, 'içine almak', 'kapsamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6969, 'içine ateş atmak', 'aşırı acı, sıkıntı veya üzüntü verecek davranışta bulunmak: ''Nazmiye''nin içine avuçla ateş atıp evden içeri giriyor ama başını kaldırıp pencereye bakmıyordu.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6970, 'içine ateş düşmek', 'büyük bir acı ve üzüntünün etkisi altına girmek: ''Pamuk zamanı gelince, köylüler Toros''tan pamuğa dökülünce içine bir ateş düştü, duramaz oldu.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6971, 'içine atmak', '1) sıkıntısını kimseye belli etmemek; 2) yapılan bir kötülüğe karşı sesini çıkarmamakla birlikte, bunu unutmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6972, 'içine baygınlıklar çökmek', 'sıkıntı, fenalık basmak: ''Şevki, ekmek öpüp çocukları üzerine yemin ettikçe onun içine baygınlıklar çöküyordu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6973, 'içine çekilmek (kapanmak)', 'çevresindeki kişilerle ilgi kurmamak, duygularını kimseye açmamak: ''O sene çok içine çekilmiş, daima boş vakti kütüphanede geçen ağır bir talebe vaziyetini almıştı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6974, 'içine çekmek', '1) soluk almak; 2) mec. bilincine varmak, anlamak: ''Bu barut kokulu alçaklık ve zorbalık havasını uzun uzun, derin derin içine çekti.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6975, 'içine daralma gelmek', 'sıkıntı basmak, sıkılmak: ''Hava kararmaya yüz tutunca, içine bir daralma geliyor çocuğun.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6976, 'içine dert olmak', 'bir şeyi yapamamaktan dolayı üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6977, 'içine doğmak', 'bir işin olacağını veya olduğunu hiçbir belirtiye dayanmadan önceden sezinlemek, malum olmak: ''Bunu git hocadan sor, elbette benden, senden önce o cennetlik kişinin içine doğmuştur.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6978, 'içine dokunmak', 'dertlendirmek, üzmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6979, 'içine fenalık gelmek (basmak)', 'ruhu daralmak, sıkılıp bunalmak: ''İçine fenalıklar basmaya başladı; bir kere rezil olmuşlardı mahalleye.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6980, 'içine hüzün çökmek', 'kederlenmeye, hüzünlenmeye başlamak: Eski bayramlar gibi olmuyor, hüzün çöküyor içimize.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6981, 'içine işlemek', 'duygulanmak, etkilenmek, dokunmak: ''Kızın pembe beyaz yanakları, simsiyah kaşı, gözü içine işlemişti.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6982, 'içine kurt düşmek', 'kendisine zararı dokunacak bir durum meydana geleceğinden kuşkulanmak: ''Kız geçen cuma, pazardan geç geldiğinden beri esasen içine kurt düşmüştü.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6983, 'içine kuşku çökmek', 'içten içe şüphesi yoğunlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6984, 'içine oturmak', 'çok etkilenmek, çok üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6985, 'içine sinmek', '1) isteğince olduğu için huzur ve mutluluk duymak: ''Uykusundan esneye gerine çıkar, içine sinmiş rüyalardan hafif hafif sıyrılırdı.'' -A. Ş. Hisar. 2) içi rahat etmek: ''Düğünümde bulunmazsan gelinliğim içime sinmeyecek, diyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6986, 'içine su serpilmek', 'ferahlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6987, 'içine tükürmek', 'bir şeyi bozup berbat etmek: ''Ne zaman ki sen ve senin gibiler ilk dönüme pey sürdünüz, bizler de dalaverenin içine tükürdük.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6988, 'içini açmak', 'derdini anlatmak, içini dökmek: ''Rabia elinden gittikten dört beş ay sonra imama verdiği söze rağmen yavaş yavaş komşulara içini açmak istedi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6989, 'içini bayıltmak (kıymak)', '1) tatlı, ağır gelip artık yiyememek; 2) mec. çok konuşarak veya ağır davranarak birini usandırmak; 3) mec. yoğun olarak hissetmek: ''Bu bahçede insanın içini bayıltan hanımeli, gül ve salkım kokuları binbir ot kokusuna karışıyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6990, 'içini boşaltmak', '1) sıkıntı ve derdini söylemek: ''Psikanalistler, insanı nasıl itiraf ettirerek içini boşaltmak suretiyle tedavi ederlerse, sanat eserleri de aşağı yukarı aynı rolü oynarlar.'' -M. Kaplan. 2) öfkesini açığa vurmak; 3) banka, şirket vb.ni yasal görüntü verip soymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6991, 'içini çekmek', 'iç çekmek: ''Öyle ağlıyor ki ben de içimi çeke çeke onu teselli etmeye çalışıyorum.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6992, 'içini çürütmek', 'ruhunu karartmak, bezdirmek, yıldırmak: ''Bazı alametler büsbütün içimi çürüttü.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6993, 'içini dondurmak', 'şaşırtmak, ürpertmek: ''Benden bu denli emin olması içimi dondurdu.'' -R. Mağden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6994, 'içini dökmek', '1) derdini anlatmak, iç dünyasındaki duygu ve düşüncelerini bir bir anlatmak: ''Rakım güldü, bu manastır kaçkını eski gâvura içini dökmekten lezzet alıyordu.'' -H. E. Adıvar. 2) ferahlamak, rahatlamak: ''Bu yazıyı niçin yazıyorum? Biraz içimi dökmek, bir parçacık olsun ferahlamak istiyorum.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6995, 'içini ezmek', 'üzüntüsünü, sıkıntısını duymak: ''Şimdi duyduğum suçluluğa karışan özlem içimi eziyor.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6996, 'içini ısıtmak', 'hoş, güzel bir şey hoşluk duygusu yaratmak, coşku vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6997, 'içini karartmak', 'bunalıma veya sıkıntıya sokmak, endişeye düşürmek: ''Annesini yanına aldığı günlerdeki mutsuzluğum hâlâ içimi karartıyor.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6998, 'içini kemirmek', 'bir üzüntüden rahatsızlık duymak, tedirgin olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (6999, 'içini kurt yemek (kemirmek)', 'sürekli bir kaygı içinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7000, 'içini parçalamak (parça parça etmek)', 'çok üzülmek, aşırı derecede sıkılıp harap olmak: ''İçini parça parça etmekle beraber Azize''nin feryadı ona tabii gelmeye başlamıştı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7001, 'içini sıkmak', 'sıkıntı vermek: ''Fakat bu lakırtı Rabia''nın içini sıkar.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7002, 'içini yakmak', 'çok üzülmek: ''Fakat küçüklerin bahçede ağlamaları o kadar içimi yaktı ki kendi kendime hiç kocaya varmamaya yemin ettiğimi hatırlıyorum.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7003, 'içini yemek', 'çok üzülmek: ''Ahmet Kerim, o gün bu kaygı ile içini yedi durdu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7004, 'içinin ateşi küllenmek', 'sıkıntıdan kurtulmak: ''İçimin ateşi hiç küllenmedi. Seneler geçtikçe daha alevleniyor. Evlat acısı bu ...'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7005, 'içinin yağı erimek', 'telaş veya kaygı ile üzülmek: ''İçimizin yağı eridiği hâlde umursamadığımızı göstermek için kendimizi cendereye soktuğumuz yıllar...'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7006, 'içli dışlı olmak', 'karşılıklı olarak candan ve içten davranmak, teklifsiz görüşmek: ''Toprakla insan hiçbir edebiyatta böylesine içli dışlı değildir.'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7007, 'içli dışlı tanımak', 'yakından, bütün özellikleriyle bilmek: ''Kaç kat elbiseleri olduğuna varıncaya kadar içli dışlı tanıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7008, 'içtikleri su ayrı gitmemek', 'sıkı fıkı dost, arkadaş olmak: ''İçtikleri su ayrı gitmez, her derdini onunla paylaşırdı.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7009, 'idare etmek', '1) yönetmek, çekip çevirmek: ''Devleti, sadece idare edenlerin sorumluluğuna bırak-mak ve bir daha onu düşünmemek, sosyal şuura sahip olmamak demektir.'' -M. Kaplan. 2) tutumlu kullanmak: ''Lakin siz, yine sabaha kadar kalacakmışız gibi idare edin mumu.'' -R. N. Güntekin. 3) yetmek, yetişmek: ''Evler ve dükkânların Ahmet''i idare edeceği belli idi.'' -S. F. Abasıyanık. 4) alışverişte yeterli olmak, kurtarmak: Bu kumaşı o fiyata veremem; idare etmez. 5) göz yummak, hoş görmek; 6) örtbas etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7010, 'idaresini bilmek', 'yerine göre harcamak, tutumlu davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7011, 'iddiaya girmek (tutuşmak)', 'karşıt iddialarda bahse girişmek: ''Balkonda yan yana oturmuş, bir yandan tutulan Ay''ı izlerken, bir yandan da iddiaya girmiştik.'' -N. Eray.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7012, 'idman yapmak', 'beden hareketleri yapmak: ''Evinden yalnız idman yapmak için çıktığına eminim.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7013, 'idrak etmek', '1) akıl erdirmek, anlamak, kavramak. 2) erişmek, ulaşmak: Cumhuriyetin yetmiş beşinci yılını idrak ettik. 3) ruh b. algılamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7014, 'ifade vermek', ' huk. bir olayla ilgili olarak gördüğünü, bildiğini yetkili veya ilgili kimseye söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7015, 'iflah olmamak', '1) onmamak, düzelmemek: ''Dal çürük çıktı mı otuz metreden düşen iflah olmuyor artık.'' -H. Taner. 2) doğru davranışta bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7016, 'iflahı kesilmek', 'çaresiz kalmak: ''Benim dört çeşit insan karşısında iflahım kesilir.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7017, 'iflahını kesmek', ' tkz. gücünü tüketmek, bir daha düzelemeyecek bir duruma getirmek: ''Bunlar dişlerine kestirdikleri mahkûma iflahını kesinceye kadar gaddarca saldırırlar.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7018, 'iflas bayrağını çekmek (borusunu çalmak)', ' tkz. 1) ticarette batmak; 2) her şeyini yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7019, 'iflas etmek', '1) bir kimse veya kuruluş için mahkeme kararıyla anaparasını yitirdiği açıklanmak, batmak: ''Ayna ithal edermiş, sonra iflas etmiş, az buçuk oynatmış.'' -S. F. Abasıyanık. 2) mec. düşünce, iddia, tez, kimse vb. yenilgiye uğramak, değeri düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7020, 'ifrat tefritte kalmak (bulunmak)', 'herhangi bir konuda çok ileri gitmek veya geride kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7021, 'ifrata kaçmak', 'çok ileri gitmek, aşırı davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7022, 'ifrata vardırmak', 'bir şeyin ölçüsünü kaçırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7023, 'ifrit kesilmek (olmak)', 'çok öfkelenmek, çok kızmak: ''Arzuma karşı konulunca ifrit kesildiğimi pek iyi bildiğinden ses çıkarmadı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7024, 'iftihara geçmek', 'okuldaki başarısı ve iyi davranışları sebebiyle üstün öğrenci seçilmek, övünç çizelgesinde yer almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7025, 'iftira atmak', 'iftira etmek: ''Elin nur topu gibi kızına iftira atmak doğru mu?'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7026, 'iftira çalmak', 'iftira etmek: ''On parmağınızda on kara, iftira üstüne iftira çalıyorsunuz.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7027, 'iftiraya uğramak', 'kasıtlı ve asılsız suç yüklenmek: ''Uykusuzdum, yorgundum, bir otobüs dolusu insanın içinde iftiraya uğramıştım.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7028, 'iğfal etmek', '1) esk. aldatmak, kandırmak, baştan çıkarmak: ''Bu takdirde hem kendilerini hem de milleti iğfal etmiş olurlar.'' -Atatürk. 2) ırzına geçmek, tecavüz etmek: ''Bir genç kızı izdivaç vaadiyle iğfal etmiş bir adamın mesuliyetini, vicdan azabını ve nihayet hicabını duyuyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7029, 'iğne atsan yere düşmez', 'çok kalabalık: ''Sabah sabah davullar vurulup meydan kurulur. Aman öyle bir kalabalık olur ki iğne atsan yere düşmez.'' -E. C. Güney.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7030, 'iğne deliği gibi', 'küçücük.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7031, 'iğne deliğinden geçmek', '1) aşırı derecede zayıflamak; 2) herhangi bir işte, durumda zorlu bir süreçten geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7032, 'iğne deliğinden Hindistan''ı seyretmek', 'küçük bir olaydan büyük anlamlar çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7033, 'iğne deliğine girmek', 'kimsenin bulamayacağı bir biçimde gizlenmek, saklanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7034, 'iğne ile kuyu kazmak', 'yetersiz araçlarla, sürekli ve sabırlı bir biçimde çalışıp çok güç olan veya çok ağır yürüyen bir işi başarmaya çalışmak: ''İğne ile kuyu kazmak gibi bir şeydi oymacılık.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7035, 'iğne ipliğe dönmek', 'çok zayıflamak: ''Sabun toprakta eridikçe insanın düşmanı da oturduğu yerde erir, iğne ipliğe dönermiş.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7036, 'iğne üstünde oturmak', 'diken üstünde oturmak: ''Konuk kadının durgunluğu evdeki tedirginliktendi, iğne üstünde oturuyormuşçasına eğretiydi duruşu.'' -B. Günel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7037, 'iğne yapmak (vurmak)', 'iğne ile vücuda sıvı bir ilaç vermek: ''Ölecek miyim? İğne yap bana doktor diyordu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7038, 'iğne yemek', 'iğne olmak: ''Sonunda doktorların ısrarıyla bir sürü kuduz iğnesi yedi.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7039, 'iğne yutmuş ite (maymuna) dönmek', ' argo zayıf ve bitkin duruma gelmek: ''Birbirimizle kavga etmekten, bekârlıktan, biraz açlıktan, iğne yutmuş ite dönmüştük.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7040, 'iğneyi kendine, çuvaldızı başkasına batır', 'başkasına zararı dokunacak bir davranışı yapmadan önce iyi düşün, kendi kendini eleştir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7041, 'ihaleye çıkarılmak', 'eksiltmeye veya artırmaya çıkarılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7042, 'ihanete uğramak', 'aldatılmak, sadakatsizlik görmek: ''Nerede sadakat beklersek orada ihanete uğrarız.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7043, 'ihata etmek', '1) çevirmek, çevrelemek, kuşatmak, sarmak: ''Dıştan uzatılmış bir merdivenle binanın üst kısmını ihata eden bir balkona çıkılıyor.'' -H. S. Tanrıöver. 2) mec. kavramak, anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7044, 'ihdas etmek', '1) ortaya çıkarmak, meydana getirmek; 2) kurmak; 3) mec. bir şeyin olmasına, ortaya çıkmasına sebep olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7045, 'ihraç etmek', '1) yurt dışına mal veya hizmet satmak; 2) mec. çıkarmak, dışarı atmak: ''Dimağlar da aynıyla hazım cihazı gibi kendisine verilen yemeğin ihtiyaca müsait olan kısmını alır, diğerini tart ve ihraç eder.'' -H. S. Tanrıöver.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7046, 'ihrama girmek', 'hac görevini yerine getirmek üzere ihram giymek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7047, 'ihramdan çıkmak', 'hac görevini tamamladıktan sonra giyilen ihramı çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7048, 'ihtarda bulunmak', 'ihtar etmek: ''Mahpushane müdürüne haber salarak ''Vazifene dikkat et.'' diyerek ihtarda bulunan oydu.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7049, 'ihtarname çekmek', ' huk. yasal yollarla yazılı uyarı göndermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7050, 'ihtilafa düşmek', 'anlaşamamak, bozuşmak, uyuşamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7051, 'ihtimal vermemek', 'bir şeyin gerçekleşeceğini, olabileceğini hiç düşünmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7052, 'ihtisas yapmak', 'belli bir konuda özel eğitim görmek, uzmanlaşmak, ihtisaslaşmak: ''Ben tütüncülük üzerinde ihtisas yapmıştım.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7053, 'ihtiyaca cevap vermek', 'gereksinimini karşılamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7054, 'ihtiyarın düşkünü, beyaz giyer kış günü', 'daha önce iyi bir durumda olan kişi bu konumunu kaybettiğinde uygun olmayan, yersiz davranışlarda bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7055, 'ihtiyatlı bulunmak', 'beklenmedik sonuçlara karşı hazırlıklı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7056, 'ihtiyatlı davranmak', 'uyanık olmak, düşünerek davranmak: ''Benim soyulmaya değer bir şeyim olduğu kimsenin aklına gelmezdi ama yine de ihtiyatlı davranmak lazımdı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7057, 'ikbali sönmek', 'daha önce iyi olan durumu veya işi bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7058, 'iki ahbap çavuş', ' şaka her yerde hep birlikte görülen, birbirinden ayrılmayan iki arkadaş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7059, 'iki arada bir derede (kalmak)', 'sıkışık, zor şartlar altında (kalmak).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7060, 'iki arada kalmak', 'birbirine karşıt iki kişi arasında ne yapacağını bilemeyerek şaşırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7061, 'iki arslan bir posta sığmaz', 'bir ülkede iki baş egemen olamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7062, 'iki at bir kazığa bağlanmaz', 'ayrı ayrı düşünceleri ve kişilikleri bulunan iki kişi bir arada yaşayamaz, bir işi birlikte yapamazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7063, 'iki ateş arasında kalmak', 'zor bir durumda karar verememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7064, 'iki ayağını bir pabuca sokmak', 'birini bir işi hemen yapması için çok sıkıştırmak: ''Nerelerdesiniz, İhsan Bey? Hem sabah sabah iki ayağımı bir pabuca sokuyorsunuz hem ortalarda görünmüyorsunuz.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7065, 'iki baş bir kazanda kaynamaz', 'ayrı ayrı düşünceleri ve kişilikleri bulunan iki kişi bir arada yaşayamaz, bir işi birlikte yapamazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7066, 'iki büklüm olmak', '1) yorgunluk, hastalık, yaşlılık vb. nedenlerle beli bükülmek, öne doğru eğilmek: ''İnsanlar iki büklüm olup düştükleri konduların içinde dozer kasnaklarının sesiyle irkildiler.'' -L. Tekin. 2) mec. riyakârlık, dalkavukluk, gerçek olmayan saygı vb. nedenlerle iki kat olup öne eğilmek: ''Değil bu yerlerin başkanına hatta hademesine saygılarını sunmak için iki büklüm olurlar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7067, 'iki cambaz bir ipte oynamaz', 'kurnazlıkta eşit olan iki kimse birbirlerini aldatamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7068, 'iki cami arasında kalmış beynamaz', 'iki yoldan hangisini tutacağını şaşırmış kimse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7069, 'iki çıplak bir hamama yakışır', 'iki yoksul kimsenin birbiriyle evlenmesi uygundur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7070, 'iki çift laf (lakırtı veya söz) etmek', '1) birkaç söz söylemek: ''O, keyfini etsin; karşılaştığı bir ahbapla iki çift lakırtı etsin de siz ne olursanız olun.'' -N. Ataç. 2) bir araya gelerek sohbet etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7071, 'iki deliye bir uslu koymuşlar', 'birbirleriyle anlaşamayan, kavga eden iki kişinin arasını bulacak bir akıllının olması gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7072, 'iki dinle bir söyle', 'çok konuşmak doğru değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7073, 'iki dirhem bir çekirdek', 'çok güzel ve özenli giyinmiş: ''İki dirhem bir çekirdek kadınların başlarında şemsiye, ellerinde de yelpaze.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7074, 'iki el bir baş içindir', 'ancak kendi geçimini sağlayabilenler, başkalarına yardım edecek bir durumda değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7075, 'iki eli (birinin) yakasında olmak', 'kıyamette ondan davacı olmak: ''Babanın kanını yerde korsan öteki dünyada iki elim yakanda diye kışkırtmıştı.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7076, 'iki eli böğründe kalmak', 'çaresiz kalıp ne yapacağını bilememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7077, 'iki eli (kızıl) kanda olsa', '''elindeki iş ne kadar önemli olursa olsun'' anlamında kullanılan bir söz: ''Eğer gece vakti hekim lazım olursa sen benim pencerenin altına gel, bir nara bas, iki elim kızıl kanda olsa yetişirim.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7078, 'iki eli şakaklarında düşünmek', 'derin derin düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7079, 'iki eli yanına gelmek', 'ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7080, 'iki elim yanıma gelecek', 'doğru söylendiği kanıtlanmak istendiğinde ''öleyim ki doğru söylüyorum'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7081, 'iki emini bir yemin aralar', 'birbirinin doğruluğuna güvenerek birlikte iş yapmakta olan iki kişiden biri, hile yapmadığına arkadaşını inandırmak için yemin ediyorsa artık güven bozulmuş demektir, ayrılmaları gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7082, 'iki gönül bir olunca samanlık seyran olur', 'birbirini sevenler için zenginlik önemli değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7083, 'iki gözü iki çeşme', '1) sürekli ağlar durumda. 2) sürekli ağlayan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7084, 'iki gözü iki çeşme ağlamak', 'sürekli veya çok ağlamak: ''Sen gittin de aylarca yas tuttu, iki gözü iki çeşme ağladı.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7085, 'iki hırtı, bir pırtı', 'aşırı yoksulluğu anlatan bir söz: ''Getirdiğimiz iki hırtı, bir pırtıyla bu ev döşenmez.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7086, 'iki kaptan bir gemiyi batırır', 'bir işi iki kişi yürütemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7087, 'iki kardeş savaşmış, ebleh buna inanmış', 'iki kardeş arasındaki anlaşmazlık geçicidir, bu durumu gerçek ve sürekli sanmak saflıktır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7088, 'iki karılı evde toz diz boyu olur', 'bir işi iki kişi yürütemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7089, 'iki karpuzu bir koltuğa sığdırmak', 'aynı anda iki işi veya görevi yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7090, 'iki kat olmak', 'iki büklüm olmak: ''Ali, birdenbire zayıflamak, birdenbire saçlarını ağarmış görmek, birdenbire belinde müthiş bir ağrı ile iki kat oluvermek, hemen yüz yaşına girmiş kadar ihtiyarlamak istiyordu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7091, 'iki kere iki dört eder', '''gerçekliğinden şüphe edilmeyecek kadar açık'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7092, 'iki kişi dinden olursa bir kişi candan olur', 'iki kişi yalan yere yemin edip dinden çıkarak bir kimsenin cinayet işlediğine tanıklık ederlerse o kimse asılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7093, 'iki kulak bir dil için', 'çok dinleyip az söylemeli.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7094, 'iki lafı (sözü) bir araya getirememek', 'düşündüğünü doğru dürüst ifade edememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7095, 'iki lakırtı etmek', 'iki çift laf etmek: ''Genç olduk, ihtiyar olduk, şu adama sor bakalım, bir gün, bir saatçik olsun oturup benimle iki lakırtı etmiş midir?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7096, 'iki lakırtıyı bir araya getirmek', 'meramını kısaca, düzgün ve açık bir biçimde anlatmak: ''Kız bir türlü iki lakırtıyı bir araya getirip kendini müdafaa edemediği için lalanın başı derde girmemiş oluyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7097, 'iki ölç, bir biç', 'bir iş yaparken ayrıntıları ve sonuçları iyice düşünülmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7098, 'iki paralık olmak', 'değerini yitirmek: ''Herifi enseleyemezsem diye, aklı başından gidiyordu. Mahalledeki on beş yıllık bekçilik haysiyeti iki paralık olacaktı.'' -S. Kocagöz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7099, 'iki rahmetten (iyilikten) biri', '''çok acı çeken ağır hastalar için ya iyileşsin ya ölüp kurtulsun, böyle çekmesin'' anlamında kullanılan iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7100, 'iki satır laf etmek (konuşmak)', 'dostça biraz söyleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7101, 'iki seksen uzanmak', ' alay bir çarpma, vurma sonucu boylu boyunca serilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7102, 'iki serçeden börek olur', 'insanların birbirlerine her zaman gereksinimleri olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7103, 'iki söz bir pazar', '''uzun boylu pazarlık etmeden'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7104, 'iki testi tokuşunca biri elbet kırılır', 'kavgaya tutuşan iki kişiden biri elbette yenilir ve zarara uğrar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7105, 'iki tımar bir yem yerine geçer', 'atı sık sık tımar etmek, onu yemle beslemek kadar önemlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7106, 'iki ucu boklu değnek', ' kaba ne yönden bakılırsa bakılsın çözülmesi çok güç iş veya durum.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7107, 'iki ucunu bir araya getirememek', 'gelirle gideri denkleştirememek, işleri düzene koyamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7108, 'iki yakası bir araya gelmemek', 'geçim sıkıntısından bir türlü kurtulamamak, borçtan kurtulamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7109, 'iki yakasını bir araya getirememek', 'maddi sıkıntıdan kurtulup rahata erememek: ''Burada kızlar çok pahalıdır. Evlenen adam kolay kolay iki yakasını bir araya getiremez. Kızın bütün sülalesine hatırı sayılır hediyeler sunmak şarttır.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7110, 'ikilemde kalmak', 'iki şey arasında karar verememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7111, 'ikileme düşmek', 'karar verememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7112, 'ikili oynamak', '1) karşı olan yanlardan hem birini hem öbürünü destekler görünmek; 2) at yarışlarında birinci ile ikinciyi tahmin edip para yatırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7113, 'ikinci baharı yaşamak', 'ileri yaşlarda mutluluk, refah ve esenlik içinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7114, 'ikinci gelmek', 'bir yarışmada birinciden sonraki dereceyi almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7115, 'ikinci plana düşmek', 'bir kimsenin veya topluluğun gözünde eski önemini, değerini yitirmek: Yanlış tutumu yüzünden ikinci plana düştü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7116, 'ikindiden sonra dükkân açmak', 'bir işe başlamakta geç kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7117, 'ikisi bir kapıya çıkmak', 'aynı sonuca varmak, aynı sonucu doğurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7118, 'ikisini bir kazana koysalar kaynamazlar', 'aralarındaki anlaşmazlık o kadar büyüktür ki onları uzlaştırma çaresi bulunamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7119, 'ikişer olmak', 'ikişer ikişer sıraya dizilmek: ''Arkadan hâkî esvaplı, ikişer olmuş rüştiye çocukları bağrışarak kaynaşıyorlardı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7120, 'ikiz doğurmak', '1) ikiz bebek dünyaya getirmek; 2) mec. herhangi bir işte çok sıkıntı çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7121, 'ikrah getirmek', 'tiksinmeye, iğrenmeye başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7122, 'ikram görmek', 'ağırlanmak: ''Mal müdürü, vergi kâtibi, evkaf memuru gibi her zaman işinin düşeceği nüfuzlu adamlarla senli benli konuşur, odalarına uğradıkça başköşede ikram görürdü.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7123, 'ikrar vermek', 'söz vermek: ''İkrar verdi cahil gönlüm inandı / Seherin yelleri esti gelmedi'' -Karacaoğlan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7124, 'iktidardan düşmek', '1) devlet yönetiminde yetkiyi başka bir partiye bırakmak zorunda kalmak; 2) cinsel gücü azalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7125, 'ilaç gibi', 'işe yarar, her derde deva.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7126, 'ilaç gibi gelmek', '1) iyileşmeyi veya çözümü kolaylaştırmak; 2) rahatlatmak, huzura kavuşturmak: ''Böylesi zor bir iş için çalışmak bana ilaç gibi geliyordu.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7127, 'ilaç için olsun', 'bir şeyin hiç bulunmadığını anlatmak için kullanılan bir söz: ''Silifke''de dalmaya başladılar ama bir gün, iki gün, on gün, denizde ilaç için olsun tek bir sünger bulamadılar.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7128, 'ilaç için yok', 'hiç yok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7129, 'ilah gibi', 'çok yakışıklı (erkek).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7130, 'ilan vermek', 'çeşitli basın yayın organlarıyla bir durumu duyurmak, açıklamak: ''Dün, bütün akşam gazetelerine ilan verdim.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7131, 'iler tutar yeri olmamak (kalmamak)', 'çok dağınık, kötü, bozuk veya berbat bir duruma gelmek: ''Mamafih, artık iler tutar yeri kalmayan paltosunu eskiciye satmak suretiyle bu kararını biraz daha uzattı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7132, 'ileri almak', '1) öne almak; 2) saati önceki vakte almak, öne ayarlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7133, 'ileri atılmak (çıkmak)', 'öne doğru çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7134, 'ileri geçmek', '1) öne geçmek; 2) üstün bir makama geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7135, 'ileri geri etmemek', 'uzun boylu tartışmamak, sorgu sual etmemek: ''Hiç pazarlığa sorguya kalkışmadan, hiç ileri geri etmeden dayağa başladılar, vurduklarını da attan aşağı yıktılar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7136, 'ileri geri konuşmak (söz etmek, laflar etmek)', 'yersiz ve gönül kıracak biçimde konuşmak: ''Şoför yolda ileri geri konuştu.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7137, 'ileri gitmek', '1) söz ve davranışta ölçü dışına çıkmak, gereksiz, aşırı davranışta bulunmak: ''Hatta daha ileriye giderek başka ve daha tuhaf şeyler düşündüm.'' -S. F. Abasıyanık. 2) ilerlemek, gelişmek: ''Hiçbir medeniyet bütün alanlarda başka bir medeniyetten daha ileri gittiğini iddia edemez.'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7138, 'ileri götürmek', 'bir durum veya davranışta ölçüyü aşmak: ''Ukalalığı daha da ileri götürmüştü.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7139, 'ileri sürmek', '1) öne doğru yürütmek; 2) bir düşünceyi veya tasarıyı önermek, serdetmek: ''Ne var ki sihirbaz parayı geri vermedi. Çünkü işin aslını bildiğini ileri sürüyordu.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7140, 'ileri varmak', 'ileri gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7141, 'ilerisini gerisini düşünmemek', 'sonucun ne olacağını hesaplamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7142, 'ilerisini gerisini hesaplamamak', 'herhangi bir konuda çok ve ayrıntılı düşünmeden hareket etmek, tedbirsizce, ihtiyarsızca davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7143, 'ileriyi görmek', 'uzağı görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7144, 'iletişim kurmak', 'bilgi, haber vb. alışverişi yapmak: ''Falih Rıfkı Atay da düşünen ve iyi iletişim kuran bir insandı.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7145, 'ilgi çekmek (uyandırmak)', 'çevresinde ilgiyi, dikkati ve merakı üzerine toplamak, alaka çekmek, alaka toplamak veya alaka uyandırmak: ''Öyle bir renk olmalı ki hemen karşıdan hem ilgi uyandırmalı hem de insan etkilenmeli.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7146, 'ilgi duymak', 'bir işe, bir olaya, bir kimseye önem vermek, yakınlık duymak: ''Yeni istidatlara her zaman ilgi duyan bir büyük sanatçı idi.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7147, 'ilgi görmek', 'ilgi çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7148, 'ilgi göstermek', 'ilgisini esirgememek, belli etmek: ''Eski gardiyan boş gözlerle bakıyor, en küçük bir ilgi göstermiyordu.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7149, 'ilgi toplamak', '1) ilgisini yoğunlaştırmak, belli etmek; 2) ilgi görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7150, 'ilgisini çekmek', 'ilgisini, dikkatini ve merakını üzerinde toplamak, alaka duymak: ''İki üç ders içinde ilgisini çeken bir öğrenci olmuştum.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7151, 'ilgisini kesmek', 'bir kimse veya şeyle bütün bağlarını koparmak, ilişkisi kalmamak, alakayı kesmek: ''Çocuğu babasına bırakıp kendisi erkekle ilgisini keser ve familyasının yanına döner.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7152, 'ilham almak', 'esinlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7153, 'ilham etmek (vermek)', 'içe doğmasına sebep olmak, esindirmek: ''Osmanlı müverrihleri, acı tecrübelerin ilham ettiği bu insafsız hükümlere elbette ki itibar etmeyeceklerdi.'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7154, 'iliği kemiği donmak', 'çok üşümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7155, 'iliği kemiği ısınmak', 'üşümüşken vücudu iyice ısınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7156, 'iliğine (iliklerine) kadar', 'iyice, en son sınırına dek: ''Ilık bir mart güneşi, iliklerine kadar ısınıyor insan.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7157, 'iliğine işlemek (geçmek)', '1) çok ıslanmak: ''Korunaklı sandığım köşe o kadar da korunaklı değildi. Yağmur iliklerime kadar işlemişti'' -S. Dölek. 2) çok üşümek; 3) bütün varlığını kaplamak, çok etkilenmek: ''En büyük payın yine de Celile''nin iliklerine işlemiş korkusu olacağını zannederim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7158, 'iliğine kadar ıslanmak', 'çok ıslanmak: ''İliklerine kadar da ıslanmış ve soğuk almış.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7159, 'iliğini kemirmek', '1) çok etkilemek; 2) sömürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7160, 'iliğini kurutmak', 'canından bezdirecek kadar sıkıntı vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7161, 'ilik gibi', '1) çok lezzetli, iyi pişmiş (et); 2) argo çok güzel, istek uyandıran (kadın veya kız).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7162, 'iliklerinde duymak', 'benliğinde yoğun bir biçimde hissetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7163, 'ilişiği kalmamak', 'var olan ilgisi, bağlılığı artık olmamak: Onunla hiçbir ilişiğim kalmadı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7164, 'ilişiğini kesmek', 'hiçbir ilgisi kalmamak, bağlantılarını koparmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7165, 'ilk vuran okçudur', 'amaca başkalarından önce ulaşan, işinin ehlidir ve kazançlı çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7166, 'ilkel kalmak', 'gelişmemek, ilk durumunda kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7167, 'illallah dedirtmek', 'bezdirmek, usandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7168, 'illallah demek (etmek)', 'usanmak, bıkmak, bezmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7169, 'ilmik atmak', 'ilmik yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7170, 'ilmini almak', 'bir işin özelliklerini, işleyişini, en ince ayrıntılarına kadar iyice öğrenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7171, 'iltibasa yol açmak', 'karışıklığa sebep olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7172, 'iltizam etmek', 'keseneğe almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7173, 'imam evinden aş, ölü gözünden yaş çıkmaz', 'bir şey alınması imkânı olmayan yerden, bir şeyler vermesini beklemek boştur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7174, 'imam osurursa, cemaat sıçar', 'yöneticilerin kötü bir iş yapmaları, onların buyruğundakilerin daha kötü bir iş yapmalarına yol açar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7175, 'imamın abdest suyu gibi', 'soğuk veya sıcak olması gerekirken ılık olan içecekler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7176, 'imamkayığına binmek', 'ölü tabut içinde veya sal üzerinde mezarlığa götürülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7177, 'iman getirmek', '1) gönül rızasıyla Müslümanlığı kabul etmek; 2) yürekten inanmak: ''Onun özveri, alçak gönüllülük taşan yüzünü görünce hayatın sadece bir para çekişmesi olmadığına iman getirir, ferahlardınız.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7178, 'imana gelmek', '1) Müslümanlığı kabul etmek; 2) en sonunda doğruyu söylemek; 3) sonradan bir şeyi kabul edip uymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7179, 'imana getirmek', '1) Müslümanlığı kabul ettirmek; 2) istenilen biçimde davranmayı zorla kabul ettirmek: ''Müslüman olmadan varmayacağını anlayınca kırkyıllık kart gâvuru imana getirdi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7180, 'imanı gevremek', ' tkz. (imanı) çok yorulmak veya sıkıntı çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7181, 'imanı yok', '1) (imanı) acımasız, insafsız; 2) kahrolası!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7182, 'imanına kadar', ' tkz. (imanına) ağzına kadar, son kertesine kadar, tıka basa, alabildiğince.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7183, 'imansız gitmek', 'Tanrı''ya inanmadan ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7184, 'imara açılmak', 'yapılaşma yasağı olan bir yerin üzerine yapı yapılmasına izin vermek: ''Boğaziçi sırtları imara açıldı.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7185, 'imbikten çekmek', 'damıtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7186, 'imdada (imdadına) koşmak (yetişmek)', 'çok zor ve tehlikeli bir anda yardım etmek: ''Aşağı kattan gürültüyü işiterek imdadıma koşan annem evvela neye uğradığını bilememişti.'' -Y. K. Karaosmanoğlu. ''Neyse bu işte de otelci imdadımıza yetişti.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7187, 'imdadına yetişmek (erişmek)', 'yardım etmek: ''Hakkı Bey karısının imdadına erişti. Selma Hanım''ın müşkül bir vaziyette kaldığını hissederek söze karıştı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7188, 'imece günü bulutlu, görmeyene ne mutlu', 'zamanın elverişli olup olmadığına bakmadan yardıma gelenleri bulunan iş sahibine ne mutlu.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7189, 'imeceye girmek', 'imece yoluyla yapılacak çalışmaya katılmak: ''Gençlerle imeceye girme gücü yitirilmediği sürece yaşlanmanın ertelenebileceğini kanıtladı.'' -A. Cemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7190, 'imkân vermek', 'olanak sağlamak: ''Nasıl boş bulunup o gazeteci kızın resmini çekmesine imkân verdi?'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7191, 'imlaya gelmemek', 'bir şey veya düşünce düzenlenemeyecek kadar karışık olmak, yönteme uyamayacak bir durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7192, 'imtihan vermek', '1) sınanmak; 2) tehlikeli ve zor bir durumdan zarar görmeden iyi bir sonuca ulaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7193, 'imtihana çekmek', '1) bilgisini ölçmek; 2) denemek, sınamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7194, 'imza toplamak', 'bir dilekçeyi veya öneriyi, destekleyenlere imzalatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7195, 'imza vermek', 'imza atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7196, 'imzayı basmak (çakmak)', ' tkz. imzalamak, imza etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7197, 'in cin top oynamak', 'hiçbir canlı varlık bulunmamak: ''Adam inlerle cinlerin top oynadığı yolda mezarlığın yıkık duvarına sıçradı.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7198, 'in cin yok', 'hiç kimse yok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7199, 'in gibi', 'dar ve karanlık (yer).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7200, 'inadı tutmak', 'çok direnmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7201, 'inadım inat olmak', 'söylediğinden veya yaptığından vazgeçmemek, çok direnmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7202, 'inan olsun', '''bana inanınız'' anlamında kullanılan bir söz: İnan olsun, ben bunu biliyordum.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7203, 'inanca vermek', 'güvence vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7204, 'inanılır gibi (şey) değil', 'çok şaşırılan, hayret edilen veya hayranlık duyulan bir olayla karşılaşıldığında söylenen bir söz: ''O şaşırtıcı yükselişten sonra düştüğü bu durum inanılır şey değil.'' -C. Külebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7205, 'inayette bulunmak', 'inayet etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7206, 'ince eleyip (eğirip) sık dokumak', 'bir şeyi en küçük ayrıntılarına kadar araştırmak, gözden veya elden geçirmek: ''Annesinin bu meseleyi nasıl ince eleyip sık dokuyacağını biliyordu.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7207, 'inceldiği yerden kopmak', 'sonucu neye varırsa varsın: ''İnceldiği yerden kopsun; kimsenin eline kalmamalı, kapılara bakmamalı insan.'' -A. Kilimci.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7208, 'inci gibi', 'küçük, temiz, güzel ve düzgün: ''Pekâlâ elinde inci gibi yazısı var, daha ziyade okuyup da ne olacak?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7209, 'inci (inciler) döktürmek', 'bir konuda önemli, anlamlı ve güzel söz söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7210, 'incir babadan, zeytin dededen', 'bağ, bir kuşak geçecek kadar yaşlandıktan sonra bol ürün verir, zeytinin bol ürün verebilmesi için en azından iki kuşaklık bir zaman geçmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7211, 'incir çekirdeği doldurmamak', 'çok az veya çok önemsiz olmak: ''İncir çekirdeği doldurmayan konularda bir araba lakırtı söylerler.'' -N. Uygur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7212, 'ineğin sarısı, toprağın karası', 'çiftçiler arasında ineğin sarı, toprağın kara renkli olanı beğenilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7213, 'inek gibi süt vermeyen, öküz gibi kutan sürer', 'yorucu olmayan işlerde kendisinden yararlanılamayan kişi, ağır işlere koşulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7214, 'infial uyandırmak', 'kızgınlığa yol açmak, öfke yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7215, 'infiale kapılmak', 'kızgınlık, öfke duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7216, 'infilak etmek', '1) patlamak; 2) mec. birdenbire şiddetle ortaya çıkmak: ''... biraz sonra hiddet, birikmiş kin, kıskançlık birdenbire infilak etti.'' -A. H. Tanpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7217, 'İngiliz ipi ile asılmak', 'İngiliz sicimi ile asılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7218, 'İngiliz sicimi ile asılmak', 'bir işi ustasına yaptırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7219, 'inhisara (inhisarına) almak', 'tekeline almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7220, 'inim inim inlemek', 'sürekli olarak inlemek, çok sıkıntıda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7221, 'inim inim inletmek', 'birini büyük sıkıntıya sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7222, 'inisiyatifi ele almak (geçirmek)', 'karar verme yetkisini kullanmak: ''Bu kurnaz dilenci böylece inisiyatifi göstermelik de olsa eline alıp sağa sola emirler vermeye başladı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7223, 'inisiyatifini kullanmak', 'gerekli kararları öncelikle almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7224, 'inkârdan gelmek', 'inkâr etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7225, 'inkıraz bulmak', 'batmak, çökmek, dağılmak, yok olmak, son bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7226, 'inkıraz gelmek', 'çökmek, dağılmak: ''O zaman da bozgun ve inkıraz geldi, çattı.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7227, 'inkıraza uğramak', 'batmak, dağılmak, çökmek, yok olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7228, 'inkıtaya uğramak', 'kesilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7229, 'inkisarı tutmak', 'ilenci gerçekleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7230, 'insafa gelmek', 'acımasız ve haksız tutumdan vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7231, 'insafına kalmış', 'bir şeyin bir kimsenin doğruluğuna, adaletine ve isteğine bağlı olduğunu belirten bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7232, 'insaflı çıkmak', 'anlayışlı, hoşgörülü olduğu belli olmak: ''Gelinin babası insaflı çıktı da verdi Gülizar''ı bizim oğlana.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7233, 'insafsızlık etmek', 'acımamak, insafsızca davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7234, 'insan (adam) kıymetini insan (adam) bilir', 'bir kimsenin, bir şeyin değerini ancak o konuda uzmanlığı olanlar bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7235, 'insan ayağı değmemiş (basmamış)', 'içine insan girmemiş, içinde insan olmayan: ''Yine yeşil yosunlu, insan ayağı değmemiş gibi yokuşlar var ağaçlı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7236, 'insan ayaktan, at tırnaktan kapar', 'birçok hastalık insana ayağını üşütmesi, ata da tırnağı yoluyla gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7237, 'insan beşer, kuldur şaşar', 'kişinin zaman zaman şaşırmasını, yanılmasını hoş görmek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7238, 'insan çeşit çeşit, yer damar damar', 'toprağın her kesimi ayrı ayrı nitelikler taşıdığı gibi insanlar da birbirlerinden farklı özelliklere sahiptirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7239, 'insan doğduğu yerde değil, doyduğu yerde', 'insan doğduğu yeri değil geçimini sağladığı yeri yurt edinir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7240, 'insan eli değmemiş (dokunmamış)', 'bakımsız kalmış yer.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7241, 'insan eti yemek', 'birini çekiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7242, 'insan gibi', 'insanlara yaraşır biçimde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7243, 'insan gönlünün artığını söyler', 'insanlar şaka yaparken içlerinden geçeni yansıtırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7244, 'insan göre göre, hayvan süre süre (alışır)', 'insanlar konuşarak birbirlerini daha iyi anlarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7245, 'insan içine çıkmak', 'toplum içine karışmak, başkalarıyla ilişki kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7246, 'insan insanın şeytanıdır', 'uygunsuz arkadaş, insanı doğru yoldan saptırır, kötülüğe sürükler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7247, 'insan kendini beğenmezse çatlar (ölür)', 'herkes kendini beğenir; bu, kendi aklını beğenmesinin sonucudur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7248, 'insan kıymetini insan bilir', 'bir kimsenin ne kadar değerli olduğunu ancak o kimsenin değerini ölçebilecek nitelikteki insanlar anlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7249, 'insan konuşa konuşa, hayvan koklaşa koklaşa', 'insanlar konuşarak birbirlerini daha iyi anlarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7250, 'insan kuş misali', 'uzakça bir yere gidildiğinde söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7251, 'insan sözünden (ikrarından), hayvan yularından tutulur', 'yularından tutulan hayvan başka yöne sapamadığı gibi insan da söylediği sözün dışına çıkamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7252, 'insan yedisinde ne ise yetmişinde de odur', 'kişinin çocukluğundaki huyları, özellikleri yaşlılığında da değişmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7253, 'insan yükü (eti) ağırdır', '1) hiç kimse başka bir kimseye yük olmamalıdır; 2) yatalak insanı kaldırmak, yatırmak güçtür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7254, 'insanda akıl bırakmamak (koymamak)', 'düşünceleri karmakarışık yapmak, kararsızlığa yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7255, 'insanın (adamın) alacası içinde, hayvanın alacası dışındadır', 'hayvanların işe yarayıp yaramayacakları görünüşlerinden belli olur ancak insanların kötü huylu olup olmadıkları dışarıdan anlaşılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7256, 'insanın adı çıkacağına canı çıksın', 'insanın haklı veya haksız yere adı bir defalık kötüye çıktı mı ondan sonra yaptıkları hep o gözle değerlendirilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7257, 'insanın eti yenmez, derisi giyilmez; tatlı dilinden başka nesi var', 'insan kendisini ancak tatlı diliyle sevdirebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7258, 'insanın kötüsü (fenası) olmaz; meğer ki parası olmaya', 'toplum içinde herkesin bir değeri vardır ancak züğürtlere değer verilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7259, 'insaniyet namına', '''insanlığa yakışır duygulara uyarak'' anlamında kullanılan bir söz: ''Hakkında bilgisi olanların aşağıdaki adrese bildirmelerini insaniyet namına rica ederim.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7260, 'insanlık sen de kalsın', '1) karşı taraf iyilik bilmese de sen yine iyilik et; 2) bu işi nasıl olsa sana yaptıracaklar, bari kendiliğinden yap da onurunu koru.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7261, 'insanlıktan çıkmak', '1) çok zayıflamış olmak; 2) insana özgü niteliklerini yitirmek: ''İki üç aydır şu Çukurova''da gezdik, gezdik, insanlıktan çıktık.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7262, 'insanoğlu çiğ süt emmiş', 'insanlardan tam bir doğruluk beklenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7263, 'inşallahla maşallahla', '''çaba harcamadan'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7264, 'inşirah bulmak', 'iç açılmak, ferahlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7265, 'intikam almak', 'öç almak: ''Gözlerimi kapadım ve ilk defa erkeklerden intikam almayı düşünerek kendimi koyuverdim.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7266, 'inzivaya çekilmek', 'toplumdan kaçıp hiçbir şeyle ilgilenmeyerek tek başına yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7267, 'ip inceldiği yerden kopar', 'bir durum, en çürük yerinden patlak verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7268, 'ip kırıldığı (koptuğu) yerden ulanır (bağlanır)', '1) iki kişi arasındaki kırgınlığın giderilmesi için kırgınlık sebebinin giderilmesi gerekir; 2) bozulan bir iş nerede kalmışsa düzeltilmesine oradan başlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7269, 'ip koptuğu yerden bağlanır', 'iki kişi arasındaki kırgınlığın giderilebilmesi için kırgınlık nedeninin ortadan kaldırılması gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7270, 'ipe dizmek', 'boncuk vb.ni ipliğe geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7271, 'ipe gitmek', 'ölüme gitmek: ''Menfaatine dokunan adam, ipe gitmek için lazım gelen hükümleri giyer.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7272, 'ipe sapa gelmemek', 'akla yakın olmamak veya birbirini tutmamak: ''Söyledikleri zaten ipe sapa gelmez şeyler.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7273, 'ipe un sermek', 'geçersiz birtakım nedenler ileri sürerek istenilen işi yapmaktan kaçınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7274, 'ipek gibi', '1) çok ince, parlak ve yumuşak; 2) güzel, iyi huylu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7275, 'ipi (birinin) eline geçmek', 'yönetimi başkasının eline geçmek, kontrolü başkasının elinde bulunmak: ''İpleri Topal Osman''ın eline geçince bir uysallaşır, bir uysallaşır kâfir!'' -R. Enis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7276, 'ipi çözmek', ' hlk. ilgisini kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7277, 'ipi kırmak', ' hlk. savuşup gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7278, 'ipi koparmak', 'bağlı bulunduğu kuruluşla veya yakınlığı bulunan kişi ile ilişkisini kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7279, 'ipi sapı yok', 'birbirini tutmaz, yersiz, anlamsız.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7280, 'ipin ucunu kaçırmak', ' tkz. yönetimde veya bir şeyi kullanmada gereken ölçüyü yitirmek: ''Bu kadar çalıştığım hâlde, ölçülerim pek mi aykırıdır nedir, yine ipin ucunu kaçırıveririm.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7281, 'ipini kırmak', 'azmak, ele avuca sığmaz bir durum almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7282, 'ipini koparmak', 'başıboş kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7283, 'iple çekmek', 'sabırsızlıkla beklemek: ''Ertesi günün öğleye doğru olan saatlerini iple çekiyordum.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7284, 'ipliği pazara çıkmak', 'kötü nitelik ve suçları ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7285, 'iplik çekmek', '1) kumaştan iplik çıkarmak; 2) iplik eğirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7286, 'ipoteği çözmek (kaldırmak)', 'tutudan kurtarmak: ''Bu ipoteği çözmek sizin elinizde! Bir tek siz çözebilirsiniz onu!'' -N. Eray.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7287, 'ipotek altında tutmak', '1) tutuda tutmak; 2) baskı altına almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7288, 'ipten kazıktan kurtulmuş', 'her türlü kötülüğü yapacak yaradılışta olan (kimse): ''İşte şimdi gördüğünüz gibi hırsızların, esrarkeşlerin, ipten kazıktan kurtulmuş, gözü kanlı canilerin arasında çilemi dolduruyorum.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7289, 'ipten kuşak kuşanmak', 'yoksul düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7290, 'ipucu vermek', 'aranılan gerçeğe ulaştırabilecek şeyle ilgili, onu bulmaya yarayan bilgi vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7291, 'irapta mahalli yok', 'hiçbir değeri ve önemi yok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7292, 'iraptan mahalsiz', 'hiçbir değeri ve önemi yok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7293, 'irtibat kurmak', 'bağlantı sağlamak: ''İrtibat kurduğu adamı gören tek kişi benim.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7294, 'irtihal etmek', 'ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7295, 'İsa''yı küstürdü, Muhammed''i memnun  edemedi', 'iyilik edeyim derken kimseyi memnun edemedi.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7296, 'isabet almak', 'vurulmak, yaralanmak: Düşman uçağı isabet aldı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7297, 'isabet oldu', '''yerinde, tam isteğe uygun'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7298, 'isim yapmak', 'ad yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7299, 'isin yanına varan is, misin yanına varan mis kokar', 'kişi kiminle arkadaşlık ederse ondan kendisine birtakım huylar geçer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7300, 'iskambil kâğıdı gibi devrilmek', 'birer birer ve birbiri ardı sıra devrilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7301, 'iskâna açmak', 'kadastrosunu, planlarını ve altyapısını bitirip bir bölgeyi inşaat yapmaya hazır duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7302, 'iskandil etmek', '1) deniz derinliğini ölçmek; 2) soruşturmak, araştırmak: ''Evde kimsenin olmadığını telefonla iskandil ediyorlarmış diyor Bedri.'' -O. Rifat. 3) mec. bir işin içyüzünü araştırmak, bilgi toplamak; 4) argo gözetlemek, çevreyi kollamak: ''Cevizlerin altını iskandil ederek böğürtlen yığınının gölgesine sığındı.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7303, 'iskele almak', '1) gemi merdivenleri kaldırılıp harekete hazırlanmak; 2) argo bir erkek, bir kadına sarkıntılık etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7304, 'iskelet gibi', 'çok zayıf.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7305, 'iskelete dönmek', 'çok zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7306, 'iskeleti çıkmak', 'çok zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7307, 'islim arkadan gelsin', 'önce istenilen iş yapılsın, gereken şartlar sonradan yerine getirilsin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7308, 'islim tutmak', 'gerekli koşulların olgunlaşmasını beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7309, 'ismi (bile) olmamak', 'adı (bile) olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7310, 'ismi çıkmak', 'adı çıkmak: ''Ama siz ecnebiler ismi çıkmış yerlerden hoşlanırsınız.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7311, 'ismi geçmek', 'adı geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7312, 'ismi gibi bilmek', 'adı gibi bilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7313, 'ismi lazım değil', 'adının anılması gerekmiyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7314, 'ismi lazım değil', '''adının anılması gerekmiyor'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7315, 'ismi var cismi yok', '1) sözü edilen ancak gerçekte var olmayan; 2) adı olmasına karşın görevini, etkinliğini yerine getirmeyen.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7316, 'ismini bağışlamak', 'adını bağışlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7317, 'ismini cismini almak', 'adını, kimliğini belirleyip kaydetmek: ''Fabrika sahibinin ismini cismini aldı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7318, 'ismini cismini bilmemek', 'tanımamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7319, 'israfa kaçmak', 'gereksiz yere aşırı harcamalarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7320, 'istasyon yapmak', 'duraklamak, beklemek: ''Bir geçitte bir dakika kadar istasyon yaparak geçit bekçisiyle yârenlik ettik.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7321, 'istavroz çıkarmak', 'haç çıkarmak: ''Lanet kelimesini her anışında istavroz çıkarıyordum.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7322, 'istediği gibi at koşturmak (oynatmak)', 'keyfince, istediği gibi davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7323, 'istediğini söyleyen istemediğini işitir', 'bir kimseye hakaret etmek, ağır sözler söylemek doğru değildir, o da ağır sözlerle karşılık verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7324, 'istek duymak', 'bir şeye karşı eğilim duymak, arzulamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7325, 'istek uyandırmak', 'istemesine, arzu duymasına yol açmak: ''İnsanda ille de saçını, yanağını okşamak isteğini uyandıran güzel kız çocuklarını andırırdı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7326, 'istemem diyenden korkmalı', 'bir şeyi istemem diyen, fırsat bulduğunda o şeyi elde etmek için aşırı hırs gösterir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7327, 'istenmeyen aş, ya karın ağrıtır ya baş', 'istenmeyerek yapılan işlerden kötü sonuçlar ortaya çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7328, 'isteyenin bir yüzü, vermeyenin iki yüzü kara', 'birinden bir şey isteyen utanır ancak isteği yerine getirmeyen daha çok utanmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7329, 'istifayı basmak', 'herhangi bir sebeple görevinden ani bir kararla çekilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7330, 'istifini bozmamak', 'aldırış etmeyip durum ve davranışını değiştirmemek: ''Adamcağız o akşam, arkasından bir bisiklet çıngırağı duymuş fakat istifini bozmamıştı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7331, 'istihareye yatmak', 'girişilecek bir işin hayırlı olup olmadığını göreceği rüyadan anlamak için abdest alıp dua okuyarak uyumak: ''İstihareye yatmazsın / Doğruca yola girmezsin'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7332, 'istikamet vermek', 'yön vermek, yöneltmek: ''Son otuz senede tarihe nasıl bir istikamet verdiğimizi görüyorum.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7333, 'istikrar bulmak', '1) karar kılmak; 2) yerleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7334, 'istim arkadan gelsin', 'önce istenilen iş yapılsın, gereken şartlar sonradan yerine getirilsin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7335, 'istim üstünde olmak', '1) buharla işleyen araçlar kalkmaya hazır duruma gelmek: ''Gemi istim üstünde, kalkması yakın. Demir almak için süvari bekleniyordu.'' -Z. Selimoğlu. 2) mec. hemen gidecek durumda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7336, 'istirhamda bulunmak', 'istirham etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7337, 'isyan bayrağı (bayrağını) açmak (çekmek)', 'karşı gelmek, başkaldırmak: ''Demek ki bunca senelik kuzu gibi yumuşak başlı karısı da nihayet isyan bayrağını açmıştı.'' -M. Ş. Esendal. ''Yeniçeriler bunu uğursuzluk telakki edip paşaya isyan bayrağı çektiler.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7338, 'isyanları oynamak', 'isyan etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7339, 'iş açmak', 'uğraştırıcı, gereksiz bir durumun ortaya çıkmasına sebep olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7340, 'iş almak', 'yapılması kesinleşen bir işi üstlenmek, taahhüt altına girmek: ''İş alacağım diye, kafasını ve meslek görüşünü de satmamış bir kişilik.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7341, 'iş amana binince kavga uzamaz', 'kavga edenlerden biri aman dilerse çekişme sona erer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7342, 'iş anlatılıncaya kadar baş elden gider', 'kızışmış bir kavgada veya herhangi bir olayda meram anlatmaya fırsat kalmadan olacak olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7343, 'iş ayağa düşmek', 'iş, sorumsuz ve yetkisiz olanların elinde kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7344, 'iş başa düşmek', 'kendi işini kendi görme zorunda kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7345, 'iş bırakmak', 'çalışanlar toplu hâlde işlerini terk etmek, çalışmayı durdurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7346, 'iş bilenin kılıç kuşanın', 'her şey, onu gereği gibi kullanmasını bilene yakışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7347, 'iş bilmek', 'becerikli olmak: ''En zekileri, en iş bilenleri olan Osman her şeyi anladı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7348, 'iş (birinde) bitmek', 'işin bitmesi veya sorunun çözümü birine bağlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7349, 'iş (birinden) bitmek', 'işin sonuçlanması ondan beklenilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7350, 'iş (birine) kalmak', 'işin bitmesi için asıl gayret birine düşmek: ''Aman hemşire hanım... Şimdi iş size kaldı. Siz inat edin. O, sizin ısrarınıza dayanamaz.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7351, 'iş bitirmek', 'bir işi iyi bir sonuca ulaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7352, 'iş çatallanmak', 'bir işte güçlükle karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7353, 'iş çevirmek', 'gizli, dolambaçlı bir iş yapmak: ''Öbürleri şüpheleniyorlar, bir iş çevirdi ama nasıl anlasak diye düşünüyorlardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7354, 'iş çığırından çıkmak', 'amacından saparak düzeltilmesi güç bir durum almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7355, 'iş çıkarmak', '1) çok iş yapmak: ''Yönettiği bütün toplantılarda, batılı bir metotla kısa zamanda verimli iş çıkarmakta üstüne yoktu.'' -H. Taner. 2) gereksiz, uğraştırıcı bir işe yol açmak, sorunlara neden olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7356, 'iş dayıya düştü', 'gayret dayıya düştü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7357, 'iş değil', '1) bir şeyin çok kolay olduğunu belirten bir söz; 2) kınama belirten bir söz: Bu senin yaptığın iş değil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7358, 'iş edinmek', '1) bir şeyi görev, meslek olarak kabul etmek: ''Yazar mutlaka bir diyeceği olan adam değil, yazmayı kendisine iş edinmiş adamdır.'' -N. Ataç. 2) bir şeyi kendi uğraşı, sorunu durumuna getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7359, 'iş görmek', '1) iş yapmak: ''Baş üstünde bir ana işlerinizi görür veya çocuklarımızı doğurur, besler ve büyütür.'' -F. R. Atay. 2) iş yapmaya uygun olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7360, 'iş göstermek', 'yapması için birine iş vermek, iş buyurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7361, 'iş inada binmek', '1) bir işi yapmakta direnmek; 2) zıt görüş ve davranışta ısrar edilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7362, 'iş insanın aynasıdır', 'bir kimsenin nasıl bir kişi olduğu yaptığı işlerden anlaşılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7363, 'iş işten geçmek', 'bir işi gerçekleştirme imkânı kalmamış olmak: Gittiği yerlerde soğuk karşılanışını sonradan anlar ama iş işten geçmiş olur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7364, 'iş karıştırmak', '1) fesat sokmak; 2) zararlı bir iş yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7365, 'iş ki', 'yeter ki: İş ki sınıfını geçsin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7366, 'iş mi?', 'yapılan bir şeyin beğenilmediğini, küçümsendiğini bildiren bir söz: ''Hint postasını getirmek iş mi?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7367, 'iş ola', 'yaptığı iş beğenilmediğinde ''sanki iş görmüş gibi'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7368, 'iş olacağına varır', 'bir soruna aldırmamayı, ne yapılırsa yapılsın yine aynı sonuca ulaşılacağını anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7369, 'iş olsun diye', 'gereksiz bir hareketi belirtmek için kullanılır.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7370, '(iş) ortada olmak', 'yapması gereken kişi belli olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7371, '(iş) pot gelmek', 'sonu iyi olmamak, ters gelmek: ''İşlerin doğru gitmeyen, pot gelen yerleri çok ise de sorulunca söylenecek karşılıklar bulunmuştu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7372, 'iş sarpa sarmak', 'iş, içinden çıkılması zor bir duruma girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7373, 'iş şirazesinden çıkmak', 'düzenini kaybetmek, çığırından çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7374, 'iş tutmak', '1) iş yapmak, çalışmak: ''Biraz çökmüşsün, iş tutuyor musun?'' -F. R. Atay. 2) kaba cinsel ilişkide bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7375, '(iş) üç nalla bir ata kaldı', 'eline önemsiz bir imkân geçtiğinde büyük işlerin düşüne kapılanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7376, 'iş vermek', '1) birine yapacak iş göstermek; 2) argo gönlü olduğunu gösterecek davranışlarda bulunmak, pas vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7377, 'iş yapmak', 'çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7378, 'işaret vermek', 'bir araç kullanarak bir şeyi belli etmek: ''Başı ile evet işareti verdi.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7379, 'işbaşı yapmak', 'iş yerinde işe başlamak: ''Şimdiye kadar sabah postasının çoktan işbaşı yapması, otobüslerin biletçileriyle birlikte seferde olmaları gerekir.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7380, 'işbaşına gelmek', 'yönetici olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7381, 'işe bak!', 'şaşırılacak bir durum karşısında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7382, 'işe girmek', 'göreve, çalışmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7383, 'işe karışmak', '1) herhangi bir konuda katkıda bulunmak, görev almak: ''Her işe karışır, fikrini söyler, uzun uzun nasihatler eder.'' -M. Ş. Esendal. 2) herhangi bir konuda olumsuz yönde müdahale etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7384, 'işe yaramak', 'elverişli olmak: ''Bunları ezberledik de ne oldu? Hiç! Ne o günlerde işimize yaradı, ne de ondan sonra.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7385, 'işemekle deniz pis olmaz', 'doğruluğuna, dürüstlüğüne herkesin inandığı bir kimse, aşağılık kimselerin atmak istedikleri çamurla kirletilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7386, 'işi ahbaplığa dökmek', 'yerli yersiz yakınlık göstermek: ''Şimdi maşallah açılmaya başladım diye söylenirsin, işi ahbaplığa dökersin, olur gider.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7387, 'işi aksi gitmek', 'istenilen sonucu elde edememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7388, 'işi Allah''a kalmak', 'güç şartlar altında, kimseden yardım umudunun kalmadığı bir durumda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7389, 'işi azıtmak', 'yanlış ve aşırı yollara sapmak: ''Hani ya kahve nerede? Bir saattir bekliyorum, hâlâ gelmedi! Yoo! Siz artık işi azıttınız gayri!'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7390, 'işi (bir şeye) vurmak', 'işi değiştirmek: ''O vakit aktör yahut aktris işi meddahlığa vuruyor.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7391, 'işi bitmek', '1) işi sona ermek; 2) hâli, gücü kalmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7392, 'işi bozmak', 'yapılan anlaşmayı, verilen sözü tutmamak: ''Hiç benim yüzüme bakmıyor, işi bozacak bir şey söyleyeceğimden korkuyor gibiydi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7393, 'işi bozulmak', 'yapmakta olduğu işten gereği kadar kazanç sağlayamaz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7394, 'işi ciddiye almak', 'soruna önem vermek: ''İşi ciddiye almış olacak ki hemen okul müdürüne çıkmış, izin istemiş.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7395, 'işi çıkmak', 'başka bir işle meşgul olmak: ''Bazen işi çıkıyor günlerce.'' -F. F. Tülbentçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7396, 'işi ...-e dökmek', 'işi değiştirip bir başka biçime çevirmek: ''Onun işi böyle filozofluğa döküşünde biraz da benim mesuliyetim olmadı değil.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7397, 'işi ...-e vurmak', 'gerekenden başka biçimde davranmak, ... gibi görünmek: İşi şakaya vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7398, 'işi gücü bırakmak', 'yaptığı işten uzaklaşmak: ''Su bulmak için işi gücü bırakarak bütün gün su peşine düşmemiz lazım gelecekti.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7399, 'işi ileri götürmek', 'beklenenden daha aşırı davranışlar içine girmek: ''Ama işi bu kadar ileri götürdüğüne göre, sonuçlarına da katlanması gerekirdi.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7400, 'işi iş, kaşığı gümüş', '''işi tam istediği yolda'' anlamında kullanılan bir söz: ''Geldiğine geleceğine şimdi bin pişman! İzmir''de işin iş, kaşığın gümüş be adam, otursana oturduğun yerde!'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7401, 'işi iş olmak', 'işi yolunda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7402, 'işi ne?', 'ne işi var?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7403, 'işi olmak', '1) yapacak bir şeyi bulunmak; 2) işi istediği gibi bitirmek; 3) uğraşma zorunda olmak: Üstelik sen de buraya girmeye kalkışırsan işimiz var.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7404, 'işi oluruna bırakmak', 'işi belli bir amaca göre değil de, kendi akışı içinde yürütmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7405, 'işi pişirmek', ' tkz. aralarında gizlice anlaşmak: ''Böyle olunca da Saffet Ferit için kızla işi pişirmek bir saat meselesi hâline gelecekti.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7406, 'işi resmiyete dökmek', 'bir iş veya durum için resmî bir yola sokmak, resmî bir nitelik vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7407, 'işi sağlama almak', 'işin gerçekleşmesi ve bozulmaması için gerekli önlemleri almak: ''Oyuna gelenlerin işi sağlama almaları dudaklarımdaki gülüşü kurutuverdi.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7408, 'işi şakaya dökmek', 'ciddi başlayan bir sözü veya davranışı şakaya çevirmek: ''Bu gayretin boşluğunu anlayarak işi şakaya dökmeye başlıyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7409, 'işi tatlıya bağlamak', 'sorunlu bir işi, iyi bir biçimde çözmek: ''Ben kahvemde çocukça siyasi iddialardan korkarken, bir de felsefe çıktı. Rica ederim bugün işi tatlıya bağlayalım.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7410, '(işi) tavına getirmek', 'işi en uygun duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7411, 'işi temizlemek', 'sorunu çözmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7412, 'işi üç nalla bir ata kalmak', 'eline önemsiz bir imkân geçtiğinde büyük işlerin düşüne kapılanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7413, '(işi veya durumu) duman olmak', 'argo 1) işi, durumu berbat olmak; 2) bir kimse veya bir şey ortadan kaybolmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7414, 'işi yokuşa sürmek', 'işi zorlaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7415, 'işi yolunda (tıkırında) gitmek (olmak)', 'iş düzenli ve istenilen biçimde yürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7416, 'işin alayında olmak', 'bir işe gereken önemi vermemek, dalga geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7417, 'işin başı', 'bir işin en önemli noktası.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7418, 'işin fenası', 'işin kötüsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7419, 'işin garibi', 'işin tuhafı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7420, 'işin içinde iş var', '''bir işin içyüzü başka'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7421, 'işin içinden çıkamamak', 'başaramamak, sorunu çözümleyememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7422, 'işin içinden çıkmak (sıyrılmak)', '1) bir şeyi anlamak, bir sorunu çözümlemek: ''Ne yaparsanız yapın, yeter ki akıllıca olsun, demiş, çıkmış işin içinden!'' -B. R. Eyuboğlu. 2) güç bir sorunu çözemeyince kestirip atmak; 3) bir konudan veya işten uzak durmak, kaçmak: ''O, ne emrederse ben razıyım, deyip kurnazlıkla işin içinden sıyrıldım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7423, 'işin kolayına kaçmak', 'derinliğine araştırmadan basit olarak düşünmek, yüzeyde kalmak, tembellik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7424, 'işin kötüsü', 'işin kötü yanı: ''Cahil adam neye uğradığını şaşırmış ve işin kötüsü yatışır gibi olan merakı yine kabarmıştı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7425, 'işin kurdu', 'bir iş en ince ayrıntısını bilen, deneyimli, uzman (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7426, 'işin kurdu olmak', 'belirli bir konuyla ilgili her şeyi bilmek, uzmanlaşmak: ''Şimdi yaşlandık, bu işlerin kurdu olduk.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7427, 'işin mi yok', '''önemli değil, boş ver'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7428, 'işin ortasını bulmak', 'ortak bir noktada anlaşmak: ''Şimdi bak Nimet, gel şu işin ortasını bulalım.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7429, 'işin rengi değişmek', 'konu başka biçimde gelişmek, öncekinden farklı davranmaya başlamak: ''O zaman işin rengi değişir, asık yüzünün morluğu uçup giderdi.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7430, 'işin tuhafı', 'işin tuhaf olan yanı: ''İşin tuhafı, tek perdesi kalkmış fayton penceresinde görünen silindir şapkalı adam da altın dişlerini göstere göstere kahkahalar atıyor.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7431, 'işin ucu birine dokunmak', 'bir işten dolaylı olarak zarar görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7432, 'işin üstesinden gelmek', 'güç bir işi başarmak, sonuçlandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7433, 'işin yoksa şahit ol, paran çoksa kefil ol', 'tanıklık boş oturan kimselerin, kefillik ise parası çok olan kimselerin işidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7434, 'işinden olmak', 'görevini yitirmek, görevinden atılmak: ''Tabii ertesi günü işinden oldu. İşinden olunca o da gitti askere yazıldı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7435, 'işine bak!', '1) ''görevini, işini sürdür'' anlamında kullanılan bir söz: ''Otururuz, otururuz sen işine bak!'' -M. Ş. Esendal. 2) ''sen karışma'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7436, 'işine gelmek', 'çıkarına, amacına, düşüncesine uygun olmak: ''Yattığı yerden işine gelen kararları onaylar, hoşlanmadıklarını bozarmış.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7437, 'işine hor bakan boynuna torba takar', 'işini küçümseyen kişi istediği gibi para kazanamaz ve sonunda dilenci olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7438, 'işine koyulmak', 'işini yapmayı sürdürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7439, 'işini bilmek', 'nereden, nasıl yararlanacağını bilmek, çıkarını bilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7440, 'işini görmek', '1) görevini yapmak: ''Bu dünyaya geldi geleli elini ılıktan soğuğa vurmamış, işini hep kurnazlıkla görmüştür.'' -Y. Kemal. 2) mec. dövmek; 3) argo öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7441, 'işini kış tut da yaz çıkarsa bahtına', 'başladığın bir işte her zaman güçlüklerle karşılaşacağını varsay ki sonunda hayal kırıklığına uğramayasın, iyi sonuçlar aldığında sevinesin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7442, 'işini uydurmak', 'kurnaz, açıkgöz davranarak işine istediği gibi biçim vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7443, 'işini yoluna koymak', 'işi veya görevi olumlu olarak yürütmek, sıkıntı çekmeden gerçekleştirmek: ''Kendisi burada işini yoluna koyduğu sıralarda, dört yıl, göğsünü, o, savaş meydanlarında siper yapmıştı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7444, 'işinin adamı', 'çalıştığı işte başarı sağlayan, işinin gerektirdiği nitelikleri taşıyan kimse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7445, 'işitmezliğe getirmek (işitmezlikten gelmek)', 'işitmemiş, duymamış gibi davranmak, aldırmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7446, 'işkembeden atmak (söylemek)', ' tkz. uydurarak söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7447, 'işkembesini düşünmek', ' tkz. öncelikle karın doyurmayı düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7448, 'işkembesini şişirmek', ' tkz. oburca yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7449, 'işkenceye sokmak', 'maddi veya manevi sıkıntı vermek, zora sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7450, 'işkilli büzük dingilder', 'gizli bir ayıbı olanlar herhangi bir sözden alınarak kendilerini ele verirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7451, 'işlem görmek', ' ekon. herhangi bir mal, kıymetli kâğıt, döviz vb. piyasada alınmak, satılmak, değiştirilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7452, 'işleme koymak', 'bir işin gerçekleşmesi için gerekli olan işlemleri başlatmak: ''Hasta ile ofis dışı ilişki kurduğunu duyarsam şikâyet dilekçemi işleme koyacağım.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7453, 'işler açılmak', 'piyasa canlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7454, 'işler arapsaçına dönmek', 'işler çok karmaşık bir hâl almak: ''İşler arapsaçına döner ve doğacak arbedeleri de önlemenin çaresi bulunmaz.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7455, 'işler becermek', 'zararlı, gizli işler yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7456, 'işleyen demir pas tutmaz (ışıldar)', 'tembel tembel oturan kimse hantallaşır, iş yapma yeteneğini yitirir, çalışan kimse gittikçe açılır, daha yararlı işler yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7457, 'işportaya düşmek', 'değerini yitirmek, herkes tarafından kullanılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7458, 'iştah açmak', 'yemek isteğini artırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7459, 'iştah dişin dibindedir', 'bir şey yemeyi istemeyen kimse, yiyecekten bir parça tattığında iştahının açıldığını görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7460, 'iştah kapamak (kesmek)', 'yemek isteğini azaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7461, 'iştaha gelmek', 'arzulamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7462, 'iştahı kapanmak (kesilmek)', 'yemek isteği yok olmak: ''Doktor muayenesine gitmek için aldığı her randevunun ardından korkudan iştahı kesiliyordu.'' -S. Erez.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7463, 'iştahı olmak', 'yemek isteği fazla olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7464, 'iştahı yerinde olmak', 'yemesi, içmesi ve yaşaması düzenli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7465, 'işten artmaz, dişten artar', 'biriktirme, çok çalışmakla kazanıp yemekle değil, kazandıklarımızın bir kısmını bir kenara ayırmakla olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7466, 'işten (bile) değil', 'çok kolay: ''... gürültüler ve rezaletler çıkarmak onun için işten bile değildi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7467, 'işten el çektirmek', 'görevden uzaklaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7468, 'işten güçten kalmak', 'herhangi bir sebeple çalışmamak, çalışamamak: ''Oraya kadar sürüklenmek, hanlarda birçok para harcamak, günlerce işten güçten kalmak köylülerin gözünü yıldırır.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7469, 'işten güçten vakit bulamamak', 'çok yoğun çalıştığı için zaman ayıramamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7470, 'iştiyak duymak', 'göreceği gelmek, özlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7471, 'it ağzını kemik tutar', 'aşağılık bir kimsenin ağzını kapamak için ona bir çıkar sağlamak yeter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7472, 'it değmekle (işemekle) deniz pis olmaz', 'doğruluğuna, dürüstlüğüne herkesin inandığı bir kimse, aşağılık kimselerin atmak istedikleri çamurla kirletilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7473, 'it derisinden post olmaz', 'aşağılık kimse veya şey, yüce ve temiz bir amaca hizmet edemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7474, 'it dişi domuz derisi', 'sevilmeyen iki kişi arasındaki anlaşmazlıktan duyulan hoşnutluğu anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7475, 'it gibi çalışmak', 'çok çalışmak, yorulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7476, 'it ite (buyurur), it de kuyruğuna', 'işi yapmak istemeyen onu mutlaka başkasına ısmarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7477, 'it iti ısırmaz', 'aynı düşüncede ve aynı yapıda olan insanlar birbirlerine zarar vermezler, birbirlerini korurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7478, 'it iti suvatta bulur', 'aşağılık kişiler birbirlerini kendi gibilerinin toplandığı yerde bulurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7479, 'it itin ayağına (kuyruğuna) basmaz', 'başkasına kötülük etmek konusunda aynı şeyi düşünenler birbirlerini incitmezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7480, 'it itle gezer', 'kötü biri ancak kendisi gibi kötü olan birisiyle arkadaş olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7481, 'it izi at izine karışmak', 'at izi it izine karışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7482, 'it kağnı gölgesinde yürür de kendi gölgesi sanırmış', 'başkasının korumasıyla iş yapan akılsız kişi, desteklendiğini unutarak kendi gücüne inanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7483, 'it ölüsü gibi', 'çok ağır.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7484, 'it sürü, para kazan', 'ekmek parası kazanmak için it sürümek gibi bir iş tutmak bile ayıp değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7485, 'it sürüsü kadar', ' hkr. çok kalabalık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7486, 'it ulur, birbirini bulur', 'aşağılık bir kimse bir konu üzerinde sesini yükselttiğinde aynı amacı güdenler onun etrafında toplanırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7487, 'it ürür, kervan yürür', 'gerçekleşmesi doğal olan işler engellenemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7488, 'ite atsan yemez', '''çok kötü, berbat'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7489, 'ite ot, ata et vermek', 'ata et, ite ot vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7490, 'ithamda bulunmak', 'birini suçlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7491, 'iti an, taşı eline al (çomağı hazırla)', 'saldırgan biriyle karşılaşmas olasılığı bulunan kimse kavgaya hazır olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7492, 'iti ite kırdırmak', 'kötülüğü kötülük aracılığıyla ortadan kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7493, 'iti (köpeği) öldürene sürütürler', 'çığırından çıkmış olan bir işin düzeltilmesi, bu işe yol açan kimseye düşer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7494, 'itibar görmek', '1) sayılmak, kendisine değer verilmek: ''Bütün satıcılar onu tanıdık bir yüzle karşılıyorlardı ve her yerde aşırı bir itibar görüyorduk.'' -K. Bilbaşar. 2) aranmak, istenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7495, 'itibara almak', 'göz önünde tutmak, hesaba katmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7496, 'itibardan düşmek', 'saygınlığını yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7497, 'itibarın sağ olsun', 'özellikle alışverişlerde kişiye güven duyulduğunda söylenen söz: ''Paran kıtsa itibarın sağ olsun.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7498, 'itidalini kaybetmek', 'aşırılığa kapılmak, soğukkanlılığını yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7499, 'itidalini muhafaza etmek', 'kendini aşırılığa kaptırmamak, kendini tutmak: ''Fakat itidalinizi muhafaza etmek şartıyla haber verebilirim.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7500, 'itimat beslemek', 'güven duymak, güvenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7501, 'itimat telkin etmek', 'güven vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7502, 'itin ahmağı baklavadan pay umar', 'aptal kişi, eline geçme olasılığı bulunmayan bir nimeti bekler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7503, 'itin götüne (kıçına) sokmak', ' kaba rezil etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7504, 'itin (köpeğin) duası kabul (makbul) olsa (-ydı) gökten kemik yağar (-dı)', 'aşağılık kişinin istediği olsaydı dünya, yalnız kendisinin işine yarayan, başkalarını rahatsız eden şeylerle dolardı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7505, 'itin kuyruğunda', ' tkz. pek çok, pek bol.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7506, 'itin ölümü gelirse cami duvarına işer', 'herkesin üzerine titrediği, kutsal saydığı şeyi kötüleyen, bozan kimse mutlaka kötü bir sonuçla karşılaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7507, 'itişip kakışmak', 'birbirini itmek: ''Gecikme korkusuyla herkes itişip kakışmakta ise kimsenin aklına gelmez konuşmak.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7508, 'itle çuvala girilmez', 'edepsiz ve saldırgan bir kimse ile bir konu üzerinde tartışmak ve kavgaya tutuşmak doğru değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7509, 'itle dalaşmaktan çalıyı dolaşmak yeğdir', 'edepsiz kimse ile uğraşmamak için onun bulunduğu yerden uzaklaşmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7510, 'itle yatan bitle kalkar', 'değersiz, kötü kimselerle ilişki kuranlar kötü huylar edinirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7511, 'iven kız ere varmaz, varsa da baht bulmaz', 'acele eden kız eşini iyi seçemeyeceği için mutlu olamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7512, 'ivme kazanmak', 'hızlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7513, 'iyi etmek', '1) iyileştirmek, hastalıktan kurtarmak; 2) uygun, yerinde bir davranışta bulunmak: ''Biraz da etrafında olup bitenlere dikkat etsen iyi edersin.'' -A. M. Dranas. 3) argo soymak, parasını, malını almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7514, 'iyi evlat babayı vezir, kötü evlat rezil eder', 'babaya ün kazandıran da el içine çıkamayacak bir duruma düşüren de çocuklarının tutumlarıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7515, 'iyi gelmek', '1) yaramak: Ağrılarıma bu ilaç iyi geldi. 2) giyecek, üstüne olmak, uygun olmak: Palto üstüne iyi geldi. 3) uğurlu gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7516, 'iyi gitmek', '1) bir iş yolunda olmak; 2) yakışmak: Bu elbise size iyi gidiyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7517, 'iyi gitmeyince kişinin işi, muhallebi yerken kırılır dişi', 'insanın işi bir kez ters gitmeye görsün, en sıradan işlerinde bile tersliklerle karşılaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7518, 'iyi gözle bakmamak', 'hakkında iyi düşünmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7519, 'iyi gün dostu olmak', 'sadece iyi günlerde görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7520, 'iyi hoş (ama)', 'bir görüşe karşıt bir düşünceyi söylerken kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7521, 'iyi insan sözünün üstüne gelir', 'yokluğunda kendisinden söz edilen kimse, konuşmanın üzerine gelirse o iyi insandır, denilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7522, 'iyi iş altı ayda çıkar', 'doğru dürüst yapılması istenen iş uzun zaman ister.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7523, 'iyi iş (doğrusu)', ' tkz. beğenilmeyen bir olay, bir durum karşısında şaşkınlığı anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7524, 'iyi karşılamak', '1) kabul etmek, beğenmek, benimsemek: ''Belki babam, güvercinlerin satıldıklarını iyi karşılamayacaktır.'' -M. Ş. Esendal. 2) ilgi göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7525, 'iyi ki', 'güzel bir rastlantı olarak, ne mutlu: ''İyi ki o günkü acı ile ölmemişiz.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7526, 'iyi nasihat verilir, iyi ad verilemez', 'bir kimse başkalarına iyi öğüt verebilir ama ün veremez, kişi ünü ancak kendisi kazanabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7527, 'iyi olacak hastanın, doktor ayağına gelir', 'Tanrı kötü bir durumun iyiliğe dönmesini dilemişse bunu yapacak kimse işin üstüne gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7528, 'iyi saatte olsunlar', 'cinler, periler: ''Yuvarlak, şen yüzlü, zaman zaman ince ve alaylı ışıldayan bir tanesi iyi saatte olsunlar ile temasta olduğu zaman şaşılaşan kara gözlü, orta yaşlı bir kadın.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7529, 'iyiliğe iyilik her kişinin kârı, kötülüğe iyilik er kişinin kârı', 'iyiliğe karşı iyiliği herkes yapabilir, önemli olan kötülüğe karşı iyilik yapabilmektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7530, 'iyiliğe iyilik olsaydı, koca öküze bıçak olmazdı', 'nasıl öküz, ömrü boyunca hizmet ederek sahibine kazanç sağladığı hâlde ihtiyarlayınca sahibi tarafından kesiliyorsa bu dünyada her zaman iyiliğe karşı iyilik görülmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7531, 'iyiliğe ''nereye gidiyorsun'' demişler, ''kötülüğe'' demiş', 'birçok iyiliğin karşısında kötülük vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7532, 'iyiliği dokunmak', 'yararlı olmak, yararını görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7533, 'iyilik bilmek', 'kendisine yapılan iyiliği unutmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7534, 'iyilik eden iyilik bulur', 'iyilik eden kimseye zamanı geldiğinde başkaları da iyilik ederler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7535, 'iyilik et denize at, balık bilmezse Halik bilir', 'karşılık beklemeden iyilik yap.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7536, 'iyilik et kele, övünsün ele', 'bir kişinin beğenilmeyen yönlerini düzelt, onu herkesin övüneceği duruma getir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7537, 'iyilik görmek', 'maddi, manevi yardım görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7538, 'iyilik iki baştan olur', 'birbiriyle ilişkileri bulunan iki kişinin iyi geçinebilmeleri için yalnızca birinin iyi olması yetmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7539, 'iyisi mi', 'yapılacak olanın en doğrusu, en uygun olanı: ''İyisi mi, yüz vermemeli ve hatta danslara iştirak etmesine müsaade etmemeli, demiş.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7540, 'iyiye çekmek', 'bir düşünce veya olayı olumlu yönüyle değerlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7541, 'iyiye iyi, kötüye kötü demek', 'hatır için söz söylememek, dürüst olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7542, 'iz bırakmak', 'etkisini kalıcı duruma getirmek: ''Her hareket yahut düşünce, hareket edenin veya düşünenin zihninde bir iz bırakır.'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7543, 'iz sürmek', '1) izlemek, arkasından gitmek, takip etmek: ''Sonradan onun da izini sürüp yerini buldum.'' -Y. K. Beyatlı. 2) av sırasında hayvanın ayak izlerine bakarak gittiği yeri bulmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7544, 'izahatta bulunmak', 'izahat vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7545, 'izanı yok', 'anlayışsız, kavrama yeteneği zayıf.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7546, 'izbandut gibi', 'çok iri, cüsseli (erkek): ''Elin izbandut gibi herifiyle dövüşür müyüm?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7547, 'izi belirsiz olmak', 'iz bırakmadan ortadan çekilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7548, 'izi silinmek', 'ortadan yok olmak, kaybolmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7549, 'izin almak', 'bir şey yapmak için onay sağlamak: ''Biz izin almadan çıkamazdık.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7550, 'izin çıkmak', 'bir şey yapmada serbest bırakılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7551, 'izin koparmak', 'güçlükle izin almak: ''Kendisi belediyeden birkaç gün izin kopararak onları ziyaret edecekti.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7552, 'izine dönmek', ' esk. bir karar veya yargıdan geri dönmek, bir karardan vazgeçmek, rücu etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7553, 'izine düşmek', 'av hayvanlarının, gittiği yolu izleyerek arkalarından gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7554, 'izine uymak', 'düşünce ve davranışlarını benimsemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7555, 'izini kaybetmek', 'bir kimse hakkında bilgi alamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7556, 'izinli çıkmak', 'izin alarak belli bir süre için bir yerden ayrılmak: ''İlk bakışta bana izinli çıkmış bir hasta bakıcı gibi göründü.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7557, 'izinli saymak', '1) izin vermek; 2) mec. bir işte ayrı tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7558, 'izlenim (izlemini) bırakmak (vermek)', 'etki bırakmak: ''Görevlilerin edalı ve dıbır dıbır yürüyüşleri bir geçit töreni izlenimini verir.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7559, 'izne çıkmak (ayrılmak)', 'bir iş yerinde üst makamların onayıyla belli bir süre için görevinden ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7560, 'izole etmek', '1) yalıtmak; 2) mec. yalnız bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7561, 'izzetinefsine dokunmak', '1) onuruna dokunmak; 2) gücüne gitmek: ''Terk edilmiş hâli izzetinefsime dokunuyor fakat onu hiç yadırgamıyorum.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7562, 'izzetinefsine yedirememek', 'onursuz kalmayı kabul edememek, düşkünlüğü veya zavallılığı reddetmek: ''Otele gidip de aptalcasına beklemeyi, yürek çarpıntılarıyla kapıyı gözetlemeyi izzetinefsime yediremiyorum.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7563, 'jet gibi', 'hızla, süratle: Otomobil önümden jet gibi geçti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7564, 'jeton geç düşmek', ' tkz. konuşulan veya sözü edilen konuyu geç anlamak, geç intikal etmek: ''Çok lafazan ve bilgisiz ama jeton sizde biraz geç düşüyor anlaşılan.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7565, 'jigolo tutmak', 'yaşlı, zengin bir kadın genç bir erkekle ilişki kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7566, 'jilet atmak', '1) jiletle saldırmak: ''İki jilet at, çiçeğe yatmış hasta gibi hurda et yüzünü!'' -K. Korcan. 2) kendini jiletlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7567, 'jilet gibi', 'çok keskin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7568, 'kabadayılık taslamak', 'kabadayı gibi davranmaya, kabadayı gibi görünmeye çalışmak: ''Kaçanın arkasından kabadayılık taslamak pek ayıp olur.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7569, 'kabahat işlemek (etmek)', 'suç olacak, kusur sayılacak bir iş yapmak: ''Bu kabahati işlemiş, bu akşam tütsüyü, şerbeti unutmuştum.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7570, 'kabahat samur kürk olsa kimse sırtına almaz', 'hiç kimse suçlu olduğunu kabul etmek istemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7571, 'kabahati (birinde) bulmak (aramak)', 'bir kusur, suç aramak: ''O, atı kızdırıyor, çileden çıkarıyor diye, bütün kabahati seyisinde buluyordu.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7572, 'kabahati (birine, bir şeye) yüklemek', 'işlediği bir suçu başkasının üzerine atmak: ''Bu işte kabahati sobaya yüklemek lazım geliyor.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7573, 'kabak (birinin) başına (başında) patlamak', 'birçok kimsenin ilgili olduğu bir olaydan, yalnızca bir kimse zarar veya ceza görmek: ''Kendi yarın cehennem olur gider, kabak bizim başımıza patlar.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7574, 'kabak çıkmak', 'ham çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7575, 'kabak çiçeği gibi açılmak', 'utangaçlıktan çabucak sıyrılarak aşırı ölçüde serbest davranmak: ''Komşular, kabak çiçeği gibi açıldı, ne malmış meğer diyorlardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7576, 'kabak gibi', 'tüysüz, çıplak, her tarafı açık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7577, 'kabak tadı vermek', 'aşırı tekrarlanması, sürdürülmesi yüzünden bir şeyden doygunluk, yorgunluk veya bıkkınlık duyarak onu istemez duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7578, 'kabakulak olmak', 'kabakulak hastalığına yakalanmak: Ahmet kabakulak oldu, üç hafta evden dışarıya çıkamadı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7579, 'kabasını almak', '1) biçim verilecek bir maddenin gereksiz yerlerini gidermek; 2) bir yeri veya bir şeyi gelişigüzel, üstünkörü temizlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7580, 'kabına sığmamak', 'duygularına engel olamayıp taşkın davranışlarda bulunmak: ''Aynı yazar bu kabına sığamayan oyuncunun el, kol, yüz kıpırtılarını da şöyle dile getirir.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7581, 'kâbına varamamak', 'değerce birinden pek aşağı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7582, 'kabiliyetli çırak ustayı geçer', 'yetenekli çırak, ustasından daha usta olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7583, 'kabine çekilmek', 'Bakanlar Kurulu görevini bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7584, 'kabine düşmek', 'Bakanlar Kurulu herhangi bir sebeple görevini bırakmak zorunda kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7585, 'kabir azabı çekmek', '1) İslam inancına göre öldükten sonra mezarda azap çekmek: ''Kabir azabı çeken ölülerin inlemelerini ibretle dinlediler.'' -İ. O. Anar. 2) mec. çok sıkılmak, üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7586, 'kabuğu dışına çıkmak', 'içinde bulunduğu ortam veya durumdan ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7587, 'kabuğuna çekilmek', 'dışarısı ile olan ilişkilerini kesmek, kimse ile görüşmemek: ''Arkadaşı, hükûmet aleyhine konuşmaya başlayınca Fuat kabuğuna çekilmek lüzumunu duyar ve başını önüne eğip susmasını bilirdi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7588, 'kabuğunu çatlatmak (kırmak)', 'içinde bulunduğu güç, olumsuz veya kötü durumdan kurtulup rahatlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7589, 'kabuk bağlamak (tutmak)', 'üstünde kabuk oluşturmak, kabuklanmak: ''Hani, insanın bir yerinde bir çıban çıkar da kabuk tutar.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7590, 'kabuk gibi', 'sağlam, sert (kumaş).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7591, 'kabuksuz yumurtlatmak', 'bir işi ivedilikle yaptırıp eksik kalmasına yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7592, 'kabul görmek', 'kabul edilmek, onaylanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7593, 'kaburgaları çıkmak (sayılmak)', 'çok zayıf olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7594, 'kâbus basmak (çökmek)', '1) kötü rüya görmek; 2) büyük sıkıntı, korku duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7595, 'kâbus görmek', '1) korkulu rüya görmek; 2) büyük sıkıntı, korku duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7596, 'kaç baharın yoğurdunu yemek', 'çok yaşamak, ömrü uzun olmak: ''Hacı Kalfa kaç baharın yoğurdunu yemiştir, bilirsin sen?'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7597, 'kaç para eder?', '''değeri nedir?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7598, 'kaç paralık (adam veya şey)', 'değersiz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7599, 'kaç parça olayım!', '''birçok iş karşısında, hangi birine yetişeyim!'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7600, 'kaç zamandır', '''belirsiz fakat çok zamandan beri, çoktan beri'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7601, 'kaça patlamak?', 'ne kadara mal olmak, fiyatı ne olmak?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7602, 'kaçacak delik aramak', 'korku ile saklanacak yer aramak: ''O adam onları aşağıladıkça utancından kaçacak delik arayan Âşık Ali''ye acıyordu.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7603, 'kaçak güreşmek', '1) asıl konuya girmeksizin başka şeylerden söz etmek; 2) politikada sık sık düşünce değiştirip esas amacını gizlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7604, 'kaçamak yapmak', 'hoş görülmeyen şeyi gizlice ara sıra yapmak: ''Fakat yeni görevini de ihmal edip fırsat buldukça Galata meyhanelerine kaçamak yapması balyosun sabrını taşırdı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7605, 'kaçan balık büyük olur', 'elden kaçırılan fırsat gözde büyütülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7606, 'kaçanı kovmazlar, yıkılanı vurmazlar', 'kaçan bir düşmanı kovalayıp ezmeye çalışmak mertliğe yakışmaz, âciz olduğunu göstereni de vurmak insanlık değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7607, 'kaçanın anası ağlamamış', 'tehlikeden kaçan kazançlı çıkmış.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7608, 'kaçmaktan kovalamaya vakit olmamak', 'önemli işler yüzünden başka işlere yetişememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7609, 'kadastroya geçmek', 'kadastrosu yapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7610, 'kadeh kaldırmak', 'herhangi birini veya bir şeyi onurlandırmak için içmeden önce kadehleri yukarı kaldırmak: ''Localarda kadınlar erkekler, kadeh kaldırıyorlar, gülüşüyorlardı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7611, 'kadeh tokuşturmak', 'içki içerken kadehleri karşılıklı olarak birbirine dokundurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7612, 'kader olmayınca kadir bilinmez', 'kişi talihsiz ise ne kadar iyi insan olursa olsun, değeri bilinmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7613, 'kadere boyun eğmek', 'yazgısını, talihini kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7614, 'kadı anlatışa göre fetva verir', 'haksız kişi, olayı kendisini haklı gibi göstererek anlatırsa dinleyen ona hak verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7615, 'kadı kızında bile kusur olur', 'üzerinde durulmaya değmeyecek kadar küçük bir kusurdur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7616, 'kadının fendi, erkeği yendi', 'kadınlar kurnazlıkta erkeklerden üstündürler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7617, 'kadının şamdanı altın olsa mumunu dikecek erkektir', 'kadın ne kadar bol, değerli çeyizle gelirse gelsin evin bütün eksiklerini erkek sağlar, giderlerini erkek karşılar, evi o geçindirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7618, 'kadının yüzünün karası erkeğin elinin kınası', 'yolsuz ilişkiler kadınlar için hoş karşılanmadığı hâlde erkekler bu gibi ilişkilerden övünme payı çıkarırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7619, 'kadidi çıkmak', '1) çok zayıflamak, bir deri bir kemik durumuna gelmek: ''Sıtmalı arabacıların titredikleri, cılız, kadidi çıkmış öküzlerin iç ezici bir şekilde düşündükleri görülürdü.'' -S. F. Abasıyanık. 2) iskeleti görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7620, 'kadife gibi', 'yumuşak, pürüzsüz ve parlak (ses, ten vb.).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7621, 'Kadir Gecesi doğmuş', 'çok şanslı, kısmetli kimseler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7622, 'kadrini anlamak', 'değerinin farkına varmak: ''Hakikaten, insan sevdiklerinin kadrini yokluklarında anlıyor.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7623, 'kadrini bilmek', 'değerini bilmek, yararlanmak: ''Onun kadrini iyi bilenler de var.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7624, 'kadük olmak (kalmak)', '1) değerini yitirmek; 2) yasama meclisinin değişmesi ile önceden sunulan yasa tasarıları değerini yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7625, 'kafa atmak', 'kavga sırasında karşıdakinin yüzüne, sert ve şiddetli bir biçimde kafayla vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7626, 'kafa cilalamak', ' tkz. içki içmek: ''İpini koparmış aylakla, çiçeği burnunda asistan, dejenere mirasyedi ile ağır işçi, burada dirsek dirseğe kafa cilalardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7627, 'kafa eskitmek', 'zihni yoran sorunlarla sürekli uğraşmak: ''Ne gücünü aşan meseleler için çene yormaya, kafa eskitmeye niyeti vardı ne de kendi başarısızlıkları için suçlu aramaya...'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7628, 'kafa göz yarmak', 'beceriksizlik göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7629, 'kafa (kafasını) dinlendirmek', 'tasa veya zihni yoran sorunlardan kendini uzaklaştırmak: ''Kırmak da istemiyorum zavallı garibancığı, ama ben de buraya kafamı dinlendirmeye geldim.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7630, 'kafa (kafasını) karıştırmak', 'önceki düşüncelerini altüst etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7631, 'kafa (kafasını) şişirmek', 'gürültü veya gevezelikle bir kimseyi tedirgin etmek: ''Kafamızı şişirmeyi sürdürecekti ki, cep telefonum çalmaya başladı.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7632, 'kafa (kafasını) ütülemek', ' argo çok laf edip tedirgin etmek: ''Evi satacağım ama içinde kiracı varken müşteri bulamıyorum diye kafamızı ütülemeye başladı.'' -S. Dölek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7633, 'kafa kafaya vermek', '1) iki veya birkaç kişi bir kenara çekilip konuşmak: ''Şimdi, isterseniz, kafa kafaya verip topunuz birden, yahut topunuzun namına içinizden birisi cevap versin.'' -N. Hikmet. 2) dayanışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7634, 'kafa (kafayı) çekmek', ' argo kafayı çekmek: ''Benimle kafa çekmenin onlar için pek keyifli olduğunu sanmıyorum.'' -E. Bener. ''Ona birisi kafayı çekmekte olduğunu söyleseydi, kılı bile kıpırdamazdı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7635, 'kafa (kafayı veya kafasını) dinlemek', '1) zihni yoran sorunlardan uzak kalmak; 2) sessiz, sakin kalmak: ''Bir dakika kafamı dinleyip başka şeylerden bahsetmek ihtiyacı duyduğum zaman...'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7636, 'kafa kalmamak', 'zihin yorularak çalışmaz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7637, 'kafa patlatmak', 'bir konu üzerinde pek çok düşünmek: ''Sen sabahtan akşama kadar rahat rahat oturuyorsun, ben kafa patlatıyorum.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7638, 'kafa sallamak', '1) ikaz etmek için başını iki yana veya öne arkaya hafifçe eğmek; 2) baş sallamak; 3) doğru veya yanlış her şeye evet demek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7639, 'kafa tutmak', 'boyun eğmemek, karşı gelmek, diklenmek: ''Hocalara, amirlere, büyüklere kafa tutmak sökmezdi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7640, 'kafa yapmak', ' argo dalga geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7641, 'kafa yok!', '''akıl, düşünce yok'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7642, 'kafa yormak', 'bir iş, bir konu üzerinde çokça düşünmek: ''Oynarken yaptığı hatalar üstüne kafa yoruyor, sonra yığınla düş kuruyordu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7643, 'kafadan atmak', 'bir konu üzerinde inceleme yapmadan rastgele konuşmak, uydurmak, yalan söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7644, 'kafakola almak', '1) sp. güreşte kafa ve kolu birlikte kavrayarak rakibi çevirmek; 2) mec. etkisi altına alıp kandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7645, 'kafası almamak', '1) anlayamamak, kavrayamamak; 2) zihin yorgunluğu sebebiyle anlayamaz duruma gelmek; 3) olabileceğine inanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7646, 'kafası bozulmak', 'öfkelenmek, kızmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7647, 'kafası bulanmak', 'bir olay karşısında aklı karışmak, anlayamaz, kavrayamaz duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7648, 'kafası dolmak', 'zihninde çeşitli konular birikmek: ''Kafası daha o yıllarda özgürlük ve eşitlik fikirleriyle dolmuştu.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7649, 'kafası dönmek', '1) sıkışık bir durumda sersemlemek; 2) kızıp öfkelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7650, 'kafası dumanlanmak', '1) çok dalgın olmak; 2) sarhoş olmak: ''Saz, söz başlasın, içki ile kafalar iyice dumanlansın, cümbüş tam kıvamını bulsun.'' -H. R. Gürpınar. 3) esrar içmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7651, 'kafası durmak', 'zihin yorgunluğundan düşünemez olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7652, 'kafası düzelmek', 'doğruyu ve iyiyi bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7653, 'kafası ile oynamak', 'takım sporlarında arkadaşlarının durumunu göz önünde tutup en iyi fırsatı değerlendirerek bedenini fazla yormadan oynamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7654, 'kafası işlemek (çalışmak)', 'aklı, zekâsı yerinde olmak, bir konu üzerinde iyi düşünebilir olmak: ''Hasan''ın kafası şimdi üç cepheli işliyordu.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7655, 'kafası (kafasına) takılmak', 'zihni bir şeyle sürekli olarak uğraşmak: ''Bu soru kafasına takıldıkça gülüşü mide spazmı geçirir gibi oluyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7656, 'kafası karışmak', 'önceki bilgi ve düşünceleri altüst olmak: ''Esir kızı unutabilmek için kendini teşkilattaki tuhaf aletleri incelemeye verdiğinde kafası iyice karışmıştı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7657, 'kafası kazan (gibi) olmak', 'kafası şişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7658, 'kafası kızmak', 'öfkelenmek: ''Namusum hakkı için bir kafam kızarsa atarım denize seni.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7659, 'kafası sarmamak', 'anlamamak, aklı ermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7660, 'kafası sersem sepet (olmak)', 'gürültü ve uğultudan zihni yorulmuş (olmak): ''Uzun bir otobüs yolculuğundan sonra, yorgun, uykusuz, kafası sersem sepet girdiği için kasabaya, henüz pek bir şeyin farkında değildi.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7661, 'kafası şişmek', '1) zihni yorulmak; 2) gürültüden tedirgin olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7662, 'kafası yerinde olmamak', 'gereği gibi düşünecek durumda olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7663, 'kafası yerine gelmek', 'kendini toparlamak, kendine gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7664, 'kafasına dank etmek', 'bir olay sebebiyle birden ayılmak, doğruyu anlamak: ''Onu içine düşürdüğüm kötü durum da böylece kafama dank etmiş oldu.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7665, 'kafasına estiği gibi', 'sadece kendi düşünce ve isteklerine göre.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7666, 'kafasına geçirmek', 'başına geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7667, 'kafasına girmek', '1) bir düşünce aklına uygun gelmek; 2) birini bir iş yapmaya kandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7668, 'kafasına girmemek', 'anlayamamak, kavrayamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7669, 'kafasına göre', 'istediği gibi: ''Otopark görevlileri, arabayı kendi kafalarına göre bir yere çekerlerdi.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7670, 'kafasına koymak', 'kararını önceden vermiş olmak, önceden şartlanmak, bir şey yapmaya kesin karar vererek zamanını beklemek: ''Burada toprağı, nesi varsa satıp savarak bir başka yere göç etmeyi kafasına koymuştur.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7671, 'kafasına sığmamak', 'akıl erdirememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7672, 'kafasına söz girmemek', '1) çok aptal veya inatçı olmak; 2) önemsememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7673, 'kafasına uymak', 'aklına uymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7674, 'kafasına vura vura', 'zorla, isteyip istemediğine bakmadan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7675, 'kafasına vurmak', 'başına vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7676, 'kafasında şimşek çakmak', 'beyninde şimşek çakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7677, 'kafasında tutmak', 'bir şeyi unutmamak, aklında tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7678, 'kafasından çıkarmak (atmak)', 'bir şeyi unutmak veya ondan vazgeçmek: ''Bir rüyadan böyle abuk sabuk sonuçlar çıkardığım için kendimi suçlayarak bu tuhaf düşünceleri attım kafamdan.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7679, 'kafasından geçirmek', 'belli belirsiz düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7680, 'kafasını ezmek', 'zararlı olabilecek bir hareketi, bir durumu başlangıçta yok etmek, etkisiz duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7681, 'kafasını işletmek', 'doğru ve iyi düşünmek: ''Biraz kafanızı işletseniz ne düğümler çözersiniz.'' -T. Oflazoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7682, 'kafasını kaldırmak', 'karşı gelmek, başkaldırmak: ''Sen bağ yeri açıyorsun ha? Çevirin şunu dese, yüz sopa çekse. Bir daha bak kimse kafasını kaldırır mı?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7683, 'kafasını kaldırmamak', '1) yoğun olarak çalışmak, meşgul olmak; 2) yoğun bir biçimde düşünmek veya çalışmak; 3) karşı gelmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7684, 'kafasını kaşıyacak vakti olmamak', 'başını kaşıyacak vakti olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7685, 'kafasını kırmak', 'iyice dövmek, pataklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7686, 'kafasını kullanmak', 'akıllıca davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7687, 'kafasını kurcalamak', 'zihnini meşgul etmek, düşündürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7688, 'kafasını sokmak', 'barınabilecek bir yere yerleşmek, başını sokmak: ''Bazen yapayalnız, kafasını sokacak bir damdan mahrum, aç, avare dolaşmış.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7689, 'kafasını taştan taşa çarpmak (vurmak)', 'başını taştan taşa çarpmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7690, 'kafasını toplamak', 'sağlıklı düşünebilir olmak: ''Soluk soluğa kuytu bir yere sinip kafasını toplamaya çalıştı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7691, 'kafasını tütsülemek', 'sarhoş etmek: ''Tekelin en keskin içkisi bizimkilerin kafasını tütsüledi.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7692, 'kafasını uçurmak', 'kellesini uçurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7693, 'kafasının bir tahtası eksik (noksan) (olmak)', ' alay akıl dışı davranışlarda bulunan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7694, 'kafasının dikine gitmek', 'kendi düşünce ve görüşünün en iyi olduğuna inanarak kimsenin öğüdünü, uyarısını dinlememek: ''Evvel zaman içinde, kafasının dikine giden bir kuş varmış, kışın güneye göç etmemeye ant içmiş.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7695, 'kafasının etini yemek', 'başının etini yemek: ''O, keşki sıhhatli olsaydı da her gün kafamın etini yiyeydi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7696, 'kafasının kontağı atmak', 'çok sinirlenmek, öfke ile dolmak: ''Kafalarının kontağı bir kere atınca eski rayına oturtmakta güçlük çekerler.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7697, 'kafaya almak', ' argo 1) zaaflarından yararlanarak kandırmak, oyuna getirmek; 2) konu önemliymiş gibi yaparak alaya almak; 3) den. gemi seyrederken akıntıyı başa almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7698, 'kafaya çıkmak', ' sp. topa kafayla vurmak için sıçramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7699, 'kafayı (bir yere) vurmak', '1) hastalanıp yatağa düşmek; 2) uyumak için yatmak: ''Ahmet de bize varır varmaz kafayı yere vurdu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7700, 'kafayı bulandırmak', 'önceki düşünceleri altüst etmek, değiştirmek: ''Öğretmenler bu gibi kitapların kafayı bulandırdığını bile söyler.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7701, 'kafayı bulmak', 'sarhoş olmak: ''Samim artık iyice kafayı bulmuştu.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7702, 'kafayı çalıştırmak (işletmek)', 'akılcı davranarak sorunları çözmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7703, 'kafayı değiştirmek', 'düşüncesini, kanaatini değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7704, 'kafayı tütsülemek (dumanlamak)', ' argo sarhoş olmak: ''Barba Manol kafayı iyice dumanladıktan sonra, iki bacağının nasıl tutulduğunu anlattı.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7705, 'kafayı üşütmek', 'delirmek, çılgınlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7706, 'kafayı yemek', ' argo aşırı yorgunluktan bunalıma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7707, 'kafes gibi', 'zayıf, kuru veya delik deşik.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7708, 'kafese girmek', ' argo 1) aldatılıp kendisinden çıkar sağlanmak; 2) hapse girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7709, 'kafese koymak', ' argo aldatıp çıkar sağlamak: ''O, ya birisini batırmak yahut da kafese koymak için ziyafet çekerdi.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7710, 'kâfi gelmek', 'yetmek, yetişmek: ''Cazibesiz güzellik kâfi gelmiyor.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7711, 'kâğıda dökmek', 'yazıya geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7712, 'kâğıt açmak', 'iskambil kâğıtlarını oyunculara dağıttıktan sonra koz olacak kâğıdın yüzünü çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7713, 'kâğıt kaleme sarılmak', 'hemen yazmaya başlamak: ''Partinin kurulacağını duyunca kâğıda kaleme sarılmış, korkunç bir telgraf yazmıştı.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7714, 'kağnı gibi gitmek', 'çok yavaş gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7715, 'kağnıyla tavşan avına çıkmak', 'bir işi bitirmemek için bahane bulmak, ayak sürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7716, 'kahır (kahrını) çekmek', 'uzun süre sıkıntıya katlanmak: ''Annesine bakabilmek için akşama kadar elliye yakın yaramazın kahrını çekiyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7717, 'kahır yüzünden lütfa uğramak', 'birine kötülük olsun diye yapılan iş, onun iyiliğine olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7718, 'kahkaha (kahkahayı) basmak (koparmak, salıvermek)', 'kendini tutamayıp yüksek sesle gülmek: ''Beni yatakta görünce kahkahayı bastı.'' -Ö. Seyfettin. ''Senyörün etrafındakilerden biri dayanamayıp bir kahkaha salıverdi.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7719, 'kahkaha tufanı kopmak', 'birdenbire toplu olarak kahkaha atmak: ''Haydi bana eyvallah, deyiverince bir kahkaha tufanı koptu.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7720, 'kahkahadan kırılmak', 'çok gülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7721, 'kahkahayı ağzında söndürmek', 'edep sınırlarını aşmamak için gülmeyi sınırlamak: ''Hâlbuki hikâyesini dinleyen eşraf efendiler, birbirlerine bakarak kahkahalarını elleriyle ağızlarında söndürmeye çalışıyorlardı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7722, 'kahpenin dölü', 'piç, soysuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7723, 'kahrından ölmek', '1) çok üzülmek; 2) aşırı üzüntü, ölümüne neden olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7724, 'kahve dövücünün hınk deyicisi', 'havan dövücünün hınk deyicisi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7725, 'kâhyalık etmek', '1) kâhyalık görevinde bulunmak; 2) mec. her şeye karışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7726, 'kalafata çekmek', '1) gemiyi onarmak için karaya çekmek; 2) mec. azarlamak, paylamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7727, 'kalas gibi', 'kaba, kibar veya nazik olmayan, incelikten yoksun.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7728, 'kalayı basmak', ' argo adamakıllı küfretmek: ''Basıyorlar kalayı bize, değil mi?'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7729, 'kalaylı bakır küflenmez', 'temizliğini herkesin bildiği kişi ve iş lekelenemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7730, 'kalbe dokunmak', 'acı veya üzüntü vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7731, 'kalbe işlemek', 'derin üzüntü uyandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7732, 'kalbe (kalbine) doğmak', 'içine doğmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7733, 'kalbi ağzına gelmek', 'yüreği ağzına gelmek: ''Kendisi de her fırsat düştükçe bunlarla yan yana harp ettiğini söylerken âdeta kalbi ağzına gelmiş gibi olurdu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7734, 'kalbi boş olmak', 'sevgilisi bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7735, 'kalbi çarpmak', '1) kalbi çok vurmak; 2) çok heyecanlanmak; 3) yüreği çarpmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7736, 'kalbi dayanmamak', '1) aşırı heyecan, üzüntü, yorgunluk veya herhangi bir hastalık yüzünden kalbi durmak, ölmek; 2) yüreği dayanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7737, 'kalbi dolu olmak', 'sevgilisi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7738, 'kalbi ferahlamak', 'yüreği ferahlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7739, 'kalbi kararmak', '1) inancını kaybetmek; 2) yüreği kararmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7740, 'kalbi parçalanmak', 'yüreği parçalanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7741, 'kalbi sıkışmak', 'kalp atışları düzensiz olmak, sıkıntı duymak; 2) mec. bir meseleden dolayı aşırı üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7742, 'kalbi sızlamak', 'yüreği sızlamak: ''Sekiz sene evvel İstanbul''dan kalbim sızlayarak çıktım.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7743, 'kalbi yerinden oynamak (fırlamak)', 'yüreği yerinden oynamak: ''En hafif bir hareketi kalbimizi yerinden oynatmaya yeterdi.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7744, 'kalbi yıkmak kolay, yapmak zordur', 'insanları kırmak ve üzmek, mutlu etmekten daha kolaydır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7745, 'kalbi yırtılmak', 'acı duymak: ''Koca Ali susar, kalbinin yırtıldığını, kilitlenen çenelerinin çatırdadığını, şakaklarının attığını duyardı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7746, 'kalbin yolu mideden geçer', 'bir kimsenin sevgisi kazanılmak istendiğinde ona güzel yiyecekler ikram edilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7747, 'kalbine girmek', 'sevgisini kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7748, 'kalbine göre', 'gönlüne göre: Allah kalbine göre verdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7749, 'kalbine saplanmak', 'yüreğine saplanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7750, 'kalbini açmak', 'yüreğini açmak: ''Bir gün kalbini İclâl''e açtı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7751, 'kalbini burmak', 'üzmek, sıkıntı vermek: ''Hikâyenin burası kalbimi burdu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7752, 'kalbini çalmak', 'sevgisini kazanmak, kendine âşık etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7753, 'kalbini eritmek', 'acımasını sağlamak, yumuşatmak: ''Edebiyat hocamız Ali Bey''in kalbini eritecek bir konu seçmeli, acıklı bir tarzda yazmalı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7754, 'kalbini okumak', 'birinin duygu ve düşüncelerini, niyetini anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7755, 'kalbiyle konuşmak', 'düşüncelerini, duygu ağırlıklı bir biçimde anlatmak: ''Bana öyle geldi ki bu adam kafasından ziyade kalbiyle konuşuyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7756, 'kalbur gibi', 'delikleri olan, delik deşik.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7757, 'kalbura çevirmek', 'delik deşik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7758, 'kalbura dönmek', 'delik deşik olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7759, 'kalburdan geçirmek', 'kalbur yardımıyla ayırmak, elemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7760, 'kalburla su taşımak', 'verimsiz, sonuçsuz bir işle uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7761, 'kalburüstü kalmak', 'kalburüstüne gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7762, 'kalburüstüne gelmek', 'benzerleri arasında sivrilmiş olmak, seçkin duruma gelmek: ''Merkez azaları, âyandan birkaç kişi, mebusların hatırlıları ile ateşlilerden kalburüstüne gelenleri oradaydı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7763, 'kaldı ki', 'bundan başka, bununla birlikte.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7764, 'kaldırım çiğnemek', 'şehirde yaşayarak görgüsü artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7765, 'kaldırıma düşmek', '1) önemini, değerini yitirmek; 2) ucuz fiyatla sokakta satışa çıkarılmak: ''Bastığı hiçbir eser kaldırıma düşmemişti.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7766, 'kaldırımları arşınlamak', 'işsiz güçsüz dolaşmak: ''Kelli felli efendiden adamların hatta sarıklı ulemanın günden güne hırpanileşen kılıklarla, elleri boyunlarında, kaldırımları arşınladıklarını görüyorum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7767, 'kale almamak', 'önem vermemek, hesaba katmamak, sözünü etmeye değer bulmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7768, 'kale gibi', '1) çok büyük, sağlam (yapı); 2) mec. kendisine güvenilen güçlü (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7769, 'kalebent etmek', 'suçluluğu yüzünden mahkûm etmek: ''Jön Türklerle alakası var diye, insanı dünyanın öbür ucuna kalebent ediverirler.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7770, 'kalem açmak', 'kalemin ucunu yontup kullanılabilecek bir duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7771, 'kalem kırmak', ' huk. idam kararı verildiğinde bir daha idam kararı imzalamamak için hâkim kalemini kırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7772, 'kalem oynatmak', '1) yazı yazmak: ''Namık Kemal''in tek başına kalem oynattığı alanlarda başyazarlar, fıkra yazarları, sanat eleştiricileri yetişir.'' -N. Cumalı. 2) bir yazıyı düzeltmek; 3) bir yazıda değişiklik yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7773, 'kaleme almak', 'bir konuyu yazı durumuna getirmek, yazıyla anlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7774, 'kaleme gelmek', 'yazılabilmek veya anlatılabilmek: ''Köyün harman yerinde anlatımı kaleme gelmez bir çalışma var.'' -F. Otyam.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7775, 'kaleme (kaleme kâğıda) sarılmak', 'hemen yazmaya başlamak: ''Hemen kaleme sarıldı. Bir hafta her gece çalışmak suretiyle hikâyesini bitirdi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7776, 'kaleminden çıkmak', 'herhangi biri tarafından yazılmak: ''Kurtuluş Savaşı boyunca ciltler tutacak ölçüde telgraf yazışmaları hep kendi kaleminden çıkmıştır.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7777, 'kaleminden kan damlamak', '1) yazıları acı ve dokunaklı olmak; 2) etkili yazmak: ''Kaleminden kan damlayan kavgacı yazarları sevmiyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7778, 'kalemine dolamak', '1) herhangi bir konuyu sürekli olarak yazmak; 2) bir kimseyi sürekli olarak yazılarıyla kötülemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7779, 'kalemiyle yaşamak (geçinmek)', 'geçimini yazılarıyla sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7780, 'kalendere ''kış geliyor'' demişler, ''titremeye hazırım'' diye cevap vermiş', 'yaşamın felsefesine eren kişi, en sevimsiz, hatta rahatsız durumları bile hoş karşılar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7781, 'kaleyi içinden fethetmek', 'davasını karşı taraftan birinin yardımıyla kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7782, 'kalıba dökmek', 'dökmecilikte erimiş madeni kalıbın içine akıtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7783, 'kalıba vurmak', 'biçimi bozulmuş bir şeyi düzeltmek için kalıba geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7784, 'kalıbı değiştirmek (dinlendirmek)', ' argo ölmek: ''Hekimler epeyce çalıştılar, ilaç verdiler ise de fayda etmedi. Bir hafta sonra kalıbı dinlendirdi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7785, 'kalıbı kıyafeti yerinde olmak', 'görünüşü gösterişli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7786, 'kalıbından utanmamak', 'dıştan görüntüsünün verdiği etkiyi hiçe saymak: ''Yalan söylüyorsun ha bire kalıbından utanmadan, sana inanmıyorum.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7787, 'kalıbını basmak', 'bir şeyi güvenle doğrulamak: ''Aklı yerinde ama sabaha çıkamayacağına kalıbımı basarım.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7788, 'kalıbının adamı olmamak', 'görünüşünden beklendiği gibi olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7789, 'kalın incelene kadar ince süzülür', 'bir hastalık, bir sıkıntı karşısında güçlü gücünden bir parçasını yitirerek zayıflar ama zayıf olan, ölecek duruma gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7790, 'kalıp gibi oturmak', 'giysi, vücuda tam uymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7791, 'kalıp gibi serilmek', 'yorgunluktan upuzun yatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7792, 'kalıp gibi uyumak', 'kımıldamadan uzun ve derin bir uyku uyumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7793, 'kalıp kesilmek', 'olduğu gibi kalmak: ''Lakin sonra mandalın gürültüsü, kanadın gıcırtısını duyunca hemen yerine donmuş, yatmış, kalıp kesilmişti.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7794, 'kalıp kıyafetle adam adam olmaz', 'gösterişli bir vücut, iyi bir giyim kuşam, kişiye insanlık değeri kazandırmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7795, 'kalıptan kalıba girmek', 'çıkar sağlamak için her duruma uymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7796, 'kalkıp kalkıp oturmak', 'öfke, heyecan vb. duygular sebebiyle yerinde duramaz olmak, hop oturup hop kalkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7797, 'kalkışa geçmek', 'uçak havalanmak için pistten ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7798, 'kalleşlik etmek', '1) sözünde durmayarak döneklik etmek; 2) birine gizlice kötülük etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7799, 'kalp kalbe karşıdır', 'sevgi karşılıklıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7800, 'kalp (kalbini) kazanmak (fethetmek)', 'ince bir davranış veya güzel bir sözle birinin sevgisini kazanmak, ilgisini çekmek: ''Hele düzmece şehzadenin kadife pantolonuyla sivri güzel çehresi derhâl kadının kalbini kazandı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7801, 'kalp (kalbini) kırmak', 'gönül kırmak: ''Hak yemek, kanuna aykırı bir şey yapmak, kalp kırmak korkusuyla bir türlü iş göremezdi.'' -H. E. Adıvar. ''Okuyucularımın hakkını yiyor hem de öteki genç okuyucularımın kalbini kırıyorum.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7802, 'kalp kazanır, kaltaban gönenir', 'iş becerme yeteneği bulunmayan kişi, düzenbazın kendisine yutturduğu şeyi kazanç sanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7803, 'kalpten kalbe yol vardır', 'sevgi karşılıklıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7804, 'kalubeladan beri', 'dünya kurulalı beri, çok eskiden beri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7805, 'kama basmak', ' hlk. oyunda yenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7806, 'kamanço etmek', 'yüklemek, aktarmak, elden ele geçirmek: ''Bu ödev kendisine kamanço edilen eleştirmen arkadaş...'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7807, 'kambersiz düğün olmaz', 'her toplantıda veya her işin içinde bulunmak merakında olanlar için yarı sitem, yarı şaka olarak söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7808, 'kambur üstüne kambur (kambur kambur üstüne)', 'sıkıntı ve tersliklerin üst üste geldiğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7809, 'kambura vermek', 'ciltlenecek kitabın sırtını, formalar dikildikten sonra çekiç veya makine yardımıyla yuvarlaklaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7810, 'kambura yatmak', 'ayakta duran birini sırtüstü düşürmek için gizlice arkasında iki büklüm olup eğilmek ve başka birinin onu önden üzerine itmesini sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7811, 'kamburu çıkmak', '1) sırtı kambur olmak: ''Mavi gözlü, köse, kamburu çıkmış bir ihtiyardı.'' -Ö. Seyfettin. 2) mec. ihtiyarlamak: ''Bir kocakarı gibi kamburu çıkmış. Ne istiyor?'' -N. Hikmet. 3) mec. eğilerek yapılan işler için çok çalışmış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7812, 'kamburunu çıkarmak', 'insan, kedi vb. sırtını tümsek duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7813, 'kamçı çalmak (vurmak)', 'kamçılamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7814, 'kamet getirmek', 'farz namazına durmak için iç ezan okumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7815, 'kameti artırmak', '1) yüksek sesle konuşmak; 2) ortalığı velveleye vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7816, 'kamış atmak (koymak)', ' argo birine oyun etmek, arabozanlık etmek: ''Sıkıntılı bir durumdu ama onun kamışı o kadar zekice atması hoşuma gitmişti.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7817, 'kamp kurmak', 'kamp için kalınacak yerde gerekli düzeni sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7818, 'kamp yapmak', 'kampa girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7819, 'kampa girmek', 'genellikle yarışma öncesi, yarışmaya gerektiği gibi hazırlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7820, 'kamuoyu oluşturmak (yaratmak)', 'bir düşünceyi yaygınlaştırmak ve halkın dikkati o düşünce etrafında toplamak ve yoğunlaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7821, 'kan ağlamak', 'büyük bir üzüntü içinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7822, 'kan akıtmak', 'kurban kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7823, 'kan akmak', '1) savaş, çatışma, dövüş olmak; 2) ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7824, 'kan alacak damarı bilmek', 'nereden veya kimden çıkar sağlanabileceğini bilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7825, 'kan beynine sıçramak (çıkmak)', 'çok sinirlenmek, hiddetlenmek, kontrolü yitirmek: ''O görüntü gözlerimin önünde canlanınca kan beynime sıçrıyor, kendimi kaybediyorum.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7826, 'kan çanağı gibi', 'kanlanan (göz).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7827, 'kan çekmek', '1) yüz ve huy, anne veya baba tarafının yüzüne ve huyuna benzemek; 2) akrabalar birbirlerine yakınlık duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7828, 'kan çıkmak', 'kan dökülmek, cinayet işlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7829, 'kan dere gibi akmak', 'vücudun bir yerinden çok kan akmak veya bir savaşta çok kişi yaralanarak ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7830, 'kan dökmek', 'ölüme yol açmak, cana kıymak: ''Şimdiyse durum değişmiş, şu sazevinde oturanlar toprak için kan bile dökebilirlerdi.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7831, 'kan gövdeyi götürmek', 'çok kan dökülmek: ''Cephelerde kan gövdeyi götürürken bu macera adamının aramızda ne aradığını düşünüyordum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7832, 'kan gütmek', 'kan dökerek öç almak istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7833, 'kan istemek', 'öldürülen bir kimsenin öcünün alınmasını istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7834, 'kan (kanı) başına çıkmak (sıçramak veya toplanmak)', 'öfkelenmek: ''Kan başına çıkarmış zavallının ve hep bağırmak, bağırmak istermiş.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7835, 'kan kaybetmek', '1) herhangi bir nedenle vücuttan çok kan akmak: ''Kadın o kadar kan kaybetmiş ki az daha ölecekmiş.'' -M. Ş. Esendal. 2) mec. güçsüzleşmek, etkisini kaybetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7836, 'kan kusup kızılcık şerbeti içtim demek', 'çok eziyet çektiği hâlde durumunu iyi göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7837, 'kan olmak', 'insan öldürülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7838, 'kan revan içinde', 'her yanı kana bulanmış: ''Öteki arkadaşların kan revan içinde sağa sola fırlatıldıklarını müşahede ettim.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7839, 'kan revan içinde kalmak', 'her yanı kana bulanmak: ''Çıplak ayağım kan revan içinde kaldıkça öbürüne bakıp şükredeceğim.'' -S. Çokum.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7840, 'kan ter içinde', 'çok terli, yorgun ve perişan bir durumda: ''İşte şimdi de kan ter içinde oturdum masanın başına.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7841, 'kan tere batmak', 'kan ter içinde kalmak: ''Yaptığınız yürüyüş, başka zamanlarda kan tere batmadan yapılacak işlerden değildir.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7842, 'kan tutmak', '1) kan gördüğünde bayılmak; 2) şok geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7843, 'kan vermek', '1) hastaya, yaralıya kan aktarmak; 2) kan nakli için kan aldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7844, 'kan yürümek', 'bir organda aşırı kan birikmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7845, 'kana boyamak (bulamak)', 'kan içinde bırakmak: ''Ondan on beş yıl sonra, Feriye sarayını kana boyayan ve zavallı babacığımın felaketine sebep olan faciayı...'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7846, 'kana kan istemek', 'kısas yapılmasını istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7847, 'kana susamak', 'öldürme hırsı duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7848, 'kanaat gibi devlet olmaz', 'elindekiyle yetinmesini bilen kişi yokluk nedir bilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7849, 'kanat alıştırmak', 'bir işe alışmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7850, 'kanat çırpmak', '1) uçmak; 2) kanatlarını hareket ettirmek: ''Komşunun kazları birikintilerde kanat çırpıp bağırıyordu.'' -R. Enis. 3) mec. yeni bir başlangıç yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7851, 'kanatsız kuş uçmaz', 'gereken koşullarla donanıp güçlenmeyen kişi amacına ulaşamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7852, 'kanayan yara olmak', 'sürekli sıkıntı, üzüntü ve zarar veren bir durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7853, 'kancayı takmak (atmak)', 'bir kimsenin kötülüğü için uğraşmak: ''İçlerinden birine kancayı atmış, maksadı, onu üzmek, ona eziyet etmektir.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7854, 'kandilin yağı tükenmek', 'hayat sona ermek, ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7855, 'kangren olmak', '1) vücudun bir yerindeki dokular ölmek; 2) mec. kangrenleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7856, 'kanı donmak (çekilmek)', 'donakalmak, çok şaşırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7857, 'kanı ısınmak', 'birine karşı yakınlık duymak: ''Kanları çabuk ısındı birbirine.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7858, 'kanı içine akmak', 'derdini dışa vuramamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7859, 'kanı kanla yumazlar, kanı suyla yurlar', 'kötülük, kötülük yapılarak düzeltilmez ancak iyilik yapılarak ortadan kaldırılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7860, 'kanı kurumak', '1) çok usanmak, çok bıkmak; 2) mec. bitkin, yorgun, cansız duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7861, 'kanı sulanmak', 'kansızlığa uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7862, 'kanı temizlenmek', 'öldürülenin arkasından, öldüren kişi veya yakınlarından birini öldürerek öç almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7863, 'kanına dokunmak', 'çok sinirlenmek: ''Bırak Allah''ını seversen müdür bey! Bazen kanıma dokunuyor vallaha. Sen onun oruçlu olduğuna inanıyor musun?'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7864, 'kanına girmek', '1) birini öldürmek veya öldürtmek: ''Kanıma gireceksiniz ama ne yapalım siz sağ olun.'' -R. N. Güntekin. 2) bir kızın kızlığını bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7865, 'kanını içine akıtmak', 'sıkıntısını belli etmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7866, 'kanını kaynatmak', 'heyecanlandırmak, coşturmak: ''Görenin kanını kaynatan bir tadı vardı duruşunun, bakışının.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7867, 'kanıya varmak', 'belli bir kanı edinmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7868, 'kanıyla ödemek', 'yaptığının cezasını hayatıyla ödemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7869, 'kanlı bıçaklı olmak', 'aralarında herhangi bir nedenden dolayı birbirini öldürecek kadar düşmanlık bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7870, 'kanlı gömlek gizlenmez', 'bazı kötü şeylerin gizlenmesi mümkün değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7871, 'kanlı yaş (yaşlar) dökmek', 'büyük üzüntüyle ağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7872, 'kantara çekmek (vurmak)', '1) bir şeyi tartmak; 2) mec. birini sınamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7873, 'kantarın topunu kaçırmak', 'ölçüyü kaçırıp aşırı davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7874, 'kantarlıyı savurmak', 'ağır bir biçimde sövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7875, 'kanun çiğnemek', 'yasal olmayan iş yapmak: ''Ben bir gazeteciyim. Kanunları çiğnemişsem bu ülkenin savcıları gerekeni yaparlar.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7876, 'kapalı geçmek', 'bir konuda önemli noktaya değinmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7877, 'kapalı olmak', '1) iş yapmamak; 2) ilgisiz kalmak: ''Nedim''i beğenmeyenler bu şenlikli dünyaya kapalı olanlardır.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7878, 'kapalı yetişmek', 'toplum hayatına girmeden, karışmadan yetişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7879, 'kapan kurmak', 'bir hayvanı tuzağa düşürmek için kapan hazırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7880, 'kapana düşmek (girmek veya kısılmak veya koymak veya tutulmak veya yakalanmak)', 'içinden çıkılmaz bir duruma düşmek, ele geçmek: ''Onlar beni kapana koyacaklarını sanadursunlar.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7881, 'kapana düşürmek (kıstırmak)', 'hile ile yakalamak: ''İçindekiler kendilerini ayaklarıyla bir kapana kıstırmışlardır.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7882, 'kapana sıkıştırmak', '1) birini zor durumda bırakmak: ''Fikirlerindeki çelişmeyi belirtip adamı kıskıvrak bir kapana sıkıştırır.'' -H. Taner. 2) birini düzenle zor duruma sokmak, işin içinden çıkamaz duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7883, 'kapanın elinde kalmak', '1) çok istenir ve aranır olmak; 2) bir şeyden ancak çabuk davranabilenler yararlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7884, 'kapı almak (yapmak)', 'tavla oyununda bir haneye üst üste iki pul getirmek ve o hanenin karşı oyuncu tarafından kullanılmasını engellemek: ''Altı bir geldi mi köşeyi kapacaksın, kapıları almayı asla unutmayacaksın.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7885, 'kapı aralamak', 'bir konuya giriş yapmak, karşısındakini hazırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7886, 'kapı aramak', 'ev ziyareti yapmak istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7887, 'kapı arkası bile gurbet', 'bir insan evinden pek uzağa gitmese bile evden ayrıldıktan sonra kendini gurbete çıkmış gibi hisseder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7888, 'kapı baca açık', 'korunmaya alınmamış.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7889, 'kapı dışarı etmek (atmak)', 'kovmak, dışarı atmak: ''Sizin hepinizi kapı dışarı edecekler. Çünkü kaçak işçiye memlekette iş yok.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7890, 'kapı gibi', '1) iri vücutlu (kimse); 2) dayanak noktası güçlü, sağlam olan: ''İçlerinden biri atından inerek celladın burnuna kapı gibi bir fermanı dayadı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7891, 'kapı kadar', 'çok enli ve uzun olan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7892, 'kapı kapı aramak', 'her yeri aramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7893, 'kapı kapı dolaşmak (gezmek)', '1) ev ev gezmek; 2) bir yerlere sürekli girip çıkmak: ''Elbette öyle ama sen böyle panik hâlinde kapı kapı dolaşırsan, teşkilatta muhalefet var sanıp gerçekten de bir temizliğe başlayabilirler.'' -A. Ümit. 3) iş aramak için her yere başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7894, 'kapı komşusu yapmak (etmek)', 'bir yere sık gidip gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7895, 'kapı yapmak', '1) bir şey istemek veya söylemek için karşısındakini önceden başka sözlerle hazırlamak: ''Rumeli''de bıraktığı çiftlikleri de anlattıktan sonra yaptığı kapıyı kâfi gördü. İşlere geçti.'' -Ö. Seyfettin. 2) ev gezmesi yapmak; 3) kapı almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7896, 'kapıda kalmak', 'içeri girememek: ''Anahtar bendedir. Onlar sonra kapıda kalırlar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7897, 'kapıdan çevirmek', 'geri döndürmek, kabul etmemek: ''Fakat görücüleri de kapıdan çevirmeyi doğru bulmuyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7898, 'kapıdan kovsan bacadan düşer', 'yüzsüz, arsız kimseler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7899, 'kapılar yüzüne (üzerine veya üstüne) kapanmak', 'istenilen şeye ulaşma imkânı verilmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7900, 'kapıları açık tutmak', 'herhangi bir konuda ilişkiyi kesmeden anlaşma ortamını sürdürmeye çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7901, 'kapıları kapamak', 'bütün ilişkileri kesmek veya anlaşma ortamını ortadan kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7902, 'kapıp koyuvermek', '1) kendini bırakmak: ''Nihayet yorgunluktan sızıp kalıncıya kadar kendimi bu buhrana kapıp koyuverdim.'' -E. İ. Benice. 2) bırakmak, vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7903, 'kapısına kilit vurmak', '1) girilip çıkılmasını önlemek için bir yeri kapamak; 2) bir yerin çalışmasına son vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7904, 'kapış kapış gitmek', 'çok çabuk satılmak, çok istenir olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7905, 'kapış kapış yapmak', 'üstüne atılmak, aceleyle almak: ''El elin ayıbını terzi kumaşı alır gibi kapış kapış yaptığı için aldırış etmem.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7906, 'kapıya dayanmak', '1) gelip çatmak: ''Kış kapıya dayandı, daha kömür alamadık.'' -R. N. Güntekin. 2) bir şey elde etmek için bir yeri, bir kimseyi zorlamak, göz korkutmak: ''Bereket versin ki padişahın cellatları kapıya dayanmadılar.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7907, 'kapıyı açmak', '1) bir işe veya bir konuya öncelikli olarak başlamak; 2) bir işte başkalarına örnek olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7908, 'kapıyı büyük açmak', 'çok masraflı bir işe girişmek veya hesapsız harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7909, 'kapıyı göstermek', 'kovmak, uzaklaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7910, 'kaplumbağa gibi', 'soğukkanlı ve yavaş hareket eden (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7911, 'kaput etmek', 'kâğıt oyununda karşısındakini tek sayı alma imkânından yoksun bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7912, 'kaput gitmek (olmak)', '1) kâğıt oyununda hiçbir sayı alamamak; 2) argo hiçbir sınavı verememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7913, 'kâr bırakmak', 'kazanç getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7914, 'kâr eden ar etmez', 'birinin sıkılmayı bir yana bırakarak yalnız çıkarına baktığı anlatılırken söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7915, 'kâr getirmek', 'bir şey para kazandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7916, 'kar gibi', 'temiz, beyaz: ''Kar gibi çamaşırları serip eve döndü.'' -O. Rifat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7917, 'kâr koymak', 'bir şeyin maliyet fiyatı üzerine kâr payını katmak, kazanç koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7918, 'kar kuytuda, para pintide eğleşir', 'her şey, saklanabilen yerde ve saklamasını bilenin yanında bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7919, 'kar ne kadar çok yağsa yaza kalmaz', 'elverişli bir ortamda çoğalan şeyler, ortam elverişliliğini yitirince yok olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7920, 'kar susuzluk kandırmaz', 'gerçek gereksinimler, avutucu, oyalayıcı şeylerle karşılanmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7921, 'kâr, zararın kardeşidir (ortağıdır)', 'ticarette sadece kâr etmek düşünülmez, zarar da edilebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7922, 'kara gün kararıp kalmaz', 'insanın sıkıntılı zamanı sürüp gitmez, arkasından iyi günler de gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7923, 'kara haber tez duyulur', 'ölüm gibi kötü haber çabuk yayılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7924, 'kara kara düşünmek', 'çok üzüntülü olmak, düşünceye dalmak: ''Kara kara düşünmeye başladım, böyle bir toplantıyı, kim, hangi kurum destekleyecekti?'' -M. C. Anday.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7925, 'kara kışta karlar, martta yağmaz, nisanda durmazsa değme çiftçinin keyfine', 'kara kışta kar yağar, martta yağış olmaz, nisanda da çok yağmur yağarsa o yıl bol ürün alınır; çiftçinin yüzü güler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7926, 'kara listeye almak', 'birini, bir grubu, bir ülkeyi sakıncalı veya zararlı görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7927, 'kara para aklamak', 'yasa dışı yollarla elde edilen parayı yasallaştırmak için yatırım yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7928, 'kara sürmek', 'kara çalmak: ''Gericiliği, insanlara kara sürme suçlamalarını kabul etmedi.'' -K. Tahir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7929, 'kara yasa bürünmek', '1) aşırı üzülmek; 2) derin derin düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7930, 'kara yaslanma kar erir, ere yaslanma er ölür', 'insan başkalarından gelecek olan desteğe çok güvenmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7931, 'karabatak gibi', 'bir görünüp bir ortadan kaybolan (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7932, 'karaborsaya düşmek', 'bir mal gizlice pahalıya alınıp satılır olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7933, 'karada ölüm yok', 'bundan sonra herhangi bir sıkıntı ile karşılaşma ihtimali yok.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7934, 'Karadeniz''de gemilerin mi battı?', 'çok düşünceli ve durgun görünen kimseler için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7935, 'karagöz oynatmak', 'komik bir durum yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7936, 'karagözlük etmek', 'güldürüp eğlendirecek davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7937, 'karakol gezmek', 'huzur ve güvenliği sağlamak amacıyla dolaşmak, devriye gezmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7938, 'karakola düşmek', 'herhangi bir suç dolayısıyla karakolluk olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7939, 'karakolluk olmak', 'kavga sonucu karakola gitmek zorunda kalmak: ''Sonra karakolluk olmaz mıyız?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7940, 'karalar bağlamak (giymek)', 'yas tutmak: ''Bütün yaşamı karardı, sokağa çıkamaz oldu, karalar bağladı.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7941, 'Karaman''ın koyunu sonra çıkar oyunu', 'bir şeye tam güvenmeyip ileride ne olacağı konusunda bilgi sahibi olunamadığı durumlarda kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7942, 'karambole getirmek', '1) karışıklıktan yararlanarak birini aldatmak; 2) bir işi aşırı bir çabuklukla yaparak gereken özeni göstermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7943, 'Karamürsel sepeti sanmak', 'bir kimse veya şeyi ufak, önemsiz saymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7944, 'karanfili sıkmak', ' argo tehlikelere ve güçlüklere göğüs gerebilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7945, 'karanlığa gömülmek', '1) koyu karanlık içinde kalmak; 2) büyük sıkıntı ve keder içinde kalmak: ''Türkiye''nin güneşi battı, karanlığa gömüldük.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7946, 'karanlığa kalmak', 'gidilecek yere varmadan akşam olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7947, 'karanlığı deşmek (yırtmak)', '1) karanlıkta görmeye çalışmak, aydınlığa çıkmak için çaba harcamak: ''Gözleriyle sokakların karanlıklarını yırtmaya uğraşarak sinirli bir telaş içinde çırpınıyordu.'' -H. R. Gürpınar. 2) mec. büyük sıkıntı ve üzüntüden kurtulmak için çabalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7948, 'karanlık basmak (çökmek)', 'hava kararmak: ''Akşamdı, ortalığa hafif bir karanlık çökmüştü.'' -R. N. Güntekin. ''Tekrar ana yola geldiğim zaman karanlık basmıştı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7949, 'karanlık etmek', 'bir şeyin önünde durarak görünmesine engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7950, 'karanlık kesilmek', 'ortalık birdenbire kararmak: ''Gece kandili birdenbire sönmüş, oda zifirî karanlık kesilmişti.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7951, 'karanlıkta göz kırpmak', 'bir şeyi anlatmak isterken karşısındakinin anlayamayacağı bir işarette bulunmak veya bir söz söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7952, 'karar almak', 'bir davayı, bir sorunu sonuca bağlamak: ''Artık ayrılmayalım diye kararlar alıyor fakat bir türlü tatbike geçemiyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7953, 'karar altına almak', 'karar vermek, kararlaştırmak: ''Dün akşam size tesadüf ettiğimde bunu karar altına almıştık.'' -H. Z. Uşaklıgil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7954, 'karar kılmak', 'birçok şeyi deneyip birini seçmek: ''Tekrar masa başına dönmekten zevkli bir iş bulamayacağımda karar kıldım.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7955, 'karar vermek', 'bir sorunu karara bağlamak, kararlaştırmak: ''Sonunda bu su tenekelerini civardaki evine kadar taşımaya karar verdi.'' -İ. H. Baltacıoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7956, 'karara bağlamak', 'bir davayı, bir sorunu çözümlemek, sonuçlandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7957, 'karara kalmak', 'davanın görüşülmesi bitip yargıcın kararını beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7958, 'karara varmak', 'bir konuda anlaşmak, bir şeyi kararlaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7959, 'kararında bırakmak', 'ölçüyü aşmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7960, 'karavana çıkmak', 'yemek hazırlanmak veya gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7961, 'karavanadan yemek', 'toplu durumda aynı kaptan yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7962, 'karaya ayak basmak', '1) deniz, göl vb.nden karaya çıkmak; 2) deniz taşıtından karaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7963, 'karaya düşmek', 'deniz içinde bulunan bir şey akıntı veya dalga ile kıyıya atılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7964, 'karaya oturmak', 'gemi denizin sığ bölümüne saplanıp kalmak: ''Olan olmuş, bizim teknenin bir yanı, pamuk şiltelere serilir gibi karaya oturmuş.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7965, 'karaya sabun, deliye öğüt neylesin', 'özü bozuk olan şey, düzeltme çabalarıyla iyi duruma getirilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7966, 'karaya vurmak', '1) karaya çarpmak: ''Loşluklar içinde bana, sandalımız ikide bir karaya vuruyor gibi geliyordu.'' -R. H. Karay. 2) denizdeki bir cisim kendini karaya atmak: ''Ağzımı, karaya vurmuş bir balık gibi sonuna kadar açıyorum ama soluk alamıyorum.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7967, 'karda yürüyüp (gezip) izini belli etmemek', 'kimsenin sezemeyeceği biçimde gizli iş çevirmek: ''Karda yürüyüp izini belli etmemek, cümlesiyle tarif edilen bu sinsilik, hedefine asla varamayan adi bir hiledir.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7968, 'kardeş kardeşi bıçaklamış, dönmüş yine kucaklamış', 'kardeşler ne kadar geçimsiz olsalar da kötü bir durumda birbirlerine yardım ederler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7969, 'kardeş kardeşin ne öldüğünü ister, ne onduğunu', 'kardeş, kardeşe zarar gelmesini istemez ama onun kendisinden üstün durumda olmasını da kıskanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7970, 'kardeşim olsun da kanlım olsun', 'kendisine çok büyük kötülük de yapsa insan kardeşinden vazgeçemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7971, 'kardeşten karın yakın (kulaktan burun yakın)', 'insanın kendi yararı her şeyden önemlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7972, 'karga bok yemeden', ' kaba çok erken bir saatte.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7973, 'karga gibi', 'çok zayıf ve esmer (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7974, 'karga ile gezen boka konar', 'kişi kiminle arkadaşlık ederse ondan kendisine birtakım huylar geçer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7975, 'karga kekliği taklit edeyim derken kendi yürüyüşünü şaşırmış', 'görgüsüz kişi, görgülü kişinin yaptığını yapmaya kalkışırsa beceremez, kendisinin doğal davranışını da yitirir, gülünç duruma düşer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7976, 'karga mandayı babası hayrına bitlemez', 'bir kimse başkasına hizmet ediyorsa bunda kendisinin de çıkarı vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7977, 'karga tulumba etmek', 'birkaç kişi, birini kollarından bacaklarından tutup kaldırmak: ''Emine kalkmak istemiyor, boyuna hıçkırıyordu. Ötekiler hep bir olup onu karga tulumba edince yanıma getirdiler.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7978, 'karga yavrusuna bakmış ''benim ak pak evladım'' demiş', 'herkesin kendi yarattığı şey çirkin de olsa gözüne güzel görünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7979, 'kargadan başka kuş tanımamak', 'bildiğinden veya öğrendiğinden kesinlikle şaşmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7980, 'kargaşa çıkarmak', 'gürültü patırtıya yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7981, 'karı gibi', 'korkak, dönek (erkek).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7982, 'karı koca bir sözle yakın, bir sözle uzaktır', 'bir kadınla bir erkek, birbirlerine bağlandıklarını bildiren bir sözle karı koca olurlar, böyle bir bağın kalmadığını bildiren bir sözle de yabancı olurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7983, 'karı malı hamam tokmağıdır', 'bir erkek, karısının malından yararlanmayı düşünmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7984, 'karılık etmek', '1) evli bir kadın kocasına olan görevini yerine getirmek; 2) hkr. erkek için döneklik etmek, hile yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7985, 'karın doyurmak', '1) geçinmek: ''Yoğurtçuda çalışanlar bu türlü karın doyuranları çok görmüşlerdi.'' -N. Cumalı. 2) yararı olmak: ''Fakat öpüşmek, sevişmek karın doyurmuyor.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7986, 'karınca duası gibi', 'çok küçük, sık ve okunaksız (yazı).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7987, 'karınca yuvası gibi', 'çok kalabalık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7988, 'karınca, zevali gelince kanatlanır', 'kişi durumunun gereklerine aykırı taşkınlıklarda bulunursa artık düşecek demektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7989, 'karıncadan ibret al, yazdan kışı karşılar', 'kişi çalışıp kazanabildiği zamanı boş geçirmemeli, çalışamayacağı günler için geçimini sağlayacak varlık edinmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7990, 'karıncayı bile ezmemek (incitmemek)', 'çok merhametli, ince duygulu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7991, 'karından kardeş yakın', 'insanın kendi yararı her şeyden önemlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7992, 'kârını tamam etmek', 'öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7993, 'karının saçlısı, tarlanın taşlısı', 'kadının saçlı olanı ile tarlanın taşlı olanı makbuldür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7994, 'karısının üstüne evlenmek', 'karısı varken bir kadınla daha evlenmek: ''Fakat hanımefendi, bugün İstanbul''da karısının üstüne evlenmiş kaç erkek var?'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7995, 'karış karış bilmek', 'en ince ayrıntısına kadar biliyor olmak: ''Buraların girdisini çıktısını, deliğini kovuğunu karış karış bilir.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7996, 'karış karış dolaşmak', 'her yeri gezmek: ''Ben Türk köylerini karış karış dolaştım, bilirim.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7997, 'karina etmek', 'gemiyi karinası ortaya çıkacak biçimde bir yanı üzerine yatırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7998, 'karinaya basmak', 'karina etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (7999, 'karine ile anlamak', 'sözün gelişinden çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8000, 'karizmayı çizdirmek', ' argo var olan etkileyiciliğini kaybetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8001, 'karman çorman etmek', 'çok karışık ve düzensiz duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8002, 'karman çorman olmak', 'çok karışık ve düzensiz duruma gelmek: ''İşler gittikçe kızışıyor, hikâyeler gittikçe karman çorman oluyor, hangi sözü kimin söylediği belli olmuyordu.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8003, 'karmanyolaya getirmek', 'soymak: ''Sarhoş buldunuz adamı karmanyolaya getireceksiniz.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8004, 'karnı büyümek', 'hamile kalmak: ''Felaket bununla bitmemiş, üç ay sonra karnı büyümeye başlamış.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8005, 'karnı tok it gölgede yatar', 'akılsız kişi bugün karnını doyurunca yarını düşünmez, yan gelir yatar, keyfine bakar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8006, 'karnı tok, sırtı pek', 'geçim sıkıntısı olmayan insanlar için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8007, 'karnı tok sırtı pek olmak', 'geçimi iyi olmak, para sıkıntısı olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8008, 'karnı zil çalmak', 'çok acıkmış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8009, 'karnından konuşmak (söylemek)', '1) işitilemeyecek kadar alçak sesle söylemek; 2) uydurarak söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8010, 'karnını doldurmak', '1) çok yemek yemek; 2) argo gebe kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8011, 'karpuz kabuğunu görmeden denize girme', 'bir işi en uygun zamanı gelmeden yapma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8012, 'karpuz kesmekle hararet sönmez', 'size kötülük yapmış olan bir kimseden başkasına zarar vermekle o kimseden öç almış olmazsınız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8013, 'karşı çıkmak', '1) dışarıdan gelenleri karşılamaya gitmek: ''Edirne''nin üç şerefelisi de kandillerden kaftanı ile ona karşı çıkmış.'' -R. E. Ünaydın. 2) bir düşünceye katılmamak, cephe almak: ''Üniversiteyi bitirince isteğimi babama açtım, önce biraz karşı çıkar gibi oldu.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8014, 'karşı karşıya gelmek', '1) birden karşılaşmak: ''Nihayet bir defa, gene dereden köye doğru giderken karşı karşıya gelmeyeyim mi?'' -Y. K. Karaosmanoğlu. 2) zıtlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8015, 'karşı karşıya olmak', 'yüz yüze gelmek: ''Az sonra, şairin çok şairane bir hayretiyle karşı karşıyayız.'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8016, 'karşı koymak', 'boyun eğmemek: ''Yabancının bu kötü kastına yalnız azmimizle karşı koyduk.'' -R. E. Ünaydın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8017, 'karşı olmak', 'birine veya bir düşünceye katılmamak, karşıt olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8018, 'karşılık vermek', '1) küçük büyüğüne karşı gelmek; 2) cevap vermek, yanıt vermek: ''Haşarı oğlan bu ağzı bozuk kadına şöyle karşılık veriyordu.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8019, 'karşılıkta bulunmak', 'cevap vermek: ''Bunun üzerine Refet Paşa kahkahalarla gülerek bana şöyle bir karşılıkta bulunmuştu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8020, 'karşısına dikilmek', '1) birinin karşısında durmak: ''Sizi bucak bucak arayan ölüm, nihayet izinizi bulup karşınıza dikildi mi?'' -A. N. Asya. 2) karşıt olmak: ''Bütün arkadaşlar yarın ele ele vererek karşınıza dikilirler.'' -H. Topuz. 3) engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8021, 'kart basmak', 'işçiler iş yerine giriş ve çıkışta gelip gittiklerini bir makine aracılığıyla belirtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8022, 'kart çıkarmak', ' sp. hakem kural dışı hareket eden oyuncuya cezalandırma amacı ile sarı veya kırmızı kart göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8023, 'kartala bir ok değmiş, yine kendi yeleğinden', 'bir kimseye en büyük kötülüğü kendisine çok yakın olanlar yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8024, 'kasap et derdinde, koyun can derdinde', 'bir kişi önemli bir kaybından dolayı çırpınıp kıvranırken başka bir kişi bu durumdan ne kadar çok yararlanabileceğini düşünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8025, 'kasap, yağı bol bulunca gerisini yağlar', 'elinde kendisine gerekli olandan fazla şey bulunan kimse, bunu gereksiz yere savurup telef eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8026, 'kasavet çekmek', 'üzülmek, tasalanmak: ''Dövüşen yiğitler de boyanır kana / Kasavet mi çeker seni doğuran ana'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8027, 'kasavet etmek', 'üzülmek, kaygılanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8028, 'kasavetsiz ağız anahtarsız açılır', 'sıkıntısı, kaygısı olmayan kimse, her konuda rahat konuşur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8029, 'kasayı devretmek', 'işletmelerde nöbetleşe çalışan kasadarlar kasa mevcudunu birbirine aktarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8030, 'kasım kasım kasılmak', 'gururlanmak, büyüklük taslamak, büyüklenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8031, 'kasım yüz elli, yaz belli', 'işin zor yanı atlatıldıktan sonra gerisi nasıl olsa gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8032, 'kasımdan on gün evvel ek, on gün sonra ekme', 'ekim zamanı kasımdan on gün önce biter, kasımdan on gün sonra ekilen tohum verimli olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8033, 'kasıp kavurmak', '1) baskı yaparak veya kıyıcı davranışlarla bir topluluğu ezmek, zulmetmek: ''Karaköy civarını kasıp kavuran iki serseri çocuğu enselerinden yakalayıp huzuruna getirmiştim.'' -Y. K. Karaosmanoğlu. 2) çok zarar vermek, mahvetmek: ''Derhâl asabi, ince, deli sesi çınlamaya başlar, etrafı kasıp kavurur ve kıyametleri koparırdı.'' -A. Ş. Hisar. 3) çok etkilemek, hüküm sürmek: ''Dışarıda ortalığı kasıp kavuran bir ayaz vardı.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8034, 'kaskatı kesilmek', 'aşırı coşku, soğuk, korku, üzüntü vb. etkisiyle hareket edemeyecek, bir şey söylemeyecek duruma gelmek, donup kalmak: ''Kaskatı kesilmiş vücudu, suyun hafif akıntısına uyarak yavaş yavaş uzaklaştı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8035, 'kasvet basmak (çökmek)', 'çok sıkılmak, içi daralmak: ''Gündüzün bu saatinde, tiyatroya ağır bir kasvet çökmüş.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8036, 'kasvet vermek', 'sıkıntı vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8037, 'kaş göz etmek', 'kaş ve göz işaretleriyle bir şey anlatmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8038, 'kaş göz işareti yapmak', 'kaş ve gözle bir şeyler anlatmak, dikkat çekmek: ''Murat Bey konuşurken bana kaş göz işaretleri yapıyor, bir yandan da kahkahalarla gülüyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8039, 'kaş ile göz gerisi söz', 'güzellik, her şeyden önce kaş ve göz güzelliğidir, vücudun başka yerlerinin güzel olması önemli değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8040, 'kaş yapayım derken (yaparken) göz çıkartmak (çıkarmak)', 'işi düzelteyim derken büsbütün bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8041, 'kaş yıkmak', 'kaş çatmak: ''El yanında yıkar gider kaşını / Tenhalarda gülüşünü sevdiğim'' -Ruhsati.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8042, 'kaşı (kaşları) çatılmak', 'öfkelenmek, kızmak: ''Babam kaşları çatılmış, başını sallayarak izliyor bizi.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8043, 'kaşık atmak (çalmak)', 'iştahla veya çabuk yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8044, 'kaşık kadar', 'çok küçük: Hastalanınca yüzü kaşık kadar kaldı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8045, 'kaşık sallamak', 'yemek yemek: ''Gençler tarhana aşına kaşık salladılar.'' -N. Araz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8046, 'kaşıkla verip kepçeyle geri almak', 'yaptığı bir iyiliğin acısını çıkarırcasına davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8047, 'kaşıkla yedirip sapıyla (gözünü) çıkartmak', 'yaptığı bir iyiliği hiçe indirecek kötülükte bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8048, 'kaşını gözünü eğmek', 'kızgın bir durumdayken kaş çatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8049, 'kaşının altında gözün var dememek', 'gözünün üstünde kaşın var dememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8050, 'kaşla göz arasında', 'kimsenin sezmesine imkân vermeyecek kadar kısa bir zaman içinde, çok çabuk: ''Kuzum, kaşla göz arasında ne zaman geldin ve ne zaman kaybettin paranı?'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8051, 'kaşlarını çatmak', 'kızmak, öfkelenmek: ''Dönüp ardına baktı, bakmasıyla kaşlarını çatması bir oldu, yüzü kararıverdi.'' -B. Günel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8052, 'kat çıkmak', 'yapıya kat eklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8053, 'katakulli okumak', 'yalan söylemek, palavra atmak: ''Her seferki gelişinde bu katakulliyi okursun fakat sözün ardı hep boşa çıkar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8054, 'katakulliye gelmek', 'tuzağa düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8055, 'katakulliye getirmek', 'tuzağa düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8056, 'katana gibi', 'iri yarı (kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8057, 'katıla katıla ağlamak', 'aşırı derecede ağlamak: ''Meğer aradan birkaç ay geçecek ve yine o evde, yine gözlerimizden yaşlar akarak katıla katıla ağlayacakmışız.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8058, 'katıla katıla gülmek', 'aşırı derecede gülmek: ''Bir kahveye yolu düşmüş, kahvede oturanların hepsi katıla katıla gülüyorlarmış.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8059, 'katır gibi', 'inatçı (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8060, 'katır kuyruğu gibi kalmak', 'bir işte ilerlemeden kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8061, 'katır tepmişe dönmek', 'çok hırpalanmak, perişan duruma düşmek, felaketin nereden geldiğini anlayamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8062, 'katıra ''baban kim?'' demişler, ''dayım at'' demiş', 'aşağılık duygusu içinde bulunan kişi, kendisini olduğu gibi göstermeye utanır da kötü yönünü gizler, sadece iyi yönüyle övünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8063, 'katkıda bulunmak', 'bir şeyin oluşmasına, gelişmesine veya gerçekleşmesine emek, bilgi, para vb. ile yardım etmek: ''Bu kitaba kendileri ayrımında olmasalar da pek çok insan katkıda bulundu.'' -E. Atasü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8064, 'katmer kaldırmak', ' hlk. karışıklık çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8065, 'katmerli katmerli gülmek', 'üst üste ve ara vermeden aşırı derecede gülmek: ''Harun sarhoşluğun sinirliliğiyle yine göbek ve gerdan titreterek katmerli katmerli gülmeye başladı.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8066, 'katran gibi', 'karaya yakın koyu renkte: ''Gece karanlığından daha kesif, katran gibi karanlık bir mübarek daire...'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8067, 'katrandan olmaz şeker, olsa da cinsine çeker', 'kötü asıllı şey ve kişi iyiye dönmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8068, 'katranı kaynatsan olur mu şeker?', 'kişi, kendi özünü veya asıl özelliklerini değiştirmiş gibi görünse de asla değişmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8069, 'katresi kalmadı (yok)', 'hiç kalmadı, hiç yok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8070, 'kav gibi', '1) kolaylıkla tutuşacak durumda olan; 2) kuru ve gevrek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8071, 'kavak, yaprağını tepeden dökerse kış çok olur', 'kavak ağacının yaprakları tepeden dökülmeye başlar, aşağıdakiler daha sonra dökülürse o yıl kış çetin olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8072, 'kavanoz dipli dünya', 'üzülmemeyi, biraz boş vermeyi, rahat bir biçimde yaşamayı anlatan söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8073, 'kavara çekmek', ' kaba yellenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8074, 'kavga bizim yorganın başına imiş', 'başkaları yüzünden zarar gören kimsenin söylediği söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8075, 'kavga çıkarmak', 'kavgaya neden olmak: ''Bir gün hiç yoktan kavga çıkarıp oğlanın ağzını burnunu bir güzel dağıtıverdiler.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8076, 'kavga çıkmak', 'dövüş meydana gelmek: ''Sık sık kavga çıkıyordu aralarında, ana avrat küfrediyorlardı.'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8077, 'kavga kopmak (patlamak)', 'dövüş başlamak: ''Softalar arasında kızıl bir kavga kopmuştu.'' -F. R. Atay. ''Su yolunda tuğlacılarla konducular arasında kavga patladı.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8078, 'kavgada kılıç ödünç verilmez', 'kişi, savunma silahını başkasına verip kendisini savunamayacak ve yenilgiye uğrayacak duruma düşmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8079, 'kavgada yumruk sayılmaz', 'kavga sırasında hem dayak yenilir hem de atılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8080, 'kavgaya girişmek (tutuşmak)', 'kavgaya başlamak: ''Kendilerine acındırmak için yalandan kavgaya tutuşup birbirlerinin saçlarını başlarını yoldular. Yüzlerini kanattılar.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8081, 'kavil kesmek', 'sözleşmek: ''Dostumla da kavil kestim / Yalan çıktı ona küstüm'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8082, 'kavis çizmek', 'yay biçiminde yol izlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8083, 'kavlükarar etmek', '1) karar vermek: ''Çeşmeler yaptırdım sular içmeye / Kavlükarar ettim alıp kaçmaya'' -Halk türküsü. 2) birlikte söz vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8084, 'kavun kökeninde büyür', 'çocuk anne baba ocağında, herhangi bir kişi doğup büyüdüğü çevrede yetişir, gelişir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8085, 'kavurga karın doyurmaz', 'gerçek gereksinimler, avutucu, oyalayıcı şeylerle karşılanmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8086, 'kaya gibi', 'çok sağlam: ''Uçan kâğıt rüzgârın hızını belli eder, kaya onu durdurur. Kaya gibi olunuz!'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8087, 'kaya uçmazsa, dere dolmaz', 'büyük gereksinimlerde çok fedakârlık yapmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8088, 'kaybolan (zayi olan) koyunun kuyruğu büyük olur', 'elden kaçırılan fırsat gözde büyütülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8089, 'kayda geçirmek', 'ilişkili bulunduğu deftere yazmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8090, 'kaydını düşmek', 'yazılı olduğu evraktan çıkarmak: ''Bir idamlık Ali vardı, asıldı / Kaydını düştüler, mühür basıldı'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8091, 'kaygı çekmek', 'üzüntü, tasa duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8092, 'kaygı vermek', 'endişelendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8093, 'kayık yanaştırmak', 'bir konuya veya soruna yavaş yavaş girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8094, 'kayıp vermek', 'ulus, toplum, kuruluş vb. değerli bireylerini yitirmek: ''Bizim yokuş son iki yılda çok kayıp vermişti. Cemal Nadir bu kayıpların en büyüğüydü.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8095, 'kayıplara karışmak', 'bulunduğu yerden ayrılıp gitmek, gittiği yeri bildirmemek, görünmez olmak: ''Şu yeşil bu mor derken bizim futbol sevgisi gene kayıplara karıştı.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8096, 'kayış bilir kotan ne çeker', 'ağır bir işin ne kadar güç yapıldığını ancak o işin yapılmasında aracı olan, emeği geçen bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8097, 'kayış gibi', '1) sert, koparılmayan: Kayış gibi et. 2) çok kirli: Kayış gibi çamaşır.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8098, 'kayıt altına girmek', '1) bir şey yapmaya zorlanmak: Ben kayıt altına giremem. 2) davranışları sınırlandırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8099, 'kayıt koymak', 'engellemek, sınırlamak, takyit etmek: ''Kanun ... kamuoyunun serbestçe oluşmasını engelleyici kayıtlar koyamaz.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8100, 'kayıtsız kalmak', 'önem vermemek, umursamamak: ''Halk, nice silik insanların en yüksek mertebelere çıkmasına kayıtsız kalır.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8101, 'kayıtsız olmak', '1) kaydedilmemiş veya yazıya geçirilmemiş olmak; 2) mec. ilgisiz, umursamaz, önem vermeyen durumda bulunmak: ''Ev sahipleri misafirlerini tanıştırmakta pek kayıtsız olduklarından ben kendimi kıza tanıttım.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8102, 'kaymağı seven mandayı yanında taşır', 'sevdiği şeyden yoksun kalmak istemeyen kişi, onu sağlayacak araçları eli altında bulundurmalı ve bunun için gereken sıkıntılara katlanmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8103, 'kaymak bağlamak (tutmak)', 'sütün veya bir sıvının üzerinde kaymak oluşmak, kaymaklanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8104, 'kaymak gibi', '1) bembeyaz ve pürüzsüz; 2) tadı güzel ve yumuşak: ''Patlıcan kızartması, pilav, bir de koca kâse kaymak gibi yoğurttan oluşan yemeğimizi yedik.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8105, 'kaynağını (bir şeyden) almak', 'bir esasa veya desteğe dayandırmak: ''Hiçbir kimse veya organ kaynağını Anayasa''dan almayan bir devlet yetkisi kullanamaz.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8106, 'kaynak yapmak', '1) iki metal veya yapay parçayı ısı yoluyla birleştirmek; 2) mec. sırayı beklemeden başkalarının hakkını alarak mevcut sıranın ön taraflarına girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8107, 'kaynana öcü, oğlu cici', 'gelinler kocalarını severler de kaynanalarını sevmezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8108, 'kaynana pamuk ipliği olup raftan düşse gelinin başını yarar', 'kaynana ne kadar yumuşak huylu, ne kadar iyi davranışlı olursa olsun, her hâli gelini rahatsız eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8109, 'kaynanalık taslamak', 'kaynana gibi davranmak: ''Hani kayınvalidem olsa, canım yanmayacak ama bana sürekli kaynanalık taslayan hanım, kocamın anası bile değil.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8110, 'kaynayan kazan kapak tutmaz', 'içten içe, gizlice gelişen olaylar veya duygular bir yerde patlak verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8111, 'kaz gelen yerden tavuk esirgenmez', 'büyük çıkarlar beklenen durumlarda küçük fedakârlıklar yapılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8112, 'kaz kazla, daz dazla, kel tavuk kel (topal) horozla', 'herkes kendi durumuna uygun gelen kişilerle anlaşıp arkadaşlık ederler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8113, 'kaza atlatmak', 'kaza tehlikesi geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8114, 'kaza geçirmek', 'can ve mal kaybına veya zararına neden olan kötü bir olayla karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8115, 'kaza geliyorum demez', 'kaza, beklenmedik zamanda, ansızın olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8116, 'kazan kaldırmak (devirmek)', ' tar. 1) yeniçeriler yemek pişirilen kazanı devirerek ayaklanmak, isyan etmek: ''İkide birde kazan deviren yeniçerilerin dışında askerlikte talim ve terbiye esaslarına göre Avrupai bir nizam ile askerliğimizin ihdası pek hayırlı olmuştu.'' -A. Ş. Hisar. 2) mec. yöneticinin bir tutumuna karşı hep birden ayaklanmak, isyan etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8117, 'kazan kaynamayan yerde maymun oynamaz', 'hiçbir iş karşılıksız yapılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8118, 'kazançlı çıkmak', 'kazanmak: ''Yarıştan kazançlı çıkmak için hasmının kayıplarına karşı duyarsız kalmak zorunludur.'' -İ. Özel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8119, 'kazanırsan dost kazan, düşmanı anan da doğurur', 'sen dost kazanmanın yoluna bak, düşman kolay kazanılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8120, 'kazanmayanın kazanı kaynamaz', 'kazancı olmayan kişinin evinde yemek pişmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8121, 'kazaya bırakmak', ' din b. 1) namazı vaktinde kılmayarak daha sonra kılmak için ertelemek: ''Bu yaşa geldim, Allah''a bin şükür, namazımı kazaya bırakmadım.'' -H. R. Gürpınar. 2) orucu vaktinde tutmayarak daha sonra tutmak için ertelemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8122, 'kazaya kalmak', ' din b. 1) namaz, vaktinde kılınamamak: ''Osman, kazaya kalan namazını daha ziyade geciktirmeden korkarak ayağa kalktı.'' -R. H. Karay. 2) oruç, vaktinde tutulamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8123, 'kazaya rıza göstermek', '1) yargıya, verilen hükümlere boyun eğmek; 2) kadere, alın yazısına boyun eğmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8124, 'kazaya uğramak', 'kaza geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8125, 'kazdığı çukura (kuyuya) kendisi düşmek', 'başkası için hazırladığı kötülüğe kendi uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8126, 'kazı koz anlamak', 'söylenen şeyi çok yanlış anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8127, 'kazığa vurmak', ' esk. bir kimseyi yere dikilmiş ucu sivri bir kazığa oturtarak öldürmek: ''Münasebetsizliklerine mukabele edeni ihtimal kazığa vuracak, derisini yüzecek, akla gelmedik kaba bir vahşetle öldürecekti.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8128, 'kazık atmak', 'aldatmak, kazıklamak: ''Bütün ilişkileri birilerine kazık atmak üzerine kurulu.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8129, 'kazık dikmek', 'devamlı kalmak, ebediyen yaşamak: ''Dünyaya kim kazık dikecek?'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8130, 'kazık gibi', 'dimdik ve sert: ''Kara, kuru, kibirli, kazık gibi bir kadın, komutan Muhsin Bey, bunun neresini beğenmiş?'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8131, 'kazık kadar', ' tkz. kocaman (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8132, 'kazık yemek', 'aldatılmak, kazıklanmak: ''O levhayı görünce istediği parayı verip afiyetle kazığı yiyerek çıkarsın.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8133, 'kazık yutmuş gibi', 'baston yutmuş gibi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8134, 'kazın ayağı öyle değil', '''bir sorun, bir durum sanıldığı gibi değildir'' anlamında kullanılan bir söz: ''Doğru söylüyorsun Ali, doğru söylüyorsun ama kazın ayağı öyle değil.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8135, 'kazma elin kuyusunu, kazarlar kuyunu', 'sen başkasına kötülük yapma yolunu tutarsan başkası da sana kötülük yapma yolunu tutar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8136, 'kazma gibi', 'büyük, kocaman (diş).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8137, 'keçe külah etmek', 'aldatmak, kandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8138, 'keçe külah olmak', ' esk. ordudan veya resmî görevden çıkarılmak: ''Askerde, vüzera, rical dairelerinde ''keçe külah olmak'' kıyafeti soyulup tardedilmek demek idi.'' -A. Rasim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8139, 'keçesini sudan çıkarmak', 'güç olan bir işi, durumu yoluna koyarak rahatlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8140, 'keçeyi suya atmak', 'ar ve namusu hiçe saymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8141, 'keçi geberse de kuyruğunu indirmez', 'inatçı ölse de inadından vazgeçmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8142, 'keçi kurttan kurtulsa gergedan olur', 'tehlikeler, zararlar olmasa her şey alabildiğine gelişir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8143, 'keçi nereye çıkarsa oğlağı da oraya çıkar', 'büyüklerin tuttuğu yol, küçüklere örnek olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8144, 'keçiboynuzu gibi', 'işi çok, verimi az olan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8145, 'keçide de sakal var', 'sakal, kişiye değer kazandırmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8146, 'keçileri kaçırmak', 'delirmek veya bunalım içinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8147, 'keçilik etmek', 'inat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8148, 'keçinin uyuzu, çeşmenin gözünden su içer', 'değersiz kişiler kendilerini değerli ve en güzel şeye layık görürler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8149, 'keçiye can kaygısı, kasaba yağ kaygısı', 'bir kişi önemli bir kaybından dolayı çırpınıp kıvranırken başka bir kişi bu durumdan ne kadar çok yararlanabileceğini düşünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8150, 'keçiyi yardan uçuran bir tutam ottur', 'gözü doymayan hırslı insanlar küçük bir çıkar için bütün varlığını tehlikeye atar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8151, 'keder çekmek', 'acı duymak, ızdırap çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8152, 'keder vermek', 'üzüntü vermek, kederlendirmek, tasalandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8153, 'kedi ciğere bakar gibi bakmak (süzmek veya seyretmek)', 'imrenerek bakmak: ''Derin bir hayranlıkla gözlerini kıza kaptırmış, kedi ciğere bakar gibi süzüp duruyordu.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8154, 'kedi gibi', 'uysal ve sokulgan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8155, 'kedi gibi dört ayak üzerine düşmek', 'en güç bir durumdan zarar görmeden kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8156, 'kedi götünü görmüş, ''yaram var'' demiş', 'bazı insanlar hiç olmayacak bir şeyi kendisine dert edinir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8157, 'kedi ile harara (çuvala) girmek', 'geçimsiz biri ile iş birliği yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8158, 'kedi ile köpek gibi', 'birbirleriyle geçinemeyen, anlaşamayan kimseler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8159, 'kedi kıçına bakar da ''yaram var'' dermiş', 'bazı insanlar hiç olmayacak bir şeyi kendisine dert edinir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8160, 'kedi ne, budu ne?', 'eti ne budu ne.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8161, 'kedi olalı bir fare tuttu', 'en sonunda bir iş başarabildi.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8162, 'kedi yavrusunu yerken sıçana benzetir', 'kişi yolsuz olduğunu bildiği bir işi yaparken kendini mazur göstermek için bahane uydurur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8163, 'kedi yetişemediği  (uzanamadığı) ciğere pis (murdar) dermiş', 'kişi, elde edemediği şeyi istemiyormuş, beğenmiyormuş gibi görünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8164, 'kedinin boynuna ciğer asılmaz', 'bir kimseye, kullanıp zarar vereceği, kendine mal edip ortadan kaldıracağı şey emanet edilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8165, 'kedinin gideceği samanlığa kadar', 'uygunsuz iş yapan kişi, ne kadar kaçarsa kaçsın, gideceği yer bellidir, kısa zamanda yakayı ele verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8166, 'kedinin kabahatini önüne koyarlar, öyle döverler', 'cezalandırılan kimse suçunun ne olduğunu bilmelidir ki o suçu bir daha işlemesin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8167, 'kedinin kanadı olsaydı, serçenin adı kalmazdı', 'saldırganlar istediklerini yapabilecek durumda olsalardı, zayıfları kolaylıkla ortadan kaldırır, bol bol çıkarlarını sağlarlardı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8168, 'kedinin usluluğu sıçan görünceye kadar', 'atılgan kişilerin sessiz ve eylemsiz durmaları, onları çileden çıkaran bir durum baş gösterince sona erer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8169, 'kediye ''bokun kimya'' demişler, üstünü örtmüş', 'iyilik sevmeyen, karşısındakinin iyi olmasını istemeyen kişi, atacağı bir şeyi diğerine yaramaması için faydalanılamayacak duruma getirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8170, 'kediye peynir (ciğer) ısmarlamak', 'güvenilmeyecek birine saklaması için bir şey bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8171, 'kediyi sıkıştırırsan üstüne atılır', 'senden çekinen, korkan kişi, çok sıkıştırırsan sana karşı gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8172, 'kefeki tutmak', 'küflenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8173, 'kefekiye dönmek', 'delik deşik olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8174, 'kefen alacak adam yüzünden belli olur', 'bir kimsenin herhangi bir eyleme girişeceği, o eyleme girişmesini zorunlu kılan durumlardan anlaşılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8175, 'kefeni boynunda olmak', 'her an ölümü göze almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8176, 'kefeni yırtmak', 'ağır bir hastalıkta ölüm tehlikesini atlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8177, 'kefenin cebi yok', 'ölürken mal veya para götürülmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8178, 'kefil göstermek', 'bir iş için gerekli olan kefili bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8179, 'kehanette bulunmak', 'kâhinlik etmek: ''Bunu belirtirken bir kehanette bulunmuş olmuyordum.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8180, 'kehribar gibi', 'sapsarı, koyu sarı: ''Üstelik tütünler kehribar gibiydi bu yıl.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8181, 'keklik gibi', 'güzel, alımlı, hareketli: ''Bir gün evvel keklik gibi seken dipdiri bir insan, bir gün sonra kargabüken yemiş gibi kıvrılmış yatıyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8182, 'kel başa şimşir tarak', 'birçok gereksinimi varken gereksiz özenti ve gösterişle uğraşanlar için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8183, 'kel ilaç bilse kendi başına sürer', 'kendi işini halledemeyen kişiden aynı durum için yardım alınamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8184, 'kel ölür, sırma saçlı olur, kör ölür badem gözlü olur', 'bir kimse veya bir şey yok olduğunda değer kazanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8185, 'kel yanında kabak anılmaz', 'bir kişinin yanında, uzaktan da olsa onun kusurunu hatırlatabilecek sözler söylemekten çekinilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8186, 'kele köseden yardım olmaz', 'kişi, kendisinin yardıma muhtaç olduğu konuda başkasına yardım edemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8187, 'kelek atmak', ' argo birisini beklemediği anda hile ve dalavere yaparak zarara sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8188, 'kelek yapmak', ' argo oyunbozanlık etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8189, 'keleklik etmek', 'görgüsüzlük, bilgisizlik nedeniyle karşısındakinin gerçek amacını anlayamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8190, 'kelepçe vurmak (takmak)', 'bileklere demir halka geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8191, 'kelepçeye vurmak', 'kelepçe vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8192, 'kelepir yakalamak', 'bir şeyi çok ucuza almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8193, 'kelepire konmak', 'kelepir yakalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8194, 'keleş keleş sırıtmak', 'pis pis gülmek: ''Asker kaçağı vatan hainlerinin keleş keleş sırıtan yüzlerini hayalliyordu.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8195, 'keleye çekmek', 'boğaya çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8196, 'keli körü toplamak', 'işe yaramaz kimseleri toplamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8197, 'kelimeleri tartarak konuşmak', 'sonucu hesaplayarak konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8198, 'kelimenin tam anlamıyla', 'bir durumu anlatmak için kullanılan sözün kapsadığı anlamın tamamıyla: Kelimenin tam anlamıyla bu işin bütün çilesini çekti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8199, 'kelin ayıbını takke örter', 'birçok kimsenin kusurunu zenginlik, makam vb. durumlar örter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8200, 'kelin medarı olsa kendi başına olur', 'kendi işini halledemeyen kişiden aynı durum için yardım alınamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8201, 'kelin merhemi olsa başına sürer', 'kendi işini halledemeyen kişiden aynı durum için yardım alınamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8202, 'kelle götürmek', 'gereksiz bir aceleyle gitmek, koşturmak, acele davranmak: ''İşi besbelli acele imiş. Bir koşturur ki sanırsın kelle götürüyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8203, 'kelle koltukta gezmek', 'gözünü budaktan esirgememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8204, 'kelle koparmak', 'olumsuz ve başarısız bir durum sonunda işe, göreve son vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8205, 'kelle koşturmak', 'gereğinden çok acele etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8206, 'kelle kulak yerinde', '1) kanlı canlı ve iri yapılı olan; 2) gösterişli, itibarlı sayılan: ''Aralarında yaşlı başlı, kelle kulak yerinde, efendiden adamlar da var.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8207, 'kelle sağ olsun da külah bulunur', 'kişi yaşasın da elbet bir iş sahibi olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8208, 'kellesinden olmak', 'can vermek, ölmek: ''Kimi kellesinden olur padişah olayım derken, kimi de yaka paça oturtulur tahtına.'' -T. Oflazoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8209, 'kellesini koltuğuna almak', 'ölümü göze almak: ''Kelleyi koltuğun altına almışız, memleketteki pisliği kanımızla temizlemeye karar vermişiz.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8210, 'kelleyi koltuğun altına almak', 'kellesini koltuğuna almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8211, 'kelleyi vermek', 'canını feda etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8212, 'kem gözle bakmak', '1) kötü niyetle bakmak; 2) nazar değdiren bir bakışla bakmak: ''Eh yakışıklı da delikanlı. Bir tanesi kem gözle baktıysa tamam.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8213, 'kem küm etmek', 'verecek cevap bulamayıp açık bir anlamı olmayan sözler söylemek: ''Mazeretin ne olursa olsun, İncir Han''ından kendi ayağınla çıktıktan sonra artık kem küm etmemek, dilenciliği meslek olarak kabul etmek lazımdır.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8214, 'kem söz, kalp (kem) akçe sahibinindir', 'kötü söz söyleyenindir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8215, 'kemal bulmak', 'kemale ermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8216, 'kemale ermek (gelmek)', 'olgunlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8217, 'keman gibi', 'ince, düzgün (kaş).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8218, 'kemer (kemerini) sıkmak', 'sıkı para politikası anlayışıyla daha az tüketmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8219, 'kemeri dolu olmak', 'çok zengin olmak: ''Genç, ihtiyar, hepsi tüysüz tüysüz, gözleri fersizdir fakat hepsinin kemeri doludur.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8220, 'kemiğine (kemiklerine) kadar', 'iyice, en son sınıra dek: Soğuk kemiklerimize kadar işlemişti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8221, 'kemiğini kurutmak', 'iliğini kurutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8222, 'kemik gibi', '1) pek kuru, katı, sert; 2) sağlam.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8223, 'kemikleri sızlamak', 'ölü huzursuz, rahatsız olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8224, 'kemlik etmek', 'kötü davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8225, 'kemre bağlamak', 'deride kir tabakası oluşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8226, 'kenar gezmek', 'bir şeyden uzaklaşmış olmak: ''Kenar gezme dolan yâr gel içeri / Bize mihman olan yâr gel içeri'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8227, 'kenara atmak', 'bir şeyin üstünde durmamak, önemsememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8228, 'kenara çekilmek', 'artık hiçbir şeye karışmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8229, 'kenarda kalmak', 'kendine yakışan yeri tutamayarak önemsiz bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8230, 'kenarın dilberi nazik de olsa nazenin olmaz', 'kibar çevrede yetişmemiş olanlar ne kadar özenseler de kibarlığın bütün inceliklerini gösteremezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8231, 'kendi ağzıyla tutulmak', 'suçu, yalanı veya iddiasının yanlışlığı kendi sözüyle ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8232, 'kendi âlemine dalmak', '1) çevre ile ilgisini kesip iç dünyasına kapanmak: ''Hayalperest kendi âlemine dalmışken uyanmasına imkân yoktur.'' -S. F. Abasıyanık. 2) eğlenceye, zevküsefaya kapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8233, 'kendi ayağı ile gelmek', 'kendi isteğiyle gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8234, 'kendi derdine düşmek', 'kendi sorunu sebebiyle başka şeyle ilgilenememek: ''Savaş yüzünden herkes kendi derdine düşmüştü.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8235, 'kendi düşen ağlamaz', 'kendi zararına kendisi neden olanın yakınmaya hakkı olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8236, 'kendi göbeğini kendi kesmek', 'gereksinim duyduğu yardım, başkalarınca esirgendiğinde işini kendi görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8237, 'kendi gölgesinden korkmak', 'çok korkak olmak, bir sakınca söz konusu olmayan işlere girişmekten bile korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8238, 'kendi havasında gitmek (olmak)', 'yalnız başına, istediği gibi davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8239, 'kendi hesabına çalışmak', 'uğraştığı işi sadece kendisi için yapmak: ''Böyle bir amatörlük devresi geçirdikten sonra biraz da kendi hesabına çalışmayı düşündü.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8240, 'kendi içine çekilmek', 'başkasıyla ilişki kurmamak, yalnız başına kalmak, inzivaya çekilmek: ''Bizim gibi dış âlemle münasebetleri aksamış, kendi içine çekilip kendi yağıyla kavrulmak zorunda kalmıştı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8241, 'kendi kabuğuna çekilmek', 'kabuğuna çekilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8242, 'kendi kanatlarıyla uçmak', 'hiç kimsenin desteği veya yardımı olmaksızın yaşamak veya bir işi olumlu sonuca ulaştırmak: ''Kendi kanatlarınla uçmayı öğreninceye dek yanından ayrılır mıyım senin yavrum?'' -T. Oflazoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8243, 'kendi kendine gelin güveyi olmak', 'ilgilinin nasıl karşılayacağını düşünmeden bir işi olmuş bitmiş sayarak sevinmek: ''Kız kardeşi ile Mahir daha ortada fol yok yumurta yokken gelin güveyi olmuşlar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8244, 'kendi kendini didiklemek', 'kendi kendini harap etmek, üzmek: ''Öfkesinin şiddetinden hep kendi kendini didikledi.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8245, 'kendi kendini yemek', 'açığa vurmadan içten içe üzülmek: ''İçinde çarpışan bu iki zıt kuvvetten hangisine tabi olacağını bir türlü kestiremiyor, kendi kendini yiyip bitiriyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8246, 'kendi keyfine gitmek', 'isteğine uygun davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8247, 'kendi köşesinde yaşamak', 'yalnız başına yaşamak: ''Bu şiirlerin okuyucuya tanıttığı kişi, kitapları, üç beş sevdiği dostu ile kendi köşesinde yaşamayı seven bir kimse olarak görünür.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8248, 'kendi kuyusunu kendi kazmak', 'kendine zarar verecek davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8249, 'kendi söyler kendi dinler', 'ne söylediği anlaşılmaz, söylediği şeylere önem verilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8250, 'kendi üstüne yormak', 'alınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8251, 'kendi yağıyla kavrulmak', 'elinde bulunanla geçinip kimseye muhtaç olmamak: ''Fakat durup dururken, kendi yağıyla kavrulan bir genç kız namusuna bu kadar namussuzca iftira olur mu?'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8252, 'kendimi bildim bileli', 'öteden beri, eskiden beri: ''Kendimi bildim bileli hep bu bozuk makine seslerini duyarım.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8253, 'kendinde olmamak', 'bilinci, aklı yerinde olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8254, 'kendinde toplamak', 'kendi üzerinde bulundurmak, kendi varlığı içinde yer almasını sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8255, 'kendinden geçmek', '1) bilinci işlemez olmak, kendini kaybetmek, bayılmak: ''Gözlerini tezgâhın arkasındaki bir kapıya dikmiş ve kendinden geçmiş gibiydi.'' -S. F. Abasıyanık. 2) bir şey karşısında coşkuya kapılmak, duygulanmak: ''Oturduğu şiltenin üstünde ayağa kalkıyor; alevi artan bakışlarla kendinden geçmiş, bir elini dizine vurarak ... haykırıyordu.'' -A. Ş. Hisar. 3) uykuya dalmak, uyuyakalmak: ''Ninniyi söyleyen anne kendinden geçeli belki bir hayli olmuştu.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8256, 'kendine dert etmek', 'bir şeyi üzüntü konusu yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8257, 'kendine gel!', ' tkz. ''aklını başına topla'' anlamında kullanılan bir uyarma sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8258, 'kendine gelmek', '1) ayılmak: ''Üzerine soğuk su dökülen sarhoş adam kendine geldiğinde sade kahve ona zorla içirildi.'' -İ. O. Anar. 2) aklı başına gelmek: ''Sonunda kendine gelen İnce Memed hemen abasını soyundu.'' -Y. Kemal. 3) durumu düzelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8259, 'kendine (herhangi bir şeye) ... süsü vermek', 'gerçeğe aykırı olarak kendisinde veya herhangi bir şeyde üstün bir nitelik ve değer varmış gibi göstermek: ''Bu zannını bir çeşit materyalist felsefeye uydurarak ona yüksek bir entelektüalizm süsü verirdi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8260, 'kendine hisse çıkarmak', 'ders almak: ''Siz niçin bundan kendinize hisse çıkarmıyorsunuz?'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8261, 'kendine kıymak', 'kendini öldürmek: ''Eğer sefirler gelip bana istifa teklif ederlerse ben de aleyhimde bulunanları mahvederim, sonra da kendime kıyarım.'' -A. Rasim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8262, 'kendine mal etmek', '1) benimsemek veya saymak: ''Fakat hiçbir taraf beni kendine mal edemiyordu.'' -H. Taner. 2) başkasının yaptığı işi kendisi yapmış gibi göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8263, 'kendine yedirememek', '1) başkasının kendisine yaptığı işi, onur kırıcı sayarak tepki ile karşılamak; 2) kendisinin başkasına yapması söz konusu olan işi, kişiliği için onur kırıcı saydığından yapmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8264, 'kendine yontmak', 'çıkan her fırsattan yararlanarak hep kendi çıkarını sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8265, 'kendini ağır (ağırdan) satmak', '1) nazlanmak, gönülsüz davranmak: ''Kız kendisini ağır satmakta devam ediyor.'' -R. H. Karay. 2) huylarını yavaş yavaş ortaya koymak: ''Müdüre göre idareci biraz çatkın olacak yani oldukça ağırdan satacak kendini.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8266, 'kendini alamamak', 'istemeyerek bir işi yapma durumuna girmek: ''Yabancı memurların karşısında bir çocuk gibi yaramazlık etmekten kendimi alamıyordum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8267, 'kendini aşağı görmek', 'kendini başkalarından değersiz görmek: ''Onun perişan kalbine ölümden beter bir felç illetini, kendini aşağı görme ukdesini yerleştirmiştir.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8268, 'kendini ateşe atmak', 'bile bile tehlikeli bir işe girişmek: Kendinizi ateşe atıyorsunuz', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8269, 'kendini avutmak', 'oyalanmak: ''Para kazanamadığın için para kazananları hor görüp alaya alarak kendini avutuyor olmalısın.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8270, 'kendini beğendirmek', 'başkalarına hoş, iyi, yetenekli görünmek: ''Kendini kibar okuyucularına beğendirebilmek için çok çalışmak zorundadır.'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8271, 'kendini beğenmek', 'başkalarını küçümseyerek kendini üstün görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8272, 'kendini bırakmak', '1) kendine özen göstermemek: ''Artık kendini bırakmak zorunda görünür gibi olan amcasının huzurundan çıktılar.'' -N. F. Kısakürek. 2) çevre ile ilgisini keserek yalnız bir konuyla uğraşmak: ''O hayalleri kuran da o hatıralara kendini bırakan da bugünkü ben değil miyim?'' -N. Ataç. 3) gevşek, rahat bir biçimde kalmak: ''Pencere kenarında, uzun bir mindere kendini bıraktı, gözlerini kapadı, öylece kaldı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8273, 'kendini bilmek', '1) aklı ve muhakemesi yerinde olmak; 2) baliğ olmak; 3) ağırbaşlı olmak; 4) kendinin ve çevresinin bilincine varmak; 5) durum ve onuruna yakışacak biçimde davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8274, 'kendini bir şey sanmak', 'kendini olduğundan çok değerli görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8275, 'kendini bir yerde bulmak', 'farkında olmadan bir yere ulaşmış olmak: ''Hacı Arif Efendi bu kıyametin içinde yarım saat boşluktan sonra kendini bir bostanın içinde buldu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8276, 'kendini (birini) temize çıkarmak (çıkartmak)', ' huk. aklandırmak: ''Sonra kendini büsbütün temize çıkartmak için üstünün ve eşyasının aranmasını istedi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8277, 'kendini bulmak', '1) kişilik kazanmak; 2) maddi ve manevi konularda durumunu düzeltmek; 3) kendine gelmek: ''Kendini, çiğ ve yakıcı ışık çerçevesi içinde bulur bulmaz, ter boğmasına uğradı.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8278, 'kendini dağıtmak', '1) farklı işlerle aynı anda uğraşmaktan kötü durumda olmak: ''Kendini böyle sağa sola dağıttıkça tadına varılmaz bir mutluluk, esenlik duyuyordu'' -Halikarnas Balıkçısı. 2) ne yaptığını bilmeyecek kadar içip kendinden geçmek: ''Onlar benim dostlarım, kendimi onların yanında dağıtmış olmam çok vahim değil.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8279, 'kendini dev aynasında görmek', 'kendini olduğundan çok üstün görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8280, 'kendini dinlemek', '1) hastalık kuruntusu içinde bulunmak; 2) yalnız, sakin kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8281, 'kendini dirhem dirhem satmak', '1) çok nazlı davranmak, ağırdan almak: ''Hâl böyleyken yine de bilmeyenlere karşı kendini dirhem dirhem satar.'' -H. Taner. 2) özelliklerini azar azar ortaya koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8282, 'kendini düşünmek', 'daima kendi çıkarını kollamak, bencil davranmak: ''Ne diye herkes bu kadar rahatını sever, kendini düşünür?'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8283, 'kendini ele vermek', 'yaptığı bir davranış veya söylediği bir sözle kendi suçunu ortaya çıkarmak: ''Çünkü âdeta kendimi ele vermiştim.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8284, 'kendini fasulye gibi nimetten saymak', ' tkz. kendini çok önemli biri gibi görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8285, 'kendini göstermek', '1) beğenilecek niteliklerini ortaya koymak: ''Hadi susmayın, gösterin kendinizi bakalım!'' -N. Cumalı. 2) ortaya çıkmak, belirmek: ''Babam aylığını alamadığı günlerde aç kalmak korkusu da kendini gösteriyordu.'' -M. Ş. Esendal. 3) sp. pas alabilmek için boş alana kaçmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8286, 'kendini harap etmek', 'sıkıntı veya üzüntüden perişan olmak: ''Daha burada kendini harap edersen yukarılarda ne halt edeceksin?'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8287, 'kendini hissettirmek', 'varlığını belli etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8288, 'kendini kapının dışında bulmak', 'kovulmak, işten atılmak, bir yerden istenmeden uzaklaştırılmak: ''Bir gazeteci gelsin de bizden bir haber alsın. Haberi veren ertesi günü kendini kapının dışında bulurdu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8289, 'kendini (kapıp) koyuvermek', 'kendine özen göstermemek, kötümser olmak: ''Belki de benim başkasıyla evlenip gidişim üzerine hayattan soğudu, kendini koyuverdi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8290, 'kendini kaptırmak', '1) bir şeyin etkisinden kurtulamayacak duruma düşmek: ''Kendini genç yaşında rakıya kaptırdı, çok sürmedi, sonunda perişan oldu.'' -O. C. Kaygılı. 2) uğraşmaya başladığı bir işten kendini kurtaramamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8291, 'kendini kaybetmek', '1) bayılmak: ''Zavallı korkudan kendini kaybetmiş.'' -Y. K. Karaosmanoğlu. 2) aşırı duygulanma dolayısıyla çevrede olup bitenin farkına varamamak: ''Org inledikçe yavaş yavaş kendimi kaybediyor, ağır bir rüya içine gömülmeye başlıyordum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8292, 'kendini matah sanmak', 'kendini olduğundan daha fazla değerli kabul etmek: ''Bunu kendini matah sanmış bir Batılı aydın olmanın kefareti olarak yaptığını söylemiş.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8293, 'kendini naza çekmek', 'istekli olduğu hâlde yapmacıklı davranışlarla isteksiz gibi davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8294, 'kendini paralamak', 'çok çaba ve özen göstermek: ''Çoğunlukla üniversite diploması alabilmek uğruna kendini paralayan bir gençlikle karşı karşıyayız.'' -A. Cemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8295, 'kendini satmak', '1) kendisinde olmayan iyi nitelikleri varmış gibi göstermek; 2) para karşılığı erkeklerle birlikte olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8296, 'kendini sıkmak', 'kendini zorlamak, çaba göstermek: ''Ben kendimi sıkarak istidadımdan daha çok şen görünmeye çalışıyordum.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8297, 'kendini sıyıramamak', 'kurtulamamak: ''Düşünen filozof yeryüzü yaşamasına sımsıkı bağlı bu maddeci görüşten sıyıramıyor kendini.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8298, 'kendini sokağa (dışarı) atmak', 'sıkıntıdan dolayı rahatlamak amacıyla açık havaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8299, 'kendini tartmak', 'ne durumda olduğunu öğrenmek için kendini yoklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8300, 'kendini toparlamak (toplamak)', '1) herhangi bir konuda eskiden kötü olan durumunu düzeltmek: ''Bir zamanlar benim de onların arasında bulunduğumu söyleyecek gibi oluyor fakat hemen kendimi toparlıyordum.'' -Ö. Seyfettin. 2) bir konuda dikkatini yoğunlaştırmak: ''Tanıdığı hastanelerden birini tarif etmek için tekrar kendini toplamaya çalıştı.'' -P. Safa. 3) sağlığına kavuşmak: ''Geçen sene bir buçuk şişe içti, biraz kendini toparladı.'' -M. Ş. Esendal. 4) çeki düzen vermek: ''Seyfi, derhâl kendini topluyor ve hürmetle eğilerek uzaktan başıyla kadına bir selam veriyor, kadın mukabele ediyor.'' -E. M. Karakurt.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8301, 'kendini tutamamak', '1) bir durum karşısında sessiz ve heyecansız kalamamak: ''Böyle bir taksim, bir gazel dinleyenler arasında, coşarak, kendilerini tutamayarak ağlayanlar az mıydı?'' -A. Ş. Hisar. 2) kendine hâkim olamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8302, 'kendini tutmak', '1) kendine hâkim olmak: ''Benim zevcemi görseniz dünyanın en güzel kadını olduğunu tasdik edeceksiniz diye haykırmak ister, zorla kendimi tutardım.'' -Ö. Seyfettin. 2) dayanmak, sabretmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8303, 'kendini yemek', 'açığa vurmadan gizli gizli üzülmek: ''Bu borcun altından nasıl kalkacağım diye kendini yiyip durmuştu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8304, 'kendini yiyip bitirmek', 'kendi kendini yemek: ''Üzülmek ne kelime efendiciğim, kendimi yiyip bitiriyorum.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8305, 'kendini yoklamak', 'duygu, düşünce ve beden bakımından kontrol etmek: ''Terbiye öğretmenimden öğrendiğim usullerle kendimi uzun uzun yokluyorum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8306, 'kene gibi yapışmak', 'istenmediği hâlde birinin peşini bırakmamak, yakasını bırakmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8307, 'kenet etmek', 'kenetle birbirine bağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8308, 'kenet gibi yapışmak', 'çok yakın dost olmak, sıkı fıkı olmak: ''Bu mevsimde kızlar ikişer, üçer kişilik gruplara ayrılır ve birbirlerine kenet gibi yapışırlardı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8309, 'kepaze etmek', 'utanılacak bir duruma düşürmek: ''Onu kepaze etmek için bu rolü vermişlerdi.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8310, 'kepaze olmak', 'gülünç veya utanılacak duruma düşmek: ''Halka, gençliğe ve basına kepaze oldu.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8311, 'kepçe gibi', 'kanat gibi öne doğru açılmış (kulak).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8312, 'kepenek altında er yatar', 'insanları giydiğine bakarak değerlendirmek yanlışlara yol açar, değerli kişiler de bazen eski giymiş olabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8313, 'kepenk kapatmak', 'çalışamaz duruma gelmek: ''Belediye çavuşu yanında jandarma onbaşısı, çarşıyı dolaşmış, esnafa kepenk kapattırmıştır.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8314, 'kepenkleri indirmek', 'işi tatil etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8315, 'keramet buyurdunuz (keramette bulundunuz)', '''çok doğru söylediniz, çok güzel yaptınız'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8316, 'kerameti kendinden menkul', 'sahip olduğu nitelikleri kendisi söyleyen: ''Kerameti kendinden menkul şeyhler gibi bu armağanlar onların eksik olan kabiliyetlerinin bir çeşit icazeti oluyor.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8317, 'keramette bulunmak', 'doğaüstü olaylar ortaya koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8318, 'kerem buyurun (eyleyin)', '''izin verin, beni dinleyin'' anlamında kullanılan bir nezaket sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8319, 'kerpiç dökmek', 'saman ve balçık karışımını kalıplara boşaltarak kerpiç yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8320, 'kerpiç gibi', 'çok sert ve kuru.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8321, 'kerteriz almak (etmek)', 'bir yerin hangi yönde veya geminin nerede bulunduğunu pusula ile ölçmek: ''Uzakta, sancak tarafında, kerteriz ettiğimiz fenerin ışığı bir yanıp bir sönüyor.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8322, 'kertesine gelmek', 'tam yerini ve zamanını bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8323, 'kertesine getirmek', 'tam sırasını, en uygun zamanını seçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8324, 'kervana katılmak', 'bir topluluğa karışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8325, 'kes parmağını çık pazara, em (merhem, ilaç) buyuran çok olur', 'kişinin bir ihtiyaç içinde bulunduğunu gören herkes ona değişik yol gösterir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8326, 'keseden yemek', 'herhangi bir üretim yapmadan, kâr elde etmeden, hazırda bulunan veya el altında olan varlığı harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8327, 'kesel gelmek', 'gevşemek, tembelleşmek: ''İzmir faciasından beri padişaha ve hükûmete kesel gelmişti.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8328, 'keseneğe almak', 'gelirini satın almak, iltizam etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8329, 'keseneğe vermek', 'bir şeyin gelirini önceden götürü olarak satmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8330, 'kesenin ağzını açmak', 'bol para harcamaya başlamak: ''Balo ve kokteyl partisine bir davetiye alabilmek için keselerinin ağzını açmak kifayet etmezse avuçlarını açarlar.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8331, 'kesenin dibi görünmek', 'para tükenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8332, 'kesenize bereket', 'maddi katkısı görülen bir kimseye ''çok kazan, kazancın bol olsun'' anlamında kullanılan bir iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8333, 'kesesi elvermemek', 'bütçesi elverişli olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8334, 'kesesine bir şey girmemek', 'bir yarar veya çıkar sağlamamak: ''Bunda benim keseme bir girecek yok ki sana yalan söyleyeyim.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8335, 'kesesine göre', 'parasına, mali imkânlarına göre.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8336, 'kesesine güvenmek', 'parasına güvenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8337, 'kesesini doldurmak', 'fırsatlardan yararlanarak para kazanıp zengin olmak: ''Böylece Tecirlilerin yanına varan bir hoca, kesesini pek çok doldururmuş.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8338, 'keseye danış, pazarlığa sonra giriş', 'ödeyecek paranız yoksa bir şey satın almaya girişmeyin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8339, 'keseye davranmak', 'ödemek istemek: ''Sizi fena alıştırmışlar. Hemen keseye davranmayın.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8340, 'kesiklik vermek', '1) ara vermek: ''Dumanlar gözlerimi yakıyor, görüş gücüme kesiklik veriyor.'' -S. Birsel. 2) hâlsizlik, kırıklık, yorgunluk ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8341, 'kesilen baş yerine konmaz', 'kesin olarak yapılıp sonuçlandırılan iş, eski durumuna getirilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8342, 'kesintiye uğramak', 'bir süre için durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8343, 'kesip atmak', '1) uzun uzadıya düşünmeden kesin yargıya varmak: ''Zaman zaman iddiacılığını da bırakamazdı, bu böyledir diye kesip atardı.'' -H. Taner. 2) kesin olarak çözmek, bitirmek: ''Oysa ben karımı hatırlatacak her şeyden kurtulmak, yaşamımın ona ait bölümünü kesip atmak istiyordum.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8344, 'kesip biçmek', '1) parçalamak, doğramak, ameliyat etmek; 2) ağzına geleni söylemek, ileri geri konuşmak; 3) zorbalıkla korkutmak: ''Nasıl sol elimle sağ elimi kesip biçeceğim?'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8345, 'keskin sirke küpüne (kabına) zarar', 'öfkeli, sert kimsenin zararı kendisinedir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8346, 'keskin zekâ keramete kıç attırır', 'zeki kimse, bir işin nereye varacağını keramet sahibi kimseden daha iyi bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8347, 'kestane kabuğundan çıkmış da kabuğunu beğenmemiş', 'soyunu, yetiştiği yeri veya çevreyi hor görenler için kınama yollu söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8348, 'kestane suyu gibi', 'sulu (kahve).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8349, 'kestirip atmak', 'ayrıntılı düşünmeden kesin yargıya varmak: ''Tartışmayı kestirip atmak bana hiçbir zaman kolay görünmemiştir.'' -M. C. Anday.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8350, 'keşiş hayatı sürmek', 'her şeyden elini ayağını çekip yalnız başına yaşamak: ''Büyük din adamlarının keşiş hayatı sürmesi boşuna mı dersiniz?'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8351, 'ketenpereye gelmek', 'dolandırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8352, 'ketenpereye getirmek', 'dolandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8353, 'ketum olmak', 'sır saklamak, ağzı sıkı olmak: ''Sefirlerin az konuşması, ketum olması şarttır derler ya, laf!'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8354, 'Kevser gibi', 'tatlı, lezzetli (içecek).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8355, 'keyfi bilmek', 'isterse yapmak, nasıl isterse öyle yapmak: Gelmeyecekmiş, keyfi bilir!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8356, 'keyfi bozulmak', '1) hastalanmak; 2) canı sıkılmak, rahatı kaçmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8357, 'keyfi gelmek', 'neşelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8358, 'keyfi kaçmak', 'neşesi kalmamak: ''Bir yatılı okul yatakhanesinde olduğunu hatırlayınca keyfi kaçmıştı.'' -R. Ilgaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8359, 'keyfi oluncaya kadar', 'razı oluncaya kadar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8360, 'keyfi yerinde olmak', 'sağlığı, neşesi, mutluluğu bulunmak: ''Bugün keyfim yerinde olmadığından, arz odasına gelemeyeceğim.'' -T. Oflazoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8361, 'keyfinden bayılmak (dörtköşe olmak)', ' tkz. bir şeyden çok kıvanç duymak: ''Derhâl terennüme başlardım, adamcağız keyfinden bayılırdı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8362, 'keyfine bakmak', 'dilediğince yaşamak, güzel vakit geçirmek: ''Beyin seni davet etmiş, iç, ye, keyfine bak!'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8363, 'keyfine diyecek olmamak', 'mutlu ve huzurlu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8364, 'keyif benim, köy Mehmet Ağa''nın', 'hiçbir şeyi tasa etmiyorum, işlerim yolunda.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8365, 'keyif çatmak', 'keyfetmek: ''Türkü söylüyorsun, keyif çatıyorsun.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8366, 'keyif sormak', '1) birine ''iyi misiniz, nasılsınız'' sorularını yönelterek sağlığı hakkında bilgi almak; 2) saygı göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8367, 'keyif sürmek', 'sıkıntısız, rahat yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8368, 'keyif vermek', 'neşe vermek, sarhoş etmek: ''Bize hakaret eden, bize utangaçlık yükleyen bu zincir şarkıları, düşmanın kulağına keyif verecektir.'' -R. E. Ünaydın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8369, 'kıç atmak', '1) çifte atmak; 2) tkz. çok istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8370, 'kıçın kıçın gitmek', '1) geriye doğru gitmek, geri geri gitmek; 2) henüz yürümeyen bebek kıçüstü gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8371, 'kıçına bakarak (baka baka)', 'başvurduğu yerden olumlu sonuç alamayarak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8372, 'kıçına kına yakmak', 'karşısındaki kişinin uğradığı bir olumsuzluğa aşırı derecede sevinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8373, 'kıçına tekmeyi atmak (vurmak veya yapıştırmak)', ' kaba kovmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8374, 'kıçını yırtmak', ' kaba 1) bağırıp çağırmak; 2) bütün gücünü kullanarak uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8375, 'kıçüstü oturmak', '1) kıçı yere gelir duruma düşmek; 2) mec. herhangi bir konuda yenilmek, umduğuna ulaşamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8376, 'kıkır kıkır gülmek', 'içinden gelerek sesli sesli bir biçimde gülmek: ''Kapalı panjurların ardında, ayıp şeyler anlatıp kıkır kıkır gülüyorlar.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8377, 'kıl gibi', 'ipince, incecik.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8378, 'kıl kapmak', 'birisine sinirlenmek, hareketlerinden rahatsız olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8379, 'kıl olmak', 'birisi sinirine dokunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8380, 'kılağısını almak', 'kesici araçları bileği taşına veya kayışa sürterek keskinliğini artırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8381, 'kılavuzu karga olanın burnu boktan kalkmaz', 'kötü kimsenin arkasına düşen kişinin başı dertten kurtulmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8382, 'kılı kıpırdamamak', 'durum ve davranışını değiştirmemek, aldırış etmemek, umursamamak: ''Hikmet Bey yaman adam, dikkat ettim, hiç istifini bozmadı, kılı kıpırdamadı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8383, 'kılı kırk yarmak', 'titiz ve ayrıntılı bir biçimde incelemek, önemle üstünde durmak: ''Senin gibi kılı kırk yaran bir kıza name beğendirme başarısından dolayı sevgiliniz beyefendiyi kutlarım.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8384, 'kılıbıklık etmek', 'kılıbığa yakışan davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8385, 'kılıcı kınına koymak', 'savaşı bırakmak, savaştan vazgeçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8386, 'kılıç çalmak', 'kılıçla savaşmak, kılıç ile öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8387, 'kılıç çekmek', 'saldırmak veya selamlamak amacıyla kılıcı kınından çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8388, 'kılıç kınını kesmez', 'sert ve öfkeli kişi yanındakilere zarar vermez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8389, 'kılıç kuşanmak (takmak)', 'kılıcı olmak ve onu taşıyacak güce ve yetkiye hak kazanmak: ''Harbiyede beraber okumuşlar, beraber kılıç kuşanmışlardı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8390, 'kılıç oynatmak', 'egemen olarak yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8391, 'kılıç sallamak', 'kılıç ile dövüşmek, düşman üzerine kılıçla saldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8392, 'kılıç üşürmek', 'kılıç çekerek saldırmak: ''Kale kapılarında Allah adına birbirine kılıç üşürenler...'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8393, 'kılıçlama kaçmak', 'yan yan koşarak çaprazlamasına gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8394, 'kılıçtan geçirmek', 'çok sayıda insanı kılıçla topluca öldürmek: ''Bizim zavallı soydaşlarımıza kadar önünüze kim rast geldiyse kılıçtan geçirdiniz.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8395, 'kılıfına uydurmak', 'bir durum ve tutuma, yöntemine uygun biçim vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8396, 'kılığa bürünmek', 'oymuş gibi görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8397, 'kılık kıyafet köpeklere ziyafet', 'giyinişi ve görünüşü kötü ve tiksindirici olanlar için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8398, 'kılık kıyafeti düzmek', 'giysilerini yenilemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8399, 'kılık kıyafetle adam adam olmaz', 'kılık kıyafet, değeri olmayan kişiye değer kazandırmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8400, 'kılıktan kılığa girmek', '1) giysi değiştirmek; 2) sık sık düşünce değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8401, 'kılına halel gelmemek', 'hiçbir zarara uğramamak: ''Her şeyi kılıfına uydurduktan sonra kılına halel gelmez.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8402, 'kılını (bile) kıpırdatmamak (oynatmamak)', 'bir olay karşısında ilgisiz kalmak, en küçük bir tepki göstermemek: ''Yüzlerce Berlinli kendisini seyrediyormuş gibi kılını kıpırdatmadan resim yapardı.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8403, 'kımkım etmek', 'bir işi ağır ağır yapmak, oyalanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8404, 'kına gibi', 'çok ince (toz durumundaki şey): ''Kına gibi derler o taraflarda iyi işlenmiş topraklara.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8405, 'kına (kınalar) yakmak (koymak, sürmek, vurmak, yakınmak, yakılmak)', '1) kınayı su ile karıştırıp bulamaç kıvamına getirerek boyanacak yere sürmek: ''Bazıları bütün ele, avuçlara değil, yalnız bir tek parmağın baş kısmına kına koyarlardı ki buna yüksük kına tabir olunurdu.'' -R. H. Karay. 2) mec. birinin uğradığı kötü duruma çok sevinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8406, 'kıpkırmızı kesilmek (olmak)', 'yüz herhangi bir nedenle çok kızarmak: ''Kız utancından kıpkırmızı kesilmiş.'' -Ö. Seyfettin. ''Orhan''ın pembe esmer yüzü kıpkırmızı olmuştu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8407, 'kır atın yanında duran ya huyundan ya suyundan', 'kişi arkadaşlık ettiği kimseden etkilenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8408, 'kır boynunu!', '''defol! çekil! git!'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8409, 'kıraat etmek', '1) Kur''an''ı belli kural ve işaretlere göre okumak; 2) okumak: ''Olsa olsa mevzun cümlelerden mürekkep bir parçayı iyi kıraat etmiş olur.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8410, 'kırağı çalmak (vurmak)', 'kırağı, dondurup bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8411, 'kırağı düşmek (yağmak)', 'kırağı oluşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8412, 'kıran girmek', '1) kısa bir zaman içinde çok sayıda ölmek: Bu yıl sığırlara kıran girdi. 2) bir şey bulunmaz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8413, 'kıratını ölçmek', 'değerini biçmek, kıymetini belirlemek: ''Yüzlerini görür görmez, aşağıdaki misafirlerinin kıratlarını ölçmüştüm.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8414, 'kırdığı koz (ceviz) kırkı (bini) aşmak', 'sürekli yakışıksız davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8415, 'kırığı olmak', 'karnede zayıf notu bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8416, 'kırığı olmak', 'yasa ve törelere aykırı olarak karşı cinsten biriyle sürekli ilişki içinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8417, 'kırık plak gibi', 'durmaksızın, aynı tonda tekrarlayarak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8418, 'kırılıp bükülmek', 'kırıtarak, kibarlığa özenerek konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8419, 'kırılıp dökülmek', '1) kibar görünmeye çalışmak; 2) çok eskimek; 3) kırıklık duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8420, 'kırıp dökmek', 'dikkatsizlik veya öfkeyle birçok şeyin kırılmasına neden olmak: ''Kaşla göz arasında ellerine geçirdiklerini kırıp dökmeye koyulmuşlardı.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8421, 'kırıp geçirmek', '1) yakıp yıkarak, öldürerek, baskı veya etki yaparak büyük zarar vermek: ''Pakize''nin kırıp geçirdiği bir şeyi görmekten hasıl olacak tesiri temaşaya gelen çocuklara...'' -H. Z. Uşaklıgil. 2) çok sert davranarak darıltmak: ''Adamın her akşam yarım kiloyu devirdikten sonra ortalığı kırıp geçirmesinden perişan oluyorlar.'' -Ç. Altan. 3) tuhaf söz ve davranışlarla herkesi çok güldürmek: ''Hoşsohbet, şakacı bir insan olduğu için Kâzım Bey''le kaynatasını kahkahadan kırıp geçirir.'' -S. Birsel. 4) hayran etmek: ''Bir İspanyol şarkıcı var. Beyoğlu''nu kırıp geçiriyor.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8422, 'kırıp sarmak', 'bir şeyi yapmak için her türlü imkândan güçlükle yararlanmak: ''Düğüne kimlerin çağrıldığı anlaşılmaz, ne hediye gönderileceği de belli olmaz. Olmaz ama hepsi çağrılmıştır, hepsi de kırıp sarar, birer hediye alır yollar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8423, 'kırış kırış olmak', 'çok kırışmak: ''Alnı da bir enlemesine, bir dikine kırış kırış oluyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8424, 'kırk basmak', 'kırk gün dolmadan doğum yapmış annenin ve bebeğin dışarı çıkarılmasının tehlikeli olacağını geleneksel olarak kabul etmek: ''Yeni doğmuş iki çocuğu da kırk basar diye yan yana getirmezler.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8425, 'kırk bir (buçuk) kere maşallah!', '''pek çok, binlerce kez nazar değmesin!'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8426, 'kırk dereden su getirmek', 'bin dereden su getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8427, 'kırk evin kedisi', 'birçok eve girip çıkan (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8428, 'kırk gün günahkâr, bir gün tövbekâr', 'sürekli kötü işler yaptıktan sonra iyi bir iş yapan insan için kullanılan söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8429, 'kırk gün taban eti, bir gün av eti', 'avcılar bir av avlayabilmek için dağ demez, taş demez, günlerce taban teperler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8430, 'kırk hırsız bir çıplağı soyamamış', 'sömürenler, asalaklar ne kadar usta olurlarsa olsunlar, sömürülecek bir şeyi olmayandan yararlanamazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8431, 'kırk kapının ipini çekmek', 'içinde bulunduğu sorunu çözmek için kapı kapı dolaşmak, birçok yere uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8432, 'kırk tarakta bezi olmak', 'birçok işi veya ilişkisi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8433, 'kırk yıl kıran olmuş, eceli gelen ölmüş', 'salgın ve öldürücü hastalık da olsa eceli gelmeyen ölmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8434, 'kırkı çıkmak', 'doğumdan veya ölümden sonra kırk gün geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8435, 'kırkı (kırkları) karışmak', 'çocuklar için aynı kırk günlük süre içinde doğmuş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8436, 'kırkından sonra at olup da kuyruk mu sallayacak', 'vakti geçmiş, artık işe yaramayacak durumda.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8437, 'kırkından sonra azanı teneşir paklar', 'yaşlandıklarında ahlakları bozulanlar artık düzelemezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8438, 'kırkından sonra azmak', 'yaşlandıktan sonra yaşına uymayan davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8439, 'kırkından sonra saz çalmak', 'yaşlandıktan sonra uzun ve güç bir işe girişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8440, 'kırkından sonra saza başlayan kıyamette çalar', 'yaşlandıktan sonra bir şey öğrenmeye, yeni bir iş yapmaya başlayan kimsenin bunu başarmaya ömrü yetmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8441, 'kırklara karışmak', 'bir kimse artık ortalarda görünmez olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8442, 'kırkyıllık Yani, olur mu Kâni', 'eskimiş bir alışkanlık kolay kolay değişmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8443, 'kırlangıcın zararını biberciden sor', 'kırlangıç, bibere çok düşkün olduğundan onun ne kadar zararlı bir yaratık olduğunu ancak biberci bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8444, 'kırmızı dipli mumla davet etmek', 'birine bir yere gelmesi için çok yalvarmak, ısrar etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8445, 'kırmızı kart görmek', '1) oyundan çıkarılma cezasına çarptırılmak; 2) mec. ciddi bir biçimde uyarılmak; 3) mec. dışlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8446, 'kırmızı kart göstermek', ' sp. 1) oyundan çıkarma cezasına çarptırmak; 2) mec. ciddi bir biçimde uyarmak; 3) mec. dışlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8447, 'kısa günün kârı az olur', 'kısa süre çalışılarak yapılan işten elde edilecek kazanç az olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8448, 'kısa kesmek', 'sözü uzatmamak: ''Ahmet Kerim annesiyle kısa kesmek istediği konuşmalarını hep kapıdan çıkarken ayak üstünde yapardı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8449, 'kısa tutmak', '1) bir şeyi gerektiği kadar uzun yapmamak. 2) bir konuyu geniş ve ayrıntılı bir biçimde vermemek: Başkan açılış konuşmasını kısa tuttu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8450, 'kısıntı yapmak', '1) tutumlu davranmak; 2) azaltmak: ''Bu durum, ister istemez evi doldurup boşaltanlarda da kısıntı yapmamızı gerektiriyordu.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8451, 'kısıt altına almak', ' huk. kısıtlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8452, 'kıskacında olmak', 'bir konu üzerinde iki taraftan da sıkıştırılıp sıkıntılı duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8453, 'kıskıvrak yakalamak (bağlamak)', '1) kurtulamayacak veya çözülemeyecek biçimde tutmak, sımsıkı tutmak: ''Gecelerden bir gece, birkaç yeniçeri onu kıskıvrak yakaladı.'' -İ. O. Anar. 2) mec. tamamen etkisi altında kalmak, bir şeyle sürekli meşgul olmak: ''Amma yalnız bu olmadı, benim muhayyilemi kıskıvrak bağlayan şey, bir başka tecrübe daha ömrüm boyunca beni tesiri altında bıraktı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8454, 'kısmet beklemek', 'evlenmeyi, evleneceği kimseyi beklemek: ''Şimdi genç değil, şöyle kırkını, kırk beşini aşmış, efendiden, ağırbaşlı bir kısmet bekliyor.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8455, 'kısmet gökten zembille inmez', 'çalışmayanın kısmeti olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8456, 'kısmet ise gelir Hint''ten Yemen''den, kısmet değilse ne gelir elden', 'Tanrı bir şeyi size kısmet etmişse o mutlaka size gelir, kısmet etmemişse yapacak bir şey yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8457, 'kısmet (kısmeti) çıkmak', 'evlenme teklifi almak: ''Zavallı kızın kısmeti çıkmış, kendine sormadan, danışmadan hemen vermişler.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8458, 'kısmeti açılmak', '1) kazancı artmak, bolluğa ermek; 2) kendisiyle evlenmek isteyen biri çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8459, 'kısmeti ayağına (kadar) gelmek', 'beklenmeyen bir nedenle kazançlı bir durumla karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8460, 'kısmeti bağlanmak', 'istediği hâlde evlenememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8461, 'kısmeti kapanmak', '1) kazancı azalmak; 2) kendisiyle evlenmek isteyen biri çıkmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8462, 'kısmeti kesilmek', 'daha önceden kendisine nasip olan bir şey artık nasip olmamak: ''Çöp tenekeleri modernleşip metal sandıklara dönüşünce bu zavallıların çöp tenekelerinden de kısmeti kesildi.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8463, 'kısmetinde ne varsa kaşığında o çıkar', 'kişi ne kadar çabalarsa çabalasın alın yazısındaki şeye ulaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8464, 'kısmetine mâni olmak', 'kazancına veya evlenmesine engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8465, 'kısmetini ayağıyla tepmek', 'kavuşacağı iyi bir durumu, değerini bilmeyerek istememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8466, 'kısmetini bağlamak', 'bir inanışa göre büyü ile evlenmesine engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8467, 'kısmetsiz köpek, sabaha karşı uyuyakalır', 'Tanrı kendisine kısmet vermemiş olan yaratık, yararlanılacak şeyi elde etmek kolaylaştığı zaman, başka bir işle uğraştığı için bundan yoksun kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8468, 'kısmetten fazlası olmaz', 'kişi ne kadar çabalarsa çabalasın alın yazısındaki şeye ulaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8469, 'kıssadan hisse almak (çıkarmak)', 'anlatılan bir olaydan ders almak: ''O zaman, diplomatlar bu kıssadan lazım gelen hisseyi çıkarmasını bilmişler miydi? Ne gezer!'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8470, 'kıstas tutmak', 'ölçü olarak almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8471, 'kış basmak', 'kışın şiddetli soğukları başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8472, 'kış kışlığını, kuş kuşluğunu gösterir', 'her olay, her varlık özelliğini belli eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8473, 'kış kışlığını, puşt puştluğunu gösterir', 'her olay, her varlık özelliğini belli eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8474, 'kış yapmak', 'hava çok soğuk ve karlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8475, 'kışı geçirmek', 'kış mevsimini bir yerde geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8476, 'kıtır atmak', ' argo yalan söylemek: ''Kim dedi bunu sana, Kâmil Bey mi? Boş ver kardeş, inanma sakın, kıtır atıyorlar, moralimizi bozmak istiyorlar.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8477, 'kıtır kıtır kesmek', 'bıçak veya kesici bir aletle acımaksızın yaralamak veya öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8478, 'kıtıra almak', ' argo alay etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8479, 'kıtlıktan çıkmış', 'doymak bilmeyen.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8480, 'kıtlıktan çıkmış gibi yemek', 'doymak bilmezcesine yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8481, 'kıvamına (kıvama) gelmek', 'kıvamını bulmak: ''Kâhya, vakit gayri Süleyman, haber saldık gelecekler, pamuklar da kıvamına geldi, demişti.'' -S. Kocagöz. ''Dışarıda şimşekler çakıp gök gürülderken koyunlar kızarmaya başlamış, kazanlar dolusu hoşaf çoktan kıvama gelmişti.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8482, 'kıvamını bulmak', 'gerekli ve istenilen şartlar yerine gelmek, en uygun anında olmak: ''Yumurtayla zeytinyağı kıvamını bulunca bir kaşıkla onu soğumuş levreğin üstüne gezdireceksin.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8483, 'kıvanç duymak', '1) övünmek; 2) sevinmek, mutlu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8484, 'kıvrım kıvrım kıvranmak', '1) çok acı çekerek kıvranmak; 2) yalvarma, sıkıntı vb. bir sebeple çok kıvranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8485, 'kıyak geçmek (çekmek)', 'maddi ve manevi destek olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8486, 'kıyak kaçmak', ' argo çok uygun düşmek, yakışık almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8487, 'kıyamet gibi (kadar)', 'pek çok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8488, 'kıyamet kopmak', '1) kıyamet günü gelmek; 2) mec. bir yerde çok gürültü ve telaş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8489, 'kıyamet mi kopar?', '''ne olur, ne çıkar, ne önemi var'' anlamında kullanılan bir söz: ''Asker az olmakla kıyamet mi kopar?'' -N. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8490, 'kıyamete kadar', 'dünya durdukça, uzun süre: ''Senin minimini elinden yediğim tokadın acısını yüzümde kıyamete kadar duyacağım.'' -A. N. Asya.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8491, 'kıyamete kalmak', 'sorun, çözülememek: ''Seni bir daha görmek kıyamete mi kaldı?'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8492, 'kıyametler koparmak', '1) bir şeye çok kızarak bağırıp çağırmak, feryat etmek: ''Eşin var, aşiyanın var, baharın var ki beklerdin / Kıyametler koparmak neydi ey bülbül, nedir derdin?'' -M. A. Ersoy. 2) aşırı gürültülere, kargaşaya yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8493, 'kıyas kabul etmez', 'iki şey arasındaki ayrımın çok fazla olduğunu belirtmek için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8494, 'kıyıcılık etmek', 'gaddarlık etmek, gaddarca davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8495, 'kıyıda köşede kalmak', 'göze çarpmayan bir yerde unutulmuş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8496, 'kıyıya atmak', 'karaya çıkartmak veya sürüklemek: ''Sular, sandalı kıyıya atıyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8497, 'kıyıya çıkmak', 'karaya çıkmak, gemiden karaya inmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8498, 'kıymete binmek', 'çok değerli duruma gelmek: ''Düşündüm ki başka bir yerde çalışmaya başlarsam belki kıymete binerim.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8499, 'kız almak', 'bir ailenin kızını gelin olarak kendi ailelerine katmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8500, 'kız beşikte (kundakta), çeyiz sandıkta', 'kız daha beşikte veya kundakta iken çeyiz düzmeye başlamak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8501, 'kız gibi', '1) kıza benzeyen: Kız gibi oğlan. 2) utangaç; 3) argo çok güzel ve yeni: ''Şimdi de kucağında evirip çevirdiği İngiliz malı, kız gibi mavzerine bakıyor, gözlerini ondan ayırmıyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8502, 'kız istemek', 'bir kızı evlenmek için ana ve babasından veya yakınlarından istemek: ''Sen kızı kandıracaksın, sonra kaynananla gidip kızı isteyeceksin.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8503, 'kız kaçırmak', 'bir kızı kendinin veya ailesinin rızası olmadan alıp götürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8504, 'kız kucakta, çeyiz bucakta', 'kız daha beşikte veya kundakta iken çeyiz düzmeye başlamak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8505, 'kız vermek', 'bir ailenin kızını bir başka aileye gelin etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8506, 'kızağa çekmek (almak)', '1) gemiyi bakım, onarım için bir süre veya hiç kullanılmamak üzere kızak üzerine almak; 2) mec. bir görevliyi etkin bir görevden alıp çalışmayı gerektirmeyen pasif bir işe vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8507, 'kızak yapmak', 'taşıt fren görevini yerine getirdiği hâlde duramayıp kaymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8508, 'kızana gelmek', 'dişi kedi ve köpek erkek istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8509, 'kızarıp bozarmak', 'utanç, öfke vb. duyguların etkisiyle yüzü renkten renge girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8510, 'kızevi, naz evi', 'kız tarafı nazlı olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8511, 'kızı gönlüne bırakırsan ya davulcuya kaçar (varır) ya zurnacıya', 'evlenme çağındaki kızı büyükleri uyarmazlarsa uygun olmayan birisiyle evlenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8512, 'kızılca kıyamet kopmak', 'kavga, gürültü olmak: ''Bunlardan herhangi birisinin hizmetine girse kızılca kıyamet asıl o zaman kopar.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8513, 'kızım sana söylüyorum (dedim) gelinim sen anla (işit)', '1) doğrudan doğruya kendisine söylenemeyen düşünce ve uyarıların, o kimsenin çok yakınına söylendiğinde kullanılan bir söz; 2) herhangi birine dolaylı olarak söylenecek uyarı söz konusu olduğunda kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8514, '(kızın) boyu bacadan mı aştı?', '''daha evlenecek yaşta değil'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8515, 'kızını dövmeyen, dizini döver', 'çocuğunu gerektiği gibi eğitmeyen, ileride çok pişman olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8516, 'kızıp durmak', 'sürekli olarak kızmak ve söylenmek: ''Tatmin olmamış bir sanatçı öfkesiyle eski arkadaşlarına kızıp duruyordu.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8517, 'kibarlığı tutmak', 'bir olay karşısında genel davranışları dışında incelik göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8518, 'kibarlık taslamak', 'kibar olmadığı hâlde kibar gibi görünmeye çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8519, 'kibrine dokunmak', 'gururu zedelenmek: ''Ayan azası olduğu için, bekleme salonunda birkaç dakika kalmak bile kibrine dokunmuştu.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8520, 'kibrine yedirememek', 'gururuna dokunmak: ''Sütninenin üstüne düşmeyi kibrine yediremediği için merak etmiyormuş.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8521, 'kibrit çakmak', 'kibriti yakmak için bir yere sürtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8522, 'kilidi küreği olmamak', 'her şeyi açıkta bulunmak, kilitli yere saklanmamış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8523, 'kilimci ile kör hacı', 'herhangi birileri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8524, 'kilise direği gibi', ' şaka çok kalın (ense).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8525, 'kilit altına almak', 'kilitlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8526, 'kilit gibi olmak', 'birbirine çok bağlı ve dayanışmalı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8527, 'kilit kürek altına almak', 'bir şeyi kilitli yere koyarak saklamak: ''O gün her tarafı kilit kürek altına aldı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8528, 'kilit kürek olmak', 'bir yeri korumak, o yerin güvenilir, sağlam adamı olmak: ''... evime kilit kürek ol diye onun sırtını okşar.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8529, 'kilit vurmak', 'kilitlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8530, 'kilo almak', 'beslenerek vücudun ağırlığı artmak, şişmanlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8531, 'kilo vermek', 'vücudun ağırlığı azalmak, zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8532, 'kilometre yapmak', 'yol almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8533, 'kim bilir', '1) belirsizlik, bilinmezlik bildiren bir söz: ''Kim bilir nerde, nasıl, kaç yaşında / Bir namazlık saltanatın olacak / Taht misali o musalla taşında'' -C. S. Tarancı. 2) olabilirlik bildiren bir söz: Kim bilir ne kadar çok beğenildi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8534, '... kim ... kim', 'yakıştırılan şeyin uygunsuzluğunu belirtmeye yarar: ''Bambu ağacından takım kim, ben kim?'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8535, 'kim kime dum duma', 'kimsenin kimseyle ilgilenmediği, kimseye önem verilmediği, çok karışık bir durumu anlatan bir söz: ''Gece yarısı kim kime dum duma, köşk de eski yapı zaten, baca gibi alev bir anda dört yanı sarıvermiş.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8536, 'kim oluyor?', '''kendini ne sanıyor, ne hakkı var?'' anlamında kullanılan bir söz: ''Sen kim oluyorsun da beni kendi yerimden kovuyorsun?'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8537, 'kim vurduya gitmek', 'bir kalabalık arasında öldürülen veya vurulan kimsenin kimin tarafından öldürüldüğü veya vurulduğu anlaşılamamak: ''Herkes suçlu bu ortamda, herkes kim vurduya gidiyor, herkes korkuyor.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8538, 'kime ne', '''başkasını ilgilendirmez'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8539, 'kimi kimsesi olmamak', 'yakını, koruyucusu bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8540, 'kimi köprü bulamaz geçmeye, kimi su bulamaz içmeye', 'insanların nasipleri arasındaki tutarsızlıkları belirten bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8541, 'kimin arabasına binerse onun türküsünü çağırır', 'çıkar sağladığı kimsenin hoşuna gidecek biçimde davranan dönek ve dalkavuk kimseler için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8542, 'kimin ki bağı var, yüreğinde dağı var', 'malı, mülkü veya evladı olanlar kaygı ve tasadan uzak olamazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8543, 'kimin nesi?', '''kimin yakını'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8544, 'kimin tavuğuna kış demişiz', 'yaptıklarımızla veya söylediklerimizle kimi rahatsız etmişiz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8545, 'kimine hay hay, kimine vay vay', 'dünyada kiminin talihi iyi, kiminin de kötü gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8546, 'kiminin parası, kiminin duası', 'bir iş yapılırken veya yapıldıktan sonra kiminden para, kiminden dua alınabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8547, 'kiminle dans ettiğini biliyor musun?', 'bu konuda benim ne kadar üstün olduğumu biliyor musun?', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8548, 'kimse bilmez, kim kazana kim yiye', 'bir kimsenin çalışıp çabalayarak kazandığı malı kimi zaman hatır ve hayalde olmayan kişiler yer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8549, 'kimse kendi memleketinde peygamber olmaz', 'insanların kendi çevrelerinde değeri bilinmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8550, 'kimse kimsenin çukurunu doldurmaz', 'kimse kimsenin yerine ölemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8551, 'kimse yoğurdum ekşi demez', 'herkes sattığı malı, kendi işini, tutumunu ve davranışını över.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8552, 'kimseden kimseye hayır yok (gelmez)', 'insan, yapacağı işte başkasının yardımına güvenirse hayal kırıklığına uğrar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8553, 'kimsenin ahı kimsede kalmaz', 'zulüm görenin ahı, zulmedene hayretmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8554, 'kimya olmak', 'bulunmaz olmak: ''Sıla kimya olmuş burnuma tüter / Yol ver dağlar ben sılaya gideyim'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8555, 'kin bağlamak', 'birine karşı öç alma duygusu duymak: ''İstanbul''dan ayrılmana o sebep oldu diye gizli gizli ona kızacak, kin bağlayacaktım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8556, 'kin beslemek (tutmak)', 'birine karşı öç alma duygusunu sürdürmek: ''Biz kimseye kin tutmayız / Kamu âlem birdir bize'' -Yunus Emre.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8557, 'kin duymak', 'birine karşı öç alma duygusunu yaşatmak veya bu duyguyu hissetmek: ''Herkes ancak bir iki düşman için kin duyar.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8558, 'kin gütmek', 'öcünü alıncaya kadar kininden vazgeçmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8559, 'kinin gibi', 'çok acı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8560, 'kip gelmek', ' hlk. tıpatıp, uygun gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8561, 'kirada olmak', 'kira karşılığında verilmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8562, 'kirada oturmak', 'kira ile tutulmuş bir yerde yaşamak: ''Biz Kudüs''te kirada oturuyoruz.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8563, 'kiraya vermek', 'kira karşılığında vermek, icara vermek: ''Hiç olmazsa kızının okuma masraflarını çıkarmak endişesiyle yukarıdaki odalardan birini kiraya vermeyi düşünmüşlerdi.'' -K. Bilbaşar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8564, 'kireç söndürmek', 'kireci kullanmadan önce üzerine bolca su dökerek kalsiyum hidroksit durumuna getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8565, 'kiri kabarmak', 'nem, ısı vb. sebeplerle kir, üzerinde bulunduğu yüzeyden ayrılabilir duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8566, 'kirişi kırmak', ' argo bulunduğu yerden ayrılmak, kaçıp gitmek: ''Ama şimdi derhâl giyineceksiniz ve kirişi kıracaksınız.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8567, 'kirliye atmak', 'yıkanmak için bir kenara koymak, bir yerde biriktirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8568, 'kirpiği kirpiğine değmemek', 'hiç uyuyamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8569, '... kisvesi altında', '''herhangi bir nitelikte veya biçimde'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8570, 'kisveye bürünmek', '1) herhangi bir kılığa girmek; 2) herhangi bir niteliğe, biçime girerek gerçek kimliğinden farklı bir görünüş almak: ''Bu sebeplerle bazıları eserin bu kisveye bürünmüş olarak yazıldığını kabul etmek istememektedirler.'' -A. H. Çelebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8571, 'kişi arkadaşından bellidir', 'bir kimsenin nasıl bir kişi olduğunu öğrenmek isteyen, arkadaşının kimliğine bakmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8572, 'kişi ektiğini biçer', 'nasıl davranırsan öyle karşılık görürsün.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8573, 'kişi ne yaparsa kendine yapar', 'iyilik yapan iyilik, kötülük yapan kötülük bulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8574, 'kişi refikinden azar', 'kötü arkadaş insanı kötü yola sürükler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8575, 'kişilik kazanmak', 'bir kişinin öz yapısı, kişiliği belirginleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8576, 'kişinin kendine ettiğini kimse edemez', 'bir kimse kimi zaman tedbirsizliği yüzünden öyle yanlış iş yapar, başını öyle derde sokar ki böyle bir kötülüğü başkaları ona yapamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8577, 'kişiyi nasıl bilirsin, kendin gibi', 'kişi herkesin kendisi gibi olduğunu sanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8578, 'kişiyi vezir eden de karısı, rezil eden de', 'bir kimsenin toplum içinde yücelmesi de alçalması da karısına bağlıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8579, 'kitaba el basmak', 'kutsal kitap üzerine elini koyarak ant içmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8580, 'kitabı kapamak', 'herhangi bir konu ile ilgiyi kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8581, 'kitabında yer almamak', 'aklına ve mantığına aykırı düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8582, 'kitap (kitaplar) devirmek (devretmek)', 'bir veya birden çok kitabı başından sonuna kadar okuyup bitirmek: ''... zengin bir tasvir ve izah yapabilmek için evde kitaplar devirdi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8583, 'kitapta yeri olmak', 'din veya yasa kitaplarında bulunmak, konusu geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8584, 'koca bulmak', 'kız veya kadın kendisi ile evlenecek bir erkek bulmak: ''Üstelik kadının adı da çıktı, bir daha koca bulamadı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8585, 'kocakarılığı tutmak', 'geçimsiz, inatçı, şirret yaşlı bir kadın gibi davranmak: ''Fakat kocakarılığı tutup kavgaya başlayınca Allah saklasın!'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8586, 'kocana göre bağla başını, harcına göre pişir aşını', 'davranışlarını içinde bulunduğun koşullara uydur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8587, 'kocaya gitmek', 'evlenmek: ''Harfleri okuyup yazamadan on üçümde kocaya gidecektim.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8588, 'kocaya kaçmak', 'kız ailesinin izni olmadan ve nikâhlanmadan bir erkekle kaçmak: ''Büyük kızı kocaya kaçtığı zaman küçükleri on iki dönüm tarlanın hakkından gelecek kadar yetişkindiler.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8589, 'kocaya varmak', 'kız, kadın evlenmek: ''On üç yaşındayken altmış altı yaşında bir kocaya vardığı için izdivaç denen şeyden nefret etmişti.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8590, 'kocaya vermek', 'kız veya kadını evlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8591, 'koç yiğit bunalıp ölmez', 'insanın sıkıntılı zamanı sürüp gitmez, arkasından iyi günler de gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8592, 'koça boynuzu yük değil', 'kişiye kendi işi ve yakınlarının sorumluluğu ağır gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8593, 'koçan bağlamak', 'mısırda koçan oluşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8594, 'kodese tıkmak', 'cezaevine sokmak: ''Belki kodese tıkarlar, hazır olsun.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8595, 'kodesi boylamak', 'cezaevine girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8596, 'kof çıkmak', 'bir kimsenin bilgisiz, değersiz, işe yaramaz biri olduğu anlaşılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8597, 'kokusu çıkmak', 'gizli tutulan bir iş anlaşılmak: ''Bir yerden kokusu çıkarsa baban vasıtasıyla önlemek isteyecekler.'' -S. Ali.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8598, 'kokusunu (koku) almak (duymak)', '1) bir nesnenin kokusunu algılamak: ''Yaz yağmuru yağdığı vakit burada toprağın güzel kokusunu duymak mümkündür.'' -M. Ş. Esendal. 2) mec. gizli tutulan bir şeyi sezmek: ''Yılların gazetecisisin oğlum, iyi haberin kokusunu kilometrelerce uzaktan alırsın.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8599, 'kol atmak', '1) bitkinin gövdesinden ayrılan bir dal bir yöne uzanmak; 2) mec. çevreye yayılmak, genişlemek, ulaşmak, uzanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8600, 'kol gezmek', '1) güvenlik amacıyla dolaşmak: ''Bunlar şehir subaşısının adamları, dizdarlardı. Kol geziyorlardı.'' -Ö. Seyfettin. 2) dolaşmak: ''İnsanı üşütmeyen, ılık gezginci bir yağmur bulutu ağır ağır kol geziyordu.'' -T. Dursun K. 3) mec. kötü durum ve davranışlar çokça olmak: ''Bazı ülkelerde sansürün kol gezdiği görülüyor.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8601, 'kol uzatmak', 'yayılmak, ulaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8602, 'kol vermek', 'destek olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8603, 'kol vurmak', 'dolaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8604, 'kola çıkmak', 'hırsız, polis vb. faaliyete geçmek, işe başlamak: ''Polis düdükleriyle yeniden fırladım. Meğer hırsızlar kola çıkmış.'' -R. Akyavaş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8605, 'kolaçan etmek', '1) çevrede olup biteni anlamak amacıyla dolaşmak: ''Çevremizi bir kolaçan edelim hele nerde olduğumuzu iyice anlayalım.'' -T. Oflazoğlu. 2) bir şeye öğrenmek amacıyla kısaca bakmak, göz atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8606, 'kolayı var', 'çaresi var.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8607, 'kolayını bulmak', 'kolay bir biçimde yapma yolunu bulmak: ''Lakin erler onu da yakalamanın kolayını bulmuşlardı.'' -A. N. Asya.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8608, 'kolları kopmak', 'ağır bir şey taşımaktan veya çok iş yapmaktan yorulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8609, 'kolları sıvamak', 'bir iş yapmaya güçlü bir biçimde, istekle hazırlanmak: ''Selami de kolları paçaları sıvayıp Ali Naci''nin yardımına koşmuştu.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8610, 'kollarını sallaya sallaya gelmek', 'hiçbir şey getirmeden gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8611, 'kollarının arasına almak', 'kucaklamak: ''Beni kollarının arasına alıyor, saçlarımı okşuyor.'' -H. Z. Uşaklıgil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8612, 'kolpoya düşmek (gelmek)', 'oyuna gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8613, 'koltuğa girmek', 'evlenmek: ''Nihayet sonbaharın yağmurlu, serin bir günü koltuğa giriyorum.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8614, 'koltuğu doldurmak', 'aldığı görevi tam olarak başarabilecek yetenekte bulunmak: ''Ercüment, memurluk hayatında her oturduğu koltuğu doldurmuş...'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8615, 'koltuğuna girmek', 'koltuğunun altına sığınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8616, 'koltuğunun altına sığınmak', 'birinin koruyuculuğuna sığınmak: ''Ben de aç duracak değilim ya! Bizim orada senin gibi bir ağa yok ki koltuğunun altına sığınalım.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8617, 'koltuk çıkmak', 'desteklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8618, 'koltuk değneği olmak', 'birine, yaptığı uygunsuz işlerde destek sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8619, 'koltukları kabarmak', 'kendine veya yakınlarına yapılan övgüden kıvanç duymak: ''Memnun oldu, koltukları kabardı, sevinçle gözlerimin içine baktı.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8620, 'koltukta olmak', ' şaka başkasının konuğu olup kendi masraf etmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8621, 'kolu kanadı kırılmak', 'bir şey yapamayacak duruma gelmek, çaresiz kalmak: ''Hem de kolu kanadı tamamıyla kırılmış, bir daha hemcinslerimize dil uzatamayacak bir hâlde...'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8622, 'koluna kuvvet', 'iş yapan bir kimseye, isteklendirmek, coşturmak için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8623, 'komadan çıkmak', 'komaya giren hasta bu durumdan kurtulmak, ölümden dönmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8624, 'komalık olmak', 'yediği dayaktan sonra kıpırdayamayacak duruma gelmek: ''Eski kocam, komalık oluncaya kadar dövüldükten sonra boş bir arsaya atılmıştı.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8625, 'komaya girmek', '1) duyma, anlama ve hareket yeteneklerini yitirerek yarı ölü duruma gelmek, kendinden geçmek; 2) mec. kendinden geçecek kadar sinirlenmek, şaşırmak, üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8626, 'komiğine gitmek', 'gülünç bulmak: ''Tıraş olan erkekleri seyretmeyi severim, komiğime gider.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8627, 'komik bulmak', 'gülünç saymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8628, 'komplekse kapılmak', 'aşağılık duygusu hissetmek: ''Haklarında yazılan yüceltici eleştirileri de tam anladığımı söyleyemem. O zaman biraz komplekse kapılıyorum.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8629, 'komplo kurbanı olmak', 'komploya kurban gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8630, 'komploya kurban gitmek', 'komplo yoluyla zarar görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8631, 'komşu boncuğunu çalan gece takınır', 'hırsızlık malı, sahibinin göremeyeceği yer ve zamanda kullanılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8632, 'komşu ekmeği komşuya borçtur', 'komşunuz size bir ikramda bulunur, bir şey armağan ederse siz de ona ikramda bulunmalı, armağan vermelisiniz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8633, 'komşu hakkı, Tanrı hakkı gibidir', 'komşunun komşu üzerindeki hakkı, Tanrının kul üzerindeki hakkı kadar kutsaldır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8634, 'komşu iti komşuya ürümez', 'komşudaki uygunsuz kişi, başkalarını incitse de komşusunu rahatsız etmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8635, 'komşu kızı almak, kalaylı kaptan (tastan) su içmek gibidir', 'komşu kızını almaya karar veren, ailenin ve kızın durumunu, gidişini iyi bildiğinden içi rahat olarak bu ilişkiyi kurar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8636, 'komşu komşunun külüne (tütününe) muhtaçtır', 'komşular en küçük şey için bile birbirlerine muhtaçtırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8637, 'komşuda pişer, bize de düşer', 'insanların, çevresindekilerin kazancından yararlanma umudunu anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8638, 'komşunu iki inekli iste ki kendin bir inekli olasın', 'başkasının daha iyi durumda olmasını iste ki Tanrı da seni ondursun.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8639, 'komşunun tavuğu, komşuya kaz görünür (karısı kız görünür)', 'başka bir kimsenin malı bize olduğundan daha değerli görünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8640, 'konak gibi', 'büyük ve gösterişli (ev).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8641, 'konferans çekmek', 'karşısındakini bıktıracak bir biçimde uzun veya öğüt verircesine konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8642, 'konferans vermek', 'herhangi bir konuda bilgi verecek biçimde konuşma yapmak: ''Türk Ocağında bir de konferans vermiş olduğunu hatırlatırım.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8643, 'konsantre olmak', '1) düşünceyi, duyguyu, gücü bir noktada toplamak; 2) mec. bilenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8644, 'konser vermek', 'dinleyicilere, müzik eserlerini çalmak veya söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8645, 'kont gibi', 'şık giyinmiş (adam).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8646, 'kont gibi yaşamak', 'bolluk içinde yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8647, 'kontak atmak', '1) elektrik donanımında karşı uçların birbirine dokunmasıyla elektrik akımı kesilmek; 2) mec. dengeyi kaybetmek, sinirlenip olağan dışı davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8648, 'kontak kapatmak (kapamak)', '1) bir taşıtın çalışan motorunu durdurmak için kontak anahtarını çevirerek elektrik devresini kapamak; 2) mec. bir olayı protesto etmek için sürücüler trafiğe çıkmamak, taşıtlarıyla trafiği engellemek veya bir süre bulunduğu yerde kalıp motoru durdurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8649, 'kontak kurmak', 'biriyle veya bir olayla bağlantı sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8650, 'kontra gitmek', 'birine zıt gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8651, 'kontrol altına almak', 'bir olayı denetim altına almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8652, 'kontrol altında olmak', 'denetlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8653, 'kontrol altında tutmak', 'denetlemek: ''Amcam öfkesini kontrol altında tutmaya çalışarak derin bir soluk alıyor.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8654, 'kontrpiyede kalmak', '1) sp. futbolda kaleci ters tarafa gitmek veya hamle yapmak; 2) mec. beklediği sonuca ulaşamamak; 3) mec. düşüncelerini açıklayamamaktan ötürü zor durumda kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8655, 'konuk etmek', 'birini evinde bir süre ağırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8656, 'konuk gelmek', 'bir yere veya birinin evine kısa bir süre kalmak için gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8657, 'konuk olmak', 'bir yerde kısa bir süre ağırlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8658, 'konuşmaya dalmak', 'başka şeylerle ilişkiyi keserek belli bir konudan söz etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8659, 'koparıp atmak', '1) koparmak; 2) mec. ilgisini kesmek, önem vermemek: ''Sana karşı içimde iki katlı bir ana yüreği var. İşte onu koparıp atamıyorum.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8660, 'kopup gelmek', 'uzak bir yerden ayrılarak gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8661, 'kopya çekmek', 'genellikle yazılı sınavlarda soruları cevaplamak için bir kaynağa gizlice bakmak: ''En bildiği derste bile kopya çeker, çekmezse hasta olur, deliye döner.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8662, 'kopya vermek', 'sınavda sorulara cevap vermesi için bir kimseye gizlice yardımda bulunmak: ''Lisede de başkalarına kopya verirken yakalanır, ikmale kalırdın.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8663, 'kor dökmek', 'yanınca dayanıklı kor durumuna girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8664, 'kor gibi', 'kıpkırmızı, ateş gibi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8665, 'kor gibi yanmak', '1) çok parlamak: ''Gözleri kor gibi yanan ve bir ölüden daha sarı olan diğer bir yaralı yatıyordu.'' -Y. K. Karaosmanoğlu. 2) büyük üzüntü çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8666, 'kordon altına almak', 'bir yere giriş çıkışı önlemek için o yeri görevlilerce korumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8667, 'kork Allah''tan korkmayandan', 'Tanrıdan korkmayan kimse, insana her türlü kötülüğü yapabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8668, 'kork aprilin beşinden, öküzü ayırır eşinden', 'nisan ayının beşinde çift süren iki öküzü birbirinden ayıracak kadar hava soğuk olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8669, 'korkak bezirgân ne kâr eder ne zarar (ziyan)', 'iş yapmaya korkan tüccar, kendisini zarardan korur ancak kazanç da sağlayamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8670, 'korktuğu başına gelmek', 'düşünülen kötü durum gerçekleşmek: ''Korktuğu başına gelmiş ve o koskoca Nahit Bey ipin ucunu kaçırarak dillere destan olmuştu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8671, 'korktuğuna uğramak', 'korktuğu başına gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8672, 'korku dağları bekler (aşırır)', 'korku her yerde varlığını gösterir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8673, 'korku düşmek', 'endişelenmek, korkmak: ''Bir korku düştü canıma acep nola benim hâlim / Derman olmaz ise bana acep nola benim hâlim?'' -Yunus Emre.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8674, 'korku saçmak', 'herkesi korkutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8675, 'korkudan çıldırmak', 'aşırı korku yüzünden aklını yitirmek, delirmek: ''Yoksa çocuk, etrafını saran hayaletlerin dehşeti karşısında mutlaka korkudan çıldırırdı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8676, 'korkulu rüya (düş) görmektense uyanık yatmak evladır (yeğdir)', 'tehlikeli bir işe girişmektense o işin sağlayacağı kazançtan vazgeçmek daha iyidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8677, 'korkunun ecele faydası yoktur', 'kişi korkmakla kendisine gelecek bir kötülüğü önleyemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8678, 'korkusundan altına etmek (kaçırmak, yapmak)', 'çok korktuğunda idrarını veya dışkısını kaçırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8679, 'korkuya kapılmak', 'korku düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8680, 'korkuya kesmek', 'korkmak: ''Ürkek ürkek dolaşıyordu evin içinde. Tepeden tırnağa korkuya kesmişti.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8681, 'korumaya almak', 'tehlikede olduğu düşünülen bir kimseyi veya eseri saldırılardan korumak üzere önlem almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8682, 'korunma görmek', 'anlayış veya hoşgörü ile karşılanmak: ''Hiçbir düşünce ve mülahazanın ... Atatürk milliyetçiliği, ilke ve inkılapları ve medeniyetçiliğinin karşısında korunma göremeyeceği...'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8683, 'koşu koparmak', 'hızla koşuvermek, çabucak atılıp gitmek: ''Sonra elinde boş tasla çeşmeye doğru bir koşu koparıyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8684, 'koşun bağlamak', 'koşun durumuna girmek, saf tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8685, 'kova olmak', 'çok gol yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8686, 'koy avucuma, koyayım avucuna', 'bize yardımda bulunan, yarar sağlayan kişiye biz de yardımda bulunur, yarar sağlarız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8687, 'koyduğum yerde otluyor', ' tkz. uzun süredir hiçbir ilerleme göstermeyenler için söylenen bir söz: ''Sen de koyduğum yerde otluyorsun, bir şey bilmiyorsun.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8688, 'koydunsa bul', 'arandığı hâlde bulunamayan şeyler veya bulunması gereken yerde bulunmayan kimseler için kullanılan bir söz: ''Gündüz olsun gece olsun, iki dakikalık bir fırsat buldu mu Allah''a ısmarladık, sütnineyi koydunsa bul!'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8689, 'koyma akıl, akıl olmaz', 'hep başkalarının verdiği akılla hareket eden kimse, bir yere kadar başarılı olur, daha sonra ne yapacağını bilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8690, 'koynunda yılan beslemek', 'bir yakınından ihanet görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8691, 'koyu koyu düşünmek', 'uzun uzun veya derin derin düşünmek: ''Nereye gideceğimi, ne yapacağımı koyu koyu düşünmeye başladığım güne kadar silah elimden düşmemiştir.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8692, 'koyun can derdinde, kasap yağ derdinde', 'bir kişi önemli bir kaybından dolayı çırpınıp kıvranırken başka bir kişi bu durumdan ne kadar çok yararlanabileceğini düşünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8693, 'koyun gibi', '1) budala, şaşkın; 2) karar ve davranışlarında başkasına bağımlı olan, başkasına uyan: ''Bizim damat da sessiz sedasız, koyun gibi adam.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8694, 'koyun güden kurdu görür', 'görevini yaparken gereken dikkati gösteren kişi, doğabilecek sorunları sezer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8695, 'koyun kaval dinler gibi dinlemek', 'hiçbir şey anlamadan dinlemek: ''Kafa göz yara yara Hüsn ü Aşk''ı okuyor, hayranları da koyun kaval dinler gibi dinliyorlardı.'' -A. H. Çelebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8696, 'koyunu yüze yetir, el onu bine yetirir', 'kimi insan, başkaları hakkında abartarak konuşmaya bayılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8697, 'koyunun bulunmadığı yerde keçiye Abdurrahman Çelebi derler', 'istenilen nitelikteki şey bulunamadığında onun daha düşük nitelikte olanına da razı olunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8698, 'koz gölgesi kız gölgesi, söğüt gölgesi yiğit gölgesi, dut gölgesi it gölgesi', 'ağaçların gölgeleri bile doğal özelliklerini yansıtır, koz, yani ceviz ağacının altında insan, herkesin peşinde koştuğu bir kızın yanındaymışçasına mutluluk duyar; söğüdün gölgesi, boylu boslu bir yiğidin güvenini kazanır; dut ise altına meyvelerini döktüğü için gölgesi çıkarcıların üşüştüğü bir yerdir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8699, 'koz kırmak', '1) oyunda elindeki kozlardan birini kullanmak; 2) mec. yanlış tutum içinde bulunmak: ''Zavallının iratlarında oturan kiracılarla uğraşarak kırmadığı koz, çevirmediği dolap kalmıyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8700, 'koz vermek', 'imkân tanımak, elverişli durum sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8701, 'koza çekmek', 'kozayı temizleyip ayıklamak: ''Pamuk ırgatları alaçıkların önüne oturmuşlar, koza çekiyorlardı.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8702, 'kozasına çekilmek', 'çevreyle ilişkisini kesmek, hiçbir şeye karışmamak: ''Hiçbir tarakta bezim kalmadı, ipek böceği gibi kozama çekilmiş, kendi hâlimde, politikaya bulaşmadan yaşıyorum.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8703, 'kök salmak', '1) iyice tutunmak, sağlamlaşmak, yayılmak, köklenmek: ''Benliğe kök salan gönül bağlarını kim tarif edebilir?'' -H. E. Adıvar. 2) bir yere iyice yerleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8704, 'kök sökmek', 'çok çetin iş görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8705, 'kök söktürmek', 'uğraştırmak, güçlük çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8706, 'kökü kazınmak', 'bir daha ortaya çıkamayacak biçimde yok edilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8707, 'kökü kurumak', 'yok olmak, ortadan kalkmak: ''Kelebeklerin kökünün kuruduğu bir dünyada çocuk istemem.'' -T. Yücel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8708, 'kökünden halletmek', 'herhangi bir konuyu veya sorunu temelden çözümlemek: ''Bu işi kökünden halletmek için kızını derhâl evlendirmeye karar vermişti.'' -A. H. Tanpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8709, 'kökünden kazımak', 'kökünü kazımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8710, 'köküne kibrit suyu', 'yerin dibine batsın, ölsün, kahrolsun.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8711, 'köküne kibrit suyu dökmek (kökünü kurutmak)', 'bir daha ortaya çıkamayacak biçimde yok etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8712, 'kökünü kazımak', 'bir daha üreyemez duruma getirmek, hiçbir kalıntısını bırakmamak, yok etmek: ''Bizimkilerin de amacı aynı / Doğan güneşle birlikte kökünüzü kazıyıp / Yeryüzünde bırakmamak izinizi'' -T. Oflazoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8713, 'kömür başa vurmak', 'kömürün iyi yanmamasından çıkan karbon oksidiyle zehirlenmekten baş ağrımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8714, 'kömür gibi', 'kapkara.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8715, 'kömürcü çırağına dönmek', 'yüzü, üstü başı siyah lekeler içinde kalmak, eli yüzü kapkara olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8716, 'köpeğe atsan yemez', '''çok kötü (yiyecek)'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8717, 'köpeğe dalaşmaktan çalıyı dolanmak yeğdir', 'edepsiz kimse ile uğraşmamak için onun bulunduğu yerden uzaklaşmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8718, 'köpeğe gem vurma, kendini at sanır', 'kendisine değeri varmış gibi davranılan değersiz kişi, gerçekten değeri bulunduğuna inanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8719, 'köpeğe hoşt, kediye pişt dememek', 'kendisine zarar verenlerden korunmak için en küçük bir tepkide bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8720, 'köpeği dövmeli ama sahibinden utanmalı', 'sana sataşan kişiyi hırpalarken onu korumakta olan saygı gösterdiğin kimseyi gücendirmemeye de dikkat etmelisin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8721, 'köpeğin ağzına kemik atmak', ' hkr. karşı gelerek bağırıp çağıran birini susturmak için ona bir çıkar sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8722, 'köpeğin ahmağı baklavadan pay umar', 'aptal kişi, eline geçme olasılığı bulunmayan bir nimeti bekler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8723, 'köpek bile yal yediği kaba pislemez', 'köpek bile yem yediği kaba saygılı davranırken insanın geçimini sağlayan yere, kendisine bu geçimi hazırlayan kimseye kötülük etmesi düşünülemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8724, 'köpek ekmek veren (yediği) kapıyı tanır', 'köpek bile kendisini besleyen yeri bilir, davranışlarıyla duygularını belli eder, insan da bundan ders almalı, gördüğü iyiliği unutmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8725, 'köpek gibi', 'çok yaltaklanan: ''Kız ona derdini yanarken, paşanın Tevfik''i buldurması için köpek gibi yalvarırken, o gözlerini tavana dikiyor, cevap vermiyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8726, 'köpek sahibini ısırmaz', 'kişi ne kadar aşağılık olursa olsun, kendini benimseyip koruyana kötülük etmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8727, 'köpek suya düşmeyince yüzmeyi öğrenmez', 'kişi, bir tehlike karşısında her yerden umudu kesilip kendine güvenmekten başka çare kalmadığını anlamadıkça kurtuluş yolunu bulamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8728, 'köpek sürünmekle etek kesilmez', 'terbiyesiz kimsenin sataşmasıyla temiz kişi lekelenmiş olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8729, 'köpek yese kudurur', 'çok ağır ve onur kırıcı sözler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8730, 'köpekle dalaşmaktan çalıyı dolaşmak yeğdir', 'edepsiz kimse ile uğraşmamak için onun bulunduğu yerden uzaklaşmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8731, 'köpekle yatan pire ile kalkar', 'uygunsuz kişilerle ilişkide bulunanın sonu kötü olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8732, 'köpeksiz köy bulmuş da çomaksız (değneksiz) geziyor', 'kendisine engel olacak, karşı çıkacak kimse olmadığı için istediği gibi davranıyor.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8733, 'köpeksiz sürüye (köye) kurt girer (iner)', 'koruyucusuz kalan yere veya ülkeye düşman girer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8734, 'köprübaşını tutmak', 'çok önemli bir mevkiyi ele geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8735, 'köprüden (köprüyü) geçinceye kadar ayıya dayı derler', 'kişi işini gördürünceye kadar yardım beklediği kimseyle iyi geçinir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8736, 'köprüleri atmak', 'bir işten vazgeçme veya geri dönme imkânı kalmayacak biçimde kesin bir davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8737, 'köprünün (köprülerin) altından çok su (sular) aktı (geçti)', 'zamanla şartlar çok değişti, eski durum kalmadı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8738, 'köpük gibi', 'beyaz, hafif ve köpük görünümlü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8739, 'kör Allah''a nasıl bakarsa Allah da köre öyle bakar', 'kişi efendisine karşı bir nankörlük ederse o da onu korumaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8740, 'kör değneğini beller gibi', 'hep aynı biçimde davranıp hiçbir yenilik veya değişiklik yapmayacak biçimde: ''Evde, kör değneğini bellemiş gibi sabahları, biraz kızarmış ekmek, tereyağı ve reçelle çay içtiğimiz hâlde, bunlar, eniştemizin köşkünde bir öğle yemeği miktarına çıkar.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8741, 'kör görmez, sezer', 'kör görmediği şeyi nasıl sezerse bir konu üzerinde bilgisi olmayan kişinin de o konu ile ilgili sezişleri olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8742, 'kör itin öldüğü yer', 'çok uzakta olan yer: ''Sabah sabah kör itin öldüğü yerlerde işim yok.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8743, 'kör (kesmez) bıçak ele (yavuz), iş bilmeyen avrat dile (yavuz)', 'kör bıçak işe yaramaz ama insanın elini keser; iş bilmeyen kadın da çok konuşmaktan başka bir şey yapmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8744, 'kör kör parmağım gözüne', '''çok belli, göze batacak kadar ortada'' anlamında kullanılan bir söz: ''Orada da bazı kimseler sanat denince ille kuru, basit, yalın kat, kör kör parmağım gözüne bir üslubu anlıyorlar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8745, 'kör kurttan bile vazgeçmemek', 'en küçük varlığı bile hor görmeden korumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8746, 'kör kuşun yuvasını Allah yapar', 'garip ve kimsesiz kişiye Tanrı yardım eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8747, 'kör ölür badem gözlü olur, kel ölür sırma saçlı olur', 'bir kimse veya bir şey yok olduğunda değer kazanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8748, 'kör pazara varmasın, pazar körsüz kalmasın', 'bir şey satın almasını bileyen kimseler alışverişe çıkmamalıdır ancak esnaf da bu gibilerden hoşlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8749, 'kör satıcının kör alıcısı olur', 'herkes dengiyle iş yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8750, 'kör şeytandan bulmak', 'kaderi kötü olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8751, 'köre renkten bahsolunmaz', 'bir şeyin niteliği hakkında bilgisi olmayan kişiye, o şeyin sözü edilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8752, 'körle yatan şaşı kalkar', 'değersiz, kötü kimselerle ilişki kuranlar kötü huylar edinirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8753, 'körler mahallesinde ayna satmak', 'bir şeyi ona gereksinim duymayacak olan çevreye götürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8754, 'körler memleketinde şaşılar padişah olur', 'hepsi bilgisiz olan bir çevrede azıcık bilgisi bulunan başa geçer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8755, 'körler memleketinde tek gözlü kraldır', 'hepsi bilgisiz olan bir çevrede azıcık bilgisi bulunan başa geçer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8756, 'körün istediği bir göz, Allah verdi iki göz', 'istenilen şey fazlasıyla elde edildi.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8757, 'körün istediği bir göz, iki olursa ne söz', 'istenilen şey fazlasıyla elde edildi.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8758, 'körün istediği iki göz, biri ela biri boz', 'istenilen şey fazlasıyla elde edildi.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8759, 'körün taşı', 'rastlantı sonucu birine zarar veren, hesapta olmayan iş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8760, 'körün yanına varırsan, sen de bir gözünü kapa', 'birtakım eksiklikleri bulunan bir kimsenin yanında ona eksikliğini sezdirip onu mutsuz edecek davranışlardan kaçınılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8761, 'kös dinlemek', 'türlü olaylar yaşadığı için bilgi ve deneyim sahibi olarak benzer veya daha basit olaylar karşısında aldırış etmemek: ''Politikacılar onun olumlu isteklerini kös dinler mi, dinlemezler mi o zaman görürüz.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8762, 'kös dinleyen, davula kulak vermez', 'başından büyük olaylar geçmiş kişi küçük dertleri sorun etmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8763, 'kösele gibi', 'çok sert, çiğnenmesi güç, koparılamaz: Kösele gibi et.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8764, 'kösemenlik etmek', 'yol göstermek, kılavuzluk etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8765, 'kösenin sakalı gibi', 'her zaman olduğu gibi kalan, değişikliğe uğramayan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8766, 'köseyle alay edenin top sakalı kara gerek', 'başkasının eksikleriyle eğlenen kimsenin kendisi kusursuz olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8767, 'kösteği kırmak', '1) çocuk yürümeye başlamak; 2) bağlı bulunduğu yerle ilişiğini kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8768, 'köstek vurmak', '1) hayvanın ayağına köstek bağlamak; 2) mec. kösteklemek; 3) sp. güreşte hasmın bir veya iki ayağını sımsıkı yakalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8769, 'köşe bucağa dağılmak', '1) her tarafa yayılmak: ''Köşe bucağa dağılmış ürkek hizmetçilerini çağırır.'' -A. Erhat. 2) darmadağın olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8770, 'köşe bucak kaçmak (saklanmak)', 'kimseye görünmek istememek: ''Anası köşe bucak kaçıyor, tenha bir yer buldukça hıçkırıyordu.'' -R. Enis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8771, 'köşe kapmaca oynamak', 'biri başkasına gidip bulamadığı sırada, o da kendisine gelip bulamamak, birbirini arayıp durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8772, 'köşe taşı köşede yakışır', 'değerli kimselerin önemli mevkilerde bulunması gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8773, 'köşe tutmak', 'karışmak, kendini belli etmek, görünmek: ''Kemanın ince gıy gıylarına boş mağaralardaki ses akisleri gibi öten pes perdeden bir öksürük köşe tutuyor.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8774, 'köşebaşını tutmak', 'etkili olabilecek en önemli makamda bulunmak veya o yeri ele geçirmek: ''Amatör diplomatlar, küme küme köşebaşlarını tutmuş ve bozgunculuk propagandasına girmişti.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8775, 'köşede bucakta kalmak', 'ilgisizlikten gözden uzakta bulunmak: Koca Sinan''ın en önemli yapısı bu durumda olursa köşede bucakta kalmış olanlara selam olsun!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8776, 'köşesine çekilmek', 'toplumdan kaçıp hiçbir şeyle ilgilenmeyerek tek başına yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8777, 'köşeyi dönmek', '1) hiçbir çaba göstermeden kısa sürede zengin olmak; 2) kısa yoldan ve büyük bir emek harcamadan sosyal ve ekonomik güç edinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8778, 'kötek atmak (çekmek)', 'dövmek, dayak atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8779, 'kötek yemek', 'dövülmek, dayak yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8780, 'kötü gözle bakmak', '1) bir kimse için iyi olmayan düşünceler beslemek, bunu belli edercesine bakmak: ''Tiyatroda kimse kimseye kötü gözle bakamaz.'' -S. F. Abasıyanık. 2) cinsel duygu ile bakmak: ''Ben bu kambur kızdan hoşlanmışsam, onu sevmişsem neden ona kötü gözle bakmış olayım?'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8781, 'kötü haber tez duyulur', 'ölüm gibi kötü haber çabuk yayılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8782, 'kötü kişi olmak', 'bazı kimseler birtakım insanların düşmanlığını kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8783, 'kötü komşu insanı hacet sahibi eder', 'kötü komşu kendisinden emanet olarak istenen şeyi vermez, emanet isteyen de gidip o şeyden satın alır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8784, 'kötü kötü düşünmek', 'üzüntülü düşüncelere dalmak: ''Ben başladım kötü kötü düşünmeye.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8785, 'kötü olmak', '1) olumsuz bir durum almak; 2) beğenilmemek, takdir edilmemek; 3) kadın kötü yola düşmek: ''En insaflıları biraz acır, ah zavallı kötü oldu, alnının yazısı imiş derler.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8786, 'kötü söyleme eşine, ağı katar aşına', 'ilişkide bulunduğun kimseleri sözlerinle incitme, kötüleme ki onlar da sana daha büyük kötülük yapmasınlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8787, 'kötü söz insanı dininden çıkarır', 'gönül alıcı, okşayıcı sözlerle karşımızdakinin inadı yenilebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8788, 'kötü yola düşmek', 'kötü kadın olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8789, 'kötü yola sapmak', 'doğruluktan ayrılıp istenilmeyen ve yanlış işler yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8790, 'kötü yola saptırmak', 'kötü yola sürüklemek: ''Parmak kadar çocuğu kötü yollara saptıranların kökünü kazırım.'' -S. Ali.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8791, 'kötü yola sürüklemek', 'yasa dışı, uygunsuz veya hoşa gitmeyen bir yaşayış içine sokmak: ''Kız kardeşini kötü yola sürükledi diye babası reddetmişti.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8792, 'kötülük etmek (yapmak)', 'kötü davranmak, zarar vermek: ''Kötülük edeni öldürür veya ayetlerin emrettiği cezalardan birini verir.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8793, 'kötülük her kişinin kârı, iyilik er kişinin kârı', 'iyiliğe karşı iyiliği herkes yapabilir, önemli olan kötülüğe karşı iyilik yapabilmektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8794, 'kötürüm olmak (kalmak)', '1) yaşlılık veya sakatlık sebebiyle yürüyememek: ''Mağdurun belinden aşağısını felce uğrattı, bütün hayatı boyunca kötürüm kaldı.'' -B. Felek. 2) mec. güçsüz kalmak: ''Acılıyım karım öleli / Kalmışım yarı kötürüm'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8795, 'kötürümden aksak, hiç yoktan torlak yeğdir', 'kusurlu da olsa bir şeyin elde bulunması, hiç bulunmamasından daha iyidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8796, 'kötüye çekmek', 'yanlış, beğenilmeyen bir anlam vermek: ''Ne oldu ki Ömer ağa, dedi. Lafımı yanlış anladın, kötüye çektin?'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8797, 'kötüye kullanmak', '1) yetkisini yasalara aykırı yolda kullanmak: Görevlerini kötüye kullandılar. 2) birinin iyi davranışından istenilmeyen yolda yararlanmak: ''O benim dinlemekteki sabrımı, saflığımı kötüye kullandı.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8798, 'köylü, misafir kabul etmeyiz demez, konacak konak yoktur der', 'kişi bir işi yapmak istemezse doğrudan doğruya yapmam demez de birtakım gerekçeler ileri sürerek bunu gerçekleştirmenin olanağı bulunmadığını söyler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8799, 'köylünün kahve cezvesi karaca amma sürece', 'köylünün kahve cezvesi gösterişsizdir ama konukları ağırlamak için sürekli olarak kaynar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8800, 'kraldan çok kralcı olmak', 'birinin davasını ondan çok savunur olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8801, 'kraliçe gibi', 'gösterişli ve ağır giyinmiş, güzel (kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8802, 'krallara layık', 'çok üstün nitelikli şeyleri belirtmek için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8803, 'kramp girmek', 'kasılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8804, 'kredisi düşmek', 'güvenilirliği, saygınlığı yitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8805, 'kriz geçirmek', '1) bir organda birdenbire fizyolojik değişiklik olmak. 2) mec. bunalım içinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8806, 'kubur sıkmak', 'silah atmak, tabanca sıkmak: ''Oğlum, ben karanlığa kubur sıkmam.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8807, 'kucağı dolu olanın, ocağı yanar', 'geleceği düşünüp gerekli önlemleri alan, geçim sıkıntısı çekmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8808, 'kucağına düşmek', 'düşman, felaket, sefalet vb. kötü şeylerin veya durumların içine düşmek, onlarla karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8809, 'kucağına kurulmak', 'kucağına oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8810, 'kucağında bulmak', 'beklemediği bir durumla karşı karşıya kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8811, 'kucaktan kucağa dolaşmak (gezmek)', 'kadın, pek çok kişiyle yasal olmayan ilişkide bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8812, 'kuğu gibi', 'ince uzun, narin (boyun).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8813, 'kukla gibi', '1) ufak tefek, çelimsiz; 2) kişiliksiz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8814, 'kukumav kuşu gibi', 'tek başına, kimsesiz: ''Çıkmış, kukumav gibi oraya tünemiş!'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8815, 'kukumav kuşu gibi düşünüp durmak', 'çok üzüntülü bir durumda düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8816, 'kul azmayınca Hak yazmaz', 'kişinin başına azgınlığı yüzünden felaket gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8817, 'kul etmek', 'kendine aşırı derecede bağlamak, boyun eğdirmek: ''Nasıl sevebilirse üç gönül bir tek gülü / Sen de güzelliğine kul edersin üç gönlü'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8818, 'kul hatasız olmaz', 'insan yanılmamak için ne kadar dikkat ederse etsin yine yanılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8819, 'kul kullanan, bir gözünü kör, bir kulağını sağır etmeli', 'işçi çalıştıran kimse, işin aksamaması için işçinin yaptığı yanlışları her zaman görmemeli, söylediği uygunsuz sözleri işitmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8820, 'kul sıkışmayınca (daralmayınca, bunalmayınca) Hızır yetişmez', 'yardım hep en zor anda gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8821, 'kula kul olmak', 'bir kimsenin buyruğu altında bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8822, 'kulağı ağır işitmek', 'kulağı iyi işitmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8823, 'kulağı (bir şeyde) olmak', 'dikkatini bir şeye vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8824, 'kulağı dikilmek', 'konuşulanları dinlemek için dikkat kesilmek: ''Şimdi kulakları, seslerimize dikilmiş bir köpek gibi yatıyordu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8825, 'kulağı duvar olmak', 'sağır olmak: ''Kulakları duvar olan ihtiyarla avaz avaz ilişki kurmaya üşenmişler.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8826, 'kulağı kirişte olmak', 'söylenecek sözü, gelecek haberi sabırsızlıkla beklemek: ''Eski bekçiler ... mahalleyi kollamalı, kim hastadır, kim yatalaktır, kim yüzünü Hazret''e çevirmiş, kimin gözü toprağa bakıyor, bunları bilmeli, kulağı kirişte olmalı.'' -R. Akyavaş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8827, 'kulağı (kulakları) çınlasın', 'konuşulan yerde bulunmayan, sevilen biri anıldığında söylenen bir söz: Kulağı çınlasın, bizim arkadaş öyle derdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8828, 'kulağı okşamak', 'kulağa hoş gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8829, 'kulağı ters taraftan göstermek', 'kolay yolu varken bir işi daha zor ve uzun yollar kullanarak yapmak: ''Tahkike mahkike, kulağı ters taraftan göstermeye ne lüzum var?'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8830, 'kulağına çalınmak', 'başkasına söylenirken kendisi de duymuş olmak: ''Bu gürültüler arasında Vildan''ın bağırarak ve daha ziyade kıymet vererek telaffuz ettiği bazı kelimeler, cümleler kulağıma çalınıyordu.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8831, 'kulağına çarpmak', 'duyulmak: ''Barın kalabalığı, hareketliliği, çalgısı ve dumanı içinde ortaya atılan bu söz, tam bir isabetle geldi, Ahmet Samim''in kulağına çarptı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8832, 'kulağına fısıldamak', 'çok alçak ve hafif bir ses tonuyla kulağına eğilip bir şeyler söylemek: ''Evet biliyorum, bunlar kulağına fısıldadılar değil mi?'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8833, 'kulağına gelmek', '1) kulağına çalınmak; 2) duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8834, 'kulağına girmemek', 'söylenilen sözlere önem vermemek, söylenenleri anlamamak, benimsememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8835, 'kulağına gitmek', 'duymak: ''Olup bitenler kulağına gitse onlardan önce çarkıma okur ya neyse.'' -M. Seyda.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8836, 'kulağına inanmamak', 'duyduklarının doğruluğundan şüphe etmek: ''Kulaklarıma inanamıyordum, bu kadar narin, bu kadar nahif bir vücutta böyle bir ruh...'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8837, 'kulağına kar suyu kaçırmak', 'dolaylı olarak duyurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8838, 'kulağına kar suyu kaçmak', 'bir duyum almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8839, 'kulağına koymak (sokmak)', 'bir duruma veya söze hazırlamak için önceden kısaca anlatmak, düşünce aşılamak, telkin etmek: ''Bunu Bayram ağanın kulağına koydular.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8840, 'kulağına küpe olmak (etmek)', 'başa gelen bir durumdan alınan dersi unutmamak: ''Bu sözümü kulağına küpe et kızım!'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8841, 'kulağına söylemek', 'fısıldamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8842, 'kulağını açmak', 'dikkatle dinlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8843, 'kulak arkası (ardı) etmek', 'dikkate almamak, göz önünde tutmamak: ''Bazıları hava kirlenmesinde olduğu gibi bu eleştirileri kulak ardı ediyorlar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8844, 'kulak asmak', 'önem vermek, dinlemek: ''Bunların sözlerine ne diye kulak asıyor, ona göre yapacağın işi kestiriyorsun?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8845, 'kulak kabartmak', 'belli etmemeye çalışarak dinlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8846, 'kulak kesilmek', 'büyük bir dikkatle dinlemek: ''Çok kızgın bir fikir çarpışmasının üzerine gelmişim, kulak kesildim.'' -İ. H. Baltacıoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8847, 'kulak kıvırmak', 'domatesin olgunlaşmasını sağlamak için işlem yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8848, 'kulak (kulağını) tırmalamak', 'kulağı rahatsız etmek: ''Evde kimse yoktu sözü kulağını tırmaladı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8849, 'kulak misafiri olmak', 'yanında konuşulanları konuşmaya katılmadan dinlemek: ''Her önünden geçtiğim insanın söylediklerine kulak misafiri oluyorum.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8850, 'kulak tutmak', 'dinlemek, işitmek istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8851, 'kulak vermek', 'merak edip dinlemek, işitmeye çalışmak: ''Eğil de kulak ver, bu sessiz yığın / Bir vatan kalbinin attığı yerdir'' -N. H. Onan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8852, 'kulakları dolmak', 'aynı şeyi dinlemekten usanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8853, 'kulakları paslanmak', 'çoktan beri müzik dinlememiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8854, 'kulakları patlatmak', 'Gürültüyle rahatsız etmek: ''Kulakları patlatan bir ses bütün ormanı, bütün kuşları, bütün dünyayı susturdu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8855, 'kulaklarına kadar kızarmak', 'çok utanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8856, 'kulaklarını dikmek', 'hayvan dikkat kesilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8857, 'kulaklarının pasını gidermek', 'çoktan beri dinlememişken müzik dinlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8858, 'kulaktan burun yakın, kardeşten karın yakın', 'insanın kendi yararı her şeyden önemlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8859, 'kulis yapmak', '1) herhangi bir toplulukta oturumlar dışında gizli çalışmalar yapmak; 2) bir amaca ulaşabilmek için ilgili kişiler arasında özel çalışma yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8860, 'kulp takmak', 'bir kimseyi, bir şeyi kusurlu göstermek için bahane, kusur bulmak: ''Başa çıkılmaz kötülerle, her meziyete kulp takarlar.'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8861, 'kulpunu bulmak', 'yapılacak uygunsuz bir iş için, yasallığı tartışılabilecek bir çözüm yolu bulmak: ''Öbür seferler arkasında Servet Bey vardı; bir kulpunu bulur, uzattırıverirdi mühletleri.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8862, 'kuluçka olmak', 'dişi kuş yumurtaya yatma zamanı gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8863, 'kuluçkaya oturmak (yatmak)', 'genellikle dişi kuş yavru çıkarmak için yumurtaların üzerine yatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8864, 'kulun atmak', 'kısrak veya eşek yavru düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8865, 'kulunç girmek', 'bir organda veya vücut bölgesinde birdenbire veya şiddetli sancı oluşmak, tutulmak: ''Ayağıma fena kulunç girdi diye topallayarak onları takip etti.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8866, 'kulunç kırmak', 'ağrıyan yeri ovmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8867, 'kum dökmek', 'idrar yoluyla böbreklerde oluşan kum taneciklerini vücuttan atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8868, 'kum gibi', 'pek çok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8869, 'kuma gemisi yürümüş, elti gemisi yürümemiş', 'bir erkeğin karıları birbirleriyle anlaşabilirler ancak kardeşlerin karıları geçinemezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8870, 'kumar oynamak', '1) ortaya para koyarak talih oyunu oynamak: ''Kazanacağından emin olmadıkça kumar oynamak deliliktir.'' -A. İlhan. 2) mec. olumlu sonuçlanması şüpheli olan bir işe bile bile girişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8871, 'kumarda kaybeden aşkta kazanır', 'halk arasındaki inanca göre kumar oynayan ve parasını kaybeden biri üzülmemelidir, böyleleri aşkta şanslıdırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8872, 'kumda oynamak', 'bir fırsat kaçırarak umulanı elde edememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8873, 'kumpas kurmak', 'gizli bir iş, hile, düzen hazırlamak: ''Üç kişi burada gizli gizli ne kumpaslar kuruyorsunuz bakayım?'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8874, 'kumpasa dâhil olmak', 'hileli bir işe ortak olmak: ''Samimi bir acıma hissiyle mi, yoksa o çok değerli elmas broşun sahibi olmak için mi bu kumpasa dâhil olduğuna karar verememişti.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8875, 'kumpasa gelmek', 'hile ile kandırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8876, 'kumru gibi', 'kendi dünyasına çekilmiş: ''Başlarını dinlerler, kumru gibi yuvalarında oturur, şunun bunun aleyhinde konuşmazlar.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8877, 'kundak sokmak (koymak)', '1) yangın çıkarmak için bir yere tutuşmuş yağlı bez parçası koymak; 2) mec. ara bozacak bir söz söylemek veya böyle bir davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8878, 'kupkuru kesilmek', 'çok kurumak: ''... ağzım kupkuru kesildi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8879, 'kur yapmak', '1) karşı cinsten birine ilgi göstererek onun hoşuna gitmek, gönlünü kazanmaya çalışmak: ''Hepsi de aynı yavan, tatsız sözlerle kur yapacaklardı.'' -H. C. Yalçın. 2) birinin duygularını okşayacak biçimde davranarak onu elde etmeye çalışmak: ''Muhtaç hemşehrilerin bir kısmı etrafımda dolaşmaya, bana kur yapmaya başladılar.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8880, 'kurabiye gibi', 'çok gevrek, ağızda dağılıveren (yiyecek).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8881, 'kurban etiyle köpek tavlanmaz', 'kimi şeyler, yararlı da olsa herkese verilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8882, 'kurban gitmek', 'suçsuz yere ölmek, zarara uğramak: ''Muhakkak bir ihanete kurban gitmiştir.'' -F. F. Tülbentçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8883, 'kurban olayım!', '1) aşırı sevgi ve hayranlık anlatan bir söz: Kurban olayım, ne güzel memleket! 2) yalvarma sözü: ''Çatma, kurban olayım çehreni ey nazlı hilal.'' -M. A. Ersoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8884, 'kurban vermek', 'can kaybına uğramak: ''Arada bizim gibi birkaç kurban verilebilir.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8885, 'kurbanlık koyun gibi', 'başına geleceklerden habersiz olan: ''Kâh yollarda tabur olmuş yürüyorlar, kâh garlarda, istasyonlarda kurbanlık koyun gibi bekleşip duruyorlardı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8886, 'kurcalama sivilceyi (sivilceyi kurcalama) çıban edersin', 'küçük bir sorunu çok kurcalar, çok deşerseniz başınıza büyük dert açarsınız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8887, 'kurda konuk (komşu) giden, köpeğini yanında götürür', 'saldırgan biriyle karşılaşacak olan kişi, kendisini koruyacak önlemler almalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8888, 'kurda ''neden boynun (ensen) kalın?'' demişler, ''işimi kendim görürüm de ondan'' demiş', 'işini başkasına inanmayarak kendisi yapan, üzülmez, rahat eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8889, 'kurdele kesmek', '1) tesis veya kuruluşun açılış töreninde gerilen şeridi iyi dileklerle kesmek; 2) herhangi bir amaçla bağlanmış olan şeridi kesip ayırmak: Nişan töreninde kırmızı kurdele kestik.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8890, 'kurdele takmak', 'okulda belli bir konudaki başarıyı belirtmek üzere öğrenci giysisinin yakasına renkli, özel bir şerit takmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8891, 'kurdun adı yaman çıkmış, tilki vardır (tilkicik var) baş keser', 'öylesine sinsi ve kurnaz kimseler vardır ki adı zalime, haine ve kötüye çıkmış kimselerden daha tehlikelidirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8892, 'kurdun oğlu akıbet kurt olur', 'kişi sonunda kendi karakterini, aslını, düşüncesini atalarına benzer biçimde ortaya koyar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8893, 'kurdunu (kurtlarını) dökmek (kırmak)', 'çoktan beri özlediği bir şeyi bol bol yapıp hevesini almak: ''Daha sonra Paris''te kurtlarını dökmeye gelen her milletten insanlara rastladık.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8894, 'kurşun atmak', '1) silahla mermi atmak: ''Yarın, öbür gün Arap çeteleri ile sarılacaksınız, Peygamberin yeşil kubbesine kurşun atacaklar.'' -F. R. Atay. 2) mec. düşmanlık etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8895, 'kurşun dokunmak', 'mermi isabet etmek: ''Suriye''de bel kemiğine bir kurşun dokunmuştu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8896, 'kurşun dökmek', 'halk inanışına göre erimiş kurşunu, hastanın üstünde, içinde su bulunan bir kaba dökerek ortaya çıkan şekillerin yorumuyla nazar, büyü, hastalık vb. şeyleri önlemek, iyileştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8897, 'kurşun gibi', '1) katlanması zor bir biçimde: ''Gurbet acısı kurşun gibi içine çökmüştü şimdi.'' -H. Taner. 2) sıkıntı veren; 3) çok ağır.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8898, 'kurşun manyağı yapmak', ' argo ölümle tehdit etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8899, 'kurşun sıkmak', 'silahı ateşlemek, mermi yakmak: ''Az bir sürede bütün köy bu kurşunları sıkanın Hasan olduğunu öğrendi.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8900, 'kurşun yağdırmak', 'çok sayıda kurşun atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8901, 'kurşun yemek', 'vurulmak: ''Kurşunu yer yemez, kayalardan aşağı yuvarlanmış leşi, ta derenin kucağına!'' -T. Oflazoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8902, 'kurt ağzı bağlamak', 'açıkta kalan hayvanların kurt tarafından boğulmasını önleme amacıyla çeşitli uygulamalar yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8903, 'kurt dumanlı havayı sever', 'kötü niyetli kimselerin ortalıktaki karışıklıklardan yararlanırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8904, 'kurt gibi', 'işini bilen, girişken (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8905, 'kurt kocayınca köpeğin maskarası olur', 'güç ve yeteneğini yitiren insan, basit ve kendini bilmezlerce aşağılanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8906, 'kurt komşusunu yemez', 'bir kişi ne kadar kötü niyetli de olsa yakınlarına dokunmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8907, 'kurt köyünü (tüyünü) değiştirir, huyunu değiştirmez', 'kötü kimse yer yurt değiştirse de kötü huylarını değiştirmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8908, 'kurtla görüşürsen köpeği yanından ayırma', 'saldırgan biriyle karşılaşacak olan kişi, kendisini koruyacak önlemler almalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8909, 'kurtla koyun, kılıçla oyun olmaz', 'saldırıcı ile güçsüzün yan yana bulunduğu yerde tehlike vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8910, 'kurtla ortak olan tilkinin hissesi, ya tırnaktır ya bağırsak', 'ortağı güçlü ve hileci olan kimse ortağının kendisine vereceği işe yaramaz paya razı olmak zorundadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8911, 'kurtlu baklanın kör alıcısı olur', 'işe yaramaz da olsa her şeyin isteklisi bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8912, 'kuru başına kalmak', 'hayatında veya yanında kimsesi kalmamak, kimsesiz, yalnız kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8913, 'kuru gayret çarık eskitir', 'bir iş rastgele bir çabayla değil amaca doğru planlı bir biçimde yürümekle başarılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8914, 'kuru gürültüye pabuç bırakmamak', 'bir durum karşısında telaşsız, korkusuz, dilediğince davranmak: ''Köse Mümeyyiz öyle denemeden kuru gürültüye pabuç bırakır takımından değildi.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8915, 'kuru hasır (kilim) üstünde kalmak', 'aç, parasız, evsiz kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8916, 'kuru laf karın doyurmaz', 'boş sözlerle olumlu iş yapılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8917, 'kuru tahtada kalmak', 'eşyası elinden gitmek, çıplak evde oturma durumunda kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8918, 'kuruda kalmak', 'deniz alçaldığında gemi karaya oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8919, 'kurum kurum kurumlanmak (kurulmak)', 'büyüklenmek, böbürlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8920, 'kurum satmak', 'böbürlenmek, büyüklenmek: ''Senin kıratında bir tane daha bulsun da kurumunu ona satsın.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8921, 'kuruntuya kapılmak', 'boş yere tasalanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8922, 'kurunun yanında yaş da yanar', 'beğenilmeyen tutumlarından dolayı cezalandırılan kişiler yanında suçsuzlar da suçlular gibi hırpalanırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8923, 'kurusıkı atmak', ' argo palavra atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8924, 'kusur bulmak', '1) bir şeyin özrünü görmek; 2) gereğinden çok titiz ve hoşgörüsüz davranmak: ''Nesine itiraz ederseniz ediniz, boyun bağına, pantolonun ütüsüne kusur bulamazsınız.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8925, 'kusur etmek', 'yanlışlık yapmak: ''Sana karşı olan vazifelerimde kusur mu ediyorum?'' -A. M. Dranas.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8926, 'kusur etmemek', 'hoş karşılanmayacak bir davranışta bulunmamak: ''Saygıda kusur etmemek için âdeta birbirleriyle yarış ediyorlardı.'' -F. F. Tülbentçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8927, 'kusur işlemek', 'yanlış davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8928, 'kusura bakmamak (kalmamak)', 'hoş görmek: ''Kusura bakma, hatırını soramadım.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8929, 'kusursuz dost arayan dostsuz kalır', 'kusursuz kişi olmadığından, kendisine kusursuz bir dost arayan kimse aradığını bulamaz, dostsuz kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8930, 'kusursuz güzel olmaz', 'her iyi şeyin hoşa gitmeyen bir yönü, her güzelin kusurlu bir tarafı vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8931, 'kuş gibi', '1) çok hafif; 2) çabuk iş gören, eline ayağına çabuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8932, 'kuş gibi çırpınmak', 'çaresizlik içinde telaşlı davranmak: ''Sokağa çıkmak, çocukların arasına karışmak için pencerede, kafeste kuş gibi çırpınırım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8933, 'kuş gibi (kadar) yemek', 'çok az yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8934, 'kuş gibi uçup gitmek (uçmak)', '1) çok kısa süren bir hastalıkla ölmek; 2) çok kısa sürmek, geçmek: ''Baktım seneler kuş gibi uçuyor / Baktım sonum bir avuç toprak'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8935, 'kuş kadar canı olmak', 'küçük, cılız, güçsüz bir yaratık olmak: ''Kaç gündür helak oluyor fukara, biraz dinlensin; kuş kadar canı var, temelli eriyip bitecek!'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8936, 'kuş kafesi gibi', 'ufak ve güzel (yapı).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8937, 'kuş kanadına kira istemez', 'kişi, kendi işi için zaten harcayacağı çabadan dolayı başkasından karşılık beklemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8938, 'kuş kanadıyla gitmek', 'çok hızlı gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8939, 'kuş mu konduracak?', 'yapacağı şey görülmemiş bir sanat eseri mi olacak?', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8940, 'kuş tüyü gibi', 'çok yumuşak (oturacak, yatacak yer).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8941, 'kuş uçmaz, kervan geçmez', 'kimsenin uğramadığı ıssız ve sapa yer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8942, 'kuş uçurmamak', 'hiçbir şeyin veya kimsenin kaçmasına, geçmesine imkân vermemek: ''Zavallının yanına kimseyi sokmaz, bağından, bahçesinden kuş uçurmazmış.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8943, 'kuş vardır eti yenir, kuş vardır et yedirilir', 'öyle kişiler vardır ki acımadan en ağır işte kullanılır, öyle kişiler de vardır ki iş gördürmek şöyle dursun onlara hizmet edilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8944, 'kuşa benzemek (dönmek)', 'bir şey düzeltilmek istenirken komik veya biçimsiz bir duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8945, 'kuşa kafes lazım, boruya nefes', 'bir şeyden yararlanmak için kullanılacak araç, onun niteliğine uygun olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8946, 'kuşa süt nasip olsa anasından olurdu', '1) yaradılışı bir şeyden yararlanmasına elverişli olmayan kişi ne kadar çabalasa o şeyden yararlanamaz; 2) kişi en yakınından sağlayamadığı faydayı başkasından hiç sağlayamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8947, 'kuşku beslemek (duymak)', 'kuşkulanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8948, 'kuşku uyanmak', 'kuşku oluşmak: ''Fakat bu mektubun yazısı önceki gün gelen zarf üzerindeki yazıya çok benzediği için genç adamın yüreğinde bir kuşku uyanıyor.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8949, 'kuşku yok', 'başka türlü olamaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8950, 'kuşkulu uyku evin bekçisidir', 'ufak bir tıkırtıdan uyanacak kadar hafif uyuyan ve tetikte olan kimse evin bekçiliğini iyi yapıyor demektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8951, 'kuşkusu kalmamak', 'bir konuda her şeyi bilmek, şüphe duymamak: ''Tek kuşkum kalmamış evrende, hangi konu açılsa tek sözle ağzını tıkarım bütün bilgilerin.'' -T. Oflazoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8952, 'kuşkuya düşmek', 'kuşkulanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8953, 'kuşsütü ile beslemek', 'eksiksiz, özenle beslemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8954, 'kuşu kuşla avlarlar', 'elde edilmek istenen kimse, daha önce elde edilmiş kimse aracılığıyla tuzağa düşürülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8955, 'kuşun kanadıyla haber salmak', 'en hızlı bir biçimde haber vermek: ''Görürseniz, duyarsanız kuşun kanadıyla haber salın demedik mi?'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8956, 'kutlu gün doğuşundan bellidir', 'mutlu sonuç verecek işler, daha başlangıçta belli olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8957, 'kutsuz kuşun yuvası doğan yanında olur', 'talihsiz kişi, her an kendisine saldıracak güçlü kimselerle yan yana bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8958, 'kutu gibi', 'küçük fakat kullanışlı ve şirin: ''Eviniz kutu gibi küçücük bir evdi / Sarmaşıklarla balkonu örtük bir evdi'' -A. M. Dranas.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8959, 'kuvvet almak', 'herhangi bir yardımla gücü artmak, kuvvetlenmek: ''Bundan kuvvet alarak âdeta bir nevi buruksu saadet içinde yaşamaktayım.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8960, 'kuvvet bulamamak', 'cesaret edememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8961, 'kuvvetini toplamak', 'gücünü artırmak, kuvvetlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8962, 'kuvvetle muhtemel', 'büyük olasılıkla.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8963, 'kuvvetten düşmek', 'gücü azalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8964, 'kuyruğa girmek', 'ayakta arka arkaya durulan diziye girmek: ''Ondan son-ra kuyruğa girenler, abdesthaneyi dörder dörder kullanmaya koyuldular.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8965, 'kuyruğu dikmek', '1) hayvan koşmaya, başlamak; 2) insan bulunduğu yerden uzaklaşmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8966, 'kuyruğu kapana kısılmak (sıkışmak)', 'çok zor duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8967, 'kuyruğu titretmek', ' argo ölmek: ''Aklın varsa bu kahpe dünyada kuyruğu titretmeden çekmene bak!'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8968, 'kuyruğunu kısmak', 'korkup sinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8969, 'kuyruğunu tava sapına çevirmek', 'haddini bildirmek, gereken dersi vermek: ''Sonra benim kuyruğumu tava sapına çevirirler efendim diye bağırıyor, masa başındaki erkândan tekrar yardım istiyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8970, 'kuyruk çekmek', 'gözün çevresine kalem veya sürme ile çizgi çekmek: ''Zehra elinde kalem, gözlerine kuyruk çekiyordu.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8971, 'kuyruk olmak', 'arka arkaya dizilmek, sıralanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8972, 'kuyruk sallamak', 'yaltaklanmak: ''Gül gibi yavrusunu bırakıp da evlenecekmiş. Kuyruk sallaya sallaya oğlumu öldürttü.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8973, 'kuyruk yapmak', 'uzun ve peş peşe bir sıra oluşturmak: ''Ama hâlâ bilet var diye bekleyen en aşağı beş bin kişi güzel bir kuyruk yapmışlar.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8974, 'kuyu gibi', '1) çok derin (yer); 2) basık ve karanlık (yer).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8975, 'kuyudan adam çıkarmak', '1) olumsuz, uygunsuz veya yasal olmayan bir duruma son vererek birini haklarına kavuşturmak; 2) unutulmaktan kurtarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8976, 'kuzgun gibi', 'çok kara, çok koyu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8977, 'kuzguna yavrusu şahin (Anka, güzel) görünür', 'herkesin kendi yarattığı şey çirkin de olsa gözüne güzel görünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8978, 'kuzu çevirmek', 'kuzunun gövdesini şişe geçirip ateş korunun üzerinde çevirerek pişirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8979, 'kuzu gibi', 'çok uysal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8980, 'kuzu gibi olmak', 'uslanmak, sessizleşmek, sakinleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8981, 'kuzu kesilmek', 'uysallaşmak, sessizleşmek, sakin bir durum almak: ''Sabık komiserin sahiden bir komisermiş gibi tavır aldığı anlarda kadın kuzu kesilirdi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8982, 'kuzu postuna bürünmek', 'karşısındakini aldatmak için gerçek kişiliğini saklamak, kendini zararsız ve uysal göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8983, 'küçük abdesti gelmek', 'idrar yapma ihtiyacı duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8984, 'küçük dağları ben yarattım demek', 'çok böbürlenmek, kibirlenmek: ''Aslarda o küçük dağları ben yarattım diyen heybet ... varken ... o güdük, o boynu bükük konçinalar onlara bir türlü el kaldıramıyorlar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8985, 'küçük dilini yutmak', 'şaşırmak, donakalmak: ''Kadıncağız beni bu hâlde görünce az kalsın küçük dilini yutacaktı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8986, 'küçük düşmek', 'değeri veya onuru sarsılmak: ''Kimsenin yanında küçük düşmeni istemem.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8987, 'küçük düşürmek', 'değerini veya şerefini sarsmak: ''Handan''ı küçük düşürdüğünü, asıl suçun da bu olduğunu kabul etmişti.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8988, 'küçük görmek', 'değer, önem vermemek: ''Bütün bu fânilikleri küçük görerek bunları ancak gönül oyalayıcı şeyler diye telakki ettiklerini gösteriyordu.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8989, 'küçük köyün büyük ağası', 'büyüklük taslayanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8990, 'küçük oynamak', 'kumarda az para ile oynamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8991, 'küçükle küçük, büyükle büyük olmak', '1) her yaştaki kişilere karşı dostça, arkadaşça davranmak; 2) her makam ve durumdaki kişilere karşı dostça ve anlayış göstererek davranmak: ''Protokol kaidelerini, çok zaman, bir yana atıp küçükle küçük, büyükle büyük olmasını biliyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8992, 'küf bağlamak (tutmak)', '1) küflenmek; 2) mec. unutulmak; 3) mec. bitmek, kalmamak: ''İsteksiz isteksiz oluyorsun tıraşı, bir küf bağlamışsa bütün heyecanların.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8993, 'küfelik olmak', 'çok sarhoş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8994, 'küfrü basmak', 'küfretmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8995, 'küfür savurmak', 'küfretmek: ''Onlara ağza alınmaz birkaç küfür savurdu.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8996, 'küfür yemek', 'kendisine küfredilmek: ''Kapının eşiğinde, şiş yarasının kabuklarını ayıklayan bir Arap eteğine basıp halis Kur''an şivesiyle şiddetli bir küfür yedikten sonra otele döndüm.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8997, 'küheylan at, çul içinde de bellidir', 'cevherli insan, kılık kıyafeti düzgün olmasa da değerini yitirmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8998, 'kül bağlamak', '1) ateş sönmek; 2) mec. gücünü, etkisini yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (8999, 'kül etmek', '1) yakmak, kavurmak; 2) mec. birinin varını yoğunu yok etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9000, 'kül gibi', 'soluk, renksiz (bet beniz).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9001, 'kül olmak', '1) bütünüyle yanmak: ''Tatlı bir cızırtı çıkararak çabucak tutuşur, mavi ve sincabi bir buhar bırakarak kül oluverirdi.'' -Ö. Seyfettin. 2) mec. varını yoğunu yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9002, 'kül ufak olmak', 'çok küçük parçalara ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9003, 'kül yemek (yutmak)', ' argo kurnazca yapılan bir oyuna düşmek, aldatılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9004, 'külah kapmak', 'düzen, dalavere ile bir işin başına geçmek: ''Hatta bunlar arasında öyleleri vardır ki zamana ayak uydurmak, göze girmek ve külah kapmak için gâvur mukallitliğinde birbiriyle âdeta yarışa girişmişlerdir.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9005, 'külah peşinde olmak', 'yalan ve dolanla bir işin başına geçmeye çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9006, 'külah takmak', 'hile ile, oyunla kandırıp parasını almak: ''Önüme gelene külah takacaktım. Külah takacağım kimseler de mutlaka benim gibi olanlardı.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9007, 'külahıma anlat!', '''söylediklerine inanamıyorum, beni kandıramazsın'' anlamında kullanılan bir söz: ''Anlat sen benim külahıma! Ah, ben hükûmette olsam size gık dedirtmem!'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9008, 'külahını havaya atmak', 'pek çok sevinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9009, 'külahları değiştirmek (değişmek)', 'tehdit ederek bozuşmak: ''Dükkânda çalışırken ters bir iş tutarsa yeniden külahları değişebilirlerdi.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9010, 'külçe gibi oturmak', 'yorgun veya bitkin bir durumda çöküvermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9011, 'külçe kesilmek', 'dermansız, güçsüz kalıp olduğu yere yığılmak: ''Sağ olup da bu hâli görseydi, o anda külçe mi kesilirdi acaba, yoksa oynatıverir miydi?'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9012, 'külfete katlanmak', 'sıkıntıya, zorluğa önem vermemek: ''Ben en hain, en merhametsiz hücumları yapmak için bu kadar külfetlere katlanıp buralara gelmiştim.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9013, 'külhancının beyliği hamamcılık demişler', 'bayağı bir işle uğraşan kimse, yükselse bile ancak yaptığı işle anılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9014, 'külünü savurmak', 'bir şeyi bütünüyle bitirip yok etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9015, 'kümeden düşmek', 'takımlar sonraki sezonda bir alt kümeye inmek, ligden düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9016, 'kümeye çıkmak', 'takımlar sonraki sezonda bir üst kümeye yükselmek, lige çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9017, 'kündeden atmak', '1) güreşçi, rakibini belinden kavrayıp kendi üzerinden aşırarak arka üzeri atmak; 2) mec. aldatarak tuzağa düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9018, 'kündeye almak (getirmek)', '1) güreşçi, rakibini altına alıp bir elini önden, ötekini arkadan geçirerek kilitlemek: ''Kolunu tutup kündeye getiriyor, bir taraftan da bacağının birini ikiye büküyorum.'' -M. İzgü. 2) mec. oyuna getirmek, tuzağa düşürmek: ''Plan kurar, tertip yaparlar; kendilerini kurtarmaya, yeni müdürü de kündeye getirmeye çalışırlar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9019, 'kündeye gelmek', 'aldanmak, tuzağa düşmek: ''Barut yoktu. Kalenin dar kapısından çıkmak imkânı yoktu. Öyle korkunç bir kündeye gelmişlerdi ki...'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9020, 'kündeye getirilmek', 'aldatılmak, tuzağa düşürülmek: ''Akıllı bir evlat olan Ali Harun Bey, annesinin böyle bir kündeye getirilmesini hazmedemez.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9021, 'künyesi gelmek', 'savaşta bir askerin ölüm haberi kendi evine bildirilmek: ''Geçen sene künyesi geldi, dedi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9022, 'küp gibi', '1) şişman; 2) sarhoş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9023, 'küpe dönmek', 'çok şişmanlamak: ''O zamandan beri küpe dönmüş. Hâlbuki o zamanlar ne ince bir kızdı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9024, 'küplere binmek', 'çok öfkelenmek: ''Ertesi günü babam horozun ölüsünü bulunca küplere bindi.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9025, 'küpünü doldurmak', 'eline fırsat geçmişken çokça para biriktirmek: ''Hamiyetini bu felsefeye uydurarak küplerini doldurmayı bilenler bu memlekette bolluk içinde yaşarlar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9026, 'kürdan gibi', 'çok zayıf, incecik, çelimsiz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9027, 'kürek kadar dili olmak', 'pabuç kadar dili olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9028, 'kürk ile börk ile adam olunmaz', 'kılık kıyafet, değeri olmayan kişiye değer kazandırmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9029, 'kürkçünün kürkü olmaz, börkçünün börkü', 'başkalarının gereksinimlerine çare bulan kişi bunlara benzeyen kendi ihtiyaçlarını savsaklar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9030, 'kürkü orak vaktinde, orağı kürk', 'gereksinimler vaktinden önce ve ucuz olduğu zaman karşılanmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9031, 'kürünü kırmak', ' hlk. hevesini almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9032, 'kürünü öldürmek', ' hlk. gururunu kırmak, güçsüzlüğünü kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9033, 'küstüğün dağın odununu kesme', 'insan onuruna düşkün olup ilişkisini kestiği bir yerden veya kimseden herhangi bir yarar elde etmeye çalışmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9034, 'küt diye', 'ansızın: Küt diye düştü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9035, 'kütüğe geçirmek', 'ana deftere yazmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9036, 'kütük gibi', '1) çok şişmiş; 2) çok sarhoş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9037, 'laçka etmek', '1) den. bir halatı koyuverip boşaltmak; 2) mec. gevşetmek, bitkin bir duruma getirmek: ''Ne oluyorum demeye vakit bulamadan her tarafını laçka eden bir kesiklik duydu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9038, 'laçka olmak', '1) vida, mil vb. makine parçaları aşınarak veya yuvaları genişleyerek gevşemek; 2) mec. herhangi bir düzen iyi işlemez olmak: ''O gece bir kadeh içkinin laçka olan sinirlerine iyi geleceğini düşünüyordu.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9039, 'lades tutuşmak', 'tavuğun lades kemiğini birer ucundan karşılıklı tutup kırarak lades oyununa başlamak: ''Lades tutuşmadık ki her defasında hatırımda diyorsun.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9040, 'ladesli olmak', 'lades oyununa tutuşmak: ''Lades tutuşanlar ladesli olduklarını unutmamaya ama rakibine unutturmaya çalışırlar.'' -Ü. Dökmen.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9041, 'laf açmak', 'söz etmek, söz açmak, konuya girmek: ''Yavere burulduğumu sezdirmeden başka bir laf açtım.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9042, 'laf altında kalmamak', 'söz altında kalmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9043, 'laf anlamaz', '1) söz dinlemeyip kendi bildiğinde inat eden; 2) kaba, aptal (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9044, 'laf anlatmak', 'sözünü dinletmek, karşıdakini ikna edinceye kadar konuşmak: ''Aralarından bir tanesi ille de laf anlatacağım diye çene patlatıp duruyormuş.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9045, 'laf aramızda', '''başkaları bilmesin, duymasın'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9046, 'laf atmak', '1) söyleşmek, konuşmak: ''Tabii Hayri Efendi''yle biraz laf atacak belki de biraz işten güçten bahsedecekti.'' -Y. K. Karaosmanoğlu. 2) uzaktan, dolayısıyla dokunacak söz söyleyip işittirmek: ''Yakınındaki erkeği kime benzetirse onun lisanından bir şarkı ile laf atıyor.'' -F. R. Atay. 3) sözle sarkıntılık etmek: ''Seyircilerin alışılmış sululuklarından, laf atmalardan kaçındıklarını söyledi.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9047, 'laf çakmak (çarptırmak, dokundurmak)', 'üstü kapalı bir biçimde karşısındakine bir şeyler ima etmek: ''Gözüne gelini ilişince açıktan açığa olmasa bile öfkesini ondan almak için laf çaktı.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9048, 'laf çıkarmak', '1) yeni bir şey söylemek, ortaya atmak: ''Şimdi unutup laf mı çıkarıyorsun?'' -Ö. Seyfettin. 2) dedikodu yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9049, 'laf çıkmak', 'dedikodu başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9050, 'laf dinlemek', 'söz dinlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9051, 'laf düşmemek', 'söz düşmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9052, 'laf etmek', 'söz etmek: ''Öyle laf ettim ki adamcağız gezdirmeye mecbur kaldı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9053, 'laf işitmek', 'azarlanmak, birisi kendisine darılmak: ''Kaç kere laf işittim bu yüzden, sineye çektim.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9054, 'laf kaynayıp gitmek', 'söz boşa söylenmek, anlaşılmaz olmak, hiçbir etki yapmamak: ''Her kafadan bir ses çıkıyor, söylenen laflar gülüşmeler arasında kaynayıp gidiyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9055, 'laf lafı açar', 'bir konu üzerinde konuşulurken ilgisi dolayısıyla söz başka bir konuya geçer, sohbet uzar, gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9056, 'laf lafı açar, laf da tabakayı açar', 'bir konu üzerinde konuşulurken ilgisi dolayısıyla söz başka bir konuya geçer, sohbet uzar, gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9057, 'laf ola beri gele!', 'konuşulan konu ile ilgili olmayan bir söz söylendiğinde veya bir sorun tartışılırken ilgisiz bir şey ifade edildiğinde söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9058, 'laf olmak', 'dedikodu çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9059, 'laf olsun âdet yerini bulsun', 'konuşacak herhangi bir konu bulunmayıp rastgele söz sarf edildiğinde söylenen bir söz: ''A hiç olur mu cümlesini de laf kıtlığında laf olsun âdet yerini bulsun diye söylemişti.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9060, 'laf oturtmak', 'karşı tarafa gerektiği yerde, beklenilmeyen bir durumda, esaslı ve gereken bir laf söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9061, 'laf söyledi bal kabağı!', ' alay gereksiz yere ve aptalca söz söyleyen kimse için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9062, 'laf taşımak', 'dedikodu ederek laf götürüp getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9063, 'laf torbaya girmez', 'ağızdan çıkan bir söz, artık gizli kalmaz, herkes onu duyar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9064, 'laf yakıştırmak', 'konuşma sırasında yerinde söz söylemek, gerekeni ifade etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9065, 'laf yapmak', 'dedikodu yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9066, 'laf yetiştirmek', 'birinin söylediklerine olur olmaz karşılık vermek, çene yarıştırmaya kalkmak: ''Evin hanımı sokaktan geçene laf yetiştirmek için vakit yitirmemeli.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9067, 'laf yok!', '''mükemmel, çok güzel, kusursuz, eleştirilecek bir tarafı yok'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9068, 'lafa boğmak', 'bir konu üzerinde konuşulurken ilgisiz, gereksiz ve anlamsız bir biçimde söz edip asıl konuyu değiştirmek, unutturmak, karıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9069, 'lafa dalmak', 'uzun süren bir sohbette bulunmak, çok konuşmak: ''Kız Binnaz, kapının önünde kiminle lafa daldın yine?'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9070, 'lafa karışmak', 'biri veya birileri konuşurken bir başkası konuşmak, konuşmaya katılmak: ''Müsteşar bile sözüne itiraz edemiyor diye lafa karışıyor.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9071, 'lafa tutmak', 'yersiz, zamansız ve sürekli konuşarak meşgul etmek, oyalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9072, 'lafı ağzına tıkamak', 'birinin rahatça konuşmasını engelleyip susturmak, söylemesine imkân tanımamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9073, 'lafı ağzında bırakmak', 'birinin konuşmasını kesmek, sözlerini bitirmesine fırsat vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9074, 'lafı ağzında gevelemek', 'söylemek isteğini söyleyememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9075, 'lafı ağzında kalmak', 'sözü ağzında kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9076, 'lafı bağlamak', 'bir konu üzerinde son sözü söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9077, 'lafı değiştirmek', 'başka konuyu dile getirmek, başka bir şeyden söz etmek: ''Bu meslekte neler gördük biz diye yuvarlayarak lafı değiştiriyorum.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9078, 'lafı dolandırmak', 'sözü uzatmak: ''Üç beş sorum var kalıplaşmış, onları soruyorum, lafı dolandırarak.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9079, 'lafı döndürüp dolaştırmak', 'sözü uzatmak: ''Her söyleşisinde lafı döndürüp dolaştırmasından bıkmışlardı.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9080, 'lafı geçmek', '1) sözü etkili olmak, sözü dinlenmek; 2) bahsedilmek: ''Kocasının erkek kardeşinin süt kardeşi imişsiniz, ben sizi hiç görmedimdi fakat bu evde lafınız geçerdi.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9081, 'lafı kıçından anlamak', 'konuşulan konuyu yanlış, ters anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9082, 'lafı kıçından dinlemek', 'konuşulan konuyu ilgisiz, üstünkörü veya önem vermeden dinlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9083, 'lafı kısa kesmek', 'söyleyeceğini kısa veya özet olarak belirtmek, az ve öz konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9084, 'lafı mı olur?', '1) ''şimdi onun sırası değil, daha önemli konular var'' anlamında kullanılan bir söz; 2) bir iş yapmak için ''seve seve zahmete girerim'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9085, 'lafı sulandırmak', 'bir konu üzerinde ciddiyetle durup konuşurken araya ilgisiz, anlamsız veya tutarsız boş laf katmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9086, 'lafı uzatmak', 'konuşmayı gereksiz bir biçimde başka sözlerle sürdürmek: ''Lafı uzatmadım, kelimesi kelimesine cevap vermeye takatim yoktu.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9087, 'lafını bilmek', 'akıllı uslu konuşup başkasını rahatsız etmemek, yerinde, güzel ve tutarlı konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9088, 'lafını etmek', 'birinden veya bir konudan söz etmek, onunla ilgili olarak konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9089, 'lafını kesmek', 'birinin sözünü bitirmesine fırsat vermeden araya girmek: ''Vedia yine feylesofun lafını kesti.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9090, 'lafını (lafınızı) balla kestim', 'bir kimsenin sözünü kesmek gerektiğinde ''izin verin'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9091, 'lafını şaşırmak', 'ne diyeceğini bilememek, şaşırarak başka şeyler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9092, 'lafını yabana atmamak', 'söylenen söze değer vermek: ''Yooo, lafımı yabana atma, bu işi rahmetli anneciğim de bilirdi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9093, 'lafını yedirmek', 'iddialı olarak söylediği sözü geri alma zorunda bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9094, 'lafını yemek', 'verdiği sözden, söylediği sözden vazgeçmek: ''Lafımı yemem avrat, dedi, kafam kızdı mı tövbe lafımı yemem!'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9095, 'lafla peynir gemisi yürümez', 'şöyle yaparım, böyle yaparım demekle yapılması gereken iş yapılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9096, 'lafta kalmak', 'bir iş düşünce aşamasında kalıp gerçekleşmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9097, 'laftan anlamak', 'söyleneni dinleyip uymak veya uygulamak: ''Adam değil ki laftan anlasın!'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9098, 'laga luga etmek (veya yapmak)', 'boş konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9099, 'lağımla atmak', 'bir kayayı delip içine patlayıcı maddeler koyduktan sonra bu maddeleri ateşleyerek parçalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9100, 'lahavle çekmek (okumak)', '''lahavle'' sözünü söylemek: ''Cömertliği karşısında olduğumu anlayınca lahavle çekip yola devam ettim.'' -A. Rasim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9101, 'lakap takmak', 'bir kimseye onun bir özelliğini belirtecek bir ad vermek: ''Arkadaşının taktığı bu lakabı, Ger Ali, soyadı kanunu çıkınca isminin başından alıp sonuna koydu mu bilmiyorum.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9102, 'lakayıt kalmak', 'ilgisiz davranmak, aldırmamak: ''Onun gözyaşlarına lakayıt kalmak mecburiyetinde bulunuyorum.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9103, 'lakırtı ağzından dökülmek', 'isteksiz konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9104, 'lakırtı çıkarmak', 'laf çıkarmak: ''Sonra tahsisat yoktur, gelecek sene bütçesine para konulacak diye lakırtı çıkardılar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9105, 'lakırtı etmek', '1) konuşmak: ''Bir gün sinirli olur da ters bir lakırtı ederse ben susarım.'' -M. Ş. Esendal. 2) dedikodu konusu etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9106, 'lakırtı taşımak', 'laf taşımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9107, 'lakırtı yetiştirmek', 'bir söze karşılık vermekte gecikmemek: ''Birbirine lakırtı yetiştirmeye, cevap bulmaya çalışıyorlar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9108, 'lakırtısı ağzında kalmak', 'konuşan kimsenin, bir başkasının söze başlaması veya ani bir olay sonucunda sözü yarım kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9109, 'lakırtısı mı olur?', 'konuşulan bir şeyin önemsizliğini veya yersizliğini anlatmak için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9110, 'lakırtısını etmek', 'hakkında konuşmak: ''Köylü ile Gülsüm, çocukları o kadar eğlendirmişti ki sofrada hep onların lakırtısını ediyorlar, durmadan gülüyorlardı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9111, 'lakırtıya boğmak', 'gereksiz ve boş sözlerle konuşmayı uzatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9112, 'lakırtıya tutmak', 'konuşarak oyalamak: ''Şükran, elleri atımın dizgininde, beni bırakmıyor, lakırtıya tutuyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9113, 'lakırtıyı ağzına tıkamak', 'birinin sözünü bitirmesine imkân vermeden onu ters bir karşılıkla susmak zorunda bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9114, 'lakırtıyı ezip büzmek', 'konuşmasını beceremeyip aynı şeyleri tekrarlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9115, 'lakırtıyı kesmek', 'susmak: ''Sanki görüşmemiz münasebetsiz bir safhaya girmişti de ister istemez lakırtıyı kesmiştik.'' -İ. H. Baltacıoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9116, 'laklak etmek', 'karşılıklı, gelişigüzel, havadan sudan konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9117, 'lal etmek', 'konuşamaz duruma sokmak, susturmak: ''Bazı ihtiyar âlimlerimizin lisana vukuflarına hayran oldum, mantıklarıyla bizi lal ettiler.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9118, 'lala paşa eğlendirmek', 'işini gücünü bırakıp karşısındakinin hoş vakit geçirmesini sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9119, 'lâm elif çevirmek (çizmek)', 'kısa bir süre dolaşıp gelmek: ''Akşamüstü gelirken, Langa''dan doğru bir lâm elif çevirelim, dedik.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9120, 'lamba açmak', 'kapı, pencere kenarlarında genellikle dik açılı girinti açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9121, 'lambayı açmak', '1) ışığı yakmak; 2) lambanın fitilini yükseltip ışığını çoğaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9122, 'lâmı cimi yok', '''değişmez, kesin, başka yolu yok'' anlamında kullanılan bir söz: ''Lütfü''ye akordeon çaldırıyorlar, lâmı cimi yok, çalacak.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9123, 'lanet etmek', 'ilenmek, kötülüğünü istemek: ''Bu nesil öyle zamanlar geçirdi ki doğduğuna lanet etti.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9124, 'lanet okumak', 'bir kimsenin Tanrı''nın merhametinden yoksun kalmasını dilemek: ''Hele sevgilisinin de hastalandığı bu korkunç haftalarda, fabrikanın cinayetlerine ne kadar lanet okuyor, biraz da kendisi vasıta olduğundan dolayı ne derece ızdırap çekiyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9125, 'lapa gibi', 'yumuşak, gevşek bir biçimde: ''Sen de kendini lapa gibi bırakma!'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9126, 'lapa vurmak', 'ağrıyı kesmek, iyileştirmek amacıyla lapa koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9127, 'larp diye', 'ansızın: Larp diye önümüze çıkıverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9128, 'lastik gibi', '1) çevik: ''Neme lazım lastik gibi kaleci.'' -H. Taner. 2) az pişmiş, sert (et).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9129, 'latife götürmek', 'şaka kaldırmak: ''Hiç latife götürecek hâlim yokken, kendimi tutamadım; kahkahaları atmaya başladım.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9130, 'latife latif gerek', 'şaka yaparken bile incelikten ayrılmamak gerek.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9131, 'lava etmek', '1) bir filikayı ilerletmek; 2) mec. birini çekiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9132, 'layığını bulmak', '1) dengini, yaraşır eşini bulmak; 2) hak ettiği cezayı bulmak: ''İster misin, Tayfun''un da gözüne ilişsin; layığını bulmuş diye sevinsin.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9133, 'layık görmek', 'yakıştırmak, uygun görmek: ''Ben işte oyum, şimdi söylemeye layık görmediğiniz Dikmen Yıldızı...'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9134, 'lazım gelmek (olmak)', 'gerekmek: ''Ayın parlaması için gece lazım olduğu, gibi, dehanın parlaması için çok defa bir felaket lazım gelir.'' -H. S. Tanrıöver. ''Nadiren kolunda, lazım oldukça kullanmak üzere bir pardösü bulunduğunu ancak fark ederdiniz.'' -İ. A. Gövsa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9135, 'leb demeden leblebiyi anlamak', 'daha söze başlanırken ne denmek istenildiğini çabucak anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9136, 'leb demeden leblebiyi anlamak', 'daha söze başlanırken ne denmek istenildiğini çabucak anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9137, 'leblebiden nem kapmak', 'en küçük bir olay veya davranıştan olumsuz etkilenmek: ''Leblebiden nem kapar.'' -F. Celâlettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9138, 'lehinde söylemek (bulunmak)', '1) iyiliğini söylemek; 2) hakkında iyi söz söylemek, desteklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9139, 'leke getirmek', 'yüz kızartacak, onur kıracak durumla karşılaşmaya yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9140, 'leke sürmek', 'birine onurunu sarsacak biçimde iftirada bulunmak, suç yüklemek, lekelemek: ''Annemi kıskanıyor, bana leke sürmek istiyor.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9141, 'leş gibi', '1) çok pis (yer); 2) rahatsız edici, ağır (koku); 3) tembel veya çok yorgun.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9142, 'leş gibi sarhoş', 'körkütük sarhoş, çok sarhoş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9143, 'leş gibi serilmek', 'kollarını bacaklarını yayarak kımıldamadan yatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9144, 'leyleği havada görmek', ' şaka çok gezmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9145, 'leyleği kuştan mı sayarsın, yazın gelir, kışın gider', 'sürekli olarak bir iş üzerinde durmayan, maymun iştahlı olan kişiye kimse güvenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9146, 'leyleğin attığı yavru', 'çevresinde yeteri kadar ilgi görmeyen kimse: ''Gözlerimi önüme indirmiştim, başım dönüyor, kulaklarım vınlıyordu. Ben, bilhassa ben, leyleğin attığı yavru.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9147, 'leyleğin ömrü (günü) laklakla geçer', 'boş, anlamsız konuşanların durumunu anlatmak için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9148, 'leylek gibi', 'zayıf ve uzun bacaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9149, 'lezzet almak', 'hoşlanmak: ''Uysal hayatımız daima gönlümün de aklımın da en lezzet aldığı meşgalesi.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9150, 'lezzetini çıkarmak', 'tadını çıkarmak: ''Lezzetini çıkara çıkara hikâyesine devam ediyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9151, 'ligden düşmek', 'kümeden düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9152, 'lige çıkmak', 'kümeye çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9153, 'limon gibi', 'sarı, çok sarı: ''Benzi limon gibi sararmaya, gözleri ateş gibi parlamaya başladı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9154, 'limon kabuğu gibi', ' tkz. küçük ve biçimsiz (şapka).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9155, 'limonata gibi', 'sıcak günlerde serin serin esen (hafif rüzgâr).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9156, 'lisana gelmek', 'konuşmaz şeyler konuşmaya başlamak, dile gelmek, dillenmek: Ağaç lisana gelerek...', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9157, 'liyakat göstermek', 'başarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9158, 'lodosa çevirmek (dönmek)', 'hava soğukken lodosla ısınmak: ''Hava öğleden beri lodosa çevirdiği için soğuk değildi.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9159, 'lodosun gözü yaşlı olur', 'lodos genellikle yağmur getirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9160, 'lokma (bile) çiğnenmeden yutulmaz', 'her iş emekle yapılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9161, 'lokma dökmek', '1) lokma tatlısı yapmak: ''Sonra arkasından lokma filan dökmek değil a, bir Yasin, bir Fatihacık bile okuyan bulunmaz.'' -Ö. Seyfettin. 2) konuk için yemek hazırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9162, 'lokma etmek', 'yemek yemek: ''Ben de uskumru aldım. Kendim kızartacağım. Ne olur kalın, beraber lokma edelim.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9163, 'lokma karın doyurmaz, şefkat artırır', 'bir kimseye verilen armağan, aradaki sevgiyi artırdığı için değerlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9164, 'lokma (lokması) ağzında büyümek', 'üzüntü veya iştahsızlık sebebiyle lokmasını yutamamak: ''Ağzımızda lokmalar büyürdü, muşambanın buz gibi teması âdeta ellerimizi yakardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9165, 'Lokman hekimin ye dediği', 'tadı güzel olan şeyler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9166, 'lokum gibi', '1) tatlı, güzel, yumuşak; 2) çok güzel (kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9167, 'lort gibi', 'rahat bir biçimde: ''Onların öyle lort gibi kurulup durduklarına bakmayın.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9168, 'lök gibi', 'bütün heybetiyle, ağırlığıyla (oturmak, çökmek).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9169, 'lügat paralamak', 'konuşma dilinde geçmeyen yabancı kelimeler kullanmak, ağdalı konuşmak: ''Deli eniştemiz, dil dökerek ve lügat paralayarak âlim görünmek isterdi.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9170, 'lüzum görmemek', 'gerekli bulmamak, gerekli görmemek: ''Bütün bunlardan bahsetmeye lüzum görmedim.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9171, 'lüzumsuz görmek', 'gereksiz bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9172, 'maaş bağlamak', 'aylık bağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9173, 'maaşa geçmek', 'aylığa geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9174, 'macera aramak', 'başına geleceklerden habersiz, sonu bilinmeyen, tehlikeli, heyecanlı bir işe girişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9175, 'maceraya atılmak', 'tehlikeli, yorucu, sıkıcı ve ne olacağı bilinmeyen bir işe kalkışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9176, 'maç satmak', 'karşılaşma sonucunu belirlemek amacıyla meşru olmayan yollardan veya para karşılığı anlaşmaya varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9177, 'maça beyi gibi kurulmak', 'saygısızca yayılarak oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9178, 'madalyonun ters tarafı (tersi, arka yüzü)', '''olumlu bir iş, bir durum veya bir olayın düşünülmesi, hesaba katılması gereken olumsuz yönü'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9179, 'madara etmek', 'kötü duruma düşürmek, yalanını, yanlışını çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9180, 'madara olmak', 'kötü duruma düşmek, yalanı, yanlışı ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9181, 'mağdur etmek', 'zarara uğratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9182, 'mağdur olmak', 'zarara uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9183, 'mahal kalmamak', 'gerek kalmamak, gereği olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9184, 'mahal yok', 'yeri, gereği yok: ''Otomobilin dinmeyen yaygarasını üstüne alınmaya mahal yoktu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9185, 'mahalle kahvesi gibi', 'havasız, gürültülü ve kalabalık (yer).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9186, 'mahalleyi ayağa kaldırmak', 'bağırıp çağırarak konu komşuyu tedirgin etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9187, 'maharet kazanmak', 'beceri edinmek, ustalaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9188, 'mahcup çıkarmamak', 'utandırmamak: ''Her yazdığımı tutan hocayı mahcup çıkarmamak için yazdıklarımı daha ciddi bir öz eleştiri eleğinden geçirir olmuştum.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9189, 'mahcup kalmak', 'utanmış olmak: ''Bu tekdir karşısında mahcup kalmak şöyle dursun, geniş geniş güldü.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9190, 'mahkeme açmak', 'mahkemede dava açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9191, 'mahkeme kadıya mülk değil', 'hiç kimse, bulunduğu kamu hizmetinde ömrünün sonuna kadar kalamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9192, 'mahkemeye düşmek', 'mahkemelik olmak: ''Korkma, ona zırnık vermem. İcap ederse mahkemeye düşeriz.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9193, 'mahkemeye vermek', 'dava açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9194, 'mahsur kalmak', '1) bir yerden çıkamamak; 2) kuşatılmak, sarılmak, çevrilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9195, 'mahşer gibi', 'çok kalabalık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9196, 'mahzur doğurmak', 'ortaya engel çıkarmak, sakınca yaratmak: ''Ne gibi mahzurlar doğurabileceğini görmemezlikten gelmek mümkün değildi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9197, 'mahzur görmek', 'sakıncalı bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9198, 'makara çekmek', 'ötücü kuşlar sürekli ötmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9199, 'makara gibi', 'aralıksız (konuşma).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9200, 'makaraları koyuvermek (zapt edememek, salıvermek)', ' tkz. kendini tutamayarak kahkahayla gülmeye başlamak: ''Dersin ciddiliğine bakmadan koyuverdik makaraları.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9201, 'makas almak', ' argo yanağı orta parmak ile işaret parmağı arasına alıp sıkıştırmak, makaslamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9202, 'makas değiştirmek', '1) tren yönünü değiştirmek; 2) mec. tuttuğu yol ve yöntemden vazgeçip başka bir yöne yönelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9203, 'makas vurmak', 'makasla kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9204, 'makbule geçmek', 'çok beğenilmek, hoşa gitmek, işe yaramak: ''O vakit o kış kıyamette / Ne kadar makbule geçer tatlı'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9205, 'makine çekmek', 'dikiş makinesinde dikmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9206, 'makine gibi', 'çok çabuk, art arda, aynı biçimde yapılan veya olan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9207, 'makine gibi adam', 'düzgün, çok ve çabuk iş çıkaran adam.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9208, 'makineli tüfek gibi', 'çok hızlı, birbiri ardınca: Makineli tüfek gibi konuşuyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9209, 'makineyi bozmak', ' şaka motoru bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9210, 'maksat gütmek', 'bir işi yaparken gizli amaç beslemek: ''Hiçbir maksat gütmeyerek yaptığım bu ufak tefek hizmetler boşa gitti.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9211, 'maksat hasıl olmak', 'amaca ulaşılmak, amaç gerçekleşmek: ''İmzanın arkasına saklanan adam dost, düşman her kim olursa olsun maksat hasıl olmuştu.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9212, 'maksat muhabbet olsun', 'dostların konu kıtlığında bile konuşacak şeyleri vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9213, 'maktul düşmek (olmak)', 'vurulup ölmek, öldürülmek, katledilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9214, 'mal adama hem dost, hem düşmandır', 'malın insana yararı olduğu gibi zararı da vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9215, 'mal bulmuş Mağribî gibi', '''büyük bir zenginliğe kavuşmuşçasına büyük sevinç ve coşku ile'' anlamında kullanılan bir söz: ''Başka bir gazeteci olsa bu fırsata mal bulmuş Mağribî gibi atlardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9216, 'mal canı kazanmaz, can malı kazanır', 'insan mal kazanacağım diye sağlığını tehlikeye atmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9217, 'mal canın yongasıdır', 'insan, malına gelen zarardan, canına gelmişçesine acı duyar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9218, 'mal da yalan mülk de yalan, var biraz da sen oyalan', 'bu dünya gelip geçicidir, mala mülke fazla değer vermemek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9219, 'mal etmek', '1) bir değer karşılığında sahip olmak: ''Sen şimdilik buna karşılık diyeceksin ki dün yüze mal ettiğin arsaları bine, bine olanları on binlere sattın.'' -Y. K. Karaosmanoğlu. 2) kendi malı, eseri, buluşu gibi benimsemek veya saymak: ''Nereden, kimden almış olursak olalım, bin yıldır onu kendimize mal etmişiz, benimsemişiz.'' -N. Ataç. 3) yüklemek, ait olduğunu göstermek: ''Başkalarından kazandığı iki çocuğu da bu zengin ihtiyara mal ederek mirasa sokacak.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9220, 'mal kaldırmak', 'ürün elde etmek: ''Kendisi şu kadarcık tarla sayesinde ancak akşamları bir kaşık sıcak çorba içecek kadar mal kaldırabiliyor.'' -N. Nâzım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9221, 'mal kapatmak', 'para karşılığında herhangi bir üretim alanındaki verimin sırf kendisine ayrılmasını sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9222, 'mal melameti örter', 'zenginlik, kişinin ayıplarını, kusurlarını kapatır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9223, 'mal meydanda', 'bir işin gizli bir yönünün olmadığını belirten bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9224, 'mal olmak', '1) bir şeye bir değer karşılığında sahip olmak: Bu kitap bana yirmi liraya mal oldu. 2) bir iş, bir davranış sonucu zarara uğramak: ''Babamın hayatta iken en çok sevdiği yemek, bütün bir senenin tasarrufuna mal olsa da o gece mutlaka pişerdi.'' -K. Bilbaşar. 3) bir yeri, bir şeyi benimsenmek: ''Gerçi Meclisimebusandaki bağımsızlarla hizipçiler henüz tamamıyla bu partiye mal olmamışlardı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9225, 'mal yapmak', 'servet sahibi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9226, 'malı götürmek', 'herkesin göz diktiği bir çıkarı elde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9227, 'malı ongun olanın adı angın olur', 'malından çok ürün alan kişinin adı her yerde anılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9228, 'malın iyisi boğazdan geçer', 'kişinin, yiyemediği malının bir değeri yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9229, 'malın iyisi suya yakın, daha iyisi eve yakın', 'çiftçinin toprağı suya ne kadar yakınsa değeri o kadar çok olur; bakımı, ürünün güvenliği ve eve kolay taşınabilmesi bakımından toprağın eve yakın olması daha da önemlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9230, 'malını yemesini bilmeyen zengin, her gün züğürttür', 'züğürt, yokluk içinde bulunduğundan yiyemez, varlık içinde olduğu hâlde yiyemeyen de bunun gibidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9231, 'malum değil', 'bilinmeyen konular için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9232, 'malum ya!', '''bilinen şey'' anlamında kullanılan bir söz: ''Bana gücenmeyiniz hanımefendi, malum ya, elçiye zeval yoktur.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9233, 'malumu ilam etmek', 'bilinen ve açık olan bir şeyi söylemeye, açıklamaya kalkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9234, 'mana çıkarmak', '1) yersiz bir yargıya varmak, yanlış değerlendirmek; 2) bir söze, söyleyenin aklından geçmeyen bir anlam vermek; 3) anlam çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9235, 'mana vermek', 'kendince bir yargıya varmak, yorumlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9236, 'manasına gelmek', 'anlamına gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9237, 'manaya gelmek', 'anlam bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9238, 'manda gibi', 'çok iri ve hantal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9239, 'manda gibi yayılmak', 'dikkatsizce ve bütün ağırlığıyla oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9240, 'manda gibi yemek', 'çok ve acele ile yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9241, 'mandepsiye basmak (düşmek)', 'aldatılmak, tuzağa düşürülmek: ''Karı kocaya benzemiyordu ya neyse beni adamakıllı mandepsiye bastırdılar.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9242, 'maneviyatı bozulmak', 'moral gücü sarsılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9243, 'maneviyatını kırmak', 'moral gücünü sarsmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9244, 'manevra yapmak', '1) bir araca istenilen hareketi yaptırmak. 2) ask. askerî birlikler savaş denemesi yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9245, 'mangal gibi yüreği olmak', 'cesareti çok olmak: ''Sen bana iyi baksana, bende mangal gibi yürek var!'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9246, 'mangalda kül bırakmamak', 'yapamayacağı işleri yapabilirmiş gibi söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9247, 'mâni düzmek (yakmak)', '1) mâni okumak; 2) müzik eşliğinde mâni söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9248, 'manken gibi', 'vücut ölçüleri düzgün ve ince olan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9249, 'mantar atmak', ' argo yalan söylemek, martaval atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9250, 'mantar gibi üremek', 'hızla çoğalıp yayılmak: ''Son zamanlarda mantar gibi üreyen yeni kahveci dükkânlarından birinde oturmuş, derin bir sohbete dalmışlardı.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9251, 'mantar gibi (yerden) bitmek', 'birdenbire veya kendiliğinden ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9252, 'mantara basmak', ' argo birinin hazırladığı oyuna düşmek, oyuna gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9253, 'manzara koymak', 'televizyon yayını sırasında beklenmeyen kesinti aralarını doldurmak için ekrana değişik manzara resimlerini getirip göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9254, 'mapusa düşmek', 'hapse girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9255, 'mapushaneye düşmek', 'hapse girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9256, 'maraza aramak', 'çekişmek, olay çıkarmak için bahane aramak: ''İkinci yarıda herkesin dili bir karış dışarı çıktığı, maraza aradığı, çamurlaştığı zaman, seninki, oyuna yeni girmiş gibi terütaze koşar durur.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9257, 'maraza çıkarmak', 'kavgaya yol açmak, kavga çıkarmak, anlaşmazlığa yol açacak işler yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9258, 'marifet göstermek', 'ustalığını ortaya koyabilmek: ''Bütün bu zahmetlere ... rağmen bari bir marifet gösterse idik.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9259, 'marifet iltifata tabidir', 'başarılı bir kimse, desteklenir, takdir edilir, övülürse daha iyi işler yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9260, 'Marmara çırası gibi yakmak (yanmak)', 'perişan etmek, mahvetmek veya perişan olmak, mahvolmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9261, 'mars etmek', '1) tavla oyununda karşısındakine hiçbir pul toplamaya fırsat vermeden kendi pullarını toplayıp oyunu kazanmak: ''İkinci oyunda beni mars etti, mantığımı ya da mantıksızlığımı kavramıştı çünkü.'' -T. Uyar. 2) tkz. karşısındakini söz söyleyemeyecek duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9262, 'mars olmak', '1) tavla oyununda pul toplamaya fırsat bulamadan oyunu kaybetmek; 2) tkz. söz söyleyemeyecek duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9263, 'marsık gibi', 'koyu esmer, kömür gibi, simsiyah.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9264, 'mart ayı dert ayı', 'mart ayında havalar sık sık değiştiği için insan kendisini koruyamaz ve hasta olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9265, 'mart çıkmadıkça dert çıkmaz', 'kış hastalıkları, mart sona ermedikçe bitmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9266, 'mart dokuzunda çıra yak, bağ buda', 'mart ayının dokuzunda bağların kesinlikle budanması gerekir, bu iş gündüz yetiştirilemezse gece çıra ışığında yapılmaya değecek kadar önemlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9267, 'mart havası gibi', 'kararsız, huysuz (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9268, 'mart içeri, pire dışarı', 'tedirgin edici biri geldiğinde gitmeye kalkan kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9269, 'mart kapıdan baktırır, kazma kürek yaktırır', 'mart ayındaki şiddetli soğuklarda insanlar ellerine geçen her şeyi yakmak zorunda kalırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9270, 'mart kedisi gibi', 'çapkın ve azgın olan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9271, 'mart kuruluk, nisan yağmurluk', 'herkes, ortam ve koşulların işine uygun olmasını bekler veya ister.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9272, 'martaval atmak (okumak)', 'inanılmayacak sözler söylemek, yalan söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9273, 'martta tezek kuruya, nisanda seller yürüye', 'herkes, ortam ve koşulların işine uygun olmasını bekler veya ister.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9274, 'martta yağmaz, nisanda dinmezse sabanlar altın olur', 'kara kışta kar yağar, martta yağış olmaz, nisanda da çok yağmur yağarsa o yıl bol ürün alınır; çiftçinin yüzü güler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9275, 'maruz bırakmak', 'bir olayın veya bir durumun etkisinde bırakmak: ''Türk Cumhuriyeti varlığını, istikbalini safsatalarla tehlikeye maruz bırakamaz.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9276, 'maruz bulunmak (olmak)', 'bir olayın veya bir durumun etkisinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9277, 'maruz kalmak', 'bir olay veya bir durumla karşı karşıya olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9278, 'masal âleminde yaşamak', '1) gerçek olmayan, gerçekleşmesi güç olan şeyler düşünerek yaşamak; 2) masallardaki gibi olağanüstü güzel anlar yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9279, 'masal gibi', 'olmayacak biçimde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9280, 'masal okumak (anlatmak)', 'inandırıcı olmayan, oyalayıcı sözlerle kandırmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9281, 'masaya oturmak', 'bir anlaşmazlığı çözümlemek üzere bir araya gelmek, toplanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9282, '(...-masıyla ...-mesi) bir olmak', 'çabucak olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9283, 'maskara etmek', '1) bir kimseyi veya şeyi gülünç ve şerefsiz duruma düşürmek: ''Şu kendini bütün memlekete maskara eden münevver züppenin eksik tarafı millî şuurdan başka nedir?'' -O. S. Orhon. 2) bir şeyi bozmak, berbat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9284, 'maskara olmak', 'gülünç bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9285, 'maskesi düşmek', 'gerçek niyeti ve niteliği ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9286, 'maskesini atmak', 'amaçlarını gizlemesini bilen kimse, bu tutumunu bırakarak gerçek kişiliğini ve amaçlarını açığa vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9287, 'maskesini düşürmek (kaldırmak)', 'gizli amaçlarını, gerçek kişiliğini ortaya çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9288, 'masraf etmek', 'para harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9289, 'masraf görmek', 'alışveriş veya ödeme işlerini yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9290, 'masraf kapısı açmak', 'para harcamayı gerektiren bir işe girişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9291, 'masrafa girmek', 'bir iş veya yapım için çok para harcamak: ''Bir adamın hiç tanımadığı insanlar için bu kadar zahmet ve masrafa girmesine bir türlü akıl erdiremiyorum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9292, 'masrafı çekmek', 'bir iş için gereken parayı ödemek, gideri karşılamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9293, 'masraftan çıkmak', 'beklenmedik bir sırada para harcama durumunda kalmak, paradan çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9294, 'mastor olmak', 'esrar içerek kendinden geçmek: ''Esrarcılar boğula boğula öksürürler, duman içinde kalırlar, duman olurlar. Buna mastor olurlar demek daha uygundur.'' -S. Sema.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9295, 'maşa gibi', 'zayıf ve kuru (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9296, 'maşa kadar', 'çok küçük doğan (çocuk).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9297, 'maşa varken elini ateşe sokma', 'başka birine yaptırabileceğin tehlikeli işe kendin girişme.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9298, 'maşa varken elini yakmak', 'bir işten gelebilecek zarardan kendini koruyacak bir yol varken o yolu tutmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9299, 'maşalık etmek', 'başkalarının çıkarı, isteği ve amaçları doğrultusunda çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9300, 'maşallahı var', 'bir kimsenin veya bir şeyin iyi bir durumu anlatılırken söylenen bir söz: Çocuğun bugün maşallahı var, hiç huysuzluk etmedi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9301, 'mat etmek', '1) satranç oyununda yenmek: ''İki kişiyi birden satrançta mat ettim.'' -A. Gündüz. 2) bir tartışma sonunda karşısındakini cevap veremez duruma düşürmek: ''Başkaları onları mat etmeden onlar kendi çelişkileri ile kendilerini çelmeliyorlardı.'' -H. Taner. 3) kötü duruma düşürmek, bozmak: ''Başka bir çocuk arabasındaki hafif cümbüşü birdenbire bu araba mat etti.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9302, 'mat olmak', '1) satranç oyununda yenilmek; 2) bir tartışma sonunda veya benzeri bir durumda yenik düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9303, 'matem tutmak', 'yas tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9304, 'matiz olmak', ' argo sarhoşluktan sızacak duruma gelmek: ''Bak şu moruğa matiz oluyor gene.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9305, 'matrağa almak', 'alaya almak, eğlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9306, 'matrak geçmek', ' argo alay etmek, eğlenmek: ''Matrak mı geçiyorsun benimle?'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9307, 'maval okumak', 'yalan söylemek, yalan söyleyerek oyalamak, masal okumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9308, 'mavi boncuk dağıtmak', 'birçok kişiye birden sevgi göstermek ve söz konusu kişileri, bu sevginin yalnız kendisine verildiğine inandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9309, 'mavra atmak (sıkmak)', '1) gevezelik etmek; 2) palavra atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9310, 'maya çalmak', 'mayalanmayı sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9311, 'mayasında olmak', 'içinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9312, 'mayasız yoğurt tutmaz', 'çok para kazanabilmek için az da olsa elde bir sermaye olması gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9313, 'maydanoz olmak', 'olur olmaz her işe karışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9314, 'mayın dökmek (döşemek)', 'denize mayın bırakmak, denizi mayınlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9315, 'maymun gibi', '1) tuhaf, gülünç hareketler yapan; 2) taklitçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9316, 'maymun gözünü açtı', 'geçen bir olaydan ders alındığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9317, 'maymuna dönmek', '1) çirkin ve gülünç duruma girmek; 2) uslanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9318, 'mayna etmek', '1) herhangi bir şeyi halat ve palanga aracılığıyla denize veya yere indirmek: ''... filikalarını mayna etmişlerdi.'' -A. İlhan. 2) mec. fırtına yatışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9319, 'maytap geçmek', 'biriyle alay etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9320, 'maziye karışmak', 'geçmişte kalmak, yürürlükten ve işlerlikten çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9321, 'mazlumun ahı, indirir şahı', 'zulüm gören kimsenin bedduası tutar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9322, 'mazlumun ahı yerde kalmaz', 'zulüm gören kimsenin bedduası tutar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9323, 'mazur görmek', 'kusura bakmamak, hoş görmek, bağışlamak, affetmek: ''Büyük işler deruhte etmemiş insanların, bu husustaki tereddütlerini mazur görmelidir.'' -Atatürk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9324, 'mecali (mecal) kalmamak', 'güç kalmamak, güçsüzleşmek: ''Artık ne yürümeye ne de ayak üstünde durmaya mecali kalmıştı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9325, 'mecalsiz düşmek', 'güçsüzleşmek, takati kalmamak: ''Bir aralık kadının mecalsiz düştüğünü fark ettiler.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9326, 'mecbur kalmak (olmak)', 'herhangi bir şeyi yapmak zorunda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9327, 'meclis kurmak', 'birkaç kişi konuşmak veya eğlenmek için toplanmak: ''Nihayet bir akşam bütün ihtiyarlar, kadın erkek meclis kurar, ahenge başlarlarmış.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9328, 'mecrası değişmek', 'bir iş, bir olay için gidişi, yönü, doğrultusu değişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9329, 'medar olmak', 'yardımı, yararı dokunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9330, 'medet ummak (beklemek)', 'yardım beklemek: ''Emin ol ki dağınık ve kasvetli bir cemiyet içinde aşktan bile medet ummayız.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9331, 'mehil vermek', 'süre tanımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9332, 'mehtaba çıkmak', 'ay ışığında gezip dolaşmak: ''O gece mehtaba çıkmak için bir hayli evvelinden başlayan tatlı bir hazırlık devresi vardı.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9333, 'mekân tutmak', 'bir yere yerleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9334, 'mekik atmak', '1) mekiği arışlar arasından hızla geçirmek; 2) mec. hiçbir yerde duramayıp iki yer arasında gidip gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9335, 'mekik dokumak', 'iki nokta veya durum arasında sürekli gidip gelmek: ''Görüntümüz iyi ile kötü arasında mekik dokudu.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9336, 'mekik gibi', 'sürekli gidip gelen.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9337, 'mektebi asmak', 'derslere girmemek için keyfî olarak okula gitmemek, okulu asmak: ''Güzel havada mektebi asamamış bir ilkokul öğrencisi somurtkanlığı ile kafileye katıldım.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9338, 'mektep görmemiş', '1) okula gitmemiş; 2) tkz. kaba, saygısız.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9339, 'mektep medrese görmüş', 'okumuş, öğrenim görmüş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9340, 'mektepten çıkan eşek Marsıvan''dan çıkmaz', 'öğrenim görmüş olsalar bile bazıları eğitilmemiş gibi davranabilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9341, 'mektubu dışından okumak', 'bir kimsenin içinden geçeni yüz çizgilerinden anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9342, 'mektup atmak', 'mektubu postaya vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9343, 'melek gibi', '1) sessiz, sakin: ''Annem melekler gibi iyi kadındır.'' -S. F. Abasıyanık. 2) güzel: ''Kimi tarafları pek ince delikanlılar melek gibi kızları övüyorlardı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9344, 'meme vermek', 'emzirmek: ''Genç kadın arkasını dönerek göğsünü açtı ve özenle meme vermeye başladı.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9345, 'meme yapmak', ' tek. motorlu araçlarda platin elektrik akımını geçirmeyecek ölçüde oksitlenmek, işlevini yapmaz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9346, 'memede olmak', 'henüz meme ile beslenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9347, 'memeden kesmek', 'artık emzirmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9348, 'mendil atmak', 'herhangi bir duyguyu, gizli bir mesajı haberleşilen insana çeşitli anlamları olan renkli mendille bildirmek: ''Pencereyi açıp gözünün önünde oyalı yeşil mendil mi atacağım Ferit''e?'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9349, 'mendil kadar', 'çok küçük (alan): ''Mendil kadar olsun tarlamızı ayır/ Beni doyuracak ağacı göster'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9350, 'mendil sallamak', 'birini uzaktan mendil sallayarak selamlamak veya uğurlamak: ''Arabalar yaklaşıyor, mendil sallayalım mı?'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9351, 'menfaat gütmek', 'çıkarını ön planda tutmak: ''Hayatımda hiçbir zaman menfaat gütmedim, paragözlü olmadım.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9352, 'menopoza girmek', '1) âdetten kesilmek, doğurma özelliğini yitirmek, hayızdan nifastan kesilmek: ''Yakışmıyordu bu hafiflik çoktan menopoza girmiş bir kadına.'' -İ. Aral. 2) mec. bu sebeplerden dolayı kadın bunalım içerisinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9353, 'menzil atmak', ' tar. ok atış yarışmalarında rekor kırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9354, 'menzil beygiri gibi koşmak', 'durup dinlenmeden çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9355, 'menzil dikmek', ' tar. atılan ok ile kırılan rekorun yerini belirten taş dikmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9356, 'merak getirmek', 'kara sevdaya tutulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9357, 'merak insanı mezara sokar', 'her şeyi öğrenme merakı, insanı birtakım tehlikelerle karşı karşıya getirebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9358, 'meraka düşmek', '1) meraklanmak: ''Sevecek birini görse bile acaba daha güzeli bulunmaz mı diye meraka düşer.'' -S. Birsel. 2) kaygılanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9359, 'meraka sokmak', 'meraklandırmak: ''Sizleri meraka sokmamı aranızda bulunmamın sevincine bağışlayın.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9360, 'merakına dokunmak', 'ilgisini çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9361, 'merakından çatlamak', '1) çok kaygılanmak; 2) bir şeyi öğrenmek isteğini aşırı ölçüde duymak: ''Rica ederim söyleyiniz, merakımdan çatlayacağım.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9362, 'merakını mucip olmak', 'merakına dokunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9363, 'merakını uyandırmak', 'merak etmesine sebep olmak, meraklanmak: ''Kızın en çok merakını uyandıran şey, Hasan''ın yeni kıyafetiydi.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9364, 'merakta bırakmak', 'kaygı içinde bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9365, 'merakta kalmak', 'kaygı içinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9366, 'meraktan çatlamak', 'merakından çatlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9367, 'meraktan ölmek', 'çok kaygılanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9368, 'meram etmek', 'üstüne düşmek, yapmak istemek: ''İşte o, meram ettiği zaman etrafındakilere böyle tahakküm ederdi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9369, 'meram (meramını) anlatmak', 'isteğini, derdini anlatmak: ''Gözlerini siyasi ihtiraslar bürüyen kimselere meram anlatmak mümkün olmamıştı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9370, 'meramın elinden bir şey kurtulmaz', 'bir şey yapmaya azmeden ve ona dört elle sarılan kişi, kesinlikle başarıya ulaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9371, 'mercek altına almak', 'çok titizlikle ve etraflıca incelemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9372, 'mercimeği fırına vermek', ' tkz. kadınla erkek gizlice aşk ilişkisi kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9373, 'mercimek kadar', 'çok küçük ve yuvarlak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9374, 'merdiven ayak ayak (basamak basamak) çıkılır', 'en yüksek mevkiye yavaş yavaş yükselerek çıkılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9375, 'merdiven dayamak', 'ileri bir yaşa yaklaşmak: ''Elliye merdiven dayadık, ötesine de geçtik.'' -N. Araz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9376, 'merhaba çakmak', ' hlk. selamlamak: ''Kişileri kendi adıyla anmıştım, ona buna, yardımına koşan bunca kişilere bir merhaba çakayım diye.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9377, 'merhaba etmek', 'hâl hatır sormak, görüşüp konuşmak: ''Bir oturun bakalım, bir merhaba edelim!'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9378, 'merhamet duymak', 'acıma veya şefkat duygusu uyanmak veya kabarmak: ''Ömrümde hiç kimseye bu kadar saf ve derin merhamet duymamıştım.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9379, 'merhamete gelmek', 'acıma duygusuna kapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9380, 'merhametten maraz doğar (hasıl olur)', 'kimi kişiler, kendilerine acıyıp iyilik edenlerin başını derde sokarlar veya bu iyiliği kötüye kullanırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9381, 'merhem olmak', 'bir derde çare olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9382, 'mermer gibi', 'beyaz, parlak, sert, sağlam ve pürüzsüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9383, 'mermer iyi taştan, iyilik iki baştan', 'birbiriyle ilişkileri bulunan iki kişinin iyi geçinebilmeleri için yalnızca birinin iyi olması yetmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9384, 'mesafe almak', 'bir konuda veya çalışmada önemli ölçüde ilerlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9385, 'mesafe bırakmak (koymak)', 'ilişkilerde samimi olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9386, 'mesafe katetmek', 'yol almak, ilerlemek: ''Tam mesafe katettiğimiz zaman bizi nasıl yarı yolda bırakırsın?'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9387, 'mesai yapmak', 'bir iş yerinde, yasal günlük iş süresi dışında ek bir ücretle fazla çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9388, 'mesaiye kalmak', 'mesai yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9389, 'mesaj bırakmak', 'yazı veya sözle bilgi vermek: ''Giderken ona bir mesaj bırakmamış, haber de vermemiş.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9390, 'mesaj vermek', 'duygu ve düşünceleri karşı tarafa dolaylı bir biçimde anlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9391, 'mescite gerek olan meyhaneye haramdır', 'insanın kendisine gerekli olan bir şeyi yabancıya vermesi doğru değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9392, 'mesele çıkarmak', 'sorun çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9393, 'mesele yok!', '''herhangi bir sıkıntı, güçlük yok!'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9394, '(...-mesi) an meselesi', 'olması her an mümkün, sürekli gerçekleşebilecek durumda: Dayımların gelmesi an meselesi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9395, '(...-mesi) gün meselesi', 'olması her an mümkün, sürekli gerçekleşebilecek durumda: Kovulması gün meselesi olduğu için usta bir taraftan sıkıştırıyor, patron bir taraftan sıkıştırıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9396, 'mesken tutmak', 'yerleşmek: ''Yârim İstanbul''u mesken mi tuttun / Gördün güzelleri beni unuttun'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9397, 'meskûn kılmak', 'bir yeri şeneltmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9398, 'meskût geçmek', 'söylemeden geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9399, 'meskût kalmak', 'konuşulmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9400, 'mesleğinin eri (erbabı) olmak', 'işinin uzmanı veya ustası olmak: ''Mesleğimin eri olduğumu takdir edersiniz.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9401, 'mesnetsiz atmak', 'dayanağı olmadan konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9402, 'mesul tutmak', 'sorumlu görmek: ''Suçuna bakmadan bir de bu vaziyetten beni mesul tutmak istiyor, kabahati bana yüklüyor.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9403, 'mesuliyet almak', 'sorumluluk almak: ''Üstümüze ağır bir mesuliyet aldık.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9404, 'meşakkat çekmek', 'güçlüklerle karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9405, 'meşakkate katlanmak', 'güçlüğe, sıkıntıya dayanmak, göğüs germek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9406, 'meşale çekmek', 'önderlik etmek, önayak olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9407, 'meşin gibi', '1) kararmış ve sertleşmiş (insan derisi); 2) iyi pişirilmeyip çiğ kalmış (et).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9408, 'meşk almak', 'ders almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9409, 'meşk vermek', 'ders vermek: ''Esasen hemen onun meşk vereceği kızları getirmiş, ona takdim etmişti.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9410, 'meşru sayılmak', 'geçerli bulunmak: ''Meşru sayılan adilik ve faziletsizliklerden hiçbiri onda yoktu.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9411, 'metanet göstermek', 'kötü bir duruma katlanmak, dayanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9412, 'meteliğe kurşun atmak', 'parası kalmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9413, 'metelik etmez', '''çok değersiz'' anlamında kullanılan bir söz: ''Gözümde, milyonu olsa da kalp para ile metelik etmez.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9414, 'metelik vermemek', 'değer ve önem vermemek, umursamamak, aldırış etmemek: ''Orayı gördükten sonra ben, gayri dünyanın hiçbir tarafına metelik vermem.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9415, 'methiye düzmek', 'övmek, övgü şiiri yazmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9416, 'metres tutmak', 'metresle yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9417, 'mevkisi olmak', 'bir işte önemli bir makamda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9418, 'mevlasını bulmak', 'istediğine erişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9419, 'mevzuya girmek', 'asıl konuyu ele almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9420, 'meyanesi gelmek', 'helva vb. kıvamına gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9421, 'meydan açmak', 'sebep olmak: ''Bu hareket, daha ileride kim bilir ne boğuşmalara meydan açacaktır?'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9422, 'meydan almak', ' esk. gelişmek, yayılmak, geniş ölçüde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9423, 'meydan bırakmamak', 'fırsat vermemek: ''Ona ağız açmaya meydan bırakmadım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9424, 'meydan (birine veya bir şeye) kalmamak', 'fırsat bulamamak: ''Bu beladan kurtulabilmek için bir çare düşünmeye meydan kalmadan Ali, bir gece kasabaya girdi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9425, 'meydan bulamamak', 'fırsat bulamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9426, 'meydan dayağı atmak', 'kalabalık içinde iyice dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9427, 'meydan dayağı yemek', 'kalabalık içinde iyice dayak yemek: ''Vallahi meydan dayağı yesem bu kadar perişan olmazdım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9428, 'meydan okumak', 'korkmadığını, çekinmediğini açıkça bildirmek, kavga veya yarışmaya çağırmak: ''Hülasa yüz türlü yüzmek bilir, dalgıçlara meydan okurdu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9429, 'meydan vermemek', 'kötü bir durumun gerçekleşmesi için imkân veya zaman bırakmamak: ''Sonra sultanın cevabına meydan vermeden döndü.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9430, 'meydana atmak', 'ortaya çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9431, 'meydana çıkarmak', '1) açıklığa kavuşturmak, ortaya çıkarmak, belli etmek: ''Marifetlerini birer birer meydana çıkarıyor.'' -R. H. Karay. 2) bularak ortaya çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9432, 'meydana çıkmak', '1) ortaya çıkmak, görünmek: ''İşte Galip, böyle bir muhitte herkesi şaşırtan büyük bir kabiliyetle meydana çıkıverdi.'' -A. H. Çelebi. 2) belli olmak: ''Şafak serinliği içinde onun yükselmesini seyrederken ilk tahminimizde yanılmadığımız meydana çıktı.'' -H. S. Tanrıöver. 3) yetişmek, büyümek: ''Altınyaprak Şirketi bizim son ekmek kapımızdı, bundan sonra iş bulabileceğim şüpheli, kardeşlerim daha meydana çıkmış sayılmaz.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9433, 'meydana dökmek', 'hepsini sergilemek, ortaya dökmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9434, 'meydana düşmek', 'bir iş yapmak için kendini ortaya atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9435, 'meydana gelmek', '1) olmak, oluşmak: ''Kum tanelerinden meydana gelen yazıları okumaya çalışan Bünyamin bir hayli zorlandı.'' -İ. O. Anar. 2) ortaya çıkmak: ''Müspet ve realist ilmî araştırmaların meydana gelebilmesi için istatistik bir zarurettir.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9436, 'meydana getirmek', 'olmasını sağlamak, oluşturmak: ''Yaşlandıkça gençleşen bir adam yalnız verdiği eserlerden değil bundan sonra meydana getireceklerinden dolayı mühimdir.'' -İ. A. Gövsa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9437, 'meydana koymak', 'yapıp ortaya çıkarmak, göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9438, 'meydana vurmak', 'belli etmek, ortaya çıkarmak: ''Beşikten beri ruhlarına akıtılan düşmanlığı meydana vurmak için tam fırsatı bulmuşlardı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9439, 'meydanda bırakmak', '1) açıkta, evsiz barksız bırakmak; 2) ortada, herkesin gözü önünde bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9440, 'meydanı (birine veya bir şeye) bırakmak', '1) savunduğu şeyden vazgeçmek: ''Çok güzel görünen bir şey var ki o da iki tarafın da meydanı bırakıp kaçmamalarıdır.'' -M. Ş. Esendal. 2) yarışmadan çekilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9441, 'meydanı boş bulmak', 'kendisini engelleyecek kimse görmeyerek aşırı davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9442, 'meyhaneciden şahit istemişler, bozacıyı göstermiş', 'uygunsuz iş yapan kimse, haklı olduğunu göstermek için kendisine benzeyen birini tanık gösterir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9443, 'meyhanecinin yüzünü bayram topu güldürür', 'yasak yüzünden işi aksamış kimse yasağın kalkmasına sevinir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9444, 'meyil vermek', '1) eğiklik sağlamak; 2) mec. ilgi göstermek, gönül vermek: ''Her dilbere meyil verme / Ya sevilir ya sevilmez'' -Erzurumlu Emrah.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9445, 'meyve almak', '1) ürün elde etmek; 2) mec. yarar elde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9446, 'meyve veren ağaç taşlanır', 'bilgili, hünerli, işinde başarılı olan kimseler kıskanılır, eleştirilir ve işlerini yapmaları zorlaştırılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9447, 'meyve vermek', '1) ürün vermek; 2) mec. bir eser ortaya çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9448, 'meyveli ağacı taşlarlar', 'bilgili, hünerli, işinde başarılı olan kimselere genellikle sataşılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9449, 'meyveye durmak', 'meyve verecek duruma gelmek: ''Oğlu Hakan''ın doğduğu yıl meyveye duran dut, en doğurgan dönemindeydi.'' -M. Uyguner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9450, 'mezada çıkarmak (koymak)', 'açık artırma yoluyla bir malı satışa çıkarmak: ''Nesi var nesi yoksa toplar, buraya getirir, mezada koyardı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9451, 'mezar taşı ile övünülmez', 'kişi geçmişteki atalarıyla değil ancak kendi değeri ile övünmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9452, 'mezardan çıkarmak', 'bir kimseyi ölümden kurtarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9453, 'mezarını kazmak', 'kötülüğünü istemek, kötü duruma düşürmek için uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9454, 'mıncığı çıkmak', 'ezilerek içi dışına çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9455, 'mır mır etmek', '1) ''mırıldanma'' sesi çıkarmak; 2) kendi kendine söylenip durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9456, 'mırın kırın etmek', 'bir isteği yerine getirmemek için çeşitli sebepler ileri sürmek, nazlanmak: ''Sorulsa, nasıl bir hayatı seviyorsun, diye, mırın kırın eder, hiçbir şey anlaşılmaz.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9457, 'mısır püskülü gibi', 'seyrek, ince ve cansız (saç).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9458, 'Mısır''daki sağır sultan bile duydu', 'duymayan kalmadı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9459, 'mısıra ''yağmur geliyor'' demişler, ''çapan birlik mi?'' demiş', 'mısır bol su ister ama çapalanmazsa sudan gereği gibi yararlanılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9460, 'mışıl mışıl uyumak', 'rahat, sessiz ve derin soluk alarak uyumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9461, 'mızrak çuvala girmez (sığmaz)', 'gizli tutulması imkânsız durumlar karşısında söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9462, 'miadı dolmak', 'bir şeyin kullanım süresi bitmek, eskimek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9463, 'miadı gelmek', 'zamanı gelmek: ''Bakkalın veresiyeyi kestiği bir gün, artık bu işin miadı geldiğine kanaat ederek satış hususunda ısrar edecek olmuş.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9464, 'mide bulandırmak', '1) kusacak bir duruma getirmek: ''Dibinde, kıyılmış kertenkele ve yılan parçaları varmış gibi midesini bulandırmıştı.'' -P. Safa. 2) mec. kuşkulandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9465, 'mide fesadına uğramak', 'çok ve çeşitli yiyecekler yemekten midesi bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9466, 'midesi almamak (kaldırmamak, kabul etmemek, götürmemek)', '1) hastalık, tiksinme vb. sebeplerle bir şeyi yiyememek; 2) mec. çirkin bir şey karşısında huzursuz olmak, rahatı kaçmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9467, 'midesi bulanmak', '1) kusacak gibi olmak; 2) mec. iğrenmek, tiksinmek; 3) mec. kuşkulanmak, işkillenmek; 4) mec. huzursuz olmak, rahatı kaçıp tedirgin olmak, hoşlanmamak: ''Bu rahatlık, bolluk, ferahlık havasına esir ticareti, sömürgecilik gibi kokular karışınca insanın midesi bulanıyor.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9468, 'midesi ekşimek (kaynamak, yanmak)', 'yeni yenilmiş yiyeceklerden ötürü midede rahatsızlık duymak: ''Şu midesi ekşimese hayat da ekşimeyecek onun için, ama ne yapsın?'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9469, 'midesi ezilmek (kazınmak)', 'açlık duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9470, 'mideye indirmek', '1) yemek içmek; 2) mec. haksız yere, kanunsuz olarak para kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9471, 'mideye oturmak', 'yenilen şey sindirilmeyip mideye rahatsızlık vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9472, 'mideyi bastırmak', 'hafif şeyler yiyerek açlığını gidermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9473, 'mihenge vurmak', 'denemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9474, 'mihnet çekmek', 'sıkıntılı bir duruma katlanmak, sıkıntı çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9475, 'mikrofona koymak', 'hikâye, roman, oyun vb. eserleri radyo için elverişli duruma getirip yayımlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9476, 'mikroskop altına koymak (almak)', 'en ince noktasına kadar araştırmak, didik didik edip incelemek: ''Başkalarını nasıl mikroskop altına koydunsa kendini de öylece koy!'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9477, 'mil yapmak', 'yol yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9478, 'milim oynamamak', '1) ölçüsüne tam olarak uygun düşmek; 2) hiç kıpırdamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9479, 'milim şaşmamak', 'tam denk düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9480, 'milletin ağzı torba değil ki büzesin (dikesin)', 'başkalarının söyleyeceklerine engel olamazsınız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9481, 'mim koymak (yapıştırmak)', '1) unutulmaması için işaret koymak; 2) önemli bularak üstünde ısrarla durmak: ''Bu lafıma mim koy, dedi, Sabri Bey.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9482, 'minare de doğru, ama içi eğri', 'doğru görünen nice kişiler vardır ki içyüzlerini bilenlerden nasıl düzenbaz oldukları öğrenilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9483, 'minare gibi', 'çok uzun.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9484, 'minareyi çalan kılıfını hazırlar', 'kolay kolay gizlenemeyecek kadar büyük bir yolsuzluğu yapan kimse, sorumluluktan kurtulma yollarını önceden düşünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9485, 'minareyi yaptırmayan yerden bitmiş sanır (bitti beller)', 'önemli iş yapmamış olanlar, yapılmış önemli işleri kendiliğinden oluvermiş sanırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9486, 'minder çürütmek', '1) işsiz, güçsüz oturmak; 2) bir yerde uzun süre oturmak; 3) otururken yapılan işlerle uzun yıllar uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9487, 'minderden kaçmak', '1) güreşte oyuna katılmamak; 2) güreşte oyun sırasında minderin dışına çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9488, 'minnet altında kalmamak', 'birinin iyiliğine karşı kendini borçlu durumdan kurtarmak için karşılık olarak bir iyilikte bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9489, 'minnet duymak', 'birinin iyiliğine karşı kendini ona borçlu saymak: ''Bana karşı gösterilen bu güven ve sevgiden dolayı çok minnet duymama rağmen, siyasi hayata atılmak istemiyordum.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9490, 'minnet etmemek', 'boyun eğmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9491, 'minnettar kalmak', 'birinden görülen iyiliğe karşı teşekkür duygusu beslemek: ''Kendine minnettar kalan ahalinin elleri üzerinde geziyor.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9492, 'miras helal hele (ele) al demişler', 'miras, alabildiği takdirde mirasçının hakkıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9493, 'miras yemek', '1) kendine miras kalmak: ''Erkek çocuk ne kadar miras yerse kız çocuk da o kadar miras yer.'' -F. R. Atay. 2) kendine kalan mirası tüketmek: ''Son zamanlarda İzmir''deki gazinocu bir amcasından beş bin liralık bir de miras yemişti.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9494, 'mirasa konmak', 'bir kimseye önemlice bir kalıt kalmak: ''Bizimkiler okkalı bir mirasa konmuşlar da o hergelenin hakkını mı yemişler ne?'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9495, 'mirî malı balık kılçığıdır, yutulmaz', 'devlet malı haksız olarak kendine mal edildiğinde rahatça kullanılamaz ve günün birinde hesabı sorulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9496, 'mis gibi', '1) çok güzel: ''Orada çamlar mis gibi kokarak rüzgârla fısıldaşırlardı.'' -Halikarnas Balıkçısı. 2) elbette: ''Ben mis gibi tornacıyım, sanatımı bırakamam.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9497, 'misafir gibi oturmak', '1) bulunduğu yerden her an ayrılacakmış gibi eğreti, üstünkörü oturmak; 2) mec. hiç iş yapmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9498, 'misafir kalmak', 'bir yerde yiyip içmek, yatmak ve konuk olarak ilgi görmek: ''Cemile sekiz, on gün çiftlikte misafir kalacaktı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9499, 'misafir kısmeti ile gelir', 'misafirin geldiği evde ya yiyecek bulunur veya beklenmedik bir yerden o sırada yiyecek gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9500, 'misafir misafiri (dilenci dilenciyi) istemez (sevmez), ev sahibi ikisini de', 'misafir, bütün ağırlamaların yalnız kendisi için olmasını istediğinden gittiği yere başka bir misafirin gelmesini istemez; ev sahibi de hiç misafir gelmese de rahatım bozulmasa diye düşünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9501, 'misafir on kısmetle gelir, birini yer dokuzunu bırakır', 'Tanrı, misafirin yediğinden kat kat fazlasını, misafir ağırlıyor diye ev sahibine verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9502, 'misafir umduğunu değil bulduğunu yer', 'konuk, ev sahibinin kendisine çok şeyler ikram etmesini bekleyebilir ama ev sahibi ancak evinde olanları ikram edebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9503, 'misafir üç gün misafirdir', 'misafir bir yerde üç gün kalırsa ne ev sahibi bunu fazla bulur ne de misafir uzun kaldım diye üzülür ama üç günden sonrası her ikisi için de sıkıcı olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9504, 'misafirin umduğu ev sahibine iki öğün olur', 'misafir, ev sahibinin kendine çok şeyler ikram edeceğini umar ama beklediklerini bulamayabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9505, 'misafirlik üç gündür', 'misafir bir yerde üç gün kalırsa ne ev sahibi bunu fazla bulur ne de misafir uzun kaldım diye üzülür ama üç günden sonrası her ikisi için de sıkıcı olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9506, 'misal göstermek', 'örnek vermek: ''Çocuklarıma beni misal gösterdiğini, ağzım kulaklarıma vararak öteden beriden işitiyordum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9507, 'misk gibi', 'mis gibi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9508, 'misk yerini belli eder', 'değerli kişi nerede olsa varlığını gösterir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9509, 'misli menendi yok', 'benzeri, eşi yok: ''Bu sonbahar sabahında Gülhane Parkı''nın misli menendi yoktur.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9510, 'mitralyöz gibi', 'durmadan, ara vermeden (konuşma).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9511, 'miyancının kesesi bol olur', 'iki kişi arasında uzlaştırıcılık yapan kimse anlaşmaları kolay olsun diye bir taraf lehine, öbür taraf aleyhine bol keseden fedakârlıkta bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9512, 'modası geçmek', '1) moda olmaktan çıkmak; 2) mec. önemini yitirmek, geçersiz duruma gelmek, artık aranmamak: Bu işin modası geçti, onu vaktiyle yapmak gerekti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9513, 'model çıkarmak', '1) kumaş kesiminden önce kâğıt vb. malzeme üzerine parçanın örneğini hazırlamak; 2) mec. bir şeyi vurarak izini çıkarmak: ''Bir tuğla işçisi kerpiç kalıbını kapmış, karısının sırtında model çıkarmış, kadın ciyak ciyak.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9514, 'mola almak', 'voleybol ve basketbolda taktik alışverişi yapmak için bir süre ara istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9515, 'mola vermek', 'uzun süren yolculuğa, yürüyüşe veya çalışmaya, dinlenmek amacıyla bir süre ara vermek, oturup dinlenmek: ''Otobüs, yol üzerinde bir kasabanın çarşısında yarım saat mola vermişti.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9516, 'moral bulmak', 'yürek gücünü, maneviyatını güçlendirmek: ''Savaşçı gruplarını moral bulmaları için bir haftalık tatile göndermeyi gerekli sayıyorlardı.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9517, 'moral vermek', 'bir kimsenin ruhsal direnme gücünü artırmak, cesaretlendirmek, yüreklendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9518, 'morali bozulmak', 'ruhsal yönden direnme gücünü yitirmek, içine korku düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9519, 'moralini bozmak', 'bir kimsenin ruhsal yönden direnme gücünü azaltmak, sarsmak: ''Bu olay, on beş gündür sıcak yemek yemeyen askerlerin morallerini bozup sinirlerini iyice gerdi.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9520, 'moratoryuma gitmek', 'tüm borçların ödeme zorunluluğunu geri bırakmak, resmî olarak geciktirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9521, 'morga kaldırmak', 'ölüleri morga götürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9522, 'mortoyu çekmek', 'ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9523, 'mosmor kesilmek (olmak)', 'kötü duruma düşmek, bozulmak, mahcup olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9524, 'mostra olmak', ' argo kendini gülünç bir duruma sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9525, 'motoru bozmak', 'bağırsakları bozulmak, ishal olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9526, 'muaf tutmak', 'bir ödevi, bir görevi bağışlamak, ayrıcalık tanımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9527, 'muafiyet tanımak', 'kendisinden beklenilen veya istenilenlerin bütününü istememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9528, 'muamele görmek', 'işlem uygulanmak, davranılmak: ''İyi muamele görmekle beraber eski neşesini kaybetmişti.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9529, 'muamma asmak', 'âşıklık geleneğinde herhangi bir konuyu manzum olarak bilmece türünde düzenleyip genellikle kahvehanelerde herkesin göreceği bir yere koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9530, 'muammer olmak', '1) yaşamak; 2) uzun ve mutlu yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9531, 'muaşakada olmak', 'sevişmek, birbirine âşık olmak: ''Ahmet''le Fazilet''in muaşakada oldukları ve evlenmeye hazırlandıkları anlaşıldı.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9532, 'mucize göstermek', '1) olağanüstü bir olay yaratmak: ''Millî hareket bu son bir sene zarfında o kadar süratli bir mucize gösterdi ki büyüklüğüyle gözleri kamaştırıyor.'' -Y. K. Beyatlı. 2) sadece peygambere özgü, insan aklının ve kabiliyetinin erişemeyeceği olağanüstülükler göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9533, 'muhabbet beslemek', 'sevgi duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9534, 'muhacir gitmek', 'göç etmek: ''Bunlar Kozan''dan Kırım''a, oradan da Tuna''ya muhacir gitmişler.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9535, 'muhafaza altına almak', 'korumak, saklamak, bir yerde tutmak, kapatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9536, 'muhakeme yürütmek', 'düşünmek, soruna bir çözüm aramak: ''Ferit bu aralık kendi kendine muhakemeler yürütmüş...'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9537, 'muhalefet etmek', 'karşı davranışta bulunmak, karşı çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9538, 'muhasara altına almak', 'kuşatılmak: ''Avluda neden bir köşede muhasara altına alındığımı o vakit anlamıştım.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9539, 'muhayyer bırakmak', 'seçmeli bırakmak, seçmeye izin vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9540, 'muhit edinmek', 'ilişkili olduğu, tanışık olduğu kimselerin sayısını çoğaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9541, 'mukabele okumak', 'topluluk karşısında dinleyicilerin takip edebileceği biçimde Kur''an''ı okumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9542, 'mukabelede bulunmak', 'karşılık vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9543, 'mukarrer bulunmak', 'kararlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9544, 'mukavemet etmek (göstermek)', 'direnmek, dayanmak, karşı koymak: ''Kalabalık bir düşman karşısında mukavemet etmek, kudretimi arttırıyordu.'' -K. Bilbaşar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9545, 'mukavemeti kırılmak', 'direnci, gücü azalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9546, 'mum dibine ışık vermez', 'etkili kişi kendi yakınlarına yardımcı olamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9547, 'mum etmek', 'muma çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9548, 'mum gibi', '1) dosdoğru, dimdik; 2) uslu, kıpırtısız; 3) tertemiz, düzgün; 4) zayıf, sararıp solmuş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9549, 'mum kesilmek', 'sessiz, uslu, doğru düzgün durmak: ''Öteki çocuklar mum kesilmişler, sahte bir sessizlikle birer disiplin modeli olmuşlardı.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9550, 'mum olmak', '1) hırçınlığı, yaramazlığı bırakmak; 2) argo razı olmak: O bu işe çoktan mum olmuştur ama kendini naza çekiyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9551, 'mum yakmak', 'kutsal sayılan bir yere giderek adak adadığında mum yakıp koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9552, 'mum yanmayınca pervane dönmez (yanmaz)', 'güzelin yoluna baş koyanların ortaya çıkması için o güzelin görünmesi gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9553, 'mum yapıştırmak', '1) bir şeyi kırmızı mumla mühürlemek; 2) mec. önemli bir şeyi unutmayıp akılda tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9554, 'muma döndürmek (çevirmek)', 'her sözü dinler duruma getirmek, uslandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9555, 'mumla aramak', 'çok isteyerek ve özlemle aramak: ''Kısacası, böyle bir komşuyu mumla arasa bulamayacaktır.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9556, 'mumya gibi', 'çok zayıf ve renksiz (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9557, 'murada (muradına) ermek', 'isteğine kavuşmak, dileği gerçekleşmek, arzusu yerine gelmek: ''Sevdalılar nihayet murada eriyorlar.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9558, 'muradı gözünde kalmak', 'emeline ulaşamamak: ''Kabrimin baş taşına yazsınlar / Muradı gözünde kalan bu diye'' -Âşık Ali İzzet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9559, 'murat almak', 'dileğine kavuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9560, 'murdar gitmek', 'murdar bir biçimde ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9561, 'musallat olmak', 'birini sürekli rahatsız etmek, birine sataşmak, peşini hiç bırakmamak:', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9562, 'muşamba gibi', 'çok kirlenmiş (çamaşır, kumaş, örtü vb.).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9563, 'muşmula gibi', 'asık (surat).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9564, 'mutabık kalmak', 'uyuşmak, anlaşmaya varmak: ''O akşam da müzakere sonunda bu yaşıtım üvey dayımla mutabık kaldık.'' -H. F. Ozansoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9565, 'muvafık bulmak', 'uygun görmek kabul etmek: ''Bu, saadet, hürriyet vadeden düşman kumandanının karşısında inat etmeyi muvafık bulmadı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9566, 'mübah görmek (saymak)', 'hoş görmek, sakıncasız bulmak: ''Kendine mübah gördüğünü bana yasak ederek beni susturmak mı istiyordun?'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9567, 'mücadele vermek', 'savaş vermek, mücadele etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9568, 'müdana etmemek', '1) kendini borçlu hissedecek duruma düşürmemek, kendi ayakları üstünde durmak, kimseye açıklama yapma gereği hissetmemek; 2) yaranmaya, iyi görünmeye çalışmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9569, 'müflis bezirgân eski defterleri karıştırır', '1) tüccar züğürtleyince, belki bir kimsede alacağım kalmıştır diye eski defterlerini gözden geçirir; 2) vaktiyle önemli işler yapmış olanlar, düşkünlüklerinde eski durumlarını anarak, anlatarak avunmaya çalışırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9570, 'müft olsun da zift olsun', 'kimileri bedava bulduklarında yenmeyecek şeyleri yerler, işe yaramayan şeyleri alırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9571, 'mühür basmak', 'mühürlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9572, 'mühür kimde ise Süleyman odur', 'bir işte yetki kimde ise kuvvet ondadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9573, 'müjde koşturmak', 'bir muştuyu bir kimseye ivedilikle ulaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9574, 'müjde vermek (götürmek)', 'bir kimseye sevindirici, mutlu bir haberi ulaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9575, 'mükâfatını görmek', 'herhangi bir olumlu davranışın, özverinin veya bir sıkıntının iyi sonucunu elde etmek: ''Zavallı babam geçirdiği yetmiş senelik azabın mükâfatını görecek.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9576, 'mülahazat hanesini açık bırakmak', 'bir kimse hakkında kesin bir kanıya varamayarak zamanla ortaya çıkacak gelişmeleri beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9577, 'mülakat vermek', 'belli bir konuda konuşmak, demeç vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9578, 'mümkün görünmek', 'olabilmek: ''Alınan tedbirlere rağmen Türkiye''nin nüfus artışını, beklenilen bir şekilde durdurmak pek mümkün görünmüyor.'' -M. Kaplan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9579, 'mümkün olmak', 'imkân bulunmak: ''Bu iki ışık, varılması mümkün olmayan bir uzaklıkta duruyordu.'' -H. S. Tanrıöver.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9580, 'münakaşa götürmemek', 'tartışmaya yer vermeyecek biçimde kesin olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9581, 'münasebet almak', 'uygun düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9582, 'münasebet düşmek', 'uygun bir durum ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9583, 'münasebet kurmak', 'iki şey arasında ilişki bulmak, yakınlık görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9584, 'münasebete girmek', '1) tanışma yolu açmak, ilişki kurmak: ''Onunla temas ve münasebete girmektense hiçbir şey yapmamayı ve hazır paradan yemeyi tercih ediyorum.'' -Y. K. Karaosmanoğlu. 2) cinsel yaklaşımda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9585, 'münasebeti düşmek', 'sırası gelmek: Bir münasebeti düşerse söylerim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9586, 'münasebetini getirmek', 'sırasını getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9587, 'münasebette bulunmak', '1) ilişkisi olmak; 2) ilişki kurmak; 3) cinsel ilişkiyi gerçekleştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9588, 'münasip bulmak', 'uygun olduğunu, yerinde görüldüğünü kabul etmek: ''El işlerini, bilhassa ziraatı münasip buldu.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9589, 'münasip düşmek', 'uygun düşmek: ''O makama daha gayur bir zat münasip düşüyordu.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9590, 'münasip görmek', 'uygun ve yerinde bulmak: ''Kendi çocukları hep kız olduğu için yeğeni Bilâl''i bu işe münasip gördü.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9591, 'müracaatta bulunmak', 'müracaat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9592, 'mürekkebi kurumadan bozmak', 'kararı, sözleşmeyi, anlaşmayı yazılmasından çok kısa süre sonra bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9593, 'mürekkep olmak', '-den oluşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9594, 'mürekkep yalamak', 'çok okumuş, yazmış olmak: ''Herhâlde aile terbiyemin, görgümün ve az buçuk mürekkep yalamış olmamın da bu Tanrı vergisini beslemekte tesiri olacaktır.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9595, 'mürekkep yalamış', 'öğrenim görmüş, kültürlü: ''Şöyle az buçuk mürekkep yalamış bir insanı böylesine üç nutuk çılgına döndürür.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9596, 'mürüvvete endaze olmaz', 'yardım ve iyiliğin sınırı yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9597, 'mürüvvetini görmek', 'anne ve baba çocuklarının sevinçli günlerini görerek mutluluk duymak: ''Ne kaldı şurada alnımızın akıyla ilk torunumun mürüvvetini görmeye?'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9598, 'mürüvvetsiz adam, suyu çekilmiş değirmene benzer', 'cömert olmayan, iyilik yapmaktan hoşlanmayan biri, içinde yaşadığı toplum için bir değer taşımaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9599, 'müsaade etmek (buyurmak)', '1) izin vermek: ''Hiçbir şey söylemesine müsaade etmedim, gayet haşin, çok sert davrandım.'' -E. İ. Benice. 2) geçiş için yol vermek, yol açmak; 3) elverişli, uygun olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9600, 'müsabakaya girmek', 'yarışmak, yarışmaya katılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9601, 'müsadere etmek', 'zor alıma çarpmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9602, 'Müslüman mahallesinde salyangoz satmak', 'körler mahallesinde ayna satmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9603, 'müstahak olmak', 'hak kazanmak, layık olmak: ''Sen bu akıbete iki defa müstahak olmuşsun çocuğum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9604, 'müşahede altına almak', 'sürekli gözlem altında bulundurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9605, 'müşkülat çekmek', 'zorluk, güçlük içinde kalmak: ''Görüyorsunuz ki cevap vermekte müşkülat çekiyorsunuz.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9606, 'mütalaa yürütmek', 'herhangi bir görüş üzerinde ayrıntılarıyla düşünce üretmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9607, 'mütalaada bulunmak', 'görüş veya düşünce ileri sürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9608, 'mütekabiliyet esası üzerine', 'karşılıklı olarak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9609, 'müze gibi', 'eski ve değerli eşyaları olan (yer).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9610, 'müzevirlik etmek', 'söz getirip götürmek, ara bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9611, 'nabız almak', 'nabzını saymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9612, 'nabız yoklamak', 'nabzını yoklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9613, 'nabzı atmak', '1) kalp vuruşu sürmek; 2) mec. ortaya çıkmak, görünmek, belli olmak: ''Viyana''da hayat sevincinin nabzı kahvelerde atar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9614, 'nabzı durmak', 'ölmek: ''Nabzı durdu, nefesi durdu galiba.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9615, 'nabzını saymak', 'bir dakikadaki kalp atışını saymak: ''Sonra bileğini avucumun içine alarak nabzını sayıyorum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9616, 'nabzını tutmak', '1) nabzını saymak için bileğini tutmak: ''Doktor, hallacın yanına vardı. Nabzını tuttu.'' -S. F. Abasıyanık. 2) mec. düşüncesini, niyetini, eğilimini anlamaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9617, 'nabzını yoklamak', '1) niyetini, düşüncesini, eğilimini anlamaya çalışmak: ''Milletin sesini işitmek, nabzını yoklamak, meselesini ve durumunu kaynakta öğrenmek istiyordu.'' -T. Buğra. 2) düşünce, niyet ve eğilimi anlamak için ön araştırma yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9618, 'naçar kalmak', 'çare, çıkar yol bulamamak: ''Bu eski kafanın nasihatlerinden yıldığı için pek naçar kaldığı anlarda bu kapıyı çalar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9619, 'nadasa bırakmak (yatırmak)', 'tarlayı ekmeyip bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9620, 'nafaka bağlanmak', 'yasaca, bakılması zorunlu olan kişiye mahkeme kararıyla evlat, koca gibi bir kimsenin, geçim parası vermesini sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9621, 'nağme yapmak', ' tkz. 1) bildiği bir şeyi bilmez görünmek; 2) bahane ileri sürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9622, 'nal deyip mıh dememek', 'bir düşüncede direnmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9623, 'nal toplamak', '1) at, yarışta sonlara kalmak veya sonuncu olmak; 2) mec. herhangi bir alanda geride kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9624, 'nalıncı keseri gibi kendine yontmak', 'yaptığı işlerde hep kendi çıkarını düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9625, 'nalları dikmek', ' argo hayvan veya hayvana benzetilen kişi ölmek: ''Kitap bastırmak, yazı yazmak takatinden mahrum, nalları dikeceksiniz.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9626, 'nam almak', 'şöhret sahibi olmak, tanınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9627, 'nam kazanmak', 'ün sahibi olarak tanınmak: ''Karaman alayı, bizim harp tarihimizde büyük nam kazanmış bir alaydır.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9628, 'nam salmak', 'ününü her yana yaymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9629, 'nam vermek', 'ün kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9630, 'namaza durmak', 'namaza başlamak: ''Arabalar uzaktan görününce köyüne, adamına göre kâh derviş, kâh sofu olur, hemen namaza dururdu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9631, 'namaza meyli olmayanın kulağı ezanda olmaz', 'kişi yapmak istemediği işin ayrıntılarıyla ilgilenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9632, 'namazı kılınmak', 'Müslüman birinin cenaze namazı kılınmak: ''Bir akşam uyudu / Uyanmayıverdi / Aldılar götürdüler / Yıkandı, namazı kılındı, gömüldü'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9633, 'namazında niyazında', 'İslam dininin gerekliliklerini tam anlamıyla yapan: ''Bunlar namazında niyazında iki temiz gardiyandı.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9634, 'name okumak', 'herkesin bildiği deyimleri veya sözleri söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9635, 'namerde muhtaç bırakmak', 'güvenilmeyecek kimselerden yardım istemek zorunda bırakmak: ''İş ki kocam olacak, erkek olsun, beni namerde muhtaç bırakmasın diyormuş.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9636, 'namerde muhtaç olmak', 'güvenilmeyecek kimselerden yardım istemek zorunda kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9637, 'namı nişanı kalmamak', 'yok olup unutulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9638, 'namusu iki paralık olmak', 'onursuz bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9639, 'namusu temizlenmek', 'bir işin içinden kendi saygınlığını yitirmeden çıkmak: ''Öyleyse evvela, senin istediğin dava görülmüş olur. Yani hırsız olmadığın meydana çıkar. Namusun temizlenmiş olur.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9640, 'namusuna dokunmak', 'birinin namus ve onurunu olumsuz biçimde etkilemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9641, 'namusuna sinek kondurmamak', '1) kollamak, gözetlemek; 2) namusuna, onuruna laf söylettirmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9642, 'namusunu temizlemek', 'ahlak ve onuruna ters düşen bir durumdan kurtulmak için birini veya kendini öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9643, 'namzet göstermek', 'bir iş için aday belirleyip sunmak, aday göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9644, 'nane yemek', 'yakışıksız bir davranışta bulunmak, uygunsuz bir iş yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9645, 'nanik yapmak', 'birini budala yerine koymak, alay etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9646, 'nankörlük görmek', 'nankörce davranışla karşılaşmak: ''Annen bu yalıya o kadar emek vermiş, sonra nankörlük görmüş.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9647, 'nar gibi', 'iyice kızarmış (yiyecek).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9648, 'nara atmak (basmak)', 'yüksek sesle uzun uzun haykırmak: ''Bu cevabı alan Şems bir nara atarak kendinden geçmiş ve bir daha Mevlâna''nın peşinden ayrılmaz olmuştu.'' -A. H. Çelebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9649, 'narh koymak', 'ihtiyaç maddeleri için değişmez fiyat belirlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9650, 'nârına (nâra) yanmak', 'ateşine yanmak: ''Bizim çocukluğumuz, söğüt ağacından düdük yontmakla geçerken bir gün ele avuca sığmayan bir arkadaşın nârına yandık.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9651, 'nasıl ki', 'iki cümle arasındaki anlam ilişkisini ''olduğu gibi'' anlamıyla bağlayan bir söz: ''Acele etmez ağırdan alır, nasıl ki bu akşam da ağırdan alıyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9652, 'nasıl olmuşsa', 'her nasılsa: ''Yine, nasıl olmuşsa, ayakta dikilecek yer yokken, yanımdaki koltuk boş.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9653, 'nasıl olsa', 'her durumda, er geç: ''Nasıl olsa, daha bir gün Bodrum''da kalacağımız anlaşılıyor.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9654, 'nasır bağlamak (tutmak)', '1) nasırlanmak; 2) mec. duygusuzlaşmak, duyarlığını yitirmek: O adamın kalbi nasır bağlamış.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9655, 'nasırına basmak', 'çıkarını engellemek: ''Tütün alıcılarının nasırına basmamak, gölgelerini bile çiğnemeden dolanıp da geçmek gerektiğini biliyordu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9656, 'nasibini almak', 'güzel, hoşa giden bir şeyden kısa bir süre de olsa yararlanmak, sebeplenmek: ''Herkes ondan haz veya hüzün, kendi nasibini alırdı.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9657, 'nasihat istersen tembele iş buyur', 'tembel, kendisine buyurulan işi yapmamak için ya onun yapılmasına ihtiyaç bulunmadığını söyler ya da buyurulan biçimde değil, kendisinin işine gelen biçimde yapmayı önerir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9658, 'nasihatte bulunmak', 'nasihat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9659, 'nasip almak', '1) Bektaşilikte tarikata girme töreni yapılmak; 2) yararlanmak, kısmetine düşeni elde etmek: ''Konaktaki hamamlardan halayıklar, hizmetçiler de nasiplerini alırmış.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9660, 'nato kafa, nato mermer', 'söz dinlemez, söz anlamaz, taş gibi kafa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9661, 'nazar değmek', 'göz değmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9662, 'nazara gelmek', 'göz değmek: ''Hele marangoz Halil''in gözünü de denemiş. Nazara gelmemek için kendi kendine okuyup üflüyor, nerede tahta görürse tak tak vuruyor.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9663, 'nazarı değmek', 'gözü değmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9664, 'nazarıdikkate almak', '1) dikkatle inceleyerek değerlendirmek; 2) göz önünde bulundurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9665, 'nazarıdikkatini çekmek', 'ilgisini çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9666, 'nazarıitibara almak', 'dikkat etmek, dikkate almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9667, 'nazına katlanmak', 'istenen her şeyi hangi durumda olursa olsun yerine getirmek: ''Cemal Paşa, gençlik akımı içinde hatırı sayılır olduğunu bildiği için sonuna kadar Halide Hanım''ın nazına katlandı.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9668, 'nazire yapmak', 'bir söze, bir davranışa benzeriyle karşılık vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9669, 'ne âlâ memleket', 'haksız ve yersiz işlerin hoş görüldüğü, kurallaştığı bir ortam için ters anlatışla ''diyecek yok, ne güzel'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9670, 'ne âlem', 'yadırganan ancak kızılmayan davranışları olan kimseler için kullanılan bir söz: Ne âlem çocuksun!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9671, 'ne âlemde?', 'nasıl?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9672, 'ne alıp veremiyor?', '''isteği, dileği nedir, niçin musallat oluyor?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9673, 'ne altını bırakmak ne üstünü', 'bir şeyin veya yerin her tarafını karıştırmak, dolaşmak vb: Geze dolaşa şehrin ne altını bıraktık ne üstünü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9674, 'ne arar (onda ... ne gezer)', 'onda yoktur: Onda para ne arar!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9675, 'ne biçim?', 'nasıl?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9676, 'ne buyrulur?', '''onun nasıl bir şey olduğunu gördünüz, buna ne diyorsunuz?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9677, 'ne çare', '''çaresi yok, elden ne gelir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9678, 'ne çıkar', '1) ne zararı var? ''Dar bir gün gelmiş, birinden üç beş kuruş almışım, bundan ne çıkar?'' -M. Ş. Esendal. 2) bir sonuç vermez; 3) nasıl bir yarar umulur?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9679, 'ne çiçektir, biliriz', '''ne denli yeteneksiz, niteliksiz olduğunu biliriz'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9680, 'ne dağda bağım var, ne çakaldan davam', 'tuttuğum bir taraf yok ki ona saldıranların karşısında olayım.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9681, 'ne de olsa', '''ne denli eksiği, kusuru olursa olsun, böyle olmakla birlikte'' anlamında kullanılan bir söz: Ne de olsa o bizden tecrübelidir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9682, 'ne dedim de', 'yapılan bir şeyden duyulan pişmanlığı belirten bir söz: Ah be ne dedim de okumadım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9683, 'ne demek?', '1) ''öyle şey olur mu, o nasıl şey, yakışık alır mı?'' anlamında kullanılan bir söz; 2) ''tabii ki, elbette, lafı mı olur?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9684, 'ne demek olsun', 'ne demek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9685, 'ne demeye', '1) ne diye, nasıl bir düşünceyle, hangi maksatla, niçin? ''Hadi müşteriyi iplediğin yok, patrona ne demeye boş verirsin!'' -R. Ilgaz. 2) hangi anlama?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9686, 'ne denir (dersin)', 'bir konuda söyleyecek söz kalmadığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9687, 'ne denli', 'ne kadar: ''Fakat şaşkınlığı ne denli büyük olursa olsun, oranın güzelliği daha büyüktü.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9688, 'ne dese beğenirsin?', 'beklenmeyen bir söz söylenildiğinde kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9689, 'ne dilersen eşine o gelir başına', 'sen başkaları için iyi şeyler dile ve yap ki başkaları da senin için iyi şeyler dilesin, yapsın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9690, 'ne diye?', 'nasıl bir düşünceyle, niçin? ''Sen bundan on yıl önce kişiliğini bulmuştun, ne diye bunu bırakıp başka şeyler arıyorsun?'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9691, 'ne doğrarsan aşına, o çıkar kaşığına', 'kişi, kendisi için önceden yaptığı hazırlıkların verimini ileride alır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9692, 'ne ekersen onu biçersin', 'nasıl davranırsan öyle karşılık görürsün.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9693, 'ne fayda', 'iş işten geçtikten sonra alınan boş önlemler için ''neye yarar'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9694, 'ne gam', 'üzülmeye gerek yok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9695, 'ne gezer', 'bulunmaz, yoktur: ''Kâr mı, ne gezer efendim? Hatta ziyanına satıyordu.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9696, 'ne gibi?', 'nasıl, ne türlü?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9697, 'ne gözle bakmak', '1) inancını belirtir biçimde bakmak; 2) değerlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9698, 'ne güne duruyor?', '1) ... varken başka şey gerekmez: Biz ne güne duruyoruz? 2) şimdi yapmazsa ne zaman yapacak?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9699, 'ne günlere kaldık!', 'zamanın olaylarından yakınma anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9700, 'ne haber?', '1) herhangi bir bilgi var mı? 2) ne var ne yok; 3) alay ''senin hiçbir şeyden haberin yok'' anlamında kullanılan bir söz: O, başkalarıyla geziyormuş, ne haber?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9701, 'ne hacet', 'gereksiz, gerek yok: ''Bir gamlı hazanın seherinde / Israra ne hacet yine bülbül?'' -A. Haşim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9702, 'ne haddine!', 'ona mı düşmüş, ona mı kalmış, ona düşmez: Böyle yazı yazmak onun ne haddine!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9703, 'ne hâlde?', 'hangi durumda?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9704, 'ne hâli varsa görsün', 'öğüt ve uyarı dinlemeyenler için ''ne yaparsa yapsın, beni ilgilendirmez'' anlamında kullanılan bir söz: ''Boş ver ne hâlleri varsa görsünler, ben bu heriflere bulaşamam.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9705, 'ne hesaba gelmek ne de kantara', 'elle tutulur olmamak, tutarlı ve sağlam görünmemek: Anlattıkların ne hesaba gelir ne de kantara.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9706, 'ne hikmetse (hikmettir)', '1) bilmezlikten gelinen durumlarda kullanılan bir söz: ''Elektrik ampulü ne hikmetse hep bozulmuş olurdu.'' -H. Taner. 2) bilinmeyen bir sebepten ötürü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9707, 'ne idiği belirsiz', 'ne olduğu, soyu sopu belirsiz: ''Ben âdeta bu ne idiği belirsiz herife gittikçe ısınıyorum.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9708, 'ne imiş?', 'ne değeri var?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9709, 'ne istediğini bilmek', 'amacını kesin ve kararlı bir biçimde belirlemek: ''Ne istediğini bilen iradeli bir kişiliği ve dişiliği vardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9710, 'ne iyi!', 'mutluluk ve beğenme anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9711, 'ne kadar', '1) nicelik bakımından miktar, ölçü, fiyat, zaman anlamlarıyla soru bildiren bir söz: ''İlçelerinde ne kadar dernek varsa hepsini harekete geçirdiler.'' -A. Kulin. 2) çok, oldukça: ''Bizim arkadaşın ne kadar bahtlı büyük anası varmış.'' -M. Ş. Esendal. 3) ne ölçüde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9712, 'ne kadar olsa', 'ne de olsa, sonuçta: ''Eh ne kadar olsa anadır. Ben de acıdım.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9713, 'ne kadar varsa', 'hepsi, tamamı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9714, 'ne karanlıkta yat, ne kara düş gör', 'her konuda tedbirli ve ihtiyatlı olmak büyük yarar sağlar ancak tedbirli ve ihtiyatlı olursak tehlikeleri kolaylıkla başımızdan kovabiliriz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9715, 'ne lazım', 'niçin ilgileniyorsun, ilgilenme.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9716, 'ne mal olduğunu bilmek (anlamak)', 'birinin nasıl bir nitelikte, yetenekte ve yaradılışta olduğunu bilmek, kestirmek: ''Büyük hanım, bir bakışta onun ne mal olduğunu anlamıştı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9717, 'ne mene', 'ne çeşit, ne türlü: ''Deve kuşunun boyunu bosunu, biçimini, ne mene bir hayvan olduğunu bilirsiniz.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9718, 'ne menem', 'ne çeşit, ne türlü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9719, 'ne mümkün', 'olacak şey değil, imkânsız: ''Görüp de sevmemek ne mümkün seni / Güzelsin, incesin, tatlısın, şensin'' -O. S. Orhon.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9720, 'ne münasebet!', 'öyle şey mi olur, ilgisi yok: ''Ümit, ideal, şahsiyet dediğiniz zaman da hep aynı şeyleri mi anlıyorsunuz? Ne münasebet!'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9721, 'ne o?', 'ne var, ne oluyor?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9722, 'ne od var ne ocak', '''yoksulluk ve perişanlık içinde'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9723, 'ne olacak!', 'ne değeri var, önemi yok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9724, 'ne olduğunu bilememek', 'şaşırmak, aklı başından gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9725, 'ne oldum delisi olmak', 'ummadığı bir duruma ulaşan kimse çok şımarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9726, 'ne oldum dememeli, ne olacağım demeli', 'kişi bugün içinde bulunduğu parlak durumun sürüp gideceğini sanmamalı, yarın kötü bir duruma düşeceğini hatırdan çıkarmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9727, 'ne olur ne olmaz', '''her ihtimali düşünmek gerekir'' anlamında kullanılan bir söz: ''Bir ara ne olur ne olmaz deyip frenleri, fren balatalarını gözden geçirdik.'' -F. Otyam.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9728, 'ne olur (olursun, olursunuz)', '''yalvarırım, lütfen, rica ederim'' anlamında kullanılan bir söz: ''Ah ne olur büyük bir adam çıksa da sanatı da böyle tarif etseydi.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9729, 'ne olursa olsun', '''her durumda, olumlu veya olumsuz bütün şartlarda'' anlamında kullanılan bir söz: ''Ne olursa olsun tahtı ele geçirmek amacını gütmüyorum ben.'' -T. Oflazoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9730, 'ne oluyor?', 'ne gereği var veya ne karışıyor?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9731, 'ne pahasına olursa olsun', '1) ''ne büyük özveri isterse istesin'' anlamında kullanılan bir söz; 2) ''her türlü sıkıntı ve tehlikeyi göze alarak'' anlamında kullanılan bir söz: ''Emin olduğu tek şey, gerçekten ne pahasına olursa olsun, artık bir daha geri dönmeyeceğiydi.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9732, 'ne sakala minnet ne bıyığa', 'insan en yakın akrabalarından bile yardım istemeyerek kendi imkânlarıyla yetinmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9733, 'ne söylüyorsun?', '1) ''söylediğine dikkat ediyor musun?'' anlamında kullanılan bir söz; 2) ''gerçek mi? doğru mu?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9734, 'ne Şam''ın şekeri ne Arap''ın zekeri (yüzü)', 'yararı olsa bile istenmeyen kimseler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9735, 'ne şeytanı gör ne salavat getir', 'gücün yetmediği işe kalkışılmamalı, kalkışılırsa da başkalarından medet umulmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9736, 'ne şiş yansın ne kebap', 'iki taraf da gücendirilmesin veya korunsun.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9737, 'ne var ki', 'aralarında aykırılık bulunan cümleleri bağlamaya yarayan bir söz, ama, fakat, lakin, gelgelelim: ''Ne var ki bunları şimdiye kadar kimseye anlatmadığım için uygun ifadeyi bulmakta zorlanıyorum.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9738, 'ne var ne yok', '1) ne haberler var, işler nasıl? 2) olanların bütünü: ''İş, hemen ne var ne yok yüklenip yola çıkmaya kalıyordu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9739, 'ne verirsen elinle o gider seninle', 'insanın bu dünyada yaptığı bir iyiliğin ahirette karşılığını göreceği düşünülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9740, 'ne yaparsın (yapmalı) ki', 'ne çare ki.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9741, 'ne yapıp yapıp', 'her ne durumda olursa olsun bir çözüm yolu bularak: ''Seni ne yapıp yapıp memleketine göndereceğim.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9742, 'ne yaptığını bilmemek', 'aklı başında olmadığından bilinçsizce davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9743, 'ne yârden geçilir ne serden', 'insan ne kendinden ne de sevdiklerinden kolay kolay vazgeçemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9744, 'ne yavuz ol asıl ne yavaş ol basıl', 'olumlu veya olumsuz konularda aşırıya kaçmak seni zor durumda bırakır; ne büyük ceza görecek kadar atılgan, dikbaşlı ol ne de herkes seni ezecek kadar yumuşak başlı ol.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9745, 'ne yazar', ' argo hükmü olur mu? değeri var mı? ''Tut ki para babası olduk, kültür ve sanat alanında bir karış yol alamazsak bütün bunlar ne yazar?'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9746, 'ne yazık ki', 'üzülerek belirtelim ki: ''Ne yazık ki bu görüşleri bozuk kimselerin gördükleri ekseriya mukavva heykellerdir.'' -A. H. Çelebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9747, 'ne yüzle', 'hiç utanmadan, sıkılmadan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9748, 'necat bulmak', 'kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9749, 'neci oluyor!', 'niçin karışıyor, ona ne? Sen neci oluyorsun, kendi işine bak!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9750, 'nedamet duymak (getirmek)', 'pişman olmak: ''Ben şimdi nedamet getirdim.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9751, 'neden olmak', 'bir şeyin olmasına veya ortaya çıkmasına yol açmak, sebep olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9752, 'nedir ki', '1) şu var ki: Nedir ki onların sözü pek dinlenmez. 2) hangi nedenle? 3) önemsiz, değersiz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9753, 'nefes aldırmamak', 'dinlenmesine fırsat vermemek, aralık vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9754, 'nefes almak', '1) havayı ciğerlerine çekmek, soluk almak: ''Nefes aldıkça içime kurum ve is kokusu doluyor sanıyorum.'' -R. E. Ünaydın. 2) dinlenmek; 3) ferahlamak, rahatlamak: ''Bu telgrafı okur okumaz, geniş bir nefes aldım.'' -Y. K. Karaosmanoğlu. 4) mutlu bir biçimde yaşamak: ''Gezecek, eğlenecek, nefes alacak hiçbir yer yok.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9755, 'nefes çekmek', '1) sigara veya başka bir şeyin dumanını içine çekmek: ''Ramazan sigarasının izmaritinden birkaç nefes çekti.'' -Ç. Altan. 2) esrar içmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9756, 'nefes darlığı çekmek', 'solumada sıkıntı yaşamak: ''Nefes darlığı çeker, sık sık tedavi olmak için başka şehirlere gider gider gelirdi.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9757, 'nefes etmek', 'boş bir inanışa göre, rahatsızlığı, illeti geçirmek için okuyup üflemek: ''Ahalinin büyük bir kayıtsızlıkla ''çiçek'' ismini verdiği frengiye nefes eder, tütsü yapardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9758, 'nefes nefese kalmak', 'soluğu tıkanacak gibi olmak: ''Delikanlı, sonunda gömleği terden sırtına yapışıp nefes nefese kaldığı bir an, gömleğinin yeniyle alnının terini silerek oyunu bıraktı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9759, 'nefes tüketmek', 'uzun uzun ve boş konuşmak: ''Enişte istediği kadar nefes tüketsin, hepsi bir kulağımdan girer, öteki kulağımdan çıkar.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9760, 'nefesi durmak', '1) ölmek: ''Nabzı durdu, nefesi durdu galiba.'' -Y. Z. Ortaç. 2) mec. şaşkınlık içinde kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9761, 'nefesi kesilmek (daralmak veya tutulmak)', '1) güç soluk alacak duruma gelmek veya soluğu büsbütün durmak: ''Nefesi daralıyor, yüzü kızarıyor, böğrüne bir ağrı giriyor ve yol ona gittikçe uzuyordu.'' -M. Ş. Esendal. 2) mec. bunalmak, sıkılmak: ''İki güzel filmin arkasından peş peşe on tane moloz film sıralanınca insanın nefesi kesiliyor.'' -B. R. Eyuboğlu. 3) mec. hayran kalmak, etkilenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9762, 'nefesine güvenen borazancıbaşı olur', 'başarabileceğinize eminseniz büyük işlere girişiniz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9763, 'nefesini tutup beklemek', 'heyecan, merak veya endişeyle sonucu izlemek: ''Uzun süren ziyaretin sona ermesini, nefeslerini tutup beklemişlerdi.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9764, 'nefret duymak', 'birinden tiksinmek, hoşlanmamak: ''Gönlümde o zamana kadar duyduğum nefret yerine büyük bir korku titriyordu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9765, 'nefret uyandırmak', 'nefret etmesine sebep olmak: ''Çünkü Ömer Bey, başka birinde son derece nefret uyandıran bir kabalık, bir kusur sayılması lazım gelen o gurur ve azamet buhranları içinde bile bir çocuk saflığını saklıyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9766, 'nefsine uymak', 'bedenin isteklerine uymak, günah işlemek: ''Nefsine uyanların, zevkten başka bir şey tanımayanların, hayvanlardan ne farkı var?'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9767, 'nefsini köreltmek (körletmek)', 'beden isteklerinden herhangi birini üstünkörü gidermek, nefsini yatıştırmak: ''Yani şüphelendiği müşterilerin yolunu kesmiyor, uzaktan uzağa onları takip etmekle nefsini köreltiyordu.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9768, 'nekesle cömerdin harcı birdir', '1) cömertin de cimrinin de sarıldığı kefen aynıdır; 2) cimri, ucuz diye her şeyin kötüsünü alır, bunlar işe yaramadıklarından veya çarçabuk bozulduklarından yenilerini almak zorunda kalır ve birkaç kez para harcar; cömert ise bir kez çok para verip her şeyin iyisini alır. Sonuç olarak cimri de, cömert de aynı parayı harcamış olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9769, 'neler', 'çok ve çeşitli şeyler: Bugün neler gördük.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9770, 'neler de neler, maydanozlu köfteler', ' alay ''akla gelmedik şaşılacak şeyler'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9771, '... nere ... nere', 'iki şeyin aralarındaki uzaklığı veya nitelik ayrımını belirten bir söz: Konya nere Ankara nere.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9772, 'nerede akşam, orada sabah', 'bir kimsenin gece kalacak belli bir yeri olmadığını, rastgele bir yerde kalabileceğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9773, 'nerede birlik, orda dirlik', 'aralarında duygu ve düşünce birliği bulunan topluluklar, dirlik ve düzenlik içinde yaşarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9774, 'nerede bu bolluk', 'bu işi yapmak sanıldığı kadar kolay değil, imkânlar sınırlı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9775, 'nerede çokluk, orada bokluk', 'birlikte iş yapmak üzere toplanan kişiler çok olursa her kafadan bir ses çıkar, anlaşmazlıklar belirir, iş yapmak güçleşir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9776, 'nerede hareket, orada bereket', 'hareket olan yerde bolluk olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9777, 'nerede kaldı', 'ne yararı oldu? ''Senin filozofluğun nerede kaldı?'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9778, 'nerede kaldı ki', 'olacak gibi görülmeyen bir düşünceyi anlatan ifadenin başına getirilen bir söz: O kendisi bilmez, nerede kaldı ki başkasına öğretsin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9779, '... nerede ... orada', 'söylenilen iki şeyin birlikte olması gerektiği anlatılmak istendiğinde kullanılan bir söz: Ben nerede sen orada.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9780, 'nereye gitsen okka dört yüz dirhem', 'konuşulan bir gerçeğin açıklığını ve tartışma götürmezliğini anlatmak için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9781, 'nesi var', '1) ''çok iyi, çok güzel'' anlamında kullanılan bir söz: ''Hem nesi var yahu, akça pakça kız.'' -S. F. Abasıyanık. 2) hastanın durumunu öğrenmek amacıyla kullanılan bir söz: Cemal''in nesi var? - Nezle olmuş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9782, 'nesi var nesi yok', 'bütün serveti, her şeyi: ''Eşkıyalar yolun gerisini de tutmuşlardı. Can maldan tatlı. Herkes nesi var nesi yok efenin önüne döktü.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9783, 'nesli tükenmek', 'bitmek, tamamen yok olmak, ortadan kalkmak: ''Oralarda nesli tükenmiş sandığımız âşıklar, halkı coşturmak için ozanların kopuzlarını çalıyorlar.'' -O. S. Orhon.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9784, 'neşesi kaçmak', 'sevinci azalmak, kederlenmek: ''O günden sonra Canan''ın uzun müddet neşesi kaçtı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9785, 'neşesini bulmak', 'neşeli bir duruma gelmek, neşelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9786, 'neşet etmek', 'kaynağını bir yerden almak, doğmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9787, 'neşter vurmak', 'bir sorunu kesin bir sonuca ulaşmak amacıyla ele almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9788, 'neşvünema bulmak', 'gelişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9789, 'netice vermek', 'sonuç vermek: ''Ağır ve onulmaz hastalıklar için yapılan tedavi, bir iyilik şeklinde görünse bile, azabı devam ettirmekten başka bir netice vermiyor.'' -İ. A. Gövsa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9790, 'nevaleyi düzmek', '1) gerekli yiyecek ve içeceği sağlamak: ''Elinde yiyecek paketleriyle evin nevalesini düzmüş, geri dönüyor.'' -R. H. Karay. 2) sofrayı hazırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9791, 'nevri dönmek', 'belli etmemeye çalıştığı bir öfkeye kapılmak, çok sinirlenmek: ''Halit''in tavrını beğenmemişti. Herifin birdenbire nevri dönmüştü.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9792, 'ney üflemek (üfürmek)', 'ney çalmak: ''Astımı olmasa babası gibi ney üfürmeye bile heves edecektir.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9793, 'neye uğradığını bilememek (anlamamak, şaşırmak)', 'ansızın üzücü, sıkıcı, neşeli, güzel veya hoş bir durumla karşılaşmak: ''Martı gibi, şiirli duygu dolu bir oyunla karşılaşınca neye uğradığını şaşırır.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9794, 'neyin nesi (kimin fesi)', '1) kimdir, nasıl bir kişidir? ''En iyisi, adam böyle böyle, evi kiraya istiyor, git, sor, soruştur, neyin nesi, kimin fesidir, derim.'' -O. Kemal. 2) ne idiği belirsiz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9795, 'neymiş', 'söylendiğine göre, güya: ''Neymiş? Projenin sahibi oymuş!'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9796, 'neyse ki', 'neyse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9797, 'neyse ne', 'bir yere, bir dereceye kadar: ''Erkekler neyse ne ama kadınlar...'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9798, 'nezaket göstermek', 'davranışlarda nazik olmak: ''Galiba beni tanımış olacak, ondan sonra biraz fazla nezaket göstermek istedi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9799, 'nezarete almak', 'gözaltına almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9800, 'nifak sokmak', 'ara açmak, bozgunculuk yapmak: ''Bülent ile haminnesinin arasına derin bir nifak sokmuştu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9801, 'nihayet vermek', '1) ilişkiyi kesmek, bir işi, alışkanlığı yapmaktan vazgeçmek: ''Tekaüt olduktan sonra doktorlara inat, oburluğa, nargileye bir nihayet vermemişti.'' -Ö. Seyfettin. 2) bitirmek, tamamlamak, sonuçlandırmak: ''Bu komediye nihayet vermek, buraya bir daha gelmemek üzere çıkıp gitmek isterdim.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9802, 'nihayete ermek', 'sona varmak, sonuçlanmak, bitmek: ''Geçirmiş olduğum elim sergüzeştin ve sefaletin nihayete ermiş olduğu bir gündü.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9803, 'nikâh düşmek', 'birbiriyle evlenmelerine yasal yönden veya örf bakımından engel bulunmamak: ''Ben kardeşinin yavuklusuyum, sana nikâh düşmez, cevabını alırdı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9804, 'nikâh kıymak', 'nikâh memuru kanuna göre çiftlerin karı koca olduklarını bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9805, 'nikâh koymak', 'nikâhlamak: ''Sana derim, güzel eltim, sen bu kızına tez vakitte nikâh koy!'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9806, 'nikâh tazelemek', '1) boşandığı kişiyle yeniden evlenmek; 2) mec. bir işe yeniden başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9807, 'nikâhta keramet vardır', 'nikâh evlenenleri sevgi bağıyla bağlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9808, 'nimet bilmek', 'bir şeyi lütuf kabul etmek: ''Çaylarımıza koşarlar, evimize davet edilmeyi nimet bilirler, etrafımızda dolaşırlar.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9809, 'nimeti ayağıyla tepmek', 'kısmetini ayağıyla tepmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9810, 'nisan yağar sap olur, mayıs yağar çeç olur', 'nisan yağmuru ekinlerin sapını geliştirir, mayıs yağmuru ise başakların dolgunlaşmasını sağlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9811, 'nisan yağmuru; altın araba, gümüş tekerlek', 'nisan da yağan yağmur ürünleri bereketli kılar, çiftçiyi zengin eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9812, 'nispet etmek', 'eşit tutmak, oranlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9813, 'nispet kabul etmemek', 'eşit tutmamak, oranlamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9814, 'nispet vermek (yapmak)', 'karşısındakini kızdırmak için ona gösteriş yapmak: ''Yolun ortasında bir kolunu belime dolayarak bana şöylece nispet vermesin mi?'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9815, 'nispeti olmak', 'ilgisi olmak, bağlantısı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9816, 'nişan almak', '1) bir hedefi vurmak için ateşli silahlara gerekli doğrultuyu vermek, gezlemek: ''Tabancasını kılıfından çıkarmış ve nişan almak üzereydi.'' -A. Gündüz. 2) kendisine nişan verilmek: ''Doktor, Türk ordusunda çalıştığını, üniformamızı taşıdığını, nişan aldığını, övünerek anlattı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9817, 'nişan takmak', '1) nişanlanan çiftin nişan yüzüklerini parmaklarına geçirmek: ''Birkaç gün sonra akrabalarımıza bir davet vereceğiz. Nişan takacağız.'' -R. N. Güntekin. 2) göğsüne nişan iliştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9818, 'nişanı (nişanını) atmak (bozmak)', 'kadın veya erkek nişandan vazgeçmek: ''Eğer nişanını bozduysa yazıklar olsun.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9819, 'niyet çekmek', 'niyetçiden niyet adı verilen fal kâğıdı almak: ''Birisi niyet çeksin de biz de bir lokma bir şey yiyelim diye bekleşiyorlar.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9820, 'niyet hayır, akıbet hayır (selamet)', 'her girişilen işin niyeti önemlidir, iyi niyetle girişilen bir işin sonu doğal olarak hayırlı olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9821, 'niyet tutmak', 'fala bakılırken olması istenilen şeyi aklından geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9822, 'nohut oda, bakla sofa', 'bir evin küçüklüğünü ve darlığını anlatmak için söylenen bir söz: ''Nohut oda, bakla sofa, bizim de evimiz olacak diye mırıldandı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9823, 'noksan bulmak', 'beğenmemek, uygun bulmamak: ''Eniştem zaten bizim terbiye ve tahsilimizi birçok bakımdan noksan bulurdu.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9824, 'nokta koymak', '1) gereken yerde nokta işaretini kullanmak; 2) mec. bir işi bitirmek, tamamlamak; 3) mec. son noktayı koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9825, 'noktasına virgülüne dokunmadan', '1) olduğu gibi; 2) hiçbir müdahale olmadan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9826, 'nostalji uyandırmak', 'özlem duygusu canlandırmak: ''İlkokulu, liseyi birlikte okuduk, belki onda nostalji uyandırıyorum.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9827, 'not almak', '1) biri konuşurken onun söylediklerini yazmak: ''Not alıyorum, Türkçeye mısra mısra hemen tercüme ediyorum.'' -R. H. Karay. 2) bir şeyi başlıca noktalarını özetleyerek yazmak; 3) öğrenci, iyi veya kötü numara, derece almak; 4) mec. bir şeyin niteliğiyle ilgili bir karar verilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9828, 'not atmak', 'öğretmen, öğrencinin çalışma durumunu not vererek değerlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9829, 'not düşmek', 'not yazmak: ''Cevdet Paşa tezkeresine şöyle bir not düşmek zorunda kalır.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9830, 'not etmek', 'not olarak yazmak, kaydetmek: Bunu not edin de unutmayın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9831, 'not kırmak', '1) verilen notu düşürmek, azaltmak; 2) az not vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9832, 'not tutmak', 'biri söz söylerken başkası onun söylediklerini yazmak: ''Benim sınıfta tuttuğum notları alır, sınavlara öyle hazırlanırdı.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9833, 'not vermek', '1) bir şeyin değeri üzerinde olumlu veya olumsuz bir kanıya varmak; 2) öğrencinin bilgisini bir sayı veya derece ile belirlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9834, 'nöbet beklemek (tutmak)', '1) asker, polis vb. bir yeri, bir kimseyi, bir aracı gözetlemek, korumak gibi amaçlarla bulunduğu yerden belli bir süre ayrılmamak: ''Kazığın yanında mızraklı bir asker nöbet beklesin!'' -N. F. Kısakürek. 2) kurum ve kuruluşlarda işlerin aksamadan yürümesi için sıra ile görev yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9835, 'nöbet çalmak', 'belli zamanlarda mızıka çalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9836, 'Nuh deyip, peygamber dememek', 'inat etmek, ayak diremek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9837, 'Nuh Nebi''den kalma', 'çok eski, çoktan modası geçmiş, köhnemiş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9838, 'numara çevirmek', 'hile yapmak, dalavereyle iş bitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9839, 'numara yapmak', ' argo bir hareketi yalandan yapmak veya yapar gibi görünmek: ''Numara yapıyorum gibi bir şey gelmesin aklınıza.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9840, 'numarasını vermek', 'notunu vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9841, 'nur gibi', 'parlak, pırıl pırıl.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9842, 'nur içinde yatsın', 'sevgiyle anılan ölüler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9843, 'nur inmek', 'kutsal bir yere gökten ilahî ışık yağmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9844, 'nur ol!', 'beğenildiği belirtilmek istendiğinde kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9845, 'nur topu gibi', 'sağlıklı, çok güzel ve temiz (çocuk): ''Oğlan nur topu gibi idi.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9846, 'nutku tutulmak', 'korkudan, şaşkınlıktan ve öfkeden konuşamaz olmak: ''Birdenbire nutku tutuldu ve bütün gayretlerine rağmen konuşamadı.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9847, 'nutuk atmak (çekmek)', 'uzun, sıkıcı bir konuşma yapmak veya özden yoksun bir söylev vermek: ''Kıyıda dalgalara nutuk çekip kekemeliğini düzeltmeye çalışıyor.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9848, 'nutuk vermek', 'bir konuda özel olarak hazırlanıp konuşmak: ''Kapıdan içeri bir adım attıktan sonra durdu, nutuk verir gibi elini sallayarak...'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9849, 'nüfusunu çıkarmak', 'nüfus kütüğüne kayıt yaptırarak nüfus cüzdanı almak: ''Kızının çocuklarının nüfusunu çıkartacağım.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9850, 'nüfuz etmek', '1) bir şeyin içine işlemek, geçmek: ''Tatlı bir duman, bütün varlığını sararak en derin yerlerine kadar nüfuz ediyordu.'' -P. Safa. 2) inceliğine varmak, anlamak: ''Bu, o kadar ince ve girift bir meseledir ki, bütün bir ömür boyunca izaha çalışılsa yine derinliğine nüfuz edilemez.'' -N. F. Kısakürek. 3) etkili olmak: ''Ecnebiler ona değil, o ecnebilere nüfuz ediyordu.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9851, 'nüfuzu altında tutmak', 'söz geçirme gücünü üstün kılmak, egemenliği altında bulundurmak: ''Onu uzun müddet nüfuzu altında tuttuğuna bir misal olarak...'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9852, 'nükte yapmak', 'nükteli söz söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9853, 'nüzul inmek (gelmek)', ' hlk. felç geçirmek, felce uğramak: ''Nedir bu hâlimiz, nüzul inmiş gibi yapıştık yere, bir türlü kıpırdayamıyoruz.'' -N. Eray.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9854, 'o duvar senin, bu duvar benim', 'birinin yalpalayacak kadar sarhoş olduğunu anlatan bir söz: ''O duvar senin, bu duvar benim, sağdan sola, soldan sağa yalpa vurarak halkın önüne çıkıvermiş.'' -H. F. Ozansoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9855, 'o gün bugün(dür)', 'o zamandan beri: ''İşte o gün bugündür ahbaplığı ilerlettik.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9856, 'o kadar', '1) çok fazla: ''Oyunları o kadar güzel olurmuş ki bunlar millî bir edebiyat eseri sayılırmış.'' -A. Ş. Hisar. 2) kâfi, yeter.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9857, 'o kapı (mahalle) senin bu kapı (mahalle) benim', 'sürekli gezip dolaşmayı anlatan bir söz: O kapı senin bu kapı benim, akşamı eder.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9858, 'o taraflı olmamak', 'konuyla ilgisi yokmuş gibi davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9859, 'o tarakta bezi olmamak', 'o şeyle ilişiği bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9860, 'o yolun yolcusu', '1) toplumun ahlak anlayışına göre kötü bir hayat sürdüren kimse; 2) sonunda ölecek olan kimse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9861, 'objektif olmak', '1) nesnel olmak; 2) tarafsız davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9862, 'ocağı batmak', 'yuvası yıkılmak veya soyu tükenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9863, 'ocağı kör kalmak', 'soyu tükenmek, çocuğu bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9864, 'ocağı sönmek', 'aile dağılmak, yok olmak, çoluk çocuk yok olmak: ''Aşk tuzakları birçok ocakların sönmesine sebep olmuştur.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9865, 'ocağı tütmek', '1) soyu devam etmek; 2) yaşamını sürdürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9866, 'ocağını yeşertmek', 'aile yuvasını canlandırmak: ''Aşkla, şevkle ocağını yeşertecek birini istiyordu.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9867, 'od ile su, dilsiz yağıdır', 'yangın ve sel felaketi tıpkı düşman gibidir, sessizce, haber vermeden gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9868, 'od yok ocak yok', 'çok yoksul.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9869, 'odun gibi', 'anlayışsız, görgüsüz, kaba.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9870, 'oduncunun gözü omçada', 'herkes işine yarayan şeyi elde etmeye çalışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9871, 'oduncunun gözü omçada, dilencinin gözü çömçede', 'herkes işine yarayan şeyi elde etmeye çalışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9872, 'of bile dememek', 'şikâyetçi olmamak, şikâyet etmemek: ''Yusuf gözlerine mil çekilirken of bile demedi.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9873, 'of çekmek', 'oflamak: ''Bir of çeksem karşıki dağlar yıkılır'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9874, 'oflaya puflaya', 'sıkılarak, acı çekerek, bunalarak: ''Oflaya puflaya neleri varsa hepsini vermek zorunda kalırlar.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9875, 'oflayıp puflamak', '''of, puf'' diyerek sıkıntısını, acısını dışa vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9876, 'ofsayta düşmek', '1) futbolda hücuma geçen takımın en az bir oyuncusu topla oynandığı anda rakip takımın kale çizgisine, o takımın en yakın oyuncusundan daha yakın bulunmak; 2) mec. istemediği hâlde kötü bir durumda kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9877, 'ofsaytta kalmak', '1) ofsayta düşmek; 2) mec. istemediği hâlde kötü bir durum içinde kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9878, 'oğlan anası kapı arkası, kız anası minder kabası', 'eve gelin geldikten sonra oğlanın anası kapı dışarı edilecek kadar fazla görülür ama kızın anası başköşeye oturtulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9879, 'oğlan atadan (babadan) öğrenir sofra açmayı, kız anadan öğrenir biçki biçmeyi', 'erkek çocuk, erkeklerin yapması gereken şeyleri babasından, kız çocuk da kadınların yapması gereken şeyleri annesinden öğrenir; anne ve baba bunları bilmiyorsa çocuktan böyle şeyler beklenemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9880, 'oğlan dayıya, kız halaya çeker', 'oğlan çocuğunun yüzü de, huyu da dayısına, kız çocuğunki ise halasına benzer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9881, 'oğlan doğuran övünsün, kız doğuran dövünsün', 'çoğu zaman doğacak çocuğun oğlan olması istenir, onun için oğlan doğuran kadın sevinir, kız doğuran kadın üzülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9882, 'oğlan yetir, kız yetir; ağır yükü sen götür', 'çocuk büyütmek, evi yönetmek hep annenin görevi olduğundan anne olmak kolay iş değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9883, 'oğlanınki oğul balı, kızınki bahçe gülü', 'torun oğlandan olursa ''oğul balı'', kızdan olursa ''bahçe gülü'' diye sevilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9884, 'oğlum deli malı neylesin, oğlum akıllı malı neylesin', 'çocuk akıllı ise babasından mal kalsın diye beklemez, malı kendisi kazanır; akılsızsa babası ne kadar çok mal bırakırsa bıraksın, değerini bilmez ve onu kısa zamanda bitirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9885, 'oğlumu (evladımı) ben doğurdum amma gönlünü ben doğurmadım', 'bir kimse evladına emredip birçok şey yaptırır ancak onun gönlüne hükmedemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9886, 'oğul çıkarmak', 'bir kovan, yeni bir oğul arısı topluluğu meydana getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9887, 'oğul vermek', 'oğul arılarının bir bölüğü kovandan ayrılıp ayrı bir kovana gitmek: ''Sivrisinekler çevresinde oğul vermeye başlamışlardı.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9888, 'oh çekmek', 'birinin kötü duruma düşmesine sevinmek: ''Mahalleli bir oh çekti bu hâlleri işitince.'' -Y. N. Nayır.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9889, 'oh demek', 'rahata ermek, rahata kavuşmak, rahat bir soluk almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9890, 'oh olsun!', 'söz dinlemeyip yanlış davranarak kötü duruma düşenlere ''çok iyi olmuş'' anlamında kullanılan bir söz: Oh olsun, benim sözümü dinlemedin!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9891, 'ok atmak', ' hlk. miras kalan malları paylaştırmak için ad çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9892, 'ok gibi (yerinden) fırlamak', 'çok hızlı gitmek: ''Affedersiniz, beni burada görürse kızar diye ok gibi fırladı.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9893, 'ok meydanında buhurdan yakmak', '1) geniş bir yeri yetersiz bir şeyle ısıtmaya çalışmak; 2) önemli bir iş için yetersiz imkânlardan yararlanmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9894, 'ok yaydan (yayından) çıkmak', 'geri dönülemeyecek bir iş yapmak: ''Amcam, beni bir kahraman gibi müdafaaya çalıştı, çalmadığı kapı bırakmadı. Fakat ok yaydan çıkmıştı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9895, 'okka çekmek', 'hacminden umulmayacak kadar okka ağırlığında olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9896, 'okka her yerde dört yüz dirhem', 'konuşulan bir gerçeğin açıklığını ve tartışma götürmezliğini anlatmak için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9897, 'okkanın altına gitmek', 'haksız yere ezilmek, bir zarar veya ceza görmek: ''Eğer gözünü açmaz, bu kör dövüşüne bir nihayet vermezsen muhakkak okkanın altına gidersin.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9898, 'oklava yutmuş gibi', 'baston yutmuş gibi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9899, 'okulu asmak (kırmak)', 'okuldan kaçmak, derslere girmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9900, 'okumayı sökmek', 'okula yeni başlayan öğrenci, verilen eğitim sonrası okumaya başlamak, okuma becerisini kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9901, 'okuyup üflemek', 'dinî inanca göre bir duayı okuduktan sonra, üfleyerek ruhlara yollamak: ''Gerçi her gece yatmadan evvel okuyup üflerse de çok geçmeden yine uyanır ve kalkardı.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9902, 'ola ki', 'olabilir ki, belki: ''Ola ki bir oldubitti yaratmayı kendi çıkarlarına uygun görmüşlerdir.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9903, 'olacak gibi değil', '''olamaz, olmuyor, olacağa benzemiyor'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9904, 'olacak o kadar', '''kabul edilebilir ölçüde'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9905, 'olacakla öleceğe çare bulunmaz', 'insanın alnına yazılmış olan şeyler önlenemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9906, 'olan dört bağlar, olmayan dert bağlar', 'zengin, giyinir, kuşanır, istediği gibi yaşar; fakirse yoksulluğun acısını çeker.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9907, 'olan oldu', '''iş işten geçti, artık yapacak bir şey kalmadı'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9908, 'olan (olup) biten', 'meydana gelen olaylar, ortaya çıkan durum veya oluşan her şey: ''Biraz da etrafında olup bitenlere dikkat etsen iyi edersin.'' -A. M. Dranas. ''Uzun İhsan Efendi olan biteni çaresizlikle seyrediyordu.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9909, 'olanak sağlamak (tanımak)', 'bir işin olmasına elverişli ortamı hazırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9910, 'olay çıkarmak', 'hoş olmayan bir durum yaratmak, hadise çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9911, 'olay yapmak', 'bir olayı gereğinden fazla büyütmek, sorun çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9912, 'olay yaratmak', 'ortada herhangi bir sebep yokken bir olaya yol açmak: ''Hınzır sıfır, sağda da olsa solda da olsa olaylar yaratıyor.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9913, 'olayın üstüne gitmek', 'olayı etraflıca araştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9914, 'oldu bilmek (saymak)', 'sorunu çözülmüş bilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9915, 'oldu olacak', 'hiç olmazsa: ''Oldu olacak bunu makamla söyle de bari biraz eğlenelim.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9916, 'oldu olacak, kırıldı nacak', 'her şey olup bitti, iş işten geçti.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9917, 'oldu olacak, kırıldı nacak', ' hlk. ''her şey olup bitti, iş işten geçti'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9918, 'oldu olanlar', '''hoş olmayan, kötü birtakım olaylar oldu'' anlamında kullanılan bir söz: Bize oldu olanlar!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9919, 'oldubittiye getirmek', 'geri dönülmesi güç veya olanaksız bir durum yaratmak, emrivaki yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9920, 'olduğu kadar', '1) kabul edilebilir düzeyde; 2) beklenilenin altında.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9921, 'olmayacak duaya âmin demek', 'gerçekleşmeyecek, sonuç vermeyecek işlerle uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9922, 'olmaz olmaz', '''olamayacak, imkânsız şey yoktur'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9923, 'olmaz olmaz deme, olmaz olmaz', 'hiçbir şey için ''olmaz'' deme, dünyada olmayacak şey yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9924, 'olmuş armut gibi eline düşmek', 'emeksiz ve zahmetsizce eline geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9925, 'olsa ile bulsayı ekmişler, yel ile yuf bitmiş', 'şu iş şöyle olsa, bu iş böyle olsa diyerek istediğimiz sonuca varamayız, elde etmek istediğimiz sonucu istekle değil çalışmakla gerçekleştirmeliyiz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9926, 'olsayı bulsaya vermişler, hiç doğmuş', 'şu iş şöyle olsa, bu iş böyle olsa diyerek istediğimiz sonuca varamayız, elde etmek istediğimiz sonucu istekle değil çalışmakla gerçekleştirmeliyiz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9927, '... olsun ... olsun', 'sözü geçen her şey: ''Er olsun subay olsun, harpte ölen her askerin müşterek sembolü meçhul askerdir.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9928, 'olta atmak', '1) balık yakalamak için olta takımını suya atmak; 2) mec. tuzak kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9929, 'oltaya düşmek', 'hileyle karşılaşmak, oyun veya düzen içine girmek: ''Sersem balık gibi bu oltaya düşeceklerdi.'' -H. F. Ozansoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9930, 'oltaya gelmek', 'aldatılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9931, 'oltaya takılmak', 'tuzağa düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9932, 'oltaya vurmak', 'balık yakalanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9933, 'oltayı yutmak', ' argo aldanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9934, 'oluk gibi akmak', 'çok bol ve arası kesilmeden gelmek: ''Para oluk gibi akıyordu Nahit''e.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9935, 'olup olacağı', '''hepsi bu kadar'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9936, 'olupbittiye getirmek', 'oldubittiye getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9937, 'olur a!', 'geniş zaman kipinden sonra gelerek belirsizlik, olasılık anlamı katan bir söz: ''Olur a! Ağası belki sılaya gitmiştir, yerine tabanı yarık biri gelmiştir, ona da mektubu emniyet edememiştir.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9938, 'olur almak', 'yetkili makamdan bir uygulamayı yapabilmek için yazılı izin almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9939, 'olur şey (iş) değil', '''olamaz veya gerçekleşmesi beklenmez'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9940, 'olur vermek', 'yetkili makam bir uygulamanın yapılabilmesi için yazılı izin vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9941, 'oluruna bakmak', 'bir işin yapılabilirliğini araştırmak, yapmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9942, 'oluruyla yetinmek', 'elde olanları yeterli bulmak, kanaat etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9943, 'omuz kaldırmak', '1) bilmez gibi davranmak; 2) kabul etmemek, geri çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9944, 'omuz öpüşmek', 'eşit derecede olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9945, 'omuz silkmek', 'aldırmamak, önem vermemek: ''Seni hizmetime alacağım, dedim. Âdeta omuz silkerek: -Pekâlâ, dedi.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9946, 'omuz vermek', '1) omzuyla dayanmak; 2) mec. destek olmak: ''Bu, insanı yanlış yollara itelese de bir çıkış noktası bulunmasına omuz verebilir.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9947, 'omuzda taşımak', 'çok saygı göstermek, yüceltmek, övmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9948, 'omuzları çökmek', 'bitkin, perişan ve yıkılmış bir durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9949, 'omzuna atmak', 'ceket vb. şeyleri tam olarak giymeden sırtına koymak: ''Kadifeye benzer dokumalı pahalı kumaştan paltolarını omuzlarına atmışlar.'' -C. Külebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9951, 'on para etmemek', 'hiçbir değeri olmamak: ''Güzelliğin on para etmez / Bu bendeki aşk olmasa'' -Âşık Veysel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9952, 'on para on aslanın ağzında', 'para kazanmak çok güçleşti.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9953, 'on paraya on takla (taklak) atmak', 'az bir miktar kazanabilmek için bile onursuzca bir sürü şey yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9954, 'on parmağı boğazında olmak', 'isteği yapılmadığında sıkıntıya düşmek, düşürmek: ''Haftada iki gün oruç tutmazsan on parmağım boğazında olsun, dedi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9955, 'on parmağında on hüner (marifet) olmak', 'elinden her iş gelmek, çok becerikli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9956, 'on parmağında on kara', 'herkesi lekelemek huyu olanlar için kullanılan bir söz: ''On parmağınızda on kara, iftira üstüne iftira çalıyorsunuz.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9957, 'ona buna dil uzatmak', 'herkes için ileri geri konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9958, 'onarım görmek', 'onarılmak: ''Ondan sonra da birkaç kez onarım görmüştür.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9959, 'onmadık (talihsiz) hacıyı deve üstünde (Arafat''ta) yılan sokar', 'amacını gerçekleştirmesi mümkün olmayan kişinin karşısına, hatır ve hayale gelmeyen ve yenilemeyen engeller çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9960, 'onmadık yılın yağmuru harman vakti yağar', 'zamanında olduğunda büyük yarar sağlayacak bir durum, zamanı geçtikten sonra gerçekleşirse zarar bile verebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9961, 'onu bunu bırak', '''bahane arama, mazeret ileri sürme'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9962, 'onur duymak', 'onurlanmak: ''Piyesini sahneye koymaktan büyük onur duyduğunu söyledi.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9963, 'onuruna dokunmak', 'birinin gururunu, haysiyetini incitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9964, 'onuruna yedirememek', 'bir kimse, kendine duyduğu saygıyla bağdaşmayan ve onur kırıcı olay veya davranışlar karşısında tepkide bulunmak, kendine yedirememek: ''Bu haksızlığı onuruna yediremeyen Mustafa, o günden sonra bu okula gitmemek için ayak diredi.'' -E. C. Güney.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9965, 'operasyona çıkmak', 'harekât gerçekleştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9966, 'oralarda olmamak', 'işi sezmemiş gibi davranmak, anlamazlıktan gelmek: ''Fakat o hiç oralarda değildi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9967, 'oralı (bile) olmamak', 'önemsememek, umursamamak, aldırmamak, ilgilenmemek: ''Hiç oralı olmaz ve hâlinden yakınır.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9968, 'orası senin, burası benim dolaşmak (gezmek)', 'durmadan gezip dolaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9969, 'orasına burasına', 'dağınık olarak, gelişigüzel bir biçimde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9970, 'orman gibi', 'gür, çok (saç, kaş vb.).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9971, 'orman taşlamak', 'bir kimsenin düşüncesini dolaylı olarak öğrenmeye çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9972, 'orta katı kiraya vermek', ' argo gebe kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9973, 'ortada bırakmak', 'birini çok güç bir durumdayken terk etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9974, 'ortada fol yok yumurta yok', 'fol yok yumurta yok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9975, 'ortada kalmak', '1) yersiz kalmak, barınacak yer bulamamak; 2) güç bir durumda veya iki şey arasında kalmak: ''Bu plana göre kadın o sırada oyuncuyu da bırakmış olduğu için ortada kalacak.'' -İ. Aral. 3) bir şeyi hiç kimse üzerine almamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9976, 'ortadan kaldırmak', '1) saklamak; 2) yok etmek. 3) mec. öldürmek: ''Mithat Paşa ve emsalini ortadan kaldırmak için ...'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9977, 'ortadan kalkmak', 'yok olmak: ''Herhangi bir taşralı kızın artık saklı kalma olasılığı ortadan kalkmış oluyordu.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9978, 'ortadan sır olmak', 'kaybolmak, arkada iz bırakmadan gitmek: ''Böylelerinin kirayı biriktirdikten sonra bir para vermeden ortadan sır olanları çoktur.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9979, 'ortadan söylemek', 'herkesin içinde, belli bir kimseyi amaçlamadan konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9980, 'ortak etmek', 'bir şeyi paylaşmaya razı olmak, katılmaya onay vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9981, 'ortak gemisi yürümüş, elti gemisi yürümemiş', 'bir erkeğin karıları birbirleriyle anlaşabilirler ancak kardeşlerin karıları geçinemezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9982, 'ortak olmak', 'bir şeyi paylaşmak veya bir şeye katılmak: ''Kadınları hayata ortak olmayan millet öksüzdü, yarı kuvvetini kullanamıyor demekti.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9983, 'ortaklık kurmak', 'şirket, kumpanya açmak veya çalıştırmak: ''Eğer bugün hepimiz bu işe karar verir ve bir ortaklık kurarsak bu gazete çıkar.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9984, 'ortaklık öküzden, başlı başına buzağı yeğdir', 'kişinin malı çok ama bunlar ortak malıysa yalnız kendisine ait azıcık malı bulunması daha iyidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9985, 'ortalığı birbirine katmak', 'kargaşa çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9986, 'ortalığı ... götürmek (almak)', 'kaplamak: ''O gün de bir yağmur yağmıştı, ortalığı sel götürmüştü ya, o gün işte.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9987, 'ortalığı gürültüye (patırtıya) vermek', 'gereksiz bir telaşa düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9988, 'ortalığı kırıp geçirmek', '1) herkesi heyecana sürüklemek: ''Avrupa tiyatrosunda işveli gerdan kırışları, meşhur kantolarıyla, ortalığı kırıp geçirdiği zamanlar!'' -A. İlhan. 2) çok kızarak çevresindekilere bağırıp çağırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9989, 'ortalık ağarmak', 'sabah olmaya başlamak: ''Bu akşamki gerçek, ortalık ağarmadan tersine döner.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9990, 'ortalık düzelmek', 'toplum içindeki karışıklık yok olmak, tedirginlik kalmamak, maddi durum düzelmek: ''Ben de ödünç para bulsam hiç düşünmeden alırım. Ortalık elbet düzelir, öderim.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9991, 'ortalık kararmak', 'akşam olmak: ''Ortalık kararana kadar kitap okurdum.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9992, 'ortalık karışmak', 'toplumda veya devletler arasında düzensizlik baş göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9993, 'ortalık sütliman olmak', 'sakinleşmek, durulmak: ''Tam ortalık sütliman oldu derken, şimdi de bu mektup çıkmıştı karşıma.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9994, 'ortalık yatışmak', 'toplum içindeki düzensizlik ve kargaşa sona erip düzenli yaşayış yeniden başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9995, 'ortam yaratmak', 'imkân sağlamak: ''Devlet ... elverişli ekonomik bir ortam yaratmak için gerekli tedbirleri alır.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9996, 'ortama ayak uydurmak', 'çevreye uyum sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9997, 'ortama uymak', 'çevreye uyum sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9998, 'ortasını bulmak', 'ılımlı derecesini bulmak, uzlaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (9999, 'ortaya almak', 'her yanını çevirmek, kuşatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10000, 'ortaya atılmak', '1) ileri sürülmek, herkesin bilgisine sunulmak; 2) bir kimse bir işi yapmak için kendini göstermek: ''Ve yarından itibaren kılıcım -yani kalemim- elimde ortaya atılacaktım.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10001, 'ortaya atmak', 'söylemek, ileri sürmek: ''İşte bu söz üzerinedir ki Servet Bey, apartmana çıkmak emelini ciddi bir tasavvur hâlinde ortaya atmıştı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10002, 'ortaya balgam atmak', ' kaba bir iş kıvamındayken, biri herkesin zihnini bulandıracak bir söz söylemek: ''Belki Tayfur''a gönlüm vardır diye ortaya balgam atıyor.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10003, 'ortaya çıkarmak', 'delilleriyle göstermek, ispat etmek: ''Bu işteki uygunsuzluğu daha iyi ortaya çıkarmak için bir mukayese yapalım.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10004, 'ortaya çıkmak', '1) yokken var olmak, meydana çıkmak, türemek: ''Ama bizi, en azından Fahri''yle beni yadırgatan başka şeyler ortaya çıkmaya başladı.'' -A. Ümit. 2) biri kendini göstermek: ''Lanet filozofum diyerek ortaya çıkıp Allah''a ve kullara karşı hezeyan eden tımarhanelik herifler!'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10005, 'ortaya dökmek', '1) çıkarmak, göstermek; 2) açıklamak: ''Bunun için dağarcığında ne var ne yok, tümünü ortaya döker.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10006, 'ortaya düşmek', 'kadın orta malı olmak, sokağa düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10007, 'ortaya konuşmak', 'sözü hiç kimseyi hedef almadan söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10008, 'ortaya koymak', '1) herkesin görebileceği yere koymak; 2) yaratmak, yapmak; 3) açıklamak: ''En sonra da görüşlerini edebiyata kaçmayan bir açık yüreklilikle ortaya koydu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10009, 'ortaya sürülmek', 'anlatılmak, belirtilmek, söylenmek: ''Artık yazılacak satır, söylenecek söz, ortaya sürülecek düşünce kalmadı.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10010, 'ortaya yayılmak', 'herkes tarafından duyulmak: ''Günün birinde ortaya yayılan ölüm haberinde bir olağanüstülük yoktu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10011, 'oruç açmak', 'vakit geldiğinde oruç bozmak, iftar etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10012, 'oruç bozmak', 'bir şey yiyerek, içerek orucunu kesmek veya sona erdirmek: ''Akşam Rabia ile beraber oruç bozuyor, iftar ediyoruz.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10013, 'oruç tutmak', 'oruç ibadetini yerine getirmek: ''Bütün sene cumadan gayri günlerde oruç tutarım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10014, 'oruç yemek', 'oruç tutmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10015, 'Osmanlı tokadı atmak', '1) bir kimseye sert ve etkili bir biçimde tokat atmak; 2) mec. sert ve etkili bir biçimde uyarmak; 3) mec. bir kimseye üstünlüğünü kabul ettirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10016, 'Osmanlı tokadı yemek', '1) sert ve etkili bir biçimde tokat atılmak; 2) mec. sert ve etkili bir biçimde uyarılmak; 3) mec. bir kimsenin üstünlüğünü kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10017, 'osurgan (osuruklu) göte arpa ekmeği bahane', '1) zaten zayıf, hastalıklı kişi sağlığının bozulmasını önemsiz etkenlere bağlar; 2) davranışları beğenilmeyen kişi sudan sebeplerle kendisini mazur göstermeye çalışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10018, 'ot gibi', '1) bilgisiz, görgüsüz, değersiz olan; 2) amaçsız, zevk almadan (yaşamak).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10019, 'ot gibi yaşamak', 'amaçsız, beklentisiz gün geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10020, 'ot tutunmak', 'vücuttaki istenmeyen kılları düşürmek için ilaç sürünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10021, 'otomatiğe almak (bağlamak)', 'kendi kendine yeniden düzene sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10022, 'otomatiğe geçmek', 'otomatik olarak çalışmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10023, 'otorite sağlamak (temin etmek)', 'yetki kurmak veya yetki sahibi olmak: ''Reisleri de tam bir otorite temin etmiş olduğunu her vesile ile belli ediyordu.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10024, 'otu çek, köküne bak', 'kişinin kimliğini öğrenmek için soyunu sopunu bilmek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10025, 'oturup kalkmak', 'hareket etmek: ''Daha ilk gecesinden karı lafı ile oturup kalkmaya başlarsa konu nereye varır?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10026, 'otuz iki dişe keman çaldırmak', 'içecek çok soğuk olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10027, 'otuz iki dişten çıkan, otuz iki mahalleye yayılır', 'bir ağızdan çıkan söz, başkalarının ağzına geçer, her tarafa yayılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10028, 'oy vermek (kullanmak)', 'herhangi bir konuya ait tercihini belirtmek, rey vermek: ''Yıllardır, kime oy verdiğini bile bilmiyorsunuz.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10029, 'oya gibi', 'ince, güzel, zarif: ''Kadın fevkalade nazik ve güzel, çocuklar oya gibi idiler.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10030, 'oya koymak (sunmak)', 'bir konuda sonucu belirlemek için oy verilmesini istemek, sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10031, 'oydaş olmak', 'aynı düşüncede, aynı inançta olmak: ''Benimle oydaş olmayan başka gazeteci.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10032, 'oylamaya geçmek', 'oy verme işlemine başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10033, 'oylamaya koymak', 'bir toplantıdaki oy sayısını belirlemek, oy verilmesini istemek, oya sunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10034, 'oynamasını bilmeyen kız ''yerim dar'' demiş; yerini genişletmişler (bollatmışlar), gerim (yenim) dar'' demiş', 'kendisinden beklenen işi beceremeyen kişi, çeşitli engellerin işi güçleştirdiğini söyleyerek yeteneksizliğini belli etmemeye çalışır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10035, 'oynaşına inanan avrat, ersiz kalır', 'çok önemli işini bir başkasına yaptırabileceğine inanmış olan kişi, beklediği sonucu hiçbir zaman elde edemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10036, 'oyun almak', 'oyunda kazanmak, sayı sahibi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10037, 'oyun bağlamak', ' sp. güreşte rakibe bir oyun uygulayıp onu sonuçlandırmadan beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10038, 'oyun bozmak', '1) tasarlanmış bir işi yersiz ve vakitsiz olarak karıştırmak, planları altüst etmek: ''Ömer de bizimle idi ama oyunumu bozacağı için sana yüzünü göstermemiştim.'' -R. H. Karay. 2) mızıkçılık etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10039, 'oyun çıkarmak', ' sp. oyun oynamak: Millî takım güzel bir oyun çıkardı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10040, 'oyun kurmak', ' sp. 1) bir yarışmayı kazanmak için belirli bir taktik uygulamak; 2) mec. hile yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10041, 'oyun oynamak', '1) birini aldatmak, kandırmak: ''Üç aydan beri bana mütemadiyen aynı oyunu oynuyorsunuz.'' -Y. K. Karaosmanoğlu. 2) mec. hile yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10042, 'oyun vermek', 'oyunda kaybetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10043, 'oyun yapmak', ' sp. 1) güreşte rakibe oyun uygulamak; 2) mec. hile yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10044, 'oyuna çıkmak', 'oyun için sahneye çıkmak: ''Ben ilk defa oyuna çıkıyorum, beyefendi de gelmiş burada allık pudra sürüştürüyor.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10045, 'oyuna gelmek', 'aldatılmak: ''Bir oyuna geldin, onuruna yediremiyorsun.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10046, 'oyuna getirmek', 'birini tuzağa düşürmek, aldatmak: ''Orada da Arif denilen hergele bizi oyuna getirdi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10047, 'oyuna kurban gitmek', 'bir hile, düzen sonunda zarara, iftiraya uğramak: ''Yakalanan bir komşunun garazına yahut bir el birliğine yahut da bir oyununa kurban gitmiştir.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10048, 'oyunu almak', 'oyunu kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10049, 'oyunun kurallarını bilmek', 'yapılan işlerin nasıl, kimler tarafından ve hangi ilişkilerle sonuçlandırıldığına ilişkin bilgisi olmak: ''Oyunun kurallarını baştan beri bilen biri olarak şimdiye kadar sömürülmemiştim.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10050, 'öbür dünyayı boylamak', 'ahireti boylamak: ''O rahmetli katırın yerine ben öbür dünyayı boylardım.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10051, 'öcün iyisi, bağışlamaktır', 'kötü bir davranış veya sözü, aynı biçimde karşılık vererek cezalandırmak yerine affetmek en büyük erdemdir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10052, 'öç (öcünü) almak (çıkarmak)', 'yapılan bir kötülüğün acısını kötülük yaparak çıkarmak, intikam almak: ''Sen öz babanın öcünü alamadın diye o da dedesinin ahını yerde mi koyacaktı?'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10053, 'ödev bilmek (saymak)', 'bir şey yapmayı kendisi için yerine getirilmesi zorunlu bir iş olarak kabul etmek, borç bilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10054, 'ödü bokuna karışmak', ' kaba çok korkmak: ''Fırsatını bulsa pencereden atlayıp kaçacak, öyle de ödü bokuna karışmış.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10055, 'ödü kopmak (patlamak)', 'çok korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10056, 'öfke baldan tatlıdır', 'öfkeye kapıldığında bağırıp çağırmak insanı rahatlatır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10057, 'öfke ile kalkan ziyanla (zararla) oturur', 'öfkesine kapılarak iş gören sonunda güç duruma düşer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10058, 'öfke yüzü göstermek', 'çok sinirlendiğini belli etmek: ''Hayatında kimseye sert muamele etmedi ve öfke yüzü göstermedi.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10059, 'öfkeden deliye dönmek', 'fazla sinirlenmek: ''Torununu gizlice tavan arasında saklamakta olduğunu öğrendiğinde öfkeden deliye dönmüştü.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10060, 'öfkesi başına sıçramak (çıkmak, vurmak)', 'çok öfkelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10061, 'öfkesi kabarmak', 'çok kızmak, sakinleşmişken yeniden öfkelenmek, tekrar sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10062, 'öfkesini kusmak', 'kızgınlıkla ağır hakaret etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10063, 'öfkesini yenmek', 'iradesini kullanarak öfkesini gidermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10064, 'öfkeye kapılmak', 'çok sinirlenmek, kızmak, hiddetlenmek: ''Siz gelin de böyle bir adamın herhangi bir öfkeye kapılacağını tahmin edin.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10065, 'öğür olmak', 'çokça birlikte bulunmaktan çok sıkı bir alışkanlık edinmek: ''Çoluk çocuk öylesine öğür oldular ki anları dışarıdan gören pekâlâ çok nüfuslu tek bir aile sanabilirdi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10066, 'öğüreceği gelmek', 'çok iğrenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10067, 'öğütte bulunmak', 'öğüt vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10068, 'ökseye basmak', 'dikkatsizlik ederek zarara uğramak veya yanılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10069, 'öksürük tutmak', 'sürekli ve şiddetli öksürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10070, 'öksürüp tıksırmak', 'öksürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10071, 'öksüz güler mi meğer yanıla', 'işleri ters giden kimsesiz kişinin yüzü gülmez, yüzünü güldürecek bir durum ortaya çıksa bile bu bir yanlışlıktan dolayıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10072, 'öksüz hırsızlığa çıkarsa ay ilk akşamdan doğar', 'talihsiz kimse bir şeyden yararlanmaya kalkışsa karşısına akla gelmedik engeller çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10073, 'öksüz kalmak', '1) anası veya hem anası hem babası ölmüş olmak; 2) kimsesiz olmak: ''O güne kadar yalnızlığımı pek o kadar duymamıştım, birden öksüz kaldım.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10074, 'öksüz kuzu toklu (öveç) olmaz', 'kimsesiz, koruyucusuz kimse ilerleyemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10075, 'öksüz neden güler? Yanılır da güler', 'işleri ters giden kimsesiz kişinin yüzü gülmez, yüzünü güldürecek bir durum ortaya çıksa bile bu bir yanlışlıktan dolayıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10076, 'öksüz oğlan (çocuk) göbeğini kendi keser', 'koruyanı, yardım edeni bulunmayan kişi, işini kendi başına görmek zorunda kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10077, 'öksüz oynaşa çıkmış, ay akşamdan doğmuş', 'talihsiz kimse bir şeyden yararlanmaya kalkışsa karşısına akla gelmedik engeller çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10078, 'öksüzün karnına vurmuşlar (öksüzü dövmüşler) ''vay arkam'' demiş', 'bir kimsenin haksızlığa uğramaması için arkası, koruyucusu bulunmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10079, 'öküz arabası gibi', 'çok yavaş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10080, 'öküz gibi', 'aptal, anlayışsız bir biçimde: ''Usta şoför olsa tramvay fren yapınca bunu sezer, gelip öyle öküz gibi bindirmezdi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10081, 'öküz gibi bakmak', 'karşısındakini rahatsız edercesine bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10082, 'öküz öldü, ortaklık bozuldu (bitti)', 'iki ortak veya taraf arasındaki yakınlığın dayandığı sebep yok olduğunda bu yakınlık da çözülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10083, 'öküz, yem bitince çifte gideceğini bilir', 'çalışmanın önemini kavramak için aç kalmak gerekmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10084, 'öküze boynuzu yük olmaz (ağır gelmez)', 'insan kendi yakınlarını ve kendi işlerini yük saymaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10085, 'öküzün altında buzağı aramak', 'olmayacak sebeplerle suç ve suçlu bulma çabasında olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10086, 'öküzün trene baktığı gibi bakmak', 'aptalca, hiçbir şey anlamadan bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10087, 'ölçü almak', '1) herhangi bir şeyin boyutlarını ölçmek; 2) terzi vücut ölçülerini tespit etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10088, 'ölçülü olmak', 'dikkatli, hassas, düşünceli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10089, 'ölçüp biçmek', 'bir konuda çok ayrıntılı düşünmek, inceden inceye düşünmek, değerlendirmek: ''Değer yargılarımızı her an, hiç durmamacasına yeniden ölçüp biçmek zorunluluğumuz, işte bu aşağılanma sorunundan kaynaklanıyor.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10090, 'ölçüyü kaçırmak', 'yiyip içmekte veya davranışlarda aşırı gitmek: ''Ateşli tartışmalara girdiği zaman bile ölçüyü kaçırmazdı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10091, 'ölenle ölünmez', 'çok sevilen birinin ölümünden sonra fazla yas tutulmamalıdır çünkü hayat devam eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10092, 'ölme eşeğim, ölme (yaza yonca bitecek)', 'umutsuz bir bekleyişi anlatmak için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10093, 'ölmek var, dönmek yok!', 'neye mal olursa olsun bu iş yapılacak, yapılmasından kaçınılmayacak.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10094, 'ölmüş de ağlayanı yok', 'çok kötülüğe uğramış, desteği ve yardım edeni bulunmayan kişi için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10095, 'ölmüş koyun (eşek) kurttan korkmaz', 'her şeyini kaybetmiş olan kişinin düşmanına bir şey kaptırmaktan korkusu olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10096, 'ölü evinde ağlamasını, düğünevinde gülmesini bilmeli', 'insan içinde bulunduğu çevrenin durum ve koşullarına uygun biçimde davranmasını bilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10097, 'ölü gibi', '1) hiç kımıldamadan: ''Arkadaşlarım ölü gibi uyuklarken, ben sabahlara kadar dans ediyordum.'' -R. N. Güntekin. 2) kımıldamayan, hareketsiz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10098, 'ölü gözü gibi', 'sönük, fersiz (ışık).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10099, 'ölü gözü kadar', 'çok az: ''Üç yıldır bizim oralarda kuraklık var. Hele bu yıl ölü gözü kadar rahmet görmedik.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10100, 'ölü gözünden yaş ummak', 'hiç olmayacak yerden, mümkün olmayan durumda yardım veya destek beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10101, 'ölüevi gibi', 'üzüntülü, sessiz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10102, 'ölüm Allah''ın emri', '1) ''herkes ölecek, ölmek kaçınılmazdır'' anlamında kullanılan bir söz; 2) tehlikeli bir karar verme durumunda ''ölümden korkmuyorum, ölümü bile göze alıyorum'' anlamında kullanılan bir söz: Ölüm Allah''ın emri, bu işi yapacağım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10103, 'ölüm bir kara devedir ki herkesin kapısına çöker', 'her eve gelin girmeyebilir ama ölüm kesinlikle girer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10104, 'ölüm dirim (kalım) bizim için', 'insan nasıl olsa öleceğini düşünmeli, işlerini ona göre düzene koymalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10105, 'ölüm döşeğinde olmak', 'son anlarını yaşamak: ''Avrupa medeniyeti de ölüm döşeğindedir ama bu ölüme bir türlü katlanamaz yazarın gönlü.'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10106, 'ölüm gibi', 'çok büyük sıkıntı, üzüntü: ''Sürgün benim için ölüm gibi bir şey olmuştu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10107, 'ölüm hak miras helal', 'ölümün olağan olması gibi mirasın da paylaşılması olağandır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10108, 'ölüm kalım meselesi (savaşı) yapmak (olmak)', 'yok olmamak amacıyla mücadeleye girişmek: ''Kurtuluş Savaşı''nda bir ölüm kalım savaşı içinde idik.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10109, 'ölüm ölüm de, hırlamaya ne borcum var?', 'sıkıntı, üzüntü, keder, dert veya yoksulluk çekmektense ölüm daha iyidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10110, 'ölüm sessizliği çökmek', 'yoğun ve derin bir sessizlik kaplamak: ''Masanın başına oturduğum zaman ortalığa gerçekten ölüm sessizliği çöktü.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10111, 'ölüm var dirim var', 'insan nasıl olsa öleceğini düşünmeli, işlerini ona göre düzene koymalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10112, 'ölüm yüz aklığı (-dır)', 'yüz kızartıcı bir yaşantı içinde bulunanların durumunu ancak ölmeleri temizler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10113, 'ölümden öte köy yoktur', 'her insan bir gün öleceğini bilmeli, sonu gelmez istekler peşinde koşmamalı, davranışlarını, işlerini bu bilinçle düzene koymalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10114, 'ölüme çare bulunmaz', 'ölüm dışında, çaresiz gibi görünen her güç işin bir çıkar yolu vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10115, 'ölüme koşmak', 'kendisini bile bile tehlikeye atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10116, 'ölümle burun buruna gelmek', 'ölümle sonuçlanabilecek çok büyük bir tehlike ile karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10117, 'ölümle öç alınmaz', 'düşmanların ölümünden sevinç duymak insanlığa yakışmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10118, 'ölümle pençeleşmek', 'can çekişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10119, 'ölümü gör (öp)', 'bir konuda karşısındakini ikna etmek için kullanılan yemin sözü: ''Sevim, Beyhan''ın ölümü öp diye ısrarla getirdiği pastasından bir dilim yedi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10120, 'ölümü gören hastalığa razı olur', 'küçük bir zarara uğramayı kabul etmezse çok büyük bir zarara uğrayacağını anlayan kimse, bu küçük zarara katlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10121, 'ölümü göze almak', 'elde etmek istediği sonuç uğruna ölüm de dâhil her türlü tehlikeye açık olmak: ''Daha İstanbul''da iken buna ahdetmiş, bu yolda ölümü göze alarak Anadolu''ya çıkmıştı.'' -E. C. Güney.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10122, 'ölümün soluğunu ensesinde duymak (hissetmek)', 'her an öleceğini beklemek, ölüm korkusu ile dolu olmak: ''Yüz yaşından daha çok insan ne kadar yaşar ki ölümün soluğunu ensemde duyuyorum.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10123, 'ölümün yüzü soğuktur', 'araya ölüm girdiğinde sevgi de, düşmanlık da sıcaklığını yitirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10124, 'ölümüne susamak', 'ölümle sonuçlanabilecek davranışlarda bulunmak: ''Ölümüne susamış kimse meydana çıksın.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10125, 'ölüp ölüp dirilmek', 'çok sıkıntı, acı çekmek veya çok ağır hastalık geçirmek: ''Çünkü çiçek kokusu. Proust''un tıknefes nöbetlerinde ölüp ölüp dirilmesine yol açarmış.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10126, 'ölür müsün, öldürür müsün?', 'çok kızılacak bir terslik karşısında kalındığında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10127, 'ölürse yer beğensin, ölmezse el beğensin', 'çocuğun öldüğünde iyi anılması, yaşarken de beğenilir bir kişi olması için anne baba çocuklarının eğitimine çok önem vermelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10128, 'ölüsü bile yetmek', 'en zayıf olduğu durumda bile başarılı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10129, 'ölüsü ortada kalmak', 'cenazesini kaldıracak kimse bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10130, 'ölüyü güldürmek', 'çok güldürmek: ''Nadide Hanım, ilahi kadın nereden de bulur? Vallahi ölüyü güldürür, derdi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10131, 'ömre bedel', 'bir ömre değecek kadar (iyi, güzel, değerli): ''Orada ümitler ve hayal sukutlarıyla geçen, bir ömre bedel hareketli hayatı!'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10132, 'ömrü uzamak', '1) uzun süre yaşamak; 2) çok dayanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10133, 'ömrü vefa etmemek', 'bir sonuca ulaşmadan ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10134, 'ömrümün varı', 'gözümün nuru: ''Yürü dilber, yürü ömrümün varı'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10135, 'ömrüne bereket', '''ömrün uzun olsun, var ol, sağ ol'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10136, 'ömrüne ömür katmak', 'sevinmesine, mutlu olmasına sebep olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10137, 'ömür çürütmek', 'uzun zaman emek vermiş olmak veya boşuna vakit geçirmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10138, 'ömür geçirmek', 'yaşamak: ''... ihtiyar adam hazin bir ömür geçiriyordu.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10139, 'ömür sürmek', '1) iyi ve rahat yaşamak; 2) yaşamı belli şartlar içinde sürüp gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10140, 'ömürler olsun', 'eli öpülenin öpene ''çok yaşa'' anlamında söylediği bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10141, 'ömürsün', '1) beklenilmeyen iyi davranışlar karşısında kullanılan bir söz; 2) ''neşeli, hoşsohbet, komik, eğlendiren birisin'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10142, 'ön tekerlek nereye giderse art tekerlek de oraya gider', 'küçükler büyüklerin izinde gider, her işte onları örnek tutarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10143, 'önce baldın, pekmez oldun; şimdi para etmez oldun', 'davranışlarına dikkat etmeyen kişinin attığı her yanlış adım, saygınlığının biraz daha azalmasına neden olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10144, 'önce can sonra canan', 'insanlar bencildir, önce kendilerini, sonra yakınlarını düşünürler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10145, 'önce iğneyi kendine batır, sonra çuvaldızı ele', 'başkasına zararı dokunacak bir davranışı yapmadan önce iyi düşün, kendi kendini eleştir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10146, 'önde gelmek', 'önemli durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10147, 'önde giden yorulur, arkada kalan soyulur', 'olumlu veya olumsuz konularda aşırıya kaçmak seni zor durumda bırakır; ne büyük ceza görecek kadar atılgan, dikbaşlı ol ne de herkes seni ezecek kadar yumuşak başlı ol.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10148, 'öne almak', 'bir şey veya bir kimseye öncelik tanımak: ''Sıraya koyunca en önemlisini öne almak lazım geldi.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10149, 'öne çıkmak', 'diğerlerinden daha iyi olmasından dolayı dikkat çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10150, 'öne düşmek', '1) önden yürümek; 2) kılavuzluk etmek: ''Siz öne düşün. Ne derseniz onu deriz.'' -A. Rasim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10151, 'öne sermek', 'ortaya koymak, meydana çıkarmak, göstermek: ''Yendiğimiz orduların bize üstün gelişi, bu çok acı hakikati önümüze serdi.'' -O. S. Orhon.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10152, 'öne sürmek', '1) birini ilk önce harekete geçmesi için önermek; 2) ileri sürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10153, 'öneride bulunmak', 'önermek, teklif etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10154, 'önü alınmak', 'önlenmek: Yangının önü alındı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10155, 'önü sıra gitmek', 'önünde yürümek: ''Bir gün, önüm sıra giden bir genç çocuk, ıslıkla bir şeyler çalmaya başladı.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10156, 'önünde ardında gidilmez', 'arkadaşlığına güvenilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10157, 'önüne arkasına bakmadan', 'iyi hesap etmeden, düşüncesizce.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10158, 'önüne bakmak', 'utanmak, utancından cevap vermemek: ''Önüne bakmıştı Mevlüt, ne diyeceğini bilemeden.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10159, 'önüne bir kemik atmak', 'ağzına bir kemik atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10160, 'önüne çıkmak', '1) rastlaşmak, karşılaşmak, karşısına çıkmak: ''Neden hiçbir korsan filosu önümüze çıkamadı?'' -F. F. Tülbentçi. 2) mec. ilk defa görmek, yüz yüze gelmek: ''Kim olursa olsun önüme çıkanla yeniden evleneceğim.'' -S. F. Abasıyanık. 3) yolunu kesmek için birdenbire karşı durmak: ''Kasabaya kömür indiren dağ köylülerinin önlerine çıkıp yol kesen haydutlar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10161, 'önüne dikilmek', '1) gelip karşısında durmak, karşısına dikilmek; 2) karşısındakine engel olmak istediğini söz veya davranışıyla göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10162, 'önüne düşmek', '1) birinin önünden yürümek: ''Adam hemen geldi, önüne düştü, konuşmadan evine vardılar.'' -Y. Kemal. 2) birine kılavuzluk etmek: ''Delikanlı Haydar ustanın önüne düştü, Hasip Bey''in evine geldiler.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10163, 'önüne geçmek', '1) yolunu kesmek; 2) önlemek: ''Bütün siyasi tedbirler öyle bir tehlikeli hareketin önüne geçmek için alınmıştı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10164, 'önüne gelen', 'olur olmaz (kimse): ''Mektep çocukları henüz dinlemesini biliyor, önüne gelen şiir yazma hevesine kapılarak gülünç olmuyordu.'' -A. H. Çelebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10165, 'önüne geleni kapar, ardına geleni teper', 'arsız, huysuz, geçimsiz kimseler için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10166, 'önüne katmak', 'önden yürütüp kendisi ardı sıra gitmek: ''Hep birden ayağa kalktılar, bizi de önlerine kattılar, yola düştük.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10167, 'önünü ardını düşünmemek', 'sonucun ne olacağını hesaplamamak, ilerisini gerisini düşünmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10168, 'önünü kesmek', '1) yolunu kesmek; 2) akarsuyun akmasına engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10169, 'öp babanın elini', ' tkz. beklenmedik, elverişsiz bir durum karşısında ''şimdi ne olacak?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10170, 'öperken ısırmak', 'güler yüz gösterirken kötülük yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10171, 'öpmeye niyeti olmayan ''yanağın nerede'' diye sorar', 'bir işi yapmaya niyeti olmayan, istek duymayan kişi, bunu davranışlarıyla belli eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10172, 'öpücük göndermek (yollamak)', 'parmaklarının iç ucunu öpüp birine atar gibi yaparak onu selamlamak: ''Kocaman avuçlarından bir öpücük gönderdi.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10173, 'öpücük kondurmak', 'hafifçe öpmek: ''Avucunun içine çikolata kokulu buz gibi bir dudak, bir öpücük kondurdu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10174, 'öpülecek el ısırılmaz', 'saygı gösterilmesi gereken kimse incitilmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10175, 'öpüp başına koymak', '1) bir nimeti veya kutsal sayılan bir varlığı saygıyla el üstünde tutmak, yüksekte tutmak; 2) bir şeyi memnunlukla karşılamak, saygı duymak, saygıyla karşılamak: ''Ne dediği bilinmez, anlaşılmaz, kapalı kutu şiirleri öpüp başımıza koymak lazım geliyor.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10176, 'örnek almak', '1) bir kimseye huy ve davranışta uymak, birini ölçü olarak benimsemek: ''Atatürk sarı bıyıklarını kestiğinden bu yana devlet adamlarının çoğu onu örnek aldılar.'' -H. Taner. 2) bir şeyden kendisi için ders çıkarmak: Bu çocuk babasını örnek alıyor. 3) incelemek üzere insan ve hayvan vücudunun veya bitkinin herhangi bir yerinden doku parçası almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10177, 'örnek olmak', 'hayır ve davranış yönünden başkasının kendisine benzemesi yolunda etkili olmak: ''Ne örnek olmaya değerim ne de gülünç olmaktan zevk alırım.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10178, 'örnek oluşturmak', 'benzerini sunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10179, 'örnek vermek', 'bir konuyu daha ayrıntılı bir biçimde anlatabilmek için örneklendirmek: ''Son olarak bir başka yazarın kaleminden, tiyatro-nun önemi, en etkili eğitim aracı olduğu görüşüne bir örnek verelim.'' -M. And.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10180, 'örs ve çekiç arasında kalmak', 'aynı derecede güçlü ve zorlu iki kişi veya düşünce arasında bulunmak: ''Bana örs ve çekiç arası bir durumda kaldığından yakınmıştı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10181, 'ört ki ölem', 'çok önemli şeyleri elde edemeyen kişilerce ''Nasıl yaşarım?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10182, 'örtüye sokmak (koymak)', 'örtünmesini sağlamak: ''Kız Ayşe, anana söyle, seni örtüye soksun.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10183, 'örülü olmak', ' ed. her şeyiyle mükemmel, eksiksiz ve estetik bütünlüğe sahip bulunmak: ''Üçüncü itiraz, aruza, bütün yüksek şiirimizin örülü olduğunu görüp de sadık kalmak isteyenlerden geliyor.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10184, 'ötesi var mı?', '''daha diyecek var mı?'' anlamında kullanılan bir söz: ''Hasta da olsalar yapmıyorum işte! Ötesi var mı? İşte başhekim, git söyle.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10185, 'ötesi yok', '''diyecek daha bir şey yok'' anlamında kullanılan bir söz: ''Ötesi yok, bütün sinirlerim, iliklerim âşık oluverdi işte!'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10186, 'övünç duymak', 'iftihar etmek, kıvanmak: ''Sevgili eşini kaçırarak almış olmaktan büyük övünç duyardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10187, 'övünmek gibi olmasın', 'kendini övmeye hazırlanan kimselerce, övünmesini hoş göstermek veya alçak gönüllü görünebilmek için kullanılan bir söz: Övünmek gibi olmasın, sesim güzeldir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10188, 'öyle olsun', 'peki, pekâlâ.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10189, 'öyle veya böyle', 'ne olursa olsun, her hâlde, bu durumda: ''Öyle veya böyle, bir amatör, bir heveskâr işte.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10190, 'öyle ya', 'kuşkusuz, tabii, elbette: ''Öyle ya içgüdü, ilgili bilim adamlarına göre, insandan çok hayvan türlerinde varsaydığımız bir özellik.'' -N. Uygur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10191, 'öyle (yağma) yok!', '''öyle bir şey olmaz, öyle bir şey yapılmamalı'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10192, 'özen göstermek', 'bir şeyi özenerek elden geldiğince iyi olmasına gayret ederek yapmak, itina etmek: ''Çay bitmesin diye yudum yudum içmeye büyük özen gösterirler.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10193, 'özenip bezenmek', 'bir işi ayrıntılarına varıncaya değin büyük bir özenle ve titizlikle yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10194, 'özgü olmak', '1) birine, bir şeye ait olmak; 2) belli bir kimsede, şeyde veya türde bulunmak; 3) aynı cinsten başka hiçbir türde veya bireyde rastlanılmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10195, 'özlemini çekmek', 'arzulamak, çok özlemek, hasretini çekmek: ''Mustafa Kemal Paşa, özlemini çektiği bir yuvaya kavuşmuştur.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10196, 'özlemini duymak', 'yürekten istemek, arzu etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10197, 'özrü kabahatinden büyük', 'bir suç veya kabahat için özür dilerken daha büyük suç işleyen kimseler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10198, 'özü sözü bir (olmak)', 'söylediği söz ile yaptığı iş veya davranışları örtüşen, tutarlı olan: ''Onların özü sözü birdir. Hayatları bizim için örnektir.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10199, 'özür dilemek', '1) özrünü ileri sürerek bir işi yapmayı istememek, bir işten bağışlanmasını istemek: ''Onları, ayakta bekleyenleri görünce özür diledi.'' -N. Araz. 2) yaptığı bir yanlıştan ötürü bağışlanmasını istemek: ''Karyolasına ilişti, odası için özür dileyip dilememeyi düşündü.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10200, 'pabucu dama atılmak', 'kendinden üstün birinin çıkmasıyla gözden düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10201, 'pabucuna kum dolmak', 'pabucuna taş kaçmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10202, 'pabucuna taş kaçmak', 'ortaya çıkan durum karşısında tedirgin olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10203, 'pabucunu dama atmak', 'kendinden üstün birini gözden düşürmek: ''Her girdiği çevrede öbür kadınların pabucunu dama atmış, hep birinci kadın rolüne çıkmıştı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10204, 'pabucunu eline vermek', 'dolaylı olarak kovmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10205, 'pabucunu ters giydirmek', 'güç bir duruma sokarak telaşla kaçırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10206, 'pabuç eskitmek (paralamak)', 'bir iş için bir yere çok gidip gelmek, işi takip etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10207, 'pabuç kadar dili olmak', 'kabaca ve terbiyesizce karşılık vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10208, 'pabuç pahalı', '1) birinin uğraşmaya kalktığı kimsenin, kendinden güçlü çıkması durumunda söylenen bir söz: Baktı pabuç pahalı, işi şakaya vurdu. 2) herhangi bir durum veya girişilen işin sonunda zararlı çıkma ihtimali bulunduğunu belirten bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10209, 'paçaları sıvamak', 'kolları sıvamak: ''Paçaları sıvadı, bir beygir kiralayıp köy köy dolaştı.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10210, 'paçalarından akmak', 'pislik ve kir çok olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10211, 'paçası tutuşmak', 'telaşlanmak: ''Ne oldu, yine Esma''nın paçası mı tutuştu?'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10212, 'paçasından tutup atmak', 'hakaretle kovmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10213, 'paçasını çekecek (toplayacak) hâli olmamak', 'güçsüz, beceriksiz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10214, 'paçavra gibi', 'değersiz (kimse veya şey).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10215, 'paçavrasını çıkarmak', 'paçavraya çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10216, 'paçavraya çevirmek', 'çok hırpalamak, dağınık, bozuk veya berbat bir duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10217, 'paçayı kaptırmak', '1) yakalanmak, ele geçirilmek; 2) karıştığı ancak sonradan ayrılmak istediği bir işten kendini kurtaramamak; 3) dilediği gibi davranamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10218, 'paçayı kurtarmak (sıyırmak)', 'kendini bir dertten, tehlikeden veya zor durumdan kurtarmak: ''Varımızı yoğumuzu teknenin oturmamış tarafına aktararak paçayı kurtardık.'' -B. R. Eyuboğlu. ''Bu kadar çapraşık işlerin üstesinden gelip paçayı sıyırdığından haklı olarak gurur duyuyordu.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10219, 'padişahın bile arkasından kılıç sallarlar', 'kendisinden çekinilen kimsenin yüzüne karşı kimse bir şey söylemez ama arkasından herkes düşmanlık gösterilerinde bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10220, 'paha biçilmez', 'değeri ölçülemeyecek kadar yüksek: ''Başında ağır ve paha biçilmez emsalsiz ve füsunlu bir taç gibi duruyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10221, 'paha biçmek', 'değerini tahmin etmek veya belirlemek: ''Alınıp satılan eşyalar gibi ona paha biçmek akılsızlık olurdu.'' -S. Ayverdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10222, 'pahalı alan aldanmaz', 'ucuz olan mal çabuk eskir, pahalıya alınmış gibi olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10223, 'pahalıya gelmek', 'fiyatı çok yüksek olmak: ''Bizim için kara kumaş pahalıya geliyor olacak, ortaya daha çok ot sap tıkıştırılıyor.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10224, 'pahalıya patlamak (mal olmak, oturmak)', 'çok para, özveri, emek gerektirmek, kolay elde edilememek veya zarara, sıkıntıya yol açmak: ''Bir tecrübe geçirmek, sana müthiş pahalıya mal oldu.'' -P. Safa. ''Bu gözü peklik, bu aşağılık anlaşma bana pahalıya patlayabilir.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10225, 'pahaya çıkmak', 'pahalanmak, pahalılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10226, 'pahaya geçmek', 'değerli bir şeymiş gibi esirgenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10227, 'pala çalmak (sallamak)', 'uğraşmak, didinmek, çabalamak: ''Üstelik gazetecilikte de yıllarca pala çaldı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10228, 'pala çekmek', 'palayı belinden çıkarıp vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10229, 'pala sürtmek', 'çabalamak, uğraşmak: ''Biz de az çok pala sürttük.'' -M. A. Ersoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10230, 'palamarı koparmak (çözmek)', ' argo kaçmak, sıvışmak: ''Bir kere palamarı çözmeye muvaffak olsa bir yere kapağı atmanın çaresini bulabilirdi.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10231, 'palamut çok biterse, kış erken olur', 'kimi olaylar onu izleyecek olayların da habercisidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10232, 'palan vurmak', 'palanı hayvanın sırtına koyup bağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10233, 'palavra atmak (savurmak, sıkmak)', ' argo 1) abartarak konuşmak, büyük başarılardan söz etmek: ''Yalana yakın palavralar savurmaktan kendini alamayan Ragıp Bey, bu sefer tamamıyla masumdu.'' -Y. K. Karaosmanoğlu. 2) uydurma, asılsız bir söz veya haberi gerçekmiş gibi ortaya atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10234, 'paldımı aşmak', 'başaramayacağı bir işe girişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10235, 'palet vurmak', 'dipte yüzerken yükselme amacıyla paletlerle suyu dövmek: ''Tepti yeri, var gücüyle palet vurarak yükseldi, kaldırdı başını.'' -Y. Koray.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10236, 'palyaço gibi', 'gülünç olacak derecede acayip kılıklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10237, 'pamuk atmak', 'yay ve tokmakla pamuğu ditmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10238, 'pamuk gibi', '1) çok yumuşak; 2) iyi huylu, munis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10239, 'pamuk ipliğiyle bağlı olmak', 'pamuk ipliğiyle bağlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10240, 'pancar gibi olmak', 'yüzüne kan hücum edip çok kızarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10241, 'pancar kesilmek', 'pancar gibi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10242, 'pandik atmak', 'elle sarkıntılık etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10243, 'pandik yemek', 'elle sarkıntılığa uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10244, 'pandomim kopmak', 'izleyenler için eğlendirici bir kavga çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10245, 'paniğe kapılmak', 'çok korkmak: ''Kendisi ile birlikte gelemeyeceğini anlayınca tam bir paniğe kapıldı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10246, 'paniğe vermek', 'büyük bir dehşete düşürmek, çok korkutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10247, 'panik yaratmak', 'korku, dehşet uyandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10248, 'papağan gibi ezberlemek', 'anlamını bilmeden ezberlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10249, 'papağan gibi tekrarlamak', 'peş peşe, art arda söylemek: ''Dört beş ay uğraşarak papağan gibi tekrarladığım ilk cümleleri duyan Fransızlar gülmeye başladılar.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10250, 'papara (paparasını) yemek', 'azar işitmek: ''Mebrure ... Nesrin''in paparasını yedikten sonra başını yere eğerek kuyruğunu sallayan büyük köpeği gördü.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10251, 'papaz gibi', 'saçı, sakalı uzayıp birbirine karışmış (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10252, 'papaz her gün pilav yemez', '1) bir insanı hep aynı hileyle kandıramazsın; 2) insan zaman zaman değişiklik ister; 3) her zaman aynı fırsat ele geçmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10253, 'papaz olmak', 'çıkarları ters düştüğü için sürtüşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10254, 'papaz uçurmak', ' argo içkili eğlence düzenlemek: ''Bu gece beş, on para çıkarırsan izinli gecemde papaz uçururuz.'' -K. Tahir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10255, 'papaza dönmek', 'saçı ve sakalı uzamak, darmadağın olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10256, 'papaza kızıp oruç (perhiz) bozmak', 'başkasına kızıp kendisine zarar verecek iş görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10257, 'papazı bulmak', 'beklemediği kötü bir sonuçla karşılaşmak, belasını bulmak: ''Her işin tehlikesine ortak. Benim başım derde girerse o da papazı bulur.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10258, 'papazlık etmek', 'ders vermek, ikna edici sözlerle kandırmak: ''Aldırma. Bana da papazlık etmeye kalktı ama ağzının payını verdim. Biz keyfimize bakalım.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10259, 'par par yanmak', 'yüksek ateşi olmak; bir yanıp bir sönmek; ışıl ışıl parlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10260, 'para basmak', '1) darphanede, basımevinde metali veya kâğıdı para durumuna getirmek; 2) mec. kumarda ortaya para koymak; 3) mec. çok kazanmak; 4) mec. çok kazandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10261, 'para bozmak', 'büyük parayı ufak paralarla değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10262, 'para çekmek', 'bir yere yatırılmış paradan bir bölümünü geri almak: ''Murat Bey artık açık kapatmak için bankadan para çekmiyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10263, 'para çıkarmak', '1) para basmak; 2) başka yerde bulunan kimseye posta veya banka ile para göndermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10264, 'para çıkışmamak', 'para yetişmemek: ''Emine göğsünün altından çıkardığı rutubetli bir meşin çantanın orta gözünü açtı, hesapladı, kırk para çıkışmıyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10265, 'para dökmek (akıtmak)', 'çok para harcamak: ''Avuç avuç bu fettan kadına para dökerler de doktora on kuruş vermeyi çok görürler.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10266, 'para dönmek', 'rüşvetle iş yapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10267, 'para etmek', 'değeri olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10268, 'para etmemek', '1) değeri pahasına satılamamak; 2) etkisi olmamak, işe yaramamak: ''Tastamam geriye dönmedik ama dönsek de para etmez.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10269, 'para getirmek', 'kazanç sağlamak: ''Hiç ömrümde bir saatimin bu kadar para getirdiğini bilmiyordum.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10270, 'para ile değil', 'çok ucuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10271, 'para ile değil, sıra ile', 'herkes sırasını beklemek zorundadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10272, 'para ile imanın kimde olduğu bilinmez', 'kimin ne kadar parası bulunduğunu, kimin ne kadar Tanrıya yakın olduğunu kimse bilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10273, 'para isteme benden, buz gibi soğurum senden', 'kişi, kendisinden para isteyen kimseden uzak durmak ister.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10274, 'para kesmek', '1) para basmak; 2) mec. çok para kazanmak: ''Büyük para kesiyor, yeni yeni bilezikler alıyor.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10275, 'para kırmak', 'çok kazanmak: ''Ayda üç yüz liradan para kırıyorsun, halis muhlis burjuvasın.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10276, 'para parayı çeker', 'elde para bulunursa onunla yeni paralar kazanılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10277, 'para peşin, kırmızı meşin', 'her işin karşılığı anında ödenmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10278, 'para pul tutmamak', 'hesabını bilmemek, birikim yapmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10279, 'para saçmak', 'gereğinden çok para harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10280, 'para saymak', 'ödemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10281, 'para sızdırmak (koparmak)', 'zorlayarak veya kandırarak birinden para almak: ''Ben kızımı bilirim, bu tartışmaları bahane ederek Metin''den para sızdıracaktı.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10282, 'para tutmak', 'para biriktirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10283, 'para yapmak', 'para kazanıp biriktirmek: ''Açıkhava''da, Maksim''de verdiği temsillerle kısa zamanda ün ve para yaptı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10284, 'para yatırmak', 'gerektiğinde almak üzere bir yere para vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10285, 'para yedirmek', '1) gereksiz olarak başkasına çok para harcamak; 2) rüşvet vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10286, 'para yemek', '1) gereksiz olarak çok para harcamak; 2) çok para harcatmak; 3) görevli bulunduğu yerin imkânlarından yararlanarak para çalmak, rüşvet almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10287, 'paradan çıkmak', 'para harcamak zorunda kalmak: ''Canım ne lüzumu var, paradan çıkıyorsun diye âdeta beni azarlıyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10288, 'paran çoksa (borcun yoksa) kefil ol, işin yoksa şahit ol', 'tanıklık boş oturan kimselerin, kefillik ise parası çok olan kimselerin işidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10289, 'paran gitti mi diye sormazlar, işin bitti mi diye sorarlar', 'yapmak istediğin işi yapabildinse bu uğurda harcadığın paralara acıma çünkü para istediğin şeyi yapmak içindir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10290, 'paran varsa cümle âlem kulun, paran yoksa tımarhane yolun', 'zengin olana herkes hizmet eder, fakir olana ise kimse yüz vermez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10291, 'paranın gittiğine bakma, işinin bittiğine bak', 'yapmak istediğin işi yapabildinse bu uğurda harcadığın paralara acıma çünkü para istediğin şeyi yapmak içindir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10292, 'paranın üstü', 'satın alınan şeyin tutarından artan para.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10293, 'paranın yüzü sıcaktır', 'para çekicidir ve geri çevrilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10294, 'parantez açmak', 'söz veya yazının içine, sözü edilen konu ile ilgili bir bölüm koymak. 2) mec. anlatılan konudan farklı bir şey söyleneceği zaman kullanılan bir söz: ''Şimdi burada yeni bir uzun parantez açmak ve bu dağ gezintisi hikâyesinden çok gerilere dönmek ihtiyacını duyuyorum.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10295, 'parası (akçesi) ucuz olanın kendisi kıymetli olur', 'parasını esirgemeyen, cömert kimseyi herkes el üstünde tutar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10296, 'parasını çıkarmak', 'anaparayı kurtarmak, masrafını çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10297, 'parasını sokağa atmak', 'değeri olmayan bir mala para vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10298, 'parasıyla rezil olmak', 'para vererek yaptırdığı bir şey iyi çıkmamak, parasının karşılığını alamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10299, 'parasızlık çekmek', 'para yönünden sürekli sıkıntı içinde olmak: ''Ömrünün büyük bölümünde parasızlık çekmiş olan bir çeşit kumarbazdı.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10300, 'paraya çevirmek', 'herhangi bir şeyi para ile değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10301, 'paraya kıymak', 'gereken yerde para harcamaktan kaçınmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10302, 'paraya para (pul) dememek', '1) çok para kazanır olmak; 2) elde edilen parayı az bulmak; 3) bol para harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10303, 'paraya sıkışmak', 'parasız kalmak, para sıkıntısı içinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10304, 'parayı araya değil, paraya vermeli', 'parayı gerekli yere harcamalı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10305, '(parayı) avucuna saymak', 'peşin olarak ödemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10306, 'parayı basmak (bastırmak)', 'para vermek: ''Fiyatını söylesem şaşar kalırsın ama aldırmıyorum, basıyorum parayı alıyorum.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10307, 'parayı denize atmak', 'parayı boşuna harcamak, israf etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10308, 'parayı veren düdüğü çalar', 'para harcadığında insan istediğini elde edebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10309, 'parayı zaptetmek deliyi zaptetmekten zor', 'elindeki parayı çarçur etmeyip tutmasını bilmek herkesin yapamayacağı zor bir iştir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10310, 'parayla dost bulunmaz', 'para kazanmayı bildiği hâlde dost kazanmayı bilmediği için sevilmeyen, sayılmayan nice insan vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10311, 'parçalı bohça gibi', 'birbirini tutmaz parçalardan oluşan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10312, 'parmağına dolamak', 'bir konuyu, bir kimseyi ele alıp sürekli uğraşmak, diline dolamak: ''Çarşının alaycıları, gevezeleri ... Halil''in yüreğinin yandığını anlayınca onu parmaklarına doladılar, ateşini körüklemeye başladılar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10313, 'parmağını bile kıpırdatmamak (oynatmamak)', 'bir iş için hiçbir davranışta bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10314, 'parmağını yaranın üzerine basmak', 'asıl derdi veya bir derdin asıl sebebini göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10315, 'parmağının ucuyla (ucunda) çevirmek', 'bir işi kolayca ve ustalıkla yapabilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10316, 'parmak atmak', 'sorun yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10317, 'parmak bozmak', 'çocuklar arasında arkadaşlığı sona erdirmek, küsmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10318, 'parmak ısırmak', 'büyük şaşkınlık duymak: ''Hele geçen gün o Meşincioğlu Kerim Bey''e yaptığın işe parmak ısırdım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10319, 'parmak kadar', 'çok küçük: ''Ne istersin çocuk, çocuktan? dedi. Daha parmak kadar, kemikleri kırılacak, öyle ince.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10320, 'parmak kaldı', '''az kaldı, az kalsın, neredeyse'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10321, 'parmak kaldırmak', 'bir toplulukta söz istemek için işaret parmağını açık bırakarak kapalı eli yukarı kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10322, 'parmakla gösterilmek', '1) bir şey az bulunmak; 2) seçkin, ünlü olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10323, 'parmakla sayılmak', 'çok az olmak: ''Liderin dehasına gerçekten inanmış olanlar parmakla sayılacak kadar azdı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10324, 'parmaklarını (birlikte) yemek', 'yemeği çok beğenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10325, 'parsayı başkası toplamak', 'bir emeğin karşılığını o emeği çeken değil, başka biri almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10326, 'partal atmak', 'yalan söylemek: ''Ne partallar attı gayrı.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10327, 'parti çevirmek', 'kâğıt oyunları, tavla vb.nde bir parti oynamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10328, 'parti vermek', 'bir şeyi kutlamak veya eğlenmek için birçok kimseyi bir araya toplamak: ''Unuttun mu, geçen sonbahar orada bir parti vermiştiniz, ben de vardım.'' -N. Eray.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10329, 'partiyi kaybetmek', '1) elde etmeye çalıştığı bir kazancı karşısındakine kaptırmak; 2) başkasıyla çekiştiği bir konuda yenilmek: ''Kızın gözlerinden damla damla yaşlar akıyordu. İmam partiyi kaybediyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10330, 'partiyi vurmak', 'büyük bir kazanç sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10331, 'pas açmak', 'bir şeyin pasını giderip parlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10332, 'pas almak', ' sp. bazı top oyunlarında bir oyuncu takım arkadaşından gelen topu kullanmak: ''Biçimli yerlerde durup paslar alır, ofsayt durumlarında beleş goller çıkarırdı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10333, 'pas atmak (vermek)', '1) sp. bazı top oyunlarında bir oyuncu takım arkadaşına top geçirmek; 2) argo karşı cinse umut ve cesaret vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10334, 'pas geçmek', '1) bazı iskambil oyunlarında o ele katılmamak; 2) ''geçiniz'' demek; 3) argo vazgeçmek, caymak, aldırış etmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10335, 'pas tutmak', '1) paslı duruma gelmek, paslanmak; 2) çalışamaz duruma gelmek: ''Hokkaların içinde mürekkep kurumuş, kalemler pas tutmuştu.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10336, 'pas vermemek', 'karşı cinse umut ve cesaret vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10337, 'paskalya yumurtası gibi', 'yüzüne çok allık süren.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10338, 'pasta çekmek', 'otomobilleri pasta ile parlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10339, 'paşa gibi yaşamak', 'bolluk içinde yaşamak, bey gibi yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10340, 'paşa gönlü bilir', 'ne yapacaksa yapsın, kendisi bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10341, 'paşa olmak', ' hlk. fazlaca içki içmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10342, 'pat diye', 'ansızın: ''İnsan öyle pat diye ölür mü canım, aklınıza nereden geliyor bunlar?'' -N. Eray.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10343, 'pata çakmak', ' argo askerce selam vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10344, 'pata gelmek', '1) kâğıt oyunlarında berabere kalmak; 2) ödeşmek, başa baş gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10345, 'patırtı çıkarmak', 'kavgaya sebep olmak, kavga çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10346, 'patırtı kopmak', 'kavga çıkmak, kargaşalık olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10347, 'patırtıya pabuç bırakmamak', ' tkz. önemli bir tehlike yaratmayacağını bildiği kışkırtmalara, yıldırmalara aldırmayıp bildiğini yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10348, 'patinaj yapmak', '1) tekerlek, tutunma eksikliği sebebiyle ilerlemeksizin aynı noktada dönmek; 2) mec. herhangi bir işte ilerleme kaydedememek, aynı noktada sayıp durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10349, 'patlak vermek', 'gizli kalması istenen veya beklenmedik bir olay, ansızın ortaya çıkmak: ''İnsanın vuzuhsuzluk içinde her an bir hadisenin patlak vermesini beklemesi kadar fena bir şey tasavvur edilemez.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10350, 'patlama!', '''sabret, sakin ol'' anlamında kullanılan uyarıcı bir söz: Patlama, geliyorum!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10351, 'patron çıkarmak', 'patronları çizili olduğu modelden kopya yolu ile bir kâğıda geçirip kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10352, 'pay bırakmak', '1) kesme, biçme, yapma sırasında, bir şeyde daha sonra kullanılmak için fazlalık bırakmak: ''Daha güzel günlere pay bırakmak için bir fedakârlık edelim.'' -P. Safa. 2) mec. bir ilişkide fazla samimi olmamak, mesafe bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10353, 'pay çıkarmak', 'bir olay veya durumdan gereken deneyimi kazanmak, tutulacak yolu belirlemek: ''Bununla beraber muhtar, bu vakadan köyün davası için bir pay çıkarmayı ihmal etmemektedir.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10354, 'pay vermek', '1) hisse vermek, bölüşmede bulunan parçalardan ayırmak: ''Batı, beynini sömürdüğü insanlara kendi uyruklarına sağladığı konfordan pay verip gönül alır.'' -H. Taner. 2) mec. küçük büyüğe karşılık vermek, saygısızca davranmak: ''Bunlar analarına, babalarına pay vermişler de böyle taş kesilmişler.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10355, 'payanda vurmak', 'payandalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10356, 'paydos borusu çalmak', 'işi bırakma zamanının geldiğini boru sesi ile bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10357, 'paydos demek', 'yapılmakta olan bir işi bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10358, 'paye vermek', 'değer, önem vermek: ''Onlar, bize bir esirden fazla paye vermemek fikrindedirler.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10359, 'payına düşmek', 'bölüşmede hisse ayrılmak, belirli bir bölüm verilmek: ''Uşak''a kadar yirmi beş otuz esir de bizim payımıza düştü.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10360, 'payını almak', '1) kendine ayrılanı almak: ''İnsan için bunları bilmek, bunların seyrine dalmak, bunlarla yetinmek, bunlarla gülüp bunlarla sevişmek varken ve bu Tanrı ihsanı nimetlerden herkesin kendi payını alması kabilken...'' -Y. K. Karaosmanoğlu. 2) mec. azarlanmak, paylanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10361, 'payidar kalmak (olmak)', 'kalmak, yok olmamak, yaşamak: ''Türkiye Cumhuriyeti ilelebet payidar kalacaktır.'' -Atatürk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10362, 'pazar, ilk pazardır', 'satılacak mala ilk olarak kaç para değer biçildiyse satıcı buna razı olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10363, 'pazar kayığı gibi', 'çok yüklenmiş (taşıt).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10364, 'pazar ola!', 'satıcılara ''satışın bol olsun'' anlamında söylenen bir iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10365, 'pazara çıkarmak', 'satılığa çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10366, 'pazarlığa girişmek', 'pazarlık yapmaya başlamak: ''... pazarlığa girişmez, müşterileri ne verirse alırdı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10367, 'pazarlığı pişirmek', 'pazarlıkta uyuşma sağlayacak duruma gelmek: ''Ne olacak efendim! Pazarlığı pişirdiler.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10368, 'pedavra gibi', 'kaburga kemikleri sayılacak kadar zayıf (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10369, 'pedavrası çıkmış', 'pedavra gibi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10370, 'pehlivan, kispetinin yağından bellidir', 'bir insanın işini bilip bilmediği çalışma biçiminden anlaşılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10371, 'pek söylemek', 'kırıcı ve sert konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10372, 'pek yaş olma, sıkılırsın; pek de kuru olma, kırılırsın', 'hüner gerektiğinde uysal, gerektiğinde sert olmayı bilmektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10373, 'peklik çekmek', 'sürekli olarak güçlükle büyük abdest bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10374, 'pekmez gibi malın olsun, Antakya''dan sinek gelir', 'malı güzel olan kimse için müşteri kaygısı yoktur, onun malına uzak yerlerden bile istekli çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10375, 'pekmezi küpten, kadını kökten al', 'yiyeceği en uygun ortamından almak gerektiği gibi eş olacak kadının da temiz ve soylu olanını almak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10376, 'pelte gibi', '1) çok gevşek; 2) çok yorgun.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10377, 'pencere açmak', 'görüş açısı kazandırmak: ''Bir insana bir şey öğrettiğiniz, ona yeni bir pencere açtığınız zamanki o parlayan bakışlar var ya, hocanın en büyük mükâfatı budur.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10378, 'pençe atmak', '1) yırtıcı hayvan ön ayaklarıyla saldırmak, vurmak: ''Aslan bir pençe atarak soysuz köpeğin kemiklerini kırmış.'' -F. R. Atay. 2) mec. gücüne güvenerek bir şeyi elde etmeye çalışmak: ''Bilirim atarsın bana pençeni / Nefsine kahretmek istedikçe sen'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10379, 'pençe pençeye gelmek', 'kıyasıya, öldürürcesine dövüşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10380, 'pençe vurmak', '1) pençelemek; 2) ayakkabıya pençe çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10381, 'perdah çekmek', 'sakalı bir daha ve kıl çıkışının ters yönünde olmak üzere tıraş etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10382, 'perdah vurmak (etmek)', 'parlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10383, 'perde çekmek', '1) bir şeyin önüne perde germek; 2) gözlemek, örtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10384, 'perde inmek', '1) hlk. gözde katarakt olmak; 2) hlk. gizlemek, örtmek; 3) bir tiyatro oyunu bitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10385, 'perde kurmak', 'Karagöz oyununa başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10386, 'perdelerini açmak', 'tiyatro yeni mevsimde temsillerine başlamak: ''Tiyatro topluluğu ''Kaos'' adlı oyunla perdelerini ilk kez açmıştı.'' -A. Cemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10387, 'perdelerini kapamak', 'tiyatro tamamen kapanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10388, 'perende atmak', 'havada çark gibi dönerek takla atmak: ''Ali çocuk gibi perendeler atarak otlarla, yamaçlarla sarmaş dolaş oluyordu.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10389, 'peresesine getirmek', 'tam sırasını, uygun zamanını bulmak, biçimine getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10390, 'pereseye almak', 'bir işi düşünmek, göz önüne almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10391, 'pergelleri açmak', ' tkz. uzun adımlarla yürümek: ''Kalem Şakir düştü peşine, öylesine açmıştı ki pergelleri, koridorun ortasında yakaladı.'' -R. Ilgaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10392, 'perhiz yapmak (etmek)', 'sağlığı korumak veya düzeltmek amacıyla özel bir beslenme düzeni uygulamak: ''Fiyatlar o kadar yükseldi ki perhiz eder gibi yediğim hâlde, yine her yemek bir buçuk lirayı geçmeye başladı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10393, 'perhize çekmek', 'perhizi titizlikle uygulamak: ''Öteki doktor bizi perhize çekerken öldürmüş de haberimiz olmamış.'' -M. İzgü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10394, 'peri gibi', 'çok güzel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10395, 'perileri bağdaşmak', 'uyuşup anlaşmak, yıldızları barışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10396, 'perisi hoşlanmamak', 'yakınlık duymamak, ısınamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10397, 'perişanlık vermek', 'perişan duruma getirmek, perişan etmek: ''Kaç defa deve kafilelerinin bir at sesi yüzünden ortalığa perişanlık verdiğine rast geldim.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10398, 'perşembenin gelişi çarşambadan bellidir', 'bir işin sonunun nasıl olacağı şimdiki gidişinden belli olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10399, 'perte çıkmak', 'taşıt hurdaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10400, 'pervane gibi dönmek', 'bir kimsenin yanından hiç ayrılmamak: ''Hanımefendinin etrafında pervane gibi dönüyor, isteyeceği şeyleri evvelden keşfetmek için gözünün içine bakıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10401, 'pervane kesilmek', '1) saygı duyduğu bir kişiye hizmet edebilmek için devamlı etrafında olmak, didinip durmak; 2) her isteği yapmak için çevrede dört dönmek: ''Ana oğul Leman''ın gözlerini sildiler, kızcağızın başında pervane kesildiler.'' -N. Hikmet. 3) dönüp durmak: ''Herkesin çevresinde saygılı bir pervane kesildiği bu huzurlu ortamda, bu genç kızın sıcak ilgisini hissetmek...'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10402, 'pervaz etmek', 'uçmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10403, 'pes demek', 'karşısındakinin kendisinden daha üstün olduğunu kabul etmek, boyun eğmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10404, 'pes etmek', '1) yenilgiyi kabul etmek, pes demek: ''Evliliği sırasında altı düşük daha yapacak sonunda pes edecekti.'' -A. Kulin. 2) yenileceğini anlayıp sırtının yere gelmesini istemeyen pehlivan, yenildiğini kabul anlamına ya ''pes ediyorum'' demek veya hasmının kispetine eliyle vurarak işaret vermek; 3) birinin aşırı kurnazlığı karşısında ancak bu kadar olur inancına varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10405, 'pes perdeden konuşmak', '1) alçak ve kalın sesle konuşmak; 2) alttan alarak, yumuşak bir dil kullanarak konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10406, 'pestil gibi', 'kımıldayamayacak kadar güçsüz, bitkin bir biçimde: ''Pestil gibi yerlerde uzandığıma bakma, anam, ben şu huysuza haddini bildirirdim.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10407, 'pestile çevirmek', 'çok yormak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10408, 'pestili çıkmak', 'çok yorulmak: ''Tulum Hayri dün voleybol oynamış, pestili çıkmıştı.'' -R. Ilgaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10409, 'peşinde (peşinden) koşmak', 'elde etmek için uğraşmak: ''Zaman oldu en renkli, en ahenkli şekillerin peşinde koştum.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10410, 'peşinden sürüklemek', 'birinin veya birçoklarının arkasından gelmesini sağlamak: ''Değişen, baş döndürücü bir hızla değişen değişiş iki ayakları topal olanları bile sürükler peşinden.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10411, 'peşinden yürümek', '1) birinin arkasından yürümek, gitmek; 2) mec. bir kimseye her konuda uymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10412, 'peşine düşmek (gitmek)', '1) arkasından gitmek, izlemek: ''Kaçarsa peşine düşerek ona korkulu dakikalar geçirtiyordu.'' -Y. N. Nayır. 2) bir isteğin gerçekleşmesini sağlamaya çalışmak: ''Her biri bir yere, ekmek parası peşine gittiler, kendi başlarını da kurtardılar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10413, 'peşine takılmak', 'ardından gitmek: ''Üftade Hanım''ın peşine takılmış olan şamatalı, gösterişli ve her yaştan, her cinsten bir kalabalık...'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10414, 'peşine takmak', 'yanında götürmek: ''Valinin yerini öğrendiği gibi savuştu Bayram, İlyas''ı peşine takıp.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10415, 'peşini bırakmamak', 'bir kimseyi veya şeyi izlemekten vazgeçmemek: ''Başımın belası! Peşimi hiç bırakmaz.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10416, 'peşkeş çekmek', '1) başkasının malını birine bağışlamak; 2) verilmemesi gereken bir şeyi uygunsuz bir amaçla veya yersiz olarak birine vermek: ''Kocasını ardı arkası gelmeksizin kandırdığı yetişmiyormuş gibi bazen genç kızları da şuna buna peşkeş çekermiş.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10417, 'peştamal kuşanmak', '1) peştamal giyinmek; 2) mec. bir zanaatta ustalık kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10418, 'pey sürmek', '1) artırma ile satılan bir şey için önce bir miktar para vermek veya önermek; 2) rekabet etmek: ''Onu, kendisine karşı pey sürecek kimsenin çıkmayacağına inanan Güdük Hacı olarak istemişti.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10419, 'peyda etmek', 'çıkarmak, oluşturmak, ortaya çıkarmak, edinmek: ''Uzun boyu hafif bir kamburluk peyda etmiş.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10420, 'peynir ekmek gibi', '1) çok revaçta, çok tutulan, beğenilen; 2) çok kolay biçimde; 3) çabucak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10421, 'pılı pırtıyı (pılıyı pırtıyı) toplamak', 'gitmek üzere bütün eşyalarını toplamak: ''Dört sene sonra ustası pılıyı pırtıyı toplamış, geldiği memlekete geri dönmüş.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10422, 'pıllım pıllım olmak', 'köhneleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10423, 'pır pır etmek', '1) ışık yanıp sönmek: ''İdare lambası pır pır edip duruyordu sofadaki merdiven başında.'' -Ç. Altan. 2) heyecanlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10424, 'pırlanta gibi', 'çok iyi nitelikleri olan, değerli, saf, temiz: ''Bunların arasında umutsuz yaşamayan pırlanta gibi delikanlılar vardı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10425, 'pışt demek', 'rahatsız edici bir söz söylemek: ... kimsenin kızına pışt demeden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10426, 'pıt pıt atmak', 'korku, heyecan vb. bir sebeple kalbi fazla çarpmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10427, 'pıtrak gibi', '1) üzerinde çok sayıda meyve bulunan (ağaç ve dal); 2) çok sayıda, tanecikli: ''Seher, ela gözlerinden pıtrak gibi yaşlar dökerek ayrılık sahnesini düşündü.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10428, 'piç olmak', '1) tadı bozulmak; 2) boşa gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10429, 'pide gibi', 'yamyassı: Minderler pide gibi olmuş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10430, 'pik yapmak', 'borsada kâğıtlar tavan yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10431, 'pike yapmak', '1) uçak dik biçimde inmek; 2) bilardoda, masaya dikey durumda tutulmuş isteka ile topa vurmak; 3) suya dalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10432, 'piknik yapmak', 'kırda yemek yemek ve gezinti yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10433, 'pikoya vermek', 'piko yapılması için bazı örtü, çarşaf, çamaşır vb.ni pikocuya götürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10434, 'pilav gibi', 'dağınık (ev, dolap, masa).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10435, 'pilav yiyen kaşığını yanında (belinde) taşır', 'bir şeyden yararlanmak isteyen kişi, bunun için gereken aracı eli altında bulundurmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10436, 'pilavdan dönenin kaşığı kırılsın', 'yararlı bir şeyi elde etmek için sonuna kadar uğraşılmalı, direnilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10437, 'pili bitmek', '1) aşırı yaşlanmak: ''Seksenini aştıktan sonra da pili bittiği için doğal bir ölümle öldü.'' -T. Uyar. 2) gücü kuvveti kesilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10438, 'pimini çekmek', 'başkasına zarar verecek kötü bir olayı başlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10439, 'pir aşkına', '''karşılık gözetmeden veya karşılık görmeden tam inançla, gerçek bir sevgi ile'' anlamında kullanılan bir söz: İki ay pir aşkına çalıştık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10440, 'pir ol!', ' şaka ''çok yaşa, var ol'' anlamında kullanılan bir beğenme sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10441, 'pire gibi', 'çevik, çok hareketli, yerinde duramayan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10442, 'pire için yorgan yakmak', 'pireye kızıp yorgan yakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10443, 'pireye kızıp yorgan yakmak', 'önemsiz bir durum karşısında kızarak kendisine daha büyük zarar verecek davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10444, 'pireyi deve yapmak', 'önemsiz bir olayı büyütmek: ''Kafaları bu işe yatmazsa müşavir beylerle müdür beylerinizin, devlet kapısında pireyi deve yaparlar.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10445, 'pireyi gözünden vurmak', 'keskin bir nişancı olmak: ''Hem o kadar nişancıdır ki pireyi gözünden vurur.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10446, 'pirüpak olmak', 'tamamen kurtulmak, rahatlamak, huzura kavuşmak: ''Akhöyük''teki nar ağacının yanında bir gün yatarsanız cümle dertlerinizden pirüpak olursunuz.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10447, 'pis pis bakmak', 'karşısındakini rahatsız edici ve sinirli bir biçimde bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10448, 'pis pis düşünmek', 'derin ve üzüntülü düşünceye dalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10449, 'pis pis gülmek', 'başkalarını kızdıracak, sinirlendirecek biçimde gülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10450, 'pislik parmağından (paçalarından) akmak', 'çok kirli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10451, 'pişirip kotarmak', 'bir işi sonuçlandırmak, tamamlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10452, 'pişkinliğe vurmak', 'kötü bir davranışa veya söze aldırmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10453, 'pişmanlık duymak (getirmek)', 'pişman olmak: ''Buraya kalkıp geldiğinden dolayı pişmanlık duyuyordu.'' -E. E. Talu. ''Gerçekte, hükûmet görevine girmiş olduğuma pek çok pişmanlık getirdim.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10454, 'pişmiş armut gibi (birinin) eline düşmek', 'olmuş armut gibi birinin eline düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10455, 'pişmiş aşa (soğuk) su katmak', 'yoluna girmiş olan bir işi bozmak: ''Biz olanca gücümüzle Batılılaşmaya çalışırken senin bu düşüncelerin pişmiş aşa soğuk su katıyor.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10456, 'pişmiş kelle gibi sırıtmak', 'dişlerini göstererek yersiz ve aptalca gülmek: ''İşi döndürüp dolaştırıp hicviyelere getiriyor ve onları pişmiş kelleler gibi sırıta sırıta okuyorlardı.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10457, 'pişmiş tavuğun başına gelmemek', 'her türlü zarara, kötülüğe, felakete uğramak, çok sıkıntı çekmek: ''Büyük kalabalığa varana kadar sanat eserinin başına gelenler pişmiş tavuğun başına bile gelmemiştir.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10458, 'pişti olmak', ' argo 1) bir ortamda birbirinden habersiz olarak aynı giyim kuşam içinde karşılaşmak; 2) istenmeyen birisiyle aynı ortamda karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10459, 'piyango çekmek', 'talih oyunu için hazırlanmış kâğıtlardan birini bulunduğu yerden almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10460, 'piyango vurmak (çıkmak)', '1) piyangoda ikramiye kazanmak; 2) beklenmedik bir yerden büyük kazanç sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10461, 'piyasa etmek', 'dolaşmak: ''Akşamları böyle kapı önünde piyasa eder.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10462, 'piyasaya almamak', 'önem vermemek, değersiz görmek: ''Beni kimse piyasaya almaz arkadaş, ben dünyanın en aşağılık insanıyım. Sokaktaki itler bile piyasaya almazlar beni.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10463, 'piyasaya çıkmak', '1) bir ürün satışa sunulmak; 2) fuhuş yapmak üzere müşteri aramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10464, 'piyasaya düşmek', '1) çok bulunur olmak; 2) kadın, kötü kadın olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10465, 'plak bozulmak', ' argo can sıkmak, bıkkınlık verecek biçimde konuşmak, dırdır etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10466, 'plan kurmak', '1) bir amacı gerçekleştirecek şeyleri düşünmek, tasarlamak: ''Yukarıdaki hizmetçisini karşısına almış, plan kuruyordu.'' -E. E. Talu. 2) mec. bir düzen hazırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10467, 'pof diye', 'pof sesi çıkararak: Yastık pof diye yere düştü. Balon pof diye söndü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10468, 'poker çevirmek', 'poker oynamak: ''Kış geceleri arkadaşlar arasında bir el poker çevirmek de keyiftir.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10469, 'polat gibi', 'çelik gibi, güçlü kuvvetli: ''Atletinin örtmediği pazıları polat gibi.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10470, 'polemiğe girmek (girişmek)', 'siyasi, bilimsel veya edebî konularda sert tartışmalar yapmak: ''Polemiğe girdiği genç kuşak yazarların soluksuzluğunu yoksulluk yılları ürünü olmalarına bağlamıştı.'' -H. Taner. ''Delegasyonumuz aleyhine çalakalem bir polemiğe girişmiş bulunuyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10471, 'polemiğe kaçmak', 'konudan uzaklaşıp dalaşmak: ''Politikada herkes birbirini polemiğe kaçmakla suçluyor.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10472, 'poliçe çekmek', 'bir müşteriye ödeme yapması için bildiride bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10473, 'politik davranmak', 'belli bir amaca ulaşmak için uzlaşmaya, iyi geçinmeye önem vererek hareket etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10474, 'politika gütmek', 'politika izlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10475, 'politika yapmak', 'bir işi çözümlemek için politika yolunu kullanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10476, 'popülarite kazanmak', 'halk tarafından sevilmek, tutulmak: ''Bir halk çocuğu olarak popülarite kazanmış, önce elinizde, sonra partinizde basamakları çıkmış, parlamentoya girmişsiniz.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10477, 'post elden gitmek', '1) öldürülmek; 2) bulunduğu yüksek makamdan ayrılmak zorunda kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10478, 'post vermek', 'canını vermek, ölmek: ''Az değildir varmadan senin gibi yurduna / Post verenler yabanın hayduduna, kurduna'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10479, 'posta etmek', '1) görevliler, birini resmî bir daireye götürmek; 2) birini, gönlü olmasa da bir kimseye teslim edip bir yere göndermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10480, 'posta koymak (atmak)', ' tkz. birini korkutmak, gözdağı vermek: ''Daha dün Kel Mahmut''u yıkayıp yağlayan yavşak bugün kalkmış ona posta koyuyor.'' -R. Ilgaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10481, 'posta yapmak', 'bir yere gidip gelmek, sefer yapmak: Araba şehre günde üç posta yapar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10482, 'postaya atmak (vermek)', 'mektup, gazete, paket vb.ni gideceği yere ulaşması için posta kuruluşuna vermek, postalamak: ''Evet. Dilekçeyi de şimdi verdim postaya.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10483, 'postayı kesmek', '1) ilgiyi kesmek; 2) bir şeyi yapmaktan vazgeçmek: Ben postayı kestim, artık toplantılara gitmeyeceğim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10484, 'postu deldirmek', ' argo 1) kurşunla vurulmak; 2) ölmek: Sen o zamana kadar postu çoktan deldirmiş, kuyruğu titretmiş olursun.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10485, 'postu kurtarmak', 'öldürülmek tehlikesini atlatmak: ''Binlerce kişiden ancak birkaç kişi postunu kurtarabildi.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10486, 'postu sermek', 'gittiği yerde uzun süre kalmak: ''Sabiha Hanım''ı eğlendirmek bahanesiyle konağa postu sermiş.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10487, 'postuna saman doldurmak', 'öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10488, 'postundan olmak', 'bulunduğu makamı yitirmek: ''Bizim Balkanlı arkadaşlar ise böyle bir hadise neticesinde postundan olmak gibi fena bir akıbete uğrayacaklarından korkuyorlardı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10489, 'pot kırmak', 'yersiz ve karşısındakine dokunacak söz söylemek, gaf yapmak: ''Gri redingotlu efendi, bir pot kırdığını hemen anlamış olacak ki sözünü çevirdi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10490, 'pot yapmak', 'dikişte kabarıklık veya büzülme olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10491, 'poz kesmek (yapmak)', 'çalım atmak, afili görüntü vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10492, 'poz vermek', 'resim yaptırmak veya fotoğraf çektirmek için durum almak: ''Tam çizerken bir arkadaşı geliyor, poz veren çocuğun ensesine bir küfür ve bir de şamar yapıştırıyor.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10493, 'pösteki saymak', 'içinden çıkılmaz bir iş yüklenip uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10494, 'pöstekisini çıkarmak', 'öldürmek, yok etmek: ''Sonra peşine herifleri taksın ha! Alimallah pöstekisini çıkarırdım.'' -İ. A. Gövsa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10495, 'pöstekiyi kurtarmak', 'hoş olmayan bir durumdan kurtulmak: ''Aman, bir karı ki görme... Maymun mu maymun, biçimsiz mi biçimsiz, pis mi pis... Ne yaparsın... Pöstekiyi kurtarmak lazım.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10496, 'prangaya vurmak', 'ayağına pranga bağlamak, zincire vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10497, 'problem çıkarmak', 'sorun çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10498, 'problem etmek', 'dert etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10499, 'problem olmak', 'dert olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10500, 'projeksiyon tutmak', 'bir konuyu aydınlatmak, açıklığa kavuşturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10501, 'projektör ışığında olmak', 'göz önünde bulunmak, ortada olmak: ''Devlet adamları her ülkede projektör ışığında kişiler olarak davranışlarına, sözlerine, görünümlerine dikkat etmek zorundadırlar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10502, 'protesto çekmek', 'protesto yollamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10503, 'puan almak (kazanmak)', '1) spor karşılaşmalarında başarılı bir oyun çıkararak kendine sayı sağlamak; 2) genellikle test biçimindeki sınavda herhangi bir puan elde etmek; 3) mec. itibar kazanmak, takdir edilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10504, 'puan toplamak', '1) sp. puan kazanmak; 2) mec. saygınlık sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10505, 'puan tutturmak', '1) gereken sayıda puan kazanmak; 2) sınavlarda istenen yere girebilmek için gerekli puanı elde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10506, 'puan vermek', '1) değer biçmek, not vermek; 2) sp. boksta ve güreşte başarısız duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10507, 'pufla gibi', 'çok yumuşak ve kabarık: Pufla gibi yastık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10508, 'pul tutmak', ' argo para kazanmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10509, 'pula dönmek', 'değersizleşmek: ''Altın idin pula döndün / Yere düşer paslanırsın'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10510, 'pulsuz iş, yolsuz iş', 'karanlık işler çeviren, yasal olmayan yollara sapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10511, 'punduna getirmek', 'bir şeyi yapmak için uygun zamanı ve yeri seçmek: ''O döner dönmez bir punduna getirip tanıştırayım sizi.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10512, 'pundunu bulmak', 'punduna getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10513, 'pupa yelken ilerlemek (gitmek...)', '1) yelkenler, arkadan esen rüzgârla şişmiş olarak, tam yolla: ''Sicim gitgide boşalıyor, gemi hafif yana yatarak pupa gidiyordu.'' -S. F. Abasıyanık. 2) mec. alabildiğince, hiçbir şeye bağımlı olmadan: ''Batı''da bilimsel araştırmalar, dramatik icat ve keşifler, pupa yelken ilerliyor.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10514, 'pusu kurmak', 'saldıracağı kimseye görünmemek için bir yerde gizlenip beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10515, 'pusuda beklemek', 'gizlenerek saldırıya hazır durumda olmak: ''Bu da hile, inanmıyorum; pusuda beklediğine ve rol oynadığına inanıyorum.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10516, 'pusudan çıkmak', '1) kurulan pusudan kurtulmak; 2) kuracağı pusudan vazgemek: ''Müdürün derviş tabiatlı olduğunu öğrenince teker teker pusudan çıkmaya başladılar.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10517, 'pusulayı şaşırmak', '1) güç bir duruma düşerek ne yapacağını bilememek: ''Aramızda bir profesör, bir de doçent vardı, hepimiz çoktan pusulayı şaşırmıştık.'' -B. R. Eyuboğlu. 2) doğru tutum ve davranıştan ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10518, 'pusuya düşmek', 'pusu kuran kimsenin saldırı alanı içine girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10519, 'pusuya yatmak', 'pusuda beklemek: ''Sakarya galibiyeti, o güne kadar pek farkına varılmayan korkunç bir düğümün çözülmesine, sinmiş, pusuya yatmış kuvvetlerin meydana çıkmasına yol açmıştı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10520, 'puşt olmak', '1) birinin ilencine uğrayıp kötüleşmek, mahvolmak: ''Bu gurbet ellerde candan usandım / El kahrını çekmede ömrüm puşt oldu'' -Halk türküsü. 2) argo bir işin uzmanı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10521, 'put gibi', 'sessiz, anlamsız bir bakışla ve kımıldamaksızın: ''Ben ağlamıyor, put gibi duruyordum.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10522, 'put kesilmek', 'sessiz ve hareketsiz bir durum almak: ''Görmediniz mi, adını söyleyince herkes put kesiliyor.'' -A. H. Tanpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10523, 'püf desen uçacak', 'çok zayıf kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10524, 'pürdikkat kesilmek', 'çok dikkat etmek: ''Bir tıkırtı, bir ayak sesi duyar mıyım diye kulaklarımı açıp pürdikkat kesildim.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10525, 'pürüz çıkarmak', 'engel çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10526, 'Rabbena hakkı için', 'ant içerken inandırmak için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10527, 'racon kesmek', '1) görünüşe göre hüküm vermek; 2) gösteriş yapmak: ''Hayati ortaya atılır, tosunca raconu keser ya da dövülürdü.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10528, 'rağbet etmek (göstermek)', 'istemek, beğenmek, istekle karşılamak: ''El işçiliğine ve çiftçiliğe rağbet göstermediler.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10529, 'rağbet görmek (kazanmak)', 'istenilmek, beğenilmek, istekle karşılanmak: ''... haftanın bir gecesinde yalnız kadınlara oynayacak kadar mahallede rağbet kazandı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10530, 'rağbet güzel ile zenginedir', 'güzellerle zenginler her zaman itibarlıdırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10531, 'rahat ararsan mezarda', 'herkesin bir sıkıntısı vardır, bu sıkıntılar ancak ölümle biter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10532, 'rahat bırakmak', 'daha rahat ve huzurlu oturmayı sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10533, 'rahat bırakmamak (vermemek)', 'tedirgin etmek: ''Beni son nefesimde rahat bırakmayan herif, bana o vakitler akla gelmez cefalar çektirmişti.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10534, 'rahat durmak', 'yaramazlık etmemek veya kımıldamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10535, 'rahat etmek', 'sıkıntısız durumda olmak, ferahlanmak, dinlenmek: ''Benim ve kardeşimin mektep veya sokak dönüşü kirliliklerimiz yüzünden içlenirdi, bizi yıkayıp temizleyinceye kadar rahat etmezdi.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10536, 'rahat kıçına batmak', ' tkz. bulunduğu rahat durumun değerini bilmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10537, 'rahat olmak', 'üzüntülü, sıkıntılı veya tedirgin durumda olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10538, 'rahat yüzü görmemek', 'hiç rahat etmemek: ''Derler ki bugünden itibaren Zeliha''nın kalbi rahat yüzü görmedi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10539, 'rahata ermek', 'rahatlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10540, 'rahata kavuşmak', 'rahatlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10541, 'rahatı kaçmak', 'rahatsız, tedirgin olmak, üzülmek: ''Eniştem de üşengen bir adamdır, rahatı kaçar diye üstüne düşmedi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10542, 'rahatına bakmak', 'hiçbir şeye aldırış etmeyerek rahatını sağlamaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10543, 'rahatsızlık duymak', 'tedirgin olmak, huzurunun ve rahatının kaçtığını hissetmek: ''Anasını ayakta, kara, korkunç bir yüzle görünce tuhaf bir rahatsızlık duydu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10544, 'rahatsızlık vermek', 'rahatını bozmak, rahatını, keyfini kaçırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10545, 'rahleitedrisinden geçmek', 'birinden eğitim almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10546, 'rahmet okumak', 'Tanrı''nın merhamet ve bağışlaması için dua etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10547, 'rahmet okutmak', 'biri, kötü bir kimseden daha kötü çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10548, 'rahmet olsun canına', ' hlk. ''Allah rahmet eylesin'' anlamında ölüler anılırken kullanılan bir iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10549, 'rahmetli olmak', 'ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10550, 'rahmetlik olmak', 'ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10551, 'rahvan at kendini yorar', 'bir işi işgüzarlık ederek veya gereksiz yere üstünde durarak uzatanın zararı kendisine olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10552, 'ramazanda yalan söyleyenin (oruç yiyenin) bayramda yüzü kara olsun', 'bir sözün yalan olduğu, bir ödevin yapılmadığı bir süre sonra gerçekleşen olaylarla anlaşılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10553, 'ramp ışığına çıkarmak', 'bir oyunu sahnelemek: ''Yasak oyunum bu rejimde aklandı, Ulvi Uraz onu ramp ışığına çıkardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10554, 'rampa etmek', '1) taşıt bir yere, bir şeye veya bir başka taşıta yanaşmak: ''Bu arabalar her akşam Beyoğlu''nda Tokatlıyan''ın yaya kaldırımına rampa eder.'' -H. R. Gürpınar. 2) argo birinin içki masasına çağrılmadığı hâlde oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10555, 'randevu almak', 'bir kimseden belli bir saat ve yerde buluşmak için söz almak, gün almak: ''Adamcağız, samimi bir refah ve zevkle yeni bir randevu aldıktan sonra gitti.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10556, 'randevu vermek', 'belli bir saatte, belli bir yerde biriyle buluşmak için söz vermek: ''Az sonra birbirimize randevu vermişiz gibi ben de gelirim.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10557, 'rap diye', 'ansızın: ''Delikanlı, yokuşa saptı, arabayı rap diye cakalı bir tavırla durdurdu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10558, 'rapor almak', '1) hasta olup olmadığını belirlemek amacıyla herhangi bir sağlık kuruluşundan belge almak; 2) sorumluluğu altındakilerden herhangi bir konuda bilgi almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10559, 'rapor etmek', 'rapor vermek: ''İşçileri ariz amik inceleyip rapor edecek.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10560, 'rapor vermek', 'herhangi bir konuda yapılan inceleme, araştırma sonucu düşünce veya gözlemleri bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10561, 'rast gele!', '''işiniz rast gitsin'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10562, 'rast gelmek', '1) düşünmediği, ummadığı hâlde karşılaşmak, rastlamak, tesadüf etmek: ''Fukara bir denizciye rast gelirsen süngerlerimden birkaç tanesini ona ver, gönlünden koparsa.'' -Halikarnas Balıkçısı. 2) düşünmediği veya düşülmediği hâlde payına düşmek: Kumaşın iyisi bana rast geldi. 3) atılan şey hedefi bulmak: Çok ev aradım ama hiçbir yerde rast gelmedi. 4) tesadüf etmek, denk gelmek: ''Yumrukları ile başına, ne tarafına rast gelirse vurmaya, tekrar vurmaya başladı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10563, 'rast getirmek', '1) rast gelmesini sağlamak: Üç kurşun attı, ikisini rast getirdi. 2) kollamak, seçmek: Neşeli bir anında rast getirip dilediğimi söyledim, hemen kabul etti. 3) aranmakta olan bir şeyi veya kimseyi umulmadık bir yer ve zamanda bulmak; 4) Tanrı, uygun getirmek, başarılı kılmak: Allah işinizi rast getirsin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10564, 'rast gitmek', 'uygun düşmek, istenilen biçimde gelişmek: İşi rast gidiyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10565, 'rastık çekmek', 'rastık sürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10566, 'raydan (rayından) çıkmak', 'düzeni bozulmak, altüst olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10567, 'rayına girmek', 'bir iş, bir girişim düzene sokulmak, iyi bir duruma getirilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10568, 'rayına oturtmak', 'bir işi yoluna, yöntemine koymak, düzgün işler duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10569, 'razı etmek', 'kabul ettirmek: ''Yalvardı yakardı, beni, fabrikayı beklemeye razı etti.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10570, 'razı olmak (gelmek)', 'uygun bulmak, beğenmek, benimsemek, istemek, kabul etmek: ''Allah''ın emri, Peygamber''in kavliyle varmaya belki razı olurum.'' -H. R. Gürpınar. ''Her akşam, üstlerinin aranmasına razı gelecek on işçi daha aldı.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10571, 'reaksiyon göstermek', 'tepkimek: ''Çok ani, hemen hiç düşünmeden reaksiyon gösterdim.'' -E. Işınsu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10572, 'reçete gibi', '1) okunaksız (yazı); 2) kesin yargı ve çözüm bildiren: ''Reçete gibi kati ve veciz bir beyit ki dört kelime ile bu derdin devasını söylüyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10573, 'reçete yaptırmak', 'reçetede yazılı olan ilaçları hazırlatmak veya satın almak: ''Tramvay caddesine çıktı, bir eczaneye girdi, reçete yaptırdı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10574, 'rehavet çökmek (basmak)', 'gevşeklik, ağırlık duymak ve uyumak istemek: ''Bütün asabıma bir rehavet çöktü, gözlerim kapandı.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10575, 'rehin almak', 'bir anlaşma, sözleşme veya isteğin yerine getirilmesini sağlamak için bir kimseyi alıkoymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10576, 'rehin etmek', 'rehin olarak vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10577, 'rehin vermek', 'tutuya bırakmak: ''Hacı''dan gereken şunu bunu alarak toprağın şu parçasını, o parçasını rehin verdik'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10578, 'rehine koymak (vermek)', 'tutuya koymak: ''Beş lira için ananın saatini rehine koyduğunu unuttun mu?'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10579, 'reklam etmek', 'herhangi bir kimseyi veya olayı, durumu açığa vurmak, ilan etmek, afişe etmek, ifşa etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10580, 'reklam yapmak', 'her türlü aracı kullanarak bir şeyi halka tanıtmak, ünlenmesini sağlamak: ''Sizin için geniş bir reklam yapacağım, adımı ortaya koyacağım.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10581, 'rekor kırmak', '1) eski rekoru aşıp yeni, üstün bir sonuç elde etmek; 2) daha iyi bir derece elde etmek: ''Tam en az elli bin satıp rekor kıracak.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10582, 'remil atmak (dökmek)', 'kumda birtakım çizgiler çizerek fala bakmak: ''Ondan sonra bakıcı hoca remil atsa nerede olduğumu bulamaz.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10583, 'rençber kırk yılda, tüccar kırk günde', 'rençberin ancak kırk yılda kazanabileceği parayı, tüccar kırk günde kazanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10584, 'rengi atmak (kaçmak, uçmak)', '1) solmak: ''Rengi uçmuş kenarları yenmiş ... bir fotoğrafı var.'' -A. Ş. Hisar. 2) korku, heyecan vb. sebeplerle benzi sararmak: ''Kadınlar da bu defa Tevfik''i dükkânın kapısında yakaladılar, aynı şeyi ona açtılar, Tevfik''in rengi uçtu, dudakları titredi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10585, 'rengini belli etmek', 'yandaşlığını açıklamak, düşüncesini, eğilimini açığa vurmak: ''Rengimizi belli etmenin sorumluluğunu her an taşıyabilmek durumundayız.'' -A. Cemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10586, 'renk almak', 'yeni bir renk kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10587, 'renk vermek (katmak)', '1) çamaşır rengi solmak; 2) neşe, canlılık veya değişiklik kazandırmak: ''Bembeyaz bulutlar kırmızılaştı / Sonra yavaş yavaş deminki renksiz / Göklere renk veren bir ziya taştı / Açılırken hülyalı enginlere biz'' -N. Hikmet. 3) açık etmek: ''O fırsatta onu yererek göze girmeye çalışan birkaç tıynetsiz dalkavuk da elbet renk verdiler.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10588, 'renk vermemek', 'duygularını, düşüncelerini veya başka bir durumunu belli etmemek, bir şeyi bildiği hâlde bilmez gibi görünmek: ''Şaşırmış gibi yüzüme bakıyor ama renk vermiyor.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10589, 'renkten renge girmek', 'korkudan veya utançtan yüzünün rengi değişmek, sıkılmak: ''Genç kız, renkten renge giriyor, verecek cevap bulamıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10590, 'replik almak', 'oyuncunun karşısındakinden kendi yapacağı espriye hazırlık mahiyetinde bir söz veya cümle almak: ''Spritüel dostum Pişekâr''ından dişi bir replik almış bir Kavuklu kadar sevinçli, gülümsedi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10591, 'resim almak', '1) bir şeyin resmini yapmak; 2) resim çekmek; 3) vergi ödetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10592, 'resim çekmek (çıkarmak)', 'fotoğraf makinesiyle bir şeyin biçimini kâğıda geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10593, 'resim gibi', 'çok güzel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10594, 'rest çekmek', '1) oyuncu önündeki paranın tümünü ortaya koymak; 2) mec. herhangi bir konuda sert ve kesin olarak son sözü söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10595, 'resti görmek', 'ileri sürülen paranın miktarını kabul edip aynı miktarda parayı ortaya koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10596, 'revaç bulmak', 'geçerli ve değerli sayılmak: ''Falcılık bilhassa işlerin kötüleştiği yerlerde revaç bulur.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10597, 'revaçta olmak', 'değerli, üstün veya geçerli olmak: ''Sakal ve bıyığın revaçta olduğu bir dönemden geçmedik değil!'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10598, 'revan olmak', 'yola çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10599, 'revnak vermek', 'hoşluk, güzellik, renklilik katmak: ''Sefiremizin tiyatrosever oluşu konuşmalara daha da revnak verdi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10600, 'rey vermek', 'oy kullanmak: ''Ekseriyeti öksürüklü ise de henüz rey verecek kadar kolunu oynatabilir.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10601, 'rezalet çıkarmak', 'rezalet sayılacak bir durumun ortaya çıkmasına yol açmak: ''Her kadının takdim edilmek için can attığı böyle büyük bir adamla dansı yarıda bırakıp rezalet çıkarmak için insanın aklı kaçık olmalı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10602, 'rezil olmak', 'çok utanacak bir duruma gelmek: ''El âleme karşı rezil olmayalım.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10603, 'rezil rüsva olmak', 'toplum içinde ayıplanacak bir duruma düşmek: ''Hasan, bu dediğini yapsaydı, dört başı mamur bir dayak yiyip âleme rezil rüsva olacaktı.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10604, 'rıza göstermek', 'razı olmak, onamak, uygun bulmak: ''Yarım yamalak ıslahat tedbirlerine inanmaktansa kazaya rıza göstermek bana daha hoş görünür.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10605, 'rızası olmak', 'izni olmak, müsaadesi olmak: ''... kişinin vücut bütünlüğüne dokunulamaz, rızası olmadan bilimsel ve tıbbi deneylere tabi tutulamaz.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10606, 'rızasını almak', 'onayını almak, müsaadesini almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10607, 'rızkını çıkarmak', 'günlük yiyecek parasını çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10608, 'rikkat vermek', 'duygulandırmak, etkilemek: ''Yıllardır devam eden bağlılıkları, kendilerini bilenler için rikkat verici bir manzaraymış.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10609, 'riske girmek (risk almak)', 'zararı göze almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10610, 'rivayet olunmak (edilmek)', 'bir olay, bir haber vb. anlatılmak: ''Mahkemeden kurtulup kapıdan çıkarken gizlice söylediği rivayet edilen bir laf var.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10611, 'robot resmini çizmek (yapmak)', 'adli olaylarda tanığın tarifiyle şüphelinin görünümünü yansıtan resmini çizmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10612, 'rol almak', 'bir oyunda görev almak: ''Kafile, rol alan şahıslar tarafından yapılan türlü oyunlarla geziyi tamamlar.'' -A. K. Tecer.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10613, 'rol çalmak', '1) oyun sırasında söz başka bir oyuncuda iken seyircinin ilgisini kendi üzerine çekmek; 2) birinin söyleyeceği sözü ondan önce söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10614, 'rol kesmek', 'yalan, uydurma söz söylemek veya içten olmayan davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10615, 'rol oynamak', '1) oyunda rol almak; 2) mec. birinin bir işte önemli etkisi olmak: ''Bunda ne hocanın bilgisi ne çocuğun çalışkanlığı rol oynar.'' -B. Felek. 3) mec. davranışlarda içtenlik olmamak: ''Bu da hile, inanmıyorum; pusuda beklediğine ve rol oynadığına inanıyorum.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10616, 'rol yapmak', 'davranışlarda içtenlik bulunmamak: ''Rol yapmaya kalkışsa yüzüne gözüne bulaştıracağını biliyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10617, 'rolü olmak', 'etkisi bulunmak: ''Sizinle benim bunda bir rolümüz oldu muydu?'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10618, 'rolüne çıkmak', 'oyunda belli bir kişiliği sahnede oynamak: ''Görgülü rolüne çıkmış zekâsız bir türediye benzeyecek.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10619, 'romanı yazılmak', 'romanlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10620, 'romatizması tutmak', 'romatizma ağrıları başlamak: ''Kardeşinin romatizmaları tutmuş.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10621, 'rota değiştirmek', '1) gidilen yolu değiştirmek: ''Turgut, ani bir kararla rotasını değiştirdi.'' -F. F. Tülbentçi. 2) mec. tutumunu değiştirmek, izlediği yoldan ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10622, 'rota kırmak', 'rota çizgisinden ayrılmak: ''Bir de baktım ki niyetlendiğim gibi kıyıya paralel gitmemiş, rota kırıp açılmışım.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10623, 'rölantide durmak (çalışmak)', 'motorlu taşıtlarda, motor boşta çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10624, 'rölantiye almak', '1) motorlu taşıtlarda motoru boşa almak, boşta çalıştırmak; 2) mec. herhangi bir işi yavaşlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10625, 'röntgen çekmek', '1) herhangi bir organın durumunu tespit etmek için film çekmek; 2) mec. bir olayın bütün geçmişini ve durumunu belirlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10626, 'rövanşı almak', '1) ikinci karşılaşmayı kazanmak; 2) mec. kensine yapılan haksızlığın karşılığını vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10627, 'Rufailere karışmak', 'psikolojik bunalıma düşüp günlük yaşamın gerçeklerinden uzaklaşmak, yaşamdan kopmak: ''Kızım sen Rufailere karışmışsın, bir doktora görünsen iyi olur.'' -E. Işınsu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10628, 'ruh gibi', '1) durgun, çevresiyle ilgilenmeyen, kendi hâlinde olan; 2) çok zayıf.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10629, 'ruh gibi dolaşmak', 'hiçbir şeyin farkında olmadan yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10630, 'ruh kazandırmak (vermek)', 'herhangi bir yeri veya şeyi canlı, hareketli, neşeli bir duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10631, 'ruhu (bile) duymamak', 'haberi olmamak, anlamamak: ''Birinin yukarıdan topladığını öteki sokağa taşır, konak soyulduğu hâlde, kimsenin ruhu bile duymaz.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10632, 'ruhu karartmak', 'sıkıntıya sokmak, bunaltmak: ''Korkunç geceler, çakalların ulumaları, köpeklerin haykırışları bu ruhu da karartan gecelerde sinirleri büsbütün gevşetiyor.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10633, 'ruhu okşamak', '1) duygulara hoş gelecek biçimde konuşmak veya davranmak; 2) hoşa gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10634, 'ruhu şad olsun!', '''ölüler, sevinsin, mutlu olsun'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10635, 'ruhunda güneş açmak', '1) rahatlamak, huzura ermek; 2) sevinmek, neşelenmek, coşmak: ''Saz dinleyenlerden bazılarının gözlerinde yaş olur, ruhlarında güneş açardı.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10636, 'ruhunu şad etmek', 'ölmüş bir kimseyi anmak: ''Hepsi örslerinin üzerine birer mum yakmışlar, pederlerinin ruhunu şad ediyorlar.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10637, 'ruhunu teslim etmek', 'ölmek: ''Hiçbir şey söylemeden ruhunu teslim etmiş.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10638, 'rutin dışına çıkmak', '1) bir şeyi her zamankinden farklı yapmak; 2) alışılmış olandan farklı davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10639, 'ruzname tutmak', 'günlük olayları bir deftere yazıp toplamak: ''Dünyada en birinci zevk ruzname tutmaktır, derdi. Ben bunu boş ve manasız ve münasebetsiz bulurdum.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10640, 'rüşdünü ispat etmek', '1) kanunlara göre ergin sayılacak yaşa gelmiş olmak; 2) mec. herhangi bir konuda yeterli seviyeye geldiğini göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10641, 'rüşvet kapıdan girince insaf (iman) bacadan (pencereden) çıkar', 'işini herkese eşit davranarak yapmak zorunda olan bir görevli, kendisine çıkar sağlayan kimselere ayrıcalık tanıyorsa o kişi hak, adalet, insaf gibi duygulardan yoksun demektir, onun gözü paradan, maldan başka bir şey görmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10642, 'rüşvet yemek', 'bir işi yapmak için birinden rüşvet almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10643, 'rüya gibi', 'olağanüstü, harika, çok güzel: Yolculuğumuz rüya gibi geçti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10644, 'rüyası çıkmak', 'görülen rüya gerçekleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10645, 'rüyasına (rüyalarına) girmek', '1) rüyasında görmek: ''Geceleri Avrupa şehirleri rüyama girer.'' -Ö. Seyfettin. 2) mec. bir şeyden çok etkilenmek, çok korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10646, 'rüyasında görememek', 'olacağını, gerçekleşeceğini düşünememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10647, 'rüyasında görse hayra yormamak', 'hatır ve hayalinden geçirmemek, olacağına inanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10648, 'rüzgâr almak', 'yel esen bir yerde bulunmak: Bu ev çok rüzgâr alıyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10649, 'rüzgâr eken fırtına biçer', 'herkesin zarar görmesine yol açacak işler yapan kimse, çok sert tepkilerle karşılaşır ve sonunda en büyük zarara kendisi uğrar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10650, 'rüzgâr ekip fırtına biçmek', 'yaptığı bir kötülüğün çok daha kötüsü ile karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10651, 'rüzgâr esemeyince yaprak oynamaz (dal kımıldamaz)', 'her durumu meydana getiren bir etken vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10652, 'rüzgâr gelecek delikleri tıkamak', 'istenmeyen bir durum veya gelişmeye karşı her türlü önlemi almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10653, 'rüzgâr gibi', 'çabucak: ''Rüzgâr gibi geçip giden gençliğimin tanıkları / Şu yıpranmış fotoğraflar, soluk renkli, siyah beyaz'' -M. Çınarlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10654, 'rüzgâr tutmamak', 'rüzgâra açık ve kapalı bulunmak: ''Rüzgâr tutmayan yerlerinde dadılar, çocuklar ve ihtiyarlar güneşlenirler.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10655, 'rüzgâra (karşı) tüküren, kendi yüzüne tükürür', 'bilgi, beceri, fizikî güç gibi yönlerden kendisinden üstün olanlarla mücadeleye giren bir kimse bu mücadelede yenik düşer, birtakım kayıplara uğrar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10656, 'rüzgârdan nem kapmak', 'en küçük bir şeyden alınmak, çok alıngan olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10657, 'rüzgârın önüne düşmeyen, yorulur', 'genel gidişe ayak direten boşuna çaba harcamış olur, bir yere varamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10658, 'rüzgârlı havanın kuytusu, yağmurlu havanın uykusu', 'rüzgârlı havada kuytu bir yer, yağmurlu bir havada da uyku tercih edilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10659, 'saat başı galiba!', 'bir toplantıda, herkesin dalıp sustuğunda bu durumu fark eden bir kimsenin söylediği söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10660, 'saat bir (iki, üç ...) yönünde', 'saat başlarını söyleyerek hedefi yön açısından belirlemek için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10661, 'saat bu saat', '''ele geçen fırsattan yararlanmanın tam zamanı'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10662, 'saat gibi', 'tam bir düzgünlükte, tıkır tıkır.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10663, 'saat gibi işlemek', 'aksamadan, ara vermeden çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10664, 'saat on bir buçuğu çalmak', 'yaşı çok ilerlemiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10665, 'saat tutmak', 'saate bakarak bir işin ne kadar sürdüğünü hesaplamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10666, 'saati çalmak', 'bir şeyin vakti gelmek: ''Herkes ona artık vaktini ibadete hasretmek zamanının geldiğini, daha doğrusu ahireti düşünmek saati çaldığını ima ediyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10667, 'saati saatine uymamak', 'durumu, huyu sık sık değişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10668, 'sabah ola, hayrola', 'sabah olsun, o vakte kadar iş belki düzelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10669, 'sabah sürçen, geceye dek sürçer', 'bir işe başladığı zaman beceriksizliği görülen kişinin bu durumu sonuna kadar sürer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10670, 'sabaha çıkmamak', 'sabaha kadar yaşayamamak, sabahtan önce ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10671, 'sabaha kadar', 'bütün gece boyunca.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10672, 'sabahı bulmak (etmek)', 'sabahlamak: ''Hiç uyuyamadım. Her dakika gelip kaldıracaklar sanıyorum. Ama işte sabahı ettik.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10673, 'sabahı sabah etmek', 'sabahın olmasını uyumadan sabırsızlıkla beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10674, 'sabahı zor etmek', 'bir türlü sabah olmamak: ''İstediği şeyler gelinceye kadar, sevinç ve sabırsızlık içinde sabahları zor ediyordu.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10675, 'sabahın kızıllığı akşamı kış eder, akşamın kızıllığı sabahı güz eder', 'sabahleyin gökyüzünde görülen kızıllık, o akşam havanın kış gibi olacağını, akşam görülen kızıllık ise ertesi sabah havanın güze döneceğini belirtir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10676, 'sabahlar (sabahışerifler) hayrolsun!', '1) günaydın! 2) iş işten geçtikten, olan olduktan sonra gösterilen ilgi için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10677, 'sabahtan karnını doyuran, küçükken evlenen aldanmamış', 'yapılacak iş için gerekli gücün elde edilebilmesi için sabahleyin karın doyurulmalıdır, çocukların anne, baba ihtiyarlamadan yetiştirilebilmeleri için de evlenmeleri geciktirilmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10678, 'saban sürmek', '1) toprağı sabanla kazıp altüst etmek; 2) sp. güreşte, hasmı ayaklarından tutup yüzükoyun yerde sürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10679, 'sabanın tutağına yapışan el aç kalmaz', 'çiftçilik yapan veya çalışan aç kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10680, 'sabır acıdır, meyvesi tatlıdır', 'sabır zor bir iştir ancak güzel sonuçları vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10681, 'sabırla koruk helva olur, dut yaprağı atlas', 'sabretmesini bilen kişi olmayacak gibi görünen işlerde bile başarı kazanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10682, 'sabit olmak', '1) bir şeyin varlığı, gerçekliği kesin olarak belli olmak: ''Önceden koyduğu teşhislerin doğruluğu sonradan kaç defa sabit olmuş.'' -A. Ş. Hisar. 2) durağan durumda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10683, 'sabreden derviş muradına ermiş', 'beklemesini bilen kimse sonunda amacına ulaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10684, 'sabreyle işine, hayır gelsin başına', 'bir işi yaparken acele etmez, sabrederseniz hayırlı sonuçlara varırsınız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10685, 'sabrı taşmak (tükenmek)', 'artık katlanamaz, dayanamaz duruma gelmek, sabrı kalmamak: ''Sabrı tükenmiş olanlardan birkaçı, birden söze başlamak istedilerse de reis izin vermedi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10686, 'sabrın sonu selamettir', 'karşılaştığı güçlükleri sabırla yenmeye çalışan kimse, sonunda başarıya ulaşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10687, 'sabunköpüğü gibi sönmek', 'gösterişli olmakla birlikte en hafif bir etki ile yok olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10688, 'saç ağartmak', 'saç sakal ağartmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10689, 'saç saça baş başa', 'kadınlar, birbirlerini kıyasıya hırpalayacak biçimde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10690, 'saç saça baş başa gelmek (dövüşmek)', 'kadınlar, birbirlerini kıyasıya hırpalayacak biçimde kapışmak: ''Eğer bu patırtıdan, ikindi uykusu başına sıçrayan imam aşağı koşmasa iki kadın, avluda saç saça baş başa dövüşeceklerdi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10691, 'saç sakal birbirine karışmak', 'saçı sakalı uzamak, bakımsız görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10692, 'saç sefadan, tırnak cefadan uzar', 'insan keyifli olursa saçı, dertli olursa tırnağı uzar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10693, 'saçak öpmek', ' tar. sarayda bayramlaşma törenine katılan büyükler, padişahın tahtından sarkıtılmış halı saçaklarını öpmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10694, 'saçı başı ağarmak', 'yaşlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10695, 'saçı başı birbirine karışmak', 'bakımsız olmak: ''Matmazelin saçı başı birbirine karışmıştı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10696, 'saçı bitmedik (yetim)', 'doğalı çok olmamış (yetim).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10697, 'saçı kılmak (atmak)', 'gelinin başından çiçek, şeker, arpa, para vb. saçmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10698, 'saçı (saçları) değirmende ağartmamak', 'deneyimli olmak: ''Bu saçları değirmende ağartmadık, siz birbirinize âşıksınız sanırım'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10699, 'saçı sakalı akar gibi', 'üstü başı perişan bir durumda: ''Hani saçı sakalı akar gibi bir adam geliyor ya buraya, o işte.'' -N. Ataç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10700, 'saçı topuklarını dövmek', 'saçı çok uzun olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10701, 'saçı uzun aklı kısa', ' esk. kadınları aşağılamak için kullanılan bir söz: ''Ona oğluna olduğu kadar güvenmiyor. Kız çocuğu bu, ne kadar okusa da saçı uzun aklı kısa olur, der.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10702, 'saçılıp dökülmek', '1) gereğinden veya kaldırabileceğinden çok harcamak; 2) mec. içindekini söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10703, 'saçın ak mı kara mı, önüne düşünce görürsün', 'acele etme, sonucun ne olduğunu biraz sonra anlarsın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10704, 'saçına ak (kır) düşmek', 'saçı ağarmaya başlamak, yaşlanmak: ''Benim bütün saçlarıma, senin sadece şakaklarına ak düşmüş.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10705, 'saçına başına bakmadan', 'ilerlemiş yaşına yakışmayacak biçimde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10706, 'saçına kar yağmak', 'saçı aklaşmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10707, 'saçını başını yolmak', 'çok üzülmek, üzüntüsünden dövünmek: ''Tanrıça Hebe çaresiz kalmış, saçını başını yoluyordu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10708, 'saçını süpürge etmek', 'kadın özveri ile çalışıp hizmet etmek: ''Ah gençliğim, ah sana saçımı süpürge ettiğim gençliğim de diyecek değilim.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10709, 'saçıp savurmak', 'parasını düşüncesizce, boşuna harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10710, 'saçları iki türlü olmak', 'yaşı ilerlemiş bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10711, 'sadakat göstermek', 'bağlı kalmak: ''Eserin aslına fevkalade sadakat gösterilmiş olması da ayrıca kayda şayandır.'' -A. H. Çelebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10712, 'sadakatsizlik göstermek', 'sadakatsiz olduğunu ortaya koymak, açıklamak: ''Ama kendi hesabına sadakatsizlik göstermemişti.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10713, 'sade pirinç zerde olmaz, bal gerektir kazana; baba malı tez tükenir evlat gerek kazana', 'hakkıyla yararlanılacak bir şeyin meydana gelmesi için birtakım ögelerin bir araya gelmesi gerektir; kişi kendi emeği ile kazanç sağlayıp bunu baba malına katmıyorsa babasından kalan mal tez tükenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10714, 'sadede gelmek', 'konuyla ilgisiz sözleri bırakarak asıl konuya dönmek: ''Ne ise bunlar hep dedikodu. Sadede gelelim.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10715, 'sadık dost akrabadan yeğdir', 'candan dost akrabadan daha hayırlı olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10716, 'sadık kalmak', 'birine, bir şeye bağlılığını sürdürmek, bağlı kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10717, 'sadra şifa vermek', 'gönlü, yüreği rahatlatmak, ferahlatmak: ''Reha Bey''e de meseleyi biraz çıtlattım. Ondan da pek sadra şifa verecek bir şeyler öğrenemedim.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10718, 'saf bağlamak', 'sıralanmak, sıraya girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10719, 'saf değiştirmek', 'başka bir gruba katılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10720, 'saf dışı etmek (bırakmak)', '1) dizinin dışına çıkarmak; 2) mec. ilgisini kesmek, işin gereğinden alıkoymak, işlemez duruma getirmek: ''Kendisini de arkadaşlarını da çok rahat susturup saf dışı bırakabilirlerdi.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10721, 'saf dışı olmak', '1) dizinin dışına çıkmak; 2) mec. ilgisi kesilmek, işin gereğinden alıkonulmak, işlemez duruma getirilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10722, 'saf tutmak', '1) saf bağlamak; 2) belli bir gruba katılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10723, 'saflara ayırmak', 'belli kümeler içinde toplamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10724, 'safra atmak', '1) insana veya araca fazla yük olan malzemeleri atmak; 2) mec. sıkıntı veren bir kimseden veya bir şeyden kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10725, 'safra bastırmak', 'açlığını yatıştıracak kadar az bir şey yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10726, 'safrası kabarmak', 'açlıktan midesi bulanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10727, 'sagu sağmak', 'ağıt yakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10728, 'sağ eliyle sol kulağını göstermek', 'kısa yoldan yapılacak bir işi dolambaçlı yollardan geçerek yapmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10729, 'sağ gösterip sol vurmak', 'şaşırtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10730, 'sağ gözünü sol gözünden sakınmak', 'çok kıskanç olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10731, 'sağ kalmak', 'ömrünü devam ettirmek, yaşamasını sürdürmek: Sağ kalsaydı daha çok kimseye yardımı olurdu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10732, 'sağ ol', 'hoşa giden bir davranış için ''çok yaşa, teşekkür ederim'' anlamlarında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10733, 'sağ olsun', '1) biri için sitem yollu bir şey söyleneceği zaman söyleyenin iyi niyetini belirtmek amacıyla sözün başına getirilen bir söz: Sağ olsun, beni hiç dinlemedi. 2) bir kişiye güven duyulduğu zamanlarda kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10734, 'sağ olsun da dağ ardında olsun', 'uzakta olan sevdiklerimize günün birinde kavuşabiliriz yeter ki yaşıyor olsunlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10735, 'sağ (sağlam) baş yastık istemez', 'sağlam insan durup dururken yatmak istemez eğer istiyorsa hasta olduğu düşünülmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10736, 'sağ yap!', '''direksiyonu sağa çevirerek sağ yöne git!'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10737, 'sağ yapmak', 'direksiyonu sağa doğru çevirmek, sağa yöneltmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10738, 'sağa kaymak', 'siyasette ve ekonomide sağ eğilimli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10739, 'sağa sola', 'rastgele yerlere: ''Bu kurnaz dilenci böylece inisiyatifi göstermelik de olsa eline alıp sağa sola emirler vermeye başladı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10740, 'sağa sola bakmadan', 'ortalığı kollamadan, saygısızca.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10741, 'sağdan geri dönmek (etmek)', 'geri dönmek, geri dönüp gitmek: ''Binbaşının gayriihtiyari içi sızladı, yaşlı bir kadını dövmeye kalkmış gibi utanma hissi duyarak sağdan geri etti, başı önünde mağlup ve mahcup merdivenleri indi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10742, 'sağı solu olmamak', '1) nasıl davranacağı kestirilmez olmak; 2) olumlu mu olumsuz mu davranacağı bilinmeyen bir kişi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10743, 'sağılır ineğin buzağısı kesilmez', 'çıkar sağlamaya yardım eden kimseye veya şeye zarar gelmemesine dikkat edilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10744, 'sağını solunu bilmemek', 'düşüncesiz, dikkatsiz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10745, 'sağır için iki kere keramet olmaz', 'herkesin işitip öğrendiği şey, dikkatsiz kimse için bir daha söylenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10746, 'sağır işitmez (duymaz) uydurur (yakıştırır)', 'sağır, yanında konuşulan şeyleri işitmez ama konuşanların durumuna bakarak ve anladığını sanarak bir şeyler yakıştırıp söyler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10747, 'sağlam durmak', 'gücünü, yeteneğini ve cesaretini toplamak: ''Daha bugünden sağlam durmayı beceremezse kaptan köprüsüne adım atmasın.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10748, 'sağlam pabuç (ayakkabı) değil', 'bir kimsenin güvenilmez olduğunu belirten bir söz: ''Nasıl aldattı beni meğer sağlam ayakkabı değilmiş.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10749, 'sağlama almak', 'güvenilir bir durumda olmasını sağlamak: ''Oyuna gelenlerin işi sağlama almaları dudaklarımdaki gülüşü kurutuverdi.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10750, 'sağlıcakla kal (kalın)', 'ayrılırken kalanlara söylenen bir esenlik sözü, hoşça kal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10751, 'sağlık olsun!', 'üzücü bir durum veya bir zarar karşısında avunma sözü olarak söylenen bir söz: Sürahi kırıldı diye üzülme, sağlık olsun!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10752, 'sağlık varlıktan yeğdir', 'sağlıktan büyük zenginlik olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10753, 'sah çekmek', 'bir yazının doğru olduğunu bu işaretle belirtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10754, 'sahabet etmek', 'korumak, kayırmak: ''Sen hayırlı bir mal mısın ki Hatice''yi sahabet edeceksin.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10755, 'sahabetçi çıkmak', 'kayırmak, arka çıkmak: ''Kahpenin gözlerine mi tutulmuş ne, sahabetçi çıkıyor.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10756, 'sahaya çıkmak', '1) spor karşılaşmasına başlamak için sahada yerini almak; 2) alan araştırması yapmak için belirlenen yere gitmek; 3) mec. mücadele etmeye başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10757, 'sahile bindirmek', ' den. gemiyi içindeki yükü oluşan tehlikeden kurtarabilmek amacıyla bilerek karaya oturtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10758, 'sahile vurmak', 'bir nesne dalga veya akıntının etkisiyle kıyıya gelmek, kıyıda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10759, 'sahip çıkmak', '1) kendinin olduğunu ileri sürmek; 2) korumak, koruyucu olmak, ilgilenip gözetmek: ''Yazarlara yalnız yazarlar sahip çıkıyor.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10760, 'sahip kılmak', 'sahip olmasını sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10761, 'sahipsiz eve it buyruk', 'kimsenin ilgilenmediği, benimsemediği sahip çıkmadığı işler üzerinde değersiz kişiler egemenlik kurarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10762, 'sahneye çıkmak', '1) tiyatro, müzik vb. sanatçılar için sanatını izleyici önünde uygulamak, göstermek: ''Türk kızı, orada sahneye çıktı ilk defa.'' -Y. Z. Ortaç. 2) mec. kullanılmak, görünmek, ortaya çıkmak: ''Almanca yanında ara sıra Hırvatça da sahneye çıkıyor.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10763, 'sahneye koymak', ' tiy. tiyatro eserini veya müzikal bir oyunu, metin, oyun, yorum, dekor, müzik vb. ögeleri birbiriyle uyumlu duruma getirerek sahne için uygulamak, oynamak, sahnelemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10764, 'sahura kalkmak', 'oruç tutan kimse gün doğmadan yemek yemek için yataktan kalkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10765, 'sak durmak', 'dikkatli, uyanık durumda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10766, 'sak yatmak', 'derin uykuya dalmadan uyumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10767, 'saka beygiri gibi', '1) bir iş uğruna birçok yere uğrayarak dolaşan (kimse); 2) yalnız vakit geçirmek için amaçsız dolaşan (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10768, 'sakal bırakmak (koyuvermek veya salıvermek veya uzatmak)', 'sakalını tıraş etmeyip büyütmek: ''Yaşıtlarının hemen hepsi sakal koyuverdi.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10769, 'sakal bıyığa denk olmayınca berber ne yapsın?', 'gelir gidere denk değilse durumu düzene koymaya çalışan kişi durumu düzeltmek için fazla bir şey yapamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10770, 'sakal keçide de var', 'sakal, kişiye değer kazandırmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10771, 'sakal oynatmaz', 'ağızda eriyecek kadar olgunlaşmış (yemiş, yiyecek).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10772, 'sakalı değirmende ağartmak', 'yıllar pek çok deneyim kazandırmış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10773, 'sakalı ele vermek (kaptırmak)', 'başkasının sözünden çıkmayacak bir duruma düşmek: ''Yumuşak durmak, yalvarmak, sakalı ele vermek demektir, sonra artık evin idaresi ne olacak?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10774, 'sakalı saydırmak', 'saygınlıktan düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10775, 'sakalım yok ki sözüm dinlensin', 'ancak yaşlı kimselerin söz ve öğütleri dinlenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10776, 'sakalına ak (kır) düşmek', 'sakalı ağarmaya başlamak, yaşlanmak: ''Düşük siyah bıyıklarına, sakalına pek az kır düşmüş olan Selim Paşa, karısından çok genç görünüyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10777, 'sakalına göre tarak vurmak', 'birinin hoşlanacağı biçimde konuşmak veya davranmak: ''Sakalına göre tarak vurdum. Oğlunun çok selamı var, dedim. Tarla icarlarını toplar, kendi elleriyle verir, dedim.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10778, 'sakalına kar yağmak', 'sakalı aklaşmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10779, 'sakalla olaydı kişi, keçiye danışırlardı her işi', 'sakal, kişiye değer kazandırmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10780, 'sakata gelmek', ' argo tuzağa düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10781, 'sakın ha!', '''yapma, yapmaktan çekin'' anlamında, yapılması istenmeyen bir davranışa engel olmak için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10782, 'sakınılan göze çöp batar', 'üzerine çok düşülen şeyler genellikle kazaya veya zarara uğrar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10783, 'sakınması olmamak', '1) korkusu, çekinmesi olmamak; 2) incelik kurallarına, saygıya aldırmadan davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10784, 'sakız gibi', '1) çok temiz, çok beyaz: ''Kız kucağında hiç kullanılmamış, sakız gibi bir çamaşır sepeti ile çadırdan çıktı.'' -O. C. Kaygılı. 2) ayrılmak bilmez, yapışkan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10785, 'sakit kalmak', 'söz söylemesi gerekirken susmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10786, 'sakla samanı, gelir zamanı', 'gereksiz görülen şey ileride gerekli olabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10787, 'saksağan, danayı babası hayrına bitlemez', 'bir kimse başkasına hizmet ediyorsa bunda kendisinin de çıkarı vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10788, 'sala vermek (okumak)', '1) minarelerde, salat okuyarak cuma namazını haber vermek: ''Safa, küçük, çarpuk çurpuk vücudu, koca kafası, minarede sala verir gibi etrafa çınlayan sesiyle konağın imamı Şadan Molla''yı hatırlatıyordu.'' -H. E. Adıvar. 2) bir kimsenin ölümünü, minareden salat okuyarak duyurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10789, 'salah bulmak', 'düzelmek, iyileşmek, onmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10790, 'salapurya gibi', 'çok büyük olan veya ayağa büyük gelen (ayakkabı).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10791, 'salavat getirmek', '1) Hz. Muhammed''e saygı bildirmek için dua okumak; 2) tehlikeli bir durumda dua okumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10792, 'saldırıya uğramak', 'saldırı karşısında kalmak, tecavüze uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10793, 'sallantıda bırakmak', 'bir şeyi sonuca bağlamamak, savsaklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10794, 'salma gezmek (dolaşmak)', 'başıboş hayvan gibi dolaşmak: ''Ne olacak çobansız köyde kurtlar boş oturacak değil ya işte böyle salma dolaşırlar.'' -R. Akyavaş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10795, 'salma salmak', 'genellikle köylerde işlerin görülmesi için ihtiyar heyetinin kararıyla her evden para toplamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10796, 'salta durmak', 'köpek arka ayakları üzerine kalkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10797, 'saltanat sürmek', '1) hükümdarlık etmek; 2) bolluk içinde yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10798, 'salto atmak', 'rakibe salto oyunu uygulamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10799, 'saman alevi gibi', 'birden parlayıp arkasından hemen yatışan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10800, 'saman altından su yürütmek', 'belli etmeden iş çevirmek, ortalığı karıştırmak: ''Saman altından su yürüten, ürkek, kaypak görünüşlü insanoğlunu tanımışlığı var.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10801, 'saman gibi', 'tatsız, yavan: ''Saman gibi bir yaşamdı günlük yaşamım ama her şey dışarıdan bakılınca hiç de kötü değildi.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10802, 'sana taşla vurana sen aşla (ekmekle, pamukla) var (dokun)', 'sana sert davranana sen yumuşak davran.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10803, 'sana vereyim bir öğüt, kendi ununu kendin öğüt', 'kişi kendi işini kendisi yapmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10804, 'sana yalan, bana gerçek', '''söylediğim şeyi sen bilmiyorsun ancak doğrudur, ben biliyorum'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10805, 'sanat altın bileziktir', 'kişinin elindeki sanat, değeri hiç eksilmeyen bir servettir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10806, 'sanatı ustadan görmeyen (öğrenmeyen) öğrenmez', 'kişi tek başına ne kadar çalışırsa çalışsın işin inceliklerini bir bilenden öğrenirse o işi daha çabuk ve kolay yapabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10807, 'sanatına hor bakan (sanatını hor gören) boğazına torba takar', 'işini küçümseyen kişi istediği gibi para kazanamaz ve sonunda dilenci olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10808, 'sanatını hor gören boğazına torba takar', 'işini küçümseyen kişi istediği gibi para kazanamaz ve sonunda dilenci olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10809, 'sancak göstermek', ' den. gemi, ulusunu belirten sancağını göndere çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10810, 'sancısı tutmak', '1) birdenbire ve şiddetli bir ağrı gelmek: ''İlk kum sancısının nasıl tuttuğunu nakledecekmiş.'' -S. M. Alus. 2) mec. tedirgin olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10811, 'sandığa gitmek', '1) seçim kararı almak; 2) oy kullanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10812, 'sandığa gömmek', 'seçimde ağır yenilgiye uğratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10813, 'sandığına olmaz, sakındığına olur', 'üzerine çok düşülen şeyler genellikle kazaya veya zarara uğrar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10814, 'sandık başına gitmek', 'sandığa gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10815, 'sandık düzmek', 'çeyiz hazırlamak: ''İleride yine ona gönderilmek üzere bir de sandık düzmesine ne mâni vardı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10816, 'sandıktaki sırtında, ambardaki karnında', 'nesi varsa giyer, nesi varsa yer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10817, 'sandıktan çıkmak', ' hlk. seçimle işbaşına gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10818, 'sanısına kapılmak', 'sanmak, zannetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10819, 'sansür koymak', 'sansürlemek: ''Onlar bu vehimle ellerinden gelse / Rüyalara sansür koyacaklar bir gün'' -A. N. Asya.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10820, 'sansürden geçirmek', 'her türlü yayını, sinema ve tiyatro eserini denetlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10821, 'santim kaçırmamak', 'çok dikkatli ve hesaplı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10822, 'sap çekmek', 'biçilmiş ekini tarladan harmana kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10823, 'sap derken saman demek', 'belirli ve doyurucu bir düşünce ortaya koyamamak: ''Konuşma, sap derken saman demek kabîlinden hiçten şeylerden ibaret kalmıştır.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10824, 'sap döner, keser döner, gün gelir hesap döner', 'her şey zaman içinde planlandığı gibi gerçekleşmeyebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10825, 'sap gibi', '1) çok ince: ''Avurtları çökmüş, boynu yakasının ortasında sap gibi kalmıştı.'' -H. Taner. 2) argo yalnız, tek başına.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10826, 'sap yiyip saman sıçmak', ' kaba 1) bir olaya kızıp ateş püskürmek; 2) yararsız şeylerle uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10827, 'saparta (sapartayı) yemek', 'azarlanmak, terslenmek: ''Mebustan saparta yiyen bu adam kimdir?'' -R. E. Ünaydın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10828, 'sapartayı vermek', 'azarlamak, terslemek: ''Hanımefendi kalkmış, ikisine de sapartayı vermiş.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10829, 'sapına kadar', 'tam anlamıyla, bütünüyle: ''O sapına kadar askerdir; asker doğmuş, asker ölecektir.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10830, 'sapır sapır dökülmek', 'başarısız olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10831, 'sapla samanı karıştırmak', 'iyi ile kötüyü ayıramamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10832, 'saplanıp kalmak', '1) takılıp kalmak: ''Mistik olmayan felsefe görünüşünde de tamamen H. Spencer''e saplanmış kalmıştı.'' -H. Taner. 2) bir konuda yoğunlaşıp başka bir şeyle ilgilenmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10833, 'sapsarı kesilmek (olmak)', 'çok sararmak: ''Heyecandan dudakları titriyordu ve benzi sapsarı kesilmişti.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10834, 'sararıp solmak', '1) giderek daha çok solmak: ''Sokakları dolduran sayılmaz şapkaların zalimce, kurnaz ve namussuz gölgelerinde sararmış solmuş.'' -Ö. Seyfettin. 2) mec. sağlığı bozulmak: ''Karısı anlaşılmayan bir illetle sararıp soldu, birkaç ay içinde ölüp gitti.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10835, 'sardalya gibi istif olmak', 'bir yerde çok kalabalık ve sıkışık bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10836, 'sarhoşa dokunma, kendi yıkılsın', 'kendi aklını beğenip başkasını dinlemeyen kimseyi gittiği yanlış yoldan döndürmeye kalkmayın, bırakın cezasını çeksin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10837, 'sarhoşluğa vurmak', 'kendini sarhoş gibi göstermek, sarhoş olmuşçasına davranmak: ''Hatta sarhoşluğa vurup orada kaldığım geceler de oldu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10838, 'sarhoştan, deli bile korkar', 'sarhoş olmak delirmekten de beterdir, sarhoş insan kendini denetleyemez, delinin yapmayacağı şeyleri yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10839, 'sarhoştum aydım, böyle yaşamaktan caydım', 'artık gerçekleri gördüm.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10840, 'sarı çizmeli Mehmet Ağa', 'kim olduğu, nerede oturduğu bilinmeyen kimse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10841, 'sarılıp kundaklanmak', 'yoğun etki altında kalmak: ''Çünkü bir bakmışım ki hep başkalarının fikirleriyle sarılıp kundaklanmışım.'' -E. Işınsu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10842, 'sarımsağı gelin etmişler de kırk gün kokusu çıkmamış', 'insanlar kötü yanlarını kolay kolay belli etmezler, haklarında yargıda bulunmakta acele edilmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10843, 'sarımsağını hesap eden paçayı yiyemez', 'küçük sakıncalarını düşünerek bir işe girişmeyen kişi, o işin kazançlarından yoksun kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10844, 'sarımsak da acı ama evde lazım bir dişi', 'gerekli olanın niceliğinden çok niteliği önemlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10845, 'sarımsak içli dışlı, soğan yalnız başlı', 'anlaşan kimselerin birbirlerinden saklısı gizlisi yoktur, başkasıyla böyle bir yakınlık kuramamış olan tek başına kendi yaşantısı içinde kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10846, 'sarımsak yemedim ki ağzım koksun', 'kötü bir iş yapmadım ki sonucundan korkayım, sorumlu olayım.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10847, 'sarıp sarmalamak', 'sıkıca sarmak: ''Bak o zaman nasıl yakınlaşacaksınız. Güven nasıl sarıp sarmalayacak ikinizi.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10848, 'sarmaş dolaş olmak', '1) birbirine sarılıp kucaklaşmak: ''Batan bir gemide birbirini arayıp bulduktan sonra sarmaş dolaş olmuş felaketzedelere benziyorlar.'' -N. F. Kısakürek. 2) iç içe girmek, karman çorman olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10849, 'sarpa sarmak (çekmek)', 'güçlükler ortaya çıkmak, çözülmesi çok güç bir duruma gelmek. ''Düz ovada sarpa çekme yolunu / Ver mektebe okutsunlar oğlunu'' -Âşık Veysel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10850, 'sarsıntı geçirmek', 'beklenmedik bir olaydan çok etkilenmek, üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10851, 'sası kokmak', 'yiyecek bozulmak, çürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10852, 'satılığa çıkarmak', 'satmak, satışa çıkarmak: ''Memleketine mi dönüyormuş neymiş, bütün eşyasını satılığa çıkarmış.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10853, 'satılık ziftin olsun, Selânikten kel gelir', 'işe yaramaz sandığın bir malı satılığa çıkarırsan akla gelmeyen yerlerden onu arayanlar gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10854, 'satıp savmak', 'gereken parayı sağlamak için elindeki malı ucuza satıp tüketmek, yok pahasına elden çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10855, 'satır atmak', 'herkesi öldürmek, kırıp geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10856, 'satışa çıkarmak', 'satmak için ortaya koymak: ''Bir şeye ad koymak, satışa çıkarılan malın üzerine yafta asmaya benzetilebilir.'' -N. Uygur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10857, 'satışa gelmek', 'uydurma bir sebeple ortada bırakılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10858, 'satıya çıkarmak', 'satışa çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10859, 'savaş açmak (ilan etmek)', '1) bir veya daha fazla devlete karşı savaş durumuna geçmek; 2) ortadan kaldırmak için uğraşmak: ''Softalığa savaş açan ilk laikler orada toplanmıştır.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10860, 'savaş vermek', 'savaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10861, 'savaşım vermek', 'bir amaca erişmek, bir güce karşı koyabilmek için uğraşmak, çaba göstermek, mücadele etmek: ''Sen ancak iyi savlar için savaşım vermekte rahat ederdin.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10862, 'savunmasını almak', 'soruşturma sebebiyle suçlanan birisinin düşüncesine başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10863, 'savuşup gitmek', 'ilgi çekmeden gizlice, aceleyle ayrılmak: ''Yemek kotaracağım diye savuşup gitti.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10864, 'say beni, sayayım seni', 'sevgi karşılıklı olur, sen beni seversen ben de seni severim.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10865, 'saya gezmek', 'köy çocukları ramazanda veya özel günlerde çeşitli tekerlemeler söyleyip kapı kapı dolaşarak ufak tefek yiyecek toplamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10866, 'sayesinde sayeban olmak', 'istenilen bir şeyi başkasının aracılığıyla elde etmek: ''Sayende sayeban olduk İstanbul şehri / Sayende sebil olduk, aç kaldık, sefil olduk'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10867, 'sayfa bağlamak', 'dizgide dökülen kurşun satırları bir sayfa düzeni içinde toplayarak sıkıca iple bağlamak: ''Sayfayı öyle sıkı bağlardı ki satırlar âdeta birbirine kenetlenirdi.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10868, 'saygı duymak (beslemek)', 'birine, bir şeye karşı saygı hissetmek: ''Şakır şakır yağan yağmurlara benzeyen insanlara, düşmanım da olsalar saygı duyarım.'' -N. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10869, 'saygı göstermek', 'saymak, değer vermek: ''Kendilerine büyük saygı gösterdim ve imdatlarına muhtaç olduğumu belirttim.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10870, 'sayı hesabıyla', 'bir spor yarışmasında bir sporcu veya takımın kazandığı sayı bakımından: ''Sayı hesabıyla bir galibiyeti bile öpüp de başımıza koyacaktık.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10871, 'sayılı günler (gün) tez (çabuk) geçer', 'bir işin yapılması veya gerçekleşmesi için konulmuş olan belli bir süre çabucak geçer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10872, 'sayılı koyunu kurt kapmaz (yemez)', 'miktarı saptanarak bir kimseye teslim edilmiş olan eşya iyi korunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10873, 'sayım suyum yok', '1) çocuk oyunlarında ''kısa bir süre için oyun dışıyım'' anlamında kullanılan bir söz; 2) çocuklar arasında bir işte şakaya yer verilmeyeceğini anlatan bir söz: ''Alır mıydım? Sevinir miydim? Yoksa mızıkçılık eder, -Olmaz, sayım suyum yok... Siz birlik olup bana oyun ettiniz -mi derdim.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10874, 'sayıp dökmek', 'ne var ne yok, hepsini söylemek: ''Böyle misaller sayıp dökmek gerekse satırlar değil, sütunlar dolar.'' -R. E. Ünaydın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10875, 'sayısını Allah bilir', '''o kadar çok ki saymakla bitmez'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10876, 'saymakla bitmemek (tükenmemek)', 'pek çok olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10877, 'sebat etmek (göstermek)', 'sözünden veya kararından dönmemek, bir işi sonuna kadar götürmek, direşmek: ''Fakat şu var ki çocuklar arzularında sebat göstermiyorlar.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10878, 'sebebiyet vermek', 'bir şeye, bir olaya sebep olmak, yol açmak: ''Bu acıya kendi sebebiyet verdiğini hissetmekten gurur duyuyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10879, 'sebepsiz kalmak', 'yoksul bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10880, 'sebepsiz kuş bile uçmaz', 'kılavuz ve yardımcı olmadan hiçbir iş başarılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10881, 'sebepsiz ölüm olmaz', 'herkesin ölümü için bir sebep vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10882, 'sebil etmek', 'bol bol vermek, dağıtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10883, 'sebilhane bardağı gibi', 'hoşa gitmeyen kalabalık (insan topluluğu).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10884, 'secde etmek', '1) alnı, eli, ayakları, dizleri, ayak parmaklarını yere getirmek: ''Tozlanmış camların arkasından secde eden ruh ile merkadi tebcile başladı.'' -A. H. Müftüoğlu. 2) mec. saygı göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10885, 'secdeye varmak (kapanmak)', 'secde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10886, 'sedyelik olmak', 'ayakta duramayacak duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10887, 'sefa geldin (geldiniz)', '''hoş geldin, hoş geldiniz'' anlamında kullanılan ağırlama, karşılama sözü: ''Her şahsa kendi ismiyle sefa geldin, derdi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10888, 'sefa geldine gitmek', 'bir kente, bir mahalleye yeni gelen veya geziden dönen birine ziyarete gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10889, 'sefa ile yenen cefa ile kazanılır', 'keyifli keyifli harcadığımız para, çok sıkıntı çekilerek kazanılmıştır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10890, 'sefa (sefalar) bulduk', '''sefa geldin, sefa geldiniz'' sözüne ''teşekkür ederim'' anlamında karşılık olarak kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10891, 'sefa sürmek', 'rahat, sakin ve eğlenceli yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10892, 'sefalet çekmek', 'yoksul ve perişan yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10893, 'sefasına bakmak', 'rahatına bakmak: ''Şöyle bir iki parça, sağlam nevinden irat ve akar edinip efendi efendi yan gel, sefana bak.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10894, 'sefasını sürmek', 'bir durumun getirdiği, sağladığı olanaklardan yararlanmak: ''Uzun yıllar cefasını çektiği Yokuş''un sefasını sürecekti artık.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10895, 'sefer etmek', 'gezmek, gezinti yapmak, yolculuk etmek: ''Ne hoş, ey güzel Tanrı''m, ne hoş / Maviliklerde sefer etmek'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10896, 'sefer tası gibi', 'her katında birer odası olan (yüksek ev).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10897, 'seferber olmak', 'birçok kimse bir iş, bir amaç için bütün olanaklarıyla girişmek: ''Anası, kardeşi, konu komşu, bilen bilmeyen, polis, jandarma, herkes seferber oldu. Nevin bulunamadı.'' -R. Çalapala.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10898, 'sefere kalkmak', 'yolculuğa başlamak: ''Sefere kalkacak bir sürü tekne vardı limanda.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10899, 'segman atmak', 'aşınan segmanı değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10900, 'sehpaya çekmek', 'asarak öldürmek, darağacına çekmek, asmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10901, 'seksen kapının ipini çekmek', 'içinde bulunduğu sorunu çözmek için kapı kapı dolaşmak, birçok yere uğramak: ''Ama şimdi, bir çift lastik için seksen kapının ipini çekiyoruz.'' -R. Enis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10902, 'sekte vermek', 'kesintiye uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10903, 'sekte vurmak', 'kesilmesine sebep olmak, kesintiye uğratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10904, 'sekteye uğratmak', 'kesmek, kesintiye uğratmak: ''Sorular sorar ve dersimi sekteye uğratırdı.'' -H. F. Ozansoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10905, 'sel gibi akmak', '1) sıvılar için bol ve gür akmak: ''Durmaz akar gözüm yaşı sel gibi.'' -Âşık Veysel. 2) zaman çabuk ve hızla geçmek; 3) insanlar kalabalık bir yığın hâlinde gitmek, yürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10906, 'sel gider, kum kalır', 'geçici durumlara güvenmek doğru değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10907, 'sel gider kum kalır, kişi ettiğini bulur', 'geçici durumlara güvenmek doğru değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10908, 'sel ile gelen yel ile gider', 'emek vermeden ele geçen para kısa zamanda çarçur olur gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10909, 'sel olup akmak', 'gitmek: ''Nerede kaldı bunlar? Sel olup aktılar mı? / Kapkara bir günümde beni bıraktılar mı?'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10910, 'sel seli götürmek', 'çok fazla sel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10911, 'selam çakmak', ' tkz. selam vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10912, 'selam etmek', 'uzakta olan birine esenlik dilemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10913, 'selam olsun', '''esenlik dileklerim ulaşsın'' anlamında kullanılan bir iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10914, 'selam para, kelam para', '1) her davranış para harcamaya bağlıdır;', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10915, 'selam (selama) durmak', 'bir büyüğe, bir üste veya saygı duyulan bir şeye ayakta selam vermek: ''Ama birader, rahat mı edeceğiz bu bahçede, gelene geçene selam mı duracağız?'' -Y. Z. Ortaç. ''Yollarda sarı ve zayıf halk selama duruyor.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10916, 'selam (selamı) almak', '1) birinin selamlamasına karşılık vermek: ''Pazara indiği zaman kendine verilen selamı bile almıyordu.'' -Ö. Seyfettin. 2) selam gönderilmiş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10917, 'selam söylemek', '1) selamını birine götürmesini söylemek; 2) birinin gönderdiği selamı başkasına iletmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10918, 'selam verdik, borçlu çıktık', 'küçük bir ilgi gösterdik, üzerimize büyük bir iş yüklendik.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10919, 'selam vermek', '1) selamlamak: ''Kapıdan içeri giren adam, topuklarını birbirine vurarak askerce bir selam veriyor.'' -E. M. Karakurt. 2) din b. başını sağ ve sol omuzlarına çevirerek namazı bitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10920, 'selamete çıkmak', 'esenliğe kavuşmak, kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10921, 'selamünaleyküm kör kadı', 'aşırı tok sözlü kişiler için uyarma yollu söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10922, 'selden gelen suya gider', 'kolay ve emeksiz kazanılan şeyler elden kolay çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10923, 'sele gitmek', '1) sele kapılmak; 2) mec. gereksiz yere telef olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10924, 'sele kapılmak', 'sel sularıyla sürüklenip gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10925, 'selin ağzı tutulur, elin ağzı tutulmaz', 'doğal yıkımlara karşı önlem alınır ama söyledikleri yalan yanlış olanı susturmaya kimsenin gücü yetmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10926, 'semen peyda etmek', 'şişmanlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10927, 'semer vurmak', '1) semeri, yük hayvanının sırtına koyup bağlamak, semerlemek; 2) semer sırtı yaralamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10928, 'semeresini vermek', 'bir şey istenilen verimi, sonucu vermek: ''Nitekim bu hummalı faaliyet, semeresini vermekte gecikmedi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10929, 'semeri devirmek', 'eşek gibi kabaca yatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10930, 'sempatisini kazanmak', 'birinin sevgisini, ilgisini ve yakınlığını kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10931, 'semtine uğramamak', '1) bir yere özellikle gitmemek: ''Mektebin semtine bile uğramamışlar da hangi derse çalışmışlar acaba?'' -M. Yesari. 2) birini hiç aramamak, onunla ilgisini kesmek: ''Nice iyiliklerde bulunduğu kimseler, onun semtine uğramaz olmuşlardı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10932, 'sen ağa ben ağa, koyunları (inekleri) kim sağa?', 'herkes kendisini buyurucu durumda görür, iş yapmakla yükümlü saymazsa ortadaki işi kim yapar?', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10933, 'sen ben davası (kavgası)', 'bir konuda anlaşmazlığa düşüldüğünü anlatan bir söz: ''Nihayeti bulunmaz bir sen ben davasına düşmüşler.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10934, 'sen bilirsin', '''nasıl uygun bulursan öyle yap'' anlamında kullanılan bir söz: ''Kuşağından mendilini çıkarıp gözyaşlarını sildi. -Ya Rabbi sen bilirsin, ya Rabbi sen bilirsin diye söylendi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10935, 'sen bilirsin deyince (değirmende) kavga olmaz', 'bir konu üzerindeki görüşme sırasında uysallık gösterir, karşınızdakinin dediğini kabul ederseniz anlaşmazlık çıkmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10936, 'sen dede ben dede, bu atı kim tımar ede', 'herkes kendisini buyurucu durumda görür, iş yapmakla yükümlü saymazsa ortadaki işi kim yapar?', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10937, 'sen dost kazan, düşman ocağın başından çıkar', 'sen dost kazanmanın yoluna bak, düşman kolay kazanılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10938, 'sen giderken ben geliyordum', 'ben bu işi senden önce yaptım.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10939, 'sen işten korkma, iş senden korksun', 'her iş, doğacak engelleri yenmeye kararlı olmakla, direnmekle başarılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10940, 'sen sağ, ben selamet', 'iyi veya kötü bir sonuçla biten bir iş karşısında artık yapacak bir şey kalmadığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10941, 'sen zot ben zot, ata kim vere ot', 'herkes kendisini buyurucu durumda görür, iş yapmakla yükümlü saymazsa ortadaki işi kim yapar?', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10942, 'senden devletliye ortak olma', 'iki insan arasındaki beraberliğin sağlıklı yürüyebilmesi, anlaşma koşullarının her iki taraf için de eşit olmasıyla sağlanır, bu denge kurulamazsa yönetim güçlü olan tarafa geçer, hep onun dediği olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10943, 'seni gidi seni (seni seni)', 'yaramaz, haylaz, çapkın: ''Başını kaldırdı, seni seni diyerek başını sallayıp gülümsedi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10944, 'seninki can da benimki patlıcan mı?', 'senin canının değeri var da benimkinin değeri yok mu?', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10945, 'senli benli olmak', '1) iç içe olmak, bütünleşmek: ''Altı ay önce tramvaylar tuhafıma gitmişti. Bu sefer onlarla daha senli benli olduk.'' -B. R. Eyuboğlu. 2) aşırı ölçüde içten, teklifsiz olmak: ''Salonda kahvelerini içerlerken senli benli olmuşlardı bile.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10946, 'sepet havası çalmak', ' argo 1) işinden çıkarmak, sepetlemek: ''Patrona kalsa sepet havasını çoktan çalardı.'' -M. Ş. Esendal. 2) yanından uzaklaştırmak, gitmesini sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10947, 'sepette pamuğu olmamak', ' tkz. bilgisiz, boş kafalı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10948, 'ser verip sır vermemek', 'ağzı sıkı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10949, 'serbest bırakmak', '1) tutuklu veya gözaltında bulunan birini serbest, özgür duruma getirmek, tahliye etmek: Elde hiçbir delil olmadığı için serbest bıraktık. 2) kendi düşüncesi ve iradesine göre davranmasına izin vermek: ''Akli muvazenesi pek sağlam bulunmadığı için serbest bırakıldı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10950, 'serbest çalışmak', 'bir işverene bağlı olmadan kendi adına kazanç sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10951, 'serçeden korkan darı ekmez', 'tehlikeleri gözde büyüterek işe girişmekte çekingen davrananlar amaçlarına ulaşamazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10952, 'serde ... var', ' alay sözü edilen kimsedeki bir niteliği anlatan söz: ''Bakakalırım giden geminin ardından / Atamam kendimi denize, dünya güzel / Serde erkeklik var, ağlayamam'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10953, 'sergi açmak', 'sergilemek: ''Şehir Galerisi''nde açtığı ilk sergide, yalnız zevkine ve hünerine değil, sabrına da şaştım.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10954, 'sergi sermek', 'kurutmak veya göstermek için bir şeyi düz bir yere yaymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10955, 'sergin vermek', 'hastalanıp yatağa yatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10956, 'serilip serpilmek', '1) rahat bir biçimde yatmak; 2) gelişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10957, 'serilip yatmak', 'rahat bir biçimde yatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10958, 'serin gel!', ' argo ''sakin ol, soğukkanlı davran'' anlamında kullanılan bir uyarma sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10959, 'serin tutmak', 'sıcaktan etkilenmeden daha soğuk bir durumda bulundurmak: ''Dedelerimiz sıcakta serin tutan birtakım kürkler bulunduğunu bilirlerdi.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10960, 'serinlik vermek', '1) serin duruma getirmek; 2) mec. acısını, sıkıntısını azaltmak, avundurmak; 3) mec. rahatlatmak, huzura kavuşturmak: ''Evimin cehennemi içinde bana biraz serinlik verebilecek, bir bu fikirler vardı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10961, 'serkeş öküz (son) soluğu kasap dükkânında alır', 'dikbaşlı olanlar, davranışlarının cezasını görürler, hatta bu davranışları hayatlarına bile mal olabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10962, 'sermaye yapmak (etmek)', 'iş yeri açmak için gereken parayı sağlamak: ''Üç yüz lirayı alırlarsa bunun yüz lirasını çocuğa sermaye yapacaktı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10963, 'sermayen bir yumurtaysa taşa çal', 'yetersiz olanaklarla büyük işler yapmayı tasarlıyorsan başarılı olamazsın, vazgeç daha iyi.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10964, 'sermayeyi doğrultmak', 'ticaret için ortaya konan anaparayı batırmadan işletmek ve para kazanmak: ''Köyden kopup, yabancı işçi olup, beş altı yılda sermayeyi doğrultup, yurtta özel teşebbüsçü bir yarım yamalak kapitalist olma özlemi görülüyor çoğunda.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10965, 'sermayeyi kediye yüklemek', ' şaka parasını yiyip bitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10966, 'sermest olmak', 'çok hoşlanmak, kendinden geçmek: ''O okurdu, ben dinlerdim; o muharebe hikâyeleriyle sermest olurdum.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10967, 'sersem gibi', 'serseme yakışır biçimde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10968, 'serseme çevirmek', 'sersem etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10969, 'serseme dönmek', 'sersem bir duruma gelmek, şaşkın bir duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10970, 'sert çıkmak', 'aşırı biçimde karşı durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10971, 'servis edilmek', 'özel bir bilgi veya belge haber kaynağı tarafından istenilen yayın organına gönderilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10972, 'servis yapmak', 'sofrada hizmet etmek ve yemeği dağıtmak: ''Özel olarak iki aşçıyla iki de ayrıca servis yapacak garson çağrıldı.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10973, 'servise çıkmak', '1) ulaşım aracı ile öğrencileri, çalışanları gidecekleri yere taşımak; 2) servis yetkilisi onarım yapmak üzere çağrılan yere gitmek; 3) doktor hastaları durumlarını gözlemlemek üzere ziyaret etmek; 4) bir iş yerinde çay, kahve dağıtımı gibi hizmetleri yapmak üzere dolaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10974, 'serzenişte bulunmak', 'yakınmak (II).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10975, 'ses çıkarmamak (etmemek)', 'bir şeyi hoş görerek karşı çıkmamak, itiraz etmemek: ''İnsanlar bizim bahçeye çağırdığımız arkadaşlarımıza bile ses çıkarmıyorlardı.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10976, 'ses çıkmamak', 'haber gelmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10977, 'ses etmek', 'seslenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10978, 'ses getirmek', 'yaptığı işle, söylediği sözle dikkatleri çekmek ve kitleleri harekete geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10979, 'ses kesilmek', 'ses, artık duyulmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10980, 'ses seda çıkmamak', '1) haber çıkmamak: ''Çok uzak ama pek çok uzak birkaç akrabadan ses seda çıkmadı.'' -A. Gündüz. 2) hiçbir tepki görülmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10981, 'ses seda kesilmek (kalmamak)', 'hiçbir ses duyulmamak: ''Sanki bütün dünyada ses seda kesilmişti.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10982, 'ses seda yok', '''hiç haber gelmedi'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10983, 'ses vermek', '1) herhangi bir sesi çıkarmak: ''Üç defa ses veren bir küçük çanın altından bahçeye girdiler.'' -Y. K. Karaosmanoğlu. 2) bir çağrıya karşılık vermek: ''Her biri bir türlü feryada başlar / Güller seda verir, bağlar ses verir'' -Âşık Veysel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10984, 'sesi ayyuka çıkmak', 'çok yüksek sesle bağırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10985, 'sesini çıkarmamak', 'bir şey üzerindeki düşüncesini söylememek: ''Sesini çıkarmadı. Mütevekkil bir hâli vardı.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10986, 'sesini kesmek', 'söylemekteyken susmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10987, 'sesini kısmak', 'sesini alçaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10988, 'sesini yükseltmek', 'yüksek, öfkeli bir sesle söylemek: ''Çardaktan kocasının sesini yükselterek söylediğini duyan kadın, kahve takımlarını alıp çıktı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10989, 'sessizliğe gömülmek', 'sessiz duruma gelmek: ''Karanlık içinden bir süre fısıltılar geldi, sonra her şey derin bir sessizliğe gömüldü.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10990, 'set çekmek', '1) suyun akmasını, toprağın kaymasını önlemek için duvar yapmak; 2) mec. bir işi, bir davranışı, bir isteği önlemek, engellemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10991, 'sev beni, seveyim seni', 'sevgi karşılıklı olur, sen beni seversen ben de seni severim.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10992, 'sev seni seveni, hâk ile yeksan ise; sevme seni sevmeyeni, Mısır''a sultan ise', 'toplumdaki yeri ne düzeyde olursa olsun, sevgi gördüğün kişiye sen de sevgiyle karşılık ver; birisi sana içinde sevgi taşımıyorsa o kişi kim olursa olsun ondan uzak dur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10993, 'sevaba girmek', 'sevap kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10994, 'sevap kazanmak (işlemek)', 'hayırlı bir davranışta bulunmak: ''Gülsüm''ün sevinci sade sevap kazanmak ümidinden doğmuyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10995, 'sevda çekmek', 'birine tutkun olmak, aşk tutkusu içinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10996, 'sevda geçer yalan olur, sonra sokar yılan olur', 'başta insanı mutlu eden duygusal ilişkiler zamanla körelir ve sonuçta acı veren birer anıya dönüşür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10997, '... sevdasına düşmek', 'bir şeyi çok fazla istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10998, 'sevenin kuluyum, sevmeyenin sultanı', 'beni sevdiğine inandığım kişi için yapmayacağım şey yoktur, onun bir dediğini iki etmem, ama sevilmediğimi anlarsam kendime olan saygımı ondan üstün tutarım.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (10999, 'sevgi beslemek', 'sevgi duymak, sevmek: ''Makedonya''da savaşmıştı ve Türk köylüsüne karşı büyük sevgi besliyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11000, 'sevinci kursağında kalmak', 'bir engel sebebiyle hayal kırıklığına uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11001, 'sevincinden ağzı kulaklarına varmak', 'çok sevinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11002, 'sevinç yaşları (gözyaşları) dökmek', 'sevinçten ağlamak: ''Şu mendilini burnuna tutmuş, sevinç yaşları döken hanım herhâlde gelinin anası olacaktı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11003, 'sevinçten uçmak', 'çok sevinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11004, 'sevk etmek', '1) göndermek, götürmek; 2) mec. sürüklemek, itmek: ''Burada başka bir olay anlatacağım ki bu, Türk''ü şuuraltı bir kuvvetle İstiklal Savaşı''na sevk eden amillerin biridir.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11005, 'seyirci kalmak (olmak)', 'bir olay karşısında hiçbir tepki göstermeyerek işe karışmamak: ''Kavga edenlere kimse karışmaz, sadece uzaktan seyirci kalırlar.'' -N. Hikmet. ''Sadece seyirci olduğumu, olayların dolayısıyla hayatın dışında kaldığımı bu sorudan sonra anladım.'' -E. Işınsu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11006, 'seyrana çıkmak', 'gezmeye, gezintiye çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11007, 'seyre çıkmak', '1) bir yerden başka bir yere gitmek için yola çıkmak; 2) eğlenmek üzere gözlemek, bakmak: ''Seyrimize çıktınız değil mi? Yürek soğutuyorsunuz değil mi? Allah sizi bizden besbeter etsin inşallah!'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11008, 'seyre dalmak', 'bir şeye kendini vererek bakmak: ''Sanki Rumeli baştan başa bir arena idi ve Avrupa siyaset adamları da birer Roma imparatoru gibi mermerden localarına kurulmuşlar, oradaki olumlu güreşleri seyre dalmışlardı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11009, 'seyrek git sen (sıkça varma) dostuna, kalksın ayak üstüne', 'kişi dostuna sık sık giderse seyrek gittiğinde gördüğü konukseverliği görmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11010, 'seyreyle gümbürtüyü', '''çıkacak olayları gör, ibretle seyret'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11011, 'sıcağa kar mı dayanır?', 'aşırı harcamalarla eldeki imkânlar çok çabuk tükenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11012, 'sıcak bakmak', 'anlayışla karşılamak, olumlu değerlendirmek, ilgi duymak: ''Onlardan genelleme yaparak bütün kol emekçilerine sıcak bakma eğilimini edindim.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11013, 'sıcak basmak', 'hava çok ısınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11014, 'sıcak yüz göstermek', 'yakınlık göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11015, 'sıçan çıktığı deliği bilir', 'bir kabahate, suça veya gizli işe kalkışan kişi, yakalanacağını anladığında nereye sığınacağını bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11016, 'sıçan deliğe sığmamış, bir de kuyruğuna kabak bağlamış', '1) yapamayacağı kadar ağır bir işi varken başka bir iş daha yüklenmiş; 2) kendisi sığıntı durumundayken yanına bir kişi daha almış.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11017, 'sıçan deliği aramak', 'saklanacak bir yer aramak: ''Askere gitmemek için sıçan deliği arıyordu değil mi?'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11018, 'sıçan deliği bin akçe', '''kaçıp saklanacak yer yok'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11019, 'sıçan deliğine paha biçilmez olmak', '''güç bir durumda sığınacak bir yer bulmakta güçlük çekmek'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11020, 'sıçan düşse başı yarılır', '''hiçbir şey yok'' anlamında kullanılan bir söz: ''Bir zamanlar hazinemiz tamtakırdı, sıçan düşse başı yarılırdı.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11021, 'sıçan geçer yol olur', 'bir iş için kötü bir yol açılırsa artık herkes o yolu tutar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11022, 'sıçana dönmek', 'üstü başı çok ıslanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11023, 'sıçıp sıvamak', ' kaba öfkelenip kaba küfürlerle dolu sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11024, 'sıçtı Cafer, bez getir', ' kaba birinin berbat bir iş gördüğünü anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11025, 'sıfır çekmek', ' sp. halter yarışmalarındaki silkme ve koparma dallarında belirlenen ağırlığı kaldıramayıp elenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11026, 'sıfıra inmek', '1) bitmek, tükenmek, yok olmak: ''Zannedersem kocamın ziyaretçileri de sıfıra indi.'' -Ö. Seyfettin. 2) sp. futbol, hentbol vb. oyunlarda hücum oyuncusu rakip alanda bitiş çizgisine kadar gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11027, 'sıfıra sıfır, elde var sıfır', 'bütün çalışmaların boşa gittiğini, istenilen sonucun alınamadığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11028, 'sıfırdan başlamak', 'en baştan, hiçbir şeye sahip olmadan bir işe girişmek: ''Batı tiyatrosu Türkiye için yepyeni bir şey olduğu için sıfırdan başlamak gerekiyordu.'' -M. And.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11029, 'sıfırı tüketmek', '1) gücü kalmamak: ''Sonra ulusal sporumuzda hızla geriledik, çok geçmeden sıfırı tükettik.'' -T. Halman. 2) yoksul duruma gelmek, yoksullaşmak; 3) ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11030, 'sıhriyet peyda etmek', 'hısımlık oluşturmak: ''Süleyman Şah, kurtuluşu Osman Oğulları ile sıhriyet peyda etmekte görüyordu.'' -F. F. Tülbentçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11031, 'sık gidersen dostuna, yatar arka', 'kişi dostuna sık sık giderse seyrek gittiğinde gördüğü konukseverliği görmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11032, 'sıkı basmak', 'güçlü davranmak, direnmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11033, 'sıkı durmak', 'güçlü, dayanıklı olmak, dikkatli bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11034, 'sıkı tutmak', '1) önem vermek: İşini sıkı tut. 2) sürekli olarak denetlemek, kontrol altında bulundurmak; 3) bir işte disiplinli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11035, 'sıkıdan geçirmek', ' argo dayak atmak: ''Onlarla ahbaplıkta direnirse iş kolaydı; kapıaltına çekilir, güzel bir sıkıdan geçirilirdi.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11036, 'sıkıntı basmak', 'çok sıkılmak, can sıkıntısı duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11037, 'sıkıntı çekmek', 'zorluk veya yoksulluk içinde yaşamak: ''İki ateş arasında epeyce sıkıntı çektik.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11038, 'sıkıntı vermek', 'tedirgin etmek, bunaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11039, 'sıkıntıda olmak', 'geçim darlığı çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11040, 'sıkıntısı olmak', '1) tedirgin, rahatsız eden bir durumda bulunmak: ''Bir derdi, bir sıkıntısı olup da öyle susup durduğu akşamlar bile yanında bulunmaktan hoşlanıyoruz.'' -N. Ataç. 2) işemesi gerekmek, sıkışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11041, 'sıkıntıya düşmek', 'darlık, yokluk içinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11042, 'sıkıntıya gelememek', 'güç işlere dayanamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11043, 'sıkıp suyunu çıkarmak', 'sömürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11044, 'sıkıya almak', '1) hareketlerini sınırlamak veya önlemler almak: ''Seniha etrafını bu kadar sıkıya alan bu adamlardan hiç sıkılmıyor mu?'' -Y. K. Karaosmanoğlu. 2) disiplin altına almak: Başkan son zamanlarda işleri sıkıya aldı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11045, 'sıkıya gelmek', 'güç bir durumla karşılaşmak: ''Sıkıya geldi mi borç etmekten çekinmez, sonra bu borçları ödemek için evinin eşyasını satar.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11046, 'sıla etmek', 'sılaya gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11047, 'sılaya gitmek', '1) bir süre ayrı kaldığı evini, yurdunu görmeye gitmek: ''Ara sıra memlekete, sılaya gitmek lazım.'' -R. H. Karay. 2) anne, baba ve diğer akrabalarını görmek için memlekete gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11048, 'sınav vermek', 'sınavdan geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11049, 'sınava çekilmek', 'birinin bilgisi ölçülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11050, 'sınava girmek', 'bir kimse, bir konu üzerindeki bilgisinin ölçülmesini sağlamak için yapılan yoklamada hazır bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11051, 'sınavda bırakmak', 'sınavda başarısız saymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11052, 'sınavdan geçmek', '1) sınava girmek: ''Evet, bir sınavdan geçeceksin. Ben de sınav amirin tayin edildim.'' -A. Ümit. 2) girilen sınavda başarılı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11053, 'sınıfta bırakmak', 'sınıf geçmesine engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11054, 'sınıfta çakmak', ' argo sınıfta kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11055, 'sınıfta kalmak', '1) başarılı olamayan öğrenci, bir üst sınıfa geçemeyerek aynı sınıfta yeniden okumak; 2) mec. herhangi bir işte başarısız olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11056, 'sınır çekmek (çizmek)', '1) sınırını belirtmek: ''1920 baharı muhteşem bir mart sabahında Sultan Dağları''nın sınır çizdiği Batı Anadolu''ya kan ve barut kokularıyla geliverdi.'' -T. Buğra. 2) son vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11057, 'sınır dışı etmek', 'bir kimseyi bulunduğu ülkede yaptığı yasa dışı eyleminden dolayı ülkenin sınırları ötesine çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11058, 'sır tutmak (saklamak)', 'bir sırrı açığa vurmamak, başkasına söylememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11059, 'sır vermek (sızdırmak)', 'bir sırrı açığa vurmak, başkasına söylemek: ''Mustafa dışarı sır sızdırmıyordu lakin üzüntüden de eriyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11060, 'sıra dayağı çekmek', 'birden çok kişiyi teker teker ve birbirinin ardı sıra dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11061, 'sıra saygı gözetmek', 'karşılıklı saygı göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11062, 'sıra (sırasını) savmak', 'sırayla yapılan bir işte sıra kendine geldiğinde gereğini yapmak: ''Neyse, sıramızı savdık ve yine yola çıktık ve yolda beni bir düşüncedir aldı.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11063, 'sıram sıram dizilmek', 'sıra veya sıralar oluşturacak biçimde yan yana, arka arkaya gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11064, 'sırası düşmek', 'uygun zamanı gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11065, 'sırası gelmek', '1) bir başkasından sonra sıra birinin veya bir şeyin olmak; 2) sırası düşmek: ''Hani bazen sırası geliyor da maziye merbutiyet, filan diyoruz.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11066, 'sırası gelmişken', '''fırsat düşmüşken, söz bu konudayken'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11067, 'sırasına getirmek', 'uygun zamanını, fırsatını bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11068, 'sırasına göre', 'durumun gerektirdiği gibi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11069, 'sırasını kaybetmek', 'çocuk veya bebek, hastalık veya başka bir sebep dolayısıyla uyku ve meme zamanını şaşırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11070, 'sırat köprüsünden geçmek', 'bir iş yapılırken sıkıntılı, eziyetli durumlar içinde kalınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11071, 'sıraya dizmek', '1) sıralamak; 2) bir topluluk içinde herkese aynı biçimde davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11072, 'sıraya koymak', 'düzenlemek, sıralamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11073, 'sırça köşkte oturan komşusuna taş atmamalı', 'insan kendinde herhangi bir kusur varken başkalarını aynı kusurla suçlamamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11074, 'sırık gibi', ' alay uzun boylu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11075, 'sırım gibi', 'ince yapılı ve güçlü: ''Şimdi, altmışını geçmiş olmasına rağmen, sırım gibi bir vücudu vardı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11076, 'sırra ermek', 'gizli tutulan veya sır durumunda olan bir şeyi anlamak, kavramak: ''Fakat bu iki genç henüz bu sırra eremedikleri için sabırsızlanıyorlar, öfkeleniyorlardı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11077, 'sırra kadem basmak', 'bir kimse ortalıktan yok olmak, ortalıkta görünmemek: ''Denizde bazı balık türleri sırra kadem bastı ama başka nice türler kıyılara akın etmeye başladılar.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11078, 'sırrını açma dostuna, o da söyler dostuna', 'bir sır en yakın dosta bile söylenmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11079, 'sırt sırta vermek', 'iş birliği yapmak: ''Sırt sırta verip bitiririz bu işi.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11080, 'sırt (sırtını) çevirmek', '1) bir şeye veya birine önem vermemek: ''Batı âlemi Türkiye''den vazgeçemez, bizi yalnız bırakamaz, askerî ihtiyaçlarımıza sırt çeviremez...'' -T. Halman. 2) bir şeyden veya bir kimseden desteğini, ilgisini kesmek; 3) birine darılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11081, 'sırtı kaşınmak', 'dayak yemeyi hak edecek davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11082, 'sırtı yere gelmek', 'yenilmek, alt olmak: ''Anladım ki hayat savaşının birinci büyük dönümünde Ayşe''nin sırtı yere gelmişti.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11083, 'sırtı yere gelmemek', 'sarsılmamak, yerinden düşürülememek, güçlü olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11084, 'sırtına almak', '1) yüklenmek, çuvalı sırtına aldı; 2) bir giyeceği giymek veya sırtına örtmek: Sırtına bir şey almadan sokağa fırladı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11085, 'sırtına geçirmek', 'bir şeyi giymek: ''Pardösüyü sırtıma geçirdim.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11086, 'sırtında yumurta küfesi olmamak', 'eski düşünce ve yönünü kolayca değiştirmek veya sözünden caymakta sakınca görmemek: ''Çelişki içinde konuşur ve sırtında yumurta küfesi olmadığından dün ak dediğine bugün rahatlıkla kara diyebilir.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11087, 'sırtından atmak', 'başından savmak veya birinin, bir şeyin sorumluluğunu, yükünü üzerine almamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11088, 'sırtından bıçaklamak', 'ihanet etmek: ''Arkadaşların birbirini sırtından bıçaklaması doğru değil. Bunu ancak düşmanlar yapabilir.'' -S. Dölek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11089, 'sırtını dönmek', 'sırt çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11090, 'sırtüstü yatmak', '1) sırtı yere gelmek üzere yatmak: ''Sırtüstü yatıp gözlerinizi kara bir bezle bağlayın.'' -H. Taner. 2) hiçbir şey yapmamak: ''Üç gün sırtüstü yattım trende.'' -N. Hikmet. 3) mec. çalışmadan rahat bir yaşam sürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11091, 'sıska olmak', '1) karın boşluğuna su dolarak karnı şişmek; 2) aşırı zayıf olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11092, 'sıskası çıkmak', 'çok zayıflamak, sıskalaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11093, 'sıtma tutmak', 'ateş ve ter nöbetleriyle titremeye başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11094, 'sıva vurmak', 'bir duvarı sıva kullanarak düzgünleştirmek, sıvamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11095, 'sıygaya çekmek', 'birine sorular sorup cevaplarını istemek: ''Yüksek tahsilli olup olmadığımızı anlamak için bizi kara cümleden bile değil de imladan sıygaya çektiler.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11096, 'sıyırıp çıkarmak', 'çekip kurtarmak: ''Bunlar yaşama yolunda bir engele çarptılar mı hemen dedelerinin adını verirler ve kendilerini güçlükten sıyırıp çıkarırlardı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11097, 'sızıp kalmak', 'çok içki içip veya çok yorulup uyuyakalmak: ''Eskimiş boş çuvallar gibi sızıp kalırlardı bir köşede.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11098, 'sicil vermek', 'sorumlu bir görevli, yanında çalışan birinin bir aşamaya gelmesinde yeterli olup olmadığını gereken makama bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11099, 'siciline işlemek', 'bir çalışanın yaptığı olumlu veya olumsuz davranışları siciline kaydetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11100, 'sicim gibi', 'damlaları ince bir sıra oluşturacak biçimde birbiri ardınca akan (yağmur, gözyaşı): ''Gözlerinden sicim gibi yaş inerek hepsini bir kömür sandığına doldurdu.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11101, 'siftah etmek', '1) esnaf sabahleyin ilk alışverişi yapmak: ''Bu vakit kim gelecek? Her günkü gibi siftahı sen ediyorsun?'' -E. E. Talu. 2) mec. bir işi ilk kez yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11102, 'sigara kâğıdı gibi', 'çok ince.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11103, 'sigarayı tellendirmek (tüttürmek)', 'keyifle sigara içmek: ''Birkaç tane bira çektikten sonra üzerlerine sigarayı tellendirdim mi değme keyfime artık.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11104, 'sigorta atmak', 'bir arıza sonucu sigortada elektrik akımı kesilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11105, 'sigortası atmak', ' argo çok sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11106, 'siktir et!', '1) ''aldırma, önem verme!'' anlamında kullanılan bir söz; 2) ''kov, defet!'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11107, 'siktirip gitmek', 'başını alıp gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11108, 'silah atmak', 'silahtan mermileri boşaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11109, 'silah başı etmek', ' ask. askerlikte, verilen komut üzerine herkes görevi başına geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11110, 'silah çatmak', ' ask. silahları uç uca çapraz bir biçimde dayayarak durdurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11111, 'silah patlamak', '1) silah ateş almak; 2) mec. savaş başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11112, 'silah silaha girmek', 'karşılıklı olarak ateş etmek: ''Üç serseri birbirleriyle silah silaha girmişler.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11113, 'silaha davranmak', 'kullanmak için silahına el atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11114, 'silahaltına almak', 'askerlik görevine başlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11115, 'silahaltında bulunmak', 'silahaltında olmak: ''Silahaltında bulunan er ve erbaşlarla askerî öğrenciler ... oy kullanamazlar.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11116, 'silindir gibi ezmek', 'bir kimseyi her yönüyle güçsüz duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11117, 'silinip gitmek', 'bir şey birdenbire yok olmak veya unutulmak: ''Bu sonuncular, ardından gelen büyük dalgaların gürültüsünde silinip gitti.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11118, 'silip atmak', 'ilgi ve ilişkisini tamamen kesmek: ''Beni aldattı diye onu kalbimden silip attım, ondan nefret ediyorum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11119, 'silip süpürmek', '1) evi, ortalığı temizlemek; 2) ne var ne yoksa hepsini yemek: ''Büyükdere''den yanına bir sepet kiraz aldığı vakit, sandalda bütün kirazı silip süpürür.'' -S. Birsel. 3) ne var ne yok hepsini alıp götürmek veya yok etmek: ''Bu, nereden ve kimden geldiği belli olmayan darbe son ümitlerini de silip süpürmüştü.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11120, 'silkinip sıyrılmak', 'kendine gelip kurtulmak: ''Sebepsiz duyduğu bu kederden bir türlü silkinip sıyrılamıyor.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11121, 'silkip atmak', 'her türlü ilgisini kesmek: ''... çocuğun olmazsa bir gün bu herif seni silkip atar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11122, 'sinameki gibi', 'mızmız, sevimsiz, kimseyle ilişki kurmayan (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11123, 'sinek avlamak', ' şaka işi veya müşterisi olmayıp boş oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11124, 'sinek küçüktür ama mide bulandırır', 'önemsiz, küçük gibi görünen bir şey kötü ve olumsuz bir izlenim yaratır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11125, 'sinek pekmezciyi tanır', 'işini bilen kişi, yararlanacağı kimseyi bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11126, 'sinekten yağ çıkarmak (çıkartmak)', 'olmayacak şeylerden yararlanmaya çalışmak: ''Elverişli durumların kokusunu hemencecik alıyor, sinekten yağ çıkartmasını biliyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11127, 'sinerji yaratmak', 'bir sonuca katkısı olabilecek birkaç etkeni bir arada harekete geçirerek güç elde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11128, 'sineye çekmek', 'kötü bir davranış, söz veya olaya ister istemez katlanmak: ''Onlar hızla geçer veya düşer; musibeti sineye çekmek millete düşer.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11129, 'sineyimillete dönmek', '1) bulunduğu makamı veya görevi terk edip halktan biri olmak; 2) halk oylamasına başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11130, 'sinir buhranı geçirmek', 'bunalım içinde olmak: ''Kız kardeşim bir sinir buhranı geçiriyordu.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11131, 'sinir buhranına tutulmak', 'bunalım geçirmek: ''Üç dört gün olmuştu, acayip sinir buhranlarına tutulmuştum.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11132, 'sinir kesilmek', 'çok sinirlenmek, öfkelenmek: ''Başkalarının rahatlık saydığı işlerde sıkıldım, sinir kesildim.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11133, 'sinir küpü olmak', 'aşırı derecede sinirli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11134, 'sinir küpüne çevirmek', 'aşırı derecede sinirlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11135, 'sinir küpüne dönmek', 'aşırı derecede sinirlenmek: ''Çocuğum bu sürekli gözaltından huzursuz oluyor; ben sinir küpüne dönüyorum.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11136, 'sinir sahibi olmak', 'devamlı sinirlenir durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11137, 'siniri oynamak', 'öfkelenmek, sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11138, 'siniri tutmak', 'birdenbire sinirlenmek veya davranışlarını denetleyememek: ''... moda deyince çıldırmaz, çok gülerse siniri tutup sonra yarım saat ağlamaz.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11139, 'sinirleri ayakta olmak', 'çok sinirlenmiş veya öfkelenmiş bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11140, 'sinirleri boşanmak', 'sinirlenip kendini tutamayarak gülmek, ağlamak veya bağırmak: ''Şimdi soğukkanlı olan amcam, benim ise sinirlerim boşanmak üzere.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11141, 'sinirleri bozulmak', 'çok sinirlenmek, ne yapıp edeceğini bilmeden şaşkın, karmaşık bir duruma düşmek: ''Başı aylarca ağrımayan, sinirleri bozulmayan, yanılmayan sanatkâr, olduğu yerde sayandır.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11142, 'sinirleri gergin olmak', 'sinirlendirici yeni bir olay çıkarsa hemen tepki gösterecek durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11143, 'sinirleri gevşemek (yatışmak)', 'sinirliyken ferahlamak, sakinleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11144, 'sinirlerine hâkim olmak', 'davranışlarını ve kendini denetleyebilmek, soğukkanlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11145, 'sinyal almak', 'işaret almak, belirtilerin farkına varmak: ''Ruhunun derinliklerinden ürperti dolu, hiç de yabancısı olmadığı sinyaller alıyordu.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11146, 'sinyal vermek', 'bir şeyi işaretle bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11147, 'sipariş almak', 'bir şeyin yapılması veya gönderilmesi kendisine ısmarlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11148, 'sipariş etmek', 'bir şeyin yapılmasını veya bir şeyin gönderilmesini istemek, ısmarlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11149, 'sipariş vermek', 'bir şeyin yapılmasını, getirilmesini veya gönderilmesini birine ısmarlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11150, 'siper olmak', 'birini veya bir şeyi korumak amacıyla kendini siper olarak kullanmak: ''Güneş yakmasın diye onun güzel başını/ Gördüm siper olurken iki arkadaşını'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11151, 'sipere yatmak', 'siper içine saklanmak, gizlenmek: ''Çatın arkadaşlar da atları çatın / Kurşun bizi tutuyor sipere yatın'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11152, 'sipsivri kalmak', 'herkesin çekilmesiyle yalnız kalmak veya ortada kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11153, 'sirayet etmek', '1) hastalık geçmek, bulaşmak: ''Valinin hızı ve coşkusu, yanındakilere de bulaşıcı bir hastalık gibi sirayet ediyordu.'' -A. Kulin. 2) mec. yayılmak, dağılmak: ''Bu dedikodular bizim eve bile sirayet etti.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11154, 'sirkesini, sarımsağını sayan paçayı yiyemez', 'küçük sakıncalarını düşünerek bir işe girişmeyen kişi, o işin kazançlarından yoksun kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11155, 'sitemde bulunmak', 'sitem etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11156, 'sitteisevir her saati bir devir', 'fırtına günlerinde hava her saat değişikliğe uğrar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11157, 'sitteisevir kapıyı çevirir', 'kötü havalarda dışarı çıkmamayı öğütleyen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11158, 'siya siya gitmek', 'geri geri gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11159, 'siyem siyem ağlamak', 'iplik iplik gözyaşı dökmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11160, 'siyem siyem yağmak', 'yağmur, kar ince ince yağmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11161, 'siz bilirsiniz', '''nasıl istersiniz öyle olsun'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11162, 'siz sağ olun', '''ne yapalım, ziyanı yok'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11163, 'sizden iyi olmasın', 'birinin, orada bulunmayan bir kimseyi överken karşısındakine söylediği bir nezaket sözü: ''... sizden iyi olmasın pek babacan, cana yakın bir adamdır.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11164, 'size doyum olmaz', 'bir yerden ayrılırken söylenen bir nezaket sözü: ''Doktor ''size doyum olmaz'' diye gülerek müsaade istedi. Ayağa kalktı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11165, 'size (sizlere) ömür', 'bir kimsenin öldüğünü bildirmek için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11166, 'skala yapmak', 'çalgı perdelerine parmak alıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11167, 'slogan atmak', 'sloganı bağırarak söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11168, 'sofra donatmak', 'sofraya bol ve türlü yiyecekler koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11169, 'sofrada elini, mecliste dilini sakla', 'topluluk içinde kendini denetle, aşırı davranışlarda bulunmaktan kaçın, açgözlülük ve gevezelik etme.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11170, 'sofu soğan yemez, bulunca sapını komaz', 'hoşa gitmeyen işlere yönelmez gibi görünen öyle kişiler vardır ki bu işlere girişince en aşırı yolu tutarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11171, 'soğanın acısını yiyen bilmez, doğrayan bilir', 'bir iş yapılırken ne kadar güçlük çekildiğini, o işi başarmış olan bilir; başarılmış olan bu işten yararlanan bilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11172, 'soğuk almak', 'üşüyerek hastalanmak, üşütmek: ''İliklerine kadar da ıslanmış ve soğuk almış.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11173, 'soğuk çalmak', 'soğuk bitkiye zarar vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11174, 'soğuk çıkmak', 'hava soğumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11175, 'soğuk durmak', 'ilgisiz, sevimsiz davranmak: ''Suat ilgilerine heyecanla karşılık vermiyor, biraz uzak ve soğuk duruyordu.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11176, 'soğuk duş etkisi yapmak', 'ansızın bildirilen tatsız bir haber olumsuz bir tepki yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11177, 'soğuk düşmek (kaçmak)', 'söz, davranış vb. yersiz ve sevimsiz olmak: ''Bir cenaze alayında böyle bir latife az buçuk soğuk kaçmakla beraber pek yersiz de sayılmazdı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11178, 'soğuk ter dökmek (basmak, boşanmak)', 'korkmak, heyecanlanmak, bunalmak, gerilmek: ''Safinaz kardeşini düşününce soğuk ter döktü.'' -H. E. Adıvar. ''Hele ansızın alnını, bıyıklarının dibini ve ensesini basan soğuk ter, sinsi bir ölüm korkusunu içine yılan gibi akıtıyor.'' -A. İlhan. ''Elleri ayakları buz kesildi, soğuk bir ter boşandı bütün vücudundan.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11179, 'soğuk vurmak (yakmak)', 'çok soğuğun etkisiyle bitki kurumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11180, 'soğukkanlı olmak', 'kolayca, öfke, telaş ve heyecana kapılmamak: ''Önce ince manevralar denedi ama soğukkanlı olamadığı için göze çarpıyordu.'' -R. Mağden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11181, 'sokağa atmak', '1) birini düşkün, yoksul kalacak biçimde evden, iş yerinden uzaklaştırmak veya kovmak: ''İnsanı kolundan tutup sokağa atmazlar.'' -Halikarnas Balıkçısı. 2) para, eşya vb.ni boş yere harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11182, 'sokağa çıkmak', 'gezmek veya bir iş görmek için evden çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11183, 'sokağa dökülmek', '1) herhangi bir sebeple dışarı çıkmak: ''Her zaman, saat on bir buçuk dedi mi kadın erkek, kol kola sokağa dökülürlerdi.'' -P. Safa. 2) gösteri, protesto gibi amaçlarla insanlar sokaklara, meydanlara inmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11184, 'sokağa (sokaklara) düşmek', '1) kadın kötü yola saparak orta malı olmak; 2) bir şey çoğalıp değerini yitirmek; 3) sükûneti, huzuru evin dışında aramak: ''Babamın iğneli bakışlarından kurtulmak için o uyurken sokaklara düşerdim.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11185, 'sokaklara dökülmek', 'kalabalık hâlde sokakta olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11186, 'sokakta kalmak', 'sığınacak yeri olmamak, bakacak kimsesi bulunmamak: ''Şimdi eski âdetler kalktı ama bu öksüzün kimseciği yok, sokakta kaldı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11187, 'sokma akıl sekiz adım gider', 'hep başkalarının verdiği akılla hareket eden kimse, bir yere kadar başarılı olur, daha sonra ne yapacağını bilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11188, 'sol eli beklemek', ' şaka yemeğe beklenilen birine, yemeğe başlandığını anlatmak için kullanılan bir söz: Sol elimiz bekliyor, çabuk gelin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11189, 'sol tarafından kalkmak', '1) aksiliği, huysuzluğu, tersliği üzerinde olmak; 2) işleri ters gitmek, iyi gününde olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11190, 'sol yapmak', 'direksiyonu sola doğru çevirmek, sola yöneltmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11191, 'sola kaymak', 'siyasette ve ekonomide sol eğilimli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11192, 'solda sıfır kalmak', '1) anlamı olmamak, değersiz olmak: ''Benim hâlim memleketin düştüğü hâlin yanında solda sıfır kalır.'' -A. Kulin. 2) sönük kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11193, 'solo yapmak', 'müzik parçası bir kişi tarafından söylenmek veya çalınmak: ''Saksafoncu, saksafonun borusunu havalara kaldırarak sololar yapıyordu.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11194, 'solucan gibi', 'solgun ve zayıf (kimse): ''Solucan gibi cılız ve pis bir çocukmuş.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11195, 'soluğan etmek', 'soluk soluğa bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11196, 'soluğu (bir yerde) almak', 'bir yere hemen gitmek veya sığınmak: ''Balığı sırtlayınca soluğu ninesinin kulübesinde aldı.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11197, 'soluğu kesilmek (tutulmak)', '1) soluk almaz duruma gelmek; 2) mec. aşırı heyecanlanmak; 3) mec. gücü tükenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11198, 'soluğunu kesmek', 'bir şey çok heyecan veya korku vermek: ''Adımı Türk Yurdu dergisinin kalın, kırmızı kapağında gördüğüm zaman sevinç soluğumu kesmişti.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11199, 'soluk aldırmamak', 'ara vermeden çalıştırmak, vakit bırakmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11200, 'soluk almak', '1) havayı ciğerlere çekmek, nefes almak: ''Caddeye çıkınca derin soluk alıyorduk.'' -A. Kutlu. 2) dinlenmek: ''Hem biraz soluk alırım hem de adamcağızın gönlünü almış olurum.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11201, 'soluk soluğa kalmak', 'nefes alamayacak duruma gelmek, çok yorulmak: ''Çıkrıkçılar yokuşunu bir sincap çevikliğiyle tırmanır ve yokuşun üst başında soluk soluğa kalırdı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11202, 'son bulmak', 'bitmek, tükenmek: ''Dallar uçlara doğru gittikçe inceliyor, gecenin karanlığına karışarak son buluyordu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11203, 'son gelmemek', 'sınır tanımamak, haddi hesabı olmamak: ''Salime kadının damadından bahsederken onu övmelerine son gelmezdi.'' -H. Z. Uşaklıgil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11204, 'son kozunu (kartını) oynamak', 'elinde bulunan son imkânı kullanmak: ''İlk uzunçaların hazırlıkları başladığında, her ikisi de son kartlarını oynadıklarını biliyordu.'' -M. Mungan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11205, 'son nefesini vermek', 'ölmek: ''Adam, iskelenin üstüne yığılmış, son nefesini verirken biçarenin şapkasını aşırmışlar.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11206, 'son noktayı koymak', 'bir işte en son sözü söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11207, 'son pişmanlık fayda vermez (etmez)', 'iş işten geçtikten sonra pişman olmanın yararı yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11208, 'son vermek', 'bitirmek, sona erdirmek: ''Gevezeliklerine tam zamanında son vermişler.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11209, 'son yolculuğa çıkmak', 'ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11210, 'son yolculuğa uğurlamak', 'birinin cenaze törenine katılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11211, 'sona ermek', 'son bulmak: ''Dağın patlatılması sona erince dolgu başlayacaktı.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11212, 'sona kalan dona kalır', 'bir işte geç kalan istediği şeyi elde edemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11213, 'sondaj yapmak', '1) sonda ile yoklamak, sondalamak; 2) mec. bir durum, bir düşünceyle ilgili olarak yoklama yapmak, araştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11214, 'sonradan gelen devlet devlet değildir', 'kişi yaşlandıktan sonra gelen zenginlik işe yaramaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11215, 'sonradan görme, gâvurdan dönme', 'sonradan görmeler fazla iyi niteliklere sahip değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11216, 'sonu gelmek', 'bitmek, tükenmek, yok olmak, ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11217, 'sonuç almak', '1) bir işi bitirmek, sonuçlandırmak; 2) istenilen sonuca ulaşmak, verim almak: Görüşmelerden sonuç alınamadı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11218, 'sonuç çıkarmak', '1) mat. bir işlemi bitirip sonuca ulaşmak; 2) kesin bir karar veya görüşe varıp bunu bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11219, 'sonuç vermek', 'bir durumun sağlanmasına imkân sağlamak: Çalışmaları sonuç vermedi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11220, 'sonunu almak', '1) bir işi bitirmek; 2) bir işin bittiğini görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11221, 'sonunu getirememek', 'iyi başladığı bir işi başarıyla bitirememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11222, 'sopa atmak (çekmek)', 'dövmek: ''Şu budalaya bir sopa çekin de bir daha para kazanmadan gurbette kalmayı öğrensin.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11223, 'sopa yemek', 'dövülmek, dayak yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11224, 'sopanın altına yatırmak', 'dövmek: ''Topal iyice küplere binse de avradını sopanın altına yatırsaydı.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11225, 'sora sora Bağdat (Kâbe) bulunur', 'insan sora sora çok uzak yerleri bile bulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11226, 'soran yanılmamış (yorulmamış)', 'bilmediği şeyi bir bilene soran, en zor işlerin bile üstesinden gelir; sormayan ise güçlükler içinde yuvarlanır gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11227, 'sorgu suale çekmek', 'sorguya çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11228, 'sorguya çekmek', 'bir suçla ilgili olarak soru sorup cevap istemek: ''Hayalimde polislerin beni karakola sürüklediklerini ve sıkı bir sorguya çektiklerini görüyordum.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11229, 'sorma kişinin aslını, sohbetinden bellidir', 'bir kişinin nasıl bir insan olduğu konuşmasından belli olur, soyunu sopunu öğrenmeye gerek yoktur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11230, 'sorma! (sormayın!, sorma gitsin!)', 'çokluk, aşırılık ve kötü bir durum anlatan bir söz: Öyle bir sıcak ki sorma gitsin! Sorma başımıza gelenleri! O işi sorma, sarpa sardı!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11231, 'sormak ayıp olmasın', 'sorulması teklifsizlik sayılan bir şeyi sormadan önce özür dilemek için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11232, 'sorması ayıp olmasın (sorması ayıp)', 'sormak ayıp olmasın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11233, 'sorti yapmak', '1) uçak bir noktadan kalkıp başka bir noktaya inmek; 2) uçak bir noktaya çeşitli nedenlerle inişe geçip yeniden yükselmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11234, 'sorumlu tutmak', 'sorumlu saymak, mesul olarak görmek: ''Belki bu matbaanın işi ama dergiler elimize ulaşmazsa sizi sorumlu tutarız.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11235, 'sorumluluk almak', 'sorumluluk yüklenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11236, 'sorumluluk düşmek', 'sorumlu sayılmak, sorumlu olarak görülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11237, 'sorun çıkarmak', 'üzüntü verecek veya içinden güç çıkılır bir durum yaratmak: ''İskemlesinde sıkıntıyla kıpırdanarak iç geçirdiğini duydum, sorun çıkarmaya başladığımı düşünüyordu.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11238, 'soruşturma açmak', 'soruşturma yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11239, 'soy asma, soyuna çeker', 'temiz soydan gelen kişi, her durumuyla soyluluğunu gösterir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11240, 'soya çekmek', 'soyunun özelliklerini taşımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11241, 'soydur çeker, boktur kokar', 'her insan veya yaratık az çok soyuna benzer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11242, 'soyunup dökünmek', 'sokak giysilerini çıkarıp ev içinde kullandığı rahat kılığını giymek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11243, 'soyup soğana çevirmek', '1) hiçbir şey bırakmamacasına soymak: ''Şimdi bu herifi soyduk soğana çevirdik, değil mi?'' -A. Midhat. 2) hırsız bir yeri veya bir kişiyi adamakıllı soymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11244, 'söküp atmak', 'gözden çıkarmak, kıymak, feda etmek: ''Kökü, ciğerimizin içini dolaşan bu filizi söküp atamıyoruz.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11245, 'sövüp saymak', 'aralıksız küfürler sıralamak, uzun uzadıya söverek yermek: ''Bir akşam ciğerci söve saya kondusundan çıktı. Başını alıp gitti.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11246, 'söylemediğini bırakmamak', 'bir kimse veya bir konu ile ilgili olarak söylenmemesi gereken şeyleri söylemek: ''Bir vakitler aralarında su sızmayan hatun kişiler şimdi birbirlerini çekemiyorlar, birbirlerinin arkasından söylemediklerini bırakmıyorlardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11247, 'söylemesi ayıp', 'utanılacak bir durumun açıklanması sırasında kullanılan bir söz: ''O zamana kadar hamallık, boyacılık, müvezzilik ve söylemesi ayıp hırsızlık yapmıştı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11248, 'söyleyenden dinleyen arif gerek', 'kapalı konuşan bir kişinin ne demek istediğini ancak o konudan haberdar olan kişi anlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11249, 'söyleyene bakma, söyletene bak', 'içinden geleni söyleyen bir kişinin sözlerine inanmak istediğimiz için bu sözleri ona Tanrı söyletiyor diye düşünürüz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11250, '(söz) abes kaçmak', 'uygun düşmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11251, 'söz açmak', 'bir konu üzerine konuşmaya başlamak, laf açmak: ''Mademki göndermişler, onlardan kısaca da olsa söz açmak boynumuzun borcu oldu.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11252, 'söz ağızdan çıkar', 'mert olan kişi, sözünde durur; verdiği sözü yerine getirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11253, 'söz almak', '1) konuşmak için toplantı başkanından izin almak, konuşmaya başlamak: Toplantıda ilk olarak başkan söz aldı. 2) birinin bir işi yapacağını kesin olarak bildirmesini sağlamak: İşimin yapılacağı konusunda bakandan söz aldım. 3) erkek tarafı oğullarıyla evlendirmek üzere kızın ailesinden olumlu cevap almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11254, 'söz altında kalmamak', '1) bir kimsenin kendisine dokunan sözüne gereken cevabı vermek; 2) kendisini inciten, itham eden veya rahatsız bir duruma düşüren söze gereken karşılığı verip durumu düzeltmek: ''Oğlunu savunmasını bilir, hiçbir sözün altında kalmazdı.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11255, 'söz anlayan beri gelsin', '''hiçbiriniz laf anlamıyorsunuz'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11256, 'söz aramızda', 'laf aramızda.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11257, 'söz atmak', '1) birine dokunacak bir sözü ortalığa söylermiş gibi söylemek, sözle takılmak, laf atmak: ''Numaralar okunuyor, görüşüyoruz, gruplardan gruplara sözler atıyoruz, şakalar ediyoruz, ne hoş eğleniyoruz.'' -R. H. Karay. 2) birine sözle sarkıntılık etmek: ''Sarhoşlar söz atıyor.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11258, 'söz ayağa düşmek', 'bir sorun, karışmaları gerekmeyen veya yetkisiz ve sorumsuz kimselerin görüş bildirdikleri duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11259, 'söz bir, Allah bir', 'verilen sözden dönülmeyeceğini anlatan bir söz: ''Söz bir, Allah bir, seni ele vermem.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11260, 'söz birliği etmek', 'ağız birliği etmek: ''Çocuklar sanki söz birliği etmişçesine ortadan yok olmuşlar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11261, 'söz çıkmak', 'ortalıkta bir söylenti dolaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11262, 'söz dediğin yaş deridir, nereye çekersen oraya gider', 'bir sözü dinleyenler kimi zaman söyleyenin aklından geçirmediği bir anlamda anlayabilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11263, 'söz dinlemek (tutmak)', 'söylenen bir sözü, verilen bir öğüdü benimsemek, davranışlarını bunlara uydurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11264, 'söz düşürmek', 'konuşmayı belli bir konuya getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11265, 'söz götürmek', '1) doğruluğu ve gerçekliği tartışılabilir olmak; 2) dedikodu yapmak; 3) tahammül etmek, katlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11266, 'söz götürmez', 'doğruluğu ve gerçekliği tartışılamayacak kadar açık olan, tersi savunulamayan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11267, 'söz gümüşse sükût altındır', 'susmak bazen konuşmaktan daha iyi sonuç verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11268, 'söz işitmek', 'laf işitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11269, 'söz kaldırmamak', 'onuruna dokunan söze dayanamayıp karşılık verir yaradılışta olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11270, 'söz kesmek', 'genellikle evlenmek için anlaşıp kesin karar vermek: ''O evlenmek üzere söz kesmiş, işi pişirmiş.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11271, 'söz konusu edilmek', 'sözü edilmek, konuşulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11272, 'söz konusu olmak', 'üzerinde konuşulmak, bahis konusu olmak, bahis mevzusu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11273, 'söz olmak', 'dedikodu yapılmak veya bir iş hoş karşılanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11274, 'söz sahibi olmak', 'bir konuda konuşma yetkisi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11275, 'söz sözü açar', 'bir konu üzerinde konuşulurken ilgisi dolayısıyla söz başka bir konuya geçer, sohbet uzar, gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11276, 'söz sözü açmak', 'bir konudan konuşurken hemen arkasından türlü konulara geçmek: ''Söz sözü açarak bizim oraları konuşmaya başlıyor ve âdeta gurbette bulunduğumuzu unutuyoruz.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11277, 'söz tutmak', 'söz dinlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11278, 'söz var dağa çıkarır, söz var dağdan indirir', 'sözün insanlar üzerinde etkisi büyüktür; yerinde söylenen sözler işlerin yoluna girmesini sağlar, ölçüsüz ve sert söylenen sözler ise karşıdakini öfkelendirir, söyleyenin öldürülmesine bile yol açabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11279, 'söz var gelir geçer, söz var deler geçer', 'sözün insanlar üzerinde etkisi büyüktür; yerinde söylenen sözler işlerin yoluna girmesini sağlar, ölçüsüz ve sert söylenen sözler ise karşıdakini öfkelendirir, söyleyenin öldürülmesine bile yol açabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11280, 'söz var iş bitirir, söz var baş yitirir', 'sözün insanlar üzerinde etkisi büyüktür; yerinde söylenen sözler işlerin yoluna girmesini sağlar, ölçüsüz ve sert söylenen sözler ise karşıdakini öfkelendirir, söyleyenin öldürülmesine bile yol açabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11281, 'söz vermek', 'bir işi yapacağını kesinlikle bildirmek: ''Vaktim yok, bana para bul, şu borcu ödeyeyim, söz verdim.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11282, 'söz yetiştirmek', '1) laf yetiştirmek: ''Kadın, kocasına söz yetiştirmeyi bıraktı, konuk kadına baktı.'' -B. Günel. 2) birinin söylediğini başkasına götürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11283, 'söz yok!', 'hakkında hiçbir şey söylenilemez: ''Bizim kibarlığımıza söz yok ama veresiye deyince dayanamam.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11284, 'sözde kalmak', 'yapılacağı bildirilmiş bir iş konuşulup gerçekleşmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11285, 'söze atılmak', 'bir konu konuşulurken birden araya girip konuşmaya başlamak: ''Neyyire Hanım hemen söze atıldı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11286, 'söze başlamak', 'konuşmaya başlamak, bir konuya girmek: ''Bu düşünce aklına gelince delikanlı hemen söze başladı.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11287, 'söze karışmak', 'başkaları konuşurken araya girip konuşmak: ''Birdenbire söze karışarak düdük gibi bir sesle işi doğruladı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11288, 'söze son vermek', 'konuşmayı bitirmek: ''Umarım ki sizi tatmin ettim diyerek sözlerine son verdi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11289, 'söze yatmak', 'söz dinlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11290, 'sözü açılmak', 'bir şey veya bir konu üzerinde konuşulmaya başlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11291, 'sözü ağzına tıkamak', 'bir kimsenin konuşmasına fırsat vermeden kendisi konuşmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11292, 'sözü ağzında bırakmak', 'sözü ağzından almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11293, 'sözü ağzında gevelemek', 'lafı ağzında gevelemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11294, 'sözü ağzında kalmak', 'konuşmasını bitirememek: ''Doktorun sözü ağzında kaldı. Sevim hanım: -Hâl neresi oluyor? diye sordu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11295, 'sözü ağzından almak', 'birinin söylemekte olduğu şeyi bitirtmemek: ''Kız, sözü anasının ağzından alarak: -Zaten biz geleli daha kaç gün oldu? dedi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11296, 'sözü bağlamak', 'konuşmayı bir sonuca vardırmak: ''Sözü şöyle mi bağlayacağız: aydın kişinin hem akıllı hem bilgili hem zeki olması zorunludur.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11297, 'sözü çevirmek', 'konuşmanın sakıncalı bir biçim aldığını anlaşıldığında başka bir konuya yönelmek, lafı veya konuyu değiştirmek: ''Yüzüm biraz değişmiş olmalı ki Hayri sözünü çevirdi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11298, 'sözü dağıtmak', 'konuşurken birçok konuya değinerek anlatmak isteği konudan uzaklaşmak: ''Konuştuğu konu üstünde, sözü dağıtmadan dikkatini, bilgisini onun kadar toplayan insan görmedim.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11299, 'sözü edilmek', '1) adı anılmak, bahsedilmek; 2) önemli sayılmak: ''Kendim askerlikte sözü edilir bir hizmet görmüş değilim.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11300, 'sözü geçmek', '1) kendisini kabul ettirmiş olmak, hatırı sayılmak: ''Sağ olsun, tanıdıklardan hatırı sayılır, sözü geçer emekli bir millî eğitim müfettişi vardı.'' -H. Taner. 2) adı anılmak, bahsedilmek: ''Zira sözü geçen memlekette gelişmiş bir proleter sınıfı mevcuttu.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11301, 'sözü kesmek', '1) konuşmasını bitirmeden susmak; 2) başkasının konuşmasını önlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11302, 'sözü mü olur?', 'üzerinde konuşacak kadar önemi yok.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11303, 'sözü sohbeti yerinde', 'güzel, oyalayıcı, kırmadan konuşan: ''Bayanın kocası olan şişman adamcağız, sözü sohbeti yerinde, efendiden bir adam.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11304, 'sözü söyle alana, kulağında kalana', 'söylediklerin bir kulağından girip öbür kulağından çıkan kimseye nefes tüketme.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11305, 'sözü tartmak', 'ölçülü konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11306, 'sözü uzatmak', 'gereğinden çok konuşmak: ''Bu hesapları yapabildiğimi göstermek için bu kadar sözü uzatıyorum.'' -A. Midhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11307, 'sözüm meclisten dışarı', 'konuşma arasında çirkin bir söz kullanmak gerektiğinde o sözden orada bulunanların alınmamasını belirtmek için söylenen bir söz: ''Gülseren, sözüm meclisten dışarı, uygunsuz bir çift yakalamış bekçi, dedi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11308, 'sözüm yabana', 'sözüm meclisten dışarı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11309, 'sözün ardı boşa çıkmak', 'söz olumlu sonuca ulaşmamak: ''Her seferki gelişimde bu katakulliyi okursun fakat sözün ardı hep boşa çıkar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11310, 'sözünde durmak', 'verdiği sözü yerine getirmek, verdiği sözden dönmemek, verdiği sözü tutmak: ''Sözümüzde durmuştuk, benzeme bahsine girmedik.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11311, 'sözünden çıkmamak', 'birinin isteklerine, öğütlerine, sözlerine uyarak davranmak: ''Halit Ağabey sen benim büyüğümsün, sözünden çıkmam.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11312, 'sözünden dönmek', 'verdiği sözü yerine getirmemek veya tutmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11313, 'sözüne sadık kalmak', 'verdiği söze bağlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11314, 'sözünü bağlamak', 'konuşmasını bitirmek için son sözlerini söylemek: ''Müdür medrese mantığı ile sözünü bağladı.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11315, 'sözünü esirgememek (sakınmamak)', 'düşündüğünü, karşısındakini kıracak bir söz olsa bile söylemekten çekinmemek: ''Dikbaşlı ve sözünü esirgemez bir insan olduğundan orada bir köşede, küçük bir kâtip kalmıştı.'' -Y. K. Beyatlı. ''Emine iskambil falı açıyor, dikiş dikiyor, çorap örüyor, kafasına uyan insanlarla konuşuyor, sözünü sakınmıyor.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11316, 'sözünü geri almak', '1) üstüne aldığı bir işten vazgeçtiğini söylemek; 2) söylemiş olduğu bir sözde haksız olduğunu kabul ederek onun söylenmemiş sayılmasını istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11317, 'sözünü kesmek', 'biri konuşurken söze karışıp onun konuşmasına fırsat vermemek: ''Birkaç söz daha söyleyip esasa geçmek istedi ise de arkada oturanlardan biri onun sözünü kesti.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11318, 'sözünü (sözünüzü) balla kestim (kesiyorum)', 'karşısındakinin konuşmasını kesip arada herhangi bir şey hatırlatmak istenildiğinde izin dilemek için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11319, 'sözünü tutmak', 'verdiği sözü yerine getirmek: ''Sözümü tuttum gibime geliyor, siz istediğiniz kadar bana meşhursun deyin.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11320, 'sözünün eri', 'verdiği sözü ne olursa olsun yerine getiren kimse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11321, 'stop etmek', 'araba, durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11322, 'stres atmak', 'bir etkinlikte bulunarak gerginlikten, sıkıntıdan kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11323, 'strese girmek', 'gerilmek, sıkıntıya girmek: ''Evine görücü gelecek kız tarafı strese girerdi.'' -Ü. Dökmen.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11324, 'strese sokmak', 'gerilime, sıkıntıya sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11325, 'su akarken testiyi doldurmalı (doldur)', 'kişi, fırsattan yararlanmalı; geliri bol olduğu zaman ilerisi için para biriktirmeye, mal mülk edinmeye bakmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11326, 'su aktığı yere (yine) akar', 'daha önce bize yararı dokunmuş olan güzel bir durum, bugün bulunmasa bile yarın yine ortaya çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11327, 'su almak', '1) suyu içine çekmek: Ayakkabılarım su alıyor. 2) den. su yapmak; 3) den. gemiye içme suyu doldurmak; 4) tıp herhangi bir organdan tedavi maksadıyla su boşaltmak; 5) mec. bozukluk, yozlaşma başlamak: ''Bu güven bir yerinden su alıyorsa o gediği zamanında kapamak gerekir.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11328, 'su basmak', 'bir şey veya yer sular altında kalmak, her yanı suyla dolmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11329, 'su başından (bendinden) kesilir (bağlanır)', 'bir işi, kimsenin karışamayacağı ve bozamayacağı biçimde bitirmek için yetkili kişilerin en büyüğü ile görüşüp anlaşmak gerektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11330, 'su bulanmayınca durulmaz', 'bir konu, türlü çekişmelerden sonra aydınlığa kavuşur, yoluna girer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11331, 'su çarpmak', 'yüzünü su ile yıkamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11332, 'su çekmek', '1) içine su almak; 2) alçak bir yerden tulumba vb. ile su çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11333, 'su dökmek', ' hlk. küçük abdest bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11334, 'su dökünmek', 'yıkanmak: ''Biraz su dökünüp hafiflik hissettikten sonra kalktılar.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11335, 'su etmek', ' den. bir geminin içine herhangi bir yerinden su girmek veya su sızmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11336, 'su gelmek', ' tıp doğumdan önce amniyon sıvısı döl yolundan akmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11337, 'su gibi', 'çok ıslak: ''Ben bir yere gidemem, arkamda gömlek su gibi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11338, 'su gibi akmak', '1) zaman hızla geçmek; 2) para, yiyecek vb. bol bol gelmek: ''Şoförlükten bir senede artırdığım para ile bu bağı almıştım. O vakit su gibi para akıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11339, 'su gibi aziz ol!', 'su getirenlere iyi dilek olarak söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11340, 'su gibi bilmek (okumak)', 'yanlışsız bilmek veya okumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11341, 'su gibi ezberlemek', 'yanlışsız okuyabilecek kadar ezberlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11342, 'su gibi olmak', 'çok ıslanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11343, 'su gibi terlemek', 'çok terlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11344, 'su görmemiş', 'çok kirli (yüz, el).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11345, 'su götürür yeri olmamak', 'başka türlü yorumlanacak bir yönü bulunmamak: Yapılanların su götürür yeri kalmadı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11346, 'su içene yılan bile dokunmaz', 'düşman olsa bile su içen kimseye dokunulmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11347, 'su içinde', 'en kötü şartlarda bile: Bu masa su içinde on bin lira eder.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11348, 'su içinde kalmak', 'çok terlemek, su gibi ıslanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11349, 'su iktiza etmek', 'gusül gerekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11350, 'su kaçırmak', '1) su sızdırmak; 2) argo baş ağrıtmak, can sıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11351, 'su kapmak', 'yaralar azmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11352, 'su katılmamış', 'kendine özgü olan durumu koruyan, başka bir etkiyle değişmemiş, bozulmamış olan: ''O bizim su katılmamış biricik münekkidimizdir.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11353, 'su kesmek', 'sulanmak: Bu yoğurt su kesmiş. Bu karpuz dura dura su kesmiş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11354, 'su koyuvermek', '1) sebze ve et pişerken suyunu salıvermek; 2) argo sözünde durmamak, cıvıtmak: ''Melahat büsbütün su koyuvermiş, yerlere yatarak gülüyor.'' -H. Taner. 3) vazgeçmek; 4) beklenen görevi yapmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11355, 'su küçüğün, söz (sofra, yemek) büyüğün', '1) temel bir ihtiyaç olan su önce küçüğe verilir, konuşmaya ise ilk önce büyükler başlar; 2) her şeyin bir sırası vardır; kişiler bu sıralamada yaşlarına, kültürlerine vb. özelliklerine göre yer alırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11356, 'su testisi su yolunda kırılır', 'bir kişi amaç edindiği işte kazaya uğrar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11357, 'su uyur, düşman uyumaz', 'düşmana karşı her zaman uyanık davranmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11358, 'su vermek', '1) bitkileri sulamak; 2) hayvanlara su içirmek; 3) insanlara içmek için su getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11359, 'su yapmak', ' den. gemi veya sandalın içine dibinden su girmek: ''Bir adam için alın damarı çatlamış, dediler mi su yapan tekneden beterdir.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11360, 'su yatağını bulur', 'bir şeyi yapma isteği ve gücü bulunan kimse, uygun bir çalışma yönü ve alanı bulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11361, 'su yürümek', 'ilkbahara doğru ağaçlar tomurcuklanmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11362, 'su yüzü görmemiş', 'su görmemiş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11363, 'su yüzüne çıkmak', 'bir süre örtülü kalmış bir iş veya sorun aydınlanmak, belli olmak: ''Tiyatroda sorunlar su yüzüne çıkmış, bunların neler olduğu anlaşılmıştır.'' -M. And.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11364, 'su yüzüne (üstüne) çıkmak', 'görünür olmak: ''Bilinçaltı bir baskı belki de ilk kez su üstüne çıkıyordu.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11365, 'sual açmak', 'üst bir mevki, sorumlu sayılan birine soru sormak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11366, 'sucuğunu çıkarmak', '1) yormak; 2) çok dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11367, 'sucuk gibi olmak (ıslanmak)', 'baştan aşağı ıslanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11368, 'suç samur kürk olsa kimse üstüne almaz', 'hiç kimse suçlu olduğunu kabul etmek istemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11369, 'suçu gelin etmişler, kimse güveyi girmemiş', 'ne kadar çekici olursa olsun, suçu kimse kabul etmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11370, 'sudan çıkmış balığa dönmek', 'herhangi bir sebeple ne yapacağını bilememek, çok şaşırmak: ''Yaşama adım attılar mı sudan çıkmış balığa dönerler. Ya yetenekleri değerlendirilmezse bu yeni çevrede? Ya saygı görmezlerse?'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11371, 'sudan geçirmek', '1) herhangi bir şeyi üstünkörü yıkamak; 2) sabunlu çamaşırı durulamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11372, 'sufle etmek', '1) tiy. oyunculara, izleyicilere duyurmadan söyleyecekleri sözü veya cümleyi fısıldamak; 2) birine unuttuğu bir sözü veya cümleyi kimseye duyurmadan hatırlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11373, 'suikastta parmağı olmak', 'düzenlenen suikast olayında rol oynamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11374, 'sular kararmak', 'akşam olmaya başlamak: ''Son vapur iskeleye sular kararırken yanaşırdı.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11375, 'sular seller gibi', 'bir metni yanlışsız söyleyecek kadar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11376, 'suna gibi', 'suna boylu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11377, 'surat asmak', 'kaşlarını çatıp yüzüne küskün veya dargın bir anlam vermek, somurtmak: ''Babam biraz surat astı ama anam katıldı gülmekten.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11378, 'surat etmek', 'birine karşı küskün durmak, asık yüzlü olmak: ''Şimdi ters yüzü eve dönsek çocuklar ağlar, bayan surat eder.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11379, 'surat kalmamak', 'utanmaz duruma gelmek: ''İkimizde de birbirimize bakacak surat kalmamıştı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11380, 'surat mahkeme duvarı', '1) asık suratlı, kimseye gülmeyen, suskun duran; 2) utanmaz, sıkılmaz: ''Onda surat mahkeme duvarı, tükürsem yağmur yağıyor sanacak.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11381, 'surat (suratı) bir karış', 'öfkeli, kızgın ve somurtkan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11382, 'surata bak süngüye davran', 'çok asık suratlı kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11383, 'suratı bir karış asılmak', 'öfkelenmek, kızmak ve somurtmak: ''Hemen suratları bir karış asılır, ona bir sövüp saymadıkları kalır.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11384, 'suratı değişmek', 'bir kimseye karşı davranışı değişmek, daha sert bir durum almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11385, 'suratı kasap süngeriyle silinmiş', '''utanması, sıkılması kalmamış'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11386, 'suratı sirke satmak', 'öfkeli, kızgın olduğu anlaşılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11387, 'suratına indirmek', 'tokat atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11388, 'suratından düşen bin parça olmak', 'öfke veya küskünlükten ileri gelen can sıkıntısıyla suratı asık olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11389, 'suratını dağıtmak', 'yüzüne zarar verecek biçimde dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11390, 'suratını ekşitmek (buruşturmak)', 'yüzüne memnun olmadığını belirten bir anlam vermek: ''İşte ilmin, âlimin kıymeti bilinmeye başladı diye suratımı ekşittim.'' -Ö. Seyfettin. ''Ben suratımı buruşturdukça, bir yaz öğlesinde yarı açık kalmış bir musluktan akarak ak mermer bir yalakta şarkı söyleyen serin bir su gibi gülsün o!'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11391, 'suret almak (çıkarmak)', 'bir belgenin kopyasını çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11392, 'sureti haktan görünmek', '1) kendisini iyi niyetli imiş gibi göstermek: ''İstanbul''a sureti haktan görünen öyle belediye başkanları geldi ki Anadolu''dan gelen hemşehrilerinin gecekondularına göz yumdu.'' -A. Boysan. 2) birinin iyiliği için çalışıyor görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11393, 'suretine girmek', 'bir şeyin görünüşüne, biçimine benzemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11394, 'suspus etmek', 'susturmak: ''Kamburunu gittikçe daha çıkararak tartışmacıları suspus eder.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11395, 'suspus olmak', 'susmak, sinmek, sesini hiç çıkarmamak: ''Bir an üçü de suspus oldular, hiç kimse konuşmadı.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11396, 'susta durdurmak', '1) köpeği arka ayakları üzerinde durdurmak; 2) mec. bir kimseyi veya birilerini yıldırmak: ''Sade kazada değil, vilayette bile en belli başlı memurları ve eşrafı susta durdurur.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11397, 'susta durmak', '1) köpek arka ayakları üzerinde durmak; 2) mec. hazır durumda beklemek: ''Benim susta durmam, ellerimi kaldırıvermem daha kolay.'' -N. Hikmet. 3) mec. korktuğu bir kimsenin karşısında saygılı ve çekingen davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11398, 'sustaya kalkmak', 'köpek susta durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11399, 'suya göstermek', 'hafifçe yıkamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11400, 'suya götürüp susuz getirmek', 'herhangi bir işte akıl, zekâ, deneyim ve kurnazlıkla bir diğerini alt etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11401, 'suya sabuna dokunmamak', '1) sakıncalı konularla ilgilenmemek: ''İyisi mi bir yazar, hep suya sabuna dokunmayan yazılar yazmalı.'' -O. V. Kanık. 2) davranışlarını kimseyi incitmeyecek biçimde ayarlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11402, 'suya salmak', 'boşuna harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11403, 'suyu baştan (başından) kesmek', 'işin aslı üzerinde kesin bir şey söyleyip ayrıntılarını konuşmaya gerek duymamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11404, 'suyu çıkmak', 'çok söz edildiği veya üzerinde yerli yersiz durulduğu için değerini yitirmek, önemsizleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11405, 'suyu getiren de bir, testiyi kıran da', 'görevini iyi yapanla kötüye kullanan arasında bir fark gözetilmemektedir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11406, 'suyu görmeden paçaları sıvamak', 'henüz hiçbir belirti yokken veya gereğinden çok önceden hazırlanmaya kalkışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11407, 'suyu görünce teyemmüm bozulur', 'bir zorunluluk dolayısıyla yapılmakta olan bir işin, bu zorunluluk ortadan kalktığında gereği gibi yapılmak için yeni baştan ele alınması gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11408, 'suyu ısınmak (kaynamak)', ' tkz. işbaşından uzaklaştırılması yaklaşmak veya gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11409, 'suyu kesilmiş değirmene dönmek', 'işlemez, yararsız duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11410, 'suyu seli kalmamak', 'sulu yemek kaynaya kaynaya suyu azalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11411, 'suyun akıntısına gitmek', 'olayların veya durumun gelişmesine göre davranmak, uymak: ''Bunlarda sezilen intibakçı hatta biraz suyun akıntısına giden ruh, Ayşe''nin mizacına pek uymuştu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11412, 'suyun başı', '1) suyun çıktığı yer, kaynak: ''Suyun başına çöküp ellerini, yüzünü yıkamaya koyuldu.'' -Y. K. Karaosmanoğlu. 2) bir işin asıl yetkililerinin bulunduğu yer; 3) mec. en çok yarar sağlanacak yer.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11413, 'suyun duru akanından, insanın yere bakanından korkmalı', 'duygu ve düşüncelerini açığa vurmayan sessiz insan yavaş akan derin su gibi tehlikelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11414, 'suyun sessizinden, insanın sözsüzünden korkmalı', 'duygu ve düşüncelerini açığa vurmayan sessiz insan yavaş akan derin su gibi tehlikelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11415, 'suyun yavaş akanından, insanın yere bakanından kork (sakın)', 'duygu ve düşüncelerini açığa vurmayan sessiz insan yavaş akan derin su gibi tehlikelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11416, 'suyuna gitmek', 'suyunca gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11417, 'suyuna tirit', 'baştan savma, değersiz, özensiz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11418, 'suyunca gitmek', 'bir kimseyi sinirlendirmeyecek biçimde davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11419, 'suyunu almak', 'kaynatılan yiyeceğin suyunu ayırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11420, 'suyunu çekmek', '1) yemek kaynayıp suyu kalmamak; 2) tkz. tükenmek: ''Paralar suyunu çekti. Fabrika da olduğu gibi Nihat''a geçti.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11421, 'suyunun suyu', 'tavşanın suyunun suyu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11422, 'sübut bulmak', 'tanıtlanmak, ispat edilmek: Suç sübut buldu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11423, 'sükse yapmak', '1) başarı kazanmak: ''Paris sosyetesinde büyük sükse yapmıştı.'' -A. Gündüz. 2) ilgi çekecek bir durum yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11424, 'sükûnet bulmak', 'sakinleşmek, rahatlamak: ''Azıcık sükûnet bulduktan sonra odayı terk etmediğime sevindim.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11425, 'sükût ikrardan gelir', 'susmak kabul etmek demektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11426, 'sükûtla geçiştirmek', 'sözü edilmesi gereken bir noktayı söylemeden atlamak, bile bile bir konuya değinmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11427, 'süluk etmek', '1) bir işe girmek; 2) bir tarikata girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11428, 'sülük gibi', 'çok sırnaşık, yapışkan (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11429, 'sülük vurmak', 'tedavi amacıyla sülük yapıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11430, 'sülün gibi', 'boylu boslu ve yürüyüşü güzel (kız veya kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11431, 'sümen altı etmek', '1) bir evrakın işleme konulmasını engellemek; 2) bir işin yapılmasını geciktirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11432, 'sünger gibi', 'çok yumuşak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11433, 'sünnet etmek', 'erkek çocukta erkeklik organının ucundaki deriyi çepeçevre kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11434, 'sünnet olmak', 'sünnet edilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11435, 'sür git dememişler, gör geç demişler', 'beğenmediğiniz durumu, sürüp giden bir anlaşmazlık konusu yapmayınız, hoş görüp kabul ediniz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11436, 'sürgit yapmak', 'iş için uzatmak, sürdürüp durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11437, 'sürgün gitmek (olmak)', '1) sürgüne gönderilmek, sürgün cezasına uğramak; 2) olağandan daha çok, daha sık ve sulu dışkı çıkarmak, ishal olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11438, 'sürgün vermek', 'filizlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11439, 'sürgüne göndermek', 'ceza olarak bir yere sürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11440, 'sürme çekmek', 'gözleri sürme ile boyamak: ''Kirpiğine sürme çek, kına yak parmağına.'' -R. C. Emek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11441, 'sürmeyi gözden çekmek', 'gözden sürmeyi çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11442, 'sürpriz yapmak', 'birini, beklenmedik, şaşırtan, sevindiren veya üzen bir olayla karşılaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11443, 'sürtünüp durmak', 'çıkarı, kazancı için yaltaklanıp durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11444, 'sürtüp durmak', ' tkz. yersiz, sebepsiz olarak durmadan dolaşmak: ''Galiba bu tarihî günün yüzü suyu hürmetine, Beyoğlu''nda sürtüp durdukları yanlarına kâr kaldı idi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11445, 'sürüden ayrılanı (ayrılan koyunu, kuzuyu) kurt kapar', 'arkadaşların yardımıyla yapılan veya bir amaç uğruna topluca girişilen bir işten ayrılanlar zarara uğrarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11446, 'sürüden ayrılmak', 'herkesin tuttuğu yolu bırakıp ayrı bir yol tutturmak, herkesin yaptığını yapmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11447, 'sürüm sürüm sürünmek', 'yoksul ve perişan bir biçimde yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11448, 'sürüp gelmek', 'eskiden beri devam etmek: ''Muhtarla hiç aram yoktu, babamın sağlığından beri sürüp gelen bir inatlaşma vardı.'' -M. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11449, 'sürüp gitmek', 'eskiden olduğu gibi, eskiden nasılsa gene öyle olmak, öyle devam etmek: ''Laf atmalar, ıslık çalmalar, kavgaya tutuşmalar gün boyu sürüp gitti.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11450, 'sürüsüne bereket!', 'pek çok, pek bol: Onda hısım akraba sürüsüne bereket!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11451, 'sürüyü güden kurdu görür', 'zor bir işe giren onun bütün sıkıntılarıyla karşılaşabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11452, 'süs için', '''yararlı olmak amacıyla değil, gerektiği için değil'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11453, 'süsleyip püslemek', 'özenle, özen göstererek süslemek, göze çarpacak kadar süslemek, telleyip pullamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11454, 'süt çekmek', 'bir özelliği akrabalarına benzemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11455, 'süt dökmüş kedi gibi', 'suçunu bilerek bundan utanarak: ''Hele süt dökmüş kedi gibi susmaktan ne çıkar?'' -N. Uygur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11456, 'süt dökmüş kediye dönmek', 'suçunu bilerek bundan utanmak: ''İş söze döküldü mü nedense tutuklaşıyor, süt dökmüş kediye dönüyordu.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11457, 'süt gibi', 'çok beyaz, çok temiz: ''Hacı yenge süt gibi saçları, buruşuk yüzüyle asıl şimdi eli öpülecek bir hacı yenge olmuştu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11458, 'süt ineği gibi sağmak', 'birinden kendi çıkarı için daima aşırı ölçülerde yarar sağlamak için uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11459, 'süt vermek', 'emzirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11460, 'sütçü beygiri gibi', 'çok tembel ve miskin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11461, 'sütçü beygiri gibi ayakta uyumak', 'çok tembel ve miskin olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11462, 'sütle giren huy, canla çıkar', 'kişinin küçükken edindiği huy, ölünceye değin sürer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11463, 'sütlüyü sürüden çıkarmazlar', 'yararlı, verimli şey elden çıkarılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11464, 'sütsüz koyun meleyen olur', 'çevresine yararlı olmayan, elinde avucunda bir şey bulunmayan kişi hep acıklı ve üzüntülü konuşur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11465, 'sütten ağzı yanan yoğurdu (ayranı) üfleyerek yer (içer)', 'bir olaydan gerekli dersi alan, sonra uyanık davranır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11466, 'sütten ağzı yanmak', 'bir olaydan gerekli dersi alarak uyanık davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11467, 'sütten çıkmış ak kaşık gibi olmak', 'temiz, saf olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11468, 'sütten kesilmek', ' tıp hastalık, üzüntü veya bebeğin emmesi nedeniyle anneye süt gelmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11469, 'sütten kesmek', 'emzirmeye son vermek: ''Fadime''yi aldım götürdüm, kaynanamın odasına bıraktım, sütten kesmiştim.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11470, 'sütun gibi', 'düzgün biçimli (bacak).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11471, 'sütununu açmak', 'yer vermek, yayımlamak: ''Sanat dergilerinden biri bir ara, genç şairlere sütunlarını açmıştı.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11472, 'sütüne kalmak', 'insanlığına, namusuna kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11473, 'süzgeçten geçirmek', 'ayrıntılı bir biçimde incelemek: ''Genç şair ile hanım ilk anlarda birbirlerini tepeden ayağa süzgeçten geçirdiler.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11474, 'süzüm süzüm süzülmek', 'kendini beğenmiş bir tavırla ağırbaşlı oturup çevreye bakmak: ''Gelin tarafı da görümceler de yerlerinden kımıldamadılar, süzüm süzüm süzüldüler.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11475, 'şafak sökmek', 'sabahleyin ortalık aydınlanmaya başlamak: ''Şafak sökerken evden çıkıyor, akşam karanlığında dönüyordu.'' -R. Enis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11476, 'Şafii köpeği gibi titremek', 'çok titremek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11477, 'Şafii köpeğine dönmek', 'yüzü gözü çok kirli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11478, 'şaha kalkmak', '1) at ön ayaklarını yerden kesip arka ayakları üstünde durmak, şahlanmak: ''Aydınlıktan huylanan atlar şaha kalkarak deli gibi dörtnala ileri atılıyorlardı.'' -Ö. Seyfettin. 2) mec. taşkınlık göstermek, coşmak, kükremek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11479, 'şaheser yaratmak', 'üstün, kalıcı niteliği olan bir eser ortaya koymak, çok önemli bir şey yapmak: ''Şu millî savaş içinde köy kadını başlı başına bir tarih, bir şaheser yaratıyor.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11480, 'şahin ile deve avlanmaz', 'küçük şeyleri elde etmek için yeterli olan araçla, büyük şeyler elde edilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11481, 'şahin küçük ama payını ele vermez', 'küçük olmak güçsüz olmak demek değildir, kendilerinden büyük olandan daha güçlü olan küçükler de vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11482, 'şahin küçüktür ama koca turnayı havadan indirir', 'küçük olmak güçsüz olmak demek değildir, kendilerinden büyük olandan daha güçlü olan küçükler de vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11483, 'şahit tutmak', 'birini tanık olarak göstermek: ''Eniştemiz bizi şahit tuttukça babam da istihzalı bir tavır alır, kıs kıs gülerdi.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11484, 'şahken şahbaz olmak', ' alay bir kimsenin herhangi bir sebeple çirkinliği veya durumunun kötülüğü artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11485, 'şahsiyat yapmak', 'söz edilen konudan uzaklaşarak olumsuz yönleriyle kişiler üzerinde durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11486, 'şahsiyata dökmek', 'şahsiyat yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11487, 'şaibe altında kalmak (tutulmak)', 'kusurlu, ayıplı, lekeli sayılmak: ''Yakın zamanlarda bizim parlamentomuz da bu gibi şaibeler altında tutuldu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11488, 'şak diye', 'ansızın: Şak diye yüzüne vurdu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11489, 'şaka etmek', 'bir kimseye eğlenmek amacıyla takılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11490, 'şaka gibi gelmek', 'bir türlü inanamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11491, 'şaka götürmemek', '1) bir durum veya iş hafifsemeye, dikkatsizliğe gelmemek: ''Rica ederim gülmeyiniz, iş pek naziktir, şaka götürmez.'' -H. R. Gürpınar. 2) şakadan hoşlanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11492, 'şaka kaldırmak', 'şakaya dayanmak, katlanmak: ''Bizim oralılar şakacıdırlar, şaka kaldırırlar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11493, 'şaka maka derken', '''işi ciddiye almadık ama'' anlamında kullanılan bir söz: ''Geçen gün, şaka maka derken az daha kavga ediyorduk bu yüzden.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11494, 'şaka yapmak', 'şaka niteliğinde bir şey yapmak veya söylemek: ''İlk defa görüştüğümüz hâlde benimle şaka yaptı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11495, 'şakakları ağarmak (beyazlanmak)', 'yaşlanmak: ''Ben o eski adam değilim, şakaklarım nasıl beyazlanmış, görmüyor musun?'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11496, 'şakakları atmak', 'çok sinirlenmek: ''Kalbinin yırtıldığını, kilitlenen çenelerinin çatırdadığını, şakaklarının attığını duyardı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11497, 'şakası yok', '''hatır gönül tanımaz, gerekeni yapar'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11498, 'şakaya almak', 'söylenilen gerçek sözü şaka gibi kabul etmek: ''Mustafa Kemal Paşa, bu isteği ilk önce şakaya alarak şöyle cevaplandırmıştı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11499, 'şakaya gelmek', 'şakaya katlanır olmak: ''Öyle zannedildiği gibi şakaya gelecek bir adam olmadığını göstermek için bu, ne güzel bir fırsattı!'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11500, 'şakaya gelmemek', '1) şakaya dayanamamak; 2) hafifsemeye, savsaklamaya gelmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11501, 'şakaya getirmek', 'ciddi bir şeyi açıktan açığa söyleyemeyip şaka görünümü vererek söylemek: ''Şakaya getirip söyledim, latife ediyordum.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11502, 'şakaya sığınmak', 'şakaya vurmak: ''Kaç kez şakaya sığınıp benzer yanıtlar vermişti.'' -E. Atasü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11503, 'şakaya vurmak', 'ciddi bir söz veya davranışı şaka yoluyla geçiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11504, 'şakayı kakaya çevirmek', ' tkz. şakayken kaka olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11505, 'şakayken kaka olmak', ' tkz. el ve dil ile yapılan şakadan, hoş olmayan bir sonuç veya kavga çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11506, 'şalvar gibi', 'çok bol (pantolon).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11507, 'şamama gibi', 'ufak tefek, sevimli (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11508, 'şamar atmak (indirmek)', 'şamarlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11509, 'şamar patlatmak', 'aniden güçlü bir tokat atmak: ''Sağ avucumun bir şamar patlatmak için nasıl kaşındığını hâlâ unutmuyorum.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11510, 'şamaroğlanına dönmek', 'yerli yersiz suçlanıp azarlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11511, 'şamata etmek (koparmak)', 'gürültü patırtı yapmak: ''Amma da şamata ettin be çorbacı, dedi.'' -H. Taner. ''Haykırarak, şamata kopararak, yarı havada, yarı yerde koşup kendilerini çeşmenin yalaklarına atarlardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11512, 'şan vermek', 'ün salmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11513, 'şanına yakışmak', 'şanından olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11514, 'şanına yedirememek', 'yenilgiyi kabul edememek: ''Onların karşısında ilk elde çekilmeyi şanına yediremedi.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11515, 'şanından olmak', 'bir şey onun büyüklüğüne, karakterine uygun olmak, yaraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11516, 'şans tanımak', 'imkân vermek, fırsat vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11517, 'şansa bırakmak', 'oluruna bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11518, 'şansa kalmak', 'bir şeyin olabilmesi için çok az umut olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11519, 'şansı dönmek', 'talihi iyiyken kötü veya kötüyken iyi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11520, 'şansı yaver gitmek', 'talihli olmak, bahtı açık olmak: ''Şansı yaver gittiği takdirde orta boylu, uzun saçlı esmerine kavuşabilecekti.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11521, 'şantaj yapmak', 'gözdağı vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11522, 'şap gibi', 'ağza alınmayacak kadar tuzlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11523, 'şap gibi donmak (kalmak)', 'şaşırarak ses çıkarmayacak duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11524, 'şap gibi yanmak', 'ortada kalmak, destek bulamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11525, 'şap ile şeker bir değil', 'görünüşte birbirine benzeyen şeyler nitelikte birbirinden çok farklı olabilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11526, 'şapa oturmak', 'içinden çıkılması güç bir duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11527, 'şapka çıkarmak', 'bir söz veya durum karşısında söyleyecek sözü kalmamak ve takdir etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11528, 'şaplak indirmek', '1) elin içiyle vurmak: ''Zeynep, bir şaplak indirdi önündeki kil hamuruna ve güldü.'' -E. Işınsu. 2) tokat atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11529, 'şaraptan bozma sirke keskin olur', 'sonradan azan kişi, eskiden beri yolunu şaşırmış kimseden daha azgın olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11530, 'şarj etmek', '1) yüklemek; 2) argo bir şeyi anlamaya, kavramaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11531, 'şarkı tutturmak', 'bir şarkının sözlerini veya sadece bestesini seslendirmek: ''Eğlenmek için derin bir heves doğdu, ıslıkla bir şarkı tutturdu.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11532, 'şart etmek', '''şart olsun'' diyerek yemin etmek: ''Anası, oğlan gelirse içeri almayacağına şart etmişti.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11533, 'şart koşmak', 'önceden bir şarta bağlamak: ''Sarfiyat hususunda bir şart koşmuyorlar.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11534, 'şart olmak', 'gerekmek, kaçınılmaz bir durum almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11535, 'şart olsun', '1) ''nikâhım üzerine yemin ederim ki, öyle değilse veya bunu yapmazsa karım boş düşsün (olsun)'' anlamında yemin olarak kullanılan bir söz: ''Artık hep, evli adamlar gibi biz de şart olsun yeminine başladık.'' -Ö. Seyfettin. 2) yemin etmek için kullanılan biz söz: ''Kim hapşırırsa şart olsun ki öldürünceye kadar döveceğim.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11536, 'şart şurt tanımamak', 'kendini hiçbir şarta bağlı saymamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11537, 'şaşı, çakır demektense kör de, kurtul', 'üstü kapalı laflar etme, açık konuş, ne diyeceksen de.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11538, 'şaşırıp kalmak', 'çok şaşırmak, büyük bir şaşkınlığa düşmek: ''Beni ilk defa dersler dışında konuşup gülerken görüyor, şaşırıp kalıyorlardı.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11539, 'şaşkın misafir, ev sahibini ağırlar', 'başkalarının görev ve yetkilerine karışmak ahmaklıktır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11540, 'şaşkın ördek başını bırakır, kıçından dalar', 'ne yaptığını bilmeyen kişi işi tersinden yürütmeye kalkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11541, 'şaşkına dönmek', 'beklenmedik bir durum karşısında şaşkınlaşmak: ''Bunlar da Mustafa Kemal''i ifratlı hareketlere, yanlış yollara sevk etmek töhmeti altında bunalmış, şaşkına dönmüş idiler.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11542, 'şato gibi', 'büyük, görkemli (yapı).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11543, 'şavkı vurmak', 'bir şeyin ışığı yansımak: ''Bir ay doğdu ilk akşamdan, geceden / Şavkı vurdu pencereden, peçeden'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11544, 'şeamet tellallığı yapmak', 'her olayı kötü ve sıkıntı yaratacak biçimde yorumlayıp dile getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11545, 'şehadet getirmek', 'İslam''ın şartlarından ''Tanrı''dan başka tapacak yoktur ve Hz. Muhammed onun kulu ve peygamberidir'' anlamına gelen  kelimeişehadet  adını taşıyan Arapça sözü söylemek', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11546, 'şehadet şerbetini içmek', 'şehit düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11547, 'şehadette bulunmak', 'tanıklık etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11548, 'şehit düşmek (olmak)', 'kutsal bir ülkü veya inanç uğrunda ölmek: ''Biraz sonra Veysel''in arkadaşlarından biri daha şehit oldu.'' -M. Ş. Esendal. ''Arkadaşı, düşmanlarla cenge varır ve şehit düşer.'' -R. Enis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11549, 'şehit edilmek', 'kutsal bir ülkü veya inanç uğrunda öldürülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11550, 'şeker gibi', '1) çok sevimli, güzel; 2) yumuşak huylu, yumuşak davranan: ''Doktor şeker gibi adam, ahbabım.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11551, 'şekeri kestirmek', 'şeker şerbetine, limon suyu veya limon tuzu katarak kaynatıp koyulaşmasını sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11552, 'şekerleme yapmak', 'kısa bir süre uyumak, kestirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11553, 'şekil almak', 'belli bir biçime girmek, biçimlenmek, şekillenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11554, 'şekil vermek', 'belirli bir biçime girmesini sağlamak, biçimlendirmek, şekillendirmek: ''O, biraz da bana yardım olsun diye, mektepteki Amerika''dan gelen gazeteleri toplar, getirir, bu işe şekil verirdi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11555, 'şekilci olmak', 'belli biçimler, kalıplar dışına çıkamamak: ''Şiir diline dayanan bir edebiyat, konuşma dilinden ayrıldığı ölçüde hayattan kopmak, şekilci olmak zorundadır.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11556, 'şenlik görmemiş', 'terbiyesiz, görgüsüz (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11557, 'şerbet gibi', 'yumuşak, güzel (hava).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11558, 'şerbet içmek', 'sözlenmek veya nişanlanmak üzere tarafların anlaşması durumunda ezilen şerbet içilerek tören yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11559, 'şeref vermek', 'onurlandırmak, şereflendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11560, 'şerh düşmek (koymak)', 'alınan karar veya kararlara karşı olumsuz yönde yazılı görüş bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11561, 'şeriatın kestiği parmak acımaz', 'kanunların uygun gördüğü cezaya katlanmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11562, 'şerrine lanet', 'kötü bir kimse ile uğraşmak istenilmediğini veya kaçınıldığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11563, 'şeşi beş görmek', ' alay yanlış görmek: ''Asıl âşığın gözü şeşi beş görür, kulağı Mısır''daki sağır sultanın duyduğunu bile duymaz.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11564, 'şevk vermek', 'isteklendirmek: ''Bir bitmeyecek şevk verirken beste / Bir tel kopar, ahenk ebediyen kesilir'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11565, 'şevke gelmek', '1) isteği, hevesi artmak: ''Öyle keyifleniyor, öyle şevke geliyordu ki...'' -Y. Z. Ortaç. 2) neşelenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11566, 'şevke getirmek', 'canlandırmak, isteğini artırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11567, 'şevki kırılmak', 'isteği, hevesi kalmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11568, 'şeyh uçmaz, müridi uçurur', 'bir kişiye inanlar, onu olduğundan çok üstün görürler ve onda olağanüstü özellikler bulunduğuna herkesi inandırmak isterler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11569, 'şeyhin kerameti kendinden menkul', 'büyük işler gördüğünü söyleyen birinin sözüne inanılmadığını anlatmak için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11570, 'şeytan aldatmak', '1) bazı davranışlarda iradeli, güçlü davranamamak, nefsine uymak; 2) uyku hâlindeyken meni boşalmak, düş azmak: ''O gecenin sabahı şeytanın aldattığı vücudunu soğuk suda temizlerdi.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11571, 'şeytan azapta gerek', '''sevilmeyen bir kimse zorluk içinde kaldığında bunu hak etmiştir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11572, 'şeytan diyor ki', 'yapılmaması gereken bir davranışı yapma isteği duyulduğunda söylenen bir söz: ''Gül tenli, kor dudaklı, kömür sürmeli / Şeytan diyor ki sarmalı, yüz kere öpmeli'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11573, 'şeytan dürtmek', 'durup dururken uygunsuz, kötü bir davranışta bulunmak: ''Ama çocukluk işte, şeytan dürttü, ya herrü ya merrü diyerek birden yukarı baktım.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11574, 'şeytan geçmiş gibi', 'birkaç kişinin konuştuğu sırada kısa bir süre sessizlik olması durumunda kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11575, 'şeytan geçti', 'şeytan geçmiş gibi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11576, 'şeytan gibi', 'çok zeki ve kurnaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11577, 'şeytan görsün yüzünü', 'sevilmeyen, görmek bile istenilmeyen kimse için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11578, 'şeytan kandırmak', 'düş azmak, şeytan aldatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11579, 'şeytan kulağına kurşun', ' hlk. aksama ihtimali bulunan durum veya işler düzenli gittiğinde ''nazar değmesin'' anlamında söylenen bir söz: Şeytan kulağına kurşun, hiçbirimiz hasta olmadık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11580, 'şeytana külahı (pabucu) ters giydirmek', 'çok kurnaz olmak: ''Fakat aynı zamanda, şeytana külahı ters giydirecek kadar açıkgöz ve kurnazdı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11581, 'şeytana parmak ısırtmak', 'çok kötü ve çirkin bir şey yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11582, 'şeytana uymak', 'kötü bir şey yapma isteğine kapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11583, 'şeytanın arka bacağı (kıç bacağı veya art ayağı)', 'çok akıllı ve yaramaz (çocuk).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11584, 'şeytanın bacağını (ayağını) kırmak', '1) herhangi bir sebeple yapılmayan bir işe başlamak veya gidilmeyen bir yere gitmek; 2) uğursuzluğu, şanssızlığı, aksiliği yenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11585, 'şeytanın dostluğu darağacına kadardır', 'kötü arkadaş kişiyi yoldan çıkartıp ölüme kadar sürükleyebilir ama ölümün eşiğinde onu kaderiyle baş başa bırakır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11586, 'şeytanın gör dediği', 'başkalarının göremediği, farkına varamadığı incelikler veya gerçekler.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11587, 'şeytanın işi yok', '''ne hikmetse, aksilik bu ya'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11588, 'şeytanın yattığı yeri bilmek', 'bilinmesi ve hatırlanması güç şeyleri bilmek, çok kurnaz ve açıkgöz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11589, 'şeytanla kabak ekenin kabak başına patlar', 'kurnaz ve hileci kimse ile ortaklık eden kişi hilenin en büyük kurbanı olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11590, 'şeytanla ortak buğday eken samanını alır', 'ortağı kurnaz, düzenbaz olanın eline kârdan bir şey geçmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11591, 'şık şık (çık çık) eden nalçadır, iş bitiren akçedir', 'değerli nesneye bir yönüyle benzeyen şey, onun yerini asla tutmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11592, 'şıkır şıkır oynamak', '1) canlı bir biçimde oynamak; 2) mec. çok sevinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11593, 'şıp diye', '1) ''şıp'' sesi çıkararak; 2) ansızın; 3) hemen: ''Öyle bir suratla karşılayacak ki seninki hiç istenmediğini şıp diye anlayıp defolacak.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11594, 'şırınga etmek', 'gaz veya sıvı bir maddeyi gözenekli başka bir maddenin içine şırınga ile doldurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11595, 'şırınga yapmak', 'şırınga ile vücuda gerekli yerinden ilaç vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11596, 'şiar edinmek', 'benimsemek, ilke olarak kabul etmek: ''Zira ki biz, orijinal mevzulara teması şiar edinmişiz.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11597, 'şiddet göstermek', 'kaba, sert davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11598, 'şiddete başvurmak', 'kaba kuvvet kullanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11599, 'şifa bulmak', 'iyi olmak, onmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11600, 'şifa niyetine', 'bir kimseye ilaç verilirken ''iyi olması, fayda sağlaması dileğiyle'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11601, 'şifa vermek', 'iyi etmek, sağlığına kavuşturmak: ''Hastalara türlü maceralarla şifa vermesini ben bilirim.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11602, 'şifayı bulmak (kapmak)', ' tkz. hastalanmak veya hastalığı artmak: ''Aksırık öksürük derken kızcağız şifayı kapmış.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11603, 'şifreyi çözmek', '1) bir şifrede kullanılan işaretlerin anlamını bulmak; 2) mec. gizli bir olayı anlayıp açıklığa kavuşturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11604, 'şiir düzmek', 'şiir yazmak veya söylemek: ''Onun bir parçası olan insan da tazelenir bu mevsimde, ozanların şiir düzmeleri bu yüzdendir işte.'' -M. C. Anday.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11605, 'şiir gibi', 'çok güzel, çok hoş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11606, 'şikâyet getirmek', 'sızlanmak, yakınmak: ''Hüsmen de yorgunluğundan şikâyet getirmiyor, hak uğruna çalışmak ona yol mihnetlerini unutturuyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11607, 'şikâyette bulunmak', 'yakınmak, şikâyet etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11608, 'şike yapmak', '1) danışık spor karşılaşması yapmak; 2) mec. bir çıkar karşılığı anlaşarak bir işi yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11609, 'şimdiden tezi yok', 'vakit geçirmeden, hemen şimdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11610, 'şimşek çakmadan gök gürlemez', '1) meydana gelmemiş bir olayın yankısı olmaz; 2) bir gürültü kopmadan önce belirtileri görülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11611, 'şimşek çakmak', 'şimşek oluşmak: ''Üst üste birkaç şimşek çakıyor.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11612, 'şimşek gibi', 'çok hızlı: ''Şimşek gibi bir semte atıldık yedi koldan / Şimşek gibi Türk atlarının geçtiği yoldan'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11613, 'şimşekleri üstüne çekmek', 'sert eleştirilere hedef olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11614, 'şirazeden çıkmak', '1) kitabın sırt bölümünde bulunan enli şeridin bozulması sebebiyle sayfalar dağılmak; 2) mec. akıl dengesini kaybetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11615, 'şirk koşmak', 'Tanrı''nın birden çok olduğunu söylemek, Tanrı''ya ortak tanımak, eş koşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11616, 'şişe çekmek (vurmak)', 'ağrı dindirmek amacıyla içinde alev yakılarak havası seyreltilen özel bir şişeyi veya bardağı sırta yapıştırmak, vantuz çekmek: ''Annem şişe çekerken kıvrılmış gazete kâğıdıyla yaptığı küçük alevli ısıtıcıları tenimize düşürür, yakardı.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11617, 'şöhret afettir', 'kişinin kazandığı ün, her zaman hoşa giden bir durum sağlamaz, kişi gücünü aşan istekler, zorlamalar karşısında bunalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11618, 'şöhret bulmak (kazanmak)', 'ün sahibi olmak, üne kavuşmak, ünlenmek: ''Fakat Nedim''den hoşlanan kızlarla kadınların çoğu onu, yeni şöhret bulan bir sinema aktörüne benzetmektedir.'' -Y. K. Karaosmanoğlu. ''Her mahallede hatta satıcılar arasında şöhret kazanmış olan güzel sesliler bulunurdu.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11619, 'şöhret kapısı açılmak', 'meşhur olmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11620, 'şöhret salmak', 'ünü yayılmak: ''Burada jandarma teğmeni olsun da daha bir defa, Ankara''da şöhret salmış olan o gözleri görmesin.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11621, 'şöhreti dünyayı tutmak', 'çok tanınmak: ''Bizim evin altında şöhreti dünyayı tutmuş bir turşucu dükkânı vardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11622, 'şölen çekmek', '1) şölen düzenlemek, ziyafet vermek; 2) mec. sanat gösterisinde bulunmak: Konuşmanın şurasına burasına espriler serpiştirerek size bir konuşma şöleni çekerdi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11623, 'şöyle bir bakmak (göz atmak)', 'kısaca bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11624, 'şöyle dursun', 'bir işin gerçekleşmekten çok uzak bulunduğunu, ona bağlı daha kolay, daha basit bir şeyin bile gerçekleşmediğini anlatan bir söz: Uyumak şöyle dursun, biraz dinlenmek bile mümkün olmadı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11625, 'şöyle ki', 'bir düşünceyi açıklamak için söylenecek sözlerin başına gelen bağlaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11626, 'şu denli', 'çok, fazla.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11627, 'şu günlerde (sırada)', '1) çok uzak olmayan bir zamanda; 2) içinde yaşadığımız günlerde: ''Şu sırada bütün belalar neredeysem gelip beni buluyor.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11628, 'şu kadar', 'çok fazla.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11629, 'şu kadar ki', 'ancak, bununla birlikte, ne var ki.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11630, 'şuna bak', 'hafifsemek veya kınamak için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11631, 'şuna buna', 'başkalarına.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11632, '(şuna veya buna) kalsa (kalırsa)', '1) herhangi birinin kanısınca: Bana kalırsa siz yanılıyorsunuz. 2) elinden gelse, elinde olsa: ''Bana kalsa çok daha önce gelirdim buraya.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11633, 'şunda bunda', 'herkeste.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11634, 'şundan bundan', 'belirsiz şeylerden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11635, 'şundan bundan konuşmak', 'havadan sudan konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11636, '(şundan veya bundan) kalır yeri yok', 'ayrımsız, farksız: ''Bu heriften bıktım. Macit''ten kalır yeri yok.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11637, 'şunu bunu', 'çeşitli nesneleri: ''Hacı''dan gereken şunu bunu alarak toprağın şu parçasını, o parçasını rehin verdik.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11638, 'şunu bunu bilmemek', 'itiraz dinlememek, mazeret kabul etmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11639, 'şunun bunun', '1) herkesin, el âlemin: ''Başlarını dinlerler, kumru gibi yuvalarında oturur, şunun bunun aleyhinde konuşmazlar.'' -B. Felek. 2) kimliği belli olmayanın, adı sanı bilinmeyenin: ''Çiftliği her zamanki gibi şunun bunun elinde unutulmuş buldu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11640, 'şunun şurası', 'küçümseme, azımsama anlatan bir söz: ''Bir incir çekirdeğini doldurmayan sebeplerle şunun şurasında ne var ki ağzımızın tadını kaçırıyorsunuz.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11641, 'şurada burada', 'birçok yerde, rastgele yerde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11642, 'şuradan buradan', '1) birçok yerden, rastgele yerden; 2) her konudan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11643, 'şusu busu', 'belirsiz mal varlığı: ''Adamın bizim adada yalıları, şatosu, köşkü, şusu busu vardır.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11644, 'şut atmak (çekmek)', 'topu sert ve hızlı bir biçimde kaleye atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11645, 'şüphe bırakmamak', 'kuşkuya sebep olan bütün ihtimalleri ortadan kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11646, 'şüphe etmek', 'kuşkulanmak: ''Bu sözünde samimi olduğuna hiç şüphe etmem.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11647, 'şüphe uyanmak', 'kuşku uyanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11648, 'şüphe yok', 'kuşku yok: ''Evinde yalnız olduğu ve hiç şüphe yok, birçok işi olduğu hâlde saatlerce benim için o pencerenin önünde duruyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11649, 'şüpheye düşmek', 'kuşkulanmak: ''Yaşayışı şüpheye düşürmüştü beni.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11650, 'şüpheye kapılmak', 'şüphe duymak: ''Eski bir bakan Ankara''nın bir köşesinde bir apartman mı yaptırmış, İsmet Paşa derhâl bir haksız iktisap şüphesine kapılıyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11651, 'şüyu bulmak', 'herkes tarafından duyulmak, yayılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11652, 'şüyuu vukuundan beter', 'bir şeyin dedikodusunun yapılması, onun gerçekleşmesinden daha kötüdür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11653, 'ta kendisi', '(ta kısa söylenir) o kimse, tastamam kendisi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11654, 'taam etmek', 'yemek yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11655, 'tabak gibi', 'dümdüz ve açık (yer).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11656, 'tabak sevdiği deriyi taştan taşa (yerden yere) çalar', 'birinin yakınlarına gösterdiği sert davranış onun iyiliği içindir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11657, 'tabakhaneye bok mu yetiştiriyorsun? (göytürüyorsun?)', '''işin bu denli acele ve önemli mi?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11658, 'taban çıkmak (girmek, koymak)', 'futbolda topla oynayan oyuncunun hareketini engellemek için doğrudan doğruya tabanla müdahale etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11659, 'taban tabana zıt (olmak)', 'birbirine son derece aykırı: ''Hiç değilse bir gazetemiz, bizim fikirlerimizle taban tabana zıt olacaktır.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11660, 'taban tepmek (patlatmak)', 'uzun yol yürümek: ''Her akşam gazete başına kırk para kazanmak için şehrin dört bir köşesinden buraya kadar taban tepmek...'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11661, 'taban yapmak', ' ekon. fiyat, en aşağı duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11662, 'tabana kuvvet', 'bir yere yayan gitmekten başka çare olmadığını anlatan bir söz: Haydi bakalım, tabana kuvvet!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11663, 'tabana kuvvet kaçmak', 'çok hızlı, koşarak kaçmak: ''Sanki yerden taş aldığımı, hayır eğildiğimi görmüş gibi tabana kuvvet kaçıyor.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11664, 'tabancaya davranmak', 'ateş etmek için tabancayı bulunduğu yerden almaya kalkışmak: ''Tabancasına davranmaya vakit kalmadan sıkışıverdi kalabalığın ortasına.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11665, 'tabanları kaldırmak', 'koşarak kaçmak: ''Ziver sanki canı çok yanmışçasına -Vay anam- diye bir çığlık kopardı ve tabanları kaldırıp kaçıyor gibi yaptı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11666, 'tabanları patlamak', 'çok yürümekten, çok ayakta durmaktan aşırı yorulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11667, 'tabanları yağlamak', ' alay 1) uzak bir yere yayan gitmeye hazırlanmak; 2) hızlıca koşmak, kaçmak: ''En iyisi, çantayı da tabancayı da atıp tabanları yağlamaktı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11668, 'tabi kılmak', 'egemenliği altına almak, boyun eğdirmek, kendine uydurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11669, 'tabi tutmak', 'tabi kılmak: ''Köylü temsilleri muhtelif bakımlardan tasnife tabi tutulabilir.'' -A. K. Tecer.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11670, 'tabiri (tabir) caizse', '1) ''sözün özünü söylemek gerekirse'' anlamında kullanılan bir söz: ''Tabiri caizse medyatik olanların adlarını duymuşlar elbette.'' -N. Meriç. 2) ''diğer bir deyişle, şöyle söylemek uygunsa'' anlamında kullanılan bir söz: ''Tabir caizse, yalanın böylesi vicdani bir suç değildir.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11671, 'taburcu etmek', 'doktor hastayı yatarak tedavi gerekmediğinde hastaneden çıkarmak: ''Kızımı bugün taburcu ettiler. Hamdolsun hiçbir şeyi kalmadı.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11672, 'tacizlik etmek', ' hlk. tedirgin etmek, can sıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11673, 'tacizlik getirmek', '1) tedirgin olmak; 2) usanç getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11674, 'tacizlik vermek', '1) tedirgin etmek; 2) usandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11675, 'taç giymek', '1) tahta çıkmak; 2) kral veya kraliçe seçilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11676, 'tadada çıkmak', ' ask. yoklamaya katılmak üzere toplanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11677, 'tadı damağında kalmak', '1) yenen bir şeyin tadını unutamamak; 2) hoşa giden, zevk alınan bir şeyi unutamamak: ''Eski seyahat hürriyeti, yine tadı damağımızda kalan tatlı bir hatıra olmuş.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11678, 'tadı gelmek', 'tat kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11679, 'tadı kaçmak (gitmek)', '1) tatsız bir duruma gelmek: ''Ali giderse bizim evin tadı iyice kaçar.'' -O. Kemal. 2) mec. bir şey hoşa gidecek yönlerini yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11680, 'tadı tuzu kalmamak (bozulmak)', 'eski zevki kalmamak, yavanlaşmak: ''Buradan itibaren anladım ki memleketin hiç tadı tuzu kalmamış.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11681, 'tadı tuzu yok', 'zevksiz, yavan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11682, 'tadına bakmak', 'ağzına alıp tadını denemek, test etmek: ''Ana çorbaya tuz atıyor, baba mancanın tadına bakıyor.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11683, 'tadına doyum olmamak', '1) bir şeyin tadı çok beğenilmek; 2) mec. herhangi bir şey çok beğenilmek: ''Bir orman, tadına doyum olmayan bükülüşlerle denize kadar iniyordu.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11684, 'tadına varmak', 'bir şeydeki ince güzelliği kavramak: ''Bir kere tadına varanlar, yine ondan ver diye başıma balta kesiliyorlar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11685, 'tadında bırakmak', 'aşırılığa kaçmamak: ''Yeter artık! Her şeyi tadında bırakmalı.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11686, 'tadından yenmemek', 'çok tatlı, çok hoşa gider olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11687, 'tadını almak', 'bir şeyin güzelliğini bilir olmak, zevkine varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11688, 'tadını bulmak', 'tadı yerine gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11689, 'tadını çıkarmak', 'bir şeyin güzelliğinden veya sağladığı imkânlardan yeterince yararlanmak: ''Kırlarda karısı ile birlikte çıkacakları uzun at gezintilerinin, ocak ateşlerinin tadını çıkarırdı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11690, 'tadını kaçırmak', 'aşırılığa kaçmak, hoşa gitmeyen bir durum yaratmak: ''Şaka ettiğini ama şakanın tadını kaçırdığını söylüyordu.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11691, 'tadını tuzunu bulmak', 'kıvamına gelmek, beklenen ölçülere ulaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11692, 'tafra satmak', 'böbürlenmek, büyüklenmek, büyüklük taslamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11693, 'tafsilat vermek', 'bir kimse, bir şey veya durumun özelliklerini, inceliklerini, ayrıntılarıyla anlatmak, uzun uzadıya anlatmak: ''Size bugün uzun uzadıya tafsilatını verecek değilim.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11694, 'tafsilata girmek', 'ayrıntılar üzerinde durmak: ''Daha fazla tafsilata girmeyi bugün zararlı gördüğüm için bu konuda susacağım.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11695, 'tahakkuk ettirmek', 'kurum, kuruluş veya kişilerin herhangi bir konuda ödemesi gereken miktarı belirlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11696, 'taharet almak', 'temizlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11697, 'tahkime gitmek', 'herhangi bir anlaşmazlığı, çözülmesini sağlamak için tahkim kuruluna taşımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11698, 'tahkire uğramak', 'hakaret görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11699, 'tahlilden geçirmek', 'gözden geçirmek: ''Kalabalığı kısa ve kuş bakışı bir tahlilden geçirelim.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11700, 'tahsil görmek', 'eğitim almak: ''İyi tahsil görmüş gençlerden bir grup meydana getiririz.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11701, 'tahta çıkmak', 'hükümdar olmak: ''Sultan Süleyman tahta çıkar çıkmaz, babası namına inşa ettirdiği cami 1522''de bitmiş ve halka açılmıştır.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11702, 'tahtalıköyü boylamak', 'ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11703, 'tahtaya kaldırmak', 'öğrenciyi sözlü sınav için sınıftaki tahtanın önüne çağırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11704, 'tahtaya kalkmak', 'öğrenci sınıfta kara tahta önüne çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11705, 'tahttan indirmek', 'hükümdarlığına son vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11706, 'takas tukas etmek', 'değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11707, 'takat getirmek', 'dayanmak, katlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11708, 'takati kalmamak (kesilmek)', 'gücü azalmak, bitmek: ''Sonra, artık takati kesilmiş gibi kendini bıraktı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11709, 'takati yetmemek', 'gücü yeterli olmamak: ''Günahlarımızın icmaline gelince ben tutamam, takatim yetmez.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11710, 'takatsizlik duymak', 'güçsüz ve kuvvetsiz kaldığını anlamak: ''Yere uzanmak isteyecek kadar vücudunda takatsizlik duyuyordu.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11711, 'takılı kalmak', 'bir iş bitmemek: ''Bütün dava şimdi Trakya''daki bu sınırlama keyfiyetine takılı kalmıştı.'' -A. N. Karacan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11712, 'takılıp kalmak', 'herhangi bir noktadan ayrılamamak: ''Duygularının bilmecesini filan çözmüş değildir. O duyguların abecesinde takılıp kalmıştır.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11713, 'takım tutmak', 'spor takımlarından birini desteklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11714, 'takıp takıştırmak', 'özenerek süslenmek: ''Kız, kalk giyin, tak takıştır, diyor.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11715, 'takibe vermek', 'banka, alacağını hukuki yoldan tahsil edilebilmek için işi avukata havale etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11716, 'takke düştü, kel göründü', 'bir ayıbı örten şey ortadan kalktığı zaman gerçek ortaya çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11717, 'takla atmak', '1) takla hareketini yapmak: ''Adam o kadar kederli, o kadar meyustu ki takla atıp ney çalan kambur cüceler bile onu güldürmeyi başaramamıştı.'' -İ. O. Anar. 2) mec. çok sevinmek: ''Biz senin yaşındayken iki altına takla atardık.'' -F. R. Atay. 3) mec. bir kimseye yaranmak için onun hoşuna giden davranışlarda bulunmak, dalkavukluk etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11718, 'takla attırmak', '1) bir şeyi dilediği gibi beceriyle kullanabilmek: ''Sizin için Türkçenin cambazıdır, kafiyelere taklalar attırır, dedi.'' -Y. Z. Ortaç. 2) birine istediği her şeyi yaptırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11719, 'taklidini yapmak', '1) bir şeyin veya kimsenin konuşmasını, davranışını komik bir biçimde tekrarlamak: ''Annesinin, babasının taklitlerini yapıyordu.'' -Ç. Altan. 2) öykünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11720, 'takoz koymak', '1) aracın hareketini önlemek için tekerleklerden birinin önüne veya arkasına takoz yerleştirmek, takoz atmak; 2) argo olacak işi engellemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11721, 'takrir etmek', 'ders anlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11722, 'takrir vermek', '1) satışlarda sattığını söylemek: ''Tapu memuruna takrir vermek için o gün bize nöbet gelmedi.'' -N. Hikmet. 2) önerge vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11723, 'taksi çevirmek', 'hareket hâlindeki taksiyi bir yere gitmek için durdurmak: ''İbrahim caddeye çıkar çıkmaz bir taksi çevirdi.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11724, 'taksite bağlamak', 'bir şeyi belli aralıklarla, belli miktarlarda ödeme şartları ile almak veya satmak: ''Yoksa musahhih maaşımdan haftada üç papel taksite bağlayıp seni bir şamaroğlanı gibi kullanırım.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11725, 'taktik vermek', 'çeşitli sorunlarda sonuca ulaşmak için yol ve yöntem göstermek: ''Artık yapacak işleri kalmamış da afyon kaçakçılarına, karaborsa gangsterlerine taktik vermeye kalkmışlar.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11726, 'talandan geçmek', 'yağmalanmak: ''Her uğradığı yerde çarşılar talandan geçer.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11727, 'talibi çıkmak', 'talip çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11728, 'talihi yaver gitmek', 'talihi iyi olmak, işi yolunda gitmek: ''Her devirde talihi yaver gitmiş birisiydi.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11729, 'talihin kucağına atılmak', 'kendi kaderine boyun eğmek: ''Bir gelinden ziyade, zalim bir nezri yerine getirmek için talihin kucağına atılmış bir kurbana benziyordu.'' -A. H. Tanpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11730, 'talihine küsmek', 'kötü bir durum veya olayla karşılaşıldığında yalnızca talihi suçlamak: ''Talihimize küseriz ama millî menfaat prensiplerinin bizim yüzümüzden kötülenmesini istemeyiz.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11731, 'talihsiz hacıyı Araf''ta yılan sokar', 'amacını gerçekleştirmesi mümkün olmayan kişinin karşısına, hatır ve hayale gelmeyen ve yenilemeyen engeller çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11732, 'talihsiz hacıyı deve üstünde yılan sokar', 'amacını gerçekleştirmesi mümkün olmayan kişinin karşısına, hatır ve hayale gelmeyen ve yenilemeyen engeller çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11733, 'talimat vermek', 'üst düzeyde bulunan biri, yaptıracağı işle ilgili olarak görüşünü belirtmek, yol göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11734, 'talip çıkmak', '1) kız evlenme teklifi almak: ''Keşke bilmeksizin, tesadüfen, İsmail''in almak istediği bu kıza talip çıkmış bir adam vaziyetinde kalsaydım.'' -Y. K. Karaosmanoğlu. 2) bir şeye istekliler bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11735, 'talkın vermek', 'ölü gömüldükten sonra mezar başında imam dinî sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11736, 'tam adamına çatmak', 'olumsuz bir davranış ve tutum içinde bulunan kimseyle karşı karşıya gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11737, 'tam adamını bulmak (adamına düşmek)', '1) en uygun kişiyi seçmek; 2) alay en uygunsuz kişiyi seçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11738, 'tam gelmek (olmak)', 'uygun gelmek, uymak: Elbise tam geldi. Ayakkabı ayağına tam oldu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11739, 'tam maaşla tekaüt (emekli)', ' şaka işi az, ödeneği çok olan bir işe yerleşenler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11740, 'tam üstüne basmak', '1) kesin olarak belirlemek: ''Tam üstüne bastın canım, ben sıradan değil sıra dışı biriyim.'' -A. Kulin. 2) doğru olanı, benzerini, istenileni bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11741, 'tamah olmasa, müflis acından ölmez', 'elinde avucunda bir şey bulunmamakla birlikte küçük kazançları beğenmeyen kişi büyük kazanç hayaliyle geçinir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11742, 'tamah varken müflis acından ölür', 'elinde avucunda bir şey bulunmamakla birlikte küçük kazançları beğenmeyen kişi büyük kazanç hayaliyle geçinir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11743, 'tamam bulmak', ' esk. bitmek, sona ermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11744, 'tamam gelmek', 'bir şeye uygun düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11745, 'tamir görmek', 'onarılmak, düzeltilmek, yenilenmek: ''Köşk tamir görmekte olduğundan Gazi, bu küçük dairede oturuyordu.'' -R. E. Ünaydın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11746, 'tamire vermek', 'onarılmak için bir şeyi onaracak kimse veya yere vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11747, 'tamtakır kuru (kırmızı) bakır', 'boş, bomboş: ''Sütnine yukarı çıktığı zaman ne görsün? Sandık tamtakır kuru bakır.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11748, 'tan ağarmak (atmak, sökmek)', 'gün doğmaya başlamak, şafak sökmek: ''Artık tan sökünceye kadar gelsin gazeller, şarkılar, feryatlar.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11749, 'tan tuna gitmek', 'öldürülmek veya başı belaya uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11750, 'tan yeri ağarınca hırsızın gözü kararır', 'kirli işler yaparak çıkar sağlayan kişi, buna elverişli olan durum sona erince sersemleşir, hiçbir iş yapamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11751, 'tan yeri ağarmak', 'sabah olmaya başlamak, ufku belli belirsiz bir aydınlık kaplamak: ''Tan yeri ağarmış, gündüz olmaya başlamıştı.'' -A. H. Müftüoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11752, 'tandem oynamak', ' sp. kalecinin önünde savunmak amacıyla duran iki oyuncu paslaşarak oynamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11753, 'tane bağlamak', 'meyve veya herhangi bir bitkinin tohumları tane durumuna gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11754, 'tane tane söylemek (konuşmak)', 'acele etmeden, seslerin hakkını vererek herkesin anlayabileceği gibi konuşmak: ''Genç kadın ağır adımlarla eski yerine oturdu, tane tane söylemeye başladı.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11755, 'tanıdık çıkmak', '1) önceden birbirlerini tanımış olmak, tanış olmak; 2) bir şeyi daha önceden öğrenmiş, duymuş olmak: ''Sırrı Bey, bu iki ada hemen tanıdık çıktı ve artık oturduğu koltukta büsbütün uzanarak, bekliyoruz paşam, dedi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11756, 'tanımazlıktan gelmek', 'bir kimseyi tanıdığı hâlde tanımıyormuş gibi davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11757, 'tanış çıkmak', 'daha önceden tanışmış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11758, 'Tanrı yarattı dememek', 'Allah yarattı dememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11759, 'Tanrı''nın günü', 'Allah''ın günü: ''Tanrı''nın günü bu pasaj sabahın yedisinden, gecenin yarısına kadar her çeşit insanla dolar taşardı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11760, 'tansiyonu çıkmak (fırlamak, yükselmek)', 'kan basıncı aniden yükselmek: ''Kocasının hiddetten tansiyonu yükseldi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11761, 'tansiyonu düşürmek', 'gerilimi azaltmak: ''Hiç değilse önde gelen fırkacıların tansiyonunu düşürmeyi de ümit etmişti.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11762, 'tansiyonu yükseltmek', 'gerilimi arttırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11763, 'tantana yapmak (etmek)', '1) kuru gürültü çıkarmak; 2) gereksiz yere, boşu boşuna konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11764, 'tapan çekmek', 'tapanlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11765, 'tapi kalmak', 'kumar oyunlarında fişsiz ve parasız kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11766, 'taraf çıkmak (olmak)', 'taraf tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11767, 'taraf gözetmek', 'birinden yana olmak: ''Meseleyi taraf gözetmeden aksettirmek için o yazıdan da bir parça almak isterdik.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11768, 'taraf (tarafını) tutmak', 'birinden yana olmak, birinin görüş ve düşüncesini desteklemek: ''Benim, daha çok erkeklerin tarafını tutar gibi görünen akıl öğretmelerime hanımlar kızabilir.'' -Ş. Rado.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11769, 'tarak vurmak', 'taramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11770, 'tarife gelmemek', 'açıklanması güç olmak: ''Oysa gece boyunca daracık bir döşekte gözünü kırpmadan uzanmak tarife gelmeyecek kadar sıkıcıydı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11771, 'tarih atmak (koymak)', 'bir şeyin üzerine tarih yazmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11772, 'tarih düşürmek', 'önemli sayılan bir olayın, çoğunlukla nazım biçiminde söylenen sözlerle, ebcet hesabına göre tarihini belirtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11773, 'tarihe geçmek', 'önemi bakımından unutulmayacak bir durum kazanmak: ''Bütün ömründe tek bir kitap yazmış ve sadece bu kitabıyla tarihe geçmiştir.'' -A. Boysan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11774, 'tarihe karışmak', 'unutulmak, yalnız adı kalmak: ''Bir yaş gelir ki ondan sonra ehemmiyet verdiğiniz şeyler tarihe karışmış yani hayattan çıkmıştır.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11775, 'tarizde bulunmak', 'sözle sataşmak, taşlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11776, 'tarla açmak', 'çalıları, ağaçları, taşları kaldırarak veya ormanlık bölgede ağaç keserek, yakarak bir yeri sürülüp ekilir duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11777, 'tarlada çayırda, bağ bayırda', 'her şey kendisi için en elverişli ortamda gelişir, verimli duruma gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11778, 'tarlada izi olmayanın harmanda sözü (yüzü) olmaz', 'kendini işe vermeyenden, bir iş üretmeyenden hayır gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11779, 'tarlanın iyisi suya yakın, daha iyisi eve yakın', 'çiftçinin toprağı suya ne kadar yakınsa değeri o kadar çok olur; bakımı, ürünün güvenliği ve eve kolay taşınabilmesi bakımından toprağın eve yakın olması daha da önemlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11780, 'tarlanın taşlısı, karının (kadının) saçlısı', 'kadının saçlı olanı ile tarlanın taşlı olanı makbuldür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11781, 'tarlanın taşlısı, kızın saçlısı, öküzün (ineğin) başlısı', 'kadının saçlı olanı ile tarlanın taşlı olanı makbuldür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11782, 'tarlayı düz al, kadını kız al', 'tarla alacak kimse bayırdan, engebeli yerden değil düz yerden almamalıdır, evlenecek erkek de dul kadın değil, kız almalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11783, 'tarlayı koçan zapt etmez, saban zapt eder', 'elinizde tarlanın tapusunun olması o tarlaya sahip olduğunuzu göstermez, onu ekip biçebiliyorsanız asıl o zaman o tarla sizin demektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11784, 'tarlayı taşlı, kızı kardeşli yerden almalı', 'tarlanın taşlı olanı, evlenilecek kızın kardeşli olanı halk arasında daha yeğ tutulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11785, 'tarlayı taşlı yerden, kızı kardaşlı yerden', 'tarlanın taşlı olanı, evlenilecek kızın kardeşli olanı halk arasında daha yeğ tutulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11786, 'tartak martak etmek', 'kazıp dağıtmak, darmadağın etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11787, 'tartışma götürmek', 'bir konu tartışmaya açık olmak: ''İki yandan gelen arabaların orada yolu tıkadığı tartışma götürmez.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11788, 'tartışmaya girmek', 'münakaşa etmeye başlamak: ''Mustafa''nın yanında bir tartışmaya girmek de istemiyorum.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11789, 'tartma tartmak', 'baş örtüsü takmak: ''Bayanlar sırtlarına toza, yağa dayanır birer takım giydiler, başlarına da birer tartma tarttılar, ... fabrikaya gittiler.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11790, 'tas gibi', '1) saçsız, dazlak; 2) çok düz, açık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11791, 'tasa çekmek', 'kaygılanmak, üzüntü içinde olmak, üzülmek: ''Toprağı yeşertmeye lazımsa benim kanım / Hiç tasa çekme, çoktan ben yurduma kurbanım'' -F. N. Çamlıbel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11792, 'tasası sana mı düştü?', '''sen karışma, seni ilgilendirmez'' anlamında kullanılan bir söz: ''Sonra, dedim, bunun tasası sana mı düştü?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11793, 'tasasına düşmek', 'derdine düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11794, 'tası tarağı toplamak', 'her türlü ilişkiyi kesmek üzere hazırlanmak: ''Tası tarağı toplayıp ortalıktan usul usul tüyüyorsunuz.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11795, 'tasvip görmek', 'birinin bir düşünce ve davranışı uygun, yerinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11796, 'tasvir gibi', 'çok güzel (kimse): ''... beyim, dadılar, tayalarla şımartılmış, kuşsütüyle beslenmiş, beyaz, tüysüz, oğlandan çok kıza yakın, tasvir gibi bir civan.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11797, 'taş altında olmasın da dağ ardında olsun', 'uzakta olan sevdiklerimize günün birinde kavuşabiliriz yeter ki yaşıyor olsunlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11798, 'taş atıp kolu yorulmamak', 'bir kazancı hiç yorulmadan sağlamak: ''Taş atıp kolunuz yorulmadan üstüne konduğunuz paranın nasıl kazanıldığını bir yazarsak görürsünüz.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11799, 'taş atmak', 'birine dolaylı olarak iğneleyici, dokunacak bir söz söylemek: ''İkide birde bana bunun için taş atıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11800, 'taş attın da kolun mu yoruldu?', 'bir kazancın hiç yorulmadan sağlandığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11801, 'taş bebek gibi', 'çok güzel fakat genellikle soğuk ve donuk (kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11802, 'taş çatlasa', '1) bütün olanakların kullanılmış olmasına karşın: ''Bunlardan en iyisini taş çatlasa konakta iki aydan fazla tutamazdı.'' -R. N. Güntekin. 2) ne olursa olsun; 3) en fazla: ''Taş çatlasa otuz yaşlarında görünen genç kadın yanındaki boş yere oturmuştu.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11803, 'taş düştüğü yerde ağırdır (kalır)', 'kişinin değerini en iyi bilenler, kendi çevresinde bulunanlardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11804, 'taş düşürmek', 'böbrekte oluşan kum ve taşları vücuttan atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11805, 'taş gibi', '1) çok sert, çok katı; 2) çok sağlam; 3) hareketsiz: ''Bütün galeyanı, bu taş gibi karşısında oturan, her an fırlayacak adam önünde tavsadı.'' -N. Hikmet. 4) vücudu diri, taze (kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11806, 'taş kesilmek', '1) çok şaşırıp ne yapacağını, ne söyleyeceğini bilememek: ''Salonun içinde kimse kımıldayamadı. Hepsi olduğu yerde dondu. Taş kesildi.'' -Ö. Seyfettin. 2) sesini çıkaramaz olmak: ''Hayvan sanki taş kesilmiş ve kulaklarını dimdik dikmişti.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11807, 'taş kırdırmak', 'böbrek taşlarını çeşitli yollarla parçalara ayırarak vücuttan atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11808, 'taş koymak', 'engelleyecek biçimde davranmak: ''Damadım hakkında kötü şeyler düşünmeni, bu işe taş koymanı istemiyorum.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11809, 'taş ne kadar ıslanırsa, deli o kadar uslanır', 'her nesnenin, her kişinin değiştirilemeyen bir özelliği vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11810, 'taş sürmek', 'satranç, dama, domino vb. oyunlarda taşlardan birini oynatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11811, 'taş taş üstünde bırakmamak', 'baştan başa yıkıp yerle bir etmek: ''Kaçsan da kaç para eder? Sana, köyde taş taş üstünde bırakmayacak diyorum.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11812, 'taş taş üstünde olur, ev ev üstünde olmaz', 'aynı evde oturan iki aile arasında er geç birtakım anlaşmazlıklar çıkar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11813, 'taş yağar kıyamet koparken', 'telaşlı ve tehlikeli zamanları anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11814, 'taş yerinde ağırdır', 'kişinin değerini en iyi bilenler, kendi çevresinde bulunanlardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11815, 'taşa çekmek', 'bileği taşında kılağılamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11816, 'taşa çıkan keçinin ağaca çıkan oğlağı olur', 'çocuklar ana ve babalarından öğrendiklerini yapmaya özenirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11817, 'taşa tutmak', '1) üst üste taş atmak, aralıksız taşlamak: ''Sokaktan her geçişinde çocuklar taşa tutarlardı onu, canını yakmanın bir yolunu bulurlardı.'' -T. Uyar. 2) tek. zımparalamak amacıyla çok hızla dönen bileği taşına hafifçe dokundurmak, pürüzlerini almak, düzgünleştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11818, 'taşı gediğine koymak', 'gerekli bir sözü tam zamanında ve yerinde söyleyerek karşısındaki kimseyi susturmak, zekice davranmak: ''Fırsat çıkmışken kim bilir hangi uzak meseleden tutturup taşı gediğine koymak ve tenkit etmiş olmak için kaplarına sığamıyordu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11819, 'taşı ölçeyim', ' hlk. kırık, ezik, yara vb. durumlar anlatılırken bir kimsenin vücudu üzerinde yer gösterildiğinde ''benden uzak olsun'' anlamında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11820, 'taşı sıksa suyunu çıkarır', 'birinin vücutça çok güçlü olduğunu belirtmek üzere söylenen söz: ''Aslan gibidir maşallah, taşı sıksa suyunu çıkarır, diyor.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11821, 'taşı toprağı altın olmak', '1) arazisi çok değerli olmak; 2) her türlü zenginliğe, olanağa, fırsata sahip olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11822, 'taşıma su ile değirmen dönmez', 'işi yapacak olanda yeteri kadar güç bulunmadıkça başkalarının küçük katkılarıyla sürekli ve büyük bir iş yürütülemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11823, 'taşın altına elini koymak', 'elini taşın altına koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11824, 'taşlar yerine oturmak', '1) her şey yerli yerinde olmak; 2) her makama, işin veya görevin gereklerine uygun kişi yerleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11825, 'taşralı kalmak', 'bir kimse taşrada edindiği görgü, örf ve âdetleri bırakmamak: ''Şehirli görünmek gururu kasaba kızının İstanbul''dan aldığı ilk kötü huy oldu; birkaç hafta geçince babasıyla anasının yeni hayata kendisi gibi uyamayacaklarını, taşralı kalacaklarını anlayınca hırçınlaştı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11826, 'tat almak', 'bir şeyden hoşlanmak, zevk almak: ''Kelimenin de tadını alır, kafiyenin de.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11827, 'tat kazanmak', 'belli bir tada kavuşmak, olgunlaşmak, tatlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11828, 'tat vermek', '1) acı, tatlı, ekşi vb. bir tat kazandırmak; 2) mec. hoşa giden bir duruma sebep olmak; 3) mec. bıktırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11829, 'Tatar''ın kılavuza ihtiyacı yok (-tur)', 'yolunu, ne yapacağını bilen kimseye başkasının yardımı gerekmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11830, 'tatil etmek', '1) başka bir güne, zamana erteleyerek çalışmaya ara vermek; 2) okul, iş yeri vb.ni kapatmak, çalışmasına ara vermek: ''O zamana kadar inşaatı tatil edeceksiniz, dediler.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11831, 'tatil olmak', 'kapanmak, ara verilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11832, 'tatil yapmak', '1) tatile çıkmak; 2) işe ara verip dinlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11833, 'tatile girmek', 'belirli bir süre için çalışmalara ara vermek: Üniversite haziranda tatile girecek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11834, 'tatlı canından etmek', 'öldürmek: ''Canımı dişime takmayayım bir kere, adama hiç acımam tatlı canından ederim.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11835, 'tatlı canından olmak', 'ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11836, 'tatlı canını sıkmak', 'gereksiz şeylere üzülmek ve bunları dert edinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11837, 'tatlı dil yılanı deliğinden çıkarır', 'gönül alıcı, okşayıcı sözlerle karşımızdakinin inadı yenilebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11838, 'tatlı söz can azığı, acı söz baş kazığı', 'gönül alıcı, okşayıcı sözlerle karşımızdakinin inadı yenilebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11839, 'tatlı söz dinletir, tatsız söz esnetir', 'güzel bir konuşmayı dinlemeyi herkes sever, sıkıcı bir konuşma dinlemek zorunda kalanlar, sıkıldıklarını belli etmekten kendilerini alamazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11840, 'tatlı tatlı yemenin acı acı geğirmesi olur', 'sonunu düşünmeden hoşlandığı şeyleri yapan kişi bir süre sonra bunun sıkıntısını çeker.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11841, 'tatlı yerinde bırakmak (kesmek)', 'bir işi can sıkıcı bir duruma sokmadan sona erdirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11842, 'tatmin etmek', '1) karşısındakinin cinsel isteklerini gidermek; 2) karşısındakine güven vererek onu istenilen bir biçimde hoşnut etmek; 3) mec. doyurmak: ''Mizaç ve karakterleri dışa dönük olanlar kendilerini tatmin eden çevrelere ihtiyaç duyarlar.'' -M. Kaplan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11843, 'tatmin olmak', '1) istediği bir şeye ulaşarak hoşnut olmak, rahatlamak, doyurulmak; 2) cinsel isteklerini gidermek; 3) mec. doyurucu bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11844, 'tatsız aşa su neylesin, akılsız başa söz neylesin', 'işe yaramayan nesneyi küçük çabalarla bir şeye benzetmek boş olduğu gibi aptal kişiyi de sözle akıllandırmak imkânsızdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11845, 'tatsızlık çıkarmak', 'hoşa gitmeyen, can sıkıcı, gergin bir duruma sebep olmak: ''Çoktandır aramızda tatsızlık çıkardığım yoktu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11846, 'tav olmak', '1) kanmak: ''Bazen kadınlara çabuk tav olduğunu düşünürüm de işe zarar verir, bilirsin aşırılık.'' -E. Işınsu. 2) tam olarak istediği olmasa da kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11847, 'tav vermek', '1) gereken ve uygun nemi sağlamak; 2) mec. en uygun duruma getirmek: ''Biraz durdu. Sonra işe az daha tav vermiş olmak için...'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11848, 'tava gelmek', '1) toprak sürülecek duruma gelmek; 2) mec. yumuşamak, kanmak, yola gelmek: ''Oğul, anasının tava geldiğini sezmişti.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11849, 'tava getirmek', '1) gereği kadar ısıtmak; 2) mec. olayları istenilen duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11850, 'tavan başına çökmek (yıkılmak)', 'beklenmeyen bir durum karşısında şaşırıp kalmak: ''Gelmeyecek mi? Neden gelmedi? diye sordukları vakit tavan başıma yıkılıyordu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11851, 'tavan yapmak', '1) Menkul Kıymetler Borsasında işlem görmekte olan hisse senedinin değeri en üst düzeye ulaşmak; 2) sinir, heyecan vb. en üst düzeye çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11852, 'tavana vurmak', 'tavan yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11853, 'tavı kaçmak (geçmek)', 'uygun zamandan yararlanamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11854, 'tavını bulmak', 'iş vb. için en uygun şartları yakalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11855, 'tavır almak (takınmak)', 'belli bir durum veya davranış biçimini benimsemek, vaziyet almak: ''Bilgin değilim. Onun için yazılarımda da bilgince tavır takınmaktan çekinirim.'' -O. V. Kanık. ''Parçasını söylerken aldığı tavır, insanı gülmekten katıltacak kadar komik.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11856, 'taviz vermek', 'ödün vermek: ''Galiplerin yenilen devletlere hiçbir taviz vermeyecekleri hissediliyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11857, 'tavla atmak', 'tavla oynamak: ''Çoğu tedaviden sonra, bir parti de tavla atardı hastaları ile...'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11858, '(tavşan boku gibi) ne kokar ne bulaşır', '''kimseye iyiliği de dokunmaz, kötülüğü de'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11859, 'tavşan boku gibi (ne kokar ne bulaşır)', ' kaba ne yararı ne de zararı olan (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11860, 'tavşan dağa küsmüş, dağın haberi olmamış', 'önemsiz kişi, önemli kişiye küsse önemli kişinin umurunda bile olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11861, 'tavşana kaç, tazıya tut demek', 'iki tarafı, karşıt olan davalarında kışkırtmak, ikili oynamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11862, 'tavşanı araba ile avlamak', 'işini telaşsız ve soğukkanlılıkla görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11863, 'tavşanın suyunun suyu', 'iki şey arasındaki ilginin çok uzak olduğunu anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11864, 'tavuk ayağı yemek', 'gevezelik etmek, dedikodu yapmak: ''A, o nasıl lakırtı, dedi. Bunlar da tavuk ayağı yemişler, ağızlarında bakla ıslanmıyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11865, 'tavuk gibi', 'erken yatıp uyuyan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11866, 'tavuk kaza bakarsa kıçı yırtılır', 'başkalarından geri kalmamak için gücünü aşan işlere girişenler büyük zararlara uğrarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11867, 'tavuk kaza bakmış da kıçını yırtmış', 'başkalarından geri kalmamak için gücünü aşan işlere girişenler büyük zararlara uğrarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11868, 'tay tay durmak', 'emekleme döneminde, henüz yürüyemeyen çocuk ayakları üzerinde durmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11869, 'tay yetişmedikçe ata paha biçilmez', 'iş gören bir şeyin yerine yenisi alınmadığı sürece eskisinin değeri ölçülemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11870, 'tayfanın akıllısı, geminin dümeninden uzak durur', 'akıllı işçi, beceremeyeceği yönetim işine el atmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11871, 'tayini çıkmak', 'atanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11872, 'tazarruda bulunmak', 'Tanrı''ya yakarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11873, 'taze bardağın suyu soğuk olur', 'hayatına giren yeni şeyler, yeni dostlar kişiye hoş görünür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11874, 'taze ot görmüş eşek gibi', 'iştahlanmış bir biçimde: ''Çamur, taze ot görmüş eşek gibi pis pis sırıtmış bunun üzerine.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11875, 'tazı gibi', '1) çok zayıf ve ince kemikli (kimse); 2) çok hızlı (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11876, 'tazı o tazı ama çulu değişmiş', '1) tanıdığımız sıradan kişi işbaşına geçmiş; 2) giyim kuşamını düzeltmiş olduğu için tanınmaz olmuş.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11877, 'tazyik etmek', '1) zorlamak, baskı yapmak; 2) sıkıştırmak: ''Adamın bileklerini iki avucunun arasına alarak tazyik ediyor.'' -E. M. Karakurt.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11878, 'tebdil gezmek', '1) tanınmamak için kılık değiştirerek gezmek; 2) mec. değişik görüntüde olmak: ''Anlayana sivrisinek saz düşüncesine dayanan ve tebdil gezen bir şiir yaygınlaştı.'' -S. Hilav.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11879, 'tebdilimekânda ferahlık vardır', 'sağlık veya görev değişikliği nedeniyle bir yerden başka bir yere gitmek huzur sağlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11880, 'tebdilimekânda ferahlık vardır', 'sağlık veya görev değişikliği nedeniyle bir yerden başka bir yere giderek huzur sağlanacağını bildiren bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11881, 'tebelleş etmek', 'birini veya bir şeyi birinin başına bela etmek: ''... hanım evladını tepemize tebelleş eden kendisidir.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11882, 'tebelleş olmak', 'onun başına dert olmak, musallat olmak: ''Ha şunu bilmende yarar var, kadın çok tebelleş olursa, ona bir randevu verip kendisini dinleyebilirim.'' -E. Işınsu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11883, 'tebligatta bulunmak', 'bildirim yayımlamak, bildirimden haberdar etmek, bildirim göndermek: ''7 Temmuz 1919 tarihinde, şu umumi tebligatta bulundum.'' -Atatürk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11884, 'tecahülüarifaneden gelmek', 'bilmez gibi davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11885, 'tecrübe tahtasına çevirmek', 'üst üste başarısız denemelere konu etmek: ''Hastaları tecrübe tahtasına çevirmiş nice vakaları rastgele bir kinin tedavisiyle tedavi ettiğimi bilirim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11886, 'tecrübe tahtasına dönmek', 'üst üste başarısız denemelere konu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11887, 'tedahülde kalmak', 'ödenmeden birikmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11888, 'tedarikte bulunmak', 'hazırlık yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11889, 'tedavi görmek (olmak)', 'iyileşmek amacıyla sağlık uygulamalarından geçmek: ''Birkaç kez alkol tedavisi gördü ama yararı olmadı.'' -C. Külebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11890, 'tedavülden kalkmak', 'para artık kullanılmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11891, 'tedavüle çıkarmak', 'parayı piyasaya çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11892, 'tedbir almak', '1) önlem almak: ''Selefleri bu yolda bir sürü tedbir almışlar fakat tam muvaffak olamamışlardır.'' -N. F. Kısakürek. 2) hazırlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11893, 'teessüf ederim!', 'yazıklar olsun!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11894, 'teessüf etmek', '1) acımak, üzülmek, yazıklanmak: ''İdraksiz, şuursuz geçen günlerimiz için teessüfler edeceksiniz.'' -Ö. Seyfettin. 2) kınamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11895, 'teessür göstermek', 'üzüntüsünü açığa vurmak: ''Artık gözümün önünde ölse teessür göstermek istemiyordum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11896, 'tef çalsan oynayacak', 'karmakarışık olan eşyalar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11897, 'tefe koyup çalmak', 'tefe koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11898, 'tefekküre dalmak', 'derin düşünmek, düşünceye dalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11899, 'tefrika çıkarmak', 'birbirine kötülük etmeye kadar varan sürekli anlaşmazlık yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11900, 'teğet geçmek', '1) yakınından geçmek; 2) mec. bir konuya üstünkörü dokunmak: ''Birbirine teğet geçmiş iki acılı yaşamın öyküsü radyofonik seslere dönüşür.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11901, 'tehdit etmek', '1) gözdağı vermek: ''Şimdi yalnızca güvenliğini tehdit edebilecek tehlikeler üzerine düşünüyor.'' -N. Hikmet. 2) tehlikeli bir durum yaratmak: ''Şimdi yalnızca güvenliğini tehdit edebilecek tehlikeler üzerine düşünüyor.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11902, 'tehdit savurmak', 'korkutmak, gözdağı vermek: ''Adam -bunu sizin yanınıza komam, ikinizi de temizleyeceğim- diye tehdit savuruyormuş.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11903, 'tehlike atlatmak', 'büyük zarar ve sıkıntılara yol açacak bir olayı savuşturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11904, 'tehlike çanları çalmak', 'kötü bir durumun ortaya çıkacağı belli olmaya başlamak: ''Bedenimde tehlike çanları çalmaya başlamış, eklem romatizmasına yakalanmıştım.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11905, 'tehlikeye atılmak', 'zarar ve sıkıntılara yol açacak bir davranışta bulunmak: ''Şimdilik sizin tehlikeye atılmanıza hacet yoktur.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11906, 'tehlikeye düşürmek', 'sıkıntı, üzüntü veya zarar oluşturacak bir duruma sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11907, 'tek dalmak', ' sp. güreşte karşı güreşçinin tek bacağını kapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11908, 'tek durmak', 'uslu durmak, yaramazlık etmemek, sessiz kalmak: ''Keşke tek duraydın da bunlar başına gelmeseydi.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11909, 'tek durmamak', '1) bir taraf öbür tarafa karşı bazı hareket ve çalışmalar içinde bulunmak; 2) yaramazlık, çapkınlık vb. yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11910, 'tek elden', 'bir yerin veya bir merkezin kumanda ve yönetimi altında olarak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11911, 'tek elin nesi var, iki elin sesi var', 'başarıya ulaşmak için birlik olmak gerek.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11912, 'tek geçmek', 'sadece onunla ilgilenmek, sadece ona önem vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11913, 'tek kanatla kuş uçmaz', 'gereken koşullarla donanıp güçlenmeyen kişi amacına ulaşamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11914, 'tek kürekle mehtaba çıkmak', '1) eksik hazırlıkla bir işe kalkışmak; 2) beceriksizce alay etmeye kalkışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11915, 'tek sıra olmak', 'sıraya girmek, sıralanmak: ''Fabrikaların akşam vardiyaları dağıldıktan sonra işçiler fabrika kapılarında tek sıra oldular.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11916, 'tek vücut olmak', 'birlikte hareket etmek: ''Birbirimize sımsıkı sarılmış, tek vücut olmuş, sallanıp duruyoruz.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11917, 'tekbir getirmek', 'Müslümanlıkta Tanrı''nın büyüklüğünü, yüceliğini anmak için söylenen ve ''Allahuekber'' sözü ile başlayan duayı okumak: ''Hemen şükran secdesine kapanarak tekbir getirir.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11918, 'tekeden süt çıkarmak', ' hlk. olamayacak şeyleri olur duruma getirmek: ''Sen meram ettikten kelli, tekeden süt çıkarırım, ağam! diyordu.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11919, 'tekelinde olmak', 'herhangi bir şey tekeli altında bulunmak, elinde tutmak, inhisarında olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11920, 'tekeline (tekellerine) almak', '1) bir şeye tek başına sahip olmak, inhisarına almak, patentini almak; 2) mec. fikir, sanat vb. alanda kendi görüşünü hâkim kılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11921, 'teker meker yuvarlanmak', '1) döne döne yuvarlanmak; 2) iyi durumda olan bir kişi durumunu birdenbire yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11922, 'tekere çomak sokmak', 'birinin yolunda giden işini aksatan, engelleyen davranışta bulunmak: ''Neden ikide bir tekere çomak sokarlar? Neden kalkınma hamlesine bir tuğla da onlar koymazlar?'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11923, 'tekerine (tekerinin önüne) taş koymak', 'tekere çomak sokmak: ''Kırıp geçirmeye niyet etmişti ama ah bu kadın, gene tekerine taş koymuştu.'' -O. Kemal. ''Doğrudur dedik herife, tekerimizin önüne taş koyma dedik, anlatamadık.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11924, 'tekerlek kırıldıktan sonra yol gösteren çok olur', 'sonucu kötü çıktıktan sonra bir davranış üzerine akıl öğreten çok bulunur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11925, 'tekkeyi bekleyen çorbayı içer', 'bir şeyi elde etmek için bazı sıkıntılara katlanmak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11926, 'tekme atmak (vurmak)', '1) ayakla bir yere sertçe vurmak: ''Kafama bir tekme vurdular, bir şeyler söylenerek bırakıp gittiler.'' -M. Ş. Esendal. 2) çifte atmak; 3) mec. ihanet etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11927, 'tekme tokat girişmek', 'dayak atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11928, 'tekme yemek', '1) birinin ayağından darbe almak; 2) ihanete uğramak: ''Bu sefer de heriften bir tekme yersen bir daha belini doğrultamazsın.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11929, 'tekmil almak', ' ask. üst, birliğin o andaki durumunu bildiren sözlü bilgiyi asttan almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11930, 'tekmil vermek', ' ask. 1) ast, bir iş ve durum hakkında üste bilgi vermek; 2) ast, üstüne künyesini söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11931, 'tel çekmek', 'telle çevirmek, tel germek: Bahçeye tel çektik.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11932, 'tel çekmek', 'telgraf çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11933, 'tel takınmak', ' alay teller takmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11934, 'tel tel dökülmek', '1) dağılıp gitmek; 2) mec. çok başarısız olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11935, 'telakki olunmak', 'sayılmak, öyle kabul edilmek: ''Pek taaccüp ettim, niye mücrim telakki olunacakmış?'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11936, 'telaş göstermek', 'telaşını belli etmek: ''Polisle ben konuşurum, siz telaş göstermeyin.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11937, 'telaşa düşmek', 'telaşlanmak: ''Vapur işlemeyecek zamanlarda bile bir gün işinden kalmadığını bilen Hayriye Hanım telaşa düştü.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11938, 'telaşa gelmek', 'bir iş telaş sırasında yapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11939, 'telaşa vermek', 'davranış ve hareketleriyle çevresindekileri heyecana, aceleye, sıkıntıya sokmak: ''Bir münasebetsizin denizde boğulma taklidi yaparak vapuru telaşa verdiğini uzun uzun anlatmıştım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11940, 'telef etmek', '1) hayvanı öldürmek; 2) mec. mahvetmek, yok etmek: ''Gönlümü gönlümü cahil gönlümü / Bir güzele telef ettim ömrümü'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11941, 'telef olmak', '1) hayvan, ölmek; 2) mec. mahvolmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11942, 'telefon etmek (açmak)', 'birini telefonla aramak ve bir şey söylemek: ''Siz gelmeyin, ben telefon eder, gelirim.'' -A. H. Tanpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11943, 'teleme peyniri gibi', 'tombul ve beyaz tenli (kadın).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11944, 'telgraf çekmek', 'telgrafla haber göndermek, tellemek: ''İstanbul''a telgraf çekip para getirtmekten başka çare yoktu.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11945, 'teli kırmak', 'bağlı bulunduğu kuruluşlarla ilişkisini kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11946, 'tellal çağırtmak', 'bir haber, bir istek vb.ni tellal aracılığıyla duyurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11947, 'teller takmak', ' alay sevincini aşırı davranışlarla gösterenler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11948, 'telleyip pullamak', '1) birçok süsle süslemek; 2) mec. değerinden çok övmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11949, 'telvis etmek', 'kirletmek, pisletmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11950, 'temas kurmak', 'ilişkiye geçmek, bağlantı sağlamak: ''Adam buraya kadar geldiği hâlde acaba neden kendisiyle temas kurmuyordu?'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11951, 'temasa geçmek', 'arada bir bağlantı kurmak, görüşme yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11952, 'temasa gelmek', 'buluşup görüşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11953, 'tembele dediler ''kapını ört'', dedi ''yel eser örter''', 'tembel, kapısının örtülmesini bile rüzgârdan bekler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11954, 'tembele iş buyur, sana akıl öğretsin', 'tembel, kendisine buyurulan işi yapmamak için ya onun yapılmasına ihtiyaç bulunmadığını söyler ya da buyurulan biçimde değil, kendisinin işine gelen biçimde yapmayı önerir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11955, 'tembelliği tutmak', 'tembelleşmek: ''Lâmi''nin bazı tembelliği tutuyor, öğleye kadar evden çıkmıyor.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11956, 'tembihatta bulunmak', 'uyarmak: ''Her türlü istirahatiniz temin edilsin diye de bu ağalara tembihatta bulundu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11957, 'temcit pilavı gibi (ısıtıp ısıtıp öne sürmek)', 'bir şeyi birçok kez tekrarlamak: ''Bu gerekçeyi tam beş yıldır temcit pilavı gibi ev sahibinin önüne koyuyordu.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11958, 'temel atmak', '1) bir yapının temellerini yapmaya başlamak; 2) herhangi bir işe başlamak, girişmek, bir şeyin gelişmesine, büyümesine sebep olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11959, 'temel tutmak', '1) temelin kazılacağı zemin sağlam olmak; 2) sürüp gidecek bir duruma gelmek, kökleşmek, yerleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11960, 'temennide bulunmak', 'dilemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11961, 'temerrüde düşmek', ' huk. ödenmesi hâlâ mümkün olan borcu ödememekte direnmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11962, 'teminat altına almak', 'güvence altına almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11963, 'temiz bir dayak atmak', 'adamakıllı dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11964, 'temiz bir dayak yemek', 'adamakıllı dayak yemek: ''Yaramazlığı bir yana, çocukları da azdırdığı için temiz bir dayak yemiş olmalı.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11965, 'temiz iş altı ayda çıkar', 'doğru dürüst yapılması istenen iş uzun zaman ister.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11966, 'temiz tutmak', 'bir şeyi kirletmeden, bozmadan kullanma, temiz olmasına özen göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11967, 'temize çekmek', 'bir yazının karalamasını temiz olarak yazmak: ''Bizim yazarımız temize çektikten sonra romanı elinde dolaşır dururdu kapı kapı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11968, 'temize çıkmak', ' huk. aklanmak: ''Gazete kendi evin, temize çıktığın gün gelmezsen küserim bak.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11969, 'temize havale etmek', '1) uzayıp giden bir işi bitirivermek; 2) yiyeceği yiyip bitirmek; 3) argo kısa yoldan çözümlemek, çabucak bitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11970, 'temizlik yapmak', '1) temizlemek; 2) mec. öldürmek: ''Ovadaki İslam köylerinde nasıl temizlik yapılacağını müzakereye koyuldular.'' -Ö. Seyfettin. 3) mec. zararlı şeyleri yok etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11971, 'tempo tutmak', 'el çırparak veya el ve ayaklarını bir yere vurarak bir müziğe eşlik etmek: ''Parmaklarımızla masanın tahtasında tempo tutuyoruz.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11972, 'temsilde hata olmaz', 'yeri geldiği zaman çirkin, kaba bir benzetme ile anlatıma daha etkili bir hava verilmesi saygısızca bir davranış değildir, kimse bundan alınmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11973, 'temyize gitmek', 'mahkemelerce verilen kararın kanun ve usul yönünden incelenmesi için Yargıtaya başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11974, 'tenakuza düşmek', 'birbiriyle çelişen sözler söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11975, 'tencere (çömlek) demiş ''dibim altın'', kaşık (kepçe) demiş ben neredeyim?'' (girdim, çıktım)', 'içyüzünü iyi bilen kimseye karşı, kusurlarını gizlemeye çalışan ve yüksek nitelikleri bulunduğunu söyleyerek övünmeye kalkışan kişi, gülünç duruma düşer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11976, 'tencere dibin kara, seninki benden kara', 'kötülük, kusur yönünden sen, benden daha betersin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11977, 'tencere tava, herkeste bir hava', 'herkes kendi bildiği gibi davranıyor, ortada düşünce birliği kalmamış.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11978, 'tencere tencereye ''yüzün kara'' demiş, çömlek utancından yere geçmiş', 'kötülük, kusur yönünden sen, benden daha betersin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11979, 'tencere yuvarlanmış, kapağını bulmuş', 'hoşa gitmeyen herhangi bir nitelik yönünden birbiriyle benzeşen iki kişi bir araya gelmiş.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11980, 'tencerede pişirip kapağında yemek', 'geçinme konusunda var olanla yetinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11981, 'tenceresi kaynarken, maymunu oynarken', 'geçimi yolunda, keyfi yerindeyken.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11982, 'tenceresi (tencereleri) kaynamak', 'geçimleri az çok yerinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11983, 'teneffüs etmek', 'soluk almak: ''Sanki teneffüs ettiği havayı kollayan bir tilki gibi tetikte, sihirli ve hamarat görünürdü.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11984, 'tenha kalmak', '1) ıssızlaşmak: ''Onlar gittikten sonra her yer tenha kaldı.'' -M. Ş. Esendal. 2) esk. yalnız kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11985, 'tepeden tırnağa süzmek', 'herhangi bir sebeple birine dikkatlice bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11986, 'tepesi aşağı gitmek', 'işleri bozulup büyük zarara uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11987, 'tepesi atmak', 'birdenbire öfkeye kapılmak, öfkelenmek: ''O sırada babalığını anımsıyordu kötü bir düşü anımsarcasına ve kızgınlıktan tepesi atıyordu.'' -M. Uyguner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11988, 'tepesi üstü', 'başı yere gelmek üzere, tepetakla.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11989, 'tepesinde değirmen çevirmek', 'tepesinde havan dövmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11990, 'tepesinde havan dövmek', 'üst katta oturan biri, gürültü yaparak alt kattakini rahatsız etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11991, 'tepesinden kaynar sular dökülmek', 'başından aşağı kaynar sular dökülmek: ''Nazmiye''nin tepesinden sanki kaynar sular döküldü, yooo ... dedi.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11992, 'tepesinin tası atmak', 'birdenbire çok sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11993, 'tepetakla etmek (devirmek)', 'birinin toplumsal veya ekonomik durumunu bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11994, 'tepetakla gitmek (yuvarlanmak)', 'hızlı bir biçimde toplumsal ve ekonomik durumu bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11995, 'tepki çekmek', 'olumsuz, sert bir eleştiriyle karşı karşıya kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11996, 'tepki duymak', 'bir olay veya durum karşısındaki düşüncesini söz veya davranışla belirtmek: ''Eski alışkanlıkların yanı sıra genel yaşantıya tepki duymuşlardı.'' -C. Külebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11997, 'tepki göstermek', 'bir olay veya durum karşısındaki düşüncesini söz veya davranışla belirtmek: ''Önemli olan, tepki gösterdiğimiz şeyden kurtulmanın yoluna bakmak, bu yolu bulmaktır.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11998, 'tepki koymak', 'bir düşünce veya harekete karşı çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (11999, 'tepki vermek', 'herhangi bir etkiye karşı söz veya davranışla karşılık vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12000, 'tepkide bulunmak', 'tepki göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12001, 'tepkimeye girmek', ' kim. bir cisim etkisi altında kaldığı bir şeye karşı tepki vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12002, 'teptim keçe oldu, sivrilttim külah oldu', 'bir şeyi işine geldiği gibi gösterenler veya yorumlayanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12003, 'ter alıştırmak', 'terinin biraz kurumasını beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12004, 'ter atmak', 'vücudu rahatlatmak amacıyla aşırı derecede terlemek: Göbek taşında ter atarken bunaldı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12005, 'ter basmak', 'çok terlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12006, 'ter boşanmak', 'çok terlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12007, 'ter dökmek', '1) çok terlemek; 2) mec. bir iş yapmak için zahmet çekmek: ''Erenköy yollarına ne kadar ter döktüğümü bilemezsin.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12008, 'ter ter tepinmek', 'direnmek, istememek, inat etmek, sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12009, 'terakki göstermek', 'geliştiğini, ilerlediğini ortaya koymak: ''Hususi surette aldığı şan derslerinde büyük terakkiler göstermiş.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12010, 'terazi tartıyla, her şey vaktiyle', 'her şeyin bir ölçüsü ve zamanı vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12011, 'terazi var, tartı var, her şeyin bir vakti var', 'her şeyin bir ölçüsü ve zamanı vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12012, 'teraziye vurmak', 'iyice tartarak düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12013, 'terbiyeli maymun gibi', 'çok saygılı, çekingen, itaatkâr.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12014, 'terbiyesini bozmak', 'terbiyesizlik etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12015, 'tercüman olmak', 'başkasının düşüncesini ve duygusunu bildirmek, dile getirmek, anlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12016, 'tereciye tere satmak', 'birine çok iyi bildiği bir şeyi öğretmeye kalkmak: ''Edebiyat dünyamız tereciye tere satmaya kalkışan sahte şöhretlere, üçkâğıtçılara kısa bir zaman için katlanıyor.'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12017, 'tereyağı gibi', 'çok yumuşak (elma, armut).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12018, 'tereyağından kıl çeker gibi', '1) her türlü mecburiyetten, mükellefiyetten ve sorumluluktan kolayca sıyrılarak: Tereyağından kıl çeker gibi bu belalı işten sıyrıldı. 2) bir işi kolayca yaparak, becerikli bir biçimde: ''Mehmetlerin askerde dev yapılı kamyonları tereyağından kıl çeker gibi çekip çevirdiklerini seyretmeye doyamazdım.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12019, 'terini soğutmak', 'terinin kurumasını bekleyerek dinlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12020, 'terlemeden para kazanılmaz, solumadan can verilmez', 'hiçbir emek harcanmadan para kazanılması mümkün değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12021, 'ters anlamak', 'yanlış yorumlamak, doğru anlam vermemek: ''Hegel''in bu sözünü ters anlamamak gerek.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12022, 'ters pers olmak', '1) yüzükoyun düşmek; 2) mec. fena hâlde bozulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12023, 'ters tarafından kalkmak', 'sol tarafından kalkmak: ''Hacı Ömer''in o gün ters tarafından kalktığına artık şüphe yoktu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12024, 'ters ters bakmak', 'düşmanca ve öfkeli bir biçimde bakmak: ''Nöbetçi, ustanın anasına ters ters baktı.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12025, 'ters yüz çevirmek', 'ters yüzüne çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12026, 'ters yüz dönmek', 'ters yüzüne dönmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12027, 'ters yüz etmek', '1) bir süre kullanılmış olan giysilerin içini dışına çevirmek; 2) mec. değiştirmek: ''Erkeklik, yiğitlik kavramının ters yüz edilmesi irdelenmedi.'' -A. Ağaoğlu. 3) mec. işleri bozmak; 4) mec. şüpheli duruma sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12028, 'ters yüz geri dönmek', 'gittiği bir yerden istediğini elde edemeden dönmek: ''Bugün hava çok sıcak, başka bir zamana bıraksak olmaz mı? -Olur ya, neden olmasın deyip köyün arka tarafından ters yüz geri döndük.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12029, 'ters yüzü geri dönmek', 'gerisin geriye gitmek: ''İçeriye bir adım atmaya cesaret edememiş, koşarak ters yüzü geri dönmüştüm.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12030, 'ters yüzüne çevirmek', 'geri döndürmek: ''Yanına uşak filan almaz. Müracaat edenleri ters yüzüne çevirir.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12031, 'ters yüzüne dönmek', 'geri gitmek, geri dönüp gitmek: ''Nezih, önce ters yüzüne dönüp eğri büğrü yollardan kaçmayı düşündü.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12032, 'tersi dönmek', 'şaşırıp bulunduğu ve gideceği yeri kestirememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12033, 'tersinden okumak', '1) yanlış anlamak; 2) olayı veya bir sanat eserini farklı biçimde değerlendirmek, yorumlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12034, 'tersine çevirmek', 'içini dışına çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12035, 'tertibat almak', 'olacağı düşünülen sakıncalı bir duruma, harekete karşı hazırlık yapmak: ''Yüz sandık cephaneyi Anadolu''ya gönderebilmek için müzakere edip tertibat aldıklarını veya depolardan silah kaçırdıklarını söylüyorlardı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12036, 'tertibe düşürmek', 'zarar verici bir eyleme, komploya uğratmak: ''Eski arkadaşının bir tertibe düşürüldüğünü sanmakla yanılmış.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12037, 'terzi kendi söküğünü dikemez', 'insanlar başkalarına yaptıkları hizmetleri kendilerine yapamazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12038, 'terzinin işi kötü, ayıbını örten ütü', 'kişi, olumsuz yanlarını gizlemeyi bilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12039, 'terziye ''dinlen'' demişler, ayağa kalkmış', 'rahat görünen öyle işler vardır ki onunla uğraşanların dinlenmesi, kimileri için yorucu olan davranışlarla olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12040, 'terziye ''göç'' demişler, ''iğnem başımda'' (yanımda) demiş', 'kendisine gerekli olan şeyler kolay taşınır olan kimsenin bir yerden başka yere göçmesi daha kolaydır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12041, 'teselli bulmak', 'avunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12042, 'teselli etmek (vermek)', 'avutmak, avundurmak: ''Öyle ağlıyor ki ben de içimi çeke çeke onu teselli etmeye çalışıyorum.'' -A. Ağaoğlu. ''Zehra Hanım, Tevfik''in ebesiydi ve onu çok severdi, arkasını sıvadı, teselli verdi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12043, 'tesir bırakmak', 'etki bırakmak: ''Bu sözler derin ve kuvvetli bir tesir bıraktı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12044, 'tesirini göstermek', 'etkisini göstermek: ''Bir hastaya tatbik edilen serum derhâl şifalı tesirini göstermiştir.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12045, 'teslim almak', '1) teslim edilen bir şeyi almak; 2) tutsak almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12046, 'teslim bayrağı çekmek', '1) yenilgiyi kabul etmek; 2) çekişme sonunda, karşısındakinin istediğini yapmaya razı olduğunu bildirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12047, 'teslimiyet göstermek', 'birinin isteğini olduğu gibi kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12048, 'tesmiye etmek', 'adlandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12049, 'tespih çekmek', '1) tespihin tanelerini birer birer iki parmak arasından geçirmek: ''Kimisi bağdaş kurmuş, tespih çekiyor, kimisi diz çökmüş Kur''an okuyor.'' -R. H. Karay. 2) Allah''ın adını zikrederek ibadet etmek; 3) tespih tanelerini çeşitli maddelerden imal etmek veya aynı boyda düzenlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12050, 'tespihe dizer gibi dizmek', ' sp. futbolda, rakip takımın oyuncuları arasından birer birer geçip gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12051, 'testi gibi', 'büyük ve sarkık (meme).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12052, 'testi kırılsa da kulpu elde kalır', 'zarar da etse varlıklı bir kimse büsbütün yoksul kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12053, 'testiyi kıran da bir, suyu getiren de', 'görevini iyi yapanla kötüye kullanan arasında bir fark gözetilmemektedir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12054, 'teşbihte hata olmaz (olmasın)', 'yeri geldiği zaman çirkin, kaba bir benzetme ile anlatıma daha etkili bir hava verilmesi saygısızca bir davranış değildir, kimse bundan alınmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12055, 'teşebbüse geçmek', 'bir işi yapmak için davranmak, girişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12056, 'teşebbüsü ele almak', 'öne atılıp bir işi yönetmeye başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12057, 'teşvik etmek', '1) isteklendirmek, özendirmek: ''Kasketi yıpranmış bir ihtiyar programı övüyor, halkı teşvik ediyordu.'' -H. E. Adıvar. 2) mec. bir kimseyi kötü bir iş yapması için kandırmak, kışkırtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12058, 'tetiğe basmak (dokunmak)', 'ateş etmek: ''Tüfeği geze aldım, ses toprağa yakın geliyordu. Porsuktur sandım, tetiğe dokundum.'' -M. Ş. Esendal. ''Şimdi tetiğe bassam hiç doğmamışa dönersin.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12059, 'tetiği çekmek', 'tetiğe basmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12060, 'tetiğini bozmamak', 'soğukkanlılığını bozmamak, telaş göstermeyerek durumunu değiştirmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12061, 'tetik bulunmak', 'tetikte bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12062, 'tetik durmak', 'hazır ve uyanık bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12063, 'tetik üstünde beklemek', 'hazır, dikkatli, uyanık bulunmak, tetikte olmak: ''Kimisi dönmeye başlamış bile kimisi tetik üstünde bekliyor.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12064, 'tetikte (tetik) olmak (beklemek, bulunmak, durmak)', 'her an uyanık ve hazır bulunmak: ''Güldane tehlikeyi sezmiş gibi tetikte.'' -T. Buğra. ''Onun sakinliği etrafta tetikte bekleyen karısına, çocuklarına da geçti.'' -N. Cumalı. ''Hele kendini güçlü hissederse tetik ol, basbayağı saldırganlaşır.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12065, 'tevdiatta bulunmak', 'para yatırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12066, 'teveccüh göstermek', 'güler yüz göstermek: ''Gösterilen bu teveccüh tamamen hasbi mi idi?'' -K. Bilbaşar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12067, 'tevekkelin gemisi batmaz (eşeğini kurt yemez)', 'gereken tedbirleri aldıktan sonra daha fazla titizlik göstermeyip sonucu Tanrının dileğine bırakan kimse rahat eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12068, 'tevil götürmek', 'söz veya davranışa başka bir anlam verebilmek: ''Ne kadar inkâr etse hırsızlığı tevil götürmüyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12069, 'teyakkuza geçmek', 'dikkatli ve tetikte olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12070, 'teyze, ana yarısıdır', 'teyze, bir kimseye annesi gibi sevgi, şefkat gösterir, onunla yakından ilgilenir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12071, 'tezada düşmek', 'bir sözü öbürünü tutmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12072, 'tezgâh açmak', 'seyyar satıcı, herhangi bir yere tezgâhını kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12073, 'tezgâhı kurmak', '1) işe başlamak üzere çalışma araçlarını hazırlamak, çalışmaya başlamak; 2) argo yasal olmayan bir işi gerçekleştirebilmek için yalan dolanla aldatmaya, kandırmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12074, 'tezgâhtarlık etmek', 'bir şeyi beğendirmeye çalışmak için fazlaca konuşmak, lüzumsuz yere övmek, methetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12075, 'tezkere almak', ' ask. askerlik görevini tamamlayarak bunu bildiren bir belge almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12076, 'tezkere bırakmak', ' ask. askerlik görevini bitirdiği hâlde orduda çalışmasını sürdürmek, orduda kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12077, 'tezkeresini eline vermek', 'işine son vermek, kovmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12078, 'tezkiyesini düzeltmek', 'ahlakça kötü tanınmışken durumunu düzeltmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12079, 'tezvir çıkarmak', 'birisi hakkında kovculuk etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12080, 'tığ gibi', 'ince, zayıf, sağlam ve çevik (kimse): ''Böyle kibar, yakışıklı, tığ gibi kocayı rüyada görsen inanmazdın.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12081, 'tıka basa doldurmak', 'doldururken çok bastırıp sıkıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12082, 'tıka basa yemek', 'mideye sıkıntı verecek kadar çok yemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12083, 'tıkırı yolunda olmak (gitmek)', 'varlıklı olmak, hâli vakti yerinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12084, 'tıkırında gitmek (olmak, yürümek)', 'işler yolunda ve düzenli gitmek: ''Kimi zaman da her şeyin tıkırında gittiğini düşünüp, haydi bir gece daha yaşasınlar, diyorum.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12085, 'tıkırını yoluna koymak', 'geçim düzenini iyi olarak sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12086, 'tıknefes olmak', 'nefesi tıkanmak, nefes darlığı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12087, 'tıngır elek tıngır saç, elim hamur karnım aç', 'çalışmalarımla başkalarına yarar sağlıyorum ancak bundan kendim yararlanmıyorum.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12088, 'tıpış tıpış yürümek', '1) kısa adımlarla çabuk yürümek; 2) mec. ister istemez bir yere gitmek veya bir yerden ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12089, 'tıraş etmek', '1) tıraş işini yapmak; 2) argo bıkkınlık verecek kadar uzun konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12090, 'tıraşa tutmak', ' argo birini bıkkınlık verici uzun konuşmalarla oyalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12091, 'tıraşı gelmek (uzamak)', 'saçı, sakalı tıraş edilecek duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12092, 'tırhallı, hep bir hâlli', 'aynı şartlar altında bulunanların aynı durumda olduklarını anlatmak için söylenen', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12093, 'tırıs gitmek', 'koşmaya yakın hızlı yürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12094, 'tırnağın varsa başını kaşı', 'hiç kimse başkasından yardım beklememeli; kendisinin olanakları varsa bir işe girişmeli, yoksa girişmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12095, 'tırnak göstermek', 'korkutmak, gözdağı vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12096, 'tırnak kadar', '1) çok küçük; 2) çok az.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12097, 'tırnak sürüştürmek', 'kavgayı körüklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12098, 'tırnak takmak', 'kötülük yapmak için bahane aramak: ''İş karıştırmak için de ilkin belediyeye tırnak takarlar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12099, 'tırpan atmak', 'tırpanlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12100, 'tırpandan geçirmek', 'tırpanlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12101, 'tırtıl çekmek', 'henüz yumuşak olan bir parçayı metal bir tırtılla süslemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12102, 'tırtıl kesmek', 'bir şeyin yanlarını diş diş kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12103, 'tıs kesilmek', 'sessiz kalmak: ''Neyzen hicaz bir taksime başladı; bu koca yeri dolduran yüzlerce insan birden tıs kesildi.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12104, 'tıs yok', 'bir yerde hiç ses olmadığını belirtmek için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12105, 'tilki gibi', 'kurnaz (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12106, 'tilki tilkiliğini anlatıncaya kadar post elden gider', 'bir gerçeği anlatıncaya kadar çoğu kez başa gelmedik şey kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12107, 'tilki uykusuna vermek', 'uyuyormuş gibi yaparak fırsat kollamak: ''Muzafferiyeti sonuna kadar yudum yudum içebilmek için kendimi tilki uykusuna verdim.'' -F. Celâlettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12108, 'tilki uykusuna yatmak', 'uyuyormuş gibi yaparak fırsat kollamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12109, 'tilkinin dönüp dolaşıp geleceği yer kürkçü dükkânıdır', 'bir kişi ne kadar farklı yerlerde yaşarsa yaşasın, ne kadar farklı işlerle uğraşırsa uğraşsın, bağlı bulunduğu çevreye veya işe dönmek zorunda kalır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12110, 'tilkiye ''tavuk kebabı yer misin?'' demişler, ''adamın güleceğini getiriyorsunuz'' demiş', 'bir kimseye çok özlediği hâlde elde edemediği bir şeyi ''ister misin?'' diye sorulmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12111, 'timsal olmak', 'simge durumuna gelmek: ''Biz, Türkler, bütün tarihî hayatımızca hürriyet ve istiklale timsal olmuş bir milletiz!'' -Atatürk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12112, 'tir tir titremek', '1) \tçok üşümek; 2) \tmec. çok korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12113, 'tiridi çıkmak', 'iyice ihtiyarlamak, çok yaşlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12114, 'tirit gibi', 'yerinden kımıldayamayacak kadar ihtiyar (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12115, 'titizlik göstermek', 'titizlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12116, 'titreme gelmek', 'titremeye başlamak, titremeye tutulmak: ''Başına küçük bir ağrı, vücuduna hafif bir titreme geliyor.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12117, 'tiye almak', ' argo biriyle alay etmek, eğlenmek: ''O günkü ne oldum delisi yeni zenginleri, özenti aydınları tiye alıyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12118, 'tohuma kaçmak', ' şaka 1) üreme veya üretme gücü kalmamak; 2) yaşlanmak, evlenme çağı geçip kartlaşmak: ''Şimdi saçlarının tepeden döküldüğüne bakarak üzülüyor, arada bir, tohuma kaçıyoruz artık diye hayıflandığı da oluyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12119, 'tohumu dökülmek', 'geçirdiği büyük bir korku dolayısıyla dölden kesilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12120, 'tok açın hâlinden bilmez', 'varlıklı olan, yoksulun ne denli sıkıntı içinde bulunduğunu bilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12121, 'tok ağırlaması (ağırlamak) güçtür (güç olur)', 'karnı tok olanlara yemek beğendirmek kolay değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12122, 'tok evin aç kedisi', 'gereksinimi olmadığı hâlde açgözlülük eden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12123, 'tok iken yemek yiyen, mezarını dişiyle kazar', 'tok karnına yemek yemek, sağlık için çok zararlıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12124, 'tok tutmak', 'açlığı uzun süre giderme veya doyurma özelliği olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12125, 'toka etmek', '1) el sıkışmak: ''Çımacı İzzet''le iki ellerini birbirine uzatarak bayramlaşır gibi toka ederler.'' -S. F. Abasıyanık. 2) den. karşılıklı iki parçayı getirip birbirine dayamak; 3) kadeh tokuşturmak; 4) argo vermek: ''Bin beş yüzü toka edip yarın halıyı çekeceksin.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12126, 'tokat aşk etmek (eylemek)', 'hızla vurmak: ''Sandalyeyi elinden alıp iki tokat aşk etti.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12127, 'tokat atmak (patlatmak)', '1) el içi ile vurmak; 2) argo dolandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12128, 'tokat (tokadı) yemek', '1) kendine tokat vurulmak: ''Senin minimini elinden yediğim tokadın acısını yüzümde kıyamete kadar duyacağım.'' -A. N. Asya. 2) mec. yenilgiye uğramak: ''Acısını unutamayacakları bir tokat yediler halktan.'' -N. Cumalı. 3) argo dolandırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12129, 'tokmak gibi', 'tıkız etli.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12130, 'tombala çekmek', 'tombala oynamak: ''Komşularınızda ya da dostlarınızda konken oynadınız, tombala çektiniz.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12131, 'tomruğa atmak', 'tutukevine koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12132, 'tomruğa vermek', 'işkence aracına suçlunun ayaklarını geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12133, 'tongaya basmak (düşmek)', 'kendisini kötü bir duruma düşürmek için hazırlanan bir düzene uğramak, tuzağa düşmek: ''Fakat insan salim kafayla bir dakika düşündü mü tongaya bastığını anlar.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12134, 'top atmak', ' argo 1) batkınlığa uğramak, iflas etmek; 2) sınıfta kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12135, 'top etmek', 'bir şeyi yığın durumuna getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12136, 'top gibi gürlemek', 'gürültülü bir biçimde bağırmak veya konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12137, 'top gibi patlamak', 'birden gelen şaşırtıcı ve ürkütücü haber duyulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12138, 'top otu beylikten olunca, güllesi Bağdat''a gider', 'yararlandığı gereci bedava elde eden ve bol bol kullananla yarışılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12139, 'top sürmek', ' sp. kısa vuruşlarla, topu kaçırmadan karşı takımın kalesine veya potasına doğru götürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12140, 'top yapmak', ' sp. topu rakibe kaptırmadan takım oyuncuları arasında dolaştırmak, topa daha uzun süre sahip olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12141, 'top yuvarlaktır', ' sp. ''karşılaşma bitmeden sonuç belli olmaz, değişebilir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12142, 'topa çıkmak', ' sp. rakibin topu rahatça kullanmasına engel olmak için topa hamle etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12143, 'topa tutmak', '1) üzerine topla ateş etmek: ''İngilizlerin topa tuttuğu yerlere gidip bir saat kadar muhtelif çapta birçok mermi ölçtüm.'' -F. R. Atay. 2) mec. kötü eleştiri amaçlı saldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12144, 'topaç gibi', 'vücutça toplu ve sağlıklı (çocuk).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12145, 'topal eşekle kervana katılmak (karışmak)', ' tkz. yetkisi ve yeteneği olmadığı hâlde önemli bir işe katılmaya yeltenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12146, 'topalla gezen, aksamak öğrenir', 'kötü insanlarla düşüp kalkanlar, onlardan kötü huy kaparlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12147, 'toprağa bakmak', 'ölümü yakın görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12148, 'toprağa düşmek', 'ölüp gömülmek: ''Bu sabah hesap ettim, küçüğüm toprağa düşeli tam yetmiş üç gece olmuş.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12149, 'toprağa vermek', 'ölüyü gömmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12150, 'toprağı bol olsun', 'Müslüman olmayanlar için ''ruhu sükûn içinde olsun'' anlamında söylenen bir söz: ''On dakikaya kalmadan adamcağız sizlere ömür! -Toprağı bol olsun diyeceksiniz.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12151, 'toprağı çekmiş', 'sürekli olarak yaşadığı yerden kısa bir süre kalmak üzere gittiği başka bir yerde ölenler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12152, 'toprağı işleyen, ekmeği dişler', 'işini yapması gerektiği biçimde yapan kişi, çalışmasının verimlerinden yararlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12153, 'toprağına ağır gelmesin', 'bir ölünün aleyhinde konuşulduğunda kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12154, 'toprak çekmek', '1) bir yerdeki toprağı başka bir yere taşımak; 2) mec. ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12155, 'toprak doyursun gözünü', 'gözünü toprak doyursun.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12156, 'toprak olmak', '1) ölümünün üzerinden çok zaman geçtiği için artık çürümüş olmak, toprağa karışmış olmak; 2) ölmek: ''Boş saatlerde, şimdi ikisi de toprak olan iki dostumla sanat tartışmaları yapıyorduk.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12157, 'toprak paklar', '''bir kimsenin yaptığı kötülükler ancak ölmesiyle son bulur'' anlamına kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12158, 'topu atmak', ' argo 1) iflas etmek: ''Biz kim oluyoruz ki veresiye verelim, iki günde topu atarız.'' -M. Ş. Esendal. 2) sınıfta kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12159, 'topu dikmek', '1) topu ayakla hızlı bir biçimde havaya doğru atmak; 2) mec. ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12160, 'topu taca atmak (bırakmak)', '1) sp. karşılaşmada topu yan çizgi dışına çıkarmak: ''Dündar koşmuyor ve topu taca bırakıyor.'' -A. İlhan. 2) mec. konuşulan konuyu saptırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12161, 'topuk çalmak', 'yürürken ayakların iç kemikleri birbirine çarpmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12162, 'topuk kapmak', 'dalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12163, 'topuk vurmak', ' ask. selamlamadan önce ayak topuklarını yan yana getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12164, 'topuz gibi', 'kısa ve tıknaz (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12165, 'torbaya koymak', ' tkz. sağlamak, elde etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12166, 'tornistan etmek', '1) den. gemi geri dönmek; 2) hlk. bir giyeceği ters yüz etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12167, 'tortop olmak', 'top biçimine girmek: ''Çocuklar köşede bir hasırın üstünde tortop olmuşlardı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12168, 'torun tosun (torba) sahibi olmak', '1) torunu olmak; 2) yaşlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12169, 'tos vurmak', 'alın veya boynuzla vurmak, süsmek: Bir tos vurduğu gibi kapıyı darmadağın ediyor.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12170, 'tosun gibi', 'tıknazca ve gürbüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12171, 'toz almak', 'bir yerin tozunu temizlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12172, 'toz etmek', 'ezip harap etmek, ortadan kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12173, 'toz kondurmamak', 'bir şeyde herhangi bir kusurun varlığını kabul etmemek, bir şeyi kusursuz göstermek: ''Toz kondurmadığı devletinin kendisini hatırlamasını sabırla bekliyor.'' -E. Atasü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12174, 'toz koparmak', 'toz kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12175, 'toz olmak', '1) toz durumuna gelmek; 2) argo kaybolup gitmek, kaçmak, uzaklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12176, 'tozdan dumandan ferman okunmamak', 'ortalık çok karışık olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12177, 'tozu dumana katmak', '1) ortalığı altüst etmek: ''Uzaktaki yoldan açık bir otomobilin tozu dumana katarak kasabaya geldiği görüldü.'' -H. Taner. 2) toz kaldırarak hızla gitmek veya kaçmak: ''Aliş tozu dumana katarak kulübeye seğirtirken o da arkadaşlarının birini aramaya çıktı.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12178, 'tozunu almak (atmak, silkelemek, silkmek)', '1) bir şeyi silerek tozdan temizlemek; 2) tkz. dövmek, hırpalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12179, 'töhmet altında kalmak', 'suçu işlediği düşünülmek: ''Hiçbir doktor her an intihar etmeye hazır bir psikopatın töhmeti altında kalmak istemez.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12180, 'tövbeler olsun! (tövbesi!)', 'bir kimsenin herhangi bir işten çok pişman olarak tekrarlamama kararı aldığını anlatan bir söz: ''Geyik çekti bizi kendi dağına / Tövbeler tövbesi geyik avına'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12181, 'tövbesini bozmak', 'tövbe ettiği herhangi bir işe, duruma yeniden dönmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12182, 'trafik kilitlenmek', 'herhangi bir sebepten dolayı araç yolu tıkanmak, işlemez duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12183, 'transa geçmek (girmek)', 'kendinden geçmek, içinde bulunduğu ortamdan başka bir dünyaya veya havaya geçmek: ''O garip bir transa girmişti; beni konuşturmak, nasıl aşağılık duygusu içinde kıvrandığımı işitmek istiyordu.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12184, 'transit geçmek', '1) bir yerden, dinlenmeden, beklemeden, durmadan geçmek; 2) mal gümrüksüz geçmek; 3) argo sürücü, trafik kurallarına uymamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12185, 'treni kaçırmak', 'bir şeyi elde etme, bir işi gerçekleştirme fırsatını ve imkânını yitirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12186, 'tribünlere oynamak', 'iş yapmadığı hâlde kendini iş yapıyor gibi göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12187, 'tu kaka etmek', 'hafife alıp bir kenara itmek, önem vermemek, kötülemek: ''Tu kaka edilen eserler de er geç eski tahtlarına geçer otururlar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12188, 'tu kaka olmak', 'hafife alınıp bir kenara itilmek, önem verilmemek, kötülenmek: ''Refik Halit Karay ''ve'' nin bu kadar tu kaka olmasının sebebini son zamanlarda pek yerli yersiz kullanılmaya başlamasında buluyor.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12189, 'tuğra çekmek', 'Osmanlı Devleti''nde ferman, berat ve resmî belgelere tuğra koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12190, 'tulum çıkarmak', '1) hayvanın derisini yarmadan çıkarmak; 2) çoğunluk sistemine dayalı seçimlerde bir partinin listesindeki bütün adaylar seçimi kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12191, 'tulum gibi', 'her yanı şiş, şişman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12192, 'tumba etmek', ' den. 1) sandalı, omurgası yukarı gelecek biçimde çevirmek; 2) araba veya vagonu ters çevirerek boşaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12193, 'tumturak yapmak', 'vurgulamak, önemini belirtmek, etkili olmasını sağlamak: ''Hecelerimiz de telaffuzda tumturak yapmak için lastik gibi çekilir.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12194, 'tun tun kaçmak', 'gizlice kaybolmak: ''Duyarlığı hastalık derecesinde yüksek, gerçekle bağlantısı zayıf, ürkek bir çocuktu İhsan, kavgadan gürültüden tun tun kaçıyordu.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12195, 'tundan tuna atmak', 'bir kişiyi uzaklara sürüp dolaştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12196, 'tur atlamak', 'spor karşılaşmalarında çok puan toplayarak veya kurada kazanarak bir sonraki tura katılma hakkını kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12197, 'tur atmak', '1) dolaşmak, dolaşıp gelmek, dönmek: ''Bir kaşıkçı kuşu çok yükseklerde tur atıyor.'' -H. Taner. 2) şampiyon olunca veya galip gelince takım oyuncuları seyircileri selamlayarak sahada dolaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12198, 'tur bindirmek', ' sp. 1) uzun mesafeli pist yarışlarında hızlı olanlar, yavaş olanları bir veya daha fazla tur geride bırakmak; 2) mec. üstün gelmek, fark atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12199, 'tura çıkmak', 'gezinti yapmak: ''Her ay, mehtapta bir iki kere merkeplerle tura çıkardık.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12200, 'turfa olmak', 'değerini yitirmek, çürümek: ''Turfa oldu artık eski felsefe.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12201, 'turnayı gözünden vurmak', 'umulmadık bir kazanç veya çıkar sağlama imkânı ele geçirmek: ''Ne talih varmış bunakta. Turnayı gözünden vurdu, dedi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12202, 'turp gibi', 'sağlığı yerinde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12203, 'turpun sıkısından seyreği iyidir', 'görüşmeyi, konuşmayı sıklaştırmamak doğrudur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12204, 'turşu gibi olmak', 'çok yorgun düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12205, 'turşu kurmak (yapmak)', 'turşuluk sebze veya meyveleri kavanoz, fıçı vb.ne yerleştirmek: ''Haminnenin içi sıkıldı mı mutfağa girer, turşu kurardı.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12206, 'turşu olmak', '1) yiyecek bozulmak, ekşimek; 2) mec. güçsüzleşmek, bitkinleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12207, 'turşusu çıkmak', '1) çok yorulmak: ''Bütün gün çocukların peşinde koşmaktan turşusu çıkmış ihtiyar lalanın karanlık bir köşede horladığı işitiliyordu.'' -R. N. Güntekin. 2) ezilmek, parçalanmak: Portakalların turşusu çıkmış.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12208, 'turşusunu kurmak', '''bir şeyin elden çıkarılması gerektiği hâlde buna bir türlü kıyamamak'' anlamında kınama yollu söylenen bir söz: ''Bir kısmetin çıkar çıkmaz seni vereceğiz. Turşunu kuracak değiliz ya!'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12209, 'turşuya dönmek', 'çok yorulmak, bitkinleşmek: ''Zaten yerinden kımıldanacak hâli kalmamıştı, turşuya dönmüştü ve lakin kabadayılığı da elden bırakmıyordu.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12210, 'turu geçmek', 'elemeli karşılaşmalarda bir üst tura yükselmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12211, 'tuşa getirmek', '1) güreşte hasmı sırtüstü yere sermek; 2) mec. yenmek, mağlup etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12212, 'tut kelin perçeminden', ' tkz. çözümü güçlük gösteren bir durum karşısında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12213, 'tut ki', 'varsay ki.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12214, 'tutarağı tutmak', 'huysuzluğu depreşmek, aşırı istekte bulunmak: ''Rakı tutarağı tutunca pantolonunu bile satıyor.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12215, 'tutkal gibi', 'sırnaşık ve yapışkan (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12216, 'tutkuya kapılmak', 'aşırı istek ve eğilim içinde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12217, 'tutsak düşmek', 'esir olmak, hükmü altına girmek: ''Geleneğe, ahlaki kaygılara tutsak düşüyor.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12218, 'tuttuğu altın olsun', '''her işin olumlu gitsin, refah içinde yaşa'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12219, 'tuttuğu dal elinde kalmak', 'dayandığı, güvendiği kimse veya şey önemini yitirerek işe yaramaz duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12220, 'tuttuğunu koparmak', 'becerikli olmak, giriştiği her işte başarı sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12221, 'tutulmayan hırsız beyden büyüktür', 'suçu ispatlanamayan, yakayı ele vermeyen hırsız, böyle bir suç işlemesi düşünülmeyen kimselerle dürüstlük yarışı içinde olabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12222, 'tutulmayan uğru, beyden doğru', 'suçu ispatlanamayan, yakayı ele vermeyen hırsız, böyle bir suç işlemesi düşünülmeyen kimselerle dürüstlük yarışı içinde olabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12223, 'tutunacak bir dal aramak', 'güvenilecek, dayanılacak bir insana ihtiyaç duymak: ''Yaşamının boşluğundan nasıl sıkıldığını, tutunacak bir dal aradığını ama bulamadığını anlatır.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12224, 'tutunacak dalı olmak', 'güveneceği bir kimse veya şey bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12225, 'tutuya bırakmak (koymak)', 'ödünç para almak için değerli bir şeyi rehin olarak vermek, rehin vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12226, 'tuvalet (tuvaletini) yapmak', 'sidik veya dışkıyı vücuttan dışarı atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12227, 'tuz buz olmak', 'tuzla buz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12228, 'tuz ekmek hakkını bilmeyen kör olur', 'iyiliğini gördüğü, ekmeğini yediği kimseye karşı saygısızlık ve hainlik eden kişinin sonu iyi olmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12229, 'tuzağa düşmek', 'birileri tarafından hazırlanan kötü bir duruma uğramak, oyuna gelmek: ''Sana bir tuzak kursak sen o tuzağa düşmezsin ey oğul!'' -S. Çokum.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12230, 'tuzak kurmak', '1) bir şeyi yakalamak için düzenek hazırlamak; 2) mec. birini güç ve tehlikeli bir duruma düşürmek için düzen hazırlamak, komplo kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12231, 'tuzla buz etmek', 'cam türünden şeyleri onarılmayacak biçimde kırmak, paramparça etmek: ''Kaldırdığı gibi pekmez çömleğini vurmuş yere, tuz buz etmiş.'' -R. Ilgaz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12232, 'tuzla buz olmak', 'cam türünden şeyler onarılamayacak biçimde kırılmak, dağılmak, paramparça olmak: ''Küçük votka kadehleri, mermi ıslıklarıyla aynalara çarpıp tuzla buz oluyorlar.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12233, 'tuzlayayım da kokmayasın (kokma)', ' hlk. birine, düşüncesinde aldandığını ve aklının bir şeye ermediğini anlatmak için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12234, 'tuzluya mal olmak (oturmak veya patlamak)', 'çok para vererek satın almak, çok pahalı gelmek: ''Bu eğlenti bize biraz tuzluya mal oldu.'' -E. E. Talu. ''... kendisine tuzluya patlamıştı.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12235, 'tüccar züğürtleyince eski defterleri karıştırır', '1) tüccar züğürtleyince, belki bir kimsede alacağım kalmıştır diye eski defterlerini gözden geçirir; 2) vaktiyle önemli işler yapmış olanlar, düşkünlüklerinde eski durumlarını anarak, anlatarak avunmaya çalışırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12236, 'tüccar züğürtleyince geçmiş defterleri yoklar', '1) tüccar züğürtleyince, belki bir kimsede alacağım kalmıştır diye eski defterlerini gözden geçirir; 2) vaktiyle önemli işler yapmış olanlar, düşkünlüklerinde eski durumlarını anarak, anlatarak avunmaya çalışırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12237, 'tüfek atmak', 'tüfekle ateş etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12238, 'tüfek çatmak', ' ask. askerlerin dinlenme sırasında tüfeklerini, dipçikleri üzerinde üçerli olarak birbirine dayamak: ''Tüfekleri çatar çatmaz ordayım.'' -B. S. Erdoğan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12239, 'tükürdüğünü yalamak', ' tkz. verdiği sözden benliğini küçülterek geri dönmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12240, 'tükürüğünü yutmak', 'imrenip ağzı sulanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12241, 'tünel geçmek', ' argo aklını yaptığı işe vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12242, 'tünelin sonunda ışık görünmek', 'sıkıntılı durumdan kurtulmak için çare belirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12243, 'türbülansa girmek', 'çalkantılı hava içerisinde güçlükle yol almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12244, 'Türk karır, kılıcı karımaz', 'Türk ihtiyarlığında bile genç gibi kılıç kullanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12245, 'türkü çağırmak', 'türkü söylemek: ''Türkü çağırmak şöyle dursun, konuşamıyorduk bile.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12246, 'türkü söylemek', 'ezgisiyle bir türküyü seslendirmek: ''İçeride bir yandan türkü söylüyor, bir yandan da iş yapıyordum.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12247, 'türkü tutturmak', 'türkü söylemek: ''Dikişine başlarken güzel bir türkü tutturmuştu.'' -R. Enis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12248, 'türkü yakmak', 'türkü sözünü bestelemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12249, 'tütün içmek', 'tütünü yakıp dumanını içine çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12250, 'tütün sarmak', 'sigara kâğıdına tütün koyup sigara yapmak: ''Bir yandan tütün sararken bir yandan da köyün evlerine bakıyorum.'' -M. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12251, 'tütünü tepesinden çıkmak', 'dumanı tepesinden çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12252, 'tüy atmak', 'hayvan tüyünü değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12253, 'tüy düzmek', '1) hayvanın tüyü düzelmek; 2) tkz. iyi bir yaşayışa kavuştuğunu belirtecek biçimde güzel giyinmek: ''Akıllı kız Güner, ortaya çıkalı ne kadar oldu, daha dün bir bugün iki, baksana iyice tüyü düzmüş.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12254, 'tüy gibi', 'çok hafif.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12255, 'tüyleri ürpermek', 'kötü bir olay, soğuk, gıcıklanma vb. sebeplerle korku veya tiksinti duymak: ''Görünce tüyleri ürperir, şeytan görmüş gibi kızar.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12256, 'tüyüne dokunmamak', 'dokunacak, zarar verecek en ufak bir davranışta bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12257, 'U dönüşü yapmak', '1) yüz seksen derecelik bir dönüş yapmak; 2) mec. önceden sahip olduğu bir düşünceden farklı bir düşünceyi savunmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12258, 'ucu bucağı olmamak (görünmemek)', 'başı sonu olmamak: ''Ucu bucağı görünmeyen okyanusların karanlık dalgaları üzerinde avare yüzen bir çöp gibi yalnız.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12259, 'ucu bucağı yok (kayıp)', 'başı sonu olmayan, sınırsız, sonsuz: ''Nevin, içinde ucu bucağı kayıp bir boşluk duydu.'' -S. F. Abasıyanık. ''Bir ucu yok, bucağı yok harabedeydim / Soğuk mehtap karanlığa kefen sarardı.'' -E. B. Koryürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12260, 'ucu (herhangi birine) dokunmak', 'birine olumsuz etkisi veya zararı gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12261, 'ucu ortası belli olmamak', 'iş neresinden başlanacağı kestirilemez durumda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12262, 'ucunda (bir şey) bulunmak', 'kötü bir şeye sebep olmak: ''Ne yapalım, ucunda ölüm yok ya!'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12263, 'ucunu bulmak', 'sona erdirmek, kolayını bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12264, 'ucuz alan, pahalı alır', 'ucuz olan mal çabuk eskir, pahalıya alınmış gibi olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12265, 'ucuz atlatmak (kurtulmak)', 'zor veya tehlikeli durumdan az zararla sıyrılmak: ''Bizim tekrar tekrar dinlemeyi sevdiğimiz bu fıkrayı anlatırken o hâlâ bu işten ucuz kurtulmuş olmasının heyecanını duyardı.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12266, 'ucuz etin yahnisi yavan (tatsız) olur', 'ucuza mal olan şeyler niteliksizdir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12267, 'ucuz pahalı dememek', 'yüksek veya düşük fiyatlı olduğuna bakmamak: ''Tavşanlarını, kekliklerini ucuz pahalı demeden ilk çıkan alıcılara sattı.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12268, 'ucuza çıkmak', 'yaptırılan bir şey az masrafla elde edilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12269, 'ucuza gitmek', 'basit, kolay, önemsiz olarak değerlendirilmek: ''Tezgâhtarlık, figüranlık derken kendini satmasını bilememiş, ucuza gitmişti.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12270, 'ucuza kapatmak', ' argo ucuza almak, fırsatı değerlendirmek: ''Hem arabayı ucuza kapattım hem sağlama bağladım.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12271, 'ucuzdur vardır illeti, pahalıdır vardır hikmeti', 'ucuz şeylerin ucuzluğuna tamah etmemeli, pahalı şeylerin de pahalılığından korkulmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12272, 'uç uca gelmek', 'ancak yetişmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12273, 'uç vermek', '1) çıban baş vermek; 2) bitki bitmek, sürmek; 3) mec. ortaya çıkmak: ''Orta Çağ hurafeleri ve kafası, okullarda uç verdi.'' -A. Boysan. 4) mec. gelişme, büyüme başlangıcı göstermek: ''Günbegün artmada dert ile gamım / Uç verdi yaralar sıralandı gel'' -Bayburtlu Zihni.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12274, 'uçan kuş aç kalmaz', 'yaşam kavgası vermeyi bilen ne yapar yapar rızkını çıkarır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12275, 'uçan kuşa borcu olmak', 'pek çok kişiye borçlu olmak: ''Ben kimsenin hususi hayatına karışmayı asla sevmem ama şu Şahin Paşa, uçan kuşa borcu olduğunu herkes bilirken nasıl oluyor da kumarda bu kadar para kaybediyor.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12276, 'uçan kuştan medet ummak', 'çok sıkıntıda kalıp en ufak bir yardımın herhangi bir yerden gelmesini beklemek, sıkıntılı bir durumdan kurtulmak için her türlü çareye başvurmak: ''O birkaç gün içinde uçan kuştan medet umdum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12277, 'uçkuruna gevşek (düşkün) olmak', 'cinsel isteklerin tutkunu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12278, 'uçkuruna sağlam olmak', ' tkz. cinsel isteklerin tutkunu olmamak, namuslu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12279, 'uçup gitmek', 'kaybolmak, yok olmak: ''Sağıma baktım. İhtiyar yoktu. Güneşin ilk ziyalarıyla beraber kaybolan hayalet gibi sanki silinmiş, uçmuş gitmişti.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12280, 'uçurumun kenarından dönmek', 'büyük bir tehlikeden son anda kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12281, 'uf olmak', '1) çocuk dilinde acımak; 2) çocuk dilinde yaralanmak: ''Aman yavrum dikkat et, düşer uf olursun.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12282, 'ufak at da civcivler yesin', 'çok yalan söyleyen veya olayları abartan kişilere inandırıcı olmadığını belirtmek için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12283, 'ufku daralmak', 'ileriyi görememek, bakış açısı geniş olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12284, 'ufkunu genişletmek', 'görüş alanını genişletmek, daha geniş, daha fazla bilgi ve görüş edinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12285, 'uflayıp puflamak', 'sürekli olarak uflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12286, 'uğru, kalana yanar; mal sahibi gidene', 'aynı sonuçtan kimi yarar görür, kimi zarar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12287, 'uğruluğa gitsen, insafı elden koma', 'kötülüğün bile sınırı vardır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12288, 'uğur getirmek', 'iyilik, şans, talih, bereket getirmek: ''Emeti ile evlenmek, Satılmış''a uğur getirmişti.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12289, 'uğur ola! (uğurlar olsun!)', 'esenlikle git, yolun açık olsun! anlamında söylenen bir iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12290, 'uğurlu kademli olsun', 'mutlu bir olay dolayısıyla söylenen bir iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12291, 'uhdesinden gelmek', 'becermek, başarmak: ''Bu işi tek bir kişiye verseniz yine uhdesinden gelir çünkü yapacağı bellidir.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12292, 'uhdesine almak', 'bir işi üstüne almak, yapacağına söz vermek, sorumluluğu altına almak: ''Kulübün masrafını Türk azalar uhdelerine almışlardır.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12293, 'ulak çıkarmak', 'haberci göndermek, posta çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12294, 'ulu ağacın gürültüsü dal ile, mutlu evin yakışığı döl ile', 'bir ağacın dal budak salarak gürleşmesi gibi bir ailenin mutluluğu da yetiştirdiği çocuklarla pekişir, gürleşir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12295, 'ulu sözü dinlemeyen, uluyakalır', 'büyüklerin verdiği her öğüt yaşadıkları veya tanık oldukları bir olaya dayanır, bir büyüğün kulak ardı ettiğimiz sözünün önemini, başımız derde girip sızlanmaya başladığımız zaman anlarız.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12296, 'ulufe vermek (dağıtmak)', '1) tar. Osmanlılarda askerî ve sivil kuruluşlardaki görevlilere üç ayda bir verilen ücreti dağıtmak; 2) mec. yerli yersiz bol keseden para harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12297, 'ulular köprü olsa, basıp geçme', 'başkaları ezici, kırıcı davransa da sen onlara uyma, büyüklere karşı saygıda kusur etme.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12298, 'ulularla urgan çekişme', 'senden daha güçlü ve bilgili olanlarla iyi geçin, zıtlaşma yenilirsin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12299, 'umacı gibi', 'korkunç ve çirkin görünüşlü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12300, 'ummadığın taş baş yarar', 'küçük veya önemsiz şeyler de çoğu kez büyük etkiler yapabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12301, 'umuda düşmek', 'gerçekleşeceğine inanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12302, 'umuda kapılmak', 'olacağını düşünmek, hayal etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12303, 'umudu boşa çıkmak', 'beklentisi, umudu gerçekleşmemek, hayal kırıklığına uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12304, 'umudu kırılmak', 'bir şeyin artık gerçekleşemeyeceği inancına varmak: ''Umudu kırılınca boynu bükük, ahıra, ineği sağmaya indi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12305, 'umudu sönmek', 'umudu kalmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12306, 'umudu (umudunu) üzmek', 'umudu kesmek: ''Millet köyden umudu üzmüş bir kere. Hele gençler bir an önce çekip gitmek istiyor.'' -M. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12307, 'umudunu kesmek', 'artık olacağını beklememek: ''Aradan dört beş yıl geçince bir yerden de haber gelmeyince sağlığından umutlarını kesmişler.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12308, 'umudunu kırmak', 'umut kesmesine yol açmak: ''Onun bu sözleri de umutlarımı biraz daha kırdı ama susmak istemedim.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12309, 'umurumda değil', '''beni hiç ilgilendirmiyor'' anlamında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12310, 'umurumun teki', 'bir işe ilgi gösterilmediğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12311, 'umut bağlanmak', 'olmasını, olacağını ummak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12312, 'umut beslemek', 'bir şeyin olabileceğini beklemek, ummak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12313, 'umut bırakmak', 'bir kimsede umut uyandırmak, umut vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12314, 'umut, fakirin (garibin) ekmeğidir', 'yoksul kişi, hep yakında bolluğa, rahata kavuşma umudu içinde yaşar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12315, 'umut kesmek', '1) umudunu kesmek; 2) bir şeyin artık gerçekleşemeyeceği inancına varmak, ummaz olmak: ''Senden umutlarını kesmişler, sağ olsun da zararı yok, yazmasın diyorlar.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12316, 'umut serpmek', 'umutlandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12317, 'umut uyanmak', 'umut doğmak, umut belirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12318, 'umut vermek', 'bir kimsede umut uyandırmak, bir kimseye güven vermek: ''Bana umut vermeye çalışıyor ama asıl onun teskin edilmeye ihtiyacı var.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12319, 'umutsuzluğa düşmek (kapılmak)', 'umudu kalmamak, güveni sarsılmak, olumsuzluğa sürüklenmek: ''Yoksa gönlümüzü kırmaktan, bizi umutsuzluğa düşürmekten bir şey kazanılmaz.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12320, 'un ufak etmek', 'çok ufak kırıntılar durumuna getirmek, parçalamak: ''O solucanları un ufak eden çocukların hırsına kapılmıştı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12321, 'un ufak olmak', 'çok ufak kırıntılar durumuna gelmek, parçalanmak: ''... bir yer sarsıntısı ile un ufak olan evlerde yaşıyorlardı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12322, 'ununu elemek, eleğini asmak', '''geri kalan ömrü süresince yapacak önemli bir işi kalmamak'' anlamında kullanılan bir söz: ''Ben unumu eledim, eleğimi astım. Benim gibi evli, çoluk çocuk sahibi adamlara öyle yerlere gitmek yakışır mı?'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12323, 'usanç getirmek', 'usanacak duruma gelmek: ''Efendim, Tanrı''nın günü aynı pilava kaşık sallamaktan usanç getirmişsindir.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12324, 'usanç vermek', 'usandırmak, bıktırmak: ''Binlerce kahraman, bu yazın usanç veren günlerini de ateşe, ısınmış demire karşı ve kızgın toprak üstünde geçirecekler.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12325, 'usançlık getirmek', 'iyice bıkmak, tamamen usanmak: ''Üzüntüyle usançlık getirip işinden soğumasın!'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12326, 'uslu durmak (oturmak)', 'yaramazlık etmemek: ''Sizin gitmeyeceğinizi bildiği için uslu oturacaktır.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12327, 'usta elinden çıkmak', 'işinin ehli olan bir kimse tarafından yapılmak: ''Sırtında koyu lacivert, usta elinden çıkmış bir kostüm.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12328, 'usta maymun kamçı istemez', 'işini iyi bilen kişiye zorlama gerekmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12329, 'usta yanında perende atılmaz', 'bir konuda tam beceri kazanmamış olan kişi, o işin uzmanıyla kalkışacağı üstünlük yarışında yenilgiye uğrar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12330, 'ustamın adı Hıdır, elimden gelen budur', 'gücüm ancak bu kadarını yapmaya yeter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12331, 'ustanın çekici bin altın', 'alanında uzman olan bir kişi, bir sorunu kolayca çözümleyebilme becerisi gösteriyorsa bu, aynı işe yıllarca verilmiş emeklerin karşılığı olarak değerlendirilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12332, 'ustura tutunmak', 'vücuttaki istenmeyen kılları temizlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12333, 'usturayı kayışa çekmek', 'usturanın kılağısını almak için berber kayışına sürtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12334, 'usul tutmak', 'dümtekle tempo tutmak: ''Usul tutarak, dümtek vurarak, başlarını sallayarak avazları çıktığı kadar şarkıya başlarlar.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12335, 'uşağı işe koş, sen de ardına düş', 'çocuk kendisine ısmarlanan işi yapamayacağından işi buyuran kimsenin onun arkasından gitmesi gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12336, 'utananın oğlu kızı olmamış', 'bir şeyi elde etmek için çalışmalı, tembel tembel oturmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12337, 'utancından yere geçmek', 'çok utanmak: ''Yalvarırım hanımcığım, beni beyin yanına çıkarmayınız, utancımdan yerlere geçerim.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12338, 'utancından yerin dibine girmek', 'istenilen biçimde ve nitelikte olmama karşısında üzüntü duymak, aşırı utanmak: ''Süleymaniye''nin avlusunu dolaşırken, utancımızdan yerin dibine gireceğimiz geldi.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12339, 'utanç duymak', 'utanmak: ''Bundan utanç duyuyor, utanılacak pek az şey yapan birisi olarak da gerginleşiyordu.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12340, 'utanç vermek', 'utandırmak, utanmasına yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12341, 'utanma pazar, dostluğu bozar', 'taraflar birbirine ne denli yakın da olsalar bir alışverişte açıkça konuşup anlaşmaları gerekir, ''ayıp olur'' kaygısıyla başta değinilmeyen konular yüzünden sonradan araya soğukluk, kırgınlık girer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12342, 'utanma pazar, mideyi bozar', 'taraflar birbirine ne denli yakın da olsalar bir alışverişte açıkça konuşup anlaşmaları gerekir, ''ayıp olur'' kaygısıyla başta değinilmeyen konular yüzünden sonradan araya soğukluk, kırgınlık girer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12343, 'uvertür yapmak', ' tkz. bir şeye giriş niteliğinde söz söylemek veya davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12344, 'uygun bulmak', 'yakışır, yaraşır görmek: ''O zaman da haydutlar rıhtım kapısına daha önce gitmeyi uygun buldular.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12345, 'uygun düşmek', 'yakışmak, yaraşmak, elverişli olmak: ''Umduk, bekledik, düşündük. Hangi şey umduğumuza uygun düştü?'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12346, 'uygun gelmek', '1) yakışmak, yaraşmak; 2) elverişli olmak; 3) uymak: ''Bu, bizim kızın yaradılışının, ablamın koyduğu yeni töreye uygun gelmeyişidir.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12347, 'uygun görmek', 'yakışır, yaraşır görmek, elverişli bulmak: ''İşte Ahmet Kerim, ilk bakışında Ömer Beyefendi''nin bu eserini yırtıp sepete atmak üzereyken bu ihtimale dayanarak onun gazeteye konulmasını uygun görmüştü.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12348, 'uyku basmak (bastırmak)', 'çok uykusu gelmek: ''İkimiz de esniyorduk, uyku bastırıyordu.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12349, 'uyku çekmek', 'iyice uyumak: ''Erken yattığı gamlı gecelerde geniş kanepenin üstünde uykusunu çekiyordu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12350, 'uyku dağıtmak', 'uyumasına engel olmak: Uykumu dağıtmak için birkaç fincan kahve içtim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12351, 'uyku durak yok', 'dinlenme imkânı yok.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12352, 'uyku gözünden akmak', 'çok uykusu gelmek: ''Yorgunsun, uyku gözlerinden akıyor.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12353, 'uyku kestirmek', 'kısa bir süre uyumak: ''Ben de bu sayede biraz uyku kestirip kuvvetimi telafi ettim.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12354, 'uyku nedir bilmeden', 'dinlenme imkânı bulamadan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12355, 'uyku tutmamak', 'uyuyamamak: ''İkisini de uyku tutmamıştı. Yan yana uzanmış, yorganı çenelerine kadar çekmiş, gözleri sonuna kadar açık dertlerine yanmışlardı.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12356, 'uyku vermek (getirmek)', 'uyuma isteği duyurmak, uyutucu özelliği olmak: ''Sıkılıyorum, uyku veriyor bu tür konuşmalar artık bana.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12357, 'uykusu açılmak (dağılmak)', 'uykulu durumu geçmek: ''Aşağıdan bir şeyler dedilerse de uykusu açılmış olan nöbetçi hekim anlayamadı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12358, 'uykusu ağır olmak', 'uykudan zor uyanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12359, 'uykusu başına sıçramak', '1) uyuyamadığı için sersemleşmek; 2) uykusunu iyi alamadığından hırçınlaşmak: ''Eğer bu patırtıdan, ikinci uykusu başına sıçrayan imam aşağı koşmasa iki kadın, avluda, saç saça, baş başa dövüşeceklerdi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12360, 'uykusu bölünmek', 'yeterince uyumadan uyanmak veya uyandırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12361, 'uykusu derin olmak', 'uykusu ağır olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12362, 'uykusu gelmek', 'uyuma isteği duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12363, 'uykusu kaçmak', '1) uyumak amacıyla yatmışken herhangi bir sebeple uyuyamamak: ''Bir olta nasıl yapacağım diye uykularım kaçtı.'' -S. F. Abasıyanık. 2) kaygılanmak, tedirgin olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12364, 'uykusunu almak', 'uykusunu tam olarak uyumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12365, 'uykusuz kalmak', 'uyuyamamak: ''Benim de mi düşüncelerim olacaktı / Ben de mi böyle uykusuz kalacaktım?'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12366, 'uykuya dalmak', 'uyumaya başlamak: ''Sonunda uykuya dalınca, ay ışığı uyuyan denizcilerin mutlu yüzlerini apak aydınlattı.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12367, 'uykuya varmak', '1) uyumak: ''Yemek biter bitmez Ali sol elini yastık yaptı, hemen uykuya vardı.'' -Y. Kemal. 2) mec. sükûnet, sessizlik, hareketsizlik içine girmek: ''Etrafı kapatan dik, sivri dağlar duman ve bulut sarılı kocaman başlarını birbirine dayayarak çoktan uykuya varmışlardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12368, 'uykuya yatmak', 'uyumak için yatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12369, 'uyruğuna girmek', '1) bir devletin yönetimini kabul etmek; 2) mec. bir kimsenin etkisi altında kalmak, ona bağlanmak: ''Kimsenin uyruğuna girmeyen, küçük, iddiasız ama özgür bir yaşamla yetindi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12370, 'uyuyan yılanın kuyruğuna basmak', 'kötü bir kimsenin yeni bir kötülük yapmasına fırsat vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12371, 'uyuz etmek', ' tkz. sinirlendirmek: ''Ne söyleyeceksen söyle Allah aşkına, uyuz etme insanı.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12372, 'uyuz olmak', '1) uyuz hastalığına yakalanmak; 2) mec. birine, bir şeye sinirlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12373, 'uzağı görmek', 'ileride ne olacağını kestirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12374, 'uzak durmak', 'yaklaşmamak, karışmamak: ''Çocuklar ilk günü senden uzak durmuşlardı, nasıl bir kişi olduğunu kestiremiyorlardı.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12375, 'uzak düşmek', 'uzak olmak, uzak bulunmak: ''Ben uzak düşmemeye çalışır, karşılarında bir yere ilişirdim.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12376, 'uzak kalmak', 'uzakta bulunmak: ''Ancak seven yürek bu, yavukludan uzak kalmaya dayanır mı?'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12377, 'uzak menzile yavaş gitmeli', 'zaman alacak işler aceleye getirilmemelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12378, 'uzak tutmak', 'uzakta kalmasını sağlamak: ''Hayatımızın o dönemlerdeki durallığı, biteviyeliği, romanı toplumumuzdan uzak tutmuştur.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12379, 'uzaklara gitmek', '1) konudan ayrılmak; 2) gözleri dalmak, dalıp gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12380, 'uzaktan bakmak (seyirci kalmak)', 'seyirci gibi davranıp karışmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12381, 'uzaktan kumanda etmek', 'kişiyi veya grubu dışarıdan yönlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12382, 'uzatmaları oynamak', '1) bir görevde son zamanlarını yaşamak; 2) sp. oyunda uzatma dakikalarını oynamak; 3) mec. ölmek üzere olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12383, 'uzatmayalım', 'kısacası: ''Uzatmayalım, bir tazminat lafıdır tutturdu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12384, 'uzun etmek', '1) tartışmayı sürdürmek: ''Pek canım istiyor, uzun etme!'' -P. Safa. 2) aşırı gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12385, 'uzun kulaktan haber almak', 'uzaktan uzağa haber almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12386, 'uzun lafın (sözün) kısası', 'kısacası, özet olarak: ''Uzun lafın kısası, eleştirmeci okuyucuya faydalı, edebiyata faydalı bir yazıcıdır.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12387, 'uzun oturmak', ' hlk. 1) uzanarak oturmak, yarı yatmış durumda oturmak; 2) şaka yatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12388, 'üç aşağı beş yukarı', 'yaklaşık olarak, az bir farkla: ''Üç aşağı beş yukarı anlaştık sayılır.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12389, 'üç aşağı beş yukarı dolaşmak', 'kararsızlık içinde, düşünerek, bir karara varmaya çalışarak gezinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12390, 'üç buçuk atmak', ' argo çok korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12391, 'üç göç, bir yangın yerini tutar', 'bir yerden başka bir yere taşınırken bazı eşya kırılır, dökülür, kaybolur, birçok kez taşınma sonunda bu eşya yangın artığına döner.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12392, 'üç günlük ömür', 'ömrün kısalığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12393, 'üç maymunu oynamak', 'gördüğü ve duyduğu bir olay hakkında görmemiş, duymamış ve söylememiş olduğunu belirtmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12394, 'üçe beşe bakmamak', 'fiyat üzerinde küçük farkları önemsememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12395, 'üçkâğıda bağlamak (getirmek)', 'karşısındakini şaşırtarak aldatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12396, 'üçlenmemiş eken, olmamış eker', 'gerekli koşullarını yerine getirmeden bir işe başlayan kişi olumlu sonuç alamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12397, 'ülke açmak', 'bir ülkeyi savaşarak almak, fethetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12398, 'ümide düşmek', 'umuda düşmek: ''Zavallı çocuk bir an geldi ki âdeta yeniden ümide düşer gibi oldu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12399, 'ümide kapılmak', 'umuda kapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12400, 'ümidi boşa çıkmak', 'umudu boşa çıkmak: ''Kaç sene var ki böyle her ümidin boşa çıktı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12401, 'ümidi kırılmak', 'umudu kırılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12402, 'ümidi sönmek', 'umudu sönmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12403, 'ümidini kesmek', 'umudunu kesmek: ''Bunu gerçekten anlamışım, ben de biliyormuşum gibi bir şeylerden ümit kestiğimi hatırlıyorum.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12404, 'ümidini kırmak', 'umudunu kırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12405, 'ümit bağlamak', 'umut bağlamak: ''Hem ne güzeldi sesindeki yankı / Ben oraya ümitlerimi bağladımdı'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12406, 'ümit beslemek', 'umut beslemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12407, 'ümit bırakmak', 'umut bırakmak: ''Cemil''in bu sözleri kalplerde hiç olmazsa yarın için biraz ümit bırakıyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12408, 'ümit kesmek', 'umut kesmek: ''Doktorların, hayatından ümit kestikleri bir sırada yavaş yavaş açılmış, hayata geri dönmüştü.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12409, 'ümit serpmek', 'umut serpmek: ''Emine ile aralarını bulmaya çalışacağını söyledi, delikanlının gönlüne biraz ümit serptikten sonra çekildi gitti.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12410, 'ümit uyanmak', 'umut uyanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12411, 'ümit vermek', 'umut vermek: ''İnsan zekâsının birliği, düşüncelerin tesanüdünden doğan büyük ve ümit verici bir netice.'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12412, 'ümitsizliğe düşmek', 'umutsuzluğa düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12413, 'ümüğüne sarılmak', 'bir iş için birini çok sıkıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12414, 'ümüğünü sıkmak', 'ümüğüne sarılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12415, 'ün almak (kazanmak, salmak, yapmak)', 'ünü herkesçe bilinmek ve her yerden duyulmak: ''Dünyaca ün almış Mark Twain Derneğinin fahri üyeliğini aldığını duyunca...'' -S. F. Abasıyanık. ''Ramazan, sertliği, zulmü ile ün salmış bir kabadayı idi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12416, 'üne kavuşmak', 'ün kazanmak, şöhret bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12417, 'ünsiyet peyda etmek', 'dostluk, arkadaşlık kurmak, samimi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12418, 'ürküntü vermek', 'ürkütmek: ''Ölmek kaderde var, bize ürküntü vermiyor / Lakin vatandan ayrılışın ızdırabı zor'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12419, 'ürperti vermek', 'korkutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12420, 'ürümesini bilmeyen köpek sürüye kurt getirir', 'beceriksiz kimselerin iyilik yapayım derken zarara yol açtıklarını anlatan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12421, 'ürüyen köpek ısırmaz', 'bağırıp çağırarak başkalarını korkutmak isteyen kimseden zarar gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12422, 'üst çıkmak (gelmek)', 'yenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12423, 'üst perdeden konuşmak', 'üstünlük taslayarak söz söylemek: ''Sen böyle üst perdeden konuşuyorsun çünkü etrafındaki o çomarlara güveniyorsun.'' -R. C. Ulunay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12424, 'üst üste gelmek', 'çakışmak: ''Yaşam çizgileri bir rastlantı sonucu üst üste geldiğinde yağmur sonrası serinliğinde birlikte yürüyecekler.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12425, 'üste çıkmak', '1) suçlu olduğu hâlde karşısındakini suçlamak; 2) zeytinyağı gibi üste çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12426, 'üste vermek', 'fazladan vermek, ödemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12427, 'üste vurmak', 'fiyatı artırmak eklemek, katmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12428, 'üstesinden gelmek', 'başarmak, becermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12429, 'üstten bakmak', 'kibirli, gururlu bir biçimde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12430, 'üstü kalsın', 'hesaptan artakalan az miktardaki paranın alınmaması, bahşiş olarak bırakılması sırasında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12431, 'üstüme (üstümüze, üstünüze) sağlık (iyilik sağlık, şifalar)', '1) ''Tanrı esirgesin, üstümden uzak olsun'' anlamında kullanılan bir iyi dilek sözü; 2) şaşma, şaşkınlık belirtmek için kullanılan bir söz; 3) kötü bir durumdan söz ederken konuşanın dinleyene söylediği iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12432, 'üstün bulmak (görmek)', 'bir şeyi veya kimseyi başkasından daha değerli bulmak veya görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12433, 'üstün olmak (gelmek)', '1) benzerlerinden daha yüksek düzeyde olmak: ''Aşk, hayatın bütün zevklerine üstün gelen ruhani bir varlıktır.'' -A. Ş. Hisar. 2) bir kimseden veya bir şeyden daha yüksek, daha değerli olmak: ''Aliço''nun bir gömlek üstün olduğu iyice belirlenmiştir.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12434, 'üstün tutmak', 'bir kimseye, bir şeye başkasından daha çok değer vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12435, 'üstünde durmak', 'bir işe önem vermek, bir işle yakından ve sürekli ilgilenmek: ''Bir çeşit ezbere okuyoruz, durmuyoruz metin üstünde, fikir üstünde.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12436, 'üstünde hakkı olmak', 'birinde emeği, iyiliği, hakkı bulunmak: ''Hanımının, çocuklarının üstünde bunca yıllık hakkım var diye ağlamaya başladı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12437, 'üstünde kalmak', '1) mal, artırma sonucunda bir kimsenin olmak: ''Mal üstünde kalınca da herkes gibi sevinmedi, böbürlenmedi.'' -H. Taner. 2) suçlanmak: ''Behiç''le Siyret benden gizlediler, kabahat bizim üstümüzde kalır.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12438, 'üstündeki üstünde, başındaki başında', '''üstündekinden başka hiçbir şey kalmadan'' anlamında kullanılan bir söz: ''Karanfil, üstündeki üstünde, başındaki başında sokağa kovulmuş.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12439, 'üstünden akmak', 'bir durumu çok belli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12440, 'üstünden atmak', '1) bir şeyi ödev olarak kabul etmemek; 2) bir şeyin kendi üzerinde bıraktığı etkiyi kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12441, 'üstünden başından akmak', 'durumu belirgin bir biçimde anlaşılmak: ''Üstünden başından itina akan bir yolcudan yol sorulabilir mi?'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12442, 'üstünden dökülmek', 'giysi, giyecek bol ve biçimsiz olmak, yakışmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12443, 'üstünden (şu kadar zaman) geçmek', 'aradan herhangi bir zaman geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12444, '(üstünden veya paçalarından) kibarlık akmak', ' tkz. aşırı derecede kibar davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12445, 'üstüne alınmak', '1) bir davranışın kendisine karşı olduğunu sanarak tedirgin olmak, alınmak: ''Otomobilin dinmeyen yaygarasını üstüne alınmaya mahal yoktu.'' -Ö. Seyfettin. 2) bir işi yapmaya söz vermek, ödev alınmak: ''Her biri, ayrı bir defter sayfasının gözden geçirilmesini üstüne aldı.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12446, 'üstüne basmak', '1) yerinde bir düşünce ileri sürmek: ''Ne iyi söylediniz, dedi; ne iyi üstüne bastınız.'' -F. R. Atay. 2) iyice belirtmek: Üstüne basa basa olmaz, dedi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12447, 'üstüne bir iki güneş doğmak', ' hlk. sabah yataktan geç kalkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12448, 'üstüne çekmek', 'kendi üzerine almak, muhatap olmak: ''Hâlâ eski zenginliğinin hasedini üstüne çeker ve eski terekelerinin veraset vergilerini öder.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12449, 'üstüne çullanmak', 'saldırarak üzerine abanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12450, 'üstüne düşmek', 'bir kimseyle veya bir şeyle çok ilgilenmek: ''Biz de hani üstüne düşüp düzeltecek yerde, Atatürk''ün Osmanlıcayı Türkçeleştirmek hususundaki güzel arzusunu bugünkü uydurma dilcilik gayretine alet etmişiz.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12451, 'üstüne fenalık gelmek', 'aşırı derecede sıkılmak, pek bunalmak: ''Bütün kan başıma çıktı, üstüme bir fenalık gelir gibiydi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12452, 'üstüne geçirmek', '1) bir malın tapusunu kendi adına yazdırmak; 2) evlat edinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12453, 'üstüne gitmek', '1) bir işe el atmak, karışmak: ''Hancı kırda yatıyormuş, üstlerine gidememiş. Karıyı gözünün önünde kesmişler de üstüne gidememiş.'' -M. Ş. Esendal. 2) üstüne doğru gitmek; 3) bir şeyi ısrarla yapmak; 4) bir işi yapmak için kişiyi zorlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12454, '(üstüne) kalem çekmek', 'gereksiz olduğunu belirtmek için üstünü çizmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12455, 'üstüne koymak', 'katmak, eklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12456, 'üstüne kuma gelmek', 'kocası, başka bir kadın almak: ''Üstüne kuma gelmesi şart değil insanın bu acıyı tatması için.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12457, 'üstüne kuş kondurmak', 'olağanüstü, o ana kadar görülmemiş bir şey yapmak: ''Tahta döşetmek değil ya, üstüne bir de kuş kondurursan yine de burada oturulmaz.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12458, 'üstüne olmamak', 'daha üstü, iyisi bulunmamak: ''İngiliz gemisi üstüne gemi olmaz.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12459, 'üstüne oturmak', ' tkz. hakkı yokken bir şeyi kendisine mal etmek: ''Bunların nesi yoksa ele geçirip sonra da üstüne oturmak mümkün.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12460, 'üstüne ölü toprağı serpilmiş gibi', '1) tembel, uyuşuk, cansız, miskin; 2) çok derin bir biçimde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12461, 'üstüne perde çekmek', 'isteyerek örtmek, gizlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12462, 'üstüne titremek', 'bir şeye veya kimseye sevgi, özen göstermek: ''Topu topu beş bin lirayı bulan bu tasarrufun üstüne titreyip durmaktaydı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12463, 'üstüne tuz biber ekmek', 'üzüntüyü, kusuru artıracak durum yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12464, 'üstüne yaptırmak', 'bir malın tapusunu kendi adına yazdırmak: ''Ev galiba halasınındı ama Emin üstüne yaptırmıştı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12465, 'üstüne yok', '''bundan daha iyisi olamaz, hepsinden iyisi bu'' anlamında kullanılan bir söz: ''Güner desinler, bir ev döşemiş, üstüne yok.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12466, 'üstünü görmek', 'gebeyken aybaşı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12467, 'üşenenin oğlu kızı olmamış', 'bir şeyi elde etmek için çalışmalı, tembel tembel oturmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12468, 'üşüntü köpekler mandayı paralar', 'birlikten güç doğar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12469, 'üvey evlat gibi tutmak (saymak)', 'horlanmak, haksızlık etmek, iyi davranmamak: ''Sanatçıyı üvey evlat sayma huyumuz yine değişmedi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12470, 'üvey evlat muamelesi yapmak', '1) dışlamak: ''Bana üvey evlat muamelesi yapıyorsun, beni burunluyorsun.'' -O. Kemal. 2) kötü davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12471, 'üvey, öz olmaz; kemha, bez olmaz', 'bir çocuk ne denli sevilse, ilgi görse de öz annesindeki şefkati üvey annesinde bulamaz, üvey anne öz annenin yerini dolduramaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12472, 'üveye etme, özünde bulursun; geline etme, kızında bulursun', 'öz evladı bir gün öksüz kalırsa başkalarının ona kötü davranmasını istemeyen, üvey evladına kötü davranmamalıdır; kızına ileride gelin olarak gideceği yerde kötü davranılmasını istemeyen, kendi gelinine kötü davranmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12473, 'üyeliği düşmek', 'üye olma niteliğini kaybetmek: ''Meclis çalışmalarına özürsüz olarak bir ay içinde toplam beş birleşim günü katılmayanların üyeliğinin düşmesine karar verilir.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12474, 'üyelik dondurmak', 'herhangi bir sebeple bir üyenin bağlı olduğu kuruluşun çalışmalarında yer almasını veya toplantılarda bulunmasını belirli bir süre için engellemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12475, 'üzerinde durmak', 'bir işe önem vermek, bir işle yakından, sürekli ilgilenmek: ''Klasik yazarlarımızın yapıtları üzerinde durmak, hepimiz için bir görev.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12476, 'üzerinde kalmak', '1) mal veya iş, artırma sırasında bir kimsenin olmak: ''Hasılı ne yaptı yaptı, elektrikli süpürge üzerinde kaldı.'' -H. Taner. 2) istenmeyen şey birine yüklenmek, sorumluluğuna bırakılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12477, 'üzerinden atlamak', 'bir şeyi ödev edinmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12478, 'üzerinden atmak', '1) sıkıntı veren bir iş veya durumdan kurtulmak; 2) işi başkasına devretmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12479, 'üzerinden dökülmek', 'bol ve biçimsiz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12480, 'üzerine alınmak', 'üstüne alınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12481, 'üzerine almak', '1) bir işi görev edinmek, deruhte etmek: ''İşte o günden beri keman sesi, ses duymayan kızların kulaklarına sevdanın sesini duyurmak işini üzerine almıştır.'' -N. Hikmet. 2) bir davranışın kendisine karşı olduğunu sanarak tedirgin olmak, alınmak: ''Söylediklerini hepimiz ayrı ayrı üzerimize almıştık, susuyor ve sıkılıyorduk.'' -Ö. Seyfettin. 3) eşinin üzerine bir başkasıyla evlenmek: ''Fakat haydi beni boşadınız. Almanya''da sevdiğiniz bir başka kadını üzerime aldınız neyse.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12482, 'üzerine bir iki güneş doğmak', ' hlk. üstüne bir iki güneş doğmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12483, 'üzerine çekmek', 'üstüne çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12484, 'üzerine çökmek', 'duygu, durum vb. bastırmak, kaplamak: ''Üzerimize çöken şimşekli, yıldırımlı havanın bana verdiği helecanı yeniden duyuyorum.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12485, 'üzerine çullanmak', 'üstüne çullanmak: Korku, su içen bir ceylana saldıran kurt gibi üzerime çullandı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12486, 'üzerine düşmek', 'üstüne düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12487, 'üzerine koymak', 'üstüne koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12488, 'üzerine oturmak', 'üstüne oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12489, 'üzerine ölü toprağı serpilmiş gibi', 'üstüne ölü toprağı serpilmiş gibi: ''Aldım eve getirdim kuşu. Başka zaman olsa üzerime ölü toprağı serpilmiş gibi uyurdum. Gece uyku girmedi gözüme. Arada bir uyanıp kuşa baktım.'' -T. Dursun K.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12490, 'üzerine titremek', 'üstüne titremek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12491, 'üzerine toz kondurmamak', 'üstüne toz kondurmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12492, 'üzerine tuz biber ekmek', 'üstüne tuz biber ekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12493, '(üzerine) tüy dikmek', ' tkz. kötü bir durum almış bir işi büsbütün kötü bir duruma sokmak: ''Otelin kapıcısı yalan söylemekte tüy dikiyordu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12494, 'üzerine tüy dikmek', 'yaşanan durumu veya olayı daha da kötüleştirmek: ''Ağustos böceklerinin ninnileri, dızdızlarının ahengi sanki bu karanlık gölgelerde saklı haşeratı uyutuyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12495, 'üzerine varmak', 'üstüne varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12496, 'üzerine vazife olmamak', 'üstüne vazife olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12497, 'üzerine yaptırmak', 'üstüne yaptırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12498, 'üzerine yatmak', 'üstüne yatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12499, 'üzerine yıkmak', 'üstüne yıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12500, 'üzerine yok', 'üstüne yok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12501, 'üzerine yüklenmek', 'üstüne yüklenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12502, 'üzerine yürümek', 'üstüne yürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12503, 'üzüm üzüme baka baka kararır', 'her zaman bir arada bulunan, arkadaşlık eden kimseler birbirlerine huy aşılar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12504, 'üzümün çöpü armudun sapı var demek', 'her şeyde bir eksiklik bulmak, güç beğenir olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12505, 'üzümünü ye de bağını sorma', 'yararlandığın şeyin nereden geldiğini araştırma.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12506, 'üzüntü vermek', 'tedirginlik yaratmak, sıkıntı ve huzursuzluğa yol açmak: ''Üzüntü versin diye ara sıra uydurduğu yalanların tesiri altında kalmıştım.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12507, 'vaadinde durmak', 'vaadini tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12508, 'vaadini tutmak', 'verdiği sözü yerine getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12509, 'vaatte bulunmak', 'söz vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12510, 'vaaz vermek', 'cami, mescit vb. yerlerde dinî konuşma yapmak: ''Nasrullah Camii''nde verdiği büyük siyasi vaaz bütün gönülleri fethetmişti.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12511, 'vacip olmak', '1) din b. İslam dinine göre yapılması gerekli olmak; 2) mec. yapılması gerekli olmak: ''Ayağın nasıl olup da mezbeleye atıldığını bulmak artık başhemşireye vacip olmuştur.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12512, 'vadesi gelmek', '1) süresi dolmak, zamanı gelmek: ''Villanın vadesi ocak sonunda geliyordu, değil mi?'' -S. F. Abasıyanık. 2) mec. ömrü sona ermek, eceli gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12513, 'vadesi yetmek', 'ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12514, 'vahamet kesbetmek', 'gittikçe zorlaşmak, tehlikeli ve korkulacak bir durum almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12515, 'vakit geçirmek', 'oyalanmak, uğraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12516, 'vakit kazanmak', '1) bir şeye ayrılan süreyi azaltmak; 2) karşı tarafı oyalayarak kendi hazırlanma süresini uzatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12517, 'vakit, nakittir', 'zaman çok değerlidir, boş yere harcanmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12518, 'vakit öldürmek', 'zamanı yararsız, gereksiz işlerle veya iş yapmadan geçirmek: ''Fakat sandal sahibi olur olmaz zaten yarı keyif, yarı kazanç için vakit öldürdüğü balıkçılık sanatında karar kılmıştı.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12519, 'vakit saat aramamak', 'zamana hiç aldırmamak: ''Sabah, öğle, akşam gibi hani vakit saat aradıkları yok.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12520, 'vakitsiz öten horozun başını keserler', 'her söz yerinde ve zamanında söylenmelidir, zamansız ve yersiz söylenen sözler büyük zararlara yol açabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12521, 'vakti gelmek', '1) ölmek üzere olmak, ölümü yaklaşmak; 2) zamanı gelmek, süresi dolmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12522, 'vaktini şaşmamak', 'her şeyi tam zamanında yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12523, 'vantuz çekmek', 'şişe çekmek: ''Doktor geldi, ilaç yazdı, sırtıma vantuz çekti.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12524, 'var eli titremez', 'varlıklı kimse, uzun boylu düşünmeden hayırlı işlere yardım eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12525, 'var evi kerem evi, yok evi verem (elem) evi', 'varlıklı ailenin durumu konuk ağırlamaya, gereken yerlere yardım etmeye, armağanlar vermeye elverişlidir; yoksul ailenin evinde sıkıntı ve dertten başka bir şey bulunmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12526, 'var ne bilsin yok hâlinden', 'varlıklı olan, yoksulun ne denli sıkıntı içinde bulunduğunu bilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12527, 'var olmak', 'sağ olmak, yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12528, 'var varlatır, yok söyletir', 'varlık, kişiye yüksekten atma ve varlığını artırma gücü verir; yokluk ise ancak sızlanmaya, yakınmaya yol açar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12529, 'var yok', 'belli bir ölçüye ya ulaşır ya ulaşmaz, herhangi bir ölçüye, miktara yakın, olduğu bile kuşkulu: ''Ünlü Haçik''in oğlu Nubar, kırk yaşlarında var yok, göbekli ve dazlak.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12530, 'vardığın yer körse, sen de bir gözünü kapa', 'girdiğin çevrede rahat etmek istiyorsan sana ters gelse bile çevrene uyum sağlamalısın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12531, 'vareste kalmak', 'bir şeyi yapıp yapmamakta özgür bırakılmak: ''Türkçeyi bilmek için aruza aşina olmaktan vareste kalamaz.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12532, 'vareste tutmak (kılmak)', 'bir şeyi yapıp yapmamakta özgür bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12533, 'varını veren utanmamış', 'kendisinden bir şey istenen kimse elinde ne varsa onu verebilir; bunun az olmasından veya düşük nitelikte bulunmasından utanç duymamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12534, 'varışına gelişim, tarhana aşına bulgur aşım', 'sen bana ne kadar değer verir, yakınlık gösterirsen benden de o ölçüde karşılığını alırsın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12535, 'varit olmak', 'geçerli durumda bulunmak: ''İster birinci, ister ikinci ihtimal varit olsun, bunun o kadar önemi yoktur.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12536, 'varla yok arası', 'belli belirsiz: ''Yüzünde varla yok arası bir gülümseme, özlem giderircesine, uzun uzun süzdü dostunu.'' -T. Yücel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12537, 'varlığa darlık olmaz', 'zengin olanın gücü her şeye yeter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12538, 'varlık göstermek', 'kendinden beklenilen görevi yerine getirmek, beğenilir bir iş yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12539, 'varlık içinde yaşamak', 'bolluk içinde sıkıntısız yaşamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12540, 'varlıkta darlık çekmek', 'herhangi bir engel yüzünden elindeki imkândan yararlanamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12541, 'varsa (var mı) pulun herkes kulun; yoksa (yok mu) pulun dardır yolun', 'zengin olana herkes hizmet eder, fakir olana ise kimse yüz vermez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12542, 'varsa ... yoksa ...', 'başına getirildiği kelimenin her şeyin üstünde tutulduğunu anlatan bir söz: Varsa kızı yoksa kızı, oğlunun yüzüne baktığı yok.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12543, 'varsay ki ''', 'bunları hesaba katmasak da'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12544, 'vartadan atlamak', 'zor bir durumdan kurtulmak: ''Bu uzun zaman boyunca, kim bilir neler çekmiş, ne vartalardan atlamıştı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12545, 'vartayı atlatmak', 'tehlikeli bir durumdan kurtulmak: ''Bu vartayı kazasız belasız atlattığı için memnundu.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12546, 'vasiyet ölüm getirmez', 'üzülecek bir durum meydana gelirse ne yapılacağını şimdiden kararlaştırmak o üzücü durumun gelmesine yol açmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12547, 'vatan tutmak', 'yurt edinmek: ''Vatan tutup bu yerlerde kalınmaz / İlleri var bizim ile benzemez'' -Karacaoğlan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12548, 'vaveyla koparmak', 'çığlık atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12549, 'vay anam! (anasını!, canına!)', ' tkz. ''çok şaşılacak şey'' anlamında kullanılan bir söz: ''Vay anasını, amma dolaştık bugün.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12550, 'vay sen misin?', 'herhangi bir söz veya davranışın öfke yarattığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12551, 'vazife etmek', 'görev bilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12552, 'vazife görmek', 'bir görevi yerine getirmek, sürdürmek: ''O evde ne yapacağını, ne gibi bir vazife göreceğini sana şimdi noktası noktasına anlatacağım.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12553, 'vazifesi mi?', 'umurunda değil: Biz burada beklemişiz, onun vazifesi mi?', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12554, 'vaziyet almak', '1) belli bir durum veya davranış biçimini benimsemek, tavır almak, tavır takınmak: ''İşgalden sonra Rumların bize karşı nasıl bir vaziyet aldıklarını da pekâlâ biliyorduk.'' -Y. K. Karaosmanoğlu. 2) karşı çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12555, 'vaziyeti kurtarmak', 'herhangi bir güç durumdan sıyrılmak: ''Karısı ve arkadaşı da bir müddet sustular, galiba bir şeyler düşündüler. Vaziyeti yine genç diplomat kurtardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12556, '... vaziyeti takınmak', 'herhangi bir tavır takınmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12557, 'vebal altında kalmak', 'manevi sorumluluk yüklenmek: ''Başını alıp kaçar da bir belaya uğrarsa vebal altında kalırsın.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12558, 'vebali boynuna', '''ben karışmam, sorumluluk sana veya ona düşer'' anlamında kullanılan bir söz: ''Vebali boynuna, bunların karınlarına sığdırdıklarını bir insan tıkınamaz.'' -H. R. Gürpınar. ''El konuşur, sevişirmiş bana ne / Sevdalım boynuna vebalim'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12559, 'vecde gelmek', 'kendinden geçecek kadar coşmak, bir şey karşısında sonsuz heyecan duymak, esrimek: ''Giydir hırkayı, fesi, Rufai tekkesinde zikrederken vecde gelen bir dervişin hayaleti olabilir.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12560, 'vecde kapılmak', 'coşmak, kendinden geçmek: ''Eski konakların mutfağını anlatırken bir tapınağı tasvir eder gibi vecde kapılır.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12561, 'veda etmek', '1) vedalaşmak, esenleşmek: ''Onu daima öper ve sefere çıkacakları zaman en sonra ona veda ederlerdi.'' -N. F. Kısakürek. 2) mec. sevilen bir şeyle olan ilgisini kesmek: ''Dünyaya veda ettik, atıldık doludizgin / En son koşumuzdur bu, asırlarca bilinsin'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12562, 'vehme kapılmak (düşmek)', 'yersiz korkuya, yanlış düşünceye kapılmak: ''Göreceksiniz işin sonu, bize vehme kapıldığımızı anlatacak.'' -R. H. Karay. ''Etrafımda bütün sisleri ve kokularıyla onu görür gibi bir vehme düşerim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12563, 'velvele kopmak', 'büyük gürültü çıkmak: ''Kıyamet kopar gibi bir velvele koptu, bütün ordu surların üstüne atıldı.'' -Y. K. Beyatlı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12564, 'velveleye vermek', 'gereksiz telaşa ve heyecana düşürmek: ''Susun, ortalığı velveleye vermeyin! Ne bağrışıyorsunuz?'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12565, 'ver elini ...', 'ansızın verilen bir kararla yola çıkıldığını anlatan bir söz: ''Pek sıkıldık mı atla bir vapura, ver elini İstanbul.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12566, 'ver yiğidi yiğide, Mevla rızkın yetire', 'iki gencin evlenmesini kolaylaştırınız, Tanrı rızıklarını verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12567, 'ver yiyeyim, ört uyuyayım; gözle, canım çıkmasın', 'başkalarının sırtından geçinmeye alışmış kişi en hayati sorunlarının bile çözümünde kendisine hizmet edecek birini arar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12568, 'verem olmak', '1) verem hastalığına yakalanmak; 2) mec. sabırsızca davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12569, 'veren el, alandan üstündür', 'yardımını esirgemeyen, eli açık olan kimseye herkes saygı gösterir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12570, 'veren eli herkes öper', 'yardımını esirgemeyen, eli açık olan kimseye herkes saygı gösterir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12571, 'veresiye almak', 'malı parasını daha sonra vermek şartıyla almak: ''Bunların içinde Nihat''a istediği kadar veresiye alabileceğini söyleyenler de var.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12572, 'veresiye (borca) şarap içen, iki kez (kere) sarhoş olur', 'veresiye alışveriş eden iki kez sarsılır; malı aldığı zaman bir süre sonra para ödemenin üzüntüsünü çeker, ödeme zamanı gelince de karşılıksız ödeme yapıyormuş gibi sıkılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12573, 'veresiye vermek', 'malı parasını daha sonra almak şartıyla vermek: ''Mütemadiyen veresiye veriyor ve müşteriler ay başında borç ödeyeceklerine Tevfik''e dert yanıyorlar.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12574, 'vergi kaçırmak', 'bildirimde bulunmamak veya eksik bildirim sonucu ödemesi gereken vergiyi ödememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12575, 'vergiye bağlamak', '1) bir kimse veya şeyden vergi almak; 2) bir yerden, bir kimseden yasal olmayan yollardan para almak, haraç almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12576, 'verilmiş sadakası olmak', 'büyük bir tehlike veya kaza atlatıldığında söylenen bir söz: ''Hiç böyle okkalı enayilik ettiğin yoktu. Ne oldu sana? Gene verilmiş sadakan varmış.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12577, 'verim düşürmek', 'verimli olmaya engel olmak: ''Siyasi amaçlı ... işi yavaşlatma, verim düşürme ve diğer direnişler yapılamaz.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12578, 'verirsen doyur, vurursan duyur', 'yaptığınız iş, amacın gerçekleşmesini sağlayacak nitelikte olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12579, 'verirsen veresiye, batarsın kara suya', 'veresiye verdiğinde alanların borçlarını ödememeleri durumunda sen zor durumda kalabilirsin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12580, 'vermeyince (vermemiş) mabut, neylesin Sultan Mahmut', 'Tanrı, rahat bir yaşam veya yetenek kısmet etmemişse kulun elinden bir şey gelmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12581, 'vesikaya bağlamak', 'mevcudu yeteri kadar bulunmayan ancak çok talep edilen bir şeyi belge karşılığı vermek: ''Aynı teşkilat yünlüden, pamukludan giyecek eşyasını da vesikaya bağlamıştı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12582, 'vesile aramak', 'bir fırsatını kollamak: ''İkide birde içimizden birine çatmak için vesile arıyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12583, 'vesile bulmak', 'sebep yaratmak, bahane göstermek: ''Bir vesile bulup size takdim edilmek pek kolay bir iş oldu.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12584, 'vesveseye düşmek', 'kuruntuya kapılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12585, 'veto yemek', 'engellenmek, reddedilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12586, 'vıdı vıdı etmek', 'çevresini rahatsız edecek biçimde yerli yersiz çok konuşmak: ''İşlerine çok karışıyor, çok vıdı vıdı ediyormuş.'' -E. Bener.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12587, 'vır vır etmek', 'usandırıcı, sinirlendirici bir biçimde durmadan konuşmak: ''Kadın durmadan vır vır eder, yakınır diye adımızı çıkarmışlar.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12588, 'vicdan azabı çekmek (duymak)', 'istenilmeden veya bilinçsizce yapılan kötü bir işten dolayı üzülmek, pişmanlık duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12589, 'vidaları gevşemek', ' argo kendini tutamayıp çok gülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12590, 'vido çekmek', 'oyundaki kazanılacak sayıyı veya parayı iki katına çıkarmayı teklif etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12591, 'vidoyu görmek', 'vidoyu kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12592, 'vikaye etmek', 'korumak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12593, 'vira etmek', 'toplamak, almak: ''Demiri vira edip açılmaya karar verdim.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12594, 'viraj almak', 'virajı dönmek: ''Memur, geç işareti verince gaza bastı ve virajı umduğundan güzel aldı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12595, 'viraneye çevirmek', 'yıkıntı durumuna getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12596, 'vites büyütmek', '1) aracın hızını artırmak için daha yüksek vitese geçmek; 2) mec. işleri hızlandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12597, 'vites değiştirmek', 'otomobilin çekiş ve hızını değiştirmek: ''Ankara Caddesi''nde bir ağır kamyon vites değiştiriyor.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12598, 'vites küçültmek', '1) aracın hızını azaltmak için daha düşük vitese geçmek; 2) mec. işleri yavaşlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12599, 'vitese takmak', 'motorlu taşıtlarda vites kolunu uygun duruma getirip aracı kalkışa hazır durumda tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12600, 'vitesten atmak', 'çok kızmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12601, 'voli çevirmek', '1) voli ile balık avlamak; 2) argo tuzağa düşürmek: ''Öyle bir voli çevir ki hem senin hem de bizim işimize yarasın.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12602, 'voli vurmak', ' argo vurgun vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12603, 'volta atmak', ' argo bir aşağı bir yukarı dolaşmak: ''Çaylarını içtikten sonra Şifa ile Moda arasında üç aşağı beş yukarı volta atmak üzere davranırlar.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12604, 'volta vurmak', '1) den. gemi zikzak yapmak; 2) argo bir aşağı bir yukarı dolaşmak: ''Onu, odamın penceresinden, her sabah kahvaltıdan önce, verandada bir aşağı bir yukarı volta vururken görürdüm.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12605, 'voltasını almak', ' argo 1) kaçmak, savuşmak; 2) çekilmek, gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12606, 'voltaya geçmek', ' argo volta atmaya başlamak: ''Daima belli bir yerden voltaya geçer bin kere gidip dönse gene hep aynı noktaya gelirdi.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12607, 'vuku bulmak', 'olmak, meydana gelmek: ''İstiyorum ki binlerce yıldızcık parlasın, göz alıcı bir fiziki hadise vuku bulsun!'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12608, 'vur abalıya', 'bütün özverinin yumuşak huylu kişiye yüklenmesi, sessiz, güçsüz kişinin hırpalanması, hakkının çiğnenmesi durumunda söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12609, 'vur aşağı tut yukarı', 'uzun uzun çekişerek, sıkı pazarlık ederek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12610, 'vur dediyse öldür demedi', 'bir dileği yerine getirirken aşırılığa düşen için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12611, 'vur patlasın, çal oynasın', 'aşırı zevk ve eğlenceyi anlatan bir söz: ''Komşu konaklarda vur patlasın çal oynasın saz âlemleri devam ediyor, uzak yakın piyano sesleri işitiliyordu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12612, 'vurduğu yerden ses gelmek', 'çok kuvvetli vurmak, eli ağır olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12613, 'vurduğunu öldür, yedirdiğini doyur', 'yaptığınız iş, amacın gerçekleşmesini sağlayacak nitelikte olmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12614, 'vurdukça tozumak', 'üzerinde çalışıldıkça, işlendikçe işi artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12615, 'vurdumduymaz kör ayvaz', 'duygusuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12616, 'vurdumduymazlıktan gelmek', 'aldırış etmemek, umursamamak, önem vermemek: ''Şimdi böyle bir iftira karşısında bizim için vurdumduymazlıktan gelmeye imkân kalır mıydı?'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12617, 'vurgun vurmak', 'yolsuzluk yaparak kısa sürede büyük kazanç elde etmek: ''İkinci Dünya Savaşı yıllarında Harun''un paralarını kullanarak vurduğu vurgunlarla bugünkü mertebesine ulaşmıştır belki.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12618, 'vurgun yemek', 'vurgun sonucu ölmek veya sakat kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12619, 'vurgunu vurmak', 'vurgun vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12620, 'vurup kafayı yatmak (uyumak)', 'uykusu geldiğinde hemen yatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12621, 'vusul bulmak', 'ulaşmak, varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12622, 'vücuda gelmek', 'ortaya çıkmak, oluşmak, meydana gelmek, olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12623, 'vücuda getirmek', 'meydana getirmek, var etmek: ''Koridor burada sola kıvrılarak yeni bir dehliz daha vücuda getirmektedir.'' -H. F. Ozansoy.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12624, '(vücudun bir yerine) kan oturmak', 'bir damarın çatlamasıyla sızan kan, dokular arasına akıp kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12625, 'vücudunu ortadan kaldırmak', 'öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12626, 'vücut bulmak', 'oluşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12627, 'vücut kocar, gönül kocamaz', 'kişi ihtiyarlar ama gönlü taze kalır, sevgisi eksilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12628, 'vücut vermek', 'vücuda getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12629, 'vücut yapmak', 'kas geliştirici hareket ve sporlarda bedeni güçlü duruma getirmek: ''Erkek sporları tarihe karışıyor, halter kaldıran, vücut yapan kadın atletler gündelik manzaralar arasında...'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12630, 'vücuttan düşmek', 'zayıflamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12631, 'ya bu deveyi gütmeli ya bu diyardan gitmeli', 'buranın şartlarına uymalı veya buradan ayrılmalı.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12632, 'ya deve ya deveci (deve üstündeki hacı)', 'ilerisi için verdiğim sözden korkmuyorum, o zamana kadar şartlar değişebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12633, 'ya devlet başa ya kuzgun leşe', 'sonunda büyük bir başarıya ulaşmak için yok olma tehlikesi bile göze alınır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12634, 'ya herrü ya merrü', 'zor, tehlikeli bir durum karşısında ''ne olursa olsun'' gibi kötü ihtimalin de göze alındığını anlatan bir söz: ''Ama çocukluk işte, şeytan dürttü, ya herrü ya merrü deyip birden yukarı bakıverdim.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12635, 'ya huyundan ya suyundan', 'bazı özellikleri olduğu gibi bir yerden, bir kimseden almış kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12636, 'ya sabır', 'katlanılması güç durumlarda sabır gerektiğini anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12637, 'ya sabır çekmek', 'bir sıkıntıya ses çıkarmadan veya ona karşı bir şey yapmadan katlanmak: ''Siz şimdi, bu yavan takazaları bir kere daha ya sabır çekerek dinlemek zorunda kalırsınız.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12638, 'yabana atmak', 'önem vermemek, önemsiz görmek: ''Kendini pek yabana atma. Olabilir ki bu kadın sana tutulmuştur.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12639, 'yabana gitmek', '1) tanınmayan, bilinmeyen biriyle, bir yabancıyla evlendirilmek; 2) bulunduğu yerden başka bir yere yaşamak için gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12640, 'yabana söylemek', 'saçma sözler söylemek, boşa konuşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12641, 'yabancı gelmek', 'tanımamak: ''Çocukluğumdan beri aşina olduğum ses bana yabancı geldi.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12642, 'yabancı gibi durmak', 'bir işe karışmamak, ilgi göstermemek, çekinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12643, 'yabancı koyun kenara yatar', 'bir topluluğa yeni katılan kimseyi çevresi hemen aralarına almaz, o yüzden bir süre yabancılık çeker, onlardan uzak durur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12644, 'yabancı kuşun başı kanadı altında olur', 'bir topluluğa yeni katılan kimseyi çevresi hemen aralarına almaz, o yüzden bir süre yabancılık çeker, onlardan uzak durur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12645, 'yabancı saymak (tutmak)', 'yabancı gibi görmek, yabancı olarak benimsemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12646, 'yabancılık duymak', 'bir kimseye, bir şeye alışamamak: ''Kendisini uykuya veremiyor, her dakika yabancılık duyuyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12647, 'yabancısı olmamak', 'tanıdık, bildik olmak: ''Ruhunun derinliklerinden ürperti dolu, hiç de yabancısı olmadığı sinyaller alıyordu.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12648, 'yad elde beylik sürmeden, yurtta züğürt gezme yeğdir', 'gurbete düşmüş bir insan, ne denli varlık içinde bir yaşam sürüyor olsa da doğup büyüdüğü yeri arar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12649, 'yadigâr bırakmak', 'hatırlanmak için arkasında bir kimseyi veya bir nesneyi bırakmak: ''Duvarlar, hattat sütbabamın yadigâr bıraktığı levhalarla süslenmişti.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12650, 'yadigâr kalmak', 'bir olayı, bir kimseyi hatırlatan bir nesne, bir özellik bırakılmış olmak: ''İçlerinde, babasının günlerinden yadigâr kalanlar birer birer göçmüş...'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12651, 'yadigâr olmak', 'hatıra olarak kalmak: ''Bir de yadigâr olmak üzere fotoğraf bıraktı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12652, 'yaftayı yapıştırmak', 'yanlış biçimde değerlendirip tanıtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12653, 'yağ bağlamak', '1) üzerine yağ birikmek; 2) semirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12654, 'yağ bal olsun', '''yenen, içilen şeyler helal ve afiyet olsun'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12655, 'yağ basmak', '1) büyük bir kaba yağ yerleştirmek; 2) mec. çok yağlanmak, semirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12656, 'yağ çekmek (yapmak)', ' argo gereksiz biçimde övmek, dalkavukluk etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12657, 'yağ gibi kaymak', 'kızak, taşıt vb. sarsılmadan hızla gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12658, 'yağ yakmak', '1) tavada yağı çok ısıtmak; 2) mec. dalkavukluk etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12659, 'yağ yedirmek', 'yağı bir şeyin içine azar azar ekleyerek belli olmaz duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12660, 'yağ yiyen köpek tüyünden belli olur', 'durup dururken yaşama düzeyinde bir yükselme olan kişi, kendisinden şüphe edildiği gibi çalıp çırpıyor demektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12661, 'yağa bala batırmak', 'bol bol yedirip içirmek, çok iyi ağırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12662, 'yağına kıymayan çöreğini yoz (kuru) yer', 'bir iş için gerektiği kadar fedakârlıkta bulunmayan kişi sonucun kusurlarını hoş görmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12663, 'yağıp gürlemek', 'birine çok sinirlenmek: ''Ne diyor, arkamdan yağıp gürlüyor mu?'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12664, 'yağlayıp ballandırmak', 'çok överek anlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12665, 'yağlı ballı olmak', 'araları çok iyi olmak, içli dışlı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12666, 'yağlı kapıya konmak', 'rahat, sıkıntısız bir yere girmek, geçimini başkasının üstüne yıkmak: ''Kondu, namussuz, yağlı kapıya, diye, hasedini belli ediyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12667, 'yağma gitmek', 'bir şey çok alıcı bulmak, çok satılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12668, 'yağma Hasan''ın böreği', '''hakkı olan veya olmayan herkesin yararlandığı kaynak'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12669, 'yağma yok', ' tkz. ''öyle şey olmaz, buna razı olunmaz'' anlamında kullanılan bir söz: ''Olur mu hiç? Bırakır mıyız sizi biz, yağma yok kuzum, yağma yok!'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12670, 'yağmasa da gürlemek', 'elinden bir şey gelmese de sözle destek vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12671, 'yağmur boşanmak', 'birdenbire çok yağmur yağmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12672, 'yağmur olsa kimsenin tarlasına düşmez (yağmaz)', 'elinden geldiği hâlde kimseye iyilik etmeyenler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12673, 'yağmur yağarken küpünü doldurmak', 'fırsat varken ondan yararlanıp para veya mal edinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12674, 'yağmur yağsın da varsın kerpiççi ağlasın', 'yağmurdan yararlanacakların sayısı zarar göreceklerden daha fazladır, yeter ki yağmur yağsın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12675, 'yağmur yemek', 'yağmurda iyice ıslanmak, sırılsıklam olmak: ''Ben önde, Nezir arkada, çamurlu yoldan, yağmur yiye yiye elimdeki pilli fenerin ışığında yürüyoruz.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12676, 'yağmurdan kaçarken doluya tutulmak', 'güç bir durumdan kurtulayım derken daha kötüsüyle karşılaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12677, 'yaka bir tarafta, paça bir tarafta', 'kılığı kıyafeti dağınık bir durumda.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12678, 'yaka ısırmak', 'şaşırarak ''Allah esirgesin'' demek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12679, 'yaka paça etmek (götürmek)', 'hiçbir itiraz dinlemeden ve zorla, apar topar götürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12680, 'yakadan atmak', 'savıp kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12681, 'yakadan geçirmek', ' esk. evlatlığa kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12682, 'yakası açılmadık', 'söylenmesinden kaçınılan (söz, sövgü veya açık saçık nükte): ''Bir yandan yakası açılmadık küfürler, bir yandan dedikodu ve türküler.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12683, 'yakasına çökmek', 'zorlamak, baskı yapmak: ''Bereket versin hekimler sıkı bastılar, yengem de yakana çöktü de seni biraz hizaya getirdiler.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12684, 'yakasını kaptırmak', 'bir şeyin, bir kimsenin etkisinden kendisini kurtaramamak: ''Ooo ... dedi, konukların en yaşlısı, sen yakanı iyice kaptırmış gibisin.'' -N. Araz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12685, 'yakayı ele vermek', 'kaçamayarak ele geçmek, yakalanmak: ''Bu konuda hiç kimsenin yakayı ele vermeyeceğine şimdiden kalıbımı basarım.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12686, 'yakayı (yakasını) kurtarmak (sıyırmak)', 'bir işten kurtulmak: ''Pek sıkboğaz ederlerse bakalım bir sırasını düşürebilirsek yolunda bir yalanla yakamızı kurtarırız.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12687, 'yakı açmak', 'iyileştirmek için bir yarayı açıp işletmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12688, 'yakı yakmak (vurmak)', 'yakı yapıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12689, 'yakın (hayırlı) dost (komşu) uzak (hayırsız) hısımdan (akrabadan) yeğdir (iyidir)', 'ilgi ve iyiliklerini görmekte olduğumuz komşu ve dostlarımız, hiçbir ilgisini görmediğimiz hısımlarımızdan bize daha yakındır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12690, 'yakın takibe almak', 'yakın takip işini yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12691, 'yakından bilmek (tanımak)', 'bir kimseyi, bir şeyi bütün özellikleriyle bilmek veya tanımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12692, 'yakınlık duymak', 'birine karşı sevgi veya ilgi duymak: ''İkisi de birbirlerine yakınlık duyuyorlardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12693, 'yakınlık görmek', 'ilgi, sevgi görmek: ''O, Türkiye''de üç yerden yakınlık gördü.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12694, 'yakınlık göstermek', 'biriyle ilgilenmek, sevgiyle davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12695, 'yakınlık kurmak', 'sıkı ilişki içinde bulunmak, ilgi ve destek vermek: ''Ben merhumla yakınlık kurmuş bahtiyarlardan değilim.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12696, 'yakıp yıkmak', 'çok büyük zarar vermek, harap etmek: ''Siyasal kuruluşların lokallerini yakıp yıkmaya kalkacaklardır.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12697, 'yakışık almamak', 'yerinde olmamak, uygun düşmemek: ''Onu gece yarısı sokağın ortasına atıvermek yakışık almazdı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12698, 'yakışıksız kaçmak', 'uygun düşmemek, çirkin olmak, münasebetsiz görünmek: ''Kitabın adı uzun fakat insanda okumak merakı uyandırdığı için bu uzunluk yakışıksız kaçmamış.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12699, 'yalan atmak (kıvırmak)', 'yalan söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12700, 'yalan çıkmak', 'bir haberin yalan olduğu anlaşılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12701, 'yalan yere', 'gerçeğe uygun olmayarak, doğru olmadığını bile bile.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12702, 'yalan yere yemin etmek', 'gerçeğe uygun olmayarak, doğru olmadığını bile bile yemin etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12703, 'yalana şerbetli olmak', 'çekinmeden yalan söyleyebilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12704, 'yalancı çıkmak', '1) bilmeyerek yalan söylemiş bulunmak; 2) sözünü yerine getirememek; 3) yalan söylediği anlaşılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12705, 'yalancının evi yanmış, kimse inanmamış', 'yalan söylemeyi huy edinen kimsenin sözlerine, gerçeği söylediği zaman bile inanılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12706, 'yalancının mumu yatsıya kadar yanar', 'söylenen söz yalansa durum çok geçmeden anlaşılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12707, 'yalanı çıkmak', 'bir kimsenin yalan söylediği anlaşılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12708, 'yalanını yakalamak (tutmak)', 'bir kimsenin yalan söylediğini anlamak: ''Yalanını yakalamış gibi başını salladı. -Ya o mukaddes sular? Onlara ne diyeceksiniz azizim?'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12709, 'yalayıp geçmek', 'rüzgâr, dalga vb. sıyırarak, dokunarak hızla geçmek: ''Sarı gri gölge bu sefer duvarın üstüne düşmüş, orayı yalayıp geçiyor, yalayıp geçiyor.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12710, 'yalayıp yutmak', '1) iştahla yemek; 2) mec. kötü bir davranış, söz karşısında ses çıkarmamak, kabullenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12711, 'yalaz yalaz yanmak', 'yüksek ateş içinde bulunmak: ''Kendisinin bizzat itiraf ettiği gibi yalaz yalaz yanıyordu.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12712, 'yalelli gibi', 'usanç verecek biçimde sürüp giden (iş, konuşma vb.).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12713, 'yalı kazığı gibi', 'uzun boylu ve iri kemikli (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12714, 'yalın ayak, başı kabak', 'çok perişan bir kılıkta: ''İçinde yaz kış, bir don bir gömlekle yalın ayak, başı kabak bir adam oturur.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12715, 'yallah çekmek', 'kovmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12716, 'yallah etmek', 'atma, yollama vb. işleri hızla yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12717, 'yalnız kalanı kurt yer', 'yardımcısı bulunmayan kişi, kendini tehlikeden koruyamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12718, 'yalnız öküz çifte (boyunduruğa) koşulmaz', 'iki kişi ile yapılması gereken bir işi tek kişi ile yapmaya kalkışmak yanlıştır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12719, 'yalnız taş, duvar olmaz', 'nasıl bir tek taş ile duvar örülmezse insan da tek başına önemli bir işi başaramaz, başkalarıyla ilişki kurmak, işbirliği yapmak zorundadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12720, 'yalnızlık, Allah''a mahsustur (yaraşır)', 'insan ancak toplumsal dayanışma ve iş bölümü içinde rahat ve huzurlu yaşayabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12721, 'yalpa vurmak', '1) rüzgâr, deniz ve yolun durumu dolayısıyla deniz taşıtları iki yana sallanmak; 2) iki yana eğilerek yürümek: ''Kendisi siyah astragan kürkünün içinde sağa sola hafif bir yalpa vuruyordu.'' -H. E. Adıvar. 3) dağılmak, sağa sola yayılmak: ''Ara sıra, çatlak bir nara, dağdan dağa yalpa vuruyor.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12722, 'yalvar yakar olmak', 'çok yalvarmak: ''Hasta olurlarsa hastaneye götürür, doktorlara, hademelere yalvar yakar olurmuş.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12723, 'yama gibi durmak', 'bulunduğu yere uymamak, eklendiğini belli etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12724, 'yama vurmak', 'delik, yırtık veya eski bir yere yama koymak, yama koyarak onarmak: ''Hacının kız kardeşi bir çoraba yama vuruyordu.'' -R. Enis.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12725, 'yaman komşu, yaman avrat, yaman at; birinden göç, birin boşa, birin sat', 'komşun kötü ise başka bir yere göç, eşin geçimsizse ayrıl, atın azgınsa sat, kurtul.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12726, 'yamuk yapmak', 'birine karşı yanlış davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12727, 'yan bakmak', '1) beğenmeyerek veya düşmanca bakmak; 2) kötü niyet beslemek: ''Kim ona yan bakarsa kemiklerini kırar, anasını ağlatırım.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12728, 'yan basmak', '1) bir işte aldanmak; 2) dürüst davranmamak, kaypaklık etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12729, 'yan çizmek', ' tkz. bir işten kaçmak: ''Bir görev olmasına karşın, biz bu göreve yan çizmeyi yeğliyoruz.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12730, 'yan gelip oturmak (yatmak)', 'yan gelmek: ''El âlem kaloriferli konaklarda yan gelip otururken sen işte böyle tir tir titrersin.'' -Y. K. Karaosmanoğlu. ''Eloğlu erkenden ayaktadır. Bunca uygarlık yan gelip yatmakla elde edilmemiştir.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12731, 'yan gelmek', 'bir işe karışmayarak rahatına bakmak, keyfince yaşamak: ''Köşke kurulalım; rahatımıza, keyfimize bakıp yan gelelim.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12732, 'yan gözle bakmak', '1) yan bakmak; 2) belli etmeden, göz ucuyla bakmak: ''Genç bir jandarma zabiti, sert bir eda ile geçiyor, yan gözle bana bakıyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12733, 'yan pala Zeydün', ' tkz. ve alay birinin, yeni bir durum karşısında ne yapacağını kestiremeyerek şaşkınlık geçirdiğini anlatmak için kullanılan bir söz: ''... bütün görevlileri yan pala Zeydün, hepimiz ve her şey eski hamam, eski tas...'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12734, 'yan tutmak', 'taraflardan yalnızca birini desteklemek, yansız davranmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12735, 'yan yan bakmak', '1) göz ucuyla bakmak: ''İhtiyar kadın yan yan torununa bakıyordu.'' -M. Yesari. 2) kin, nefret veya öfke ile bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12736, 'yan yatmak', '1) yana doğru çok eğilmek; 2) sağa veya sola doğru eğilerek devrilmek: ''Batık gemi on kulaçta yan yatıyordu.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12737, 'yana yana istemek', 'ısrarla, içtenlikle dilemek: ''Bir babam olduğunu, nasıl yana yana istediğini size anlatamam.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12738, 'yanağına kan gelmek', 'yüzü daha canlı ve renkli olmak, iyi beslenmekten dolayı gürbüz görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12739, 'yanağından kan damlamak', 'çok sağlıklı olduğu benzinden anlaşılmak: ''Sağımızdaki, yanağından kan damlayan iri Çerkez''i gösterdim.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12740, 'yandı gülüm keten helva', '''kaçırılmış bir fırsat'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12741, 'yangın bacayı sarmak', 'durum olağanüstü kötüye gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12742, 'yangın yerine dönmek', 'çok kalabalıklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12743, 'yangına körükle gitmek', 'gerginliği, uzlaşmazlığı artıracak biçimde davranmak: ''Bey, bana teselli verecek yerde sen de yangına körükle gidiyorsun.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12744, 'yangına vermek', 'tutuşturmak, bir şeyi bilerek yakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12745, 'yangından mal kaçırır gibi', 'bir işte gereksiz telaş ve ivedilik göstererek, herkesten saklamaya çalışarak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12746, 'yangını körüklemek', 'gerginliği, anlaşmazlığı artırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12747, 'yanık kokmak', '1) is kokmak; 2) ortalıkta bir şeyin yandığını anlatan koku bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12748, 'yanık yerin otu tez biter', 'kişinin yüreğini yakan acı, az zaman sonra küllenir, yerini yeni ve neşeli duygulara bırakır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12749, 'yanılgıya düşmek', 'bilmeden bir yanlışlık yapmak: ''Eski bakan bir yanılgıya düşmüştü.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12750, 'yanına almak', '1) yanında çalıştırmak: ''Ben seni yanıma alayım ama çok para veremem.'' -Ö. Seyfettin. 2) geçimini sağlamak için yanında bulundurmak: Annesini yanına almış. 3) beraberinde götürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12751, 'yanına bırakmamak (koymamak)', 'cezasız bırakmamak, öç almak: ''Böylece yaptıklarını yanlarına bırakmamış olacağım, insanlar neden öldürüldüğümüzü anlayacaklar.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12752, 'yanına kâr kalmak', 'cezasız kalmak: ''Galiba bu tarihî günün yüzü suyu hürmetine Beyoğlu''nda sürtüp durmaları yanlarına kâr kaldı.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12753, 'yanına salavatla varılmaz', '1) çok pahalı olan şeyler için kullanılan bir söz; 2) kibirli, gururlu kimseler için kullanılan bir söz; 3) çok öfkeli kimseler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12754, 'yanına salavatla yaklaşılmak', 'birini yanına gitmekten korkmak, çekinmek: ''Biliyorum, yarın bu kâğıtları yazanların da yanlarına salavatla yaklaşılacak.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12755, 'yanında olmak', 'desteklemek, yardımcı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12756, 'yanıp durmak', 'pişman olmak: ''Herife bir tokat yahut bir yumruk yerleştiremediğine bile yandı durdu.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12757, 'yanıp tutuşmak', '1) güçlü bir aşk ile sevmek; 2) bir şeyi elde etmek için güçlü bir istek duymak: ''Her şeyden önce bir bakanlık koltuğuna kurulmak ihtirasıyla yanıp tutuştuğunu ve oraya varmak için her vasıtayı mübah saydığını sezip anlamamış mıydı?'' -Y. K. Karaosmanoğlu. 3) elde edemediği bir şey için büyük üzüntü duymak: ''Fabrikayı boşaltmaları için dışarıdan çağrı yapılırken kaçma arzusuyla yanıp tutuşanlar oldu.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12758, 'yanıp yakılmak', 'sızlanmak, şikâyet etmek: ''Ali Safa Bey bir şeye çok yanıp yakılıyordu, işini daha gizli görebilirdi.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12759, 'yanıt hakkı doğmak', 'cevap hakkı doğmak: ''Anlamamı istediler. Yanıt hakkı doğarmış, bu da gereksiz dediydim dediydin meselesine götürürmüş işi.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12760, 'yanıt vermek', 'yanıtlamak, cevaplamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12761, 'yankı uyandırmak', 'bir olgu çevrede duygusallık, düşünce, dedikodu gibi tepki yapmak: ''Memleket dışında bile birtakım yankılar uyandırmaya başlamıştı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12762, 'yanlış çıkmak', 'yanlış olduğu anlaşılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12763, 'yanlış hesap Bağdat''tan döner', 'ortaya çıkan bir yanlışlık çok geç de olsa düzeltilebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12764, 'yanlış kapı çalmak', 'isteğinin yapılmayacağı, yersiz sayılacağı bir yere başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12765, 'yanlışını çıkarmak', 'yanlışını bulup göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12766, 'yanlıştan dönmek', 'bir işte yapılanın yanlış olduğunu anlayıp yeni bir uygulamaya geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12767, 'yanmış harmanın öşrü alınmaz', 'önce verimli iken kazaya uğramış olan şeyden, artık gelir, verim beklenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12768, 'yapı taşı, yapıdan (yerde) kalmaz', 'değerli kimse boşta kalmaz, kendisine iş verilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12769, 'yapı taşı yerde kalmaz', 'değerli kimse boşta kalmaz, kendisine bir iş verilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12770, 'yapıp etmek', ' hlk. yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12771, 'yapma (yapma yahu)', 'şaşılacak durumlarda ''öyle mi, doğru mu, gerçek mi?'' gibi anlamlar bildiren bir söz: Onca yolu iki saatte almışlar. -Yapma!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12772, 'yapmadığı kalmamak', '1) kendisi için zararlı olan birçok iş yapmak; 2) yapmadığını bırakmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12773, 'yapmadığını bırakmamak', 'elinden gelen her türlü kötülüğü yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12774, 'yaprak gibi titremek', 'aşırı titremek: Bütün vücudu yaprak gibi titriyordu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12775, 'yaprak oynamamak (kıpırdamamak)', 'hava rüzgârsız, çok durgun olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12776, 'yaptığı hayır, ürküttüğü kurbağaya değmemek', 'yol açtığı zarar, yaptığı iyilikten büyük olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12777, 'yaptığı yanına (kâr) kalmamak', 'yaptığı kötülük karşılıksız kalmak, cezasını görememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12778, 'yâr beni ansın bir koz ile, o da çürük çıksın', 'bir dostun verdiği armağan küçük ve değersiz olsa bile verilen kişinin hatırlandığını göstermesi bakımından çok değerlidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12779, 'yâr olmak', 'yardım etmek, yararlı olmak: Talihi yâr olmadı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12780, 'yar, yakıldığı gün tozar', 'bir felaket meydana geldiği anda büyük bir tepki ve panik yaratır, daha sonra durumun eski ağırlığı kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12781, 'yara açmak', '1) vücutta veya bir şeyin yüzünde yara oluşmasına sebep olmak; 2) mec. büyük üzüntü vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12782, 'yara almak', '1) yaralanmak: ''Beyzade sağ salim kurtulacak ama İbiş ağır bir yara alacaktı.'' -T. Buğra. 2) mec. itibar kaybetmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12783, 'yara bere içinde olmak', 'vücudunda çokça yara, ezik, sıyrık, çürük bulunmak: ''Üstü başı parça parça, vücudu yara bere içinde.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12784, 'yara işlemek', '1) yara kapanmayıp akıntı sürmek; 2) mec. üzücü bir olayın etkisi bitmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12785, 'yara kapanmak', 'yara iyi olup geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12786, 'yara sıcakken sarılır', 'bir acıyı dindirmek için gereken yardım hemen yapılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12787, 'Yaradan''a kurban (olayım)', ' hlk. bir şeye hayran kalındığında söylenen bir söz: ''Maşallah'' şu güzelliğe bak Ruhsar, Yaradan''a kurban olayım.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12788, 'Yaradan''a sığınıp', 'bütün gücünü kullanarak: ''Yaradan''a sığınıp Osmanlı tokadını çarptık mı adamı lobut yemişe çeviren biz değil miydik?'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12789, 'yaralı kuşa kurşun sıkılmaz', 'birinin düşkünlüğünden yararlanarak ondan öç almak doğru değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12790, 'yaralı parmağa işememek', ' tkz. en küçük bir yardımı bile esirgemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12791, 'yararı dokunmak', 'yararlı olmak, kâr sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12792, 'yararlı kılmak', 'fayda sağlayan ve üretken duruma getirmek: ''Devlet özel eğitime ihtiyacı olanları topluma yararlı kılacak tedbirler alır.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12793, 'yarası olan gocunur (gocunsun)', 'bir işte sorumlu aranırken kusuru olan kimse telaşa düşer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12794, 'yarasını deşmek', 'acıyı, üzüntüyü hatırlatmak, tazelemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12795, 'yaraşık almak', 'yaraşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12796, 'yaraya merhem olmak', 'zorunlu ihtiyacı karşılamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12797, 'yaraya tuz biber ekmek', 'bir derdin acısını çoğaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12798, 'yarayı tazelemek', 'üzüntüyü, sıkıntıyı, acıyı hatırlatmak, yeniden ortaya çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12799, 'yardan atmak', 'kazaya uğratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12800, 'yârden mi geçersin, serden mi?', 'insan ne kendinden ne de sevdiklerinden kolay kolay vazgeçemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12801, 'yardım görmek', 'destek elde etmek, bağış almak: ''Oysaki yarım saat tek bir insandan, tek bir yerden yardım görmeksizin yaralı olduğu yerde kalmış.'' -N. Uygur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12802, 'yardımda bulunmak', 'yardım etmek: ''Mal sahibi Rafet Reis, ona epey yardımda bulunmuştu.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12803, 'yardımına koşmak', 'güç duruma düşene istekle yardım etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12804, 'yargıya başvurmak', 'dava açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12805, 'yargıya gitmek', 'bir anlaşmazlığı gidermek amacıyla mahkemeye başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12806, 'yargıya varmak', 'karşılaştırma ve değerlendirme yaparak bir sonuca ulaşmak, anlam vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12807, 'yarıda kalmak', 'bitmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12808, 'yarım elma, gönül (hatır) alma', 'armağan küçük de olsa gönül almaya yeter.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12809, 'yarım elmanın yarısı o, yarısı bu', 'birbirine çok benzeyenler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12810, 'yarım hekim candan eder, yarım hoca dinden eder', 'bir iş, uzmanına değil de o işten anlamayana yaptırılırsa istenilen değil, ters bir sonuç elde edilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12811, 'yarım kalmak', 'tamamlanmamak, sonuçlanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12812, 'yarım kulak dinlemek', 'umursamadan, önem vermeden dinlemek: ''Dersleri yarım kulak dinliyor, etütlerde uzun uzun mektuplar yazıyordu.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12813, 'yarım sağ etmek', 'sağ yana biraz yönelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12814, 'yarım sol etmek', 'sol yana biraz yönelmek: ''Doktorun elini tuttu, salladı. Sonra yarım sol etti, yan tarafta duran koltuğa oturdu.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12815, 'yarından tezi yok', 'gecikmeksizin, hemen: ''Yarından tezi yok, gitmeniz için icap edenleri yapmaya başlamalısınız.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12816, 'yarınki kazdan bugünkü tavuk (yumurta) yeğdir', 'sağlanmış bir kazanç beklenen, umulan daha büyük bir kazanca feda edilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12817, 'yarışa girmek', 'yarışmak: ''Mısır''ın değme ağıtçıları bile sanırım vaveylalarında benimle yarışa giremezlerdi.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12818, 'yarışa kalkmak', '1) yarışmaya başlamak; 2) yarışmaya niyetlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12819, 'yarma gibi', 'çok iri yarı (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12820, 'yas bağlamak', 'Yas tutmak: ''Yurdumuzda düşmanlar dert ekti, elem biçti / Yas bağlayan milletin gözyaşlarını içti'' -E. B. Koryürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12821, 'yas çekmek', 'yas tutmak: ''Hem yârinden hem yoldaştan hem köyden / Ayrı düşen garip kullar yas çeker'' -A. İ. Özkan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12822, 'yas tutmak', '1) çok üzülmek, yasa bürünmek, matem tutmak: ''Sen gitmezsen Ankara''da yas tutmazlar, demek dilimin ucuna kadar gelmişken tuttum.'' -M. Ş. Esendal. 2) duyulan acı ve üzüntüyü bazı davranışlarla belli etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12823, 'yasa bürünmek (boğulmak, gömülmek)', 'çok üzülmek: ''Bütün ev halkı yasa bürünmüştü, o gece hiçbirinin gözüne uyku girmedi.'' -H. Topuz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12824, 'yasa çıkarmak (yapmak, koymak)', 'bir yasa önerisi, yasama gücü tarafından onaylanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12825, 'yasağı (yasakları) çiğnemek', 'uyulması gereken kurallara uymamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12826, 'yasak savmak', '1) bir nesne, bir gereksinimi geçici olarak karşılamak, şimdilik işe yaramak: Bu boyun bağı eskimiş ama bugünlük yasak savar. 2) bir işi hatır için, gönülsüz olarak üstünkörü yapmak: ''Bunu da yasak savar gibi değil, yararlı olmak kaygısı ile yani özenle yapacaksınız.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12827, 'yaş akıtmak (dökmek)', 'ağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12828, 'yaş ilerlemek', 'yaşlanmak, ihtiyarlamak: ''Yaş ilerliyor. Artık geçti bizden / Kişi ev bark edinmeli vakitten'' -C. S. Tarancı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12829, 'yaş kesen baş keser', 'çeşit çeşit yararları olan ağacı sadece odun sağlamak için kesen insan canına kıymış gibi büyük bir suç işlemiş olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12830, 'yaş tahtaya (yere) basmamak', 'bir işte uyanık davranarak aldanmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12831, 'yaş yetmiş iş bitmiş', 'yaşı ilerlemiş insandan fayda beklenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12832, 'yaşı at pazarında sorarlar', 'akıllı olmanın yaşla ilgisi yoktur, bazı küçükler büyüklerden daha akıllı olabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12833, 'yaşı benzemesin', 'erken ölmüş birine herhangi bir yönden benzetilen bir kimse için ''aynı yaşta ölmesin'' anlamında söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12834, 'yaşı ne, başı ne?', 'konuşulan iş için genç bir kimsenin yaşının ve deneyiminin elverişli olmadığını anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12835, 'yaşı yerde (toprakta) sayılası', '''ölsün'' anlamında kullanılan bir ilenme sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12836, 'yaşın arasında (yanında) kuru da yanar', 'beğenilmeyen tutumlarından dolayı cezalandırılan kişiler yanında suçsuzlar da suçlular gibi hırpalanırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12837, 'yaşını başını almak', '1) yaşı ilerlemiş olmak: ''Yarını ne olacak dünyamızın / Biz yaşımızı başımızı aldık / Allah çocuklarımıza acısın'' -C. S. Tarancı. 2) deneyim kazanmış olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12838, 'yaşını bitirmek (doldurmak)', 'öngörülen belli bir yaş sınırına ulaşmak: ''On sekiz yaşını doldurmayan talebelerin kahveye gitmeleri yasak edilmiş.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12839, 'yaşını içine akıtmak', 'duyduğu acıyı, üzüntüyü sezdirmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12840, '... yaşını sürmek', '... yaşında olmak: ''Artık çocuk değilsin, on yedi yaşını sürüyorsun.'' -O. V. Kanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12841, 'yaşlara boğulmak', 'çok ağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12842, 'yaşta kalmış kavat pabucu gibi', 'çaresiz, kırgın, üzgün: ''Behice iyi kadındır, hoş kadındır gelgelelim pokere oturup da beş kâğıtçığını aldın mı yaşta kalmış kavat pabucu gibi yayılıverir.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12843, 'yatağa bağlamak', 'yataktan kalkamayacak kadar hasta etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12844, 'yatağa bağlanmak', 'yataktan kalkamayacak kadar hasta olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12845, 'yatağa serilmek', 'bitkin, yorgun bir durumda yatağa uzanıp yatmak: Yorgunluktan yatağa seriliverdim.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12846, 'yatağa (yataklara) düşmek', 'yataktan kalkamayacak kadar hasta olmak: ''Daha on yaşımda yokken annem yatağa düşmüştü.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12847, 'yatağını ayırmak', 'ayrı yatakta yatmak: ''Bey, şimdi yemin edeceğim, yatağımı ayıracağım, anladın mı?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12848, 'yatak yorgan yatmak', 'ağır hasta olmak: ''Gerçekten yatak yorgan, kolu boynu sargılar içinde, pestil gibi yatıyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12849, 'yataklık yapmak (etmek)', 'suçluları gizlice barındırmak, suçlulara yardım etmek: ''Bir kanlı katile yataklık yapmış gibi pişmanlık duyuyordu.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12850, 'yatan (hasta yatan) ölmez, eceli yeten ölür', 'hasta olan eceli gelmemişse ölmez; sapasağlam biri de eceli gelmişse sağlıklıyken ölüverir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12851, 'yatan ölmez, yeten ölür', 'hasta olan eceli gelmemişse ölmez; sapasağlam biri de eceli gelmişse sağlıklıyken ölüverir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12852, 'yatan (yatar) aslandan (kurttan), gezen (yeler) tilki yeğdir (iyidir)', '1) az güçlü olup çalışan, çok güçlü olup çalışmayandan daha başarılı olur; 2) soylu, güçlü olmadıkları hâlde geçimlerini sağlamak için çalışanlar soylu, güçlü olup da tembel tembel oturanlara yeğlenirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12853, 'yatanın yürüyene borcu var', '1) çalışmayanın sürekli olarak çalışana borcu vardır; 2) çalışmayarak bir şeyden yararlanan kişi, bu kolaylığı, çalışıp o şeyi meydana getirene borçludur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12854, 'yatıp kalkıp', 'her zaman, hep: Yatıp kalkıp anama dua ediyorum.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12855, 'yatıp kalkmak', '1) gecelerini geçirmek: ''Yatıp kalktığım odamın penceresinden bakınca bir baştan bir başa bütün sokağı görüyordum.'' -N. Cumalı. 2) cinsel ilişkide bulunmak: ''Öteki, çok kadınla oynaşmış ve hatta yatıp kalkmış, sevda damarları kaşarlanmış bir gençti.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12856, 'yatırım yapmak', '1) gelir amacıyla bir işe para yatırmak; 2) mec. ileride bir çıkar veya kazanç sağlamak için önceden ortam hazırlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12857, 'yatıya kalmak', 'gidilen yerde geceyi geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12858, 'yatsının faziletini güveyden sormalı', 'eskiden gerdeğe yatsı namazından sonra girildiğinden güvey için yatsı çok önemliydi tıpkı bunun gibi birçok kimse için önemli bulunmayan bir durum kimi kişiler için özel bir önem taşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12859, 'yavaş atın tekmesi yavuz olur', 'yumuşak huylu kimseler öfkelendiklerinde aşırı davranışlarda bulunurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12860, 'yavaş gel! (ol!)', ' argo abartarak konuşanlar için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12861, 'yavaş tükürüğün sakala zararı var', 'sert davranmak gereken durumlarda gevşek davranan kişi bu tutumunun zararını görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12862, 'yavaştan almak', 'işi gereken sürede yapmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12863, 'yave okumak', 'gereksiz söz söylemek, boşa konuşmak: ''Çevre mevre yaveleri okuyan, doğa deniz kutsallığını sosyete övünmesi şekline dönüştürenlere değil lafım.'' -Y. Koray.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12864, 'yavru atmak', 'gebe hayvan yavrusunu düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12865, 'yavru kuş, yuvada gördüğünü yapar', 'aile içinde edinilen görgü, eğitimin temelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12866, 'yavuz hırsız ev sahibini bastırır', 'biri, suçunu zarar verdiği kimseye yüklediğinde söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12867, 'yay gibi', '1) eğri: ''... ama işe yaramadı, yay gibi kaşlar birbirlerine yaklaşır gibi oldular.'' -T. Buğra. 2) çok gergin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12868, 'yaya bırakmak', '1) yarışma söz konusu olan durumlarda geride bırakmak: ''Özellikle süper devletler, kendi çıkarları için kendilerine muhtaç dostları bir çırpıda yaya bırakıverirler.'' -T. Halman. 2) yarı yolda bırakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12869, 'yaya kaldın tatar ağası', 'istediğini elde edemeyen, başarısızlığa uğrayan kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12870, 'yaya kalmak', '1) istediği şeyi yapamaz duruma gelmek: ''Yetişmiş adamları, pek çok paraları olanlar bile bu yolda yaya kalıyorlar.'' -M. Ş. Esendal. 2) binecek bir şeyi olmamak; 3) yardımcısız kalmak: ''İddiası sosyal adalet ilkesi bazında oldukça yaya kalmıştı.'' -H. Taner. 4) geri kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12871, 'yaygara koparmak', 'yaygarayı basmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12872, 'yaygarayı basmak', 'bağırıp çağırmak: ''Gün geçmiyor ki evdeki kadınlardan biri, önüne bir ıslak şeker parçası düştüğünü görüp yaygarayı basmasın!'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12873, 'yayı, atıcısına vermeli', 'ülkenin yönetimine, bunu başarıyla yerine getirebilecek olanlar seçilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12874, 'yayık dövmek', 'yayık yaymak: ''Yayık dövmek, yağ çıkartmak yeni gelinlerin görevi.'' -N. Araz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12875, 'yayık yaymak', 'sütün ayranını ve yağını ayırmak için yayığı çalkalamak: ''Yayık yaydım kolum şişti / Kolumdan kol bağım düştü'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12876, 'yaz yalan, kış gerçek', 'yazın yaşama koşulları kolay kışın zordur, tıpkı bunun gibi bazı olaylar önem vermeye değmez ama bazı olaylar çok dikkatli ve özenli olmamızı gerektirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12877, 'yaza çıkardık danayı, beğenmez oldu anayı', 'yetiştirdiğimiz, büyüttüğümüz gençler, bizi beğenmezler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12878, 'yaza çıkmak', 'yaz mevsimine ulaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12879, 'yazboz tahtasına çevrilmek (döndürmek)', 'bir konuda art arda birbirini tutmayan kararlar alınmak: ''Millete mal olmuş şehitlerin, büyük hizmet sahiplerinin saygınlığı ulu orta yazboz tahtasına çevrilemez.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12880, 'yazı getirmek', 'yazlık giysiler giymek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12881, 'yazı tura atmak', 'bir oyunda ilk başlayacak olanı tespit etmek amacıyla veya girişilen bir iddiada kazananı belirlemek için metal parayı havaya döndürerek atmak ve yere düştüğünde hangi yüzün üste geldiğine bakarak karar vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12882, 'yazıklar olsun', 'üzüntü ve kınamanın çokluğunu anlatan bir söz: ''Yazıklar olsun, seni sevmesini bilmeyenlere; ey gamlı ülke!..'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12883, 'yazın araması, kışın taraması olmazsa herkes besler mandayı', 'iyi bir iş yapılmaya çalışılırken karşılaşılan sıkıntılara herkes kolay kolay katlanamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12884, 'yazın başı pişenin, kışın aşı pişer', '1) yazın güneş altında çalışan, ailesinin kışlık ihtiyacını kazanır; 2) gençliğinde çok çalışıp varlık edinen hastalığında veya ihtiyarlığında rahat eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12885, 'yazın çalışan, kışın gülüşür', 'yazın çalışan kışın rahat eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12886, 'yazın gölge hoş, kışın çuval boş', '1) yazın keyifli yerlerde tembel tembel oturan kışın yiyecek bulamaz; 2) gençliğinde kazanç peşinde koşmayıp zevke dalan hastalığında veya ihtiyarlığında perişan olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12887, 'yazın gölge kovan, kışın karın ovar', 'yazın çalışmayıp keyif ve zevk ile vakit geçiren çiftçi, nasıl kışın aç kalırsa çalışma gücü ve ortamı bulunduğu hâlde zamanını değerlendirmeyen ve geleceğini düşünmeyen kişi de çalışma olanakları kalmadığı zaman öyle perişan olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12888, 'yazın sıcakta terleyen, kışın soğukta üşümez', 'yazın çalışan kışın rahat eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12889, 'yazın yorulmayan, kışın kurulmaz', 'yazın çalışan kışın rahat eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12890, 'yazının cahili olmak', 'okuma yazması olmamak, bilgisiz olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12891, 'yazıp çizmek', 'yazmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12892, 'yazıya dökmek', 'herhangi bir konuyu yazı ile anlatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12893, 'yazıya geçirmek', 'yazmak, yazılı duruma getirmek: ''Dün anlattıklarımı yazıya geçirirken bir yığın yazım yanlışı yapmışlar.'' -İ. Aral.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12894, 'yazıya gelmemek', 'yazı ile anlatılamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12895, 'yazıyı çıkarmak (sökmek)', 'okuyabilmek: ''Benim yerinden dahi kımıldatmaya gücümün yetmediği Afrika seyahatnamesini yere indirtir, kendim de yere uzanır, gözlerim ağrıyıncaya kadar yazıları sökmeye çalışırdım.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12896, 'yazlığa çıkmak', 'yazı geçirecek bir yere gitmek: ''Bu sene yazlığa çıkmışlar, Boyacıköyü''ndeki yeni yalıya taşınmışlar.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12897, 'ye kürküm ye!', 'gösterilen saygının kişiliğe değil, giyim kuşam düzgünlüğüne olduğunu belirtmek için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12898, 'yedeğe almak (takmak)', '1) bağlayarak ardından çekip götürmek: ''Sonra otomobili yedeğe takıp götürdüler.'' -R. H. Karay. 2) destek verip yanında yürümek, yürümesine ve hareketine yardımcı olmak: ''Bunlardan yürümeye mecali olmayan bazılarını erkekler iki taraflarından kollarına girmek suretiyle yedeğe almışlar.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12899, 'yedek (yedekte) çekmek', 'akıntılı suda kayığı karadan iple çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12900, 'yedi adım yolun, bir yudum suyun hakkı vardır', 'iki insanın tanışıp kaynaşması ve aralarında bir dostluk bağı oluşması için özel bir kurala, yola, yönteme gerek yoktur, kısa bir yolculuk, küçük bir ikram bile bu yakınlığı sağlayabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12901, 'yedi düvelle barışık', 'herkesle iyi geçinen kimse.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12902, 'yedi iklim dört bucak', 'her yer.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12903, 'yedi kat yerin dibine geçmek', '1) çok güçlü olarak yere çakılmak; 2) fazlasıyla utanmak, mahcup olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12904, 'yedi kubbeli hamam kurmak', 'büyük hayaller peşinde koşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12905, 'yediği naneye bak!', '''yaptığı yersiz, uygunsuz işe bakın'' anlamında kullanılan bir söz: ''Yediği naneye bak! Hanımın kocası uyanmasın diye gelip beni uyandırıyor.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12906, 'yediği önünde, yemediği ardında', 'bolluk, refah içinde yaşayanlar için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12907, 'yedikleri içtikleri ayrı gitmemek', 'her zaman bir arada olmak ve sıkı ilişki içinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12908, 'yedirip içirmek', 'beslemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12909, 'yeğ tutmak', 'yeğlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12910, 'yeğniyi yel alır, ağır yerinde kalır', 'hoppa züppe kimseler, hiçbir yerde barınamazlar; ağırbaşlı olanları kimse tedirgin edemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12911, 'yeis duymak', 'üzüntü çekmek, kahrolmak: ''Bu kelimeyi işitince derin bir yeis, anlatılmaz bir elem duyarım.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12912, 'yeise bürünmek', 'umutsuz, üzüntülü olmak: ''Omuzları bir ihtiyar gibi çökmüş, sesi yeise bürünmüş, kendi kendine söyleniyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12913, 'yeise kapılmak', 'çok üzülmek: ''Şimdi bu ümidin boşa çıktığını anlayınca birden yeise kapıldı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12914, 'yekûn çekmek', 'konuşmaya son vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12915, 'yekvücut olmak', 'birleşmek, tek bir yürek olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12916, 'yel gibi', 'çok hızlı bir biçimde, çabucak, hızla.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12917, 'yel gibi gelen sel gibi gider', 'emek vermeden ele geçen para kısa zamanda çarçur olur gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12918, 'yel kayadan ne koparır (alır, anlar, aparır)', 'sağlam karakterli kişiler, sağlam temele dayanan işler önemsiz etkilerle sarsılmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12919, 'yel üfürdü, sel (su) götürdü', 'mal birdenbire ve sebepsiz ortadan yok oluverdi.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12920, 'yel vermek', 'rüzgârı veya havayı herhangi bir şeyin üzerine yöneltmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12921, 'yel yeperek yelken kürek', 'aceleyle, telaşla: ''Kızım çıldırdın mı? Böyle yel yeperek yelken kürek, sağını solunu görmeden nereye gidiyorsun?'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12922, 'yele vermek', 'savurmak, boşuna harcamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12923, 'yelken açmak', 'yola çıkmak için hareket etmek: ''Kayıkçı yelkeni açmak için ilkin direği yerine oturtmalıdır.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12924, 'yelken basmak', 'yola çıkmak, hareket etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12925, 'yelken dikmek', 'tekneye yelken takmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12926, 'yelkenleri suya indirmek', 'direnmekten vazgeçip karşısındakinin dediğini benimsemek, kabul etmek: ''Ben böyle çıkışınca ister istemez yelkenleri suya indiriyorlardı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12927, 'yem dökmek (koymak)', '1) avlanılacak hayvanları bir yere çekmek için yiyecek dökmek; 2) mec. aldatabilmek için inanç verici davranışta bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12928, 'yem istemez, su istemez', 'elde tutulması hiçbir külfet getirmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12929, 'yem kestirmek', 'yolda durup hayvanlara yem yedirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12930, 'yem olmak', '1) herhangi bir hayvan tarafından yenilmek; 2) mec. birinin tuzağına düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12931, 'yeme de yanında yat', 'çok lezzetli veya çok hoş olan şeyler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12932, 'yemeden içmeden', 'vakit geçirmeden, hemen: Yemeden içmeden gitmiş, benim söylediklerimi yetiştirmiş.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12933, 'yemeden içmeden kesilmek', 'bir üzüntü veya heyecan sebebiyle yiyemez, içemez duruma gelmek, iştahı kesilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12934, 'yemek çıkarmak', 'ağırlamak için yemek sunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12935, 'yemek seçmek', 'bazı yemekleri sevmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12936, 'yemek vermek', 'konukları yemeğe çağırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12937, 'yemeyenin malını yerler (demine hu çekerler) (üstüne bir bardak su içerler)', 'pintinin yemeye kıyamayarak biriktirdiği malı, sağlığında gücünün yetmediği kişiler, öldükten sonra da mirasçıları bol bol yerler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12938, 'yemin billah etmek', ' hlk. Tanrı adını anarak ant içmek: ''Bir yandan inliyor, bir yandan da yemin billahlar ediyordur.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12939, 'yemin billah vermek', 'yemin etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12940, 'yemin etsem başım ağrımaz', 'gerçek olduğuna korkmadan yemin ederim.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12941, 'yemin içmek', ' hlk. ant içmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12942, 'yemin verdirmek (ettirmek)', 'ant içirmek: ''Hey, aklıevvel kadın! Nasıl bir yemin ettirmek niyetindesin bize?'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12943, 'yemin vermek', 'ant vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12944, 'yemini basmak', 'çabuk ve kuvvetli olarak yemin etmek: ''Her akşam, beş paralık alışveriş etmedim diye yemini basar.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12945, 'yemişsiz ağaca taş atmazlar', 'bilgili, hünerli, işinde başarılı olan kimselere genellikle sataşılır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12946, 'yenene içilene bakılmamak', 'bir şey gidere önem verilmeden bol bol harcanmak: ''Bütün bu hayatın mahrumiyetleri pahasına elde edilmiş para ortaya dökülür, yenene içilene bakılmaz.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12947, 'yengece ''niçin yan yan gidersin?'' demişler, ''serde kabadayılık var'' demiş', 'bir işi, herkesten farklı bir yöntemle yapanların bu yöntemleri olumlu sonuç veriyorsa onların kendilerine özgü olan davranışlarını iyi karşılamak gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12948, 'yengeç gibi', 'yan yan yürüyen (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12949, 'yeni dosttan vefa gelmez', 'aralarında ufak tefek dargınlıklar olsa bile eski dostlar birbirlerine düşman olmazlar, yeni kazanılan dostlarla arada henüz sıkı bir bağ oluşmadığı için bu durum söz konusu değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12950, 'yenice eleğim, seni nerelere asayım?', 'kişi, elde ettiği yeni ve güzel bir şeyi örselememek için büyük özen gösterir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12951, 'yenik düşmek', 'yenilmek, mağlup olmak: ''Bütün savaşlardan alnının akıyla çıkmış bir denizci. Hiç yenik düşmemiş.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12952, 'yenik pehlivan güreşten bıkmaz', 'başarılı olmak için yaptığın işi iyi bilmek yetmez hırslı olmak da gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12953, 'yenik pehlivan, oyuna doymaz', 'başarılı olmak için yaptığın işi iyi bilmek yetmez hırslı olmak da gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12954, 'yenik saymak', 'yenilmiş olarak kabul etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12955, 'yenilgiye uğramak', 'yenilmek, mağlup olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12956, 'yenilik yapmak', 'değişiklik yapmak, değişiklik getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12957, 'yenilir yutulur değil (olmamak)', '1) yenmeyecek nitelikte olan (yiyecek); 2) hoşa gitmeyen, beğenilmeyen nitelikte olan: ''Kağnı gıcırtısını sineye çekmek zor, bu zıkkım pek yenir yutulur şey değil ki!'' -B. R. Eyuboğlu. 3) çok ağır (söz); 4) mec. kendisiyle başa çıkılamayacak durumda olan: Bu adam öyle yenilir yutulur gibi değil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12958, 'yer açmak', '1) bir kimseye oturması için yer hazırlamak; 2) mec. yer bırakmak, imkân vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12959, 'yer bakır gök demir kesilmek', 'tamamen tükenmek, bitmek, yoksul duruma düşmek: ''Yer bakır gök demir kesilmiş, günlerden beri deniz karış karış aranmış, balık yoktur.'' -S. F. Abasıyanık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12960, 'yer bulmak', '1) oturacak yer sağlamak: Sinemada zar zor bir yer bulduk. 2) bir kimse bir işe, görev yapacağı bir yere yerleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12961, 'yer değiştirmek', 'bulunduğu yerden bir başka yere geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12962, 'yer demir gök bakır', '1) çorak ve sıcak bir yeri niteler; 2) mec. şartların zor, imkânların kısıtlı olduğu durumlarda söylenen bir söz; 3) mec. hiçbir yardım ve umut olmadığında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12963, 'yer etmek', '1) iz bırakmak; 2) iyice yerleşmek: ''Aklımda yer etmiş olmalı ki mahalleden çıkarken biliyordum oraya gideceğimi.'' -O. Pamuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12964, 'yer kaplamak', 'önemli bir hacim tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12965, 'yer kapmak', 'kalabalık içinde kendine yer bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12966, 'yer öpmek', ' esk. bir büyüğün önüne eğilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12967, 'yer tutmak', '1) yer ayırmak; 2) yer kaplamak; 3) işlevi ve etkisi olmak: ''Çağdaş insanın hayatında gazete mühim bir yer tutar.'' -M. Kaplan. 4) önemli sayılmak, önemi olmak: ''Herkes onun az zamanda büyük yer tutacağını, bir zaman gelip sefir, nazır olacağını söylüyorlar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12968, 'yer vermek', '1) önemli saymak, saygı göstermek: ''Etrafını zehirleye zehirleye yaşadıktan sonra hâlâ insanlar ona kendi aralarında bir yer veriyorlardı.'' -M. Yesari. 2) bir olaya yol açmak, imkân tanımak; 3) önemli bir görev vermek; 4) kendi yerini bir başkasına bırakmak: ''Kadınlara yer vermek alışkanlığı da olmadığından, çok kez ayakta kalır.'' -E. Bener. 5) kullanmak: ''Orta oyununda dekor gibi donatıma da pek az yer verilmiştir.'' -M. And. 6) söz etmek, değinmek; 7) ağırlık vermek: ''Bu dönem, daha çok kısa ve vodvil türünde komedyalara yer vermiştir.'' -M. And. 8) konu edinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12969, 'yer yarılıp içine girmek (geçmek)', '1) yitirilip bir türlü bulamamak; 2) çok utanmak: ''Hanımların içinde rezil olmuştur, yer yarılsa da içine geçsem diye aklından geçmiştir.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12970, 'yer yerinden oynamak', '1) bir iş çok gürültülü, telaşla ve heyecanla yapılmak: ''Ertesi gün cenaze kaldırılırken yer yerinden oynamalıydı.'' -H. Topuz. 2) bir olay toplumda büyük tedirginlik yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12971, 'yerde kalmak', 'saygı görmemek, yüzüne bakılmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12972, 'yerde yatan yumurta, gökte uçan kuş olur', 'bugün beğenmediğiniz, kendisinde yetenek bulmadığınız çocuğun zamanla bilgisi, görgüsü artar, toplumda önemli bir yeri olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12973, 'yerdeki yüze basılmaz (kimse basmaz)', 'alçak gönüllü olanları kimse hor görmez, herkes onları korur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12974, 'yerden göğe kadar', 'pek çok: ''Hakkın var imam, hakkın var, yerden göğe kadar hakkın var.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12975, 'yerden yere çalmak', 'çok hırpalamak: ''Bir oyunu belli ölçülere göre değil, ne olduğu belli olmayan kendi beğenisine göre yerden yere çalıyor.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12976, 'yerden yere vurmak', 'birine türlü yönlerden saldırarak onu çok aşağılayıcı bir duruma düşürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12977, 'yerdiğin oğlan (küçük) yer tutar', 'bugün beğenmediğiniz, kendisinde yetenek bulmadığınız çocuğun zamanla bilgisi, görgüsü artar, toplumda önemli bir yeri olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12978, 'yere bakan yürek yakan', '''uysal ve uslu göründüğü hâlde sinsice kötülük yapan'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12979, 'yere bakmak', 'ihtiyarların ölümü yakın olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12980, 'yere baktırmak', 'utandırmak, mahcup etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12981, 'yere batasıca (batsın)', '''yok olsun, ölsün'' anlamında kullanılan bir ilenme sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12982, 'yere batmak', '1) yok olmak; 2) çok utanmak, mahcup olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12983, 'yere çalmak', 'yere atmak, yere fırlatmak: ''Ellerini uzatıp o koca gövdeyi havaya kaldırdı ve başının üzerinde döndürüp sırtüstü yere çaldı.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12984, 'yere göğe koymamak', 'nasıl ağırlayacağını, nasıl memnun edeceğini bilmemek, çok önem vermek: ''Bunun için Necla ile Ayşe onu yere göğe koymuyor -enişte diye- pervane gibi etrafında dönüyorlar.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12985, 'yere sağlam basmak', 'titiz ve dikkatli davranmak: ''Uyanıklar, elbette yere sağlam basarlar. Çevreyi hesaba katarlar.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12986, 'yere sermek', '1) kötü bir duruma sokmak, yenmek: ''Sen beni yere seren darbenin ne olduğunu anlıyor musun?'' -Ö. Seyfettin. 2) vurup öldürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12987, 'yere vurmak', '1) kötü bir duruma sokmak: ''Bu askerlik oyununda yere vurduğu adama, kaideye uygun olan hareketi muhafaza ediyor.'' -H. E. Adıvar. 2) yenmek, alt etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12988, 'yere yığılmak', 'yere düşmek: ''Bu kadar insanın, bu kadar alçağın gözü önünde yere yığılmak istemiyordu.'' -O. Pamuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12989, 'yere yıkılmak', 'yere düşmek: ''Bana öyle geldi ki, zevcem, bu ilk manzarayı görünce, bayılmamak, yere yıkılmamak için bütün gücünü sarf etti.'' -N. F. Kısakürek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12990, 'yeri başka', '''daha başka bir değeri olan, önemi olan'' anlamında kullanılan bir söz: ''Bu kadın da iyi kadındır. Bunu da seviyorum ama Naime ilk göz ağrısıydı. Yeri başkadır.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12991, 'yeri gelmek', 'sırası gelmek, zamanı uygun olmak: ''Yeri gelmişken delikanlılara bir öğüt notu daha düşeyim.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12992, 'yeri göğü ben yarattım demek', 'çok gururlu olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12993, 'yeri göğü birbirine katmak', 'aşırı telaş yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12994, 'yeri göğü inletmek', 'yüksek sesle ve olanca güçle bağırmak: ''Havayı kokladı, sonra bütün gücüyle yeri göğü inleten bir kişnemeyle kişnedi.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12995, 'yeri göğü tırmalamak', 'çok sancı, acı çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12996, 'yeri göğü tutmak', 'her tarafı ele geçirmek, denetim altında bulundurmak: ''Müttefikler yeri göğü ve bütün köşe başlarını tutmuştur.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12997, 'yeri olmak', '1) uygun olmak; 2) sırası, uygun zamanı olmak; 3) saygınlığı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12998, 'yeri öpmek', ' alay yere düşmek, yere serilmek: Ayağı kayınca yeri öptü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (12999, 'yeri soğumadan', 'ayrılan bir kimsenin ardından çok zaman geçmeden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13000, 'yeri var!', '''uygundur, iyidir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13001, 'yeri yurdu belirsiz olmak', 'belli bir yeri olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13002, 'yeridir', '''layıktır, uygundur, münasiptir'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13003, 'yerin dibine batırıp çıkarmak', 'çok utandırmak, rezil etmek: ''Bir hikâye anlatır, erkekleri yerin dibine batırır çıkarırdı.'' -N. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13004, 'yerin dibine geçmek (batmak veya girmek)', '1) çok utanıp sıkılmak: ''Memleketin ne tarafına gitsem haritayı şaşırıyor, bilgisizliğimden yerin dibine geçiyordum.'' -B. R. Eyuboğlu. 2) görünmez olmak, kaybolmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13005, 'yerin kulağı var', 'gizli konuşulan bir şey umulmadık bir yoldan başkalarınca duyulabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13006, 'yerinde bulmak', 'doğru olduğunu kabul etmek: ''Hayatını değiştirme kararımı yerinde bulması beni de memnun etti.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13007, 'yerinde duramamak', '1) sürekli kıpırdamak; 2) içi içine sığmamak: ''En ufak bir şeyden sevinir, yerlerinde duramaz olurlar.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13008, 'yerinde kalmak', '1) başka yere gitmemek; 2) makam veya aşama değişmemek: ''Bu keyif düşkünü memurlar suya sabuna dokunan işlere karışmadıklarından senelerce yerlerinde kalırlar.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13009, 'yerinde olmak', '1) uygun olmak; 2) tamam olmak, iyi durumda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13010, 'yerinde saymak', '1) yürür gibi yaparak hep aynı yerde, sürekli olarak ayağın birini kaldırıp birini basmak; 2) mec. ilerleyememek, gelişememek, değişememek: ''Bu yerinde sayan kafamıza ne ad takmalı?'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13011, 'yerinde su çıkmak', 'haklı bir sebep olmadan yerini bırakanlara veya bırakmak isteyenlere kınama ve engelleme amacıyla söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13012, 'yerinde yeller esmek', 'artık bulunmamak, yok olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13013, 'yerinden fırlamak', 'oturulan yerden hızla kalkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13014, 'yerinden oynamak', '1) yerinden ayrılmak; 2) coşkulu, gürültülü, karışık bir zaman yaşamak: ''Sol cenah uzun ve merhametsiz gülleler altında yerinden oynuyor.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13015, 'yerinden oynatmak', 'başka yere kaldırmak, yerini değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13016, 'yerine düşmeyen gelin yerine yerine, boyuna düşmeyen esvap sürüne sürüne eskir', 'kendine uygun bir evlilik yapamayan kız yerine yerine helak olur, boya göre dikilmemiş elbise de yerde sürünerek yıpranır gider.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13017, 'yerine geçmek', '1) görevden ayrılan birinin yerini almak; 2) bulunmayan bir nesnenin veya kavramın yerine kullanılabilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13018, 'yerine gelmek', '1) yapılmak, olmak: İsteğiniz yerine geldi. 2) eski duruma dönmek: Sağlığım yerine geldi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13019, 'yerine getirmek', '1) istenileni, gerekeni yapmak: ''Sırf iri, kara gözlerindeki endişe dinsin diye, itiraz etmeden her isteğini yerine getirdim.'' -N. Uygur. 2) eski duruma döndürmek; 3) ifa etmek: ''Şirket su veremeyecekse taahhüdünü yerine getirmediği için dağıtılır.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13020, 'yerine koymak', '1) gibi görmek, saymak: ''Hem de yetişkin, yosma bir kadın beni erkek yerine koymuştu.'' -N. Cumalı. 2) yitirilen, elden çıkan bir şeyin, benzerini veya eşini sağlamak: Kaybolan kitaplarımı yerine koyamadım.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13021, 'yerine oturmak', '1) iyi yerleşmek; 2) bir durum, bir düşünce vb. benimsenmek, yaygın duruma gelmek, yerleşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13022, 'yerini almak', 'yerine geçmek: ''Öğretim ve öğrenim yerini de ister istemez politik tartışmalar almıştı.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13023, 'yerini beğenmek', 'bitki yerini gelişmesine çok uygun bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13024, 'yerini bilmeyen, yılda bir kat urba eskitir', 'hangi alanda çalışabileceğini önceden iyi kestirememiş olan kişi sık sık yer ve iş değiştirme yüzünden hayli zarar görür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13025, 'yerini bulmak', '1) uygun olan yerde olmak: Depremden sonra gönderilen battaniyeler yerini buldu. 2) kendine yakışan makamı, durumu bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13026, 'yerini doldurmak', '1) görevini başarı ile yapar olmak; 2) görevinden ayrılan birinin yerine gelen kişi, önceki görevli kadar başarılı olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13027, 'yerini ısıtmak', 'bir yerde uzun süre kalmak: ''Yeni nazır olmuşsun, yerini ısıtmaya bakacaksın, yüzyıllardır düzelmemiş işleri düzeltecek değilsin ya!'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13028, 'yerini sevmek', 'yerini beğenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13029, 'yerini yapmak', 'bir şey elde etmek amacıyla girişimde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13030, 'yerle beraber', 'yer düzeyinde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13031, 'yerle bir etmek', 'temeline kadar yok etmek, tahrip etmek: ''Ali bütün karargâhı yerle bir edecek bu korkunç alete bakmak istedi.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13032, 'yerle gök bir olsa', '''sonu ne olursa olsun'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13033, 'yerle yeksan etmek', 'yerle bir etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13034, 'yerlerde sürünmek', 'çok perişan, acınacak bir durumda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13035, 'yerlere geçmek', 'çok utanıp sıkılmak veya kahrolmak: ''O kahkaha nöbetlerinden birini tatmak üzere olduğunu hissediyor, yerlere geçiyordum.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13036, 'yerlere kadar eğilmek', 'aşırı saygı göstermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13037, 'yerleri süpürmek', 'saç, etek, paça çok uzun olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13038, 'yerli yerinde olmak', '1) eskiden olduğu yerde bulunmak: ''... birçok yalılar ve köşklerse ... şimdi sazları ve sözleri susmuş olmakla beraber yine yerli yerindeydi.'' -A. Ş. Hisar. 2) uygun, yakışır olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13039, 'yerli yerine oturmak', 'uygun düşmek: ''Her şey denk düşüyor, müthiş bir düzenle yerli yerine oturuyordu.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13040, 'yersen kap açık, yemezsen kapı açık', 'sabırlı ol, katlan ve kararını ona göre ver.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13041, 'yersiz yurtsuz kalmak', '1) barınacak bir yeri bulunmamak, oturacak yeri olmamak: ''Dünya üzerinde yersiz yurtsuz kalmış iki arkadaş.'' -R. H. Karay. 2) bütün varlığını yitirip çok zor durumda olmak: ''Vaktiyle bir mahalle halkını barındıran hanların, bir çarşı teşkil eden dükkânların sahibi şimdi yersiz, yurtsuz kalmıştır.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13042, 'yeşil ışık yakmak', 'uygun olabileceğini, izin verilebileceğini belli etmek: ''Bu anıt, onun kişiliğinin getirdiği bir dokunulmazlıkla daha sonra nice heykellere yeşil ışık yakıyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13043, 'yeter de artar', '''fazlasıyla yeter'' anlamında kullanılan bir söz: ''Onun okudukları ona yeter de artar bile.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13044, 'yeter ki', 'ancak, şu şartla: ''Yeter ki biri ona iyice bakmış, oturup onunla konuşmuş olsun!'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13045, 'yeteri kadar', 'yetecek ölçüde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13046, 'yetimi okşamışlar, vay sırtım demiş', 'bir kimsenin haksızlığa uğramaması için arkası, koruyucusu bulunmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13047, 'yetişemediğin köyün alt tarafında (beri yanında) yat', 'yapmak istediğiniz şeyi eksiksiz olarak başaramıyorsanız kendinizi boş yere zorlamayıp yapabildiğiniz kadarıyla yetinmeniz gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13048, 'yetkili kılmak', 'yetkisini kullanma imkânını vermek: ''Fakat kendileri gelmeden önce, bir küçük pürüzü gidermek konusunda da yetkili kıldılar.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13049, 'yetmişine merdiven dayamak', 'ileri yaşlara ulaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13050, 'yetti artık (gayrı)', 'bir olaydan veya sözden aşırı derecede sıkıntı duyulduğunu anlatan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13051, 'yığılıp kalmak', '1) birikmek; 2) düşmek, yıkılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13052, 'yıkım olmak', 'büyük zarara yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13053, 'yıkkınlık göstermek', 'yıkılmaya yüz tutmak: ''Şimdi büsbütün yanan Aksaray''ın daha benim küçüklüğümde yıkkınlık gösteren konaklarını bilmem hatırlayanlarınız var mıdır?'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13054, 'yıl on iki ay', 'sürekli olarak, sürekli bir biçimde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13055, 'yıl uğursuzun', 'arsız, yüzsüz kimselerin el üstünde tutulduğu ve işlerini dürüst kimselerden daha kolay yürüttükleri bir zamanda yaşıyoruz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13056, 'yılan gibi', '1) hain, sevimsiz ve soğuk (kimse): ''Yılan gibisin, insanları sokmaktan zevk alırsın.'' -N. Hikmet. 2) kıvrım kıvrım: ''Geminin arkasına gittim, dümen suyunun bir yılan gibi uzayıp gittiğini gördüm.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13057, 'yılan gibi sokmak', 'bir kimseye sinsice kötülük etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13058, 'yılan sokan uyumuş, aç kalan uyumamış', 'yoksulluk, açlık acıların en dayanılmazıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13059, 'yılana yumuşak diye el sunma', 'kişi, yumuşak huylu görünen herkese aldanmamalı, daima tehlikeli bir durumla karşılaşabileceğini düşünmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13060, 'yılanın başı küçükken ezilir', 'büyüyeceği belli olan her tehlike daha en başta önlenmeli, düşman güçlenip büyük zarar verecek duruma gelmeden tedbir alınmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13061, 'yılanın kuyruğuna basmak', 'kötü bir kimseye kötülük yapacak fırsat vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13062, 'yılanın sevmediği ot, deliğinin ağzında biter', 'başkalarına kötülük etmek isteyenler karşılarında hep hoşlanmadıkları şeyleri bulurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13063, 'yıldırım çarpmışa dönmek', 'apansız kötü bir durum karşısında kalıp ne yapacağını bilememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13064, 'yıldırım gibi', 'büyük bir hızla: ''Taarruz bir yıldırım gibi inecekti.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13065, 'yıldırımla vurulmuşa dönmek', 'yıldırım çarpmışa dönmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13066, 'yıldırımları üstüne çekmek', 'bazı davranışlarıyla birçok kimseyi kızdırarak saldırılarına, eleştirilerine yol açmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13067, 'yıldız akmak (kaymak, uçmak)', 'yıldız gökyüzünde hızla yer değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13068, 'yıldızı parlamak', 'başarı yönünden herkesin dikkatini çekecek bir duruma gelmek, ün kazanmak: ''Yeni Dâhiliye Nazırı Zati Bey''in yıldızı parladıkça Zaptiye Nazırı Selim Paşa''nın ikbali sönmeye yüz tuttu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13069, 'yıldızı sönmek', 'ününü yitirmek: ''Bu gecelerin artık benzi soluyor, talihi kararıyor, yıldızı sönüyordu.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13070, 'yıldızı (yıldızları) barışmamak', 'görüş, duygu ve düşünce bakımından uyuşmamak: ''Adayı ve adalıları o kadar sevmeme rağmen bir türlü yıldızım barışmamıştır.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13071, 'yıldızları saymak', 'geceleri uyku uyuyamamak: ''Yıldızları sayarak bekliyordum sabahı.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13072, 'yılın eksiğini nisan getirir, nisanın eksiğini yıl getirmez', 'en verimli zaman olan nisan ayında hava koşulları uygunsa bütün bir yılın olumsuz etkilerini giderir, tam verim alınır ama bunun tersi olur da nisan ayı uygun hava koşullarında geçmezse verilen emeklerin karşılığı alınamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13073, 'yırtıcı kuşun ömrü az olur', 'başkalarına saldırmayı alışkanlık edinen kimsenin düşmanı çok olur, bu düşmanlar onun canına kıyarlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13074, 'yısa beraber!', 'hep birlikte.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13075, 'yiğidim yiğit olsun da yerim çalı dibi olsun', 'kadının kocasının fakir olması önemli değildir, yeter ki aile sorumluluklarını yerine getirsin.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13076, 'yiğidin malı meydandadır', 'çıplak gezmekten hoşlanan ve utanması, sıkılması olmayan kimselerle alay etmek için kullanılan bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13077, 'yiğidin sözü, demirin kertiği', 'mert adamın ağzından çıkan söz demire kazınmış çentik gibi kalıcı olur; dediğini yapar, sözünden dönmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13078, 'yiğit başından devlet ırak değildir', 'bir gencin zengin olması her zaman beklenebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13079, 'yiğit bin yaşar, fırsat bir düşer', 'kişiye ömrü boyunca ancak bir kez çok önemli bir iş yapma fırsatı geçer, bu fırsatı kaçırmamalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13080, 'yiğit ekmeğiyle yiğit beslenir', 'seni korumalarını istediğin kimselerle aynı nitelikleri taşımıyorsan onların yanında barınamazsın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13081, 'yiğit lakabıyla anılır', 'yiğit kazandığı ünle tanınır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13082, 'yiğit meydanda belli olur', 'sözle atıp tutmak kolaydır, asıl marifet, işbaşında kendini göstermektir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13083, 'yiğit yarasına yiğit katlanır', 'yiğitten gelen ağır saldırıyı ancak yiğit olan kaldırabilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13084, 'yiğit yiğide at bağışlar', 'aynı yolun yolcuları birbirleri için her tür özveriyi gösterirler, gerektiğinde en değerli varlıklarını bile birbirlerinin yararına sunarak yardımlaşmaktan kaçınmazlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13085, 'yiğitliğe leke (bok) sürmemek', 'mertliğe aykırı davranışta bulunmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13086, 'yiğitlik sende kalsın', 'özveri, hoşgörü ve ılımlılık öğütleyen söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13087, 'yiğitlik taslamak', 'yiğitmiş gibi davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13088, 'yitip gitmek', 'görünmez olmak, ortadan kalkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13089, 'yiyen bilmez, doğrayan bilir', 'bir iş yapılırken ne kadar güçlük çekildiğini, o işi başarmış olan bilir; başarılmış olan bu işten yararlanan bilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13090, 'yiyim yeri etmek (yapmak)', 'bir yeri kendi çıkarına kullanmak: ''Şimdi de mahalle bakkallığını mı yiyim yeri yaptın?'' -Ö. Seyfettin. ''Bir sürü halayık ve hizmetçiden başka takım takım fakir akrabalar, paşayı yiyim yeri etmiş.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13091, 'yiyip bitirmek', '1) tüketmek; 2) onmaz duruma getirmek, yıkımına sebep olmak; 3) sürekli olarak tedirgin etmek, üzmek, hırpalamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13092, 'yiyip içmek', 'karın doyurmak, beslenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13093, 'yoğun incelene kadar, ince süzülür', 'bir hastalık, bir sıkıntı karşısında güçlü gücünden bir parçasını yitirerek zayıflar ama zayıf olan, ölecek duruma gelir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13094, 'yoğurdum (ayranım) ekşidir diyen olmaz', 'herkes sattığı malı, kendi işini, tutumunu ve davranışını över.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13095, 'yoğurt çalmak', 'yoğurt yapmak için süte yoğurt mayası koymak: ''Ana, inek sağar; yoğurt çalar, yayık vurur.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13096, 'yoğurt gibi', 'koyu ve katılaşmış (nesne).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13097, 'yok ananın örekesi', ' argo saçma bir söze karşı verilen karşılık: ''Dünya yuvarlakmış... Yok ananın örekesi.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13098, 'yok canım', '1) ''öyle şey olmaz, hayır, inanmayın'' anlamında kullanılan bir söz: ''Yok canım, ben belediye taraflısı değilim. Sizden yanayım.'' -M. Ş. Esendal. 2) ''sahi mi, öyle mi?'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13099, 'yok devenin başı (pabucu veya nalı)', ' tkz. çok abartılı bir söz karşısında kullanılan bir söz: İki saatte ağaç yetiştireceklermiş. -Yok, devenin başı!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13100, 'yok etmek', '1) ortadan kaldırmak, ifna etmek, izale etmek; 2) mec. varlığına son vermek: ''Kurtulmak için ya yok olmalı ya yok etmeli.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13101, 'yok olmak', '1) ortadan kalkmak, kaybolmak; 2) mec. varlığı sona ermek: ''Bir umuttur yok olmaya karşı az çok hazırlıklı olmak.'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13102, 'yok satmak', 'bir mal, çok satıldığı için çabucak tükenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13103, 'yok yok', '1) ''ne istersen var'' anlamında kullanılan bir söz: Bu mağazada yok yok. 2) ''hayır hayır!'' anlamında kullanılan bir söz: Yok yok, gidelim!', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13104, 'yoksul âlâ ata binse selam almaz', 'yoksul kimse geçici olarak bile iyi bir duruma gelse herkese yüksekten bakar, kimseye selam bile vermez olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13105, 'yoksul, ata binse selam almaz', 'yoksul kimse geçici olarak bile iyi bir duruma gelse herkese yüksekten bakar, kimseye selam bile vermez olur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13106, 'yoksulluk çekmek', 'sürekli yoksulluk içinde bulunmak: ''O hep faydasız üzüntüler duyar, sıradan arzularla, varlıklar içinde, yoksulluklar çekerdi.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13107, 'yoksun bırakmak (etmek, kılmak)', 'yoksun duruma getirmek, bir şeyin yokluğunu çektirmek: ''Sözlerimi dinlediler ve öyle cimrileştiler ki kendilerini bile bir lokma yemekten yoksun bıraktılar.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13108, 'yoksun kalmak', 'sahip olunan bir şeyi kaybetmek, kullanamamak: ''Ben de kendimi, köklerinden yoksun kalmış herkesin düştüğü o sefahat âleminin gergin tekdüzeliğine bırakmıştım.'' -R. Mağden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13109, 'yoksun olmak', 'belli bir şeye, sahip olamamak: ''Kadın konularında sağduyudan hayli yoksun oluşu kalıtımsaldı.'' -R. Erduran.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13110, 'yoktan var etmek', 'yaratmak, ortaya çıkarmak: ''Ama bu düşmanları kendisi âdeta çalışarak hazırlar, yoktan var ederdi.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13111, 'yoktan yonga çıkmaz', 'olmayan şeyden hiçbir şey elde edilmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13112, 'yol açmak', '1) yol yapmak; 2) kapanmış olan yolu geçilir duruma getirmek; 3) kalabalık bir yerde genellikle saygıdeğer bir kişinin geçmesi için insanları kenara çekip yol vermek; 4) mec. bir olayın sebebi olmak: ''Seniha''nın bu hareketi türlü türlü tefsirlere yol açtı.'' -Y. K. Karaosmanoğlu. 5) mec. davranışlarıyla başkalarına örnek olmak: ''Bize yol aç, erkân göster; yollar aç bize de, biz de adam sırasına girelim.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13113, 'yol almak', 'yolda ilerlemek: ''Hayatta epeyce yol almış, çoluk çocuğa karışmış bir münevver olarak sürüden ayrılmaya korkuyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13114, 'yol aramak', 'çare bulmaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13115, 'yol ayrımına gelmek', '1) yolların birbirinden ayrıldığı yerde bulunmak; 2) mec. farklı düşünce, görüş ve ülkü yüzünden birbirinden ayrılmak: ''Seksen iki yılı birlikte yürümüş, yol ayrımına gelmişlerdi nihayet.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13116, 'yol bilen kervana katılmaz', 'bir işi kendi başına yapabilecek olan başkalarının yardımına gerek duymaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13117, 'yol bilenle yürüyen, yorulmaz', 'bir işi yoluyla, yöntemiyle yapan kolay yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13118, 'yol bulmak', 'çare bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13119, 'yol çizmek', 'bir konuda plan yapmak: ''Bütün günlerimiz için kendimize bir yol çizer, sonra her gün bunun aksine hareket ederiz.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13120, 'yol gitmek', 'yolda ilerlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13121, 'yol görünmek', 'gitmek gerekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13122, 'yol göstermek', '1) kılavuzluk etmek, yolu bilmeyene anlatmak, tarif etmek: ''Elinde güçlü bir çıra vardı, onu yüksekte tutarak yolculara yol gösteriyordu.'' -N. Araz. 2) mec. ne yapılacağını, nasıl davranılacağını öğretmek: ''Biz benzincinin istihkakını düşeriz, siz de benzini alırsınız diye yol gösterirler.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13123, 'yol gözlemek', '1) bir şeyin olmasını ummak; 2) bir kimsenin gelmesini beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13124, 'yol ile giden yorulmaz', 'bir işi yoluyla, yöntemiyle yapan kolay yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13125, 'yol iz bilmek', '1) gideceği yolu ve yeri bilmek; 2) görgülü davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13126, 'yol kesmek', '1) geçmesine engel olmak, durdurmak: ''Senin yolunu kesecek, engel olacak değilim.'' -M. Yesari. 2) ıssız yerlerde soygunculuk yapmak; 3) motor vb. hızını azaltmak, devrini düşürmek: ''Motorun yanaşmasını bekliyorum, yol kestiği için şimdi hiç gürültü etmiyor.'' -Z. Selimoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13127, 'yol sormakla bulunur', 'bir işte izlenecek doğru yol, bilenlerden sorularak öğrenilmelidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13128, 'yol şaşmak', ' esk. yol çatallaşıp karışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13129, 'yol tepmek', 'çok uzun bir süre yürümek: ''Adam onca yolu tepip buraya dek gelmiş.'' -T. Oflazoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13130, 'yol tutmak', 'bir yoldan kimseyi geçirmeyecek biçimde düzen kurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13131, 'yol vermek', '1) geçmesine izin vermek: ''Hafif sağ yapıp askerî bir kamyona yol verdi.'' -A. İlhan. 2) hızını artırmak; 3) işten çıkarmak, işine son vermek: ''Mademki bu işi yapamıyorsun, o hâlde başka işimiz yok derler, bana yol verirler.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13132, 'yol vurmak', ' esk. yol kesmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13133, 'yol yakınken', 'sezilen veya beliren kötü duruma düşmeden: ''Bizimle birlikte gelmesinler. Yol yakınken başlarının çaresine baksınlar.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13134, 'yol yapmak', '1) yol oluşturmak: ''Geçen köylünün, arabanın, sürünün izi buraları yol yapmıştır.'' -R. H. Karay. 2) kandırmaya çalışmak, avutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13135, 'yol yürümek', 'yolda gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13136, 'yol yürümekle, borç ödemekle (vermekle) (tükenir) (biter)', 'birden ödenemeyen bir borç azar azar verilerek ödenebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13137, 'yola çıkmak', '1) araca binmek üzere yolüstünde durmak; 2) bir yere varmak için bulunduğu yerden ayrılarak yolculuğa başlamak, harekete geçmek: ''Yola öğle yemeğinden sonra çıktık.'' -S. Kocagöz. 3) herhangi bir şeyi esas almak, oradan başlamak: ''Bir roman konusundan yola çıkarak Salâh Birsel''in ''Dört Köşeli Üçgen''iyle Orhan Kemal''in ''Murtaza''sı arasında bir akrabalık kuruverdi.'' -S. İleri.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13138, 'yola dizilmek', 'yol kenarında sıralanmak: ''Başında bir tavus tuğ gibi çamlar / Yollara dizilmiş tığ gibi çamlar'' -Z. Ö. Defne.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13139, 'yola düzülmek', 'gidilecek yere doğru yola çıkmak: ''Güneş doğarken yola düzüldük.'' -R. Mağden.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13140, 'yola gelmek', 'istenilen biçimde davranışı kabullenmek, düzelmek, uslanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13141, 'yola getirmek', 'birinin bir konudaki ters tutumunu düzeltmek: ''Her karşısına çıkışta ona nasihat eder, bazen sert söyler, bazen tatlı tatlı yola getirmeye çalışır.'' -H. Pulur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13142, 'yola gitmek', 'yolculuğa çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13143, 'yola revan olmak', ' esk. yola çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13144, 'yola vurmak', ' hlk. 1) yolcu etmek, uğurlamak; 2) yola koyulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13145, 'yola yatmak', 'yola gelmek: ''Birden kabarırsın, sonra yola yatarsın.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13146, 'yola (yollara) düşmek', 'yola çıkmak, yol almaya başlamak: ''Yâre gidecek günümdür / Düşem yollara yollara'' -Erzurumlu Emrah.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13147, 'yola (yoluna) koyulmak', 'yola düzülmek: ''Rüzgâr, karanlığı karıştırır gibi garip bir ahenk içinde eserken biz de yolumuza koyulduk.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13148, 'yolcu etmek', 'yola çıkanı uğurlamak: ''Saat dörtte Vedat''ı yolcu etmiştik.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13149, 'yolcu yolunda gerek', '1) vakit geçirmeden yola devam edilmeli;', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13150, 'yolda kalmak', 'kaza, doğal afet vb. sebeplerden olayı yolda ilerleyememek, gideceği yere varamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13151, 'yoldan çevirmek', 'gideni durdurmak, gitmesine engel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13152, 'yoldan çıkmak', '1) belli bir yol izleyen taşıtlar herhangi bir sebeple yolundan ayrılmak, gitmez olmak; 2) mec. doğru yoldan ayrılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13153, 'yoldan giden yorulmaz', 'bir işi yoluyla, yöntemiyle yapan kolay yapar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13154, 'yoldan kal, yoldaştan kalma', 'yolculukta arkadaş çok önemli olduğundan insan, iyi bir arkadaşla yolculuk yapmak için gerekirse yolculuğunu ileri bir tarihe bırakmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13155, 'yoldan (yolundan) kalmak', 'gidilmek istenen yere gidememek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13156, 'yollara dökülmek', 'kalabalık hâlde yolda olmak: ''İhtiyar annemle büyük dayım, uslanmak bilmeyen okul kaçağını aramak için yollara dökülmüşlerdi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13157, 'yolları ayrılmak', 'iki kişi veya topluluk arasında görüş, düşünce ayrılığı ortaya çıkmak, ayrı görüş ve düşünceleri benimsemek: ''Hayata beraber başladığımız / Dostlarla da yollar ayrıldı bir bir'' -C. S. Tarancı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13158, 'yolları tutmak', 'geçecek kimselere engel olmak, bırakmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13159, 'yolsuz kalmak', 'parasız kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13160, 'yolu açık olmak', 'bir iş, önünde engel olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13161, 'yolu açmak', 'yolda geçişi önleyen engelleri kaldırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13162, 'yolu almak', 'yolun sonuna varmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13163, 'yolu (yolunu) şaşırmak', 'yanlış yola sapmak: ''Yollar ıssızdı, el ayak çekilmişti, sokaklarda yolu şaşırdım.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13164, 'yolun açık olsun', 'yolculara söylenen bir iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13165, 'yoluna baş koymak', 'bir amaca, bir gayeye yönelmek, bütün varlığıyla kendini vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13166, 'yoluna can (canını) vermek', 'birinin uğruna ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13167, 'yoluna çıkmak', '1) karşılamaya gitmek; 2) yolda karşısına çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13168, 'yoluna girmek', 'istenilen, gerekli olan biçimde gelişmeye başlamak: ''Göreceksin, bu konaktan çıkar çıkmaz her şey öyle bir yoluna girecek ki! Bütün uğursuzluklar bu evden geliyor.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13169, 'yoluna koymak', 'istenilen biçime getirmek, düzene koymak: ''Arkadaşının mektebe alınması işini o hafta içinde yoluna koymuş.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13170, 'yoluna sapmak', 'başvurmak: Hile yoluna saptı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13171, 'yolunda gitmek (yürümek)', 'olumlu gelişme göstermek: ''Ticareti yolunda gidiyordu.'' -Ö. Seyfettin. ''Dikkat ve dirayetiyle her işin yolunda yürümesini temin etmişti.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13172, 'yolunu beklemek (gözlemek)', 'gelmesini beklemek: ''Ben merak ederdim, gece yarılarına kadar yolunu beklerdim.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13173, 'yolunu bilmek', 'yöntemini biliyor olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13174, 'yolunu bulmak', '1) gereken çareyi bulmak: ''Bir yolunu bulduğu hâlde onları mektepten atmaya çoktan karar vermişti.'' -R. N. Güntekin. 2) argo yasal olmayan yollardan kazanç sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13175, 'yolunu değiştirmek', 'gittiği yoldan ayrılarak başka yola geçmek: ''Aradan uzun seneler geçer, o kadını sokakta gördüler mi yollarını değiştirirler.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13176, 'yolunu kesmek', 'engel olmak, engellemek: ''Yani şüphelendiği müşterilerin yolunu kesmiyor, uzaktan uzağa onları takip etmekle nefsini köreltiyordu.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13177, 'yolunu sapıtmak', 'doğru yoldan ayrılmak, kötü yola sapmak: ''Feride, senin kaşların lakırtılarına benziyor, güzel güzel, ince ince başlıyor fakat sonra yolunu sapıtıyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13178, 'yolunu tutmak', 'bir yere doğru gitmeye başlamak: ''Bir süre sonra, kara kış gelince bakmış ki olacak gibi değil, güneyin yolunu tutmuş.'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13179, 'yom tutmak', 'uğurlu saymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13180, 'yorgan döşek yatmak', 'ağır hasta olmak: ''Aksi gibi çamaşırcının ihtiyar kocası o akşam birdenbire hastalanmış, kim bilir kaç derece ile yorgan döşek yatmıştı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13181, 'yorgan gitti, kavga bitti', 'anlaşmazlık sebebi olan şey ortadan kalktığında anlaşmazlık da sona erdi.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13182, 'yorgun düşmek', 'çok yorulmak, bitkin duruma gelmek: ''Ben de uykusuzluktan yorgun düşmek üzereyim, yatacağım.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13183, 'yorgun eşeğin (öküzün) çüş (ıslık) canına minnet', 'verilen dinlenme fırsatı, yorgun kişiyi çok sevindirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13184, 'yorgunluğunu almak', '1) dinlenmesine sebep olmak; 2) birini dinlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13185, 'yorgunluk (yorgunluğunu) atmak (çıkarmak)', '1) dinlenmek: ''Mesela şimdi yorgunluk çıkarmak için yıkanmak istersiniz.'' -R. H. Karay. ''Hele trenin yorgunluğunu at bir üzerinden.'' -T. Dursun K. 2) yaptığı işten, yorgunluğu unutturan, sevindirici bir sonuç almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13186, 'yorgunu yokuşa sürmek', 'yapılması güç bir işin, büsbütün güç şartlarda gerçekleştirilmesini istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13187, 'yosun bağlamak (tutmak)', 'üzerini yosun kaplamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13188, 'yön vermek', 'yeni bir biçim, yeni bir düzen vermek: ''Ama unutmayalım ki tecessüslerimize yön veren ihtiyaçlarımızdır.'' -C. Meriç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13189, 'yörüngesine oturmak', '1) yapma uydu uzayda istenilen yörüngede hareket etmek; 2) mec. bir iş yoluna girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13190, 'yufka açmak', 'hamuru yufka durumuna getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13191, 'yuh çekmek', 'beğenilmeyen, tasvip edilmeyen birine veya bir duruma karşı haykırmak: ''Bu yeni kişilik artık Beşiktaş tribününden hakeme yuh çekemez.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13192, 'yuha çekmek', 'yuh çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13193, 'yuhaya tutmak', 'yuh çekmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13194, 'yukarı tükürsem bıyık, aşağı tükürsem sakal', 'aşağı tükürsem sakal, yukarı tükürsem bıyık.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13195, 'yukarıdan almak', 'yumuşaklık göstermemek, ağır önerilerde bulunmak, sert davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13196, 'yuları birinin elinde olmak', 'bir kimsenin davranışları birinin denetiminde, yönetiminde olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13197, 'yuları ele vermek (kaptırmak)', 'birinin sözünden çıkmayacak duruma gelmek, kendi iradesiyle davranmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13198, 'yuları takmak', 'birini sözünden çıkamayacak duruma getirmek, egemenliği altına almak: ''O da sana er geç yuları takar, benden beter olursun.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13199, 'yuları teslim etmek', 'yuları ele vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13200, 'yularsız ata binilmez', 'kurallara bağlı olmayan işin başına geçmek doğru değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13201, 'yularsız deve yedilmez', 'kurallara bağlı olmayan kişi istenildiği gibi yönetilemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13202, 'yumruğuna güvenmek', 'isteklerini yaptırmak için yalnızca bedensel gücüne güvenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13203, 'yumruk atmak (indirmek)', 'yumrukla vurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13204, 'yumruk gibi', 'yumruk büyüklüğünde.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13205, 'yumruk göstermek', 'korkutmak, gözdağı vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13206, 'yumruk kadar', '1) çok iri, büyük: ''Yemek yemek için kıyı kumsalına çıkmış, orada ona yumruk kadar bir örümcek musallat olmuştu.'' -Halikarnas Balıkçısı. 2) küçücük: ''Yumruk kadar çocukcağızı tek başına trene oturtamaz ya...'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13207, 'yumruk yumruğa gelmek', 'yumruklaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13208, 'yumurta kapıya dayanmak (gelmek)', 'yapılacak iş için zaman çok daralmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13209, 'yumurtada kıl bitmez', 'kısır ortamdan verim beklenmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13210, 'yumurtadan çıkan yine yumurta çıkarır', 'her canlı soyuna çeker, soyunun özelliklerini taşır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13211, 'yumurtadan daha dün çıkmış', '''bilgiçlik taslayan toy kimse'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13212, 'yumurtaya kulp takmak', 'bahane bulmakta usta olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13213, 'yumurtayı çalkamak', 'hayvan, üstüne oturduğu yumurtayı çevirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13214, 'yumurtlayan tavuk bağırgan olur', 'çalışkan ve verimli kişi kendini göstermek ve yaptığı yararlı işi herkese duyurmak için sesini yükseltir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13215, 'yumuşak huylu atın çiftesi pek olur', 'yumuşak huylu kimseler öfkelendiklerinde aşırı davranışlarda bulunurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13216, 'yunmuş arınmış (yıkanmış)', '1) yıkanıp temizlenmiş; 2) mec. suçu olmayan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13217, 'yurdun otlusundan kutlusu yeğdir', 'insanın üzerinde yaşadığı yer, rahat ve huzurlu olmadıktan sonra oranın verimli olması çok önemli değildir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13218, 'yurt edinmek (tutmak)', 'bir yeri kendisine, ailesine yurt olarak kabul etmek, vatan tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13219, 'yuva kurmak', 'evlenmek: ''Âşıksak, âşık olduğumuz gençle, yalnız onunla bir yuva kurmak istiyorsak, o kapı da her şeye karşın hâlâ ve hep açıktı.'' -A. Ağaoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13220, 'yuva yapmak', '1) yuva hazırlamak, yuva oluşturmak; 2) evlenmek: ''Hâlbuki genç bir kızla yuva yapmak, ölünceye kadar bahtiyar yaşamak için...'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13221, 'yuvarlak konuşmak', 'bir şeyin ayrıntılarını gereği gibi belirtmeden genel konuşmak: ''Yuvarlak konuşmayı bırak da söyleyeceğini açıkça söyle diye hatibe müdahale etti.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13222, 'yuvarlanan taş yosun tutmaz', 'sürekli olarak iş değiştiren bir kimse başarı kazanamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13223, 'yuvarlanıp gitmek', '1) eldeki imkânlarla geçinmek: ''Biz işte aile gibi bir şeyiz burada, büyük hanımı da kendimize uydurduk, yuvarlanıp gidiyoruz.'' -R. N. Güntekin. 2) birdenbire ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13224, 'yuvasını bozmak', 'aile düzenini dağıtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13225, 'yuvasını dağıtmak', 'kurulu ev düzenini bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13226, 'yuvasını yıkmak', '1) birinin eşinden boşanmasına sebep olmak; 2) biri eşinden ayrılarak kendi aile düzenini yok etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13227, 'yuvayı dişi kuş yapar', 'evin kadını anlayışlı, idareci ve tutumlu olursa ancak o zaman evde dirlik düzenlik sağlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13228, 'yuvayı yapan dişi kuştur', 'evin kadını anlayışlı, idareci ve tutumlu olursa ancak o zaman evde dirlik düzenlik sağlanır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13229, 'yuvayı yürütmek', 'evlilik birliğini sürdürmek: ''Yuva kurma, yuvayı yürütme sorumluluğu yine benim üstümdeydi.'' -C. Uçuk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13230, 'yüğrük at yemini artırır', 'bir işte üstün çaba gösterenler, o ölçüde bir karşılık görürler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13231, 'yük altına girmek', 'ağır bir görevi üzerine almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13232, 'yük altında eşek kalır', 'insan olan, bir kimseden gördüğü iyiliğin altında kalmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13233, 'yük altında (yüklü) eşek anırmaz', 'ağır bir iş altında bunalmış olan kişi bu durumdan kurtulmadan rahatlayamaz, keyifli bir duruma gelemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13234, 'yük vurmak', 'hayvana yük yüklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13235, 'yüksek oynamak', 'kumar ve şans oyunlarına çok para ile katılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13236, 'yüksek perdeden konuşmak', '1) yüksek sesle konuşmak; 2) meydan okurcasına sert konuşmak; 3) yapılması güç şeyleri gerçekleştirebilecekmiş gibi abartmalı konuşmak: ''Güya bütün memleket arkamızda imiş gibi yüksek perdeden konuşmaya başlamıştık.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13237, 'yükseklerde dolaşmak', 'elde edilmesi güç şeyler istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13238, 'yüksekten almak', 'olduğundan fazla böbürlenmek, abartılı davranmak: ''Karşımdakilerin içtimai mevkileri ne kadar yüksek olursa ben o kadar yüksekten alırım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13239, 'yüksekten atmak', 'yapamayacağı şeyleri yapabilirmiş gibi söylemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13240, 'yüksekten konuşmak', 'kendini çevresindekilere kabul ettirebilmek için övünerek konuşmak: ''Bekçi, onlardan cesaret almış gibi şimdi daha yüksekten konuşuyordu.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13241, 'yüksekten uçmak', '1) yükseklerde dolaşmak; 2) argo palavra atmak, çok abartmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13242, 'yüksük kadar', 'çok az, az miktarda.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13243, 'yükte hafif pahada ağır', 'taşınması kolay olan değerli (eşya): ''... işgal altındaki memleketlere o günlerde sık sık ve kolaylıkla seyahat etmiş, yükte hafif pahada ağır eşya sokup çıkarmışlardır.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13244, 'yükünü almak', '1) taşıyabileceği en ağır yükü yüklenmiş olmak; 2) yeterli sayıda bulundurmak, dolmak: ''Lokanta da her akşamki yükünü almaya başlamıştı.'' -T. Buğra. 3) yükünü tutmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13245, 'yükünü çekmek', 'bütün ağırlığını taşımak, her türlü eziyete katlanmak: ''Şikâyet etmeden yükünü çektiği yitik bir yaşamı olmalıydı.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13246, 'yükünü tutmak', 'çok zengin olmak, zenginleşmek: ''Zira bazı insanlar da vardır ki yüklerini tuttuktan ve biraz da yaşlandıktan sonra kendilerini bir nevi santimantal veya dinî mistisizme verirler.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13247, 'yüreğe işlemek', 'çok derin acı uyandırmak: ''Fakat sesi kulaklara değil, doğru yüreğe çarpar, yüreğe işlerdi.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13248, 'yüreği ağzına gelmek', 'birdenbire çok korkmak, aşırı korku veya sevinçten fazlasıyla heyecanlanmak, endişelenmek: ''Çıngırağın her çekilişinde ikisinin de heyecandan yürekleri ağızlarına geliyor.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13249, 'yüreği bayılmak', 'karnı çok acıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13250, 'yüreği boğazına tıkanmak', 'sıkılmak, üzülmek, dertlenmek: ''Yüreğim boğazıma tıkanmış bir hâlde, bu basit, bu aşağılık konuşmaları dinliyorum.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13251, 'yüreği bozulmak', 'bunalmak, sıkılmak: ''Yüreği bozulanların gözleri karanlık koridorlara, kapılara, pencerelere kaydı.'' -L. Tekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13252, 'yüreği burkulmak', 'çok üzülmek, çok acı duymak: ''Babamın küçük yalısını eşyasıyla satın alan ... bir Meşrutiyet devri mebusunu ziyaret ettiğim zaman ... yüreğim burkulmuştu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13253, 'yüreği cız etmek (cızlamak)', 'çok acımak, içi sızlamak: ''Aklımıza eski günler gelince / Yüreğimiz cız eder'' -B. Necatigil.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13254, 'yüreği çarpmak', '1) kalbi çarpmak veya çalışmak; 2) coşku sebebiyle kalp hızlı hızlı çarpmak veya çalışmak; 3) merak, kaygı, korku, heyecan vb. duygularla tedirgin olmak, huzursuz olmak: ''Bunu düşündükçe gülümser, tatlı tatlı yüreği çarpar, ruhunda kopan bir hamleyle örsünün üzerinde milyarlarca kıvılcım tutuştururdu.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13255, 'yüreği daralmak', 'sıkılmak, bunalmak, içi daralmak: ''Yusuf bütün olayları korkuyla, yüreği daralarak izliyordu.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13256, 'yüreği dayanmamak', 'acısına katlanamamak, çok acı duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13257, 'yüreği ezilmek', '1) üzülmek, acı duymak: ''Yüreğim merhametten eziliyor, dizlerim vücudumun yükü altında çökecek gibi oluyordu.'' -R. N. Güntekin. 2) açlık duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13258, 'yüreği ferahlamak (hafiflemek)', 'kaygıdan kurtulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13259, 'yüreği götürmemek', 'dayanmamak, katlanamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13260, 'yüreği göz göz olmak', 'dert, acı ve sıkıntıdan içi kabarmak, aşırı dertlenmek: ''Göz göz oldu yüreğim, gözlerinin derdinden'' -Halk türküsü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13261, 'yüreği hop etmek (hoplamak veya oynamak)', 'birdenbire korkup heyecanlanmak: ''Ansızın geldin, dedi, yüreğim oynadı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13262, 'yüreği kabarmak', '1) içi sıkıntı ile dolup derin soluk alma gereğini duymak; 2) midesi bulanmak: ''Ne dersiniz kız bayağı hasta oldu, deniz tutmuş gibi yüreği kabarmaya başladı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13263, 'yüreği kaldırmamak', 'dayanamamak, katlanamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13264, 'yüreği kalkmak', 'heyecanlanmak: ''Kapıda her araba durdukça yüreğim kalkıyordu.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13265, 'yüreği kan ağlamak', 'derinden acı duymak, çok üzülmek: ''Yüreği kan ağlıyordu, onların şu perişan, sürüm sürüm hâllerini gördükçe...'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13266, 'yüreği kanamak', 'aşırı üzüntüden sarsılmak: ''Zaten kostüm meselesinden dolayı üzülen ve hırçınlaşan yüreği sanki bir diken yığınına sürtünür gibi kanıyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13267, 'yüreği kararmak', 'içine karamsarlık ve sıkıntı çökmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13268, 'yüreği katılmak', 'ağlamaktan veya soğuktan nefesi tutulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13269, 'yüreği kaynamak', 'içinde şüphe ve endişe uyanmak: ''Namazı nasıl kıldığını bilmedi, yüreğinde bir şeyler kaynıyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13270, 'yüreği oynamak', 'ansızın heyecanlanmak veya korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13271, 'yüreği parça parça olmak', 'pek çok acımak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13272, 'yüreği parçalanmak', 'çok acımak: ''Bir dilenci çocuğuna dayak attığını görmüş, yüreği parçalanmıştı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13273, 'yüreği parlamak', 'coşmak, heyecanlanmak: ''Bir sözden, bir asker geçişinden, bir düşünceden yüreği parlar, gönlü ateş alır adam olmalı.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13274, 'yüreği rahatlamak', 'üzüntü ve kaygısı azalmak, kalmamak: ''Lüzumsuz bir şey satın aldığı zaman garip bir üzüntü duyar, karısı -ziyanı yok, üzülme, ne yapalım, olmuş bir şey -diye teselli etmedikçe bir türlü yüreği rahatlamazdı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13275, 'yüreği serinlemek', 'üzüntüsü bir dereceye kadar azalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13276, 'yüreği sıkılmak', 'içi sıkılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13277, 'yüreği sıkışmak (tıkanmak)', '1) kalp atışları düzensiz olmak, sıkıntı duymak; 2) mec. bir meseleden dolayı aşırı üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13278, 'yüreği sızlamak', 'çok acımak, çok üzülmek: ''O, inledikçe benim de yüreğim sızlıyor, sıkıntıdan damarlarımı saran yağ eriyor.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13279, 'yüreği soğumak', 'düşmanın bir felakete uğramasına sevinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13280, 'yüreği şişmek', 'can sıkıcı şeyler dinlemekten bunalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13281, 'yüreği titremek', 'duygulanmak, endişe, korku duymak: ''İçinden yüreği titreyerek tepeden indi, ağır adımlarla saraya girdi.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13282, 'yüreği tükenmek', 'bir şeyi anlatmak için çok yorulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13283, 'yüreği ürpermek', 'çok korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13284, 'yüreği yağ bağlamak', 'istenilen bir şeyin olmasından ferahlık duymak: ''Oh ... oh yüreğim bir karış yağ bağladı.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13285, 'yüreği yanmak', '1) çok acımak: ''Nahit onu yorgun, kederli ve umutsuz, sitem yüklü görmüştü. Yüreği yanmıştı.'' -T. Buğra. 2) felakete uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13286, 'yüreği yarılmak', 'çok korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13287, 'yüreği yerinden oynamak', 'birdenbire heyecanlanmak veya korkmak: ''Odanın içinde birdenbire kızılca kıyamet kopmasın mı zavallı halamın yüreği yerinden oynamış.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13288, 'yüreğinden geçmek', 'düşünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13289, 'yüreğinden gelmek', 'bir şeyi isteyerek, severek yapmak: ''Piyanistin takdiri yüreğinden geliyordu.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13290, 'yüreğine (bir şey) çökmek', 'derinden ızdırap duymak: ''Ankara ufuklarına bakarken eskisi gibi insanın yüreğine gariplik çökmüyordu.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13291, 'yüreğine dert olmak', 'başkasının herhangi bir davranışı, sonradan kendisi için sürekli bir üzüntü kaynağı olmak: ''Zavallı adam, son nefesinde bir ekmek kadayıfı istediydi; alıp yediremedim. O, yüreğime dert oluyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13292, 'yüreğine dokunmak', 'üzülmek: ''Hem öyle manzaralar benim yüreğime dokunuyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13293, 'yüreğine inmek', 'kötü bir olay dolayısıyla fazlaca etkilenmek: ''Eğer bizden gizli Paris''e kaçsaydın babamın yüreğine inerdi.'' -P. Safa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13294, 'yüreğine kar yağmak', 'kıskançlık duyarak üzülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13295, 'yüreğine kurt düşmek', 'şüphelenmek, içine kurt düşmek: ''Reyhan''ın yüreğine küçük bir kurt düşmüştü.'' -M. Yesari.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13296, 'yüreğine od (ateş) düşmek', 'felakete uğramak, çok üzülmek: ''Adam odur ki komşusunun ineği dişi doğurdu der, yüreğine od düşer.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13297, 'yüreğine saplanmak', 'aşırı derecede acı duymak, içine oturmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13298, 'yüreğine sinmek', 'içine sinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13299, 'yüreğine su serpmek', 'bir kimseyi kaygı sebebinin ortadan kalkmasıyla veya yeniden umut verecek bir haberle ferahlatmak: ''Bizim nesil sözü, Selma Hanım''ın yüreğine biraz su serpti.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13300, 'yüreğini açmak', 'kalbini açmak, derdini dökmek, içini dökmek, senli benli konuşmak ve davranmak: ''Sanki bana herkese yaptığından fazla yüreğini açardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13301, 'yüreğini ateş almak', 'aşırı üzülmek, fazla üzüntüden içi yanmak: ''Gülbahar''ın yüreğini ateş almış yanıyordu.'' -Y. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13302, 'yüreğini boşaltmak (dökmek)', 'derdini, üzüntüsünü anlatarak hafiflemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13303, 'yüreğini dağlamak', 'acıyla ve özlemle içi yanmak, acıyla kıvranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13304, 'yüreğini eritmek (sızlatmak)', 'çok üzmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13305, 'yüreğini hoplatmak (oynatmak veya kaldırmak)', 'heyecanlandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13306, 'yüreğini kaplamak', 'endişe ve üzüntü duymak: ''Onu tanıyamamak sinsi bir korku gibi yüreğini kapladı.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13307, 'yüreğini kemirmek', 'içini kemirmek, tedirgin olmak: ''Güzelliğine pek güvenen Zişan''ın yanında bu kadar zavallı kalışı yüreğini kemirip duruyor.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13308, 'yüreğini pek tutmak', 'kendini korkuya kaptırmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13309, 'yüreğini serinletmek', 'üzüntüsünü azaltmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13310, 'yüreğini tüketmek', 'bir şey anlatmaya çalışarak yorulmak: ''Aman, dedi. Yüreğimi tüketeceğime her işi kendim yaparım, daha iyi...'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13311, 'yüreğinin başı sızlamak', 'yüreği sızlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13312, 'yüreğinin yağı (yağları) erimek', '1) çok üzülmek; 2) çok korkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13313, 'yürek burkmak', 'insanın içini acıyla doldurmak, insana çok üzüntü vermek: ''Yörede, şimdi yürek burkan bir suskunluk vardı.'' -T. Buğra.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13314, 'yürek paralamak', 'çok üzmek: ''Son yürek paralayıcı yalvarmama aldırış etmedi.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13315, 'yürek soğutmak', 'sevmediği birinin bir felakete uğramasına sevinmek: ''Seyrimize çıktınız değil mi? Yürek soğutuyorsunuz değil mi? Allah sizi bizden besbeter etsin inşallah!'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13316, 'yürek tüketmek', 'yüreği tükenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13317, 'yürek vermek', 'yüreklendirmek, cesaretlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13318, 'yüreklilik göstermek', 'korkmamak, cesur davranmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13319, 'yüreksizlik göstermek', 'korkmak, ürküp kaçmak: ''Tek üzüldüğüm, gecenin büyüsünü yitirmemek için masadan erken kalkmak yüreksizliğini göstermem.'' -T. Uyar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13320, 'yürekten çağırmak', 'aşırı derecede arzu etmek, istemek: ''Bu kadar yürekten çağırma beni / Bir gece ansızın gelebilirim'' -Şarkı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13321, 'yürü ense tıraşını göreyim (görelim)', ' alay görüştüğü kimseye gitmesini söylemek veya görüşmeyi kısa kesmek için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13322, 'yürük at kamçı değdirmez', 'işini ivedilik ve yeterlilikle yapan kişiyi sıkıştırmak gerekmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13323, 'yürük at yemini artırır', 'bir işte üstün çaba gösterenler, o ölçüde bir karşılık görürler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13324, 'yürük ata kamçı olmaz', 'işini ivedilik ve yeterlilikle yapan kişiyi sıkıştırmak gerekmez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13325, 'yürük ata paha olmaz', 'işini ivedilik ve yeterlilikle başaran kimsenin değeri çok büyüktür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13326, 'yürük tazıyı tilki sevmez', 'kurnaz, akıllıyla boy ölçüşemez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13327, 'yürürlüğe girmek', 'bir kanun, bir karar, bir iş uygulanır, yapılır duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13328, 'yürürlüğe konmak', 'bir kanun veya bir karar uygulama alanına konulmak: ''Ekonomik, ticari veya teknik ilişkileri düzenleyen ... antlaşmalar ... yayımlanma ile yürürlüğe konabilir.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13329, 'yürürlükte bulunmak', 'bir kanun veya bir karar uygulama alanında olmak: ''Kimse, işlediği zaman yürürlükte bulunan kanunun suç saymadığı bir fiilden dolayı cezalandırılamaz...'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13330, 'yürürlükte kalmak', 'bir kanun veya karar geçerli olmaya devam etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13331, 'yürürlükten kaldırmak', 'uygulanmaz duruma getirmek: ''Öte yandan, dünyadaki sorunların çokluğu da uykuyu yürürlükten kaldırmaya yetmez.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13332, 'yürüyüş düzenlemek', 'bir olayı protesto etmek veya bir konuya dikkat çekmek amacıyla toplu yürüyüş tertip etmek: ''Toplantı ve gösteri yürüyüşünü düzenleme hakkını kullanmasında uygulanacak şekil, şart ve usuller kanununda gösterilir.'' -Anayasa.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13333, 'yürüyüş yapmak', '1) spor amacıyla yürümek: ''Bir gün Küplüce arkalarında uzun bir yürüyüş yapmış.'' -H. Taner. 2) bir olayı protesto etmek veya bir konuya dikkati çekmek amacıyla topluca yürümek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13334, 'yürüyüşe çıkmak', 'dolaşmaya, gezintiye çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13335, 'yürüyüşe geçmek', '1) bir yerden başka bir yere gitmek için yürümeye başlamak: Askerler yürüyüşe geçti. 2) bir yeri almak için o yöne doğru ilerlemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13336, 'yüz aklığı göstermek', 'bir işte başarıya ulaşmak: ''Arkadaşları arasında sivrilmiş, birçok savaşlarda yüz aklığı göstermiş cesur bir kaptandı.'' -F. F. Tülbentçi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13337, 'yüz bulmak', 'ilgi ve yakınlık görmek: ''Akça pakça bir hanım gördü mü biraz da yüz buldu mu hemen bohçacı madamlardan birini evine gönderir, pırlanta gerdanlık vadedermiş.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13338, 'yüz bulunca astar istemek', 'yüz verince astar istemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13339, 'yüz çevirmek', 'gösterdiği ilgiyi kesmek: ''... vergi kâtibinden yüz çevirmişler, kendisine hasım olmuşlardı.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13340, 'yüz etmek', ' hlk. ısmarlamak, havale etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13341, 'yüz geri etmek', 'geri döndürmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13342, 'yüz göstermek', 'ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13343, 'yüz göz olmak', 'biriyle gereksiz yere, aşırı derecede senli benli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13344, 'yüz karası olmak', 'utanılacak bir durum ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13345, 'yüz kızartmak', '1) sıkılarak yalvarmak; 2) utandırmak: ''Meşhur bir edibimizin cinsî hayatına dair yüz kızartıcı sözler söylenirdi.'' -Y. Z. Ortaç.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13346, 'yüz kızdırmak', 'utanmayı göze almak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13347, 'yüz surat davul derisi (mahkeme duvarı)', ' tkz. utanması olmayanlar için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13348, 'yüz sürmek', 'aşırı sevgi göstermek için yere eğilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13349, 'yüz takınmak', 'yüze verilen biçimle bir duyguyu belirtmek: ''Osman Nuri Bey umutsuzluğa düşerek sessiz sessiz ağlamaya başlayınca Seniye Hanım onu teselli için hemen güler bir yüz takınmış, aman ne yapıyorsunuz bey, demişti.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13350, 'yüz verince astar istemek', 'kendisine gösterilen küçük bir ilgiden şımararak geniş yetki elde etmeye, daha çok yarar sağlamaya çalışmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13351, 'yüz verme arsız olur, az verme hırsız olur', 'yönetimi altında bulunan kimselere sık sık müdahale edenler bekledikleri verimi alamadıkları gibi onları da arsız ederler; yiyecek ve para bakımından da sıkıntıya düşürenler onları hırsızlığa itmiş olurlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13352, 'yüz vermemek', '1) ilgi, yakınlık göstermemek: ''Sonra geniş bir odada orta yaşlı bankacı kendisine yüz vermeyen yargıcın kızına saldırdı.'' -Y. Atılgan. 2) önemsememek: ''Bursa, yeşiline en uygun maviyi kondururken yüksek mimarlarımız renge hiç yüz vermiyorlar.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13353, 'yüz yapmak', 'makyaj yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13354, 'yüz yazmak', '1) makyaj yapmak; 2) hlk. köy seyirlik oyunlarında taklit edilen kişinin özelliklerini belirtecek biçimde yüz boyamak, maske yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13355, 'yüz yüzden utanır', 'insanlar karşı karşıya geldiklerinde daha kolay uzlaşabilirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13356, 'yüz yüze bakmak', 'arada hatır gönül meselesi olduğu için karşılıklı ilişkiyi korumak zorunda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13357, 'yüz yüze gelmek', '1) birden karşılaşmak: ''Tırmanıp gedikten girer girmez toprak dolu çuvallarla burayı tıkamaya çalışan insanlarla yüz yüze geldi.'' -İ. O. Anar. 2) bir araya gelmek: ''Bir daha yüz yüze gelmemek için ayrılmışlardı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13358, 'yüz yüze getirmek', 'karşı karşıya getirmek: ''Her fırsatta yavrucakları ölümle yüz yüze getiriyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13359, 'yüz yüze kalmak', 'aynı ortam içerisinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13360, 'yüz yüze yaşamak', 'sürekli olarak bir arada olmak zorunda bulunmak: ''Ölümle aylarca yüz yüze yaşamış, hayatımla oyuncak gibi oynamıştım.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13361, 'yüze çıkmak', '1) bir sıvının üst bölümüne çıkmak; 2) belli olmak, açığa çıkmak, belirmek: ''Evimizde artık pek de gizli tutulamayarak yüze çıkmaya başlayan bu rezalet yani gelin ve damat arasındaki bu sevda alışverişi böyle devam edip duracak mı?'' -M. Ş. Esendal. 3) yüzsüz olmak, şımarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13362, 'yüze duramamak', 'birinin hatırından çıkamamak, birinin hatırını kıramamak: ''Belki ihtiyaçları olur isterler, yüze duramam.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13363, 'yüze gelmek', 'çekinmemek: ''Ne ben yüze gelip sorabiliyordum ne de o cesaret edip anlatabiliyordu.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13364, 'yüze gülmek', '1) yalandan dost görünmek; 2) sevimli, alımlı görünmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13365, 'yüze vurmak', 'yüzüne vurmak: ''Fakat politikada kabahatleri yüze vurmak yoktu.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13366, 'yüzsuyu dökmek', 'onurunu sarsacak kadar çok yalvarmak: ''Hâlbuki Emin Efendi, feleğin çemberlerinden geçerek, kâh kuvvetlerin önünde diz çöküp yüzsuyu dökerek, kâh zayıflara çelme vurup tuzak kurarak bu mertebeye ulaşmış.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13367, 'yüzü ak olsun', '''sağ olsun'' anlamında kullanılan bir iyi dilek sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13368, 'yüzü asılmak', 'somurtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13369, 'yüzü düşmek', 'somurtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13370, 'yüzü gözü açılmak', '1) sıkılmaz, utanmaz bir duruma gelmek; 2) toplumsal ilişkiler kurmaya, çevresini, dünyayı tanımaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13371, 'yüzü gülmek', '1) sevinci yüzünden belli olmak: ''Otele gidip lavabolu odayı görünce yüzüm güldü.'' -F. Otyam. 2) feraha kavuşmak: Şehirlilerle köylüler arasındaki alışveriş şartları düzenlendikten sonra hepsinin yüzü gülmeye başladı. 3) temiz, tertipli duruma gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13372, 'yüzü güzel olanın huyu (da) güzel olur', 'insanın yüzü, içinin aynasıdır, güler yüz hoşgörülü, iyi niyetli, uyuşma eğilimli, temiz yürekli kişilerde bulunur; içinde kötülük bulunan kişinin duyguları da yüzüne vurur, kendisini çirkinleştirir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13373, 'yüzü kalmamak', 'bir kimseden daha önce birçok ricada bulunduğu için yeni bir şey istemeye sıkılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13374, 'yüzü karışmak (allak bullak olmak veya alabora olmak)', 'can sıkıcı bir durum, yüzünden belli olmak: ''Beraberce binmiş olduğumuz bir takside birdenbire yüzü karıştı, şoföre yüksek bir sesle...'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13375, 'yüzü kasap süngeriyle silinmiş', '''utanmayan, utanması sıkılması olmayan'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13376, 'yüzü kızarmak', 'utanmak: ''Boynundan bir kese çıkardı fakat içine bakmadan ani bir fikirle yüzü kızardı.'' -H. E. Adıvar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13377, 'yüzü kireç gibi olmak (ağarmak)', 'yüzünde renk kalmamak, rengi solmak: ''Genç kadının yüzü kireç gibi ağarmıştı.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13378, 'yüzü kireç kesilmek', 'yüzünde renk kalmamak: ''Bizim bacınınsa yüzü kireç kesildi.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13379, 'yüzü sararmak', 'korku, üzüntü, coşku vb. sebeplerle yüzün rengi solmak: ''Gözleri büsbütün büyüdü, saçları dikildi, yüzü sarardı.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13380, 'yüzü seçilmemek', 'açıkça tanınmamak, belli belirsiz görünmek: ''Işık arkadan geldiği için yüzü seçilmiyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13381, 'yüzü sıcak olmak', 'çok sevilmek, hoşlanılmak: Paranın yüzü sıcaktır.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13382, 'yüzü soğuk olmak', 'ürkütücü olmak: Ölümün yüzü soğuktur.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13383, 'yüzü yazılı kalmak', 'kullanılmak, yenilmek için hazırlanmışken herhangi bir sebeple olduğu gibi dokunulmadan kalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13384, 'yüzü yere gelmek (geçmek)', 'çok utanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13385, 'yüzüğü geriye çevirmek', 'evlenme sözünü geri almak, nişanı bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13386, 'yüzük takmak', 'nişanlanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13387, 'yüzünden akmak', 'herhangi bir durum yüzünden çok belli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13388, 'yüzünden düşen bin parça olmak', 'öfke veya küskünlükten ileri gelen can sıkıntısıyla suratı asık olmak: ''Ama iktisadi bunalım ayyuka çıktı maşallah, yurttaşın yüzünden düşen bin parça olacak.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13389, 'yüzünden kan damlamak', 'çok sağlıklı olmak, sağlığı yüzünün renginden belli olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13390, 'yüzünden okumak', '1) ezbere değil, yazılmış kâğıttan okumak; 2) herhangi bir durumu yüzünden anlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13391, 'yüzüne bakamaz olmak', 'utanç, yüreksizlik vb. sebeplerle bir kimsenin karşısına çıkamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13392, 'yüzüne bakılacak gibi olmak', 'çok çirkin olmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13393, 'yüzüne bakılır olmak', 'çirkin sayılmamak: ''Hem bakalım, yirmi yaşında da olsa yüzüne bakılır cinsten midir?'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13394, 'yüzüne bakılmaz olmak', 'çok çirkin olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13395, 'yüzüne bakmamak', '1) önem vermemek, ilgilenmemek; 2) darılmak, gücenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13396, 'yüzüne bakmaya kıyamamak', 'biri çok güzel olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13397, 'yüzüne bir daha bakmamak', 'darılıp konuşmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13398, 'yüzüne duramamak', 'dayanamamak, bir isteğe hayır diyememek, kıramamak: ''Aman sayın bayan beni çağırmayınız. Güzel yüzüne duramam, içeri girerim. Girince de...'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13399, 'yüzüne gözüne bulaştırmak', 'bir işi becerememek, bozmak: ''Onun bu işi nasıl olup da yüzüne gözüne bulaştırdığını bir türlü anlayamadım.'' -E. E. Talu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13400, 'yüzüne gülmek', '1) dostmuş gibi görünmek: ''Köylünün yüzüne gülüp arkadan jurnalliyormuş.'' -E. Işınsu. 2) dostluk göstermek, ilgi göstermek, alakalanmak: ''Köyde, ondan başka yüzümüze gülen, bize yol gösteren olmadı.'' -Ö. Seyfettin. 3) temizliği, yeniliği dolayısıyla ferahlık vermek: ''Banyo, tuvalet, vesair kısımlar, o ne temizlik, o ne genişlik, insanın yüzüne gülen o ne ferahlıktı.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13401, 'yüzüne kan gelmek', 'sağlığı yerine gelmek, benzinin solgunluğu geçmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13402, 'yüzüne tükürseler yağmur yağıyor sanır', 'çok arsız ve onursuz kimseler için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13403, 'yüzüne vurmak (çarpmak)', 'ayıplayarak kusurunu yüzüne söylemek: ''Bir büyük kabahatim varmış da yüzüme vuracaklarmış gibi açıp okumaktan çekiniyorum.'' -A. Gündüz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13404, 'yüzüne yazmak', ' hlk. gelinin yüzünü süslemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13405, 'yüzünü buruşturmak (ekşitmek)', 'yüzüne öfke ve hoşnutsuzluk gösteren bir biçim vermek: ''Yüzünü buruşturuyor Fuat, ukalalığından sıyrılıyor, üzüldüğü belli.'' -A. Ümit. ''Ağır işler görüp de güler yüzünü ekşitmemeyi ve kimseyi incitmeden yaşamayı analar bu adamlara öğretmeli idiler.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13406, 'yüzünü duvara yapıştırmak', 'ilgiyi kesmek: ''Artık anlaşabileceğimizi sanmıyorum, diyerek herifin yüzünü duvara yapıştırıyor Mustafa.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13407, 'yüzünü gören cennetlik', 'uzun süre görünmeyen kimseler için söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13408, 'yüzünü görmemek', '1) uzun süre görmemek: ''Yüzünü de gördüğüm yoktu. Kırkyıl da görmesem göreceğim gelmezdi.'' -M. Ş. Esendal. 2) gereksinim duyulan bir şeyi özlemek, ona hasret kalmak: Harp içinde kahvenin yüzünü görmedik.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13409, 'yüzünü kara çıkarmak', 'birini utandırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13410, 'yüzünü karartmak', 'birine sinirlenerek somurtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13411, 'yüzünü kızartmak (kızdırmak)', 'onuruna, gururuna önem vermeden bir şey istemek, utançla, utanarak istemek: ''Fakat ben boş ümitle insan avutmanın faydasından ziyade zararına inandığım için çok kere yüzümü kızdırır, açıkça mümkün değil derim.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13412, 'yüzünü şeytan görsün', 'sevilmeyen bir kimseye karşı duyulan nefreti belirtmek için kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13413, 'yüzünü yere getirmek (geçirmek)', 'utandırmak, mahcup duruma düşürmek: ''Baban da, olur, demiş, ak sakallı adamın yüzünü yere mi geçireceksin?'' -E. Işınsu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13414, 'yüzünün derisi kalın', 'utanması, arlanması olmayan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13415, 'yüzünün derisi yere geçmek', 'yüzü yere gelmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13416, 'yüzünüze güller', ' hlk. iğrenç bir şey anlatılırken söylenen bir söz: ''Yüzünüze güller, büyüklerin pisliğini temizlemek bile bizde forsla, pistonla oluyor.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13417, 'yüzüp yüzüp kuyruğuna gelmek', 'uzun sürmüş bir işi bitirmek üzere olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13418, 'yüzüstü bırakmak', '1) birini yapayalnız, kimsesiz, kötü bir durumda bırakmak: ''Adam yüzüstü bırakıp gidince böyle bir numara çevirip başına kalmayı deniyor anlaşılan.'' -E. Bener. 2) bir işi zamanında yapmayıp savsaklamak, olduğu gibi bırakmak, ihmal etmek: ''Evdeki işimi gücümü yüzüstü bıraktım.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13419, 'yüzüstü kalmak', '1) bir iş, zamanında yapılmayıp olduğu gibi bırakılmak: ''Altı hücreyle cümle kapısının taş kemeri, kalın meşe tahtasından kapı kanatları yüzüstü kaldılar.'' -K. Tahir. 2) bir iş bitirilmeden bırakılmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13420, 'zaaf göstermek', 'zayıflığı, yeteneksizliği ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13421, 'zaafa düşmek', 'zaafa kapılmak: ''Korktuğumuzu, zaafa düştüğümüzü hissederlerse, büsbütün üstümüze gelirler.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13422, 'zaafa kapılmak', 'direnme gücü gösterememek: ''Genç adamı her gördüğünde, zaafa kapılarak bütün hakaretlerini bağışlar olmuştu.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13423, 'zaafa uğramak', 'eksikliği, yetersizliği belli olmak: ''Hayatımı karartan bir adamı bile teşhiste zaafa uğradım.'' -O. Aysu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13424, 'zabıt tutmak', 'tutanak düzenlemek: ''Şimdi bir zabıt daha tutsam görev başında memura hakaretten, sülaleni yakarım senin.'' -Ç. Altan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13425, 'zafiyet geçirmek', 'zayıflayıp iyice kuvvetten düşmek: ''Hastalığı için, ciğerleri zayıf düşmüş, zafiyet geçiriyor, demişlerdi doktorlar.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13426, 'zahirenin (çiftçinin) ambarı sabanın ucundadır', 'toprak, iyi sürülürse bol ürün vereceği için iyi sonuç almak isteyen işine dört elle sarılmalıdır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13427, 'zahmet çekmek', 'güçlükle karşılaşmak, sıkıntıya katlanmak: ''Yolda çok zahmet çekmiş, bereket versin Paris sefareti erkânından biri kendisine refakat etmiş.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13428, 'zahmet olmazsa', '''rica ederim'' yerine kullanılan bir nezaket sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13429, 'zahmete girmek (katlanmak)', 'zahmet etmek: ''Bu kadarcık zahmete de katlanacaksınız artık.'' -A. Ümit. ''Bunun için büyük zahmetlere girmeye gerek yoktur.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13430, 'zahmete sokmak', 'birine yorgunluk vermek veya masraf ettirmek: ''Onu kâh susadım, kâh acıktım diye türlü türlü zahmetlere sokmuştum.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13431, 'zahmetine değmek', 'verilen emeği karşılamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13432, 'zahmetsiz rahmet olmaz', 'sıkıntı, güçlük çekmeden iyi ve güzel işler başarılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13433, 'zam gelmek', 'fiyatı artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13434, 'zam görmek', '1) fiyatı artmak: Ekmek iki ayda üç defa zam gördü. 2) ücreti artmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13435, 'zam yapmak', 'söz konusu fiyatı artırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13436, 'zaman almak', 'sürmek, devam edip zamanı geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13437, 'zaman bırakmak', 'bir iş için süre ayırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13438, 'zaman ile yarışmak', 'hızlı hareket etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13439, 'zaman kazanmak', 'vakit kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13440, 'zaman kollamak', '1) bir işin sırasını beklemek; 2) uygun bir fırsat beklemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13441, 'zaman öldürmek', 'boş şeylerle vakit geçirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13442, 'zaman sana uymazsa sen zamana uy', 'yaşadığın zamanın koşulları ve çevrendekilerin davranışları senin tutumuna uygun değilse sen onlara uymalısın.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13443, 'zaman tanımak', '1) bir iş için yeterli zaman vermek; 2) bitmeyen bir iş için süreyi uzatmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13444, 'zaman vermek', 'bir iş için belli bir süre ayırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13445, 'zamana uymak', 'davranışlarını içinde bulunulan günün şartlarına uydurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13446, 'zamanı avlamak', 'uygun zamanı bulmak: ''Nihayet yalnız kaldığım bir zamanı avlayarak yanıma yaklaşıyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13447, 'zamanı dolmak', 'bir iş için ayrılan süre sona ermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13448, 'zamanı geçirmek', 'oyalanmak: ''Kaybolmuş şeyleri bulurum ama sen zamanı geçirmişsin, saatini bulamadım.'' -A. Ş. Hisar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13449, 'zambırından geçilmemek', 'çok çalım yapmak: ''Oğlum subay oldu diye zambırından geçilmiyordu.'' -O. Kemal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13450, 'zan altında bulunmak', 'bir şeyle suçlanmak, sanık durumunda olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13451, 'zangır zangır titremek', 'aşırı bir biçimde titremek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13452, 'zannına düşmek', 'sanmak: ''Âdeta elimi uzatsam dokunabilirim zannına düşmüştüm.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13453, 'zapt etmek', '1) zorla almak: ''Bizans''ta Sırp memleketlerini zapt ettilerse de bir müddet sonra bazı kısımlara geniş otonomiler verdiler.'' -F. R. Atay. 2) tutmak: ''Neveser bir sevinç çığlığını zor zapt etmişti.'' -A. İlhan. 3) bir şeyi güç kullanarak önlemek: ''El ele vermiş polisler kaldırımlardan taşan halk kitlesini zor zapt ediyorlardı.'' -H. Taner. 4) yazıya geçirmek: İfadesini zapt edenlere sessizce baktı. 5) hatırında tutmak: Söylediklerinizin birçoğunu zapt ettim. 6) anlamak, kavramak, bütünüyle öğrenmek: ''Bütün ayrıntılarıyla bu âlemi zapt etmiş belleği başlıca dayanağı idi.'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13454, 'zapt olunmak', 'kavranmak, bütünüyle öğrenilmek: ''Bir milletin ruhu zapt olunmadıkça, bir milletin azim ve iradesi kırılmadıkça o millete hâkim olmanın imkânı yoktur.'' -Atatürk.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13455, 'zapturapt altına almak', 'düzeni ve disiplini sağlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13456, 'zar almak', 'oyunu kazanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13457, 'zar atmak', '1) zarı hızla yuvarlamak; 2) mec. kader ile oynamak, geleceği için plan uygulamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13458, 'zar gelmek', 'şansı iyi olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13459, 'zar gibi', 'çok ince, saydam.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13460, 'zar kesmek', 'zarını bozmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13461, 'zar tutmak', 'istediği sayıyı getirmek için zarı, atmadan önce parmaklar arasında düzene sokmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13462, 'zarar çekmek', 'zarara uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13463, 'zarar gelmek', 'kötülük gelmek: ''Bizden hiç kimseye zarar gelmez.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13464, 'zarar görmek', 'kötü sonuca uğramak: ''Usulleri, kaideleri bozanların zarar görecekleri muhakkaktı.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13465, 'zarar vermek', '1) kötülük etmek: ''Bu davaya zarar verecek ihtiyarları ortadan kaldırmaya çalışmaktadır.'' -F. R. Atay. 2) birinin parasal kayba uğramasına sebep olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13466, 'zarara sokmak', 'zarar vermek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13467, 'zarara uğramak', '1) kötü bir durumla karşılaşmak; 2) parasal kayba uğramak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13468, 'zararda olmak', '1) alışverişte kâr elde edememek; 2) kötü duruma düşmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13469, 'zararı dokunmak', 'kötülüğe uğratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13470, 'zararı olmamak', 'kötülüğe yol açmamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13471, 'zararı yok', 'özür dileyenlere karşılık olarak bağışlandığını, olayın pek önemli olmadığını bildirmek için söylenen bir söz: ''Dürbünleri mi kırdınız, zararı yok, hiç zararı yok.'' -A. Dino.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13472, 'zararın neresinden dönülse kârdır', 'sürüp giden zararlı bir işten ne kadar erken vazgeçersek daha sonra uğrayacağımız zararı o kadar azaltmış oluruz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13473, 'zararlı çıkmak', '1) bir işin sonunda değerli sanılan bazı şeyleri yitirmek; 2) zarar etmek: ''Bu kitap, kendi ağırlığında altınla dahi satılsa satan yine zararlı çıkar.'' -A. Kabaklı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13474, 'zarf atmak', '1) dolandırıcı zarf vb. kullanarak bir tür para sızdırmak veya çarpmak; 2) karşısındakinin gerçek duygu ve düşüncelerini öğrenmek için kasıtlı olarak uygun sözler söylemek veya bazı davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13475, 'zarını bozmak', '1) tavla oyununda oyuncu, yenilmesini yanına oturan kimseden bilmek; 2) atılan zarı karşıdaki oyuncu, eliyle karıştırmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13476, 'zart zurt etmek', 'yüksekten atıp tutarak çıkışmak, kaba kuvvet gösterisinde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13477, 'zartayı çekmek', ' argo ölmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13478, 'zayıf düşmek', '1) zayıflamak: ''Ne yiyip içiyorsun? Zayıf düşmekten korkmuyor musun?'' -N. F. Kısakürek. 2) mec. güçsüzleşmek: ''Fakat aldıkları yerlerin ahalisini Türkleştiremediklerinden bu büyüklük onların zayıf düşmelerine sebep olmuş.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13479, 'zayıf yerinden yakalamak', 'güçsüz, eksik ve yanlış bir tutum ve davranışı yüzünden zor durumda bırakmak: ''Kendisini en zayıf yerinden yakalamak istediğinden şüphelenir gibi.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13480, 'zayi olan koyunun kuyruğu büyük olur', 'elden kaçırılan fırsat gözde büyütülür.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13481, 'zayiat vermek', 'kayba uğramak, zarar ziyan görmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13482, 'zebun kalmak', 'güçsüz, zavallı durumda bulunmak: ''Bir zaman gelir ki sırf kendi icadımız olan bir his elinde zebun kalırız.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13483, 'zehaba (zehabına) kapılmak', 'kuruntuya düşmek, vesveselenmek: ''Bu makalemin, adını koyduğum kitap için, ona ayrıca ehemmiyet verdiğim zehabına kapılmamalarını okuyucularımdan rica ederim.'' -A. H. Çelebi.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13484, 'zehapta bulunmak', 'vesveseye kapılmak, kuruntu içinde olmak: ''Kim bilir ne taraflara yorar, ne zehaplarda bulunur?'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13485, 'zehir gibi', '1) çok acı; 2) çok soğuk (hava); 3) çok becerikli, usta: Zehir gibi şoför. 4) çok üstün: Zehir gibi bir zekâ. 5) çok iyi: ''Oğlan süngerlerin çeşidini zehir gibi tanıyordu.'' -Halikarnas Balıkçısı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13486, 'zehir kesilmek', '1) çok acı ve yakıcı olmak; 2) mec. ortalık ümit, sıkıntılı bir durum olmak: ''İçimde elim bir boşluk, aşk ve hayat ortasında derin bir yalnızlık hissiyle bütün uykum acı ve zehir kesildi.'' -H. C. Yalçın.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13487, 'zehir saçmak', 'çevreye kötü propaganda yapmak veya insanları olumsuz davranışlara yönlendirmek, tahrik etmek, ortalığı karıştırmak: ''Bunlar, etraflarına mütemadiyen zehir saçmakta ve kendi kuruntularını ancak birtakım garip snopluklarla avutmaya çalışmaktadırlar.'' -Y. K. Karaosmanoğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13488, 'zekât vermek', 'Müslümanlıkta, sahip olunan mal ve paranın kırkta birlik payını sadaka olarak dağıtmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13489, 'zembereği boşalmak (boşanmak)', '1) zembereği kurulmaz duruma gelmek; 2) mec. kendini tutamayarak uzun uzun ve sesli gülmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13490, 'zemberek gibi', 'birdenbire, aniden: ''Halim zemberek gibi boşanıyor, sağa bir omuz, sola bir omuz, kalabalığı yarıp Korkut''un karşısına dikiliyor.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13491, 'zemberek kurulmak', 'durum kızışmak: ''Gayri zemberek kuruldu. Söz kâr etmez bunlara...'' -H. Taner.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13492, 'zemheride sür de çalı sür', 'iyi verim alabilmek için üstünkörü olsa bile tarlayı zemheride sürmek gerekir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13493, 'zemheride yoğurt isteyen cebinde bir inek taşır', 'gerçekleşmesi güç bir şey isteyen kimse isteğini gerçekleştirecek çareyi kendisi bulmak zorundadır.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13494, 'zemin hazırlamak', 'uygun ortam yaratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13495, 'zemin ve zamana uygun', 'konuya, içinde bulunulan şartlara uygun.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13496, 'zemzem kuyusuna işemek', 'ünlü olsun, adı anılsın diye herkesi iğrendirip kızdıran kötü bir iş yapmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13497, 'zemzem suyu ile yıkanmak', 'hiçbir suçu veya günahı olmamak: ''Konferansı düzenleyenlerin hepsi, zemzem suyuyla mı yıkanmışlar sanki?'' -T. Halman.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13498, 'zengin arabasını dağdan aşırır, fakir düz ovada yolunu şaşırır', 'zengin, para gücüyle güçlükleri yenerken yoksul, parasızlık yüzünden en kolay işi bile başaramaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13499, 'zengin arabasını dağdan aşırır, züğürt ovada yolunu şaşırır', 'zengin, para gücüyle güçlükleri yenerken yoksul, parasızlık yüzünden en kolay işi bile başaramaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13500, 'zengin kesesini döver, züğürt dizini', 'bir iş yapılacağı zaman zengin ''işte para'' diye kesesini döver, fakirse yapmak istediği işi parasızlık yüzünden yapamayacağı için dizini döver.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13501, 'zenginin horozu bile yumurtlar', 'paralı kişi başarılı olunamayacak sanılan işlerden bile kâr sağlama yolunu bulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13502, 'zenginin malı züğürdün çenesini yorar', 'birinin zenginliğinden çok söz etmenin gereksizliğini, yersizliğini belirtmek için söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13503, 'zenginin sermayesi kasasında, âlimin sermayesi kafasında', 'zengin kişinin zenginliği parasıdır, her işini parayla kolayca yaptırır; bilgin kişinin zenginliği ise kafasındaki bilgisidir, düşüncesidir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13504, 'zenneye çıkmak', ' tiy. orta oyununda erkek oyuncu, kadın rolüne çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13505, 'zerre kadar', '1) bir parça, çok az: ''Tuhaf! Çocukların yüzünde zerre kadar utanma belirtisi yok.'' -A. Ümit. 2) hiç: ''Bu iki hikâyecik üzerinde zerre kadar edebî münakaşalara girişmemişlerdi.'' -O. C. Kaygılı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13506, 'zerresi (zerre kadar eseri) kalmamak (olmamak veya yok)', 'hiç bulunmamak, tükenmek, yok olmak: ''Bazen o muammalı hâl tamamen üstünden kalkıyor, zerre kadar eseri kalmıyor.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13507, 'zevahiri kurtarmak', 'görünüşü kurtarmak: ''Öyle yapmakla beraber zevahiri kurtarıyor, konuşuyor, gülüşüyordum.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13508, 'zeval bulmak', 'bozulup yok olmak, çökmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13509, 'zeval vermemek', 'yok etmemek, sona erdirmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13510, 'zevale ermek', 'zeval bulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13511, 'zevale yüz tutmak', 'bozulmaya, alçalmaya, yok olmaya başlamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13512, 'zevk etmek', 'eğlenmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13513, 'zevk için', '1) yalnız eğlenmek için; 2) alay etmek için.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13514, 'zevki çıkmak', 'hoşa gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13515, 'zevkinde olmak', 'zevkine bakmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13516, 'zevkine bakmak', 'yalnız kendi eğlencesini düşünmek: ''Terfi ümidinde olmadıklarından resmî işlere ehemmiyet vermezler, zevklerine bakarlardı.'' -R. H. Karay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13517, 'zevkine ermek (varmak)', 'zevkini çıkarmak: ''Hem kitap okumak hem de ağabeyimle birlikte bulmaca çözmek zevkine erdim.'' -A. Erhat.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13518, 'zevkine gitmek', ' hlk. hoşuna gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13519, 'zevkli geçmek', 'eğlenceli bir biçimde sürmek: ''Orta yolu bulunca oyunlar çok zevkli geçerdi.'' -A. Kutlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13520, 'zevkli gelmek', 'eğlenceli olduğunu düşünmek: ''Askerlik bana idman ve gezinti gibi kolay ve zevkli geldi.'' -F. R. Atay.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13521, 'zevkten dörtköşe olmak', 'çok sevinip keyiflenmek, aşırı zevk duymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13522, 'zeyrek kuş iki ayağından tutulur', 'işini hile ile yürüten kimse sonunda yakayı ele verir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13523, 'zeytin dalı uzatmak', 'barış için ilk adımı atmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13524, 'zeytinyağı gibi üste çıkmak', 'bir sorunda haksız olduğunu kabul etmemek, ustalıkla kendini haklı çıkarmaya çalışmak: ''Sizler hep böylesiniz. Zeytinyağı gibi üste çıkmaya alışmışsınız.'' -A. Kulin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13525, 'zıddına basmak (gitmek)', 'sinirlendirmek, sinirini bozmak: ''Niçin babanın zıddına basıyorsun evladım, seni hiç incitmemiş bir baba, bir gün bir fiske vurmadı, bir dediğin iki olmuyor.'' -H. E. Adıvar. ''Yalnız akrep kuyruğu gibi bükülmüş pomatlı ibrişim bıyıklar zıddıma gidiyor.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13526, 'zıkkımın kökünü (pekini, dibini) ye!', 'sunulan yiyeceği beğenmeyenlere söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13527, 'zılgıt vermek', 'korkutmak, çıkışmak, azarlamak, gözdağı vermek: ''Şehrin büyükleri otelciye adamakıllı bir zılgıt vermişler.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13528, 'zılgıt yemek', 'azar işitmek: ''Dün akşam benden yediği zılgıttan adamakıllı afallamış görünüyordu.'' -R. N. Güntekin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13529, 'zınk diye durmak', 'birdenbire durmak: ''Askerî bir cip, Camekân Sokağı''nı sarsıla sarsıla geçti, apartmanın kapısı önünde zınk diye durdu.'' -A. İlhan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13530, 'zıp diye çıkmak', 'beklenmeyen bir zamanda ortaya çıkmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13531, 'zıp zıp zıplamak', 'çok sevinmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13532, 'zırıltı çıkarmak', 'anlaşmazlık sebebiyle kavga etmek: ''Durup dururken zırıltı mı çıkarmalı?'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13533, 'zırnık (bile) koklatmamak', 'en ufak bir şey vermekten kaçınmak: ''Karın, kaynanan, çocukların hepsini yiyip sana zırnık koklatmazlar.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13534, 'zırnık (bile) vermemek', 'en ufak bir şey vermemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13535, 'zırva tevil götürmez', 'saçma olan bir düşünceyi döndürme, çevirme yolu ile savunmaya kalkışanlara söylenen bir söz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13536, 'zıvanadan çıkmak', '1) çok sinirlenmek, öfkelenmek: ''Dürdane Hanım''ın aşkı seni zıvanadan büsbütün çıkarmış.'' -H. R. Gürpınar. 2) aklını yitirmek, çılgın gibi davranmak: ''Kaç zamandır zaten bir acayipleşen oğlanın artık adamakıllı zıvanadan çıktığına hükmediyorlardı.'' -H. Taner. 3) denetlenemez duruma gelmek: Bu iş zıvanadan çıktı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13537, 'zifafa girmek', 'gerdeğe girmek: ''Zifafa girdiği gece kaynatası ölüverdi.'' -Ö. Seyfettin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13538, 'zift gibi', '1) çok acı; 2) simsiyah.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13539, 'zift yesin', '''ne yerse yesin'' anlamında öfke bildiren bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13540, 'ziftin pekini yesin', 'zift yesin.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13541, 'zihin açmak', 'zihni daha iyi çalışır duruma getirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13542, 'zihin yormak', 'bir konuda çok düşünmek, kafa yormak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13543, 'zihinde (zihninde) yer etmek', 'çıkmamak üzere belleğe yerleşmek: ''Bir hadise insanın zihninde yer etti mi orada kerpiçleşip kalıyor.'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13544, 'zihni açılmak', 'kavrayışı, anlayışı çoğalmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13545, 'zihni boşalmak', 'kafası rahat ve dingin olmak: ''Zihnim boşaldıkça daha doğrusu rahat zamanlarımda Türkçenin güzelliklerini, orijinal cilvelerini düşünürüm.'' -B. Felek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13546, 'zihni bulanmak (karışmak)', '1) düşünürken olaylar arasındaki bağlantıyı yitirmek; 2) ne yapacağını şaşırmak: ''Duvar saatine bakmayı akıl ettiğinde ise zihni adamakıllı bulandı.'' -İ. O. Anar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13547, 'zihnini altüst etmek', 'düşüncelerini karmakarışık duruma getirmek: ''Günlerden beri bu düşünce, Anadolu''ya geçmek zihnini altüst ediyordu.'' -S. Kocagöz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13548, 'zihnini dağıtmak', 'gerektiği gibi düşünmemek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13549, 'zihnini oynatmak', 'çıldırmak, delirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13550, 'zihnini toplamak', 'kendine gelmek, sağlıklı düşünmeye başlamak: ''Vehibe benden önce zihnini toplayarak cevap verdi.'' -H. R. Gürpınar.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13551, 'zikri geçmek', 'anılmak, adı geçmek: Dün sizin zikriniz geçti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13552, 'zikzak yapmak', '1) sık sık sağa sola yön değiştirmek; 2) mec. sık sık düşünce değiştirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13553, 'zil kalmak', 'parasız kalmak: ''Zaten müdür aç herifin biri, zil kalmış da gelmiş buralara.'' -K. Korcan.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13554, 'zil takıp oynamak', 'çok sevindiğini belli etmek: ''Birini buldu, ne güzel oldu diye zil takıp oynayacak mıydım?'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13555, 'zillete düşmek', 'hor görülmek, aşağılanmak: ''Zillete düşmemek için tehlikeyi kabul etmek daha iyi olmaz mı?'' -E. İ. Benice.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13556, 'zilsiz oynamak', 'çok sevindiğini belli etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13557, 'zimmet çıkarmak', 'eksik veya yanlış yapılan bir işlemden dolayı kişiye fazladan ödenen miktarı belirlemek ve ödemesini sağlamak için bildirimde bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13558, 'zincir gibi', 'art arda sıralanmış şey.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13559, 'zindan gibi', 'karanlık veya iç sıkıcı (yer).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13560, 'zinde tutmak', 'genç ve diri kalmasını sağlamak: ''İçim ürperiyor, ürpertinin beni zinde tutacağına inanıyorum.'' -A. Ümit.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13561, 'zirveye çıkmak', 'en üst düzeyde ilgi çekmek, herkes tarafından konuşulur olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13562, 'zirzopluk etmek', 'uygunsuz, yakışıksız davranışlarda bulunmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13563, 'ziyade olsun!', 'yemekte bulunanlara veya yemeğe buyurun diyenlere ''artsın, çoğalsın'' anlamında söylenen bir nezaket sözü.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13564, 'ziyafet çekmek (vermek)', 'konukları yemekli ağırlamak: ''O gece telgrafçı, gümrükçü, liman çavuşu, müdür beye bir ziyafet vermek istemişlerdi.'' -M. Ş. Esendal.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13565, '... ziyafeti çekmek', 'herhangi bir şeyi en iyi biçimde başarmak, herhangi bir yönüyle doyurmak: Orkestra tam bir müzik ziyafeti çekti.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13566, 'ziyan etmek', '1) yersiz, boş yere harcamak: ''Ah budala kız, gençliğinin kıymetini bilmiyorsun, güzelliğini ziyan ediyorsun.'' -S. M. Alus. 2) zarara uğratmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13567, 'ziyan olmak', 'boşuna harcanmak, zarar görmek: ''Bu fennî ihmal yüzünden Hacer''in çocuğu ziyan oldu.'' -N. Hikmet.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13568, 'ziyan zebil olmak', ' hlk. boşuna, boş yere harcanmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13569, 'ziyanı yok!', 'özür dileyenlere karşılık olarak bağışlandığını, olayın pek önemli olmadığını bildirmek için söylenen bir söz: ''Biraz çabuk işe girişmiş olacağız ama ziyanı yok diye düşündü.'' -S. Kocagöz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13570, 'ziyaret etmek', '1) birini görmeye gitmek: ''Sporcular da birbirlerini sık sık ziyaret etsinler.'' -N. Hikmet. 2) bir yeri görmeye gitmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13571, 'zokayı yutmak', ' argo aldatılıp zarara sokulmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13572, 'zom olmak', 'çok sarhoş olmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13573, 'zor alıma çarpmak', 'kişi mallarına devlet adına yasal olarak el koymak, müsadere etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13574, 'zor gelmek', 'bir işin yapılması birine güç gelmek: ''Ama, sevdiğimiz insanın acı çekmesini seyretmek, ölüm acısından çok daha zor gelmiştir bana.'' -K. Tahir.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13575, 'zor kapıdan girerse, şeriat bacadan çıkar', 'zorbalığın hüküm sürdüğü yerde din kuralları, kanun emirleri yürümez.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13576, 'zor kullanmak', 'bir işin yapılması için her türlü baskıya başvurmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13577, 'zor oyunu bozar', 'hileyle hazırlanan bir düzenin güç kullanılarak üstesinden gelinebilir.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13578, 'zora, beylerin borcu var', 'zor kullanan kişilerin istediğini en güçlü kimseler bile verirler.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13579, 'zora binmek', '1) iş ancak zor kullanılmakla sonuçlanacak bir hâl almak; 2) zorlaşmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13580, 'zora dağlar dayanmaz', 'zor kullanan kişilere çok güçlü sanılan kimseler bile boyun eğer.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13581, 'zora gelememek', 'baskıya, sıkıntıya veya sıkı bir çalışmaya dayanamamak, katlanamamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13582, 'zora koşmak', 'güçlük çıkarmak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13583, 'zorla güzellik olmaz', '1) kişiye, beğenmediği şey zorla beğendirilemez; 2) bir iş insana zor kullanılarak yaptırılamaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13584, 'zoru olmak', 'kendisini zorlayan bir durumu, bir sıkıntısı olmak, sorunu bulunmak, güçlüğü olmak: ''Bizim anlayacağımız, bu memleketin iki tek zoru var. Biri okul, öteki de yol.'' -B. R. Eyuboğlu.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13585, 'zorun ne?', '1) ''amacın ne, ne istiyorsun?'' anlamında kullanılan bir söz; 2) ''neden kendini bu kadar zorluyorsun'' anlamında kullanılan bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13586, 'zorunda bırakmak', 'yapmaya mecbur etmek: ''Onları susmak zorunda bırakmanın sıkıntısını duyuyorum.'' -N. Cumalı.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13587, 'zorunda kalmak (olmak)', 'kesinlikle yapması gerekmek, yapmaya mecbur olmak: ''Kitabını basacak yayınevi bulamamış, onu kendi parasıyla bastırmak zorunda kalmıştır.'' -S. Birsel.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13588, 'zorunlu kılmak', 'mecbur etmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13589, 'zula etmek', '1) çalmak, aşırmak; 2) gizlemek, saklamak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13590, 'zulaya atmak', 'bir şeyi gizli bir yere koymak.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13591, 'zulüm görmek', 'kendisine eziyet edilmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13592, 'zurna gibi', 'dar (pantolon).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13593, 'zurnacının karşısında limon yemek gibi', 'birinin zihni çelinip işini göremeyecek duruma getirildiği anlatılırken söylenen bir söz.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13594, 'zurnada peşrev olmaz, ne çıkarsa bahtına', 'rastgele yapılan plansız işlerde yöntem, kural aranmaz.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13595, 'zurnanın zırt dediği yer', 'sürdürülmekte olan bir işin en can alıcı noktası.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13596, 'züğürt bezirgân eski defterleri karıştırır', '1) tüccar züğürtleyince, belki bir kimsede alacağım kalmıştır diye eski defterlerini gözden geçirir; 2) vaktiyle önemli işler yapmış olanlar, düşkünlüklerinde eski durumlarını anarak, anlatarak avunmaya çalışırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13597, 'züğürt olup düşmektense uyuz olup kaşınmak yeğdir', 'uyuz olup kaşınmak insanı çok rahatsız eder ama züğürtlükten dolayı ne yapacağını düşünmek daha çok rahatsız eder.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13598, 'züğürtleyen bezirgân, eski defterleri yoklar', '1) tüccar züğürtleyince, belki bir kimsede alacağım kalmıştır diye eski defterlerini gözden geçirir; 2) vaktiyle önemli işler yapmış olanlar, düşkünlüklerinde eski durumlarını anarak, anlatarak avunmaya çalışırlar.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13599, 'züğürtlük, zadeliği bozar', 'soylu kimse fakirleşince soyluluğu unutulur.', 'Atasözü');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13600, 'zül saymak (addetmek)', 'bir olay veya sözü küçültücü, alçaltıcı, aşağılayıcı olarak değerlendirmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13601, 'zülfüyâre dokunmak', '1) hatırlı, güçlü bir kimseyi veya bir makamı gücendirmek, darılmasına yol açmak; 2) birine zarar veya sıkıntı vermek; 3) sıkıntı verecek, sorun olacak konulara girmek.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13602, 'Zümrüdüanka gibi', 'hayal ürünü olan veya adı olup da kendi var olmayan (iyi ve güzel şeyler).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13603, 'zümrüt gibi', 'yemyeşil: ''Şu zümrüt gibi Kuşdili deresi burnumuzun önünde de bir sandalla bir defa bile dolaşamadık.'' -S. M. Alus.', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13604, 'zürafa gibi', 'ince, uzun boylu, uzun boyunlu (kimse).', 'Deyim');
INSERT INTO proverb (id, proverb, meaning, proverb_type) VALUES (13605, 'zürefanın düşkünü, beyaz giyer kış günü', 'daha önce iyi bir durumda olan kişi bu konumunu kaybettiğinde uygun olmayan, yersiz davranışlarda bulunur.', 'Atasözü');
